/*In the name of ALLAH*/
ayat=new Array();
ayat[1]=[1,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ','به نام خداوند بخشنده بخشایشگر'];
ayat[2]=[1,2,'الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ','ستایش مخصوص خداوندی است که پروردگار جهانیان است.'];
ayat[3]=[1,3,'الرَّحْمَٰنِ الرَّحِيمِ','(خداوندی که) بخشنده و بخشایشگر است (و رحمت عام و خاصش همگان را فرا گرفته).'];
ayat[4]=[1,4,'مَالِكِ يَوْمِ الدِّينِ','(خداوندی که) مالک روز جزاست.'];
ayat[5]=[1,5,'إِيَّاكَ نَعْبُدُ وَإِيَّاكَ نَسْتَعِينُ','(پروردگارا!) تنها تو را می‌پرستیم؛ و تنها از تو یاری می‌جوییم.'];
ayat[6]=[1,6,'اهْدِنَا الصِّرَاطَ الْمُسْتَقِيمَ','ما را به راه راست هدایت کن...'];
ayat[7]=[1,7,'صِرَاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَلَا الضَّالِّينَ','راه کسانی که آنان را مشمول نعمت خود ساختی؛ نه کسانی که بر آنان غضب کرده‌ای؛ و نه گمراهان.'];
ayat[8]=[2,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم','الم (بزرگ است خداوندی که این کتاب عظیم را، از حروف ساده الفبا به وجود آورده).'];
ayat[9]=[2,2,'ذَٰلِكَ الْكِتَابُ لَا رَيْبَ ۛ فِيهِ ۛ هُدًى لِّلْمُتَّقِينَ','آن کتاب با عظمتی است که شک در آن راه ندارد؛ و مایه هدایت پرهیزکاران است.'];
ayat[10]=[2,3,'الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ الصَّلَاةَ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ','(پرهیزکاران) کسانی هستند که به غیب [=آنچه از حس پوشیده و پنهان است‌] ایمان می‌آورند؛ و نماز را برپا می‌دارند؛ و از تمام نعمتها و مواهبی که به آنان روزی داده‌ایم، انفاق می‌کنند.'];
ayat[11]=[2,4,'وَالَّذِينَ يُؤْمِنُونَ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ وَبِالْآخِرَةِ هُمْ يُوقِنُونَ','و آنان که به آنچه بر تو نازل شده، و آنچه پیش از تو (بر پیامبران پیشین) نازل گردیده، ایمان می‌آورند؛ و به رستاخیز یقین دارند.'];
ayat[12]=[2,5,'أُولَٰئِكَ عَلَىٰ هُدًى مِّن رَّبِّهِمْ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','آنان بر طریق هدایت پروردگارشانند؛ و آنان رستگارانند.'];
ayat[13]=[2,6,'إِنَّ الَّذِينَ كَفَرُوا سَوَاءٌ عَلَيْهِمْ أَأَنذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَ','کسانی که کافر شدند، برای آنان تفاوت نمی‌کند که آنان را (از عذاب الهی) بترسانی یا نترسانی؛ ایمان نخواهند آورد.'];
ayat[14]=[2,7,'خَتَمَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَعَلَىٰ سَمْعِهِمْ ۖ وَعَلَىٰ أَبْصَارِهِمْ غِشَاوَةٌ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ','خدا بر دلها و گوشهای آنان مهر نهاده؛ و بر چشمهایشان پرده‌ای افکنده شده؛ و عذاب بزرگی در انتظار آنهاست.'];
ayat[15]=[2,8,'وَمِنَ النَّاسِ مَن يَقُولُ آمَنَّا بِاللَّهِ وَبِالْيَوْمِ الْآخِرِ وَمَا هُم بِمُؤْمِنِينَ','گروهی از مردم کسانی هستند که می‌گویند: «به خدا و روز رستاخیز ایمان آورده‌ایم.» در حالی که ایمان ندارند.'];
ayat[16]=[2,9,'يُخَادِعُونَ اللَّهَ وَالَّذِينَ آمَنُوا وَمَا يَخْدَعُونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَ','می‌خواهند خدا و مؤمنان را فریب دهند؛ در حالی که جز خودشان را فریب نمی‌دهند؛ (اما) نمی‌فهمند.'];
ayat[17]=[2,10,'فِي قُلُوبِهِم مَّرَضٌ فَزَادَهُمُ اللَّهُ مَرَضًا ۖ وَلَهُمْ عَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْذِبُونَ','در دلهای آنان یک نوع بیماری است؛ خداوند بر بیماری آنان افزوده؛ و به خاطر دروغهایی که میگفتند، عذاب دردناکی در انتظار آنهاست.'];
ayat[18]=[2,11,'وَإِذَا قِيلَ لَهُمْ لَا تُفْسِدُوا فِي الْأَرْضِ قَالُوا إِنَّمَا نَحْنُ مُصْلِحُونَ','و هنگامی که به آنان گفته شود: «در زمین فساد نکنید» می‌گویند: «ما فقط اصلاح‌کننده‌ایم»!'];
ayat[19]=[2,12,'أَلَا إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَلَٰكِن لَّا يَشْعُرُونَ','آگاه باشید! اینها همان مفسدانند؛ ولی نمی‌فهمند.'];
ayat[20]=[2,13,'وَإِذَا قِيلَ لَهُمْ آمِنُوا كَمَا آمَنَ النَّاسُ قَالُوا أَنُؤْمِنُ كَمَا آمَنَ السُّفَهَاءُ ۗ أَلَا إِنَّهُمْ هُمُ السُّفَهَاءُ وَلَٰكِن لَّا يَعْلَمُونَ','و هنگامی که به آنان گفته شود: «همانند (سایر) مردم ایمان بیاورید!» می‌گویند: «آیا همچون ابلهان ایمان بیاوریم؟!» بدانید اینها همان ابلهانند ولی نمی‌دانند!'];
ayat[21]=[2,14,'وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَوْا إِلَىٰ شَيَاطِينِهِمْ قَالُوا إِنَّا مَعَكُمْ إِنَّمَا نَحْنُ مُسْتَهْزِئُونَ','و هنگامی که افراد باایمان را ملاقات می‌کنند، و می‌گویند: «ما ایمان آورده‌ایم!» (ولی) هنگامی که با شیطانهای خود خلوت می‌کنند، می‌گویند: «ما با شمائیم! ما فقط (آنها را) مسخره می‌کنیم!»'];
ayat[22]=[2,15,'اللَّهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ','خداوند آنان را استهزا می‌کند؛ و آنها را در طغیانشان نگه می‌دارد، تا سرگردان شوند.'];
ayat[23]=[2,16,'أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ فَمَا رَبِحَت تِّجَارَتُهُمْ وَمَا كَانُوا مُهْتَدِينَ','آنان کسانی هستند که «هدایت» را به «گمراهی» فروخته‌اند؛ و (این) تجارت آنها سودی نداده؛ و هدایت نیافته‌اند.'];
ayat[24]=[2,17,'مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نَارًا فَلَمَّا أَضَاءَتْ مَا حَوْلَهُ ذَهَبَ اللَّهُ بِنُورِهِمْ وَتَرَكَهُمْ فِي ظُلُمَاتٍ لَّا يُبْصِرُونَ','آنان [= منافقان‌] همانند کسی هستند که آتشی افروخته (تا در بیابان تاریک، راه خود را پیدا کند)، ولی هنگامی که آتش اطراف او را روشن ساخت، خداوند (طوفانی می‌فرستد و) آن را خاموش می‌کند؛ و در تاریکیهای وحشتناکی که چشم کار نمی‌کند، آنها را رها می‌سازد.'];
ayat[25]=[2,18,'صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُونَ','آنها کران، گنگها و کورانند؛ لذا (از راه خطا) بازنمی‌گردند!'];
ayat[26]=[2,19,'أَوْ كَصَيِّبٍ مِّنَ السَّمَاءِ فِيهِ ظُلُمَاتٌ وَرَعْدٌ وَبَرْقٌ يَجْعَلُونَ أَصَابِعَهُمْ فِي آذَانِهِم مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِ ۚ وَاللَّهُ مُحِيطٌ بِالْكَافِرِينَ','یا همچون بارانی از آسمان، که در شب تاریک همراه با رعد و برق و صاعقه (بر سر رهگذران) ببارد. آنها از ترس مرگ، انگشتانشان را در گوشهای خود می‌گذارند؛ تا صدای صاعقه را نشنوند. و خداوند به کافران احاطه دارد (و در قبضه قدرت او هستند).'];
ayat[27]=[2,20,'يَكَادُ الْبَرْقُ يَخْطَفُ أَبْصَارَهُمْ ۖ كُلَّمَا أَضَاءَ لَهُم مَّشَوْا فِيهِ وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُوا ۚ وَلَوْ شَاءَ اللَّهُ لَذَهَبَ بِسَمْعِهِمْ وَأَبْصَارِهِمْ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','(روشنائی خیره کننده) برق، نزدیک است چشمانشان را برباید. هر زمان که (برق جستن می‌کند، و صفحه بیابان را) برای آنها روشن می‌سازد، (چند گامی) در پرتو آن راه می‌روند؛ و چون خاموش می‌شود، توقف می‌کنند. و اگر خدا بخواهد، گوش و چشم آنها را از بین می‌برد؛ چرا که خداوند بر هر چیز تواناست.'];
ayat[28]=[2,21,'يَا أَيُّهَا النَّاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَالَّذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ','ای مردم! پروردگار خود را پرستش کنید؛ آن کس که شما، و کسانی را که پیش از شما بودند آفرید، تا پرهیزکار شوید.'];
ayat[29]=[2,22,'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِرَاشًا وَالسَّمَاءَ بِنَاءً وَأَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَّكُمْ ۖ فَلَا تَجْعَلُوا لِلَّهِ أَندَادًا وَأَنتُمْ تَعْلَمُونَ','آن کس که زمین را بستر شما، و آسمان [= جو زمین‌] را همچون سقفی بالای سر شما قرار داد؛ و از آسمان آبی فرو فرستاد؛ و به وسیله آن، میوه‌ها را پرورش داد؛ تا روزی شما باشد. بنابر این، برای خدا همتایانی قرار ندهید، در حالی که می‌دانید (هیچ یک از آنها، نه شما را آفریده‌اند، و نه شما را روزی می‌دهند).'];
ayat[30]=[2,23,'وَإِن كُنتُمْ فِي رَيْبٍ مِّمَّا نَزَّلْنَا عَلَىٰ عَبْدِنَا فَأْتُوا بِسُورَةٍ مِّن مِّثْلِهِ وَادْعُوا شُهَدَاءَكُم مِّن دُونِ اللَّهِ إِن كُنتُمْ صَادِقِينَ','و اگر در باره آنچه بر بنده خود [= پیامبر] نازل کرده‌ایم شک و تردید دارید، (دست کم) یک سوره همانند آن بیاورید؛ و گواهان خود را - غیر خدا - برای این کار، فرا خوانید اگر راست می‌گویید!'];
ayat[31]=[2,24,'فَإِن لَّمْ تَفْعَلُوا وَلَن تَفْعَلُوا فَاتَّقُوا النَّارَ الَّتِي وَقُودُهَا النَّاسُ وَالْحِجَارَةُ ۖ أُعِدَّتْ لِلْكَافِرِينَ','پس اگر چنین نکنید - که هرگز نخواهید کرد - از آتشی بترسید که هیزم آن، بدنهای مردم (گنهکار) و سنگها [= بتها] است، و برای کافران، آماده شده است!'];
ayat[32]=[2,25,'وَبَشِّرِ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أَنَّ لَهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۖ كُلَّمَا رُزِقُوا مِنْهَا مِن ثَمَرَةٍ رِّزْقًا ۙ قَالُوا هَٰذَا الَّذِي رُزِقْنَا مِن قَبْلُ ۖ وَأُتُوا بِهِ مُتَشَابِهًا ۖ وَلَهُمْ فِيهَا أَزْوَاجٌ مُّطَهَّرَةٌ ۖ وَهُمْ فِيهَا خَالِدُونَ','به کسانی که ایمان آورده، و کارهای شایسته انجام داده‌اند، بشارت ده که باغهایی از بهشت برای آنهاست که نهرها از زیر درختانش جاریست. هر زمان که میوه‌ای از آن، به آنان داده شود، می‌گویند: «این همان است که قبلا به ما روزی داده شده بود. (ولی اینها چقدر از آنها بهتر و عالیتر است.)» و میوه‌هایی که برای آنها آورده می‌شود، همه (از نظر خوبی و زیبایی) یکسانند. و برای آنان همسرانی پاک و پاکیزه است، و جاودانه در آن خواهند بود.'];
ayat[33]=[2,26,'۞ إِنَّ اللَّهَ لَا يَسْتَحْيِي أَن يَضْرِبَ مَثَلًا مَّا بَعُوضَةً فَمَا فَوْقَهَا ۚ فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِن رَّبِّهِمْ ۖ وَأَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مَاذَا أَرَادَ اللَّهُ بِهَٰذَا مَثَلًا ۘ يُضِلُّ بِهِ كَثِيرًا وَيَهْدِي بِهِ كَثِيرًا ۚ وَمَا يُضِلُّ بِهِ إِلَّا الْفَاسِقِينَ','خداوند از این که (به موجودات ظاهرا کوچکی مانند) پشه، و حتی کمتر از آن، مثال بزند شرم نمی‌کند. (در این میان) آنان که ایمان آورده‌اند، می‌دانند که آن، حقیقتی است از طرف پروردگارشان؛ و اما آنها که راه کفر را پیموده‌اند، (این موضوع را بهانه کرده) می‌گویند: «منظور خداوند از این مثل چه بوده است؟!» (آری،) خدا جمع زیادی را با آن گمراه، و گروه بسیاری را هدایت می‌کند؛ ولی تنها فاسقان را با آن گمراه می‌سازد!'];
ayat[34]=[2,27,'الَّذِينَ يَنقُضُونَ عَهْدَ اللَّهِ مِن بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَن يُوصَلَ وَيُفْسِدُونَ فِي الْأَرْضِ ۚ أُولَٰئِكَ هُمُ الْخَاسِرُونَ','فاسقان کسانی هستند که پیمان خدا را، پس از محکم ساختن آن، میشکنند؛ و پیوندهایی را که خدا دستور داده برقرار سازند، قطع نموده، و در روی زمین فساد میکنند؛ اینها زیانکارانند.'];
ayat[35]=[2,28,'كَيْفَ تَكْفُرُونَ بِاللَّهِ وَكُنتُمْ أَمْوَاتًا فَأَحْيَاكُمْ ۖ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْجَعُونَ','چگونه به خداوند کافر می‌شوید؟! در حالی که شما مردگان (و اجسام بی‌روحی) بودید، و او شما را زنده کرد؛ سپس شما را می‌میراند؛ و بار دیگر شما را زنده می‌کند؛ سپس به سوی او بازگردانده می‌شوید. (بنابر این، نه حیات و زندگی شما از شماست، و نه مرگتان؛ آنچه دارید از خداست).'];
ayat[36]=[2,29,'هُوَ الَّذِي خَلَقَ لَكُم مَّا فِي الْأَرْضِ جَمِيعًا ثُمَّ اسْتَوَىٰ إِلَى السَّمَاءِ فَسَوَّاهُنَّ سَبْعَ سَمَاوَاتٍ ۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ','او خدایی است که همه آنچه را (از نعمتها) در زمین وجود دارد، برای شما آفرید؛ سپس به آسمان پرداخت؛ و آنها را به صورت هفت آسمان مرتب نمود؛ و او به هر چیز آگاه است.'];
ayat[37]=[2,30,'وَإِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي جَاعِلٌ فِي الْأَرْضِ خَلِيفَةً ۖ قَالُوا أَتَجْعَلُ فِيهَا مَن يُفْسِدُ فِيهَا وَيَسْفِكُ الدِّمَاءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۖ قَالَ إِنِّي أَعْلَمُ مَا لَا تَعْلَمُونَ','(به خاطر بیاور) هنگامی را که پروردگارت به فرشتگان گفت: «من در روی زمین، جانشینی [= نماینده‌ای‌] قرار خواهم داد.» فرشتگان گفتند: «پروردگارا!» آیا کسی را در آن قرار می‌دهی که فساد و خونریزی کند؟! (زیرا موجودات زمینی دیگر، که قبل از این آدم وجود داشتند نیز، به فساد و خونریزی آلوده شدند. اگر هدف از آفرینش این انسان، عبادت است،) ما تسبیح و حمد تو را بجا می‌آوریم، و تو را تقدیس می‌کنیم.» پروردگار فرمود: «من حقایقی را می‌دانم که شما نمی‌دانید.»'];
ayat[38]=[2,31,'وَعَلَّمَ آدَمَ الْأَسْمَاءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلَائِكَةِ فَقَالَ أَنبِئُونِي بِأَسْمَاءِ هَٰؤُلَاءِ إِن كُنتُمْ صَادِقِينَ','سپس علم اسماء [= علم اسرار آفرینش و نامگذاری موجودات‌] را همگی به آدم آموخت. بعد آنها را به فرشتگان عرضه داشت و فرمود: «اگر راست می‌گویید، اسامی اینها را به من خبر دهید!»'];
ayat[39]=[2,32,'قَالُوا سُبْحَانَكَ لَا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا ۖ إِنَّكَ أَنتَ الْعَلِيمُ الْحَكِيمُ','فرشتگان عرض کردند: «منزهی تو! ما چیزی جز آنچه به ما تعلیم داده‌ای، نمی‌دانیم؛ تو دانا و حکیمی.»'];
ayat[40]=[2,33,'قَالَ يَا آدَمُ أَنبِئْهُم بِأَسْمَائِهِمْ ۖ فَلَمَّا أَنبَأَهُم بِأَسْمَائِهِمْ قَالَ أَلَمْ أَقُل لَّكُمْ إِنِّي أَعْلَمُ غَيْبَ السَّمَاوَاتِ وَالْأَرْضِ وَأَعْلَمُ مَا تُبْدُونَ وَمَا كُنتُمْ تَكْتُمُونَ','فرمود: «ای آدم! آنان را از اسامی (و اسرار) این موجودات آگاه کن.» هنگامی که آنان را آگاه کرد، خداوند فرمود: «آیا به شما نگفتم که من، غیب آسمانها و زمین را میدانم؟! و نیز میدانم آنچه را شما آشکار میکنید، و آنچه را پنهان میداشتید!»'];
ayat[41]=[2,34,'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ أَبَىٰ وَاسْتَكْبَرَ وَكَانَ مِنَ الْكَافِرِينَ','و (یاد کن) هنگامی را که به فرشتگان گفتیم: «برای آدم سجده و خضوع کنید!» همگی سجده کردند؛ جز ابلیس که سر باز زد، و تکبر ورزید، (و به خاطر نافرمانی و تکبرش) از کافران شد.'];
ayat[42]=[2,35,'وَقُلْنَا يَا آدَمُ اسْكُنْ أَنتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هَٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ','و گفتیم: «ای آدم! تو با همسرت در بهشت سکونت کن؛ و از (نعمتهای) آن، از هر جا می‌خواهید، گوارا بخورید؛ (اما) نزدیک این درخت نشوید؛ که از ستمگران خواهید شد.'];
ayat[43]=[2,36,'فَأَزَلَّهُمَا الشَّيْطَانُ عَنْهَا فَأَخْرَجَهُمَا مِمَّا كَانَا فِيهِ ۖ وَقُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ وَلَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَىٰ حِينٍ','پس شیطان موجب لغزش آنها از بهشت شد؛ و آنان را از آنچه در آن بودند، بیرون کرد. و (در این هنگام) به آنها گفتیم: «همگی (به زمین) فرود آیید! در حالی که بعضی دشمن دیگری خواهید بود. و برای شما در زمین، تا مدت معینی قرارگاه و وسیله بهره برداری خواهد بود.»'];
ayat[44]=[2,37,'فَتَلَقَّىٰ آدَمُ مِن رَّبِّهِ كَلِمَاتٍ فَتَابَ عَلَيْهِ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ','سپس آدم از پروردگارش کلماتی دریافت داشت؛ (و با آنها توبه کرد.) و خداوند توبه او را پذیرفت؛ چرا که خداوند توبه‌پذیر و مهربان است.'];
ayat[45]=[2,38,'قُلْنَا اهْبِطُوا مِنْهَا جَمِيعًا ۖ فَإِمَّا يَأْتِيَنَّكُم مِّنِّي هُدًى فَمَن تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','گفتیم: «همگی از آن، فرود آیید! هرگاه هدایتی از طرف من برای شما آمد، کسانی که از آن پیروی کنند، نه ترسی بر آنهاست، و نه غمگین شوند.»'];
ayat[46]=[2,39,'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','و کسانی که کافر شدند، و آیات ما را دروغ پنداشتند اهل دوزخند؛ و همیشه در آن خواهند بود.'];
ayat[47]=[2,40,'يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَإِيَّايَ فَارْهَبُونِ','ای فرزندان اسرائیل! نعمتهایی را که به شما ارزانی داشتم به یاد آورید! و به پیمانی که با من بسته‌اید وفا کنید، تا من نیز به پیمان شما وفا کنم. (و در راه انجام وظیفه، و عمل به پیمانها) تنها از من بترسید!'];
ayat[48]=[2,41,'وَآمِنُوا بِمَا أَنزَلْتُ مُصَدِّقًا لِّمَا مَعَكُمْ وَلَا تَكُونُوا أَوَّلَ كَافِرٍ بِهِ ۖ وَلَا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلًا وَإِيَّايَ فَاتَّقُونِ','و به آنچه نازل کرده‌ام [= قرآن‌] ایمان بیاورید! که نشانه‌های آن، با آنچه در کتابهای شماست، مطابقت دارد؛ و نخستین کافر به آن نباشید! و آیات مرا به بهای ناچیزی نفروشید! (و به خاطر درآمد مختصری، نشانه‌های قرآن و پیامبر اسلام را، که در کتب شما موجود است، پنهان نکنید!) و تنها از من (و مخالفت دستورهایم) بترسید (نه از مردم)!'];
ayat[49]=[2,42,'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَأَنتُمْ تَعْلَمُونَ','و حق را با باطل نیامیزید! و حقیقت را با اینکه می‌دانید کتمان نکنید!'];
ayat[50]=[2,43,'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَارْكَعُوا مَعَ الرَّاكِعِينَ','و نماز را بپا دارید، و زکات را بپردازید، و همراه رکوع کنندگان رکوع کنید (و نماز را با جماعت بگزارید)!'];
ayat[51]=[2,44,'۞ أَتَأْمُرُونَ النَّاسَ بِالْبِرِّ وَتَنسَوْنَ أَنفُسَكُمْ وَأَنتُمْ تَتْلُونَ الْكِتَابَ ۚ أَفَلَا تَعْقِلُونَ','آیا مردم را به نیکی (و ایمان به پیامبری که صفات او آشکارا در تورات آمده) دعوت می‌کنید، اما خودتان را فراموش می‌نمایید؛ با اینکه شما کتاب (آسمانی) را می‌خوانید! آیا نمی‌اندیشید؟!'];
ayat[52]=[2,45,'وَاسْتَعِينُوا بِالصَّبْرِ وَالصَّلَاةِ ۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى الْخَاشِعِينَ','از صبر و نماز یاری جوئید؛ (و با استقامت و مهار هوسهای درونی و توجه به پروردگار، نیرو بگیرید؛) و این کار، جز برای خاشعان، گران است.'];
ayat[53]=[2,46,'الَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَاقُو رَبِّهِمْ وَأَنَّهُمْ إِلَيْهِ رَاجِعُونَ','آنها کسانی هستند که می‌دانند دیدارکننده پروردگار خویشند، و به سوی او بازمی‌گردند.'];
ayat[54]=[2,47,'يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ','ای بنی اسرائیل! نعمتهایی را که به شما ارزانی داشتم به خاطر بیاورید؛ و (نیز به یاد آورید که) من، شما را بر جهانیان، برتری بخشیدم.'];
ayat[55]=[2,48,'وَاتَّقُوا يَوْمًا لَّا تَجْزِي نَفْسٌ عَن نَّفْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَلَا هُمْ يُنصَرُونَ','و از آن روز بترسید که کسی مجازات دیگری را نمی‌پذیرد و نه از او شفاعت پذیرفته می‌شود؛ و نه غرامت از او قبول خواهد شد؛ و نه یاری می‌شوند.'];
ayat[56]=[2,49,'وَإِذْ نَجَّيْنَاكُم مِّنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ يُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُم بَلَاءٌ مِّن رَّبِّكُمْ عَظِيمٌ','و (نیز به یاد آورید) آن زمان که شما را از چنگال فرعونیان رهایی بخشیدیم؛ که همواره شما را به بدترین صورت آزار می‌دادند: پسران شما را سر می‌بریدند؛ و زنان شما را (برای کنیزی) زنده نگه می‌داشتند. و در اینها، آزمایش بزرگی از طرف پروردگارتان برای شما بود.'];
ayat[57]=[2,50,'وَإِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَأَنجَيْنَاكُمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ وَأَنتُمْ تَنظُرُونَ','و (به خاطر بیاورید) هنگامی را که دریا را برای شما شکافتیم؛ و شما را نجات دادیم؛ و فرعونیان را غرق ساختیم؛ در حالی که شما تماشا می‌کردید.'];
ayat[58]=[2,51,'وَإِذْ وَاعَدْنَا مُوسَىٰ أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِن بَعْدِهِ وَأَنتُمْ ظَالِمُونَ','و (به یاد آورید) هنگامی را که با موسی چهل شب وعده گذاردیم؛ (و او، برای گرفتن فرمانهای الهی، به میعادگاه آمد؛) سپس شما گوساله را بعد از او (معبود خود) انتخاب نمودید؛ در حالی که ستمکار بودید.'];
ayat[59]=[2,52,'ثُمَّ عَفَوْنَا عَنكُم مِّن بَعْدِ ذَٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ','سپس شما را بعد از آن بخشیدیم؛ شاید شکر (این نعمت را) بجا آورید.'];
ayat[60]=[2,53,'وَإِذْ آتَيْنَا مُوسَى الْكِتَابَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَ','و (نیز به خاطر آورید) هنگامی را که به موسی، کتاب و وسیله تشخیص (حق از باطل) را دادیم؛ تا هدایت شوید.'];
ayat[61]=[2,54,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنفُسَكُم بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوبُوا إِلَىٰ بَارِئِكُمْ فَاقْتُلُوا أَنفُسَكُمْ ذَٰلِكُمْ خَيْرٌ لَّكُمْ عِندَ بَارِئِكُمْ فَتَابَ عَلَيْكُمْ ۚ إِنَّهُ هُوَ التَّوَّابُ الرَّحِيمُ','و زمانی را که موسی به قوم خود گفت: «ای قوم من! شما با انتخاب گوساله (برای پرستش) به خود ستم کردید! پس توبه کنید؛ و به سوی خالق خود باز گردید! و خود را [=یکدیگر را] به قتل برسانید! این کار، برای شما در پیشگاه پروردگارتان بهتر است.» سپس خداوند توبه شما را پذیرفت؛ زیرا که او توبه‌پذیر و رحیم است.'];
ayat[62]=[2,55,'وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَن نُّؤْمِنَ لَكَ حَتَّىٰ نَرَى اللَّهَ جَهْرَةً فَأَخَذَتْكُمُ الصَّاعِقَةُ وَأَنتُمْ تَنظُرُونَ','و (نیز به یاد آورید) هنگامی را که گفتید: «ای موسی! ما هرگز به تو ایمان نخواهیم آورد؛ مگر اینکه خدا را آشکارا (با چشم خود) ببینیم!» پس صاعقه شما را گرفت؛ در حالی که تماشا می‌کردید.'];
ayat[63]=[2,56,'ثُمَّ بَعَثْنَاكُم مِّن بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ','سپس شما را پس از مرگتان، حیات بخشیدیم؛ شاید شکر (نعمت او را) بجا آورید.'];
ayat[64]=[2,57,'وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَأَنزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَىٰ ۖ كُلُوا مِن طَيِّبَاتِ مَا رَزَقْنَاكُمْ ۖ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','و ابر را بر شما سایبان قرار دادیم؛ و «من» [= شیره مخصوص و لذیذ درختان] و «سلوی» [= مرغان مخصوص شبیه کبوتر] را بر شما فرستادیم؛ (و گفتیم:) «از نعمتهای پاکیزه‌ای که به شما روزی داده‌ایم بخورید!» (ولی شما کفران کردید!) آنها به ما ستم نکردند؛ بلکه به خود ستم می‌نمودند.'];
ayat[65]=[2,58,'وَإِذْ قُلْنَا ادْخُلُوا هَٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَادْخُلُوا الْبَابَ سُجَّدًا وَقُولُوا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطَايَاكُمْ ۚ وَسَنَزِيدُ الْمُحْسِنِينَ','و (به خاطر بیاورید) زمانی را که گفتیم: «در این شهر [= بیت المقدس‌] وارد شوید! و از نعمتهای فراوان آن، هر چه می‌خواهید بخورید! و از در (معبد بیت المقدس) با خضوع و خشوع وارد گردید! و بگویید: «خداوندا! گناهان ما را بریز!» تا خطاهای شما را ببخشیم؛ و به نیکوکاران پاداش بیشتری خواهیم داد.»'];
ayat[66]=[2,59,'فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلًا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنزَلْنَا عَلَى الَّذِينَ ظَلَمُوا رِجْزًا مِّنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ','اما افراد ستمگر، این سخن را که به آنها گفته شده بود، تغییر دادند؛ (و به جای آن، جمله استهزاآمیزی گفتند؛) لذا بر ستمگران، در برابر این نافرمانی، عذابی از آسمان فرستادیم.'];
ayat[67]=[2,60,'۞ وَإِذِ اسْتَسْقَىٰ مُوسَىٰ لِقَوْمِهِ فَقُلْنَا اضْرِب بِّعَصَاكَ الْحَجَرَ ۖ فَانفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۖ قَدْ عَلِمَ كُلُّ أُنَاسٍ مَّشْرَبَهُمْ ۖ كُلُوا وَاشْرَبُوا مِن رِّزْقِ اللَّهِ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ','و (به یاد آورید) زمانی را که موسی برای قوم خویش، آب طلبید، به او دستور دادیم: «عصای خود را بر آن سنگ مخصوص بزن!» ناگاه دوازده چشمه آب از آن جوشید؛ آن‌گونه که هر یک (از طوایف دوازده‌گانه بنی اسرائیل)، چشمه مخصوص خود را می‌شناختند! (و گفتیم:) «از روزیهای الهی بخورید و بیاشامید! و در زمین فساد نکنید!»'];
ayat[68]=[2,61,'وَإِذْ قُلْتُمْ يَا مُوسَىٰ لَن نَّصْبِرَ عَلَىٰ طَعَامٍ وَاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنبِتُ الْأَرْضُ مِن بَقْلِهَا وَقِثَّائِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَا ۖ قَالَ أَتَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنَىٰ بِالَّذِي هُوَ خَيْرٌ ۚ اهْبِطُوا مِصْرًا فَإِنَّ لَكُم مَّا سَأَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَاءُوا بِغَضَبٍ مِّنَ اللَّهِ ۗ ذَٰلِكَ بِأَنَّهُمْ كَانُوا يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ الْحَقِّ ۗ ذَٰلِكَ بِمَا عَصَوا وَّكَانُوا يَعْتَدُونَ','و (نیز به خاطر بیاورید) زمانی را که گفتید: «ای موسی! هرگز حاضر نیستیم به یک نوع غذا اکتفا کنیم! از خدای خود بخواه که از آنچه زمین می‌رویاند، از سبزیجات و خیار و سیر و عدس و پیازش، برای ما فراهم سازد.» موسی گفت: «آیا غذای پست‌تر را به جای غذای بهتر انتخاب می‌کنید؟! (اکنون که چنین است، بکوشید از این بیابان) در شهری فرود آئید؛ زیرا هر چه خواستید، در آنجا برای شما هست.» و (مهر) ذلت و نیاز، بر پیشانی آنها زده شد؛ و باز گرفتار خشم خدائی شدند؛ چرا که آنان نسبت به آیات الهی، کفر می‌ورزیدند؛ و پیامبران را به ناحق می‌کشتند. اینها به خاطر آن بود که گناهکار و متجاوز بودند.'];
ayat[69]=[2,62,'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالنَّصَارَىٰ وَالصَّابِئِينَ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','کسانی که (به پیامبر اسلام) ایمان آورده‌اند، و کسانی که به آئین یهود گرویدند و نصاری و صابئان [= پیروان یحیی‌] هر گاه به خدا و روز رستاخیز ایمان آورند، و عمل صالح انجام دهند، پاداششان نزد پروردگارشان مسلم است؛ و هیچ‌گونه ترس و اندوهی برای آنها نیست. (هر کدام از پیروان ادیان الهی، که در عصر و زمان خود، بر طبق وظایف و فرمان دین عمل کرده‌اند، مأجور و رستگارند.)'];
ayat[70]=[2,63,'وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ','و (به یاد آورید) زمانی را که از شما پیمان گرفتیم؛ و کوه طور را بالای سر شما قرار دادیم؛ (و به شما گفتیم:) «آنچه را (از آیات و دستورهای خداوند) به شما داده‌ایم، با قدرت بگیرید؛ و آنچه را در آن است به یاد داشته باشید (و به آن عمل کنید)؛ شاید پرهیزکار شوید!»'];
ayat[71]=[2,64,'ثُمَّ تَوَلَّيْتُم مِّن بَعْدِ ذَٰلِكَ ۖ فَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَكُنتُم مِّنَ الْخَاسِرِينَ','سپس شما پس از این، روگردان شدید؛ و اگر فضل و رحمت خداوند بر شما نبود، از زیانکاران بودید.'];
ayat[72]=[2,65,'وَلَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنكُمْ فِي السَّبْتِ فَقُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ','به طور قطع از حال کسانی از شما، که در روز شنبه نافرمانی و گناه کردند، آگاه شده‌اید! ما به آنها گفتیم: «به صورت بوزینه‌هایی طردشده درآیید!»'];
ayat[73]=[2,66,'فَجَعَلْنَاهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِّلْمُتَّقِينَ','ما این کیفر را درس عبرتی برای مردم آن زمان و نسلهای بعد از آنان، و پند و اندرزی برای پرهیزکاران قرار دادیم.'];
ayat[74]=[2,67,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ إِنَّ اللَّهَ يَأْمُرُكُمْ أَن تَذْبَحُوا بَقَرَةً ۖ قَالُوا أَتَتَّخِذُنَا هُزُوًا ۖ قَالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ الْجَاهِلِينَ','و (به یاد آورید) هنگامی را که موسی به قوم خود گفت: «خداوند به شما دستور می‌دهد ماده‌گاوی را ذبح کنید (و قطعه‌ای از بدن آن را به مقتولی که قاتل او شناخته نشده بزنید، تا زنده شود و قاتل خویش را معرفی کند؛ و غوغا خاموش گردد.)» گفتند: «آیا ما را مسخره می‌کنی؟» (موسی) گفت: «به خدا پناه می‌برم از اینکه از جاهلان باشم!»'];
ayat[75]=[2,68,'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَّا فَارِضٌ وَلَا بِكْرٌ عَوَانٌ بَيْنَ ذَٰلِكَ ۖ فَافْعَلُوا مَا تُؤْمَرُونَ','گفتند: «(پس) از خدای خود بخواه که برای ما روشن کند این ماده‌گاو چگونه ماده‌گاوی باشد؟» گفت: خداوند می‌فرماید: «ماده‌گاوی است که نه پیر و از کار افتاده باشد، و نه بکر و جوان؛ بلکه میان این دو باشد. آنچه به شما دستور داده شده، (هر چه زودتر) انجام دهید.»'];
ayat[76]=[2,69,'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا لَوْنُهَا ۚ قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ صَفْرَاءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النَّاظِرِينَ','گفتند: «از پروردگار خود بخواه که برای ما روشن سازد رنگ آن چگونه باشد؟ «گفت: خداوند می‌گوید: «گاوی باشد زرد یکدست، که رنگ آن، بینندگان را شاد و مسرور سازد.»'];
ayat[77]=[2,70,'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ إِنَّ الْبَقَرَ تَشَابَهَ عَلَيْنَا وَإِنَّا إِن شَاءَ اللَّهُ لَمُهْتَدُونَ','گفتند: «از خدایت بخواه برای ما روشن کند که چگونه گاوی باید باشد؟ زیرا این گاو برای ما مبهم شده! و اگر خدا بخواهد ما هدایت خواهیم شد!»'];
ayat[78]=[2,71,'قَالَ إِنَّهُ يَقُولُ إِنَّهَا بَقَرَةٌ لَّا ذَلُولٌ تُثِيرُ الْأَرْضَ وَلَا تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لَّا شِيَةَ فِيهَا ۚ قَالُوا الْآنَ جِئْتَ بِالْحَقِّ ۚ فَذَبَحُوهَا وَمَا كَادُوا يَفْعَلُونَ','گفت: خداوند می‌فرماید: «گاوی باشد که نه برای شخم زدن رام شده؛ و نه برای زراعت آبکشی کند؛ از هر عیبی برکنار باشد، و حتی هیچ‌گونه رنگ دیگری در آن نباشد.» گفتند: «الان حق مطلب را آوردی!» سپس (چنان گاوی را پیدا کردند و) آن را سر بریدند؛ ولی مایل نبودند این کار را انجام دهند.'];
ayat[79]=[2,72,'وَإِذْ قَتَلْتُمْ نَفْسًا فَادَّارَأْتُمْ فِيهَا ۖ وَاللَّهُ مُخْرِجٌ مَّا كُنتُمْ تَكْتُمُونَ','و (به یاد آورید) هنگامی را که فردی را به قتل رساندید؛ سپس درباره (قاتل) او به نزاع پرداختید؛ و خداوند آنچه را مخفی می‌داشتید، آشکار می‌سازد.'];
ayat[80]=[2,73,'فَقُلْنَا اضْرِبُوهُ بِبَعْضِهَا ۚ كَذَٰلِكَ يُحْيِي اللَّهُ الْمَوْتَىٰ وَيُرِيكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ','سپس گفتیم: «قسمتی از گاو را به مقتول بزنید! (تا زنده شود، و قاتل را معرفی کند.) خداوند این‌گونه مردگان را زنده می‌کند؛ و آیات خود را به شما نشان می‌دهد؛ شاید اندیشه کنید!»'];
ayat[81]=[2,74,'ثُمَّ قَسَتْ قُلُوبُكُم مِّن بَعْدِ ذَٰلِكَ فَهِيَ كَالْحِجَارَةِ أَوْ أَشَدُّ قَسْوَةً ۚ وَإِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْأَنْهَارُ ۚ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاءُ ۚ وَإِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ','سپس دلهای شما بعد از این واقعه سخت شد؛ همچون سنگ، یا سخت‌تر! چرا که پاره‌ای از سنگها می‌شکافد، و از آن نهرها جاری می‌شود؛ و پاره‌ای از آنها شکاف برمی‌دارد، و آب از آن تراوش می‌کند؛ و پاره‌ای از خوف خدا (از فراز کوه) به زیر می‌افتد؛ (اما دلهای شما، نه از خوف خدا می‌تپد، و نه سرچشمه علم و دانش و عواطف انسانی است!) و خداوند از اعمال شما غافل نیست.'];
ayat[82]=[2,75,'۞ أَفَتَطْمَعُونَ أَن يُؤْمِنُوا لَكُمْ وَقَدْ كَانَ فَرِيقٌ مِّنْهُمْ يَسْمَعُونَ كَلَامَ اللَّهِ ثُمَّ يُحَرِّفُونَهُ مِن بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَ','آیا انتظار دارید به (آئین) شما ایمان بیاورند، با اینکه عده‌ای از آنان، سخنان خدا را می‌شنیدند و پس از فهمیدن، آن را تحریف می‌کردند، در حالی که علم و اطلاع داشتند؟!'];
ayat[83]=[2,76,'وَإِذَا لَقُوا الَّذِينَ آمَنُوا قَالُوا آمَنَّا وَإِذَا خَلَا بَعْضُهُمْ إِلَىٰ بَعْضٍ قَالُوا أَتُحَدِّثُونَهُم بِمَا فَتَحَ اللَّهُ عَلَيْكُمْ لِيُحَاجُّوكُم بِهِ عِندَ رَبِّكُمْ ۚ أَفَلَا تَعْقِلُونَ','و هنگامی که مؤمنان را ملاقات کنند، می‌گویند: «ایمان آورده‌ایم.» ولی هنگامی که با یکدیگر خلوت می‌کنند، (بعضی به بعضی دیگر اعتراض کرده،) می‌گویند: «چرا مطالبی را که خداوند (در باره صفات پیامبر اسلام) برای شما بیان کرد، به مسلمانان بازگو می‌کنید تا (روز رستاخیز) در پیشگاه خدا، بر ضد شما به آن استدلال کنند؟! آیا نمی‌فهمید؟!»'];
ayat[84]=[2,77,'أَوَلَا يَعْلَمُونَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ','آیا اینها نمی‌دانند خداوند آنچه را پنهان می‌دارند یا آشکار می‌کنند می‌داند؟!'];
ayat[85]=[2,78,'وَمِنْهُمْ أُمِّيُّونَ لَا يَعْلَمُونَ الْكِتَابَ إِلَّا أَمَانِيَّ وَإِنْ هُمْ إِلَّا يَظُنُّونَ','و پاره‌ای از آنان عوامانی هستند که کتاب خدا را جز یک مشت خیالات و آرزوها نمی‌دانند؛ و تنها به پندارهایشان دل بسته‌اند.'];
ayat[86]=[2,79,'فَوَيْلٌ لِّلَّذِينَ يَكْتُبُونَ الْكِتَابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هَٰذَا مِنْ عِندِ اللَّهِ لِيَشْتَرُوا بِهِ ثَمَنًا قَلِيلًا ۖ فَوَيْلٌ لَّهُم مِّمَّا كَتَبَتْ أَيْدِيهِمْ وَوَيْلٌ لَّهُم مِّمَّا يَكْسِبُونَ','پس وای بر آنها که نوشته‌ای با دست خود می‌نویسند، سپس می‌گویند: «این، از طرف خداست.» تا آن را به بهای کمی بفروشند. پس وای بر آنها از آنچه با دست خود نوشتند؛ و وای بر آنان از آنچه از این راه به دست می‌آورند!'];
ayat[87]=[2,80,'وَقَالُوا لَن تَمَسَّنَا النَّارُ إِلَّا أَيَّامًا مَّعْدُودَةً ۚ قُلْ أَتَّخَذْتُمْ عِندَ اللَّهِ عَهْدًا فَلَن يُخْلِفَ اللَّهُ عَهْدَهُ ۖ أَمْ تَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ','و گفتند: «هرگز آتش دوزخ، جز چند روزی، به ما نخواهد رسید.» بگو: «آیا پیمانی از خدا گرفته‌اید؟! -و خداوند هرگز از پیمانش تخلف نمی‌ورزد- یا چیزی را که نمی‌دانید به خدا نسبت می‌دهید»؟!'];
ayat[88]=[2,81,'بَلَىٰ مَن كَسَبَ سَيِّئَةً وَأَحَاطَتْ بِهِ خَطِيئَتُهُ فَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','آری، کسانی که کسب گناه کنند، و آثار گناه، سراسر وجودشان را بپوشاند، آنها اهل آتشند؛ و جاودانه در آن خواهند بود.'];
ayat[89]=[2,82,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ','و آنها که ایمان آورده، و کارهای شایسته انجام داده‌اند، آنان اهل بهشتند؛ و همیشه در آن خواهند ماند.'];
ayat[90]=[2,83,'وَإِذْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ لَا تَعْبُدُونَ إِلَّا اللَّهَ وَبِالْوَالِدَيْنِ إِحْسَانًا وَذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَقُولُوا لِلنَّاسِ حُسْنًا وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ثُمَّ تَوَلَّيْتُمْ إِلَّا قَلِيلًا مِّنكُمْ وَأَنتُم مُّعْرِضُونَ','و (به یاد آورید) زمانی را که از بنی اسرائیل پیمان گرفتیم که جز خداوند یگانه را پرستش نکنید؛ و به پدر و مادر و نزدیکان و یتیمان و بینوایان نیکی کنید؛ و به مردم نیک بگویید؛ نماز را برپا دارید؛ و زکات بدهید. سپس (با اینکه پیمان بسته بودید) همه شما -جز عده کمی- سرپیچی کردید؛ و (از وفای به پیمان خود) روی‌گردان شدید.'];
ayat[91]=[2,84,'وَإِذْ أَخَذْنَا مِيثَاقَكُمْ لَا تَسْفِكُونَ دِمَاءَكُمْ وَلَا تُخْرِجُونَ أَنفُسَكُم مِّن دِيَارِكُمْ ثُمَّ أَقْرَرْتُمْ وَأَنتُمْ تَشْهَدُونَ','و هنگامی را که از شما پیمان گرفتیم که خون هم را نریزید؛ و یکدیگر را از سرزمین خود، بیرون نکنید. سپس شما اقرار کردید؛ (و بر این پیمان) گواه بودید.'];
ayat[92]=[2,85,'ثُمَّ أَنتُمْ هَٰؤُلَاءِ تَقْتُلُونَ أَنفُسَكُمْ وَتُخْرِجُونَ فَرِيقًا مِّنكُم مِّن دِيَارِهِمْ تَظَاهَرُونَ عَلَيْهِم بِالْإِثْمِ وَالْعُدْوَانِ وَإِن يَأْتُوكُمْ أُسَارَىٰ تُفَادُوهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرَاجُهُمْ ۚ أَفَتُؤْمِنُونَ بِبَعْضِ الْكِتَابِ وَتَكْفُرُونَ بِبَعْضٍ ۚ فَمَا جَزَاءُ مَن يَفْعَلُ ذَٰلِكَ مِنكُمْ إِلَّا خِزْيٌ فِي الْحَيَاةِ الدُّنْيَا ۖ وَيَوْمَ الْقِيَامَةِ يُرَدُّونَ إِلَىٰ أَشَدِّ الْعَذَابِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ','اما این شما هستید که یکدیگر را می‌کشید و جمعی از خودتان را از سرزمینشان بیرون می‌کنید؛ و در این گناه و تجاوز، به یکدیگر کمک می‌نمایید؛ (و اینها همه نقض پیمانی است که با خدا بسته‌اید) در حالی که اگر بعضی از آنها به صورت اسیران نزد شما آیند، فدیه می‌دهید و آنان را آزاد می‌سازید! با اینکه بیرون ساختن آنان بر شما حرام بود. آیا به بعضی از دستورات کتاب آسمانی ایمان می‌آورید، و به بعضی کافر می‌شوید؟! برای کسی از شما که این عمل (تبعیض در میان احکام و قوانین الهی) را انجام دهد، جز رسوایی در این جهان، چیزی نخواهد بود، و روز رستاخیز به شدیدترین عذابها گرفتار می‌شوند. و خداوند از آنچه انجام می‌دهید غافل نیست.'];
ayat[93]=[2,86,'أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الْحَيَاةَ الدُّنْيَا بِالْآخِرَةِ ۖ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنصَرُونَ','اینها همان کسانند که آخرت را به زندگی دنیا فروخته‌اند؛ از این رو عذاب آنها تخفیف داده نمی‌شود؛ و کسی آنها را یاری نخواهد کرد.'];
ayat[94]=[2,87,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَقَفَّيْنَا مِن بَعْدِهِ بِالرُّسُلِ ۖ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ ۗ أَفَكُلَّمَا جَاءَكُمْ رَسُولٌ بِمَا لَا تَهْوَىٰ أَنفُسُكُمُ اسْتَكْبَرْتُمْ فَفَرِيقًا كَذَّبْتُمْ وَفَرِيقًا تَقْتُلُونَ','ما به موسی کتاب (تورات) دادیم؛ و بعد از او، پیامبرانی پشت سر هم فرستادیم؛ و به عیسی بن مریم دلایل روشن دادیم؛ و او را به وسیله روح القدس تأیید کردیم. آیا چنین نیست که هر زمان، پیامبری چیزی بر خلاف هوای نفس شما آورد، در برابر او تکبر کردید (و از ایمان آوردن به او خودداری نمودید)؛ پس عده‌ای را تکذیب کرده، و جمعی را به قتل رساندید؟!'];
ayat[95]=[2,88,'وَقَالُوا قُلُوبُنَا غُلْفٌ ۚ بَل لَّعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَقَلِيلًا مَّا يُؤْمِنُونَ','و (آنها از روی استهزا) گفتند: دلهای ما در غلاف است! (و ما از گفته تو چیزی نمی‌فهمیم. آری، همین طور است!) خداوند آنها را به خاطر کفرشان، از رحمت خود دور ساخته، (به همین دلیل، چیزی درک نمی‌کنند؛) و کمتر ایمان می‌آورند.'];
ayat[96]=[2,89,'وَلَمَّا جَاءَهُمْ كِتَابٌ مِّنْ عِندِ اللَّهِ مُصَدِّقٌ لِّمَا مَعَهُمْ وَكَانُوا مِن قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمَّا جَاءَهُم مَّا عَرَفُوا كَفَرُوا بِهِ ۚ فَلَعْنَةُ اللَّهِ عَلَى الْكَافِرِينَ','و هنگامی که از طرف خداوند، کتابی برای آنها آمد که موافق نشانه‌هایی بود که با خود داشتند، و پیش از این، به خود نوید پیروزی بر کافران می‌دادند (که با کمک آن، بر دشمنان پیروز گردند.) با این همه، هنگامی که این کتاب، و پیامبری را که از قبل شناخته بودند نزد آنها آمد، به او کافر شدند؛ لعنت خدا بر کافران باد!'];
ayat[97]=[2,90,'بِئْسَمَا اشْتَرَوْا بِهِ أَنفُسَهُمْ أَن يَكْفُرُوا بِمَا أَنزَلَ اللَّهُ بَغْيًا أَن يُنَزِّلَ اللَّهُ مِن فَضْلِهِ عَلَىٰ مَن يَشَاءُ مِنْ عِبَادِهِ ۖ فَبَاءُوا بِغَضَبٍ عَلَىٰ غَضَبٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ مُّهِينٌ','ولی آنها در مقابل بهای بدی، خود را فروختند؛ که به ناروا، به آیاتی که خدا فرستاده بود، کافر شدند. و معترض بودند، چرا خداوند به فضل خویش، بر هر کس از بندگانش بخواهد، آیات خود را نازل می‌کند؟! از این رو به خشمی بعد از خشمی (از سوی خدا) گرفتار شدند. و برای کافران مجازاتی خوارکننده است.'];
ayat[98]=[2,91,'وَإِذَا قِيلَ لَهُمْ آمِنُوا بِمَا أَنزَلَ اللَّهُ قَالُوا نُؤْمِنُ بِمَا أُنزِلَ عَلَيْنَا وَيَكْفُرُونَ بِمَا وَرَاءَهُ وَهُوَ الْحَقُّ مُصَدِّقًا لِّمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُونَ أَنبِيَاءَ اللَّهِ مِن قَبْلُ إِن كُنتُم مُّؤْمِنِينَ','و هنگامی که به آنها گفته شود: «به آنچه خداوند نازل فرموده، ایمان بیاورید!» می‌گویند: «ما به چیزی ایمان می‌آوریم که بر خود ما نازل شده است.» و به غیر آن، کافر می‌شوند؛ در حالی که حق است؛ و آیاتی را که بر آنها نازل شده، تصدیق می‌کند. بگو: «اگر (راست می‌گویید، و به آیاتی که بر خودتان نازل شده) ایمان دارید، پس چرا پیامبران خدا را پیش از این، به قتل می‌رساندید؟!»'];
ayat[99]=[2,92,'۞ وَلَقَدْ جَاءَكُم مُّوسَىٰ بِالْبَيِّنَاتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِن بَعْدِهِ وَأَنتُمْ ظَالِمُونَ','و (نیز) موسی آن همه معجزات را برای شما آورد، و شما پس از (غیبت) او، گوساله را انتخاب کردید؛ در حالی که ستمگر بودید.'];
ayat[100]=[2,93,'وَإِذْ أَخَذْنَا مِيثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّورَ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاسْمَعُوا ۖ قَالُوا سَمِعْنَا وَعَصَيْنَا وَأُشْرِبُوا فِي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْ ۚ قُلْ بِئْسَمَا يَأْمُرُكُم بِهِ إِيمَانُكُمْ إِن كُنتُم مُّؤْمِنِينَ','و (به یاد آورید) زمانی را که از شما پیمان گرفتیم؛ و کوه طور را بالای سر شما برافراشتیم؛ (و گفتیم:) «این دستوراتی را که به شما داده‌ایم محکم بگیرید، و درست بشنوید!» آنها گفتند: «شنیدیم؛ ولی مخالفت کردیم.» و دلهای آنها، بر اثر کفرشان، با محبت گوساله آمیخته شد. بگو: «ایمان شما، چه فرمان بدی به شما می‌دهد، اگر ایمان دارید!»'];
ayat[101]=[2,94,'قُلْ إِن كَانَتْ لَكُمُ الدَّارُ الْآخِرَةُ عِندَ اللَّهِ خَالِصَةً مِّن دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِن كُنتُمْ صَادِقِينَ','بگو: «اگر آن (چنان که مدعی هستید) سرای دیگر در نزد خدا، مخصوص شماست نه سایر مردم، پس آرزوی مرگ کنید اگر راست می‌گویید!»'];
ayat[102]=[2,95,'وَلَن يَتَمَنَّوْهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ','ولی آنها، به خاطر اعمال بدی که پیش از خود فرستاده‌اند، هرگز آرزوی مرگ نخواهند کرد؛ و خداوند از ستمگران آگاه است.'];
ayat[103]=[2,96,'وَلَتَجِدَنَّهُمْ أَحْرَصَ النَّاسِ عَلَىٰ حَيَاةٍ وَمِنَ الَّذِينَ أَشْرَكُوا ۚ يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْفَ سَنَةٍ وَمَا هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذَابِ أَن يُعَمَّرَ ۗ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ','و آنها را حریص‌ترین مردم -حتی حریصتر از مشرکان- بر زندگی (این دنیا، و اندوختن ثروت) خواهی یافت؛ (تا آنجا) که هر یک از آنها آرزو دارد هزار سال عمر به او داده شود! در حالی که این عمر طولانی، او را از کیفر (الهی) باز نخواهد داشت. و خداوند به اعمال آنها بیناست.'];
ayat[104]=[2,97,'قُلْ مَن كَانَ عَدُوًّا لِّجِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلَىٰ قَلْبِكَ بِإِذْنِ اللَّهِ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَهُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ','(آنها می‌گویند: «چون فرشته‌ای که وحی را بر تو نازل می‌کند، جبرئیل است، و ما با جبرئیل دشمن هستیم، به تو ایمان نمی‌آوریم!») بگو: «کسی که دشمن جبرئیل باشد (در حقیقت دشمن خداست) چرا که او به فرمان خدا، قرآن را بر قلب تو نازل کرده است؛ در حالی که کتب آسمانی پیشین را تصدیق می‌کند؛ و هدایت و بشارت است برای مؤمنان.»'];
ayat[105]=[2,98,'مَن كَانَ عَدُوًّا لِّلَّهِ وَمَلَائِكَتِهِ وَرُسُلِهِ وَجِبْرِيلَ وَمِيكَالَ فَإِنَّ اللَّهَ عَدُوٌّ لِّلْكَافِرِينَ','کسی که دشمن خدا و فرشتگان و رسولان او و جبرئیل و میکائیل باشد (کافر است؛ و) خداوند دشمن کافران است.'];
ayat[106]=[2,99,'وَلَقَدْ أَنزَلْنَا إِلَيْكَ آيَاتٍ بَيِّنَاتٍ ۖ وَمَا يَكْفُرُ بِهَا إِلَّا الْفَاسِقُونَ','ما نشانه‌های روشنی برای تو فرستادیم؛ و جز فاسقان کسی به آنها کفر نمی‌ورزد.'];
ayat[107]=[2,100,'أَوَكُلَّمَا عَاهَدُوا عَهْدًا نَّبَذَهُ فَرِيقٌ مِّنْهُم ۚ بَلْ أَكْثَرُهُمْ لَا يُؤْمِنُونَ','و آیا چنین نیست که هر بار آنها [=یهود] پیمانی (با خدا و پیامبر) بستند، جمعی آن را دور افکندند (و مخالفت کردند.) آری، بیشتر آنان ایمان نمی‌آورند.'];
ayat[108]=[2,101,'وَلَمَّا جَاءَهُمْ رَسُولٌ مِّنْ عِندِ اللَّهِ مُصَدِّقٌ لِّمَا مَعَهُمْ نَبَذَ فَرِيقٌ مِّنَ الَّذِينَ أُوتُوا الْكِتَابَ كِتَابَ اللَّهِ وَرَاءَ ظُهُورِهِمْ كَأَنَّهُمْ لَا يَعْلَمُونَ','و هنگامی که فرستاده‌ای از سوی خدا به سراغشان آمد، و با نشانه‌هایی که نزد آنها بود مطابقت داشت، جمعی از آنان که به آنها کتاب (آسمانی) داده شده بود، کتاب خدا را پشت سر افکندند؛ گویی هیچ از آن خبر ندارند!!'];
ayat[109]=[2,102,'وَاتَّبَعُوا مَا تَتْلُو الشَّيَاطِينُ عَلَىٰ مُلْكِ سُلَيْمَانَ ۖ وَمَا كَفَرَ سُلَيْمَانُ وَلَٰكِنَّ الشَّيَاطِينَ كَفَرُوا يُعَلِّمُونَ النَّاسَ السِّحْرَ وَمَا أُنزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوتَ وَمَارُوتَ ۚ وَمَا يُعَلِّمَانِ مِنْ أَحَدٍ حَتَّىٰ يَقُولَا إِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْ ۖ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَزَوْجِهِ ۚ وَمَا هُم بِضَارِّينَ بِهِ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اللَّهِ ۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنفَعُهُمْ ۚ وَلَقَدْ عَلِمُوا لَمَنِ اشْتَرَاهُ مَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ ۚ وَلَبِئْسَ مَا شَرَوْا بِهِ أَنفُسَهُمْ ۚ لَوْ كَانُوا يَعْلَمُونَ','و (یهود) از آنچه شیاطین در عصر سلیمان بر مردم می‌خواندند پیروی کردند. سلیمان هرگز (دست به سحر نیالود؛ و) کافر نشد؛ ولی شیاطین کفر ورزیدند؛ و به مردم سحر آموختند. و (نیز یهود) از آنچه بر دو فرشته بابل «هاروت» و «ماروت»، نازل شد پیروی کردند. (آن دو، راه سحر کردن را، برای آشنایی با طرز ابطال آن، به مردم یاد می‌دادند. و) به هیچ کس چیزی یاد نمی‌دادند، مگر اینکه از پیش به او می‌گفتند: «ما وسیله آزمایشیم کافر نشو! (و از این تعلیمات، سوء استفاده نکن!)» ولی آنها از آن دو فرشته، مطالبی را می‌آموختند که بتوانند به وسیله آن، میان مرد و همسرش جدایی بیفکنند؛ ولی هیچ گاه نمی‌توانند بدون اجازه خداوند، به انسانی زیان برسانند. آنها قسمتهایی را فرامی‌گرفتند که به آنان زیان می‌رسانید و نفعی نمی‌داد. و مسلما می‌دانستند هر کسی خریدار این گونه متاع باشد، در آخرت بهره‌ای نخواهد داشت. و چه زشت و ناپسند بود آنچه خود را به آن فروختند، اگر می‌دانستند!!'];
ayat[110]=[2,103,'وَلَوْ أَنَّهُمْ آمَنُوا وَاتَّقَوْا لَمَثُوبَةٌ مِّنْ عِندِ اللَّهِ خَيْرٌ ۖ لَّوْ كَانُوا يَعْلَمُونَ','و اگر آنها ایمان می‌آوردند و پرهیزکاری پیشه می‌کردند، پاداشی که نزد خداست، برای آنان بهتر بود، اگر آگاهی داشتند!!'];
ayat[111]=[2,104,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقُولُوا رَاعِنَا وَقُولُوا انظُرْنَا وَاسْمَعُوا ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ','ای افراد باایمان! (هنگامی که از پیغمبر تقاضای مهلت برای درک آیات قرآن می‌کنید) نگویید: «راعنا»؛ بلکه بگویید: «انظرنا». (زیرا کلمه اول، هم به معنی «ما را مهلت بده!»، و هم به معنی «ما را تحمیق کن!» می‌باشد؛ و دستاویزی برای دشمنان است.) و (آنچه به شما دستور داده می‌شود) بشنوید! و برای کافران (و استهزاکنندگان) عذاب دردناکی است.'];
ayat[112]=[2,105,'مَّا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَلَا الْمُشْرِكِينَ أَن يُنَزَّلَ عَلَيْكُم مِّنْ خَيْرٍ مِّن رَّبِّكُمْ ۗ وَاللَّهُ يَخْتَصُّ بِرَحْمَتِهِ مَن يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ','کافران اهل کتاب، و (همچنین) مشرکان، دوست ندارند که از سوی خداوند، خیر و برکتی بر شما نازل گردد؛ در حالی که خداوند، رحمت خود را به هر کس بخواهد، اختصاص می‌دهد؛ و خداوند، صاحب فضل بزرگ است.'];
ayat[113]=[2,106,'۞ مَا نَنسَخْ مِنْ آيَةٍ أَوْ نُنسِهَا نَأْتِ بِخَيْرٍ مِّنْهَا أَوْ مِثْلِهَا ۗ أَلَمْ تَعْلَمْ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','هر حکمی را نسخ کنیم، و یا نسخ آن را به تأخیر اندازیم، بهتر از آن، یا همانند آن را می‌آوریم. آیا نمی‌دانستی که خداوند بر هر چیز توانا است؟!'];
ayat[114]=[2,107,'أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَمَا لَكُم مِّن دُونِ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ','آیا نمی‌دانستی که حکومت آسمانها و زمین، از آن خداست؟! (و حق دارد هر گونه تغییر و تبدیلی در احکام خود طبق مصالح بدهد؟!) و جز خدا، ولی و یاوری برای شما نیست. (و اوست که مصلحت شما را می‌داند و تعیین می‌کند).'];
ayat[115]=[2,108,'أَمْ تُرِيدُونَ أَن تَسْأَلُوا رَسُولَكُمْ كَمَا سُئِلَ مُوسَىٰ مِن قَبْلُ ۗ وَمَن يَتَبَدَّلِ الْكُفْرَ بِالْإِيمَانِ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ','آیا می‌خواهید از پیامبر خود، همان تقاضای (نامعقولی را) بکنید که پیش از این، از موسی کردند؟! (و با این بهانه‌جویی‌ها، از ایمان آوردن سر باز زدند.) کسی که کفر را به جای ایمان بپذیرد، از راه مستقیم (عقل و فطرت) گمراه شده است.'];
ayat[116]=[2,109,'وَدَّ كَثِيرٌ مِّنْ أَهْلِ الْكِتَابِ لَوْ يَرُدُّونَكُم مِّن بَعْدِ إِيمَانِكُمْ كُفَّارًا حَسَدًا مِّنْ عِندِ أَنفُسِهِم مِّن بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۖ فَاعْفُوا وَاصْفَحُوا حَتَّىٰ يَأْتِيَ اللَّهُ بِأَمْرِهِ ۗ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','بسیاری از اهل کتاب، از روی حسد -که در وجود آنها ریشه دوانده- آرزو می‌کردند شما را بعد از اسلام و ایمان، به حال کفر باز گردانند؛ با اینکه حق برای آنها کاملا روشن شده است. شما آنها را عفو کنید و گذشت نمایید؛ تا خداوند فرمان خودش (فرمان جهاد) را بفرستد؛ خداوند بر هر چیزی تواناست.'];
ayat[117]=[2,110,'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ ۚ وَمَا تُقَدِّمُوا لِأَنفُسِكُم مِّنْ خَيْرٍ تَجِدُوهُ عِندَ اللَّهِ ۗ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ','و نماز را برپا دارید و زکات را ادا کنید؛ و هر کار خیری را برای خود از پیش می‌فرستید، آن را نزد خدا (در سرای دیگر) خواهید یافت؛ خداوند به اعمال شما بیناست.'];
ayat[118]=[2,111,'وَقَالُوا لَن يَدْخُلَ الْجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوْ نَصَارَىٰ ۗ تِلْكَ أَمَانِيُّهُمْ ۗ قُلْ هَاتُوا بُرْهَانَكُمْ إِن كُنتُمْ صَادِقِينَ','آنها گفتند: «هیچ کس، جز یهود یا نصاری، هرگز داخل بهشت نخواهد شد.» این آرزوی آنهاست! بگو: «اگر راست می‌گویید، دلیل خود را (بر این موضوع) بیاورید!»'];
ayat[119]=[2,112,'بَلَىٰ مَنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ فَلَهُ أَجْرُهُ عِندَ رَبِّهِ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','آری، کسی که روی خود را تسلیم خدا کند و نیکوکار باشد، پاداش او نزد پروردگارش ثابت است؛ نه ترسی بر آنهاست و نه غمگین می‌شوند. (بنابر این، بهشت خدا در انحصار هیچ گروهی نیست.)'];
ayat[120]=[2,113,'وَقَالَتِ الْيَهُودُ لَيْسَتِ النَّصَارَىٰ عَلَىٰ شَيْءٍ وَقَالَتِ النَّصَارَىٰ لَيْسَتِ الْيَهُودُ عَلَىٰ شَيْءٍ وَهُمْ يَتْلُونَ الْكِتَابَ ۗ كَذَٰلِكَ قَالَ الَّذِينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْ ۚ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ','یهودیان گفتند: «مسیحیان هیچ موقعیتی (نزد خدا) ندارند»، و مسیحیان نیز گفتند: «یهودیان هیچ موقعیتی ندارند (و بر باطلند)»؛ در حالی که هر دو دسته، کتاب آسمانی را می‌خوانند (و باید از این گونه تعصبها برکنار باشند) افراد نادان (دیگر، همچون مشرکان) نیز، سخنی همانند سخن آنها داشتند! خداوند، روز قیامت، در باره آنچه در آن اختلاف داشتند، داوری می‌کند.'];
ayat[121]=[2,114,'وَمَنْ أَظْلَمُ مِمَّن مَّنَعَ مَسَاجِدَ اللَّهِ أَن يُذْكَرَ فِيهَا اسْمُهُ وَسَعَىٰ فِي خَرَابِهَا ۚ أُولَٰئِكَ مَا كَانَ لَهُمْ أَن يَدْخُلُوهَا إِلَّا خَائِفِينَ ۚ لَهُمْ فِي الدُّنْيَا خِزْيٌ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ','کیست ستمکارتر از آن کس که از بردن نام خدا در مساجد او جلوگیری کرد و سعی در ویرانی آنها نمود؟! شایسته نیست آنان، جز با ترس و وحشت، وارد این (کانونهای عبادت) شوند. بهره آنها در دنیا (فقط) رسوایی است و در سرای دیگر، عذاب عظیم (الهی)!!'];
ayat[122]=[2,115,'وَلِلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ فَأَيْنَمَا تُوَلُّوا فَثَمَّ وَجْهُ اللَّهِ ۚ إِنَّ اللَّهَ وَاسِعٌ عَلِيمٌ','مشرق و مغرب، از آن خداست! و به هر سو رو کنید، خدا آنجاست! خداوند بی‌نیاز و داناست!'];
ayat[123]=[2,116,'وَقَالُوا اتَّخَذَ اللَّهُ وَلَدًا ۗ سُبْحَانَهُ ۖ بَل لَّهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ كُلٌّ لَّهُ قَانِتُونَ','و (یهود و نصاری و مشرکان) گفتند: «خداوند، فرزندی برای خود انتخاب کرده است»! -منزه است او- بلکه آنچه در آسمانها و زمین است، از آن اوست؛ و همه در برابر او خاضعند!'];
ayat[124]=[2,117,'بَدِيعُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُن فَيَكُونُ','هستی بخش آسمانها و زمین اوست! و هنگامی که فرمان وجود چیزی را صادر کند، تنها می‌گوید: «موجود باش!» و آن، فوری موجود می‌شود.'];
ayat[125]=[2,118,'وَقَالَ الَّذِينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اللَّهُ أَوْ تَأْتِينَا آيَةٌ ۗ كَذَٰلِكَ قَالَ الَّذِينَ مِن قَبْلِهِم مِّثْلَ قَوْلِهِمْ ۘ تَشَابَهَتْ قُلُوبُهُمْ ۗ قَدْ بَيَّنَّا الْآيَاتِ لِقَوْمٍ يُوقِنُونَ','افراد ناآگاه گفتند: «چرا خدا با ما سخن نمی‌گوید؟! و یا چرا آیه و نشانه‌ای برای خود ما نمی‌آید؟!» پیشینیان آنها نیز، همین گونه سخن می‌گفتند؛ دلها و افکارشان مشابه یکدیگر است؛ ولی ما (به اندازه کافی) آیات و نشانه‌ها را برای اهل یقین (و حقیقت‌جویان) روشن ساخته‌ایم.'];
ayat[126]=[2,119,'إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا ۖ وَلَا تُسْأَلُ عَنْ أَصْحَابِ الْجَحِيمِ','ما تو را به حق، برای بشارت و بیم دادن (مردم جهان) فرستادیم؛ و تو مسئول (گمراهی) دوزخیان (پس از ابلاغ رسالت) نیستی!'];
ayat[127]=[2,120,'وَلَن تَرْضَىٰ عَنكَ الْيَهُودُ وَلَا النَّصَارَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمْ ۗ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَىٰ ۗ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُم بَعْدَ الَّذِي جَاءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ','هرگز یهود و نصاری از تو راضی نخواهند شد، (تا به طور کامل، تسلیم خواسته‌های آنها شوی، و) از آیین (تحریف یافته) آنان، پیروی کنی. بگو: «هدایت، تنها هدایت الهی است!» و اگر از هوی و هوسهای آنان پیروی کنی، بعد از آنکه آگاه شده‌ای، هیچ سرپرست و یاوری از سوی خدا برای تو نخواهد بود.'];
ayat[128]=[2,121,'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَتْلُونَهُ حَقَّ تِلَاوَتِهِ أُولَٰئِكَ يُؤْمِنُونَ بِهِ ۗ وَمَن يَكْفُرْ بِهِ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ','کسانی که کتاب آسمانی به آنها داده‌ایم [= یهود و نصاری‌] آن را چنان که شایسته آن است می‌خوانند؛ آنها به پیامبر اسلام ایمان می‌آورند؛ و کسانی که به او کافر شوند، زیانکارند.'];
ayat[129]=[2,122,'يَا بَنِي إِسْرَائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَأَنِّي فَضَّلْتُكُمْ عَلَى الْعَالَمِينَ','ای بنی اسرائیل! نعمت مرا، که به شما ارزانی داشتم، به یاد آورید! و (نیز به خاطر آورید) که من شما را بر جهانیان برتری بخشیدم!'];
ayat[130]=[2,123,'وَاتَّقُوا يَوْمًا لَّا تَجْزِي نَفْسٌ عَن نَّفْسٍ شَيْئًا وَلَا يُقْبَلُ مِنْهَا عَدْلٌ وَلَا تَنفَعُهَا شَفَاعَةٌ وَلَا هُمْ يُنصَرُونَ','از روزی بترسید که هیچ کس از دیگری دفاع نمی‌کند؛ و هیچ‌گونه عوضی از او قبول نمی‌شود؛ و شفاعت، او را سود نمی‌دهد؛ و (از هیچ سوئی) یاری نمی‌شوند!'];
ayat[131]=[2,124,'۞ وَإِذِ ابْتَلَىٰ إِبْرَاهِيمَ رَبُّهُ بِكَلِمَاتٍ فَأَتَمَّهُنَّ ۖ قَالَ إِنِّي جَاعِلُكَ لِلنَّاسِ إِمَامًا ۖ قَالَ وَمِن ذُرِّيَّتِي ۖ قَالَ لَا يَنَالُ عَهْدِي الظَّالِمِينَ','(به خاطر آورید) هنگامی که خداوند، ابراهیم را با وسایل گوناگونی آزمود. و او به خوبی از عهده این آزمایشها برآمد. خداوند به او فرمود: «من تو را امام و پیشوای مردم قرار دادم!» ابراهیم عرض کرد: «از دودمان من (نیز امامانی قرار بده!)» خداوند فرمود: «پیمان من، به ستمکاران نمی‌رسد! (و تنها آن دسته از فرزندان تو که پاک و معصوم باشند، شایسته این مقامند)».'];
ayat[132]=[2,125,'وَإِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِّلنَّاسِ وَأَمْنًا وَاتَّخِذُوا مِن مَّقَامِ إِبْرَاهِيمَ مُصَلًّى ۖ وَعَهِدْنَا إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ أَن طَهِّرَا بَيْتِيَ لِلطَّائِفِينَ وَالْعَاكِفِينَ وَالرُّكَّعِ السُّجُودِ','و (به خاطر بیاورید) هنگامی که خانه کعبه را محل بازگشت و مرکز امن و امان برای مردم قرار دادیم! و (برای تجدید خاطره،) از مقام ابراهیم، عبادتگاهی برای خود انتخاب کنید! و ما به ابراهیم و اسماعیل امر کردیم که: «خانه مرا برای طواف‌کنندگان و مجاوران و رکوع‌کنندگان و سجده‌کنندگان، پاک و پاکیزه کنید!»'];
ayat[133]=[2,126,'وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَٰذَا بَلَدًا آمِنًا وَارْزُقْ أَهْلَهُ مِنَ الثَّمَرَاتِ مَنْ آمَنَ مِنْهُم بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُ قَلِيلًا ثُمَّ أَضْطَرُّهُ إِلَىٰ عَذَابِ النَّارِ ۖ وَبِئْسَ الْمَصِيرُ','و (به یاد آورید) هنگامی را که ابراهیم عرض کرد: «پروردگارا! این سرزمین را شهر امنی قرار ده! و اهل آن را -آنها که به خدا و روز بازپسین، ایمان آورده‌اند- از ثمرات (گوناگون)، روزی ده!» گفت: «(ما دعای تو را اجابت کردیم؛ و مؤمنان را از انواع برکات، بهره‌مند ساختیم؛) اما به آنها که کافر شدند، بهره کمی خواهیم داد؛ سپس آنها را به عذاب آتش می‌کشانیم؛ و چه بد سرانجامی دارند»'];
ayat[134]=[2,127,'وَإِذْ يَرْفَعُ إِبْرَاهِيمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَإِسْمَاعِيلُ رَبَّنَا تَقَبَّلْ مِنَّا ۖ إِنَّكَ أَنتَ السَّمِيعُ الْعَلِيمُ','و (نیز به یاد آورید) هنگامی را که ابراهیم و اسماعیل، پایه‌های خانه (کعبه) را بالا می‌بردند، (و می‌گفتند:) «پروردگارا! از ما بپذیر، که تو شنوا و دانایی!'];
ayat[135]=[2,128,'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةً مُّسْلِمَةً لَّكَ وَأَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا ۖ إِنَّكَ أَنتَ التَّوَّابُ الرَّحِيمُ','پروردگارا! ما را تسلیم فرمان خود قرار ده! و از دودمان ما، امتی که تسلیم فرمانت باشند، به وجود آور! و طرز عبادتمان را به ما نشان ده و توبه ما را بپذیر، که تو توبه‌پذیر و مهربانی!'];
ayat[136]=[2,129,'رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولًا مِّنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِكَ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُزَكِّيهِمْ ۚ إِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ','پروردگارا! در میان آنها پیامبری از خودشان برانگیز، تا آیات تو را بر آنان بخواند، و آنها را کتاب و حکمت بیاموزد، و پاکیزه کند؛ زیرا تو توانا و حکیمی (و بر این کار، قادری)!»'];
ayat[137]=[2,130,'وَمَن يَرْغَبُ عَن مِّلَّةِ إِبْرَاهِيمَ إِلَّا مَن سَفِهَ نَفْسَهُ ۚ وَلَقَدِ اصْطَفَيْنَاهُ فِي الدُّنْيَا ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ','جز افراد سفیه و نادان، چه کسی از آیین ابراهیم، (با آن پاکی و درخشندگی،) روی‌گردان خواهد شد؟! ما او را در این جهان برگزیدیم؛ و او در جهان دیگر، از صالحان است.'];
ayat[138]=[2,131,'إِذْ قَالَ لَهُ رَبُّهُ أَسْلِمْ ۖ قَالَ أَسْلَمْتُ لِرَبِّ الْعَالَمِينَ','در آن هنگام که پروردگارش به او گفت: اسلام بیاور! (و در برابر حق، تسلیم باش! او فرمان پروردگار را، از جان و دل پذیرفت؛ و) گفت: «در برابر پروردگار جهانیان، تسلیم شدم.»'];
ayat[139]=[2,132,'وَوَصَّىٰ بِهَا إِبْرَاهِيمُ بَنِيهِ وَيَعْقُوبُ يَا بَنِيَّ إِنَّ اللَّهَ اصْطَفَىٰ لَكُمُ الدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسْلِمُونَ','و ابراهیم و یعقوب (در واپسین لحظات عمر،) فرزندان خود را به این آیین، وصیت کردند؛ (و هر کدام به فرزندان خویش گفتند:) «فرزندان من! خداوند این آیین پاک را برای شما برگزیده است؛ و شما، جز به آیین اسلام [= تسلیم در برابر فرمان خدا] از دنیا نروید!»'];
ayat[140]=[2,133,'أَمْ كُنتُمْ شُهَدَاءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قَالَ لِبَنِيهِ مَا تَعْبُدُونَ مِن بَعْدِي قَالُوا نَعْبُدُ إِلَٰهَكَ وَإِلَٰهَ آبَائِكَ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ إِلَٰهًا وَاحِدًا وَنَحْنُ لَهُ مُسْلِمُونَ','آیا هنگامی که مرگ یعقوب فرا رسید، شما حاضر بودید؟! در آن هنگام که به فرزندان خود گفت: «پس از من، چه چیز را می‌پرستید؟» گفتند: «خدای تو، و خدای پدرانت، ابراهیم و اسماعیل و اسحاق، خداوند یکتا را، و ما در برابر او تسلیم هستیم.»'];
ayat[141]=[2,134,'تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُم مَّا كَسَبْتُمْ ۖ وَلَا تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ','آنها امتی بودند که درگذشتند. اعمال آنان، مربوط به خودشان بود و اعمال شما نیز مربوط به خود شماست؛ و شما هیچ‌گاه مسئول اعمال آنها نخواهید بود.'];
ayat[142]=[2,135,'وَقَالُوا كُونُوا هُودًا أَوْ نَصَارَىٰ تَهْتَدُوا ۗ قُلْ بَلْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۖ وَمَا كَانَ مِنَ الْمُشْرِكِينَ','(اهل کتاب) گفتند: «یهودی یا مسیحی شوید، تا هدایت یابید!» بگو: «(این آیینهای تحریف شده، هرگز نمی‌تواند موجب هدایت گردد،) بلکه از آیین خالص ابراهیم پیروی کنید! و او هرگز از مشرکان نبود!»'];
ayat[143]=[2,136,'قُولُوا آمَنَّا بِاللَّهِ وَمَا أُنزِلَ إِلَيْنَا وَمَا أُنزِلَ إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَا أُوتِيَ النَّبِيُّونَ مِن رَّبِّهِمْ لَا نُفَرِّقُ بَيْنَ أَحَدٍ مِّنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ','بگویید: «ما به خدا ایمان آورده‌ایم؛ و به آنچه بر ما نازل شده؛ و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و پیامبران از فرزندان او نازل گردید، و (همچنین) آنچه به موسی و عیسی و پیامبران (دیگر) از طرف پروردگار داده شده است، و در میان هیچ یک از آنها جدایی قائل نمی‌شویم، و در برابر فرمان خدا تسلیم هستیم؛ (و تعصبات نژادی و اغراض شخصی، سبب نمی‌شود که بعضی را بپذیریم و بعضی را رها کنیم.)»'];
ayat[144]=[2,137,'فَإِنْ آمَنُوا بِمِثْلِ مَا آمَنتُم بِهِ فَقَدِ اهْتَدَوا ۖ وَّإِن تَوَلَّوْا فَإِنَّمَا هُمْ فِي شِقَاقٍ ۖ فَسَيَكْفِيكَهُمُ اللَّهُ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ','اگر آنها نیز به مانند آنچه شما ایمان آورده‌اید ایمان بیاورند، هدایت یافته‌اند؛ و اگر سرپیچی کنند، از حق جدا شده‌اند و خداوند، شر آنها را از تو دفع می‌کند؛ و او شنونده و داناست.'];
ayat[145]=[2,138,'صِبْغَةَ اللَّهِ ۖ وَمَنْ أَحْسَنُ مِنَ اللَّهِ صِبْغَةً ۖ وَنَحْنُ لَهُ عَابِدُونَ','رنگ خدایی (بپذیرید! رنگ ایمان و توحید و اسلام؛) و چه رنگی از رنگ خدایی بهتر است؟! و ما تنها او را عبادت می‌کنیم.'];
ayat[146]=[2,139,'قُلْ أَتُحَاجُّونَنَا فِي اللَّهِ وَهُوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ وَنَحْنُ لَهُ مُخْلِصُونَ','بگو: «آیا در باره خداوند با ما محاجه می‌کنید؟! در حالی که او، پروردگار ما و شماست؛ و اعمال ما از آن ما، و اعمال شما از آن شماست؛ و ما او را با اخلاص پرستش می‌کنیم، (و موحد خالصیم).»'];
ayat[147]=[2,140,'أَمْ تَقُولُونَ إِنَّ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطَ كَانُوا هُودًا أَوْ نَصَارَىٰ ۗ قُلْ أَأَنتُمْ أَعْلَمُ أَمِ اللَّهُ ۗ وَمَنْ أَظْلَمُ مِمَّن كَتَمَ شَهَادَةً عِندَهُ مِنَ اللَّهِ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ','یا می‌گویید: «ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط، یهودی یا نصرانی بودند»؟! بگو: «شما بهتر می‌دانید یا خدا؟! (و با اینکه می‌دانید آنها یهودی یا نصرانی نبودند، چرا حقیقت را کتمان می‌کنید؟)» و چه کسی ستمکارتر است از آن کس که گواهی و شهادت الهی را که نزد اوست، کتمان می‌کند؟! و خدا از اعمال شما غافل نیست.'];
ayat[148]=[2,141,'تِلْكَ أُمَّةٌ قَدْ خَلَتْ ۖ لَهَا مَا كَسَبَتْ وَلَكُم مَّا كَسَبْتُمْ ۖ وَلَا تُسْأَلُونَ عَمَّا كَانُوا يَعْمَلُونَ','(به هر حال) آنها امتی بودند که درگذشتند. آنچه کردند، برای خودشان است؛ و آنچه هم شما کرده‌اید، برای خودتان است؛ و شما مسئول اعمال آنها نیستید.'];
ayat[149]=[2,142,'۞ سَيَقُولُ السُّفَهَاءُ مِنَ النَّاسِ مَا وَلَّاهُمْ عَن قِبْلَتِهِمُ الَّتِي كَانُوا عَلَيْهَا ۚ قُل لِّلَّهِ الْمَشْرِقُ وَالْمَغْرِبُ ۚ يَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','به زودی سبک‌مغزان از مردم می‌گویند: «چه چیز آنها [= مسلمانان‌] را، از قبله‌ای که بر آن بودند، بازگردانید؟!» بگو: «مشرق و مغرب، از آن خداست؛ خدا هر کس را بخواهد، به راه راست هدایت می‌کند.»'];
ayat[150]=[2,143,'وَكَذَٰلِكَ جَعَلْنَاكُمْ أُمَّةً وَسَطًا لِّتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ وَيَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنتَ عَلَيْهَا إِلَّا لِنَعْلَمَ مَن يَتَّبِعُ الرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيْهِ ۚ وَإِن كَانَتْ لَكَبِيرَةً إِلَّا عَلَى الَّذِينَ هَدَى اللَّهُ ۗ وَمَا كَانَ اللَّهُ لِيُضِيعَ إِيمَانَكُمْ ۚ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوفٌ رَّحِيمٌ','همان‌گونه (که قبله شما، یک قبله میانه است) شما را نیز، امت میانه‌ای قرار دادیم (در حد اعتدال، میان افراط و تفریط؛) تا بر مردم گواه باشید؛ و پیامبر هم بر شما گواه است. و ما، آن قبله‌ای را که قبلا بر آن بودی، تنها برای این قرار دادیم که افرادی که از پیامبر پیروی می‌کنند، از آنها که به جاهلیت بازمی‌گردند، مشخص شوند. و مسلماً این حکم، جز بر کسانی که خداوند آنها را هدایت کرده، دشوار بود. (این را نیز بدانید که نمازهای شما در برابر قبله سابق، صحیح بوده است؛) و خدا هرگز ایمان [= نماز] شما را ضایع نمی‌گرداند؛ زیرا خداوند، نسبت به مردم، رحیم و مهربان است.'];
ayat[151]=[2,144,'قَدْ نَرَىٰ تَقَلُّبَ وَجْهِكَ فِي السَّمَاءِ ۖ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضَاهَا ۚ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ ۗ وَإِنَّ الَّذِينَ أُوتُوا الْكِتَابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِن رَّبِّهِمْ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا يَعْمَلُونَ','نگاه‌های انتظارآمیز تو را به سوی آسمان (برای تعیین قبله نهایی) می‌بینیم! اکنون تو را به سوی قبله‌ای که از آن خشنود باشی، باز می‌گردانیم. پس روی خود را به سوی مسجد الحرام کن! و هر جا باشید، روی خود را به سوی آن بگردانید! و کسانی که کتاب آسمانی به آنها داده شده، بخوبی می‌دانند این فرمانِ حقی است که از ناحیه پروردگارشان صادر شده؛ (و در کتابهای خود خوانده‌اند که پیغمبر اسلام، به سوی دو قبله، نماز می‌خواند). و خداوند از اعمال آنها (در مخفی داشتن این آیات) غافل نیست!'];
ayat[152]=[2,145,'وَلَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتَابَ بِكُلِّ آيَةٍ مَّا تَبِعُوا قِبْلَتَكَ ۚ وَمَا أَنتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُم بِتَابِعٍ قِبْلَةَ بَعْضٍ ۚ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُم مِّن بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ ۙ إِنَّكَ إِذًا لَّمِنَ الظَّالِمِينَ','سوگند که اگر برای (این گروه از) اهل کتاب، هرگونه آیه (و نشانه و دلیلی) بیاوری، از قبله تو پیروی نخواهند کرد؛ و تو نیز، هیچ‌گاه از قبله آنان، پیروی نخواهی نمود. (آنها نباید تصور کنند که بار دیگر، تغییر قبله امکان‌پذیر است!) و حتی هیچ‌یک از آنها، پیروی از قبله دیگری نخواهد کرد! و اگر تو، پس از این آگاهی، متابعت هوسهای آنها کنی، مسلماً از ستمگران خواهی بود!'];
ayat[153]=[2,146,'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ أَبْنَاءَهُمْ ۖ وَإِنَّ فَرِيقًا مِّنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَهُمْ يَعْلَمُونَ','کسانی که کتاب آسمانی به آنان داده‌ایم، او [= پیامبر] را همچون فرزندان خود می‌شناسند؛ (ولی) جمعی از آنان، حق را آگاهانه کتمان می‌کنند!'];
ayat[154]=[2,147,'الْحَقُّ مِن رَّبِّكَ ۖ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ','این (فرمان تغییر قبله) حکمِ حقی از طرف پروردگار توست، بنابراین، هرگز از تردیدکنندگان در آن مباش!'];
ayat[155]=[2,148,'وَلِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيهَا ۖ فَاسْتَبِقُوا الْخَيْرَاتِ ۚ أَيْنَ مَا تَكُونُوا يَأْتِ بِكُمُ اللَّهُ جَمِيعًا ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','هر طایفه‌ای قبله‌ای دارد که خداوند آن را تعیین کرده است؛ (بنابراین، زیاد در باره قبله گفتگو نکنید! و به جای آن،) در نیکی‌ها و اعمال خیر، بر یکدیگر سبقت جویید! هر جا باشید، خداوند همه شما را (برای پاداش و کیفر در برابر اعمال نیک و بد، در روز رستاخیز،) حاضر می‌کند؛ زیرا او، بر هر کاری تواناست.'];
ayat[156]=[2,149,'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۖ وَإِنَّهُ لَلْحَقُّ مِن رَّبِّكَ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ','از هر جا (و از هر شهر و نقطه‌ای) خارج شدی، (به هنگام نماز،) روی خود را به جانب «مسجد الحرام» کن! این دستور حقی از طرف پروردگار توست! و خداوند، از آنچه انجام می‌دهید، غافل نیست!'];
ayat[157]=[2,150,'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۚ وَحَيْثُ مَا كُنتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ إِلَّا الَّذِينَ ظَلَمُوا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِي وَلِأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَ','و از هر جا خارج شدی، روی خود را به جانب مسجد الحرام کن! و هر جا بودید، روی خود را به سوی آن کنید! تا مردم، جز ظالمان (که دست از لجاجت برنمی‌دارند،) دلیلی بر ضدّ شما نداشته باشند؛ (زیرا از نشانه‌های پیامبر، که در کتب آسمانی پیشین آمده، این است که او، به سوی دو قبله، نماز می‌خواند.) از آنها نترسید! و (تنها) از من بترسید! (این تغییر قبله، به خاطر آن بود که) نعمت خود را بر شما تمام کنم، شاید هدایت شوید!'];
ayat[158]=[2,151,'كَمَا أَرْسَلْنَا فِيكُمْ رَسُولًا مِّنكُمْ يَتْلُو عَلَيْكُمْ آيَاتِنَا وَيُزَكِّيكُمْ وَيُعَلِّمُكُمُ الْكِتَابَ وَالْحِكْمَةَ وَيُعَلِّمُكُم مَّا لَمْ تَكُونُوا تَعْلَمُونَ','همان‌گونه (که با تغییر قبله، نعمت خود را بر شما کامل کردیم،) رسولی از خودتان در میان شما فرستادیم؛ تا آیات ما را بر شما بخواند؛ و شما را پاک کند؛ و به شما، کتاب و حکمت بیاموزد؛ و آنچه را نمی‌دانستید، به شما یاد دهد.'];
ayat[159]=[2,152,'فَاذْكُرُونِي أَذْكُرْكُمْ وَاشْكُرُوا لِي وَلَا تَكْفُرُونِ','پس به یاد من باشید، تا به یاد شما باشم! و شکر مرا گویید و (در برابر نعمتهایم) کفران نکنید!'];
ayat[160]=[2,153,'يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّبْرِ وَالصَّلَاةِ ۚ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ','ای افرادی که ایمان آورده‌اید! از صبر (و استقامت) و نماز، کمک بگیرید! (زیرا) خداوند با صابران است.'];
ayat[161]=[2,154,'وَلَا تَقُولُوا لِمَن يُقْتَلُ فِي سَبِيلِ اللَّهِ أَمْوَاتٌ ۚ بَلْ أَحْيَاءٌ وَلَٰكِن لَّا تَشْعُرُونَ','و به آنها که در راه خدا کشته می‌شوند، مرده نگویید! بلکه آنان زنده‌اند، ولی شما نمی‌فهمید!'];
ayat[162]=[2,155,'وَلَنَبْلُوَنَّكُم بِشَيْءٍ مِّنَ الْخَوْفِ وَالْجُوعِ وَنَقْصٍ مِّنَ الْأَمْوَالِ وَالْأَنفُسِ وَالثَّمَرَاتِ ۗ وَبَشِّرِ الصَّابِرِينَ','قطعاً همه شما را با چیزی از ترس، گرسنگی، و کاهش در مالها و جانها و میوه‌ها، آزمایش می‌کنیم؛ و بشارت ده به استقامت‌کنندگان!'];
ayat[163]=[2,156,'الَّذِينَ إِذَا أَصَابَتْهُم مُّصِيبَةٌ قَالُوا إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَاجِعُونَ','آنها که هر گاه مصیبتی به ایشان می‌رسد، می‌گویند: «ما از آنِ خدائیم؛ و به سوی او بازمی‌گردیم!»'];
ayat[164]=[2,157,'أُولَٰئِكَ عَلَيْهِمْ صَلَوَاتٌ مِّن رَّبِّهِمْ وَرَحْمَةٌ ۖ وَأُولَٰئِكَ هُمُ الْمُهْتَدُونَ','اینها، همانها هستند که الطاف و رحمت خدا شامل حالشان شده؛ و آنها هستند هدایت‌یافتگان!'];
ayat[165]=[2,158,'۞ إِنَّ الصَّفَا وَالْمَرْوَةَ مِن شَعَائِرِ اللَّهِ ۖ فَمَنْ حَجَّ الْبَيْتَ أَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ أَن يَطَّوَّفَ بِهِمَا ۚ وَمَن تَطَوَّعَ خَيْرًا فَإِنَّ اللَّهَ شَاكِرٌ عَلِيمٌ','«صفا» و «مروه» از شعائر (و نشانه‌های) خداست! بنابراین، کسانی که حجِ خانه خدا و یا عمره انجام می‌دهند، مانعی نیست که بر آن دو طواف کنند؛ (و سعیِ صفا و مروه انجام دهند. و هرگز اعمال بی‌رویّه مشرکان، که بتهایی بر این دو کوه نصب کرده بودند، از موقعیّت این دو مکان مقدّس نمی‌کاهد!) و کسی که فرمان خدا را در انجام کارهای نیک اطاعت کند، خداوند (در برابر عمل او) شکرگزار، و (از افعال وی) آگاه است.'];
ayat[166]=[2,159,'إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنزَلْنَا مِنَ الْبَيِّنَاتِ وَالْهُدَىٰ مِن بَعْدِ مَا بَيَّنَّاهُ لِلنَّاسِ فِي الْكِتَابِ ۙ أُولَٰئِكَ يَلْعَنُهُمُ اللَّهُ وَيَلْعَنُهُمُ اللَّاعِنُونَ','کسانی که دلایل روشن، و وسیله هدایتی را که نازل کرده‌ایم، بعد از آنکه در کتاب برای مردم بیان نمودیم، کتمان کنند، خدا آنها را لعنت می‌کند؛ و همه لعن‌کنندگان نیز، آنها را لعن می‌کنند؛'];
ayat[167]=[2,160,'إِلَّا الَّذِينَ تَابُوا وَأَصْلَحُوا وَبَيَّنُوا فَأُولَٰئِكَ أَتُوبُ عَلَيْهِمْ ۚ وَأَنَا التَّوَّابُ الرَّحِيمُ','مگر آنها که توبه و بازگشت کردند، و (اعمال بد خود را، با اعمال نیک،) اصلاح نمودند، (و آنچه را کتمان کرده بودند؛ آشکار ساختند؛) من توبه آنها را می‌پذیرم؛ که من توّاب و رحیمم.'];
ayat[168]=[2,161,'إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ أُولَٰئِكَ عَلَيْهِمْ لَعْنَةُ اللَّهِ وَالْمَلَائِكَةِ وَالنَّاسِ أَجْمَعِينَ','کسانی که کافر شدند، و در حالِ کفر از دنیا رفتند، لعنت خداوند و فرشتگان و همه مردم بر آنها خواهد بود!'];
ayat[169]=[2,162,'خَالِدِينَ فِيهَا ۖ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنظَرُونَ','همیشه در آن (لعن و دوری از رحمت پروردگار) باقی می‌مانند؛ نه در عذاب آنان تخفیف داده می‌شود، و نه مهلتی خواهند داشت!'];
ayat[170]=[2,163,'وَإِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ لَّا إِلَٰهَ إِلَّا هُوَ الرَّحْمَٰنُ الرَّحِيمُ','و خدای شما، خداوند یگانه‌ای است، که غیر از او معبودی نیست! اوست بخشنده و مهربان (و دارای رحمت عام و خاصّ)!'];
ayat[171]=[2,164,'إِنَّ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافِ اللَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّتِي تَجْرِي فِي الْبَحْرِ بِمَا يَنفَعُ النَّاسَ وَمَا أَنزَلَ اللَّهُ مِنَ السَّمَاءِ مِن مَّاءٍ فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَابَّةٍ وَتَصْرِيفِ الرِّيَاحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاءِ وَالْأَرْضِ لَآيَاتٍ لِّقَوْمٍ يَعْقِلُونَ','در آفرینش آسمانها و زمین، و آمد و شد شب و روز، و کشتیهایی که در دریا به سود مردم در حرکتند، و آبی که خداوند از آسمان نازل کرده، و با آن، زمین را پس از مرگ، زنده نموده، و انواع جنبندگان را در آن گسترده، و (همچنین) در تغییر مسیر بادها و ابرهایی که میان زمین و آسمان مسخرند، نشانه‌هایی است (از ذات پاک خدا و یگانگی او) برای مردمی که عقل دارند و می‌اندیشند!'];
ayat[172]=[2,165,'وَمِنَ النَّاسِ مَن يَتَّخِذُ مِن دُونِ اللَّهِ أَندَادًا يُحِبُّونَهُمْ كَحُبِّ اللَّهِ ۖ وَالَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِّلَّهِ ۗ وَلَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذَابَ أَنَّ الْقُوَّةَ لِلَّهِ جَمِيعًا وَأَنَّ اللَّهَ شَدِيدُ الْعَذَابِ','بعضی از مردم، معبودهایی غیر از خداوند برای خود انتخاب می‌کنند؛ و آنها را همچون خدا دوست می‌دارند. امّا آنها که ایمان دارند، عشقشان به خدا، (از مشرکان نسبت به معبودهاشان،) شدیدتر است. و آنها که ستم کردند، (و معبودی غیر خدا برگزیدند،) هنگامی که عذاب (الهی) را مشاهده کنند، خواهند دانست که تمامِ قدرت، از آنِ خداست؛ و خدا دارای مجازات شدید است؛ (نه معبودهای خیالی که از آنها می‌هراسند.)'];
ayat[173]=[2,166,'إِذْ تَبَرَّأَ الَّذِينَ اتُّبِعُوا مِنَ الَّذِينَ اتَّبَعُوا وَرَأَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْأَسْبَابُ','در آن هنگام، رهبران (گمراه و گمراه‌کننده) از پیروانِ خود، بیزاری می‌جویند؛ و کیفر خدا را مشاهده می‌کنند؛ و دستشان از همه جا کوتاه می‌شود.'];
ayat[174]=[2,167,'وَقَالَ الَّذِينَ اتَّبَعُوا لَوْ أَنَّ لَنَا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَمَا تَبَرَّءُوا مِنَّا ۗ كَذَٰلِكَ يُرِيهِمُ اللَّهُ أَعْمَالَهُمْ حَسَرَاتٍ عَلَيْهِمْ ۖ وَمَا هُم بِخَارِجِينَ مِنَ النَّارِ','و (در این هنگام) پیروان می‌گویند: «کاش بار دیگر به دنیا برمی‌گشتیم، تا از آنها [= پیشوایان گمراه‌] بیزاری جوییم، آن چنان که آنان (امروز) از ما بیزاری جستند! (آری،) خداوند این چنین اعمال آنها را به صورت حسرت‌زایی به آنان نشان می‌دهد؛ و هرگز از آتش (دوزخ) خارج نخواهند شد!'];
ayat[175]=[2,168,'يَا أَيُّهَا النَّاسُ كُلُوا مِمَّا فِي الْأَرْضِ حَلَالًا طَيِّبًا وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُّبِينٌ','ای مردم! از آنچه در زمین است، حلال و پاکیزه بخورید! و از گامهای شیطان، پیروی نکنید! چه اینکه او، دشمن آشکار شماست!'];
ayat[176]=[2,169,'إِنَّمَا يَأْمُرُكُم بِالسُّوءِ وَالْفَحْشَاءِ وَأَن تَقُولُوا عَلَى اللَّهِ مَا لَا تَعْلَمُونَ','او شما را فقط به بدیها و کار زشت فرمان می‌دهد؛ (و نیز دستور می‌دهد) آنچه را که نمی‌دانید، به خدا نسبت دهید.'];
ayat[177]=[2,170,'وَإِذَا قِيلَ لَهُمُ اتَّبِعُوا مَا أَنزَلَ اللَّهُ قَالُوا بَلْ نَتَّبِعُ مَا أَلْفَيْنَا عَلَيْهِ آبَاءَنَا ۗ أَوَلَوْ كَانَ آبَاؤُهُمْ لَا يَعْقِلُونَ شَيْئًا وَلَا يَهْتَدُونَ','و هنگامی که به آنها گفته شود: «از آنچه خدا نازل کرده است، پیروی کنید!» می‌گویند: «نه، ما از آنچه پدران خود را بر آن یافتیم، پیروی می‌نماییم.» آیا اگر پدران آنها، چیزی نمی‌فهمیدند و هدایت نیافتند (باز از آنها پیروی خواهند کرد)؟!'];
ayat[178]=[2,171,'وَمَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِمَا لَا يَسْمَعُ إِلَّا دُعَاءً وَنِدَاءً ۚ صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُونَ','مَثَل (تو در دعوت) کافران، بسان کسی است که (گوسفندان و حیوانات را برای نجات از چنگال خطر،) صدا می‌زند؛ ولی آنها چیزی جز سر و صدا نمی‌شنوند؛ (و حقیقت و مفهوم گفتار او را درک نمی‌کنند. این کافران، در واقع) کر و لال و نابینا هستند؛ از این رو چیزی نمی‌فهمند!'];
ayat[179]=[2,172,'يَا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِن طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَاشْكُرُوا لِلَّهِ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ','ای کسانی که ایمان آورده‌اید! از نعمتهای پاکیزه‌ای که به شما روزی داده‌ایم، بخورید و شکر خدا را بجا آورید؛ اگر او را پرستش می‌کنید!'];
ayat[180]=[2,173,'إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنزِيرِ وَمَا أُهِلَّ بِهِ لِغَيْرِ اللَّهِ ۖ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','خداوند، تنها (گوشت) مردار، خون، گوشت خوک و آنچه را نام غیرِ خدا به هنگام ذبح بر آن گفته شود، حرام کرده است. (ولی) آن کس که مجبور شود، در صورتی که ستمگر و متجاوز نباشد، گناهی بر او نیست؛ (و می‌تواند برای حفظ جان خود، در موقع ضرورت، از آن بخورد؛) خداوند بخشنده و مهربان است.'];
ayat[181]=[2,174,'إِنَّ الَّذِينَ يَكْتُمُونَ مَا أَنزَلَ اللَّهُ مِنَ الْكِتَابِ وَيَشْتَرُونَ بِهِ ثَمَنًا قَلِيلًا ۙ أُولَٰئِكَ مَا يَأْكُلُونَ فِي بُطُونِهِمْ إِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللَّهُ يَوْمَ الْقِيَامَةِ وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ','کسانی که کتمان می‌کنند آنچه را خدا از کتاب نازل کرده، و آن را به بهای کمی می‌فروشند، آنها جز آتش چیزی نمی‌خورند؛ (و هدایا و اموالی که از این رهگذر به دست می‌آورند، در حقیقت آتش سوزانی است.) و خداوند، روز قیامت، با آنها سخن نمی‌گوید؛ و آنان را پاکیزه نمی‌کند؛ و برای آنها عذاب دردناکی است.'];
ayat[182]=[2,175,'أُولَٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلَالَةَ بِالْهُدَىٰ وَالْعَذَابَ بِالْمَغْفِرَةِ ۚ فَمَا أَصْبَرَهُمْ عَلَى النَّارِ','اینان، همانهایی هستند که گمراهی را با هدایت، و عذاب را با آمرزش، مبادله کرده‌اند؛ راستی چقدر در برابر عذاب خداوند، شکیبا هستند!!'];
ayat[183]=[2,176,'ذَٰلِكَ بِأَنَّ اللَّهَ نَزَّلَ الْكِتَابَ بِالْحَقِّ ۗ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتَابِ لَفِي شِقَاقٍ بَعِيدٍ','اینها، به خاطر آن است که خداوند، کتاب (آسمانی) را به حق، (و توأم با نشانه‌ها و دلایل روشن،) نازل کرده؛ و آنها که در آن اختلاف می‌کنند، (و با کتمان و تحریف، اختلاف به وجود می‌آورند،) در شکاف و (پراکندگی) عمیقی قرار دارند.'];
ayat[184]=[2,177,'۞ لَّيْسَ الْبِرَّ أَن تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلَٰكِنَّ الْبِرَّ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَالْمَلَائِكَةِ وَالْكِتَابِ وَالنَّبِيِّينَ وَآتَى الْمَالَ عَلَىٰ حُبِّهِ ذَوِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينَ وَابْنَ السَّبِيلِ وَالسَّائِلِينَ وَفِي الرِّقَابِ وَأَقَامَ الصَّلَاةَ وَآتَى الزَّكَاةَ وَالْمُوفُونَ بِعَهْدِهِمْ إِذَا عَاهَدُوا ۖ وَالصَّابِرِينَ فِي الْبَأْسَاءِ وَالضَّرَّاءِ وَحِينَ الْبَأْسِ ۗ أُولَٰئِكَ الَّذِينَ صَدَقُوا ۖ وَأُولَٰئِكَ هُمُ الْمُتَّقُونَ','نیکی، (تنها) این نیست که (به هنگام نماز،) رویِ خود را به سوی مشرق و (یا) مغرب کنید؛ (و تمام گفتگوی شما، در باره قبله و تغییر آن باشد؛ و همه وقت خود را مصروف آن سازید؛) بلکه نیکی (و نیکوکار) کسی است که به خدا، و روز رستاخیز، و فرشتگان، و کتاب (آسمانی)، و پیامبران، ایمان آورده؛ و مال (خود) را، با همه علاقه‌ای که به آن دارد، به خویشاوندان و یتیمان و مسکینان و واماندگان در راه و سائلان و بردگان، انفاق می‌کند؛ نماز را برپا می‌دارد و زکات را می‌پردازد؛ و (همچنین) کسانی که به عهد خود -به هنگامی که عهد بستند-وفا می‌کنند؛ و در برابر محرومیتها و بیماریها و در میدان جنگ، استقامت به خرج می‌دهند؛ اینها کسانی هستند که راست می‌گویند؛ و (گفتارشان با اعتقادشان هماهنگ است؛) و اینها هستند پرهیزکاران!'];
ayat[185]=[2,178,'يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِي الْقَتْلَى ۖ الْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْأُنثَىٰ بِالْأُنثَىٰ ۚ فَمَنْ عُفِيَ لَهُ مِنْ أَخِيهِ شَيْءٌ فَاتِّبَاعٌ بِالْمَعْرُوفِ وَأَدَاءٌ إِلَيْهِ بِإِحْسَانٍ ۗ ذَٰلِكَ تَخْفِيفٌ مِّن رَّبِّكُمْ وَرَحْمَةٌ ۗ فَمَنِ اعْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُ عَذَابٌ أَلِيمٌ','ای افرادی که ایمان آورده‌اید! حکم قصاص در مورد کشتگان، بر شما نوشته شده است: آزاد در برابر آزاد، و برده در برابر برده، و زن در برابر زن، پس اگر کسی از سوی برادر (دینی) خود، چیزی به او بخشیده شود، (و حکم قصاص او، تبدیل به خونبها گردد،) باید از راه پسندیده پیروی کند. (و صاحب خون، حال پرداخت کننده دیه را در نظر بگیرد.) و او [= قاتل‌] نیز، به نیکی دیه را (به ولی مقتول) بپردازد؛ (و در آن، مسامحه نکند.) این، تخفیف و رحمتی است از ناحیه پروردگار شما! و کسی که بعد از آن، تجاوز کند، عذاب دردناکی خواهد داشت.'];
ayat[186]=[2,179,'وَلَكُمْ فِي الْقِصَاصِ حَيَاةٌ يَا أُولِي الْأَلْبَابِ لَعَلَّكُمْ تَتَّقُونَ','و برای شما در قصاص، حیات و زندگی است، ای صاحبان خِرد! شاید شما تقوا پیشه کنید.'];
ayat[187]=[2,180,'كُتِبَ عَلَيْكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِن تَرَكَ خَيْرًا الْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْأَقْرَبِينَ بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُتَّقِينَ','بر شما نوشته شده: «هنگامی که یکی از شما را مرگ فرا رسد، اگر چیز خوبی [= مالی‌] از خود به جای گذارده، برای پدر و مادر و نزدیکان، بطور شایسته وصیت کند! این حقّی است بر پرهیزکاران!»'];
ayat[188]=[2,181,'فَمَن بَدَّلَهُ بَعْدَمَا سَمِعَهُ فَإِنَّمَا إِثْمُهُ عَلَى الَّذِينَ يُبَدِّلُونَهُ ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ','پس کسانی که بعد از شنیدنش آن را تغییر دهند، گناه آن، تنها بر کسانی است که آن (وصیّت) را تغییر می‌دهند؛ خداوند، شنوا و داناست.'];
ayat[189]=[2,182,'فَمَنْ خَافَ مِن مُّوصٍ جَنَفًا أَوْ إِثْمًا فَأَصْلَحَ بَيْنَهُمْ فَلَا إِثْمَ عَلَيْهِ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','و کسی که از انحرافِ وصیت کننده (و تمایل یک‌جانبه او به بعض ورثه)، یا از گناه او (که مبادا وصیّت به کار خلافی کند) بترسد، و میان آنها را اصلاح دهد، گناهی بر او نیست؛ (و مشمولِ حکم تبدیلِ وصیّت نمی‌باشد.) خداوند، آمرزنده و مهربان است.'];
ayat[190]=[2,183,'يَا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ','ای افرادی که ایمان آورده‌اید! روزه بر شما نوشته شده، همان‌گونه که بر کسانی که قبل از شما بودند نوشته شد؛ تا پرهیزکار شوید.'];
ayat[191]=[2,184,'أَيَّامًا مَّعْدُودَاتٍ ۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوْ عَلَىٰ سَفَرٍ فَعِدَّةٌ مِّنْ أَيَّامٍ أُخَرَ ۚ وَعَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعَامُ مِسْكِينٍ ۖ فَمَن تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَّهُ ۚ وَأَن تَصُومُوا خَيْرٌ لَّكُمْ ۖ إِن كُنتُمْ تَعْلَمُونَ','چند روز معدودی را (باید روزه بدارید!) و هر کس از شما بیمار یا مسافر باشد تعدادی از روزهای دیگر را (روزه بدارد) و بر کسانی که روزه برای آنها طاقت‌فرساست؛ (همچون بیماران مزمن، و پیرمردان و پیرزنان،) لازم است کفّاره بدهند: مسکینی را اطعام کنند؛ و کسی که کارِ خیری انجام دهد، برای او بهتر است؛ و روزه داشتن برای شما بهتر است اگر بدانید!'];
ayat[192]=[2,185,'شَهْرُ رَمَضَانَ الَّذِي أُنزِلَ فِيهِ الْقُرْآنُ هُدًى لِّلنَّاسِ وَبَيِّنَاتٍ مِّنَ الْهُدَىٰ وَالْفُرْقَانِ ۚ فَمَن شَهِدَ مِنكُمُ الشَّهْرَ فَلْيَصُمْهُ ۖ وَمَن كَانَ مَرِيضًا أَوْ عَلَىٰ سَفَرٍ فَعِدَّةٌ مِّنْ أَيَّامٍ أُخَرَ ۗ يُرِيدُ اللَّهُ بِكُمُ الْيُسْرَ وَلَا يُرِيدُ بِكُمُ الْعُسْرَ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللَّهَ عَلَىٰ مَا هَدَاكُمْ وَلَعَلَّكُمْ تَشْكُرُونَ','(روزه، در چند روز معدودِ) ماهِ رمضان است؛ ماهی که قرآن، برای راهنمایی مردم، و نشانه‌های هدایت، و فرق میان حق و باطل، در آن نازل شده است. پس آن کس از شما که در ماه رمضان در حضر باشد، روزه بدارد! و آن کس که بیمار یا در سفر است، روزهای دیگری را به جای آن، روزه بگیرد! خداوند، راحتی شما را می‌خواهد، نه زحمت شما را! هدف این است که این روزها را تکمیل کنید؛ و خدا را بر اینکه شما را هدایت کرده، بزرگ بشمرید؛ باشد که شکرگزاری کنید!'];
ayat[193]=[2,186,'وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌ ۖ أُجِيبُ دَعْوَةَ الدَّاعِ إِذَا دَعَانِ ۖ فَلْيَسْتَجِيبُوا لِي وَلْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُدُونَ','و هنگامی که بندگان من، از تو در باره من سؤال کنند، (بگو:) من نزدیکم! دعای دعا کننده را، به هنگامی که مرا می‌خواند، پاسخ می‌گویم! پس باید دعوت مرا بپذیرند، و به من ایمان بیاورند، تا راه یابند (و به مقصد برسند)!'];
ayat[194]=[2,187,'أُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ إِلَىٰ نِسَائِكُمْ ۚ هُنَّ لِبَاسٌ لَّكُمْ وَأَنتُمْ لِبَاسٌ لَّهُنَّ ۗ عَلِمَ اللَّهُ أَنَّكُمْ كُنتُمْ تَخْتَانُونَ أَنفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنكُمْ ۖ فَالْآنَ بَاشِرُوهُنَّ وَابْتَغُوا مَا كَتَبَ اللَّهُ لَكُمْ ۚ وَكُلُوا وَاشْرَبُوا حَتَّىٰ يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْأَبْيَضُ مِنَ الْخَيْطِ الْأَسْوَدِ مِنَ الْفَجْرِ ۖ ثُمَّ أَتِمُّوا الصِّيَامَ إِلَى اللَّيْلِ ۚ وَلَا تُبَاشِرُوهُنَّ وَأَنتُمْ عَاكِفُونَ فِي الْمَسَاجِدِ ۗ تِلْكَ حُدُودُ اللَّهِ فَلَا تَقْرَبُوهَا ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَ','آمیزش جنسی با همسرانتان، در شبِ روزهایی که روزه می‌گیرید، حلال است. آنها لباس شما هستند؛ و شما لباس آنها (هر دو زینت هم و سبب حفظ یکدیگرید). خداوند می‌دانست که شما به خود خیانت می‌کردید؛ (و این کارِ ممنوع را انجام می‌دادید؛) پس توبه شما را پذیرفت و شما را بخشید. اکنون با آنها آمیزش کنید، و آنچه را خدا برای شما مقرر داشته، طلب نمایید! و بخورید و بیاشامید، تا رشته سپید صبح، از رشته سیاه (شب) برای شما آشکار گردد! سپس روزه را تا شب، تکمیل کنید! و در حالی که در مساجد به اعتکاف پرداخته‌اید، با زنان آمیزش نکنید! این، مرزهای الهی است؛ پس به آن نزدیک نشوید! خداوند، این چنین آیات خود را برای مردم، روشن می‌سازد، باشد که پرهیزکار گردند!'];
ayat[195]=[2,188,'وَلَا تَأْكُلُوا أَمْوَالَكُم بَيْنَكُم بِالْبَاطِلِ وَتُدْلُوا بِهَا إِلَى الْحُكَّامِ لِتَأْكُلُوا فَرِيقًا مِّنْ أَمْوَالِ النَّاسِ بِالْإِثْمِ وَأَنتُمْ تَعْلَمُونَ','و اموال یکدیگر را به باطل (و ناحق) در میان خود نخورید! و برای خوردن بخشی از اموال مردم به گناه، (قسمتی از) آن را (به عنوان رشوه) به قضات ندهید، در حالی که می‌دانید (این کار، گناه است)!'];
ayat[196]=[2,189,'۞ يَسْأَلُونَكَ عَنِ الْأَهِلَّةِ ۖ قُلْ هِيَ مَوَاقِيتُ لِلنَّاسِ وَالْحَجِّ ۗ وَلَيْسَ الْبِرُّ بِأَن تَأْتُوا الْبُيُوتَ مِن ظُهُورِهَا وَلَٰكِنَّ الْبِرَّ مَنِ اتَّقَىٰ ۗ وَأْتُوا الْبُيُوتَ مِنْ أَبْوَابِهَا ۚ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ','در باره «هلالهای ماه» از تو سؤال می‌کنند؛ بگو: «آنها، بیان اوقات (و تقویم طبیعی) برای (نظامِ زندگیِ) مردم و (تعیینِ وقتِ) حج است». و (آن چنان که در جاهلیّت مرسوم بود که به هنگام حج، که جامه احرام می‌پوشیدند، از درِ خانه وارد نمی‌شدند، و از نقبِ پشتِ خانه وارد می‌شدند، نکنید!) کارِ نیک، آن نیست که از پشتِ خانه‌ها وارد شوید؛ بلکه نیکی این است که پرهیزگار باشید! و از درِ خانه‌ها وارد شوید و تقوا پیشه کنید، تا رستگار گردید!'];
ayat[197]=[2,190,'وَقَاتِلُوا فِي سَبِيلِ اللَّهِ الَّذِينَ يُقَاتِلُونَكُمْ وَلَا تَعْتَدُوا ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْمُعْتَدِينَ','و در راه خدا، با کسانی که با شما می‌جنگند، نبرد کنید! و از حدّ تجاوز نکنید، که خدا تعدّی‌کنندگان را دوست نمی‌دارد!'];
ayat[198]=[2,191,'وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَأَخْرِجُوهُم مِّنْ حَيْثُ أَخْرَجُوكُمْ ۚ وَالْفِتْنَةُ أَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوهُمْ عِندَ الْمَسْجِدِ الْحَرَامِ حَتَّىٰ يُقَاتِلُوكُمْ فِيهِ ۖ فَإِن قَاتَلُوكُمْ فَاقْتُلُوهُمْ ۗ كَذَٰلِكَ جَزَاءُ الْكَافِرِينَ','و آنها را [= بت پرستانی که از هیچ گونه جنایتی ابا ندارند] هر کجا یافتید، به قتل برسانید! و از آن جا که شما را بیرون ساختند [= مکه‌]، آنها را بیرون کنید! و فتنه (و بت پرستی) از کشتار هم بدتر است! و با آنها، در نزد مسجد الحرام (در منطقه حرم)، جنگ نکنید! مگر اینکه در آن جا با شما بجنگند. پس اگر (در آن جا) با شما پیکار کردند، آنها را به قتل برسانید! چنین است جزای کافران!'];
ayat[199]=[2,192,'فَإِنِ انتَهَوْا فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','و اگر خودداری کردند، خداوند آمرزنده و مهربان است.'];
ayat[200]=[2,193,'وَقَاتِلُوهُمْ حَتَّىٰ لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ لِلَّهِ ۖ فَإِنِ انتَهَوْا فَلَا عُدْوَانَ إِلَّا عَلَى الظَّالِمِينَ','و با آنها پیکار کنید! تا فتنه (و بت پرستی، و سلب آزادی از مردم،) باقی نماند؛ و دین، مخصوص خدا گردد. پس اگر (از روش نادرست خود) دست برداشتند، (مزاحم آنها نشوید! زیرا) تعدّی جز بر ستمکاران روا نیست.'];
ayat[201]=[2,194,'الشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمَاتُ قِصَاصٌ ۚ فَمَنِ اعْتَدَىٰ عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدَىٰ عَلَيْكُمْ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ','ماهِ حرام، در برابر ماهِ حرام! (اگر دشمنان، احترام آن را شکستند، و در آن با شما جنگیدند، شما نیز حق دارید مقابله به مثل کنید.) و تمام حرامها، (قابلِ) قصاص است. و (به طور کلّی) هر کس به شما تجاوز کرد، همانند آن بر او تعدّی کنید! و از خدا بپرهیزید (و زیاده روی ننمایید)! و بدانید خدا با پرهیزکاران است!'];
ayat[202]=[2,195,'وَأَنفِقُوا فِي سَبِيلِ اللَّهِ وَلَا تُلْقُوا بِأَيْدِيكُمْ إِلَى التَّهْلُكَةِ ۛ وَأَحْسِنُوا ۛ إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ','و در راهِ خدا، انفاق کنید! و (با ترک انفاق،) خود را به دست خود، به هلاکت نیفکنید! و نیکی کنید! که خداوند، نیکوکاران را دوست می‌دارد.'];
ayat[203]=[2,196,'وَأَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلَّهِ ۚ فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ ۖ وَلَا تَحْلِقُوا رُءُوسَكُمْ حَتَّىٰ يَبْلُغَ الْهَدْيُ مَحِلَّهُ ۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوْ بِهِ أَذًى مِّن رَّأْسِهِ فَفِدْيَةٌ مِّن صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ ۚ فَإِذَا أَمِنتُمْ فَمَن تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ ۚ فَمَن لَّمْ يَجِدْ فَصِيَامُ ثَلَاثَةِ أَيَّامٍ فِي الْحَجِّ وَسَبْعَةٍ إِذَا رَجَعْتُمْ ۗ تِلْكَ عَشَرَةٌ كَامِلَةٌ ۗ ذَٰلِكَ لِمَن لَّمْ يَكُنْ أَهْلُهُ حَاضِرِي الْمَسْجِدِ الْحَرَامِ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ','و حج و عمره را برای خدا به اتمام برسانید! و اگر محصور شدید، (و مانعی مانند ترس از دشمن یا بیماری، اجازه نداد که پس از احرام‌بستن، وارد مکه شوید،) آنچه از قربانی فراهم شود (ذبح کنید، و از احرام خارج شوید)! و سرهای خود را نتراشید، تا قربانی به محلش برسد (و در قربانگاه ذبح شود)! و اگر کسی از شما بیمار بود، و یا ناراحتی در سر داشت، (و ناچار بود سر خود را بتراشد،) باید فدیه و کفّاره‌ای از قبیل روزه یا صدقه یا گوسفندی بدهد! و هنگامی که (از بیماری و دشمن) در امان بودید، هر کس با ختم عمره، حج را آغاز کند، آنچه از قربانی برای او میسّر است (ذبح کند)! و هر که نیافت، سه روز در ایام حج، و هفت روز هنگامی که باز می‌گردید، روزه بدارد! این، ده روز کامل است. (البته) این برای کسی است که خانواده او، نزد مسجد الحرام نباشد [= اهل مکّه و اطرافِ آن نباشد]. و از خدا بپرهیزید! و بدانید که او، سخت‌کیفر است!'];
ayat[204]=[2,197,'الْحَجُّ أَشْهُرٌ مَّعْلُومَاتٌ ۚ فَمَن فَرَضَ فِيهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِي الْحَجِّ ۗ وَمَا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللَّهُ ۗ وَتَزَوَّدُوا فَإِنَّ خَيْرَ الزَّادِ التَّقْوَىٰ ۚ وَاتَّقُونِ يَا أُولِي الْأَلْبَابِ','حج، در ماه‌های معینی است! و کسانی که (با بستن احرام، و شروع به مناسک حج،) حج را بر خود فرض کرده‌اند، (باید بدانند که) در حج، آمیزش جنسی با زنان، و گناه و جدال نیست! و آنچه از کارهای نیک انجام دهید، خدا آن را می‌داند. و زاد و توشه تهیه کنید، که بهترین زاد و توشه، پرهیزکاری است! و از من بپرهیزید ای خردمندان!'];
ayat[205]=[2,198,'لَيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَبْتَغُوا فَضْلًا مِّن رَّبِّكُمْ ۚ فَإِذَا أَفَضْتُم مِّنْ عَرَفَاتٍ فَاذْكُرُوا اللَّهَ عِندَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوهُ كَمَا هَدَاكُمْ وَإِن كُنتُم مِّن قَبْلِهِ لَمِنَ الضَّالِّينَ','گناهی بر شما نیست که از فضلِ پروردگارتان (و از منافع اقتصادی در ایّام حج) طلب کنید (که یکی از منافع حج، پی ریزیِ یک اقتصادِ صحیح است). و هنگامی که از «عرفات» کوچ کردید، خدا را نزد «مشعَر الحرام» یاد کنید! او را یاد کنید همان‌طور که شما را هدایت نمود و قطعاً شما پیش از این، از گمراهان بودید.'];
ayat[206]=[2,199,'ثُمَّ أَفِيضُوا مِنْ حَيْثُ أَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','سپس از همان‌جا که مردم کوچ می‌کنند، (به سوی سرزمین منی) کوچ کنید! و از خداوند، آمرزش بطلبید، که خدا آمرزنده مهربان است!'];
ayat[207]=[2,200,'فَإِذَا قَضَيْتُم مَّنَاسِكَكُمْ فَاذْكُرُوا اللَّهَ كَذِكْرِكُمْ آبَاءَكُمْ أَوْ أَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَن يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا وَمَا لَهُ فِي الْآخِرَةِ مِنْ خَلَاقٍ','و هنگامی که مناسکِ (حج) ِ خود را انجام دادید، خدا را یاد کنید، همانند یادآوری از پدرانتان (آن‌گونه که رسم آن زمان بود) بلکه از آن هم بیشتر! (در این مراسم، مردم دو گروهند:) بعضی از مردم می‌گویند: «خداوندا! به ما در دنیا، (*نیکی*) عطا کن!» ولی در آخرت، بهره‌ای ندارند.'];
ayat[208]=[2,201,'وَمِنْهُم مَّن يَقُولُ رَبَّنَا آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ','و بعضی می‌گویند: «پروردگارا! به ما در دنیا (*نیکی*) عطا کن! و در آخرت نیز (*نیکی*) مرحمت فرما! و ما را از عذابِ آتش نگاه دار!»'];
ayat[209]=[2,202,'أُولَٰئِكَ لَهُمْ نَصِيبٌ مِّمَّا كَسَبُوا ۚ وَاللَّهُ سَرِيعُ الْحِسَابِ','آنها از کار (و دعای) خود، نصیب و بهره‌ای دارند؛ و خداوند، سریع الحساب است.'];
ayat[210]=[2,203,'۞ وَاذْكُرُوا اللَّهَ فِي أَيَّامٍ مَّعْدُودَاتٍ ۚ فَمَن تَعَجَّلَ فِي يَوْمَيْنِ فَلَا إِثْمَ عَلَيْهِ وَمَن تَأَخَّرَ فَلَا إِثْمَ عَلَيْهِ ۚ لِمَنِ اتَّقَىٰ ۗ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُمْ إِلَيْهِ تُحْشَرُونَ','و خدا را در روزهای معیّنی یاد کنید! (روزهای 11 و 12 و 13 ماه ذی حجه). و هر کس شتاب کند، (و ذکر خدا را) در دو روز انجام دهد، گناهی بر او نیست، و هر که تأخیر کند، (و سه روز انجام دهد نیز) گناهی بر او نیست؛ برای کسی که تقوا پیشه کند. و از خدا بپرهیزید! و بدانید شما به سوی او محشور خواهید شد!'];
ayat[211]=[2,204,'وَمِنَ النَّاسِ مَن يُعْجِبُكَ قَوْلُهُ فِي الْحَيَاةِ الدُّنْيَا وَيُشْهِدُ اللَّهَ عَلَىٰ مَا فِي قَلْبِهِ وَهُوَ أَلَدُّ الْخِصَامِ','و از مردم، کسانی هستند که گفتار آنان، در زندگی دنیا مایه اعجاب تو می‌شود؛ (در ظاهر، اظهار محبّت شدید می‌کنند) و خدا را بر آنچه در دل دارند گواه می‌گیرند. (این در حالی است که) آنان، سرسخت‌ترین دشمنانند.'];
ayat[212]=[2,205,'وَإِذَا تَوَلَّىٰ سَعَىٰ فِي الْأَرْضِ لِيُفْسِدَ فِيهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ ۗ وَاللَّهُ لَا يُحِبُّ الْفَسَادَ','(نشانه آن، این است که) هنگامی که روی برمی‌گردانند (و از نزد تو خارج می‌شوند)، در راه فساد در زمین، کوشش می‌کنند، و زراعتها و چهارپایان را نابود می‌سازند؛ (با اینکه می‌دانند) خدا فساد را دوست نمی‌دارد.'];
ayat[213]=[2,206,'وَإِذَا قِيلَ لَهُ اتَّقِ اللَّهَ أَخَذَتْهُ الْعِزَّةُ بِالْإِثْمِ ۚ فَحَسْبُهُ جَهَنَّمُ ۚ وَلَبِئْسَ الْمِهَادُ','و هنگامی که به آنها گفته شود: «از خدا بترسید!» (لجاجت آنان بیشتر می‌شود)، و لجاجت و تعصب، آنها را به گناه می‌کشاند. آتش دوزخ برای آنان کافی است؛ و چه بد جایگاهی است!'];
ayat[214]=[2,207,'وَمِنَ النَّاسِ مَن يَشْرِي نَفْسَهُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ ۗ وَاللَّهُ رَءُوفٌ بِالْعِبَادِ','بعضی از مردم (با ایمان و فداکار، همچون علی (ع) در «لیلة المبیت» به هنگام خفتن در جایگاه پیغمبر ص)، جان خود را به خاطر خشنودی خدا می‌فروشند؛ و خداوند نسبت به بندگان مهربان است.'];
ayat[215]=[2,208,'يَا أَيُّهَا الَّذِينَ آمَنُوا ادْخُلُوا فِي السِّلْمِ كَافَّةً وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُّبِينٌ','ای کسانی که ایمان آورده‌اید همگی در صلح و آشتی درآیید! و از گامهای شیطان، پیروی نکنید؛ که او دشمن آشکار شماست‌'];
ayat[216]=[2,209,'فَإِن زَلَلْتُم مِّن بَعْدِ مَا جَاءَتْكُمُ الْبَيِّنَاتُ فَاعْلَمُوا أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','و اگر بعد از این همه نشانه‌های روشن، که برای شما آمده است، لغزش کردید (و گمراه شدید)، بدانید (از چنگال عدالت خدا، فرار نتوانید کرد؛) که خداوند، توانا و حکیم است.'];
ayat[217]=[2,210,'هَلْ يَنظُرُونَ إِلَّا أَن يَأْتِيَهُمُ اللَّهُ فِي ظُلَلٍ مِّنَ الْغَمَامِ وَالْمَلَائِكَةُ وَقُضِيَ الْأَمْرُ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ','آیا (پیروان فرمان شیطان، پس از این همه نشانه‌ها و برنامه‌های روشن) انتظار دارند که خداوند و فرشتگان، در سایه‌هائی از ابرها به سوی آنان بیایند (و دلایل تازه‌ای در اختیارشان بگذارند؟! با اینکه چنین چیزی محال است!) و همه چیز انجام شده، و همه کارها به سوی خدا بازمی‌گردد.'];
ayat[218]=[2,211,'سَلْ بَنِي إِسْرَائِيلَ كَمْ آتَيْنَاهُم مِّنْ آيَةٍ بَيِّنَةٍ ۗ وَمَن يُبَدِّلْ نِعْمَةَ اللَّهِ مِن بَعْدِ مَا جَاءَتْهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ','از بنی اسرائیل بپرس: «چه اندازه نشانه‌های روشن به آنها دادیم؟» (ولی آنان، نعمتها و امکانات مادی و معنوی را که خداوند در اختیارشان گذاشته بود، در راه غلط به کار گرفتند.) و کسی که نعمت خدا را، پس از آن که به سراغش آمد، تبدیل کند (و در مسیر خلاف به کار گیرد، گرفتار عذاب شدید الهی خواهد شد) که خداوند شدید العقاب است.'];
ayat[219]=[2,212,'زُيِّنَ لِلَّذِينَ كَفَرُوا الْحَيَاةُ الدُّنْيَا وَيَسْخَرُونَ مِنَ الَّذِينَ آمَنُوا ۘ وَالَّذِينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيَامَةِ ۗ وَاللَّهُ يَرْزُقُ مَن يَشَاءُ بِغَيْرِ حِسَابٍ','زندگی دنیا برای کافران زینت داده شده است، از این‌رو افراد باایمان را (که گاهی دستشان تهی است)، مسخره می‌کنند؛ در حالی که پرهیزگاران در قیامت، بالاتر از آنان هستند؛ (چراکه ارزشهای حقیقی در آنجا آشکار می‌گردد، و صورت عینی به خود می‌گیرد؛) و خداوند، هر کس را بخواهد بدون حساب روزی می‌دهد.'];
ayat[220]=[2,213,'كَانَ النَّاسُ أُمَّةً وَاحِدَةً فَبَعَثَ اللَّهُ النَّبِيِّينَ مُبَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ الْكِتَابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيمَا اخْتَلَفُوا فِيهِ ۚ وَمَا اخْتَلَفَ فِيهِ إِلَّا الَّذِينَ أُوتُوهُ مِن بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ بَغْيًا بَيْنَهُمْ ۖ فَهَدَى اللَّهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ ۗ وَاللَّهُ يَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','مردم (در آغاز) یک دسته بودند؛ (و تضادی در میان آنها وجود نداشت. بتدریج جوامع و طبقات پدید آمد و اختلافات و تضادهایی در میان آنها پیدا شد، در این حال) خداوند، پیامبران را برانگیخت؛ تا مردم را بشارت و بیم دهند و کتاب آسمانی، که به سوی حق دعوت می‌کرد، با آنها نازل نمود؛ تا در میان مردم، در آنچه اختلاف داشتند، داوری کند. (افراد باایمان، در آن اختلاف نکردند؛) تنها (گروهی از) کسانی که کتاب را دریافت داشته بودند، و نشانه‌های روشن به آنها رسیده بود، به خاطر انحراف از حق و ستمگری، در آن اختلاف کردند. خداوند، آنهایی را که ایمان آورده بودند، به حقیقت آنچه مورد اختلاف بود، به فرمان خودش، رهبری نمود. (امّا افراد بی‌ایمان، همچنان در گمراهی و اختلاف، باقی ماندند.) و خدا، هر کس را بخواهد، به راه راست هدایت می‌کند.'];
ayat[221]=[2,214,'أَمْ حَسِبْتُمْ أَن تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُم مَّثَلُ الَّذِينَ خَلَوْا مِن قَبْلِكُم ۖ مَّسَّتْهُمُ الْبَأْسَاءُ وَالضَّرَّاءُ وَزُلْزِلُوا حَتَّىٰ يَقُولَ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ مَتَىٰ نَصْرُ اللَّهِ ۗ أَلَا إِنَّ نَصْرَ اللَّهِ قَرِيبٌ','آیا گمان کردید داخل بهشت می‌شوید، بی‌آنکه حوادثی همچون حوادث گذشتگان به شما برسد؟! همانان که گرفتاریها و ناراحتیها به آنها رسید، و آن چنان ناراحت شدند که پیامبر و افرادی که ایمان آورده بودند گفتند: «پس یاری خدا کی خواهد آمد؟!» (در این هنگام، تقاضای یاری از او کردند، و به آنها گفته شد:) آگاه باشید، یاریِ خدا نزدیک است!'];
ayat[222]=[2,215,'يَسْأَلُونَكَ مَاذَا يُنفِقُونَ ۖ قُلْ مَا أَنفَقْتُم مِّنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْأَقْرَبِينَ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ ۗ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ','از تو سؤال می‌کنند چه چیز انفاق کنند؟ بگو: «هر خیر و نیکی (و سرمایه سودمند مادی و معنوی) که انفاق می‌کنید، باید برای پدر و مادر و نزدیکان و یتیمان و مستمندان و درماندگان در راه باشد.» و هر کار خیری که انجام دهید، خداوند از آن آگاه است. (لازم نیست تظاهر کنید، او می‌داند).'];
ayat[223]=[2,216,'كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَّكُمْ ۖ وَعَسَىٰ أَن تَكْرَهُوا شَيْئًا وَهُوَ خَيْرٌ لَّكُمْ ۖ وَعَسَىٰ أَن تُحِبُّوا شَيْئًا وَهُوَ شَرٌّ لَّكُمْ ۗ وَاللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ','جهاد در راه خدا، بر شما مقرّر شد؛ در حالی که برایتان ناخوشایند است. چه بسا چیزی را خوش نداشته باشید، حال آن که خیرِ شما در آن است. و یا چیزی را دوست داشته باشید، حال آنکه شرِّ شما در آن است. و خدا می‌داند، و شما نمی‌دانید.'];
ayat[224]=[2,217,'يَسْأَلُونَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيهِ ۖ قُلْ قِتَالٌ فِيهِ كَبِيرٌ ۖ وَصَدٌّ عَن سَبِيلِ اللَّهِ وَكُفْرٌ بِهِ وَالْمَسْجِدِ الْحَرَامِ وَإِخْرَاجُ أَهْلِهِ مِنْهُ أَكْبَرُ عِندَ اللَّهِ ۚ وَالْفِتْنَةُ أَكْبَرُ مِنَ الْقَتْلِ ۗ وَلَا يَزَالُونَ يُقَاتِلُونَكُمْ حَتَّىٰ يَرُدُّوكُمْ عَن دِينِكُمْ إِنِ اسْتَطَاعُوا ۚ وَمَن يَرْتَدِدْ مِنكُمْ عَن دِينِهِ فَيَمُتْ وَهُوَ كَافِرٌ فَأُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ ۖ وَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','از تو، در باره جنگ کردن در ماه حرام، سؤال می‌کنند؛ بگو: «جنگ در آن، (گناهی) بزرگ است؛ ولی جلوگیری از راه خدا (و گرایش مردم به آیین حق) و کفر ورزیدن نسبت به او و هتک احترام مسجد الحرام، و اخراج ساکنان آن، نزد خداوند مهمتر از آن است؛ و ایجاد فتنه، (و محیط نامساعد، که مردم را به کفر، تشویق و از ایمان بازمی‌دارد) حتّی از قتل بالاتر است. و مشرکان، پیوسته با شما می‌جنگند، تا اگر بتوانند شما را از آیینتان برگردانند؛ ولی کسی که از آیینش برگردد، و در حال کفر بمیرد، تمام اعمال نیک (گذشته) او، در دنیا و آخرت، برباد می‌رود؛ و آنان اهل دوزخند؛ و همیشه در آن خواهند بود.'];
ayat[225]=[2,218,'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ أُولَٰئِكَ يَرْجُونَ رَحْمَتَ اللَّهِ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','کسانی که ایمان آورده و کسانی که هجرت کرده و در راه خدا جهاد نموده‌اند، آنها امید به رحمت پروردگار دارند و خداوند آمرزنده و مهربان است.'];
ayat[226]=[2,219,'۞ يَسْأَلُونَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِ ۖ قُلْ فِيهِمَا إِثْمٌ كَبِيرٌ وَمَنَافِعُ لِلنَّاسِ وَإِثْمُهُمَا أَكْبَرُ مِن نَّفْعِهِمَا ۗ وَيَسْأَلُونَكَ مَاذَا يُنفِقُونَ قُلِ الْعَفْوَ ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ','در باره شراب و قمار از تو سؤال می‌کنند، بگو: «در آنها گناه و زیان بزرگی است؛ و منافعی (از نظر مادی) برای مردم در بردارد؛ (ولی) گناه آنها از نفعشان بیشتر است. و از تو می‌پرسند چه چیز انفاق کنند؟ بگو: از مازاد نیازمندی خود.» اینچنین خداوند آیات را برای شما روشن می‌سازد، شاید اندیشه کنید!'];
ayat[227]=[2,220,'فِي الدُّنْيَا وَالْآخِرَةِ ۗ وَيَسْأَلُونَكَ عَنِ الْيَتَامَىٰ ۖ قُلْ إِصْلَاحٌ لَّهُمْ خَيْرٌ ۖ وَإِن تُخَالِطُوهُمْ فَإِخْوَانُكُمْ ۚ وَاللَّهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ ۚ وَلَوْ شَاءَ اللَّهُ لَأَعْنَتَكُمْ ۚ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','(تا اندیشه کنید) درباره دنیا و آخرت! و از تو در باره یتیمان سؤال می‌کنند، بگو: «اصلاح کار آنان بهتر است. و اگر زندگی خود را با زندگی آنان بیامیزید، (مانعی ندارد؛) آنها برادر (دینی) شما هستند.» (و همچون یک برادر با آنها رفتار کنید!) خداوند، مفسدان را از مصلحان، بازمی‌شناسد. و اگر خدا بخواهد، شما را به زحمت می‌اندازد؛ (و دستور می‌دهد در عین سرپرستی یتیمان، زندگی و اموال آنها را بکلی از اموال خود، جدا سازید؛ ولی خداوند چنین نمی‌کند؛) زیرا او توانا و حکیم است.'];
ayat[228]=[2,221,'وَلَا تَنكِحُوا الْمُشْرِكَاتِ حَتَّىٰ يُؤْمِنَّ ۚ وَلَأَمَةٌ مُّؤْمِنَةٌ خَيْرٌ مِّن مُّشْرِكَةٍ وَلَوْ أَعْجَبَتْكُمْ ۗ وَلَا تُنكِحُوا الْمُشْرِكِينَ حَتَّىٰ يُؤْمِنُوا ۚ وَلَعَبْدٌ مُّؤْمِنٌ خَيْرٌ مِّن مُّشْرِكٍ وَلَوْ أَعْجَبَكُمْ ۗ أُولَٰئِكَ يَدْعُونَ إِلَى النَّارِ ۖ وَاللَّهُ يَدْعُو إِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِإِذْنِهِ ۖ وَيُبَيِّنُ آيَاتِهِ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ','و با زنان مشرک و بت‌پرست، تا ایمان نیاورده‌اند، ازدواج نکنید! (اگر چه جز به ازدواج با کنیزان، دسترسی نداشته باشید؛ زیرا) کنیز باایمان، از زن آزاد بت‌پرست، بهتر است؛ هر چند (زیبایی، یا ثروت، یا موقعیت او) شما را به شگفتی آورد. و زنان خود را به ازدواج مردان بت‌پرست، تا ایمان نیاورده‌اند، در نیاورید! (اگر چه ناچار شوید آنها را به همسری غلامان باایمان درآورید؛ زیرا) یک غلام باایمان، از یک مرد آزاد بت‌پرست، بهتر است؛ هر چند (مال و موقعیت و زیبایی او،) شما را به شگفتی آورد. آنها دعوت به سوی آتش می‌کنند؛ و خدا دعوت به بهشت و آمرزش به فرمان خود می‌نماید، و آیات خویش را برای مردم روشن می‌سازد؛ شاید متذکر شوند!'];
ayat[229]=[2,222,'وَيَسْأَلُونَكَ عَنِ الْمَحِيضِ ۖ قُلْ هُوَ أَذًى فَاعْتَزِلُوا النِّسَاءَ فِي الْمَحِيضِ ۖ وَلَا تَقْرَبُوهُنَّ حَتَّىٰ يَطْهُرْنَ ۖ فَإِذَا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اللَّهُ ۚ إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ وَيُحِبُّ الْمُتَطَهِّرِينَ','و از تو، در باره خون حیض سؤال می‌کنند، بگو: «چیز زیانبار و آلوده‌ای است؛ از این‌رو در حالت قاعدگی، از آنان کناره‌گیری کنید! و با آنها نزدیکی ننماید، تا پاک شوند! و هنگامی که پاک شدند، از طریقی که خدا به شما فرمان داده، با آنها آمیزش کنید! خداوند، توبه‌کنندگان را دوست دارد، و پاکان را (نیز) دوست دارد.'];
ayat[230]=[2,223,'نِسَاؤُكُمْ حَرْثٌ لَّكُمْ فَأْتُوا حَرْثَكُمْ أَنَّىٰ شِئْتُمْ ۖ وَقَدِّمُوا لِأَنفُسِكُمْ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّكُم مُّلَاقُوهُ ۗ وَبَشِّرِ الْمُؤْمِنِينَ','زنان شما، محل بذرافشانی شما هستند؛ پس هر زمان که بخواهید، می‌توانید با آنها آمیزش کنید. و (سعی نمائید از این فرصت بهره گرفته، با پرورش فرزندان صالح) اثر نیکی برای خود، از پیش بفرستید! و از خدا بپرهیزید و بدانید او را ملاقات خواهید کرد و به مؤمنان، بشارت ده!'];
ayat[231]=[2,224,'وَلَا تَجْعَلُوا اللَّهَ عُرْضَةً لِّأَيْمَانِكُمْ أَن تَبَرُّوا وَتَتَّقُوا وَتُصْلِحُوا بَيْنَ النَّاسِ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','خدا را در معرض سوگندهای خود قرار ندهید! و برای اینکه نیکی کنید، و تقوا پیشه سازید، و در میان مردم اصلاح کنید (سوگند یاد ننمایید)! و خداوند شنوا و داناست.'];
ayat[232]=[2,225,'لَّا يُؤَاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ وَلَٰكِن يُؤَاخِذُكُم بِمَا كَسَبَتْ قُلُوبُكُمْ ۗ وَاللَّهُ غَفُورٌ حَلِيمٌ','خداوند شما را به خاطر سوگندهایی که بدون توجه یاد می‌کنید، مؤاخذه نخواهد کرد، اما به آنچه دلهای شما کسب کرده، (و سوگندهایی که از روی اراده و اختیار، یاد می‌کنید،) مؤاخذه می‌کند. و خداوند، آمرزنده و بردبار است.'];
ayat[233]=[2,226,'لِّلَّذِينَ يُؤْلُونَ مِن نِّسَائِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٍ ۖ فَإِن فَاءُوا فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','کسانی که زنان خود را «ایلاء» می‌نمایند [= سوگند یاد می‌کنند که با آنها، آمیزش‌جنسی ننمایند،] حق دارند چهار ماه انتظار بکشند. (و در ضمن این چهار ماه، وضع خود را با همسر خویش، از نظر ادامه زندگی یا طلاق، روشن سازند.) اگر (در این فرصت،) بازگشت کنند، (چیزی بر آنها نیست؛ زیرا) خداوند، آمرزنده و مهربان است.'];
ayat[234]=[2,227,'وَإِنْ عَزَمُوا الطَّلَاقَ فَإِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ','و اگر تصمیم به جدایی گرفتند، (آن هم با شرایطش مانعی ندارد؛) خداوند شنوا و داناست.'];
ayat[235]=[2,228,'وَالْمُطَلَّقَاتُ يَتَرَبَّصْنَ بِأَنفُسِهِنَّ ثَلَاثَةَ قُرُوءٍ ۚ وَلَا يَحِلُّ لَهُنَّ أَن يَكْتُمْنَ مَا خَلَقَ اللَّهُ فِي أَرْحَامِهِنَّ إِن كُنَّ يُؤْمِنَّ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنْ أَرَادُوا إِصْلَاحًا ۚ وَلَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوفِ ۚ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ','زنان مطلقه، باید به مدت سه مرتبه عادت ماهانه دیدن (و پاک شدن) انتظار بکشند! [= عده نگه دارند] و اگر به خدا و روز رستاخیز، ایمان دارند، برای آنها حلال نیست که آنچه را خدا در رحمهایشان آفریده، کتمان کنند. و همسرانشان، برای بازگرداندن آنها (و از سرگرفتن زندگی زناشویی) در این مدت، (از دیگران) سزاوارترند؛ در صورتی که (براستی) خواهان اصلاح باشند. و برای آنان، همانند وظایفی که بر دوش آنهاست، حقوق شایسته‌ای قرار داده شده؛ و مردان بر آنان برتری دارند؛ و خداوند توانا و حکیم است.'];
ayat[236]=[2,229,'الطَّلَاقُ مَرَّتَانِ ۖ فَإِمْسَاكٌ بِمَعْرُوفٍ أَوْ تَسْرِيحٌ بِإِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ أَن تَأْخُذُوا مِمَّا آتَيْتُمُوهُنَّ شَيْئًا إِلَّا أَن يَخَافَا أَلَّا يُقِيمَا حُدُودَ اللَّهِ ۖ فَإِنْ خِفْتُمْ أَلَّا يُقِيمَا حُدُودَ اللَّهِ فَلَا جُنَاحَ عَلَيْهِمَا فِيمَا افْتَدَتْ بِهِ ۗ تِلْكَ حُدُودُ اللَّهِ فَلَا تَعْتَدُوهَا ۚ وَمَن يَتَعَدَّ حُدُودَ اللَّهِ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ','طلاق، (طلاقی که رجوع و بازگشت دارد،) دو مرتبه است؛ (و در هر مرتبه،) باید به طور شایسته همسر خود را نگاهداری کند (و آشتی نماید)، یا با نیکی او را رها سازد (و از او جدا شود). و برای شما حلال نیست که چیزی از آنچه به آنها داده‌اید، پس بگیرید؛ مگر اینکه دو همسر، بترسند که حدود الهی را برپا ندارند. اگر بترسید که حدود الهی را رعایت نکنند، مانعی برای آنها نیست که زن، فدیه و عوضی بپردازد (و طلاق بگیرد). اینها حدود و مرزهای الهی است؛ از آن، تجاوز نکنید! و هر کس از آن تجاوز کند، ستمگر است.'];
ayat[237]=[2,230,'فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُ مِن بَعْدُ حَتَّىٰ تَنكِحَ زَوْجًا غَيْرَهُ ۗ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَا أَن يَتَرَاجَعَا إِن ظَنَّا أَن يُقِيمَا حُدُودَ اللَّهِ ۗ وَتِلْكَ حُدُودُ اللَّهِ يُبَيِّنُهَا لِقَوْمٍ يَعْلَمُونَ','اگر (بعد از دو طلاق و رجوع، بار دیگر) او را طلاق داد، از آن به بعد، زن بر او حلال نخواهد بود؛ مگر اینکه همسر دیگری انتخاب کند (و با او، آمیزش‌جنسی نماید. در این صورت،) اگر (همسر دوم) او را طلاق گفت، گناهی ندارد که بازگشت کنند؛ (و با همسر اول، دوباره ازدواج نماید؛) در صورتی که امید داشته باشند که حدود الهی را محترم میشمرند. اینها حدود الهی است که (خدا) آن را برای گروهی که آگاهند، بیان می‌نماید.'];
ayat[238]=[2,231,'وَإِذَا طَلَّقْتُمُ النِّسَاءَ فَبَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍ ۚ وَلَا تُمْسِكُوهُنَّ ضِرَارًا لِّتَعْتَدُوا ۚ وَمَن يَفْعَلْ ذَٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُ ۚ وَلَا تَتَّخِذُوا آيَاتِ اللَّهِ هُزُوًا ۚ وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ وَمَا أَنزَلَ عَلَيْكُم مِّنَ الْكِتَابِ وَالْحِكْمَةِ يَعِظُكُم بِهِ ۚ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ','و هنگامی که زنان را طلاق دادید، و به آخرین روزهای «عدّه» رسیدند، یا به طرز صحیحی آنها را نگاه دارید (و آشتی کنید)، و یا به طرز پسندیده‌ای آنها را رها سازید! و هیچ‌گاه به خاطر زیان رساندن و تعدّی کردن، آنها را نگاه ندارید! و کسی که چنین کند، به خویشتن ستم کرده است. (و با این اعمال، و سوء استفاده از قوانین الهی،) آیات خدا را به استهزا نگیرید! و به یاد بیاورید نعمت خدا را بر خود، و کتاب آسمانی و علم و دانشی که بر شما نازل کرده، و شما را با آن، پند می‌دهد! و از خدا بپرهیزید! و بدانید خداوند از هر چیزی آگاه است (و از نیات کسانی که از قوانین او، سوء استفاده می‌کنند، با خبر است)!'];
ayat[239]=[2,232,'وَإِذَا طَلَّقْتُمُ النِّسَاءَ فَبَلَغْنَ أَجَلَهُنَّ فَلَا تَعْضُلُوهُنَّ أَن يَنكِحْنَ أَزْوَاجَهُنَّ إِذَا تَرَاضَوْا بَيْنَهُم بِالْمَعْرُوفِ ۗ ذَٰلِكَ يُوعَظُ بِهِ مَن كَانَ مِنكُمْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۗ ذَٰلِكُمْ أَزْكَىٰ لَكُمْ وَأَطْهَرُ ۗ وَاللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ','و هنگامی که زنان را طلاق دادید و عدّه خود را به پایان رساندند، مانع آنها نشوید که با همسران (سابق) خویش، ازدواج کنند! اگر در میان آنان، به طرز پسندیده‌ای تراضی برقرار گردد. این دستوری است که تنها افرادی از شما، که ایمان به خدا و روز قیامت دارند، از آن، پند می‌گیرند (و به آن، عمل می‌کنند). این (دستور)، برای رشد (خانواده‌های) شما مؤثرتر، و برای شستن آلودگیها مفیدتر است؛ و خدا می‌داند و شما نمی‌دانید.'];
ayat[240]=[2,233,'۞ وَالْوَالِدَاتُ يُرْضِعْنَ أَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ ۖ لِمَنْ أَرَادَ أَن يُتِمَّ الرَّضَاعَةَ ۚ وَعَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوفِ ۚ لَا تُكَلَّفُ نَفْسٌ إِلَّا وُسْعَهَا ۚ لَا تُضَارَّ وَالِدَةٌ بِوَلَدِهَا وَلَا مَوْلُودٌ لَّهُ بِوَلَدِهِ ۚ وَعَلَى الْوَارِثِ مِثْلُ ذَٰلِكَ ۗ فَإِنْ أَرَادَا فِصَالًا عَن تَرَاضٍ مِّنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَا ۗ وَإِنْ أَرَدتُّمْ أَن تَسْتَرْضِعُوا أَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ إِذَا سَلَّمْتُم مَّا آتَيْتُم بِالْمَعْرُوفِ ۗ وَاتَّقُوا اللَّهَ وَاعْلَمُوا أَنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ','مادران، فرزندان خود را دو سال تمام، شیر می‌دهند. (این) برای کسی است که بخواهد دوران شیرخوارگی را تکمیل کند. و بر آن کس که فرزند برای او متولّد شده [= پدر]، لازم است خوراک و پوشاک مادر را به طور شایسته (در مدت شیر دادن بپردازد؛ حتی اگر طلاق گرفته باشد.) هیچ کس موظّف به بیش از مقدار توانایی خود نیست! نه مادر (به خاطر اختلاف با پدر) حق ضرر زدن به کودک را دارد، و نه پدر. و بر وارث او نیز لازم است این کار را انجام دهد [= هزینه مادر را در دوران شیرخوارگی تأمین نماید]. و اگر آن دو، با رضایت یکدیگر و مشورت، بخواهند کودک را (زودتر) از شیر بازگیرند، گناهی بر آنها نیست. و اگر (با عدم توانایی، یا عدم موافقت مادر) خواستید دایه‌ای برای فرزندان خود بگیرید، گناهی بر شما نیست؛ به شرط اینکه حق گذشته مادر را به طور شایسته بپردازید. و از (مخالفت فرمان) خدا بپرهیزید! و بدانید خدا، به آنچه انجام می‌دهید، بیناست!'];
ayat[241]=[2,234,'وَالَّذِينَ يُتَوَفَّوْنَ مِنكُمْ وَيَذَرُونَ أَزْوَاجًا يَتَرَبَّصْنَ بِأَنفُسِهِنَّ أَرْبَعَةَ أَشْهُرٍ وَعَشْرًا ۖ فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيمَا فَعَلْنَ فِي أَنفُسِهِنَّ بِالْمَعْرُوفِ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','و کسانی که از شما می‌میرند و همسرانی باقی می‌گذارند، باید چهار ماه و ده روز، انتظار بکشند (و عدّه نگه دارند)! و هنگامی که به آخر مدتشان رسیدند، گناهی بر شما نیست که هر چه می‌خواهند، در باره خودشان به طور شایسته انجام دهند (و با مرد دلخواه خود، ازدواج کنند). و خدا به آنچه عمل می‌کنید، آگاه است.'];
ayat[242]=[2,235,'وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا عَرَّضْتُم بِهِ مِنْ خِطْبَةِ النِّسَاءِ أَوْ أَكْنَنتُمْ فِي أَنفُسِكُمْ ۚ عَلِمَ اللَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّا أَن تَقُولُوا قَوْلًا مَّعْرُوفًا ۚ وَلَا تَعْزِمُوا عُقْدَةَ النِّكَاحِ حَتَّىٰ يَبْلُغَ الْكِتَابُ أَجَلَهُ ۚ وَاعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي أَنفُسِكُمْ فَاحْذَرُوهُ ۚ وَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ حَلِيمٌ','و گناهی بر شما نیست که به طور کنایه، (از زنانی که همسرانشان مرده‌اند) خواستگاری کنید، و یا در دل تصمیم بر این کار بگیرید (بدون اینکه آن را اظهار کنید). خداوند می‌دانست شما به یاد آنها خواهید افتاد؛ (و با خواسته طبیعی شما به شکل معقول، مخالف نیست؛) ولی پنهانی با آنها قرار زناشویی نگذارید، مگر اینکه به طرز پسندیده‌ای (به طور کنایه) اظهار کنید! (ولی در هر حال،) اقدام به ازدواج ننمایید، تا عدّه آنها سرآید! و بدانید خداوند آنچه را در دل دارید، می‌داند! از مخالفت او بپرهیزید! و بدانید خداوند، آمرزنده و بردبار است (و در مجازات بندگان، عجله نمی‌کند)!'];
ayat[243]=[2,236,'لَّا جُنَاحَ عَلَيْكُمْ إِن طَلَّقْتُمُ النِّسَاءَ مَا لَمْ تَمَسُّوهُنَّ أَوْ تَفْرِضُوا لَهُنَّ فَرِيضَةً ۚ وَمَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَعَلَى الْمُقْتِرِ قَدَرُهُ مَتَاعًا بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُحْسِنِينَ','اگر زنان را قبل از آمیزش‌جنسی یا تعیین مهر، (به عللی) طلاق دهید، گناهی بر شما نیست. (و در این موقع،) آنها را (با هدیه‌ای مناسب،) بهره‌مند سازید! آن کس که توانایی دارد، به اندازه تواناییش، و آن کس که تنگدست است، به اندازه خودش، هدیه‌ای شایسته (که مناسب حال دهنده و گیرنده باشد) بدهد! و این بر نیکوکاران، الزامی است.'];
ayat[244]=[2,237,'وَإِن طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةً فَنِصْفُ مَا فَرَضْتُمْ إِلَّا أَن يَعْفُونَ أَوْ يَعْفُوَ الَّذِي بِيَدِهِ عُقْدَةُ النِّكَاحِ ۚ وَأَن تَعْفُوا أَقْرَبُ لِلتَّقْوَىٰ ۚ وَلَا تَنسَوُا الْفَضْلَ بَيْنَكُمْ ۚ إِنَّ اللَّهَ بِمَا تَعْمَلُونَ بَصِيرٌ','و اگر آنان را، پیش از آن که با آنها تماس بگیرید و (آمیزش‌جنسی کنید) طلاق دهید، در حالی که مهری برای آنها تعیین کرده‌اید، (لازم است) نصف آنچه را تعیین کرده‌اید (به آنها بدهید) مگر اینکه آنها (حق خود را) ببخشند؛ یا (در صورتی که صغیر و سفیه باشند، ولیّ آنها، یعنی) آن کس که گره ازدواج به دست اوست، آن را ببخشد. و گذشت کردن شما (و بخشیدن تمام مهر به آنها) به پرهیزکاری نزدیکتر است، و گذشت و نیکوکاری را در میان خود فراموش نکنید، که خداوند به آنچه انجام می‌دهید، بیناست!'];
ayat[245]=[2,238,'حَافِظُوا عَلَى الصَّلَوَاتِ وَالصَّلَاةِ الْوُسْطَىٰ وَقُومُوا لِلَّهِ قَانِتِينَ','در انجام همه نمازها، (به خصوص) نماز وسطی [= نماز ظهر] کوشا باشید! و از روی خضوع و اطاعت، برای خدا بپاخیزید!'];
ayat[246]=[2,239,'فَإِنْ خِفْتُمْ فَرِجَالًا أَوْ رُكْبَانًا ۖ فَإِذَا أَمِنتُمْ فَاذْكُرُوا اللَّهَ كَمَا عَلَّمَكُم مَّا لَمْ تَكُونُوا تَعْلَمُونَ','و اگر (به خاطر جنگ، یا خطر دیگری) بترسید، (نماز را) در حال پیاده یا سواره انجام دهید! اما هنگامی که امنیت خود را بازیافتید، خدا را یاد کنید! [= نماز را به صورت معمولی بخوانید!] همان‌گونه که خداوند، چیزهایی را که نمی‌دانستید، به شما تعلیم داد.'];
ayat[247]=[2,240,'وَالَّذِينَ يُتَوَفَّوْنَ مِنكُمْ وَيَذَرُونَ أَزْوَاجًا وَصِيَّةً لِّأَزْوَاجِهِم مَّتَاعًا إِلَى الْحَوْلِ غَيْرَ إِخْرَاجٍ ۚ فَإِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِي مَا فَعَلْنَ فِي أَنفُسِهِنَّ مِن مَّعْرُوفٍ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ','و کسانی که از شما در آستانه مرگ قرارمی‌گیرند و همسرانی از خود به‌جا می‌گذارند، باید برای همسران خود وصیت کنند که تا یک سال، آنها را (با پرداختن هزینه زندگی) بهره‌مند سازند؛ به شرط اینکه آنها (از خانه شوهر) بیرون نروند (و اقدام به ازدواج مجدد نکنند). و اگر بیرون روند، (حقی در هزینه ندارند؛ ولی) گناهی بر شما نیست نسبت به آنچه در باره خود، به طور شایسته انجام می‌دهند. و خداوند، توانا و حکیم است.'];
ayat[248]=[2,241,'وَلِلْمُطَلَّقَاتِ مَتَاعٌ بِالْمَعْرُوفِ ۖ حَقًّا عَلَى الْمُتَّقِينَ','و برای زنان مطلقه، هدیه مناسبی لازم است (که از طرف شوهر، پرداخت گردد). این، حقی است بر مردان پرهیزکار.'];
ayat[249]=[2,242,'كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَعْقِلُونَ','این چنین، خداوند آیات خود را برای شما شرح می‌دهد؛ شاید اندیشه کنید!'];
ayat[250]=[2,243,'۞ أَلَمْ تَرَ إِلَى الَّذِينَ خَرَجُوا مِن دِيَارِهِمْ وَهُمْ أُلُوفٌ حَذَرَ الْمَوْتِ فَقَالَ لَهُمُ اللَّهُ مُوتُوا ثُمَّ أَحْيَاهُمْ ۚ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ','آیا ندیدی جمعیتی را که از ترس مرگ، از خانه‌های خود فرار کردند؟ و آنان، هزارها نفر بودند (که به بهانه بیماری طاعون، از شرکت در میدان جهاد خودداری نمودند). خداوند به آنها گفت: بمیرید! (و به همان بیماری، که آن را بهانه قرار داده بودند، مردند.) سپس خدا آنها را زنده کرد؛ (و ماجرای زندگی آنها را درس عبرتی برای آیندگان قرار داد.) خداوند نسبت به بندگان خود احسان می‌کند؛ ولی بیشتر مردم، شکر (او را) بجا نمی‌آورند.'];
ayat[251]=[2,244,'وَقَاتِلُوا فِي سَبِيلِ اللَّهِ وَاعْلَمُوا أَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ','و در راه خدا، پیکار کنید! و بدانید خداوند، شنوا و داناست.'];
ayat[252]=[2,245,'مَّن ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ أَضْعَافًا كَثِيرَةً ۚ وَاللَّهُ يَقْبِضُ وَيَبْسُطُ وَإِلَيْهِ تُرْجَعُونَ','کیست که به خدا «قرض الحسنه‌ای» دهد، (و از اموالی که خدا به او بخشیده، انفاق کند،) تا آن را برای او، چندین برابر کند؟ و خداوند است (که روزی بندگان را) محدود یا گسترده می‌سازد؛ (و انفاق، هرگز باعث کمبود روزی آنها نمی‌شود). و به سوی او باز می‌گردید (و پاداش خود را خواهید گرفت).'];
ayat[253]=[2,246,'أَلَمْ تَرَ إِلَى الْمَلَإِ مِن بَنِي إِسْرَائِيلَ مِن بَعْدِ مُوسَىٰ إِذْ قَالُوا لِنَبِيٍّ لَّهُمُ ابْعَثْ لَنَا مَلِكًا نُّقَاتِلْ فِي سَبِيلِ اللَّهِ ۖ قَالَ هَلْ عَسَيْتُمْ إِن كُتِبَ عَلَيْكُمُ الْقِتَالُ أَلَّا تُقَاتِلُوا ۖ قَالُوا وَمَا لَنَا أَلَّا نُقَاتِلَ فِي سَبِيلِ اللَّهِ وَقَدْ أُخْرِجْنَا مِن دِيَارِنَا وَأَبْنَائِنَا ۖ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا إِلَّا قَلِيلًا مِّنْهُمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ','آیا مشاهده نکردی جمعی از بنی اسرائیل را بعد از موسی، که به پیامبر خود گفتند: «زمامدار (و فرماندهی) برای ما انتخاب کن! تا (زیر فرمان او) در راه خدا پیکار کنیم. پیامبر آنها گفت: «شاید اگر دستور پیکار به شما داده شود، (سرپیچی کنید، و) در راه خدا، جهاد و پیکار نکنید!» گفتند: «چگونه ممکن است در راه خدا پیکار نکنیم، در حالی که از خانه‌ها و فرزندانمان رانده شده‌ایم، (و شهرهای ما به وسیله دشمن اشغال، و فرزندان ما اسیر شده‌اند)؟!» اما هنگامی که دستور پیکار به آنها داده شد، جز عدّه کمی از آنان، همه سرپیچی کردند. و خداوند از ستمکاران، آگاه است.'];
ayat[254]=[2,247,'وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ اللَّهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكًا ۚ قَالُوا أَنَّىٰ يَكُونُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِّنَ الْمَالِ ۚ قَالَ إِنَّ اللَّهَ اصْطَفَاهُ عَلَيْكُمْ وَزَادَهُ بَسْطَةً فِي الْعِلْمِ وَالْجِسْمِ ۖ وَاللَّهُ يُؤْتِي مُلْكَهُ مَن يَشَاءُ ۚ وَاللَّهُ وَاسِعٌ عَلِيمٌ','و پیامبرشان به آنها گفت: «خداوند (*طالوت*) را برای زمامداری شما مبعوث (و انتخاب) کرده است.» گفتند: «چگونه او بر ما حکومت کند، با اینکه ما از او شایسته‌تریم، و او ثروت زیادی ندارد؟!» گفت: «خدا او را بر شما برگزیده، و او را در علم و (قدرت) جسم، وسعت بخشیده است. خداوند، ملکش را به هر کس بخواهد، می‌بخشد؛ و احسان خداوند، وسیع است؛ و (از لیاقت افراد برای منصب‌ها) آگاه است.»'];
ayat[255]=[2,248,'وَقَالَ لَهُمْ نَبِيُّهُمْ إِنَّ آيَةَ مُلْكِهِ أَن يَأْتِيَكُمُ التَّابُوتُ فِيهِ سَكِينَةٌ مِّن رَّبِّكُمْ وَبَقِيَّةٌ مِّمَّا تَرَكَ آلُ مُوسَىٰ وَآلُ هَارُونَ تَحْمِلُهُ الْمَلَائِكَةُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لَّكُمْ إِن كُنتُم مُّؤْمِنِينَ','و پیامبرشان به آنها گفت: «نشانه حکومت او، این است که (*صندوق عهد*) به سوی شما خواهد آمد. (همان صندوقی که) در آن، آرامشی از پروردگار شما، و یادگارهای خاندان موسی و هارون قرار دارد؛ در حالی که فرشتگان، آن را حمل می‌کنند. در این موضوع، نشانه‌ای (روشن) برای شماست؛ اگر ایمان داشته باشید.»'];
ayat[256]=[2,249,'فَلَمَّا فَصَلَ طَالُوتُ بِالْجُنُودِ قَالَ إِنَّ اللَّهَ مُبْتَلِيكُم بِنَهَرٍ فَمَن شَرِبَ مِنْهُ فَلَيْسَ مِنِّي وَمَن لَّمْ يَطْعَمْهُ فَإِنَّهُ مِنِّي إِلَّا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ ۚ فَشَرِبُوا مِنْهُ إِلَّا قَلِيلًا مِّنْهُمْ ۚ فَلَمَّا جَاوَزَهُ هُوَ وَالَّذِينَ آمَنُوا مَعَهُ قَالُوا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوتَ وَجُنُودِهِ ۚ قَالَ الَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَاقُو اللَّهِ كَم مِّن فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللَّهِ ۗ وَاللَّهُ مَعَ الصَّابِرِينَ','و هنگامی که طالوت (به فرماندهی لشکر بنی اسرائیل منصوب شد، و) سپاهیان را با خود بیرون برد، به آنها گفت: «خداوند، شما را به وسیله یک نهر آب، آزمایش می‌کند؛ آنها (که به هنگام تشنگی،) از آن بنوشند، از من نیستند؛ و آنها که جز یک پیمانه با دست خود، بیشتر از آن نخورند، از من هستند» جز عده کمی، همگی از آن آب نوشیدند. سپس هنگامی که او، و افرادی که با او ایمان آورده بودند، (و از بوته آزمایش، سالم به‌در آمدند،) از آن نهر گذشتند، (از کمی نفرات خود، ناراحت شدند؛ و عده‌ای) گفتند: «امروز، ما توانایی مقابله با (*جالوت*) و سپاهیان او را نداریم.» اما آنها که می‌دانستند خدا را ملاقات خواهند کرد (و به روز رستاخیز، ایمان داشتند) گفتند: «چه بسیار گروه‌های کوچکی که به فرمان خدا، بر گروه‌های عظیمی پیروز شدند!» و خداوند، با صابران و استقامت‌کنندگان) است.'];
ayat[257]=[2,250,'وَلَمَّا بَرَزُوا لِجَالُوتَ وَجُنُودِهِ قَالُوا رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْرًا وَثَبِّتْ أَقْدَامَنَا وَانصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ','و هنگامی که در برابر (*جالوت*) و سپاهیان او قرارگرفتند گفتند: «پروردگارا! پیمانه شکیبایی و استقامت را بر ما بریز! و قدمهای ما را ثابت بدار! و ما را بر جمعیّت کافران، پیروز بگردان!'];
ayat[258]=[2,251,'فَهَزَمُوهُم بِإِذْنِ اللَّهِ وَقَتَلَ دَاوُودُ جَالُوتَ وَآتَاهُ اللَّهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُ مِمَّا يَشَاءُ ۗ وَلَوْلَا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُم بِبَعْضٍ لَّفَسَدَتِ الْأَرْضُ وَلَٰكِنَّ اللَّهَ ذُو فَضْلٍ عَلَى الْعَالَمِينَ','سپس به فرمان خدا، آنها سپاه دشمن را به هزیمت واداشتند. و «داوود» (نوجوان نیرومند و شجاعی که در لشکر «طالوت» بود)، «جالوت» را کشت؛ و خداوند، حکومت و دانش را به او بخشید؛ و از آنچه می‌خواست به او تعلیم داد. و اگر خداوند، بعضی از مردم را به وسیله بعضی دیگر دفع نمی‌کرد، زمین را فساد فرامی‌گرفت، ولی خداوند نسبت به جهانیان، لطف و احسان دارد.'];
ayat[259]=[2,252,'تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۚ وَإِنَّكَ لَمِنَ الْمُرْسَلِينَ','اینها، آیات خداست که به حق، بر تو می‌خوانیم؛ و تو از رسولان (ما) هستی.'];
ayat[260]=[2,253,'۞ تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلَىٰ بَعْضٍ ۘ مِّنْهُم مَّن كَلَّمَ اللَّهُ ۖ وَرَفَعَ بَعْضَهُمْ دَرَجَاتٍ ۚ وَآتَيْنَا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنَاتِ وَأَيَّدْنَاهُ بِرُوحِ الْقُدُسِ ۗ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلَ الَّذِينَ مِن بَعْدِهِم مِّن بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ وَلَٰكِنِ اخْتَلَفُوا فَمِنْهُم مَّنْ آمَنَ وَمِنْهُم مَّن كَفَرَ ۚ وَلَوْ شَاءَ اللَّهُ مَا اقْتَتَلُوا وَلَٰكِنَّ اللَّهَ يَفْعَلُ مَا يُرِيدُ','بعضی از آن رسولان را بر بعضی دیگر برتری دادیم؛ برخی از آنها، خدا با او سخن می‌گفت؛ و بعضی را درجاتی برتر داد؛ و به عیسی بن مریم، نشانه‌های روشن دادیم؛ و او را با «روح القدس» تأیید نمودیم؛ (ولی فضیلت و مقام آن پیامبران، مانع اختلاف امتها نشد.) و اگر خدا می‌خواست، کسانی که بعد از آنها بودند، پس از آن همه نشانه‌های روشن که برای آنها آمد، جنگ و ستیز نمی‌کردند؛ (اما خدا مردم را مجبور نساخته؛ و آنها را در پیمودن راه سعادت، آزاد گذارده است؛) ولی این امتها بودند که با هم اختلاف کردند؛ بعضی ایمان آوردند و بعضی کافر شدند؛ (و جنگ و خونریزی بروز کرد. و باز) اگر خدا می‌خواست، با هم پیکار نمی‌کردند؛ ولی خداوند، آنچه را می‌خواهد، (از روی حکمت) انجام می‌دهد (و هیچ‌کس را به قبول چیزی مجبور نمی‌کند).'];
ayat[261]=[2,254,'يَا أَيُّهَا الَّذِينَ آمَنُوا أَنفِقُوا مِمَّا رَزَقْنَاكُم مِّن قَبْلِ أَن يَأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيهِ وَلَا خُلَّةٌ وَلَا شَفَاعَةٌ ۗ وَالْكَافِرُونَ هُمُ الظَّالِمُونَ','ای کسانی که ایمان آورده‌اید! از آنچه به شما روزی داده‌ایم، انفاق کنید! پیش از آنکه روزی فرا رسد که در آن، نه خرید و فروش است (تا بتوانید سعادت و نجات از کیفر را برای خود خریداری کنید)، و نه دوستی (و رفاقتهای مادی سودی دارد)، و نه شفاعت؛ (زیرا شما شایسته شفاعت نخواهید بود.) و کافران، خود ستمگرند؛ (هم به خودشان ستم می‌کنند، هم به دیگران).'];
ayat[262]=[2,255,'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ ۚ لَا تَأْخُذُهُ سِنَةٌ وَلَا نَوْمٌ ۚ لَّهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ مَن ذَا الَّذِي يَشْفَعُ عِندَهُ إِلَّا بِإِذْنِهِ ۚ يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ ۖ وَلَا يُحِيطُونَ بِشَيْءٍ مِّنْ عِلْمِهِ إِلَّا بِمَا شَاءَ ۚ وَسِعَ كُرْسِيُّهُ السَّمَاوَاتِ وَالْأَرْضَ ۖ وَلَا يَئُودُهُ حِفْظُهُمَا ۚ وَهُوَ الْعَلِيُّ الْعَظِيمُ','هیچ معبودی نیست جز خداوند یگانه زنده، که قائم به ذات خویش است، و موجودات دیگر، قائم به او هستند؛ هیچگاه خواب سبک و سنگینی او را فرانمی‌گیرد؛ (و لحظه‌ای از تدبیر جهان هستی، غافل نمی‌ماند؛) آنچه در آسمانها و آنچه در زمین است، از آن اوست؛ کیست که در نزد او، جز به فرمان او شفاعت کند؟! (بنابراین، شفاعت شفاعت‌کنندگان، برای آنها که شایسته شفاعتند، از مالکیت مطلقه او نمی‌کاهد.) آنچه را در پیش روی آنها [= بندگان‌] و پشت سرشان است می‌داند؛ (و گذشته و آینده، در پیشگاه علم او، یکسان است.) و کسی از علم او آگاه نمی‌گردد؛ جز به مقداری که او بخواهد. (اوست که به همه چیز آگاه است؛ و علم و دانش محدود دیگران، پرتوی از علم بی‌پایان و نامحدود اوست.) تخت (حکومت) او، آسمانها و زمین را دربرگرفته؛ و نگاهداری آن دو [= آسمان و زمین]، او را خسته نمیکند. بلندی مقام و عظمت، مخصوص اوست.'];
ayat[263]=[2,256,'لَا إِكْرَاهَ فِي الدِّينِ ۖ قَد تَّبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَن يَكْفُرْ بِالطَّاغُوتِ وَيُؤْمِن بِاللَّهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَىٰ لَا انفِصَامَ لَهَا ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','در قبول دین، اکراهی نیست. (زیرا) راه درست از راه انحرافی، روشن شده است. بنابر این، کسی که به طاغوت [= بت و شیطان، و هر موجود طغیانگر] کافر شود و به خدا ایمان آورد، به دستگیره محکمی چنگ زده است، که گسستن برای آن نیست. و خداوند، شنوا و داناست.'];
ayat[264]=[2,257,'اللَّهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِجُهُم مِّنَ الظُّلُمَاتِ إِلَى النُّورِ ۖ وَالَّذِينَ كَفَرُوا أَوْلِيَاؤُهُمُ الطَّاغُوتُ يُخْرِجُونَهُم مِّنَ النُّورِ إِلَى الظُّلُمَاتِ ۗ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','خداوند، ولی و سرپرست کسانی است که ایمان آورده‌اند؛ آنها را از ظلمتها، به سوی نور بیرون می‌برد. (اما) کسانی که کافر شدند، اولیای آنها طاغوتها هستند؛ که آنها را از نور، به سوی ظلمتها بیرون می‌برند؛ آنها اهل آتشند و همیشه در آن خواهند ماند.'];
ayat[265]=[2,258,'أَلَمْ تَرَ إِلَى الَّذِي حَاجَّ إِبْرَاهِيمَ فِي رَبِّهِ أَنْ آتَاهُ اللَّهُ الْمُلْكَ إِذْ قَالَ إِبْرَاهِيمُ رَبِّيَ الَّذِي يُحْيِي وَيُمِيتُ قَالَ أَنَا أُحْيِي وَأُمِيتُ ۖ قَالَ إِبْرَاهِيمُ فَإِنَّ اللَّهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِي كَفَرَ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','آیا ندیدی (و آگاهی نداری از) کسی [= نمرود] که با ابراهیم در باره پروردگارش محاجه و گفتگو کرد؟ زیرا خداوند به او حکومت داده بود؛ (و بر اثر کمی ظرفیت، از باده غرور سرمست شده بود؛) هنگامی که ابراهیم گفت: «خدای من آن کسی است که زنده می‌کند و می‌میراند.» او گفت: «من نیز زنده می‌کنم و می‌میرانم!» (و برای اثبات این کار و مشتبه‌ساختن بر مردم دستور داد دو زندانی را حاضر کردند، فرمان آزادی یکی و قتل دیگری را داد) ابراهیم گفت: «خداوند، خورشید را از افق مشرق می‌آورد؛ (اگر راست می‌گویی که حاکم بر جهان هستی تویی،) خورشید را از مغرب بیاور!» (در اینجا) آن مرد کافر، مبهوت و وامانده شد. و خداوند، قوم ستمگر را هدایت نمی‌کند.'];
ayat[266]=[2,259,'أَوْ كَالَّذِي مَرَّ عَلَىٰ قَرْيَةٍ وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنَّىٰ يُحْيِي هَٰذِهِ اللَّهُ بَعْدَ مَوْتِهَا ۖ فَأَمَاتَهُ اللَّهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهُ ۖ قَالَ كَمْ لَبِثْتَ ۖ قَالَ لَبِثْتُ يَوْمًا أَوْ بَعْضَ يَوْمٍ ۖ قَالَ بَل لَّبِثْتَ مِائَةَ عَامٍ فَانظُرْ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ ۖ وَانظُرْ إِلَىٰ حِمَارِكَ وَلِنَجْعَلَكَ آيَةً لِّلنَّاسِ ۖ وَانظُرْ إِلَى الْعِظَامِ كَيْفَ نُنشِزُهَا ثُمَّ نَكْسُوهَا لَحْمًا ۚ فَلَمَّا تَبَيَّنَ لَهُ قَالَ أَعْلَمُ أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','یا همانند کسی که از کنار یک آبادی (ویران شده) عبور کرد، در حالی که دیوارهای آن، به روی سقفها فرو ریخته بود، (و اجساد و استخوانهای اهل آن، در هر سو پراکنده بود؛ او با خود) گفت: «چگونه خدا اینها را پس از مرگ، زنده می‌کند؟!» (در این هنگام،) خدا او را یکصد سال میراند؛ سپس زنده کرد؛ و به او گفت: «چه‌قدر درنگ کردی؟» گفت: «یک روز؛ یا بخشی از یک روز.» فرمود: «نه، بلکه یکصد سال درنگ کردی! نگاه کن به غذا و نوشیدنی خود (که همراه داشتی، با گذشت سالها) هیچ‌گونه تغییر نیافته است! (خدایی که یک چنین مواد فاسدشدنی را در طول این مدت، حفظ کرده، بر همه چیز قادر است!) ولی به الاغ خود نگاه کن (که چگونه از هم متلاشی شده! این زنده شدن تو پس از مرگ، هم برای اطمینان خاطر توست، و هم) برای اینکه تو را نشانه‌ای برای مردم (در مورد معاد) قرار دهیم. (اکنون) به استخوانها (ی مرکب سواری خود نگاه کن که چگونه آنها را برداشته، به هم پیوند می‌دهیم، و گوشت بر آن می‌پوشانیم!» هنگامی که (این حقایق) بر او آشکار شد، گفت: «می‌دانم خدا بر هر کاری توانا است».'];
ayat[267]=[2,260,'وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِي الْمَوْتَىٰ ۖ قَالَ أَوَلَمْ تُؤْمِن ۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطْمَئِنَّ قَلْبِي ۖ قَالَ فَخُذْ أَرْبَعَةً مِّنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلَىٰ كُلِّ جَبَلٍ مِّنْهُنَّ جُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْيًا ۚ وَاعْلَمْ أَنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','و (به خاطر بیاور) هنگامی را که ابراهیم گفت: «خدایا! به من نشان بده چگونه مردگان را زنده می‌کنی؟» فرمود: «مگر ایمان نیاورده‌ای؟!» عرض کرد: «آری، ولی می‌خواهم قلبم آرامش یابد.» فرمود: «در این صورت، چهار نوع از مرغان را انتخاب کن! و آنها را (پس از ذبح کردن،) قطعه قطعه کن (و در هم بیامیز)! سپس بر هر کوهی، قسمتی از آن را قرار بده، بعد آنها را بخوان، به سرعت به سوی تو می‌آیند! و بدان خداوند قادر و حکیم است؛ (هم از ذرات بدن مردگان آگاه است، و هم توانایی بر جمع آنها دارد)».'];
ayat[268]=[2,261,'مَّثَلُ الَّذِينَ يُنفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ كَمَثَلِ حَبَّةٍ أَنبَتَتْ سَبْعَ سَنَابِلَ فِي كُلِّ سُنبُلَةٍ مِّائَةُ حَبَّةٍ ۗ وَاللَّهُ يُضَاعِفُ لِمَن يَشَاءُ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ','کسانی که اموال خود را در راه خدا انفاق می‌کنند، همانند بذری هستند که هفت خوشه برویاند؛ که در هر خوشه، یکصد دانه باشد؛ و خداوند آن را برای هر کس بخواهد (و شایستگی داشته باشد)، دو یا چند برابر می‌کند؛ و خدا (از نظر قدرت و رحمت،) وسیع، و (به همه چیز) داناست.'];
ayat[269]=[2,262,'الَّذِينَ يُنفِقُونَ أَمْوَالَهُمْ فِي سَبِيلِ اللَّهِ ثُمَّ لَا يُتْبِعُونَ مَا أَنفَقُوا مَنًّا وَلَا أَذًى ۙ لَّهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','کسانی که اموال خود را در راه خدا انفاق می‌کنند، سپس به دنبال انفاقی که کرده‌اند، منت نمی‌گذارند و آزاری نمی‌رسانند، پاداش آنها نزد پروردگارشان (محفوظ) است؛ و نه ترسی دارند، و نه غمگین می‌شوند.'];
ayat[270]=[2,263,'۞ قَوْلٌ مَّعْرُوفٌ وَمَغْفِرَةٌ خَيْرٌ مِّن صَدَقَةٍ يَتْبَعُهَا أَذًى ۗ وَاللَّهُ غَنِيٌّ حَلِيمٌ','گفتار پسندیده (در برابر نیازمندان)، و عفو (و گذشت از خشونتهای آنها)، از بخششی که آزاری به دنبال آن باشد، بهتر است؛ و خداوند، بی‌نیاز و بردبار است.'];
ayat[271]=[2,264,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُبْطِلُوا صَدَقَاتِكُم بِالْمَنِّ وَالْأَذَىٰ كَالَّذِي يُنفِقُ مَالَهُ رِئَاءَ النَّاسِ وَلَا يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ فَمَثَلُهُ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَأَصَابَهُ وَابِلٌ فَتَرَكَهُ صَلْدًا ۖ لَّا يَقْدِرُونَ عَلَىٰ شَيْءٍ مِّمَّا كَسَبُوا ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ','ای کسانی که ایمان آورده‌اید! بخششهای خود را با منت و آزار، باطل نسازید! همانند کسی که مال خود را برای نشان دادن به مردم، انفاق می‌کند؛ و به خدا و روز رستاخیز، ایمان نمی‌آورد؛ (کار او) همچون قطعه سنگی است که بر آن، (قشر نازکی از) خاک باشد؛ (و بذرهایی در آن افشانده شود؛) و رگبار باران به آن برسد، (و همه خاکها و بذرها را بشوید،) و آن را صاف (و خالی از خاک و بذر) رها کند. آنها از کاری که انجام داده‌اند، چیزی به دست نمی‌آورند؛ و خداوند، جمعیت کافران را هدایت نمی‌کند.'];
ayat[272]=[2,265,'وَمَثَلُ الَّذِينَ يُنفِقُونَ أَمْوَالَهُمُ ابْتِغَاءَ مَرْضَاتِ اللَّهِ وَتَثْبِيتًا مِّنْ أَنفُسِهِمْ كَمَثَلِ جَنَّةٍ بِرَبْوَةٍ أَصَابَهَا وَابِلٌ فَآتَتْ أُكُلَهَا ضِعْفَيْنِ فَإِن لَّمْ يُصِبْهَا وَابِلٌ فَطَلٌّ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','و (کار) کسانی که اموال خود را برای خشنودی خدا، و تثبیت (ملکات انسانی در) روح خود، انفاق می‌کنند، همچون باغی است که در نقطه بلندی باشد، و بارانهای درشت به آن برسد، (و از هوای آزاد و نور آفتاب، به حد کافی بهره گیرد،) و میوه خود را دو چندان دهد (که همیشه شاداب و با طراوت است.) و خداوند به آنچه انجام می‌دهید، بیناست.'];
ayat[273]=[2,266,'أَيَوَدُّ أَحَدُكُمْ أَن تَكُونَ لَهُ جَنَّةٌ مِّن نَّخِيلٍ وَأَعْنَابٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ لَهُ فِيهَا مِن كُلِّ الثَّمَرَاتِ وَأَصَابَهُ الْكِبَرُ وَلَهُ ذُرِّيَّةٌ ضُعَفَاءُ فَأَصَابَهَا إِعْصَارٌ فِيهِ نَارٌ فَاحْتَرَقَتْ ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ','آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد که از زیر درختان آن، نهرها بگذرد، و برای او در آن (باغ)، از هر گونه میوه‌ای وجود داشته باشد، در حالی که به سن پیری رسیده و فرزندانی (کوچک و) ضعیف دارد؛ (در این هنگام،) گردبادی (کوبنده)، که در آن آتش (سوزانی) است، به آن برخورد کند و شعله‌ور گردد و بسوزد؟! (همین‌طور است حال کسانی که انفاقهای خود را، با ریا و منت و آزار، باطل می‌کنند.) این چنین خداوند آیات خود را برای شما آشکار می‌سازد؛ شاید بیندیشید (و با اندیشه، راه حق را بیابید)!'];
ayat[274]=[2,267,'يَا أَيُّهَا الَّذِينَ آمَنُوا أَنفِقُوا مِن طَيِّبَاتِ مَا كَسَبْتُمْ وَمِمَّا أَخْرَجْنَا لَكُم مِّنَ الْأَرْضِ ۖ وَلَا تَيَمَّمُوا الْخَبِيثَ مِنْهُ تُنفِقُونَ وَلَسْتُم بِآخِذِيهِ إِلَّا أَن تُغْمِضُوا فِيهِ ۚ وَاعْلَمُوا أَنَّ اللَّهَ غَنِيٌّ حَمِيدٌ','ای کسانی که ایمان آورده‌اید! از قسمتهای پاکیزه اموالی که (از طریق تجارت) به دست آورده‌اید، و از آنچه از زمین برای شما خارج ساخته‌ایم (از منابع و معادن و درختان و گیاهان)، انفاق کنید! و برای انفاق، به سراغ قسمتهای ناپاک نروید در حالی که خود شما، (به هنگام پذیرش اموال،) حاضر نیستید آنها را بپذیرید؛ مگر از روی اغماض و کراهت! و بدانید خداوند، بی‌نیاز و شایسته ستایش است.'];
ayat[275]=[2,268,'الشَّيْطَانُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُم بِالْفَحْشَاءِ ۖ وَاللَّهُ يَعِدُكُم مَّغْفِرَةً مِّنْهُ وَفَضْلًا ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ','شیطان، شما را (به هنگام انفاق،) وعده فقر و تهیدستی می‌دهد؛ و به فحشا (و زشتیها) امر می‌کند؛ ولی خداوند وعده «آمرزش» و «فزونی» به شما می‌دهد؛ و خداوند، قدرتش وسیع، و (به هر چیز) داناست. (به همین دلیل، به وعده‌های خود، وفا می‌کند.)'];
ayat[276]=[2,269,'يُؤْتِي الْحِكْمَةَ مَن يَشَاءُ ۚ وَمَن يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْرًا كَثِيرًا ۗ وَمَا يَذَّكَّرُ إِلَّا أُولُو الْأَلْبَابِ','(خدا) دانش و حکمت را به هر کس بخواهد (و شایسته بداند) می‌دهد؛ و به هر کس دانش داده شود، خیر فراوانی داده شده است. و جز خردمندان، (این حقایق را درک نمی‌کنند، و) متذکر نمی‌گردند.'];
ayat[277]=[2,270,'وَمَا أَنفَقْتُم مِّن نَّفَقَةٍ أَوْ نَذَرْتُم مِّن نَّذْرٍ فَإِنَّ اللَّهَ يَعْلَمُهُ ۗ وَمَا لِلظَّالِمِينَ مِنْ أَنصَارٍ','و هر چیز را که انفاق می‌کنید، یا (اموالی را که) نذر کرده‌اید (در راه خدا انفاق کنید)، خداوند آنها را می‌داند. و ستمگران یاوری ندارند.'];
ayat[278]=[2,271,'إِن تُبْدُوا الصَّدَقَاتِ فَنِعِمَّا هِيَ ۖ وَإِن تُخْفُوهَا وَتُؤْتُوهَا الْفُقَرَاءَ فَهُوَ خَيْرٌ لَّكُمْ ۚ وَيُكَفِّرُ عَنكُم مِّن سَيِّئَاتِكُمْ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','اگر انفاقها را آشکار کنید، خوب است! و اگر آنها را مخفی ساخته و به نیازمندان بدهید، برای شما بهتر است! و قسمتی از گناهان شما را می‌پوشاند؛ (و در پرتو بخشش در راه خدا، بخشوده خواهید شد.) و خداوند به آنچه انجام می‌دهید، آگاه است.'];
ayat[279]=[2,272,'۞ لَّيْسَ عَلَيْكَ هُدَاهُمْ وَلَٰكِنَّ اللَّهَ يَهْدِي مَن يَشَاءُ ۗ وَمَا تُنفِقُوا مِنْ خَيْرٍ فَلِأَنفُسِكُمْ ۚ وَمَا تُنفِقُونَ إِلَّا ابْتِغَاءَ وَجْهِ اللَّهِ ۚ وَمَا تُنفِقُوا مِنْ خَيْرٍ يُوَفَّ إِلَيْكُمْ وَأَنتُمْ لَا تُظْلَمُونَ','هدایت آنها (به‌طور اجبار،) بر تو نیست؛ (بنابر این، ترک انفاق به غیر مسلمانان، برای اجبار به اسلام، صحیح نیست؛) ولی خداوند، هر که را بخواهد (و شایسته بداند)، هدایت می‌کند. و آنچه را از خوبیها و اموال انفاق می‌کنید، برای خودتان است؛ (ولی) جز برای رضای خدا، انفاق نکنید! و آنچه از خوبیها انفاق می‌کنید، (پاداش آن) به طور کامل به شما داده می‌شود؛ و به شما ستم نخواهد شد.'];
ayat[280]=[2,273,'لِلْفُقَرَاءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللَّهِ لَا يَسْتَطِيعُونَ ضَرْبًا فِي الْأَرْضِ يَحْسَبُهُمُ الْجَاهِلُ أَغْنِيَاءَ مِنَ التَّعَفُّفِ تَعْرِفُهُم بِسِيمَاهُمْ لَا يَسْأَلُونَ النَّاسَ إِلْحَافًا ۗ وَمَا تُنفِقُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ','(انفاقِ شما، مخصوصاً باید) برای نیازمندانی باشد که در راه خدا، در تنگنا قرار گرفته‌اند؛ (و توجّه به آیین خدا، آنها را از وطنهای خویش آواره ساخته؛ و شرکت در میدانِ جهاد، به آنها اجازه نمی‌دهد تا برای تأمین هزینه زندگی، دست به کسب و تجارتی بزنند؛) نمی‌توانند مسافرتی کنند (و سرمایه‌ای به دست آورند؛) و از شدّت خویشتن‌داری، افراد ناآگاه آنها را بی‌نیاز می‌پندارند؛ امّا آنها را از چهره‌هایشان می‌شناسی؛ و هرگز با اصرار چیزی از مردم نمی‌خواهند. (این است مشخّصات آنها!) و هر چیز خوبی در راه خدا انفاق کنید، خداوند از آن آگاه است.'];
ayat[281]=[2,274,'الَّذِينَ يُنفِقُونَ أَمْوَالَهُم بِاللَّيْلِ وَالنَّهَارِ سِرًّا وَعَلَانِيَةً فَلَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','آنها که اموال خود را، شب و روز، پنهان و آشکار، انفاق می‌کنند، مزدشان نزد پروردگارشان است؛ نه ترسی بر آنهاست، و نه غمگین می‌شوند.'];
ayat[282]=[2,275,'الَّذِينَ يَأْكُلُونَ الرِّبَا لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ الَّذِي يَتَخَبَّطُهُ الشَّيْطَانُ مِنَ الْمَسِّ ۚ ذَٰلِكَ بِأَنَّهُمْ قَالُوا إِنَّمَا الْبَيْعُ مِثْلُ الرِّبَا ۗ وَأَحَلَّ اللَّهُ الْبَيْعَ وَحَرَّمَ الرِّبَا ۚ فَمَن جَاءَهُ مَوْعِظَةٌ مِّن رَّبِّهِ فَانتَهَىٰ فَلَهُ مَا سَلَفَ وَأَمْرُهُ إِلَى اللَّهِ ۖ وَمَنْ عَادَ فَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','کسانی که ربا می‌خورند، (در قیامت) برنمی‌خیزند مگر مانند کسی که بر اثر تماسّ شیطان، دیوانه شده (و نمی‌تواند تعادل خود را حفظ کند؛ گاهی زمین می‌خورد، گاهی بپا می‌خیزد). این، به خاطر آن است که گفتند: «داد و ستد هم مانند ربا است (و تفاوتی میان آن دو نیست.)» در حالی که خدا بیع را حلال کرده، و ربا را حرام! (زیرا فرق میان این دو، بسیار است.) و اگر کسی اندرز الهی به او رسد، و (از رباخواری) خودداری کند، سودهایی که در سابق [= قبل از نزول حکم تحریم‌] به دست آورده، مال اوست؛ (و این حکم، گذشته را شامل نمی‌گردد؛) و کار او به خدا واگذار می‌شود؛ (و گذشته او را خواهد بخشید.) امّا کسانی که بازگردند (و بار دیگر مرتکب این گناه شوند)، اهل آتشند؛ و همیشه در آن می‌مانند.'];
ayat[283]=[2,276,'يَمْحَقُ اللَّهُ الرِّبَا وَيُرْبِي الصَّدَقَاتِ ۗ وَاللَّهُ لَا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍ','خداوند، ربا را نابود می‌کند؛ و صدقات را افزایش می‌دهد! و خداوند، هیچ انسانِ ناسپاسِ گنهکاری را دوست نمی‌دارد.'];
ayat[284]=[2,277,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ لَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','کسانی که ایمان آوردند و اعمال صالح انجام دادند و نماز را برپا داشتند و زکات را پرداختند، اجرشان نزد پروردگارشان است؛ و نه ترسی بر آنهاست، و نه غمگین می‌شوند.'];
ayat[285]=[2,278,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَذَرُوا مَا بَقِيَ مِنَ الرِّبَا إِن كُنتُم مُّؤْمِنِينَ','ای کسانی که ایمان آورده‌اید! از (مخالفت فرمان) خدا بپرهیزید، و آنچه از (مطالبات) ربا باقی مانده، رها کنید؛ اگر ایمان دارید!'];
ayat[286]=[2,279,'فَإِن لَّمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِّنَ اللَّهِ وَرَسُولِهِ ۖ وَإِن تُبْتُمْ فَلَكُمْ رُءُوسُ أَمْوَالِكُمْ لَا تَظْلِمُونَ وَلَا تُظْلَمُونَ','اگر (چنین) نمی‌کنید، بدانید خدا و رسولش، با شما پیکار خواهند کرد! و اگر توبه کنید، سرمایه‌های شما، از آنِ شماست [= اصل سرمایه، بدون سود]؛ نه ستم می‌کنید، و نه بر شما ستم وارد می‌شود.'];
ayat[287]=[2,280,'وَإِن كَانَ ذُو عُسْرَةٍ فَنَظِرَةٌ إِلَىٰ مَيْسَرَةٍ ۚ وَأَن تَصَدَّقُوا خَيْرٌ لَّكُمْ ۖ إِن كُنتُمْ تَعْلَمُونَ','و اگر (بدهکار،) قدرت پرداخت نداشته باشد، او را تا هنگام توانایی، مهلت دهید! (و در صورتی که براستی قدرت پرداخت را ندارد،) برای خدا به او ببخشید بهتر است؛ اگر (منافع این کار را) بدانید!'];
ayat[288]=[2,281,'وَاتَّقُوا يَوْمًا تُرْجَعُونَ فِيهِ إِلَى اللَّهِ ۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ','و از روزی بپرهیزید (و بترسید) که در آن روز، شما را به سوی خدا بازمی‌گردانند؛ سپس به هر کس، آنچه انجام داده، به طور کامل باز پس داده می‌شود، و به آنها ستم نخواهد شد. (چون هر چه می‌بینند، نتایج اعمال خودشان است.)'];
ayat[289]=[2,282,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَدَايَنتُم بِدَيْنٍ إِلَىٰ أَجَلٍ مُّسَمًّى فَاكْتُبُوهُ ۚ وَلْيَكْتُب بَّيْنَكُمْ كَاتِبٌ بِالْعَدْلِ ۚ وَلَا يَأْبَ كَاتِبٌ أَن يَكْتُبَ كَمَا عَلَّمَهُ اللَّهُ ۚ فَلْيَكْتُبْ وَلْيُمْلِلِ الَّذِي عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللَّهَ رَبَّهُ وَلَا يَبْخَسْ مِنْهُ شَيْئًا ۚ فَإِن كَانَ الَّذِي عَلَيْهِ الْحَقُّ سَفِيهًا أَوْ ضَعِيفًا أَوْ لَا يَسْتَطِيعُ أَن يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِ ۚ وَاسْتَشْهِدُوا شَهِيدَيْنِ مِن رِّجَالِكُمْ ۖ فَإِن لَّمْ يَكُونَا رَجُلَيْنِ فَرَجُلٌ وَامْرَأَتَانِ مِمَّن تَرْضَوْنَ مِنَ الشُّهَدَاءِ أَن تَضِلَّ إِحْدَاهُمَا فَتُذَكِّرَ إِحْدَاهُمَا الْأُخْرَىٰ ۚ وَلَا يَأْبَ الشُّهَدَاءُ إِذَا مَا دُعُوا ۚ وَلَا تَسْأَمُوا أَن تَكْتُبُوهُ صَغِيرًا أَوْ كَبِيرًا إِلَىٰ أَجَلِهِ ۚ ذَٰلِكُمْ أَقْسَطُ عِندَ اللَّهِ وَأَقْوَمُ لِلشَّهَادَةِ وَأَدْنَىٰ أَلَّا تَرْتَابُوا ۖ إِلَّا أَن تَكُونَ تِجَارَةً حَاضِرَةً تُدِيرُونَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَلَّا تَكْتُبُوهَا ۗ وَأَشْهِدُوا إِذَا تَبَايَعْتُمْ ۚ وَلَا يُضَارَّ كَاتِبٌ وَلَا شَهِيدٌ ۚ وَإِن تَفْعَلُوا فَإِنَّهُ فُسُوقٌ بِكُمْ ۗ وَاتَّقُوا اللَّهَ ۖ وَيُعَلِّمُكُمُ اللَّهُ ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','ای کسانی که ایمان آورده‌اید! هنگامی که بدهی مدّت‌داری (به خاطر وام یا داد و ستد) به یکدیگر پیدا کنید، آن را بنویسید! و باید نویسنده‌ای از روی عدالت، (سند را) در میان شما بنویسد! و کسی که قدرت بر نویسندگی دارد، نباید از نوشتن -همان طور که خدا به او تعلیم داده- خودداری کند! پس باید بنویسد، و آن کس که حق بر عهده اوست، باید املا کند، و از خدا که پروردگار اوست بپرهیزد، و چیزی را فروگذار ننماید! و اگر کسی که حق بر ذمه اوست، سفیه (یا از نظر عقل) ضعیف (و مجنون) است، یا (به خاطر لال بودن،) توانایی بر املاکردن ندارد، باید ولیّ او (به جای او،) با رعایت عدالت، املا کند! و دو نفر از مردان (عادل) خود را (بر این حقّ) شاهد بگیرید! و اگر دو مرد نبودند، یک مرد و دو زن، از کسانی که مورد رضایت و اطمینان شما هستند، انتخاب کنید! (و این دو زن، باید با هم شاهد قرار گیرند،) تا اگر یکی انحرافی یافت، دیگری به او یادآوری کند. و شهود نباید به هنگامی که آنها را (برای شهادت) دعوت می‌کنند، خودداری نمایند! و از نوشتن (بدهیِ خود،) چه کوچک باشد یا بزرگ، ملول نشوید (هر چه باشد بنویسید)! این، در نزد خدا به عدالت نزدیکتر، و برای شهادت مستقیم تر، و برای جلوگیری از تردید و شک (و نزاع و گفتگو) بهتر می‌باشد؛ مگر اینکه داد و ستد نقدی باشد که بین خود، دست به دست می‌کنید. در این صورت، گناهی بر شما نیست که آن را ننویسید. ولی هنگامی که خرید و فروش (نقدی) می‌کنید، شاهد بگیرید! و نباید به نویسنده و شاهد، (به خاطر حقگویی،) زیانی برسد (و تحت فشار قرار گیرند)! و اگر چنین کنید، از فرمان پروردگار خارج شده‌اید. از خدا بپرهیزید! و خداوند به شما تعلیم می‌دهد؛ خداوند به همه چیز داناست.'];
ayat[290]=[2,283,'۞ وَإِن كُنتُمْ عَلَىٰ سَفَرٍ وَلَمْ تَجِدُوا كَاتِبًا فَرِهَانٌ مَّقْبُوضَةٌ ۖ فَإِنْ أَمِنَ بَعْضُكُم بَعْضًا فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ أَمَانَتَهُ وَلْيَتَّقِ اللَّهَ رَبَّهُ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَ ۚ وَمَن يَكْتُمْهَا فَإِنَّهُ آثِمٌ قَلْبُهُ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ','و اگر در سفر بودید، و نویسنده‌ای نیافتید، گروگان بگیرید! (گروگانی که در اختیار طلبکار قرار گیرد.) و اگر به یکدیگر اطمینان (کامل) داشته باشید، (گروگان لازم نیست، و) باید کسی که امین شمرده شده (و بدون گروگان، چیزی از دیگری گرفته)، امانت (و بدهی خود را بموقع) بپردازد؛ و از خدایی که پروردگار اوست. بپرهیزد! و شهادت را کتمان نکنید! و هر کس آن را کتمان کند، قلبش گناهکار است. و خداوند، به آنچه انجام می‌دهید، داناست.'];
ayat[291]=[2,284,'لِّلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَإِن تُبْدُوا مَا فِي أَنفُسِكُمْ أَوْ تُخْفُوهُ يُحَاسِبْكُم بِهِ اللَّهُ ۖ فَيَغْفِرُ لِمَن يَشَاءُ وَيُعَذِّبُ مَن يَشَاءُ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آنچه در آسمانها و زمین است، از آنِ خداست. و (از این رو) اگر آنچه را در دل دارید، آشکار سازید یا پنهان، خداوند شما را بر طبق آن، محاسبه می‌کند. سپس هر کس را بخواهد (و شایستگی داشته باشد)، می‌بخشد؛ و هر کس را بخواهد (و مستحق باشد)، مجازات می‌کند. و خداوند به همه چیز قدرت دارد.'];
ayat[292]=[2,285,'آمَنَ الرَّسُولُ بِمَا أُنزِلَ إِلَيْهِ مِن رَّبِّهِ وَالْمُؤْمِنُونَ ۚ كُلٌّ آمَنَ بِاللَّهِ وَمَلَائِكَتِهِ وَكُتُبِهِ وَرُسُلِهِ لَا نُفَرِّقُ بَيْنَ أَحَدٍ مِّن رُّسُلِهِ ۚ وَقَالُوا سَمِعْنَا وَأَطَعْنَا ۖ غُفْرَانَكَ رَبَّنَا وَإِلَيْكَ الْمَصِيرُ','پیامبر، به آنچه از سوی پروردگارش بر او نازل شده، ایمان آورده است. (و او، به تمام سخنان خود، کاملاً مؤمن می‌باشد.) و همه مؤمنان (نیز)، به خدا و فرشتگان او و کتابها و فرستادگانش، ایمان آورده‌اند؛ (و می‌گویند:) ما در میان هیچ یک از پیامبران او، فرق نمی‌گذاریم (و به همه ایمان داریم). و (مؤمنان) گفتند: «ما شنیدیم و اطاعت کردیم. پروردگارا! (انتظارِ) آمرزش تو را (داریم)؛ و بازگشت (ما) به سوی توست.»'];
ayat[293]=[2,286,'لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا وُسْعَهَا ۚ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّنَا لَا تُؤَاخِذْنَا إِن نَّسِينَا أَوْ أَخْطَأْنَا ۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَا إِصْرًا كَمَا حَمَلْتَهُ عَلَى الَّذِينَ مِن قَبْلِنَا ۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهِ ۖ وَاعْفُ عَنَّا وَاغْفِرْ لَنَا وَارْحَمْنَا ۚ أَنتَ مَوْلَانَا فَانصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ','خداوند هیچ کس را، جز به اندازه تواناییش، تکلیف نمی‌کند. (انسان،) هر کار (نیکی) را انجام دهد، برای خود انجام داده؛ و هر کار (بدی) کند، به زیان خود کرده است. (مؤمنان می‌گویند:) پروردگارا! اگر ما فراموش یا خطا کردیم، ما را مؤاخذه مکن! پروردگارا! تکلیف سنگینی بر ما قرار مده، آن چنان که (به خاطر گناه و طغیان،) بر کسانی که پیش از ما بودند، قرار دادی! پروردگارا! آنچه طاقت تحمل آن را نداریم، بر ما مقرّر مدار! و آثار گناه را از ما بشوی! ما را ببخش و در رحمت خود قرار ده! تو مولا و سرپرست مایی، پس ما را بر جمعیّت کافران، پیروز گردان!'];
ayat[294]=[3,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم','الم‌'];
ayat[295]=[3,2,'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ الْحَيُّ الْقَيُّومُ','معبودی جز خداوندِ یگانه زنده و پایدار و نگهدارنده، نیست.'];
ayat[296]=[3,3,'نَزَّلَ عَلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَأَنزَلَ التَّوْرَاةَ وَالْإِنجِيلَ','(همان کسی که) کتاب را بحق بر تو نازل کرد، که با نشانه‌های کتب پیشین، منطبق است؛ و «تورات» و «انجیل» را.'];
ayat[297]=[3,4,'مِن قَبْلُ هُدًى لِّلنَّاسِ وَأَنزَلَ الْفُرْقَانَ ۗ إِنَّ الَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ لَهُمْ عَذَابٌ شَدِيدٌ ۗ وَاللَّهُ عَزِيزٌ ذُو انتِقَامٍ','پیش از آن، برای هدایت مردم فرستاد؛ و (نیز) کتابی که حق را از باطل مشخّص می‌سازد، نازل کرد؛ کسانی که به آیات خدا کافر شدند، کیفر شدیدی دارند؛ و خداوند (برای کیفرِ بدکاران و کافران لجوج،) توانا و صاحب انتقام است.'];
ayat[298]=[3,5,'إِنَّ اللَّهَ لَا يَخْفَىٰ عَلَيْهِ شَيْءٌ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ','هیچ چیز، در آسمان و زمین، بر خدا مخفی نمی‌ماند. (بنابر این، تدبیر آنها بر او مشکل نیست.)'];
ayat[299]=[3,6,'هُوَ الَّذِي يُصَوِّرُكُمْ فِي الْأَرْحَامِ كَيْفَ يَشَاءُ ۚ لَا إِلَٰهَ إِلَّا هُوَ الْعَزِيزُ الْحَكِيمُ','او کسی است که شما را در رحمِ (مادران)، آنچنان که می‌خواهد تصویر می‌کند. معبودی جز خداوندِ توانا و حکیم، نیست.'];
ayat[300]=[3,7,'هُوَ الَّذِي أَنزَلَ عَلَيْكَ الْكِتَابَ مِنْهُ آيَاتٌ مُّحْكَمَاتٌ هُنَّ أُمُّ الْكِتَابِ وَأُخَرُ مُتَشَابِهَاتٌ ۖ فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مَا تَشَابَهَ مِنْهُ ابْتِغَاءَ الْفِتْنَةِ وَابْتِغَاءَ تَأْوِيلِهِ ۗ وَمَا يَعْلَمُ تَأْوِيلَهُ إِلَّا اللَّهُ ۗ وَالرَّاسِخُونَ فِي الْعِلْمِ يَقُولُونَ آمَنَّا بِهِ كُلٌّ مِّنْ عِندِ رَبِّنَا ۗ وَمَا يَذَّكَّرُ إِلَّا أُولُو الْأَلْبَابِ','او کسی است که این کتاب (آسمانی) را بر تو نازل کرد، که قسمتی از آن، آیات «محکم» [= صریح و روشن‌] است؛ که اساس این کتاب می‌باشد؛ (و هر گونه پیچیدگی در آیات دیگر، با مراجعه به اینها، برطرف می‌گردد.) و قسمتی از آن، «متشابه» است [= آیاتی که به خاطر بالا بودن سطح مطلب و جهات دیگر، در نگاه اول، احتمالات مختلفی در آن می‌رود؛ ولی با توجه به آیات محکم، تفسیر آنها آشکار می‌گردد.] اما آنها که در قلوبشان انحراف است، به دنبال متشابهاتند، تا فتنه‌انگیزی کنند (و مردم را گمراه سازند)؛ و تفسیر (نادرستی) برای آن می‌طلبند؛ در حالی که تفسیر آنها را، جز خدا و راسخان در علم، نمی‌دانند. (آنها که به دنبال فهم و درکِ اسرارِ همه آیات قرآن در پرتو علم و دانش الهی) می‌گویند: «ما به همه آن ایمان آوردیم؛ همه از طرف پروردگارِ ماست.» و جز صاحبان عقل، متذکر نمی‌شوند (و این حقیقت را درک نمی‌کنند).'];
ayat[301]=[3,8,'رَبَّنَا لَا تُزِغْ قُلُوبَنَا بَعْدَ إِذْ هَدَيْتَنَا وَهَبْ لَنَا مِن لَّدُنكَ رَحْمَةً ۚ إِنَّكَ أَنتَ الْوَهَّابُ','(راسخانِ در علم، می‌گویند:) «پروردگارا! دلهایمان را، بعد از آنکه ما را هدایت کردی، (از راه حق) منحرف مگردان! و از سوی خود، رحمتی بر ما ببخش، زیرا تو بخشنده‌ای!'];
ayat[302]=[3,9,'رَبَّنَا إِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَّا رَيْبَ فِيهِ ۚ إِنَّ اللَّهَ لَا يُخْلِفُ الْمِيعَادَ','پروردگارا! تو مردم را، برای روزی که تردیدی در آن نیست، جمع خواهی کرد؛ زیرا خداوند، از وعده خود، تخلّف نمی‌کند. (ما به تو و رحمت بی‌پایانت، و به وعده رستاخیز و قیامت ایمان داریم.)»'];
ayat[303]=[3,10,'إِنَّ الَّذِينَ كَفَرُوا لَن تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُم مِّنَ اللَّهِ شَيْئًا ۖ وَأُولَٰئِكَ هُمْ وَقُودُ النَّارِ','ثروتها و فرزندانِ کسانی که کافر شدند، نمی‌تواند آنان را از (عذابِ) خداوند باز دارد؛ (و از کیفر، رهایی بخشد.) و آنان خود، آتشگیره دوزخند.'];
ayat[304]=[3,11,'كَدَأْبِ آلِ فِرْعَوْنَ وَالَّذِينَ مِن قَبْلِهِمْ ۚ كَذَّبُوا بِآيَاتِنَا فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ ۗ وَاللَّهُ شَدِيدُ الْعِقَابِ','(عادت آنان در انکار و تحریفِ حقایق،) همچون عادتِ آل فرعون و کسانی است که پیش از آنها بودند؛ آیات ما را تکذیب کردند، و خداوند آنها را به (کیفر) گناهانشان گرفت؛ و خداوند، شدید العقاب است.'];
ayat[305]=[3,12,'قُل لِّلَّذِينَ كَفَرُوا سَتُغْلَبُونَ وَتُحْشَرُونَ إِلَىٰ جَهَنَّمَ ۚ وَبِئْسَ الْمِهَادُ','به آنها که کافر شدند بگو: «(از پیروزی موقت خود در جنگ اُحُد، شاد نباشید!) بزودی مغلوب خواهید شد؛ (و سپس در رستاخیز) به سوی جهنم، محشور خواهید شد. و چه بد جایگاهی است!'];
ayat[306]=[3,13,'قَدْ كَانَ لَكُمْ آيَةٌ فِي فِئَتَيْنِ الْتَقَتَا ۖ فِئَةٌ تُقَاتِلُ فِي سَبِيلِ اللَّهِ وَأُخْرَىٰ كَافِرَةٌ يَرَوْنَهُم مِّثْلَيْهِمْ رَأْيَ الْعَيْنِ ۚ وَاللَّهُ يُؤَيِّدُ بِنَصْرِهِ مَن يَشَاءُ ۗ إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِّأُولِي الْأَبْصَارِ','در دو گروهی که (در میدان جنگ بدر،) با هم رو به رو شدند، نشانه (و درس عبرتی) برای شما بود: یک گروه، در راه خدا نبرد می‌کرد؛ و جمع دیگری که کافر بود، (در راه شیطان و بت،) در حالی که آنها (گروه مؤمنان) را با چشم خود، دو برابر آنچه بودند، می‌دیدند. (و این خود عاملی برای وحشت و شکست آنها شد.) و خداوند، هر کس را بخواهد (و شایسته بداند)، با یاری خود، تأیید می‌کند. در این، عبرتی است برای بینایان!'];
ayat[307]=[3,14,'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوَاتِ مِنَ النِّسَاءِ وَالْبَنِينَ وَالْقَنَاطِيرِ الْمُقَنطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْأَنْعَامِ وَالْحَرْثِ ۗ ذَٰلِكَ مَتَاعُ الْحَيَاةِ الدُّنْيَا ۖ وَاللَّهُ عِندَهُ حُسْنُ الْمَآبِ','محبّت امور مادی، از زنان و فرزندان و اموال هنگفت از طلا و نقره و اسبهای ممتاز و چهارپایان و زراعت، در نظر مردم جلوه داده شده است؛ (تا در پرتو آن، آزمایش و تربیت شوند؛ ولی) اینها (در صورتی که هدف نهایی آدمی را تشکیل دهند،) سرمایه زندگی پست (مادی) است؛ و سرانجام نیک (و زندگیِ والا و جاویدان)، نزد خداست.'];
ayat[308]=[3,15,'۞ قُلْ أَؤُنَبِّئُكُم بِخَيْرٍ مِّن ذَٰلِكُمْ ۚ لِلَّذِينَ اتَّقَوْا عِندَ رَبِّهِمْ جَنَّاتٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا وَأَزْوَاجٌ مُّطَهَّرَةٌ وَرِضْوَانٌ مِّنَ اللَّهِ ۗ وَاللَّهُ بَصِيرٌ بِالْعِبَادِ','بگو: «آیا شما را از چیزی آگاه کنم که از این (سرمایه‌های مادی)، بهتر است؟» برای کسانی که پرهیزگاری پیشه کرده‌اند، (و از این سرمایه‌ها، در راه مشروع و حق و عدالت، استفاده می‌کنند،) در نزد پروردگارشان (در جهان دیگر)، باغهایی است که نهرها از پایِ درختانش می‌گذرد؛ همیشه در آن خواهند بود؛ و همسرانی پاکیزه، و خشنودی خداوند (نصیب آنهاست). و خدا به (امورِ) بندگان، بیناست.'];
ayat[309]=[3,16,'الَّذِينَ يَقُولُونَ رَبَّنَا إِنَّنَا آمَنَّا فَاغْفِرْ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ النَّارِ','همان کسانی که می‌گویند: «پروردگارا! ما ایمان آورده‌ایم؛ پس گناهان ما را بیامرز، و ما را از عذابِ آتش، نگاهدار!»'];
ayat[310]=[3,17,'الصَّابِرِينَ وَالصَّادِقِينَ وَالْقَانِتِينَ وَالْمُنفِقِينَ وَالْمُسْتَغْفِرِينَ بِالْأَسْحَارِ','آنها که (در برابر مشکلات، و در مسیر اطاعت و ترک گناه،) استقامت می‌ورزند، راستگو هستند، (در برابر خدا) خضوع، و (در راه او) انفاق می‌کنند، و در سحرگاهان، استغفار می‌نمایند.'];
ayat[311]=[3,18,'شَهِدَ اللَّهُ أَنَّهُ لَا إِلَٰهَ إِلَّا هُوَ وَالْمَلَائِكَةُ وَأُولُو الْعِلْمِ قَائِمًا بِالْقِسْطِ ۚ لَا إِلَٰهَ إِلَّا هُوَ الْعَزِيزُ الْحَكِيمُ','خداوند، (با ایجادِ نظامِ واحدِ جهانِ هستی،) گواهی می‌دهد که معبودی جز او نیست؛ و فرشتگان و صاحبان دانش، (هر کدام به گونه‌ای بر این مطلب،) گواهی می‌دهند؛ در حالی که (خداوند در تمام عالم) قیام به عدالت دارد؛ معبودی جز او نیست، که هم توانا و هم حکیم است.'];
ayat[312]=[3,19,'إِنَّ الدِّينَ عِندَ اللَّهِ الْإِسْلَامُ ۗ وَمَا اخْتَلَفَ الَّذِينَ أُوتُوا الْكِتَابَ إِلَّا مِن بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ ۗ وَمَن يَكْفُرْ بِآيَاتِ اللَّهِ فَإِنَّ اللَّهَ سَرِيعُ الْحِسَابِ','دین در نزد خدا، اسلام (و تسلیم بودن در برابر حق) است. و کسانی که کتاب آسمانی به آنان داده شد، اختلافی (در آن) ایجاد نکردند، مگر بعد از آگاهی و علم، آن هم به خاطر ظلم و ستم در میان خود؛ و هر کس به آیات خدا کفر ورزد، (خدا به حساب او می‌رسد؛ زیرا) خداوند، سریع الحساب است.'];
ayat[313]=[3,20,'فَإِنْ حَاجُّوكَ فَقُلْ أَسْلَمْتُ وَجْهِيَ لِلَّهِ وَمَنِ اتَّبَعَنِ ۗ وَقُل لِّلَّذِينَ أُوتُوا الْكِتَابَ وَالْأُمِّيِّينَ أَأَسْلَمْتُمْ ۚ فَإِنْ أَسْلَمُوا فَقَدِ اهْتَدَوا ۖ وَّإِن تَوَلَّوْا فَإِنَّمَا عَلَيْكَ الْبَلَاغُ ۗ وَاللَّهُ بَصِيرٌ بِالْعِبَادِ','اگر با تو، به گفتگو و ستیز برخیزند، (با آنها مجادله نکن! و) بگو: «من و پیروانم، در برابر خداوند (و فرمان او)، تسلیم شده‌ایم.» و به آنها که اهل کتاب هستند [= یهود و نصاری‌] و بی‌سوادان [= مشرکان‌] بگو: «آیا شما هم تسلیم شده‌اید؟» اگر (در برابر فرمان و منطق حق، تسلیم شوند، هدایت می‌یابند؛ و اگر سرپیچی کنند، (نگران مباش! زیرا) بر تو، تنها ابلاغ (رسالت) است؛ و خدا نسبت به (اعمال و عقاید) بندگان، بیناست.'];
ayat[314]=[3,21,'إِنَّ الَّذِينَ يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ حَقٍّ وَيَقْتُلُونَ الَّذِينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النَّاسِ فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ','کسانی که نسبت به آیات خدا کفر می‌ورزند و پیامبران را بناحق می‌کشند، و (نیز) مردمی را که امر به عدالت می‌کنند به قتل می‌رسانند، و به کیفر دردناک (الهی) بشارت ده!'];
ayat[315]=[3,22,'أُولَٰئِكَ الَّذِينَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ وَمَا لَهُم مِّن نَّاصِرِينَ','آنها کسانی هستند که اعمال (نیکشان، به خاطر این گناهان بزرگ،) در دنیا و آخرت تباه شده، و یاور و مددکار (و شفاعت کننده‌ای) ندارند.'];
ayat[316]=[3,23,'أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِّنَ الْكِتَابِ يُدْعَوْنَ إِلَىٰ كِتَابِ اللَّهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلَّىٰ فَرِيقٌ مِّنْهُمْ وَهُم مُّعْرِضُونَ','آیا ندیدی کسانی را که بهره‌ای از کتاب (آسمانی) داشتند، به سوی کتاب الهی دعوت شدند تا در میان آنها داوری کند، سپس گروهی از آنان، (با علم و آگاهی،) روی می‌گردانند، در حالی که (از قبول حق) اعراض دارند؟'];
ayat[317]=[3,24,'ذَٰلِكَ بِأَنَّهُمْ قَالُوا لَن تَمَسَّنَا النَّارُ إِلَّا أَيَّامًا مَّعْدُودَاتٍ ۖ وَغَرَّهُمْ فِي دِينِهِم مَّا كَانُوا يَفْتَرُونَ','این عمل آنها، به خاطر آن است که می‌گفتند: «آتش (دوزخ)، جز چند روزی به ما نمی‌رسد. (و کیفر ما، به خاطر امتیازی که بر اقوام دیگر داریم، بسیار محدود است.)» این افترا (و دروغی که به خدا بسته بودند،) آنها را در دینشان مغرور ساخت (و گرفتار انواع گناهان شدند).'];
ayat[318]=[3,25,'فَكَيْفَ إِذَا جَمَعْنَاهُمْ لِيَوْمٍ لَّا رَيْبَ فِيهِ وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ','پس چه گونه خواهند بود هنگامی که آنها را برای روزی که شکّی در آن نیست [= روز رستاخیز] جمع کنیم، و به هر کس، آنچه (از اعمال برای خود) فراهم کرده، بطور کامل داده شود؟ و به آنها ستم نخواهد شد (زیرا محصول اعمال خود را می‌چینند).'];
ayat[319]=[3,26,'قُلِ اللَّهُمَّ مَالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَن تَشَاءُ وَتَنزِعُ الْمُلْكَ مِمَّن تَشَاءُ وَتُعِزُّ مَن تَشَاءُ وَتُذِلُّ مَن تَشَاءُ ۖ بِيَدِكَ الْخَيْرُ ۖ إِنَّكَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','بگو: «بارالها! مالک حکومتها تویی؛ به هر کس بخواهی، حکومت می‌بخشی؛ و از هر کس بخواهی، حکومت را می‌گیری؛ هر کس را بخواهی، عزت می‌دهی؛ و هر که را بخواهی خوار می‌کنی. تمام خوبیها به دست توست؛ تو بر هر چیزی قادری.'];
ayat[320]=[3,27,'تُولِجُ اللَّيْلَ فِي النَّهَارِ وَتُولِجُ النَّهَارَ فِي اللَّيْلِ ۖ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ ۖ وَتَرْزُقُ مَن تَشَاءُ بِغَيْرِ حِسَابٍ','شب را در روز داخل می‌کنی، و روز را در شب؛ و زنده را از مرده بیرون می‌آوری، و مرده را زنده؛ و به هر کس بخواهی، بدون حساب، روزی می‌بخشی.»'];
ayat[321]=[3,28,'لَّا يَتَّخِذِ الْمُؤْمِنُونَ الْكَافِرِينَ أَوْلِيَاءَ مِن دُونِ الْمُؤْمِنِينَ ۖ وَمَن يَفْعَلْ ذَٰلِكَ فَلَيْسَ مِنَ اللَّهِ فِي شَيْءٍ إِلَّا أَن تَتَّقُوا مِنْهُمْ تُقَاةً ۗ وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ ۗ وَإِلَى اللَّهِ الْمَصِيرُ','افراد باایمان نباید به جای مؤمنان، کافران را دوست و سرپرست خود انتخاب کنند؛ و هر کس چنین کند، هیچ رابطه‌ای با خدا ندارد (و پیوند او بکلّی از خدا گسسته می‌شود)؛ مگر اینکه از آنها بپرهیزید (و به خاطر هدفهای مهمتری تقیّه کنید). خداوند شما را از (نافرمانی) خود، برحذر می‌دارد؛ و بازگشت (شما) به سوی خداست.'];
ayat[322]=[3,29,'قُلْ إِن تُخْفُوا مَا فِي صُدُورِكُمْ أَوْ تُبْدُوهُ يَعْلَمْهُ اللَّهُ ۗ وَيَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','بگو: «اگر آنچه را در سینه‌های شماست، پنهان دارید یا آشکار کنید، خداوند آن را می‌داند؛ و (نیز) از آنچه در آسمانها و زمین است، آگاه می‌باشد؛ و خداوند بر هر چیزی تواناست.'];
ayat[323]=[3,30,'يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَّا عَمِلَتْ مِنْ خَيْرٍ مُّحْضَرًا وَمَا عَمِلَتْ مِن سُوءٍ تَوَدُّ لَوْ أَنَّ بَيْنَهَا وَبَيْنَهُ أَمَدًا بَعِيدًا ۗ وَيُحَذِّرُكُمُ اللَّهُ نَفْسَهُ ۗ وَاللَّهُ رَءُوفٌ بِالْعِبَادِ','روزی که هر کس، آنچه را از کار نیک انجام داده، حاضر می‌بیند؛ و آرزو می‌کند میان او، و آنچه از اعمال بد انجام داده، فاصله زمانیِ زیادی باشد. خداوند شما را از (نافرمانی) خودش، برحذر می‌دارد؛ و (در عین حال،) خدا نسبت به همه بندگان، مهربان است.»'];
ayat[324]=[3,31,'قُلْ إِن كُنتُمْ تُحِبُّونَ اللَّهَ فَاتَّبِعُونِي يُحْبِبْكُمُ اللَّهُ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ ۗ وَاللَّهُ غَفُورٌ رَّحِيمٌ','بگو: «اگر خدا را دوست می‌دارید، از من پیروی کنید! تا خدا (نیز) شما را دوست بدارد؛ و گناهانتان را ببخشد؛ و خدا آمرزنده مهربان است.»'];
ayat[325]=[3,32,'قُلْ أَطِيعُوا اللَّهَ وَالرَّسُولَ ۖ فَإِن تَوَلَّوْا فَإِنَّ اللَّهَ لَا يُحِبُّ الْكَافِرِينَ','بگو: «از خدا و فرستاده (او)، اطاعت کنید! و اگر سرپیچی کنید، خداوند کافران را دوست نمی‌دارد.»'];
ayat[326]=[3,33,'۞ إِنَّ اللَّهَ اصْطَفَىٰ آدَمَ وَنُوحًا وَآلَ إِبْرَاهِيمَ وَآلَ عِمْرَانَ عَلَى الْعَالَمِينَ','خداوند، آدم و نوح و آل ابراهیم و آل عمران را بر جهانیان برتری داد.'];
ayat[327]=[3,34,'ذُرِّيَّةً بَعْضُهَا مِن بَعْضٍ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','آنها فرزندان و (دودمانی) بودند که (از نظر پاکی و تقوا و فضیلت،) بعضی از بعض دیگر گرفته شده بودند؛ و خداوند، شنوا و داناست (و از کوششهای آنها در مسیر رسالت خود، آگاه می‌باشد).'];
ayat[328]=[3,35,'إِذْ قَالَتِ امْرَأَتُ عِمْرَانَ رَبِّ إِنِّي نَذَرْتُ لَكَ مَا فِي بَطْنِي مُحَرَّرًا فَتَقَبَّلْ مِنِّي ۖ إِنَّكَ أَنتَ السَّمِيعُ الْعَلِيمُ','(به یاد آورید) هنگامی را که همسرِ «عمران» گفت: «خداوندا! آنچه را در رحم دارم، برای تو نذر کردم، که «محرَّر» (و آزاد، برای خدمت خانه تو) باشد. از من بپذیر، که تو شنوا و دانایی!'];
ayat[329]=[3,36,'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ إِنِّي وَضَعْتُهَا أُنثَىٰ وَاللَّهُ أَعْلَمُ بِمَا وَضَعَتْ وَلَيْسَ الذَّكَرُ كَالْأُنثَىٰ ۖ وَإِنِّي سَمَّيْتُهَا مَرْيَمَ وَإِنِّي أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطَانِ الرَّجِيمِ','ولی هنگامی که او را به دنیا آورد، (و او را دختر یافت،) گفت: «خداوندا! من او را دختر آوردم -ولی خدا از آنچه او به دنیا آورده بود، آگاهتر بود- و پسر، همانند دختر نیست. (دختر نمی‌تواند وظیفه خدمتگزاری معبد را همانند پسر انجام دهد.) من او را مریم نام گذاردم؛ و او و فرزندانش را از (وسوسه‌های) شیطان رانده شده، در پناه تو قرار می‌دهم.»'];
ayat[330]=[3,37,'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٍ وَأَنبَتَهَا نَبَاتًا حَسَنًا وَكَفَّلَهَا زَكَرِيَّا ۖ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَ وَجَدَ عِندَهَا رِزْقًا ۖ قَالَ يَا مَرْيَمُ أَنَّىٰ لَكِ هَٰذَا ۖ قَالَتْ هُوَ مِنْ عِندِ اللَّهِ ۖ إِنَّ اللَّهَ يَرْزُقُ مَن يَشَاءُ بِغَيْرِ حِسَابٍ','خداوند، او [= مریم‌] را به طرز نیکویی پذیرفت؛ و به طرز شایسته‌ای، (نهال وجود) او را رویانید (و پرورش داد)؛ و کفالت او را به «زکریا» سپرد. هر زمان زکریا وارد محراب او می‌شد، غذای مخصوصی در آن جا می‌دید. از او پرسید: «ای مریم! این را از کجا آورده‌ای؟!» گفت: «این از سوی خداست. خداوند به هر کس بخواهد، بی حساب روزی می‌دهد.»'];
ayat[331]=[3,38,'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهُ ۖ قَالَ رَبِّ هَبْ لِي مِن لَّدُنكَ ذُرِّيَّةً طَيِّبَةً ۖ إِنَّكَ سَمِيعُ الدُّعَاءِ','در آنجا بود که زکریا، (با مشاهده آن همه شایستگی در مریم،) پروردگار خویش را خواند و عرض کرد: «خداوندا! از طرف خود، فرزند پاکیزه‌ای (نیز) به من عطا فرما، که تو دعا را می‌شنوی!»'];
ayat[332]=[3,39,'فَنَادَتْهُ الْمَلَائِكَةُ وَهُوَ قَائِمٌ يُصَلِّي فِي الْمِحْرَابِ أَنَّ اللَّهَ يُبَشِّرُكَ بِيَحْيَىٰ مُصَدِّقًا بِكَلِمَةٍ مِّنَ اللَّهِ وَسَيِّدًا وَحَصُورًا وَنَبِيًّا مِّنَ الصَّالِحِينَ','و هنگامی که او در محراب ایستاده، مشغول نیایش بود، فرشتگان او را صدا زدند که: «خدا تو را به» یحیی «بشارت می‌دهد؛ (کسی) که کلمه خدا [= مسیح‌] را تصدیق می‌کند؛ و رهبر خواهد بود؛ و از هوسهای سرکش برکنار، و پیامبری از صالحان است.»'];
ayat[333]=[3,40,'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَامٌ وَقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَأَتِي عَاقِرٌ ۖ قَالَ كَذَٰلِكَ اللَّهُ يَفْعَلُ مَا يَشَاءُ','او عرض کرد: «پروردگارا! چگونه ممکن است فرزندی برای من باشد، در حالی که پیری به سراغ من آمده، و همسرم نازاست؟!» فرمود: «بدین گونه خداوند هر کاری را بخواهد انجام می‌دهد.»'];
ayat[334]=[3,41,'قَالَ رَبِّ اجْعَل لِّي آيَةً ۖ قَالَ آيَتُكَ أَلَّا تُكَلِّمَ النَّاسَ ثَلَاثَةَ أَيَّامٍ إِلَّا رَمْزًا ۗ وَاذْكُر رَّبَّكَ كَثِيرًا وَسَبِّحْ بِالْعَشِيِّ وَالْإِبْكَارِ','(زکریا) عرض کرد: «پروردگارا! نشانه‌ای برای من قرار ده!» گفت: «نشانه تو آن است که سه روز، جز به اشاره و رمز، با مردم سخن نخواهی گفت. (و زبان تو، بدون هیچ علّت ظاهری، برای گفتگو با مردم از کار می‌افتد.) پروردگار خود را (به شکرانه این نعمت بزرگ،) بسیار یاد کن! و به هنگام صبح و شام، او را تسبیح بگو!'];
ayat[335]=[3,42,'وَإِذْ قَالَتِ الْمَلَائِكَةُ يَا مَرْيَمُ إِنَّ اللَّهَ اصْطَفَاكِ وَطَهَّرَكِ وَاصْطَفَاكِ عَلَىٰ نِسَاءِ الْعَالَمِينَ','و (به یاد آورید) هنگامی را که فرشتگان گفتند: «ای مریم! خدا تو را برگزیده و پاک ساخته؛ و بر تمام زنان جهان، برتری داده است.'];
ayat[336]=[3,43,'يَا مَرْيَمُ اقْنُتِي لِرَبِّكِ وَاسْجُدِي وَارْكَعِي مَعَ الرَّاكِعِينَ','ای مریم! (به شکرانه این نعمت) برای پروردگار خود، خضوع کن و سجده بجا آور! و با رکوع‌کنندگان، رکوع کن!'];
ayat[337]=[3,44,'ذَٰلِكَ مِنْ أَنبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ ۚ وَمَا كُنتَ لَدَيْهِمْ إِذْ يُلْقُونَ أَقْلَامَهُمْ أَيُّهُمْ يَكْفُلُ مَرْيَمَ وَمَا كُنتَ لَدَيْهِمْ إِذْ يَخْتَصِمُونَ','(ای پیامبر!) این، از خبرهای غیبی است که به تو وحی می‌کنیم؛ و تو در آن هنگام که قلمهای خود را (برای قرعه‌کشی) به آب می‌افکندند تا کدامیک کفالت و سرپرستی مریم را عهده‌دار شود، و (نیز) به هنگامی که (دانشمندان بنی اسرائیل، برای کسب افتخار سرپرستی او،) با هم کشمکش داشتند، حضور نداشتی؛ و همه اینها، از راه وحی به تو گفته شد.)'];
ayat[338]=[3,45,'إِذْ قَالَتِ الْمَلَائِكَةُ يَا مَرْيَمُ إِنَّ اللَّهَ يُبَشِّرُكِ بِكَلِمَةٍ مِّنْهُ اسْمُهُ الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ وَجِيهًا فِي الدُّنْيَا وَالْآخِرَةِ وَمِنَ الْمُقَرَّبِينَ','(به یاد آورید) هنگامی را که فرشتگان گفتند: «ای مریم! خداوند تو را به کلمه‌ای [= وجود باعظمتی‌] از طرف خودش بشارت می‌دهد که نامش «مسیح، عیسی پسر مریم» است؛ در حالی که در این جهان و جهان دیگر، صاحب شخصیّت خواهد بود؛ و از مقرّبان (الهی) است.'];
ayat[339]=[3,46,'وَيُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلًا وَمِنَ الصَّالِحِينَ','و با مردم، در گاهواره و در حالت کهولت (و میانسال شدن) سخن خواهد گفت؛ و از شایستگان است.»'];
ayat[340]=[3,47,'قَالَتْ رَبِّ أَنَّىٰ يَكُونُ لِي وَلَدٌ وَلَمْ يَمْسَسْنِي بَشَرٌ ۖ قَالَ كَذَٰلِكِ اللَّهُ يَخْلُقُ مَا يَشَاءُ ۚ إِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُن فَيَكُونُ','(مریم) گفت: «پروردگارا! چگونه ممکن است فرزندی برای من باشد، در حالی که انسانی با من تماس نگرفته است؟!» فرمود: «خداوند، این‌گونه هر چه را بخواهد می‌آفریند! هنگامی که چیزی را مقرّر دارد (و فرمان هستی آن را صادر کند)، فقط به آن می‌گوید: «موجود باش!» آن نیز فوراً موجود می‌شود.'];
ayat[341]=[3,48,'وَيُعَلِّمُهُ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرَاةَ وَالْإِنجِيلَ','و به او، کتاب و دانش و تورات و انجیل، می‌آموزد.'];
ayat[342]=[3,49,'وَرَسُولًا إِلَىٰ بَنِي إِسْرَائِيلَ أَنِّي قَدْ جِئْتُكُم بِآيَةٍ مِّن رَّبِّكُمْ ۖ أَنِّي أَخْلُقُ لَكُم مِّنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ فَأَنفُخُ فِيهِ فَيَكُونُ طَيْرًا بِإِذْنِ اللَّهِ ۖ وَأُبْرِئُ الْأَكْمَهَ وَالْأَبْرَصَ وَأُحْيِي الْمَوْتَىٰ بِإِذْنِ اللَّهِ ۖ وَأُنَبِّئُكُم بِمَا تَأْكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمْ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لَّكُمْ إِن كُنتُم مُّؤْمِنِينَ','و (او را به عنوان) رسول و فرستاده به سوی بنی اسرائیل (قرار داده، که به آنها می‌گوید:) من نشانه‌ای از طرف پروردگار شما، برایتان آورده‌ام؛ من از گِل، چیزی به شکل پرنده می‌سازم؛ سپس در آن می‌دمم و به فرمان خدا، پرنده‌ای می‌گردد. و به اذن خدا، کورِ مادرزاد و مبتلایان به برص [= پیسی‌] را بهبودی می‌بخشم؛ و مردگان را به اذن خدا زنده می‌کنم؛ و از آنچه می‌خورید، و در خانه‌های خود ذخیره می‌کنید، به شما خبر می‌دهم؛ مسلماً در اینها، نشانه‌ای برای شماست، اگر ایمان داشته باشید!'];
ayat[343]=[3,50,'وَمُصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَلِأُحِلَّ لَكُم بَعْضَ الَّذِي حُرِّمَ عَلَيْكُمْ ۚ وَجِئْتُكُم بِآيَةٍ مِّن رَّبِّكُمْ فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','و آنچه را پیش از من از تورات بوده، تصدیق می‌کنم؛ و (آمده‌ام) تا پاره‌ای از چیزهایی را که (بر اثر ظلم و گناه،) بر شما حرام شده، (مانند گوشت بعضی از چهارپایان و ماهیها،) حلال کنم؛ و نشانه‌ای از طرف پروردگار شما، برایتان آورده‌ام؛ پس از خدا بترسید، و مرا اطاعت کنید!'];
ayat[344]=[3,51,'إِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ ۗ هَٰذَا صِرَاطٌ مُّسْتَقِيمٌ','خداوند، پروردگار من و شماست؛ او را بپرستید (نه من، و نه چیز دیگر را)! این است راه راست!»'];
ayat[345]=[3,52,'۞ فَلَمَّا أَحَسَّ عِيسَىٰ مِنْهُمُ الْكُفْرَ قَالَ مَنْ أَنصَارِي إِلَى اللَّهِ ۖ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنصَارُ اللَّهِ آمَنَّا بِاللَّهِ وَاشْهَدْ بِأَنَّا مُسْلِمُونَ','هنگامی که عیسی از آنان احساس کفر (و مخالفت) کرد، گفت: «کیست که یاور من به سوی خدا (برای تبلیغ آیین او) گردد؟» حواریان [= شاگردانِ مخصوصِ او] گفتند: «ما یاوران خداییم؛ به خدا ایمان آوردیم؛ و تو (نیز) گواه باش که ما اسلام آورده‌ایم.'];
ayat[346]=[3,53,'رَبَّنَا آمَنَّا بِمَا أَنزَلْتَ وَاتَّبَعْنَا الرَّسُولَ فَاكْتُبْنَا مَعَ الشَّاهِدِينَ','پروردگارا! به آنچه نازل کرده‌ای، ایمان آوردیم و از فرستاده (تو) پیروی نمودیم؛ ما را در زمره گواهان بنویس!»'];
ayat[347]=[3,54,'وَمَكَرُوا وَمَكَرَ اللَّهُ ۖ وَاللَّهُ خَيْرُ الْمَاكِرِينَ','و (یهود و دشمنان مسیح، برای نابودی او و آیینش،) نقشه کشیدند؛ و خداوند (بر حفظ او و آیینش،) چاره‌جویی کرد؛ و خداوند، بهترین چاره‌جویان است.'];
ayat[348]=[3,55,'إِذْ قَالَ اللَّهُ يَا عِيسَىٰ إِنِّي مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذِينَ كَفَرُوا وَجَاعِلُ الَّذِينَ اتَّبَعُوكَ فَوْقَ الَّذِينَ كَفَرُوا إِلَىٰ يَوْمِ الْقِيَامَةِ ۖ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأَحْكُمُ بَيْنَكُمْ فِيمَا كُنتُمْ فِيهِ تَخْتَلِفُونَ','(به یاد آورید) هنگامی را که خدا به عیسی فرمود: «من تو را برمی‌گیرم و به سوی خود، بالا می‌برم و تو را از کسانی که کافر شدند، پاک می‌سازم؛ و کسانی را که از تو پیروی کردند، تا روز رستاخیز، برتر از کسانی که کافر شدند، قرارمی‌دهم؛ سپس بازگشت شما به سوی من است و در میان شما، در آنچه اختلاف داشتید، داوری می‌کنم.'];
ayat[349]=[3,56,'فَأَمَّا الَّذِينَ كَفَرُوا فَأُعَذِّبُهُمْ عَذَابًا شَدِيدًا فِي الدُّنْيَا وَالْآخِرَةِ وَمَا لَهُم مِّن نَّاصِرِينَ','امّا آنها که کافر شدند، (و پس از شناختن حق، آن را انکار کردند،) در دنیا و آخرت، آنان را مجازات دردناکی خواهم کرد؛ و برای آنها، یاورانی نیست.'];
ayat[350]=[3,57,'وَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ ۗ وَاللَّهُ لَا يُحِبُّ الظَّالِمِينَ','امّا آنها که ایمان آوردند، و اعمال صالح انجام دادند، خداوند پاداش آنان را بطور کامل خواهد داد؛ و خداوند، ستمکاران را دوست نمی‌دارد.»'];
ayat[351]=[3,58,'ذَٰلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الْآيَاتِ وَالذِّكْرِ الْحَكِيمِ','اینها را که بر تو می‌خوانیم، از نشانه‌ها (ی حقّانیّت تو) است، و یادآوری حکیمانه است.'];
ayat[352]=[3,59,'إِنَّ مَثَلَ عِيسَىٰ عِندَ اللَّهِ كَمَثَلِ آدَمَ ۖ خَلَقَهُ مِن تُرَابٍ ثُمَّ قَالَ لَهُ كُن فَيَكُونُ','مَثَل عیسی در نزد خدا، همچون آدم است؛ که او را از خاک آفرید، و سپس به او فرمود: «موجود باش!» او هم فوراً موجود شد. (بنابر این، ولادت مسیح بدون پدر، هرگز دلیل بر الوهیّت او نیست.)'];
ayat[353]=[3,60,'الْحَقُّ مِن رَّبِّكَ فَلَا تَكُن مِّنَ الْمُمْتَرِينَ','اینها حقیقتی است از جانب پروردگار تو؛ بنابر این، از تردید کنندگان مباش!'];
ayat[354]=[3,61,'فَمَنْ حَاجَّكَ فِيهِ مِن بَعْدِ مَا جَاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ أَبْنَاءَنَا وَأَبْنَاءَكُمْ وَنِسَاءَنَا وَنِسَاءَكُمْ وَأَنفُسَنَا وَأَنفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَل لَّعْنَتَ اللَّهِ عَلَى الْكَاذِبِينَ','هرگاه بعد از علم و دانشی که (در باره مسیح) به تو رسیده، (باز) کسانی با تو به محاجّه و ستیز برخیزند، به آنها بگو: «بیایید ما فرزندان خود را دعوت کنیم، شما هم فرزندان خود را؛ ما زنان خویش را دعوت نماییم، شما هم زنان خود را؛ ما از نفوس خود دعوت کنیم، شما هم از نفوس خود؛ آنگاه مباهله کنیم؛ و لعنت خدا را بر دروغگویان قرار دهیم.'];
ayat[355]=[3,62,'إِنَّ هَٰذَا لَهُوَ الْقَصَصُ الْحَقُّ ۚ وَمَا مِنْ إِلَٰهٍ إِلَّا اللَّهُ ۚ وَإِنَّ اللَّهَ لَهُوَ الْعَزِيزُ الْحَكِيمُ','این همان سرگذشتِ واقعی (مسیح) است. (و ادعاهایی همچون الوهیّت او، یا فرزند خدا بودنش، بی‌اساس است.) و هیچ معبودی، جز خداوند یگانه نیست؛ و خداوند توانا و حکیم است.'];
ayat[356]=[3,63,'فَإِن تَوَلَّوْا فَإِنَّ اللَّهَ عَلِيمٌ بِالْمُفْسِدِينَ','اگر (با این همه شواهد روشن، باز هم از پذیرش حق) روی گردانند، (بدان که طالب حق نیستند؛ و) خداوند از مفسده‌جویان، آگاه است.'];
ayat[357]=[3,64,'قُلْ يَا أَهْلَ الْكِتَابِ تَعَالَوْا إِلَىٰ كَلِمَةٍ سَوَاءٍ بَيْنَنَا وَبَيْنَكُمْ أَلَّا نَعْبُدَ إِلَّا اللَّهَ وَلَا نُشْرِكَ بِهِ شَيْئًا وَلَا يَتَّخِذَ بَعْضُنَا بَعْضًا أَرْبَابًا مِّن دُونِ اللَّهِ ۚ فَإِن تَوَلَّوْا فَقُولُوا اشْهَدُوا بِأَنَّا مُسْلِمُونَ','بگو: «ای اهل کتاب! بیایید به سوی سخنی که میان ما و شما یکسان است؛ که جز خداوند یگانه را نپرستیم و چیزی را همتای او قرار ندهیم؛ و بعضی از ما، بعضی دیگر را -غیر از خدای یگانه- به خدایی نپذیرد.» هرگاه (از این دعوت،) سرباز زنند، بگویید: «گواه باشید که ما مسلمانیم!»'];
ayat[358]=[3,65,'يَا أَهْلَ الْكِتَابِ لِمَ تُحَاجُّونَ فِي إِبْرَاهِيمَ وَمَا أُنزِلَتِ التَّوْرَاةُ وَالْإِنجِيلُ إِلَّا مِن بَعْدِهِ ۚ أَفَلَا تَعْقِلُونَ','ای اهل کتاب! چرا درباره ابراهیم، گفتگو و نزاع می‌کنید (و هر کدام، او را پیرو آیین خودتان معرفی می‌نمایید)؟! در حالی که تورات و انجیل، بعد از او نازل شده است! آیا اندیشه نمی‌کنید؟!'];
ayat[359]=[3,66,'هَا أَنتُمْ هَٰؤُلَاءِ حَاجَجْتُمْ فِيمَا لَكُم بِهِ عِلْمٌ فَلِمَ تُحَاجُّونَ فِيمَا لَيْسَ لَكُم بِهِ عِلْمٌ ۚ وَاللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ','شما کسانی هستید که درباره آنچه نسبت به آن آگاه بودید، گفتگو و ستیز کردید؛ چرا درباره آنچه آگاه نیستید، گفتگو می‌کنید؟! و خدا می‌داند، و شما نمی‌دانید.'];
ayat[360]=[3,67,'مَا كَانَ إِبْرَاهِيمُ يَهُودِيًّا وَلَا نَصْرَانِيًّا وَلَٰكِن كَانَ حَنِيفًا مُّسْلِمًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ','ابراهیم نه یهودی بود و نه نصرانی؛ بلکه موحّدی خالص و مسلمان بود؛ و هرگز از مشرکان نبود.'];
ayat[361]=[3,68,'إِنَّ أَوْلَى النَّاسِ بِإِبْرَاهِيمَ لَلَّذِينَ اتَّبَعُوهُ وَهَٰذَا النَّبِيُّ وَالَّذِينَ آمَنُوا ۗ وَاللَّهُ وَلِيُّ الْمُؤْمِنِينَ','سزاوارترین مردم به ابراهیم، آنها هستند که از او پیروی کردند، و (در زمان و عصر او، به مکتب او وفادار بودند؛ همچنین) این پیامبر و کسانی که (به او) ایمان آورده‌اند (از همه سزاوارترند)؛ و خداوند، ولیّ و سرپرست مؤمنان است.'];
ayat[362]=[3,69,'وَدَّت طَّائِفَةٌ مِّنْ أَهْلِ الْكِتَابِ لَوْ يُضِلُّونَكُمْ وَمَا يُضِلُّونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَ','جمعی از اهل کتاب (از یهود)، دوست داشتند (و آرزو می‌کردند) شما را گمراه کنند؛ (امّا آنها باید بدانند که نمی‌توانند شما را گمراه سازند،) آنها گمراه نمی‌کنند مگر خودشان را، و نمی‌فهمند!'];
ayat[363]=[3,70,'يَا أَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَأَنتُمْ تَشْهَدُونَ','ای اهل کتاب! چرا به آیات خدا کافر می‌شوید، در حالی که (به درستی آن) گواهی می‌دهید؟!'];
ayat[364]=[3,71,'يَا أَهْلَ الْكِتَابِ لِمَ تَلْبِسُونَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُونَ الْحَقَّ وَأَنتُمْ تَعْلَمُونَ','ای اهل کتاب! چرا حق را با باطل (می‌زمیزید و) مشتبه می‌کنید (تا دیگران نفهمند و گمراه شوند)، و حقیقت را پوشیده می‌دارید در حالی که می‌دانید؟!'];
ayat[365]=[3,72,'وَقَالَت طَّائِفَةٌ مِّنْ أَهْلِ الْكِتَابِ آمِنُوا بِالَّذِي أُنزِلَ عَلَى الَّذِينَ آمَنُوا وَجْهَ النَّهَارِ وَاكْفُرُوا آخِرَهُ لَعَلَّهُمْ يَرْجِعُونَ','و جمعی از اهل کتاب (از یهود) گفتند: «(بروید در ظاهر) به آنچه بر مؤمنان نازل شده، در آغاز روز ایمان بیاورید؛ و در پایان روز، کافر شوید (و باز گردید)! شاید آنها (از آیین خود) بازگردند! (زیرا شما را، اهل کتاب و آگاه از بشارات آسمانی پیشین می‌دانند؛ و این توطئه کافی است که آنها را متزلزل سازد).'];
ayat[366]=[3,73,'وَلَا تُؤْمِنُوا إِلَّا لِمَن تَبِعَ دِينَكُمْ قُلْ إِنَّ الْهُدَىٰ هُدَى اللَّهِ أَن يُؤْتَىٰ أَحَدٌ مِّثْلَ مَا أُوتِيتُمْ أَوْ يُحَاجُّوكُمْ عِندَ رَبِّكُمْ ۗ قُلْ إِنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَن يَشَاءُ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ','و جز به کسی که از آیین شما پیروی می‌کند، (واقعاً) ایمان نیاورید!» بگو: «هدایت، هدایت الهی است! (و این توطئه شما، در برابر آن بی اثر است)»! (سپس اضافه کردند: «تصور نکنید) به کسی همانند شما (کتاب آسمانی) داده می‌شود، یا اینکه می‌توانند در پیشگاه پروردگارتان، با شما بحث و گفتگو کنند، (بلکه نبوّت و منطق، هر دو نزد شماست!)» بگو: «فضل (و موهبت نبوّت و عقل و منطق، در انحصار کسی نیست؛ بلکه) به دست خداست؛ و به هر کس بخواهد (و شایسته بداند،) می‌دهد؛ و خداوند، واسع [= دارای مواهب گسترده‌] و آگاه (از موارد شایسته آن) است.'];
ayat[367]=[3,74,'يَخْتَصُّ بِرَحْمَتِهِ مَن يَشَاءُ ۗ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ','هر کس را بخواهد، ویژه رحمت خود می‌کند؛ و خداوند، دارای مواهب عظیم است.»'];
ayat[368]=[3,75,'۞ وَمِنْ أَهْلِ الْكِتَابِ مَنْ إِن تَأْمَنْهُ بِقِنطَارٍ يُؤَدِّهِ إِلَيْكَ وَمِنْهُم مَّنْ إِن تَأْمَنْهُ بِدِينَارٍ لَّا يُؤَدِّهِ إِلَيْكَ إِلَّا مَا دُمْتَ عَلَيْهِ قَائِمًا ۗ ذَٰلِكَ بِأَنَّهُمْ قَالُوا لَيْسَ عَلَيْنَا فِي الْأُمِّيِّينَ سَبِيلٌ وَيَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ','و در میان اهل کتاب، کسانی هستند که اگر ثروت زیادی به رسم امانت به آنها بسپاری، به تو باز می‌گردانند؛ و کسانی هستند که اگر یک دینار هم به آنان بسپاری، به تو باز نمی‌گردانند؛ مگر تا زمانی که بالای سر آنها ایستاده (و بر آنها مسلّط) باشی! این بخاطر آن است که می‌گویند: «ما در برابر امّیّین [= غیر یهود]، مسؤول نیستیم.» و بر خدا دروغ می‌بندند؛ در حالی که می‌دانند (این سخن دروغ است).'];
ayat[369]=[3,76,'بَلَىٰ مَنْ أَوْفَىٰ بِعَهْدِهِ وَاتَّقَىٰ فَإِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ','آری، کسی که به پیمان خود وفا کند و پرهیزگاری پیشه نماید، (خدا او را دوست می‌دارد؛ زیرا) خداوند پرهیزگاران را دوست دارد.'];
ayat[370]=[3,77,'إِنَّ الَّذِينَ يَشْتَرُونَ بِعَهْدِ اللَّهِ وَأَيْمَانِهِمْ ثَمَنًا قَلِيلًا أُولَٰئِكَ لَا خَلَاقَ لَهُمْ فِي الْآخِرَةِ وَلَا يُكَلِّمُهُمُ اللَّهُ وَلَا يَنظُرُ إِلَيْهِمْ يَوْمَ الْقِيَامَةِ وَلَا يُزَكِّيهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ','کسانی که پیمان الهی و سوگندهای خود (به نام مقدس او) را به بهای ناچیزی می‌فروشند، آنها بهره‌ای در آخرت نخواهند داشت؛ و خداوند با آنها سخن نمی‌گوید و به آنان در قیامت نمی‌نگرد و آنها را (از گناه) پاک نمی‌سازد؛ و عذاب دردناکی برای آنهاست.'];
ayat[371]=[3,78,'وَإِنَّ مِنْهُمْ لَفَرِيقًا يَلْوُونَ أَلْسِنَتَهُم بِالْكِتَابِ لِتَحْسَبُوهُ مِنَ الْكِتَابِ وَمَا هُوَ مِنَ الْكِتَابِ وَيَقُولُونَ هُوَ مِنْ عِندِ اللَّهِ وَمَا هُوَ مِنْ عِندِ اللَّهِ وَيَقُولُونَ عَلَى اللَّهِ الْكَذِبَ وَهُمْ يَعْلَمُونَ','در میان آنها [= یهود] کسانی هستند که به هنگام تلاوت کتاب (خدا)، زبان خود را چنان می‌گردانند که گمان کنید (آنچه را می‌خوانند،) از کتاب (خدا) است؛ در حالی که از کتاب (خدا) نیست! (و با صراحت) می‌گویند: «آن از طرف خداست!» با اینکه از طرف خدا نیست، و به خدا دروغ می‌بندند در حالی که می‌دانند!'];
ayat[372]=[3,79,'مَا كَانَ لِبَشَرٍ أَن يُؤْتِيَهُ اللَّهُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُوا عِبَادًا لِّي مِن دُونِ اللَّهِ وَلَٰكِن كُونُوا رَبَّانِيِّينَ بِمَا كُنتُمْ تُعَلِّمُونَ الْكِتَابَ وَبِمَا كُنتُمْ تَدْرُسُونَ','برای هیچ بشری سزاوار نیست که خداوند، کتاب آسمانی و حکم و نبوّت به او دهد سپس او به مردم بگوید: «غیر از خدا، مرا پرستش کنید!» بلکه (سزاوار مقام او، این است که بگوید:) مردمی الهی باشید، آن‌گونه که کتاب خدا را می‌آموختید و درس می‌خواندید! (و غیر از خدا را پرستش نکنید!)'];
ayat[373]=[3,80,'وَلَا يَأْمُرَكُمْ أَن تَتَّخِذُوا الْمَلَائِكَةَ وَالنَّبِيِّينَ أَرْبَابًا ۗ أَيَأْمُرُكُم بِالْكُفْرِ بَعْدَ إِذْ أَنتُم مُّسْلِمُونَ','و نه اینکه به شما دستور دهد که فرشتگان و پیامبران را، پروردگار خود انتخاب کنید. آیا شما را، پس از آنکه مسلمان شدید، به کفر دعوت می‌کند؟!'];
ayat[374]=[3,81,'وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ النَّبِيِّينَ لَمَا آتَيْتُكُم مِّن كِتَابٍ وَحِكْمَةٍ ثُمَّ جَاءَكُمْ رَسُولٌ مُّصَدِّقٌ لِّمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهِ وَلَتَنصُرُنَّهُ ۚ قَالَ أَأَقْرَرْتُمْ وَأَخَذْتُمْ عَلَىٰ ذَٰلِكُمْ إِصْرِي ۖ قَالُوا أَقْرَرْنَا ۚ قَالَ فَاشْهَدُوا وَأَنَا مَعَكُم مِّنَ الشَّاهِدِينَ','و (به خاطر بیاورید) هنگامی را که خداوند، از پیامبران (و پیروان آنها)، پیمان مؤکّد گرفت، که هرگاه کتاب و دانش به شما دادم، سپس پیامبری به سوی شما آمد که آنچه را با شماست تصدیق می‌کند، به او ایمان بیاورید و او را یاری کنید! سپس (خداوند) به آنها گفت: «آیا به این موضوع، اقرار دارید؟ و بر آن، پیمان مؤکّد بستید؟» گفتند: «(آری) اقرار داریم!» (خداوند به آنها) گفت: «پس گواه باشید! و من نیز با شما از گواهانم.»'];
ayat[375]=[3,82,'فَمَن تَوَلَّىٰ بَعْدَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ','پس کسی که بعد از این (پیمان محکم)، روی گرداند، فاسق است.'];
ayat[376]=[3,83,'أَفَغَيْرَ دِينِ اللَّهِ يَبْغُونَ وَلَهُ أَسْلَمَ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ طَوْعًا وَكَرْهًا وَإِلَيْهِ يُرْجَعُونَ','آیا آنها غیر از آیین خدا می‌طلبند؟! (آیین او همین اسلام است؛) و تمام کسانی که در آسمانها و زمین هستند، از روی اختیار یا از روی اجبار، در برابرِ (فرمان) او تسلیمند، و همه به سوی او بازگردانده می‌شوند.'];
ayat[377]=[3,84,'قُلْ آمَنَّا بِاللَّهِ وَمَا أُنزِلَ عَلَيْنَا وَمَا أُنزِلَ عَلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَالنَّبِيُّونَ مِن رَّبِّهِمْ لَا نُفَرِّقُ بَيْنَ أَحَدٍ مِّنْهُمْ وَنَحْنُ لَهُ مُسْلِمُونَ','بگو: «به خدا ایمان آوردیم؛ و (همچنین) به آنچه بر ما و بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط نازل گردیده؛ و آنچه به موسی و عیسی و (دیگر) پیامبران، از طرف پروردگارشان داده شده است؛ ما در میان هیچ یک از آنان فرقی نمی‌گذاریم؛ و در برابرِ (فرمان) او تسلیم هستیم.»'];
ayat[378]=[3,85,'وَمَن يَبْتَغِ غَيْرَ الْإِسْلَامِ دِينًا فَلَن يُقْبَلَ مِنْهُ وَهُوَ فِي الْآخِرَةِ مِنَ الْخَاسِرِينَ','و هر کس جز اسلام (و تسلیم در برابر فرمان حق،) آیینی برای خود انتخاب کند، از او پذیرفته نخواهد شد؛ و او در آخرت، از زیانکاران است.'];
ayat[379]=[3,86,'كَيْفَ يَهْدِي اللَّهُ قَوْمًا كَفَرُوا بَعْدَ إِيمَانِهِمْ وَشَهِدُوا أَنَّ الرَّسُولَ حَقٌّ وَجَاءَهُمُ الْبَيِّنَاتُ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','چگونه خداوند جمعیّتی را هدایت می‌کند که بعد از ایمان و گواهی به حقّانیّت رسول و آمدن نشانه‌های روشن برای آنها، کافر شدند؟! و خدا، جمعیّت ستمکاران را هدایت نخواهد کرد!'];
ayat[380]=[3,87,'أُولَٰئِكَ جَزَاؤُهُمْ أَنَّ عَلَيْهِمْ لَعْنَةَ اللَّهِ وَالْمَلَائِكَةِ وَالنَّاسِ أَجْمَعِينَ','کیفر آنها، این است که لعن (و طرد) خداوند و فرشتگان و مردم همگی بر آنهاست.'];
ayat[381]=[3,88,'خَالِدِينَ فِيهَا لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنظَرُونَ','همواره در این لعن (و طرد و نفرین) می‌مانند؛ مجازاتشان تخفیف نمی‌یابد؛ و به آنها مهلت داده نمی‌شود.'];
ayat[382]=[3,89,'إِلَّا الَّذِينَ تَابُوا مِن بَعْدِ ذَٰلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','مگر کسانی که پس از آن، توبه کنند و اصلاح نمایند؛ (و در مقام جبران گناهان گذشته برآیند، که توبه آنها پذیرفته خواهد شد؛) زیرا خداوند، آمرزنده و بخشنده است.'];
ayat[383]=[3,90,'إِنَّ الَّذِينَ كَفَرُوا بَعْدَ إِيمَانِهِمْ ثُمَّ ازْدَادُوا كُفْرًا لَّن تُقْبَلَ تَوْبَتُهُمْ وَأُولَٰئِكَ هُمُ الضَّالُّونَ','کسانی که پس از ایمان کافر شدند و سپس بر کفر (خود) افزودند، (و در این راه اصرار ورزیدند،) هیچ‌گاه توبه آنان، (که از روی ناچاری یا در آستانه مرگ صورت می‌گیرد،) قبول نمی‌شود؛ و آنها گمراهان (واقعی) اند (چرا که هم راه خدا را گم کرده‌اند، و هم راه توبه را!).'];
ayat[384]=[3,91,'إِنَّ الَّذِينَ كَفَرُوا وَمَاتُوا وَهُمْ كُفَّارٌ فَلَن يُقْبَلَ مِنْ أَحَدِهِم مِّلْءُ الْأَرْضِ ذَهَبًا وَلَوِ افْتَدَىٰ بِهِ ۗ أُولَٰئِكَ لَهُمْ عَذَابٌ أَلِيمٌ وَمَا لَهُم مِّن نَّاصِرِينَ','کسانی که کافر شدند و در حال کفر از دنیا رفتند، اگر چه روی زمین پر از طلا باشد، و آن را بعنوان فدیه (و کفّاره اعمال بد خویش) بپردازند، هرگز از هیچ‌یک آنها قبول نخواهد شد؛ و برای آنان، مجازاتِ دردناک است؛ و یاورانی ندارند.'];
ayat[385]=[3,92,'لَن تَنَالُوا الْبِرَّ حَتَّىٰ تُنفِقُوا مِمَّا تُحِبُّونَ ۚ وَمَا تُنفِقُوا مِن شَيْءٍ فَإِنَّ اللَّهَ بِهِ عَلِيمٌ','هرگز به (حقیقت) نیکوکاری نمی‌رسید مگر اینکه از آنچه دوست می‌دارید، (در راه خدا) انفاق کنید؛ و آنچه انفاق می‌کنید، خداوند از آن آگاه است.'];
ayat[386]=[3,93,'۞ كُلُّ الطَّعَامِ كَانَ حِلًّا لِّبَنِي إِسْرَائِيلَ إِلَّا مَا حَرَّمَ إِسْرَائِيلُ عَلَىٰ نَفْسِهِ مِن قَبْلِ أَن تُنَزَّلَ التَّوْرَاةُ ۗ قُلْ فَأْتُوا بِالتَّوْرَاةِ فَاتْلُوهَا إِن كُنتُمْ صَادِقِينَ','همه غذاها (ی پاک) بر بنی اسرائیل حلال بود، جز آنچه اسرائیل (یعقوب)، پیش از نزول تورات، بر خود تحریم کرده بود؛ (مانند گوشت شتر که برای او ضرر داشت.) بگو: «اگر راست می‌گویید تورات را بیاورید و بخوانید! (این نسبتهایی که به پیامبران پیشین می‌دهید، حتّی در تورات تحریف شده شما نیست!)»'];
ayat[387]=[3,94,'فَمَنِ افْتَرَىٰ عَلَى اللَّهِ الْكَذِبَ مِن بَعْدِ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ','بنا بر این، آنها که بعد از این به خدا دروغ می‌بندند، ستمگرند! (زیرا از روی علم و عمد چنین می‌کنند).'];
ayat[388]=[3,95,'قُلْ صَدَقَ اللَّهُ ۗ فَاتَّبِعُوا مِلَّةَ إِبْرَاهِيمَ حَنِيفًا وَمَا كَانَ مِنَ الْمُشْرِكِينَ','بگو: «خدا راست گفته (و اینها در آیین پاک ابراهیم نبوده) است. بنا بر این، از آیین ابراهیم پیروی کنید، که به حق گرایش داشت، و از مشرکان نبود!»'];
ayat[389]=[3,96,'إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنَّاسِ لَلَّذِي بِبَكَّةَ مُبَارَكًا وَهُدًى لِّلْعَالَمِينَ','نخستین خانه‌ای که برای مردم (و نیایش خداوند) قرار داده شد، همان است که در سرزمین مکّه است، که پر برکت، و مایه هدایت جهانیان است.'];
ayat[390]=[3,97,'فِيهِ آيَاتٌ بَيِّنَاتٌ مَّقَامُ إِبْرَاهِيمَ ۖ وَمَن دَخَلَهُ كَانَ آمِنًا ۗ وَلِلَّهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ إِلَيْهِ سَبِيلًا ۚ وَمَن كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ عَنِ الْعَالَمِينَ','در آن، نشانه‌های روشن، (از جمله) مقام ابراهیم است؛ و هر کس داخل آن [= خانه خدا] شود؛ در امان خواهد بود، و برای خدا بر مردم است که آهنگ خانه (او) کنند، آنها که توانایی رفتن به سوی آن دارند. و هر کس کفر ورزد (و حج را ترک کند، به خود زیان رسانده)، خداوند از همه جهانیان، بی‌نیاز است.'];
ayat[391]=[3,98,'قُلْ يَا أَهْلَ الْكِتَابِ لِمَ تَكْفُرُونَ بِآيَاتِ اللَّهِ وَاللَّهُ شَهِيدٌ عَلَىٰ مَا تَعْمَلُونَ','بگو: «ای اهل کتاب! چرا به آیات خدا کفر می‌ورزید؟! و خدا گواه است بر اعمالی که انجام می‌دهید!»'];
ayat[392]=[3,99,'قُلْ يَا أَهْلَ الْكِتَابِ لِمَ تَصُدُّونَ عَن سَبِيلِ اللَّهِ مَنْ آمَنَ تَبْغُونَهَا عِوَجًا وَأَنتُمْ شُهَدَاءُ ۗ وَمَا اللَّهُ بِغَافِلٍ عَمَّا تَعْمَلُونَ','بگو: «ای اهل کتاب! چرا افرادی را که ایمان آورده‌اند، از راه خدا بازمی‌دارید، و می‌خواهید این راه را کج سازید؟! در حالی که شما (به درستی این راه) گواه هستید؛ و خداوند از آنچه انجام می‌دهید، غافل نیست!»'];
ayat[393]=[3,100,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِن تُطِيعُوا فَرِيقًا مِّنَ الَّذِينَ أُوتُوا الْكِتَابَ يَرُدُّوكُم بَعْدَ إِيمَانِكُمْ كَافِرِينَ','ای کسانی که ایمان آورده‌اید! اگر از گروهی از اهل کتاب، (که کارشان نفاق‌افکنی و شعله‌ورساختنِ آتش کینه و عداوت است) اطاعت کنید، شما را پس از ایمان، به کفر بازمی‌گردانند.'];
ayat[394]=[3,101,'وَكَيْفَ تَكْفُرُونَ وَأَنتُمْ تُتْلَىٰ عَلَيْكُمْ آيَاتُ اللَّهِ وَفِيكُمْ رَسُولُهُ ۗ وَمَن يَعْتَصِم بِاللَّهِ فَقَدْ هُدِيَ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','و چگونه ممکن است شما کافر شوید، با اینکه (در دامان وحی قرار گرفته‌اید، و) آیات خدا بر شما خوانده می‌شود، و پیامبر او در میان شماست؟! (بنابر این، به خدا تمسّک جویید!) و هر کس به خدا تمسّک جوید، به راهی راست، هدایت شده است.'];
ayat[395]=[3,102,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ حَقَّ تُقَاتِهِ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسْلِمُونَ','ای کسانی که ایمان آورده‌اید! آن گونه که حق تقوا و پرهیزکاری است، از خدا بپرهیزید! و از دنیا نروید، مگر اینکه مسلمان باشید! (باید گوهر ایمان را تا پایان عمر، حفظ کنید!)'];
ayat[396]=[3,103,'وَاعْتَصِمُوا بِحَبْلِ اللَّهِ جَمِيعًا وَلَا تَفَرَّقُوا ۚ وَاذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ كُنتُمْ أَعْدَاءً فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُم بِنِعْمَتِهِ إِخْوَانًا وَكُنتُمْ عَلَىٰ شَفَا حُفْرَةٍ مِّنَ النَّارِ فَأَنقَذَكُم مِّنْهَا ۗ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَهْتَدُونَ','و همگی به ریسمان خدا [= قرآن و اسلام، و هرگونه وسیله وحدت‌]، چنگ زنید، و پراکنده نشوید! و نعمت (بزرگِ) خدا را بر خود، به یاد آرید که چگونه دشمن یکدیگر بودید، و او میان دلهای شما، الفت ایجاد کرد، و به برکتِ نعمتِ او، برادر شدید! و شما بر لبِ حفره‌ای از آتش بودید، خدا شما را از آن نجات داد؛ این چنین، خداوند آیات خود را برای شما آشکار می‌سازد؛ شاید پذیرای هدایت شوید.'];
ayat[397]=[3,104,'وَلْتَكُن مِّنكُمْ أُمَّةٌ يَدْعُونَ إِلَى الْخَيْرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنكَرِ ۚ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','باید از میان شما، جمعی دعوت به نیکی، و امر به معروف و نهی از منکر کنند! و آنها همان رستگارانند.'];
ayat[398]=[3,105,'وَلَا تَكُونُوا كَالَّذِينَ تَفَرَّقُوا وَاخْتَلَفُوا مِن بَعْدِ مَا جَاءَهُمُ الْبَيِّنَاتُ ۚ وَأُولَٰئِكَ لَهُمْ عَذَابٌ عَظِيمٌ','و مانند کسانی نباشید که پراکنده شدند و اختلاف کردند؛ (آن هم) پس از آنکه نشانه‌های روشن (پروردگار) به آنان رسید! و آنها عذاب عظیمی دارند.'];
ayat[399]=[3,106,'يَوْمَ تَبْيَضُّ وُجُوهٌ وَتَسْوَدُّ وُجُوهٌ ۚ فَأَمَّا الَّذِينَ اسْوَدَّتْ وُجُوهُهُمْ أَكَفَرْتُم بَعْدَ إِيمَانِكُمْ فَذُوقُوا الْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَ','(آن عذاب عظیم) روزی خواهد بود که چهره‌هائی سفید، و چهره‌هائی سیاه می‌گردد، اما آنها که صورتهایشان سیاه شده، (به آنها گفته می‌شود:) آیا بعد از ایمان، و (اخوّت و برادری در سایه آن،) کافر شدید؟! پس بچشید عذاب را، به سبب آنچه کفر می‌ورزیدید!'];
ayat[400]=[3,107,'وَأَمَّا الَّذِينَ ابْيَضَّتْ وُجُوهُهُمْ فَفِي رَحْمَةِ اللَّهِ هُمْ فِيهَا خَالِدُونَ','و امّا آنها که چهره‌هایشان سفید شده، در رحمت خداوند خواهند بود؛ و جاودانه در آن می‌مانند.'];
ayat[401]=[3,108,'تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۗ وَمَا اللَّهُ يُرِيدُ ظُلْمًا لِّلْعَالَمِينَ','اینها آیات خداست؛ که بحق بر تو می‌خوانیم. و خداوند (هیچ گاه) ستمی برای (احدی از) جهانیان نمی‌خواهد.'];
ayat[402]=[3,109,'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ','و (چگونه ممکن است خدا ستم کند؟! در حالی که) آنچه در آسمانها و آنچه در زمین است، مال اوست؛ و همه کارها، به سوی او باز می‌گردد (و به فرمان اوست.)'];
ayat[403]=[3,110,'كُنتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَتَنْهَوْنَ عَنِ الْمُنكَرِ وَتُؤْمِنُونَ بِاللَّهِ ۗ وَلَوْ آمَنَ أَهْلُ الْكِتَابِ لَكَانَ خَيْرًا لَّهُم ۚ مِّنْهُمُ الْمُؤْمِنُونَ وَأَكْثَرُهُمُ الْفَاسِقُونَ','شما بهترین امتی بودید که به سود انسانها آفریده شده‌اند؛ (چه اینکه) امر به معروف و نهی از منکر می‌کنید و به خدا ایمان دارید. و اگر اهل کتاب، (به چنین برنامه و آیین درخشانی،) ایمان آورند، برای آنها بهتر است! (ولی تنها) عده کمی از آنها با ایمانند، و بیشتر آنها فاسقند، (و خارج از اطاعت پروردگار)'];
ayat[404]=[3,111,'لَن يَضُرُّوكُمْ إِلَّا أَذًى ۖ وَإِن يُقَاتِلُوكُمْ يُوَلُّوكُمُ الْأَدْبَارَ ثُمَّ لَا يُنصَرُونَ','آنها [= اهل کتاب، مخصوصا» یهود] هرگز نمی‌توانند به شما زیان برسانند، جز آزارهای مختصر؛ و اگر با شما پیکار کنند، به شما پشت خواهند کرد (و شکست می‌خورند؛ سپس کسی آنها را یاری نمی‌کند.'];
ayat[405]=[3,112,'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ أَيْنَ مَا ثُقِفُوا إِلَّا بِحَبْلٍ مِّنَ اللَّهِ وَحَبْلٍ مِّنَ النَّاسِ وَبَاءُوا بِغَضَبٍ مِّنَ اللَّهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ۚ ذَٰلِكَ بِأَنَّهُمْ كَانُوا يَكْفُرُونَ بِآيَاتِ اللَّهِ وَيَقْتُلُونَ الْأَنبِيَاءَ بِغَيْرِ حَقٍّ ۚ ذَٰلِكَ بِمَا عَصَوا وَّكَانُوا يَعْتَدُونَ','هر جا یافت شوند، مهر ذلت بر آنان خورده است؛ مگر با ارتباط به خدا، (و تجدید نظر در روش ناپسند خود،) و (یا) با ارتباط به مردم (و وابستگی به این و آن)؛ و به خشم خدا، گرفتار شده‌اند؛ و مهر بیچارگی بر آنها زده شده؛ چرا که آنها به آیات خدا، کفر می‌ورزیدند و پیامبران را بناحق می‌کشتند. اینها بخاطر آن است که گناه کردند؛ و (به حقوق دیگران،) تجاوز می‌نمودند.'];
ayat[406]=[3,113,'۞ لَيْسُوا سَوَاءً ۗ مِّنْ أَهْلِ الْكِتَابِ أُمَّةٌ قَائِمَةٌ يَتْلُونَ آيَاتِ اللَّهِ آنَاءَ اللَّيْلِ وَهُمْ يَسْجُدُونَ','آنها همه یکسان نیستند؛ از اهل کتاب، جمعیّتی هستند که (به حق و ایمان) قیام می‌کنند؛ و پیوسته در اوقات شب، آیات خدا را می‌خوانند؛ در حالی که سجده می‌نمایند.'];
ayat[407]=[3,114,'يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنكَرِ وَيُسَارِعُونَ فِي الْخَيْرَاتِ وَأُولَٰئِكَ مِنَ الصَّالِحِينَ','به خدا و روز دیگر ایمان می‌آورند؛ امر به معروف و نهی از منکر می‌کنند؛ و در انجام کارهای نیک، پیشی می‌گیرند؛ و آنها از صالحانند.'];
ayat[408]=[3,115,'وَمَا يَفْعَلُوا مِنْ خَيْرٍ فَلَن يُكْفَرُوهُ ۗ وَاللَّهُ عَلِيمٌ بِالْمُتَّقِينَ','و آنچه از اعمال نیک انجام دهند، هرگز کفران نخواهد شد! (و پاداش شایسته آن را می‌بینند.) و خدا از پرهیزکاران، آگاه است.'];
ayat[409]=[3,116,'إِنَّ الَّذِينَ كَفَرُوا لَن تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُم مِّنَ اللَّهِ شَيْئًا ۖ وَأُولَٰئِكَ أَصْحَابُ النَّارِ ۚ هُمْ فِيهَا خَالِدُونَ','کسانی که کافر شدند، هرگز نمی‌توانند در پناه اموال و فرزندانشان، از مجازات خدا در امان بمانند! آنها اصحاب دوزخند؛ و جاودانه در آن خواهند ماند.'];
ayat[410]=[3,117,'مَثَلُ مَا يُنفِقُونَ فِي هَٰذِهِ الْحَيَاةِ الدُّنْيَا كَمَثَلِ رِيحٍ فِيهَا صِرٌّ أَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوا أَنفُسَهُمْ فَأَهْلَكَتْهُ ۚ وَمَا ظَلَمَهُمُ اللَّهُ وَلَٰكِنْ أَنفُسَهُمْ يَظْلِمُونَ','آنچه آنها در این زندگی پست دنیوی انفاق می‌کنند، همانند باد سوزانی است که به زراعت قومی که بر خود ستم کرده (و در غیر محل و وقت مناسب، کشت نموده‌اند)، بوزد؛ و آن را نابود سازد. خدا به آنها ستم نکرده؛ بلکه آنها، خودشان به خویشتن ستم می‌کنند.'];
ayat[411]=[3,118,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا بِطَانَةً مِّن دُونِكُمْ لَا يَأْلُونَكُمْ خَبَالًا وَدُّوا مَا عَنِتُّمْ قَدْ بَدَتِ الْبَغْضَاءُ مِنْ أَفْوَاهِهِمْ وَمَا تُخْفِي صُدُورُهُمْ أَكْبَرُ ۚ قَدْ بَيَّنَّا لَكُمُ الْآيَاتِ ۖ إِن كُنتُمْ تَعْقِلُونَ','ای کسانی که ایمان آورده‌اید! محرم اسراری از غیر خود، انتخاب نکنید! آنها از هرگونه شرّ و فسادی در باره شما، کوتاهی نمی‌کنند. آنها دوست دارند شما در رنج و زحمت باشید. (نشانه‌های) دشمنی از دهان (و کلام) شان آشکار شده؛ و آنچه در دلهایشان پنهان می‌دارند، از آن مهمتر است. ما آیات (و راه‌های پیشگیری از شرّ آنها) را برای شما بیان کردیم اگر اندیشه کنید!'];
ayat[412]=[3,119,'هَا أَنتُمْ أُولَاءِ تُحِبُّونَهُمْ وَلَا يُحِبُّونَكُمْ وَتُؤْمِنُونَ بِالْكِتَابِ كُلِّهِ وَإِذَا لَقُوكُمْ قَالُوا آمَنَّا وَإِذَا خَلَوْا عَضُّوا عَلَيْكُمُ الْأَنَامِلَ مِنَ الْغَيْظِ ۚ قُلْ مُوتُوا بِغَيْظِكُمْ ۗ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ','شما کسانی هستید که آنها را دوست می‌دارید؛ امّا آنها شما را دوست ندارند! در حالی که شما به همه کتابهای آسمانی ایمان دارید (و آنها به کتاب آسمانی شما ایمان ندارند). هنگامی که شما را ملاقات می‌کنند، (به دروغ) می‌گویند: «ایمان آورده‌ایم!» امّا هنگامی که تنها می‌شوند، از شدّت خشم بر شما، سر انگشتان خود را به دندان می‌گزند! بگو: «با همین خشمی که دارید بمیرید! خدا از (اسرار) درون سینه‌ها آگاه است.»'];
ayat[413]=[3,120,'إِن تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْ وَإِن تُصِبْكُمْ سَيِّئَةٌ يَفْرَحُوا بِهَا ۖ وَإِن تَصْبِرُوا وَتَتَّقُوا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْئًا ۗ إِنَّ اللَّهَ بِمَا يَعْمَلُونَ مُحِيطٌ','اگر نیکی به شما برسد، آنها را ناراحت می‌کند؛ و اگر حادثه ناگواری برای شما رخ دهد، خوشحال می‌شوند. (امّا) اگر (در برابرشان) استقامت و پرهیزگاری پیشه کنید، نقشه‌های (خائنانه) آنان، به شما زیانی نمی‌رساند؛ خداوند به آنچه انجام می‌دهند، احاطه دارد.'];
ayat[414]=[3,121,'وَإِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّئُ الْمُؤْمِنِينَ مَقَاعِدَ لِلْقِتَالِ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','و (به یاد آور) زمانی را که صبحگاهان، از میان خانواده خود، جهت انتخاب اردوگاه جنگ برای مؤمنان، بیرون رفتی! و خداوند، شنوا و داناست (گفتگوهای مختلفی را که درباره طرح جنگ گفته می‌شد، می‌شنید؛ و اندیشه‌هایی را که بعضی در سر می‌پروراندند، می‌دانست).'];
ayat[415]=[3,122,'إِذْ هَمَّت طَّائِفَتَانِ مِنكُمْ أَن تَفْشَلَا وَاللَّهُ وَلِيُّهُمَا ۗ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','(و نیز به یاد آور) زمانی را که دو طایفه از شما تصمیم گرفتند سستی نشان دهند (و از وسط راه بازگردند)؛ و خداوند پشتیبان آنها بود (و به آنها کمک کرد که از این فکر بازگردند)؛ و افراد باایمان، باید تنها بر خدا توکّل کنند!'];
ayat[416]=[3,123,'وَلَقَدْ نَصَرَكُمُ اللَّهُ بِبَدْرٍ وَأَنتُمْ أَذِلَّةٌ ۖ فَاتَّقُوا اللَّهَ لَعَلَّكُمْ تَشْكُرُونَ','خداوند شما را در «بدر» یاری کرد (و بر دشمنان خطرناک، پیروز ساخت)؛ در حالی که شما (نسبت به آنها)، ناتوان بودید. پس، از خدا بپرهیزید (و در برابر دشمن، مخالفتِ فرمانِ پیامبر نکنید)، تا شکر نعمت او را بجا آورده باشید!'];
ayat[417]=[3,124,'إِذْ تَقُولُ لِلْمُؤْمِنِينَ أَلَن يَكْفِيَكُمْ أَن يُمِدَّكُمْ رَبُّكُم بِثَلَاثَةِ آلَافٍ مِّنَ الْمَلَائِكَةِ مُنزَلِينَ','در آن هنگام که تو به مؤمنان می‌گفتنی: «آیا کافی نیست که پروردگارتان، شما را به سه هزار نفر از فرشتگان، که از آسمان فرود می‌آیند، یاری کند؟!»'];
ayat[418]=[3,125,'بَلَىٰ ۚ إِن تَصْبِرُوا وَتَتَّقُوا وَيَأْتُوكُم مِّن فَوْرِهِمْ هَٰذَا يُمْدِدْكُمْ رَبُّكُم بِخَمْسَةِ آلَافٍ مِّنَ الْمَلَائِكَةِ مُسَوِّمِينَ','آری، (امروز هم) اگر استقامت و تقوا پیشه کنید، و دشمن به همین زودی به سراغ شما بیاید، خداوند شما را به پنج هزار نفر از فرشتگان، که نشانه‌هایی با خود دارند، مدد خواهد داد!'];
ayat[419]=[3,126,'وَمَا جَعَلَهُ اللَّهُ إِلَّا بُشْرَىٰ لَكُمْ وَلِتَطْمَئِنَّ قُلُوبُكُم بِهِ ۗ وَمَا النَّصْرُ إِلَّا مِنْ عِندِ اللَّهِ الْعَزِيزِ الْحَكِيمِ','ولی اینها را خداوند فقط بشارت، و برای اطمینان خاطر شما قرار داده؛ وگرنه، پیروزی تنها از جانب خداوند توانای حکیم است!'];
ayat[420]=[3,127,'لِيَقْطَعَ طَرَفًا مِّنَ الَّذِينَ كَفَرُوا أَوْ يَكْبِتَهُمْ فَيَنقَلِبُوا خَائِبِينَ','(این وعده را که خدا به شما داده،) برای این است که قسمتی از پیکر لشکر کافران را قطع کند؛ یا آنها را با ذلّت برگرداند؛ تا مأیوس و ناامید، (به وطن خود) بازگردند.'];
ayat[421]=[3,128,'لَيْسَ لَكَ مِنَ الْأَمْرِ شَيْءٌ أَوْ يَتُوبَ عَلَيْهِمْ أَوْ يُعَذِّبَهُمْ فَإِنَّهُمْ ظَالِمُونَ','هیچ‌گونه اختیاری (در باره عفو کافران، یا مؤمنان فراری از جنگ،) برای تو نیست؛ مگر اینکه (خدا) بخواهد آنها را ببخشد، یا مجازات کند؛ زیرا آنها ستمگرند.'];
ayat[422]=[3,129,'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ يَغْفِرُ لِمَن يَشَاءُ وَيُعَذِّبُ مَن يَشَاءُ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','و آنچه در آسمانها و زمین است، از آن خداست. هر کس را بخواهد (و شایسته بداند)، می‌بخشد؛ و هر کس را بخواهد، مجازات می‌کند؛ و خداوند آمرزنده مهربان است.'];
ayat[423]=[3,130,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَأْكُلُوا الرِّبَا أَضْعَافًا مُّضَاعَفَةً ۖ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ','ای کسانی که ایمان آورده‌اید! ربا (و سود پول) را چند برابر نخورید! از خدا بپرهیزید، تا رستگار شوید!'];
ayat[424]=[3,131,'وَاتَّقُوا النَّارَ الَّتِي أُعِدَّتْ لِلْكَافِرِينَ','و از آتشی بپرهیزید که برای کافران آماده شده است!'];
ayat[425]=[3,132,'وَأَطِيعُوا اللَّهَ وَالرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ','و خدا و پیامبر را اطاعت کنید، تا مشمول رحمت شوید!'];
ayat[426]=[3,133,'۞ وَسَارِعُوا إِلَىٰ مَغْفِرَةٍ مِّن رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمَاوَاتُ وَالْأَرْضُ أُعِدَّتْ لِلْمُتَّقِينَ','و شتاب کنید برای رسیدن به آمرزش پروردگارتان؛ و بهشتی که وسعت آن، آسمانها و زمین است؛ و برای پرهیزگاران آماده شده است.'];
ayat[427]=[3,134,'الَّذِينَ يُنفِقُونَ فِي السَّرَّاءِ وَالضَّرَّاءِ وَالْكَاظِمِينَ الْغَيْظَ وَالْعَافِينَ عَنِ النَّاسِ ۗ وَاللَّهُ يُحِبُّ الْمُحْسِنِينَ','همانها که در توانگری و تنگدستی، انفاق می‌کنند؛ و خشم خود را فرو می‌برند؛ و از خطای مردم درمی‌گذرند؛ و خدا نیکوکاران را دوست دارد.'];
ayat[428]=[3,135,'وَالَّذِينَ إِذَا فَعَلُوا فَاحِشَةً أَوْ ظَلَمُوا أَنفُسَهُمْ ذَكَرُوا اللَّهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْ وَمَن يَغْفِرُ الذُّنُوبَ إِلَّا اللَّهُ وَلَمْ يُصِرُّوا عَلَىٰ مَا فَعَلُوا وَهُمْ يَعْلَمُونَ','و آنها که وقتی مرتکب عمل زشتی شوند، یا به خود ستم کنند، به یاد خدا می‌افتند؛ و برای گناهان خود، طلب آمرزش می‌کنند -و کیست جز خدا که گناهان را ببخشد؟- و بر گناه، اصرار نمی‌ورزند، با اینکه می‌دانند.'];
ayat[429]=[3,136,'أُولَٰئِكَ جَزَاؤُهُم مَّغْفِرَةٌ مِّن رَّبِّهِمْ وَجَنَّاتٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَنِعْمَ أَجْرُ الْعَامِلِينَ','آنها پاداششان آمرزش پروردگار، و بهشتهایی است که از زیر درختانش، نهرها جاری است؛ جاودانه در آن میمانند؛ چه نیکو است پاداش اهل عمل!'];
ayat[430]=[3,137,'قَدْ خَلَتْ مِن قَبْلِكُمْ سُنَنٌ فَسِيرُوا فِي الْأَرْضِ فَانظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ','پیش از شما، سنّت‌هایی وجود داشت؛ (و هر قوم، طبق اعمال و صفات خود، سرنوشتهایی داشتند؛ که شما نیز، همانند آن را دارید.) پس در روی زمین، گردش کنید و ببینید سرانجام تکذیب‌کنندگان (آیات خدا) چگونه بود؟!'];
ayat[431]=[3,138,'هَٰذَا بَيَانٌ لِّلنَّاسِ وَهُدًى وَمَوْعِظَةٌ لِّلْمُتَّقِينَ','این، بیانی است برای عموم مردم؛ و هدایت و اندرزی است برای پرهیزگاران!'];
ayat[432]=[3,139,'وَلَا تَهِنُوا وَلَا تَحْزَنُوا وَأَنتُمُ الْأَعْلَوْنَ إِن كُنتُم مُّؤْمِنِينَ','و سست نشوید! و غمگین نگردید! و شما برترید اگر ایمان داشته باشید!'];
ayat[433]=[3,140,'إِن يَمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِّثْلُهُ ۚ وَتِلْكَ الْأَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِ وَلِيَعْلَمَ اللَّهُ الَّذِينَ آمَنُوا وَيَتَّخِذَ مِنكُمْ شُهَدَاءَ ۗ وَاللَّهُ لَا يُحِبُّ الظَّالِمِينَ','اگر (در میدان احد،) به شما جراحتی رسید (و ضربه‌ای وارد شد)، به آن جمعیّت نیز (در میدان بدر)، جراحتی همانند آن وارد گردید. و ما این روزها(ی پیروزی و شکست) را در میان مردم می‌گردانیم؛ (-و این خاصیّت زندگی دنیاست-) تا خدا، افرادی را که ایمان آورده‌اند، بداند (و شناخته شوند)؛ و خداوند از میان شما، شاهدانی بگیرد. و خدا ظالمان را دوست نمی‌دارد.'];
ayat[434]=[3,141,'وَلِيُمَحِّصَ اللَّهُ الَّذِينَ آمَنُوا وَيَمْحَقَ الْكَافِرِينَ','و تا خداوند، افراد باایمان را خالص گرداند (و ورزیده شوند)؛ و کافران را به تدریج نابود سازد.'];
ayat[435]=[3,142,'أَمْ حَسِبْتُمْ أَن تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنكُمْ وَيَعْلَمَ الصَّابِرِينَ','آیا چنین پنداشتید که (تنها با ادّعای ایمان) وارد بهشت خواهید شد، در حالی که خداوند هنوز مجاهدان از شما و صابران را مشخصّ نساخته است؟!'];
ayat[436]=[3,143,'وَلَقَدْ كُنتُمْ تَمَنَّوْنَ الْمَوْتَ مِن قَبْلِ أَن تَلْقَوْهُ فَقَدْ رَأَيْتُمُوهُ وَأَنتُمْ تَنظُرُونَ','و شما مرگ (و شهادت در راه خدا) را، پیش از آنکه با آن روبه‌رو شوید، آرزو می‌کردید؛ سپس آن را با چشم خود دیدید، در حالی که به آن نگاه می‌کردید (و حاضر نبودید به آن تن دردهید! چقدر میان گفتار و کردار شما فاصله است؟!)'];
ayat[437]=[3,144,'وَمَا مُحَمَّدٌ إِلَّا رَسُولٌ قَدْ خَلَتْ مِن قَبْلِهِ الرُّسُلُ ۚ أَفَإِن مَّاتَ أَوْ قُتِلَ انقَلَبْتُمْ عَلَىٰ أَعْقَابِكُمْ ۚ وَمَن يَنقَلِبْ عَلَىٰ عَقِبَيْهِ فَلَن يَضُرَّ اللَّهَ شَيْئًا ۗ وَسَيَجْزِي اللَّهُ الشَّاكِرِينَ','محمد (ص) فقط فرستاده خداست؛ و پیش از او، فرستادگان دیگری نیز بودند؛ آیا اگر او بمیرد و یا کشته شود، شما به عقب برمی‌گردید؟ (و اسلام را رها کرده به دوران جاهلیّت و کفر بازگشت خواهید نمود؟) و هر کس به عقب باز گردد، هرگز به خدا ضرری نمی‌زند؛ و خداوند بزودی شاکران (و استقامت‌کنندگان) را پاداش خواهد داد.'];
ayat[438]=[3,145,'وَمَا كَانَ لِنَفْسٍ أَن تَمُوتَ إِلَّا بِإِذْنِ اللَّهِ كِتَابًا مُّؤَجَّلًا ۗ وَمَن يُرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهِ مِنْهَا وَمَن يُرِدْ ثَوَابَ الْآخِرَةِ نُؤْتِهِ مِنْهَا ۚ وَسَنَجْزِي الشَّاكِرِينَ','هیچ‌کس، جز به فرمان خدا، نمی‌میرد؛ سرنوشتی است تعیین شده؛ (بنابر این، مرگ پیامبر یا دیگران، یک سنّت الهی است.) هر کس پاداش دنیا را بخواهد (و در زندگی خود، در این راه گام بردارد،) چیزی از آن به او خواهیم داد؛ و هر کس پاداش آخرت را بخواهد، از آن به او می‌دهیم؛ و بزودی سپاسگزاران را پاداش خواهیم داد.'];
ayat[439]=[3,146,'وَكَأَيِّن مِّن نَّبِيٍّ قَاتَلَ مَعَهُ رِبِّيُّونَ كَثِيرٌ فَمَا وَهَنُوا لِمَا أَصَابَهُمْ فِي سَبِيلِ اللَّهِ وَمَا ضَعُفُوا وَمَا اسْتَكَانُوا ۗ وَاللَّهُ يُحِبُّ الصَّابِرِينَ','چه بسیار پیامبرانی که مردان الهی فراوانی به همراه آنان جنگ کردند! آنها هیچ‌گاه در برابر آنچه در راه خدا به آنان می‌رسید، سست و ناتوان نشدند (و تن به تسلیم ندادند)؛ و خداوند استقامت‌کنندگان را دوست دارد.'];
ayat[440]=[3,147,'وَمَا كَانَ قَوْلَهُمْ إِلَّا أَن قَالُوا رَبَّنَا اغْفِرْ لَنَا ذُنُوبَنَا وَإِسْرَافَنَا فِي أَمْرِنَا وَثَبِّتْ أَقْدَامَنَا وَانصُرْنَا عَلَى الْقَوْمِ الْكَافِرِينَ','سخنشان تنها این بود که: «پروردگارا! گناهان ما را ببخش! و از تندرویهای ما در کارها، چشم‌پوشی کن! قدمهای ما را استوار بدار! و ما را بر جمعیّت کافران، پیروز گردان!'];
ayat[441]=[3,148,'فَآتَاهُمُ اللَّهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْآخِرَةِ ۗ وَاللَّهُ يُحِبُّ الْمُحْسِنِينَ','از این‌رو خداوند پاداش این جهان، و پاداش نیک آن جهان را به آنها داد؛ و خداوند نیکوکاران را دوست می‌دارد.'];
ayat[442]=[3,149,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِن تُطِيعُوا الَّذِينَ كَفَرُوا يَرُدُّوكُمْ عَلَىٰ أَعْقَابِكُمْ فَتَنقَلِبُوا خَاسِرِينَ','ای کسانی که ایمان آورده‌اید! اگر از کسانی که کافر شده‌اند اطاعت کنید، شما را به گذشته‌هایتان بازمی‌گردانند؛ و سرانجام، زیانکار خواهید شد.'];
ayat[443]=[3,150,'بَلِ اللَّهُ مَوْلَاكُمْ ۖ وَهُوَ خَيْرُ النَّاصِرِينَ','(آنها تکیه‌گاه شما نیستند،) بلکه ولی و سرپرست شما، خداست؛ و او بهترین یاوران است.'];
ayat[444]=[3,151,'سَنُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ بِمَا أَشْرَكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا ۖ وَمَأْوَاهُمُ النَّارُ ۚ وَبِئْسَ مَثْوَى الظَّالِمِينَ','بزودی در دلهای کافران، بخاطر اینکه بدون دلیل، چیزهایی را برای خدا همتا قرار دادند، رعب و ترس می‌افکنیم؛ و جایگاه آنها، آتش است؛ و چه بد جایگاهی است جایگاه ستمکاران!'];
ayat[445]=[3,152,'وَلَقَدْ صَدَقَكُمُ اللَّهُ وَعْدَهُ إِذْ تَحُسُّونَهُم بِإِذْنِهِ ۖ حَتَّىٰ إِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِي الْأَمْرِ وَعَصَيْتُم مِّن بَعْدِ مَا أَرَاكُم مَّا تُحِبُّونَ ۚ مِنكُم مَّن يُرِيدُ الدُّنْيَا وَمِنكُم مَّن يُرِيدُ الْآخِرَةَ ۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ ۖ وَلَقَدْ عَفَا عَنكُمْ ۗ وَاللَّهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ','خداوند، وعده خود را به شما، (در باره پیروزی بر دشمن در احد،) تحقق بخشید؛ در آن هنگام (که در آغاز جنگ،) دشمنان را به فرمان او، به قتل می‌رساندید؛ (و این پیروزی ادامه داشت) تا اینکه سست شدید؛ و (بر سر رهاکردن سنگرها،) در کار خود به نزاع پرداختید؛ و بعد از آن که آنچه را دوست می‌داشتید (از غلبه بر دشمن) به شما نشان داد، نافرمانی کردید. بعضی از شما، خواهان دنیا بودند؛ و بعضی خواهان آخرت. سپس خداوند شما را از آنان منصرف ساخت؛ (و پیروزی شما به شکست انجامید؛) تا شما را آزمایش کند. و او شما را بخشید؛ و خداوند نسبت به مؤمنان، فضل و بخشش دارد.'];
ayat[446]=[3,153,'۞ إِذْ تُصْعِدُونَ وَلَا تَلْوُونَ عَلَىٰ أَحَدٍ وَالرَّسُولُ يَدْعُوكُمْ فِي أُخْرَاكُمْ فَأَثَابَكُمْ غَمًّا بِغَمٍّ لِّكَيْلَا تَحْزَنُوا عَلَىٰ مَا فَاتَكُمْ وَلَا مَا أَصَابَكُمْ ۗ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ','(به خاطر بیاورید) هنگامی را که از کوه بالا میرفتید؛ و جمعی در وسط بیابان پراکنده شدند؛ و از شدت وحشت،) به عقب ماندگان نگاه نمی‌کردید، و پیامبر از پشت سر، شما را صدا می‌زد. سپس اندوه‌ها را یکی پس از دیگری به شما جزا داد؛ این بخاطر آن بود که دیگر برای از دست رفتن (غنایم جنگی) غمگین نشوید، و نه بخاطر مصیبت‌هایی که بر شما وارد می‌گردد. و خداوند از آنچه انجام می‌دهید، آگاه است.'];
ayat[447]=[3,154,'ثُمَّ أَنزَلَ عَلَيْكُم مِّن بَعْدِ الْغَمِّ أَمَنَةً نُّعَاسًا يَغْشَىٰ طَائِفَةً مِّنكُمْ ۖ وَطَائِفَةٌ قَدْ أَهَمَّتْهُمْ أَنفُسُهُمْ يَظُنُّونَ بِاللَّهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ ۖ يَقُولُونَ هَل لَّنَا مِنَ الْأَمْرِ مِن شَيْءٍ ۗ قُلْ إِنَّ الْأَمْرَ كُلَّهُ لِلَّهِ ۗ يُخْفُونَ فِي أَنفُسِهِم مَّا لَا يُبْدُونَ لَكَ ۖ يَقُولُونَ لَوْ كَانَ لَنَا مِنَ الْأَمْرِ شَيْءٌ مَّا قُتِلْنَا هَاهُنَا ۗ قُل لَّوْ كُنتُمْ فِي بُيُوتِكُمْ لَبَرَزَ الَّذِينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ إِلَىٰ مَضَاجِعِهِمْ ۖ وَلِيَبْتَلِيَ اللَّهُ مَا فِي صُدُورِكُمْ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمْ ۗ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','سپس بدنبال این غم و اندوه، آرامشی بر شما فرستاد. این آرامش، بصورت خواب سبکی بود که (در شب بعد از حادثه احد،) گروهی از شما را فرا گرفت؛ اماگروه دیگری در فکر جان خویش بودند؛ (و خواب به چشمانشان نرفت.) آنها گمانهای نادرستی -همچون گمانهای دوران جاهلیت- درباره خدا داشتند؛ و می‌گفتند: «آیا چیزی از پیروزی نصیب ما می‌شود؟!» بگو: «همه کارها (و پیروزیها) به دست خداست!» آنها در دل خود، چیزی را پنهان می‌دارند که برای تو آشکار نمی‌سازند؛ می‌گویند: «اگر ما سهمی از پیروزی داشتیم، در این جا کشته نمی‌شدیم!» بگو: «اگر هم در خانه‌های خود بودید، آنهایی که کشته‌شدن بر آنها مقرر شده بود، قطعاً به سوی آرامگاه‌های خود، بیرون می‌آمدند (و آنها را به قتل می‌رساندند). و اینها برای این است که خداوند، آنچه در سینه‌هایتان پنهان دارید، بیازماید؛ و آنچه را در دلهای شما (از ایمان) است، خالص گرداند؛ و خداوند از آنچه در درون سینه‌هاست، با خبر است.'];
ayat[448]=[3,155,'إِنَّ الَّذِينَ تَوَلَّوْا مِنكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطَانُ بِبَعْضِ مَا كَسَبُوا ۖ وَلَقَدْ عَفَا اللَّهُ عَنْهُمْ ۗ إِنَّ اللَّهَ غَفُورٌ حَلِيمٌ','کسانی که در روز روبرو شدن دو جمعیت با یکدیگر (در جنگ احد)، فرار کردند، شیطان آنها را بر اثر بعضی از گناهانی که مرتکب شده بودند، به لغزش انداخت؛ و خداوند آنها را بخشید. خداوند، آمرزنده و بردبار است.'];
ayat[449]=[3,156,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَكُونُوا كَالَّذِينَ كَفَرُوا وَقَالُوا لِإِخْوَانِهِمْ إِذَا ضَرَبُوا فِي الْأَرْضِ أَوْ كَانُوا غُزًّى لَّوْ كَانُوا عِندَنَا مَا مَاتُوا وَمَا قُتِلُوا لِيَجْعَلَ اللَّهُ ذَٰلِكَ حَسْرَةً فِي قُلُوبِهِمْ ۗ وَاللَّهُ يُحْيِي وَيُمِيتُ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','ای کسانی که ایمان آورده‌اید! همانند کافران نباشید که چون برادرانشان به مسافرتی می‌روند، یا در جنگ شرکت می‌کنند (و از دنیا می‌روند و یا کشته می‌شوند)، می‌گویند: «اگر آنها نزد ما بودند، نمی‌مردند و کشته نمی‌شدند!» (شما از این گونه سخنان نگویید،) تا خدا این حسرت را بر دل آنها [= کافران‌] بگذارد. خداوند، زنده می‌کند و می‌میراند؛ (و زندگی و مرگ، به دست اوست؛) و خدا به آنچه انجام می‌دهید، بیناست.'];
ayat[450]=[3,157,'وَلَئِن قُتِلْتُمْ فِي سَبِيلِ اللَّهِ أَوْ مُتُّمْ لَمَغْفِرَةٌ مِّنَ اللَّهِ وَرَحْمَةٌ خَيْرٌ مِّمَّا يَجْمَعُونَ','اگر هم در راه خدا کشته شوید یا بمیرید، (زیان نکرده‌اید؛ زیرا) آمرزش و رحمت خدا، از تمام آنچه آنها (در طول عمر خود،) جمع آوری میکنند، بهتر است!'];
ayat[451]=[3,158,'وَلَئِن مُّتُّمْ أَوْ قُتِلْتُمْ لَإِلَى اللَّهِ تُحْشَرُونَ','و اگر بمیرید یا کشته شوید، به سوی خدا محشور می‌شوید. (بنابراین، فانی نمی‌شوید که از فنا، وحشت داشته باشید.)'];
ayat[452]=[3,159,'فَبِمَا رَحْمَةٍ مِّنَ اللَّهِ لِنتَ لَهُمْ ۖ وَلَوْ كُنتَ فَظًّا غَلِيظَ الْقَلْبِ لَانفَضُّوا مِنْ حَوْلِكَ ۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِي الْأَمْرِ ۖ فَإِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللَّهِ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُتَوَكِّلِينَ','به (برکت) رحمت الهی، در برابر آنان [= مردم‌] نرم (و مهربان) شدی! و اگر خشن و سنگدل بودی، از اطراف تو، پراکنده می‌شدند. پس آنها را ببخش و برای آنها آمرزش بطلب! و در کارها، با آنان مشورت کن! اما هنگامی که تصمیم گرفتی، (قاطع باش! و) بر خدا توکل کن! زیرا خداوند متوکلان را دوست دارد.'];
ayat[453]=[3,160,'إِن يَنصُرْكُمُ اللَّهُ فَلَا غَالِبَ لَكُمْ ۖ وَإِن يَخْذُلْكُمْ فَمَن ذَا الَّذِي يَنصُرُكُم مِّن بَعْدِهِ ۗ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','اگر خداوند شما را یاری کند، هیچ کس بر شما پیروز نخواهد شد! و اگر دست از یاری شما بردارد، کیست که بعد از او، شما را یاری کند؟! و مؤمنان، تنها بر خداوند باید توکل کنند!'];
ayat[454]=[3,161,'وَمَا كَانَ لِنَبِيٍّ أَن يَغُلَّ ۚ وَمَن يَغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيَامَةِ ۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ','(گمان کردید ممکن است پیامبر به شما خیانت کند؟! در حالی که) ممکن نیست هیچ پیامبری خیانت کند! و هر کس خیانت کند، روز رستاخیز، آنچه را در آن خیانت کرده، با خود (به صحنه محشر) می‌آورد؛ سپس به هر کس، آنچه را فراهم کرده (و انجام داده است)، بطور کامل داده می‌شود؛ و (به همین دلیل) به آنها ستم نخواهد شد (چرا که محصول اعمال خود را خواهند دید).'];
ayat[455]=[3,162,'أَفَمَنِ اتَّبَعَ رِضْوَانَ اللَّهِ كَمَن بَاءَ بِسَخَطٍ مِّنَ اللَّهِ وَمَأْوَاهُ جَهَنَّمُ ۚ وَبِئْسَ الْمَصِيرُ','آیا کسی که از رضای خدا پیروی کرده، همانند کسی است که به خشم و غضب خدا بازگشته؟! و جایگاه او جهنم، و پایان کار او بسیار بد است.'];
ayat[456]=[3,163,'هُمْ دَرَجَاتٌ عِندَ اللَّهِ ۗ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ','هر یک از آنان، درجه و مقامی در پیشگاه خدا دارند؛ و خداوند به آنچه انجام می‌دهند، بیناست.'];
ayat[457]=[3,164,'لَقَدْ مَنَّ اللَّهُ عَلَى الْمُؤْمِنِينَ إِذْ بَعَثَ فِيهِمْ رَسُولًا مِّنْ أَنفُسِهِمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِن كَانُوا مِن قَبْلُ لَفِي ضَلَالٍ مُّبِينٍ','خداوند بر مؤمنان منت نهاد [= نعمت بزرگی بخشید] هنگامی که در میان آنها، پیامبری از خودشان برانگیخت؛ که آیات او را بر آنها بخواند، و آنها را پاک کند و کتاب و حکمت بیاموزد؛ هر چند پیش از آن، در گمراهی آشکاری بودند.'];
ayat[458]=[3,165,'أَوَلَمَّا أَصَابَتْكُم مُّصِيبَةٌ قَدْ أَصَبْتُم مِّثْلَيْهَا قُلْتُمْ أَنَّىٰ هَٰذَا ۖ قُلْ هُوَ مِنْ عِندِ أَنفُسِكُمْ ۗ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آیا هنگامی که مصیبتی (در میدان جنک احد) به شما رسید، در حالی که دو برابر آن را (در میدان جنگ بدر بر دشمن) وارد ساخته بودید، گفتید: «این مصیبت از کجاست؟!» بگو: «از ناحیه خود شماست (که در میدان جنگ احد، با دستور پیامبر مخالفت کردید)! خداوند بر هر چیزی قادر است. (و چنانچه روش خود را اصلاح کنید، در آینده شما را پیروز می‌کند.)»'];
ayat[459]=[3,166,'وَمَا أَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعَانِ فَبِإِذْنِ اللَّهِ وَلِيَعْلَمَ الْمُؤْمِنِينَ','و آنچه (در روز احد،) در روزی که دو دسته [= مؤمنان و کافران‌] با هم نبرد کردند به شما رسید، به فرمان خدا (و طبق سنت الهی) بود؛ و برای این بود که مؤمنان را مشخص کند.'];
ayat[460]=[3,167,'وَلِيَعْلَمَ الَّذِينَ نَافَقُوا ۚ وَقِيلَ لَهُمْ تَعَالَوْا قَاتِلُوا فِي سَبِيلِ اللَّهِ أَوِ ادْفَعُوا ۖ قَالُوا لَوْ نَعْلَمُ قِتَالًا لَّاتَّبَعْنَاكُمْ ۗ هُمْ لِلْكُفْرِ يَوْمَئِذٍ أَقْرَبُ مِنْهُمْ لِلْإِيمَانِ ۚ يَقُولُونَ بِأَفْوَاهِهِم مَّا لَيْسَ فِي قُلُوبِهِمْ ۗ وَاللَّهُ أَعْلَمُ بِمَا يَكْتُمُونَ','و نیز برای این بود که منافقان شناخته شوند؛ آنهایی که به ایشان گفته شد: «بیایید در راه خدا نبرد کنید! یا (حداقل) از حریم خود، دفاع نمایید!» گفتند: «اگر می‌دانستیم جنگی روی خواهد داد، از شما پیروی می‌کردیم. (اما می‌دانیم جنگی نمی‌شود.)» آنها در آن هنگام، به کفر نزدیکتر بودند تا به ایمان؛ به زبان خود چیزی می‌گویند که در دلهایشان نیست! و خداوند از آنچه کتمان می‌کنند، آگاهتر است.'];
ayat[461]=[3,168,'الَّذِينَ قَالُوا لِإِخْوَانِهِمْ وَقَعَدُوا لَوْ أَطَاعُونَا مَا قُتِلُوا ۗ قُلْ فَادْرَءُوا عَنْ أَنفُسِكُمُ الْمَوْتَ إِن كُنتُمْ صَادِقِينَ','(منافقان) آنها هستند که به برادران خود -در حالی که از حمایت آنها دست کشیده بودند- گفتند: «اگر آنها از ما پیروی می‌کردند، کشته نمی‌شدند!» بگو: «(مگر شما می‌توانید مرگ افراد را پیش‌بینی کنید؟!) پس مرگ را از خودتان دور سازید اگر راست می‌گویید!»'];
ayat[462]=[3,169,'وَلَا تَحْسَبَنَّ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ أَمْوَاتًا ۚ بَلْ أَحْيَاءٌ عِندَ رَبِّهِمْ يُرْزَقُونَ','(ای پیامبر!) هرگز گمان مبر کسانی که در راه خدا کشته شدند، مردگانند! بلکه آنان زنده‌اند، و نزد پروردگارشان روزی داده می‌شوند.'];
ayat[463]=[3,170,'فَرِحِينَ بِمَا آتَاهُمُ اللَّهُ مِن فَضْلِهِ وَيَسْتَبْشِرُونَ بِالَّذِينَ لَمْ يَلْحَقُوا بِهِم مِّنْ خَلْفِهِمْ أَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','آنها بخاطر نعمتهای فراوانی که خداوند از فضل خود به ایشان بخشیده است، خوشحالند؛ و بخاطر کسانی که هنوز به آنها ملحق نشده‌اند [= مجاهدان و شهیدان آینده]، خوشوقتند؛ (زیرا مقامات برجسته آنها را در آن جهان می‌بینند؛ و می‌دانند) که نه ترسی بر آنهاست، و نه غمی خواهند داشت.'];
ayat[464]=[3,171,'۞ يَسْتَبْشِرُونَ بِنِعْمَةٍ مِّنَ اللَّهِ وَفَضْلٍ وَأَنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُؤْمِنِينَ','و از نعمت خدا و فضل او (نسبت به خودشان نیز) مسرورند؛ و (می‌بینند که) خداوند، پاداش مؤمنان را ضایع نمی‌کند؛ (نه پاداش شهیدان، و نه پاداش مجاهدانی که شهید نشدند).'];
ayat[465]=[3,172,'الَّذِينَ اسْتَجَابُوا لِلَّهِ وَالرَّسُولِ مِن بَعْدِ مَا أَصَابَهُمُ الْقَرْحُ ۚ لِلَّذِينَ أَحْسَنُوا مِنْهُمْ وَاتَّقَوْا أَجْرٌ عَظِيمٌ','آنها که دعوت خدا و پیامبر (ص) را، پس از آن همه جراحاتی که به ایشان رسید، اجابت کردند؛ (و هنوز زخمهای میدان احد التیام نیافته بود، به سوی میدان «حمرار الاسد» حرکت نمودند؛) برای کسانی از آنها، که نیکی کردند و تقوا پیش گرفتند، پاداش بزرگی است.'];
ayat[466]=[3,173,'الَّذِينَ قَالَ لَهُمُ النَّاسُ إِنَّ النَّاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ إِيمَانًا وَقَالُوا حَسْبُنَا اللَّهُ وَنِعْمَ الْوَكِيلُ','اینها کسانی بودند که (بعضی از) مردم، به آنان گفتند: «مردم [= لشکر دشمن] برای (حمله به) شما اجتماع کرده‌اند؛ از آنها بترسید!» اما این سخن، بر ایمانشان افزود؛ و گفتند: «خدا ما را کافی است؛ و او بهترین حامی ماست.»'];
ayat[467]=[3,174,'فَانقَلَبُوا بِنِعْمَةٍ مِّنَ اللَّهِ وَفَضْلٍ لَّمْ يَمْسَسْهُمْ سُوءٌ وَاتَّبَعُوا رِضْوَانَ اللَّهِ ۗ وَاللَّهُ ذُو فَضْلٍ عَظِيمٍ','به همین جهت، آنها (از این میدان،) با نعمت و فضل پروردگارشان، بازگشتند؛ در حالی که هیچ ناراحتی به آنان نرسید؛ و از رضای خدا، پیروی کردند؛ و خداوند دارای فضل و بخشش بزرگی است.'];
ayat[468]=[3,175,'إِنَّمَا ذَٰلِكُمُ الشَّيْطَانُ يُخَوِّفُ أَوْلِيَاءَهُ فَلَا تَخَافُوهُمْ وَخَافُونِ إِن كُنتُم مُّؤْمِنِينَ','این فقط شیطان است که پیروان خود را (با سخنان و شایعات بی‌اساس،) می‌ترساند. از آنها نترسید! و تنها از من بترسید اگر ایمان دارید!'];
ayat[469]=[3,176,'وَلَا يَحْزُنكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ ۚ إِنَّهُمْ لَن يَضُرُّوا اللَّهَ شَيْئًا ۗ يُرِيدُ اللَّهُ أَلَّا يَجْعَلَ لَهُمْ حَظًّا فِي الْآخِرَةِ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ','کسانی که در راه کفر، شتاب میکنند، تو را غمگین نسازند! به یقین، آنها هرگز زیانی به خداوند نمی‌رسانند. (بعلاوه) خدا می‌خواهد (آنها را به حال خودشان واگذارد؛ و در نتیجه،) بهره‌ای برای آنها در آخرت قرار ندهد. و برای آنها مجازات بزرگی است!'];
ayat[470]=[3,177,'إِنَّ الَّذِينَ اشْتَرَوُا الْكُفْرَ بِالْإِيمَانِ لَن يَضُرُّوا اللَّهَ شَيْئًا وَلَهُمْ عَذَابٌ أَلِيمٌ','(آری،) کسانی که ایمان را دادند و کفر را خریداری کردند، هرگز به خدا زیانی نمی‌رسانند؛ و برای آنها، مجازات دردناکی است!'];
ayat[471]=[3,178,'وَلَا يَحْسَبَنَّ الَّذِينَ كَفَرُوا أَنَّمَا نُمْلِي لَهُمْ خَيْرٌ لِّأَنفُسِهِمْ ۚ إِنَّمَا نُمْلِي لَهُمْ لِيَزْدَادُوا إِثْمًا ۚ وَلَهُمْ عَذَابٌ مُّهِينٌ','آنها که کافر شدند، (و راه طغیان پیش گرفتند،) تصور نکنند اگر به آنان مهلت می‌دهیم، به سودشان است! ما به آنان مهلت می‌دهیم فقط برای اینکه بر گناهان خود بیفزایند؛ و برای آنها، عذاب خوارکننده‌ای (آماده شده) است!'];
ayat[472]=[3,179,'مَّا كَانَ اللَّهُ لِيَذَرَ الْمُؤْمِنِينَ عَلَىٰ مَا أَنتُمْ عَلَيْهِ حَتَّىٰ يَمِيزَ الْخَبِيثَ مِنَ الطَّيِّبِ ۗ وَمَا كَانَ اللَّهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلَٰكِنَّ اللَّهَ يَجْتَبِي مِن رُّسُلِهِ مَن يَشَاءُ ۖ فَآمِنُوا بِاللَّهِ وَرُسُلِهِ ۚ وَإِن تُؤْمِنُوا وَتَتَّقُوا فَلَكُمْ أَجْرٌ عَظِيمٌ','چنین نبود که خداوند، مؤمنان را به همان‌گونه که شما هستید واگذارد؛ مگر آنکه ناپاک را از پاک جدا سازد. و نیز چنین نبود که خداوند شما را از اسرار غیب، آگاه کند (تا مؤمنان و منافقان را از این راه بشناسید؛ این بر خلاف سنت الهی است؛) ولی خداوند از میان رسولان خود، هر کس را بخواهد برمیگزیند؛ (و قسمتی از اسرار نهان را که برای مقام رهبری او لازم است، در اختیار او می‌گذارد.) پس (اکنون که این جهان، بوته آزمایش پاک و ناپاک است،) به خدا و رسولان او ایمان بیاورید! و اگر ایمان بیاورید و تقوا پیشه کنید، پاداش بزرگی برای شماست.'];
ayat[473]=[3,180,'وَلَا يَحْسَبَنَّ الَّذِينَ يَبْخَلُونَ بِمَا آتَاهُمُ اللَّهُ مِن فَضْلِهِ هُوَ خَيْرًا لَّهُم ۖ بَلْ هُوَ شَرٌّ لَّهُمْ ۖ سَيُطَوَّقُونَ مَا بَخِلُوا بِهِ يَوْمَ الْقِيَامَةِ ۗ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','کسانی که بخل می‌ورزند، و آنچه را خدا از فضل خویش به آنان داده، انفاق نمی‌کنند، گمان نکنند این کار به سود آنها است؛ بلکه برای آنها شر است؛ بزودی در روز قیامت، آنچه را نسبت به آن بخل ورزیدند، همانند طوقی به گردنشان می‌افکنند. و میراث آسمانها و زمین، از آن خداست؛ و خداوند، از آنچه انجام می‌دهید، آگاه است.'];
ayat[474]=[3,181,'لَّقَدْ سَمِعَ اللَّهُ قَوْلَ الَّذِينَ قَالُوا إِنَّ اللَّهَ فَقِيرٌ وَنَحْنُ أَغْنِيَاءُ ۘ سَنَكْتُبُ مَا قَالُوا وَقَتْلَهُمُ الْأَنبِيَاءَ بِغَيْرِ حَقٍّ وَنَقُولُ ذُوقُوا عَذَابَ الْحَرِيقِ','خداوند، سخن آنها را که گفتند: «خدا فقیر است، و ما بی‌نیازیم»، شنید! به زودی آنچه را گفتند، خواهیم نوشت؛ و (همچنین) کشتن پیامبران را بناحق (می‌نویسیم)؛ و به آنها می‌گوییم: «بچشید عذاب سوزان را (در برابر کارهایتان!)»'];
ayat[475]=[3,182,'ذَٰلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اللَّهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيدِ','این بخاطر چیزی است که دستهای شما از پیش فرستاده (و نتیجه کار شماست) و بخاطر آن است که خداوند، به بندگان (خود)، ستم نمی‌کند.'];
ayat[476]=[3,183,'الَّذِينَ قَالُوا إِنَّ اللَّهَ عَهِدَ إِلَيْنَا أَلَّا نُؤْمِنَ لِرَسُولٍ حَتَّىٰ يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ ۗ قُلْ قَدْ جَاءَكُمْ رُسُلٌ مِّن قَبْلِي بِالْبَيِّنَاتِ وَبِالَّذِي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ إِن كُنتُمْ صَادِقِينَ','(اینها) همان کسانی (هستند) که گفتند: «خداوند از ما پیمان گرفته که به هیچ پیامبری ایمان نیاوریم تا (این معجزه را انجام دهد:) یک قربانی بیاورد، که آتش [= صاعقه آسمانی‌] آن را بخورد!» بگو: «پیامبرانی پیش از من، برای شما آمدند؛ و دلایل روشن، و آنچه را گفتید آوردند؛ پس چرا آنها را به قتل رساندید اگر راست می‌گویید؟!»'];
ayat[477]=[3,184,'فَإِن كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِّن قَبْلِكَ جَاءُوا بِالْبَيِّنَاتِ وَالزُّبُرِ وَالْكِتَابِ الْمُنِيرِ','پس (اگر این بهانه‌جویان،) تو را تکذیب کنند، (چیز تازه‌ای نیست؛) رسولان پیش از تو (نیز) تکذیب شدند؛ پیامبرانی که دلایل آشکار، و نوشته‌های متین و محکم، و کتاب روشنی‌بخش آورده بودند.'];
ayat[478]=[3,185,'كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ۗ وَإِنَّمَا تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ الْقِيَامَةِ ۖ فَمَن زُحْزِحَ عَنِ النَّارِ وَأُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ ۗ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ','هر کسی مرگ را می‌چشد؛ و شما پاداش خود را بطور کامل در روز قیامت خواهید گرفت؛ آنها که از آتش (دوزخ) دور شده، و به بهشت وارد شوند نجات یافته و رستگار شده‌اند و زندگی دنیا، چیزی جز سرمایه فریب نیست!'];
ayat[479]=[3,186,'۞ لَتُبْلَوُنَّ فِي أَمْوَالِكُمْ وَأَنفُسِكُمْ وَلَتَسْمَعُنَّ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِن قَبْلِكُمْ وَمِنَ الَّذِينَ أَشْرَكُوا أَذًى كَثِيرًا ۚ وَإِن تَصْبِرُوا وَتَتَّقُوا فَإِنَّ ذَٰلِكَ مِنْ عَزْمِ الْأُمُورِ','به یقین (همه شما) در اموال و جانهای خود، آزمایش می‌شوید! و از کسانی که پیش از شما به آنها کتاب (آسمانی) داده شده [= یهود]، و (همچنین) از مشرکان، سخنان آزاردهنده فراوان خواهید شنید! و اگر استقامت کنید و تقوا پیشه سازید، (شایسته‌تر است؛ زیرا) این از کارهای مهم و قابل اطمینان است.'];
ayat[480]=[3,187,'وَإِذْ أَخَذَ اللَّهُ مِيثَاقَ الَّذِينَ أُوتُوا الْكِتَابَ لَتُبَيِّنُنَّهُ لِلنَّاسِ وَلَا تَكْتُمُونَهُ فَنَبَذُوهُ وَرَاءَ ظُهُورِهِمْ وَاشْتَرَوْا بِهِ ثَمَنًا قَلِيلًا ۖ فَبِئْسَ مَا يَشْتَرُونَ','و (به خاطر بیاورید) هنگامی را که خدا، از کسانی که کتاب (آسمانی) به آنها داده شده، پیمان گرفت که حتماً آن را برای مردم آشکار سازید و کتمان نکنید! ولی آنها، آن را پشت سر افکندند؛ و به بهای کمی فروختند؛ و چه بد متاعی می‌خرند؟!'];
ayat[481]=[3,188,'لَا تَحْسَبَنَّ الَّذِينَ يَفْرَحُونَ بِمَا أَتَوا وَّيُحِبُّونَ أَن يُحْمَدُوا بِمَا لَمْ يَفْعَلُوا فَلَا تَحْسَبَنَّهُم بِمَفَازَةٍ مِّنَ الْعَذَابِ ۖ وَلَهُمْ عَذَابٌ أَلِيمٌ','گمان مبر آنها که از اعمال (زشت) خود خوشحال می‌شوند، و دوست دارند در برابر کار (نیکی) که انجام نداده‌اند مورد ستایش قرار گیرند، از عذاب (الهی) برکنارند! (بلکه) برای آنها، عذاب دردناکی است!'];
ayat[482]=[3,189,'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','و حکومت آسمانها و زمین، از آن خداست؛ و خدا بر همه چیز تواناست.'];
ayat[483]=[3,190,'إِنَّ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافِ اللَّيْلِ وَالنَّهَارِ لَآيَاتٍ لِّأُولِي الْأَلْبَابِ','مسلماً در آفرینش آسمانها و زمین، و آمد و رفت شب و روز، نشانه‌های (روشنی) برای خردمندان است.'];
ayat[484]=[3,191,'الَّذِينَ يَذْكُرُونَ اللَّهَ قِيَامًا وَقُعُودًا وَعَلَىٰ جُنُوبِهِمْ وَيَتَفَكَّرُونَ فِي خَلْقِ السَّمَاوَاتِ وَالْأَرْضِ رَبَّنَا مَا خَلَقْتَ هَٰذَا بَاطِلًا سُبْحَانَكَ فَقِنَا عَذَابَ النَّارِ','همانها که خدا را در حال ایستاده و نشسته، و آنگاه که بر پهلو خوابیده‌اند، یاد می‌کنند؛ و در اسرار آفرینش آسمانها و زمین می‌اندیشند؛ (و می‌گویند:) بار الها! اینها را بیهوده نیافریده‌ای! منزهی تو! ما را از عذاب آتش، نگاه دار!'];
ayat[485]=[3,192,'رَبَّنَا إِنَّكَ مَن تُدْخِلِ النَّارَ فَقَدْ أَخْزَيْتَهُ ۖ وَمَا لِلظَّالِمِينَ مِنْ أَنصَارٍ','پروردگارا! هر که را تو (بخاطر اعمالش،) به آتش افکنی، او را خوار و رسوا ساخته‌ای! و برای افراد ستمگر، هیچ یاوری نیست!'];
ayat[486]=[3,193,'رَّبَّنَا إِنَّنَا سَمِعْنَا مُنَادِيًا يُنَادِي لِلْإِيمَانِ أَنْ آمِنُوا بِرَبِّكُمْ فَآمَنَّا ۚ رَبَّنَا فَاغْفِرْ لَنَا ذُنُوبَنَا وَكَفِّرْ عَنَّا سَيِّئَاتِنَا وَتَوَفَّنَا مَعَ الْأَبْرَارِ','پروردگارا! ما صدای منادی (تو) را شنیدیم که به ایمان دعوت می‌کرد که: «به پروردگار خود، ایمان بیاورید!» و ما ایمان آوردیم؛ پروردگارا! گناهان ما را ببخش! و بدیهای ما را بپوشان! و ما را با نیکان (و در مسیر آنها) بمیران!'];
ayat[487]=[3,194,'رَبَّنَا وَآتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيَامَةِ ۗ إِنَّكَ لَا تُخْلِفُ الْمِيعَادَ','پروردگارا! آنچه را به وسیله پیامبرانت به ما وعده فرمودی، به ما عطا کن! و ما را در روز رستاخیز، رسوا مگردان! زیرا تو هیچ‌گاه از وعده خود، تخلف نمی‌کنی.'];
ayat[488]=[3,195,'فَاسْتَجَابَ لَهُمْ رَبُّهُمْ أَنِّي لَا أُضِيعُ عَمَلَ عَامِلٍ مِّنكُم مِّن ذَكَرٍ أَوْ أُنثَىٰ ۖ بَعْضُكُم مِّن بَعْضٍ ۖ فَالَّذِينَ هَاجَرُوا وَأُخْرِجُوا مِن دِيَارِهِمْ وَأُوذُوا فِي سَبِيلِي وَقَاتَلُوا وَقُتِلُوا لَأُكَفِّرَنَّ عَنْهُمْ سَيِّئَاتِهِمْ وَلَأُدْخِلَنَّهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ثَوَابًا مِّنْ عِندِ اللَّهِ ۗ وَاللَّهُ عِندَهُ حُسْنُ الثَّوَابِ','خداوند، درخواست آنها را پذیرفت؛ (و فرمود:) من عمل هیچ عمل‌کننده‌ای از شما را، زن باشد یا مرد، ضایع نخواهم کرد؛ شما همنوعید، و از جنس یکدیگر! آنها که در راه خدا هجرت کردند، و از خانه‌های خود بیرون رانده شدند و در راه من آزار دیدند، و جنگ کردند و کشته شدند، بیقین گناهانشان را می‌بخشم؛ و آنها را در باغهای بهشتی، که از زیر درختانش نهرها جاری است، وارد می‌کنم. این پاداشی است از طرف خداوند؛ و بهترین پاداشها نزد پروردگار است.'];
ayat[489]=[3,196,'لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذِينَ كَفَرُوا فِي الْبِلَادِ','رفت و آمد (پیروزمندانه) کافران در شهرها، تو را نفریبد!'];
ayat[490]=[3,197,'مَتَاعٌ قَلِيلٌ ثُمَّ مَأْوَاهُمْ جَهَنَّمُ ۚ وَبِئْسَ الْمِهَادُ','این متاع ناچیزی است؛ و سپس جایگاهشان دوزخ، و چه بد جایگاهی است!'];
ayat[491]=[3,198,'لَٰكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنَّاتٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا نُزُلًا مِّنْ عِندِ اللَّهِ ۗ وَمَا عِندَ اللَّهِ خَيْرٌ لِّلْأَبْرَارِ','ولی کسانی (که ایمان دارند، و) از پروردگارشان می‌پرهیزند، برای آنها باغهایی از بهشت است، که از زیر درختانش نهرها جاری است؛ همیشه در آن خواهند بود. این، نخستین پذیرایی است که از سوی خداوند به آنها می‌رسد؛ و آنچه در نزد خداست، برای نیکان بهتر است!'];
ayat[492]=[3,199,'وَإِنَّ مِنْ أَهْلِ الْكِتَابِ لَمَن يُؤْمِنُ بِاللَّهِ وَمَا أُنزِلَ إِلَيْكُمْ وَمَا أُنزِلَ إِلَيْهِمْ خَاشِعِينَ لِلَّهِ لَا يَشْتَرُونَ بِآيَاتِ اللَّهِ ثَمَنًا قَلِيلًا ۗ أُولَٰئِكَ لَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ ۗ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ','و از اهل کتاب، کسانی هستند که به خدا، و آنچه بر شما نازل شده، و آنچه بر خودشان نازل گردیده، ایمان دارند؛ در برابر (فرمان) خدا خاضعند؛ و آیات خدا را به بهای ناچیزی نمی‌فروشند. پاداش آنها، نزد پروردگارشان است. خداوند، سریع الحساب است. (تمام اعمال نیک آنها را به سرعت حساب می‌کند، و پاداش می‌دهد.)'];
ayat[493]=[3,200,'يَا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَصَابِرُوا وَرَابِطُوا وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُفْلِحُونَ','ای کسانی که ایمان آورده‌اید! (در برابر مشکلات و هوسها،) استقامت کنید! و در برابر دشمنان (نیز)، پایدار باشید و از مرزهای خود، مراقبت کنید و از خدا بپرهیزید، شاید رستگار شوید!'];
ayat[494]=[4,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمُ الَّذِي خَلَقَكُم مِّن نَّفْسٍ وَاحِدَةٍ وَخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالًا كَثِيرًا وَنِسَاءً ۚ وَاتَّقُوا اللَّهَ الَّذِي تَسَاءَلُونَ بِهِ وَالْأَرْحَامَ ۚ إِنَّ اللَّهَ كَانَ عَلَيْكُمْ رَقِيبًا','ای مردم! از (مخالفت) پروردگارتان بپرهیزید! همان کسی که همه شما را از یک انسان آفرید؛ و همسر او را (نیز) از جنس او خلق کرد؛ و از آن دو، مردان و زنان فراوانی (در روی زمین) منتشر ساخت. و از خدایی بپرهیزید که (همگی به عظمت او معترفید؛ و) هنگامی که چیزی از یکدیگر می‌خواهید، نام او را می‌برید! (و نیز) (از قطع رابطه با) خویشاوندان خود، پرهیز کنید! زیرا خداوند، مراقب شماست.'];
ayat[495]=[4,2,'وَآتُوا الْيَتَامَىٰ أَمْوَالَهُمْ ۖ وَلَا تَتَبَدَّلُوا الْخَبِيثَ بِالطَّيِّبِ ۖ وَلَا تَأْكُلُوا أَمْوَالَهُمْ إِلَىٰ أَمْوَالِكُمْ ۚ إِنَّهُ كَانَ حُوبًا كَبِيرًا','و اموال یتیمان را (هنگامی که به حد رشد رسیدند) به آنها بدهید! و اموال بد (خود) را، با اموال خوب (آنها) عوض نکنید! و اموال آنان را همراه اموال خودتان (با مخلوط کردن یا تبدیل نمودن) نخورید، زیرا این گناه بزرگی است!'];
ayat[496]=[4,3,'وَإِنْ خِفْتُمْ أَلَّا تُقْسِطُوا فِي الْيَتَامَىٰ فَانكِحُوا مَا طَابَ لَكُم مِّنَ النِّسَاءِ مَثْنَىٰ وَثُلَاثَ وَرُبَاعَ ۖ فَإِنْ خِفْتُمْ أَلَّا تَعْدِلُوا فَوَاحِدَةً أَوْ مَا مَلَكَتْ أَيْمَانُكُمْ ۚ ذَٰلِكَ أَدْنَىٰ أَلَّا تَعُولُوا','و اگر می‌ترسید که (بهنگام ازدواج با دختران یتیم،) عدالت را رعایت نکنید، (از ازدواج با آنان، چشم‌پوشی کنید و) با زنان پاک (دیگر) ازدواج نمائید، دو یا سه یا چهار همسر و اگر می‌ترسید عدالت را (درباره همسران متعدد) رعایت نکنید، تنها یک همسر بگیرید، و یا از زنانی که مالک آنهائید استفاده کنید، این کار، از ظلم و ستم بهتر جلوگیری می‌کند.'];
ayat[497]=[4,4,'وَآتُوا النِّسَاءَ صَدُقَاتِهِنَّ نِحْلَةً ۚ فَإِن طِبْنَ لَكُمْ عَن شَيْءٍ مِّنْهُ نَفْسًا فَكُلُوهُ هَنِيئًا مَّرِيئًا','و مهر زنان را (بطور کامل) بعنوان یک بدهی (یا عطیه،) به آنان بپردازید! (ولی) اگر آنها چیزی از آن را با رضایت خاطر به شما ببخشند، حلال و گوارا مصرف کنید!'];
ayat[498]=[4,5,'وَلَا تُؤْتُوا السُّفَهَاءَ أَمْوَالَكُمُ الَّتِي جَعَلَ اللَّهُ لَكُمْ قِيَامًا وَارْزُقُوهُمْ فِيهَا وَاكْسُوهُمْ وَقُولُوا لَهُمْ قَوْلًا مَّعْرُوفًا','اموال خود را، که خداوند وسیله قوام زندگی شما قرار داده، به دست سفیهان نسپارید و از آن، به آنها روزی دهید! و لباس بر آنان بپوشانید و با آنها سخن شایسته بگویید!'];
ayat[499]=[4,6,'وَابْتَلُوا الْيَتَامَىٰ حَتَّىٰ إِذَا بَلَغُوا النِّكَاحَ فَإِنْ آنَسْتُم مِّنْهُمْ رُشْدًا فَادْفَعُوا إِلَيْهِمْ أَمْوَالَهُمْ ۖ وَلَا تَأْكُلُوهَا إِسْرَافًا وَبِدَارًا أَن يَكْبَرُوا ۚ وَمَن كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ ۖ وَمَن كَانَ فَقِيرًا فَلْيَأْكُلْ بِالْمَعْرُوفِ ۚ فَإِذَا دَفَعْتُمْ إِلَيْهِمْ أَمْوَالَهُمْ فَأَشْهِدُوا عَلَيْهِمْ ۚ وَكَفَىٰ بِاللَّهِ حَسِيبًا','و یتیمان را چون به حد بلوغ برسند، بیازمایید! اگر در آنها رشد (کافی) یافتید، اموالشان را به آنها بدهید! و پیش از آنکه بزرگ شوند، اموالشان را از روی اسراف نخورید! هر کس که بی‌نیاز است، (از برداشت حق الزحمه) خودداری کند؛ و آن کس که نیازمند است، به طور شایسته (و مطابق زحمتی که می‌کشد،) از آن بخورد. و هنگامی که اموالشان را به آنها بازمی‌گردانید، شاهد بگیرید! اگر چه خداوند برای محاسبه کافی است.'];
ayat[500]=[4,7,'لِّلرِّجَالِ نَصِيبٌ مِّمَّا تَرَكَ الْوَالِدَانِ وَالْأَقْرَبُونَ وَلِلنِّسَاءِ نَصِيبٌ مِّمَّا تَرَكَ الْوَالِدَانِ وَالْأَقْرَبُونَ مِمَّا قَلَّ مِنْهُ أَوْ كَثُرَ ۚ نَصِيبًا مَّفْرُوضًا','برای مردان، از آنچه پدر و مادر و خویشاوندان از خود بر جای می‌گذارند، سهمی است؛ و برای زنان نیز، از آنچه پدر و مادر و خویشاوندان می‌گذارند، سهمی؛ خواه آن مال، کم باشد یا زیاد؛ این سهمی است تعیین شده و پرداختنی.'];
ayat[501]=[4,8,'وَإِذَا حَضَرَ الْقِسْمَةَ أُولُو الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينُ فَارْزُقُوهُم مِّنْهُ وَقُولُوا لَهُمْ قَوْلًا مَّعْرُوفًا','و اگر بهنگام تقسیم (ارث)، خویشاوندان (و طبقه‌ای که ارث نمی‌برند) و یتیمان و مستمندان، حضور داشته باشند، چیزی از آن اموال را به آنها بدهید! و با آنان به طور شایسته سخن بگویید!'];
ayat[502]=[4,9,'وَلْيَخْشَ الَّذِينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعَافًا خَافُوا عَلَيْهِمْ فَلْيَتَّقُوا اللَّهَ وَلْيَقُولُوا قَوْلًا سَدِيدًا','کسانی که اگر فرزندان ناتوانی از خود بیادگار بگذارند از آینده آنان می‌ترسند، باید (از ستم درباره یتیمان مردم) بترسند! از (مخالفت) خدا بپرهیزند، و سخنی استوار بگویند.'];
ayat[503]=[4,10,'إِنَّ الَّذِينَ يَأْكُلُونَ أَمْوَالَ الْيَتَامَىٰ ظُلْمًا إِنَّمَا يَأْكُلُونَ فِي بُطُونِهِمْ نَارًا ۖ وَسَيَصْلَوْنَ سَعِيرًا','کسانی که اموال یتیمان را به ظلم و ستم می‌خورند، (در حقیقت،) تنها آتش می‌خورند؛ و بزودی در شعله‌های آتش (دوزخ) می‌سوزند.'];
ayat[504]=[4,11,'يُوصِيكُمُ اللَّهُ فِي أَوْلَادِكُمْ ۖ لِلذَّكَرِ مِثْلُ حَظِّ الْأُنثَيَيْنِ ۚ فَإِن كُنَّ نِسَاءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ ۖ وَإِن كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ ۚ وَلِأَبَوَيْهِ لِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُ وَلَدٌ ۚ فَإِن لَّمْ يَكُن لَّهُ وَلَدٌ وَوَرِثَهُ أَبَوَاهُ فَلِأُمِّهِ الثُّلُثُ ۚ فَإِن كَانَ لَهُ إِخْوَةٌ فَلِأُمِّهِ السُّدُسُ ۚ مِن بَعْدِ وَصِيَّةٍ يُوصِي بِهَا أَوْ دَيْنٍ ۗ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ لَا تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نَفْعًا ۚ فَرِيضَةً مِّنَ اللَّهِ ۗ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا','خداوند در باره فرزندانتان به شما سفارش می‌کند که سهم (میراث) پسر، به اندازه سهم دو دختر باشد؛ و اگر فرزندان شما، (دو دختر و) بیش از دو دختر باشند، دو سوم میراث از آن آنهاست؛ و اگر یکی باشد، نیمی (از میراث،) از آن اوست. و برای هر یک از پدر و مادر او، یک ششم میراث است، اگر (میت) فرزندی داشته باشد؛ و اگر فرزندی نداشته باشد، و (تنها) پدر و مادر از او ارث برند، برای مادر او یک سوم است (و بقیه از آن پدر است)؛ و اگر او برادرانی داشته باشد، مادرش یک ششم می‌برد (و پنج ششم باقیمانده، برای پدر است). (همه اینها،) بعد از انجام وصیتی است که او کرده، و بعد از ادای دین است -شما نمی‌دانید پدران و مادران و فرزندانتان، کدامیک برای شما سودمندترند!- این فریضه الهی است؛ و خداوند، دانا و حکیم است.'];
ayat[505]=[4,12,'۞ وَلَكُمْ نِصْفُ مَا تَرَكَ أَزْوَاجُكُمْ إِن لَّمْ يَكُن لَّهُنَّ وَلَدٌ ۚ فَإِن كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ ۚ مِن بَعْدِ وَصِيَّةٍ يُوصِينَ بِهَا أَوْ دَيْنٍ ۚ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ إِن لَّمْ يَكُن لَّكُمْ وَلَدٌ ۚ فَإِن كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُم ۚ مِّن بَعْدِ وَصِيَّةٍ تُوصُونَ بِهَا أَوْ دَيْنٍ ۗ وَإِن كَانَ رَجُلٌ يُورَثُ كَلَالَةً أَوِ امْرَأَةٌ وَلَهُ أَخٌ أَوْ أُخْتٌ فَلِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُ ۚ فَإِن كَانُوا أَكْثَرَ مِن ذَٰلِكَ فَهُمْ شُرَكَاءُ فِي الثُّلُثِ ۚ مِن بَعْدِ وَصِيَّةٍ يُوصَىٰ بِهَا أَوْ دَيْنٍ غَيْرَ مُضَارٍّ ۚ وَصِيَّةً مِّنَ اللَّهِ ۗ وَاللَّهُ عَلِيمٌ حَلِيمٌ','و برای شما، نصف میراث زنانتان است، اگر آنها فرزندی نداشته باشند؛ و اگر فرزندی داشته باشند، یک چهارم از آن شماست؛ پس از انجام وصیتی که کرده‌اند، و ادای دین (آنها). و برای زنان شما، یک چهارم میراث شماست، اگر فرزندی نداشته باشید؛ و اگر برای شما فرزندی باشد، یک هشتم از آن آنهاست؛ بعد از انجام وصیتی که کرده‌اید، و ادای دین. و اگر مردی بوده باشد که کلاله [= خواهر یا برادر] از او ارث می‌برد، یا زنی که برادر یا خواهری دارد، سهم هر کدام، یک ششم است (اگر برادران و خواهران مادری باشند)؛ و اگر بیش از یک نفر باشند، آنها در یک سوم شریکند؛ پس از انجام وصیتی که شده، و ادای دین؛ بشرط آنکه (از طریق وصیت و اقرار به دین،) به آنها ضرر نزند. این سفارش خداست؛ و خدا دانا و بردبار است.'];
ayat[506]=[4,13,'تِلْكَ حُدُودُ اللَّهِ ۚ وَمَن يُطِعِ اللَّهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَذَٰلِكَ الْفَوْزُ الْعَظِيمُ','اینها مرزهای الهی است؛ و هر کس خدا و پیامبرش را اطاعت کند، (و قوانین او را محترم بشمرد،) خداوند وی را در باغهایی از بهشت وارد می‌کند که همواره، آب از زیر درختانش جاری است؛ جاودانه در آن می‌مانند؛ و این، پیروزی بزرگی است!'];
ayat[507]=[4,14,'وَمَن يَعْصِ اللَّهَ وَرَسُولَهُ وَيَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نَارًا خَالِدًا فِيهَا وَلَهُ عَذَابٌ مُّهِينٌ','و آن کس که نافرمانی خدا و پیامبرش را کند و از مرزهای او تجاوز نماید، او را در آتشی وارد می‌کند که جاودانه در آن خواهد ماند؛ و برای او مجازات خوارکننده‌ای است.'];
ayat[508]=[4,15,'وَاللَّاتِي يَأْتِينَ الْفَاحِشَةَ مِن نِّسَائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ أَرْبَعَةً مِّنكُمْ ۖ فَإِن شَهِدُوا فَأَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتَّىٰ يَتَوَفَّاهُنَّ الْمَوْتُ أَوْ يَجْعَلَ اللَّهُ لَهُنَّ سَبِيلًا','و کسانی از زنان شما که مرتکب زنا شوند، چهار نفر از مسلمانان را بعنوان شاهد بر آنها بطلبید! اگر گواهی دادند، آنان [= زنان‌] را در خانه ها (ی خود) نگاه دارید تا مرگشان فرارسد؛ یا اینکه خداوند، راهی برای آنها قرار دهد.'];
ayat[509]=[4,16,'وَاللَّذَانِ يَأْتِيَانِهَا مِنكُمْ فَآذُوهُمَا ۖ فَإِن تَابَا وَأَصْلَحَا فَأَعْرِضُوا عَنْهُمَا ۗ إِنَّ اللَّهَ كَانَ تَوَّابًا رَّحِيمًا','و از میان شما، آن مردان و زنانی که (همسر ندارند، و) مرتکب آن کار (زشت) می‌شوند، آنها را آزار دهید (و حد بر آنان جاری نمایید)! و اگر توبه کنند، و (خود را) اصلاح نمایند، (و به جبران گذشته بپردازند،) از آنها درگذرید! زیرا خداوند، توبه‌پذیر و مهربان است.'];
ayat[510]=[4,17,'إِنَّمَا التَّوْبَةُ عَلَى اللَّهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهَالَةٍ ثُمَّ يَتُوبُونَ مِن قَرِيبٍ فَأُولَٰئِكَ يَتُوبُ اللَّهُ عَلَيْهِمْ ۗ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا','پذیرش توبه از سوی خدا، تنها برای کسانی است که کار بدی را از روی جهالت انجام می‌دهند، سپس زود توبه می‌کنند. خداوند، توبه چنین اشخاصی را می‌پذیرد؛ و خدا دانا و حکیم است.'];
ayat[511]=[4,18,'وَلَيْسَتِ التَّوْبَةُ لِلَّذِينَ يَعْمَلُونَ السَّيِّئَاتِ حَتَّىٰ إِذَا حَضَرَ أَحَدَهُمُ الْمَوْتُ قَالَ إِنِّي تُبْتُ الْآنَ وَلَا الَّذِينَ يَمُوتُونَ وَهُمْ كُفَّارٌ ۚ أُولَٰئِكَ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا','برای کسانی که کارهای بد را انجام می‌دهند، و هنگامی که مرگ یکی از آنها فرا می‌رسد می‌گوید: «الان توبه کردم!» توبه نیست؛ و نه برای کسانی که در حال کفر از دنیا می‌روند؛ اینها کسانی هستند که عذاب دردناکی برایشان فراهم کرده‌ایم.'];
ayat[512]=[4,19,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا يَحِلُّ لَكُمْ أَن تَرِثُوا النِّسَاءَ كَرْهًا ۖ وَلَا تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ مَا آتَيْتُمُوهُنَّ إِلَّا أَن يَأْتِينَ بِفَاحِشَةٍ مُّبَيِّنَةٍ ۚ وَعَاشِرُوهُنَّ بِالْمَعْرُوفِ ۚ فَإِن كَرِهْتُمُوهُنَّ فَعَسَىٰ أَن تَكْرَهُوا شَيْئًا وَيَجْعَلَ اللَّهُ فِيهِ خَيْرًا كَثِيرًا','ای کسانی که ایمان آورده‌اید! برای شما حلال نیست که از زنان، از روی اکراه (و ایجاد ناراحتی برای آنها،) ارث ببرید! و آنان را تحت فشار قرار ندهید که قسمتی از آنچه را به آنها داده‌اید (از مهر)، تملک کنید! مگر اینکه آنها عمل زشت آشکاری انجام دهند. و با آنان، بطور شایسته رفتار کنید! و اگر از آنها، (بجهتی) کراهت داشتید، (فوراً تصمیم به جدایی نگیرید!) چه بسا چیزی خوشایند شما نباشد، و خداوند خیر فراوانی در آن قرار می‌دهد!'];
ayat[513]=[4,20,'وَإِنْ أَرَدتُّمُ اسْتِبْدَالَ زَوْجٍ مَّكَانَ زَوْجٍ وَآتَيْتُمْ إِحْدَاهُنَّ قِنطَارًا فَلَا تَأْخُذُوا مِنْهُ شَيْئًا ۚ أَتَأْخُذُونَهُ بُهْتَانًا وَإِثْمًا مُّبِينًا','و اگر تصمیم گرفتید که همسر دیگری به جای همسر خود انتخاب کنید، و مال فراوانی (بعنوان مهر) به او پرداخته‌اید، چیزی از آن را پس نگیرید! آیا برای بازپس گرفتن مهر آنان، به تهمت و گناه آشکار متوسل می‌شوید؟!'];
ayat[514]=[4,21,'وَكَيْفَ تَأْخُذُونَهُ وَقَدْ أَفْضَىٰ بَعْضُكُمْ إِلَىٰ بَعْضٍ وَأَخَذْنَ مِنكُم مِّيثَاقًا غَلِيظًا','و چگونه آن را باز پس می‌گیرید، در حالی که شما با یکدیگر تماس و آمیزش کامل داشته‌اید؟ و (از این گذشته،) آنها (هنگام ازدواج،) از شما پیمان محکمی گرفته‌اند!'];
ayat[515]=[4,22,'وَلَا تَنكِحُوا مَا نَكَحَ آبَاؤُكُم مِّنَ النِّسَاءِ إِلَّا مَا قَدْ سَلَفَ ۚ إِنَّهُ كَانَ فَاحِشَةً وَمَقْتًا وَسَاءَ سَبِيلًا','با زنانی که پدران شما با آنها ازدواج کرده‌اند، هرگز ازدواج نکنید! مگر آنچه درگذشته (پیش از نزول این حکم) انجام شده است؛ زیرا این کار، عملی زشت و تنفرآور و راه نادرستی است.'];
ayat[516]=[4,23,'حُرِّمَتْ عَلَيْكُمْ أُمَّهَاتُكُمْ وَبَنَاتُكُمْ وَأَخَوَاتُكُمْ وَعَمَّاتُكُمْ وَخَالَاتُكُمْ وَبَنَاتُ الْأَخِ وَبَنَاتُ الْأُخْتِ وَأُمَّهَاتُكُمُ اللَّاتِي أَرْضَعْنَكُمْ وَأَخَوَاتُكُم مِّنَ الرَّضَاعَةِ وَأُمَّهَاتُ نِسَائِكُمْ وَرَبَائِبُكُمُ اللَّاتِي فِي حُجُورِكُم مِّن نِّسَائِكُمُ اللَّاتِي دَخَلْتُم بِهِنَّ فَإِن لَّمْ تَكُونُوا دَخَلْتُم بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْ وَحَلَائِلُ أَبْنَائِكُمُ الَّذِينَ مِنْ أَصْلَابِكُمْ وَأَن تَجْمَعُوا بَيْنَ الْأُخْتَيْنِ إِلَّا مَا قَدْ سَلَفَ ۗ إِنَّ اللَّهَ كَانَ غَفُورًا رَّحِيمًا','حرام شده است بر شما، مادرانتان، و دختران، و خواهران، و عمه‌ها، و خاله‌ها، و دختران برادر، و دختران خواهر شما، و مادرانی که شما را شیر داده‌اند، و خواهران رضاعی شما، و مادران همسرانتان، و دختران همسرتان که در دامان شما پرورش یافته‌اند از همسرانی که با آنها آمیزش جنسی داشته‌اید -و چنانچه با آنها آمیزش جنسی نداشته‌اید، (دختران آنها) برای شما مانعی ندارد- و (همچنین) همسرهای پسرانتان که از نسل شما هستند (-نه پسرخوانده‌ها-) و (نیز حرام است بر شما) جمع میان دو خواهر کنید؛ مگر آنچه در گذشته واقع شده؛ چرا که خداوند، آمرزنده و مهربان است.'];
ayat[517]=[4,24,'۞ وَالْمُحْصَنَاتُ مِنَ النِّسَاءِ إِلَّا مَا مَلَكَتْ أَيْمَانُكُمْ ۖ كِتَابَ اللَّهِ عَلَيْكُمْ ۚ وَأُحِلَّ لَكُم مَّا وَرَاءَ ذَٰلِكُمْ أَن تَبْتَغُوا بِأَمْوَالِكُم مُّحْصِنِينَ غَيْرَ مُسَافِحِينَ ۚ فَمَا اسْتَمْتَعْتُم بِهِ مِنْهُنَّ فَآتُوهُنَّ أُجُورَهُنَّ فَرِيضَةً ۚ وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا تَرَاضَيْتُم بِهِ مِن بَعْدِ الْفَرِيضَةِ ۚ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا','و زنان شوهردار (بر شما حرام است؛) مگر آنها را که (از راه اسارت) مالک شده‌اید؛ (زیرا اسارت آنها در حکم طلاق است؛) اینها احکامی است که خداوند بر شما مقرّر داشته است. اما زنان دیگر غیر از اینها (که گفته شد)، برای شما حلال است که با اموال خود، آنان را اختیار کنید؛ در حالی که پاکدامن باشید و از زنا، خودداری نمایید. و زنانی را که متعه [= ازدواج موقت‌] می‌کنید، واجب است مهر آنها را بپردازید. و گناهی بر شما نیست در آنچه بعد از تعیین مهر، با یکدیگر توافق کرده‌اید. (بعداً می‌توانید با توافق، آن را کم یا زیاد کنید.) خداوند، دانا و حکیم است.'];
ayat[518]=[4,25,'وَمَن لَّمْ يَسْتَطِعْ مِنكُمْ طَوْلًا أَن يَنكِحَ الْمُحْصَنَاتِ الْمُؤْمِنَاتِ فَمِن مَّا مَلَكَتْ أَيْمَانُكُم مِّن فَتَيَاتِكُمُ الْمُؤْمِنَاتِ ۚ وَاللَّهُ أَعْلَمُ بِإِيمَانِكُم ۚ بَعْضُكُم مِّن بَعْضٍ ۚ فَانكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّ وَآتُوهُنَّ أُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنَاتٍ غَيْرَ مُسَافِحَاتٍ وَلَا مُتَّخِذَاتِ أَخْدَانٍ ۚ فَإِذَا أُحْصِنَّ فَإِنْ أَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنَاتِ مِنَ الْعَذَابِ ۚ ذَٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنكُمْ ۚ وَأَن تَصْبِرُوا خَيْرٌ لَّكُمْ ۗ وَاللَّهُ غَفُورٌ رَّحِيمٌ','و آنها که توانایی ازدواج با زنان (آزاد) پاکدامن باایمان را ندارند، می‌توانند با زنان پاکدامن از بردگان باایمانی که در اختیار دارید ازدواج کنند -خدا به ایمان شما آگاه‌تر است؛ و همگی اعضای یک پیکرید- آنها را با اجازه صاحبان آنان تزویج نمایید، و مهرشان را به خودشان بدهید؛ به شرط آنکه پاکدامن باشند، نه بطور آشکار مرتکب زنا شوند، و نه دوست پنهانی بگیرند. و در صورتی که «محصنه» باشند و مرتکب عمل منافی عفت شوند، نصف مجازات زنان آزاد را خواهند داشت. این (اجازه ازدواج با کنیزان) برای کسانی از شماست که بترسند (از نظر غریزه جنسی) به زحمت بیفتند؛ و (با این حال نیز) خودداری (از ازدواج با آنان) برای شما بهتر است. و خداوند، آمرزنده و مهربان است.'];
ayat[519]=[4,26,'يُرِيدُ اللَّهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِينَ مِن قَبْلِكُمْ وَيَتُوبَ عَلَيْكُمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','خداوند می‌خواهد (با این دستورها، راه‌های خوشبختی و سعادت را) برای شما آشکار سازد، و به سنتهای (صحیح) پیشینیان رهبری کند. و خداوند دانا و حکیم است.'];
ayat[520]=[4,27,'وَاللَّهُ يُرِيدُ أَن يَتُوبَ عَلَيْكُمْ وَيُرِيدُ الَّذِينَ يَتَّبِعُونَ الشَّهَوَاتِ أَن تَمِيلُوا مَيْلًا عَظِيمًا','خدا می‌خواهد شما را ببخشد (و از آلودگی پاک نماید)، امّا آنها که پیرو شهواتند، می‌خواهند شما بکلی منحرف شوید.'];
ayat[521]=[4,28,'يُرِيدُ اللَّهُ أَن يُخَفِّفَ عَنكُمْ ۚ وَخُلِقَ الْإِنسَانُ ضَعِيفًا','خدا میخواهد (با احکام مربوط به ازدواج با کنیزان و مانند آن،) کار را بر شما سبک کند؛ و انسان، ضعیف آفریده شده؛ (و در برابر طوفان غرایز، مقاومت او کم است)'];
ayat[522]=[4,29,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَأْكُلُوا أَمْوَالَكُم بَيْنَكُم بِالْبَاطِلِ إِلَّا أَن تَكُونَ تِجَارَةً عَن تَرَاضٍ مِّنكُمْ ۚ وَلَا تَقْتُلُوا أَنفُسَكُمْ ۚ إِنَّ اللَّهَ كَانَ بِكُمْ رَحِيمًا','ای کسانی که ایمان آورده‌اید! اموال یکدیگر را به باطل (و از طرق نامشروع) نخورید مگر اینکه تجارتی با رضایت شما انجام گیرد. و خودکشی نکنید! خداوند نسبت به شما مهربان است.'];
ayat[523]=[4,30,'وَمَن يَفْعَلْ ذَٰلِكَ عُدْوَانًا وَظُلْمًا فَسَوْفَ نُصْلِيهِ نَارًا ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا','و هر کس این عمل را از روی تجاوز و ستم انجام دهد، بزودی او را در آتشی وارد خواهیم ساخت؛ و این کار برای خدا آسان است.'];
ayat[524]=[4,31,'إِن تَجْتَنِبُوا كَبَائِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنكُمْ سَيِّئَاتِكُمْ وَنُدْخِلْكُم مُّدْخَلًا كَرِيمًا','اگر از گناهان بزرگی که از آن نهی می‌شوید پرهیز کنید، گناهان کوچک شما را می‌پوشانیم؛ و شما را در جایگاه خوبی وارد می‌سازیم.'];
ayat[525]=[4,32,'وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللَّهُ بِهِ بَعْضَكُمْ عَلَىٰ بَعْضٍ ۚ لِّلرِّجَالِ نَصِيبٌ مِّمَّا اكْتَسَبُوا ۖ وَلِلنِّسَاءِ نَصِيبٌ مِّمَّا اكْتَسَبْنَ ۚ وَاسْأَلُوا اللَّهَ مِن فَضْلِهِ ۗ إِنَّ اللَّهَ كَانَ بِكُلِّ شَيْءٍ عَلِيمًا','برتریهایی را که خداوند برای بعضی از شما بر بعضی دیگر قرار داده آرزو نکنید! (این تفاوتهای طبیعی و حقوقی، برای حفظ نظام زندگی شما، و بر طبق عدالت است. ولی با این حال،) مردان نصیبی از آنچه به دست می‌آورند دارند، و زنان نیز نصیبی؛ (و نباید حقوق هیچ‌یک پایمال گردد). و از فضل (و رحمت و برکت) خدا، برای رفع تنگناها طلب کنید! و خداوند به هر چیز داناست.'];
ayat[526]=[4,33,'وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدَانِ وَالْأَقْرَبُونَ ۚ وَالَّذِينَ عَقَدَتْ أَيْمَانُكُمْ فَآتُوهُمْ نَصِيبَهُمْ ۚ إِنَّ اللَّهَ كَانَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدًا','برای هر کسی، وارثانی قرار دادیم، که از میراث پدر و مادر و نزدیکان ارث ببرند؛ و (نیز) کسانی که با آنها پیمان بسته‌اید، نصیبشان را بپردازید! خداوند بر هر چیز، شاهد و ناظر است.'];
ayat[527]=[4,34,'الرِّجَالُ قَوَّامُونَ عَلَى النِّسَاءِ بِمَا فَضَّلَ اللَّهُ بَعْضَهُمْ عَلَىٰ بَعْضٍ وَبِمَا أَنفَقُوا مِنْ أَمْوَالِهِمْ ۚ فَالصَّالِحَاتُ قَانِتَاتٌ حَافِظَاتٌ لِّلْغَيْبِ بِمَا حَفِظَ اللَّهُ ۚ وَاللَّاتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاهْجُرُوهُنَّ فِي الْمَضَاجِعِ وَاضْرِبُوهُنَّ ۖ فَإِنْ أَطَعْنَكُمْ فَلَا تَبْغُوا عَلَيْهِنَّ سَبِيلًا ۗ إِنَّ اللَّهَ كَانَ عَلِيًّا كَبِيرًا','مردان، سرپرست و نگهبان زنانند، بخاطر برتریهایی که خداوند (از نظر نظام اجتماع) برای بعضی نسبت به بعضی دیگر قرار داده است، و بخاطر انفاقهایی که از اموالشان (در مورد زنان) می‌کنند. و زنان صالح، زنانی هستند که متواضعند، و در غیاب (همسر خود،) اسرار و حقوق او را، در مقابل حقوقی که خدا برای آنان قرار داده، حفظ می‌کنند. و (امّا) آن دسته از زنان را که از سرکشی و مخالفتشان بیم دارید، پند و اندرز دهید! (و اگر مؤثر واقع نشد،) در بستر از آنها دوری نمایید! و (اگر هیچ راهی جز شدت عمل، برای وادار کردن آنها به انجام وظایفشان نبود،) آنها را تنبیه کنید! و اگر از شما پیروی کردند، راهی برای تعدّی بر آنها نجویید! (بدانید) خداوند، بلندمرتبه و بزرگ است. (و قدرت او، بالاترین قدرتهاست.)'];
ayat[528]=[4,35,'وَإِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوا حَكَمًا مِّنْ أَهْلِهِ وَحَكَمًا مِّنْ أَهْلِهَا إِن يُرِيدَا إِصْلَاحًا يُوَفِّقِ اللَّهُ بَيْنَهُمَا ۗ إِنَّ اللَّهَ كَانَ عَلِيمًا خَبِيرًا','و اگر از جدایی و شکاف میان آن دو (همسر) بیم داشته باشید، یک داور از خانواده شوهر، و یک داور از خانواده زن انتخاب کنید (تا به کار آنان رسیدگی کنند). اگر این دو داور، تصمیم به اصلاح داشته باشند، خداوند به توافق آنها کمک می‌کند؛ زیرا خداوند، دانا و آگاه است (و از نیات همه، با خبر است).'];
ayat[529]=[4,36,'۞ وَاعْبُدُوا اللَّهَ وَلَا تُشْرِكُوا بِهِ شَيْئًا ۖ وَبِالْوَالِدَيْنِ إِحْسَانًا وَبِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَالْجَارِ ذِي الْقُرْبَىٰ وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنبِ وَابْنِ السَّبِيلِ وَمَا مَلَكَتْ أَيْمَانُكُمْ ۗ إِنَّ اللَّهَ لَا يُحِبُّ مَن كَانَ مُخْتَالًا فَخُورًا','و خدا را بپرستید! و هیچ‌چیز را همتای او قرار ندهید! و به پدر و مادر، نیکی کنید؛ همچنین به خویشاوندان و یتیمان و مسکینان، و همسایه نزدیک، و همسایه دور، و دوست و همنشین، و واماندگان در سفر، و بردگانی که مالک آنها هستید؛ زیرا خداوند، کسی را که متکبر و فخر فروش است، (و از ادای حقوق دیگران سرباز می‌زند،) دوست نمی‌دارد.'];
ayat[530]=[4,37,'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُونَ مَا آتَاهُمُ اللَّهُ مِن فَضْلِهِ ۗ وَأَعْتَدْنَا لِلْكَافِرِينَ عَذَابًا مُّهِينًا','آنها کسانی هستند که بخل می‌ورزند، و مردم را به بخل دعوت می‌کنند، و آنچه را که خداوند از فضل (و رحمت) خود به آنها داده، کتمان می‌نمایند. (این عمل، در حقیقت از کفرشان سرچشمه گرفته؛) و ما برای کافران، عذاب خوارکننده‌ای آماده کرده‌ایم.'];
ayat[531]=[4,38,'وَالَّذِينَ يُنفِقُونَ أَمْوَالَهُمْ رِئَاءَ النَّاسِ وَلَا يُؤْمِنُونَ بِاللَّهِ وَلَا بِالْيَوْمِ الْآخِرِ ۗ وَمَن يَكُنِ الشَّيْطَانُ لَهُ قَرِينًا فَسَاءَ قَرِينًا','و آنها کسانی هستند که اموال خود را برای نشان‌دادن به مردم انفاق می‌کنند، و ایمان به خدا و روز بازپسین ندارند؛ (چرا که شیطان، رفیق و همنشین آنهاست؛) و کسی که شیطان قرین او باشد، بد همنشین و قرینی است.'];
ayat[532]=[4,39,'وَمَاذَا عَلَيْهِمْ لَوْ آمَنُوا بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَأَنفَقُوا مِمَّا رَزَقَهُمُ اللَّهُ ۚ وَكَانَ اللَّهُ بِهِمْ عَلِيمًا','چه می‌شد اگر آنها به خدا و روز بازپسین ایمان می‌آوردند، و از آنچه خدا به آنان روزی داده، (در راه او) انفاق می‌کردند؟! و خداوند از (اعمال و نیات) آنها آگاه است.'];
ayat[533]=[4,40,'إِنَّ اللَّهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍ ۖ وَإِن تَكُ حَسَنَةً يُضَاعِفْهَا وَيُؤْتِ مِن لَّدُنْهُ أَجْرًا عَظِيمًا','خداوند (حتّی) به اندازه سنگینی ذره‌ای ستم نمی‌کند؛ و اگر کار نیکی باشد، آن را دو چندان می‌سازد؛ و از نزد خود، پاداش عظیمی (در برابر آن) می‌دهد.'];
ayat[534]=[4,41,'فَكَيْفَ إِذَا جِئْنَا مِن كُلِّ أُمَّةٍ بِشَهِيدٍ وَجِئْنَا بِكَ عَلَىٰ هَٰؤُلَاءِ شَهِيدًا','حال آنها چگونه است آن روزی که از هر امتی، شاهد و گواهی (بر اعمالشان) می‌آوریم، و تو را نیز بر آنان گواه خواهیم آورد؟'];
ayat[535]=[4,42,'يَوْمَئِذٍ يَوَدُّ الَّذِينَ كَفَرُوا وَعَصَوُا الرَّسُولَ لَوْ تُسَوَّىٰ بِهِمُ الْأَرْضُ وَلَا يَكْتُمُونَ اللَّهَ حَدِيثًا','در آن روز، آنها که کافر شدند و با پیامبر (ص) بمخالفت برخاستند، آرزو می‌کنند که ای کاش (خاک بودند، و) خاک آنها با زمینهای اطراف یکسان می‌شد (و بکلی محو و فراموش می‌شدند). در آن روز، (با آن همه گواهان،) سخنی را نمی‌توانند از خدا پنهان کنند.'];
ayat[536]=[4,43,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقْرَبُوا الصَّلَاةَ وَأَنتُمْ سُكَارَىٰ حَتَّىٰ تَعْلَمُوا مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِي سَبِيلٍ حَتَّىٰ تَغْتَسِلُوا ۚ وَإِن كُنتُم مَّرْضَىٰ أَوْ عَلَىٰ سَفَرٍ أَوْ جَاءَ أَحَدٌ مِّنكُم مِّنَ الْغَائِطِ أَوْ لَامَسْتُمُ النِّسَاءَ فَلَمْ تَجِدُوا مَاءً فَتَيَمَّمُوا صَعِيدًا طَيِّبًا فَامْسَحُوا بِوُجُوهِكُمْ وَأَيْدِيكُمْ ۗ إِنَّ اللَّهَ كَانَ عَفُوًّا غَفُورًا','ای کسانی که ایمان آورده‌اید! در حال مستی به نماز نزدیک نشوید، تا بدانید چه می‌گویید! و همچنین هنگامی که جنب هستید -مگر اینکه مسافر باشید- تا غسل کنید. و اگر بیمارید، یا مسافر، و یا «قضای حاجت» کرده‌اید، و یا با زنان آمیزش جنسی داشته‌اید، و در این حال، آب (برای وضو یا غسل) نیافتید، با خاک پاکی تیمّم کنید! (به این طریق که) صورتها و دستهایتان را با آن مسح نمایید. خداوند، بخشنده و آمرزنده است.'];
ayat[537]=[4,44,'أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِّنَ الْكِتَابِ يَشْتَرُونَ الضَّلَالَةَ وَيُرِيدُونَ أَن تَضِلُّوا السَّبِيلَ','آیا ندیدی کسانی را که بهره‌ای از کتاب (خدا) به آنها داده شده بود، (به جای اینکه از آن، برای هدایت خود و دیگران استفاده کنند، برای خویش) گمراهی می‌خرند، و می‌خواهند شما نیز گمراه شوید؟'];
ayat[538]=[4,45,'وَاللَّهُ أَعْلَمُ بِأَعْدَائِكُمْ ۚ وَكَفَىٰ بِاللَّهِ وَلِيًّا وَكَفَىٰ بِاللَّهِ نَصِيرًا','خدا به دشمنان شما آگاهتر است؛ (ولی آنها زیانی به شما نمی‌رسانند.) و کافی است که خدا ولیِّ شما باشد؛ و کافی است که خدا یاور شما باشد.'];
ayat[539]=[4,46,'مِّنَ الَّذِينَ هَادُوا يُحَرِّفُونَ الْكَلِمَ عَن مَّوَاضِعِهِ وَيَقُولُونَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَرَاعِنَا لَيًّا بِأَلْسِنَتِهِمْ وَطَعْنًا فِي الدِّينِ ۚ وَلَوْ أَنَّهُمْ قَالُوا سَمِعْنَا وَأَطَعْنَا وَاسْمَعْ وَانظُرْنَا لَكَانَ خَيْرًا لَّهُمْ وَأَقْوَمَ وَلَٰكِن لَّعَنَهُمُ اللَّهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ إِلَّا قَلِيلًا','بعضی از یهود، سخنان را از جای خود، تحریف می‌کنند؛ و (به جای اینکه بگویند: «شنیدیم و اطاعت کردیم»)، می‌گویند: «شنیدیم و مخالفت کردیم! و (نیز می‌گویند:) بشنو! که هرگز نشنوی! و (از روی تمسخر می‌گویند:) راعنا [= ما را تحمیق کن!]» تا با زبان خود، حقایق را بگردانند و در آیین خدا، طعنه زنند. ولی اگر آنها (به جای این همه لجاجت) می‌گفتند: «شنیدیم و اطاعت کردیم؛ و سخنان ما را بشنو و به ما مهلت ده (تا حقایق را درک کنیم)»، برای آنان بهتر، و با واقعیت سازگارتر بود. ولی خداوند، آنها را بخاطر کفرشان، از رحمت خود دور ساخته است؛ از این رو جز عدّه کمی ایمان نمی‌آورند.'];
ayat[540]=[4,47,'يَا أَيُّهَا الَّذِينَ أُوتُوا الْكِتَابَ آمِنُوا بِمَا نَزَّلْنَا مُصَدِّقًا لِّمَا مَعَكُم مِّن قَبْلِ أَن نَّطْمِسَ وُجُوهًا فَنَرُدَّهَا عَلَىٰ أَدْبَارِهَا أَوْ نَلْعَنَهُمْ كَمَا لَعَنَّا أَصْحَابَ السَّبْتِ ۚ وَكَانَ أَمْرُ اللَّهِ مَفْعُولًا','ای کسانی که کتاب (خدا) به شما داده شده! به آنچه (بر پیامبر خود) نازل کردیم -و هماهنگ با نشانه‌هایی است که با شماست- ایمان بیاورید، پیش از آنکه صورتهایی را محو کنیم، سپس به پشت سر بازگردانیم، یا آنها را از رحمت خود دور سازیم، همان گونه که «اصحاب سبت» [= گروهی از تبهکاران بنی اسرائیل‌] را دور ساختیم؛ و فرمان خدا، در هر حال انجام شدنی است!'];
ayat[541]=[4,48,'إِنَّ اللَّهَ لَا يَغْفِرُ أَن يُشْرَكَ بِهِ وَيَغْفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَاءُ ۚ وَمَن يُشْرِكْ بِاللَّهِ فَقَدِ افْتَرَىٰ إِثْمًا عَظِيمًا','خداوند (هرگز) شرک را نمی‌بخشد! و پایین‌تر از آن را برای هر کس (بخواهد و شایسته بداند) می‌بخشد. و آن کسی که برای خدا، شریکی قرار دهد، گناه بزرگی مرتکب شده است.'];
ayat[542]=[4,49,'أَلَمْ تَرَ إِلَى الَّذِينَ يُزَكُّونَ أَنفُسَهُم ۚ بَلِ اللَّهُ يُزَكِّي مَن يَشَاءُ وَلَا يُظْلَمُونَ فَتِيلًا','آیا ندیدی کسانی را که خودستایی می‌کنند؟! (این خود ستاییها، بی‌ارزش است؛) بلکه خدا هر کس را بخواهد، ستایش می‌کند؛ و کمترین ستمی به آنها نخواهد شد.'];
ayat[543]=[4,50,'انظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ ۖ وَكَفَىٰ بِهِ إِثْمًا مُّبِينًا','ببین چگونه بر خدا دروغ می‌بندند! و همین گناه آشکار، (برای مجازات آنان) کافی است.'];
ayat[544]=[4,51,'أَلَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيبًا مِّنَ الْكِتَابِ يُؤْمِنُونَ بِالْجِبْتِ وَالطَّاغُوتِ وَيَقُولُونَ لِلَّذِينَ كَفَرُوا هَٰؤُلَاءِ أَهْدَىٰ مِنَ الَّذِينَ آمَنُوا سَبِيلًا','آیا ندیدی کسانی را که بهره‌ای از کتاب (خدا) به آنان داده شده، (با این حال)، به «جبت» و «طاغوت» [= بت و بت‌پرستان‌] ایمان می‌آورند، و درباره کافران می‌گویند: «آنها، از کسانی که ایمان آورده‌اند، هدایت یافته‌ترند»؟!'];
ayat[545]=[4,52,'أُولَٰئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ ۖ وَمَن يَلْعَنِ اللَّهُ فَلَن تَجِدَ لَهُ نَصِيرًا','آنها کسانی هستند که خداوند، ایشان را از رحمت خود، دور ساخته است؛ و هر کس را خدا از رحمتش دور سازد، یاوری برای او نخواهی یافت.'];
ayat[546]=[4,53,'أَمْ لَهُمْ نَصِيبٌ مِّنَ الْمُلْكِ فَإِذًا لَّا يُؤْتُونَ النَّاسَ نَقِيرًا','آیا آنها [= یهود] سهمی در حکومت دارند (که بخواهند چنین داوری کنند)؟ در حالی که اگر چنین بود، (همه چیز را در انحصار خود می‌گرفتند،) و کمترین حق را به مردم نمی‌دادند.'];
ayat[547]=[4,54,'أَمْ يَحْسُدُونَ النَّاسَ عَلَىٰ مَا آتَاهُمُ اللَّهُ مِن فَضْلِهِ ۖ فَقَدْ آتَيْنَا آلَ إِبْرَاهِيمَ الْكِتَابَ وَالْحِكْمَةَ وَآتَيْنَاهُم مُّلْكًا عَظِيمًا','یا اینکه نسبت به مردم [= پیامبر و خاندانش‌]، و بر آنچه خدا از فضلش به آنان بخشیده، حسد می‌ورزند؟ ما به آل ابراهیم، (که یهود از خاندان او هستند نیز،) کتاب و حکمت دادیم؛ و حکومت عظیمی در اختیار آنها [= پیامبران بنی اسرائیل‌] قرار دادیم.'];
ayat[548]=[4,55,'فَمِنْهُم مَّنْ آمَنَ بِهِ وَمِنْهُم مَّن صَدَّ عَنْهُ ۚ وَكَفَىٰ بِجَهَنَّمَ سَعِيرًا','ولی جمعی از آنها به آن ایمان آوردند؛ و جمعی راه (مردم را) بر آن بستند. و شعله فروزانِ آتش دوزخ، برای آنها کافی است!'];
ayat[549]=[4,56,'إِنَّ الَّذِينَ كَفَرُوا بِآيَاتِنَا سَوْفَ نُصْلِيهِمْ نَارًا كُلَّمَا نَضِجَتْ جُلُودُهُم بَدَّلْنَاهُمْ جُلُودًا غَيْرَهَا لِيَذُوقُوا الْعَذَابَ ۗ إِنَّ اللَّهَ كَانَ عَزِيزًا حَكِيمًا','کسانی که به آیات ما کافر شدند، بزودی آنها را در آتشی وارد می‌کنیم که هرگاه پوستهای تنشان (در آن) بریان گردد (و بسوزد)، پوستهای دیگری به جای آن قرار می‌دهیم، تا کیفر (الهی) را بچشند. خداوند، توانا و حکیم است (و روی حساب، کیفر می‌دهد).'];
ayat[550]=[4,57,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ لَّهُمْ فِيهَا أَزْوَاجٌ مُّطَهَّرَةٌ ۖ وَنُدْخِلُهُمْ ظِلًّا ظَلِيلًا','و کسانی که ایمان آوردند و کارهای شایسته انجام دادند، بزودی آنها را در باغهایی از بهشت وارد می‌کنیم که نهرها از زیر درختانش جاری است؛ همیشه در آن خواهند ماند؛ و همسرانی پاکیزه برای آنها خواهد بود؛ و آنان را در سایه‌های گسترده (و فرح بخش) جای میدهیم.'];
ayat[551]=[4,58,'۞ إِنَّ اللَّهَ يَأْمُرُكُمْ أَن تُؤَدُّوا الْأَمَانَاتِ إِلَىٰ أَهْلِهَا وَإِذَا حَكَمْتُم بَيْنَ النَّاسِ أَن تَحْكُمُوا بِالْعَدْلِ ۚ إِنَّ اللَّهَ نِعِمَّا يَعِظُكُم بِهِ ۗ إِنَّ اللَّهَ كَانَ سَمِيعًا بَصِيرًا','خداوند به شما فرمان می‌دهد که امانتها را به صاحبانش بدهید! و هنگامی که میان مردم داوری می‌کنید، به عدالت داوری کنید! خداوند، اندرزهای خوبی به شما می‌دهد! خداوند، شنوا و بیناست.'];
ayat[552]=[4,59,'يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَأُولِي الْأَمْرِ مِنكُمْ ۖ فَإِن تَنَازَعْتُمْ فِي شَيْءٍ فَرُدُّوهُ إِلَى اللَّهِ وَالرَّسُولِ إِن كُنتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ ذَٰلِكَ خَيْرٌ وَأَحْسَنُ تَأْوِيلًا','ای کسانی که ایمان آورده‌اید! اطاعت کنید خدا را! و اطاعت کنید پیامبر خدا و اولو الأمر [= اوصیای پیامبر] را! و هرگاه در چیزی نزاع داشتید، آن را به خدا و پیامبر بازگردانید (و از آنها داوری بطلبید) اگر به خدا و روز رستاخیز ایمان دارید! این (کار) برای شما بهتر، و عاقبت و پایانش نیکوتر است.'];
ayat[553]=[4,60,'أَلَمْ تَرَ إِلَى الَّذِينَ يَزْعُمُونَ أَنَّهُمْ آمَنُوا بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ يُرِيدُونَ أَن يَتَحَاكَمُوا إِلَى الطَّاغُوتِ وَقَدْ أُمِرُوا أَن يَكْفُرُوا بِهِ وَيُرِيدُ الشَّيْطَانُ أَن يُضِلَّهُمْ ضَلَالًا بَعِيدًا','آیا ندیدی کسانی را که گمان می‌کنند به آنچه (از کتابهای آسمانی که) بر تو و بر پیشینیان نازل شده، ایمان آورده‌اند، ولی می‌خواهند برای داوری نزد طاغوت و حکّام باطل بروند؟! با اینکه به آنها دستور داده شده که به طاغوت کافر شوند. امّا شیطان می‌خواهد آنان را گمراه کند، و به بیراهه‌های دور دستی بیفکند.'];
ayat[554]=[4,61,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَىٰ مَا أَنزَلَ اللَّهُ وَإِلَى الرَّسُولِ رَأَيْتَ الْمُنَافِقِينَ يَصُدُّونَ عَنكَ صُدُودًا','و هنگامی که به آنها گفته شود: «به سوی آنچه خداوند نازل کرده، و به سوی پیامبر بیایید»، منافقان را می‌بینی که (از قبول دعوت) ِ تو، اعراض می‌کنند!'];
ayat[555]=[4,62,'فَكَيْفَ إِذَا أَصَابَتْهُم مُّصِيبَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ ثُمَّ جَاءُوكَ يَحْلِفُونَ بِاللَّهِ إِنْ أَرَدْنَا إِلَّا إِحْسَانًا وَتَوْفِيقًا','پس چگونه وقتی به خاطر اعمالشان، گرفتار مصیبتی می‌شوند، سپس به سراغ تو می‌آیند، سوگند یاد می‌کنند که منظورِ (ما از بردنِ داوری نزد دیگران)، جز نیکی کردن و توافق (میان طرفین نزاع،) نبوده است؟!'];
ayat[556]=[4,63,'أُولَٰئِكَ الَّذِينَ يَعْلَمُ اللَّهُ مَا فِي قُلُوبِهِمْ فَأَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُل لَّهُمْ فِي أَنفُسِهِمْ قَوْلًا بَلِيغًا','آنها کسانی هستند که خدا، آنچه را در دل دارند، می‌داند. از (مجازات) ِ آنان صرف نظر کن! و آنها را اندرز ده! و با بیانی رسا، نتایج اعمالشان را به آنها گوشزد نما!'];
ayat[557]=[4,64,'وَمَا أَرْسَلْنَا مِن رَّسُولٍ إِلَّا لِيُطَاعَ بِإِذْنِ اللَّهِ ۚ وَلَوْ أَنَّهُمْ إِذ ظَّلَمُوا أَنفُسَهُمْ جَاءُوكَ فَاسْتَغْفَرُوا اللَّهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللَّهَ تَوَّابًا رَّحِيمًا','ما هیچ پیامبری را نفرستادیم مگر برای این که به فرمان خدا، از وی اطاعت شود. و اگر این مخالفان، هنگامی که به خود ستم می‌کردند (و فرمانهای خدا را زیر پا می‌گذاردند)، به نزد تو می‌آمدند؛ و از خدا طلب آمرزش می‌کردند؛ و پیامبر هم برای آنها استغفار می‌کرد؛ خدا را توبه پذیر و مهربان می‌یافتند.'];
ayat[558]=[4,65,'فَلَا وَرَبِّكَ لَا يُؤْمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُوا فِي أَنفُسِهِمْ حَرَجًا مِّمَّا قَضَيْتَ وَيُسَلِّمُوا تَسْلِيمًا','به پروردگارت سوگند که آنها مؤمن نخواهند بود، مگر اینکه در اختلافات خود، تو را به داوری طلبند؛ و سپس از داوری تو، در دل خود احساس ناراحتی نکنند؛ و کاملا تسلیم باشند.'];
ayat[559]=[4,66,'وَلَوْ أَنَّا كَتَبْنَا عَلَيْهِمْ أَنِ اقْتُلُوا أَنفُسَكُمْ أَوِ اخْرُجُوا مِن دِيَارِكُم مَّا فَعَلُوهُ إِلَّا قَلِيلٌ مِّنْهُمْ ۖ وَلَوْ أَنَّهُمْ فَعَلُوا مَا يُوعَظُونَ بِهِ لَكَانَ خَيْرًا لَّهُمْ وَأَشَدَّ تَثْبِيتًا','اگر (همانند بعضی از امتهای پیشین،) به آنان دستور می‌دادیم: «یکدیگر را به قتل برسانید»، و یا: «از وطن و خانه خود، بیرون روید»، تنها عده کمی از آنها عمل می‌کردند! و اگر اندرزهایی را که به آنان داده می‌شد انجام می‌دادند، برای آنها بهتر بود؛ و موجب تقویت ایمان آنها می‌شد.'];
ayat[560]=[4,67,'وَإِذًا لَّآتَيْنَاهُم مِّن لَّدُنَّا أَجْرًا عَظِيمًا','و در این صورت، پاداش بزرگی از ناحیه خود به آنها می‌دادیم.'];
ayat[561]=[4,68,'وَلَهَدَيْنَاهُمْ صِرَاطًا مُّسْتَقِيمًا','و آنان را به راه راست، هدایت می‌کردیم.'];
ayat[562]=[4,69,'وَمَن يُطِعِ اللَّهَ وَالرَّسُولَ فَأُولَٰئِكَ مَعَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِم مِّنَ النَّبِيِّينَ وَالصِّدِّيقِينَ وَالشُّهَدَاءِ وَالصَّالِحِينَ ۚ وَحَسُنَ أُولَٰئِكَ رَفِيقًا','و کسی که خدا و پیامبر را اطاعت کند، (در روز رستاخیز،) همنشین کسانی خواهد بود که خدا، نعمت خود را بر آنان تمام کرده؛ از پیامبران و صدّیقان و شهدا و صالحان؛ و آنها رفیقهای خوبی هستند!'];
ayat[563]=[4,70,'ذَٰلِكَ الْفَضْلُ مِنَ اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ عَلِيمًا','این موهبتی از ناحیه خداست. و کافی است که او، (از حالِ بندگان، و نیّات و اعمالشان) آگاه است.'];
ayat[564]=[4,71,'يَا أَيُّهَا الَّذِينَ آمَنُوا خُذُوا حِذْرَكُمْ فَانفِرُوا ثُبَاتٍ أَوِ انفِرُوا جَمِيعًا','ای کسانی که ایمان آورده‌اید! آمادگی خود را (در برابر دشمن) حفظ کنید و در دسته‌های متعدّد، یا بصورت دسته واحد، (طبق شرایط هر زمان و هر مکان،) به سوی دشمن حرکت نمایید!'];
ayat[565]=[4,72,'وَإِنَّ مِنكُمْ لَمَن لَّيُبَطِّئَنَّ فَإِنْ أَصَابَتْكُم مُّصِيبَةٌ قَالَ قَدْ أَنْعَمَ اللَّهُ عَلَيَّ إِذْ لَمْ أَكُن مَّعَهُمْ شَهِيدًا','در میان شما، افرادی (منافق) هستند، که (هم خودشان سست می‌باشند، و هم) دیگران را به سستی می‌کشانند؛ اگر مصیبتی به شما برسد، می‌گویند: «خدا به ما نعمت داد که با مجاهدان نبودیم، تا شاهد (آن مصیبت) باشیم!»'];
ayat[566]=[4,73,'وَلَئِنْ أَصَابَكُمْ فَضْلٌ مِّنَ اللَّهِ لَيَقُولَنَّ كَأَن لَّمْ تَكُن بَيْنَكُمْ وَبَيْنَهُ مَوَدَّةٌ يَا لَيْتَنِي كُنتُ مَعَهُمْ فَأَفُوزَ فَوْزًا عَظِيمًا','و اگر غنیمتی از جانب خدا به شما برسد، درست مثل اینکه هرگز میان شما و آنها دوستی و موّدتی نبوده، می‌گویند: «ای کاش ما هم با آنها بودیم، و به رستگاری (و پیروزی) ِ بزرگی می‌رسیدیم!»'];
ayat[567]=[4,74,'۞ فَلْيُقَاتِلْ فِي سَبِيلِ اللَّهِ الَّذِينَ يَشْرُونَ الْحَيَاةَ الدُّنْيَا بِالْآخِرَةِ ۚ وَمَن يُقَاتِلْ فِي سَبِيلِ اللَّهِ فَيُقْتَلْ أَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيهِ أَجْرًا عَظِيمًا','کسانی که زندگی دنیا را به آخرت فروخته‌اند، باید در راه خدا پیکار کنند! و آن کس که در راه خدا پیکار کند، و کشته شود یا پیروز گردد، پاداش بزرگی به او خواهیم داد.'];
ayat[568]=[4,75,'وَمَا لَكُمْ لَا تُقَاتِلُونَ فِي سَبِيلِ اللَّهِ وَالْمُسْتَضْعَفِينَ مِنَ الرِّجَالِ وَالنِّسَاءِ وَالْوِلْدَانِ الَّذِينَ يَقُولُونَ رَبَّنَا أَخْرِجْنَا مِنْ هَٰذِهِ الْقَرْيَةِ الظَّالِمِ أَهْلُهَا وَاجْعَل لَّنَا مِن لَّدُنكَ وَلِيًّا وَاجْعَل لَّنَا مِن لَّدُنكَ نَصِيرًا','چرا در راه خدا، و (در راه) مردان و زنان و کودکانی که (به دست ستمگران) تضعیف شده‌اند، پیکار نمی‌کنید؟! همان افراد (ستمدیده‌ای) که می‌گویند: «پروردگارا! ما را از این شهر (مکه)، که اهلش ستمگرند، بیرون ببر! و از طرف خود، برای ما سرپرستی قرار ده! و از جانب خود، یار و یاوری برای ما تعیین فرما!'];
ayat[569]=[4,76,'الَّذِينَ آمَنُوا يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ ۖ وَالَّذِينَ كَفَرُوا يُقَاتِلُونَ فِي سَبِيلِ الطَّاغُوتِ فَقَاتِلُوا أَوْلِيَاءَ الشَّيْطَانِ ۖ إِنَّ كَيْدَ الشَّيْطَانِ كَانَ ضَعِيفًا','کسانی که ایمان دارند، در راه خدا پیکار می‌کنند؛ و آنها که کافرند، در راه طاغوت [= بت و افراد طغیانگر]. پس شما با یاران شیطان، پیکار کنید! (و از آنها نهراسید!) زیرا که نقشه شیطان، (همانند قدرتش) ضعیف است.'];
ayat[570]=[4,77,'أَلَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أَيْدِيَكُمْ وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ إِذَا فَرِيقٌ مِّنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللَّهِ أَوْ أَشَدَّ خَشْيَةً ۚ وَقَالُوا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَ لَوْلَا أَخَّرْتَنَا إِلَىٰ أَجَلٍ قَرِيبٍ ۗ قُلْ مَتَاعُ الدُّنْيَا قَلِيلٌ وَالْآخِرَةُ خَيْرٌ لِّمَنِ اتَّقَىٰ وَلَا تُظْلَمُونَ فَتِيلًا','آیا ندیدی کسانی را که (در مکّه) به آنها گفته شد: «فعلا) ً دست از جهاد بدارید! و نماز را برپا کنید! و زکات بپردازید!» (امّا آنها از این دستور، ناراحت بودند)، ولی هنگامی که (در مدینه) فرمان جهاد به آنها داده شد، جمعی از آنان، از مردم می‌ترسیدند، همان گونه که از خدا می‌ترسند، بلکه بیشتر! و گفتند: «پروردگارا! چرا جهاد را بر ما مقرّر داشتی؟! چرا این فرمان را تا زمان نزدیکی تأخیر نینداختی؟!» به آنها بگو: «سرمایه زندگی دنیا، ناچیز است! و سرای آخرت، برای کسی که پرهیزگار باشد، بهتر است! و به اندازه رشته شکافِ هسته خرمایی، به شما ستم نخواهد شد!'];
ayat[571]=[4,78,'أَيْنَمَا تَكُونُوا يُدْرِككُّمُ الْمَوْتُ وَلَوْ كُنتُمْ فِي بُرُوجٍ مُّشَيَّدَةٍ ۗ وَإِن تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هَٰذِهِ مِنْ عِندِ اللَّهِ ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هَٰذِهِ مِنْ عِندِكَ ۚ قُلْ كُلٌّ مِّنْ عِندِ اللَّهِ ۖ فَمَالِ هَٰؤُلَاءِ الْقَوْمِ لَا يَكَادُونَ يَفْقَهُونَ حَدِيثًا','هر جا باشید، مرگ شما را درمی‌یابد؛ هر چند در برجهای محکم باشید! و اگر به آنها [= منافقان‌] حسنه (و پیروزی) برسد، می‌گویند: «این، از ناحیه خداست.» و اگر سیّئه (و شکستی) برسد، می‌گویند: «این، از ناحیه توست.» بگو: «همه اینها از ناحیه خداست.» پس چرا این گروه حاضر نیستند سخنی را درک کنند؟!'];
ayat[572]=[4,79,'مَّا أَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللَّهِ ۖ وَمَا أَصَابَكَ مِن سَيِّئَةٍ فَمِن نَّفْسِكَ ۚ وَأَرْسَلْنَاكَ لِلنَّاسِ رَسُولًا ۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا','(آری،) آنچه از نیکیها به تو می‌رسد، از طرف خداست؛ و آنچه از بدی به تو می‌رسد، از سوی خود توست. و ما تو را رسول برای مردم فرستادیم؛ و گواهی خدا در این باره، کافی است!'];
ayat[573]=[4,80,'مَّن يُطِعِ الرَّسُولَ فَقَدْ أَطَاعَ اللَّهَ ۖ وَمَن تَوَلَّىٰ فَمَا أَرْسَلْنَاكَ عَلَيْهِمْ حَفِيظًا','کسی که از پیامبر اطاعت کند، خدا را اطاعت کرده؛ و کسی که سرباز زند، تو را نگهبان (و مراقب) او نفرستادیم (و در برابر او، مسؤول نیستی).'];
ayat[574]=[4,81,'وَيَقُولُونَ طَاعَةٌ فَإِذَا بَرَزُوا مِنْ عِندِكَ بَيَّتَ طَائِفَةٌ مِّنْهُمْ غَيْرَ الَّذِي تَقُولُ ۖ وَاللَّهُ يَكْتُبُ مَا يُبَيِّتُونَ ۖ فَأَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا','آنها در حضور تو می‌گویند: «فرمانبرداریم»؛ امّا هنگامی که از نزد تو بیرون می‌روند، جمعی از آنان بر خلاف گفته‌های تو، جلسات سرّی شبانه تشکیل می‌دهند؛ آنچه را در این جلسات می‌گویند، خداوند می‌نویسد. اعتنایی به آنها نکن! (و از نقشه‌های آنان وحشت نداشته باش!) و بر خدا توکّل کن! کافی است که او یار و مدافع تو باشد.'];
ayat[575]=[4,82,'أَفَلَا يَتَدَبَّرُونَ الْقُرْآنَ ۚ وَلَوْ كَانَ مِنْ عِندِ غَيْرِ اللَّهِ لَوَجَدُوا فِيهِ اخْتِلَافًا كَثِيرًا','آیا درباره قرآن نمی‌اندیشند؟! اگر از سوی غیر خدا بود، اختلاف فراوانی در آن می‌یافتند.'];
ayat[576]=[4,83,'وَإِذَا جَاءَهُمْ أَمْرٌ مِّنَ الْأَمْنِ أَوِ الْخَوْفِ أَذَاعُوا بِهِ ۖ وَلَوْ رَدُّوهُ إِلَى الرَّسُولِ وَإِلَىٰ أُولِي الْأَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِينَ يَسْتَنبِطُونَهُ مِنْهُمْ ۗ وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ لَاتَّبَعْتُمُ الشَّيْطَانَ إِلَّا قَلِيلًا','و هنگامی که خبری از پیروزی یا شکست به آنها برسد، (بدون تحقیق،) آن را شایع می‌سازند؛ در حالی که اگر آن را به پیامبر و پیشوایان -که قدرت تشخیص کافی دارند- بازگردانند، از ریشه‌های مسائل آگاه خواهند شد. و اگر فضل و رحمت خدا بر شما نبود، جز عدّه کمی، همگی از شیطان پیروی می‌کردید (و گمراه می‌شدید).'];
ayat[577]=[4,84,'فَقَاتِلْ فِي سَبِيلِ اللَّهِ لَا تُكَلَّفُ إِلَّا نَفْسَكَ ۚ وَحَرِّضِ الْمُؤْمِنِينَ ۖ عَسَى اللَّهُ أَن يَكُفَّ بَأْسَ الَّذِينَ كَفَرُوا ۚ وَاللَّهُ أَشَدُّ بَأْسًا وَأَشَدُّ تَنكِيلًا','در راه خدا پیکار کن! تنها مسؤول وظیفه خود هستی! و مؤمنان را (بر این کار،) تشویق نما! امید است خداوند از قدرت کافران جلوگیری کند (حتی اگر تنها خودت به میدان بروی)! و خداوند قدرتش بیشتر، و مجازاتش دردناکتر است.'];
ayat[578]=[4,85,'مَّن يَشْفَعْ شَفَاعَةً حَسَنَةً يَكُن لَّهُ نَصِيبٌ مِّنْهَا ۖ وَمَن يَشْفَعْ شَفَاعَةً سَيِّئَةً يَكُن لَّهُ كِفْلٌ مِّنْهَا ۗ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ مُّقِيتًا','کسی که شفاعت [= تشویق و کمک‌] به کار نیکی کند، نصیبی از آن برای او خواهد بود؛ و کسی که شفاعت [= تشویق و کمک‌] به کار بدی کند، سهمی از آن خواهد داشت. و خداوند، حسابرس و نگهدار هر چیز است.'];
ayat[579]=[4,86,'وَإِذَا حُيِّيتُم بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْهَا أَوْ رُدُّوهَا ۗ إِنَّ اللَّهَ كَانَ عَلَىٰ كُلِّ شَيْءٍ حَسِيبًا','هرگاه به شما تحیّت گویند، پاسخ آن را بهتر از آن بدهید؛ یا (لااقل) به همان گونه پاسخ گویید! خداوند حساب همه چیز را دارد.'];
ayat[580]=[4,87,'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۚ لَيَجْمَعَنَّكُمْ إِلَىٰ يَوْمِ الْقِيَامَةِ لَا رَيْبَ فِيهِ ۗ وَمَنْ أَصْدَقُ مِنَ اللَّهِ حَدِيثًا','خداوند، معبودی جز او نیست! و به یقین، همه شما را در روز رستاخیز -که شکی در آن نیست- جمع می‌کند! و کیست که از خداوند، راستگوتر باشد؟'];
ayat[581]=[4,88,'۞ فَمَا لَكُمْ فِي الْمُنَافِقِينَ فِئَتَيْنِ وَاللَّهُ أَرْكَسَهُم بِمَا كَسَبُوا ۚ أَتُرِيدُونَ أَن تَهْدُوا مَنْ أَضَلَّ اللَّهُ ۖ وَمَن يُضْلِلِ اللَّهُ فَلَن تَجِدَ لَهُ سَبِيلًا','چرا درباره منافقین دو دسته شده‌اید؟! (بعضی جنگ با آنها را ممنوع، و بعضی مجاز می‌دانید.) در حالی که خداوند بخاطر اعمالشان، (افکار) آنها را کاملاً وارونه کرده است! آیا شما می‌خواهید کسانی را که خداوند (بر اثر اعمال زشتشان) گمراه کرده، هدایت کنید؟! در حالی که هر کس را خداوند گمراه کند، راهی برای او نخواهی یافت.'];
ayat[582]=[4,89,'وَدُّوا لَوْ تَكْفُرُونَ كَمَا كَفَرُوا فَتَكُونُونَ سَوَاءً ۖ فَلَا تَتَّخِذُوا مِنْهُمْ أَوْلِيَاءَ حَتَّىٰ يُهَاجِرُوا فِي سَبِيلِ اللَّهِ ۚ فَإِن تَوَلَّوْا فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ وَجَدتُّمُوهُمْ ۖ وَلَا تَتَّخِذُوا مِنْهُمْ وَلِيًّا وَلَا نَصِيرًا','آنان آرزو می‌کنند که شما هم مانند ایشان کافر شوید، و مساوی یکدیگر باشید. بنابر این، از آنها دوستانی انتخاب نکنید، مگر اینکه (توبه کنند، و) در راه خدا هجرت نمایند. هرگاه از این کار سر باز زنند، (و به اقدام بر ضدّ شما ادامه دهند،) هر جا آنها را یافتید، اسیر کنید! و (در صورت احساس خطر) به قتل برسانید! و از میان آنها، دوست و یار و یاوری اختیار نکنید!.'];
ayat[583]=[4,90,'إِلَّا الَّذِينَ يَصِلُونَ إِلَىٰ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُم مِّيثَاقٌ أَوْ جَاءُوكُمْ حَصِرَتْ صُدُورُهُمْ أَن يُقَاتِلُوكُمْ أَوْ يُقَاتِلُوا قَوْمَهُمْ ۚ وَلَوْ شَاءَ اللَّهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوكُمْ ۚ فَإِنِ اعْتَزَلُوكُمْ فَلَمْ يُقَاتِلُوكُمْ وَأَلْقَوْا إِلَيْكُمُ السَّلَمَ فَمَا جَعَلَ اللَّهُ لَكُمْ عَلَيْهِمْ سَبِيلًا','مگر آنها که با همپیمانان شما، پیمان بسته‌اند؛ یا آنها که به سوی شما می‌آیند، و از پیکار با شما، یا پیکار با قوم خود ناتوان شده‌اند؛ (نه سر جنگ با شما دارند، و نه توانایی مبارزه با قوم خود.) و اگر خداوند بخواهد، آنان را بر شما مسلطّ می‌کند تا با شما پیکار کنند. پس اگر از شما کناره‌گیری کرده و با شما پیکار ننمودند، (بلکه) پیشنهاد صلح کردند، خداوند به شما اجازه نمی‌دهد که متعرّض آنان شوید.'];
ayat[584]=[4,91,'سَتَجِدُونَ آخَرِينَ يُرِيدُونَ أَن يَأْمَنُوكُمْ وَيَأْمَنُوا قَوْمَهُمْ كُلَّ مَا رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيهَا ۚ فَإِن لَّمْ يَعْتَزِلُوكُمْ وَيُلْقُوا إِلَيْكُمُ السَّلَمَ وَيَكُفُّوا أَيْدِيَهُمْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ ۚ وَأُولَٰئِكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطَانًا مُّبِينًا','بزودی جمعیّت دیگری را می‌یابید که می‌خواهند هم از ناحیه شما در امان باشند، و هم از ناحیه قوم خودشان (که مشرکند. لذا نزد شما ادّعای ایمان می‌کنند؛ ولی) هر زمان آنان را به سوی فتنه (و بت پرستی) بازگردانند، با سر در آن فرو می‌روند! اگر از درگیری با شما کنار نرفتند و پیشنهاد صلح نکردند و دست از شما نکشیدند، آنها را هر جا یافتید اسیر کنید و (یا) به قتل برسانید! آنها کسانی هستند که ما برای شما، تسلّط آشکاری نسبت به آنان قرار داده‌ایم.'];
ayat[585]=[4,92,'وَمَا كَانَ لِمُؤْمِنٍ أَن يَقْتُلَ مُؤْمِنًا إِلَّا خَطَأً ۚ وَمَن قَتَلَ مُؤْمِنًا خَطَأً فَتَحْرِيرُ رَقَبَةٍ مُّؤْمِنَةٍ وَدِيَةٌ مُّسَلَّمَةٌ إِلَىٰ أَهْلِهِ إِلَّا أَن يَصَّدَّقُوا ۚ فَإِن كَانَ مِن قَوْمٍ عَدُوٍّ لَّكُمْ وَهُوَ مُؤْمِنٌ فَتَحْرِيرُ رَقَبَةٍ مُّؤْمِنَةٍ ۖ وَإِن كَانَ مِن قَوْمٍ بَيْنَكُمْ وَبَيْنَهُم مِّيثَاقٌ فَدِيَةٌ مُّسَلَّمَةٌ إِلَىٰ أَهْلِهِ وَتَحْرِيرُ رَقَبَةٍ مُّؤْمِنَةٍ ۖ فَمَن لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ تَوْبَةً مِّنَ اللَّهِ ۗ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا','هیچ فرد باایمانی مجاز نیست که مؤمنی را به قتل برساند، مگر اینکه این کار از روی خطا و اشتباه از او سر زند؛ (و در عین حال،) کسی که مؤمنی را از روی خطا به قتل رساند، باید یک برده مؤمن را آزاد کند و خونبهایی به کسان او بپردازد؛ مگر اینکه آنها خونبها را ببخشند. و اگر مقتول، از گروهی باشد که دشمنان شما هستند (و کافرند)، ولی مقتول باایمان بوده، (تنها) باید یک برده مؤمن را آزاد کند (و پرداختن خونبها لازم نیست). و اگر از جمعیّتی باشد که میان شما و آنها پیمانی برقرار است، باید خونبهای او را به کسان او بپردازد، و یک برده مؤمن (نیز) آزاد کند. و آن کس که دسترسی (به آزاد کردن برده) ندارد، دو ماه پی در پی روزه می‌گیرد. این، (یک نوع تخفیف، و) توبه الهی است. و خداوند، دانا و حکیم است.'];
ayat[586]=[4,93,'وَمَن يَقْتُلْ مُؤْمِنًا مُّتَعَمِّدًا فَجَزَاؤُهُ جَهَنَّمُ خَالِدًا فِيهَا وَغَضِبَ اللَّهُ عَلَيْهِ وَلَعَنَهُ وَأَعَدَّ لَهُ عَذَابًا عَظِيمًا','و هر کس، فرد باایمانی را از روی عمد به قتل برساند، مجازاتِ او دوزخ است؛ در حالی که جاودانه در آن می‌ماند؛ و خداوند بر او غضب می‌کند؛ و او را از رحمتش دور می‌سازد؛ و عذاب عظیمی برای او آماده ساخته است.'];
ayat[587]=[4,94,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا ضَرَبْتُمْ فِي سَبِيلِ اللَّهِ فَتَبَيَّنُوا وَلَا تَقُولُوا لِمَنْ أَلْقَىٰ إِلَيْكُمُ السَّلَامَ لَسْتَ مُؤْمِنًا تَبْتَغُونَ عَرَضَ الْحَيَاةِ الدُّنْيَا فَعِندَ اللَّهِ مَغَانِمُ كَثِيرَةٌ ۚ كَذَٰلِكَ كُنتُم مِّن قَبْلُ فَمَنَّ اللَّهُ عَلَيْكُمْ فَتَبَيَّنُوا ۚ إِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا','ای کسانی که ایمان آورده‌اید! هنگامی که در راه خدا گام می‌زنید (و به سفری برای جهاد می‌روید)، تحقیق کنید! و بخاطر اینکه سرمایه ناپایدار دنیا (و غنایمی) به دست آورید، به کسی که اظهار صلح و اسلام می‌کند نگویید: «مسلمان نیستی» زیرا غنیمتهای فراوانی (برای شما) نزد خداست. شما قبلاً چنین بودید؛ و خداوند بر شما منّت نهاد (و هدایت شدید). پس، (بشکرانه این نعمت بزرگ،) تحقیق کنید! خداوند به آنچه انجام می‌دهید آگاه است.'];
ayat[588]=[4,95,'لَّا يَسْتَوِي الْقَاعِدُونَ مِنَ الْمُؤْمِنِينَ غَيْرُ أُولِي الضَّرَرِ وَالْمُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنفُسِهِمْ ۚ فَضَّلَ اللَّهُ الْمُجَاهِدِينَ بِأَمْوَالِهِمْ وَأَنفُسِهِمْ عَلَى الْقَاعِدِينَ دَرَجَةً ۚ وَكُلًّا وَعَدَ اللَّهُ الْحُسْنَىٰ ۚ وَفَضَّلَ اللَّهُ الْمُجَاهِدِينَ عَلَى الْقَاعِدِينَ أَجْرًا عَظِيمًا','(هرگز) افراد باایمانی که بدون بیماری و ناراحتی، از جهاد بازنشستند، با مجاهدانی که در راه خدا با مال و جان خود جهاد کردند، یکسان نیستند! خداوند، مجاهدانی را که با مال و جان خود جهاد نمودند، بر قاعدان [= ترک‌کنندگان جهاد] برتری مهمّی بخشیده؛ و به هر یک از این دو گروه (به نسبت اعمال نیکشان،) خداوند وعده پاداش نیک داده، و مجاهدان را بر قاعدان، با پاداش عظیمی برتری بخشیده است.'];
ayat[589]=[4,96,'دَرَجَاتٍ مِّنْهُ وَمَغْفِرَةً وَرَحْمَةً ۚ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','درجات (مهمّی) از ناحیه خداوند، و آمرزش و رحمت (نصیب آنان می‌گردد)؛ و (اگر لغزشهایی داشته‌اند،) خداوند آمرزنده و مهربان است.'];
ayat[590]=[4,97,'إِنَّ الَّذِينَ تَوَفَّاهُمُ الْمَلَائِكَةُ ظَالِمِي أَنفُسِهِمْ قَالُوا فِيمَ كُنتُمْ ۖ قَالُوا كُنَّا مُسْتَضْعَفِينَ فِي الْأَرْضِ ۚ قَالُوا أَلَمْ تَكُنْ أَرْضُ اللَّهِ وَاسِعَةً فَتُهَاجِرُوا فِيهَا ۚ فَأُولَٰئِكَ مَأْوَاهُمْ جَهَنَّمُ ۖ وَسَاءَتْ مَصِيرًا','کسانی که فرشتگان (قبض ارواح)، روح آنها را گرفتند در حالی که به خویشتن ستم کرده بودند، به آنها گفتند: «شما در چه حالی بودید؟ (و چرا با اینکه مسلمان بودید، در صفِ کفّار جای داشتید؟!)» گفتند: «ما در سرزمین خود، تحت فشار و مستضعف بودیم.» آنها [= فرشتگان‌] گفتند: «مگر سرزمین خدا، پهناور نبود که مهاجرت کنید؟!» آنها (عذری نداشتند، و) جایگاهشان دوزخ است، و سرانجام بدی دارند.'];
ayat[591]=[4,98,'إِلَّا الْمُسْتَضْعَفِينَ مِنَ الرِّجَالِ وَالنِّسَاءِ وَالْوِلْدَانِ لَا يَسْتَطِيعُونَ حِيلَةً وَلَا يَهْتَدُونَ سَبِيلًا','مگر آن دسته از مردان و زنان و کودکانی که براستی تحت فشار قرار گرفته‌اند (و حقیقتاً مستضعفند)؛ نه چاره‌ای دارند، و نه راهی (برای نجات از آن محیط آلوده) می‌یابند.'];
ayat[592]=[4,99,'فَأُولَٰئِكَ عَسَى اللَّهُ أَن يَعْفُوَ عَنْهُمْ ۚ وَكَانَ اللَّهُ عَفُوًّا غَفُورًا','ممکن است خداوند، آنها را مورد عفو قرار دهد؛ و خداوند، عفو کننده و آمرزنده است.'];
ayat[593]=[4,100,'۞ وَمَن يُهَاجِرْ فِي سَبِيلِ اللَّهِ يَجِدْ فِي الْأَرْضِ مُرَاغَمًا كَثِيرًا وَسَعَةً ۚ وَمَن يَخْرُجْ مِن بَيْتِهِ مُهَاجِرًا إِلَى اللَّهِ وَرَسُولِهِ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ أَجْرُهُ عَلَى اللَّهِ ۗ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','کسی که در راه خدا هجرت کند، جاهای امنِ فراوان و گسترده‌ای در زمین می‌یابد. و هر کس بعنوان مهاجرت به سوی خدا و پیامبر او، از خانه خود بیرون رود، سپس مرگش فرا رسد، پاداش او بر خداست؛ و خداوند، آمرزنده و مهربان است.'];
ayat[594]=[4,101,'وَإِذَا ضَرَبْتُمْ فِي الْأَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَقْصُرُوا مِنَ الصَّلَاةِ إِنْ خِفْتُمْ أَن يَفْتِنَكُمُ الَّذِينَ كَفَرُوا ۚ إِنَّ الْكَافِرِينَ كَانُوا لَكُمْ عَدُوًّا مُّبِينًا','هنگامی که سفر می‌کنید، گناهی بر شما نیست که نماز را کوتاه کنید اگر از فتنه (و خطر) ِ کافران بترسید؛ زیرا کافران، برای شما دشمن آشکاری هستند.'];
ayat[595]=[4,102,'وَإِذَا كُنتَ فِيهِمْ فَأَقَمْتَ لَهُمُ الصَّلَاةَ فَلْتَقُمْ طَائِفَةٌ مِّنْهُم مَّعَكَ وَلْيَأْخُذُوا أَسْلِحَتَهُمْ فَإِذَا سَجَدُوا فَلْيَكُونُوا مِن وَرَائِكُمْ وَلْتَأْتِ طَائِفَةٌ أُخْرَىٰ لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَلْيَأْخُذُوا حِذْرَهُمْ وَأَسْلِحَتَهُمْ ۗ وَدَّ الَّذِينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وَأَمْتِعَتِكُمْ فَيَمِيلُونَ عَلَيْكُم مَّيْلَةً وَاحِدَةً ۚ وَلَا جُنَاحَ عَلَيْكُمْ إِن كَانَ بِكُمْ أَذًى مِّن مَّطَرٍ أَوْ كُنتُم مَّرْضَىٰ أَن تَضَعُوا أَسْلِحَتَكُمْ ۖ وَخُذُوا حِذْرَكُمْ ۗ إِنَّ اللَّهَ أَعَدَّ لِلْكَافِرِينَ عَذَابًا مُّهِينًا','و هنگامی که در میان آنها باشی، و (در میدان جنگ) برای آنها نماز را برپا کنی، باید دسته‌ای از آنها با تو (به نماز) برخیزند، و سلاحهایشان را با خود برگیرند؛ و هنگامی که سجده کردند (و نماز را به پایان رساندند)، باید به پشتِ سرِ شما (به میدان نبرد) بروند، و آن دسته دیگر که نماز نخوانده‌اند (و مشغول پیکار بوده‌اند)، بیایند و با تو نماز بخوانند؛ آنها باید وسایل دفاعی و سلاحهایشان (را در حال نماز) با خود حمل کنند؛ (زیرا) کافران آرزو دارند که شما از سلاحها و متاعهای خود غافل شوید و یکباره به شما هجوم آورند. و اگر از باران ناراحتید، و یا بیمار (و مجروح) هستید، مانعی ندارد که سلاحهای خود را بر زمین بگذارید؛ ولی وسایل دفاعی (مانند زره و خود را) با خود بردارید خداوند، عذاب خوارکننده‌ای برای کافران فراهم ساخته است.'];
ayat[596]=[4,103,'فَإِذَا قَضَيْتُمُ الصَّلَاةَ فَاذْكُرُوا اللَّهَ قِيَامًا وَقُعُودًا وَعَلَىٰ جُنُوبِكُمْ ۚ فَإِذَا اطْمَأْنَنتُمْ فَأَقِيمُوا الصَّلَاةَ ۚ إِنَّ الصَّلَاةَ كَانَتْ عَلَى الْمُؤْمِنِينَ كِتَابًا مَّوْقُوتًا','و هنگامی که نماز را به پایان رساندید، خدا را یاد کنید؛ ایستاده، و نشسته، و در حالی که به پهلو خوابیده‌اید! و هرگاه آرامش یافتید (و حالت ترس زایل گشت)، نماز را (به طور معمول) انجام دهید، زیرا نماز، وظیفه ثابت و معیّنی برای مؤمنان است!'];
ayat[597]=[4,104,'وَلَا تَهِنُوا فِي ابْتِغَاءِ الْقَوْمِ ۖ إِن تَكُونُوا تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَ ۖ وَتَرْجُونَ مِنَ اللَّهِ مَا لَا يَرْجُونَ ۗ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا','و در راه تعقیب دشمن، (هرگز) سست نشوید! (زیرا) اگر شما درد و رنج می‌بینید، آنها نیز همانند شما درد و رنج می‌بینند؛ ولی شما امیدی از خدا دارید که آنها ندارند؛ و خداوند، دانا و حکیم است.'];
ayat[598]=[4,105,'إِنَّا أَنزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَا أَرَاكَ اللَّهُ ۚ وَلَا تَكُن لِّلْخَائِنِينَ خَصِيمًا','ما این کتاب را بحق بر تو نازل کردیم؛ تا به آنچه خداوند به تو آموخته، در میان مردم قضاوت کنی؛ و از کسانی مباش که از خائنان حمایت نمایی!'];
ayat[599]=[4,106,'وَاسْتَغْفِرِ اللَّهَ ۖ إِنَّ اللَّهَ كَانَ غَفُورًا رَّحِيمًا','و از خداوند، طلب آمرزش نما! که خداوند، آمرزنده و مهربان است.'];
ayat[600]=[4,107,'وَلَا تُجَادِلْ عَنِ الَّذِينَ يَخْتَانُونَ أَنفُسَهُمْ ۚ إِنَّ اللَّهَ لَا يُحِبُّ مَن كَانَ خَوَّانًا أَثِيمًا','و از آنها که به خود خیانت کردند، دفاع مکن! زیرا خداوند، افراد خیانت‌پیشه گنهکار را دوست ندارد.'];
ayat[601]=[4,108,'يَسْتَخْفُونَ مِنَ النَّاسِ وَلَا يَسْتَخْفُونَ مِنَ اللَّهِ وَهُوَ مَعَهُمْ إِذْ يُبَيِّتُونَ مَا لَا يَرْضَىٰ مِنَ الْقَوْلِ ۚ وَكَانَ اللَّهُ بِمَا يَعْمَلُونَ مُحِيطًا','آنها زشتکاریِ خود را از مردم پنهان می‌دارند؛ اما از خدا پنهان نمی‌دارند، و هنگامی که در مجالس شبانه، سخنانی که خدا راضی نبود می‌گفتند، خدا با آنها بود، خدا به آنچه انجام می‌دهند، احاطه دارد.'];
ayat[602]=[4,109,'هَا أَنتُمْ هَٰؤُلَاءِ جَادَلْتُمْ عَنْهُمْ فِي الْحَيَاةِ الدُّنْيَا فَمَن يُجَادِلُ اللَّهَ عَنْهُمْ يَوْمَ الْقِيَامَةِ أَم مَّن يَكُونُ عَلَيْهِمْ وَكِيلًا','آری، شما همانها هستید که در زندگی این جهان، از آنان دفاع کردید! اما کیست که در برابر خداوند، در روز رستاخیز از آنها دفاع کند؟! یا چه کسی است که وکیل و حامی آنها باشد؟!'];
ayat[603]=[4,110,'وَمَن يَعْمَلْ سُوءًا أَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللَّهَ يَجِدِ اللَّهَ غَفُورًا رَّحِيمًا','کسی که کار بدی انجام دهد یا به خود ستم کند، سپس از خداوند طلب آمرزش نماید، خدا را آمرزنده و مهربان خواهد یافت.'];
ayat[604]=[4,111,'وَمَن يَكْسِبْ إِثْمًا فَإِنَّمَا يَكْسِبُهُ عَلَىٰ نَفْسِهِ ۚ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا','و کسی که گناهی مرتکب شود، به زیان خود مرتکب شده؛ خداوند، دانا و حکیم است.'];
ayat[605]=[4,112,'وَمَن يَكْسِبْ خَطِيئَةً أَوْ إِثْمًا ثُمَّ يَرْمِ بِهِ بَرِيئًا فَقَدِ احْتَمَلَ بُهْتَانًا وَإِثْمًا مُّبِينًا','و کسی که خطا یا گناهی مرتکب شود، سپس بیگناهی را متهم سازد، بار بهتان و گناهِ آشکاری بر دوش گرفته است.'];
ayat[606]=[4,113,'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكَ وَرَحْمَتُهُ لَهَمَّت طَّائِفَةٌ مِّنْهُمْ أَن يُضِلُّوكَ وَمَا يُضِلُّونَ إِلَّا أَنفُسَهُمْ ۖ وَمَا يَضُرُّونَكَ مِن شَيْءٍ ۚ وَأَنزَلَ اللَّهُ عَلَيْكَ الْكِتَابَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُن تَعْلَمُ ۚ وَكَانَ فَضْلُ اللَّهِ عَلَيْكَ عَظِيمًا','اگر فضل و رحمت خدا شامل حال تو نبود، گروهی از آنان تصمیم داشتند تو را گمراه کنند؛ اما جز خودشان را گمراه نمی‌کنند؛ و هیچ‌گونه زیانی به تو نمی‌رسانند. و خداوند، کتاب و حکمت بر تو نازل کرد؛ و آنچه را نمی‌دانستی، به تو آموخت؛ و فضل خدا بر تو (همواره) بزرگ بوده است.'];
ayat[607]=[4,114,'۞ لَّا خَيْرَ فِي كَثِيرٍ مِّن نَّجْوَاهُمْ إِلَّا مَنْ أَمَرَ بِصَدَقَةٍ أَوْ مَعْرُوفٍ أَوْ إِصْلَاحٍ بَيْنَ النَّاسِ ۚ وَمَن يَفْعَلْ ذَٰلِكَ ابْتِغَاءَ مَرْضَاتِ اللَّهِ فَسَوْفَ نُؤْتِيهِ أَجْرًا عَظِيمًا','در بسیاری از سخنانِ درِگوشی (و جلسات محرمانه) آنها، خیر و سودی نیست؛ مگر کسی که (به این وسیله،) امر به کمک به دیگران، یا کار نیک، یا اصلاح در میان مردم کند؛ و هر کس برای خشنودی پروردگار چنین کند، پاداش بزرگی به او خواهیم داد.'];
ayat[608]=[4,115,'وَمَن يُشَاقِقِ الرَّسُولَ مِن بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدَىٰ وَيَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ نُوَلِّهِ مَا تَوَلَّىٰ وَنُصْلِهِ جَهَنَّمَ ۖ وَسَاءَتْ مَصِيرًا','کسی که بعد از آشکار شدن حق، با پیامبر مخالفت کند، و از راهی جز راه مؤمنان پیروی نماید، ما او را به همان راه که می‌رود می‌بریم؛ و به دوزخ داخل می‌کنیم؛ و جایگاه بدی دارد.'];
ayat[609]=[4,116,'إِنَّ اللَّهَ لَا يَغْفِرُ أَن يُشْرَكَ بِهِ وَيَغْفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَاءُ ۚ وَمَن يُشْرِكْ بِاللَّهِ فَقَدْ ضَلَّ ضَلَالًا بَعِيدًا','خداوند، شرک به او را نمی‌آمرزد؛ (ولی) کمتر از آن را برای هر کس بخواهد (و شایسته بداند) می‌آمرزد. و هر کس برای خدا همتایی قرار دهد، در گمراهی دوری افتاده است.'];
ayat[610]=[4,117,'إِن يَدْعُونَ مِن دُونِهِ إِلَّا إِنَاثًا وَإِن يَدْعُونَ إِلَّا شَيْطَانًا مَّرِيدًا','آنچه غیر از خدا می‌خوانند، فقط بتهایی است (بی‌روح)، که هیچ اثری ندارد؛ و (یا) شیطان سرکش و ویرانگر است.'];
ayat[611]=[4,118,'لَّعَنَهُ اللَّهُ ۘ وَقَالَ لَأَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيبًا مَّفْرُوضًا','خدا او را از رحمت خویش دور ساخته؛ و او گفته است: «از بندگان تو، سهم معیّنی خواهم گرفت!'];
ayat[612]=[4,119,'وَلَأُضِلَّنَّهُمْ وَلَأُمَنِّيَنَّهُمْ وَلَآمُرَنَّهُمْ فَلَيُبَتِّكُنَّ آذَانَ الْأَنْعَامِ وَلَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللَّهِ ۚ وَمَن يَتَّخِذِ الشَّيْطَانَ وَلِيًّا مِّن دُونِ اللَّهِ فَقَدْ خَسِرَ خُسْرَانًا مُّبِينًا','و آنها را گمراه می‌کنم! و به آرزوها سرگرم می‌سازم! و به آنان دستور می‌دهم که (اعمال خرافی انجام دهند، و) گوش چهارپایان را بشکافند، و آفرینشِ پاک خدایی را تغییر دهند! (و فطرت توحید را به شرک بیالایند!)» و هر کس، شیطان را به جای خدا ولّیِ خود برگزیند، زیانِ آشکاری کرده است.'];
ayat[613]=[4,120,'يَعِدُهُمْ وَيُمَنِّيهِمْ ۖ وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلَّا غُرُورًا','شیطان به آنها وعده‌ها (ی دروغین) می‌دهد؛ و به آرزوها، سرگرم می‌سازد؛ در حالی که جز فریب و نیرنگ، به آنها وعده نمی‌دهد.'];
ayat[614]=[4,121,'أُولَٰئِكَ مَأْوَاهُمْ جَهَنَّمُ وَلَا يَجِدُونَ عَنْهَا مَحِيصًا','آنها [= پیروان شیطان‌] جایگاهشان جهنم است؛ و هیچ راه فراری ندارند.'];
ayat[615]=[4,122,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَنُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ وَعْدَ اللَّهِ حَقًّا ۚ وَمَنْ أَصْدَقُ مِنَ اللَّهِ قِيلًا','و کسانی که ایمان آورده‌اند و اعمال صالح انجام داده‌اند، بزودی آن را در باغهایی از بهشت وارد می‌کنیم که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند ماند. وعده حق خداوند است و کیست که در گفتار و وعده‌هایش، از خدا صادقتر باشد؟!'];
ayat[616]=[4,123,'لَّيْسَ بِأَمَانِيِّكُمْ وَلَا أَمَانِيِّ أَهْلِ الْكِتَابِ ۗ مَن يَعْمَلْ سُوءًا يُجْزَ بِهِ وَلَا يَجِدْ لَهُ مِن دُونِ اللَّهِ وَلِيًّا وَلَا نَصِيرًا','(فضیلت و برتری) به آرزوهای شما و آرزوهای اهل کتاب نیست؛ هر کس عمل بدی انجام دهد، کیفر داده می‌شود؛ و کسی را جز خدا، ولّی و یاور خود نخواهد یافت.'];
ayat[617]=[4,124,'وَمَن يَعْمَلْ مِنَ الصَّالِحَاتِ مِن ذَكَرٍ أَوْ أُنثَىٰ وَهُوَ مُؤْمِنٌ فَأُولَٰئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ نَقِيرًا','و کسی که چیزی از اعمال صالح را انجام دهد، خواه مرد باشد یا زن، در حالی که ایمان داشته باشد، چنان کسانی داخل بهشت می‌شوند؛ و کمترین ستمی به آنها نخواهد شد.'];
ayat[618]=[4,125,'وَمَنْ أَحْسَنُ دِينًا مِّمَّنْ أَسْلَمَ وَجْهَهُ لِلَّهِ وَهُوَ مُحْسِنٌ وَاتَّبَعَ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۗ وَاتَّخَذَ اللَّهُ إِبْرَاهِيمَ خَلِيلًا','دین و آیین چه کسی بهتر است از آن کس که خود را تسلیم خدا کند، و نیکوکار باشد، و پیرو آیین خالص و پاکِ ابراهیم گردد؟ و خدا ابراهیم را به دوستیِ خود، انتخاب کرد.'];
ayat[619]=[4,126,'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ مُّحِيطًا','آنچه در آسمانها و زمین است، از آن خداست؛ و خداوند به هر چیزی احاطه دارد.'];
ayat[620]=[4,127,'وَيَسْتَفْتُونَكَ فِي النِّسَاءِ ۖ قُلِ اللَّهُ يُفْتِيكُمْ فِيهِنَّ وَمَا يُتْلَىٰ عَلَيْكُمْ فِي الْكِتَابِ فِي يَتَامَى النِّسَاءِ اللَّاتِي لَا تُؤْتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُونَ أَن تَنكِحُوهُنَّ وَالْمُسْتَضْعَفِينَ مِنَ الْوِلْدَانِ وَأَن تَقُومُوا لِلْيَتَامَىٰ بِالْقِسْطِ ۚ وَمَا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللَّهَ كَانَ بِهِ عَلِيمًا','از تو درباره حکم زنان سؤال می‌کنند؛ بگو: «خداوند درباره آنان به شما پاسخ می دهد: آنچه در قرآن درباره زنان یتیمی که حقوقشان را به آنها نمی‌دهید، و می‌خواهید با آنها ازدواج کنید، و نیز آنچه درباره کودکان صغیر و ناتوان برای شما بیان شده است، (قسمتی از سفارشهای خداوند در این زمینه می‌باشد؛ و نیز به شما سفارش می‌کند که) با یتیمان به عدالت رفتار کنید! و آنچه از نیکیها انجام می‌دهید؛ خداوند از آن آگاه است (و به شما پاداش شایسته می‌دهد).'];
ayat[621]=[4,128,'وَإِنِ امْرَأَةٌ خَافَتْ مِن بَعْلِهَا نُشُوزًا أَوْ إِعْرَاضًا فَلَا جُنَاحَ عَلَيْهِمَا أَن يُصْلِحَا بَيْنَهُمَا صُلْحًا ۚ وَالصُّلْحُ خَيْرٌ ۗ وَأُحْضِرَتِ الْأَنفُسُ الشُّحَّ ۚ وَإِن تُحْسِنُوا وَتَتَّقُوا فَإِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا','و اگر زنی، از طغیان و سرکشی یا اعراضِ شوهرش، بیم داشته باشد، مانعی ندارد با هم صلح کنند (و زن یا مرد، از پاره‌ای از حقوق خود، بخاطر صلح، صرف نظر نماید.) و صلح، بهتر است؛ اگر چه مردم (طبق غریزه حبّ ذات، در این گونه موارد) بخل می‌ورزند. و اگر نیکی کنید و پرهیزگاری پیشه سازید (و بخاطر صلح، گذشت نمایید)، خداوند به آنچه انجام می‌دهید، آگاه است (و پاداش شایسته به شما خواهد داد).'];
ayat[622]=[4,129,'وَلَن تَسْتَطِيعُوا أَن تَعْدِلُوا بَيْنَ النِّسَاءِ وَلَوْ حَرَصْتُمْ ۖ فَلَا تَمِيلُوا كُلَّ الْمَيْلِ فَتَذَرُوهَا كَالْمُعَلَّقَةِ ۚ وَإِن تُصْلِحُوا وَتَتَّقُوا فَإِنَّ اللَّهَ كَانَ غَفُورًا رَّحِيمًا','شما هرگز نمی‌توانید (از نظر محّبت قلبی) در میان زنان، عدالت برقرار کنید، هر چند کوشش نمایید! ولی تمایل خود را بکلّی متوجّه یک طرف نسازید که دیگری را بصورت زنی که شوهرش را از دست داده درآورید! و اگر راه صلاح و پرهیزگاری پیش گیرید، خداوند آمرزنده و مهربان است.'];
ayat[623]=[4,130,'وَإِن يَتَفَرَّقَا يُغْنِ اللَّهُ كُلًّا مِّن سَعَتِهِ ۚ وَكَانَ اللَّهُ وَاسِعًا حَكِيمًا','(اما) اگر (راهی برای اصلاح در میان خود نیابند، و) از هم جدا شوند، خداوند هر کدام از آنها را با فضل و کرم خود، بی‌نیاز می‌کند؛ و خداوند، دارای فضل و کرم، و حکیم است.'];
ayat[624]=[4,131,'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَلَقَدْ وَصَّيْنَا الَّذِينَ أُوتُوا الْكِتَابَ مِن قَبْلِكُمْ وَإِيَّاكُمْ أَنِ اتَّقُوا اللَّهَ ۚ وَإِن تَكْفُرُوا فَإِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَكَانَ اللَّهُ غَنِيًّا حَمِيدًا','آنچه در آسمانها و آنچه در زمین است، از آن خداست. و ما به کسانی که پیش از شما، کتاب آسمانی به آنها داده شده بود، سفارش کردیم، (همچنین) به شما (نیز) سفارش می‌کنیم که از (نافرمانی) خدا بپرهیزید! و اگر کافر شوید، (به خدا زیانی نمی‌رسد؛ زیرا) برای خداست آنچه در آسمانها و آنچه در زمین است، و خداوند، بی‌نیاز و ستوده است.'];
ayat[625]=[4,132,'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا','برای خداست آنچه در آسمانها و زمین است؛ و کافی است که خدا، حافظ و نگاهبان آنها باشد.'];
ayat[626]=[4,133,'إِن يَشَأْ يُذْهِبْكُمْ أَيُّهَا النَّاسُ وَيَأْتِ بِآخَرِينَ ۚ وَكَانَ اللَّهُ عَلَىٰ ذَٰلِكَ قَدِيرًا','ای مردم! اگر او بخواهد، شما را از میان می‌برد و افراد دیگری را (به جای شما) می آورد، و خداوند، بر این کار تواناست.'];
ayat[627]=[4,134,'مَّن كَانَ يُرِيدُ ثَوَابَ الدُّنْيَا فَعِندَ اللَّهِ ثَوَابُ الدُّنْيَا وَالْآخِرَةِ ۚ وَكَانَ اللَّهُ سَمِيعًا بَصِيرًا','کسانی که پاداش دنیوی بخواهند، (و در قید نتایج معنوی و اخروی نباشند، در اشتباهند؛ زیرا) پاداش دنیا و آخرت نزد خداست؛ و خداوند، شنوا و بیناست.'];
ayat[628]=[4,135,'۞ يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ بِالْقِسْطِ شُهَدَاءَ لِلَّهِ وَلَوْ عَلَىٰ أَنفُسِكُمْ أَوِ الْوَالِدَيْنِ وَالْأَقْرَبِينَ ۚ إِن يَكُنْ غَنِيًّا أَوْ فَقِيرًا فَاللَّهُ أَوْلَىٰ بِهِمَا ۖ فَلَا تَتَّبِعُوا الْهَوَىٰ أَن تَعْدِلُوا ۚ وَإِن تَلْوُوا أَوْ تُعْرِضُوا فَإِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا','ای کسانی که ایمان آورده‌اید! کاملاً قیام به عدالت کنید! برای خدا شهادت دهید، اگر چه (این گواهی) به زیان خود شما، یا پدر و مادر و نزدیکان شما بوده باشد! (چرا که) اگر آنها غنیّ یا فقیر باشند، خداوند سزاوارتر است که از آنان حمایت کند. بنابراین، از هوی و هوس پیروی نکنید؛ که از حق، منحرف خواهید شد! و اگر حق را تحریف کنید، و یا از اظهار آن، اعراض نمایید، خداوند به آنچه انجام می‌دهید، آگاه است.'];
ayat[629]=[4,136,'يَا أَيُّهَا الَّذِينَ آمَنُوا آمِنُوا بِاللَّهِ وَرَسُولِهِ وَالْكِتَابِ الَّذِي نَزَّلَ عَلَىٰ رَسُولِهِ وَالْكِتَابِ الَّذِي أَنزَلَ مِن قَبْلُ ۚ وَمَن يَكْفُرْ بِاللَّهِ وَمَلَائِكَتِهِ وَكُتُبِهِ وَرُسُلِهِ وَالْيَوْمِ الْآخِرِ فَقَدْ ضَلَّ ضَلَالًا بَعِيدًا','ای کسانی که ایمان آورده‌اید! به خدا و پیامبرش، و کتابی که بر او نازل کرده، و کتب (آسمانی) که پیش از این فرستاده است، ایمان (واقعی) بیاورید کسی که خدا و فرشتگان او و کتابها و پیامبرانش و روز واپسین را انکار کند، در گمراهی دور و درازی افتاده است.'];
ayat[630]=[4,137,'إِنَّ الَّذِينَ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ ازْدَادُوا كُفْرًا لَّمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيلًا','کسانی که ایمان آوردند، سپس کافر شدند، باز هم ایمان آوردند، و دیگربار کافر شدند، سپس بر کفر خود افزودند، خدا هرگز آنها را نخواهد بخشید، و آنها را به راه (راست) هدایت نخواهد کرد.'];
ayat[631]=[4,138,'بَشِّرِ الْمُنَافِقِينَ بِأَنَّ لَهُمْ عَذَابًا أَلِيمًا','به منافقان بشارت ده که مجازات دردناکی در انتظار آنهاست!'];
ayat[632]=[4,139,'الَّذِينَ يَتَّخِذُونَ الْكَافِرِينَ أَوْلِيَاءَ مِن دُونِ الْمُؤْمِنِينَ ۚ أَيَبْتَغُونَ عِندَهُمُ الْعِزَّةَ فَإِنَّ الْعِزَّةَ لِلَّهِ جَمِيعًا','همانها که کافران را به جای مؤمنان، دوست خود انتخاب می‌کنند. آیا عزّت و آبرو نزد آنان می‌جویند؟ با اینکه همه عزّتها از آن خداست؟!'];
ayat[633]=[4,140,'وَقَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتَابِ أَنْ إِذَا سَمِعْتُمْ آيَاتِ اللَّهِ يُكْفَرُ بِهَا وَيُسْتَهْزَأُ بِهَا فَلَا تَقْعُدُوا مَعَهُمْ حَتَّىٰ يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ ۚ إِنَّكُمْ إِذًا مِّثْلُهُمْ ۗ إِنَّ اللَّهَ جَامِعُ الْمُنَافِقِينَ وَالْكَافِرِينَ فِي جَهَنَّمَ جَمِيعًا','و خداوند (این حکم را) در قرآن بر شما نازل کرده که هرگاه بشنوید افرادی آیات خدا را انکار و استهزا می‌کنند، با آنها ننشینید تا به سخن دیگری بپردازند! وگرنه، شما هم مثل آنان خواهید بود. خداوند، منافقان و کافران را همگی در دوزخ جمع می‌کند.'];
ayat[634]=[4,141,'الَّذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِن كَانَ لَكُمْ فَتْحٌ مِّنَ اللَّهِ قَالُوا أَلَمْ نَكُن مَّعَكُمْ وَإِن كَانَ لِلْكَافِرِينَ نَصِيبٌ قَالُوا أَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُم مِّنَ الْمُؤْمِنِينَ ۚ فَاللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ ۗ وَلَن يَجْعَلَ اللَّهُ لِلْكَافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلًا','منافقان همانها هستند که پیوسته انتظار می‌کشند و مراقب شما هستند؛ اگر فتح و پیروزی نصیب شما گردد، می‌گویند: مگر ما با شما نبودیم؟ (پس ما نیز در افتخارات و غنایم شریکیم!) «و اگر بهره‌ای نصیب کافران گردد، به آنان می‌گویند: مگر ما شما را به مبارزه و عدم تسلیم در برابر مؤمنان، تشویق نمی‌کردیم؟ (پس با شما شریک خواهیم بود!)» خداوند در روز رستاخیز، میان شما داوری می‌کند؛ و خداوند هرگز کافران را بر مؤمنان تسلّطی نداده است.'];
ayat[635]=[4,142,'إِنَّ الْمُنَافِقِينَ يُخَادِعُونَ اللَّهَ وَهُوَ خَادِعُهُمْ وَإِذَا قَامُوا إِلَى الصَّلَاةِ قَامُوا كُسَالَىٰ يُرَاءُونَ النَّاسَ وَلَا يَذْكُرُونَ اللَّهَ إِلَّا قَلِيلًا','منافقان می‌خواهند خدا را فریب دهند؛ در حالی که او آنها را فریب می‌دهد؛ و هنگامی که به نماز برمی‌خیزند، با کسالت برمی‌خیزند؛ و در برابر مردم ریا می‌کنند؛ و خدا را جز اندکی یاد نمی‌نمایند!'];
ayat[636]=[4,143,'مُّذَبْذَبِينَ بَيْنَ ذَٰلِكَ لَا إِلَىٰ هَٰؤُلَاءِ وَلَا إِلَىٰ هَٰؤُلَاءِ ۚ وَمَن يُضْلِلِ اللَّهُ فَلَن تَجِدَ لَهُ سَبِيلًا','آنها افراد بی‌هدفی هستند که نه سوی اینها، و نه سوی آنهایند! (نه در صف مؤمنان قرار دارند، و نه در صف کافران!) و هر کس را خداوند گمراه کند، راهی برای او نخواهی یافت.'];
ayat[637]=[4,144,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا الْكَافِرِينَ أَوْلِيَاءَ مِن دُونِ الْمُؤْمِنِينَ ۚ أَتُرِيدُونَ أَن تَجْعَلُوا لِلَّهِ عَلَيْكُمْ سُلْطَانًا مُّبِينًا','ای کسانی که ایمان آورده‌اید! غیر از مؤمنان، کافران را ولّی و تکیه‌گاه خود قرار ندهید! آیا می‌خواهید (با این عمل،) دلیل آشکاری بر ضدّ خود در پیشگاه خدا قرار دهید؟!'];
ayat[638]=[4,145,'إِنَّ الْمُنَافِقِينَ فِي الدَّرْكِ الْأَسْفَلِ مِنَ النَّارِ وَلَن تَجِدَ لَهُمْ نَصِيرًا','منافقان در پایین‌ترین درکات دوزخ قرار دارند؛ و هرگز یاوری برای آنها نخواهی یافت! (بنابر این، از طرح دوستی با دشمنان خدا، که نشانه نفاق است، بپرهیزید!)'];
ayat[639]=[4,146,'إِلَّا الَّذِينَ تَابُوا وَأَصْلَحُوا وَاعْتَصَمُوا بِاللَّهِ وَأَخْلَصُوا دِينَهُمْ لِلَّهِ فَأُولَٰئِكَ مَعَ الْمُؤْمِنِينَ ۖ وَسَوْفَ يُؤْتِ اللَّهُ الْمُؤْمِنِينَ أَجْرًا عَظِيمًا','مگر آنها که توبه کنند، و جبران و اصلاح نمایند، به (دامن لطف) خدا، چنگ زنند، و دین خود را برای خدا خالص کنند؛ آنها با مؤمنان خواهند بود؛ و خداوند به افراد باایمان، پاداش عظیمی خواهد داد.'];
ayat[640]=[4,147,'مَّا يَفْعَلُ اللَّهُ بِعَذَابِكُمْ إِن شَكَرْتُمْ وَآمَنتُمْ ۚ وَكَانَ اللَّهُ شَاكِرًا عَلِيمًا','خدا چه نیازی به مجازات شما دارد اگر شکرگزاری کنید و ایمان آورید؟ خدا شکرگزار و آگاه است. (اعمال و نیّات شما را می‌داند، و به اعمال نیک، پاداش نیک می دهد.)'];
ayat[641]=[4,148,'۞ لَّا يُحِبُّ اللَّهُ الْجَهْرَ بِالسُّوءِ مِنَ الْقَوْلِ إِلَّا مَن ظُلِمَ ۚ وَكَانَ اللَّهُ سَمِيعًا عَلِيمًا','خداوند دوست ندارد کسی با سخنان خود، بدیها (ی دیگران) را اظهار کند؛ مگر آن کس که مورد ستم واقع شده باشد. خداوند، شنوا و داناست.'];
ayat[642]=[4,149,'إِن تُبْدُوا خَيْرًا أَوْ تُخْفُوهُ أَوْ تَعْفُوا عَن سُوءٍ فَإِنَّ اللَّهَ كَانَ عَفُوًّا قَدِيرًا','اگر نیکیها را آشکار یا مخفی سازید، و از بدیها گذشت نمایید، خداوند بخشنده و تواناست (و با اینکه قادر بر انتقام است، عفو و گذشت می‌کند).'];
ayat[643]=[4,150,'إِنَّ الَّذِينَ يَكْفُرُونَ بِاللَّهِ وَرُسُلِهِ وَيُرِيدُونَ أَن يُفَرِّقُوا بَيْنَ اللَّهِ وَرُسُلِهِ وَيَقُولُونَ نُؤْمِنُ بِبَعْضٍ وَنَكْفُرُ بِبَعْضٍ وَيُرِيدُونَ أَن يَتَّخِذُوا بَيْنَ ذَٰلِكَ سَبِيلًا','کسانی که خدا و پیامبرانِ او را انکار می‌کنند، و می‌خواهند میان خدا و پیامبرانش تبعیض قائل شوند، و می‌گویند: «به بعضی ایمان می‌آوریم، و بعضی را انکار می کنیم» و می‌خواهند در میان این دو، راهی برای خود انتخاب کنند...'];
ayat[644]=[4,151,'أُولَٰئِكَ هُمُ الْكَافِرُونَ حَقًّا ۚ وَأَعْتَدْنَا لِلْكَافِرِينَ عَذَابًا مُّهِينًا','آنها کافران حقیقی‌اند؛ و برای کافران، مجازات خوارکننده‌ای فراهم ساخته‌ایم.'];
ayat[645]=[4,152,'وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ وَلَمْ يُفَرِّقُوا بَيْنَ أَحَدٍ مِّنْهُمْ أُولَٰئِكَ سَوْفَ يُؤْتِيهِمْ أُجُورَهُمْ ۗ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','(ولی) کسانی که به خدا و رسولان او ایمان آورده، و میان احدی از آنها فرق نمی‌گذارند، پاداششان را خواهد داد؛ خداوند، آمرزنده و مهربان است.'];
ayat[646]=[4,153,'يَسْأَلُكَ أَهْلُ الْكِتَابِ أَن تُنَزِّلَ عَلَيْهِمْ كِتَابًا مِّنَ السَّمَاءِ ۚ فَقَدْ سَأَلُوا مُوسَىٰ أَكْبَرَ مِن ذَٰلِكَ فَقَالُوا أَرِنَا اللَّهَ جَهْرَةً فَأَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْ ۚ ثُمَّ اتَّخَذُوا الْعِجْلَ مِن بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَاتُ فَعَفَوْنَا عَن ذَٰلِكَ ۚ وَآتَيْنَا مُوسَىٰ سُلْطَانًا مُّبِينًا','اهل کتاب از تو می‌خواهند کتابی از آسمان (یکجا) بر آنها نازل کنی؛ (در حالی که این یک بهانه است؛) آنها از موسی، بزرگتر از این را خواستند، و گفتند: «خدا را آشکارا به ما نشان ده!» و بخاطر این ظلم و ستم، صاعقه آنها را فرا گرفت. سپس گوساله (سامری) را، پس از آن همه دلایل روشن که برای آنها آمد، (به خدایی) انتخاب کردند، ولی ما از آن درگذشتیم (و عفو کردیم) و به موسی، برهان آشکاری دادیم.'];
ayat[647]=[4,154,'وَرَفَعْنَا فَوْقَهُمُ الطُّورَ بِمِيثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّدًا وَقُلْنَا لَهُمْ لَا تَعْدُوا فِي السَّبْتِ وَأَخَذْنَا مِنْهُم مِّيثَاقًا غَلِيظًا','و کوه طور را بر فراز آنها برافراشتیم؛ و در همان حال از آنها پیمان گرفتیم، و به آنها گفتیم: «(برای توبه،) از در (بیت المقدس) با خضوع درآیید!» و (نیز) گفتیم: «روز شنبه تعدّی نکنید (و دست از کار بکشید!)» و از آنان (در برابر همه اینها،) پیمان محکمی گرفتیم.'];
ayat[648]=[4,155,'فَبِمَا نَقْضِهِم مِّيثَاقَهُمْ وَكُفْرِهِم بِآيَاتِ اللَّهِ وَقَتْلِهِمُ الْأَنبِيَاءَ بِغَيْرِ حَقٍّ وَقَوْلِهِمْ قُلُوبُنَا غُلْفٌ ۚ بَلْ طَبَعَ اللَّهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ إِلَّا قَلِيلًا','(ولی) بخاطر پیمان‌شکنی آنها، و انکار آیات خدا، و کشتن پیامبران بناحق، و بخاطر اینکه (از روی استهزا) می‌گفتند: «بر دلهای ما، پرده افکنده (شده و سخنان پیامبر را درک نمی‌کنیم!» رانده درگاه خدا شدند.) آری، خداوند بعلّت کفرشان، بر دلهای آنها مهر زده؛ که جز عده کمی (که راه حق می‌پویند و لجاج ندارند،) ایمان نمی‌آورند.'];
ayat[649]=[4,156,'وَبِكُفْرِهِمْ وَقَوْلِهِمْ عَلَىٰ مَرْيَمَ بُهْتَانًا عَظِيمًا','و (نیز) بخاطر کفرشان، و تهمت بزرگی که بر مریم زدند.'];
ayat[650]=[4,157,'وَقَوْلِهِمْ إِنَّا قَتَلْنَا الْمَسِيحَ عِيسَى ابْنَ مَرْيَمَ رَسُولَ اللَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمْ ۚ وَإِنَّ الَّذِينَ اخْتَلَفُوا فِيهِ لَفِي شَكٍّ مِّنْهُ ۚ مَا لَهُم بِهِ مِنْ عِلْمٍ إِلَّا اتِّبَاعَ الظَّنِّ ۚ وَمَا قَتَلُوهُ يَقِينًا','و گفتارشان که: «ما، مسیح عیسی بن مریم، پیامبر خدا را کشتیم!» در حالی که نه او را کشتند، و نه بر دار آویختند؛ لکن امر بر آنها مشتبه شد. و کسانی که در مورد (قتل) او اختلاف کردند، از آن در شک هستند و علم به آن ندارند و تنها از گمان پیروی می‌کنند؛ و قطعاً او را نکشتند!'];
ayat[651]=[4,158,'بَل رَّفَعَهُ اللَّهُ إِلَيْهِ ۚ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا','بلکه خدا او را به سوی خود، بالا برد. و خداوند، توانا و حکیم است.'];
ayat[652]=[4,159,'وَإِن مِّنْ أَهْلِ الْكِتَابِ إِلَّا لَيُؤْمِنَنَّ بِهِ قَبْلَ مَوْتِهِ ۖ وَيَوْمَ الْقِيَامَةِ يَكُونُ عَلَيْهِمْ شَهِيدًا','و هیچ یک از اهل کتاب نیست مگر اینکه پیش از مرگش به او [= حضرت مسیح‌] ایمان می‌آورد؛ و روز قیامت، بر آنها گواه خواهد بود.'];
ayat[653]=[4,160,'فَبِظُلْمٍ مِّنَ الَّذِينَ هَادُوا حَرَّمْنَا عَلَيْهِمْ طَيِّبَاتٍ أُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَن سَبِيلِ اللَّهِ كَثِيرًا','بخاطر ظلمی که از یهود صادر شد، و (نیز) بخاطر جلوگیری بسیار آنها از راه خدا، بخشی از چیزهای پاکیزه را که بر آنها حلال بود، حرام کردیم.'];
ayat[654]=[4,161,'وَأَخْذِهِمُ الرِّبَا وَقَدْ نُهُوا عَنْهُ وَأَكْلِهِمْ أَمْوَالَ النَّاسِ بِالْبَاطِلِ ۚ وَأَعْتَدْنَا لِلْكَافِرِينَ مِنْهُمْ عَذَابًا أَلِيمًا','و (همچنین) بخاطر ربا گرفتن، در حالی که از آن نهی شده بودند؛ و خوردن اموال مردم بباطل؛ و برای کافران آنها، عذاب دردناکی آماده کرده‌ایم.'];
ayat[655]=[4,162,'لَّٰكِنِ الرَّاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُونَ يُؤْمِنُونَ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ ۚ وَالْمُقِيمِينَ الصَّلَاةَ ۚ وَالْمُؤْتُونَ الزَّكَاةَ وَالْمُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ أُولَٰئِكَ سَنُؤْتِيهِمْ أَجْرًا عَظِيمًا','ولی راسخانِ در علم از آنها، و مؤمنان (از امّت اسلام،) به تمام آنچه بر تو نازل شده، و آنچه پیش از تو نازل گردیده، ایمان می‌آورند. (همچنین) نمازگزاران و زکات‌دهندگان و ایمان‌آورندگان به خدا و روز قیامت، بزودی به همه آنان پاداش عظیمی خواهیم داد.'];
ayat[656]=[4,163,'۞ إِنَّا أَوْحَيْنَا إِلَيْكَ كَمَا أَوْحَيْنَا إِلَىٰ نُوحٍ وَالنَّبِيِّينَ مِن بَعْدِهِ ۚ وَأَوْحَيْنَا إِلَىٰ إِبْرَاهِيمَ وَإِسْمَاعِيلَ وَإِسْحَاقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَعِيسَىٰ وَأَيُّوبَ وَيُونُسَ وَهَارُونَ وَسُلَيْمَانَ ۚ وَآتَيْنَا دَاوُودَ زَبُورًا','ما به تو وحی فرستادیم؛ همان گونه که به نوح و پیامبران بعد از او وحی فرستادیم؛ و (نیز) به ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط [= بنی اسرائیل‌] و عیسی و ایّوب و یونس و هارون و سلیمان وحی نمودیم؛ و به داوود زبور دادیم.'];
ayat[657]=[4,164,'وَرُسُلًا قَدْ قَصَصْنَاهُمْ عَلَيْكَ مِن قَبْلُ وَرُسُلًا لَّمْ نَقْصُصْهُمْ عَلَيْكَ ۚ وَكَلَّمَ اللَّهُ مُوسَىٰ تَكْلِيمًا','و پیامبرانی که سرگذشت آنها را پیش از این، برای تو باز گفته‌ایم؛ و پیامبرانی که سرگذشت آنها را بیان نکرده‌ایم؛ و خداوند با موسی سخن گفت. (و این امتیاز، از آن او بود.)'];
ayat[658]=[4,165,'رُّسُلًا مُّبَشِّرِينَ وَمُنذِرِينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اللَّهِ حُجَّةٌ بَعْدَ الرُّسُلِ ۚ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا','پیامبرانی که بشارت‌دهنده و بیم‌دهنده بودند، تا بعد از این پیامبران، حجتی برای مردم بر خدا باقی نماند، (و بر همه اتمام حجت شود؛) و خداوند، توانا و حکیم است.'];
ayat[659]=[4,166,'لَّٰكِنِ اللَّهُ يَشْهَدُ بِمَا أَنزَلَ إِلَيْكَ ۖ أَنزَلَهُ بِعِلْمِهِ ۖ وَالْمَلَائِكَةُ يَشْهَدُونَ ۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا','ولی خداوند گواهی می‌دهد به آنچه بر تو نازل کرده؛ که از روی علمش نازل کرده است، و فرشتگان (نیز) گواهی می‌دهند؛ هر چند گواهی خدا کافی است.'];
ayat[660]=[4,167,'إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَن سَبِيلِ اللَّهِ قَدْ ضَلُّوا ضَلَالًا بَعِيدًا','کسانی که کافر شدند، و (مردم را) از راه خدا بازداشتند، در گمراهی دوری گرفتار شده‌اند.'];
ayat[661]=[4,168,'إِنَّ الَّذِينَ كَفَرُوا وَظَلَمُوا لَمْ يَكُنِ اللَّهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيقًا','کسانی که کافر شدند، و (به خود و دیگران) ستم کردند، هرگز خدا آنها را نخواهد بخشید، و آنان را به هیچ راهی هدایت نخواهد کرد،'];
ayat[662]=[4,169,'إِلَّا طَرِيقَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَدًا ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا','مگر به راه دوزخ! که جاودانه در آن خواهند ماند؛ و این کار برای خدا آسان است!'];
ayat[663]=[4,170,'يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمُ الرَّسُولُ بِالْحَقِّ مِن رَّبِّكُمْ فَآمِنُوا خَيْرًا لَّكُمْ ۚ وَإِن تَكْفُرُوا فَإِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا','ای مردم! پیامبر (ی که انتظارش را می‌کشیدید،) حق را از جانب پروردگارتان آورد؛ به او ایمان بیاورید که برای شما بهتر است! و اگر کافر شوید، (به خدا زیانی نمی رسد، زیرا) آنچه در آسمانها و زمین است از آن خداست، و خداوند، دانا و حکیم است.'];
ayat[664]=[4,171,'يَا أَهْلَ الْكِتَابِ لَا تَغْلُوا فِي دِينِكُمْ وَلَا تَقُولُوا عَلَى اللَّهِ إِلَّا الْحَقَّ ۚ إِنَّمَا الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ رَسُولُ اللَّهِ وَكَلِمَتُهُ أَلْقَاهَا إِلَىٰ مَرْيَمَ وَرُوحٌ مِّنْهُ ۖ فَآمِنُوا بِاللَّهِ وَرُسُلِهِ ۖ وَلَا تَقُولُوا ثَلَاثَةٌ ۚ انتَهُوا خَيْرًا لَّكُمْ ۚ إِنَّمَا اللَّهُ إِلَٰهٌ وَاحِدٌ ۖ سُبْحَانَهُ أَن يَكُونَ لَهُ وَلَدٌ ۘ لَّهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَكَفَىٰ بِاللَّهِ وَكِيلًا','ای اهل کتاب! در دین خود، غلوّ (و زیاده روی) نکنید! و در باره خدا، غیر از حق نگویید! مسیح عیسی بن مریم فقط فرستاده خدا، و کلمه (و مخلوق) اوست، که او را به مریم القا نمود؛ و روحی (شایسته) از طرف او بود. بنابر این، به خدا و پیامبران او، ایمان بیاورید! و نگویید: «(خداوند) سه‌گانه است!» (از این سخن) خودداری کنید که برای شما بهتر است! خدا، تنها معبود یگانه است؛ او منزه است که فرزندی داشته باشد؛ (بلکه) از آن اوست آنچه در آسمانها و در زمین است؛ و برای تدبیر و سرپرستی آنها، خداوند کافی است.'];
ayat[665]=[4,172,'لَّن يَسْتَنكِفَ الْمَسِيحُ أَن يَكُونَ عَبْدًا لِّلَّهِ وَلَا الْمَلَائِكَةُ الْمُقَرَّبُونَ ۚ وَمَن يَسْتَنكِفْ عَنْ عِبَادَتِهِ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ إِلَيْهِ جَمِيعًا','هرگز مسیح از این ابا نداشت که بنده خدا باشد؛ و نه فرشتگان مقرّب او (از این ابا دارند). و آنها که از عبودیّت و بندگی او، روی برتابند و تکبّر کنند، بزودی همه آنها را (در قیامت) نزد خود جمع خواهد کرد.'];
ayat[666]=[4,173,'فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَيَزِيدُهُم مِّن فَضْلِهِ ۖ وَأَمَّا الَّذِينَ اسْتَنكَفُوا وَاسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذَابًا أَلِيمًا وَلَا يَجِدُونَ لَهُم مِّن دُونِ اللَّهِ وَلِيًّا وَلَا نَصِيرًا','امّا آنها که ایمان آوردند و اعمال صالح انجام دادند، پاداششان را بطور کامل خواهد داد؛ و از فضل و بخشش خود، بر آنها خواهد افزود. و آنها را که ابا کردند و تکبّر ورزیدند، مجازات دردناکی خواهد کرد؛ و برای خود، غیر از خدا، سرپرست و یاوری نخواهند یافت.'];
ayat[667]=[4,174,'يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُم بُرْهَانٌ مِّن رَّبِّكُمْ وَأَنزَلْنَا إِلَيْكُمْ نُورًا مُّبِينًا','ای مردم! دلیل روشن از طرف پروردگارتان برای شما آمد؛ و نور آشکاری به سوی شما نازل کردیم.'];
ayat[668]=[4,175,'فَأَمَّا الَّذِينَ آمَنُوا بِاللَّهِ وَاعْتَصَمُوا بِهِ فَسَيُدْخِلُهُمْ فِي رَحْمَةٍ مِّنْهُ وَفَضْلٍ وَيَهْدِيهِمْ إِلَيْهِ صِرَاطًا مُّسْتَقِيمًا','امّا آنها که به خدا ایمان آوردند و به (آن کتاب آسمانی) چنگ زدند، بزودی همه را در رحمت و فضل خود، وارد خواهد ساخت؛ و در راه راستی، به سوی خودش هدایت می‌کند.'];
ayat[669]=[4,176,'يَسْتَفْتُونَكَ قُلِ اللَّهُ يُفْتِيكُمْ فِي الْكَلَالَةِ ۚ إِنِ امْرُؤٌ هَلَكَ لَيْسَ لَهُ وَلَدٌ وَلَهُ أُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَ ۚ وَهُوَ يَرِثُهَا إِن لَّمْ يَكُن لَّهَا وَلَدٌ ۚ فَإِن كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثَانِ مِمَّا تَرَكَ ۚ وَإِن كَانُوا إِخْوَةً رِّجَالًا وَنِسَاءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْأُنثَيَيْنِ ۗ يُبَيِّنُ اللَّهُ لَكُمْ أَن تَضِلُّوا ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','از تو (در باره ارث خواهران و برادران) سؤال می‌کنند، بگو: «خداوند، حکم کلاله (خواهر و برادر) را برای شما بیان می‌کند: اگر مردی از دنیا برود، که فرزند نداشته باشد، و برای او خواهری باشد، نصف اموالی را که به جا گذاشته، از او (ارث) می‌برد؛ و (اگر خواهری از دنیا برود، وارث او یک برادر باشد،) او تمام مال را از آن خواهر به ارث می‌برد، در صورتی که (میّت) فرزند نداشته باشد؛ و اگر دو خواهر (از او) باقی باشند دو سوم اموال را می‌برند؛ و اگر برادران و خواهران با هم باشند، (تمام اموال را میان خود تقسیم می‌کنند؛ و) برای هر مذکّر، دو برابر سهم مؤنّث است. خداوند (احکام خود را) برای شما بیان می‌کند تا گمراه نشوید؛ و خداوند به همه چیز داناست.»'];
ayat[670]=[5,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا أَوْفُوا بِالْعُقُودِ ۚ أُحِلَّتْ لَكُم بَهِيمَةُ الْأَنْعَامِ إِلَّا مَا يُتْلَىٰ عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَأَنتُمْ حُرُمٌ ۗ إِنَّ اللَّهَ يَحْكُمُ مَا يُرِيدُ','ای کسانی که ایمان آورده‌اید! به پیمانها (و قراردادها) وفا کنید! چهارپایان (و جنین آنها) برای شما حلال شده است؛ مگر آنچه بر شما خوانده می‌شود (و استثنا خواهد شد). و به هنگام احرام، صید را حلال نشمرید! خداوند هر چه بخواهد (و مصلحت باشد) حکم می‌کند.'];
ayat[671]=[5,2,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُحِلُّوا شَعَائِرَ اللَّهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَائِدَ وَلَا آمِّينَ الْبَيْتَ الْحَرَامَ يَبْتَغُونَ فَضْلًا مِّن رَّبِّهِمْ وَرِضْوَانًا ۚ وَإِذَا حَلَلْتُمْ فَاصْطَادُوا ۚ وَلَا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ أَن صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ أَن تَعْتَدُوا ۘ وَتَعَاوَنُوا عَلَى الْبِرِّ وَالتَّقْوَىٰ ۖ وَلَا تَعَاوَنُوا عَلَى الْإِثْمِ وَالْعُدْوَانِ ۚ وَاتَّقُوا اللَّهَ ۖ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ','ای کسانی که ایمان آورده‌اید! شعائر و حدود الهی (و مراسم حج را محترم بشمرید! و مخالفت با آنها) را حلال ندانید! و نه ماه حرام را، و نه قربانیهای بی‌نشان و نشاندار را، و نه آنها را که به قصد خانه خدا برای به دست آوردن فضل پروردگار و خشنودی او می‌آیند! اما هنگامی که از احرام بیرون آمدید، صیدکردن برای شما مانعی ندارد. و خصومت با جمعیّتی که شما را از آمدن به مسجد الحرام (در سال حدیبیه) بازداشتند، نباید شما را وادار به تعدّی و تجاوز کند! و (همواره) در راه نیکی و پرهیزگاری با هم تعاون کنید! و (هرگز) در راه گناه و تعدّی همکاری ننمایید! و از (مخالفت فرمان) خدا بپرهیزید که مجازات خدا شدید است!'];
ayat[672]=[5,3,'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنزِيرِ وَمَا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ وَالْمُنْخَنِقَةُ وَالْمَوْقُوذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيحَةُ وَمَا أَكَلَ السَّبُعُ إِلَّا مَا ذَكَّيْتُمْ وَمَا ذُبِحَ عَلَى النُّصُبِ وَأَن تَسْتَقْسِمُوا بِالْأَزْلَامِ ۚ ذَٰلِكُمْ فِسْقٌ ۗ الْيَوْمَ يَئِسَ الَّذِينَ كَفَرُوا مِن دِينِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِ ۚ الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَأَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَرَضِيتُ لَكُمُ الْإِسْلَامَ دِينًا ۚ فَمَنِ اضْطُرَّ فِي مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِّإِثْمٍ ۙ فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','گوشت مردار، و خون، و گوشت خوک، و حیواناتی که به غیر نام خدا ذبح شوند، و حیوانات خفه‌شده، و به زجر کشته شده، و آنها که بر اثر پرت‌شدن از بلندی بمیرند، و آنها که به ضرب شاخ حیوان دیگری مرده باشند، و باقیمانده صید حیوان درنده -مگر آنکه (بموقع به آن حیوان برسید، و) آن را سرببرید- و حیواناتی که روی بتها (یا در برابر آنها) ذبح می‌شوند، (همه) بر شما حرام شده است؛ و (همچنین) قسمت کردن گوشت حیوان به وسیله چوبه‌های تیر مخصوص بخت آزمایی؛ تمام این اعمال، فسق و گناه است -امروز، کافران از (زوال) آیین شما، مأیوس شدند؛ بنابر این، از آنها نترسید! و از (مخالفت) من بترسید! امروز، دین شما را کامل کردم؛ و نعمت خود را بر شما تمام نمودم؛ و اسلام را به عنوان آیین (جاودان) شما پذیرفتم- امّا آنها که در حال گرسنگی، دستشان به غذای دیگری نرسد، و متمایل به گناه نباشند، (مانعی ندارد که از گوشتهای ممنوع بخورند؛) خداوند، آمرزنده و مهربان است.'];
ayat[673]=[5,4,'يَسْأَلُونَكَ مَاذَا أُحِلَّ لَهُمْ ۖ قُلْ أُحِلَّ لَكُمُ الطَّيِّبَاتُ ۙ وَمَا عَلَّمْتُم مِّنَ الْجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اللَّهُ ۖ فَكُلُوا مِمَّا أَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللَّهِ عَلَيْهِ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ','از تو سؤال می‌کنند چه چیزهایی برای آنها حلال شده است؟ بگو: «آنچه پاکیزه است، برای شما حلال گردیده؛ (و نیز صید) حیوانات شکاری و سگهای آموخته (و تربیت یافته) که از آنچه خداوند به شما تعلیم داده به آنها یاد داده‌اید، (بر شما حلال است؛) پس، از آنچه این حیوانات برای شما (صید می‌کنند و) نگاه می‌دارند، بخورید؛ و نام خدا را (به هنگام فرستادن حیوان برای شکار،) بر آن ببرید؛ و از (معصیت) خدا بپرهیزید که خداوند سریع الحساب است!»'];
ayat[674]=[5,5,'الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّبَاتُ ۖ وَطَعَامُ الَّذِينَ أُوتُوا الْكِتَابَ حِلٌّ لَّكُمْ وَطَعَامُكُمْ حِلٌّ لَّهُمْ ۖ وَالْمُحْصَنَاتُ مِنَ الْمُؤْمِنَاتِ وَالْمُحْصَنَاتُ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ مِن قَبْلِكُمْ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسَافِحِينَ وَلَا مُتَّخِذِي أَخْدَانٍ ۗ وَمَن يَكْفُرْ بِالْإِيمَانِ فَقَدْ حَبِطَ عَمَلُهُ وَهُوَ فِي الْآخِرَةِ مِنَ الْخَاسِرِينَ','امروز چیزهای پاکیزه برای شما حلال شده؛ و (همچنین) طعام اهل کتاب، برای شما حلال است؛ و طعام شما برای آنها حلال؛ و (نیز) زنان پاکدامن از مسلمانان، و زنان پاکدامن از اهل کتاب، حلالند؛ هنگامی که مهر آنها را بپردازید و پاکدامن باشید؛ نه زناکار، و نه دوست پنهانی و نامشروع گیرید. و کسی که انکار کند آنچه را باید به آن ایمان بیاورد، اعمال او تباه می‌گردد؛ و در سرای دیگر، از زیانکاران خواهد بود.'];
ayat[675]=[5,6,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قُمْتُمْ إِلَى الصَّلَاةِ فَاغْسِلُوا وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى الْمَرَافِقِ وَامْسَحُوا بِرُءُوسِكُمْ وَأَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ ۚ وَإِن كُنتُمْ جُنُبًا فَاطَّهَّرُوا ۚ وَإِن كُنتُم مَّرْضَىٰ أَوْ عَلَىٰ سَفَرٍ أَوْ جَاءَ أَحَدٌ مِّنكُم مِّنَ الْغَائِطِ أَوْ لَامَسْتُمُ النِّسَاءَ فَلَمْ تَجِدُوا مَاءً فَتَيَمَّمُوا صَعِيدًا طَيِّبًا فَامْسَحُوا بِوُجُوهِكُمْ وَأَيْدِيكُم مِّنْهُ ۚ مَا يُرِيدُ اللَّهُ لِيَجْعَلَ عَلَيْكُم مِّنْ حَرَجٍ وَلَٰكِن يُرِيدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ','ای کسانی که ایمان آورده‌اید! هنگامی که به نماز می‌ایستید، صورت و دستها را تا آرنج بشویید! و سر و پاها را تا مفصل [= برآمدگی پشت پا] مسح کنید! و اگر جنب باشید، خود را بشویید (و غسل کنید)! و اگر بیمار یا مسافر باشید، یا یکی از شما از محل پستی آمده [= قضای حاجت کرده‌]، یا با زنان تماس گرفته (و آمیزش جنسی کرده‌اید)، و آب (برای غسل یا وضو) نیابید، با خاک پاکی تیمم کنید! و از آن، بر صورت [= پیشانی‌] و دستها بکشید! خداوند نمی‌خواهد مشکلی برای شما ایجاد کند؛ بلکه می‌خواهد شما را پاک سازد و نعمتش را بر شما تمام نماید؛ شاید شکر او را بجا آورید!'];
ayat[676]=[5,7,'وَاذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ وَمِيثَاقَهُ الَّذِي وَاثَقَكُم بِهِ إِذْ قُلْتُمْ سَمِعْنَا وَأَطَعْنَا ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ','و به یاد آورید نعمت خدا را بر شما، و پیمانی را که با تأکید از شما گرفت، آن زمان که گفتید: «شنیدیم و اطاعت کردیم»! و از (مخالفت فرمان) خدا بپرهیزید که خدا، از آنچه درون سینه‌هاست، آگاه است!'];
ayat[677]=[5,8,'يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوَّامِينَ لِلَّهِ شُهَدَاءَ بِالْقِسْطِ ۖ وَلَا يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ عَلَىٰ أَلَّا تَعْدِلُوا ۚ اعْدِلُوا هُوَ أَقْرَبُ لِلتَّقْوَىٰ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ','ای کسانی که ایمان آورده‌اید! همواره برای خدا قیام کنید، و از روی عدالت، گواهی دهید! دشمنی با جمعیّتی، شما را به گناه و ترک عدالت نکشاند! عدالت کنید، که به پرهیزگاری نزدیکتر است! و از (معصیت) خدا بپرهیزید، که از آنچه انجام می‌دهید، با خبر است!'];
ayat[678]=[5,9,'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ ۙ لَهُم مَّغْفِرَةٌ وَأَجْرٌ عَظِيمٌ','خداوند، به آنها که ایمان آورده و اعمال صالح انجام داده‌اند، وعده آمرزش و پاداش عظیمی داده است.'];
ayat[679]=[5,10,'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ','و کسانی که کافر شدند و آیات ما را تکذیب کردند، اهل دوزخند.'];
ayat[680]=[5,11,'يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ إِذْ هَمَّ قَوْمٌ أَن يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنكُمْ ۖ وَاتَّقُوا اللَّهَ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','ای کسانی که ایمان آورده‌اید! نعمتی را که خدا به شما بخشید، به یاد آورید؛ آن زمان که جمعی (از دشمنان)، قصد داشتند دست به سوی شما دراز کنند (و شما را از میان بردارند)، اما خدا دست آنها را از شما باز داشت! از خدا بپرهیزید! و مؤمنان باید تنها بر خدا توکّل کنند!'];
ayat[681]=[5,12,'۞ وَلَقَدْ أَخَذَ اللَّهُ مِيثَاقَ بَنِي إِسْرَائِيلَ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيبًا ۖ وَقَالَ اللَّهُ إِنِّي مَعَكُمْ ۖ لَئِنْ أَقَمْتُمُ الصَّلَاةَ وَآتَيْتُمُ الزَّكَاةَ وَآمَنتُم بِرُسُلِي وَعَزَّرْتُمُوهُمْ وَأَقْرَضْتُمُ اللَّهَ قَرْضًا حَسَنًا لَّأُكَفِّرَنَّ عَنكُمْ سَيِّئَاتِكُمْ وَلَأُدْخِلَنَّكُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۚ فَمَن كَفَرَ بَعْدَ ذَٰلِكَ مِنكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ','خدا از بنی اسرائیل پیمان گرفت. و از آنها، دوازده نقیب [= سرپرست‌] برانگیختیم. و خداوند (به آنها) گفت: «من با شما هستم! اگر نماز را برپا دارید، و زکات را بپردازید، و به رسولان من ایمان بیاورید و آنها را یاری کنید، و به خدا قرض الحسن بدهید [= در راه او، به نیازمندان کمک کنید]، گناهان شما را می‌پوشانم [= می بخشم‌]؛ و شما را در باغهایی از بهشت، که نهرها از زیر درختانش جاری است، وارد می‌کنم. اما هر کس از شما بعد از این کافر شود، از راه راست منحرف گردیده است.'];
ayat[682]=[5,13,'فَبِمَا نَقْضِهِم مِّيثَاقَهُمْ لَعَنَّاهُمْ وَجَعَلْنَا قُلُوبَهُمْ قَاسِيَةً ۖ يُحَرِّفُونَ الْكَلِمَ عَن مَّوَاضِعِهِ ۙ وَنَسُوا حَظًّا مِّمَّا ذُكِّرُوا بِهِ ۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَائِنَةٍ مِّنْهُمْ إِلَّا قَلِيلًا مِّنْهُمْ ۖ فَاعْفُ عَنْهُمْ وَاصْفَحْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ','ولی بخاطر پیمان‌شکنی، آنها را از رحمت خویش دور ساختیم؛ و دلهای آنان را سخت و سنگین نمودیم؛ سخنان (خدا) را از موردش تحریف می‌کنند؛ و بخشی از آنچه را به آنها گوشزد شده بود، فراموش کردند؛ و هر زمان، از خیانتی (تازه) از آنها آگاه می‌شوی، مگر عده کمی از آنان؛ ولی از آنها درگذر و صرف‌نظر کن، که خداوند نیکوکاران را دوست می‌دارد!'];
ayat[683]=[5,14,'وَمِنَ الَّذِينَ قَالُوا إِنَّا نَصَارَىٰ أَخَذْنَا مِيثَاقَهُمْ فَنَسُوا حَظًّا مِّمَّا ذُكِّرُوا بِهِ فَأَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَىٰ يَوْمِ الْقِيَامَةِ ۚ وَسَوْفَ يُنَبِّئُهُمُ اللَّهُ بِمَا كَانُوا يَصْنَعُونَ','و از کسانی که ادّعای نصرانیّت (و یاری مسیح) داشتند (نیز) پیمان گرفتیم؛ ولی آنها قسمت مهمّی را از آنچه به آنان تذکّر داده شده بود فراموش کردند؛ از این رو در میان آنها، تا دامنه قیامت، عداوت و دشمنی افکندیم. و خداوند، (در قیامت) آنها را از آنچه انجام می‌دادند (و نتایج آن)، آگاه خواهد ساخت.'];
ayat[684]=[5,15,'يَا أَهْلَ الْكِتَابِ قَدْ جَاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ كَثِيرًا مِّمَّا كُنتُمْ تُخْفُونَ مِنَ الْكِتَابِ وَيَعْفُو عَن كَثِيرٍ ۚ قَدْ جَاءَكُم مِّنَ اللَّهِ نُورٌ وَكِتَابٌ مُّبِينٌ','ای اهل کتاب! پیامبر ما، که بسیاری از حقایق کتاب آسمانی را که شما کتمان می‌کردید روشن می‌سازد، به سوی شما آمد؛ و از بسیاری از آن، (که فعلاً افشای آن مصلحت نیست،) صرف نظر می‌نماید. (آری،) از طرف خدا، نور و کتاب آشکاری به سوی شما آمد.'];
ayat[685]=[5,16,'يَهْدِي بِهِ اللَّهُ مَنِ اتَّبَعَ رِضْوَانَهُ سُبُلَ السَّلَامِ وَيُخْرِجُهُم مِّنَ الظُّلُمَاتِ إِلَى النُّورِ بِإِذْنِهِ وَيَهْدِيهِمْ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','خداوند به برکت آن، کسانی را که از خشنودی او پیروی کنند، به راه‌های سلامت، هدایت می‌کند؛ و به فرمان خود، از تاریکیها به سوی روشنایی می‌برد؛ و آنها را به سوی راه راست، رهبری می‌نماید.'];
ayat[686]=[5,17,'لَّقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ ۚ قُلْ فَمَن يَمْلِكُ مِنَ اللَّهِ شَيْئًا إِنْ أَرَادَ أَن يُهْلِكَ الْمَسِيحَ ابْنَ مَرْيَمَ وَأُمَّهُ وَمَن فِي الْأَرْضِ جَمِيعًا ۗ وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۚ يَخْلُقُ مَا يَشَاءُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آنها که گفتند: «خدا، همان مسیح بن مریم است»، بطور مسلّم کافر شدند؛ بگو: «اگر خدا بخواهد مسیح بن مریم و مادرش و همه کسانی را که روی زمین هستند هلاک کند، چه کسی می‌تواند جلوگیری کند؟ (آری،) حکومت آسمانها و زمین، و آنچه میان آن دو قرار دارد از آن خداست؛ هر چه بخواهد، می‌آفریند؛ (حتّی انسانی بدون پدر، مانند مسیح؛) و او، بر هر چیزی تواناست.»'];
ayat[687]=[5,18,'وَقَالَتِ الْيَهُودُ وَالنَّصَارَىٰ نَحْنُ أَبْنَاءُ اللَّهِ وَأَحِبَّاؤُهُ ۚ قُلْ فَلِمَ يُعَذِّبُكُم بِذُنُوبِكُم ۖ بَلْ أَنتُم بَشَرٌ مِّمَّنْ خَلَقَ ۚ يَغْفِرُ لِمَن يَشَاءُ وَيُعَذِّبُ مَن يَشَاءُ ۚ وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ وَإِلَيْهِ الْمَصِيرُ','یهود و نصاری گفتند: «ما، فرزندان خدا و دوستان (خاصّ) او هستیم.» بگو: «پس چرا شما را در برابر گناهانتان مجازات می‌کند؟! بلکه شما هم بشری هستید از مخلوقاتی که آفریده؛ هر کس را بخواهد (و شایسته بداند)، می‌بخشد؛ و هر کس را بخواهد (و مستحق بداند)، مجازات می‌کند؛ و حکومت آسمانها و زمین و آنچه در میان آنهاست، از آن اوست؛ و بازگشت همه موجودات، به سوی اوست.»'];
ayat[688]=[5,19,'يَا أَهْلَ الْكِتَابِ قَدْ جَاءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ عَلَىٰ فَتْرَةٍ مِّنَ الرُّسُلِ أَن تَقُولُوا مَا جَاءَنَا مِن بَشِيرٍ وَلَا نَذِيرٍ ۖ فَقَدْ جَاءَكُم بَشِيرٌ وَنَذِيرٌ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','ای اهل کتاب! رسول ما، پس از فاصله و فترتی میان پیامبران، به سوی شما آمد؛ در حالی که حقایق را برای شما بیان می‌کند؛ تا مبادا (روز قیامت) بگویید: «نه بشارت دهنده‌ای به سراغ ما آمد، و نه بیم دهنده‌ای»! (هم اکنون، پیامبر) بشارت‌دهنده و بیم‌دهنده، به سوی شما آمد! و خداوند بر همه چیز تواناست.'];
ayat[689]=[5,20,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جَعَلَ فِيكُمْ أَنبِيَاءَ وَجَعَلَكُم مُّلُوكًا وَآتَاكُم مَّا لَمْ يُؤْتِ أَحَدًا مِّنَ الْعَالَمِينَ','(به یاد آورید) هنگامی را که موسی به قوم خود گفت: «ای قوم من! نعمت خدا را بر خود متذکّر شوید هنگامی که در میان شما، پیامبرانی قرار داد؛ (و زنجیر بندگی و اسارت فرعونی را شکست) و شما را حاکم و صاحب اختیار خود قرار داد؛ و به شما چیزهایی بخشید که به هیچ یک از جهانیان نداده بود!'];
ayat[690]=[5,21,'يَا قَوْمِ ادْخُلُوا الْأَرْضَ الْمُقَدَّسَةَ الَّتِي كَتَبَ اللَّهُ لَكُمْ وَلَا تَرْتَدُّوا عَلَىٰ أَدْبَارِكُمْ فَتَنقَلِبُوا خَاسِرِينَ','ای قوم! به سرزمین مقدّسی که خداوند برای شما مقرّر داشته، وارد شوید! و به پشت سر خود بازنگردید (و عقب گرد نکنید) که زیانکار خواهید بود!»'];
ayat[691]=[5,22,'قَالُوا يَا مُوسَىٰ إِنَّ فِيهَا قَوْمًا جَبَّارِينَ وَإِنَّا لَن نَّدْخُلَهَا حَتَّىٰ يَخْرُجُوا مِنْهَا فَإِن يَخْرُجُوا مِنْهَا فَإِنَّا دَاخِلُونَ','گفتند: «ای موسی! در آن (سرزمین)، جمعیّتی (نیرومند و) ستمگرند؛ و ما هرگز وارد آن نمی‌شویم تا آنها از آن خارج شوند؛ اگر آنها از آن خارج شوند، ما وارد خواهیم شد!»'];
ayat[692]=[5,23,'قَالَ رَجُلَانِ مِنَ الَّذِينَ يَخَافُونَ أَنْعَمَ اللَّهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبَابَ فَإِذَا دَخَلْتُمُوهُ فَإِنَّكُمْ غَالِبُونَ ۚ وَعَلَى اللَّهِ فَتَوَكَّلُوا إِن كُنتُم مُّؤْمِنِينَ','(ولی) دو نفر از مردانی که از خدا می‌ترسیدند، و خداوند به آنها، نعمت (عقل و ایمان و شهامت) داده بود، گفتند: «شما وارد دروازه شهر آنان شوید! هنگامی که وارد شدید، پیروز خواهید شد. و بر خدا توکل کنید اگر ایمان دارید!»'];
ayat[693]=[5,24,'قَالُوا يَا مُوسَىٰ إِنَّا لَن نَّدْخُلَهَا أَبَدًا مَّا دَامُوا فِيهَا ۖ فَاذْهَبْ أَنتَ وَرَبُّكَ فَقَاتِلَا إِنَّا هَاهُنَا قَاعِدُونَ','(بنی اسرائیل) گفتند: «ای موسی! تا آنها در آنجا هستند، ما هرگز وارد نخواهیم شد! تو و پروردگارت بروید و (با آنان) بجنگید، ما همینجا نشسته‌ایم»!'];
ayat[694]=[5,25,'قَالَ رَبِّ إِنِّي لَا أَمْلِكُ إِلَّا نَفْسِي وَأَخِي ۖ فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفَاسِقِينَ','(موسی) گفت: «پروردگارا! من تنها اختیار خودم و برادرم را دارم، میان ما و این جمعیّت گنهکار، جدایی بیفکن!»'];
ayat[695]=[5,26,'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ ۛ أَرْبَعِينَ سَنَةً ۛ يَتِيهُونَ فِي الْأَرْضِ ۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفَاسِقِينَ','خداوند (به موسی) فرمود: «این سرزمین (مقدس)، تا چهل سال بر آنها ممنوع است (و به آن نخواهند رسید)؛ پیوسته در زمین (در این بیابان)، سرگردان خواهند بود؛ و در باره (سرنوشت) این جمعیّت گنهکار، غمگین مباش!»'];
ayat[696]=[5,27,'۞ وَاتْلُ عَلَيْهِمْ نَبَأَ ابْنَيْ آدَمَ بِالْحَقِّ إِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ أَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْآخَرِ قَالَ لَأَقْتُلَنَّكَ ۖ قَالَ إِنَّمَا يَتَقَبَّلُ اللَّهُ مِنَ الْمُتَّقِينَ','و داستان دو فرزند آدم را بحقّ بر آنها بخوان: هنگامی که هر کدام، کاری برای تقرّب (به پروردگار) انجام دادند؛ امّا از یکی پذیرفته شد، و از دیگری پذیرفته نشد؛ (برادری که عملش مردود شده بود، به برادر دیگر) گفت: «به خدا سوگند تو را خواهم کشت!» (برادر دیگر) گفت: «(من چه گناهی دارم؟ زیرا) خدا، تنها از پرهیزگاران می‌پذیرد!'];
ayat[697]=[5,28,'لَئِن بَسَطتَ إِلَيَّ يَدَكَ لِتَقْتُلَنِي مَا أَنَا بِبَاسِطٍ يَدِيَ إِلَيْكَ لِأَقْتُلَكَ ۖ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ','اگر تو برای کشتن من، دست دراز کنی، من هرگز به قتل تو دست نمی‌گشایم، چون از پروردگار جهانیان می‌ترسم!'];
ayat[698]=[5,29,'إِنِّي أُرِيدُ أَن تَبُوءَ بِإِثْمِي وَإِثْمِكَ فَتَكُونَ مِنْ أَصْحَابِ النَّارِ ۚ وَذَٰلِكَ جَزَاءُ الظَّالِمِينَ','من می‌خواهم تو با گناه من و خودت (از این عمل) بازگردی (و بار هر دو گناه را به دوش کشی)؛ و از دوزخیان گردی. و همین است سزای ستمکاران!'];
ayat[699]=[5,30,'فَطَوَّعَتْ لَهُ نَفْسُهُ قَتْلَ أَخِيهِ فَقَتَلَهُ فَأَصْبَحَ مِنَ الْخَاسِرِينَ','نفس سرکش، کم کم او را به کشتن برادرش ترغیب کرد؛ (سرانجام) او را کشت؛ و از زیانکاران شد.'];
ayat[700]=[5,31,'فَبَعَثَ اللَّهُ غُرَابًا يَبْحَثُ فِي الْأَرْضِ لِيُرِيَهُ كَيْفَ يُوَارِي سَوْءَةَ أَخِيهِ ۚ قَالَ يَا وَيْلَتَا أَعَجَزْتُ أَنْ أَكُونَ مِثْلَ هَٰذَا الْغُرَابِ فَأُوَارِيَ سَوْءَةَ أَخِي ۖ فَأَصْبَحَ مِنَ النَّادِمِينَ','سپس خداوند زاغی را فرستاد که در زمین، جستجو (و کندوکاو) می‌کرد؛ تا به او نشان دهد چگونه جسد برادر خود را دفن کند. او گفت: «وای بر من! آیا من نتوانستم مثل این زاغ باشم و جسد برادرم را دفن کنم؟!» و سرانجام (از ترس رسوایی، و بر اثر فشار وجدان، از کار خود) پشیمان شد.'];
ayat[701]=[5,32,'مِنْ أَجْلِ ذَٰلِكَ كَتَبْنَا عَلَىٰ بَنِي إِسْرَائِيلَ أَنَّهُ مَن قَتَلَ نَفْسًا بِغَيْرِ نَفْسٍ أَوْ فَسَادٍ فِي الْأَرْضِ فَكَأَنَّمَا قَتَلَ النَّاسَ جَمِيعًا وَمَنْ أَحْيَاهَا فَكَأَنَّمَا أَحْيَا النَّاسَ جَمِيعًا ۚ وَلَقَدْ جَاءَتْهُمْ رُسُلُنَا بِالْبَيِّنَاتِ ثُمَّ إِنَّ كَثِيرًا مِّنْهُم بَعْدَ ذَٰلِكَ فِي الْأَرْضِ لَمُسْرِفُونَ','به همین جهت، بر بنی اسرائیل مقرّر داشتیم که هر کس، انسانی را بدون ارتکاب قتل یا فساد در روی زمین بکشد، چنان است که گویی همه انسانها را کشته؛ و هر کس، انسانی را از مرگ رهایی بخشد، چنان است که گویی همه مردم را زنده کرده است. و رسولان ما، دلایل روشن برای بنی اسرائیل آوردند، اما بسیاری از آنها، پس از آن در روی زمین، تعدّی و اسراف کردند.'];
ayat[702]=[5,33,'إِنَّمَا جَزَاءُ الَّذِينَ يُحَارِبُونَ اللَّهَ وَرَسُولَهُ وَيَسْعَوْنَ فِي الْأَرْضِ فَسَادًا أَن يُقَتَّلُوا أَوْ يُصَلَّبُوا أَوْ تُقَطَّعَ أَيْدِيهِمْ وَأَرْجُلُهُم مِّنْ خِلَافٍ أَوْ يُنفَوْا مِنَ الْأَرْضِ ۚ ذَٰلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيَا ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ','کیفر آنها که با خدا و پیامبرش به جنگ برمی‌خیزند، و اقدام به فساد در روی زمین می‌کنند، (و با تهدید اسلحه، به جان و مال و ناموس مردم حمله می‌برند،) فقط این است که اعدام شوند؛ یا به دار آویخته گردند؛ یا (چهار انگشت از) دست (راست) و پای (چپ) آنها، بعکس یکدیگر، بریده شود؛ و یا از سرزمین خود تبعید گردند. این رسوایی آنها در دنیاست؛ و در آخرت، مجازات عظیمی دارند.'];
ayat[703]=[5,34,'إِلَّا الَّذِينَ تَابُوا مِن قَبْلِ أَن تَقْدِرُوا عَلَيْهِمْ ۖ فَاعْلَمُوا أَنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','مگر آنها که پیش از دست یافتن شما بر آنان، توبه کنند؛ پس بدانید (خدا توبه آنها را می‌پذیرد؛) خداوند آمرزنده و مهربان است.'];
ayat[704]=[5,35,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَابْتَغُوا إِلَيْهِ الْوَسِيلَةَ وَجَاهِدُوا فِي سَبِيلِهِ لَعَلَّكُمْ تُفْلِحُونَ','ای کسانی که ایمان آورده‌اید! از (مخالفت فرمان) خدا بپرهیزید! و وسیله‌ای برای تقرب به او بجوئید! و در راه او جهاد کنید، باشد که رستگار شوید!'];
ayat[705]=[5,36,'إِنَّ الَّذِينَ كَفَرُوا لَوْ أَنَّ لَهُم مَّا فِي الْأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لِيَفْتَدُوا بِهِ مِنْ عَذَابِ يَوْمِ الْقِيَامَةِ مَا تُقُبِّلَ مِنْهُمْ ۖ وَلَهُمْ عَذَابٌ أَلِيمٌ','بیقین کسانی که کافر شدند، اگر تمام آنچه روی زمین است و همانند آن، مال آنها باشد و همه آن را برای نجات از کیفر روز قیامت بدهند، از آنان پذیرفته نخواهد شد؛ و مجازات دردناکی خواهند داشت.'];
ayat[706]=[5,37,'يُرِيدُونَ أَن يَخْرُجُوا مِنَ النَّارِ وَمَا هُم بِخَارِجِينَ مِنْهَا ۖ وَلَهُمْ عَذَابٌ مُّقِيمٌ','پیوسته می‌خواهند از آتش خارج شوند، ولی نمی‌توانند از آن خارج گردند؛ و برای آنها مجازاتی پایدار است.'];
ayat[707]=[5,38,'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوا أَيْدِيَهُمَا جَزَاءً بِمَا كَسَبَا نَكَالًا مِّنَ اللَّهِ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ','دست مرد دزد و زن دزد را، به کیفر عملی که انجام داده‌اند، بعنوان یک مجازات الهی، قطع کنید! و خداوند توانا و حکیم است.'];
ayat[708]=[5,39,'فَمَن تَابَ مِن بَعْدِ ظُلْمِهِ وَأَصْلَحَ فَإِنَّ اللَّهَ يَتُوبُ عَلَيْهِ ۗ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','اما آن کس که پس از ستم کردن، توبه و جبران نماید، خداوند توبه او را می‌پذیرد؛ (و از این مجازات؛ معاف می‌شود، زیرا) خداوند، آمرزنده و مهربان است.'];
ayat[709]=[5,40,'أَلَمْ تَعْلَمْ أَنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ يُعَذِّبُ مَن يَشَاءُ وَيَغْفِرُ لِمَن يَشَاءُ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آیا نمی‌دانی که حکومت و فرمانروائی آسمانها و زمین از آن خداست؟ هر کس را بخواهد (و مستحق بداند)، کیفر می‌کند؛ و هر کس را بخواهد و شایسته بداند، می‌بخشد؛ و خداوند بر هر چیزی قادر است.'];
ayat[710]=[5,41,'۞ يَا أَيُّهَا الرَّسُولُ لَا يَحْزُنكَ الَّذِينَ يُسَارِعُونَ فِي الْكُفْرِ مِنَ الَّذِينَ قَالُوا آمَنَّا بِأَفْوَاهِهِمْ وَلَمْ تُؤْمِن قُلُوبُهُمْ ۛ وَمِنَ الَّذِينَ هَادُوا ۛ سَمَّاعُونَ لِلْكَذِبِ سَمَّاعُونَ لِقَوْمٍ آخَرِينَ لَمْ يَأْتُوكَ ۖ يُحَرِّفُونَ الْكَلِمَ مِن بَعْدِ مَوَاضِعِهِ ۖ يَقُولُونَ إِنْ أُوتِيتُمْ هَٰذَا فَخُذُوهُ وَإِن لَّمْ تُؤْتَوْهُ فَاحْذَرُوا ۚ وَمَن يُرِدِ اللَّهُ فِتْنَتَهُ فَلَن تَمْلِكَ لَهُ مِنَ اللَّهِ شَيْئًا ۚ أُولَٰئِكَ الَّذِينَ لَمْ يُرِدِ اللَّهُ أَن يُطَهِّرَ قُلُوبَهُمْ ۚ لَهُمْ فِي الدُّنْيَا خِزْيٌ ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابٌ عَظِيمٌ','ای فرستاده (خدا)! آنها که در مسیر کفر شتاب می‌کنند و با زبان می‌گویند: «ایمان آوردیم» و قلب آنها ایمان نیاورده، تو را اندوهگین نسازند! و (همچنین) گروهی از یهودیان که خوب به سخنان تو گوش می‌دهند، تا دستاویزی برای تکذیب تو بیابند؛ آنها جاسوسان گروه دیگری هستند که خودشان نزد تو نیامده‌اند؛ آنها سخنان را از مفهوم اصلیش تحریف می‌کنند، و (به یکدیگر) می‌گویند: «اگر این (که ما می‌خواهیم) به شما داده شد (و محمد بر طبق خواسته شما داوری کرد،) بپذیرید، وگرنه (از او) دوری کنید!» (ولی) کسی را که خدا (بر اثر گناهان پی‌درپی او) بخواهد مجازات کند، قادر به دفاع از او نیستی؛ آنها کسانی هستند که خدا نخواسته دلهایشان را پاک کند؛ در دنیا رسوایی، و در آخرت مجازات بزرگی نصیبشان خواهد شد.'];
ayat[711]=[5,42,'سَمَّاعُونَ لِلْكَذِبِ أَكَّالُونَ لِلسُّحْتِ ۚ فَإِن جَاءُوكَ فَاحْكُم بَيْنَهُمْ أَوْ أَعْرِضْ عَنْهُمْ ۖ وَإِن تُعْرِضْ عَنْهُمْ فَلَن يَضُرُّوكَ شَيْئًا ۖ وَإِنْ حَكَمْتَ فَاحْكُم بَيْنَهُم بِالْقِسْطِ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ','آنها بسیار به سخنان تو گوش می‌دهند تا آن را تکذیب کنند؛ مال حرام فراوان می خورند؛ پس اگر نزد تو آمدند، در میان آنان داوری کن، یا (اگر صلاح دانستی) آنها را به حال خود واگذار! و اگر از آنان صرف‌نظر کنی، به تو هیچ زیانی نمی‌رسانند؛ و اگر میان آنها داوری کنی، با عدالت داوری کن، که خدا عادلان را دوست دارد!'];
ayat[712]=[5,43,'وَكَيْفَ يُحَكِّمُونَكَ وَعِندَهُمُ التَّوْرَاةُ فِيهَا حُكْمُ اللَّهِ ثُمَّ يَتَوَلَّوْنَ مِن بَعْدِ ذَٰلِكَ ۚ وَمَا أُولَٰئِكَ بِالْمُؤْمِنِينَ','چگونه تو را به داوری می‌طلبند؟! در حالی که تورات نزد ایشان است؛ و در آن، حکم خدا هست. (وانگهی) پس از داوری‌خواستن از حکم تو، (چرا) روی می گردانند؟! آنها مؤمن نیستند.'];
ayat[713]=[5,44,'إِنَّا أَنزَلْنَا التَّوْرَاةَ فِيهَا هُدًى وَنُورٌ ۚ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذِينَ أَسْلَمُوا لِلَّذِينَ هَادُوا وَالرَّبَّانِيُّونَ وَالْأَحْبَارُ بِمَا اسْتُحْفِظُوا مِن كِتَابِ اللَّهِ وَكَانُوا عَلَيْهِ شُهَدَاءَ ۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوا بِآيَاتِي ثَمَنًا قَلِيلًا ۚ وَمَن لَّمْ يَحْكُم بِمَا أَنزَلَ اللَّهُ فَأُولَٰئِكَ هُمُ الْكَافِرُونَ','ما تورات را نازل کردیم در حالی که در آن، هدایت و نور بود؛ و پیامبران، که در برابر فرمان خدا تسلیم بودند، با آن برای یهود حکم می‌کردند؛ و (همچنین) علما و دانشمندان به این کتاب که به آنها سپرده شده و بر آن گواه بودند، داوری می‌نمودند. بنابر این، (بخاطر داوری بر طبق آیات الهی،) از مردم نهراسید! و از من بترسید! و آیات مرا به بهای ناچیزی نفروشید! و آنها که به احکامی که خدا نازل کرده حکم نمی‌کنند، کافرند.'];
ayat[714]=[5,45,'وَكَتَبْنَا عَلَيْهِمْ فِيهَا أَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْأَنفَ بِالْأَنفِ وَالْأُذُنَ بِالْأُذُنِ وَالسِّنَّ بِالسِّنِّ وَالْجُرُوحَ قِصَاصٌ ۚ فَمَن تَصَدَّقَ بِهِ فَهُوَ كَفَّارَةٌ لَّهُ ۚ وَمَن لَّمْ يَحْكُم بِمَا أَنزَلَ اللَّهُ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ','و بر آنها [= بنی اسرائیل‌] در آن [= تورات‌]، مقرر داشتیم که جان در مقابل جان، و چشم در مقابل چشم، و بینی در برابر بینی، و گوش در مقابل گوش، و دندان در برابر دندان می‌باشد؛ و هر زخمی، قصاص دارد؛ و اگر کسی آن را ببخشد (و از قصاص، صرف‌نظر کند)، کفاره (گناهان) او محسوب می‌شود؛ و هر کس به احکامی که خدا نازل کرده حکم نکند، ستمگر است.'];
ayat[715]=[5,46,'وَقَفَّيْنَا عَلَىٰ آثَارِهِم بِعِيسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرَاةِ ۖ وَآتَيْنَاهُ الْإِنجِيلَ فِيهِ هُدًى وَنُورٌ وَمُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرَاةِ وَهُدًى وَمَوْعِظَةً لِّلْمُتَّقِينَ','و بدنبال آنها [= پیامبران پیشین‌]، عیسی بن مریم را فرستادیم در حالی که کتاب تورات را که پیش از او فرستاده شده بود تصدیق داشت؛ و انجیل را به او دادیم که در آن، هدایت و نور بود؛ و (این کتاب آسمانی نیز) تورات را، که قبل از آن بود، تصدیق می‌کرد؛ و هدایت و موعظه‌ای برای پرهیزگاران بود.'];
ayat[716]=[5,47,'وَلْيَحْكُمْ أَهْلُ الْإِنجِيلِ بِمَا أَنزَلَ اللَّهُ فِيهِ ۚ وَمَن لَّمْ يَحْكُم بِمَا أَنزَلَ اللَّهُ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ','اهل انجیل [= پیروان مسیح‌] نیز باید به آنچه خداوند در آن نازل کرده حکم کنند! و کسانی که بر طبق آنچه خدا نازل کرده حکم نمی‌کنند، فاسقند.'];
ayat[717]=[5,48,'وَأَنزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ الْكِتَابِ وَمُهَيْمِنًا عَلَيْهِ ۖ فَاحْكُم بَيْنَهُم بِمَا أَنزَلَ اللَّهُ ۖ وَلَا تَتَّبِعْ أَهْوَاءَهُمْ عَمَّا جَاءَكَ مِنَ الْحَقِّ ۚ لِكُلٍّ جَعَلْنَا مِنكُمْ شِرْعَةً وَمِنْهَاجًا ۚ وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَٰكِن لِّيَبْلُوَكُمْ فِي مَا آتَاكُمْ ۖ فَاسْتَبِقُوا الْخَيْرَاتِ ۚ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا فَيُنَبِّئُكُم بِمَا كُنتُمْ فِيهِ تَخْتَلِفُونَ','و این کتاب [= قرآن‌] را به حق بر تو نازل کردیم، در حالی که کتب پیشین را تصدیق می‌کند، و حافظ و نگاهبان آنهاست؛ پس بر طبق احکامی که خدا نازل کرده، در میان آنها حکم کن! از هوی و هوسهای آنان پیروی نکن! و از احکام الهی، روی مگردان! ما برای هر کدام از شما، آیین و طریقه روشنی قرار دادیم؛ و اگر خدا می‌خواست، همه شما را امت واحدی قرارمی‌داد؛ ولی خدا می‌خواهد شما را در آنچه به شما بخشیده بیازماید؛ (و استعدادهای مختلف شما را پرورش دهد). پس در نیکیها بر یکدیگر سبقت جویید! بازگشت همه شما، به سوی خداست؛ سپس از آنچه در آن اختلاف می‌کردید؛ به شما خبر خواهد داد.'];
ayat[718]=[5,49,'وَأَنِ احْكُم بَيْنَهُم بِمَا أَنزَلَ اللَّهُ وَلَا تَتَّبِعْ أَهْوَاءَهُمْ وَاحْذَرْهُمْ أَن يَفْتِنُوكَ عَن بَعْضِ مَا أَنزَلَ اللَّهُ إِلَيْكَ ۖ فَإِن تَوَلَّوْا فَاعْلَمْ أَنَّمَا يُرِيدُ اللَّهُ أَن يُصِيبَهُم بِبَعْضِ ذُنُوبِهِمْ ۗ وَإِنَّ كَثِيرًا مِّنَ النَّاسِ لَفَاسِقُونَ','و در میان آنها [= اهل کتاب‌]، طبق آنچه خداوند نازل کرده، داوری کن! و از هوسهای آنان پیروی مکن! و از آنها برحذر باش، مبادا تو را از بعض احکامی که خدا بر تو نازل کرده، منحرف سازند! و اگر آنها (از حکم و داوری تو)، روی گردانند، بدان که خداوند می‌خواهد آنان را بخاطر پاره‌ای از گناهانشان مجازات کند؛ و بسیاری از مردم فاسقند.'];
ayat[719]=[5,50,'أَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُونَ ۚ وَمَنْ أَحْسَنُ مِنَ اللَّهِ حُكْمًا لِّقَوْمٍ يُوقِنُونَ','آیا آنها حکم جاهلیّت را (از تو) می‌خواهند؟! و چه کسی بهتر از خدا، برای قومی که اهل یقین هستند، حکم می‌کند؟!'];
ayat[720]=[5,51,'۞ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا الْيَهُودَ وَالنَّصَارَىٰ أَوْلِيَاءَ ۘ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ وَمَن يَتَوَلَّهُم مِّنكُمْ فَإِنَّهُ مِنْهُمْ ۗ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','ای کسانی که ایمان آورده‌اید! یهود و نصاری را ولّی (و دوست و تکیه‌گاه خود،) انتخاب نکنید! آنها اولیای یکدیگرند؛ و کسانی که از شما با آنان دوستی کنند، از آنها هستند؛ خداوند، جمعیّت ستمکار را هدایت نمی‌کند'];
ayat[721]=[5,52,'فَتَرَى الَّذِينَ فِي قُلُوبِهِم مَّرَضٌ يُسَارِعُونَ فِيهِمْ يَقُولُونَ نَخْشَىٰ أَن تُصِيبَنَا دَائِرَةٌ ۚ فَعَسَى اللَّهُ أَن يَأْتِيَ بِالْفَتْحِ أَوْ أَمْرٍ مِّنْ عِندِهِ فَيُصْبِحُوا عَلَىٰ مَا أَسَرُّوا فِي أَنفُسِهِمْ نَادِمِينَ','(ولی) کسانی را که در دلهایشان بیماری است می‌بینی که در (دوستی با آنان)، بر یکدیگر پیشی می‌گیرند، و می‌گویند: «می‌ترسیم حادثه‌ای برای ما اتفاق بیفتد (و نیاز به کمک آنها داشته باشیم!)» شاید خداوند پیروزی یا حادثه دیگری از سوی خود (به نفع مسلمانان) پیش بیاورد؛ و این دسته، از آنچه در دل پنهان داشتند، پشیمان گردند!'];
ayat[722]=[5,53,'وَيَقُولُ الَّذِينَ آمَنُوا أَهَٰؤُلَاءِ الَّذِينَ أَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ ۙ إِنَّهُمْ لَمَعَكُمْ ۚ حَبِطَتْ أَعْمَالُهُمْ فَأَصْبَحُوا خَاسِرِينَ','آنها که ایمان آورده‌اند می‌گویند: «آیا این (منافقان) همانها هستند که با نهایت تأکید سوگند یاد کردند که با شما هستند؟! (چرا کارشان به اینجا رسید؟!)» (آری،) اعمالشان نابود گشت، و زیانکار شدند.'];
ayat[723]=[5,54,'يَا أَيُّهَا الَّذِينَ آمَنُوا مَن يَرْتَدَّ مِنكُمْ عَن دِينِهِ فَسَوْفَ يَأْتِي اللَّهُ بِقَوْمٍ يُحِبُّهُمْ وَيُحِبُّونَهُ أَذِلَّةٍ عَلَى الْمُؤْمِنِينَ أَعِزَّةٍ عَلَى الْكَافِرِينَ يُجَاهِدُونَ فِي سَبِيلِ اللَّهِ وَلَا يَخَافُونَ لَوْمَةَ لَائِمٍ ۚ ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَن يَشَاءُ ۚ وَاللَّهُ وَاسِعٌ عَلِيمٌ','ای کسانی که ایمان آورده‌اید! هر کس از شما، از آیین خود بازگردد، (به خدا زیانی نمی‌رساند؛ خداوند جمعیّتی را می‌آورد که آنها را دوست دارد و آنان (نیز) او را دوست دارند، در برابر مؤمنان متواضع، و در برابر کافران سرسخت و نیرومندند؛ آنها در راه خدا جهاد می‌کنند، و از سرزنش هیچ ملامتگری هراسی ندارند. این، فضل خداست که به هر کس بخواهد (و شایسته ببیند) می‌دهد؛ و (فضل) خدا وسیع، و خداوند داناست.'];
ayat[724]=[5,55,'إِنَّمَا وَلِيُّكُمُ اللَّهُ وَرَسُولُهُ وَالَّذِينَ آمَنُوا الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُمْ رَاكِعُونَ','سرپرست و ولیّ شما، تنها خداست و پیامبر او و آنها که ایمان آورده‌اند؛ همانها که نماز را برپا می‌دارند، و در حال رکوع، زکات می‌دهند.'];
ayat[725]=[5,56,'وَمَن يَتَوَلَّ اللَّهَ وَرَسُولَهُ وَالَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللَّهِ هُمُ الْغَالِبُونَ','و کسانی که ولایت خدا و پیامبر او و افراد باایمان را بپذیرند، پیروزند؛ (زیرا) حزب و جمعیّت خدا پیروز است.'];
ayat[726]=[5,57,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا الَّذِينَ اتَّخَذُوا دِينَكُمْ هُزُوًا وَلَعِبًا مِّنَ الَّذِينَ أُوتُوا الْكِتَابَ مِن قَبْلِكُمْ وَالْكُفَّارَ أَوْلِيَاءَ ۚ وَاتَّقُوا اللَّهَ إِن كُنتُم مُّؤْمِنِينَ','ای کسانی که ایمان آورده‌اید! افرادی که آیین شما را به باد استهزاء و بازی می‌گیرند -از اهل کتاب و مشرکان- ولیّ خود انتخاب نکنید؛ و از خدا بپرهیزید اگر ایمان دارید!'];
ayat[727]=[5,58,'وَإِذَا نَادَيْتُمْ إِلَى الصَّلَاةِ اتَّخَذُوهَا هُزُوًا وَلَعِبًا ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَعْقِلُونَ','آنها هنگامی که (اذان می‌گویید، و مردم را) به نماز فرا می‌خوانید، آن را به مسخره و بازی می‌گیرند؛ این بخاطر آن است که آنها جمعی نابخردند.'];
ayat[728]=[5,59,'قُلْ يَا أَهْلَ الْكِتَابِ هَلْ تَنقِمُونَ مِنَّا إِلَّا أَنْ آمَنَّا بِاللَّهِ وَمَا أُنزِلَ إِلَيْنَا وَمَا أُنزِلَ مِن قَبْلُ وَأَنَّ أَكْثَرَكُمْ فَاسِقُونَ','بگو: «ای اهل کتاب! آیا به ما خرده می‌گیرید؟ (مگر ما چه کرده‌ایم) جز اینکه به خداوند یگانه، و به آنچه بر ما نازل شده، و به آنچه پیش از این نازل گردیده، ایمان آورده‌ایم. و این، بخاطر آن است که بیشتر شما، از راه حق، خارج شده‌اید.»'];
ayat[729]=[5,60,'قُلْ هَلْ أُنَبِّئُكُم بِشَرٍّ مِّن ذَٰلِكَ مَثُوبَةً عِندَ اللَّهِ ۚ مَن لَّعَنَهُ اللَّهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيرَ وَعَبَدَ الطَّاغُوتَ ۚ أُولَٰئِكَ شَرٌّ مَّكَانًا وَأَضَلُّ عَن سَوَاءِ السَّبِيلِ','بگو: «آیا شما را از کسانی که موقعیّت و پاداششان نزد خدا بدتر از این است، با خبر کنم؟ کسانی که خداوند آنها را از رحمت خود دور ساخته، و مورد خشم قرار داده، (و مسخ کرده،) و از آنها، میمونها و خوکهایی قرار داده، و پرستش بت کرده‌اند؛ موقعیّت و محل آنها، بدتر است؛ و از راه راست، گمراهترند.»'];
ayat[730]=[5,61,'وَإِذَا جَاءُوكُمْ قَالُوا آمَنَّا وَقَد دَّخَلُوا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوا بِهِ ۚ وَاللَّهُ أَعْلَمُ بِمَا كَانُوا يَكْتُمُونَ','هنگامی که نزد شما می‌آیند، می‌گویند: «ایمان آورده‌ایم!» (امّا) با کفر وارد می‌شوند، و با کفر خارج می‌گردند؛ و خداوند، از آنچه کتمان می‌کردند، آگاهتر است!'];
ayat[731]=[5,62,'وَتَرَىٰ كَثِيرًا مِّنْهُمْ يُسَارِعُونَ فِي الْإِثْمِ وَالْعُدْوَانِ وَأَكْلِهِمُ السُّحْتَ ۚ لَبِئْسَ مَا كَانُوا يَعْمَلُونَ','بسیاری از آنان را می‌بینی که در گناه و تعدّی، و خوردن مال حرام، شتاب می‌کنند! چه زشت است کاری که انجام می‌دادند!'];
ayat[732]=[5,63,'لَوْلَا يَنْهَاهُمُ الرَّبَّانِيُّونَ وَالْأَحْبَارُ عَن قَوْلِهِمُ الْإِثْمَ وَأَكْلِهِمُ السُّحْتَ ۚ لَبِئْسَ مَا كَانُوا يَصْنَعُونَ','چرا دانشمندان نصاری و علمای یهود، آنها را از سخنان گناه‌آمیز و خوردن مال حرام، نهی نمی‌کنند؟! چه زشت است عملی که انجام می‌دادند!'];
ayat[733]=[5,64,'وَقَالَتِ الْيَهُودُ يَدُ اللَّهِ مَغْلُولَةٌ ۚ غُلَّتْ أَيْدِيهِمْ وَلُعِنُوا بِمَا قَالُوا ۘ بَلْ يَدَاهُ مَبْسُوطَتَانِ يُنفِقُ كَيْفَ يَشَاءُ ۚ وَلَيَزِيدَنَّ كَثِيرًا مِّنْهُم مَّا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ طُغْيَانًا وَكُفْرًا ۚ وَأَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ إِلَىٰ يَوْمِ الْقِيَامَةِ ۚ كُلَّمَا أَوْقَدُوا نَارًا لِّلْحَرْبِ أَطْفَأَهَا اللَّهُ ۚ وَيَسْعَوْنَ فِي الْأَرْضِ فَسَادًا ۚ وَاللَّهُ لَا يُحِبُّ الْمُفْسِدِينَ','و یهود گفتند: «دست خدا (با زنجیر) بسته است.» دستهایشان بسته باد! و بخاطر این سخن، از رحمت (الهی) دور شوند! بلکه هر دو دست (قدرت) او، گشاده است؛ هرگونه بخواهد، می‌بخشد! ولی این آیات، که از طرف پروردگارت بر تو نازل شده، بر طغیان و کفر بسیاری از آنها می‌افزاید. و ما در میان آنها تا روز قیامت عداوت و دشمنی افکندیم. هر زمان آتش جنگی افروختند، خداوند آن را خاموش ساخت؛ و برای فساد در زمین، تلاش می‌کنند؛ و خداوند، مفسدان را دوست ندارد.'];
ayat[734]=[5,65,'وَلَوْ أَنَّ أَهْلَ الْكِتَابِ آمَنُوا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّئَاتِهِمْ وَلَأَدْخَلْنَاهُمْ جَنَّاتِ النَّعِيمِ','و اگر اهل کتاب ایمان بیاورند و تقوا پیشه کنند، گناهان آنها را می‌بخشیم؛ و آنها را در باغهای پرنعمت بهشت، وارد می‌سازیم.'];
ayat[735]=[5,66,'وَلَوْ أَنَّهُمْ أَقَامُوا التَّوْرَاةَ وَالْإِنجِيلَ وَمَا أُنزِلَ إِلَيْهِم مِّن رَّبِّهِمْ لَأَكَلُوا مِن فَوْقِهِمْ وَمِن تَحْتِ أَرْجُلِهِم ۚ مِّنْهُمْ أُمَّةٌ مُّقْتَصِدَةٌ ۖ وَكَثِيرٌ مِّنْهُمْ سَاءَ مَا يَعْمَلُونَ','و اگر آنان، تورات و انجیل و آنچه را از سوی پروردگارشان بر آنها نازل شده [= قرآن‌] برپا دارند، از آسمان و زمین، روزی خواهند خورد؛ جمعی از آنها، معتدل و میانه‌رو هستند، ولی بیشترشان اعمال بدی انجام می‌دهند.'];
ayat[736]=[5,67,'۞ يَا أَيُّهَا الرَّسُولُ بَلِّغْ مَا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ ۖ وَإِن لَّمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَتَهُ ۚ وَاللَّهُ يَعْصِمُكَ مِنَ النَّاسِ ۗ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ','ای پیامبر! آنچه از طرف پروردگارت بر تو نازل شده است، کاملاً (به مردم) برسان! و اگر نکنی، رسالت او را انجام نداده‌ای! خداوند تو را از (خطرات احتمالی) مردم، نگاه می‌دارد؛ و خداوند، جمعیّت کافران (لجوج) را هدایت نمی‌کند.'];
ayat[737]=[5,68,'قُلْ يَا أَهْلَ الْكِتَابِ لَسْتُمْ عَلَىٰ شَيْءٍ حَتَّىٰ تُقِيمُوا التَّوْرَاةَ وَالْإِنجِيلَ وَمَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُمْ ۗ وَلَيَزِيدَنَّ كَثِيرًا مِّنْهُم مَّا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ طُغْيَانًا وَكُفْرًا ۖ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكَافِرِينَ','ای اهل کتاب! شما هیچ آیین صحیحی ندارید، مگر اینکه تورات و انجیل و آنچه را از طرف پروردگارتان بر شما نازل شده است، برپا دارید. ولی آنچه بر تو از سوی پروردگارت نازل شده، (نه تنها مایه بیداری آنها نمی‌گردد، بلکه) بر طغیان و کفر بسیاری از آنها می‌افزاید. بنابر این، از این قوم کافر، (و مخالفت آنها،) غمگین مباش!'];
ayat[738]=[5,69,'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالصَّابِئُونَ وَالنَّصَارَىٰ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَعَمِلَ صَالِحًا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','آنها که ایمان آورده‌اند، و یهود و صابئان و مسیحیان، هرگاه به خداوند یگانه و روز جزا، ایمان بیاورند، و عمل صالح انجام دهند، نه ترسی بر آنهاست، و نه غمگین خواهند شد.'];
ayat[739]=[5,70,'لَقَدْ أَخَذْنَا مِيثَاقَ بَنِي إِسْرَائِيلَ وَأَرْسَلْنَا إِلَيْهِمْ رُسُلًا ۖ كُلَّمَا جَاءَهُمْ رَسُولٌ بِمَا لَا تَهْوَىٰ أَنفُسُهُمْ فَرِيقًا كَذَّبُوا وَفَرِيقًا يَقْتُلُونَ','ما از بنی اسرائیل پیمان گرفتیم؛ و رسولانی به سوی آنها فرستادیم؛ (ولی) هر زمان پیامبری حکمی بر خلاف هوسها و دلخواه آنها می‌آورد، عده‌ای را تکذیب می‌کردند؛ و عده‌ای را می‌کشتند.'];
ayat[740]=[5,71,'وَحَسِبُوا أَلَّا تَكُونَ فِتْنَةٌ فَعَمُوا وَصَمُّوا ثُمَّ تَابَ اللَّهُ عَلَيْهِمْ ثُمَّ عَمُوا وَصَمُّوا كَثِيرٌ مِّنْهُمْ ۚ وَاللَّهُ بَصِيرٌ بِمَا يَعْمَلُونَ','گمان کردند مجازاتی در کار نخواهد بود! از این‌رو (از دیدن حقایق و شنیدن سخنان حق،) نابینا و کر شدند؛ سپس (بیدار گشتند، و) خداوند توبه آنها را پذیرفت، دیگربار (در خواب غفلت فرو رفتند، و) بسیاری از آنها کور و کر شدند؛ و خداوند، به آنچه انجام می‌دهند، بیناست.'];
ayat[741]=[5,72,'لَقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ ۖ وَقَالَ الْمَسِيحُ يَا بَنِي إِسْرَائِيلَ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ ۖ إِنَّهُ مَن يُشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اللَّهُ عَلَيْهِ الْجَنَّةَ وَمَأْوَاهُ النَّارُ ۖ وَمَا لِلظَّالِمِينَ مِنْ أَنصَارٍ','آنها که گفتند: «خداوند همان مسیح بن مریم است»، بیقین کافر شدند، (با اینکه خود) مسیح گفت: ای بنی اسرائیل! خداوند یگانه را، که پروردگار من و شماست، پرستش کنید! زیرا هر کس شریکی برای خدا قرار دهد، خداوند بهشت را بر او حرام کرده است؛ و جایگاه او دوزخ است؛ و ستمکاران، یار و یاوری ندارند.'];
ayat[742]=[5,73,'لَّقَدْ كَفَرَ الَّذِينَ قَالُوا إِنَّ اللَّهَ ثَالِثُ ثَلَاثَةٍ ۘ وَمَا مِنْ إِلَٰهٍ إِلَّا إِلَٰهٌ وَاحِدٌ ۚ وَإِن لَّمْ يَنتَهُوا عَمَّا يَقُولُونَ لَيَمَسَّنَّ الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابٌ أَلِيمٌ','آنها که گفتند: «خداوند، یکی از سه خداست» (نیز) بیقین کافر شدند؛ معبودی جز معبود یگانه نیست؛ و اگر از آنچه می‌گویند دست بر ندارند، عذاب دردناکی به کافران آنها (که روی این عقیده ایستادگی کنند،) خواهد رسید.'];
ayat[743]=[5,74,'أَفَلَا يَتُوبُونَ إِلَى اللَّهِ وَيَسْتَغْفِرُونَهُ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','آیا به سوی خدا بازنمی‌گردند، و از او طلب آمرزش نمی‌کنند؟ (در حالی که) خداوند آمرزنده مهربان است.'];
ayat[744]=[5,75,'مَّا الْمَسِيحُ ابْنُ مَرْيَمَ إِلَّا رَسُولٌ قَدْ خَلَتْ مِن قَبْلِهِ الرُّسُلُ وَأُمُّهُ صِدِّيقَةٌ ۖ كَانَا يَأْكُلَانِ الطَّعَامَ ۗ انظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْآيَاتِ ثُمَّ انظُرْ أَنَّىٰ يُؤْفَكُونَ','مسیح فرزند مریم، فقط فرستاده (خدا) بود؛ پیش از وی نیز، فرستادگان دیگری بودند، مادرش، زن بسیار راستگویی بود؛ هر دو، غذا می‌خوردند؛ (با این حال، چگونه دعوی الوهیّت مسیح و پرستش مریم را دارید؟!) بنگر چگونه نشانه را برای آنها آشکار می‌سازیم! سپس بنگر چگونه از حق بازگردانده می‌شوند!'];
ayat[745]=[5,76,'قُلْ أَتَعْبُدُونَ مِن دُونِ اللَّهِ مَا لَا يَمْلِكُ لَكُمْ ضَرًّا وَلَا نَفْعًا ۚ وَاللَّهُ هُوَ السَّمِيعُ الْعَلِيمُ','بگو: «آیا جز خدا چیزی را می‌پرستید که مالک سود و زیان شما نیست؟! و خداوند، شنوا و داناست.»'];
ayat[746]=[5,77,'قُلْ يَا أَهْلَ الْكِتَابِ لَا تَغْلُوا فِي دِينِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُوا أَهْوَاءَ قَوْمٍ قَدْ ضَلُّوا مِن قَبْلُ وَأَضَلُّوا كَثِيرًا وَضَلُّوا عَن سَوَاءِ السَّبِيلِ','بگو: «ای اهل کتاب! در دین خود، غلوّ (و زیاده روی) نکنید! و غیر از حق نگویید! و از هوسهای جمعیّتی که پیشتر گمراه شدند و دیگران را گمراه کردند و از راه راست منحرف گشتند، پیروی ننمایید!»'];
ayat[747]=[5,78,'لُعِنَ الَّذِينَ كَفَرُوا مِن بَنِي إِسْرَائِيلَ عَلَىٰ لِسَانِ دَاوُودَ وَعِيسَى ابْنِ مَرْيَمَ ۚ ذَٰلِكَ بِمَا عَصَوا وَّكَانُوا يَعْتَدُونَ','کافران بنی اسرائیل، بر زبان داوود و عیسی بن مریم، لعن (و نفرین) شدند! این بخاطر آن بود که گناه کردند، و تجاوز می‌نمودند.'];
ayat[748]=[5,79,'كَانُوا لَا يَتَنَاهَوْنَ عَن مُّنكَرٍ فَعَلُوهُ ۚ لَبِئْسَ مَا كَانُوا يَفْعَلُونَ','آنها از اعمال زشتی که انجام می‌دادند، یکدیگر را نهی نمی‌کردند؛ چه بدکاری انجام می‌دادند!'];
ayat[749]=[5,80,'تَرَىٰ كَثِيرًا مِّنْهُمْ يَتَوَلَّوْنَ الَّذِينَ كَفَرُوا ۚ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ أَنفُسُهُمْ أَن سَخِطَ اللَّهُ عَلَيْهِمْ وَفِي الْعَذَابِ هُمْ خَالِدُونَ','بسیاری از آنها را می‌بینی که کافران (و بت‌پرستان) را دوست می‌دارند (و با آنها طرح دوستی می‌ریزند)؛ نفس (سرکش) آنها، چه بد اعمالی از پیش برای (معاد) آنها فرستاد! که نتیجه آن، خشم خداوند بود؛ و در عذاب (الهی) جاودانه خواهند ماند.'];
ayat[750]=[5,81,'وَلَوْ كَانُوا يُؤْمِنُونَ بِاللَّهِ وَالنَّبِيِّ وَمَا أُنزِلَ إِلَيْهِ مَا اتَّخَذُوهُمْ أَوْلِيَاءَ وَلَٰكِنَّ كَثِيرًا مِّنْهُمْ فَاسِقُونَ','و اگر به خدا و پیامبر (ص) و آنچه بر او نازل شده، ایمان می‌آوردند، (هرگز) آنان [= کافران‌] را به دوستی اختیار نمی‌کردند؛ ولی بسیاری از آنها فاسقند.'];
ayat[751]=[5,82,'۞ لَتَجِدَنَّ أَشَدَّ النَّاسِ عَدَاوَةً لِّلَّذِينَ آمَنُوا الْيَهُودَ وَالَّذِينَ أَشْرَكُوا ۖ وَلَتَجِدَنَّ أَقْرَبَهُم مَّوَدَّةً لِّلَّذِينَ آمَنُوا الَّذِينَ قَالُوا إِنَّا نَصَارَىٰ ۚ ذَٰلِكَ بِأَنَّ مِنْهُمْ قِسِّيسِينَ وَرُهْبَانًا وَأَنَّهُمْ لَا يَسْتَكْبِرُونَ','بطور مسلّم، دشمنترین مردم نسبت به مؤمنان را، یهود و مشرکان خواهی یافت؛ و نزدیکترین دوستان به مؤمنان را کسانی می‌یابی که می‌گویند: «ما نصاری هستیم»؛ این بخاطر آن است که در میان آنها، افرادی عالم و تارک دنیا هستند؛ و آنها (در برابر حق) تکبّر نمی‌ورزند.'];
ayat[752]=[5,83,'وَإِذَا سَمِعُوا مَا أُنزِلَ إِلَى الرَّسُولِ تَرَىٰ أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوا مِنَ الْحَقِّ ۖ يَقُولُونَ رَبَّنَا آمَنَّا فَاكْتُبْنَا مَعَ الشَّاهِدِينَ','و هر زمان آیاتی را که بر پیامبر (اسلام) نازل شده بشنوند، چشمهای آنها را می‌بینی که (از شوق،) اشک می‌ریزد، بخاطر حقیقتی که دریافته‌اند؛ آنها می‌گویند: «پروردگارا! ایمان آوردیم؛ پس ما را با گواهان (و شاهدان حق، در زمره یاران محمد) بنویس!'];
ayat[753]=[5,84,'وَمَا لَنَا لَا نُؤْمِنُ بِاللَّهِ وَمَا جَاءَنَا مِنَ الْحَقِّ وَنَطْمَعُ أَن يُدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصَّالِحِينَ','چرا ما به خدا و آنچه از حق به ما رسیده است، ایمان نیاوریم، در حالی که آرزو داریم پروردگارمان ما را در زمره صالحان قرار دهد؟!»'];
ayat[754]=[5,85,'فَأَثَابَهُمُ اللَّهُ بِمَا قَالُوا جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ الْمُحْسِنِينَ','خداوند بخاطر این سخن، به آنها باغهایی از بهشت پاداش داد که از زیر درختانش، نهرها جاری است؛ جاودانه در آن خواهند ماند؛ و این است جزای نیکوکاران!'];
ayat[755]=[5,86,'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ','و کسانی که کافر شدند و آیات ما را تکذیب کردند، همانها اهل دوزخند.'];
ayat[756]=[5,87,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُحَرِّمُوا طَيِّبَاتِ مَا أَحَلَّ اللَّهُ لَكُمْ وَلَا تَعْتَدُوا ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْمُعْتَدِينَ','ای کسانی که ایمان آورده‌اید! چیزهای پاکیزه را که خداوند برای شما حلال کرده است، حرام نکنید! و از حدّ، تجاوز ننمایید! زیرا خداوند متجاوزان را دوست نمی‌دارد.'];
ayat[757]=[5,88,'وَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلَالًا طَيِّبًا ۚ وَاتَّقُوا اللَّهَ الَّذِي أَنتُم بِهِ مُؤْمِنُونَ','و از نعمتهای حلال و پاکیزه‌ای که خداوند به شما روزی داده است، بخورید! و از (مخالفت) خداوندی که به او ایمان دارید، بپرهیزید!'];
ayat[758]=[5,89,'لَا يُؤَاخِذُكُمُ اللَّهُ بِاللَّغْوِ فِي أَيْمَانِكُمْ وَلَٰكِن يُؤَاخِذُكُم بِمَا عَقَّدتُّمُ الْأَيْمَانَ ۖ فَكَفَّارَتُهُ إِطْعَامُ عَشَرَةِ مَسَاكِينَ مِنْ أَوْسَطِ مَا تُطْعِمُونَ أَهْلِيكُمْ أَوْ كِسْوَتُهُمْ أَوْ تَحْرِيرُ رَقَبَةٍ ۖ فَمَن لَّمْ يَجِدْ فَصِيَامُ ثَلَاثَةِ أَيَّامٍ ۚ ذَٰلِكَ كَفَّارَةُ أَيْمَانِكُمْ إِذَا حَلَفْتُمْ ۚ وَاحْفَظُوا أَيْمَانَكُمْ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ لَعَلَّكُمْ تَشْكُرُونَ','خداوند شما را بخاطر سوگندهای بیهوده (و خالی از اراده،) مؤاخذه نمی‌کند؛ ولی در برابر سوگندهایی که (از روی اراده) محکم کرده‌اید، مؤاخذه می‌نماید. کفاره این‌گونه قسمها، اطعام ده نفر مستمند، از غذاهای معمولی است که به خانواده خود می‌دهید؛ یا لباس پوشاندن بر آن ده نفر؛ و یا آزاد کردن یک برده؛ و کسی که هیچ کدام از اینها را نیابد، سه روز روزه می‌گیرد؛ این، کفاره سوگندهای شماست به هنگامی که سوگند یاد می‌کنید (و مخالفت می‌نمایید). و سوگندهای خود را حفظ کنید (و نشکنید!) خداوند آیات خود را این چنین برای شما بیان می‌کند، شاید شکر او را بجا آورید!'];
ayat[759]=[5,90,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْأَنصَابُ وَالْأَزْلَامُ رِجْسٌ مِّنْ عَمَلِ الشَّيْطَانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ','ای کسانی که ایمان آورده‌اید! شراب و قمار و بتها و ازلام [= نوعی بخت‌آزمایی]، پلید و از عمل شیطان است، از آنها دوری کنید تا رستگار شوید!'];
ayat[760]=[5,91,'إِنَّمَا يُرِيدُ الشَّيْطَانُ أَن يُوقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَاءَ فِي الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَن ذِكْرِ اللَّهِ وَعَنِ الصَّلَاةِ ۖ فَهَلْ أَنتُم مُّنتَهُونَ','شیطان می‌خواهد به وسیله شراب و قمار، در میان شما عداوت و کینه ایجاد کند، و شما را از یاد خدا و از نماز بازدارد. آیا (با این همه زیان و فساد، و با این نهی اکید،) خودداری خواهید کرد؟!'];
ayat[761]=[5,92,'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَاحْذَرُوا ۚ فَإِن تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّمَا عَلَىٰ رَسُولِنَا الْبَلَاغُ الْمُبِينُ','اطاعت خدا و اطاعت پیامبر کنید! و (از مخالفت فرمان او) بترسید! و اگر روی برگردانید، (مستحق مجازات خواهید بود؛ و) بدانید بر پیامبر ما، جز ابلاغ آشکار، چیز دیگری نیست (و این وظیفه را در برابر شما، انجام داده است).'];
ayat[762]=[5,93,'لَيْسَ عَلَى الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جُنَاحٌ فِيمَا طَعِمُوا إِذَا مَا اتَّقَوا وَّآمَنُوا وَعَمِلُوا الصَّالِحَاتِ ثُمَّ اتَّقَوا وَّآمَنُوا ثُمَّ اتَّقَوا وَّأَحْسَنُوا ۗ وَاللَّهُ يُحِبُّ الْمُحْسِنِينَ','بر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، گناهی در آنچه خورده‌اند نیست؛ (و نسبت به نوشیدن شراب، قبل از نزول حکم تحریم، مجازات نمی‌شوند؛) اگر تقوا پیشه کنند، و ایمان بیاورند، و اعمال صالح انجام دهند؛ سپس تقوا پیشه کنند و ایمان آورند؛ سپس تقوا پیشه کنند و نیکی نمایند. و خداوند، نیکوکاران را دوست می‌دارد.'];
ayat[763]=[5,94,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَيَبْلُوَنَّكُمُ اللَّهُ بِشَيْءٍ مِّنَ الصَّيْدِ تَنَالُهُ أَيْدِيكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللَّهُ مَن يَخَافُهُ بِالْغَيْبِ ۚ فَمَنِ اعْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُ عَذَابٌ أَلِيمٌ','ای کسانی که ایمان آورده‌اید! خداوند شما را به چیزی از شکار که (به نزدیکی شما می‌آید، بطوری که) دستها و نیزه‌هایتان به آن می‌رسد، می‌آزماید؛ تا معلوم شود چه کسی باایمان به غیب، از خدا می‌ترسد؛ و هر کس بعد از آن تجاوز کند، مجازات دردناکی خواهد داشت.'];
ayat[764]=[5,95,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَقْتُلُوا الصَّيْدَ وَأَنتُمْ حُرُمٌ ۚ وَمَن قَتَلَهُ مِنكُم مُّتَعَمِّدًا فَجَزَاءٌ مِّثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهِ ذَوَا عَدْلٍ مِّنكُمْ هَدْيًا بَالِغَ الْكَعْبَةِ أَوْ كَفَّارَةٌ طَعَامُ مَسَاكِينَ أَوْ عَدْلُ ذَٰلِكَ صِيَامًا لِّيَذُوقَ وَبَالَ أَمْرِهِ ۗ عَفَا اللَّهُ عَمَّا سَلَفَ ۚ وَمَنْ عَادَ فَيَنتَقِمُ اللَّهُ مِنْهُ ۗ وَاللَّهُ عَزِيزٌ ذُو انتِقَامٍ','ای کسانی که ایمان آورده‌اید! در حال احرام، شکار نکنید، و هر کس از شما عمداً آن را به قتل برساند، باید کفاره‌ای معادل آن از چهارپایان بدهد؛ کفاره‌ای که دو نفر عادل از شما، معادل بودن آن را تصدیق کنند؛ و به صورت قربانی به (حریم) کعبه برسد؛ یا (به جای قربانی،) اطعام مستمندان کند؛ یا معادل آن، روزه بگیرد، تا کیفر کار خود را بچشد. خداوند گذشته را عفو کرده، ولی هر کس تکرار کند، خدا از او انتقام می‌گیرد؛ و خداوند، توانا و صاحب انتقام است.'];
ayat[765]=[5,96,'أُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهُ مَتَاعًا لَّكُمْ وَلِلسَّيَّارَةِ ۖ وَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا ۗ وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ','صید دریا و طعام آن برای شما و کاروانیان حلال است؛ تا (در حال احرام) از آن بهره‌مند شوید؛ ولی مادام که محرم هستید، شکار صحرا برای شما حرام است؛ و از (نافرمانی) خدایی که به سوی او محشور می‌شوید، بترسید!'];
ayat[766]=[5,97,'۞ جَعَلَ اللَّهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيَامًا لِّلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَائِدَ ۚ ذَٰلِكَ لِتَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَأَنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ','خداوند، کعبه -بیت الحرام- را وسیله‌ای برای استواری و سامان بخشیدن به کار مردم قرار داده؛ و همچنین ماه حرام، و قربانیهای بی‌نشان، و قربانیهای نشاندار را؛ این‌گونه احکام (حساب شده و دقیق،) بخاطر آن است که بدانید خداوند، آنچه در آسمانها و آنچه در زمین است، می‌داند؛ و خدا به هر چیزی داناست.'];
ayat[767]=[5,98,'اعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ وَأَنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','بدانید خدا دارای مجازات شدید، و (در عین حال) آمرزنده و مهربان است.'];
ayat[768]=[5,99,'مَّا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ ۗ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ','پیامبر وظیفه‌ای جز رسانیدن پیام (الهی) ندارد؛ (و مسؤول اعمال شما نیست). و خداوند آنچه را آشکار، و آنچه را پنهان می‌دارید می‌داند.'];
ayat[769]=[5,100,'قُل لَّا يَسْتَوِي الْخَبِيثُ وَالطَّيِّبُ وَلَوْ أَعْجَبَكَ كَثْرَةُ الْخَبِيثِ ۚ فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ لَعَلَّكُمْ تُفْلِحُونَ','بگو: «(هیچ‌گاه) ناپاک و پاک مساوی نیستند؛ هر چند فزونی ناپاکها، تو را به شگفتی اندازد! از (مخالفت) خدا بپرهیزید ای صاحبان خرد، شاید رستگار شوید!'];
ayat[770]=[5,101,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَسْأَلُوا عَنْ أَشْيَاءَ إِن تُبْدَ لَكُمْ تَسُؤْكُمْ وَإِن تَسْأَلُوا عَنْهَا حِينَ يُنَزَّلُ الْقُرْآنُ تُبْدَ لَكُمْ عَفَا اللَّهُ عَنْهَا ۗ وَاللَّهُ غَفُورٌ حَلِيمٌ','ای کسانی که ایمان آورده‌اید! از چیزهایی نپرسید که اگر برای شما آشکار گردد، شما را ناراحت می‌کند! و اگر به هنگام نزول قرآن، از آنها سؤال کنید، برای شما آشکار می‌شود؛ خداوند آنها را بخشیده (و نادیده گرفته) است. و خداوند، آمرزنده و بردبار است.'];
ayat[771]=[5,102,'قَدْ سَأَلَهَا قَوْمٌ مِّن قَبْلِكُمْ ثُمَّ أَصْبَحُوا بِهَا كَافِرِينَ','جمعی از پیشینیان شما، از آن سئوال کردند؛ و سپس با آن به مخالفت برخاستند. (ممکن است شما هم چنین سرنوشتی پیدا کنید.)'];
ayat[772]=[5,103,'مَا جَعَلَ اللَّهُ مِن بَحِيرَةٍ وَلَا سَائِبَةٍ وَلَا وَصِيلَةٍ وَلَا حَامٍ ۙ وَلَٰكِنَّ الَّذِينَ كَفَرُوا يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ ۖ وَأَكْثَرُهُمْ لَا يَعْقِلُونَ','خداوند هیچ‌گونه «بحیره» و «سائبه» و «وصیله» و «حام» قرار نداده است [اشاره به چهار نوع از حیوانات اهلی است که در زمان جاهلیت، استفاده از آنها را بعللی حرام می‌دانستند؛ و این بدعت، در اسلام ممنوع شد.] ولی کسانی که کافر شدند، بر خدا دروغ می‌بندند؛ و بیشتر آنها نمی‌فهمند!'];
ayat[773]=[5,104,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا إِلَىٰ مَا أَنزَلَ اللَّهُ وَإِلَى الرَّسُولِ قَالُوا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ آبَاءَنَا ۚ أَوَلَوْ كَانَ آبَاؤُهُمْ لَا يَعْلَمُونَ شَيْئًا وَلَا يَهْتَدُونَ','و هنگامی که به آنها گفته شود: «به سوی آنچه خدا نازل کرده، و به سوی پیامبر بیایید!»، می‌گویند: «آنچه از پدران خود یافته‌ایم، ما را بس است!»؛ آیا اگر پدران آنها چیزی نمی‌دانستند، و هدایت نیافته بودند (باز از آنها پیروی می‌کنند)؟!'];
ayat[774]=[5,105,'يَا أَيُّهَا الَّذِينَ آمَنُوا عَلَيْكُمْ أَنفُسَكُمْ ۖ لَا يَضُرُّكُم مَّن ضَلَّ إِذَا اهْتَدَيْتُمْ ۚ إِلَى اللَّهِ مَرْجِعُكُمْ جَمِيعًا فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','ای کسانی که ایمان آورده‌اید! مراقب خود باشید! اگر شما هدایت یافته‌اید، گمراهی کسانی که گمراه شده‌اند، به شما زیانی نمی‌رساند. بازگشت همه شما به سوی خداست؛ و شما را از آنچه عمل می‌کردید، آگاه می‌سازد.'];
ayat[775]=[5,106,'يَا أَيُّهَا الَّذِينَ آمَنُوا شَهَادَةُ بَيْنِكُمْ إِذَا حَضَرَ أَحَدَكُمُ الْمَوْتُ حِينَ الْوَصِيَّةِ اثْنَانِ ذَوَا عَدْلٍ مِّنكُمْ أَوْ آخَرَانِ مِنْ غَيْرِكُمْ إِنْ أَنتُمْ ضَرَبْتُمْ فِي الْأَرْضِ فَأَصَابَتْكُم مُّصِيبَةُ الْمَوْتِ ۚ تَحْبِسُونَهُمَا مِن بَعْدِ الصَّلَاةِ فَيُقْسِمَانِ بِاللَّهِ إِنِ ارْتَبْتُمْ لَا نَشْتَرِي بِهِ ثَمَنًا وَلَوْ كَانَ ذَا قُرْبَىٰ ۙ وَلَا نَكْتُمُ شَهَادَةَ اللَّهِ إِنَّا إِذًا لَّمِنَ الْآثِمِينَ','ای کسانی که ایمان آورده‌اید! هنگامی که مرگ یکی از شما فرا رسد، در موقع وصیت باید از میان شما، دو نفر عادل را به شهادت بطلبد؛ یا اگر مسافرت کردید، و مصیبت مرگ شما فرا رسید، (و در آن جا مسلمانی نیافتید،) دو نفر از غیر خودتان را به گواهی بطلبید، و اگر به هنگام ادای شهادت، در صدق آنها شک کردید، آنها را بعد از نماز نگاه می‌دارید تا سوگند یاد کنند که: «ما حاضر نیستیم حق را به چیزی بفروشیم، هر چند در مورد خویشاوندان ما باشد! و شهادت الهی را کتمان نمی‌کنیم، که از گناهکاران خواهیم بود!»'];
ayat[776]=[5,107,'فَإِنْ عُثِرَ عَلَىٰ أَنَّهُمَا اسْتَحَقَّا إِثْمًا فَآخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ الَّذِينَ اسْتَحَقَّ عَلَيْهِمُ الْأَوْلَيَانِ فَيُقْسِمَانِ بِاللَّهِ لَشَهَادَتُنَا أَحَقُّ مِن شَهَادَتِهِمَا وَمَا اعْتَدَيْنَا إِنَّا إِذًا لَّمِنَ الظَّالِمِينَ','و اگر اطلاعی حاصل شود که آن دو، مرتکب گناهی شده‌اند (و حق را کتمان کرده‌اند)، دو نفر از کسانی که نسبت به میت، اولی هستند، به جای آنها قرار می‌گیرند، و به خدا سوگند یاد می‌کنند که: «گواهی ما، از گواهی آن دو، به حق نزدیکتر است! و ما تجاوزی نکرده‌ایم؛ که اگر چنین کرده باشیم، از ظالمان خواهیم بود!»'];
ayat[777]=[5,108,'ذَٰلِكَ أَدْنَىٰ أَن يَأْتُوا بِالشَّهَادَةِ عَلَىٰ وَجْهِهَا أَوْ يَخَافُوا أَن تُرَدَّ أَيْمَانٌ بَعْدَ أَيْمَانِهِمْ ۗ وَاتَّقُوا اللَّهَ وَاسْمَعُوا ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ','این کار، نزدیکتر است به اینکه گواهی بحق دهند، (و از خدا بترسند،) و یا (از مردم) بترسند که (دروغشان فاش گردد، و) سوگندهایی جای سوگندهای آنها را بگیرد. از (مخالفت) خدا بپرهیزید، و گوش فرا دهید! و خداوند، جمعیّت فاسقان را هدایت نمی‌کند.'];
ayat[778]=[5,109,'۞ يَوْمَ يَجْمَعُ اللَّهُ الرُّسُلَ فَيَقُولُ مَاذَا أُجِبْتُمْ ۖ قَالُوا لَا عِلْمَ لَنَا ۖ إِنَّكَ أَنتَ عَلَّامُ الْغُيُوبِ','(از) روزی (بترسید) که خداوند، پیامبران را جمع می‌کند، و به آنها می‌گوید: «(در برابر دعوت شما،) چه پاسخی به شما داده شد؟»، می‌گویند: «ما چیزی نمی‌دانیم؛ تو خود، از همه اسرار نهان آگاهی.»'];
ayat[779]=[5,110,'إِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِي عَلَيْكَ وَعَلَىٰ وَالِدَتِكَ إِذْ أَيَّدتُّكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النَّاسَ فِي الْمَهْدِ وَكَهْلًا ۖ وَإِذْ عَلَّمْتُكَ الْكِتَابَ وَالْحِكْمَةَ وَالتَّوْرَاةَ وَالْإِنجِيلَ ۖ وَإِذْ تَخْلُقُ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ بِإِذْنِي فَتَنفُخُ فِيهَا فَتَكُونُ طَيْرًا بِإِذْنِي ۖ وَتُبْرِئُ الْأَكْمَهَ وَالْأَبْرَصَ بِإِذْنِي ۖ وَإِذْ تُخْرِجُ الْمَوْتَىٰ بِإِذْنِي ۖ وَإِذْ كَفَفْتُ بَنِي إِسْرَائِيلَ عَنكَ إِذْ جِئْتَهُم بِالْبَيِّنَاتِ فَقَالَ الَّذِينَ كَفَرُوا مِنْهُمْ إِنْ هَٰذَا إِلَّا سِحْرٌ مُّبِينٌ','(به خاطر بیاور) هنگامی را که خداوند به عیسی بن مریم گفت: «یاد کن نعمتی را که به تو و مادرت بخشیدم! زمانی که تو را با» روح القدس «تقویت کردم؛ که در گاهواره و به هنگام بزرگی، با مردم سخن می‌گفتی؛ و هنگامی که کتاب و حکمت و تورات و انجیل را به تو آموختم؛ و هنگامی که به فرمان من، از گل چیزی بصورت پرنده می‌ساختی، و در آن می‌دمیدی، و به فرمان من، پرنده‌ای می‌شد؛ و کور مادرزاد، و مبتلا به بیماری پیسی را به فرمان من، شفا می‌دادی؛ و مردگان را (نیز) به فرمان من زنده می‌کردی؛ و هنگامی که بنی اسرائیل را از آسیب رساندن به تو، بازداشتم؛ در آن موقع که دلایل روشن برای آنها آوردی، ولی جمعی از کافران آنها گفتند: اینها جز سحر آشکار نیست!»'];
ayat[780]=[5,111,'وَإِذْ أَوْحَيْتُ إِلَى الْحَوَارِيِّينَ أَنْ آمِنُوا بِي وَبِرَسُولِي قَالُوا آمَنَّا وَاشْهَدْ بِأَنَّنَا مُسْلِمُونَ','و (به یاد آور) زمانی را که به حواریون وحی فرستادم که: «به من و فرستاده من، ایمان بیاورید!» آنها گفتند: «ایمان آوردیم، و گواه باش که ما مسلمانیم!»'];
ayat[781]=[5,112,'إِذْ قَالَ الْحَوَارِيُّونَ يَا عِيسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيعُ رَبُّكَ أَن يُنَزِّلَ عَلَيْنَا مَائِدَةً مِّنَ السَّمَاءِ ۖ قَالَ اتَّقُوا اللَّهَ إِن كُنتُم مُّؤْمِنِينَ','در آن هنگام که حواریون گفتند: «ای عیسی بن مریم! آیا پروردگارت می‌تواند مائده‌ای از آسمان بر ما نازل کند؟» او (در پاسخ) گفت: «از خدا بپرهیزید اگر با ایمان هستید!»'];
ayat[782]=[5,113,'قَالُوا نُرِيدُ أَن نَّأْكُلَ مِنْهَا وَتَطْمَئِنَّ قُلُوبُنَا وَنَعْلَمَ أَن قَدْ صَدَقْتَنَا وَنَكُونَ عَلَيْهَا مِنَ الشَّاهِدِينَ','گفتند: «(ما نظر بدی نداریم،) می‌خواهیم از آن بخوریم، و دلهای ما (به رسالت تو) مطمئن گردد؛ و بدانیم به ما راست گفته‌ای؛ و بر آن، گواه باشیم.»'];
ayat[783]=[5,114,'قَالَ عِيسَى ابْنُ مَرْيَمَ اللَّهُمَّ رَبَّنَا أَنزِلْ عَلَيْنَا مَائِدَةً مِّنَ السَّمَاءِ تَكُونُ لَنَا عِيدًا لِّأَوَّلِنَا وَآخِرِنَا وَآيَةً مِّنكَ ۖ وَارْزُقْنَا وَأَنتَ خَيْرُ الرَّازِقِينَ','عیسی بن مریم عرض کرد: «خداوندا! پروردگارا! از آسمان مائده‌ای بر ما بفرست! تا برای اول و آخر ما، عیدی باشد، و نشانه‌ای از تو؛ و به ما روزی ده! تو بهترین روزی دهندگانی!»'];
ayat[784]=[5,115,'قَالَ اللَّهُ إِنِّي مُنَزِّلُهَا عَلَيْكُمْ ۖ فَمَن يَكْفُرْ بَعْدُ مِنكُمْ فَإِنِّي أُعَذِّبُهُ عَذَابًا لَّا أُعَذِّبُهُ أَحَدًا مِّنَ الْعَالَمِينَ','خداوند (دعای او را مستجاب کرد؛ و) فرمود: «من آن را بر شما نازل می‌کنم؛ ولی هر کس از شما بعد از آن کافر گردد (و راه انکار پوید)، او را مجازاتی می‌کنم که احدی از جهانیان را چنان مجازات نکرده باشم!»'];
ayat[785]=[5,116,'وَإِذْ قَالَ اللَّهُ يَا عِيسَى ابْنَ مَرْيَمَ أَأَنتَ قُلْتَ لِلنَّاسِ اتَّخِذُونِي وَأُمِّيَ إِلَٰهَيْنِ مِن دُونِ اللَّهِ ۖ قَالَ سُبْحَانَكَ مَا يَكُونُ لِي أَنْ أَقُولَ مَا لَيْسَ لِي بِحَقٍّ ۚ إِن كُنتُ قُلْتُهُ فَقَدْ عَلِمْتَهُ ۚ تَعْلَمُ مَا فِي نَفْسِي وَلَا أَعْلَمُ مَا فِي نَفْسِكَ ۚ إِنَّكَ أَنتَ عَلَّامُ الْغُيُوبِ','و آنگاه که خداوند به عیسی بن مریم می‌گوید: «آیا تو به مردم گفتی که من و مادرم را بعنوان دو معبود غیر از خدا انتخاب کنید؟!»، او می‌گوید: «منزهی تو! من حق ندارم آنچه را که شایسته من نیست، بگویم! اگر چنین سخنی را گفته باشم، تو می‌دانی! تو از آنچه در روح و جان من است، آگاهی؛ و من از آنچه در ذات (پاک) توست، آگاه نیستم! بیقین تو از تمام اسرار و پنهانیها باخبری.'];
ayat[786]=[5,117,'مَا قُلْتُ لَهُمْ إِلَّا مَا أَمَرْتَنِي بِهِ أَنِ اعْبُدُوا اللَّهَ رَبِّي وَرَبَّكُمْ ۚ وَكُنتُ عَلَيْهِمْ شَهِيدًا مَّا دُمْتُ فِيهِمْ ۖ فَلَمَّا تَوَفَّيْتَنِي كُنتَ أَنتَ الرَّقِيبَ عَلَيْهِمْ ۚ وَأَنتَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ','من، جز آنچه مرا به آن فرمان دادی، چیزی به آنها نگفتم؛ (به آنها گفتم:) خداوندی را بپرستید که پروردگار من و پروردگار شماست! و تا زمانی که در میان آنها بودم، مراقب و گواهشان بودم؛ ولی هنگامی که مرا از میانشان برگرفتی، تو خود مراقب آنها بودی؛ و تو بر هر چیز، گواهی!'];
ayat[787]=[5,118,'إِن تُعَذِّبْهُمْ فَإِنَّهُمْ عِبَادُكَ ۖ وَإِن تَغْفِرْ لَهُمْ فَإِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ','(با این حال،) اگر آنها را مجازات کنی، بندگان تواند. (و قادر به فرار از مجازات تو نیستند)؛ و اگر آنان را ببخشی، توانا و حکیمی! (نه کیفر تو نشانه بی‌حکمتی است، و نه بخشش تو نشانه ضعف!)»'];
ayat[788]=[5,119,'قَالَ اللَّهُ هَٰذَا يَوْمُ يَنفَعُ الصَّادِقِينَ صِدْقُهُمْ ۚ لَهُمْ جَنَّاتٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ رَّضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ','خداوند می‌گوید: «امروز، روزی است که راستی راستگویان، به آنها سود می‌بخشد؛ برای آنها باغهایی از بهشت است که نهرها از زیر (درختان) آن می‌گذرد، و تا ابد، جاودانه در آن می‌مانند؛ هم خداوند از آنها خشنود است، و هم آنها از خدا خشنودند؛ این، رستگاری بزرگ است!»'];
ayat[789]=[5,120,'لِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا فِيهِنَّ ۚ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','حکومت آسمانها و زمین و آنچه در آنهاست، از آن خداست؛ و او بر هر چیزی تواناست.'];
ayat[790]=[6,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَجَعَلَ الظُّلُمَاتِ وَالنُّورَ ۖ ثُمَّ الَّذِينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ','ستایش برای خداوندی است که آسمانها و زمین را آفرید، و ظلمتها و نور را پدید آورد؛ اما کافران برای پروردگار خود، شریک و شبیه قرارمی‌دهند (با اینکه دلایل توحید و یگانگی او، در آفرینش جهان آشکار است)!'];
ayat[791]=[6,2,'هُوَ الَّذِي خَلَقَكُم مِّن طِينٍ ثُمَّ قَضَىٰ أَجَلًا ۖ وَأَجَلٌ مُّسَمًّى عِندَهُ ۖ ثُمَّ أَنتُمْ تَمْتَرُونَ','او کسی است که شما را از گل آفرید؛ سپس مدتی مقرّر داشت (تا انسان تکامل یابد)؛ و اجل حتمی نزد اوست (و فقط او از آن آگاه است). با این همه، شما (مشرکان در توحید و یگانگی و قدرت او،) تردید می‌کنید!'];
ayat[792]=[6,3,'وَهُوَ اللَّهُ فِي السَّمَاوَاتِ وَفِي الْأَرْضِ ۖ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُونَ','اوست خداوند در آسمانها و در زمین؛ پنهان و آشکار شما را می‌داند؛ و از آنچه (انجام می‌دهید و) به دست می‌آورید، با خبر است.'];
ayat[793]=[6,4,'وَمَا تَأْتِيهِم مِّنْ آيَةٍ مِّنْ آيَاتِ رَبِّهِمْ إِلَّا كَانُوا عَنْهَا مُعْرِضِينَ','هیچ نشانه و آیه‌ای از آیات پروردگارشان برای آنان نمی‌آید، مگر اینکه از آن رویگردان می‌شوند!'];
ayat[794]=[6,5,'فَقَدْ كَذَّبُوا بِالْحَقِّ لَمَّا جَاءَهُمْ ۖ فَسَوْفَ يَأْتِيهِمْ أَنبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ','آنان، حق را هنگامی که سراغشان آمد، تکذیب کردند! ولی بزودی خبر آنچه را به باد مسخره می‌گرفتند، به آنان می‌رسد؛ (و از نتایج کار خود، آگاه می‌شوند).'];
ayat[795]=[6,6,'أَلَمْ يَرَوْا كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّن قَرْنٍ مَّكَّنَّاهُمْ فِي الْأَرْضِ مَا لَمْ نُمَكِّن لَّكُمْ وَأَرْسَلْنَا السَّمَاءَ عَلَيْهِم مِّدْرَارًا وَجَعَلْنَا الْأَنْهَارَ تَجْرِي مِن تَحْتِهِمْ فَأَهْلَكْنَاهُم بِذُنُوبِهِمْ وَأَنشَأْنَا مِن بَعْدِهِمْ قَرْنًا آخَرِينَ','آیا ندیدند چقدر از اقوام پیشین را هلاک کردیم؟! اقوامی که (از شما نیرومندتر بودند؛ و) قدرتهایی به آنها داده بودیم که به شما ندادیم؛ بارانهای پی‌درپی برای آنها فرستادیم؛ و از زیر (آبادیهای) آنها، نهرها را جاری ساختیم؛ (اما هنگامی که سرکشی و طغیان کردند،) آنان را بخاطر گناهانشان نابود کردیم؛ و جمعیت دیگری بعد از آنان پدید آوردیم.'];
ayat[796]=[6,7,'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتَابًا فِي قِرْطَاسٍ فَلَمَسُوهُ بِأَيْدِيهِمْ لَقَالَ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا سِحْرٌ مُّبِينٌ','(حتّی) اگر ما نامه‌ای روی صفحه‌ای بر تو نازل کنیم، و (علاوه بر دیدن و خواندن،) آن را با دستهای خود لمس کنند، باز کافران می‌گویند: «این، چیزی جز یک سحر آشکار نیست»!'];
ayat[797]=[6,8,'وَقَالُوا لَوْلَا أُنزِلَ عَلَيْهِ مَلَكٌ ۖ وَلَوْ أَنزَلْنَا مَلَكًا لَّقُضِيَ الْأَمْرُ ثُمَّ لَا يُنظَرُونَ','(از بهانه‌های آنها این بود که) گفتند: «چرا فرشته‌ای بر او نازل نشده (تا او را در دعوت مردم به سوی خدا همراهی کند؟!)» ولی اگر فرشته‌ای بفرستیم، (و موضوع، جنبه حسی و شهود پیدا کند،) کار تمام می‌شود؛ (یعنی اگر مخالفت کنند،) دیگر به آنها مهلت داده نخواهد شد (و همه هلاک می‌شوند).'];
ayat[798]=[6,9,'وَلَوْ جَعَلْنَاهُ مَلَكًا لَّجَعَلْنَاهُ رَجُلًا وَلَلَبَسْنَا عَلَيْهِم مَّا يَلْبِسُونَ','و اگر او را فرشته‌ای قرارمی‌دادیم، حتماً وی را بصورت انسانی درمی‌آوردیم؛ (باز به پندار آنان،) کار را بر آنها مشتبه می‌ساختیم؛ همان‌طور که آنها کار را بر دیگران مشتبه می‌سازند!'];
ayat[799]=[6,10,'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّن قَبْلِكَ فَحَاقَ بِالَّذِينَ سَخِرُوا مِنْهُم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','(با این حال، نگران نباش!) جمعی از پیامبران پیش از تو را استهزا کردند؛ امّا سرانجام، آنچه را مسخره می‌کردند، دامانشان را می‌گرفت؛ (و عذاب الهی بر آنها فرود آمد).'];
ayat[800]=[6,11,'قُلْ سِيرُوا فِي الْأَرْضِ ثُمَّ انظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ','بگو: «روی زمین گردش کنید! سپس بنگرید سرانجام تکذیب‌کنندگان آیات الهی چه شد؟!»'];
ayat[801]=[6,12,'قُل لِّمَن مَّا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ قُل لِّلَّهِ ۚ كَتَبَ عَلَىٰ نَفْسِهِ الرَّحْمَةَ ۚ لَيَجْمَعَنَّكُمْ إِلَىٰ يَوْمِ الْقِيَامَةِ لَا رَيْبَ فِيهِ ۚ الَّذِينَ خَسِرُوا أَنفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ','بگو: «آنچه در آسمانها و زمین است، از آن کیست؟» بگو: «از آن خداست؛ رحمت (و بخشش) را بر خود، حتم کرده؛ (و به همین دلیل،) بطور قطع همه شما را در روز قیامت، که در آن شکّ و تردیدی نیست، گرد خواهد آورد. (آری،) فقط کسانی که سرمایه‌های وجود خویش را از دست داده و گرفتار خسران شدند، ایمان نمی‌آورند.'];
ayat[802]=[6,13,'۞ وَلَهُ مَا سَكَنَ فِي اللَّيْلِ وَالنَّهَارِ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ','و برای اوست آنچه در شب و روز قرار دارد؛ و او، شنوا و داناست.'];
ayat[803]=[6,14,'قُلْ أَغَيْرَ اللَّهِ أَتَّخِذُ وَلِيًّا فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُ ۗ قُلْ إِنِّي أُمِرْتُ أَنْ أَكُونَ أَوَّلَ مَنْ أَسْلَمَ ۖ وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ','بگو: «آیا غیر خدا را ولیّ خود انتخاب کنم؟! (خدایی) که آفریننده آسمانها و زمین است؛ اوست که روزی می‌دهد، و از کسی روزی نمی‌گیرد.» بگو: «من مأمورم که نخستین مسلمان باشم؛ و (خداوند به من دستور داده که) از مشرکان نباش!»'];
ayat[804]=[6,15,'قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ','بگو: «من (نیز) اگر نافرمانی پروردگارم کنم، از عذاب روزی بزرگ [= روز رستاخیز] می‌ترسم!'];
ayat[805]=[6,16,'مَّن يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُ ۚ وَذَٰلِكَ الْفَوْزُ الْمُبِينُ','آن کس که در آن روز، مجازات الهی به او نرسد، خداوند او را مشمول رحمت خویش ساخته؛ و این همان پیروزی آشکار است.»'];
ayat[806]=[6,17,'وَإِن يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلَا كَاشِفَ لَهُ إِلَّا هُوَ ۖ وَإِن يَمْسَسْكَ بِخَيْرٍ فَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','اگر خداوند زیانی به تو برساند، هیچ کس جز او نمی‌تواند آن را برطرف سازد! و اگر خیری به تو رساند، او بر همه چیز تواناست؛ (و از قدرت او، هرگونه نیکی ساخته است.)'];
ayat[807]=[6,18,'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهِ ۚ وَهُوَ الْحَكِيمُ الْخَبِيرُ','اوست که بر بندگان خود، قاهر و مسلّط است؛ و اوست حکیم آگاه!'];
ayat[808]=[6,19,'قُلْ أَيُّ شَيْءٍ أَكْبَرُ شَهَادَةً ۖ قُلِ اللَّهُ ۖ شَهِيدٌ بَيْنِي وَبَيْنَكُمْ ۚ وَأُوحِيَ إِلَيَّ هَٰذَا الْقُرْآنُ لِأُنذِرَكُم بِهِ وَمَن بَلَغَ ۚ أَئِنَّكُمْ لَتَشْهَدُونَ أَنَّ مَعَ اللَّهِ آلِهَةً أُخْرَىٰ ۚ قُل لَّا أَشْهَدُ ۚ قُلْ إِنَّمَا هُوَ إِلَٰهٌ وَاحِدٌ وَإِنَّنِي بَرِيءٌ مِّمَّا تُشْرِكُونَ','بگو: «بالاترین گواهی، گواهی کیست؟» (و خودت پاسخ بده و) بگو: «خداوند، گواه میان من و شماست؛ و (بهترین دلیل آن این است که) این قرآن بر من وحی شده، تا شما و تمام کسانی را که این قرآن به آنها می‌رسد، بیم دهم (و از مخالفت فرمان خدا بترسانم). آیا براستی شما گواهی می‌دهید که معبودان دیگری با خداست؟!» بگو: «من هرگز چنین گواهی نمی‌دهم». بگو: «اوست تنها معبود یگانه؛ و من از آنچه برای او شریک قرارمی‌دهید، بیزارم!»'];
ayat[809]=[6,20,'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْرِفُونَهُ كَمَا يَعْرِفُونَ أَبْنَاءَهُمُ ۘ الَّذِينَ خَسِرُوا أَنفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَ','آنان که کتاب آسمانی به ایشان داده‌ایم، بخوبی او [= پیامبر] را می‌شناسند، همان‌گونه که فرزندان خود را می‌شناسند؛ فقط کسانی که سرمایه وجود خود را از دست داده‌اند، ایمان نمی‌آورند.'];
ayat[810]=[6,21,'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ ۗ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ','چه کسی ستمکارتر است از آن کس که بر خدا دروغ بسته [= همتایی برای او قائل شده‌]، و یا آیات او را تکذیب کرده است؟! مسلماً ظالمان، رستگار نخواهند شد!'];
ayat[811]=[6,22,'وَيَوْمَ نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا أَيْنَ شُرَكَاؤُكُمُ الَّذِينَ كُنتُمْ تَزْعُمُونَ','آن روز که همه آنها را محشور می‌کنیم؛ سپس به مشرکان می‌گوییم: «معبودهایتان، که همتای خدا می‌پنداشتید، کجایند؟» (چرا به یاری شما نمی‌شتابند؟!)'];
ayat[812]=[6,23,'ثُمَّ لَمْ تَكُن فِتْنَتُهُمْ إِلَّا أَن قَالُوا وَاللَّهِ رَبِّنَا مَا كُنَّا مُشْرِكِينَ','سپس پاسخ و عذر آنها، چیزی جز این نیست که می‌گویند: «به خداوندی که پروردگار ماست سوگند که ما مشرک نبودیم!»'];
ayat[813]=[6,24,'انظُرْ كَيْفَ كَذَبُوا عَلَىٰ أَنفُسِهِمْ ۚ وَضَلَّ عَنْهُم مَّا كَانُوا يَفْتَرُونَ','ببین چگونه به خودشان (نیز) دروغ می‌گویند، و آنچه را بدروغ همتای خدا می‌پنداشتند، از دست می‌دهند!'];
ayat[814]=[6,25,'وَمِنْهُم مَّن يَسْتَمِعُ إِلَيْكَ ۖ وَجَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَن يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا ۚ وَإِن يَرَوْا كُلَّ آيَةٍ لَّا يُؤْمِنُوا بِهَا ۚ حَتَّىٰ إِذَا جَاءُوكَ يُجَادِلُونَكَ يَقُولُ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ','پاره‌ای از آنها به (سخنان) تو، گوش فرامی‌دهند؛ ولی بر دلهای آنان پرده‌ها افکنده‌ایم تا آن را نفهمند؛ و در گوش آنها، سنگینی قرار داده‌ایم. و (آنها بقدری لجوجند که) اگر تمام نشانه‌های حق را ببینند، ایمان نمی‌آورند؛ تا آنجا که وقتی به سراغ تو می‌آیند که با تو پرخاشگری کنند، کافران می‌گویند: «اینها فقط افسانه‌های پیشینیان است!»'];
ayat[815]=[6,26,'وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْأَوْنَ عَنْهُ ۖ وَإِن يُهْلِكُونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَ','آنها دیگران را از آن بازمی‌دارند؛ و خود نیز از آن دوری می‌کنند؛ آنها جز خود را هلاک نمی‌کنند، ولی نمی‌فهمند!'];
ayat[816]=[6,27,'وَلَوْ تَرَىٰ إِذْ وُقِفُوا عَلَى النَّارِ فَقَالُوا يَا لَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِآيَاتِ رَبِّنَا وَنَكُونَ مِنَ الْمُؤْمِنِينَ','کاش (حال آنها را) هنگامی که در برابر آتش (دوزخ) ایستاده‌اند، ببینی! می‌گویند: ای کاش (بار دیگر، به دنیا) بازگردانده می‌شدیم، و آیات پروردگارمان را تکذیب نمی‌کردیم، و از مؤمنان می‌بودیم!'];
ayat[817]=[6,28,'بَلْ بَدَا لَهُم مَّا كَانُوا يُخْفُونَ مِن قَبْلُ ۖ وَلَوْ رُدُّوا لَعَادُوا لِمَا نُهُوا عَنْهُ وَإِنَّهُمْ لَكَاذِبُونَ','(آنها در واقع پشیمان نیستند،) بلکه اعمال و نیّاتی را که قبلاً پنهان می‌کردند، در برابر آنها آشکار شده (و به وحشت افتاده‌اند). و اگر بازگردند، به همان اعمالی که از آن نهی شده بودند بازمی‌گردند؛ آنها دروغگویانند.'];
ayat[818]=[6,29,'وَقَالُوا إِنْ هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ','آنها گفتند: «چیزی جز این زندگی دنیای ما نیست؛ و ما هرگز برانگیخته نخواهیم شد!»'];
ayat[819]=[6,30,'وَلَوْ تَرَىٰ إِذْ وُقِفُوا عَلَىٰ رَبِّهِمْ ۚ قَالَ أَلَيْسَ هَٰذَا بِالْحَقِّ ۚ قَالُوا بَلَىٰ وَرَبِّنَا ۚ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَ','اگر آنها را به هنگامی که در پیشگاه پروردگارشان ایستاده‌اند، ببینی! (به آنها) می‌گوید: «آیا این حق نیست؟» می‌گویند: «آری، قسم به پروردگارمان (حق است!)» می‌گوید: «پس مجازات را بچشید به سزای آنچه انکار می‌کردید!»'];
ayat[820]=[6,31,'قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقَاءِ اللَّهِ ۖ حَتَّىٰ إِذَا جَاءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوا يَا حَسْرَتَنَا عَلَىٰ مَا فَرَّطْنَا فِيهَا وَهُمْ يَحْمِلُونَ أَوْزَارَهُمْ عَلَىٰ ظُهُورِهِمْ ۚ أَلَا سَاءَ مَا يَزِرُونَ','آنها که لقای پروردگار را تکذیب کردند، مسلماً زیان دیدند؛ (و این تکذیب، ادامه می یابد) تا هنگامی که ناگهان قیامت به سراغشان بیاید؛ می‌گویند: «ای افسوس بر ما که درباره آن، کوتاهی کردیم!» و آنها (بار سنگین) گناهانشان را بر دوش می‌کشند؛ چه بد باری بر دوش خواهند داشت!'];
ayat[821]=[6,32,'وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا لَعِبٌ وَلَهْوٌ ۖ وَلَلدَّارُ الْآخِرَةُ خَيْرٌ لِّلَّذِينَ يَتَّقُونَ ۗ أَفَلَا تَعْقِلُونَ','زندگی دنیا، چیزی جز بازی و سرگرمی نیست! و سرای آخرت، برای آنها که پرهیزگارند، بهتر است! آیا نمی‌اندیشید؟!'];
ayat[822]=[6,33,'قَدْ نَعْلَمُ إِنَّهُ لَيَحْزُنُكَ الَّذِي يَقُولُونَ ۖ فَإِنَّهُمْ لَا يُكَذِّبُونَكَ وَلَٰكِنَّ الظَّالِمِينَ بِآيَاتِ اللَّهِ يَجْحَدُونَ','ما می‌دانیم که گفتار آنها، تو را غمگین می‌کند؛ ولی (غم مخور! و بدان که) آنها تو را تکذیب نمی‌کنند؛ بلکه ظالمان، آیات خدا را انکار می‌نمایند.'];
ayat[823]=[6,34,'وَلَقَدْ كُذِّبَتْ رُسُلٌ مِّن قَبْلِكَ فَصَبَرُوا عَلَىٰ مَا كُذِّبُوا وَأُوذُوا حَتَّىٰ أَتَاهُمْ نَصْرُنَا ۚ وَلَا مُبَدِّلَ لِكَلِمَاتِ اللَّهِ ۚ وَلَقَدْ جَاءَكَ مِن نَّبَإِ الْمُرْسَلِينَ','پیش از تو نیز پیامبرانی تکذیب شدند؛ و در برابر تکذیبها، صبر و استقامت کردند؛ و (در این راه،) آزار دیدند، تا هنگامی که یاری ما به آنها رسید. (تو نیز چنین باش! و این، یکی از سنتهای الهی است؛) و هیچ چیز نمی‌تواند سنن خدا را تغییر دهد؛ و اخبار پیامبران به تو رسیده است.'];
ayat[824]=[6,35,'وَإِن كَانَ كَبُرَ عَلَيْكَ إِعْرَاضُهُمْ فَإِنِ اسْتَطَعْتَ أَن تَبْتَغِيَ نَفَقًا فِي الْأَرْضِ أَوْ سُلَّمًا فِي السَّمَاءِ فَتَأْتِيَهُم بِآيَةٍ ۚ وَلَوْ شَاءَ اللَّهُ لَجَمَعَهُمْ عَلَى الْهُدَىٰ ۚ فَلَا تَكُونَنَّ مِنَ الْجَاهِلِينَ','و اگر اعراض آنها بر تو سنگین است، چنانچه بتوانی نقبی در زمین بزنی، یا نردبانی به آسمان بگذاری (و اعماق زمین و آسمانها را جستجو کنی، چنین کن) تا آیه (و نشانه دیگری) برای آنها بیاوری! (ولی بدان که این لجوجان، ایمان نمی‌آورند!) امّا اگر خدا بخواهد، آنها را (به اجبار) بر هدایت جمع خواهد کرد. (ولی هدایت اجباری، چه سودی دارد؟) پس هرگز از جاهلان مباش!'];
ayat[825]=[6,36,'۞ إِنَّمَا يَسْتَجِيبُ الَّذِينَ يَسْمَعُونَ ۘ وَالْمَوْتَىٰ يَبْعَثُهُمُ اللَّهُ ثُمَّ إِلَيْهِ يُرْجَعُونَ','تنها کسانی (دعوت تو را) می‌پذیرند که گوش شنوا دارند؛ امّا مردگان (و آنها که روح انسانی را از دست داده‌اند، ایمان نمی‌آورند؛ و) خدا آنها را (در قیامت) برمی‌انگیزد؛ سپس به سوی او، بازمی‌گردند.'];
ayat[826]=[6,37,'وَقَالُوا لَوْلَا نُزِّلَ عَلَيْهِ آيَةٌ مِّن رَّبِّهِ ۚ قُلْ إِنَّ اللَّهَ قَادِرٌ عَلَىٰ أَن يُنَزِّلَ آيَةً وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','و گفتند: «چرا نشانه (و معجزه‌ای) از طرف پروردگارش بر او نازل نمی‌شود؟!» بگو: «خداوند، قادر است که نشانه‌ای نازل کند؛ ولی بیشتر آنها نمی‌دانند!»'];
ayat[827]=[6,38,'وَمَا مِن دَابَّةٍ فِي الْأَرْضِ وَلَا طَائِرٍ يَطِيرُ بِجَنَاحَيْهِ إِلَّا أُمَمٌ أَمْثَالُكُم ۚ مَّا فَرَّطْنَا فِي الْكِتَابِ مِن شَيْءٍ ۚ ثُمَّ إِلَىٰ رَبِّهِمْ يُحْشَرُونَ','هیچ جنبنده‌ای در زمین، و هیچ پرنده‌ای که با دو بال خود پرواز می‌کند، نیست مگر اینکه امتهایی همانند شما هستند. ما هیچ چیز را در این کتاب، فرو گذار نکردیم؛ سپس همگی به سوی پروردگارشان محشور می‌گردند.'];
ayat[828]=[6,39,'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا صُمٌّ وَبُكْمٌ فِي الظُّلُمَاتِ ۗ مَن يَشَإِ اللَّهُ يُضْلِلْهُ وَمَن يَشَأْ يَجْعَلْهُ عَلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','آنها که آیات ما را تکذیب کردند، کرها و لالهایی هستند که در تاریکیها قرار دارند. هر کس را خدا بخواهد (و مستحق باشد،) گمراه می‌کند؛ و هر کس را بخواهد (و شایسته بداند،) بر راه راست قرار خواهد داد.'];
ayat[829]=[6,40,'قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ أَوْ أَتَتْكُمُ السَّاعَةُ أَغَيْرَ اللَّهِ تَدْعُونَ إِن كُنتُمْ صَادِقِينَ','بگو: «به من خبر دهید اگر عذاب پروردگار به سراغ شما آید، یا رستاخیز برپا شود، آیا (برای حل مشکلات خود،) غیر خدا را می‌خوانید اگر راست می‌گویید؟!»'];
ayat[830]=[6,41,'بَلْ إِيَّاهُ تَدْعُونَ فَيَكْشِفُ مَا تَدْعُونَ إِلَيْهِ إِن شَاءَ وَتَنسَوْنَ مَا تُشْرِكُونَ','(نه،) بلکه تنها او را می‌خوانید! و او اگر بخواهد، مشکلی را که بخاطر آن او را خوانده‌اید، برطرف می‌سازد؛ و آنچه را (امروز) همتای خدا قرارمی‌دهید، (در آن روز) فراموش خواهید کرد.'];
ayat[831]=[6,42,'وَلَقَدْ أَرْسَلْنَا إِلَىٰ أُمَمٍ مِّن قَبْلِكَ فَأَخَذْنَاهُم بِالْبَأْسَاءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ','ما به سوی امتهایی که پیش از تو بودند، (پیامبرانی) فرستادیم؛ (و هنگامی که با این پیامبران به مخالفت برخاستند،) آنها را با شدّت و رنج و ناراحتی مواجه ساختیم؛ شاید (بیدار شوند و در برابر حق،) خضوع کنند و تسلیم گردند!'];
ayat[832]=[6,43,'فَلَوْلَا إِذْ جَاءَهُم بَأْسُنَا تَضَرَّعُوا وَلَٰكِن قَسَتْ قُلُوبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطَانُ مَا كَانُوا يَعْمَلُونَ','چرا هنگامی که مجازات ما به آنان رسید، (خضوع نکردند و) تسلیم نشدند؟! بلکه دلهای آنها قساوت پیدا کرد؛ و شیطان، هر کاری را که می‌کردند، در نظرشان زینت داد!'];
ayat[833]=[6,44,'فَلَمَّا نَسُوا مَا ذُكِّرُوا بِهِ فَتَحْنَا عَلَيْهِمْ أَبْوَابَ كُلِّ شَيْءٍ حَتَّىٰ إِذَا فَرِحُوا بِمَا أُوتُوا أَخَذْنَاهُم بَغْتَةً فَإِذَا هُم مُّبْلِسُونَ','(آری،) هنگامی که (اندرزها سودی نبخشید، و) آنچه را به آنها یادآوری شده بود فراموش کردند، درهای همه چیز (از نعمتها) را به روی آنها گشودیم؛ تا (کاملا) ً خوشحال شدند (و دل به آن بستند)؛ ناگهان آنها را گرفتیم (و سخت مجازات کردیم)؛ در این هنگام، همگی مأیوس شدند؛ (و درهای امید به روی آنها بسته شد).'];
ayat[834]=[6,45,'فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِينَ ظَلَمُوا ۚ وَالْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ','و (به این ترتیب،) دنباله (زندگی) جمعیّتی که ستم کرده بودند، قطع شد. و ستایش مخصوص خداوند، پروردگار جهانیان است.'];
ayat[835]=[6,46,'قُلْ أَرَأَيْتُمْ إِنْ أَخَذَ اللَّهُ سَمْعَكُمْ وَأَبْصَارَكُمْ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنْ إِلَٰهٌ غَيْرُ اللَّهِ يَأْتِيكُم بِهِ ۗ انظُرْ كَيْفَ نُصَرِّفُ الْآيَاتِ ثُمَّ هُمْ يَصْدِفُونَ','بگو: «به من خبر دهید اگر خداوند، گوش و چشمهایتان را بگیرد، و بر دلهای شما مهر نهد (که چیزی را نفهمید)، چه کسی جز خداست که آنها را به شما بدهد؟!» ببین چگونه آیات را به گونه‌های مختلف برای آنها شرح می‌دهیم، سپس آنها روی می‌گردانند!'];
ayat[836]=[6,47,'قُلْ أَرَأَيْتَكُمْ إِنْ أَتَاكُمْ عَذَابُ اللَّهِ بَغْتَةً أَوْ جَهْرَةً هَلْ يُهْلَكُ إِلَّا الْقَوْمُ الظَّالِمُونَ','بگو: «به من خبر دهید اگر عذاب خدا بطور ناگهانی (و پنهانی) یا آشکارا به سراغ شما بیاید، آیا جز جمعیّت ستمکار هلاک می‌شوند؟!»'];
ayat[837]=[6,48,'وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَ ۖ فَمَنْ آمَنَ وَأَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','ما پیامبران را، جز (به عنوان) بشارت دهنده و بیم دهنده، نمی‌فرستیم؛ آنها که ایمان بیاورند و (خویشتن را) اصلاح کنند، نه ترسی بر آنهاست و نه غمگین می‌شوند.'];
ayat[838]=[6,49,'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوا يَفْسُقُونَ','و آنها که آیات ما را تکذیب کردند، عذاب (پروردگار) بخاطر نافرمانیها به آنان می‌رسد.'];
ayat[839]=[6,50,'قُل لَّا أَقُولُ لَكُمْ عِندِي خَزَائِنُ اللَّهِ وَلَا أَعْلَمُ الْغَيْبَ وَلَا أَقُولُ لَكُمْ إِنِّي مَلَكٌ ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّ ۚ قُلْ هَلْ يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ ۚ أَفَلَا تَتَفَكَّرُونَ','بگو: «من نمی‌گویم خزاین خدا نزد من است؛ و من، (جز آنچه خدا به من بیاموزد،) از غیب آگاه نیستم! و به شما نمی‌گویم من فرشته‌ام؛ تنها از آنچه به من وحی می‌شود پیروی می‌کنم.» بگو: «آیا نابینا و بینا مساویند؟! پس چرا نمی‌اندیشید؟!»'];
ayat[840]=[6,51,'وَأَنذِرْ بِهِ الَّذِينَ يَخَافُونَ أَن يُحْشَرُوا إِلَىٰ رَبِّهِمْ ۙ لَيْسَ لَهُم مِّن دُونِهِ وَلِيٌّ وَلَا شَفِيعٌ لَّعَلَّهُمْ يَتَّقُونَ','و به وسیله آن (قرآن)، کسانی را که از روز حشر و رستاخیز می‌ترسند، بیم ده! (روزی که در آن،) یاور و سرپرست و شفاعت‌کننده‌ای جز او [= خدا] ندارند؛ شاید پرهیزگاری پیشه کنند!'];
ayat[841]=[6,52,'وَلَا تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُم بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ ۖ مَا عَلَيْكَ مِنْ حِسَابِهِم مِّن شَيْءٍ وَمَا مِنْ حِسَابِكَ عَلَيْهِم مِّن شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظَّالِمِينَ','و کسانی را که صبح و شام خدا را می‌خوانند، و جز ذات پاک او نظری ندارند، از خود دور مکن! نه چیزی از حساب آنها بر توست، و نه چیزی از حساب تو بر آنها! اگر آنها را طرد کنی، از ستمگران خواهی بود!'];
ayat[842]=[6,53,'وَكَذَٰلِكَ فَتَنَّا بَعْضَهُم بِبَعْضٍ لِّيَقُولُوا أَهَٰؤُلَاءِ مَنَّ اللَّهُ عَلَيْهِم مِّن بَيْنِنَا ۗ أَلَيْسَ اللَّهُ بِأَعْلَمَ بِالشَّاكِرِينَ','و این چنین بعضی از آنها را با بعض دیگر آزمودیم (توانگران را بوسیله فقیران)؛ تا بگویند: «آیا اینها هستند که خداوند از میان ما (برگزیده، و) بر آنها منّت گذارده (و نعمت ایمان بخشیده است؟!» آیا خداوند، شاکران را بهتر نمی‌شناسد؟!'];
ayat[843]=[6,54,'وَإِذَا جَاءَكَ الَّذِينَ يُؤْمِنُونَ بِآيَاتِنَا فَقُلْ سَلَامٌ عَلَيْكُمْ ۖ كَتَبَ رَبُّكُمْ عَلَىٰ نَفْسِهِ الرَّحْمَةَ ۖ أَنَّهُ مَنْ عَمِلَ مِنكُمْ سُوءًا بِجَهَالَةٍ ثُمَّ تَابَ مِن بَعْدِهِ وَأَصْلَحَ فَأَنَّهُ غَفُورٌ رَّحِيمٌ','هرگاه کسانی که به آیات ما ایمان دارند نزد تو آیند، به آنها بگو: «سلام بر شما پروردگارتان، رحمت را بر خود فرض کرده؛ هر کس از شما کار بدی از روی نادانی کند، سپس توبه و اصلاح (و جبران) نماید، (مشمول رحمت خدا می‌شود چرا که) او آمرزنده مهربان است.»'];
ayat[844]=[6,55,'وَكَذَٰلِكَ نُفَصِّلُ الْآيَاتِ وَلِتَسْتَبِينَ سَبِيلُ الْمُجْرِمِينَ','و این چنین آیات را برمیشمریم، (تا حقیقت بر شما روشن شود،) و راه گناهکاران آشکار گردد.'];
ayat[845]=[6,56,'قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِن دُونِ اللَّهِ ۚ قُل لَّا أَتَّبِعُ أَهْوَاءَكُمْ ۙ قَدْ ضَلَلْتُ إِذًا وَمَا أَنَا مِنَ الْمُهْتَدِينَ','بگو: «من از پرستش کسانی که غیر از خدا می‌خوانید، نهی شده‌ام!» بگو: «من از هوی و هوسهای شما، پیروی نمی‌کنم؛ اگر چنین کنم، گمراه شده‌ام؛ و از هدایت‌یافتگان نخواهم بود!»'];
ayat[846]=[6,57,'قُلْ إِنِّي عَلَىٰ بَيِّنَةٍ مِّن رَّبِّي وَكَذَّبْتُم بِهِ ۚ مَا عِندِي مَا تَسْتَعْجِلُونَ بِهِ ۚ إِنِ الْحُكْمُ إِلَّا لِلَّهِ ۖ يَقُصُّ الْحَقَّ ۖ وَهُوَ خَيْرُ الْفَاصِلِينَ','بگو: «من دلیل روشنی از پروردگارم دارم؛ و شما آن را تکذیب کرده‌اید! آنچه شما در باره آن (از نزول عذاب الهی) عجله دارید، به دستِ من نیست! حکم و فرمان، تنها از آنِ خداست! حق را از باطل جدا می‌کند، و او بهترین جداکننده (حق از باطل) است.»'];
ayat[847]=[6,58,'قُل لَّوْ أَنَّ عِندِي مَا تَسْتَعْجِلُونَ بِهِ لَقُضِيَ الْأَمْرُ بَيْنِي وَبَيْنَكُمْ ۗ وَاللَّهُ أَعْلَمُ بِالظَّالِمِينَ','بگو: «اگر آنچه درباره آن عجله دارید نزد من بود، (و به درخواست شما ترتیب‌اثر می‌دادم، عذاب الهی بر شما نازل می‌گشت؛) و کار میان من و شما پایان گرفته بود؛ ولی خداوند ظالمان را بهتر می‌شناسد (و بموقع مجازات می‌کند.)»'];
ayat[848]=[6,59,'۞ وَعِندَهُ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَا إِلَّا هُوَ ۚ وَيَعْلَمُ مَا فِي الْبَرِّ وَالْبَحْرِ ۚ وَمَا تَسْقُطُ مِن وَرَقَةٍ إِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ فِي ظُلُمَاتِ الْأَرْضِ وَلَا رَطْبٍ وَلَا يَابِسٍ إِلَّا فِي كِتَابٍ مُّبِينٍ','کلیدهای غیب، تنها نزد اوست؛ و جز او، کسی آنها را نمی‌داند. او آنچه را در خشکی و دریاست می‌داند؛ هیچ برگی (از درختی) نمی‌افتد، مگر اینکه از آن آگاه است؛ و نه هیچ دانه‌ای در تاریکیهای زمین، و نه هیچ تر و خشکی وجود دارد، جز اینکه در کتابی آشکار [= در کتاب علم خدا] ثبت است.'];
ayat[849]=[6,60,'وَهُوَ الَّذِي يَتَوَفَّاكُم بِاللَّيْلِ وَيَعْلَمُ مَا جَرَحْتُم بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيهِ لِيُقْضَىٰ أَجَلٌ مُّسَمًّى ۖ ثُمَّ إِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','او کسی است که (روح) شما را در شب (به هنگام خواب) می‌گیرد؛ و از آنچه در روز کرده‌اید، با خبر است؛ سپس در روز شما را (از خواب) برمی‌انگیزد؛ و (این وضع همچنان ادامه می‌یابد) تا سرآمد معینی فرا رسد؛ سپس بازگشت شما به سوی اوست؛ و سپس شما را از آنچه عمل می‌کردید، با خبر می‌سازد.'];
ayat[850]=[6,61,'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهِ ۖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً حَتَّىٰ إِذَا جَاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُونَ','او بر بندگان خود تسلّط کامل دارد؛ و مراقبانی بر شما می‌گمارد؛ تا زمانی که یکی از شما را مرگ فرا رسد؛ (در این موقع،) فرستادگان ما جان او را می‌گیرند؛ و آنها (در نگاهداری حساب عمر و اعمال بندگان،) کوتاهی نمی‌کنند.'];
ayat[851]=[6,62,'ثُمَّ رُدُّوا إِلَى اللَّهِ مَوْلَاهُمُ الْحَقِّ ۚ أَلَا لَهُ الْحُكْمُ وَهُوَ أَسْرَعُ الْحَاسِبِينَ','سپس (تمام بندگان) به سوی خدا، که مولای حقیقی آنهاست، بازمی‌گردند. بدانید که حکم و داوری، مخصوص اوست؛ و او، سریعترین حسابگران است!'];
ayat[852]=[6,63,'قُلْ مَن يُنَجِّيكُم مِّن ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ تَدْعُونَهُ تَضَرُّعًا وَخُفْيَةً لَّئِنْ أَنجَانَا مِنْ هَٰذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ','بگو: «چه کسی شما را از تاریکیهای خشکی و دریا رهایی می‌بخشد؟ در حالی که او را با حالت تضرع (و آشکارا) و در پنهانی می‌خوانید؛ (و می‌گویید:) اگر از این (خطرات و ظلمتها) ما را رهایی می‌بخشد، از شکرگزاران خواهیم بود.»'];
ayat[853]=[6,64,'قُلِ اللَّهُ يُنَجِّيكُم مِّنْهَا وَمِن كُلِّ كَرْبٍ ثُمَّ أَنتُمْ تُشْرِكُونَ','بگو: «خداوند شما را از اینها، و از هر مشکل و ناراحتی، نجات می‌دهد؛ باز هم شما برای او شریک قرار می‌دهید! (و راه کفر می‌پویید.)»'];
ayat[854]=[6,65,'قُلْ هُوَ الْقَادِرُ عَلَىٰ أَن يَبْعَثَ عَلَيْكُمْ عَذَابًا مِّن فَوْقِكُمْ أَوْ مِن تَحْتِ أَرْجُلِكُمْ أَوْ يَلْبِسَكُمْ شِيَعًا وَيُذِيقَ بَعْضَكُم بَأْسَ بَعْضٍ ۗ انظُرْ كَيْفَ نُصَرِّفُ الْآيَاتِ لَعَلَّهُمْ يَفْقَهُونَ','بگو: «او قادر است که از بالا یا از زیر پای شما، عذابی بر شما بفرستد؛ یا بصورت دسته‌های پراکنده شما را با هم بیامیزد؛ و طعم جنگ (و اختلاف) را به هر یک از شما بوسیله دیگری بچشاند.» ببین چگونه آیات گوناگون را (برای آنها) بازگو می‌کنیم! شاید بفهمند (و بازگردند)!'];
ayat[855]=[6,66,'وَكَذَّبَ بِهِ قَوْمُكَ وَهُوَ الْحَقُّ ۚ قُل لَّسْتُ عَلَيْكُم بِوَكِيلٍ','قوم و جمعیّت تو، آن (آیات الهی) را تکذیب و انکار کردند، در حالی که حق است! (به آنها) بگو: «من مسؤول (ایمان‌آوردن) شما نیستم! (وظیفه من، تنها ابلاغ رسالت است، نه اجبار شما بر ایمان.)»'];
ayat[856]=[6,67,'لِّكُلِّ نَبَإٍ مُّسْتَقَرٌّ ۚ وَسَوْفَ تَعْلَمُونَ','هر خبری (که خداوند به شما داده،) سرانجام قرارگاهی دارد، (و در موعد خود انجام میگیرد.) و بزودی خواهید دانست!'];
ayat[857]=[6,68,'وَإِذَا رَأَيْتَ الَّذِينَ يَخُوضُونَ فِي آيَاتِنَا فَأَعْرِضْ عَنْهُمْ حَتَّىٰ يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ ۚ وَإِمَّا يُنسِيَنَّكَ الشَّيْطَانُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرَىٰ مَعَ الْقَوْمِ الظَّالِمِينَ','هرگاه کسانی را دیدی که آیات ما را استهزا می‌کنند، از آنها روی بگردان تا به سخن دیگری بپردازند! و اگر شیطان از یاد تو ببرد، هرگز پس از یادآمدن با این جمعیّت ستمگر منشین!'];
ayat[858]=[6,69,'وَمَا عَلَى الَّذِينَ يَتَّقُونَ مِنْ حِسَابِهِم مِّن شَيْءٍ وَلَٰكِن ذِكْرَىٰ لَعَلَّهُمْ يَتَّقُونَ','و (اگر) افراد با تقوا (برای ارشاد و اندرز با آنها بنشینند)، چیزی از حساب (و گناه) آنها بر ایشان نیست؛ ولی (این کار، باید تنها) برای یادآوری آنها باشد، شاید (بشنوند و) تقوی پیشه کنند!'];
ayat[859]=[6,70,'وَذَرِ الَّذِينَ اتَّخَذُوا دِينَهُمْ لَعِبًا وَلَهْوًا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا ۚ وَذَكِّرْ بِهِ أَن تُبْسَلَ نَفْسٌ بِمَا كَسَبَتْ لَيْسَ لَهَا مِن دُونِ اللَّهِ وَلِيٌّ وَلَا شَفِيعٌ وَإِن تَعْدِلْ كُلَّ عَدْلٍ لَّا يُؤْخَذْ مِنْهَا ۗ أُولَٰئِكَ الَّذِينَ أُبْسِلُوا بِمَا كَسَبُوا ۖ لَهُمْ شَرَابٌ مِّنْ حَمِيمٍ وَعَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْفُرُونَ','و رها کن کسانی را که آیین (فطری) خود را به بازی و سرگرمی گرفتند، و زندگی دنیا، آنها را مغرور ساخته، و با این (قرآن)، به آنها یادآوری نما، تا گرفتار (عواقب شوم) اعمال خود نشوند! (و در قیامت) جز خدا، نه یاوری دارند، و نه شفاعت‌کننده‌ای! و (چنین کسی) هر گونه عوضی بپردازد، از او پذیرفته نخواهد شد؛ آنها کسانی هستند که گرفتار اعمالی شده‌اند که خود انجام داده‌اند؛ نوشابه‌ای از آب سوزان برای آنهاست؛ و عذاب دردناکی بخاطر اینکه کفر می‌ورزیدند (و آیات الهی را انکار) می‌کردند.'];
ayat[860]=[6,71,'قُلْ أَنَدْعُو مِن دُونِ اللَّهِ مَا لَا يَنفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰ أَعْقَابِنَا بَعْدَ إِذْ هَدَانَا اللَّهُ كَالَّذِي اسْتَهْوَتْهُ الشَّيَاطِينُ فِي الْأَرْضِ حَيْرَانَ لَهُ أَصْحَابٌ يَدْعُونَهُ إِلَى الْهُدَى ائْتِنَا ۗ قُلْ إِنَّ هُدَى اللَّهِ هُوَ الْهُدَىٰ ۖ وَأُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعَالَمِينَ','بگو: «آیا غیر از خدا، چیزی را بخوانیم (و عبادت کنیم) که نه سودی به حال ما دارد، نه زیانی؛ و (به این ترتیب،) به عقب برگردیم بعد از آنکه خداوند ما را هدایت کرده است؟! همانند کسی که بر اثر وسوسه‌های شیطان، در روی زمین راه را گم کرده، و سرگردان مانده است؛ در حالی که یارانی هم دارد که او را به هدایت دعوت می‌کنند (و می‌گویند:) به سوی ما بیا!» بگو: «تنها هدایت خداوند، هدایت است؛ و ما دستور داریم که تسلیم پروردگار عالمیان باشیم.'];
ayat[861]=[6,72,'وَأَنْ أَقِيمُوا الصَّلَاةَ وَاتَّقُوهُ ۚ وَهُوَ الَّذِي إِلَيْهِ تُحْشَرُونَ','و (نیز به ما فرمان داده شده به) اینکه: نماز را برپا دارید! و از او بپرهیزید! و تنها اوست که به سویش محشور خواهید شد.»'];
ayat[862]=[6,73,'وَهُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۖ وَيَوْمَ يَقُولُ كُن فَيَكُونُ ۚ قَوْلُهُ الْحَقُّ ۚ وَلَهُ الْمُلْكُ يَوْمَ يُنفَخُ فِي الصُّورِ ۚ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ ۚ وَهُوَ الْحَكِيمُ الْخَبِيرُ','اوست که آسمانها و زمین را بحق آفرید؛ و آن روز که (به هر چیز) می‌گوید: «موجود باش!» موجود می‌شود؛ سخن او، حق است؛ و در آن روز که در «صور» دمیده می‌شود، حکومت مخصوص اوست، از پنهان و آشکار با خبر است، و اوست حکیم و آگاه.'];
ayat[863]=[6,74,'۞ وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ آزَرَ أَتَتَّخِذُ أَصْنَامًا آلِهَةً ۖ إِنِّي أَرَاكَ وَقَوْمَكَ فِي ضَلَالٍ مُّبِينٍ','(به خاطر بیاورید) هنگامی را که ابراهیم به پدرش [= عمویش‌] «آزر» گفت: «آیا بتهائی را معبودان خود انتخاب می‌کنی؟! من، تو و قوم تو را در گمراهی آشکاری می‌بینم.»'];
ayat[864]=[6,75,'وَكَذَٰلِكَ نُرِي إِبْرَاهِيمَ مَلَكُوتَ السَّمَاوَاتِ وَالْأَرْضِ وَلِيَكُونَ مِنَ الْمُوقِنِينَ','و این چنین، ملکوت آسمانها و زمین (و حکومت مطلقه خداوند بر آنها) را به ابراهیم نشان دادیم؛ (تا به آن استدلال کند،) و اهل یقین گردد.'];
ayat[865]=[6,76,'فَلَمَّا جَنَّ عَلَيْهِ اللَّيْلُ رَأَىٰ كَوْكَبًا ۖ قَالَ هَٰذَا رَبِّي ۖ فَلَمَّا أَفَلَ قَالَ لَا أُحِبُّ الْآفِلِينَ','هنگامی که (تاریکی) شب او را پوشانید، ستاره‌ای مشاهده کرد، گفت: «این خدای من است؟» امّا هنگامی که غروب کرد، گفت: «غروب‌کنندگان را دوست ندارم!»'];
ayat[866]=[6,77,'فَلَمَّا رَأَى الْقَمَرَ بَازِغًا قَالَ هَٰذَا رَبِّي ۖ فَلَمَّا أَفَلَ قَالَ لَئِن لَّمْ يَهْدِنِي رَبِّي لَأَكُونَنَّ مِنَ الْقَوْمِ الضَّالِّينَ','و هنگامی که ماه را دید که (سینه افق را) می‌شکافد، گفت: «این خدای من است؟» امّا هنگامی که (آن هم) غروب کرد، گفت: «اگر پروردگارم مرا راهنمایی نکند، مسلّماً از گروه گمراهان خواهم بود.»'];
ayat[867]=[6,78,'فَلَمَّا رَأَى الشَّمْسَ بَازِغَةً قَالَ هَٰذَا رَبِّي هَٰذَا أَكْبَرُ ۖ فَلَمَّا أَفَلَتْ قَالَ يَا قَوْمِ إِنِّي بَرِيءٌ مِّمَّا تُشْرِكُونَ','و هنگامی که خورشید را دید که (سینه افق را) می‌شکافت، گفت: «این خدای من است؟ این (که از همه) بزرگتر است!» امّا هنگامی که غروب کرد، گفت: «ای قوم من از شریکهایی که شما (برای خدا) می‌سازید، بیزارم!'];
ayat[868]=[6,79,'إِنِّي وَجَّهْتُ وَجْهِيَ لِلَّذِي فَطَرَ السَّمَاوَاتِ وَالْأَرْضَ حَنِيفًا ۖ وَمَا أَنَا مِنَ الْمُشْرِكِينَ','من روی خود را به سوی کسی کردم که آسمانها و زمین را آفریده؛ من در ایمان خود خالصم؛ و از مشرکان نیستم!'];
ayat[869]=[6,80,'وَحَاجَّهُ قَوْمُهُ ۚ قَالَ أَتُحَاجُّونِّي فِي اللَّهِ وَقَدْ هَدَانِ ۚ وَلَا أَخَافُ مَا تُشْرِكُونَ بِهِ إِلَّا أَن يَشَاءَ رَبِّي شَيْئًا ۗ وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْمًا ۗ أَفَلَا تَتَذَكَّرُونَ','ولی قوم او [= ابراهیم‌]، با وی به گفتگو و ستیز پرداختند؛ گفت: «آیا درباره خدا با من گفتگو و ستیز می‌کنید؟! در حالی که خداوند، مرا با دلایل روشن هدایت کرده؛ و من از آنچه شما همتای (خدا) قرار می‌دهید، نمی‌ترسم (و به من زیانی نمی رسانند)! مگر پروردگارم چیزی را بخواهد! وسعت آگاهی پروردگارم همه چیز را در برمی‌گیرد؛ آیا متذکّر (و بیدار) نمی‌شوید؟!'];
ayat[870]=[6,81,'وَكَيْفَ أَخَافُ مَا أَشْرَكْتُمْ وَلَا تَخَافُونَ أَنَّكُمْ أَشْرَكْتُم بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ عَلَيْكُمْ سُلْطَانًا ۚ فَأَيُّ الْفَرِيقَيْنِ أَحَقُّ بِالْأَمْنِ ۖ إِن كُنتُمْ تَعْلَمُونَ','چگونه من از بتهای شما بترسم؟! در حالی که شما از این نمی‌ترسید که برای خدا، همتایی قرار داده‌اید که هیچ گونه دلیلی درباره آن، بر شما نازل نکرده است! (راست بگویید) کدام یک از این دو دسته (بت‌پرستان و خداپرستان)، شایسته‌تر به ایمنی (از مجازات) هستند اگر می‌دانید؟!'];
ayat[871]=[6,82,'الَّذِينَ آمَنُوا وَلَمْ يَلْبِسُوا إِيمَانَهُم بِظُلْمٍ أُولَٰئِكَ لَهُمُ الْأَمْنُ وَهُم مُّهْتَدُونَ','(آری،) آنها که ایمان آوردند، و ایمان خود را با شرک و ستم نیالودند، ایمنی تنها از آن آنهاست؛ و آنها هدایت‌یافتگانند!»'];
ayat[872]=[6,83,'وَتِلْكَ حُجَّتُنَا آتَيْنَاهَا إِبْرَاهِيمَ عَلَىٰ قَوْمِهِ ۚ نَرْفَعُ دَرَجَاتٍ مَّن نَّشَاءُ ۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ','اینها دلایل ما بود که به ابراهیم در برابر قومش دادیم! درجات هر کس را بخواهیم (و شایسته بدانیم،) بالا می‌بریم؛ پروردگار تو، حکیم و داناست.'];
ayat[873]=[6,84,'وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ ۚ كُلًّا هَدَيْنَا ۚ وَنُوحًا هَدَيْنَا مِن قَبْلُ ۖ وَمِن ذُرِّيَّتِهِ دَاوُودَ وَسُلَيْمَانَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَىٰ وَهَارُونَ ۚ وَكَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','و اسحاق و یعقوب را به او [= ابراهیم‌] بخشیدیم؛ و هر دو را هدایت کردیم؛ و نوح را (نیز) پیش از آن هدایت نمودیم؛ و از فرزندان او، داوود و سلیمان و ایّوب و یوسف و موسی و هارون را (هدایت کردیم)؛ این‌گونه نیکوکاران را پاداش می‌دهیم!'];
ayat[874]=[6,85,'وَزَكَرِيَّا وَيَحْيَىٰ وَعِيسَىٰ وَإِلْيَاسَ ۖ كُلٌّ مِّنَ الصَّالِحِينَ','و (همچنین) زکریّا و یحیی و عیسی و الیاس را؛ همه از صالحان بودند.'];
ayat[875]=[6,86,'وَإِسْمَاعِيلَ وَالْيَسَعَ وَيُونُسَ وَلُوطًا ۚ وَكُلًّا فَضَّلْنَا عَلَى الْعَالَمِينَ','و اسماعیل و الیسع و یونس و لوط را؛ و همه را بر جهانیان برتری دادیم.'];
ayat[876]=[6,87,'وَمِنْ آبَائِهِمْ وَذُرِّيَّاتِهِمْ وَإِخْوَانِهِمْ ۖ وَاجْتَبَيْنَاهُمْ وَهَدَيْنَاهُمْ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','و از پدران و فرزندان و برادران آنها (افرادی را برتری دادیم) و برگزیدیم و به راه راست، هدایت نمودیم.'];
ayat[877]=[6,88,'ذَٰلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَن يَشَاءُ مِنْ عِبَادِهِ ۚ وَلَوْ أَشْرَكُوا لَحَبِطَ عَنْهُم مَّا كَانُوا يَعْمَلُونَ','این، هدایت خداست؛ که هر کس از بندگان خود را بخواهد با آن راهنمایی می‌کند! و اگر آنها مشرک شوند، اعمال (نیکی) که انجام داده‌اند، نابود می‌گردد (و نتیجه‌ای از آن نمی‌گیرند).'];
ayat[878]=[6,89,'أُولَٰئِكَ الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ ۚ فَإِن يَكْفُرْ بِهَا هَٰؤُلَاءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَّيْسُوا بِهَا بِكَافِرِينَ','آنها کسانی هستند که کتاب و حکم و نبوّت به آنان دادیم؛ و اگر (بفرض) نسبت به آن کفر ورزند، (آیین حقّ زمین نمی‌ماند؛ زیرا) کسان دیگری را نگاهبان آن می‌سازیم که نسبت به آن، کافر نیستند.'];
ayat[879]=[6,90,'أُولَٰئِكَ الَّذِينَ هَدَى اللَّهُ ۖ فَبِهُدَاهُمُ اقْتَدِهْ ۗ قُل لَّا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا ۖ إِنْ هُوَ إِلَّا ذِكْرَىٰ لِلْعَالَمِينَ','آنها کسانی هستند که خداوند هدایتشان کرده؛ پس به هدایت آنان اقتدا کن! (و) بگو: «در برابر این (رسالت و تبلیغ)، پاداشی از شما نمی‌طلبم! این (رسالت)، چیزی جز یک یادآوری برای جهانیان نیست! (این وظیفه من است)»'];
ayat[880]=[6,91,'وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ إِذْ قَالُوا مَا أَنزَلَ اللَّهُ عَلَىٰ بَشَرٍ مِّن شَيْءٍ ۗ قُلْ مَنْ أَنزَلَ الْكِتَابَ الَّذِي جَاءَ بِهِ مُوسَىٰ نُورًا وَهُدًى لِّلنَّاسِ ۖ تَجْعَلُونَهُ قَرَاطِيسَ تُبْدُونَهَا وَتُخْفُونَ كَثِيرًا ۖ وَعُلِّمْتُم مَّا لَمْ تَعْلَمُوا أَنتُمْ وَلَا آبَاؤُكُمْ ۖ قُلِ اللَّهُ ۖ ثُمَّ ذَرْهُمْ فِي خَوْضِهِمْ يَلْعَبُونَ','آنها خدا را درست نشناختند که گفتند: «خدا، هیچ چیز بر هیچ انسانی، نفرستاده است!» بگو: «چه کسی کتابی را که موسی آورد، نازل کرد؟! کتابی که برای مردم، نور و هدایت بود؛ 0امّا شما) آن را بصورت پراکنده قرارمی‌دهید؛ قسمتی را آشکار، و قسمت زیادی را پنهان می‌دارید؛ و مطالبی به شما تعلیم داده شده که نه شما و نه پدرانتان، از آن با خبر نبودید!» بگو: «خدا!» سپس آنها را در گفتگوهای لجاجت‌آمیزشان رها کن، تا بازی کنند!'];
ayat[881]=[6,92,'وَهَٰذَا كِتَابٌ أَنزَلْنَاهُ مُبَارَكٌ مُّصَدِّقُ الَّذِي بَيْنَ يَدَيْهِ وَلِتُنذِرَ أُمَّ الْقُرَىٰ وَمَنْ حَوْلَهَا ۚ وَالَّذِينَ يُؤْمِنُونَ بِالْآخِرَةِ يُؤْمِنُونَ بِهِ ۖ وَهُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَ','و این کتابی است که ما آن را نازل کردیم؛ کتابی است پربرکت، که آنچه را پیش از آن آمده، تصدیق می‌کند؛ (آن را فرستادیم تا مردم را به پاداشهای الهی، بشارت دهی،) و تا (اهل) امّ‌القری [= مکّه‌] و کسانی را که گرد آن هستند، بترسانی! (یقین بدان) آنها که به آخرت ایمان دارند، به آن ایمان می‌آورند؛ و بر نمازهای خویش، مراقبت می کنند!'];
ayat[882]=[6,93,'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ قَالَ أُوحِيَ إِلَيَّ وَلَمْ يُوحَ إِلَيْهِ شَيْءٌ وَمَن قَالَ سَأُنزِلُ مِثْلَ مَا أَنزَلَ اللَّهُ ۗ وَلَوْ تَرَىٰ إِذِ الظَّالِمُونَ فِي غَمَرَاتِ الْمَوْتِ وَالْمَلَائِكَةُ بَاسِطُو أَيْدِيهِمْ أَخْرِجُوا أَنفُسَكُمُ ۖ الْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنتُمْ تَقُولُونَ عَلَى اللَّهِ غَيْرَ الْحَقِّ وَكُنتُمْ عَنْ آيَاتِهِ تَسْتَكْبِرُونَ','چه کسی ستمکارتر است از کسی که دروغی به خدا ببندد، یا بگوید: «بر من، وحی فرستاده شده»، در حالی که به او وحی نشده است، و کسی که بگوید: «من نیز همانند آنچه خدا نازل کرده است، نازل می‌کنم»؟! و اگر ببینی هنگامی که (این) ظالمان در شداید مرگ فرو رفته‌اند، و فرشتگان دستها را گشوده، به آنان می‌گویند: «جان خود را خارج سازید! امروز در برابر دروغهایی که به خدا بستید و نسبت به آیات او تکبّر ورزیدید، مجازات خوارکننده‌ای خواهید دید»! (به حال آنها تأسف خواهی خورد)'];
ayat[883]=[6,94,'وَلَقَدْ جِئْتُمُونَا فُرَادَىٰ كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ وَتَرَكْتُم مَّا خَوَّلْنَاكُمْ وَرَاءَ ظُهُورِكُمْ ۖ وَمَا نَرَىٰ مَعَكُمْ شُفَعَاءَكُمُ الَّذِينَ زَعَمْتُمْ أَنَّهُمْ فِيكُمْ شُرَكَاءُ ۚ لَقَد تَّقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنكُم مَّا كُنتُمْ تَزْعُمُونَ','و (روز قیامت به آنها گفته می‌شود:) همه شما تنها به سوی ما بازگشت نمودید، همان‌گونه که روز اوّل شما را آفریدیم! و آنچه را به شما بخشیده بودیم، پشت سر گذاردید! و شفیعانی را که شریک در شفاعت خود می‌پنداشتید، با شما نمی‌بینیم! پیوندهای شما بریده شده است؛ و تمام آنچه را تکیه‌گاه خود تصوّر می‌کردید، از شما دور و گم شده‌اند!'];
ayat[884]=[6,95,'۞ إِنَّ اللَّهَ فَالِقُ الْحَبِّ وَالنَّوَىٰ ۖ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ۚ ذَٰلِكُمُ اللَّهُ ۖ فَأَنَّىٰ تُؤْفَكُونَ','خداوند، شکافنده دانه و هسته است؛ زنده را از مرده خارج می‌سازد، و مرده را از زنده بیرون می‌آورد؛ این است خدای شما! پس چگونه از حقّ منحرف می‌شوید؟!'];
ayat[885]=[6,96,'فَالِقُ الْإِصْبَاحِ وَجَعَلَ اللَّيْلَ سَكَنًا وَالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ۚ ذَٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ','او شکافنده صبح است؛ و شب را مایه آرامش، و خورشید و ماه را وسیله حساب قرار داده است؛ این، اندازه‌گیری خداوند توانای داناست!'];
ayat[886]=[6,97,'وَهُوَ الَّذِي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِهَا فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ ۗ قَدْ فَصَّلْنَا الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ','او کسی است که ستارگان را برای شما قرار داد، تا در تاریکیهای خشکی و دریا، به وسیله آنها راه یابید! ما نشانه‌ها (ی خود) را برای کسانی که می‌دانند، (و اهل فکر و اندیشه‌اند) بیان داشتیم!'];
ayat[887]=[6,98,'وَهُوَ الَّذِي أَنشَأَكُم مِّن نَّفْسٍ وَاحِدَةٍ فَمُسْتَقَرٌّ وَمُسْتَوْدَعٌ ۗ قَدْ فَصَّلْنَا الْآيَاتِ لِقَوْمٍ يَفْقَهُونَ','او کسی است که شما را از یک نفس آفرید! و شما دو گروه هستید: بعضی پایدار (از نظر ایمان یا خلقت کامل)، و بعضی ناپایدار؛ ما آیات خود را برای کسانی که می‌فهمند، تشریح نمودیم!'];
ayat[888]=[6,99,'وَهُوَ الَّذِي أَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ نَبَاتَ كُلِّ شَيْءٍ فَأَخْرَجْنَا مِنْهُ خَضِرًا نُّخْرِجُ مِنْهُ حَبًّا مُّتَرَاكِبًا وَمِنَ النَّخْلِ مِن طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَجَنَّاتٍ مِّنْ أَعْنَابٍ وَالزَّيْتُونَ وَالرُّمَّانَ مُشْتَبِهًا وَغَيْرَ مُتَشَابِهٍ ۗ انظُرُوا إِلَىٰ ثَمَرِهِ إِذَا أَثْمَرَ وَيَنْعِهِ ۚ إِنَّ فِي ذَٰلِكُمْ لَآيَاتٍ لِّقَوْمٍ يُؤْمِنُونَ','او کسی است که از آسمان، آبی نازل کرد، و به وسیله آن، گیاهان گوناگون رویاندیم؛ و از آن، ساقه‌ها و شاخه‌های سبز، خارج ساختیم؛ و از آنها دانه‌های متراکم، و از شکوفه نخل، شکوفه‌هایی با رشته‌های باریک بیرون فرستادیم؛ و باغهایی از انواع انگور و زیتون و انار، (گاه) شبیه به یکدیگر، و (گاه) بی‌شباهت! هنگامی که میوه می دهد، به میوه آن و طرز رسیدنش بنگرید که در آن، نشانه‌هایی (از عظمت خدا) برای افراد باایمان است!'];
ayat[889]=[6,100,'وَجَعَلُوا لِلَّهِ شُرَكَاءَ الْجِنَّ وَخَلَقَهُمْ ۖ وَخَرَقُوا لَهُ بَنِينَ وَبَنَاتٍ بِغَيْرِ عِلْمٍ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يَصِفُونَ','آنان برای خدا همتایانی از جنّ قرار دادند، در حالی که خداوند همه آنها را آفریده است؛ و برای خدا، به دروغ و از روی جهل، پسران و دخترانی ساختند؛ منزّه است خدا، و برتر است از آنچه توصیف می‌کنند!'];
ayat[890]=[6,101,'بَدِيعُ السَّمَاوَاتِ وَالْأَرْضِ ۖ أَنَّىٰ يَكُونُ لَهُ وَلَدٌ وَلَمْ تَكُن لَّهُ صَاحِبَةٌ ۖ وَخَلَقَ كُلَّ شَيْءٍ ۖ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ','او پدید آورنده آسمانها و زمین است؛ چگونه ممکن است فرزندی داشته باشد؟! حال آنکه همسری نداشته، و همه چیز را آفریده؛ و او به همه چیز داناست.'];
ayat[891]=[6,102,'ذَٰلِكُمُ اللَّهُ رَبُّكُمْ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُ ۚ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ وَكِيلٌ','(آری، این است پروردگار شما! هیچ معبودی جز او نیست؛ آفریدگار همه چیز است؛ او را بپرستید و او نگهبان و مدّبر همه موجودات است.'];
ayat[892]=[6,103,'لَّا تُدْرِكُهُ الْأَبْصَارُ وَهُوَ يُدْرِكُ الْأَبْصَارَ ۖ وَهُوَ اللَّطِيفُ الْخَبِيرُ','چشمها او را نمی‌بینند؛ ولی او همه چشمها را می‌بیند؛ و او بخشنده (انواع نعمتها، و با خبر از دقایق موجودات،) و آگاه (از همه) چیز است.'];
ayat[893]=[6,104,'قَدْ جَاءَكُم بَصَائِرُ مِن رَّبِّكُمْ ۖ فَمَنْ أَبْصَرَ فَلِنَفْسِهِ ۖ وَمَنْ عَمِيَ فَعَلَيْهَا ۚ وَمَا أَنَا عَلَيْكُم بِحَفِيظٍ','دلایل روشن از طرف پروردگارتان برای شما آمد؛ کسی که (به وسیله آن، حقّ را) ببیند، به سود خود اوست؛ و کسی که از دیدن آن چشم بپوشد، به زیان خودش می‌باشد؛ و من نگاهبان شما نیستم (و شما را بر قبول ایمان مجبور نمی‌کنم)'];
ayat[894]=[6,105,'وَكَذَٰلِكَ نُصَرِّفُ الْآيَاتِ وَلِيَقُولُوا دَرَسْتَ وَلِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ','و اینچنین آیات (خود) را تشریح می‌کنیم؛ بگذار آنها بگویند: «تو درس خوانده‌ای (و آنها را از دیگری آموخته‌ای)»! می‌خواهیم آن را برای کسانی که آماده درک حقایقند، روشن سازیم.'];
ayat[895]=[6,106,'اتَّبِعْ مَا أُوحِيَ إِلَيْكَ مِن رَّبِّكَ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ','از آنچه که از سوی پروردگارت بر تو وحی شده، پیروی کن! هیچ معبودی جز او نیست! و از مشرکان، روی بگردان!'];
ayat[896]=[6,107,'وَلَوْ شَاءَ اللَّهُ مَا أَشْرَكُوا ۗ وَمَا جَعَلْنَاكَ عَلَيْهِمْ حَفِيظًا ۖ وَمَا أَنتَ عَلَيْهِم بِوَكِيلٍ','اگر خدا می‌خواست، (همه به اجبار ایمان می‌آوردند،) و هیچ یک مشرک نمی‌شدند؛ و ما تو را مسؤول (اعمال) آنها قرار نداده‌ایم؛ و وظیفه نداری آنها را (به ایمان) مجبور سازی!'];
ayat[897]=[6,108,'وَلَا تَسُبُّوا الَّذِينَ يَدْعُونَ مِن دُونِ اللَّهِ فَيَسُبُّوا اللَّهَ عَدْوًا بِغَيْرِ عِلْمٍ ۗ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمْ ثُمَّ إِلَىٰ رَبِّهِم مَّرْجِعُهُمْ فَيُنَبِّئُهُم بِمَا كَانُوا يَعْمَلُونَ','(به معبود) کسانی که غیر خدا را می‌خوانند دشنام ندهید، مبادا آنها (نیز) از روی (ظلم و) جهل، خدا را دشنام دهند! اینچنین برای هر امّتی عملشان را زینت دادیم سپس بازگشت همه آنان به سوی پروردگارشان است؛ و آنها را از آنچه عمل می‌کردند، آگاه می‌سازد (و پاداش و کیفر می‌دهد).'];
ayat[898]=[6,109,'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِن جَاءَتْهُمْ آيَةٌ لَّيُؤْمِنُنَّ بِهَا ۚ قُلْ إِنَّمَا الْآيَاتُ عِندَ اللَّهِ ۖ وَمَا يُشْعِرُكُمْ أَنَّهَا إِذَا جَاءَتْ لَا يُؤْمِنُونَ','با نهایت اصرار، به خدا سوگند یاد کردند که اگر نشانه‌ای [= معجزه‌ای‌] برای آنان بیاید، حتماً به آن ایمان می‌آورند؛ بگو: «معجزات فقط از سوی خداست (و در اختیار من نیست که به میل شما معجزه‌ای بیاورم)؛ و شما از کجا می‌دانید که هرگاه معجزه‌ای بیاید (ایمان می‌آورند؟ خیر،) ایمان نمی‌آورند!»'];
ayat[899]=[6,110,'وَنُقَلِّبُ أَفْئِدَتَهُمْ وَأَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوا بِهِ أَوَّلَ مَرَّةٍ وَنَذَرُهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ','و ما دلها و چشمهای آنها را واژگونه می‌سازیم؛ (آری آنها ایمان نمی‌آورند) همان‌گونه که در آغاز، به آن ایمان نیاوردند! و آنان را در حال طغیان و سرکشی، به خود وامی‌گذاریم تا سرگردان شوند!'];
ayat[900]=[6,111,'۞ وَلَوْ أَنَّنَا نَزَّلْنَا إِلَيْهِمُ الْمَلَائِكَةَ وَكَلَّمَهُمُ الْمَوْتَىٰ وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلًا مَّا كَانُوا لِيُؤْمِنُوا إِلَّا أَن يَشَاءَ اللَّهُ وَلَٰكِنَّ أَكْثَرَهُمْ يَجْهَلُونَ','(و حتّی) اگر فرشتگان را بر آنها نازل می‌کردیم، و مردگان با آنان سخن می‌گفتند، و همه چیز را در برابر آنها جمع می‌نمودیم، هرگز ایمان نمی‌آوردند؛ مگر آنکه خدا بخواهد! ولی بیشتر آنها نمی‌دانند!'];
ayat[901]=[6,112,'وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيَاطِينَ الْإِنسِ وَالْجِنِّ يُوحِي بَعْضُهُمْ إِلَىٰ بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُورًا ۚ وَلَوْ شَاءَ رَبُّكَ مَا فَعَلُوهُ ۖ فَذَرْهُمْ وَمَا يَفْتَرُونَ','اینچنین در برابر هر پیامبری، دشمنی از شیاطین انس و جنّ قرار دادیم؛ آنها بطور سری (و درگوشی) سخنان فریبنده و بی‌اساس (برای اغفال مردم) به یکدیگر می‌گفتند؛ و اگر پروردگارت می‌خواست، چنین نمی‌کردند؛ (و می‌توانست جلو آنها را بگیرد؛ ولی اجبار سودی ندارد.) بنابر این، آنها و تهمتهایشان را به حال خود واگذار!'];
ayat[902]=[6,113,'وَلِتَصْغَىٰ إِلَيْهِ أَفْئِدَةُ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوا مَا هُم مُّقْتَرِفُونَ','نتیجه (وسوسه‌های شیطان و تبلیغات شیطان‌صفتان) این خواهد شد که دلهای منکران قیامت، به آنها متمایل گردد؛ و به آن راضی شوند؛ و هر گناهی که بخواهند، انجام دهند!'];
ayat[903]=[6,114,'أَفَغَيْرَ اللَّهِ أَبْتَغِي حَكَمًا وَهُوَ الَّذِي أَنزَلَ إِلَيْكُمُ الْكِتَابَ مُفَصَّلًا ۚ وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَعْلَمُونَ أَنَّهُ مُنَزَّلٌ مِّن رَّبِّكَ بِالْحَقِّ ۖ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ','(با این حال،) آیا غیر خدا را به داوری طلبم؟! در حالی که اوست که این کتاب آسمانی را، که همه چیز در آن آمده، به سوی شما فرستاده است؛ و کسانی که به آنها کتاب آسمانی داده‌ایم می‌دانند این کتاب، بحق از طرف پروردگارت نازل شده؛ بنابر این از تردیدکنندگان مباش!'];
ayat[904]=[6,115,'وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَعَدْلًا ۚ لَّا مُبَدِّلَ لِكَلِمَاتِهِ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ','و کلام پروردگار تو، با صدق و عدل، به حدّ تمام رسید؛ هیچ کس نمی‌تواند کلمات او را دگرگون سازد؛ و او شنونده داناست.'];
ayat[905]=[6,116,'وَإِن تُطِعْ أَكْثَرَ مَن فِي الْأَرْضِ يُضِلُّوكَ عَن سَبِيلِ اللَّهِ ۚ إِن يَتَّبِعُونَ إِلَّا الظَّنَّ وَإِنْ هُمْ إِلَّا يَخْرُصُونَ','اگر از بیشتر کسانی که در روی زمین هستند اطاعت کنی، تو را از راه خدا گمراه می کنند؛ (زیرا) آنها تنها از گمان پیروی می‌نمایند، و تخمین و حدس (واهی) می‌زنند.'];
ayat[906]=[6,117,'إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَن يَضِلُّ عَن سَبِيلِهِ ۖ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ','پروردگارت به کسانی که از راه او گمراه گشته‌اند، آگاهتر است؛ و همچنین از کسانی که هدایت یافته‌اند.'];
ayat[907]=[6,118,'فَكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ إِن كُنتُم بِآيَاتِهِ مُؤْمِنِينَ','از (گوشت) آنچه نام خدا (هنگام سر بریدن) بر آن گفته شده، بخورید (و غیر از آن نخورید) اگر به آیات او ایمان دارید!'];
ayat[908]=[6,119,'وَمَا لَكُمْ أَلَّا تَأْكُلُوا مِمَّا ذُكِرَ اسْمُ اللَّهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُم مَّا حَرَّمَ عَلَيْكُمْ إِلَّا مَا اضْطُرِرْتُمْ إِلَيْهِ ۗ وَإِنَّ كَثِيرًا لَّيُضِلُّونَ بِأَهْوَائِهِم بِغَيْرِ عِلْمٍ ۗ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِالْمُعْتَدِينَ','چرا از چیزها [= گوشتها] ئی که نام خدا بر آنها برده شده نمی‌خورید؟! در حالی که (خداوند) آنچه را بر شما حرام بوده، بیان کرده است! مگر اینکه ناچار باشید؛ (که در این صورت، خوردن از گوشت آن حیوانات جایز است.) و بسیاری از مردم، به خاطر هوی و هوس و بی‌دانشی، (دیگران را) گمراه می‌سازند؛ و پروردگارت، تجاوزکاران را بهتر می‌شناسد.'];
ayat[909]=[6,120,'وَذَرُوا ظَاهِرَ الْإِثْمِ وَبَاطِنَهُ ۚ إِنَّ الَّذِينَ يَكْسِبُونَ الْإِثْمَ سَيُجْزَوْنَ بِمَا كَانُوا يَقْتَرِفُونَ','گناهان آشکار و پنهان را رها کنید! زیرا کسانی که گناه می‌کنند، بزودی در برابر آنچه مرتکب می‌شدند، مجازات خواهند شد.'];
ayat[910]=[6,121,'وَلَا تَأْكُلُوا مِمَّا لَمْ يُذْكَرِ اسْمُ اللَّهِ عَلَيْهِ وَإِنَّهُ لَفِسْقٌ ۗ وَإِنَّ الشَّيَاطِينَ لَيُوحُونَ إِلَىٰ أَوْلِيَائِهِمْ لِيُجَادِلُوكُمْ ۖ وَإِنْ أَطَعْتُمُوهُمْ إِنَّكُمْ لَمُشْرِكُونَ','و از آنچه نام خدا بر آن برده نشده، نخورید! این کار گناه است؛ و شیاطین به دوستان خود مطالبی مخفیانه القا می‌کنند، تا با شما به مجادله برخیزند؛ اگر از آنها اطاعت کنید، شما هم مشرک خواهید بود!'];
ayat[911]=[6,122,'أَوَمَن كَانَ مَيْتًا فَأَحْيَيْنَاهُ وَجَعَلْنَا لَهُ نُورًا يَمْشِي بِهِ فِي النَّاسِ كَمَن مَّثَلُهُ فِي الظُّلُمَاتِ لَيْسَ بِخَارِجٍ مِّنْهَا ۚ كَذَٰلِكَ زُيِّنَ لِلْكَافِرِينَ مَا كَانُوا يَعْمَلُونَ','آیا کسی که مرده بود، سپس او را زنده کردیم، و نوری برایش قرار دادیم که با آن در میان مردم راه برود، همانند کسی است که در ظلمتها باشد و از آن خارج نگردد؟! این گونه برای کافران، اعمال (زشتی) که انجام می‌دادند، تزیین شده (و زیبا جلوه کرده) است.'];
ayat[912]=[6,123,'وَكَذَٰلِكَ جَعَلْنَا فِي كُلِّ قَرْيَةٍ أَكَابِرَ مُجْرِمِيهَا لِيَمْكُرُوا فِيهَا ۖ وَمَا يَمْكُرُونَ إِلَّا بِأَنفُسِهِمْ وَمَا يَشْعُرُونَ','و (نیز) این گونه در هر شهر و آبادی، بزرگان گنهکاری قرار دادیم؛ (افرادی که همه گونه قدرت در اختیارشان گذاردیم؛ اما آنها سوء استفاده کرده، و راه خطا پیش گرفتند؛) و سرانجام کارشان این شد که به مکر (و فریب مردم) پرداختند؛ ولی تنها خودشان را فریب می‌دهند و نمی‌فهمند!'];
ayat[913]=[6,124,'وَإِذَا جَاءَتْهُمْ آيَةٌ قَالُوا لَن نُّؤْمِنَ حَتَّىٰ نُؤْتَىٰ مِثْلَ مَا أُوتِيَ رُسُلُ اللَّهِ ۘ اللَّهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسَالَتَهُ ۗ سَيُصِيبُ الَّذِينَ أَجْرَمُوا صَغَارٌ عِندَ اللَّهِ وَعَذَابٌ شَدِيدٌ بِمَا كَانُوا يَمْكُرُونَ','و هنگامی که آیه‌ای برای آنها بیاید، می‌گویند: «ما هرگز ایمان نمی‌آوریم، مگر اینکه همانند چیزی که به پیامبران خدا داده شده، به ما هم داده شود!» خداوند آگاهتر است که رسالت خویش را کجا قرار دهد! بزودی کسانی که مرتکب گناه شدند، (و مردم را از راه حق منحرف ساختند،) در مقابل مکر (و فریب و نیرنگی) که می‌کردند، گرفتار حقارت در پیشگاه خدا، و عذاب شدید خواهند شد.'];
ayat[914]=[6,125,'فَمَن يُرِدِ اللَّهُ أَن يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلَامِ ۖ وَمَن يُرِدْ أَن يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقًا حَرَجًا كَأَنَّمَا يَصَّعَّدُ فِي السَّمَاءِ ۚ كَذَٰلِكَ يَجْعَلُ اللَّهُ الرِّجْسَ عَلَى الَّذِينَ لَا يُؤْمِنُونَ','آن کس را که خدا بخواهد هدایت کند، سینه‌اش را برای (پذیرش) اسلام، گشاده می سازد؛ و آن کس را که بخاطر اعمال خلافش بخواهد گمراه سازد، سینه‌اش را آنچنان تنگ می‌کند که گویا می‌خواهد به آسمان بالا برود؛ این گونه خداوند پلیدی را بر افرادی که ایمان نمی‌آورند قرار می‌دهد!'];
ayat[915]=[6,126,'وَهَٰذَا صِرَاطُ رَبِّكَ مُسْتَقِيمًا ۗ قَدْ فَصَّلْنَا الْآيَاتِ لِقَوْمٍ يَذَّكَّرُونَ','و این راه مستقیم (و سنت جاویدان) پروردگار توست؛ ما آیات خود را برای کسانی که پند می‌گیرند، بیان کردیم!'];
ayat[916]=[6,127,'۞ لَهُمْ دَارُ السَّلَامِ عِندَ رَبِّهِمْ ۖ وَهُوَ وَلِيُّهُم بِمَا كَانُوا يَعْمَلُونَ','برای آنها (در بهشت) خانه امن و امان نزد پروردگارشان خواهد بود؛ و او، ولیّ و یاور آنهاست بخاطر اعمال (نیکی) که انجام می‌دادند.'];
ayat[917]=[6,128,'وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا يَا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُم مِّنَ الْإِنسِ ۖ وَقَالَ أَوْلِيَاؤُهُم مِّنَ الْإِنسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَبَلَغْنَا أَجَلَنَا الَّذِي أَجَّلْتَ لَنَا ۚ قَالَ النَّارُ مَثْوَاكُمْ خَالِدِينَ فِيهَا إِلَّا مَا شَاءَ اللَّهُ ۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ','در آن روز که (خدا) همه آنها را جمع و محشور میسازد، (می‌گوید:) ای جمعیّت شیاطین و جن! ّ شما افراد زیادی از انسانها را گمراه ساختید! دوستان و پیروان آنها از میان انسانها می‌گویند: «پروردگارا! هر یک از ما دو گروه [= پیشوایان و پیروان‌گمراه‌] از دیگری استفاده کردیم؛ (ما به لذّات هوس آلود و زودگذر رسیدیم؛ و آنها بر ما حکومت کردند؛) و به اجلی که برای ما مقرّر داشته بودی رسیدیم.» (خداوند) می‌گوید: «آتش جایگاه شماست؛ جاودانه در آن خواهید ماند، مگر آنچه خدا بخواهد» پروردگار تو حکیم و داناست.'];
ayat[918]=[6,129,'وَكَذَٰلِكَ نُوَلِّي بَعْضَ الظَّالِمِينَ بَعْضًا بِمَا كَانُوا يَكْسِبُونَ','ما این‌گونه بعضی از ستمگران را به بعضی دیگر وامی‌گذاریم به سبب اعمالی که انجام می‌دادند.'];
ayat[919]=[6,130,'يَا مَعْشَرَ الْجِنِّ وَالْإِنسِ أَلَمْ يَأْتِكُمْ رُسُلٌ مِّنكُمْ يَقُصُّونَ عَلَيْكُمْ آيَاتِي وَيُنذِرُونَكُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا ۚ قَالُوا شَهِدْنَا عَلَىٰ أَنفُسِنَا ۖ وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا وَشَهِدُوا عَلَىٰ أَنفُسِهِمْ أَنَّهُمْ كَانُوا كَافِرِينَ','(در آن روز به آنها می‌گوید:) ای گروه جنّ و انس! آیا رسولانی از شما به سوی شما نیامدند که آیات مرا برایتان بازگو می‌کردند، و شما را از ملاقات چنین روزی بیم می دادند؟! آنها می‌گویند: «بر ضدّ خودمان گواهی می‌دهیم؛ (آری، ما بد کردیم.)» و زندگی (پر زرق و برق) دنیا آنها را فریب داد؛ و به زیان خود گواهی می‌دهند که کافر بودند!'];
ayat[920]=[6,131,'ذَٰلِكَ أَن لَّمْ يَكُن رَّبُّكَ مُهْلِكَ الْقُرَىٰ بِظُلْمٍ وَأَهْلُهَا غَافِلُونَ','این بخاطر آن است که پروردگارت هیچ‌گاه (مردم) شهرها و آبادیها را بخاطر ستمهایشان در حال غفلت و بی‌خبری هلاک نمی‌کند. (بلکه قبلاً رسولانی برای آنها می‌فرستد.)'];
ayat[921]=[6,132,'وَلِكُلٍّ دَرَجَاتٌ مِّمَّا عَمِلُوا ۚ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُونَ','و برای هر یک (از این دو دسته)، درجات (و مراتبی) است از آنچه عمل کردند؛ و پروردگارت از اعمالی که انجام می‌دهند، غافل نیست.'];
ayat[922]=[6,133,'وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ ۚ إِن يَشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِن بَعْدِكُم مَّا يَشَاءُ كَمَا أَنشَأَكُم مِّن ذُرِّيَّةِ قَوْمٍ آخَرِينَ','پروردگارت بی‌نیاز و مهربان است؛ (پس به کسی ستم نمی‌کند؛ بلکه همه، نتیجه اعمال خود را می‌گیرند؛) اگر بخواهد، همه شما را می‌برد؛ سپس هر کس را بخواهد جانشین شما می‌سازد؛ همان‌طور که شما را از نسل اقوام دیگری به وجود آورد.'];
ayat[923]=[6,134,'إِنَّ مَا تُوعَدُونَ لَآتٍ ۖ وَمَا أَنتُم بِمُعْجِزِينَ','آنچه به شما وعده داده می‌شود، یقیناً می‌آید؛ و شما نمی‌توانید (خدا را) ناتوان سازید (و از عدالت و کیفر او فرار کنید)!'];
ayat[924]=[6,135,'قُلْ يَا قَوْمِ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنِّي عَامِلٌ ۖ فَسَوْفَ تَعْلَمُونَ مَن تَكُونُ لَهُ عَاقِبَةُ الدَّارِ ۗ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ','بگو: «ای قوم من! هر کار در قدرت دارید بکنید! من (هم به وظیفه خود) عمل می‌کنم؛ امّا بزودی خواهید دانست چه کسی سرانجام نیک خواهد داشت (و پیروزی با چه کسی است! امّا) به یقین، ظالمان رستگار نخواهند شد!»'];
ayat[925]=[6,136,'وَجَعَلُوا لِلَّهِ مِمَّا ذَرَأَ مِنَ الْحَرْثِ وَالْأَنْعَامِ نَصِيبًا فَقَالُوا هَٰذَا لِلَّهِ بِزَعْمِهِمْ وَهَٰذَا لِشُرَكَائِنَا ۖ فَمَا كَانَ لِشُرَكَائِهِمْ فَلَا يَصِلُ إِلَى اللَّهِ ۖ وَمَا كَانَ لِلَّهِ فَهُوَ يَصِلُ إِلَىٰ شُرَكَائِهِمْ ۗ سَاءَ مَا يَحْكُمُونَ','آنها [= مشرکان‌] سهمی از آنچه خداوند از زراعت و چهارپایان آفریده، برای او قرار دادند؛ (و سهمی برای بتها!) و بگمان خود گفتند: «این مال خداست! و این هم مال شرکای ما [= یعنی بتها] است!» آنچه مال شرکای آنها بود، به خدا نمی‌رسید؛ ولی آنچه مال خدا بود، به شرکایشان می‌رسید! (آری، اگر سهم بتها با کمبودی مواجه می‌شد، مال خدا را به بتها می‌دادند؛ امّا عکس آن را مجاز نمی‌دانستند!) چه بد حکم می‌کنند (که علاوه بر شرک، حتّی خدا را کمتر از بتها می‌دانند)!'];
ayat[926]=[6,137,'وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٍ مِّنَ الْمُشْرِكِينَ قَتْلَ أَوْلَادِهِمْ شُرَكَاؤُهُمْ لِيُرْدُوهُمْ وَلِيَلْبِسُوا عَلَيْهِمْ دِينَهُمْ ۖ وَلَوْ شَاءَ اللَّهُ مَا فَعَلُوهُ ۖ فَذَرْهُمْ وَمَا يَفْتَرُونَ','همین‌گونه شرکای آنها [= بتها]، قتل فرزندانشان را در نظرشان جلوه دادند؛ (کودکان خود را قربانی بتها می‌کردند، و افتخار می‌نمودند!) سرانجام آنها را به هلاکت افکندند؛ و آیینشان را بر آنان مشتبه ساختند. و اگر خدا می‌خواست، چنین نمی‌کردند؛ (زیرا می‌توانست جلو آنان را بگیرد؛ ولی اجبار سودی ندارد.) بنابر این، آنها و تهمتهایشان را به حال خود واگذار (و به آنها اعتنا مکن)!'];
ayat[927]=[6,138,'وَقَالُوا هَٰذِهِ أَنْعَامٌ وَحَرْثٌ حِجْرٌ لَّا يَطْعَمُهَا إِلَّا مَن نَّشَاءُ بِزَعْمِهِمْ وَأَنْعَامٌ حُرِّمَتْ ظُهُورُهَا وَأَنْعَامٌ لَّا يَذْكُرُونَ اسْمَ اللَّهِ عَلَيْهَا افْتِرَاءً عَلَيْهِ ۚ سَيَجْزِيهِم بِمَا كَانُوا يَفْتَرُونَ','و گفتند: «این قسمت از چهارپایان و زراعت (که مخصوص بتهاست، برای همه) ممنوع است؛ و جز کسانی که ما بخواهیم -به گمان آنها- نباید از آن بخورند! و (اینها) چهارپایانی است که سوارشدن بر آنها (بر ما) حرام شده است!» و (نیز) چهارپایانی (بود) که (هنگام ذبح،) نام خدا را بر آن نمی‌بردند، و به خدا دروغ می‌بستند؛ (و می‌گفتند: «این احکام، همه از ناحیه اوست.») بزودی (خدا) کیفر افتراهای آنها را می‌دهد!'];
ayat[928]=[6,139,'وَقَالُوا مَا فِي بُطُونِ هَٰذِهِ الْأَنْعَامِ خَالِصَةٌ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰ أَزْوَاجِنَا ۖ وَإِن يَكُن مَّيْتَةً فَهُمْ فِيهِ شُرَكَاءُ ۚ سَيَجْزِيهِمْ وَصْفَهُمْ ۚ إِنَّهُ حَكِيمٌ عَلِيمٌ','و گفتند: «آنچه (از بچّه) در شکم این حیوانات است، مخصوص مردان ماست؛ و بر همسران ما حرام است! امّا اگر مرده باشد [= مرده متولّد شود]، همگی در آن شریکند.» بزودی (خدا) کیفر این توصیف (و احکام دروغین) آنها را می‌دهد؛ او حکیم و داناست.'];
ayat[929]=[6,140,'قَدْ خَسِرَ الَّذِينَ قَتَلُوا أَوْلَادَهُمْ سَفَهًا بِغَيْرِ عِلْمٍ وَحَرَّمُوا مَا رَزَقَهُمُ اللَّهُ افْتِرَاءً عَلَى اللَّهِ ۚ قَدْ ضَلُّوا وَمَا كَانُوا مُهْتَدِينَ','به یقین آنها که فرزندان خود را از روی جهل و نادانی کشتند، گرفتار خسران شدند؛ (زیرا) آنچه را خدا به آنها روزی داده بود، بر خود تحریم کردند؛ و بر خدا افترا بستند. آنها گمراه شدند؛ و (هرگز) هدایت نیافته بودند.'];
ayat[930]=[6,141,'۞ وَهُوَ الَّذِي أَنشَأَ جَنَّاتٍ مَّعْرُوشَاتٍ وَغَيْرَ مَعْرُوشَاتٍ وَالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا أُكُلُهُ وَالزَّيْتُونَ وَالرُّمَّانَ مُتَشَابِهًا وَغَيْرَ مُتَشَابِهٍ ۚ كُلُوا مِن ثَمَرِهِ إِذَا أَثْمَرَ وَآتُوا حَقَّهُ يَوْمَ حَصَادِهِ ۖ وَلَا تُسْرِفُوا ۚ إِنَّهُ لَا يُحِبُّ الْمُسْرِفِينَ','اوست که باغهای معروش [= باغهایی که درختانش روی داربست‌ها قرار دارد]، و باغهای غیرمعروش [= باغهایی که نیاز به داربست ندارد] را آفرید؛ همچنین نخل و انواع زراعت را، که از نظر میوه و طعم با هم متفاوتند؛ و (نیز) درخت زیتون و انار را، که از جهتی با هم شبیه، و از جهتی تفاوت دارند؛ (برگ و ساختمان ظاهریشان شبیه یکدیگر است، در حالی که طعم میوه آنها متفاوت می‌باشد.) از میوه آن، به هنگامی که به ثمر می‌نشیند، بخورید! و حقّ آن را به هنگام درو، بپردازید! و اسراف نکنید، که خداوند مسرفان را دوست ندارد!'];
ayat[931]=[6,142,'وَمِنَ الْأَنْعَامِ حَمُولَةً وَفَرْشًا ۚ كُلُوا مِمَّا رَزَقَكُمُ اللَّهُ وَلَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ إِنَّهُ لَكُمْ عَدُوٌّ مُّبِينٌ','(او کسی است که) از چهارپایان، برای شما حیوانات باربر، و حیوانات کوچک (برای منافع دیگر) آفرید؛ از آنچه به شما روزی داده است، بخورید! و از گامهای شیطان پیروی ننمایید، که او دشمن آشکار شماست!'];
ayat[932]=[6,143,'ثَمَانِيَةَ أَزْوَاجٍ ۖ مِّنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِ ۗ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحَامُ الْأُنثَيَيْنِ ۖ نَبِّئُونِي بِعِلْمٍ إِن كُنتُمْ صَادِقِينَ','هشت جفت از چهارپایان (برای شما) آفرید؛ از میش دو جفت، و از بز دو جفت؛ بگو: «آیا خداوند نرهای آنها را حرام کرده، یا ماده‌ها را؟ یا آنچه شکم ماده‌ها در برگرفته؟ اگر راست می‌گویید (و بر تحریم اینها دلیلی دارید)، به من خبر دهید!»'];
ayat[933]=[6,144,'وَمِنَ الْإِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِ ۗ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحَامُ الْأُنثَيَيْنِ ۖ أَمْ كُنتُمْ شُهَدَاءَ إِذْ وَصَّاكُمُ اللَّهُ بِهَٰذَا ۚ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا لِّيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍ ۗ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','و از شتر یک جفت، و از گاو هم یک جفت (برای شما آفرید)؛ بگو: «کدامیک از اینها را خدا حرام کرده است؟ نرها یا ماده‌ها را؟ یا آنچه را شکم ماده‌ها دربرگرفته؟ یا هنگامی که خدا شما را به این موضوع توصیه کرد، شما گواه (بر این تحریم) بودید؟! پس چه کسی ستمکارتر است از آن کس که بر خدا دروغ می‌بندد، تا مردم را از روی جهل گمراه سازد؟! خداوند هیچ گاه ستمگران را هدایت نمی‌کند»'];
ayat[934]=[6,145,'قُل لَّا أَجِدُ فِي مَا أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَىٰ طَاعِمٍ يَطْعَمُهُ إِلَّا أَن يَكُونَ مَيْتَةً أَوْ دَمًا مَّسْفُوحًا أَوْ لَحْمَ خِنزِيرٍ فَإِنَّهُ رِجْسٌ أَوْ فِسْقًا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَإِنَّ رَبَّكَ غَفُورٌ رَّحِيمٌ','بگو: «در آنچه بر من وحی شده، هیچ غذای حرامی نمی‌یابم؛ بجز اینکه مردار باشد، یا خونی که (از بدن حیوان) بیرون ریخته، یا گوشت خوک -که اینها همه پلیدند- یا حیوانی که به گناه، هنگام سر بریدن، نام غیر خدا [= نام بتها] بر آن برده شده است.» اما کسی که مضطر (به خوردن این محرمات) شود، بی آنکه خواهان لذت باشد و یا زیاده روی کند (گناهی بر او نیست)؛ زیرا پروردگارت، آمرزنده مهربان است.'];
ayat[935]=[6,146,'وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا كُلَّ ذِي ظُفُرٍ ۖ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَا إِلَّا مَا حَمَلَتْ ظُهُورُهُمَا أَوِ الْحَوَايَا أَوْ مَا اخْتَلَطَ بِعَظْمٍ ۚ ذَٰلِكَ جَزَيْنَاهُم بِبَغْيِهِمْ ۖ وَإِنَّا لَصَادِقُونَ','و بر یهودیان، هر حیوان ناخن‌دار [= حیواناتی که سم یکپارچه دارند] را حرام کردیم؛ و از گاو و گوسفند، پیه و چربیشان را بر آنان تحریم نمودیم؛ مگر چربیهایی که بر پشت آنها قرار دارد، و یا در دو طرف پهلوها، و یا آنها که با استخوان آمیخته است؛ این را بخاطر ستمی که می‌کردند به آنها کیفر دادیم؛ و ما راست می‌گوییم.'];
ayat[936]=[6,147,'فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمْ ذُو رَحْمَةٍ وَاسِعَةٍ وَلَا يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِمِينَ','اگر تو را تکذیب کنند (و این حقایق را نپذیرند)، به آنها بگو: «پروردگار شما، رحمت گسترده‌ای دارد؛ اما مجازات او هم از مجرمان دفع شدنی نیست! (و اگر ادامه دهید کیفر شما حتمی است)'];
ayat[937]=[6,148,'سَيَقُولُ الَّذِينَ أَشْرَكُوا لَوْ شَاءَ اللَّهُ مَا أَشْرَكْنَا وَلَا آبَاؤُنَا وَلَا حَرَّمْنَا مِن شَيْءٍ ۚ كَذَٰلِكَ كَذَّبَ الَّذِينَ مِن قَبْلِهِمْ حَتَّىٰ ذَاقُوا بَأْسَنَا ۗ قُلْ هَلْ عِندَكُم مِّنْ عِلْمٍ فَتُخْرِجُوهُ لَنَا ۖ إِن تَتَّبِعُونَ إِلَّا الظَّنَّ وَإِنْ أَنتُمْ إِلَّا تَخْرُصُونَ','بزودی مشرکان (برای تبرئه خویش) می‌گویند: «اگر خدا می‌خواست، نه ما مشرک می‌شدیم و نه پدران ما؛ و نه چیزی را تحریم می‌کردیم!» کسانی که پیش از آنها بودند نیز، همین گونه دروغ می‌گفتند؛ و سرانجام (طعم) کیفر ما را چشیدند. بگو: «آیا دلیل روشنی (بر این موضوع) دارید؟ پس آن را به ما نشان دهید؟ شما فقط از پندارهای بی‌اساس پیروی می‌کنید، و تخمینهای نابجا می‌زنید.»'];
ayat[938]=[6,149,'قُلْ فَلِلَّهِ الْحُجَّةُ الْبَالِغَةُ ۖ فَلَوْ شَاءَ لَهَدَاكُمْ أَجْمَعِينَ','بگو: «دلیل رسا (و قاطع) برای خداست (دلیلی که برای هیچ کس بهانه‌ای باقی نمی‌گذارد). و اگر او بخواهد، همه شما را (به اجبار) هدایت می‌کند. (ولی چون هدایت اجباری بی‌ثمر است، این کار را نمی‌کند.)»'];
ayat[939]=[6,150,'قُلْ هَلُمَّ شُهَدَاءَكُمُ الَّذِينَ يَشْهَدُونَ أَنَّ اللَّهَ حَرَّمَ هَٰذَا ۖ فَإِن شَهِدُوا فَلَا تَشْهَدْ مَعَهُمْ ۚ وَلَا تَتَّبِعْ أَهْوَاءَ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَالَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ وَهُم بِرَبِّهِمْ يَعْدِلُونَ','بگو: «گواهان خود را، که گواهی می‌دهند خداوند اینها را حرام کرده است، بیاورید!» اگر آنها (بدروغ) گواهی دهند، تو با آنان (همصدا نشو! و) گواهی نده! و از هوی و هوس کسانی که آیات ما را تکذیب کردند، و کسانی که به آخرت ایمان ندارند و برای خدا شریک قائلند، پیروی مکن!'];
ayat[940]=[6,151,'۞ قُلْ تَعَالَوْا أَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ ۖ أَلَّا تُشْرِكُوا بِهِ شَيْئًا ۖ وَبِالْوَالِدَيْنِ إِحْسَانًا ۖ وَلَا تَقْتُلُوا أَوْلَادَكُم مِّنْ إِمْلَاقٍ ۖ نَّحْنُ نَرْزُقُكُمْ وَإِيَّاهُمْ ۖ وَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ ۖ وَلَا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بِالْحَقِّ ۚ ذَٰلِكُمْ وَصَّاكُم بِهِ لَعَلَّكُمْ تَعْقِلُونَ','بگو: «بیایید آنچه را پروردگارتان بر شما حرام کرده است برایتان بخوانم: اینکه چیزی را شریک خدا قرار ندهید! و به پدر و مادر نیکی کنید! و فرزندانتان را از (ترس) فقر، نکشید! ما شما و آنها را روزی می‌دهیم؛ و نزدیک کارهای زشت نروید، چه آشکار باشد چه پنهان! و انسانی را که خداوند محترم شمرده، به قتل نرسانید! مگر بحق (و از روی استحقاق)؛ این چیزی است که خداوند شما را به آن سفارش کرده، شاید درک کنید!'];
ayat[941]=[6,152,'وَلَا تَقْرَبُوا مَالَ الْيَتِيمِ إِلَّا بِالَّتِي هِيَ أَحْسَنُ حَتَّىٰ يَبْلُغَ أَشُدَّهُ ۖ وَأَوْفُوا الْكَيْلَ وَالْمِيزَانَ بِالْقِسْطِ ۖ لَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا ۖ وَإِذَا قُلْتُمْ فَاعْدِلُوا وَلَوْ كَانَ ذَا قُرْبَىٰ ۖ وَبِعَهْدِ اللَّهِ أَوْفُوا ۚ ذَٰلِكُمْ وَصَّاكُم بِهِ لَعَلَّكُمْ تَذَكَّرُونَ','و به مال یتیم، جز به بهترین صورت (و برای اصلاح)، نزدیک نشوید، تا به حد رشد خود برسد! و حق پیمانه و وزن را بعدالت ادا کنید! -هیچ کس را، جز بمقدار تواناییش، تکلیف نمی‌کنیم- و هنگامی که سخنی می‌گویید، عدالت را رعایت نمایید، حتی اگر در مورد نزدیکان (شما) بوده باشد و به پیمان خدا وفا کنید، این چیزی است که خداوند شما را به آن سفارش می‌کند، تا متذکّر شوید!'];
ayat[942]=[6,153,'وَأَنَّ هَٰذَا صِرَاطِي مُسْتَقِيمًا فَاتَّبِعُوهُ ۖ وَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَن سَبِيلِهِ ۚ ذَٰلِكُمْ وَصَّاكُم بِهِ لَعَلَّكُمْ تَتَّقُونَ','این راه مستقیم من است، از آن پیروی کنید! و از راه‌های پراکنده (و انحرافی) پیروی نکنید، که شما را از طریق حق، دور می‌سازد! این چیزی است که خداوند شما را به آن سفارش می‌کند، شاید پرهیزگاری پیشه کنید!»'];
ayat[943]=[6,154,'ثُمَّ آتَيْنَا مُوسَى الْكِتَابَ تَمَامًا عَلَى الَّذِي أَحْسَنَ وَتَفْصِيلًا لِّكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لَّعَلَّهُم بِلِقَاءِ رَبِّهِمْ يُؤْمِنُونَ','سپس به موسی کتاب (آسمانی) دادیم؛ (و نعمت خود را) بر آنها که نیکوکار بودند، کامل کردیم؛ و همه چیز را (که مورد نیاز آنها بود، در آن) روشن ساختیم؛ کتابی که مایه هدایت و رحمت بود؛ شاید به لقای پروردگارشان (و روز رستاخیز)، ایمان بیاورند!'];
ayat[944]=[6,155,'وَهَٰذَا كِتَابٌ أَنزَلْنَاهُ مُبَارَكٌ فَاتَّبِعُوهُ وَاتَّقُوا لَعَلَّكُمْ تُرْحَمُونَ','و این کتابی است پر برکت، که ما (بر تو) نازل کردیم؛ از آن پیروی کنید، و پرهیزگاری پیشه نمائید، باشد که مورد رحمت (خدا) قرار گیرید!'];
ayat[945]=[6,156,'أَن تَقُولُوا إِنَّمَا أُنزِلَ الْكِتَابُ عَلَىٰ طَائِفَتَيْنِ مِن قَبْلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمْ لَغَافِلِينَ','(ما این کتاب را با این امتیازات نازل کردیم) تا نگویید: «کتاب آسمانی تنها بر دو طایفه پیش از ما [= یهود و نصاری‌] نازل شده بود؛ و ما از بحث و بررسی آنها بی خبر بودیم»!'];
ayat[946]=[6,157,'أَوْ تَقُولُوا لَوْ أَنَّا أُنزِلَ عَلَيْنَا الْكِتَابُ لَكُنَّا أَهْدَىٰ مِنْهُمْ ۚ فَقَدْ جَاءَكُم بَيِّنَةٌ مِّن رَّبِّكُمْ وَهُدًى وَرَحْمَةٌ ۚ فَمَنْ أَظْلَمُ مِمَّن كَذَّبَ بِآيَاتِ اللَّهِ وَصَدَفَ عَنْهَا ۗ سَنَجْزِي الَّذِينَ يَصْدِفُونَ عَنْ آيَاتِنَا سُوءَ الْعَذَابِ بِمَا كَانُوا يَصْدِفُونَ','یا نگویید: «اگر کتاب آسمانی بر ما نازل می‌شد، از آنها هدایت یافته‌تر بودیم»! اینک آیات و دلایل روشن از جانب پروردگارتان، و هدایت و رحمت برای شما آمد! پس، چه کسی ستمکارتر است از کسی که آیات خدا را تکذیب کرده، و از آن روی گردانده است؟! اما بزودی کسانی را که از آیات ما روی می‌گردانند، بخاطر همین اعراض بی‌دلیلشان، مجازات شدید خواهیم کرد!'];
ayat[947]=[6,158,'هَلْ يَنظُرُونَ إِلَّا أَن تَأْتِيَهُمُ الْمَلَائِكَةُ أَوْ يَأْتِيَ رَبُّكَ أَوْ يَأْتِيَ بَعْضُ آيَاتِ رَبِّكَ ۗ يَوْمَ يَأْتِي بَعْضُ آيَاتِ رَبِّكَ لَا يَنفَعُ نَفْسًا إِيمَانُهَا لَمْ تَكُنْ آمَنَتْ مِن قَبْلُ أَوْ كَسَبَتْ فِي إِيمَانِهَا خَيْرًا ۗ قُلِ انتَظِرُوا إِنَّا مُنتَظِرُونَ','آیا جز این انتظار دارند که فرشتگان (مرگ) به سراغشان آیند، یا خداوند (خودش) به سوی آنها بیاید، یا بعضی از آیات پروردگارت (و نشانه‌های رستاخیز)؟! اما آن روز که بعضی از آیات پروردگارت تحقّق پذیرد، ایمان‌آوردن افرادی که قبلاً ایمان نیاورده‌اند، یا در ایمانشان عمل نیکی انجام نداده‌اند، سودی به حالشان نخواهد داشت! بگو: «(اکنون که شما چنین انتظارات نادرستی دارید،) انتظار بکشید ما هم انتظار (کیفر شما را) می‌کشیم!»'];
ayat[948]=[6,159,'إِنَّ الَّذِينَ فَرَّقُوا دِينَهُمْ وَكَانُوا شِيَعًا لَّسْتَ مِنْهُمْ فِي شَيْءٍ ۚ إِنَّمَا أَمْرُهُمْ إِلَى اللَّهِ ثُمَّ يُنَبِّئُهُم بِمَا كَانُوا يَفْعَلُونَ','کسانی که آیین خود را پراکنده ساختند، و به دسته‌های گوناگون (و مذاهب مختلف) تقسیم شدند، تو هیچ گونه رابطه‌ای با آنها نداری! سر و کار آنها تنها با خداست؛ سپس خدا آنها را از آنچه انجام می‌دادند، با خبر می‌کند.'];
ayat[949]=[6,160,'مَن جَاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ أَمْثَالِهَا ۖ وَمَن جَاءَ بِالسَّيِّئَةِ فَلَا يُجْزَىٰ إِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُونَ','هر کس کار نیکی بجا آورد، ده برابر آن پاداش دارد، و هر کس کار بدی انجام دهد، جز بمانند آن، کیفر نخواهد دید؛ و ستمی بر آنها نخواهد شد.'];
ayat[950]=[6,161,'قُلْ إِنَّنِي هَدَانِي رَبِّي إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ دِينًا قِيَمًا مِّلَّةَ إِبْرَاهِيمَ حَنِيفًا ۚ وَمَا كَانَ مِنَ الْمُشْرِكِينَ','بگو: «پروردگارم مرا به راه راست هدایت کرده؛ آیینی پابرجا (و ضامن سعادت دین و دنیا)؛ آیین ابراهیم؛ که از آیینهای خرافی روی برگرداند؛ و از مشرکان نبود.»'];
ayat[951]=[6,162,'قُلْ إِنَّ صَلَاتِي وَنُسُكِي وَمَحْيَايَ وَمَمَاتِي لِلَّهِ رَبِّ الْعَالَمِينَ','بگو: «نماز و تمام عبادات من، و زندگی و مرگ من، همه برای خداوند پروردگار جهانیان است.'];
ayat[952]=[6,163,'لَا شَرِيكَ لَهُ ۖ وَبِذَٰلِكَ أُمِرْتُ وَأَنَا أَوَّلُ الْمُسْلِمِينَ','همتایی برای او نیست؛ و به همین مأمور شده‌ام؛ و من نخستین مسلمانم!»'];
ayat[953]=[6,164,'قُلْ أَغَيْرَ اللَّهِ أَبْغِي رَبًّا وَهُوَ رَبُّ كُلِّ شَيْءٍ ۚ وَلَا تَكْسِبُ كُلُّ نَفْسٍ إِلَّا عَلَيْهَا ۚ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرْجِعُكُمْ فَيُنَبِّئُكُم بِمَا كُنتُمْ فِيهِ تَخْتَلِفُونَ','بگو: «آیا غیر خدا، پروردگاری را بطلبم، در حالی که او پروردگار همه چیز است؟! هیچ کس، عمل (بدی) جز به زیان خودش، انجام نمی‌دهد؛ و هیچ گنهکاری گناه دیگری را متحمّل نمی‌شود؛ سپس بازگشت همه شما به سوی پروردگارتان است؛ و شما را از آنچه در آن اختلاف داشتید، خبر خواهد داد.'];
ayat[954]=[6,165,'وَهُوَ الَّذِي جَعَلَكُمْ خَلَائِفَ الْأَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِّيَبْلُوَكُمْ فِي مَا آتَاكُمْ ۗ إِنَّ رَبَّكَ سَرِيعُ الْعِقَابِ وَإِنَّهُ لَغَفُورٌ رَّحِيمٌ','و او کسی است که شما را جانشینان (و نمایندگان) خود در زمین ساخت، و درجات بعضی از شما را بالاتر از بعضی دیگر قرار داد، تا شما را به وسیله آنچه در اختیارتان قرار داده بیازماید؛ به یقین پروردگار تو سریع العقاب و آمرزنده مهربان است. (کیفر کسانی را که از بوته امتحان نادرست درآیند، زود می‌دهد؛ و نسبت به حق پویان مهربان است.)'];
ayat[955]=[7,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ المص','المص‌'];
ayat[956]=[7,2,'كِتَابٌ أُنزِلَ إِلَيْكَ فَلَا يَكُن فِي صَدْرِكَ حَرَجٌ مِّنْهُ لِتُنذِرَ بِهِ وَذِكْرَىٰ لِلْمُؤْمِنِينَ','این کتابی است که بر تو نازل شده؛ و نباید از ناحیه آن، ناراحتی در سینه داشته باشی! تا به وسیله آن، (مردم را از عواقب سوء عقاید و اعمال نادرستشان) بیم دهی؛ و تذکّری است برای مؤمنان.'];
ayat[957]=[7,3,'اتَّبِعُوا مَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُمْ وَلَا تَتَّبِعُوا مِن دُونِهِ أَوْلِيَاءَ ۗ قَلِيلًا مَّا تَذَكَّرُونَ','از چیزی که از طرف پروردگارتان بر شما نازل شده، پیروی کنید! و از اولیا و معبودهای دیگر جز او، پیروی نکنید! اما کمتر متذکّر می‌شوید!'];
ayat[958]=[7,4,'وَكَم مِّن قَرْيَةٍ أَهْلَكْنَاهَا فَجَاءَهَا بَأْسُنَا بَيَاتًا أَوْ هُمْ قَائِلُونَ','چه بسیار شهرها و آبادیها که آنها را (بر اثر گناه فراوانشان) هلاک کردیم! و عذاب ما شب‌هنگام، یا در روز هنگامی که استراحت کرده بودند، به سراغشان آمد.'];
ayat[959]=[7,5,'فَمَا كَانَ دَعْوَاهُمْ إِذْ جَاءَهُم بَأْسُنَا إِلَّا أَن قَالُوا إِنَّا كُنَّا ظَالِمِينَ','و در آن موقع که عذاب ما به سراغ آنها آمد، سخنی نداشتند جز اینکه گفتند: «ما ظالم بودیم!» (ولی این اعتراف به گناه، دیگر دیر شده بود؛ و سودی به حالشان نداشت.)'];
ayat[960]=[7,6,'فَلَنَسْأَلَنَّ الَّذِينَ أُرْسِلَ إِلَيْهِمْ وَلَنَسْأَلَنَّ الْمُرْسَلِينَ','به یقین، (هم) از کسانی که پیامبران به سوی آنها فرستاده شدند سؤال خواهیم کرد؛ (و هم) از پیامبران سؤال می‌کنیم!'];
ayat[961]=[7,7,'فَلَنَقُصَّنَّ عَلَيْهِم بِعِلْمٍ ۖ وَمَا كُنَّا غَائِبِينَ','و مسلماً (اعمالشان را) با علم (خود) برای آنان شرح خواهیم داد؛ و ما هرگز غایب نبودیم (بلکه همه جا حاضر و ناظر اعمال بندگان هستیم)!'];
ayat[962]=[7,8,'وَالْوَزْنُ يَوْمَئِذٍ الْحَقُّ ۚ فَمَن ثَقُلَتْ مَوَازِينُهُ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','وزن کردن (اعمال، و سنجش ارزش آنها) در آن روز، حقّ است! کسانی که میزانهای (عمل) آنها سنگین است، همان رستگارانند!'];
ayat[963]=[7,9,'وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَٰئِكَ الَّذِينَ خَسِرُوا أَنفُسَهُم بِمَا كَانُوا بِآيَاتِنَا يَظْلِمُونَ','و کسانی که میزانهای (عمل) آنها سبک است، افرادی هستند که سرمایه وجود خود را، بخاطر ظلم و ستمی که نسبت به آیات ما می‌کردند، از دست داده‌اند.'];
ayat[964]=[7,10,'وَلَقَدْ مَكَّنَّاكُمْ فِي الْأَرْضِ وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ ۗ قَلِيلًا مَّا تَشْكُرُونَ','ما تسلّط و مالکیّت و حکومت بر زمین را برای شما قرار دادیم؛ و انواع وسایل زندگی را برای شما فراهم ساختیم؛ اما کمتر شکرگزاری می‌کنید!'];
ayat[965]=[7,11,'وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ لَمْ يَكُن مِّنَ السَّاجِدِينَ','ما شما را آفریدیم؛ سپس صورت بندی کردیم؛ بعد به فرشتگان گفتیم: «برای آدم خضوع کنید!» آنها همه سجده کردند؛ جز ابلیس که از سجده‌کنندگان نبود.'];
ayat[966]=[7,12,'قَالَ مَا مَنَعَكَ أَلَّا تَسْجُدَ إِذْ أَمَرْتُكَ ۖ قَالَ أَنَا خَيْرٌ مِّنْهُ خَلَقْتَنِي مِن نَّارٍ وَخَلَقْتَهُ مِن طِينٍ','(خداوند به او) فرمود: «در آن هنگام که به تو فرمان دادم، چه چیز تو را مانع شد که سجده کنی؟» گفت: «من از او بهترم؛ مرا از آتش آفریده‌ای و او را از گل!»'];
ayat[967]=[7,13,'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُونُ لَكَ أَن تَتَكَبَّرَ فِيهَا فَاخْرُجْ إِنَّكَ مِنَ الصَّاغِرِينَ','گفت: «از آن (مقام و مرتبه‌ات) فرود آی! تو حقّ نداری در آن (مقام و مرتبه) تکبّر کنی! بیرون رو، که تو از افراد پست و کوچکی!'];
ayat[968]=[7,14,'قَالَ أَنظِرْنِي إِلَىٰ يَوْمِ يُبْعَثُونَ','گفت: «مرا تا روزی که (مردم) برانگیخته می‌شوند مهلت ده (و زنده بگذار!)»'];
ayat[969]=[7,15,'قَالَ إِنَّكَ مِنَ الْمُنظَرِينَ','فرمود: «تو از مهلت داده شدگانی!»'];
ayat[970]=[7,16,'قَالَ فَبِمَا أَغْوَيْتَنِي لَأَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيمَ','گفت: «اکنون که مرا گمراه ساختی، من بر سر راه مستقیم تو، در برابر آنها کمین می کنم!'];
ayat[971]=[7,17,'ثُمَّ لَآتِيَنَّهُم مِّن بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَانِهِمْ وَعَن شَمَائِلِهِمْ ۖ وَلَا تَجِدُ أَكْثَرَهُمْ شَاكِرِينَ','سپس از پیش رو و از پشت سر، و از طرف راست و از طرف چپ آنها، به سراغشان می‌روم؛ و بیشتر آنها را شکرگزار نخواهی یافت!»'];
ayat[972]=[7,18,'قَالَ اخْرُجْ مِنْهَا مَذْءُومًا مَّدْحُورًا ۖ لَّمَن تَبِعَكَ مِنْهُمْ لَأَمْلَأَنَّ جَهَنَّمَ مِنكُمْ أَجْمَعِينَ','فرمود: «از آن (مقام)، با ننگ و عار و خواری، بیرون رو! و سوگند یاد می‌کنم که هر کس از آنها از تو پیروی کند، جهنم را از شما همگی پر می‌کنم!'];
ayat[973]=[7,19,'وَيَا آدَمُ اسْكُنْ أَنتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هَٰذِهِ الشَّجَرَةَ فَتَكُونَا مِنَ الظَّالِمِينَ','و ای آدم! تو و همسرت در بهشت ساکن شوید! و از هر جا که خواستید، بخورید! امّا به این درخت نزدیک نشوید، که از ستمکاران خواهید بود!»'];
ayat[974]=[7,20,'فَوَسْوَسَ لَهُمَا الشَّيْطَانُ لِيُبْدِيَ لَهُمَا مَا وُورِيَ عَنْهُمَا مِن سَوْآتِهِمَا وَقَالَ مَا نَهَاكُمَا رَبُّكُمَا عَنْ هَٰذِهِ الشَّجَرَةِ إِلَّا أَن تَكُونَا مَلَكَيْنِ أَوْ تَكُونَا مِنَ الْخَالِدِينَ','سپس شیطان آن دو را وسوسه کرد، تا آنچه را از اندامشان پنهان بود، آشکار سازد؛ و گفت: «پروردگارتان شما را از این درخت نهی نکرده مگر بخاطر اینکه (اگر از آن بخورید،) فرشته خواهید شد، یا جاودانه (در بهشت) خواهید ماند!»'];
ayat[975]=[7,21,'وَقَاسَمَهُمَا إِنِّي لَكُمَا لَمِنَ النَّاصِحِينَ','و برای آنها سوگند یاد کرد که من برای شما از خیرخواهانم.'];
ayat[976]=[7,22,'فَدَلَّاهُمَا بِغُرُورٍ ۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْآتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِن وَرَقِ الْجَنَّةِ ۖ وَنَادَاهُمَا رَبُّهُمَا أَلَمْ أَنْهَكُمَا عَن تِلْكُمَا الشَّجَرَةِ وَأَقُل لَّكُمَا إِنَّ الشَّيْطَانَ لَكُمَا عَدُوٌّ مُّبِينٌ','و به این ترتیب، آنها را با فریب (از مقامشان) فرودآورد. و هنگامی که از آن درخت چشیدند، اندامشان [= عورتشان‌] بر آنها آشکار شد؛ و شروع کردند به قرار دادن برگهای (درختان) بهشتی بر خود، تا آن را بپوشانند. و پروردگارشان آنها را نداد داد که: «آیا شما را از آن درخت نهی نکردم؟! و نگفتم که شیطان برای شما دشمن آشکاری است؟!»'];
ayat[977]=[7,23,'قَالَا رَبَّنَا ظَلَمْنَا أَنفُسَنَا وَإِن لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ','گفتند: «پروردگارا! ما به خویشتن ستم کردیم! و اگر ما را نبخشی و بر ما رحم نکنی، از زیانکاران خواهیم بود!»'];
ayat[978]=[7,24,'قَالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ وَلَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَمَتَاعٌ إِلَىٰ حِينٍ','فرمود: «(از مقام خویش،) فرود آیید، در حالی که بعضی از شما نسبت به بعض دیگر، دشمن خواهید بود! (شیطان دشمن شماست، و شما دشمن او!) و برای شما در زمین، قرارگاه و وسیله بهره‌گیری تا زمان معینی خواهد بود.»'];
ayat[979]=[7,25,'قَالَ فِيهَا تَحْيَوْنَ وَفِيهَا تَمُوتُونَ وَمِنْهَا تُخْرَجُونَ','فرمود: «در آن [= زمین‌] زنده می‌شوید؛ و در آن می‌میرید؛ و (در رستاخیز) از آن خارج خواهید شد.»'];
ayat[980]=[7,26,'يَا بَنِي آدَمَ قَدْ أَنزَلْنَا عَلَيْكُمْ لِبَاسًا يُوَارِي سَوْآتِكُمْ وَرِيشًا ۖ وَلِبَاسُ التَّقْوَىٰ ذَٰلِكَ خَيْرٌ ۚ ذَٰلِكَ مِنْ آيَاتِ اللَّهِ لَعَلَّهُمْ يَذَّكَّرُونَ','ای فرزندان آدم! لباسی برای شما فرستادیم که اندام شما را می‌پوشاند و مایه زینت شماست؛ اما لباس پرهیزگاری بهتر است! اینها (همه) از آیات خداست، تا متذکّر (نعمتهای او) شوند!'];
ayat[981]=[7,27,'يَا بَنِي آدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطَانُ كَمَا أَخْرَجَ أَبَوَيْكُم مِّنَ الْجَنَّةِ يَنزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْآتِهِمَا ۗ إِنَّهُ يَرَاكُمْ هُوَ وَقَبِيلُهُ مِنْ حَيْثُ لَا تَرَوْنَهُمْ ۗ إِنَّا جَعَلْنَا الشَّيَاطِينَ أَوْلِيَاءَ لِلَّذِينَ لَا يُؤْمِنُونَ','ای فرزندان آدم! شیطان شما را نفریبد، آن گونه که پدر و مادر شما را از بهشت بیرون کرد، و لباسشان را از تنشان بیرون ساخت تا عورتشان را به آنها نشان دهد! چه اینکه او و همکارانش شما را می‌بینند از جایی که شما آنها را نمی‌بینید؛ (امّا بدانید) ما شیاطین را اولیای کسانی قرار دادیم که ایمان نمی‌آورند!'];
ayat[982]=[7,28,'وَإِذَا فَعَلُوا فَاحِشَةً قَالُوا وَجَدْنَا عَلَيْهَا آبَاءَنَا وَاللَّهُ أَمَرَنَا بِهَا ۗ قُلْ إِنَّ اللَّهَ لَا يَأْمُرُ بِالْفَحْشَاءِ ۖ أَتَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ','و هنگامی که کار زشتی انجام می‌دهند می‌گویند: «پدران خود را بر این عمل یافتیم؛ و خداوند ما را به آن دستور داده است!» بگو: «خداوند (هرگز) به کار زشت فرمان نمی‌دهد! آیا چیزی به خدا نسبت می‌دهید که نمی‌دانید؟!»'];
ayat[983]=[7,29,'قُلْ أَمَرَ رَبِّي بِالْقِسْطِ ۖ وَأَقِيمُوا وُجُوهَكُمْ عِندَ كُلِّ مَسْجِدٍ وَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ ۚ كَمَا بَدَأَكُمْ تَعُودُونَ','بگو: «پروردگارم امر به عدالت کرده است؛ و توجّه خویش را در هر مسجد (و به هنگام عبادت) به سوی او کنید! و او را بخوانید، در حالی که دین (خود) را برای او خالص گردانید! (و بدانید) همان گونه که در آغاز شما را آفرید، (بار دیگر در رستاخیز) بازمی‌گردید!'];
ayat[984]=[7,30,'فَرِيقًا هَدَىٰ وَفَرِيقًا حَقَّ عَلَيْهِمُ الضَّلَالَةُ ۗ إِنَّهُمُ اتَّخَذُوا الشَّيَاطِينَ أَوْلِيَاءَ مِن دُونِ اللَّهِ وَيَحْسَبُونَ أَنَّهُم مُّهْتَدُونَ','جمعی را هدایت کرده؛ و جمعی (که شایستگی نداشته‌اند،) گمراهی بر آنها مسلّم شده است. آنها (کسانی هستند که) شیاطین را به جای خداوند، اولیای خود انتخاب کردند؛ و گمان می‌کنند هدایت یافته‌اند!'];
ayat[985]=[7,31,'۞ يَا بَنِي آدَمَ خُذُوا زِينَتَكُمْ عِندَ كُلِّ مَسْجِدٍ وَكُلُوا وَاشْرَبُوا وَلَا تُسْرِفُوا ۚ إِنَّهُ لَا يُحِبُّ الْمُسْرِفِينَ','ای فرزندان آدم! زینت خود را به هنگام رفتن به مسجد، با خود بردارید! و (از نعمتهای الهی) بخورید و بیاشامید، ولی اسراف نکنید که خداوند مسرفان را دوست نمی‌دارد!'];
ayat[986]=[7,32,'قُلْ مَنْ حَرَّمَ زِينَةَ اللَّهِ الَّتِي أَخْرَجَ لِعِبَادِهِ وَالطَّيِّبَاتِ مِنَ الرِّزْقِ ۚ قُلْ هِيَ لِلَّذِينَ آمَنُوا فِي الْحَيَاةِ الدُّنْيَا خَالِصَةً يَوْمَ الْقِيَامَةِ ۗ كَذَٰلِكَ نُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ','بگو: «چه کسی زینتهای الهی را که برای بندگان خود آفریده، و روزیهای پاکیزه را حرام کرده است؟!» بگو: «اینها در زندگی دنیا، برای کسانی است که ایمان آورده‌اند؛ (اگر چه دیگران نیز با آنها مشارکت دارند؛ ولی) در قیامت، خالص (برای مؤمنان) خواهد بود.» این گونه آیات (خود) را برای کسانی که آگاهند، شرح می‌دهیم!'];
ayat[987]=[7,33,'قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْإِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَأَن تُشْرِكُوا بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا وَأَن تَقُولُوا عَلَى اللَّهِ مَا لَا تَعْلَمُونَ','بگو: «خداوند، تنها اعمال زشت را، چه آشکار باشد چه پنهان، حرام کرده است؛ و (همچنین) گناه و ستم بناحق را؛ و اینکه چیزی را که خداوند دلیلی برای آن نازل نکرده، شریک او قرار دهید؛ و به خدا مطلبی نسبت دهید که نمی‌دانید.»'];
ayat[988]=[7,34,'وَلِكُلِّ أُمَّةٍ أَجَلٌ ۖ فَإِذَا جَاءَ أَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً ۖ وَلَا يَسْتَقْدِمُونَ','برای هر قوم و جمعیّتی، زمان و سرآمد (معیّنی) است؛ و هنگامی که سرآمد آنها فرا رسد، نه ساعتی از آن تأخیر می‌کنند، و نه بر آن پیشی می‌گیرند.'];
ayat[989]=[7,35,'يَا بَنِي آدَمَ إِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِّنكُمْ يَقُصُّونَ عَلَيْكُمْ آيَاتِي ۙ فَمَنِ اتَّقَىٰ وَأَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','ای فرزندان آدم! اگر رسولانی از خود شما به سراغتان بیایند که آیات مرا برای شما بازگو کنند، (از آنها پیروی کنید؛) کسانی که پرهیزگاری پیشه کنند و عمل صالح انجام دهند (و در اصلاح خویش و دیگران بکوشند)، نه ترسی بر آنهاست و نه غمناک می شوند.'];
ayat[990]=[7,36,'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','و آنها که آیات ما را تکذیب کنند، و در برابر آن تکبّر ورزند، اهل دوزخند؛ جاودانه در آن خواهند ماند.'];
ayat[991]=[7,37,'فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ ۚ أُولَٰئِكَ يَنَالُهُمْ نَصِيبُهُم مِّنَ الْكِتَابِ ۖ حَتَّىٰ إِذَا جَاءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْ قَالُوا أَيْنَ مَا كُنتُمْ تَدْعُونَ مِن دُونِ اللَّهِ ۖ قَالُوا ضَلُّوا عَنَّا وَشَهِدُوا عَلَىٰ أَنفُسِهِمْ أَنَّهُمْ كَانُوا كَافِرِينَ','چه کسی ستمکارتر است از آنها که بر خدا دروغ می‌بندند، یا آیات او را تکذیب می کنند؟! آنها نصیبشان را از آنچه مقدّر شده (از نعمتها و مواهب این جهان) می‌برند؛ تا زمانی که فرستادگان ما [= فرشتگان قبض ارواح‌] به سراغشان روند و جانشان را بگیرند؛ از آنها می‌پرسند: «کجایند معبودهایی که غیر از خدا می‌خواندید؟! (چرا به یاری شما نمی‌آیند؟!)» می‌گویند: «آنها (همه) گم شدند (و از ما دور گشتند!)» و بر ضدّ خود گواهی می‌دهند که کافر بودند!'];
ayat[992]=[7,38,'قَالَ ادْخُلُوا فِي أُمَمٍ قَدْ خَلَتْ مِن قَبْلِكُم مِّنَ الْجِنِّ وَالْإِنسِ فِي النَّارِ ۖ كُلَّمَا دَخَلَتْ أُمَّةٌ لَّعَنَتْ أُخْتَهَا ۖ حَتَّىٰ إِذَا ادَّارَكُوا فِيهَا جَمِيعًا قَالَتْ أُخْرَاهُمْ لِأُولَاهُمْ رَبَّنَا هَٰؤُلَاءِ أَضَلُّونَا فَآتِهِمْ عَذَابًا ضِعْفًا مِّنَ النَّارِ ۖ قَالَ لِكُلٍّ ضِعْفٌ وَلَٰكِن لَّا تَعْلَمُونَ','(خداوند به آنها) می‌گوید: «در صفّ گروه‌های مشابه خود از جنّ و انس در آتش وارد شوید!» هر زمان که گروهی وارد می‌شوند، گروه دیگر را لعن می‌کنند؛ تا همگی با ذلّت در آن قرار گیرند. (در این هنگام) گروه پیروان درباره پیشوایان خود می‌گویند: «خداوندا! اینها بودند که ما را گمراه ساختند؛ پس کیفر آنها را از آتش دو برابر کن! (کیفری برای گمراهیشان، و کیفری بخاطر گمراه ساختن ما.)» می‌فرماید: «برای هر کدام (از شما) عذاب مضاعف است؛ ولی نمی‌دانید! (چرا که پیروان اگر گرد پیشوایان گمراه را نگرفته بودند، قدرتی بر اغوای مردم نداشتند.)»'];
ayat[993]=[7,39,'وَقَالَتْ أُولَاهُمْ لِأُخْرَاهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِن فَضْلٍ فَذُوقُوا الْعَذَابَ بِمَا كُنتُمْ تَكْسِبُونَ','و پیشوایان آنها به پیروان خود می‌گویند: «شما امتیازی بر ما نداشتید؛ پس بچشید عذاب (الهی) را در برابر آنچه انجام می‌دادید!'];
ayat[994]=[7,40,'إِنَّ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَاسْتَكْبَرُوا عَنْهَا لَا تُفَتَّحُ لَهُمْ أَبْوَابُ السَّمَاءِ وَلَا يَدْخُلُونَ الْجَنَّةَ حَتَّىٰ يَلِجَ الْجَمَلُ فِي سَمِّ الْخِيَاطِ ۚ وَكَذَٰلِكَ نَجْزِي الْمُجْرِمِينَ','کسانی که آیات ما را تکذیب کردند، و در برابر آن تکبّر ورزیدند، (هرگز) درهای آسمان به رویشان گشوده نمی‌شود؛ و (هیچ گاه) داخل بهشت نخواهند شد مگر اینکه شتر از سوراخ سوزن بگذرد! این گونه، گنهکاران را جزا می‌دهیم!'];
ayat[995]=[7,41,'لَهُم مِّن جَهَنَّمَ مِهَادٌ وَمِن فَوْقِهِمْ غَوَاشٍ ۚ وَكَذَٰلِكَ نَجْزِي الظَّالِمِينَ','برای آنها بستری از (آتش) دوزخ، و روی آنها پوششهایی (از آن) است؛ و اینچنین ظالمان را جزا میدهیم!'];
ayat[996]=[7,42,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ','و کسانی که ایمان آورده و اعمال صالح انجام داده‌اند -البته هیچ کس را جز به اندازه تواناییش تکلیف نمی‌کنیم- آنها اهل بهشتند؛ و جاودانه در آن خواهند ماند.'];
ayat[997]=[7,43,'وَنَزَعْنَا مَا فِي صُدُورِهِم مِّنْ غِلٍّ تَجْرِي مِن تَحْتِهِمُ الْأَنْهَارُ ۖ وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي هَدَانَا لِهَٰذَا وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَا أَنْ هَدَانَا اللَّهُ ۖ لَقَدْ جَاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ ۖ وَنُودُوا أَن تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوهَا بِمَا كُنتُمْ تَعْمَلُونَ','و آنچه در دلها از کینه و حسد دارند، برمی‌کنیم (تا در صفا و صمیمیّت با هم زندگی کنند)؛ و از زیر (قصرها و درختان) آنها، نهرها جریان دارد؛ می‌گویند: «ستایش مخصوص خداوندی است که ما را به این (همه نعمتها) رهنمون شد؛ و اگر خدا ما را هدایت نکرده بود، ما (به اینها) راه نمی‌یافتیم! مسلّماً فرستادگان پروردگار ما حق را آوردند!» و (در این هنگام) به آنان ندا داده می‌شود که: «این بهشت را در برابر اعمالی که انجام می‌دادید، به ارث بردید!»'];
ayat[998]=[7,44,'وَنَادَىٰ أَصْحَابُ الْجَنَّةِ أَصْحَابَ النَّارِ أَن قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدتُّم مَّا وَعَدَ رَبُّكُمْ حَقًّا ۖ قَالُوا نَعَمْ ۚ فَأَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ أَن لَّعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ','و بهشتیان دوزخیان را صدا می‌زنند که: «آنچه را پروردگارمان به ما وعده داده بود، همه را حق یافتیم؛ آیا شما هم آنچه را پروردگارتان به شما وعده داده بود حق یافتید؟!» گفتند: «بله!» در این هنگام، ندادهنده‌ای در میان آنها ندا می‌دهد که: «لعنت خدا بر ستمگران باد!'];
ayat[999]=[7,45,'الَّذِينَ يَصُدُّونَ عَن سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا وَهُم بِالْآخِرَةِ كَافِرُونَ','همانها که (مردم را) از راه خدا بازمی‌دارند، و (با القای شبهات) می‌خواهند آن را کج و معوج نشان دهند؛ و آنها به آخرت کافرند!»'];
ayat[1000]=[7,46,'وَبَيْنَهُمَا حِجَابٌ ۚ وَعَلَى الْأَعْرَافِ رِجَالٌ يَعْرِفُونَ كُلًّا بِسِيمَاهُمْ ۚ وَنَادَوْا أَصْحَابَ الْجَنَّةِ أَن سَلَامٌ عَلَيْكُمْ ۚ لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَ','و در میان آن دو [= بهشتیان و دوزخیان‌]، حجابی است؛ و بر «اعراف» مردانی هستند که هر یک از آن دو را از چهره‌شان می‌شناسند؛ و به بهشتیان صدا می‌زنند که: «درود بر شما باد!» امّا داخل بهشت نمی‌شوند، در حالی که امید آن را دارند.'];
ayat[1001]=[7,47,'۞ وَإِذَا صُرِفَتْ أَبْصَارُهُمْ تِلْقَاءَ أَصْحَابِ النَّارِ قَالُوا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظَّالِمِينَ','و هنگامی که چشمشان به دوزخیان می‌افتد می‌گویند: «پروردگارا! ما را با گروه ستمگران قرار مده!»'];
ayat[1002]=[7,48,'وَنَادَىٰ أَصْحَابُ الْأَعْرَافِ رِجَالًا يَعْرِفُونَهُم بِسِيمَاهُمْ قَالُوا مَا أَغْنَىٰ عَنكُمْ جَمْعُكُمْ وَمَا كُنتُمْ تَسْتَكْبِرُونَ','و اصحاب اعراف، مردانی (از دوزخیان را) که از سیمایشان آنها را می‌شناسند، صدا می‌زنند و می‌گویند: «(دیدید که) گردآوری شما (از مال و ثروت و آن و فرزند) و تکبّرهای شما، به حالتان سودی نداد!»'];
ayat[1003]=[7,49,'أَهَٰؤُلَاءِ الَّذِينَ أَقْسَمْتُمْ لَا يَنَالُهُمُ اللَّهُ بِرَحْمَةٍ ۚ ادْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَا أَنتُمْ تَحْزَنُونَ','آیا اینها [= این واماندگان بر اعراف‌] همانان نیستند که سوگند یاد کردید رحمت خدا هرگز شامل حالشان نخواهد شد؟! (ولی خداوند بخاطر ایمان و بعضی اعمال خیرشان، آنها را بخشید؛ هم اکنون به آنها گفته می‌شود:) داخل بهشت شوید، که نه ترسی دارید و نه غمناک می‌شوید!'];
ayat[1004]=[7,50,'وَنَادَىٰ أَصْحَابُ النَّارِ أَصْحَابَ الْجَنَّةِ أَنْ أَفِيضُوا عَلَيْنَا مِنَ الْمَاءِ أَوْ مِمَّا رَزَقَكُمُ اللَّهُ ۚ قَالُوا إِنَّ اللَّهَ حَرَّمَهُمَا عَلَى الْكَافِرِينَ','و دوزخیان، بهشتیان را صدا می‌زنند که: «(محبّت کنید) و مقداری آب، یا از آنچه خدا به شما روزی داده، به ما ببخشید!» آنها (در پاسخ) می‌گویند: «خداوند اینها را بر کافران حرام کرده است!»'];
ayat[1005]=[7,51,'الَّذِينَ اتَّخَذُوا دِينَهُمْ لَهْوًا وَلَعِبًا وَغَرَّتْهُمُ الْحَيَاةُ الدُّنْيَا ۚ فَالْيَوْمَ نَنسَاهُمْ كَمَا نَسُوا لِقَاءَ يَوْمِهِمْ هَٰذَا وَمَا كَانُوا بِآيَاتِنَا يَجْحَدُونَ','همانها که دین و آیین خود را سرگرمی و بازیچه گرفتند؛ و زندگی دنیا آنان را مغرور ساخت؛ امروز ما آنها را فراموش می‌کنیم، همان گونه که لقای چنین روزی را فراموش کردند و آیات ما را انکار نمودند.'];
ayat[1006]=[7,52,'وَلَقَدْ جِئْنَاهُم بِكِتَابٍ فَصَّلْنَاهُ عَلَىٰ عِلْمٍ هُدًى وَرَحْمَةً لِّقَوْمٍ يُؤْمِنُونَ','ما کتابی برای آنها آوردیم که (اسرار و رموز) آن را با آگاهی شرح دادیم؛ (کتابی) که مایه هدایت و رحمت برای جمعیّتی است که ایمان می‌آورند.'];
ayat[1007]=[7,53,'هَلْ يَنظُرُونَ إِلَّا تَأْوِيلَهُ ۚ يَوْمَ يَأْتِي تَأْوِيلُهُ يَقُولُ الَّذِينَ نَسُوهُ مِن قَبْلُ قَدْ جَاءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ فَهَل لَّنَا مِن شُفَعَاءَ فَيَشْفَعُوا لَنَا أَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِي كُنَّا نَعْمَلُ ۚ قَدْ خَسِرُوا أَنفُسَهُمْ وَضَلَّ عَنْهُم مَّا كَانُوا يَفْتَرُونَ','آیا آنها جز انتظار تأویل آیات (و فرا رسیدن تهدیدهای الهی) دارند؟ آن روز که تأویل آنها فرا رسد، (کار از کار گذشته، و پشیمانی سودی ندارد؛ و) کسانی که قبلاً آن را فراموش کرده بودند می‌گویند: «فرستادگان پروردگار ما، حق را آوردند؛ آیا (امروز) شفیعانی برای ما وجود دارند که برای ما شفاعت کنند؟ یا (به ما اجازه داده شود به دنیا) بازگردیم، و اعمالی غیر از آنچه انجام می‌دادیم، انجام دهیم؟!» (ولی) آنها سرمایه وجود خود را از دست داده‌اند؛ و معبودهایی را که به دروغ ساخته بودند، همگی از نظرشان: گم می‌شوند. (نه راه بازگشتی دارند، و نه شفیعانی!)'];
ayat[1008]=[7,54,'إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ يُغْشِي اللَّيْلَ النَّهَارَ يَطْلُبُهُ حَثِيثًا وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومَ مُسَخَّرَاتٍ بِأَمْرِهِ ۗ أَلَا لَهُ الْخَلْقُ وَالْأَمْرُ ۗ تَبَارَكَ اللَّهُ رَبُّ الْعَالَمِينَ','پروردگار شما، خداوندی است که آسمانها و زمین را در شش روز [= شش دوران‌] آفرید؛ سپس به تدبیر جهان هستی پرداخت؛ با (پرده تاریک) شب، روز را می پوشاند؛ و شب به دنبال روز، به سرعت در حرکت است؛ و خورشید و ماه و ستارگان را آفرید، که مسخّر فرمان او هستند. آگاه باشید که آفرینش و تدبیر (جهان)، از آن او (و به فرمان او) ست! پر برکت (و زوال‌ناپذیر) است خداوندی که پروردگار جهانیان است!'];
ayat[1009]=[7,55,'ادْعُوا رَبَّكُمْ تَضَرُّعًا وَخُفْيَةً ۚ إِنَّهُ لَا يُحِبُّ الْمُعْتَدِينَ','پروردگار خود را (آشکارا) از روی تضرّع، و در پنهانی، بخوانید! (و از تجاوز، دست بردارید که) او متجاوزان را دوست نمی‌دارد!'];
ayat[1010]=[7,56,'وَلَا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلَاحِهَا وَادْعُوهُ خَوْفًا وَطَمَعًا ۚ إِنَّ رَحْمَتَ اللَّهِ قَرِيبٌ مِّنَ الْمُحْسِنِينَ','و در زمین پس از اصلاح آن فساد نکنید، و او را با بیم و امید بخوانید! (بیم از مسؤولیتها، و امید به رحمتش. و نیکی کنید) زیرا رحمت خدا به نیکوکاران نزدیک است!'];
ayat[1011]=[7,57,'وَهُوَ الَّذِي يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۖ حَتَّىٰ إِذَا أَقَلَّتْ سَحَابًا ثِقَالًا سُقْنَاهُ لِبَلَدٍ مَّيِّتٍ فَأَنزَلْنَا بِهِ الْمَاءَ فَأَخْرَجْنَا بِهِ مِن كُلِّ الثَّمَرَاتِ ۚ كَذَٰلِكَ نُخْرِجُ الْمَوْتَىٰ لَعَلَّكُمْ تَذَكَّرُونَ','او کسی است که بادها را بشارت دهنده در پیشاپیش (باران) رحمتش می‌فرستد؛ تا ابرهای سنگین‌بار را (بر دوش) کشند؛ (سپس) ما آنها را به سوی زمینهای مرده می‌فرستیم؛ و به وسیله آنها، آب (حیاتبخش) را نازل می‌کنیم؛ و با آن، از هرگونه میوه‌ای (از خاک تیره) بیرون می‌آوریم؛ این گونه (که زمینهای مرده را زنده کردیم،) مردگان را (نیز در قیامت) زنده می‌کنیم، شاید (با توجه به این مثال) متذکّر شوید!'];
ayat[1012]=[7,58,'وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهُ بِإِذْنِ رَبِّهِ ۖ وَالَّذِي خَبُثَ لَا يَخْرُجُ إِلَّا نَكِدًا ۚ كَذَٰلِكَ نُصَرِّفُ الْآيَاتِ لِقَوْمٍ يَشْكُرُونَ','سرزمین پاکیزه) و شیرین)، گیاهش به فرمان پروردگار می‌روید؛ امّا سرزمینهای بد طینت (و شوره‌زار)، جز گیاه ناچیز و بی‌ارزش، از آن نمی‌روید؛ این گونه آیات (خود) را برای آنها که شکرگزارند، بیان می‌کنیم!'];
ayat[1013]=[7,59,'لَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ','ما نوح را به سوی قومش فرستادیم؛ او به آنان گفت: «ای قوم من! (تنها) خداوند یگانه را پرستش کنید، که معبودی جز او برای شما نیست! (و اگر غیر او را عبادت کنید،) من بر شما از عذاب روز بزرگی می‌ترسم!»'];
ayat[1014]=[7,60,'قَالَ الْمَلَأُ مِن قَوْمِهِ إِنَّا لَنَرَاكَ فِي ضَلَالٍ مُّبِينٍ','(ولی) اشراف قومش به او گفتند: «ما تو را در گمراهی آشکاری می‌بینیم!»'];
ayat[1015]=[7,61,'قَالَ يَا قَوْمِ لَيْسَ بِي ضَلَالَةٌ وَلَٰكِنِّي رَسُولٌ مِّن رَّبِّ الْعَالَمِينَ','گفت: «ای قوم من! هیچ گونه گمراهی در من نیست؛ ولی من فرستاده‌ای از جانب پروردگار جهانیانم!'];
ayat[1016]=[7,62,'أُبَلِّغُكُمْ رِسَالَاتِ رَبِّي وَأَنصَحُ لَكُمْ وَأَعْلَمُ مِنَ اللَّهِ مَا لَا تَعْلَمُونَ','رسالتهای پروردگارم را به شما ابلاغ می‌کنم؛ و خیرخواه شما هستم؛ و از خداوند چیزهایی می‌دانم که شما نمی‌دانید.'];
ayat[1017]=[7,63,'أَوَعَجِبْتُمْ أَن جَاءَكُمْ ذِكْرٌ مِّن رَّبِّكُمْ عَلَىٰ رَجُلٍ مِّنكُمْ لِيُنذِرَكُمْ وَلِتَتَّقُوا وَلَعَلَّكُمْ تُرْحَمُونَ','آیا تعجّب کرده‌اید که دستور آگاه کننده پروردگارتان به وسیله مردی از میان شما به شما برسد، تا (از عواقب اعمال خلاف) بیمتان دهد، و (در پرتو این دستور،) پرهیزگاری پیشه کنید و شاید مشمول رحمت (الهی) گردید؟!'];
ayat[1018]=[7,64,'فَكَذَّبُوهُ فَأَنجَيْنَاهُ وَالَّذِينَ مَعَهُ فِي الْفُلْكِ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۚ إِنَّهُمْ كَانُوا قَوْمًا عَمِينَ','امّا سرانجام او را تکذیب کردند؛ و ما او و کسانی را که با وی در کشتی بودند، رهایی بخشیدیم؛ و کسانی که آیات ما را تکذیب کردند، غرق کردیم؛ چه اینکه آنها گروهی نابینا (و کوردل) بودند.'];
ayat[1019]=[7,65,'۞ وَإِلَىٰ عَادٍ أَخَاهُمْ هُودًا ۗ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۚ أَفَلَا تَتَّقُونَ','و به سوی قوم عاد، برادرشان «هود» را (فرستادیم)؛ گفت: «ای قوم من! (تنها) خدا را پرستش کنید، که جز او معبودی برای شما نیست! آیا پرهیزگاری پیشه نمی‌کنید؟!»'];
ayat[1020]=[7,66,'قَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِن قَوْمِهِ إِنَّا لَنَرَاكَ فِي سَفَاهَةٍ وَإِنَّا لَنَظُنُّكَ مِنَ الْكَاذِبِينَ','اشراف کافر قوم او گفتند: «ما تو را در سفاهت (و نادانی و سبک مغزی) می‌بینیم، و ما مسلّماً تو را از دروغگویان می‌دانیم!»'];
ayat[1021]=[7,67,'قَالَ يَا قَوْمِ لَيْسَ بِي سَفَاهَةٌ وَلَٰكِنِّي رَسُولٌ مِّن رَّبِّ الْعَالَمِينَ','گرفت: «ای قوم من! هیچ گونه سفاهتی در من نیست؛ ولی فرستاده‌ای از طرف پروردگار جهانیانم.'];
ayat[1022]=[7,68,'أُبَلِّغُكُمْ رِسَالَاتِ رَبِّي وَأَنَا لَكُمْ نَاصِحٌ أَمِينٌ','رسالتهای پروردگارم را به شما ابلاغ می‌کنم؛ و من خیرخواه امینی برای شما هستم.'];
ayat[1023]=[7,69,'أَوَعَجِبْتُمْ أَن جَاءَكُمْ ذِكْرٌ مِّن رَّبِّكُمْ عَلَىٰ رَجُلٍ مِّنكُمْ لِيُنذِرَكُمْ ۚ وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِن بَعْدِ قَوْمِ نُوحٍ وَزَادَكُمْ فِي الْخَلْقِ بَسْطَةً ۖ فَاذْكُرُوا آلَاءَ اللَّهِ لَعَلَّكُمْ تُفْلِحُونَ','آیا تعجّب کرده‌اید که دستور آگاه کننده پروردگارتان به وسیله مردی از میان شما به شما برسد تا (از مجازات الهی) بیمتان دهد؟! و به یاد آورید هنگامی که شما را جانشینان قوم نوح قرار داد؛ و شما را از جهت خلقت (جسمانی) گسترش (و قدرت) داد؛ پس نعمتهای خدا را به یاد آورید، شاید رستگار شوید!»'];
ayat[1024]=[7,70,'قَالُوا أَجِئْتَنَا لِنَعْبُدَ اللَّهَ وَحْدَهُ وَنَذَرَ مَا كَانَ يَعْبُدُ آبَاؤُنَا ۖ فَأْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ الصَّادِقِينَ','گفتند: «آیا به سراغ ما آمده‌ای که تنها خدای یگانه را بپرستیم، و آنچه را پدران ما می‌پرستند، رها کنیم؟! پس اگر راست می‌گوئی آنچه را (از بلا و عذاب الهی) به ما وعده می‌دهی، بیاور»!'];
ayat[1025]=[7,71,'قَالَ قَدْ وَقَعَ عَلَيْكُم مِّن رَّبِّكُمْ رِجْسٌ وَغَضَبٌ ۖ أَتُجَادِلُونَنِي فِي أَسْمَاءٍ سَمَّيْتُمُوهَا أَنتُمْ وَآبَاؤُكُم مَّا نَزَّلَ اللَّهُ بِهَا مِن سُلْطَانٍ ۚ فَانتَظِرُوا إِنِّي مَعَكُم مِّنَ الْمُنتَظِرِينَ','گفت: «پلیدی و غضب پروردگارتان، شما را فرا گرفته است! آیا با من در مورد نامهایی مجادله می‌کنید که شما و پدرانتان 0بعنوان معبود و خدا، بر بتها) گذارده‌اید، در حالی که خداوند هیچ دلیلی درباره آن نازل نکرده است؟! پس شما منتظر باشید، من هم با شما انتظار می‌کشم! (شما انتظار شکست من، و من انتظار عذاب الهی برای شما!)»'];
ayat[1026]=[7,72,'فَأَنجَيْنَاهُ وَالَّذِينَ مَعَهُ بِرَحْمَةٍ مِّنَّا وَقَطَعْنَا دَابِرَ الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۖ وَمَا كَانُوا مُؤْمِنِينَ','سرانجام، او و کسانی را که با او بودند، برحمت خود نجات بخشیدیم؛ و ریشه کسانی که آیات ما را تکذیب کردند و ایمان نیاوردند، قطع کردیم!'];
ayat[1027]=[7,73,'وَإِلَىٰ ثَمُودَ أَخَاهُمْ صَالِحًا ۗ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ قَدْ جَاءَتْكُم بَيِّنَةٌ مِّن رَّبِّكُمْ ۖ هَٰذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً ۖ فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ ۖ وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابٌ أَلِيمٌ','و به سوی (قوم) ثمود، برادرشان صالح را (فرستادیم)؛ گفت: «ای قوم من! (تنها) خدا را بپرستید، که جز او، معبودی برای شما نیست! دلیل روشنی از طرف پروردگارتان برای شما آمده: این «ناقه» الهی برای شما معجزه‌ای است؛ او را به حال خود واگذارید که در زمین خدا (از علفهای بیابان) بخورد! و آن را آزار نرسانید، که عذاب دردناکی شما را خواهد گرفت!'];
ayat[1028]=[7,74,'وَاذْكُرُوا إِذْ جَعَلَكُمْ خُلَفَاءَ مِن بَعْدِ عَادٍ وَبَوَّأَكُمْ فِي الْأَرْضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُورًا وَتَنْحِتُونَ الْجِبَالَ بُيُوتًا ۖ فَاذْكُرُوا آلَاءَ اللَّهِ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ','و به خاطر بیاورید که شما را جانشینان قوم «عاد» قرار داد، و در زمین مستقر ساخت، که در دشتهایش، قصرها برای خود بنا می‌کنید؛ و در کوه‌ها، برای خود خانه‌ها می تراشید! بنابر این، نعمتهای خدا را متذکر شوید! و در زمین، به فساد نکوشید!»'];
ayat[1029]=[7,75,'قَالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِن قَوْمِهِ لِلَّذِينَ اسْتُضْعِفُوا لِمَنْ آمَنَ مِنْهُمْ أَتَعْلَمُونَ أَنَّ صَالِحًا مُّرْسَلٌ مِّن رَّبِّهِ ۚ قَالُوا إِنَّا بِمَا أُرْسِلَ بِهِ مُؤْمِنُونَ','(ولی) اشراف متکبر قوم او، به مستضعفانی که ایمان آورده بودند، گفتند: «آیا (براستی) شما یقین دارید که صالح از طرف پروردگارش فرستاده شده است؟!» آنها گفتند: «ما به آنچه او بدان مأموریت یافته، ایمان آورده‌ایم.»'];
ayat[1030]=[7,76,'قَالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا بِالَّذِي آمَنتُم بِهِ كَافِرُونَ','متکبران گفتند: «(ولی) ما به آنچه شما به آن ایمان آورده‌اید، کافریم!»'];
ayat[1031]=[7,77,'فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ أَمْرِ رَبِّهِمْ وَقَالُوا يَا صَالِحُ ائْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ الْمُرْسَلِينَ','سپس «ناقه» را پی کردند، و از فرمان پروردگارشان سرپیچیدند؛ و گفتند: «ای صالح! اگر تو از فرستادگان (خدا) هستی، آنچه ما را با آن تهدید می‌کنی، بیاور!»'];
ayat[1032]=[7,78,'فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ','سرانجام زمین لرزه آنها را فرا گرفت؛ و صبحگاهان، (تنها) جسم بی‌جانشان در خانه‌هاشان باقی مانده بود.'];
ayat[1033]=[7,79,'فَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَةَ رَبِّي وَنَصَحْتُ لَكُمْ وَلَٰكِن لَّا تُحِبُّونَ النَّاصِحِينَ','(صالح) از آنها روی برتافت؛ و گفت: «ای قوم! من رسالت پروردگارم را به شما ابلاغ کردم، و شرط خیرخواهی را انجام دادم، ولی (چه کنم که) شما خیرخواهان را دوست ندارید!»'];
ayat[1034]=[7,80,'وَلُوطًا إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُم بِهَا مِنْ أَحَدٍ مِّنَ الْعَالَمِينَ','و (به خاطر آورید) لوط را، هنگامی که به قوم خود گفت: «آیا عمل بسیار زشتی را انجام می‌دهید که هیچیک از جهانیان، پیش از شما انجام نداده است؟!'];
ayat[1035]=[7,81,'إِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِّن دُونِ النِّسَاءِ ۚ بَلْ أَنتُمْ قَوْمٌ مُّسْرِفُونَ','آیا شما از روی شهوت به سراغ مردان می‌روید، نه زنان؟! شما گروه اسرافکار (و منحرفی) هستید!'];
ayat[1036]=[7,82,'وَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَن قَالُوا أَخْرِجُوهُم مِّن قَرْيَتِكُمْ ۖ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ','ولی پاسخ قومش چیزی جز این نبود که گفتند: «اینها را از شهر و دیار خود بیرون کنید، که اینها مردمی هستند که پاکدامنی را می‌طلبند (و با ما همصدا نیستند!)»'];
ayat[1037]=[7,83,'فَأَنجَيْنَاهُ وَأَهْلَهُ إِلَّا امْرَأَتَهُ كَانَتْ مِنَ الْغَابِرِينَ','(چون کار به اینجا رسید،) ما او و خاندانش را رهایی بخشیدیم؛ جز همسرش، که از بازماندگان (در شهر) بود.'];
ayat[1038]=[7,84,'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًا ۖ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِينَ','و (سپس چنان) بارانی (از سنگ) بر آنها فرستادیم؛ (که آنها را در هم کوبید و نابود ساخت.) پس بنگر سرانجام کار مجرمان چه شد!'];
ayat[1039]=[7,85,'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا ۗ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ قَدْ جَاءَتْكُم بَيِّنَةٌ مِّن رَّبِّكُمْ ۖ فَأَوْفُوا الْكَيْلَ وَالْمِيزَانَ وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلَاحِهَا ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُم مُّؤْمِنِينَ','و به سوی مدین، برادرشان شعیب را (فرستادیم)؛ گفت: «ای قوم من! خدا را بپرستید، که جز او معبودی ندارید! دلیل روشنی از طرف پروردگارتان برای شما آمده است؛ بنابر این، حق پیمانه و وزن را ادا کنید! و از اموال مردم چیزی نکاهید! و در روی زمین، بعد از آنکه (در پرتو ایمان و دعوت انبیاء) اصلاح شده است، فساد نکنید! این برای شما بهتر است اگر با ایمان هستید!'];
ayat[1040]=[7,86,'وَلَا تَقْعُدُوا بِكُلِّ صِرَاطٍ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِيلِ اللَّهِ مَنْ آمَنَ بِهِ وَتَبْغُونَهَا عِوَجًا ۚ وَاذْكُرُوا إِذْ كُنتُمْ قَلِيلًا فَكَثَّرَكُمْ ۖ وَانظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ','و بر سر هر راه ننشینید که (مردم با ایمان را) تهدید کنید و مؤمنان را از راه خدا باز دارید، و با (القای شبهات،) آن را کج و معوج نشان دهید! و به خاطر بیاورید زمانی را که اندک بودید، و او شما را فزونی داد! و بنگرید سرانجام مفسدان چگونه بود!'];
ayat[1041]=[7,87,'وَإِن كَانَ طَائِفَةٌ مِّنكُمْ آمَنُوا بِالَّذِي أُرْسِلْتُ بِهِ وَطَائِفَةٌ لَّمْ يُؤْمِنُوا فَاصْبِرُوا حَتَّىٰ يَحْكُمَ اللَّهُ بَيْنَنَا ۚ وَهُوَ خَيْرُ الْحَاكِمِينَ','و اگر گروهی از شما به آنچه من به آن فرستاده شده‌ام ایمان آورده‌اند، و گروهی ایمان نیاورده‌اند، صبر کنید تا خداوند میان ما داوری کند، که او بهترین داوران است!'];
ayat[1042]=[7,88,'۞ قَالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِن قَوْمِهِ لَنُخْرِجَنَّكَ يَا شُعَيْبُ وَالَّذِينَ آمَنُوا مَعَكَ مِن قَرْيَتِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا ۚ قَالَ أَوَلَوْ كُنَّا كَارِهِينَ','اشراف زورمند و متکبّر از قوم او گفتند: «ای شعیب! به یقین، تو و کسانی را که به تو ایمان آورده‌اند، از شهر و دیار خود بیرون خواهیم کرد، یا به آیین ما بازگردید!» کفت: «آیا (می‌خواهید ما را بازگردانید) اگر چه مایل نباشیم؟!'];
ayat[1043]=[7,89,'قَدِ افْتَرَيْنَا عَلَى اللَّهِ كَذِبًا إِنْ عُدْنَا فِي مِلَّتِكُم بَعْدَ إِذْ نَجَّانَا اللَّهُ مِنْهَا ۚ وَمَا يَكُونُ لَنَا أَن نَّعُودَ فِيهَا إِلَّا أَن يَشَاءَ اللَّهُ رَبُّنَا ۚ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًا ۚ عَلَى اللَّهِ تَوَكَّلْنَا ۚ رَبَّنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَأَنتَ خَيْرُ الْفَاتِحِينَ','اگر ما به آیین شما بازگردیم، بعد از آنکه خدا ما را از آن نجات بخشیده، به خدا دروغ بسته‌ایم؛ و شایسته نیست که ما به آن بازگردیم مگر اینکه خدایی که پروردگار ماست بخواهد؛ علم پروردگار ما، به همه چیز احاطه دارد. تنها بر خدا توکل کرده‌ایم. پروردگارا! میان ما و قوم ما بحق داوری کن، که تو بهترین داورانی!»'];
ayat[1044]=[7,90,'وَقَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِن قَوْمِهِ لَئِنِ اتَّبَعْتُمْ شُعَيْبًا إِنَّكُمْ إِذًا لَّخَاسِرُونَ','اشراف زورمند از قوم او که کافر شده بودند گفتند: «اگر از شعیب پیروی کنید، شما هم زیانکار خواهید شد!»'];
ayat[1045]=[7,91,'فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ','سپس زمین لرزه آنها را فرا گرفت؛ و صبحگاهان بصورت اجسادی بی‌جان در خانه‌هاشان مانده بودند.'];
ayat[1046]=[7,92,'الَّذِينَ كَذَّبُوا شُعَيْبًا كَأَن لَّمْ يَغْنَوْا فِيهَا ۚ الَّذِينَ كَذَّبُوا شُعَيْبًا كَانُوا هُمُ الْخَاسِرِينَ','آنها که شعیب را تکذیب کردند، (آنچنان نابود شدند که) گویا هرگز در آن (خانه‌ها) سکونت نداشتند! آنها که شعیب را تکذیب کردند، زیانکار بودند!'];
ayat[1047]=[7,93,'فَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَاتِ رَبِّي وَنَصَحْتُ لَكُمْ ۖ فَكَيْفَ آسَىٰ عَلَىٰ قَوْمٍ كَافِرِينَ','سپس از آنان روی برتافت و گفت: «ای قوم من! من رسالتهای پروردگارم را به شما ابلاغ کردم و برای شما خیرخواهی نمودم؛ با این حال، چگونه بر حال قوم بی‌ایمان تأسف بخورم؟!»'];
ayat[1048]=[7,94,'وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِّن نَّبِيٍّ إِلَّا أَخَذْنَا أَهْلَهَا بِالْبَأْسَاءِ وَالضَّرَّاءِ لَعَلَّهُمْ يَضَّرَّعُونَ','و ما در هیچ شهر و آبادی پیامبری نفرستادیم مگر اینکه اهل آن را به ناراحتیها و خسارتها گرفتار ساختیم؛ شاید (به خود آیند، و به سوی خدا) بازگردند و تضرع کنند!'];
ayat[1049]=[7,95,'ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتَّىٰ عَفَوا وَّقَالُوا قَدْ مَسَّ آبَاءَنَا الضَّرَّاءُ وَالسَّرَّاءُ فَأَخَذْنَاهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ','سپس (هنگامی که این هشدارها در آنان اثر نگذاشت)، نیکی (و فراوانی نعمت و رفاه) را به جای بدی (و ناراحتی و گرفتاری) قرار دادیم؛ آنچنان که فزونی گرفتند، (و همه‌گونه نعمت و برکت یافتند، و مغرور شدند،) و گفتند: «(تنها ما نبودیم که گرفتار این مشکلات شدیم؛) به پدران ما نیز ناراحتیهای جسمی و مالی رسید.» چون چنین شد، آنها را ناگهان (به سبب اعمالشان) گرفتیم (و مجازات کردیم)، در حالی که نمی‌فهمیدند.'];
ayat[1050]=[7,96,'وَلَوْ أَنَّ أَهْلَ الْقُرَىٰ آمَنُوا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِم بَرَكَاتٍ مِّنَ السَّمَاءِ وَالْأَرْضِ وَلَٰكِن كَذَّبُوا فَأَخَذْنَاهُم بِمَا كَانُوا يَكْسِبُونَ','و اگر اهل شهرها و آبادیها، ایمان می‌آوردند و تقوا پیشه می‌کردند، برکات آسمان و زمین را بر آنها می‌گشودیم؛ ولی (آنها حق را) تکذیب کردند؛ ما هم آنان را به کیفر اعمالشان مجازات کردیم.'];
ayat[1051]=[7,97,'أَفَأَمِنَ أَهْلُ الْقُرَىٰ أَن يَأْتِيَهُم بَأْسُنَا بَيَاتًا وَهُمْ نَائِمُونَ','آیا اهل این آبادیها، از این ایمنند که عذاب ما شبانه به سراغ آنها بیاید در حالی که در خواب باشند؟!'];
ayat[1052]=[7,98,'أَوَأَمِنَ أَهْلُ الْقُرَىٰ أَن يَأْتِيَهُم بَأْسُنَا ضُحًى وَهُمْ يَلْعَبُونَ','آیا اهل این آبادیها، از این ایمنند که عذاب ما هنگام روز به سراغشان بیاید در حالی که سرگرم بازی هستند؟!'];
ayat[1053]=[7,99,'أَفَأَمِنُوا مَكْرَ اللَّهِ ۚ فَلَا يَأْمَنُ مَكْرَ اللَّهِ إِلَّا الْقَوْمُ الْخَاسِرُونَ','آیا آنها خود را از مکر الهی در امان می‌دانند؟! در حالی که جز زیانکاران، خود را از مکر (و مجازات) خدا ایمن نمی‌دانند!'];
ayat[1054]=[7,100,'أَوَلَمْ يَهْدِ لِلَّذِينَ يَرِثُونَ الْأَرْضَ مِن بَعْدِ أَهْلِهَا أَن لَّوْ نَشَاءُ أَصَبْنَاهُم بِذُنُوبِهِمْ ۚ وَنَطْبَعُ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَسْمَعُونَ','آیا کسانی که وارث روی زمین بعد از صاحبان آن می‌شوند، عبرت نمی‌گیرند که اگر بخواهیم، آنها را نیز به گناهانشان هلاک می‌کنیم، و بر دلهایشان مهر می‌نهیم تا (صدای حق را) نشنوند؟!'];
ayat[1055]=[7,101,'تِلْكَ الْقُرَىٰ نَقُصُّ عَلَيْكَ مِنْ أَنبَائِهَا ۚ وَلَقَدْ جَاءَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا مِن قَبْلُ ۚ كَذَٰلِكَ يَطْبَعُ اللَّهُ عَلَىٰ قُلُوبِ الْكَافِرِينَ','اینها، شهرها و آبادیهایی است که قسمتی از اخبار آن را برای تو شرح می‌دهیم؛ پیامبرانشان دلایل روشن برای آنان آوردند؛ (ولی آنها چنان لجوج بودند که) به آنچه قبلاً تکذیب کرده بودند، ایمان نمی‌آوردند! این‌گونه خداوند بر دلهای کافران مهر می‌نهد (و بر اثر لجاجت و ادامه گناه، حس تشخیصشان را سلب می‌کند)!'];
ayat[1056]=[7,102,'وَمَا وَجَدْنَا لِأَكْثَرِهِم مِّنْ عَهْدٍ ۖ وَإِن وَجَدْنَا أَكْثَرَهُمْ لَفَاسِقِينَ','و بیشتر آنها را بر سر پیمان خود نیافتیم؛ (بلکه) اکثر آنها را فاسق و گنهکار یافتیم!'];
ayat[1057]=[7,103,'ثُمَّ بَعَثْنَا مِن بَعْدِهِم مُّوسَىٰ بِآيَاتِنَا إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَظَلَمُوا بِهَا ۖ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ','سپس بدنبال آنها [= پیامبران پیشین‌] موسی را با آیات خویش به سوی فرعون و اطرافیان او فرستادیم؛ اما آنها (با عدم پذیرش)، به آن (آیات) ظلم کردند. ببین عاقبت مفسدان چگونه بود!'];
ayat[1058]=[7,104,'وَقَالَ مُوسَىٰ يَا فِرْعَوْنُ إِنِّي رَسُولٌ مِّن رَّبِّ الْعَالَمِينَ','و موسی گفت: «ای فرعون! من فرستاده‌ای از سوی پروردگار جهانیانم.'];
ayat[1059]=[7,105,'حَقِيقٌ عَلَىٰ أَن لَّا أَقُولَ عَلَى اللَّهِ إِلَّا الْحَقَّ ۚ قَدْ جِئْتُكُم بِبَيِّنَةٍ مِّن رَّبِّكُمْ فَأَرْسِلْ مَعِيَ بَنِي إِسْرَائِيلَ','سزاوار است که بر خدا جز حق نگویم. من دلیل روشنی از پروردگارتان برای شما آورده‌ام؛ پس بنی اسرائیل را با من بفرست!»'];
ayat[1060]=[7,106,'قَالَ إِن كُنتَ جِئْتَ بِآيَةٍ فَأْتِ بِهَا إِن كُنتَ مِنَ الصَّادِقِينَ','(فرعون) گفت: «اگر نشانه‌ای آورده‌ای، نشان بده اگر از راستگویانی!»'];
ayat[1061]=[7,107,'فَأَلْقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُّبِينٌ','(موسی) عصای خود را افکند؛ ناگهان اژدهای آشکاری شد!'];
ayat[1062]=[7,108,'وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ','و دست خود را (از گریبان) بیرون آورد؛ سفید (و درخشان) برای بینندگان بود!'];
ayat[1063]=[7,109,'قَالَ الْمَلَأُ مِن قَوْمِ فِرْعَوْنَ إِنَّ هَٰذَا لَسَاحِرٌ عَلِيمٌ','اطرافیان فرعون گفتند: «بی‌شک، این ساحری ماهر و دانا است!'];
ayat[1064]=[7,110,'يُرِيدُ أَن يُخْرِجَكُم مِّنْ أَرْضِكُمْ ۖ فَمَاذَا تَأْمُرُونَ','می‌خواهد شما را از سرزمینتان بیرون کند؛ (نظر شما چیست، و) در برابر او چه دستوری دارید؟»'];
ayat[1065]=[7,111,'قَالُوا أَرْجِهْ وَأَخَاهُ وَأَرْسِلْ فِي الْمَدَائِنِ حَاشِرِينَ','(سپس به فرعون) گفتند: «(کار) او و برادرش را به تأخیر انداز، و جمع‌آوری‌کنندگان را به همه شهرها بفرست...'];
ayat[1066]=[7,112,'يَأْتُوكَ بِكُلِّ سَاحِرٍ عَلِيمٍ','تا هر ساحر دانا (و کارآزموده‌ای) را به خدمت تو بیاورند!»'];
ayat[1067]=[7,113,'وَجَاءَ السَّحَرَةُ فِرْعَوْنَ قَالُوا إِنَّ لَنَا لَأَجْرًا إِن كُنَّا نَحْنُ الْغَالِبِينَ','ساحران نزد فرعون آمدند و گفتند: «آیا اگر ما پیروز گردیم، اجر و پاداش مهمی خواهیم داشت؟!»'];
ayat[1068]=[7,114,'قَالَ نَعَمْ وَإِنَّكُمْ لَمِنَ الْمُقَرَّبِينَ','گفت: «آری، و شما از مقربان خواهید بود!»'];
ayat[1069]=[7,115,'قَالُوا يَا مُوسَىٰ إِمَّا أَن تُلْقِيَ وَإِمَّا أَن نَّكُونَ نَحْنُ الْمُلْقِينَ','(روز مبارزه فرا رسید. ساحران) گفتند: «ای موسی! یا تو (وسایل سحرت را) بیفکن، یا ما می‌افکنیم!»'];
ayat[1070]=[7,116,'قَالَ أَلْقُوا ۖ فَلَمَّا أَلْقَوْا سَحَرُوا أَعْيُنَ النَّاسِ وَاسْتَرْهَبُوهُمْ وَجَاءُوا بِسِحْرٍ عَظِيمٍ','گفت: «شما بیفکنید!» و هنگامی (که وسایل سحر خود را) افکندند، مردم را چشم‌بندی کردند و ترساندند؛ و سحر عظیمی پدید آوردند.'];
ayat[1071]=[7,117,'۞ وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَلْقِ عَصَاكَ ۖ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ','(ما) به موسی وحی کردیم که: «عصای خود را بیفکن!» ناگهان (بصورت مار عظیمی در آمد که) وسایل دروغین آنها را بسرعت برمی‌گرفت.'];
ayat[1072]=[7,118,'فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوا يَعْمَلُونَ','(در این هنگام،) حق آشکار شد؛ و آنچه آنها ساخته بودند، باطل گشت.'];
ayat[1073]=[7,119,'فَغُلِبُوا هُنَالِكَ وَانقَلَبُوا صَاغِرِينَ','و در آنجا (همگی) مغلوب شدند؛ و خوار و کوچک گشتند.'];
ayat[1074]=[7,120,'وَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ','و ساحران (بی‌اختیار) به سجده افتادند.'];
ayat[1075]=[7,121,'قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ','و گفتند: «ما به پروردگار جهانیان ایمان آوردیم؛'];
ayat[1076]=[7,122,'رَبِّ مُوسَىٰ وَهَارُونَ','پروردگار موسی و هارون!»'];
ayat[1077]=[7,123,'قَالَ فِرْعَوْنُ آمَنتُم بِهِ قَبْلَ أَنْ آذَنَ لَكُمْ ۖ إِنَّ هَٰذَا لَمَكْرٌ مَّكَرْتُمُوهُ فِي الْمَدِينَةِ لِتُخْرِجُوا مِنْهَا أَهْلَهَا ۖ فَسَوْفَ تَعْلَمُونَ','فرعون گفت: «آیا پیش از آنکه به شما اجازه دهم، به او ایمان آوردید؟! حتماً این نیرنگ و توطئه‌ای است که در این شهر (و دیار) چیده‌اید، تا اهلش را از آن بیرون کنید؛ ولی بزودی خواهید دانست!'];
ayat[1078]=[7,124,'لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُم مِّنْ خِلَافٍ ثُمَّ لَأُصَلِّبَنَّكُمْ أَجْمَعِينَ','سوگند می‌خورم که دستها و پاهای شما را بطور مخالف [= دست راست با پای چپ، یا دست چپ با پای راست‌] قطع می‌کنم؛ سپس همگی را به دار می‌آویزم!'];
ayat[1079]=[7,125,'قَالُوا إِنَّا إِلَىٰ رَبِّنَا مُنقَلِبُونَ','(ساحران) گفتند: «(مهم نیست،) ما به سوی پروردگارمان بازمی‌گردیم!'];
ayat[1080]=[7,126,'وَمَا تَنقِمُ مِنَّا إِلَّا أَنْ آمَنَّا بِآيَاتِ رَبِّنَا لَمَّا جَاءَتْنَا ۚ رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْرًا وَتَوَفَّنَا مُسْلِمِينَ','انتقام تو از ما، تنها بخاطر این است که ما به آیات پروردگار خویش -هنگامی که به سراغ ما آمد- ایمان آوردیم. بار الها! صبر و استقامت بر ما فرو ریز! (و آخرین درجه شکیبائی را به ما مرحمت فرما!) و ما را مسلمان بمیران!»'];
ayat[1081]=[7,127,'وَقَالَ الْمَلَأُ مِن قَوْمِ فِرْعَوْنَ أَتَذَرُ مُوسَىٰ وَقَوْمَهُ لِيُفْسِدُوا فِي الْأَرْضِ وَيَذَرَكَ وَآلِهَتَكَ ۚ قَالَ سَنُقَتِّلُ أَبْنَاءَهُمْ وَنَسْتَحْيِي نِسَاءَهُمْ وَإِنَّا فَوْقَهُمْ قَاهِرُونَ','و اشراف قوم فرعون (به او) گفتند: «آیا موسی و قومش را رها می‌کنی که در زمین فساد کنند، و تو و خدایانت را رها سازد؟!» گفت: «بزودی پسرانشان را می‌کشیم، و دخترانشان را زنده نگه می‌داریم (تا به ما خدمت کنند)؛ و ما بر آنها کاملاً مسلّطیم!»'];
ayat[1082]=[7,128,'قَالَ مُوسَىٰ لِقَوْمِهِ اسْتَعِينُوا بِاللَّهِ وَاصْبِرُوا ۖ إِنَّ الْأَرْضَ لِلَّهِ يُورِثُهَا مَن يَشَاءُ مِنْ عِبَادِهِ ۖ وَالْعَاقِبَةُ لِلْمُتَّقِينَ','موسی به قوم خود گفت: «از خدا یاری جویید، و استقامت پیشه کنید، که زمین از آن خداست، و آن را به هر کس از بندگانش که بخواهد، واگذار می‌کند؛ و سرانجام (نیک) برای پرهیزکاران است!»'];
ayat[1083]=[7,129,'قَالُوا أُوذِينَا مِن قَبْلِ أَن تَأْتِيَنَا وَمِن بَعْدِ مَا جِئْتَنَا ۚ قَالَ عَسَىٰ رَبُّكُمْ أَن يُهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِي الْأَرْضِ فَيَنظُرَ كَيْفَ تَعْمَلُونَ','گفتند: «پیش از آنکه به سوی ما بیایی آزار دیدیم، (هم اکنون) پس از آمدنت نیز آزار می‌بینیم! (کی این آزارها سر خواهد آمد؟)» گفت: «امید است پروردگارتان دشمن شما را هلاک کند، و شما را در زمین جانشین (آنها) سازد، و بنگرد چگونه عمل می‌کنید!»'];
ayat[1084]=[7,130,'وَلَقَدْ أَخَذْنَا آلَ فِرْعَوْنَ بِالسِّنِينَ وَنَقْصٍ مِّنَ الثَّمَرَاتِ لَعَلَّهُمْ يَذَّكَّرُونَ','و ما نزدیکان فرعون (و قوم او) را به خشکسالی و کمبود میوه‌ها گرفتار کردیم، شاید متذکر گردند!'];
ayat[1085]=[7,131,'فَإِذَا جَاءَتْهُمُ الْحَسَنَةُ قَالُوا لَنَا هَٰذِهِ ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسَىٰ وَمَن مَّعَهُ ۗ أَلَا إِنَّمَا طَائِرُهُمْ عِندَ اللَّهِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','(اما آنها نه تنها پند نگرفتند، بلکه) هنگامی که نیکی (و نعمت) به آنها می‌رسید، می‌گفتند: «بخاطر خود ماست.» ولی موقعی که بدی (و بلا) به آنها می‌رسید، می‌گفتند: «از شومی موسی و کسان اوست»! آگاه باشید سرچشمه همه اینها، نزد خداست؛ ولی بیشتر آنها نمی‌دانند!'];
ayat[1086]=[7,132,'وَقَالُوا مَهْمَا تَأْتِنَا بِهِ مِنْ آيَةٍ لِّتَسْحَرَنَا بِهَا فَمَا نَحْنُ لَكَ بِمُؤْمِنِينَ','و گفتند: «هر زمان نشانه و معجزه‌ای برای ما بیاوری که سحرمان کنی، ما به تو ایمان نمی‌آوریم!»'];
ayat[1087]=[7,133,'فَأَرْسَلْنَا عَلَيْهِمُ الطُّوفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ آيَاتٍ مُّفَصَّلَاتٍ فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا مُّجْرِمِينَ','سپس (بلاها را پشت سر هم بر آنها نازل کردیم:) طوفان و ملخ و آفت گیاهی و قورباغه‌ها و خون را -که نشانه‌هایی از هم جدا بودند- بر آنها فرستادیم؛ (ولی باز بیدار نشدند، و) تکبر ورزیدند، و جمعیّت گنهکاری بودند!'];
ayat[1088]=[7,134,'وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوا يَا مُوسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ ۖ لَئِن كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِي إِسْرَائِيلَ','هنگامی که بلا بر آنها مسلط می‌شد، می‌گفتند: «ای موسی! از خدایت برای ما بخواه به عهدی که با تو کرده، رفتار کند! اگر این بلا را از ما مرتفع سازی، قطعاً به تو ایمان می‌آوریم، و بنی اسرائیل را با تو خواهیم فرستاد!»'];
ayat[1089]=[7,135,'فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ إِلَىٰ أَجَلٍ هُم بَالِغُوهُ إِذَا هُمْ يَنكُثُونَ','اما هنگامی که بلا را، پس از مدت معینی که به آن می‌رسیدند، از آنها برمی‌داشتیم، پیمان خویش را می‌شکستند!'];
ayat[1090]=[7,136,'فَانتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَاهُمْ فِي الْيَمِّ بِأَنَّهُمْ كَذَّبُوا بِآيَاتِنَا وَكَانُوا عَنْهَا غَافِلِينَ','سرانجام از آنها انتقام گرفتیم، و آنان را در دریا غرق کردیم؛ زیرا آیات ما را تکذیب کردند، و از آن غافل بودند.'];
ayat[1091]=[7,137,'وَأَوْرَثْنَا الْقَوْمَ الَّذِينَ كَانُوا يُسْتَضْعَفُونَ مَشَارِقَ الْأَرْضِ وَمَغَارِبَهَا الَّتِي بَارَكْنَا فِيهَا ۖ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنَىٰ عَلَىٰ بَنِي إِسْرَائِيلَ بِمَا صَبَرُوا ۖ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهُ وَمَا كَانُوا يَعْرِشُونَ','و مشرقها و مغربهای پر برکت زمین را به آن قومِ به ضعف کشانده شده (زیر زنجیر ظلم و ستم)، واگذار کردیم؛ و وعده نیک پروردگارت بر بنی اسرائیل، بخاطر صبر و استقامتی که به خرج دادند، تحقّق یافت؛ و آنچه فرعون و فرعونیان (از کاخهای مجلّل) می‌ساختند، و آنچه از باغهای داربست‌دار فراهم ساخته بودند، در هم کوبیدیم!'];
ayat[1092]=[7,138,'وَجَاوَزْنَا بِبَنِي إِسْرَائِيلَ الْبَحْرَ فَأَتَوْا عَلَىٰ قَوْمٍ يَعْكُفُونَ عَلَىٰ أَصْنَامٍ لَّهُمْ ۚ قَالُوا يَا مُوسَى اجْعَل لَّنَا إِلَٰهًا كَمَا لَهُمْ آلِهَةٌ ۚ قَالَ إِنَّكُمْ قَوْمٌ تَجْهَلُونَ','و بنی اسرائیل را (سالم) از دریا عبور دادیم؛ (ناگاه) در راه خود به گروهی رسیدند که اطراف بتهایشان، با تواضع و خضوع، گرد آمده بودند. (در این هنگام، بنی اسرائیل) به موسی گفتند: «تو هم برای ما معبودی قرار ده، همان‌گونه که آنها معبودان (و خدایانی) دارند!» گفت: «شما جمعیّتی جاهل و نادان هستید!'];
ayat[1093]=[7,139,'إِنَّ هَٰؤُلَاءِ مُتَبَّرٌ مَّا هُمْ فِيهِ وَبَاطِلٌ مَّا كَانُوا يَعْمَلُونَ','اینها (را که می‌بینید)، سرانجام کارشان نابودی است؛ و آنچه انجام می‌دهند، باطل (و بیهوده) است.'];
ayat[1094]=[7,140,'قَالَ أَغَيْرَ اللَّهِ أَبْغِيكُمْ إِلَٰهًا وَهُوَ فَضَّلَكُمْ عَلَى الْعَالَمِينَ','(سپس) گفت: «آیا غیر از خداوند، مبعودی برای شما بطلبم؟! خدایی که شما را بر جهانیان (و مردم عصرتان) برتری داد!»'];
ayat[1095]=[7,141,'وَإِذْ أَنجَيْنَاكُم مِّنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ ۖ يُقَتِّلُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُم بَلَاءٌ مِّن رَّبِّكُمْ عَظِيمٌ','(به خاطر بیاورید) زمانی را که از (چنگال) فرعونیان نجاتتان بخشیدیم! آنها که پیوسته شما را شکنجه می‌دادند، پسرانتان را می‌کشتند، و زنانتان را (برای خدمتگاری) زنده می‌گذاشتند؛ و در این، آزمایش بزرگی از سوی خدا برای شما بود.'];
ayat[1096]=[7,142,'۞ وَوَاعَدْنَا مُوسَىٰ ثَلَاثِينَ لَيْلَةً وَأَتْمَمْنَاهَا بِعَشْرٍ فَتَمَّ مِيقَاتُ رَبِّهِ أَرْبَعِينَ لَيْلَةً ۚ وَقَالَ مُوسَىٰ لِأَخِيهِ هَارُونَ اخْلُفْنِي فِي قَوْمِي وَأَصْلِحْ وَلَا تَتَّبِعْ سَبِيلَ الْمُفْسِدِينَ','و ما با موسی، سی شب وعده گذاشتیم؛ سپس آن را با ده شب (دیگر) تکمیل نمودیم؛ به این ترتیب، میعاد پروردگارش (با او)، چهل شب تمام شد. و موسی به برادرش هارون گفت: «جانشین من در میان قومم باش. و (آنها) را اصلاح کن! و از روش مفسدان، پیروی منما!»'];
ayat[1097]=[7,143,'وَلَمَّا جَاءَ مُوسَىٰ لِمِيقَاتِنَا وَكَلَّمَهُ رَبُّهُ قَالَ رَبِّ أَرِنِي أَنظُرْ إِلَيْكَ ۚ قَالَ لَن تَرَانِي وَلَٰكِنِ انظُرْ إِلَى الْجَبَلِ فَإِنِ اسْتَقَرَّ مَكَانَهُ فَسَوْفَ تَرَانِي ۚ فَلَمَّا تَجَلَّىٰ رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكًّا وَخَرَّ مُوسَىٰ صَعِقًا ۚ فَلَمَّا أَفَاقَ قَالَ سُبْحَانَكَ تُبْتُ إِلَيْكَ وَأَنَا أَوَّلُ الْمُؤْمِنِينَ','و هنگامی که موسی به میعادگاه ما آمد، و پروردگارش با او سخن گفت، عرض کرد: «پروردگارا! خودت را به من نشان ده، تا تو را ببینم!» گفت: «هرگز مرا نخواهی دید! ولی به کوه بنگر، اگر در جای خود ثابت ماند، مرا خواهی دید!» اما هنگامی که پروردگارش بر کوه جلوه کرد، آن را همسان خاک قرار داد؛ و موسی مدهوش به زمین افتاد. چون به هوش آمد، عرض کرد: «خداوندا! منزهی تو (از اینکه با چشم تو را ببینم)! من به سوی تو بازگشتم! و من نخستین مؤمنانم!»'];
ayat[1098]=[7,144,'قَالَ يَا مُوسَىٰ إِنِّي اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسَالَاتِي وَبِكَلَامِي فَخُذْ مَا آتَيْتُكَ وَكُن مِّنَ الشَّاكِرِينَ','(خداوند) فرمود: «ای موسی! من تو را با رسالتهای خویش، و با سخن‌گفتنم (با تو)، بر مردم برتری دادم و برگزیدم؛ پس آنچه را به تو داده‌ام بگیر و از شکرگزاران باش!»'];
ayat[1099]=[7,145,'وَكَتَبْنَا لَهُ فِي الْأَلْوَاحِ مِن كُلِّ شَيْءٍ مَّوْعِظَةً وَتَفْصِيلًا لِّكُلِّ شَيْءٍ فَخُذْهَا بِقُوَّةٍ وَأْمُرْ قَوْمَكَ يَأْخُذُوا بِأَحْسَنِهَا ۚ سَأُرِيكُمْ دَارَ الْفَاسِقِينَ','و برای او در الواح اندرزی از هر موضوعی نوشتیم؛ و بیانی از هر چیز کردیم -«پس آن را با جدّیت بگیر! و به قوم خود بگو: به نیکوترین آنها عمل کنند! (و آنها که به مخالفت برخیزند، کیفرشان دوزخ است؛) و بزودی جایگاه فاسقان را به شما نشان خواهم داد!»'];
ayat[1100]=[7,146,'سَأَصْرِفُ عَنْ آيَاتِيَ الَّذِينَ يَتَكَبَّرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَإِن يَرَوْا كُلَّ آيَةٍ لَّا يُؤْمِنُوا بِهَا وَإِن يَرَوْا سَبِيلَ الرُّشْدِ لَا يَتَّخِذُوهُ سَبِيلًا وَإِن يَرَوْا سَبِيلَ الْغَيِّ يَتَّخِذُوهُ سَبِيلًا ۚ ذَٰلِكَ بِأَنَّهُمْ كَذَّبُوا بِآيَاتِنَا وَكَانُوا عَنْهَا غَافِلِينَ','بزودی کسانی را که در روی زمین بناحق تکبّر می‌ورزند، از (ایمان به) آیات خود، منصرف می‌سازم! آنها چنانند که اگر هر آیه و نشانه‌ای را ببینند، به آن ایمان نمی‌آورند؛ اگر راه هدایت را ببینند، آن را راه خود انتخاب نمی‌کنند؛ و اگر طریق گمراهی را ببینند، آن را راه خود انتخاب می‌کنند! (همه اینها) بخاطر آن است که آیات ما را تکذیب کردند، و از آن غافل بودند!'];
ayat[1101]=[7,147,'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الْآخِرَةِ حَبِطَتْ أَعْمَالُهُمْ ۚ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُوا يَعْمَلُونَ','و کسانی که آیات، و دیدار رستاخیز را تکذیب (و انکار) کنند، اعمالشان نابود می گردد؛ آیا جز آنچه را عمل می‌کردند پاداش داده می‌شوند؟!'];
ayat[1102]=[7,148,'وَاتَّخَذَ قَوْمُ مُوسَىٰ مِن بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلًا جَسَدًا لَّهُ خُوَارٌ ۚ أَلَمْ يَرَوْا أَنَّهُ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيهِمْ سَبِيلًا ۘ اتَّخَذُوهُ وَكَانُوا ظَالِمِينَ','قوم موسی بعد (از رفتن) او (به میعادگاه خدا)، از زیورهای خود گوساله‌ای ساختند؛ جسد بی‌جانی که صدای گوساله داشت! آیا آنها نمی‌دیدند که با آنان سخن نمی‌گوید، و به راه (راست) هدایتشان نمی‌کند؟! آن را (خدای خود) انتخاب کردند، و ظالم بودند!'];
ayat[1103]=[7,149,'وَلَمَّا سُقِطَ فِي أَيْدِيهِمْ وَرَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قَالُوا لَئِن لَّمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ الْخَاسِرِينَ','و هنگامی که حقیقت به دستشان افتاد، و دیدند گمراه شده‌اند، گفتند: «اگر پروردگارمان به ما رحم نکند، و ما را نیامرزد، بطور قطع از زیانکاران خواهیم بود!»'];
ayat[1104]=[7,150,'وَلَمَّا رَجَعَ مُوسَىٰ إِلَىٰ قَوْمِهِ غَضْبَانَ أَسِفًا قَالَ بِئْسَمَا خَلَفْتُمُونِي مِن بَعْدِي ۖ أَعَجِلْتُمْ أَمْرَ رَبِّكُمْ ۖ وَأَلْقَى الْأَلْوَاحَ وَأَخَذَ بِرَأْسِ أَخِيهِ يَجُرُّهُ إِلَيْهِ ۚ قَالَ ابْنَ أُمَّ إِنَّ الْقَوْمَ اسْتَضْعَفُونِي وَكَادُوا يَقْتُلُونَنِي فَلَا تُشْمِتْ بِيَ الْأَعْدَاءَ وَلَا تَجْعَلْنِي مَعَ الْقَوْمِ الظَّالِمِينَ','و هنگامی که موسی خشمگین و اندوهناک به سوی قوم خود بازگشت، گفت: «پس از من، بد جانشینانی برایم بودید (و آیین مرا ضایع کردید)! آیا درمورد فرمان پروردگارتان (و تمدید مدّت میعاد او)، عجله نمودید (و زود قضاوت کردید؟!)» سپس الواح را افکند، و سر برادر خود را گرفت (و با عصبانیت) به سوی خود کشید؛ او گفت: «فرزند مادرم! این گروه، مرا در فشار گذاردند و ناتوان کردند؛ و نزدیک بود مرا بکشند، پس کاری نکن که دشمنان مرا شماتت کنند و مرا با گروه ستمکاران قرار مده!»'];
ayat[1105]=[7,151,'قَالَ رَبِّ اغْفِرْ لِي وَلِأَخِي وَأَدْخِلْنَا فِي رَحْمَتِكَ ۖ وَأَنتَ أَرْحَمُ الرَّاحِمِينَ','(موسی) گفت: «پروردگارا! من و برادرم را بیامرز، و ما را در رحمت خود داخل فرما، و تو مهربانترین مهربانانی!»'];
ayat[1106]=[7,152,'إِنَّ الَّذِينَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِّن رَّبِّهِمْ وَذِلَّةٌ فِي الْحَيَاةِ الدُّنْيَا ۚ وَكَذَٰلِكَ نَجْزِي الْمُفْتَرِينَ','کسانی که گوساله را (معبود خود) قرار دادند، بزودی خشم پروردگارشان، و ذلّت در زندگی دنیا به آنها می‌رسد؛ و اینچنین، کسانی را که (بر خدا) افترا می‌بندند، کیفر می دهیم!'];
ayat[1107]=[7,153,'وَالَّذِينَ عَمِلُوا السَّيِّئَاتِ ثُمَّ تَابُوا مِن بَعْدِهَا وَآمَنُوا إِنَّ رَبَّكَ مِن بَعْدِهَا لَغَفُورٌ رَّحِيمٌ','و آنها که گناه کردند، و بعد از آن توبه نمودند و ایمان آوردند، (امید عفو او را دارند؛ زیرا) پروردگار تو، در پی این کار، آمرزنده و مهربان است.'];
ayat[1108]=[7,154,'وَلَمَّا سَكَتَ عَن مُّوسَى الْغَضَبُ أَخَذَ الْأَلْوَاحَ ۖ وَفِي نُسْخَتِهَا هُدًى وَرَحْمَةٌ لِّلَّذِينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ','هنگامی که خشم موسی فرو نشست؛ الواح (تورات) را برگرفت؛ و در نوشته‌های آن، هدایت و رحمت برای کسانی بود که از پروردگار خویش می‌ترسند (و از مخالفت فرمانش بیم دارند).'];
ayat[1109]=[7,155,'وَاخْتَارَ مُوسَىٰ قَوْمَهُ سَبْعِينَ رَجُلًا لِّمِيقَاتِنَا ۖ فَلَمَّا أَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ أَهْلَكْتَهُم مِّن قَبْلُ وَإِيَّايَ ۖ أَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاءُ مِنَّا ۖ إِنْ هِيَ إِلَّا فِتْنَتُكَ تُضِلُّ بِهَا مَن تَشَاءُ وَتَهْدِي مَن تَشَاءُ ۖ أَنتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا ۖ وَأَنتَ خَيْرُ الْغَافِرِينَ','موسی از قوم خود، هفتاد تن از مردان را برای میعادگاه ما برگزید؛ و هنگامی که زمین‌لرزه آنها را فرا گرفت (و هلاک شدند)، گفت: «پروردگارا! اگر می‌خواستی، می توانستی آنها و مرا پیش از این نیز هلاک کنی! آیا ما را به آنچه سفیهانمان انجام داده‌اند، (مجازات و) هلاک می‌کنی؟! این، جز آزمایش تو، چیز دیگر نیست؛ که هر کس را بخواهی (و مستحق بدانی)، به وسیله آن گمراه می‌سازی؛ و هر کس را بخواهی (و شایسته ببینی)، هدایت می‌کنی! تو ولیّ مایی، و ما را بیامرز، بر ما رحم کن، و تو بهترین آمرزندگانی!'];
ayat[1110]=[7,156,'۞ وَاكْتُبْ لَنَا فِي هَٰذِهِ الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ إِنَّا هُدْنَا إِلَيْكَ ۚ قَالَ عَذَابِي أُصِيبُ بِهِ مَنْ أَشَاءُ ۖ وَرَحْمَتِي وَسِعَتْ كُلَّ شَيْءٍ ۚ فَسَأَكْتُبُهَا لِلَّذِينَ يَتَّقُونَ وَيُؤْتُونَ الزَّكَاةَ وَالَّذِينَ هُم بِآيَاتِنَا يُؤْمِنُونَ','و برای ما، در این دنیا و سرای دیگر، نیکی مقرّر فرما؛ چه اینکه ما به سوی تو بازگشت کرده‌ایم! «(خداوند در برابر این تقاضا، به موسی) گفت:» مجازاتم را به هر کس بخواهم می‌رسانم؛ و رحمتم همه چیز را فراگرفته؛ و آن را برای آنها که تقوا پیشه کنند، و زکات را بپردازند، و آنها که به آیات ما ایمان می‌آورند، مقرّر خواهم داشت!'];
ayat[1111]=[7,157,'الَّذِينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الْأُمِّيَّ الَّذِي يَجِدُونَهُ مَكْتُوبًا عِندَهُمْ فِي التَّوْرَاةِ وَالْإِنجِيلِ يَأْمُرُهُم بِالْمَعْرُوفِ وَيَنْهَاهُمْ عَنِ الْمُنكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبَاتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبَائِثَ وَيَضَعُ عَنْهُمْ إِصْرَهُمْ وَالْأَغْلَالَ الَّتِي كَانَتْ عَلَيْهِمْ ۚ فَالَّذِينَ آمَنُوا بِهِ وَعَزَّرُوهُ وَنَصَرُوهُ وَاتَّبَعُوا النُّورَ الَّذِي أُنزِلَ مَعَهُ ۙ أُولَٰئِكَ هُمُ الْمُفْلِحُونَ','همانها که از فرستاده (خدا)، پیامبر «امّی» پیروی می‌کنند؛ پیامبری که صفاتش را، در تورات و انجیلی که نزدشان است، می‌یابند؛ آنها را به معروف دستور می‌دهد، و از منکر باز میدارد؛ اشیار پاکیزه را برای آنها حلال می‌شمرد، و ناپاکیها را تحریم می کند؛ و بارهای سنگین، و زنجیرهایی را که بر آنها بود، (از دوش و گردنشان) بر می‌دارد، پس کسانی که به او ایمان آوردند، و حمایت و یاریش کردند، و از نوری که با او نازل شده پیروی نمودند، آنان رستگارانند.'];
ayat[1112]=[7,158,'قُلْ يَا أَيُّهَا النَّاسُ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ جَمِيعًا الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ لَا إِلَٰهَ إِلَّا هُوَ يُحْيِي وَيُمِيتُ ۖ فَآمِنُوا بِاللَّهِ وَرَسُولِهِ النَّبِيِّ الْأُمِّيِّ الَّذِي يُؤْمِنُ بِاللَّهِ وَكَلِمَاتِهِ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ','بگو: «ای مردم! من فرستاده خدا به سوی همه شما هستم؛ همان خدایی که حکومت آسمانها و زمین، از آن اوست؛ معبودی جز او نیست؛ زنده می‌کند و می‌میراند؛ پس ایمان بیاورید به خدا و فرستاده‌اش، آن پیامبر درس نخوانده‌ای که به خدا و کلماتش ایمان دارد؛ و از او پیروی کنید تا هدایت یابید!»'];
ayat[1113]=[7,159,'وَمِن قَوْمِ مُوسَىٰ أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِهِ يَعْدِلُونَ','و از قوم موسی، گروهی هستند که به سوی حق هدایت می‌کنند؛ و به حق و عدالت حکم می‌نمایند.'];
ayat[1114]=[7,160,'وَقَطَّعْنَاهُمُ اثْنَتَيْ عَشْرَةَ أَسْبَاطًا أُمَمًا ۚ وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ إِذِ اسْتَسْقَاهُ قَوْمُهُ أَنِ اضْرِب بِّعَصَاكَ الْحَجَرَ ۖ فَانبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۖ قَدْ عَلِمَ كُلُّ أُنَاسٍ مَّشْرَبَهُمْ ۚ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَأَنزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوَىٰ ۖ كُلُوا مِن طَيِّبَاتِ مَا رَزَقْنَاكُمْ ۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','ما آنها را به دوازده گروه -که هر یک شاخه‌ای (از دودمان اسرائیل) بود - تقسیم کردیم. و هنگامی که قوم موسی (در بیابان) از او تقاضای آب کردند، به او وحی فرستادیم که: «عصای خود را بر سنگ بزن!» ناگهان دوازده چشمه از آن بیرون جست؛ آنچنان که هر گروه، چشمه و آبشخور خود را می‌شناخت. و ابر را بر سر آنها سایبان ساختیم؛ و بر آنها «مَن» و «سَلوی» فرستادیم؛ (و به آنان گفتیم:) از روزیهای پاکیزه‌ای که به شما داده‌ایم، بخورید! (و شکر خدا را بجا آورید! آنها نافرمانی و ستم کردند؛ ولی) به ما ستم نکردند، لکن به خودشان ستم می‌نمودند.'];
ayat[1115]=[7,161,'وَإِذْ قِيلَ لَهُمُ اسْكُنُوا هَٰذِهِ الْقَرْيَةَ وَكُلُوا مِنْهَا حَيْثُ شِئْتُمْ وَقُولُوا حِطَّةٌ وَادْخُلُوا الْبَابَ سُجَّدًا نَّغْفِرْ لَكُمْ خَطِيئَاتِكُمْ ۚ سَنَزِيدُ الْمُحْسِنِينَ','و (به خاطر بیاورید) هنگامی را که به آنها گفته شد: «در این شهر [= بیت المقدّس] ساکن شوید، و از هر جا (و به هر کیفیت) بخواهید، از آن بخورید (و بهره گیرید)! و بگویید: خداوندا! گناهان ما را بریز! و از درِ (بیت المقدس) با تواضع وارد شوید! که اگر چنین کنید، گناهان شما را می‌بخشم؛ و نیکوکاران را پاداش بیشتر خواهیم داد.»'];
ayat[1116]=[7,162,'فَبَدَّلَ الَّذِينَ ظَلَمُوا مِنْهُمْ قَوْلًا غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِّنَ السَّمَاءِ بِمَا كَانُوا يَظْلِمُونَ','اما ستمگران آنها، این سخن (و آن فرمانها) را، بغیر آنچه به آنها گفته شده بود، تغییر دادند؛ از این‌رو بخاطر ستمی که روا میداشتند، بلایی از آسمان بر آنها فرستادیم (و مجازاتشان کردیم).'];
ayat[1117]=[7,163,'وَاسْأَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كَانَتْ حَاضِرَةَ الْبَحْرِ إِذْ يَعْدُونَ فِي السَّبْتِ إِذْ تَأْتِيهِمْ حِيتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَيَوْمَ لَا يَسْبِتُونَ ۙ لَا تَأْتِيهِمْ ۚ كَذَٰلِكَ نَبْلُوهُم بِمَا كَانُوا يَفْسُقُونَ','و از آنها درباره (سرگذشت) شهری که در ساحل دریا بود بپرس! زمانی که آنها در روزهای شنبه، تجاوز (و نافرمانی) خدا می‌کردند؛ همان هنگام که ماهیانشان، روز شنبه (که روز تعطیل و استراحت و عبادت بود، بر سطح آب،) آشکار می‌شدند؛ امّا در غیر روز شنبه، به سراغ آنها نمی‌آمدند؛ این چنین آنها را به چیزی آزمایش کردیم که نافرمانی می‌کردند!'];
ayat[1118]=[7,164,'وَإِذْ قَالَتْ أُمَّةٌ مِّنْهُمْ لِمَ تَعِظُونَ قَوْمًا ۙ اللَّهُ مُهْلِكُهُمْ أَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيدًا ۖ قَالُوا مَعْذِرَةً إِلَىٰ رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُونَ','و (به یاد آر) هنگامی را که گروهی از آنها (به گروه دیگر) گفتند: «چرا جمعی (گنهکار) را اندرز می‌دهید که سرانجام خداوند آنها را هلاک خواهد کرد، یا به عذاب شدیدی گرفتار خواهد ساخت؟! (آنها را به حال خود واگذارید تا نابود شوند!)» گفتند: «(این اندرزها،) برای اعتذار (و رفع مسؤولیت) در پیشگاه پروردگار شماست؛ بعلاوه شاید آنها (بپذیرند، و از گناه باز ایستند، و) تقوا پیشه کنند!»'];
ayat[1119]=[7,165,'فَلَمَّا نَسُوا مَا ذُكِّرُوا بِهِ أَنجَيْنَا الَّذِينَ يَنْهَوْنَ عَنِ السُّوءِ وَأَخَذْنَا الَّذِينَ ظَلَمُوا بِعَذَابٍ بَئِيسٍ بِمَا كَانُوا يَفْسُقُونَ','امّا هنگامی که تذکراتی را که به آنها داده شده بود فراموش کردند، (لحظه عذاب فرا رسید؛ و) نهی‌کنندگان از بدی را رهایی بخشیدیم؛ و کسانی را که ستم کردند، بخاطر نافرمانیشان به عذاب شدیدی گرفتار ساختیم.'];
ayat[1120]=[7,166,'فَلَمَّا عَتَوْا عَن مَّا نُهُوا عَنْهُ قُلْنَا لَهُمْ كُونُوا قِرَدَةً خَاسِئِينَ','(آری،) هنگامی که در برابر آنچه از آن نهی شده بودند سرکشی کردند، به آنها گفتیم: «به شکل میمونهایی طردشده در آیید!»'];
ayat[1121]=[7,167,'وَإِذْ تَأَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ إِلَىٰ يَوْمِ الْقِيَامَةِ مَن يَسُومُهُمْ سُوءَ الْعَذَابِ ۗ إِنَّ رَبَّكَ لَسَرِيعُ الْعِقَابِ ۖ وَإِنَّهُ لَغَفُورٌ رَّحِيمٌ','و (نیز به خاطر بیاور) هنگامی را که پروردگارت اعلام کرد: تا دامنه قیامت، کسی را بر آنها مسلّط خواهد ساخت که همواره آنها را در عذاب سختی قرار دهد؛ زیرا پروردگارت مجازاتش سریع، (و در عین حال، نسبت به توبه‌کاران) آمرزنده و مهربان است.'];
ayat[1122]=[7,168,'وَقَطَّعْنَاهُمْ فِي الْأَرْضِ أُمَمًا ۖ مِّنْهُمُ الصَّالِحُونَ وَمِنْهُمْ دُونَ ذَٰلِكَ ۖ وَبَلَوْنَاهُم بِالْحَسَنَاتِ وَالسَّيِّئَاتِ لَعَلَّهُمْ يَرْجِعُونَ','و آنها را در زمین بصورت گروه‌هایی، پراکنده ساختیم؛ گروهی از آنها صالح، و گروهی ناصالحند. و آنها را با نیکی‌ها و بدی‌ها آزمودیم، شاید بازگردند!'];
ayat[1123]=[7,169,'فَخَلَفَ مِن بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتَابَ يَأْخُذُونَ عَرَضَ هَٰذَا الْأَدْنَىٰ وَيَقُولُونَ سَيُغْفَرُ لَنَا وَإِن يَأْتِهِمْ عَرَضٌ مِّثْلُهُ يَأْخُذُوهُ ۚ أَلَمْ يُؤْخَذْ عَلَيْهِم مِّيثَاقُ الْكِتَابِ أَن لَّا يَقُولُوا عَلَى اللَّهِ إِلَّا الْحَقَّ وَدَرَسُوا مَا فِيهِ ۗ وَالدَّارُ الْآخِرَةُ خَيْرٌ لِّلَّذِينَ يَتَّقُونَ ۗ أَفَلَا تَعْقِلُونَ','پس از آنها، فرزندانی جای آنها را گرفتند که وارث کتاب (آسمانی، تورات) شدند؛ (امّا با این حال،) متاع این دنیای پست را گرفته، (بر اطاعت فرمان خدا ترجیح می‌دهند) و می‌گویند: «(اگر ما گنهکاریم توبه می‌کنیم و) بزودی بخشیده خواهیم شد!» اما اگر متاع دیگری همانند آن به دستشان بیفتد، آن را (نیز) می‌گیرند، (و باز حکم خدا را پشت سر می‌افکنند.) آیا پیمان کتاب (خدا) از آنها گرفته نشده که بر خدا (دروغ نبندند، و) جز حق نگویند، و آنان بارها آنرا خوانده‌اند؟! و سرای آخرت برای پرهیزگاران بهتر است، آیا نمی‌فهمید؟!'];
ayat[1124]=[7,170,'وَالَّذِينَ يُمَسِّكُونَ بِالْكِتَابِ وَأَقَامُوا الصَّلَاةَ إِنَّا لَا نُضِيعُ أَجْرَ الْمُصْلِحِينَ','و آنها که به کتاب (خدا) تمسّک جویند، و نماز را برپا دارند، (پاداش بزرگی خواهند داشت؛ زیرا) ما پاداش مصلحان را ضایع نخواهیم کرد!'];
ayat[1125]=[7,171,'۞ وَإِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَأَنَّهُ ظُلَّةٌ وَظَنُّوا أَنَّهُ وَاقِعٌ بِهِمْ خُذُوا مَا آتَيْنَاكُم بِقُوَّةٍ وَاذْكُرُوا مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَ','و (نیز به خاطر بیاور) هنگامی که کوه را همچون سایبانی بر فراز آنها بلند کردیم، آنچنان که گمان کردند بر آنان فرود می‌آمد؛ (و در همین حال، از آنها پیمان گرفتیم و گفتیم:) آنچه را (از احکام و دستورها) به شما داده‌ایم، با قوّت (و جدیت) بگیرید! و آنچه در آن است، به یاد داشته باشید، (و عمل کنید،) تا پرهیزگار شوید!'];
ayat[1126]=[7,172,'وَإِذْ أَخَذَ رَبُّكَ مِن بَنِي آدَمَ مِن ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَأَشْهَدَهُمْ عَلَىٰ أَنفُسِهِمْ أَلَسْتُ بِرَبِّكُمْ ۖ قَالُوا بَلَىٰ ۛ شَهِدْنَا ۛ أَن تَقُولُوا يَوْمَ الْقِيَامَةِ إِنَّا كُنَّا عَنْ هَٰذَا غَافِلِينَ','و (به خاطر بیاور) زمانی را که پروردگارت از پشت و صلب فرزندان آدم، ذریه آنها را برگرفت؛ و آنها را گواه بر خویشتن ساخت؛ (و فرمود:) «آیا من پروردگار شما نیستم؟» گفتند: «آری، گواهی می‌دهیم!» (چنین کرد مبادا) روز رستاخیز بگویید: «ما از این، غافل بودیم؛ (و از پیمان فطری توحید بی‌خبر ماندیم)»!'];
ayat[1127]=[7,173,'أَوْ تَقُولُوا إِنَّمَا أَشْرَكَ آبَاؤُنَا مِن قَبْلُ وَكُنَّا ذُرِّيَّةً مِّن بَعْدِهِمْ ۖ أَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُونَ','یا بگویید: «پدرانمان پیش از ما مشرک بودند، ما هم فرزندانی بعد از آنها بودیم؛ (و چاره‌ای جز پیروی از آنان نداشتیم؛) آیا ما را به آنچه باطل‌گرایان انجام دادند مجازات می‌کنی؟!»'];
ayat[1128]=[7,174,'وَكَذَٰلِكَ نُفَصِّلُ الْآيَاتِ وَلَعَلَّهُمْ يَرْجِعُونَ','این گونه، آیات را توضیح می‌دهیم؛ و شاید به سوی حق بازگردند (و بدانند ندای توحید در درون جانشان، از روز نخست بوده است)!'];
ayat[1129]=[7,175,'وَاتْلُ عَلَيْهِمْ نَبَأَ الَّذِي آتَيْنَاهُ آيَاتِنَا فَانسَلَخَ مِنْهَا فَأَتْبَعَهُ الشَّيْطَانُ فَكَانَ مِنَ الْغَاوِينَ','و بر آنها بخوان سرگذشت آن کس را که آیات خود را به او دادیم؛ ولی (سرانجام) خود را از آن تهی ساخت و شیطان در پی او افتاد، و از گمراهان شد!'];
ayat[1130]=[7,176,'وَلَوْ شِئْنَا لَرَفَعْنَاهُ بِهَا وَلَٰكِنَّهُ أَخْلَدَ إِلَى الْأَرْضِ وَاتَّبَعَ هَوَاهُ ۚ فَمَثَلُهُ كَمَثَلِ الْكَلْبِ إِن تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَث ۚ ذَّٰلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ','و اگر می‌خواستیم، (مقام) او را با این آیات (و علوم و دانشها) بالا می‌بردیم؛ (اما اجبار، بر خلاف سنت ماست؛ پس او را به حال خود رها کردیم) و او به پستی گرایید، و از هوای نفس پیروی کرد! مثل او همچون سگ (هار) است که اگر به او حمله کنی، دهانش را باز، و زبانش را برون می‌آورد، و اگر او را به حال خود واگذاری، باز همین کار را می‌کند؛ (گویی چنان تشنه دنیاپرستی است که هرگز سیراب نمی‌شود! (این مثل گروهی است که آیات ما را تکذیب کردند؛ این داستانها را (برای آنها) بازگو کن، شاید بیندیشند (و بیدار شوند)!'];
ayat[1131]=[7,177,'سَاءَ مَثَلًا الْقَوْمُ الَّذِينَ كَذَّبُوا بِآيَاتِنَا وَأَنفُسَهُمْ كَانُوا يَظْلِمُونَ','چه بد مثلی دارند گروهی که آیات ما را تکذیب کردند؛ و آنها تنها به خودشان ستم می‌نمودند!'];
ayat[1132]=[7,178,'مَن يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِي ۖ وَمَن يُضْلِلْ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ','آن کس را که خدا هدایت کند، هدایت یافته (واقعی) اوست؛ و کسانی را که (بخاطر اعمالشان) گمراه سازد، زیانکاران (واقعی) آنها هستند!'];
ayat[1133]=[7,179,'وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيرًا مِّنَ الْجِنِّ وَالْإِنسِ ۖ لَهُمْ قُلُوبٌ لَّا يَفْقَهُونَ بِهَا وَلَهُمْ أَعْيُنٌ لَّا يُبْصِرُونَ بِهَا وَلَهُمْ آذَانٌ لَّا يَسْمَعُونَ بِهَا ۚ أُولَٰئِكَ كَالْأَنْعَامِ بَلْ هُمْ أَضَلُّ ۚ أُولَٰئِكَ هُمُ الْغَافِلُونَ','به یقین، گروه بسیاری از جن و انس را برای دوزخ آفریدیم؛ آنها دلها [= عقلها] یی دارند که با آن (اندیشه نمی‌کنند، و) نمی‌فهمند؛ و چشمانی که با آن نمی‌بینند؛ و گوشهایی که با آن نمی‌شنوند؛ آنها همچون چهارپایانند؛ بلکه گمراهتر! اینان همان غافلانند (چرا که با داشتن همه‌گونه امکانات هدایت، باز هم گمراهند)!'];
ayat[1134]=[7,180,'وَلِلَّهِ الْأَسْمَاءُ الْحُسْنَىٰ فَادْعُوهُ بِهَا ۖ وَذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمَائِهِ ۚ سَيُجْزَوْنَ مَا كَانُوا يَعْمَلُونَ','و برای خدا، نامهای نیک است؛ خدا را به آن (نامها) بخوانید! و کسانی را که در اسماء خدا تحریف می‌کنند (و بر غیر او می‌نهند، و شریک برایش قائل می‌شوند)، رها سازید! آنها بزودی جزای اعمالی را که انجام می‌دادند، می‌بینند!'];
ayat[1135]=[7,181,'وَمِمَّنْ خَلَقْنَا أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَبِهِ يَعْدِلُونَ','و از آنها که آفریدیم، گروهی بحق هدایت می‌کنند، و بحق اجرای عدالت می‌نمایند.'];
ayat[1136]=[7,182,'وَالَّذِينَ كَذَّبُوا بِآيَاتِنَا سَنَسْتَدْرِجُهُم مِّنْ حَيْثُ لَا يَعْلَمُونَ','و آنها که آیات ما را تکذیب کردند، به تدریج از جائی که نمی‌دانند، گرفتار مجازاتشان خواهیم کرد.'];
ayat[1137]=[7,183,'وَأُمْلِي لَهُمْ ۚ إِنَّ كَيْدِي مَتِينٌ','و به آنها مهلت می‌دهم (تا مجازاتشان دردناکتر باشد)؛ زیرا طرح و نقشه من، قوی (و حساب شده) است. (و هیچ کس را قدرت فرار از آن نیست.)'];
ayat[1138]=[7,184,'أَوَلَمْ يَتَفَكَّرُوا ۗ مَا بِصَاحِبِهِم مِّن جِنَّةٍ ۚ إِنْ هُوَ إِلَّا نَذِيرٌ مُّبِينٌ','آیا فکر نکردند که همنشین آنها [= پیامبر] هیچ‌گونه (اثری از) جنون ندارد؟! (پس چگونه چنین نسبت ناروایی به او می‌دهند؟!) او فقط بیم دهنده آشکاری است (که مردم را متوجه وظایفشان می‌سازد).'];
ayat[1139]=[7,185,'أَوَلَمْ يَنظُرُوا فِي مَلَكُوتِ السَّمَاوَاتِ وَالْأَرْضِ وَمَا خَلَقَ اللَّهُ مِن شَيْءٍ وَأَنْ عَسَىٰ أَن يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ ۖ فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ','آیا در حکومت و نظام آسمانها و زمین، و آنچه خدا آفریده است، (از روی دقت و عبرت) نظر نیفکندند؟! (و آیا در این نیز اندیشه نکردند که) شاید پایان زندگی آنها نزدیک شده باشد؟! (اگر به این کتاب آسمانی روشن ایمان نیاورند،) بعد از آن به کدام سخن ایمان خواهند آورد؟!'];
ayat[1140]=[7,186,'مَن يُضْلِلِ اللَّهُ فَلَا هَادِيَ لَهُ ۚ وَيَذَرُهُمْ فِي طُغْيَانِهِمْ يَعْمَهُونَ','هر کس را خداوند (به جرم اعمال زشتش) گمراه سازد، هدایت کننده‌ای ندارد؛ و آنها را در طغیان و سرکشی‌شان رها می‌سازد، تا سرگردان شوند!'];
ayat[1141]=[7,187,'يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا ۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ رَبِّي ۖ لَا يُجَلِّيهَا لِوَقْتِهَا إِلَّا هُوَ ۚ ثَقُلَتْ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ لَا تَأْتِيكُمْ إِلَّا بَغْتَةً ۗ يَسْأَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْهَا ۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ اللَّهِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','درباره قیامت از تو سؤال می‌کنند، کی فرامی‌رسد؟! بگو: «علمش فقط نزد پروردگار من است؛ و هیچ‌کس جز او (نمی‌تواند) وقت آن را آشکار سازد؛ (اما قیام قیامت، حتی) در آسمانها و زمین، سنگین (و بسیار پر اهمیت) است؛ و جز بطور ناگهانی، به سراغ شما نمی‌آید!» (باز) از تو سؤال می‌کنند، چنان که گویی تو از زمان وقوع آن باخبری! بگو: «علمش تنها نزد خداست؛ ولی بیشتر مردم نمی‌دانند.»'];
ayat[1142]=[7,188,'قُل لَّا أَمْلِكُ لِنَفْسِي نَفْعًا وَلَا ضَرًّا إِلَّا مَا شَاءَ اللَّهُ ۚ وَلَوْ كُنتُ أَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِ وَمَا مَسَّنِيَ السُّوءُ ۚ إِنْ أَنَا إِلَّا نَذِيرٌ وَبَشِيرٌ لِّقَوْمٍ يُؤْمِنُونَ','بگو: «من مالک سود و زیان خویش نیستم، مگر آنچه را خدا بخواهد؛ (و از غیب و اسرار نهان نیز خبر ندارم، مگر آنچه خداوند اراده کند؛) و اگر از غیب باخبر بودم، سود فراوانی برای خود فراهم می‌کردم، و هیچ بدی (و زیانی) به من نمی‌رسید؛ من فقط بیم‌دهنده و بشارت‌دهنده‌ام برای گروهی که ایمان می‌آورند! (و آماده پذیرش حقند)'];
ayat[1143]=[7,189,'۞ هُوَ الَّذِي خَلَقَكُم مِّن نَّفْسٍ وَاحِدَةٍ وَجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ إِلَيْهَا ۖ فَلَمَّا تَغَشَّاهَا حَمَلَتْ حَمْلًا خَفِيفًا فَمَرَّتْ بِهِ ۖ فَلَمَّا أَثْقَلَت دَّعَوَا اللَّهَ رَبَّهُمَا لَئِنْ آتَيْتَنَا صَالِحًا لَّنَكُونَنَّ مِنَ الشَّاكِرِينَ','او خدایی است که (همه) شما را از یک فرد آفرید؛ و همسرش را نیز از جنس او قرار داد، تا در کنار او بیاساید. سپس هنگامی که با او آمیزش کرد، حملی سبک برداشت، که با وجود آن، به کارهای خود ادامه می‌داد؛ و چون سنگین شد، هر دو از خداوند و پروردگار خود خواستند «اگر فرزند صالحی به ما دهی، از شاکران خواهیم بود!»'];
ayat[1144]=[7,190,'فَلَمَّا آتَاهُمَا صَالِحًا جَعَلَا لَهُ شُرَكَاءَ فِيمَا آتَاهُمَا ۚ فَتَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ','اما هنگامی که خداوند فرزند صالحی به آنها داد، (موجودات دیگر را در این موهبت مؤثر دانستند؛ و) برای خدا، در این نعمت که به آنها بخشیده بود، همتایانی قائل شدند؛ خداوند برتر است از آنچه همتای او قرار می‌دهند!'];
ayat[1145]=[7,191,'أَيُشْرِكُونَ مَا لَا يَخْلُقُ شَيْئًا وَهُمْ يُخْلَقُونَ','آیا موجوداتی را همتای او قرارمی‌دهند که چیزی را نمی‌آفرینند، و خودشان مخلوقند.'];
ayat[1146]=[7,192,'وَلَا يَسْتَطِيعُونَ لَهُمْ نَصْرًا وَلَا أَنفُسَهُمْ يَنصُرُونَ','و نمی‌توانند آنان را یاری کنند، و نه خودشان را یاری می‌دهند.'];
ayat[1147]=[7,193,'وَإِن تَدْعُوهُمْ إِلَى الْهُدَىٰ لَا يَتَّبِعُوكُمْ ۚ سَوَاءٌ عَلَيْكُمْ أَدَعَوْتُمُوهُمْ أَمْ أَنتُمْ صَامِتُونَ','و هرگاه آنها را به سوی هدایت دعوت کنید، از شما پیروی نمی‌کنند؛ و برای شما یکسان است چه آنها را دعوت کنید و چه خاموش باشید؟!'];
ayat[1148]=[7,194,'إِنَّ الَّذِينَ تَدْعُونَ مِن دُونِ اللَّهِ عِبَادٌ أَمْثَالُكُمْ ۖ فَادْعُوهُمْ فَلْيَسْتَجِيبُوا لَكُمْ إِن كُنتُمْ صَادِقِينَ','آنهایی را که غیر از خدا می‌خوانید (و پرستش می‌کنید)، بندگانی همچون خود شما هستند؛ آنها را بخوانید، و اگر راست می‌گویید باید به شما پاسخ دهند (و تقاضایتان را برآورند)!'];
ayat[1149]=[7,195,'أَلَهُمْ أَرْجُلٌ يَمْشُونَ بِهَا ۖ أَمْ لَهُمْ أَيْدٍ يَبْطِشُونَ بِهَا ۖ أَمْ لَهُمْ أَعْيُنٌ يُبْصِرُونَ بِهَا ۖ أَمْ لَهُمْ آذَانٌ يَسْمَعُونَ بِهَا ۗ قُلِ ادْعُوا شُرَكَاءَكُمْ ثُمَّ كِيدُونِ فَلَا تُنظِرُونِ','آیا (آنها حداقل همانند خود شما) پاهایی دارند که با آن راه بروند؟! یا دستهایی دارند که با آن چیزی را بگیرند (و کاری انجام دهند)؟! یا چشمانی دارند که با آن ببینند؟! یا گوشهایی دارند که با آن بشنوند؟! (نه، هرگز، هیچ‌کدام،) بگو: «(اکنون که چنین است،) بتهای خویش را که شریک خدا قرار داده‌اید (بر ضد من) بخوانید، و برای من نقشه بکشید، و لحظه‌ای مهلت ندهید، (تا بدانید کاری از آنها ساخته نیست)!'];
ayat[1150]=[7,196,'إِنَّ وَلِيِّيَ اللَّهُ الَّذِي نَزَّلَ الْكِتَابَ ۖ وَهُوَ يَتَوَلَّى الصَّالِحِينَ','ولی و سرپرست من، خدایی است که این کتاب را نازل کرده؛ و او همه صالحان را سرپرستی می‌کند.'];
ayat[1151]=[7,197,'وَالَّذِينَ تَدْعُونَ مِن دُونِهِ لَا يَسْتَطِيعُونَ نَصْرَكُمْ وَلَا أَنفُسَهُمْ يَنصُرُونَ','و آنهایی را که جز او می‌خوانید، نمی‌توانند یاریتان کنند، و نه (حتی) خودشان را یاری دهند؛'];
ayat[1152]=[7,198,'وَإِن تَدْعُوهُمْ إِلَى الْهُدَىٰ لَا يَسْمَعُوا ۖ وَتَرَاهُمْ يَنظُرُونَ إِلَيْكَ وَهُمْ لَا يُبْصِرُونَ','و اگر آنها را به هدایت فرا خوانید، سخنانتان را نمی‌شنوند! و آنها را می‌بینی (که با چشمهای مصنوعیشان) به تو نگاه می‌کنند، اما در حقیقت نمی‌بینند!»'];
ayat[1153]=[7,199,'خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَأَعْرِضْ عَنِ الْجَاهِلِينَ','(به هر حال) با آنها مدارا کن و عذرشان را بپذیر، و به نیکی‌ها دعوت نما، و از جاهلان روی بگردان (و با آنان ستیزه مکن)!'];
ayat[1154]=[7,200,'وَإِمَّا يَنزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ ۚ إِنَّهُ سَمِيعٌ عَلِيمٌ','و هرگاه وسوسه‌ای از شیطان به تو رسد، به خدا پناه بر؛ که او شنونده و داناست!'];
ayat[1155]=[7,201,'إِنَّ الَّذِينَ اتَّقَوْا إِذَا مَسَّهُمْ طَائِفٌ مِّنَ الشَّيْطَانِ تَذَكَّرُوا فَإِذَا هُم مُّبْصِرُونَ','پرهیزگاران هنگامی که گرفتار وسوسه‌های شیطان شوند، به یاد (خدا و پاداش و کیفر او) می‌افتند؛ و (در پرتو یاد او، راه حق را می‌بینند و) ناگهان بینا می‌گردند.'];
ayat[1156]=[7,202,'وَإِخْوَانُهُمْ يَمُدُّونَهُمْ فِي الْغَيِّ ثُمَّ لَا يُقْصِرُونَ','و (ناپرهیزگاران را) برادرانشان (از شیاطین) پیوسته در گمراهی پیش می‌برند، و باز نمی‌ایستند!'];
ayat[1157]=[7,203,'وَإِذَا لَمْ تَأْتِهِم بِآيَةٍ قَالُوا لَوْلَا اجْتَبَيْتَهَا ۚ قُلْ إِنَّمَا أَتَّبِعُ مَا يُوحَىٰ إِلَيَّ مِن رَّبِّي ۚ هَٰذَا بَصَائِرُ مِن رَّبِّكُمْ وَهُدًى وَرَحْمَةٌ لِّقَوْمٍ يُؤْمِنُونَ','هنگامی که (در نزول وحی تاخیر افتد، و) آیه‌ای برای آنان نیاوری، می‌گویند: «چرا خودت (از پیش خود) آن را برنگزیدی؟!» بگو: «من تنها از چیزی پیروی می‌کنم که بر من وحی می‌شود؛ این وسیله بینایی از طرف پروردگارتان، و مایه هدایت و رحمت است برای جمعیّتی که ایمان می‌آورند.'];
ayat[1158]=[7,204,'وَإِذَا قُرِئَ الْقُرْآنُ فَاسْتَمِعُوا لَهُ وَأَنصِتُوا لَعَلَّكُمْ تُرْحَمُونَ','هنگامی که قرآن خوانده شود، گوش فرا دهید و خاموش باشید؛ شاید مشمول رحمت خدا شوید!'];
ayat[1159]=[7,205,'وَاذْكُر رَّبَّكَ فِي نَفْسِكَ تَضَرُّعًا وَخِيفَةً وَدُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْآصَالِ وَلَا تَكُن مِّنَ الْغَافِلِينَ','پروردگارت را در دل خود، از روی تضرع و خوف، آهسته و آرام، صبحگاهان و شامگاهان، یاد کن؛ و از غافلان مباش!'];
ayat[1160]=[7,206,'إِنَّ الَّذِينَ عِندَ رَبِّكَ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِ وَيُسَبِّحُونَهُ وَلَهُ يَسْجُدُونَ ۩','آنها که (در مقام قرب) نزد پروردگار تو هستند، (هیچ‌گاه) از عبادتش تکبر نمی‌ورزند، و او را تسبیح می‌گویند، و برایش سجده می‌کنند.'];
ayat[1161]=[8,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَسْأَلُونَكَ عَنِ الْأَنفَالِ ۖ قُلِ الْأَنفَالُ لِلَّهِ وَالرَّسُولِ ۖ فَاتَّقُوا اللَّهَ وَأَصْلِحُوا ذَاتَ بَيْنِكُمْ ۖ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ إِن كُنتُم مُّؤْمِنِينَ','از تو درباره انفال [= غنایم، و هرگونه مال بدون مالک مشخص‌] سؤال می‌کنند؛ بگو: «انفال مخصوص خدا و پیامبر است؛ پس، از (مخالفت فرمان) خدا بپرهیزید! و خصومتهایی را که در میان شماست، آشتی دهید! و خدا و پیامبرش را اطاعت کنید اگر ایمان دارید!'];
ayat[1162]=[8,2,'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَإِذَا تُلِيَتْ عَلَيْهِمْ آيَاتُهُ زَادَتْهُمْ إِيمَانًا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ','مؤمنان، تنها کسانی هستند که هرگاه نام خدا برده شود، دلهاشان ترسان میگردد؛ و هنگامی که آیات او بر آنها خوانده می‌شود، ایمانشان فزونتر می‌گردد؛ و تنها بر پروردگارشان توکل دارند.'];
ayat[1163]=[8,3,'الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ','آنها که نماز را برپا می‌دارند؛ و از آنچه به آنها روزی داده‌ایم، انفاق می‌کنند.'];
ayat[1164]=[8,4,'أُولَٰئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا ۚ لَّهُمْ دَرَجَاتٌ عِندَ رَبِّهِمْ وَمَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ','(آری،) مؤمنان حقیقی آنها هستند؛ برای آنان درجاتی (مهم) نزد پروردگارشان است؛ و برای آنها، آمرزش و روزی بی‌نقص و عیب است.'];
ayat[1165]=[8,5,'كَمَا أَخْرَجَكَ رَبُّكَ مِن بَيْتِكَ بِالْحَقِّ وَإِنَّ فَرِيقًا مِّنَ الْمُؤْمِنِينَ لَكَارِهُونَ','همان‌گونه که خدا تو را بحق از خانه (به سوی میدان بدر،) بیرون فرستاد، در حالی که گروهی از مؤمنان ناخشنود بودند (؛ ولی سرانجامش پیروزی بود! ناخشنودی عده‌ای از چگونگی تقسیم غنایم بدر نیز چنین است)!'];
ayat[1166]=[8,6,'يُجَادِلُونَكَ فِي الْحَقِّ بَعْدَمَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى الْمَوْتِ وَهُمْ يَنظُرُونَ','آنها پس از روشن شدن حق، باز با تو مجادله می‌کردند؛ (و چنان ترس و وحشت آنها را فراگرفته بود، که) گویی به سوی مرگ رانده می‌شوند، و آن را با چشم خود می‌نگرند!'];
ayat[1167]=[8,7,'وَإِذْ يَعِدُكُمُ اللَّهُ إِحْدَى الطَّائِفَتَيْنِ أَنَّهَا لَكُمْ وَتَوَدُّونَ أَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَيُرِيدُ اللَّهُ أَن يُحِقَّ الْحَقَّ بِكَلِمَاتِهِ وَيَقْطَعَ دَابِرَ الْكَافِرِينَ','و (به یاد آرید) هنگامی را که خداوند به شما وعده داد که یکی از دو گروه [= کاروان تجاری قریش، یا لشکر مسلح آنها] نصیب شما خواهد بود؛ و شما دوست می‌داشتید که کاروان (غیر مسلح) برای شما باشد (و بر آن پیروز شوید)؛ ولی خداوند می‌خواهد حق را با کلمات خود تقویت، و ریشه کافران را قطع کند؛ (از این رو شما را بر خلاف میلتان با لشکر قریش درگیر ساخت، و آن پیروزی بزرگ نصیبتان شد.)'];
ayat[1168]=[8,8,'لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُونَ','تا حق را تثبیت کند، و باطل را از میان بردارد، هر چند مجرمان کراهت داشته باشند.'];
ayat[1169]=[8,9,'إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ أَنِّي مُمِدُّكُم بِأَلْفٍ مِّنَ الْمَلَائِكَةِ مُرْدِفِينَ','(به خاطر بیاورید) زمانی را (که از شدت ناراحتی در میدان بدر،) از پروردگارتان کمک می‌خواستید؛ و او خواسته شما را پذیرفت (و گفت): من شما را با یکهزار از فرشتگان، که پشت سر هم فرود می‌آیند، یاری می‌کنم.'];
ayat[1170]=[8,10,'وَمَا جَعَلَهُ اللَّهُ إِلَّا بُشْرَىٰ وَلِتَطْمَئِنَّ بِهِ قُلُوبُكُمْ ۚ وَمَا النَّصْرُ إِلَّا مِنْ عِندِ اللَّهِ ۚ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','ولی خداوند، این را تنها برای شادی و اطمینان قلب شما قرار داد؛ وگرنه، پیروزی جز از طرف خدا نیست؛ خداوند توانا و حکیم است!'];
ayat[1171]=[8,11,'إِذْ يُغَشِّيكُمُ النُّعَاسَ أَمَنَةً مِّنْهُ وَيُنَزِّلُ عَلَيْكُم مِّنَ السَّمَاءِ مَاءً لِّيُطَهِّرَكُم بِهِ وَيُذْهِبَ عَنكُمْ رِجْزَ الشَّيْطَانِ وَلِيَرْبِطَ عَلَىٰ قُلُوبِكُمْ وَيُثَبِّتَ بِهِ الْأَقْدَامَ','و (یاد آورید) هنگامی را که خواب سبکی که مایه آرامش از سوی خدا بود، شما را فراگرفت؛ و آبی از آسمان برایتان فرستاد، تا شما را با آن پاک کند؛ و پلیدی شیطان را از شما دور سازد؛ و دلهایتان را محکم، و گامها را با آن استوار دارد!'];
ayat[1172]=[8,12,'إِذْ يُوحِي رَبُّكَ إِلَى الْمَلَائِكَةِ أَنِّي مَعَكُمْ فَثَبِّتُوا الَّذِينَ آمَنُوا ۚ سَأُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الْأَعْنَاقِ وَاضْرِبُوا مِنْهُمْ كُلَّ بَنَانٍ','و (به یاد آر) موقعی را که پروردگارت به فرشتگان وحی کرد: «من با شما هستم؛ کسانی را که ایمان آورده‌اند، ثابت قدم دارید! بزودی در دلهای کافران ترس و وحشت می‌افکنم؛ ضربه‌ها را بر بالاتر از گردن (بر سرهای دشمنان) فرود آرید! و همه انگشتانشان را قطع کنید!'];
ayat[1173]=[8,13,'ذَٰلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ ۚ وَمَن يُشَاقِقِ اللَّهَ وَرَسُولَهُ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ','این بخاطر آن است که آنها با خدا و پیامبرش (ص) دشمنی ورزیدند؛ و هر کس با خدا و پیامبرش دشمنی کند، (کیفر شدیدی می‌بیند؛) و خداوند شدید العقاب است!'];
ayat[1174]=[8,14,'ذَٰلِكُمْ فَذُوقُوهُ وَأَنَّ لِلْكَافِرِينَ عَذَابَ النَّارِ','این (مجازات دنیا) را بچشید! و برای کافران، مجازات آتش (در جهان دیگر) خواهد بود!'];
ayat[1175]=[8,15,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفًا فَلَا تُوَلُّوهُمُ الْأَدْبَارَ','ای کسانی که ایمان آورده‌اید! هنگامی که با انبوه کافران در میدان نبرد روبه‌رو شوید، به آنها پشت نکنید (و فرار ننمایید)!'];
ayat[1176]=[8,16,'وَمَن يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُ إِلَّا مُتَحَرِّفًا لِّقِتَالٍ أَوْ مُتَحَيِّزًا إِلَىٰ فِئَةٍ فَقَدْ بَاءَ بِغَضَبٍ مِّنَ اللَّهِ وَمَأْوَاهُ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ','و هر کس در آن هنگام به آنها پشت کند -مگر آنکه هدفش کناره‌گیری از میدان برای حمله مجدد، و یا به قصد پیوستن به گروهی (از مجاهدان) بوده باشد- (چنین کسی) به غضب خدا گرفتار خواهد شد؛ و جایگاه او جهنم، و چه بد جایگاهی است!'];
ayat[1177]=[8,17,'فَلَمْ تَقْتُلُوهُمْ وَلَٰكِنَّ اللَّهَ قَتَلَهُمْ ۚ وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَٰكِنَّ اللَّهَ رَمَىٰ ۚ وَلِيُبْلِيَ الْمُؤْمِنِينَ مِنْهُ بَلَاءً حَسَنًا ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ','این شما نبودید که آنها را کشتید؛ بلکه خداوند آنها را کشت! و این تو نبودی (ای پیامبر که خاک و سنگ به صورت آنها) انداختی؛ بلکه خدا انداخت! و خدا می‌خواست مؤمنان را به این وسیله امتحان خوبی کند؛ خداوند شنوا و داناست.'];
ayat[1178]=[8,18,'ذَٰلِكُمْ وَأَنَّ اللَّهَ مُوهِنُ كَيْدِ الْكَافِرِينَ','سرنوشت مؤمنان و کافران، همان بود که دیدید! و خداوند سست‌کننده نقشه‌های کافران است.'];
ayat[1179]=[8,19,'إِن تَسْتَفْتِحُوا فَقَدْ جَاءَكُمُ الْفَتْحُ ۖ وَإِن تَنتَهُوا فَهُوَ خَيْرٌ لَّكُمْ ۖ وَإِن تَعُودُوا نَعُدْ وَلَن تُغْنِيَ عَنكُمْ فِئَتُكُمْ شَيْئًا وَلَوْ كَثُرَتْ وَأَنَّ اللَّهَ مَعَ الْمُؤْمِنِينَ','اگر شما فتح و پیروزی می‌خواهید، پیروزی به سراغ شما آمد! و اگر (از مخالفت) خودداری کنید، برای شما بهتر است! و اگر بازگردید، ما هم باز خواهیم گشت؛ و جمعیت شما هر چند زیاد باشد، شما را (از یاری خدا) بی‌نیاز نخواهد کرد؛ و خداوند با مؤمنان است!'];
ayat[1180]=[8,20,'يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَرَسُولَهُ وَلَا تَوَلَّوْا عَنْهُ وَأَنتُمْ تَسْمَعُونَ','ای کسانی که ایمان آورده‌اید! خدا و پیامبرش را اطاعت کنید؛ و سرپیچی ننمایید در حالی که (سخنان او را) می‌شنوید!'];
ayat[1181]=[8,21,'وَلَا تَكُونُوا كَالَّذِينَ قَالُوا سَمِعْنَا وَهُمْ لَا يَسْمَعُونَ','و همانند کسانی نباشید که می‌گفتند: «شنیدیم!» ولی در حقیقت نمی‌شنیدند!'];
ayat[1182]=[8,22,'۞ إِنَّ شَرَّ الدَّوَابِّ عِندَ اللَّهِ الصُّمُّ الْبُكْمُ الَّذِينَ لَا يَعْقِلُونَ','بدترین جنبندگان نزد خدا، افراد کر و لالی هستند که اندیشه نمی‌کنند.'];
ayat[1183]=[8,23,'وَلَوْ عَلِمَ اللَّهُ فِيهِمْ خَيْرًا لَّأَسْمَعَهُمْ ۖ وَلَوْ أَسْمَعَهُمْ لَتَوَلَّوا وَّهُم مُّعْرِضُونَ','و اگر خداوند خیری در آنها می‌دانست، (حرف حق را) به گوش آنها می‌رساند؛ ولی (با این حال که دارند،) اگر حق را به گوش آنها برساند، سرپیچی کرده و روگردان می‌شوند.'];
ayat[1184]=[8,24,'يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَجِيبُوا لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمْ لِمَا يُحْيِيكُمْ ۖ وَاعْلَمُوا أَنَّ اللَّهَ يَحُولُ بَيْنَ الْمَرْءِ وَقَلْبِهِ وَأَنَّهُ إِلَيْهِ تُحْشَرُونَ','ای کسانی که ایمان آورده‌اید! دعوت خدا و پیامبر را اجابت کنید هنگامی که شما را به سوی چیزی می‌خواند که شما را حیات می‌بخشد! و بدانید خداوند میان انسان و قلب او حایل می‌شود، و همه شما (در قیامت) نزد او گردآوری می‌شوید!'];
ayat[1185]=[8,25,'وَاتَّقُوا فِتْنَةً لَّا تُصِيبَنَّ الَّذِينَ ظَلَمُوا مِنكُمْ خَاصَّةً ۖ وَاعْلَمُوا أَنَّ اللَّهَ شَدِيدُ الْعِقَابِ','و از فتنه‌ای بپرهیزید که تنها به ستمکاران شما نمی‌رسد؛ (بلکه همه را فرا خواهد گرفت؛ چرا که دیگران سکوت اختیار کردند.) و بدانید خداوند کیفر شدید دارد!'];
ayat[1186]=[8,26,'وَاذْكُرُوا إِذْ أَنتُمْ قَلِيلٌ مُّسْتَضْعَفُونَ فِي الْأَرْضِ تَخَافُونَ أَن يَتَخَطَّفَكُمُ النَّاسُ فَآوَاكُمْ وَأَيَّدَكُم بِنَصْرِهِ وَرَزَقَكُم مِّنَ الطَّيِّبَاتِ لَعَلَّكُمْ تَشْكُرُونَ','و به خاطر بیاورید هنگامی را که شما در روی زمین، گروهی کوچک و اندک و زبون بودید؛ آنچنان که می‌ترسیدید مردم شما را بربایند! ولی او شما را پناه داد؛ و با یاری خود تقویت کرد؛ و از روزیهای پاکیزه بهره‌مند ساخت؛ شاید شکر نعمتش را بجا آورید!'];
ayat[1187]=[8,27,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَخُونُوا اللَّهَ وَالرَّسُولَ وَتَخُونُوا أَمَانَاتِكُمْ وَأَنتُمْ تَعْلَمُونَ','ای کسانی که ایمان آورده‌اید! به خدا و پیامبر خیانت نکنید! و (نیز) در امانات خود خیانت روا مدارید، در حالی که میدانید (این کار، گناه بزرگی است)!'];
ayat[1188]=[8,28,'وَاعْلَمُوا أَنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ وَأَنَّ اللَّهَ عِندَهُ أَجْرٌ عَظِيمٌ','و بدانید اموال و اولاد شما، وسیله آزمایش است؛ و (برای کسانی که از عهده امتحان برآیند،) پاداش عظیمی نزد خداست!'];
ayat[1189]=[8,29,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِن تَتَّقُوا اللَّهَ يَجْعَل لَّكُمْ فُرْقَانًا وَيُكَفِّرْ عَنكُمْ سَيِّئَاتِكُمْ وَيَغْفِرْ لَكُمْ ۗ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ','ای کسانی که ایمان آورده‌اید! اگر از (مخالفت فرمان) خدا بپرهیزید، برای شما وسیله‌ای جهت جدا ساختن حق از باطل قرارمی‌دهد؛ (روشن‌بینی خاصّی که در پرتو آن، حق را از باطل خواهید شناخت؛) و گناهانتان را می‌پوشاند؛ و شما را می‌زمرزد؛ و خداوند صاحب فضل و بخشش عظیم است!'];
ayat[1190]=[8,30,'وَإِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا لِيُثْبِتُوكَ أَوْ يَقْتُلُوكَ أَوْ يُخْرِجُوكَ ۚ وَيَمْكُرُونَ وَيَمْكُرُ اللَّهُ ۖ وَاللَّهُ خَيْرُ الْمَاكِرِينَ','(به خاطر بیاور) هنگامی را که کافران نقشه می‌کشیدند که تو را به زندان بیفکنند، یا به قتل برسانند، و یا (از مکّه) خارج سازند؛ آنها چاره می‌اندیشیدند (و نقشه می‌کشیدند)، و خداوند هم تدبیر می‌کرد؛ و خدا بهترین چاره جویان و تدبیرکنندگان است!'];
ayat[1191]=[8,31,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا قَالُوا قَدْ سَمِعْنَا لَوْ نَشَاءُ لَقُلْنَا مِثْلَ هَٰذَا ۙ إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ','و هنگامی که آیات ما بر آنها خوانده می‌شود، می‌گویند: «شنیدیم؛ (چیز مهمّی نیست؛) ما هم اگر بخواهیم مثل آن را می‌گوییم؛ اینها همان افسانه‌های پیشینیان است!» (ولی دروغ می‌گویند، و هرگز مثل آن را نمی‌آورند.)'];
ayat[1192]=[8,32,'وَإِذْ قَالُوا اللَّهُمَّ إِن كَانَ هَٰذَا هُوَ الْحَقَّ مِنْ عِندِكَ فَأَمْطِرْ عَلَيْنَا حِجَارَةً مِّنَ السَّمَاءِ أَوِ ائْتِنَا بِعَذَابٍ أَلِيمٍ','و (به خاطر بیاور) زمانی را که گفتند: «پروردگارا! اگر این حق است و از طرف توست، بارانی از سنگ از آسمان بر ما فرود آر! یا عذاب دردناکی برای ما بفرست!»'];
ayat[1193]=[8,33,'وَمَا كَانَ اللَّهُ لِيُعَذِّبَهُمْ وَأَنتَ فِيهِمْ ۚ وَمَا كَانَ اللَّهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَ','ولی (ای پیامبر!) تا تو در میان آنها هستی، خداوند آنها را مجازات نخواهد کرد؛ و (نیز) تا استغفار می‌کنند، خدا عذابشان نمی‌کند.'];
ayat[1194]=[8,34,'وَمَا لَهُمْ أَلَّا يُعَذِّبَهُمُ اللَّهُ وَهُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوا أَوْلِيَاءَهُ ۚ إِنْ أَوْلِيَاؤُهُ إِلَّا الْمُتَّقُونَ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','چرا خدا آنها را مجازات نکند، با اینکه از (عبادت موحدّان در کنار) مسجد الحرام جلوگیری می‌کنند در حالی که سرپرست آن نیستند؟! سرپرست آن، فقط پرهیزگارانند؛ ولی بیشتر آنها نمی‌دانند.'];
ayat[1195]=[8,35,'وَمَا كَانَ صَلَاتُهُمْ عِندَ الْبَيْتِ إِلَّا مُكَاءً وَتَصْدِيَةً ۚ فَذُوقُوا الْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَ','(آنها که مدّعی هستند ما هم نماز داریم،) نمازشان نزد خانه (خدا)، چیزی جز «سوت کشیدن» «کف زدن» نبود؛ پس بچشید عذاب (الهی) را بخاطر کفرتان!'];
ayat[1196]=[8,36,'إِنَّ الَّذِينَ كَفَرُوا يُنفِقُونَ أَمْوَالَهُمْ لِيَصُدُّوا عَن سَبِيلِ اللَّهِ ۚ فَسَيُنفِقُونَهَا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَ ۗ وَالَّذِينَ كَفَرُوا إِلَىٰ جَهَنَّمَ يُحْشَرُونَ','آنها که کافر شدند، اموالشان را برای بازداشتن (مردم) از راه خدا خرج می‌کنند؛ آنان این اموال را (که برای به دست آوردنش زحمت کشیده‌اند، در این راه) مصرف می‌کنند، امّا مایه حسرت و اندوهشان خواهد شد؛ و سپس شکست خواهند خورد؛ و (در جهان دیگر) کافران همگی به سوی دوزخ گردآوری خواهند شد.'];
ayat[1197]=[8,37,'لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلَىٰ بَعْضٍ فَيَرْكُمَهُ جَمِيعًا فَيَجْعَلَهُ فِي جَهَنَّمَ ۚ أُولَٰئِكَ هُمُ الْخَاسِرُونَ','(اینها همه) بخاطر آن است که خداوند (می‌خواهد) ناپاک را از پاک جدا سازد، و ناپاکها را روی هم بگذارد، و همه را متراکم سازد، و یکجا در دوزخ قرار دهد؛ و اینها هستند زیانکاران!'];
ayat[1198]=[8,38,'قُل لِّلَّذِينَ كَفَرُوا إِن يَنتَهُوا يُغْفَرْ لَهُم مَّا قَدْ سَلَفَ وَإِن يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْأَوَّلِينَ','به آنها که کافر شدند بگو: «چنانچه از مخالفت باز ایستند، (و ایمان آورند،) گذشته آنها بخشوده خواهد شد؛ و اگر به اعمال سابق بازگردند، سنّت خداوند در گذشتگان، درباره آنها جاری می‌شود (؛ و حکم نابودی آنان صادر می‌گردد).'];
ayat[1199]=[8,39,'وَقَاتِلُوهُمْ حَتَّىٰ لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ كُلُّهُ لِلَّهِ ۚ فَإِنِ انتَهَوْا فَإِنَّ اللَّهَ بِمَا يَعْمَلُونَ بَصِيرٌ','و با آنها پیکار کنید، تا فتنه [= شرک و سلب آزادی‌] برچیده شود، و دین (و پرستش) همه مخصوص خدا باشد! و اگر آنها (از راه شرک و فساد بازگردند، و از اعمال نادرست) خودداری کنند، (خداوند آنها را می‌پذیرد؛) خدا به آنچه انجام می‌دهند بیناست.'];
ayat[1200]=[8,40,'وَإِن تَوَلَّوْا فَاعْلَمُوا أَنَّ اللَّهَ مَوْلَاكُمْ ۚ نِعْمَ الْمَوْلَىٰ وَنِعْمَ النَّصِيرُ','و اگر سرپیچی کنند، بدانید (ضرری به شما نمی‌رسانند؛) خداوند سرپرست شماست! چه سرپرست خوبی! و چه یاور خوبی!'];
ayat[1201]=[8,41,'۞ وَاعْلَمُوا أَنَّمَا غَنِمْتُم مِّن شَيْءٍ فَأَنَّ لِلَّهِ خُمُسَهُ وَلِلرَّسُولِ وَلِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ إِن كُنتُمْ آمَنتُم بِاللَّهِ وَمَا أَنزَلْنَا عَلَىٰ عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعَانِ ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','بدانید هرگونه غنیمتی به دست آورید، خمس آن برای خدا، و برای پیامبر، و برای ذی‌القربی و یتیمان و مسکینان و واماندگان در راه (از آنها) است، اگر به خدا و آنچه بر بنده خود در روز جدایی حق از باطل، روز درگیری دو گروه (باایمان و بی‌ایمان) [= روز جنگ بدر] نازل کردیم، ایمان آورده‌اید؛ و خداوند بر هر چیزی تواناست!'];
ayat[1202]=[8,42,'إِذْ أَنتُم بِالْعُدْوَةِ الدُّنْيَا وَهُم بِالْعُدْوَةِ الْقُصْوَىٰ وَالرَّكْبُ أَسْفَلَ مِنكُمْ ۚ وَلَوْ تَوَاعَدتُّمْ لَاخْتَلَفْتُمْ فِي الْمِيعَادِ ۙ وَلَٰكِن لِّيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولًا لِّيَهْلِكَ مَنْ هَلَكَ عَن بَيِّنَةٍ وَيَحْيَىٰ مَنْ حَيَّ عَن بَيِّنَةٍ ۗ وَإِنَّ اللَّهَ لَسَمِيعٌ عَلِيمٌ','در آن هنگام که شما در طرف پایین بودید، و آنها در طرف بالأ (و دشمن بر شما برتری داشت؛) و کاروان (قریش)، پایین تر از شما بود؛ (و وضع چنان سخت بود که) اگر با یکدیگر وعده می‌گذاشتید (که در میدان نبرد حاضر شوید)، در انجام وعده خود اختلاف می‌کردید؛ ولی (همه اینها) برای آن بود که خداوند، کاری را که می‌بایست انجام شود، تحقّق بخشد؛ تا آنها که هلاک (و گمراه) می‌شوند، از روی اتمام حجّت باشد؛ و آنها که زنده می‌شوند (و هدایت می‌یابند)، از روی دلیل روشن باشد؛ و خداوند شنوا و داناست.'];
ayat[1203]=[8,43,'إِذْ يُرِيكَهُمُ اللَّهُ فِي مَنَامِكَ قَلِيلًا ۖ وَلَوْ أَرَاكَهُمْ كَثِيرًا لَّفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِي الْأَمْرِ وَلَٰكِنَّ اللَّهَ سَلَّمَ ۗ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','در آن هنگام که خداوند تعداد آنها را در خواب به تو کم نشان داد؛ و اگر فراوان نشان می‌داد، مسلّماً سست می‌شدید؛ و (درباره شروع جنگ با آنها) کارتان به اختلاف می‌کشید؛ ولی خداوند (شما را از شرّ اینها) سالم نگه داشت؛ خداوند به آنچه درون سینه‌هاست، داناست.'];
ayat[1204]=[8,44,'وَإِذْ يُرِيكُمُوهُمْ إِذِ الْتَقَيْتُمْ فِي أَعْيُنِكُمْ قَلِيلًا وَيُقَلِّلُكُمْ فِي أَعْيُنِهِمْ لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولًا ۗ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ','و در آن هنگام (که در میدان نبرد،) با هم روبه‌رو شدید، آنها را به چشم شما کم نشان می‌داد؛ و شما را (نیز) به چشم آنها کم می‌نمود؛ تا خداوند، کاری را که می‌بایست انجام گیرد، صورت بخشد؛ (شما نترسید و اقدام به جنگ کنید، آنها هم وحشت نکنند و حاضر به جنگ شوند، و سرانجام شکست بخورند!) و همه کارها به خداوند باز می‌گردد.'];
ayat[1205]=[8,45,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمْ فِئَةً فَاثْبُتُوا وَاذْكُرُوا اللَّهَ كَثِيرًا لَّعَلَّكُمْ تُفْلِحُونَ','ای کسانی که ایمان آورده‌اید! هنگامی که (در میدان نبرد) با گروهی رو به رو می‌شوید، ثابت قدم باشید! و خدا را فراوان یاد کنید، تا رستگار شوید!'];
ayat[1206]=[8,46,'وَأَطِيعُوا اللَّهَ وَرَسُولَهُ وَلَا تَنَازَعُوا فَتَفْشَلُوا وَتَذْهَبَ رِيحُكُمْ ۖ وَاصْبِرُوا ۚ إِنَّ اللَّهَ مَعَ الصَّابِرِينَ','و (فرمان) خدا و پیامبرش را اطاعت نمایید! و نزاع (و کشمکش) نکنید، تا سست نشوید، و قدرت (و شوکت) شما از میان نرود! و صبر و استقامت کنید که خداوند با استقامت کنندگان است!'];
ayat[1207]=[8,47,'وَلَا تَكُونُوا كَالَّذِينَ خَرَجُوا مِن دِيَارِهِم بَطَرًا وَرِئَاءَ النَّاسِ وَيَصُدُّونَ عَن سَبِيلِ اللَّهِ ۚ وَاللَّهُ بِمَا يَعْمَلُونَ مُحِيطٌ','و مانند کسانی نباشید که از روی هوی پرستی و غرور و خودنمایی در برابر مردم، از سرزمین خود به (سوی میدان بدر) بیرون آمدند؛ و (مردم را) از راه خدا بازمی‌داشتند؛ (و سرانجام شکست خوردند) و خداوند به آنچه عمل می‌کنند، احاطه (و آگاهی) دارد!'];
ayat[1208]=[8,48,'وَإِذْ زَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَإِنِّي جَارٌ لَّكُمْ ۖ فَلَمَّا تَرَاءَتِ الْفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَيْهِ وَقَالَ إِنِّي بَرِيءٌ مِّنكُمْ إِنِّي أَرَىٰ مَا لَا تَرَوْنَ إِنِّي أَخَافُ اللَّهَ ۚ وَاللَّهُ شَدِيدُ الْعِقَابِ','و (به یاد آور) هنگامی را که شیطان، اعمال آنها [= مشرکان‌] را در نظرشان جلوه داد، و گفت: «امروز هیچ کس از مردم بر شما پیروز نمی‌گردد! و من، همسایه (و پناه‌دهنده) شما هستم!» امّا هنگامی که دو گروه (کافران، و مؤمنان مورد حمایت فرشتگان) در برابر یکدیگر قرار گرفتند، به عقب برگشت و گفت: «من از شما (دوستان و پیروانم) بیزارم! من چیزی می‌بینم که شما نمی‌بینید؛ من از خدا می‌ترسم، خداوند شدیدالعقاب است!»'];
ayat[1209]=[8,49,'إِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِم مَّرَضٌ غَرَّ هَٰؤُلَاءِ دِينُهُمْ ۗ وَمَن يَتَوَكَّلْ عَلَى اللَّهِ فَإِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','و هنگامی را که منافقان، و آنها که در دلهایشان بیماری است می‌گفتند: «این گروه (مسلمانان) را دینشان مغرور ساخته است.» (آنها نمی‌دانستند که) هر کس بر خدا توکّل کند، (پیروز می‌گردد؛) خداوند قدرتمند و حکیم است!'];
ayat[1210]=[8,50,'وَلَوْ تَرَىٰ إِذْ يَتَوَفَّى الَّذِينَ كَفَرُوا ۙ الْمَلَائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَارَهُمْ وَذُوقُوا عَذَابَ الْحَرِيقِ','و اگر ببینی کافران را هنگامی که فرشتگان (مرگ)، جانشان را می‌گیرند و بر صورت و پشت آنها می‌زنند و (می‌گویند:) بچشید عذاب سوزنده را (، به حال آنان تأسف خواهی خورد)!'];
ayat[1211]=[8,51,'ذَٰلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اللَّهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيدِ','این، در مقابل کارهایی است که از پیش فرستاده‌اید؛ و خداوند نسبت به بندگانش، هرگز ستم روا نمی‌دارد!'];
ayat[1212]=[8,52,'كَدَأْبِ آلِ فِرْعَوْنَ ۙ وَالَّذِينَ مِن قَبْلِهِمْ ۚ كَفَرُوا بِآيَاتِ اللَّهِ فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ ۗ إِنَّ اللَّهَ قَوِيٌّ شَدِيدُ الْعِقَابِ','(حال این گروه مشرکان،) همانند حال نزدیکان فرعون، و کسانی است که پیش از آنان بودند؛ آنها آیات خدا را انکار کردند؛ خداوند هم آنان را به گناهانشان کیفر داد؛ خداوند قویّ، و کیفرش شدید است‌'];
ayat[1213]=[8,53,'ذَٰلِكَ بِأَنَّ اللَّهَ لَمْ يَكُ مُغَيِّرًا نِّعْمَةً أَنْعَمَهَا عَلَىٰ قَوْمٍ حَتَّىٰ يُغَيِّرُوا مَا بِأَنفُسِهِمْ ۙ وَأَنَّ اللَّهَ سَمِيعٌ عَلِيمٌ','این، بخاطر آن است که خداوند، هیچ نعمتی را که به گروهی داده، تغییر نمی‌دهد؛ جز آنکه آنها خودشان را تغییر دهند؛ و خداوند، شنوا و داناست!'];
ayat[1214]=[8,54,'كَدَأْبِ آلِ فِرْعَوْنَ ۙ وَالَّذِينَ مِن قَبْلِهِمْ ۚ كَذَّبُوا بِآيَاتِ رَبِّهِمْ فَأَهْلَكْنَاهُم بِذُنُوبِهِمْ وَأَغْرَقْنَا آلَ فِرْعَوْنَ ۚ وَكُلٌّ كَانُوا ظَالِمِينَ','این، (درست) شبیه (حال) فرعونیان و کسانی است که پیش از آنها بودند؛ آیات پروردگارشان را تکذیب کردند؛ ما هم بخاطر گناهانشان، آنها را هلاک کردیم، و فرعونیان را غرق نمودیم؛ و همه آنها ظالم (و ستمگر) بودند!'];
ayat[1215]=[8,55,'إِنَّ شَرَّ الدَّوَابِّ عِندَ اللَّهِ الَّذِينَ كَفَرُوا فَهُمْ لَا يُؤْمِنُونَ','به یقین، بدترین جنبندگان نزد خدا، کسانی هستند که کافر شدند و ایمان نمی‌آورند.'];
ayat[1216]=[8,56,'الَّذِينَ عَاهَدتَّ مِنْهُمْ ثُمَّ يَنقُضُونَ عَهْدَهُمْ فِي كُلِّ مَرَّةٍ وَهُمْ لَا يَتَّقُونَ','همان کسانی که با آنها پیمان بستی؛ سپس هر بار عهد و پیمان خود را می‌شکنند؛ و (از پیمان شکنی و خیانت،) پرهیز ندارند.'];
ayat[1217]=[8,57,'فَإِمَّا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِم مَّنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ','اگر آنها را در (میدان) جنگ بیابی، آنچنان به آنها حمله کن که جمعیّتهایی که پشت سر آنها هستند، پراکنده شوند؛ شاید متذکّر گردند (و عبرت گیرند)!'];
ayat[1218]=[8,58,'وَإِمَّا تَخَافَنَّ مِن قَوْمٍ خِيَانَةً فَانبِذْ إِلَيْهِمْ عَلَىٰ سَوَاءٍ ۚ إِنَّ اللَّهَ لَا يُحِبُّ الْخَائِنِينَ','و هرگاه (با ظهور نشانه‌هایی،) از خیانت گروهی بیم داشته باشی (که عهد خود را شکسته، حمله غافلگیرانه کنند)، بطور عادلانه به آنها اعلام کن که پیمانشان لغو شده است؛ زیرا خداوند، خائنان را دوست نمی‌دارد!'];
ayat[1219]=[8,59,'وَلَا يَحْسَبَنَّ الَّذِينَ كَفَرُوا سَبَقُوا ۚ إِنَّهُمْ لَا يُعْجِزُونَ','آنها که راه کفر پیش گرفتند، گمان نکنند (با این اعمال،) پیش برده‌اند (و از قلمرو کیفر ما، بیرون رفته‌اند)! آنها هرگز ما را ناتوان نخواهند کرد!'];
ayat[1220]=[8,60,'وَأَعِدُّوا لَهُم مَّا اسْتَطَعْتُم مِّن قُوَّةٍ وَمِن رِّبَاطِ الْخَيْلِ تُرْهِبُونَ بِهِ عَدُوَّ اللَّهِ وَعَدُوَّكُمْ وَآخَرِينَ مِن دُونِهِمْ لَا تَعْلَمُونَهُمُ اللَّهُ يَعْلَمُهُمْ ۚ وَمَا تُنفِقُوا مِن شَيْءٍ فِي سَبِيلِ اللَّهِ يُوَفَّ إِلَيْكُمْ وَأَنتُمْ لَا تُظْلَمُونَ','هر نیرویی در قدرت دارید، برای مقابله با آنها [= دشمنان‌]، آماده سازید! و (همچنین) اسبهای ورزیده (برای میدان نبرد)، تا به وسیله آن، دشمن خدا و دشمن خویش را بترسانید! و (همچنین) گروه دیگری غیر از اینها را، که شما نمی‌شناسید و خدا آنها را می‌شناسد! و هر چه در راه خدا (و تقویت بنیه دفاعی اسلام) انفاق کنید، بطور کامل به شما بازگردانده می‌شود، و به شما ستم نخواهد شد!'];
ayat[1221]=[8,61,'۞ وَإِن جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللَّهِ ۚ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ','و اگر تمایل به صلح نشان دهند، تو نیز از در صلح درآی؛ و بر خدا توکّل کن، که او شنوا و داناست!'];
ayat[1222]=[8,62,'وَإِن يُرِيدُوا أَن يَخْدَعُوكَ فَإِنَّ حَسْبَكَ اللَّهُ ۚ هُوَ الَّذِي أَيَّدَكَ بِنَصْرِهِ وَبِالْمُؤْمِنِينَ','و اگر بخواهند تو را فریب دهند، خدا برای تو کافی است؛ او همان کسی است که تو را، با یاری خود و مؤمنان، تقویت کرد...'];
ayat[1223]=[8,63,'وَأَلَّفَ بَيْنَ قُلُوبِهِمْ ۚ لَوْ أَنفَقْتَ مَا فِي الْأَرْضِ جَمِيعًا مَّا أَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَلَٰكِنَّ اللَّهَ أَلَّفَ بَيْنَهُمْ ۚ إِنَّهُ عَزِيزٌ حَكِيمٌ','و دلهای آنها را با هم، الفت داد! اگر تمام آنچه را روی زمین است صرف می‌کردی که میان دلهای آنان الفت دهی، نمی‌توانستی! ولی خداوند در میان آنها الفت ایجاد کرد! او توانا و حکیم است!'];
ayat[1224]=[8,64,'يَا أَيُّهَا النَّبِيُّ حَسْبُكَ اللَّهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ','ای پیامبر! خداوند و مؤمنانی که از تو پیروی می‌کنند، برای حمایت تو کافی است (؛ فقط بر آنها تکیه کن)!'];
ayat[1225]=[8,65,'يَا أَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِينَ عَلَى الْقِتَالِ ۚ إِن يَكُن مِّنكُمْ عِشْرُونَ صَابِرُونَ يَغْلِبُوا مِائَتَيْنِ ۚ وَإِن يَكُن مِّنكُم مِّائَةٌ يَغْلِبُوا أَلْفًا مِّنَ الَّذِينَ كَفَرُوا بِأَنَّهُمْ قَوْمٌ لَّا يَفْقَهُونَ','ای پیامبر! مؤمنان را به جنگ (با دشمن) تشویق کن! هرگاه بیست نفر با استقامت از شما باشند، بر دویست نفر غلبه می‌کنند؛ و اگر صد نفر باشند، بر هزار نفر از کسانی که کافر شدند، پیروز می‌گردند؛ چرا که آنها گروهی هستند که نمی‌فهمند!'];
ayat[1226]=[8,66,'الْآنَ خَفَّفَ اللَّهُ عَنكُمْ وَعَلِمَ أَنَّ فِيكُمْ ضَعْفًا ۚ فَإِن يَكُن مِّنكُم مِّائَةٌ صَابِرَةٌ يَغْلِبُوا مِائَتَيْنِ ۚ وَإِن يَكُن مِّنكُمْ أَلْفٌ يَغْلِبُوا أَلْفَيْنِ بِإِذْنِ اللَّهِ ۗ وَاللَّهُ مَعَ الصَّابِرِينَ','هم اکنون خداوند به شما تخفیف داد، و دانست که در شما ضعفی است؛ بنابراین، هرگاه یکصد نفر با استقامت از شما باشند، بر دویست نفر پیروز می‌شوند؛ و اگر یکهزار نفر باشند، بر دو هزار نفر به فرمان خدا غلبه خواهند کرد! و خدا با صابران است!'];
ayat[1227]=[8,67,'مَا كَانَ لِنَبِيٍّ أَن يَكُونَ لَهُ أَسْرَىٰ حَتَّىٰ يُثْخِنَ فِي الْأَرْضِ ۚ تُرِيدُونَ عَرَضَ الدُّنْيَا وَاللَّهُ يُرِيدُ الْآخِرَةَ ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ','هیچ پیامبری حق ندارد اسیرانی (از دشمن) بگیرد تا کاملاً بر آنها پیروز گردد (؛ و جای پای خود را در زمین محکم کند)! شما متاع ناپایدار دنیا را می‌خواهید؛ (و مایلید اسیران بیشتری بگیرید، و در برابر گرفتن فدیه آزاد کنید؛ ولی خداوند، سرای دیگر را (برای شما) می‌خواهد؛ و خداوند قادر و حکیم است!'];
ayat[1228]=[8,68,'لَّوْلَا كِتَابٌ مِّنَ اللَّهِ سَبَقَ لَمَسَّكُمْ فِيمَا أَخَذْتُمْ عَذَابٌ عَظِيمٌ','اگر فرمان سابق خدا نبود (که بدون ابلاغ، هیچ امّتی را کیفر ندهد)، بخاطر چیزی [= اسیرانی‌] که گرفتید، مجازات بزرگی به شما می‌رسید.'];
ayat[1229]=[8,69,'فَكُلُوا مِمَّا غَنِمْتُمْ حَلَالًا طَيِّبًا ۚ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','از آنچه به غنیمت گرفته‌اید، حلال و پاکیزه بخورید؛ و از خدا بپرهیزید؛ خداوند آمرزنده و مهربان است!'];
ayat[1230]=[8,70,'يَا أَيُّهَا النَّبِيُّ قُل لِّمَن فِي أَيْدِيكُم مِّنَ الْأَسْرَىٰ إِن يَعْلَمِ اللَّهُ فِي قُلُوبِكُمْ خَيْرًا يُؤْتِكُمْ خَيْرًا مِّمَّا أُخِذَ مِنكُمْ وَيَغْفِرْ لَكُمْ ۗ وَاللَّهُ غَفُورٌ رَّحِيمٌ','ای پیامبر! به اسیرانی که در دست شما هستند بگو: «اگر خداوند، خیری در دلهای شما بداند، (و نیّات پاکی داشته باشید،) بهتر از آنچه از شما گرفته شده به شما می‌دهد؛ و شما را می‌بخشد؛ و خداوند آمرزنده و مهربان است!»'];
ayat[1231]=[8,71,'وَإِن يُرِيدُوا خِيَانَتَكَ فَقَدْ خَانُوا اللَّهَ مِن قَبْلُ فَأَمْكَنَ مِنْهُمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','امّا اگر بخواهند با تو خیانت کنند، (تازگی ندارد) آنها پیش از این (نیز) به خدا خیانت کردند؛ و خداوند (شما را) بر آنها پیروز کرد؛ خداوند دانا و حکیم است!'];
ayat[1232]=[8,72,'إِنَّ الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا بِأَمْوَالِهِمْ وَأَنفُسِهِمْ فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوا وَّنَصَرُوا أُولَٰئِكَ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ وَالَّذِينَ آمَنُوا وَلَمْ يُهَاجِرُوا مَا لَكُم مِّن وَلَايَتِهِم مِّن شَيْءٍ حَتَّىٰ يُهَاجِرُوا ۚ وَإِنِ اسْتَنصَرُوكُمْ فِي الدِّينِ فَعَلَيْكُمُ النَّصْرُ إِلَّا عَلَىٰ قَوْمٍ بَيْنَكُمْ وَبَيْنَهُم مِّيثَاقٌ ۗ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','کسانی که ایمان آوردند و هجرت نمودند و با اموال و جانهای خود در راه خدا جهاد کردند، و آنها که پناه دادند و یاری نمودند، آنها یاران یکدیگرند؛ و آنها که ایمان آوردند و مهاجرت نکردند، هیچ گونه ولایت [= دوستی و تعّهدی‌] در برابر آنها ندارید تا هجرت کنند! و (تنها) اگر در (حفظ) دین (خود) از شما یاری طلبند، بر شماست که آنها را یاری کنید، جز بر ضدّ گروهی که میان شما و آنها، پیمان (ترک مخاصمه) است؛ و خداوند به آنچه عمل می‌کنید، بیناست!'];
ayat[1233]=[8,73,'وَالَّذِينَ كَفَرُوا بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ إِلَّا تَفْعَلُوهُ تَكُن فِتْنَةٌ فِي الْأَرْضِ وَفَسَادٌ كَبِيرٌ','کسانی که کافر شدند، اولیاء (و یاوران و مدافعان) یکدیگرند؛ اگر (این دستور را) انجام ندهید، فتنه و فساد عظیمی در زمین روی می‌دهد.'];
ayat[1234]=[8,74,'وَالَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ وَالَّذِينَ آوَوا وَّنَصَرُوا أُولَٰئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا ۚ لَّهُم مَّغْفِرَةٌ وَرِزْقٌ كَرِيمٌ','و آنها که ایمان آوردند و هجرت نمودند و در راه خدا جهاد کردند، و آنها که پناه دادند و یاری نمودند، آنان مؤمنان حقیقی‌اند؛ برای آنها، آمرزش (و رحمت خدا) و روزی شایسته‌ای است.'];
ayat[1235]=[8,75,'وَالَّذِينَ آمَنُوا مِن بَعْدُ وَهَاجَرُوا وَجَاهَدُوا مَعَكُمْ فَأُولَٰئِكَ مِنكُمْ ۚ وَأُولُو الْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٍ فِي كِتَابِ اللَّهِ ۗ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ','و کسانی که بعداً ایمان آوردند و هجرت کردند و با شما جهاد نمودند، از شما هستند؛ و خویشاوندان نسبت به یکدیگر، در احکامی که خدا مقرّر داشته، (از دیگران) سزاوارترند؛ خداوند به همه چیز داناست.'];
ayat[1236]=[9,1,'بَرَاءَةٌ مِّنَ اللَّهِ وَرَسُولِهِ إِلَى الَّذِينَ عَاهَدتُّم مِّنَ الْمُشْرِكِينَ','(این، اعلام) بیزاری از سوی خدا و پیامبر او، به کسانی از مشرکان است که با آنها عهد بسته‌اید!'];
ayat[1237]=[9,2,'فَسِيحُوا فِي الْأَرْضِ أَرْبَعَةَ أَشْهُرٍ وَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ ۙ وَأَنَّ اللَّهَ مُخْزِي الْكَافِرِينَ','با این حال، چهار ماه (مهلت دارید که آزادانه) در زمین سیر کنید (و هر جا می‌خواهید بروید، و بیندیشید)! و بدانید شما نمی‌توانید خدا را ناتوان سازید، (و از قدرت او فرار کنید! و بدانید) خداوند خوارکننده کافران است!'];
ayat[1238]=[9,3,'وَأَذَانٌ مِّنَ اللَّهِ وَرَسُولِهِ إِلَى النَّاسِ يَوْمَ الْحَجِّ الْأَكْبَرِ أَنَّ اللَّهَ بَرِيءٌ مِّنَ الْمُشْرِكِينَ ۙ وَرَسُولُهُ ۚ فَإِن تُبْتُمْ فَهُوَ خَيْرٌ لَّكُمْ ۖ وَإِن تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللَّهِ ۗ وَبَشِّرِ الَّذِينَ كَفَرُوا بِعَذَابٍ أَلِيمٍ','و این، اعلامی است از ناحیه خدا و پیامبرش به (عموم) مردم در روز حج اکبر [= روز عید قربان‌] که: خداوند و پیامبرش از مشرکان بیزارند! با این حال، اگر توبه کنید، برای شما بهتر است! و اگر سرپیچی نمایید، بدانید شما نمی‌توانید خدا را ناتوان سازید (و از قلمرو قدرتش خارج شوید)! و کافران را به مجازات دردناک بشارت ده!'];
ayat[1239]=[9,4,'إِلَّا الَّذِينَ عَاهَدتُّم مِّنَ الْمُشْرِكِينَ ثُمَّ لَمْ يَنقُصُوكُمْ شَيْئًا وَلَمْ يُظَاهِرُوا عَلَيْكُمْ أَحَدًا فَأَتِمُّوا إِلَيْهِمْ عَهْدَهُمْ إِلَىٰ مُدَّتِهِمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ','مگر کسانی از مشرکان که با آنها عهد بستید، و چیزی از آن را در حقّ شما فروگذار نکردند، و احدی را بر ضدّ شما تقویت ننمودند؛ پیمان آنها را تا پایان مدّتشان محترم بشمرید؛ زیرا خداوند پرهیزگاران را دوست دارد!'];
ayat[1240]=[9,5,'فَإِذَا انسَلَخَ الْأَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِينَ حَيْثُ وَجَدتُّمُوهُمْ وَخُذُوهُمْ وَاحْصُرُوهُمْ وَاقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍ ۚ فَإِن تَابُوا وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ فَخَلُّوا سَبِيلَهُمْ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','(امّا) وقتی ماه‌های حرام پایان گرفت، مشرکان را هر جا یافتید به قتل برسانید؛ و آنها را اسیر سازید؛ و محاصره کنید؛ و در هر کمینگاه، بر سر راه آنها بنشینید! هرگاه توبه کنند، و نماز را برپا دارند، و زکات را بپردازند، آنها را رها سازید؛ زیرا خداوند آمرزنده و مهربان است!'];
ayat[1241]=[9,6,'وَإِنْ أَحَدٌ مِّنَ الْمُشْرِكِينَ اسْتَجَارَكَ فَأَجِرْهُ حَتَّىٰ يَسْمَعَ كَلَامَ اللَّهِ ثُمَّ أَبْلِغْهُ مَأْمَنَهُ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَعْلَمُونَ','و اگر یکی از مشرکان از تو پناهندگی بخواهد، به او پناه ده تا سخن خدا را بشنود (و در آن بیندیشد)! سپس او را به محل امنش برسان، چرا که آنها گروهی ناآگاهند!'];
ayat[1242]=[9,7,'كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِندَ اللَّهِ وَعِندَ رَسُولِهِ إِلَّا الَّذِينَ عَاهَدتُّمْ عِندَ الْمَسْجِدِ الْحَرَامِ ۖ فَمَا اسْتَقَامُوا لَكُمْ فَاسْتَقِيمُوا لَهُمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُتَّقِينَ','چگونه برای مشرکان پیمانی نزد خدا و رسول او خواهد بود (در حالی که آنها همواره آماده شکستن پیمانشان هستند)؟! مگر کسانی که نزد مسجد الحرام با آنان پیمان بستید؛ (و پیمان خود را محترم شمردند؛) تا زمانی که در برابر شما وفادار باشند، شما نیز وفاداری کنید، که خداوند پرهیزگاران را دوست دارد!'];
ayat[1243]=[9,8,'كَيْفَ وَإِن يَظْهَرُوا عَلَيْكُمْ لَا يَرْقُبُوا فِيكُمْ إِلًّا وَلَا ذِمَّةً ۚ يُرْضُونَكُم بِأَفْوَاهِهِمْ وَتَأْبَىٰ قُلُوبُهُمْ وَأَكْثَرُهُمْ فَاسِقُونَ','چگونه (پیمان مشرکان ارزش دارد)، در حالی که اگر بر شما غالب شوند، نه ملاحظه خویشاوندی با شما را می‌کنند، و نه پیمان را؟! شما را با زبان خود خشنود می‌کنند، ولی دلهایشان ابا دارد؛ و بیشتر آنها فرمانبردار نیستند!'];
ayat[1244]=[9,9,'اشْتَرَوْا بِآيَاتِ اللَّهِ ثَمَنًا قَلِيلًا فَصَدُّوا عَن سَبِيلِهِ ۚ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ','آنها آیات خدا را به بهای کمی فروختند؛ و (مردم را) از راه او باز داشتند؛ آنها اعمال بدی انجام می‌دادند!'];
ayat[1245]=[9,10,'لَا يَرْقُبُونَ فِي مُؤْمِنٍ إِلًّا وَلَا ذِمَّةً ۚ وَأُولَٰئِكَ هُمُ الْمُعْتَدُونَ','(نه تنها درباره شما،) درباره هیچ فرد باایمانی رعایت خویشاوندی و پیمان را نمی‌کنند؛ و آنها همان تجاوز کارانند!'];
ayat[1246]=[9,11,'فَإِن تَابُوا وَأَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ فَإِخْوَانُكُمْ فِي الدِّينِ ۗ وَنُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ','(ولی) اگر توبه کنند، نماز را برپا دارند، و زکات را بپردازند، برادر دینی شما هستند؛ و ما آیات خود را برای گروهی که می‌دانند (و می‌اندیشند)، شرح می‌دهیم!'];
ayat[1247]=[9,12,'وَإِن نَّكَثُوا أَيْمَانَهُم مِّن بَعْدِ عَهْدِهِمْ وَطَعَنُوا فِي دِينِكُمْ فَقَاتِلُوا أَئِمَّةَ الْكُفْرِ ۙ إِنَّهُمْ لَا أَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنتَهُونَ','و اگر پیمانهای خود را پس از عهد خویش بشکنند، و آیین شما را مورد طعن قرار دهند، با پیشوایان کفر پیکار کنید؛ چرا که آنها پیمانی ندارند؛ شاید (با شدّت عمل) دست بردارند!'];
ayat[1248]=[9,13,'أَلَا تُقَاتِلُونَ قَوْمًا نَّكَثُوا أَيْمَانَهُمْ وَهَمُّوا بِإِخْرَاجِ الرَّسُولِ وَهُم بَدَءُوكُمْ أَوَّلَ مَرَّةٍ ۚ أَتَخْشَوْنَهُمْ ۚ فَاللَّهُ أَحَقُّ أَن تَخْشَوْهُ إِن كُنتُم مُّؤْمِنِينَ','آیا با گروهی که پیمانهای خود را شکستند، و تصمیم به اخراج پیامبر گرفتند، پیکار نمی‌کنید؟! در حالی که آنها نخستین بار (پیکار با شما را) آغاز کردند؛ آیا از آنها می‌ترسید؟! با اینکه خداوند سزاوارتر است که از او بترسید، اگر مؤمن هستید!'];
ayat[1249]=[9,14,'قَاتِلُوهُمْ يُعَذِّبْهُمُ اللَّهُ بِأَيْدِيكُمْ وَيُخْزِهِمْ وَيَنصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُورَ قَوْمٍ مُّؤْمِنِينَ','با آنها پیکار کنید، که خداوند آنان را به دست شما مجازات می‌کند؛ و آنان را رسوا می‌سازد؛ و سینه گروهی از مؤمنان را شفا می‌بخشد (؛ و بر قلب آنها مرهم می‌نهد)'];
ayat[1250]=[9,15,'وَيُذْهِبْ غَيْظَ قُلُوبِهِمْ ۗ وَيَتُوبُ اللَّهُ عَلَىٰ مَن يَشَاءُ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','و خشم دلهای آنان را از میان می‌برد! و خدا توبه هر کس را بخواهد (و شایسته بداند)، می‌پذیرد؛ و خداوند دانا و حکیم است.'];
ayat[1251]=[9,16,'أَمْ حَسِبْتُمْ أَن تُتْرَكُوا وَلَمَّا يَعْلَمِ اللَّهُ الَّذِينَ جَاهَدُوا مِنكُمْ وَلَمْ يَتَّخِذُوا مِن دُونِ اللَّهِ وَلَا رَسُولِهِ وَلَا الْمُؤْمِنِينَ وَلِيجَةً ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ','آیا گمان کردید که (به حال خود) رها می‌شوید در حالی که هنوز کسانی که از شما جهاد کردند، و غیر از خدا و رسولش و مؤمنان را محرم اسرار خویش انتخاب ننمودند، (از دیگران) مشخصّ نشده‌اند؟! (باید آزمون شوید؛ و صفوف از هم جدا گردد؛) و خداوند به آنچه عمل می‌کنید، آگاه است!'];
ayat[1252]=[9,17,'مَا كَانَ لِلْمُشْرِكِينَ أَن يَعْمُرُوا مَسَاجِدَ اللَّهِ شَاهِدِينَ عَلَىٰ أَنفُسِهِم بِالْكُفْرِ ۚ أُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ وَفِي النَّارِ هُمْ خَالِدُونَ','مشرکان حق ندارند مساجد خدا را آباد کنند در حالی که به کفر خویش گواهی می‌دهند! آنها اعمالشان نابود (و بی‌ارزش) شده؛ و در آتش (دوزخ)، جاودانه خواهند ماند!'];
ayat[1253]=[9,18,'إِنَّمَا يَعْمُرُ مَسَاجِدَ اللَّهِ مَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَأَقَامَ الصَّلَاةَ وَآتَى الزَّكَاةَ وَلَمْ يَخْشَ إِلَّا اللَّهَ ۖ فَعَسَىٰ أُولَٰئِكَ أَن يَكُونُوا مِنَ الْمُهْتَدِينَ','مساجد خدا را تنها کسی آباد می‌کند که ایمان به خدا و روز قیامت آورده، و نماز را برپا دارد، و زکات را بپردازد، و جز از خدا نترسد؛ امید است چنین گروهی از هدایت‌یافتگان باشند.'];
ayat[1254]=[9,19,'۞ أَجَعَلْتُمْ سِقَايَةَ الْحَاجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ آمَنَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَجَاهَدَ فِي سَبِيلِ اللَّهِ ۚ لَا يَسْتَوُونَ عِندَ اللَّهِ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','آیا سیراب کردن حجاج، و آباد ساختن مسجد الحرام را، همانند (عمل) کسی قرار دادید که به خدا و روز قیامت ایمان آورده، و در راه او جهاد کرده است؟! (این دو،) نزد خدا مساوی نیستند! و خداوند گروه ظالمان را هدایت نمی‌کند!'];
ayat[1255]=[9,20,'الَّذِينَ آمَنُوا وَهَاجَرُوا وَجَاهَدُوا فِي سَبِيلِ اللَّهِ بِأَمْوَالِهِمْ وَأَنفُسِهِمْ أَعْظَمُ دَرَجَةً عِندَ اللَّهِ ۚ وَأُولَٰئِكَ هُمُ الْفَائِزُونَ','آنها که ایمان آوردند، و هجرت کردند، و با اموال و جانهایشان در راه خدا جهاد نمودند، مقامشان نزد خدا برتر است؛ و آنها پیروز و رستگارند!'];
ayat[1256]=[9,21,'يُبَشِّرُهُمْ رَبُّهُم بِرَحْمَةٍ مِّنْهُ وَرِضْوَانٍ وَجَنَّاتٍ لَّهُمْ فِيهَا نَعِيمٌ مُّقِيمٌ','پروردگارشان آنها را به رحمتی از ناحیه خود، و رضایت (خویش)، و باغهایی از بهشت بشارت می‌دهد که در آن، نعمتهای جاودانه دارند؛'];
ayat[1257]=[9,22,'خَالِدِينَ فِيهَا أَبَدًا ۚ إِنَّ اللَّهَ عِندَهُ أَجْرٌ عَظِيمٌ','همواره و تا ابد در این باغها (و در میان این نعمتها) خواهند بود؛ زیرا پاداش عظیم نزد خداوند است!'];
ayat[1258]=[9,23,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا آبَاءَكُمْ وَإِخْوَانَكُمْ أَوْلِيَاءَ إِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْإِيمَانِ ۚ وَمَن يَتَوَلَّهُم مِّنكُمْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ','ای کسانی که ایمان آورده‌اید! هرگاه پدران و برادران شما، کفر را بر ایمان ترجیح دهند، آنها را ولیّ (و یار و یاور و تکیه‌گاه) خود قرار ندهید! و کسانی از شما که آنان را ولیّ خود قرار دهند، ستمگرند!'];
ayat[1259]=[9,24,'قُلْ إِن كَانَ آبَاؤُكُمْ وَأَبْنَاؤُكُمْ وَإِخْوَانُكُمْ وَأَزْوَاجُكُمْ وَعَشِيرَتُكُمْ وَأَمْوَالٌ اقْتَرَفْتُمُوهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسَاكِنُ تَرْضَوْنَهَا أَحَبَّ إِلَيْكُم مِّنَ اللَّهِ وَرَسُولِهِ وَجِهَادٍ فِي سَبِيلِهِ فَتَرَبَّصُوا حَتَّىٰ يَأْتِيَ اللَّهُ بِأَمْرِهِ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ','بگو: «اگر پدران و فرزندان و برادران و همسران و طایفه شما، و اموالی که به دست آورده‌اید، و تجارتی که از کساد شدنش می‌ترسید، و خانه هائی که به آن علاقه دارید، در نظرتان از خداوند و پیامبرش و جهاد در راهش محبوبتر است، در انتظار باشید که خداوند عذابش را بر شما نازل کند؛ و خداوند جمعیّت نافرمانبردار را هدایت نمی‌کند!'];
ayat[1260]=[9,25,'لَقَدْ نَصَرَكُمُ اللَّهُ فِي مَوَاطِنَ كَثِيرَةٍ ۙ وَيَوْمَ حُنَيْنٍ ۙ إِذْ أَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنكُمْ شَيْئًا وَضَاقَتْ عَلَيْكُمُ الْأَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُم مُّدْبِرِينَ','خداوند شما را در جاهای زیادی یاری کرد (و بر دشمن پیروز شدید)؛ و در روز حنین (نیز یاری نمود)؛ در آن هنگام که فزونی جمعیّتتان شما را مغرور ساخت، ولی (این فزونی جمعیّت) هیچ به دردتان نخورد و زمین با همه وسعتش بر شما تنگ شده؛ سپس پشت (به دشمن) کرده، فرار نمودید!'];
ayat[1261]=[9,26,'ثُمَّ أَنزَلَ اللَّهُ سَكِينَتَهُ عَلَىٰ رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَنزَلَ جُنُودًا لَّمْ تَرَوْهَا وَعَذَّبَ الَّذِينَ كَفَرُوا ۚ وَذَٰلِكَ جَزَاءُ الْكَافِرِينَ','سپس خداوند «سکینه» خود را بر پیامبرش و بر مؤمنان نازل کرد؛ و لشکرهایی فرستاد که شما نمی‌دیدید؛ و کافران را مجازات کرد؛ و این است جزای کافران!'];
ayat[1262]=[9,27,'ثُمَّ يَتُوبُ اللَّهُ مِن بَعْدِ ذَٰلِكَ عَلَىٰ مَن يَشَاءُ ۗ وَاللَّهُ غَفُورٌ رَّحِيمٌ','سپس خداوند -بعد از آن- توبه هر کس را بخواهد (و شایسته بداند)، می‌پذیرد؛ و خداوند آمرزنده و مهربان است.'];
ayat[1263]=[9,28,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هَٰذَا ۚ وَإِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيكُمُ اللَّهُ مِن فَضْلِهِ إِن شَاءَ ۚ إِنَّ اللَّهَ عَلِيمٌ حَكِيمٌ','ای کسانی که ایمان آورده‌اید! مشرکان ناپاکند؛ پس نباید بعد از امسال، نزدیک مسجد الحرام شوند! و اگر از فقر می‌ترسید، خداوند هرگاه بخواهد، شما را به کرمش بی‌نیاز می‌سازد؛ (و از راه دیگر جبران می‌کند؛) خداوند دانا و حکیم است.'];
ayat[1264]=[9,29,'قَاتِلُوا الَّذِينَ لَا يُؤْمِنُونَ بِاللَّهِ وَلَا بِالْيَوْمِ الْآخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ اللَّهُ وَرَسُولُهُ وَلَا يَدِينُونَ دِينَ الْحَقِّ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ حَتَّىٰ يُعْطُوا الْجِزْيَةَ عَن يَدٍ وَهُمْ صَاغِرُونَ','با کسانی از اهل کتاب که نه به خدا، و نه به روز جزا ایمان دارند، و نه آنچه را خدا و رسولش تحریم کرده حرام می‌شمرند، و نه آیین حق را می‌پذیرند، پیکار کنید تا زمانی که با خضوع و تسلیم، جزیه را به دست خود بپردازند!'];
ayat[1265]=[9,30,'وَقَالَتِ الْيَهُودُ عُزَيْرٌ ابْنُ اللَّهِ وَقَالَتِ النَّصَارَى الْمَسِيحُ ابْنُ اللَّهِ ۖ ذَٰلِكَ قَوْلُهُم بِأَفْوَاهِهِمْ ۖ يُضَاهِئُونَ قَوْلَ الَّذِينَ كَفَرُوا مِن قَبْلُ ۚ قَاتَلَهُمُ اللَّهُ ۚ أَنَّىٰ يُؤْفَكُونَ','یهود گفتند: «عزیر پسر خداست!» و نصاری کفتند: «مسیح پسر خداست!» این سخنی است که با زبان خود می‌گویند، که همانند گفتار کافران پیشین است؛ خدا آنان را بکشد، چگونه از حق انحراف می‌یابند؟!'];
ayat[1266]=[9,31,'اتَّخَذُوا أَحْبَارَهُمْ وَرُهْبَانَهُمْ أَرْبَابًا مِّن دُونِ اللَّهِ وَالْمَسِيحَ ابْنَ مَرْيَمَ وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا إِلَٰهًا وَاحِدًا ۖ لَّا إِلَٰهَ إِلَّا هُوَ ۚ سُبْحَانَهُ عَمَّا يُشْرِكُونَ','(آنها) دانشمندان و راهبان خویش را معبودهایی در برابر خدا قرار دادند، و (همچنین) مسیح فرزند مریم را؛ در حالی که دستور نداشتند جز خداوند یکتائی را که معبودی جز او نیست، بپرستند، او پاک و منزه است از آنچه همتایش قرار می‌دهند!'];
ayat[1267]=[9,32,'يُرِيدُونَ أَن يُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَيَأْبَى اللَّهُ إِلَّا أَن يُتِمَّ نُورَهُ وَلَوْ كَرِهَ الْكَافِرُونَ','آنها می‌خواهند نور خدا را با دهان خود خاموش کنند؛ ولی خدا جز این نمی‌خواهد که نور خود را کامل کند، هر چند کافران ناخشنود باشند!'];
ayat[1268]=[9,33,'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ','او کسی است که رسولش را با هدایت و آیین حق فرستاد، تا آن را بر همه آیین‌ها غالب گرداند، هر چند مشرکان کراهت داشته باشند!'];
ayat[1269]=[9,34,'۞ يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ كَثِيرًا مِّنَ الْأَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُونَ أَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّونَ عَن سَبِيلِ اللَّهِ ۗ وَالَّذِينَ يَكْنِزُونَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنفِقُونَهَا فِي سَبِيلِ اللَّهِ فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ','ای کسانی که ایمان آورده‌اید! بسیاری از دانشمندان (اهل کتاب) و راهبان، اموال مردم را بباطل می‌خورند، و (آنان را) از راه خدا بازمی‌دارند! و کسانی که طلا و نقره را گنجینه (و ذخیره و پنهان) می‌سازند، و در راه خدا انفاق نمی‌کنند، به مجازات دردناکی بشارت ده!'];
ayat[1270]=[9,35,'يَوْمَ يُحْمَىٰ عَلَيْهَا فِي نَارِ جَهَنَّمَ فَتُكْوَىٰ بِهَا جِبَاهُهُمْ وَجُنُوبُهُمْ وَظُهُورُهُمْ ۖ هَٰذَا مَا كَنَزْتُمْ لِأَنفُسِكُمْ فَذُوقُوا مَا كُنتُمْ تَكْنِزُونَ','در آن روز که آن را در آتش جهنم، گرم و سوزان کرده، و با آن صورتها و پهلوها و پشتهایشان را داغ می‌کنند؛ (و به آنها می‌گویند): این همان چیزی است که برای خود اندوختید (و گنجینه ساختید)! پس بچشید چیزی را که برای خود می‌اندوختید!'];
ayat[1271]=[9,36,'إِنَّ عِدَّةَ الشُّهُورِ عِندَ اللَّهِ اثْنَا عَشَرَ شَهْرًا فِي كِتَابِ اللَّهِ يَوْمَ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ مِنْهَا أَرْبَعَةٌ حُرُمٌ ۚ ذَٰلِكَ الدِّينُ الْقَيِّمُ ۚ فَلَا تَظْلِمُوا فِيهِنَّ أَنفُسَكُمْ ۚ وَقَاتِلُوا الْمُشْرِكِينَ كَافَّةً كَمَا يُقَاتِلُونَكُمْ كَافَّةً ۚ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ','تعداد ماه‌ها نزد خداوند در کتاب الهی، از آن روز که آسمانها و زمین را آفریده، دوازده ماه است؛ که چهار ماه از آن، ماه حرام است؛ (و جنگ در آن ممنوع می‌باشد.) این، آیین ثابت و پابرجا (ی الهی) است! بنابر این، در این ماه‌ها به خود ستم نکنید (و از هرگونه خونریزی بپرهیزید)! و (به هنگام نبرد) با مشرکان، دسته جمعی پیکار کنید، همان گونه که آنها دسته جمعی با شما پیکار می‌کنند؛ و بدانید خداوند با پرهیزگاران است!'];
ayat[1272]=[9,37,'إِنَّمَا النَّسِيءُ زِيَادَةٌ فِي الْكُفْرِ ۖ يُضَلُّ بِهِ الَّذِينَ كَفَرُوا يُحِلُّونَهُ عَامًا وَيُحَرِّمُونَهُ عَامًا لِّيُوَاطِئُوا عِدَّةَ مَا حَرَّمَ اللَّهُ فَيُحِلُّوا مَا حَرَّمَ اللَّهُ ۚ زُيِّنَ لَهُمْ سُوءُ أَعْمَالِهِمْ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ','نسی‌ء [= جا به جا کردن و تأخیر ماه‌های حرام‌]، افزایشی در کفر (مشرکان) است؛ که با آن، کافران گمراه می‌شوند؛ یک سال، آن را حلال، و سال دیگر آن را حرام می کنند، تا به مقدار ماه‌هایی که خداوند تحریم کرده بشود (و عدد چهار ماه، به پندارشان تکمیل گردد)؛ و به این ترتیب، آنچه را خدا حرام کرده، حلال بشمرند. اعمال زشتشان در نظرشان زیبا جلوه داده شده؛ و خداوند جمعیّت کافران را هدایت نمی‌کند!'];
ayat[1273]=[9,38,'يَا أَيُّهَا الَّذِينَ آمَنُوا مَا لَكُمْ إِذَا قِيلَ لَكُمُ انفِرُوا فِي سَبِيلِ اللَّهِ اثَّاقَلْتُمْ إِلَى الْأَرْضِ ۚ أَرَضِيتُم بِالْحَيَاةِ الدُّنْيَا مِنَ الْآخِرَةِ ۚ فَمَا مَتَاعُ الْحَيَاةِ الدُّنْيَا فِي الْآخِرَةِ إِلَّا قَلِيلٌ','ای کسانی که ایمان آورده‌اید! چرا هنگامی که به شما گفته می‌شود: «به سوی جهاد در راه خدا حرکت کنید!» بر زمین سنگینی می‌کنید (و سستی به خرج می‌دهید)؟! آیا به زندگی دنیا به جای آخرت راضی شده‌اید؟! با اینکه متاع زندگی دنیا، در برابر آخرت، جز اندکی نیست!'];
ayat[1274]=[9,39,'إِلَّا تَنفِرُوا يُعَذِّبْكُمْ عَذَابًا أَلِيمًا وَيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّوهُ شَيْئًا ۗ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','اگر (به سوی میدان جهاد) حرکت نکنید، شما را مجازات دردناکی می‌کند، و گروه دیگری غیر از شما را به جای شما قرارمی‌دهد؛ و هیچ زیانی به او نمی‌رسانید؛ و خداوند بر هر چیزی تواناست!'];
ayat[1275]=[9,40,'إِلَّا تَنصُرُوهُ فَقَدْ نَصَرَهُ اللَّهُ إِذْ أَخْرَجَهُ الَّذِينَ كَفَرُوا ثَانِيَ اثْنَيْنِ إِذْ هُمَا فِي الْغَارِ إِذْ يَقُولُ لِصَاحِبِهِ لَا تَحْزَنْ إِنَّ اللَّهَ مَعَنَا ۖ فَأَنزَلَ اللَّهُ سَكِينَتَهُ عَلَيْهِ وَأَيَّدَهُ بِجُنُودٍ لَّمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِينَ كَفَرُوا السُّفْلَىٰ ۗ وَكَلِمَةُ اللَّهِ هِيَ الْعُلْيَا ۗ وَاللَّهُ عَزِيزٌ حَكِيمٌ','اگر او را یاری نکنید، خداوند او را یاری کرد؛ (و در مشکلترین ساعات، او را تنها نگذاشت؛) آن هنگام که کافران او را (از مکّه) بیرون کردند، در حالی که دوّمین نفر بود (و یک نفر بیشتر همراه نداشت)؛ در آن هنگام که آن دو در غار بودند، و او به همراه خود می‌گفت: «غم مخور، خدا با ماست!» در این موقع، خداوند سکینه (و آرامش) خود را بر او فرستاد؛ و با لشکرهایی که مشاهده نمی‌کردید، او را تقویت نمود؛ و گفتار (و هدف) کافران را پایین قرار داد، (و آنها را با شکست مواجه ساخت؛) و سخن خدا (و آیین او)، بالا (و پیروز) است؛ و خداوند عزیز و حکیم است!'];
ayat[1276]=[9,41,'انفِرُوا خِفَافًا وَثِقَالًا وَجَاهِدُوا بِأَمْوَالِكُمْ وَأَنفُسِكُمْ فِي سَبِيلِ اللَّهِ ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ','(همگی به سوی میدان جهاد) حرکت کنید؛ سبکبار باشید یا سنگین بار! و با اموال و جانهای خود، در راه خدا جهاد نمایید؛ این برای شما بهتر است اگر بدانید!'];
ayat[1277]=[9,42,'لَوْ كَانَ عَرَضًا قَرِيبًا وَسَفَرًا قَاصِدًا لَّاتَّبَعُوكَ وَلَٰكِن بَعُدَتْ عَلَيْهِمُ الشُّقَّةُ ۚ وَسَيَحْلِفُونَ بِاللَّهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْ يُهْلِكُونَ أَنفُسَهُمْ وَاللَّهُ يَعْلَمُ إِنَّهُمْ لَكَاذِبُونَ','(امّا گروهی از آنها، چنانند که) اگر غنایمی نزدیک (و در دسترس)، و سفری آسان باشد، (به طمع دنیا) از تو پیروی می‌کنند؛ ولی (اکنون که برای میدان تبوک،) راه بر آنها دور (و پر مشقت) است، (سرباز می‌زنند؛) و بزودی به خدا سوگند یاد می‌کنند که: «اگر توانایی داشتیم، همراه شما حرکت می‌کردیم!» (آنها با این اعمال و این دروغها، در واقع) خود را هلاک می‌کنند؛ و خداوند می‌داند آنها دروغگو هستند!'];
ayat[1278]=[9,43,'عَفَا اللَّهُ عَنكَ لِمَ أَذِنتَ لَهُمْ حَتَّىٰ يَتَبَيَّنَ لَكَ الَّذِينَ صَدَقُوا وَتَعْلَمَ الْكَاذِبِينَ','خداوند تو را بخشید؛ چرا پیش از آنکه راستگویان و دروغگویان را بشناسی، به آنها اجازه دادی؟! (خوب بود صبر می‌کردی، تا هر دو گروه خود را نشان دهند!)'];
ayat[1279]=[9,44,'لَا يَسْتَأْذِنُكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ أَن يُجَاهِدُوا بِأَمْوَالِهِمْ وَأَنفُسِهِمْ ۗ وَاللَّهُ عَلِيمٌ بِالْمُتَّقِينَ','آنها که به خدا و روز جزا ایمان دارند، هیچ گاه برای ترک جهاد (در راه خدا) با اموال و جانهایشان، از تو اجازه نمی‌گیرند؛ و خداوند پرهیزگاران را می‌شناسد.'];
ayat[1280]=[9,45,'إِنَّمَا يَسْتَأْذِنُكَ الَّذِينَ لَا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَارْتَابَتْ قُلُوبُهُمْ فَهُمْ فِي رَيْبِهِمْ يَتَرَدَّدُونَ','تنها کسانی از تو اجازه (این کار را) می‌گیرند که به خدا و روز جزا ایمان ندارند، و دلهایشان با شکّ و تردید آمیخته است؛ آنها در تردید خود سرگردانند.'];
ayat[1281]=[9,46,'۞ وَلَوْ أَرَادُوا الْخُرُوجَ لَأَعَدُّوا لَهُ عُدَّةً وَلَٰكِن كَرِهَ اللَّهُ انبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيلَ اقْعُدُوا مَعَ الْقَاعِدِينَ','اگر آنها (راست می‌گفتند، و) اراده داشتند که (بسوی میدان جهاد) خارج شوند، وسیله‌ای برای آن فراهم می‌ساختند! ولی خدا از حرکت آنها کراهت داشت؛ از این رو (توفیقش را از آنان سلب کرد؛ و) آنها را (از جهاد) باز داشت؛ و به آنان گفته شد: «با «قاعدین» [= کودکان و پیران و بیماران‌] بنشینید!»'];
ayat[1282]=[9,47,'لَوْ خَرَجُوا فِيكُم مَّا زَادُوكُمْ إِلَّا خَبَالًا وَلَأَوْضَعُوا خِلَالَكُمْ يَبْغُونَكُمُ الْفِتْنَةَ وَفِيكُمْ سَمَّاعُونَ لَهُمْ ۗ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ','اگر آنها همراه شما (بسوی میدان جهاد) خارج می‌شدند، جز اضطراب و تردید، چیزی بر شما نمی‌افزودند؛ و بسرعت در بین شما به فتنه‌انگیزی (و ایجاد تفرقه و نفاق) می‌پرداختند؛ و در میان شما، افرادی (سست و ضعیف) هستند که به سخنان آنها کاملاً گوش فرامی‌دهند؛ و خداوند، ظالمان را می‌شناسد.'];
ayat[1283]=[9,48,'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِن قَبْلُ وَقَلَّبُوا لَكَ الْأُمُورَ حَتَّىٰ جَاءَ الْحَقُّ وَظَهَرَ أَمْرُ اللَّهِ وَهُمْ كَارِهُونَ','آنها پیش از این (نیز) در پی فتنه‌انگیزی بودند، و کارها را بر تو دگرگون ساختند (و به هم ریختند)؛ تا آن که حق فرا رسید، و فرمان خدا آشکار گشت (و پیروز شدید)، در حالی که آنها کراهت داشتند.'];
ayat[1284]=[9,49,'وَمِنْهُم مَّن يَقُولُ ائْذَن لِّي وَلَا تَفْتِنِّي ۚ أَلَا فِي الْفِتْنَةِ سَقَطُوا ۗ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكَافِرِينَ','بعضی از آنها می‌گویند: «به ما اجازه ده (تا در جهاد شرکت نکنیم)، و ما را به گناه نیفکن»! آگاه باشید آنها (هم اکنون) در گناه سقوط کرده‌اند؛ و جهنم، کافران را احاطه کرده است!'];
ayat[1285]=[9,50,'إِن تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْ ۖ وَإِن تُصِبْكَ مُصِيبَةٌ يَقُولُوا قَدْ أَخَذْنَا أَمْرَنَا مِن قَبْلُ وَيَتَوَلَّوا وَّهُمْ فَرِحُونَ','هرگاه نیکی به تو رسد، آنها را ناراحت می‌کند؛ و اگر مصیبتی به تو رسد، می‌گویند: «ما تصمیم خود را از پیش گرفته‌ایم.» و بازمی‌گردند در حالی که خوشحالند!'];
ayat[1286]=[9,51,'قُل لَّن يُصِيبَنَا إِلَّا مَا كَتَبَ اللَّهُ لَنَا هُوَ مَوْلَانَا ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','بگو: «هیچ حادثه‌ای برای ما رخ نمی‌دهد، مگر آنچه خداوند برای ما نوشته و مقرّر داشته است؛ او مولا (و سرپرست) ماست؛ و مؤمنان باید تنها بر خدا توکّل کنند!»'];
ayat[1287]=[9,52,'قُلْ هَلْ تَرَبَّصُونَ بِنَا إِلَّا إِحْدَى الْحُسْنَيَيْنِ ۖ وَنَحْنُ نَتَرَبَّصُ بِكُمْ أَن يُصِيبَكُمُ اللَّهُ بِعَذَابٍ مِّنْ عِندِهِ أَوْ بِأَيْدِينَا ۖ فَتَرَبَّصُوا إِنَّا مَعَكُم مُّتَرَبِّصُونَ','بگو: «آیا درباره ما، جز یکی از دو نیکی را انتظار دارید؟! (: یا پیروزی یا شهادت) ولی ما انتظار داریم که خداوند، عذابی از سوی خودش (در آن جهان) به شما برساند، یا (در این جهان) به دست ما (مجازات شوید) اکنون که چنین است، شما انتظار بکشید، ما هم با شما انتظار می‌کشیم!»'];
ayat[1288]=[9,53,'قُلْ أَنفِقُوا طَوْعًا أَوْ كَرْهًا لَّن يُتَقَبَّلَ مِنكُمْ ۖ إِنَّكُمْ كُنتُمْ قَوْمًا فَاسِقِينَ','بگو: «انفاق کنید؛ خواه از روی میل باشد یا اکراه، هرگز از شما پذیرفته نمی‌شود؛ چرا که شما قوم فاسقی بودید!»'];
ayat[1289]=[9,54,'وَمَا مَنَعَهُمْ أَن تُقْبَلَ مِنْهُمْ نَفَقَاتُهُمْ إِلَّا أَنَّهُمْ كَفَرُوا بِاللَّهِ وَبِرَسُولِهِ وَلَا يَأْتُونَ الصَّلَاةَ إِلَّا وَهُمْ كُسَالَىٰ وَلَا يُنفِقُونَ إِلَّا وَهُمْ كَارِهُونَ','هیچ چیز مانع قبول انفاقهای آنها نشد، جز اینکه آنها به خدا و پیامبرش کافر شدند، و نماز بجا نمی‌آورند جز با کسالت، و انفاق نمی‌کنند مگر با کراهت!'];
ayat[1290]=[9,55,'فَلَا تُعْجِبْكَ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ ۚ إِنَّمَا يُرِيدُ اللَّهُ لِيُعَذِّبَهُم بِهَا فِي الْحَيَاةِ الدُّنْيَا وَتَزْهَقَ أَنفُسُهُمْ وَهُمْ كَافِرُونَ','و (فزونی) اموال و اولاد آنها، تو را در شگفتی فرو نبرد؛ خدا می‌خواهد آنان را به وسیله آن، در زندگی دنیا عذاب کند، و در حال کفر بمیرند!'];
ayat[1291]=[9,56,'وَيَحْلِفُونَ بِاللَّهِ إِنَّهُمْ لَمِنكُمْ وَمَا هُم مِّنكُمْ وَلَٰكِنَّهُمْ قَوْمٌ يَفْرَقُونَ','آنها به خدا سوگند می‌خورند که از شما هستند، در حالی که از شما نیستند؛ ولی آنها گروهی هستند که می‌ترسند (و به خاطر ترس از فاش شدن اسرارشان دروغ می‌گویند)!'];
ayat[1292]=[9,57,'لَوْ يَجِدُونَ مَلْجَأً أَوْ مَغَارَاتٍ أَوْ مُدَّخَلًا لَّوَلَّوْا إِلَيْهِ وَهُمْ يَجْمَحُونَ','اگر پناهگاه یا غارها یا راهی در زیر زمین بیابند، بسوی آن حرکت می‌کنند، و با سرعت و شتاب فرار می‌کنند.'];
ayat[1293]=[9,58,'وَمِنْهُم مَّن يَلْمِزُكَ فِي الصَّدَقَاتِ فَإِنْ أُعْطُوا مِنْهَا رَضُوا وَإِن لَّمْ يُعْطَوْا مِنْهَا إِذَا هُمْ يَسْخَطُونَ','و در میان آنها کسانی هستند که در (تقسیم) غنایم به تو خرده میگیرند؛ اگر از آن (غنایم، سهمی) به آنها داده شود، راضی می‌شوند؛ و اگر داده نشود، خشم می‌گیرند (؛ خواه حقّ آنها باشد یا نه)!'];
ayat[1294]=[9,59,'وَلَوْ أَنَّهُمْ رَضُوا مَا آتَاهُمُ اللَّهُ وَرَسُولُهُ وَقَالُوا حَسْبُنَا اللَّهُ سَيُؤْتِينَا اللَّهُ مِن فَضْلِهِ وَرَسُولُهُ إِنَّا إِلَى اللَّهِ رَاغِبُونَ','(در حالی که) اگر به آنچه خدا و پیامبرش به آنان داده راضی باشند، و بگویند: «خداوند برای ما کافی است! و بزودی خدا و رسولش، از فضل خود به ما می‌بخشند؛ ما تنها رضای او را می‌طلبیم.» (برای آنها بهتر است)!'];
ayat[1295]=[9,60,'۞ إِنَّمَا الصَّدَقَاتُ لِلْفُقَرَاءِ وَالْمَسَاكِينِ وَالْعَامِلِينَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوبُهُمْ وَفِي الرِّقَابِ وَالْغَارِمِينَ وَفِي سَبِيلِ اللَّهِ وَابْنِ السَّبِيلِ ۖ فَرِيضَةً مِّنَ اللَّهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','زکاتها مخصوص فقرا و مساکین و کارکنانی است که برای (جمع آوری) آن زحمت می‌کشند، و کسانی که برای جلب محبّتشان اقدام می‌شود، و برای (آزادی) بردگان، و (ادای دین) بدهکاران، و در راه (تقویت آیین) خدا، و واماندگان در راه؛ این، یک فریضه (مهم) الهی است؛ و خداوند دانا و حکیم است!'];
ayat[1296]=[9,61,'وَمِنْهُمُ الَّذِينَ يُؤْذُونَ النَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٌ ۚ قُلْ أُذُنُ خَيْرٍ لَّكُمْ يُؤْمِنُ بِاللَّهِ وَيُؤْمِنُ لِلْمُؤْمِنِينَ وَرَحْمَةٌ لِّلَّذِينَ آمَنُوا مِنكُمْ ۚ وَالَّذِينَ يُؤْذُونَ رَسُولَ اللَّهِ لَهُمْ عَذَابٌ أَلِيمٌ','از آنها کسانی هستند که پیامبر را آزار می‌دهند و می‌گویند: «او آدم خوش‌باوری است!» بگو: «خوش‌باور بودن او به نفع شماست! (ولی بدانید) او به خدا ایمان دارد؛ و (تنها) مؤمنان را تصدیق می‌کند؛ و رحمت است برای کسانی از شما که ایمان آورده‌اند!» و آنها که رسول خدا را آزار می‌دهند، عذاب دردناکی دارند!'];
ayat[1297]=[9,62,'يَحْلِفُونَ بِاللَّهِ لَكُمْ لِيُرْضُوكُمْ وَاللَّهُ وَرَسُولُهُ أَحَقُّ أَن يُرْضُوهُ إِن كَانُوا مُؤْمِنِينَ','آنها برای شما به خدا سوگند یاد می‌کنند، تا شما را راضی سازند؛ در حالی که شایسته‌تر این است که خدا و رسولش را راضی کنند، اگر ایمان دارند!'];
ayat[1298]=[9,63,'أَلَمْ يَعْلَمُوا أَنَّهُ مَن يُحَادِدِ اللَّهَ وَرَسُولَهُ فَأَنَّ لَهُ نَارَ جَهَنَّمَ خَالِدًا فِيهَا ۚ ذَٰلِكَ الْخِزْيُ الْعَظِيمُ','آیا نمی‌دانند هر کس با خدا و رسولش دشمنی کند، برای او آتش دوزخ است؛ جاودانه در آن می‌ماند؟! این، همان رسوایی بزرگ است!'];
ayat[1299]=[9,64,'يَحْذَرُ الْمُنَافِقُونَ أَن تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُم بِمَا فِي قُلُوبِهِمْ ۚ قُلِ اسْتَهْزِئُوا إِنَّ اللَّهَ مُخْرِجٌ مَّا تَحْذَرُونَ','منافقان از آن بیم دارند که سوره‌ای بر ضدّ آنان نازل گردد، و به آنها از اسرار درون قلبشان خبر دهد. بگو: «استهزا کنید! خداوند، آنچه را از آن بیم دارید، آشکار می‌سازد!»'];
ayat[1300]=[9,65,'وَلَئِن سَأَلْتَهُمْ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلْعَبُ ۚ قُلْ أَبِاللَّهِ وَآيَاتِهِ وَرَسُولِهِ كُنتُمْ تَسْتَهْزِئُونَ','و اگر از آنها بپرسی (: «چرا این اعمال خلاف را انجام دادید؟!»)، می‌گویند: «ما بازی و شوخی می‌کردیم!» بگو: «آیا خدا و آیات او و پیامبرش را مسخره می‌کردید؟!»'];
ayat[1301]=[9,66,'لَا تَعْتَذِرُوا قَدْ كَفَرْتُم بَعْدَ إِيمَانِكُمْ ۚ إِن نَّعْفُ عَن طَائِفَةٍ مِّنكُمْ نُعَذِّبْ طَائِفَةً بِأَنَّهُمْ كَانُوا مُجْرِمِينَ','(بگو:) عذر خواهی نکنید (که بیهوده است؛ چرا که) شما پس از ایمان آوردن، کافر شدید! اگر گروهی از شما را (بخاطر توبه) مورد عفو قرار دهیم، گروه دیگری را عذاب خواهیم کرد؛ زیرا مجرم بودند!'];
ayat[1302]=[9,67,'الْمُنَافِقُونَ وَالْمُنَافِقَاتُ بَعْضُهُم مِّن بَعْضٍ ۚ يَأْمُرُونَ بِالْمُنكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوفِ وَيَقْبِضُونَ أَيْدِيَهُمْ ۚ نَسُوا اللَّهَ فَنَسِيَهُمْ ۗ إِنَّ الْمُنَافِقِينَ هُمُ الْفَاسِقُونَ','مردان منافق و زنان منافق، همه از یک گروهند! آنها امر به منکر، و نهی از معروف می‌کنند؛ و دستهایشان را (از انفاق و بخشش) می‌بندند؛ خدا را فراموش کردند، و خدا (نیز) آنها را فراموش کرد (، و رحمتش را از آنها قطع نمود)؛ به یقین، منافقان همان فاسقانند!'];
ayat[1303]=[9,68,'وَعَدَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا ۚ هِيَ حَسْبُهُمْ ۚ وَلَعَنَهُمُ اللَّهُ ۖ وَلَهُمْ عَذَابٌ مُّقِيمٌ','خداوند به مردان و زنان منافق و کفّار، وعده آتش دوزخ داده؛ جاودانه در آن خواهند ماند -همان برای آنها کافی است!- و خدا آنها را از رحمت خود دور ساخته؛ و عذاب همیشگی برای آنهاست!'];
ayat[1304]=[9,69,'كَالَّذِينَ مِن قَبْلِكُمْ كَانُوا أَشَدَّ مِنكُمْ قُوَّةً وَأَكْثَرَ أَمْوَالًا وَأَوْلَادًا فَاسْتَمْتَعُوا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُم بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِينَ مِن قَبْلِكُم بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذِي خَاضُوا ۚ أُولَٰئِكَ حَبِطَتْ أَعْمَالُهُمْ فِي الدُّنْيَا وَالْآخِرَةِ ۖ وَأُولَٰئِكَ هُمُ الْخَاسِرُونَ','(شما منافقان،) همانند کسانی هستید که قبل از شما بودند (و راه نفاق پیمودند؛ بلکه) آنها از شما نیرومندتر، و اموال و فرزندانشان بیشتر بود! آنها از بهره خود (از مواهب الهی در راه گناه و هوس) استفاده کردند؛ شما نیز از بهره خود، (در این راه) استفاده کردید، همان گونه که آنها استفاده کردند؛ شما (در کفر و نفاق و استهزای مؤمنان) فرو رفتید، همان گونه که آنها فرو رفتند؛ (ولی سرانجام) اعمالشان در دنیا و آخرت نابود شد؛ و آنها همان زیانکارانند!'];
ayat[1305]=[9,70,'أَلَمْ يَأْتِهِمْ نَبَأُ الَّذِينَ مِن قَبْلِهِمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَقَوْمِ إِبْرَاهِيمَ وَأَصْحَابِ مَدْيَنَ وَالْمُؤْتَفِكَاتِ ۚ أَتَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ ۖ فَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','آیا خبر کسانی که پیش از آنها بودند، به آنان نرسیده است؟! «قوم نوح» و «عاد» و «ثمود» و «قوم ابراهیم» و «اصحاب مدین» [= قوم شعیب‌] و «شهرهای زیر و رو شده» [= قوم لوط]؛ پیامبرانشان دلایل روشن برای آنان آوردند، (ولی نپذیرفتند؛) خداوند به آنها ستم نکرد، امّا خودشان بر خویشتن ستم می‌کردند!'];
ayat[1306]=[9,71,'وَالْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۚ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنكَرِ وَيُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَيُطِيعُونَ اللَّهَ وَرَسُولَهُ ۚ أُولَٰئِكَ سَيَرْحَمُهُمُ اللَّهُ ۗ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','مردان و زنان باایمان، ولیّ (و یار و یاور) یکدیگرند؛ امر به معروف، و نهی از منکر می‌کنند؛ نماز را برپا می‌دارند؛ و زکات را می‌پردازند؛ و خدا و رسولش را اطاعت می‌کنند؛ بزودی خدا آنان را مورد رحمت خویش قرارمی‌دهد؛ خداوند توانا و حکیم است!'];
ayat[1307]=[9,72,'وَعَدَ اللَّهُ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ۚ وَرِضْوَانٌ مِّنَ اللَّهِ أَكْبَرُ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','خداوند به مردان و زنان باایمان، باغهایی از بهشت وعده داده که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند ماند؛ و مسکن‌های پاکیزه‌ای در بهشتهای جاودان (نصیب آنها ساخته)؛ و (خشنودی و) رضای خدا، (از همه اینها) برتر است؛ و پیروزی بزرگ، همین است!'];
ayat[1308]=[9,73,'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ ۚ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ','ای پیامبر! با کافران و منافقان جهاد کن، و بر آنها سخت بگیر! جایگاهشان جهنم است؛ و چه بد سرنوشتی دارند!'];
ayat[1309]=[9,74,'يَحْلِفُونَ بِاللَّهِ مَا قَالُوا وَلَقَدْ قَالُوا كَلِمَةَ الْكُفْرِ وَكَفَرُوا بَعْدَ إِسْلَامِهِمْ وَهَمُّوا بِمَا لَمْ يَنَالُوا ۚ وَمَا نَقَمُوا إِلَّا أَنْ أَغْنَاهُمُ اللَّهُ وَرَسُولُهُ مِن فَضْلِهِ ۚ فَإِن يَتُوبُوا يَكُ خَيْرًا لَّهُمْ ۖ وَإِن يَتَوَلَّوْا يُعَذِّبْهُمُ اللَّهُ عَذَابًا أَلِيمًا فِي الدُّنْيَا وَالْآخِرَةِ ۚ وَمَا لَهُمْ فِي الْأَرْضِ مِن وَلِيٍّ وَلَا نَصِيرٍ','به خدا سوگند می‌خورند که (در غیاب پیامبر، سخنان نادرست) نگفته‌اند؛ در حالی که قطعاً سخنان کفرآمیز گفته‌اند؛ و پس از اسلام‌آوردنشان، کافر شده‌اند؛ و تصمیم (به کار خطرناکی) گرفتند، که به آن نرسیدند. آنها فقط از این انتقام می‌گیرند که خداوند و رسولش، آنان را به فضل (و کرم) خود، بی‌نیاز ساختند! (با این حال،) اگر توبه کنند، برای آنها بهتر است؛ و اگر روی گردانند، خداوند آنها را در دنیا و آخرت، به مجازات دردناکی کیفر خواهد داد؛ و در سراسر زمین، نه ولیّ و حامی دارند، و نه یاوری!'];
ayat[1310]=[9,75,'۞ وَمِنْهُم مَّنْ عَاهَدَ اللَّهَ لَئِنْ آتَانَا مِن فَضْلِهِ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ الصَّالِحِينَ','بعضی از آنها با خدا پیمان بسته بودند که: «اگر خداوند ما را از فضل خود روزی دهد، قطعاً صدقه خواهیم داد؛ و از صالحان (و شاکران) خواهیم بود!»'];
ayat[1311]=[9,76,'فَلَمَّا آتَاهُم مِّن فَضْلِهِ بَخِلُوا بِهِ وَتَوَلَّوا وَّهُم مُّعْرِضُونَ','امّا هنگامی که خدا از فضل خود به آنها بخشید، بخل ورزیدند و سرپیچی کردند و روی برتافتند!'];
ayat[1312]=[9,77,'فَأَعْقَبَهُمْ نِفَاقًا فِي قُلُوبِهِمْ إِلَىٰ يَوْمِ يَلْقَوْنَهُ بِمَا أَخْلَفُوا اللَّهَ مَا وَعَدُوهُ وَبِمَا كَانُوا يَكْذِبُونَ','این عمل، (روح) نفاق را، تا روزی که خدا را ملاقات کنند، در دلهایشان برقرار ساخت. این بخاطر آن است که از پیمان الهی تخلّف جستند؛ و بخاطر آن است که دروغ می‌گفتند.'];
ayat[1313]=[9,78,'أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَعْلَمُ سِرَّهُمْ وَنَجْوَاهُمْ وَأَنَّ اللَّهَ عَلَّامُ الْغُيُوبِ','آیا نمی‌دانستند که خداوند، اسرار و سخنان درگوشی آنها را می‌داند؛ و خداوند دانای همه غیبها (و امور پنهانی) است؟!'];
ayat[1314]=[9,79,'الَّذِينَ يَلْمِزُونَ الْمُطَّوِّعِينَ مِنَ الْمُؤْمِنِينَ فِي الصَّدَقَاتِ وَالَّذِينَ لَا يَجِدُونَ إِلَّا جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْ ۙ سَخِرَ اللَّهُ مِنْهُمْ وَلَهُمْ عَذَابٌ أَلِيمٌ','آنهایی که از مؤمنان اطاعت کار، در صدقاتشان عیبجویی می‌کنند، و کسانی را که (برای انفاق در راه خدا) جز به مقدار (ناچیز) توانائی خود دسترسی ندارند، مسخره می‌نمایند، خدا آنها را مسخره می‌کند؛ (و کیفر استهزاکنندگان را به آنها می‌دهد؛) و برای آنها عذاب دردناکی است!'];
ayat[1315]=[9,80,'اسْتَغْفِرْ لَهُمْ أَوْ لَا تَسْتَغْفِرْ لَهُمْ إِن تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةً فَلَن يَغْفِرَ اللَّهُ لَهُمْ ۚ ذَٰلِكَ بِأَنَّهُمْ كَفَرُوا بِاللَّهِ وَرَسُولِهِ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ','چه برای آنها استغفار کنی، و چه نکنی، (حتّی) اگر هفتاد بار برای آنها استغفار کنی، هرگز خدا آنها را نمی‌آمرزد! چرا که خدا و پیامبرش را انکار کردند؛ و خداوند جمعیّت فاسقان را هدایت نمی‌کند!'];
ayat[1316]=[9,81,'فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلَافَ رَسُولِ اللَّهِ وَكَرِهُوا أَن يُجَاهِدُوا بِأَمْوَالِهِمْ وَأَنفُسِهِمْ فِي سَبِيلِ اللَّهِ وَقَالُوا لَا تَنفِرُوا فِي الْحَرِّ ۗ قُلْ نَارُ جَهَنَّمَ أَشَدُّ حَرًّا ۚ لَّوْ كَانُوا يَفْقَهُونَ','تخلّف‌جویان (از جنگ تبوک،) از مخالفت با رسول خدا خوشحال شدند؛ و کراهت داشتند که با اموال و جانهای خود، در راه خدا جهاد کنند؛ و (به یکدیگر و به مؤمنان) گفتند: «در این گرما، (بسوی میدان) حرکت نکنید!» (به آنان) بگو: «آتش دوزخ از این هم گرمتر است!» اگر می‌دانستند!'];
ayat[1317]=[9,82,'فَلْيَضْحَكُوا قَلِيلًا وَلْيَبْكُوا كَثِيرًا جَزَاءً بِمَا كَانُوا يَكْسِبُونَ','از این‌رو آنها باید کمتر بخندند و بسیار بگریند! (چرا که آتش جهنم در انتظارشان است) این، جزای کارهایی است که انجام می‌دادند!'];
ayat[1318]=[9,83,'فَإِن رَّجَعَكَ اللَّهُ إِلَىٰ طَائِفَةٍ مِّنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُل لَّن تَخْرُجُوا مَعِيَ أَبَدًا وَلَن تُقَاتِلُوا مَعِيَ عَدُوًّا ۖ إِنَّكُمْ رَضِيتُم بِالْقُعُودِ أَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخَالِفِينَ','هرگاه خداوند تو را بسوی گروهی از آنان بازگرداند، و از تو اجازه خروج (بسوی میدان جهاد) بخواهند، بگو: «هیچ گاه با من خارج نخواهید شد! و هرگز همراه من، با دشمنی نخواهید جنگید! شما نخستین بار به کناره‌گیری راضی شدید، اکنون نیز با متخلّفان بمانید!»'];
ayat[1319]=[9,84,'وَلَا تُصَلِّ عَلَىٰ أَحَدٍ مِّنْهُم مَّاتَ أَبَدًا وَلَا تَقُمْ عَلَىٰ قَبْرِهِ ۖ إِنَّهُمْ كَفَرُوا بِاللَّهِ وَرَسُولِهِ وَمَاتُوا وَهُمْ فَاسِقُونَ','هرگز بر مرده هیچ یک از آنان، نماز نخوان! و بر کنار قبرش، (برای دعا و طلب آمرزش،) نایست! چرا که آنها به خدا و رسولش کافر شدند؛ و در حالی که فاسق بودند از دنیا رفتند!'];
ayat[1320]=[9,85,'وَلَا تُعْجِبْكَ أَمْوَالُهُمْ وَأَوْلَادُهُمْ ۚ إِنَّمَا يُرِيدُ اللَّهُ أَن يُعَذِّبَهُم بِهَا فِي الدُّنْيَا وَتَزْهَقَ أَنفُسُهُمْ وَهُمْ كَافِرُونَ','مبادا اموال و فرزندانشان، مایه شگفتی تو گردد! (این برای آنها نعمت نیست؛ بلکه) خدا می‌خواهد آنها را به این وسیله در دنیا عذاب کند، و جانشان برآید در حالی که کافرند!'];
ayat[1321]=[9,86,'وَإِذَا أُنزِلَتْ سُورَةٌ أَنْ آمِنُوا بِاللَّهِ وَجَاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ أُولُو الطَّوْلِ مِنْهُمْ وَقَالُوا ذَرْنَا نَكُن مَّعَ الْقَاعِدِينَ','و هنگامی که سوره‌ای نازل شود (و به آنان دستور دهد) که: «به خدا ایمان بیاورید! و همراه پیامبرش جهاد کنید!»، افرادی از آنها [= گروه منافقان‌] که توانایی دارند، از تو اجازه می‌خواهند و می‌گویند: «بگذار ما با قاعدین [= آنها که از جهاد معافند] باشیم!»'];
ayat[1322]=[9,87,'رَضُوا بِأَن يَكُونُوا مَعَ الْخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ','(آری،) آنها راضی شدند که با متخلّفان باشند؛ و بر دلهایشان مهر نهاده شده؛ از این رو (چیزی) نمی‌فهمند!'];
ayat[1323]=[9,88,'لَٰكِنِ الرَّسُولُ وَالَّذِينَ آمَنُوا مَعَهُ جَاهَدُوا بِأَمْوَالِهِمْ وَأَنفُسِهِمْ ۚ وَأُولَٰئِكَ لَهُمُ الْخَيْرَاتُ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','ولی پیامبر و کسانی که با او ایمان آوردند، با اموال و جانهایشان جهاد کردند؛ و همه نیکیها برای آنهاست؛ و آنها همان رستگارانند!'];
ayat[1324]=[9,89,'أَعَدَّ اللَّهُ لَهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ','خداوند برای آنها باغهایی از بهشت فراهم ساخته که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند بود؛ و این است رستگاری (و پیروزی) بزرگ!'];
ayat[1325]=[9,90,'وَجَاءَ الْمُعَذِّرُونَ مِنَ الْأَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذِينَ كَذَبُوا اللَّهَ وَرَسُولَهُ ۚ سَيُصِيبُ الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابٌ أَلِيمٌ','و عذرآورندگان از اعراب، (نزد تو) آمدند که به آنها اجازه (عدم شرکت در جهاد) داده شود؛ و آنها که به خدا و پیامبرش دروغ گفتند، (بدون هیچ عذری در خانه خود) نشستند؛ بزودی به کسانی از آنها که مخالفت کردند (و معذور نبودند)، عذاب دردناکی خواهد رسید!'];
ayat[1326]=[9,91,'لَّيْسَ عَلَى الضُّعَفَاءِ وَلَا عَلَى الْمَرْضَىٰ وَلَا عَلَى الَّذِينَ لَا يَجِدُونَ مَا يُنفِقُونَ حَرَجٌ إِذَا نَصَحُوا لِلَّهِ وَرَسُولِهِ ۚ مَا عَلَى الْمُحْسِنِينَ مِن سَبِيلٍ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','بر ضعیفان و بیماران و آنها که وسیله‌ای برای انفاق (در راه جهاد) ندارند، ایرادی نیست (که در میدان جنگ شرکت نجویند،) هرگاه برای خدا و رسولش خیرخواهی کنند (؛ و از آنچه در توان دارند، مضایقه ننمایند). بر نیکوکاران راه مؤاخذه نیست؛ و خداوند آمرزنده و مهربان است!'];
ayat[1327]=[9,92,'وَلَا عَلَى الَّذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَا أَجِدُ مَا أَحْمِلُكُمْ عَلَيْهِ تَوَلَّوا وَّأَعْيُنُهُمْ تَفِيضُ مِنَ الدَّمْعِ حَزَنًا أَلَّا يَجِدُوا مَا يُنفِقُونَ','و (نیز) ایرادی نیست بر آنها که وقتی نزد تو آمدند که آنان را بر مرکبی (برای جهاد) سوار کنی، گفتی: «مرکبی که شما را بر آن سوار کنم، ندارم!» (از نزد تو) بازگشتند در حالی که چشمانشان از اندوه اشکبار بود؛ زیرا چیزی نداشتند که در راه خدا انفاق کنند (و با آن به میدان بروند)'];
ayat[1328]=[9,93,'۞ إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَسْتَأْذِنُونَكَ وَهُمْ أَغْنِيَاءُ ۚ رَضُوا بِأَن يَكُونُوا مَعَ الْخَوَالِفِ وَطَبَعَ اللَّهُ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَعْلَمُونَ','راه مؤاخذه تنها به روی کسانی باز است که از تو اجازه می‌خواهند در حالی که توانگرند؛ (و امکانات کافی برای جهاد دارند؛) آنها راضی شدند که با متخلّفان [= زنان و کودکان و بیماران‌] بمانند؛ و خداوند بر دلهایشان مهر نهاده؛ به همین جهت چیزی نمی‌دانند!'];
ayat[1329]=[9,94,'يَعْتَذِرُونَ إِلَيْكُمْ إِذَا رَجَعْتُمْ إِلَيْهِمْ ۚ قُل لَّا تَعْتَذِرُوا لَن نُّؤْمِنَ لَكُمْ قَدْ نَبَّأَنَا اللَّهُ مِنْ أَخْبَارِكُمْ ۚ وَسَيَرَى اللَّهُ عَمَلَكُمْ وَرَسُولُهُ ثُمَّ تُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','هنگامی که بسوی آنها (که از جهاد تخلّف کردند) باز گردید، از شما عذرخواهی می‌کنند؛ بگو: «عذرخواهی نکنید، ما هرگز به شما ایمان نخواهیم آورد! چرا که خدا ما را از اخبارتان آگاه ساخته؛ و خدا و رسولش، اعمال شما را می‌بینند؛ سپس به سوی کسی که دانای پنهان و آشکار است بازگشت داده می‌شوید؛ و او شما را به آنچه انجام می‌دادید، آگاه می‌کند (و جزا می‌دهد!)»'];
ayat[1330]=[9,95,'سَيَحْلِفُونَ بِاللَّهِ لَكُمْ إِذَا انقَلَبْتُمْ إِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْ ۖ فَأَعْرِضُوا عَنْهُمْ ۖ إِنَّهُمْ رِجْسٌ ۖ وَمَأْوَاهُمْ جَهَنَّمُ جَزَاءً بِمَا كَانُوا يَكْسِبُونَ','هنگامی که بسوی آنان بازگردید، برای شما به خدا سوگند یاد می‌کنند، تا از آنها اعراض (و صرف نظر) کنید؛ از آنها اعراض کنید (و روی بگردانید)؛ چرا که پلیدند! و جایگاهشان دوزخ است، بکیفر اعمالی که انجام می‌دادند.'];
ayat[1331]=[9,96,'يَحْلِفُونَ لَكُمْ لِتَرْضَوْا عَنْهُمْ ۖ فَإِن تَرْضَوْا عَنْهُمْ فَإِنَّ اللَّهَ لَا يَرْضَىٰ عَنِ الْقَوْمِ الْفَاسِقِينَ','برای شما قسم یاد می‌کنند تا از آنها راضی شوید؛ اگر شما از آنها راضی شوید، خداوند (هرگز) از جمعیّت فاسقان راضی نخواهد شد!'];
ayat[1332]=[9,97,'الْأَعْرَابُ أَشَدُّ كُفْرًا وَنِفَاقًا وَأَجْدَرُ أَلَّا يَعْلَمُوا حُدُودَ مَا أَنزَلَ اللَّهُ عَلَىٰ رَسُولِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','بادیه‌نشینان عرب، کفر و نفاقشان شدیدتر است؛ و به ناآگاهی از حدود و احکامی که خدا بر پیامبرش نازل کرده، سزاوارترند؛ و خداوند دانا و حکیم است!'];
ayat[1333]=[9,98,'وَمِنَ الْأَعْرَابِ مَن يَتَّخِذُ مَا يُنفِقُ مَغْرَمًا وَيَتَرَبَّصُ بِكُمُ الدَّوَائِرَ ۚ عَلَيْهِمْ دَائِرَةُ السَّوْءِ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','گروهی از (این) اعراب بادیه‌نشین، چیزی را که (در راه خدا) انفاق می‌کنند، غرامت محسوب می‌دارند؛ و انتظار حوادث دردناکی برای شما می‌کشند؛ حوادث دردناک برای خود آنهاست؛ و خداوند شنوا و داناست!'];
ayat[1334]=[9,99,'وَمِنَ الْأَعْرَابِ مَن يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ وَيَتَّخِذُ مَا يُنفِقُ قُرُبَاتٍ عِندَ اللَّهِ وَصَلَوَاتِ الرَّسُولِ ۚ أَلَا إِنَّهَا قُرْبَةٌ لَّهُمْ ۚ سَيُدْخِلُهُمُ اللَّهُ فِي رَحْمَتِهِ ۗ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','گروهی (دیگر) از عربهای بادیه‌نشین، به خدا و روز رستاخیز ایمان دارند؛ و آنچه را انفاق می‌کنند، مایه تقرّب به خدا، و دعای پیامبر می‌دانند؛ آگاه باشید اینها مایه تقرّب آنهاست! خداوند بزودی آنان را در رحمت خود وارد خواهد ساخت؛ به یقین، خداوند آمرزنده و مهربان است!'];
ayat[1335]=[9,100,'وَالسَّابِقُونَ الْأَوَّلُونَ مِنَ الْمُهَاجِرِينَ وَالْأَنصَارِ وَالَّذِينَ اتَّبَعُوهُم بِإِحْسَانٍ رَّضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ وَأَعَدَّ لَهُمْ جَنَّاتٍ تَجْرِي تَحْتَهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ','پیشگامان نخستین از مهاجرین و انصار، و کسانی که به نیکی از آنها پیروی کردند، خداوند از آنها خشنود گشت، و آنها (نیز) از او خشنود شدند؛ و باغهایی از بهشت برای آنان فراهم ساخته، که نهرها از زیر درختانش جاری است؛ جاودانه در آن خواهند ماند؛ و این است پیروزی بزرگ!'];
ayat[1336]=[9,101,'وَمِمَّنْ حَوْلَكُم مِّنَ الْأَعْرَابِ مُنَافِقُونَ ۖ وَمِنْ أَهْلِ الْمَدِينَةِ ۖ مَرَدُوا عَلَى النِّفَاقِ لَا تَعْلَمُهُمْ ۖ نَحْنُ نَعْلَمُهُمْ ۚ سَنُعَذِّبُهُم مَّرَّتَيْنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٍ','و از (میان) اعراب بادیه‌نشین که اطراف شما هستند، جمعی منافقند؛ و از اهل مدینه (نیز)، گروهی سخت به نفاق پای بندند. تو آنها را نمی‌شناسی، ولی ما آنها را می شناسیم. بزودی آنها را دو بار مجازات می‌کنیم (: مجازاتی با رسوایی در دنیا، و مجازاتی به هنگام مرگ)؛ سپس بسوی مجازات بزرگی (در قیامت) فرستاده می‌شوند.'];
ayat[1337]=[9,102,'وَآخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلًا صَالِحًا وَآخَرَ سَيِّئًا عَسَى اللَّهُ أَن يَتُوبَ عَلَيْهِمْ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','و گروهی دیگر، به گناهان خود اعتراف کردند؛ و کار خوب و بد را به هم آمیختند؛ امید می‌رود که خداوند توبه آنها را بپذیرد؛ به یقین، خداوند آمرزنده و مهربان است!'];
ayat[1338]=[9,103,'خُذْ مِنْ أَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيهِم بِهَا وَصَلِّ عَلَيْهِمْ ۖ إِنَّ صَلَاتَكَ سَكَنٌ لَّهُمْ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','از اموال آنها صدقه‌ای (بعنوان زکات) بگیر، تا بوسیله آن، آنها را پاک سازی و پرورش دهی! و (به هنگام گرفتن زکات،) به آنها دعا کن؛ که دعای تو، مایه آرامش آنهاست؛ و خداوند شنوا و داناست!'];
ayat[1339]=[9,104,'أَلَمْ يَعْلَمُوا أَنَّ اللَّهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهِ وَيَأْخُذُ الصَّدَقَاتِ وَأَنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ','آیا نمی‌دانستند که فقط خداوند توبه را از بندگانش می‌پذیرد، و صدقات را می‌گیرد، و خداوند توبه‌پذیر و مهربان است؟!'];
ayat[1340]=[9,105,'وَقُلِ اعْمَلُوا فَسَيَرَى اللَّهُ عَمَلَكُمْ وَرَسُولُهُ وَالْمُؤْمِنُونَ ۖ وَسَتُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','بگو: «عمل کنید! خداوند و فرستاده او و مؤمنان، اعمال شما را می‌بینند! و بزودی، بسوی دانای نهان و آشکار، بازگردانده می‌شوید؛ و شما را به آنچه عمل می‌کردید، خبر می‌دهد!»'];
ayat[1341]=[9,106,'وَآخَرُونَ مُرْجَوْنَ لِأَمْرِ اللَّهِ إِمَّا يُعَذِّبُهُمْ وَإِمَّا يَتُوبُ عَلَيْهِمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','و گروهی دیگر، به فرمان خدا واگذار شده‌اند (و کارشان با خداست)؛ یا آنها را مجازات می‌کند، و یا توبه آنان را می‌پذیرد (، هر طور که شایسته باشند)؛ و خداوند دانا و حکیم است!'];
ayat[1342]=[9,107,'وَالَّذِينَ اتَّخَذُوا مَسْجِدًا ضِرَارًا وَكُفْرًا وَتَفْرِيقًا بَيْنَ الْمُؤْمِنِينَ وَإِرْصَادًا لِّمَنْ حَارَبَ اللَّهَ وَرَسُولَهُ مِن قَبْلُ ۚ وَلَيَحْلِفُنَّ إِنْ أَرَدْنَا إِلَّا الْحُسْنَىٰ ۖ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ','(گروهی دیگر از آنها) کسانی هستند که مسجدی ساختند برای زیان (به مسلمانان)، و (تقوّیت) کفر، و تفرقه‌افکنی میان مؤمنان، و کمینگاه برای کسی که از پیش با خدا و پیامبرش مبارزه کرده بود؛ آنها سوگند یاد می‌کنند که: «جز نیکی (و خدمت)، نظری نداشته‌ایم!» امّا خداوند گواهی می‌دهد که آنها دروغگو هستند!'];
ayat[1343]=[9,108,'لَا تَقُمْ فِيهِ أَبَدًا ۚ لَّمَسْجِدٌ أُسِّسَ عَلَى التَّقْوَىٰ مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَن تَقُومَ فِيهِ ۚ فِيهِ رِجَالٌ يُحِبُّونَ أَن يَتَطَهَّرُوا ۚ وَاللَّهُ يُحِبُّ الْمُطَّهِّرِينَ','هرگز در آن (مسجد به عبادت) نایست! آن مسجدی که از روز نخست بر پایه تقوا بنا شده، شایسته‌تر است که در آن (به عبادت) بایستی؛ در آن، مردانی هستند که دوست می‌دارند پاکیزه باشند؛ و خداوند پاکیزگان را دوست دارد!'];
ayat[1344]=[9,109,'أَفَمَنْ أَسَّسَ بُنْيَانَهُ عَلَىٰ تَقْوَىٰ مِنَ اللَّهِ وَرِضْوَانٍ خَيْرٌ أَم مَّنْ أَسَّسَ بُنْيَانَهُ عَلَىٰ شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهِ فِي نَارِ جَهَنَّمَ ۗ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','آیا کسی که شالوده آن را بر تقوای الهی و خشنودی او بنا کرده بهتر است، یا کسی که اساس آن را بر کنار پرتگاه سستی بنا نموده که ناگهان در آتش دوزخ فرومی‌ریزد؟ و خداوند گروه ستمگران را هدایت نمی‌کند!'];
ayat[1345]=[9,110,'لَا يَزَالُ بُنْيَانُهُمُ الَّذِي بَنَوْا رِيبَةً فِي قُلُوبِهِمْ إِلَّا أَن تَقَطَّعَ قُلُوبُهُمْ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','(امّا) این بنایی را که آنها ساختند، همواره بصورت یک وسیله شک و تردید، در دلهایشان باقی می‌ماند؛ مگر اینکه دلهایشان پاره پاره شود (و بمیرند؛ وگرنه، هرگز از دل آنها بیرون نمی‌رود)؛ و خداوند دانا و حکیم است!'];
ayat[1346]=[9,111,'۞ إِنَّ اللَّهَ اشْتَرَىٰ مِنَ الْمُؤْمِنِينَ أَنفُسَهُمْ وَأَمْوَالَهُم بِأَنَّ لَهُمُ الْجَنَّةَ ۚ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ فَيَقْتُلُونَ وَيُقْتَلُونَ ۖ وَعْدًا عَلَيْهِ حَقًّا فِي التَّوْرَاةِ وَالْإِنجِيلِ وَالْقُرْآنِ ۚ وَمَنْ أَوْفَىٰ بِعَهْدِهِ مِنَ اللَّهِ ۚ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذِي بَايَعْتُم بِهِ ۚ وَذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','خداوند از مؤمنان، جانها و اموالشان را خریداری کرده، که (در برابرش) بهشت برای آنان باشد؛ (به این گونه که:) در راه خدا پیکار می‌کنند، می‌کشند و کشته می‌شوند؛ این وعده حقّی است بر او، که در تورات و انجیل و قرآن ذکر فرموده؛ و چه کسی از خدا به عهدش وفادارتر است؟! اکنون بشارت باد بر شما، به داد و ستدی که با خدا کرده‌اید؛ و این است آن پیروزی بزرگ!'];
ayat[1347]=[9,112,'التَّائِبُونَ الْعَابِدُونَ الْحَامِدُونَ السَّائِحُونَ الرَّاكِعُونَ السَّاجِدُونَ الْآمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ الْمُنكَرِ وَالْحَافِظُونَ لِحُدُودِ اللَّهِ ۗ وَبَشِّرِ الْمُؤْمِنِينَ','توبه‌کنندگان، عبادت کاران، سپاسگویان، سیاحت کنندگان، رکوع کنندگان، سجده‌آوران، آمران به معروف، نهی کنندگان از منکر، و حافظان حدود (و مرزهای) الهی، (مؤمنان حقیقی‌اند)؛ و بشارت ده به (اینچنین) مؤمنان!'];
ayat[1348]=[9,113,'مَا كَانَ لِلنَّبِيِّ وَالَّذِينَ آمَنُوا أَن يَسْتَغْفِرُوا لِلْمُشْرِكِينَ وَلَوْ كَانُوا أُولِي قُرْبَىٰ مِن بَعْدِ مَا تَبَيَّنَ لَهُمْ أَنَّهُمْ أَصْحَابُ الْجَحِيمِ','برای پیامبر و مؤمنان، شایسته نبود که برای مشرکان (از خداوند) طلب آمرزش کنند، هر چند از نزدیکانشان باشند؛ (آن هم) پس از آنکه بر آنها روشن شد که این گروه، اهل دوزخند!'];
ayat[1349]=[9,114,'وَمَا كَانَ اسْتِغْفَارُ إِبْرَاهِيمَ لِأَبِيهِ إِلَّا عَن مَّوْعِدَةٍ وَعَدَهَا إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُ أَنَّهُ عَدُوٌّ لِّلَّهِ تَبَرَّأَ مِنْهُ ۚ إِنَّ إِبْرَاهِيمَ لَأَوَّاهٌ حَلِيمٌ','و استغفار ابراهیم برای پدرش [= عمویش آزر]، فقط بخاطر وعده‌ای بود که به او داده بود (تا وی را بسوی ایمان جذب کند)؛ امّا هنگامی که برای او روشن شد که وی دشمن خداست، از او بیزاری جست؛ به یقین، ابراهیم مهربان و بردبار بود!'];
ayat[1350]=[9,115,'وَمَا كَانَ اللَّهُ لِيُضِلَّ قَوْمًا بَعْدَ إِذْ هَدَاهُمْ حَتَّىٰ يُبَيِّنَ لَهُم مَّا يَتَّقُونَ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ','چنان نبود که خداوند قومی را، پس از آن که آنها را هدایت کرد (و ایمان آوردند) گمراه (و مجازات) کند؛ مگر آنکه اموری را که باید از آن بپرهیزند، برای آنان بیان نماید (و آنها مخالفت کنند)؛ زیرا خداوند به هر چیزی داناست!'];
ayat[1351]=[9,116,'إِنَّ اللَّهَ لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يُحْيِي وَيُمِيتُ ۚ وَمَا لَكُم مِّن دُونِ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ','حکومت آسمانها و زمین تنها از آن خداست؛ زنده می‌کند و می‌میراند؛ و جز خدا، ولیّ و یاوری ندارید!'];
ayat[1352]=[9,117,'لَّقَد تَّابَ اللَّهُ عَلَى النَّبِيِّ وَالْمُهَاجِرِينَ وَالْأَنصَارِ الَّذِينَ اتَّبَعُوهُ فِي سَاعَةِ الْعُسْرَةِ مِن بَعْدِ مَا كَادَ يَزِيغُ قُلُوبُ فَرِيقٍ مِّنْهُمْ ثُمَّ تَابَ عَلَيْهِمْ ۚ إِنَّهُ بِهِمْ رَءُوفٌ رَّحِيمٌ','مسلّماً خداوند رحمت خود را شامل حال پیامبر و مهاجران و انصار، که در زمان عسرت و شدّت (در جنگ تبوک) از او پیروی کردند، نمود؛ بعد از آنکه نزدیک بود دلهای گروهی از آنها، از حقّ منحرف شود (و از میدان جنگ بازگردند)؛ سپس خدا توبه آنها را پذیرفت، که او نسبت به آنان مهربان و رحیم است!'];
ayat[1353]=[9,118,'وَعَلَى الثَّلَاثَةِ الَّذِينَ خُلِّفُوا حَتَّىٰ إِذَا ضَاقَتْ عَلَيْهِمُ الْأَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ أَنفُسُهُمْ وَظَنُّوا أَن لَّا مَلْجَأَ مِنَ اللَّهِ إِلَّا إِلَيْهِ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوبُوا ۚ إِنَّ اللَّهَ هُوَ التَّوَّابُ الرَّحِيمُ','(همچنین) آن سه نفر که (از شرکت در جنگ تبوک) تخلّف جستند، (و مسلمانان با آنان قطع رابطه نمودند،) تا آن حدّ که زمین با همه وسعتش بر آنها تنگ شد؛ (حتّی) در وجود خویش، جایی برای خود نمی‌یافتند؛ (در آن هنگام) دانستند پناهگاهی از خدا جز بسوی او نیست؛ سپس خدا رحمتش را شامل حال آنها نمود، (و به آنان توفیق داد) تا توبه کنند؛ خداوند بسیار توبه‌پذیر و مهربان است!'];
ayat[1354]=[9,119,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَكُونُوا مَعَ الصَّادِقِينَ','ای کسانی که ایمان آورده‌اید! از (مخالفت فرمان) خدا بپرهیزید، و با صادقان باشید!'];
ayat[1355]=[9,120,'مَا كَانَ لِأَهْلِ الْمَدِينَةِ وَمَنْ حَوْلَهُم مِّنَ الْأَعْرَابِ أَن يَتَخَلَّفُوا عَن رَّسُولِ اللَّهِ وَلَا يَرْغَبُوا بِأَنفُسِهِمْ عَن نَّفْسِهِ ۚ ذَٰلِكَ بِأَنَّهُمْ لَا يُصِيبُهُمْ ظَمَأٌ وَلَا نَصَبٌ وَلَا مَخْمَصَةٌ فِي سَبِيلِ اللَّهِ وَلَا يَطَئُونَ مَوْطِئًا يَغِيظُ الْكُفَّارَ وَلَا يَنَالُونَ مِنْ عَدُوٍّ نَّيْلًا إِلَّا كُتِبَ لَهُم بِهِ عَمَلٌ صَالِحٌ ۚ إِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ','سزاوار نیست که اهل مدینه، و بادیه‌نشینانی که اطراف آنها هستند، از رسول خدا تخلّف جویند؛ و برای حفظ جان خویش، از جان او چشم بپوشند! این بخاطر آن است که هیچ تشنگی و خستگی، و گرسنگی در راه خدا به آنها نمی‌رسد و هیچ گامی که موجب خشم کافران می‌شود برنمی‌دارند، و ضربه‌ای از دشمن نمی‌خورند، مگر اینکه به خاطر آن، عمل صالحی برای آنها نوشته می‌شود؛ زیرا خداوند پاداش نیکوکاران را تباه نمی‌کند!'];
ayat[1356]=[9,121,'وَلَا يُنفِقُونَ نَفَقَةً صَغِيرَةً وَلَا كَبِيرَةً وَلَا يَقْطَعُونَ وَادِيًا إِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا كَانُوا يَعْمَلُونَ','و هیچ مال کوچک یا بزرگی را (در این راه) انفاق نمی‌کنند، و هیچ سرزمینی را (بسوی میدان جهاد و یا در بازگشت) نمی‌پیمایند، مگر اینکه برای آنها نوشته می‌شود؛ تا خداوند آن را بعنوان بهترین اعمالشان، پاداش دهد.'];
ayat[1357]=[9,122,'۞ وَمَا كَانَ الْمُؤْمِنُونَ لِيَنفِرُوا كَافَّةً ۚ فَلَوْلَا نَفَرَ مِن كُلِّ فِرْقَةٍ مِّنْهُمْ طَائِفَةٌ لِّيَتَفَقَّهُوا فِي الدِّينِ وَلِيُنذِرُوا قَوْمَهُمْ إِذَا رَجَعُوا إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ','شایسته نیست مؤمنان همگی (بسوی میدان جهاد) کوچ کنند؛ چرا از هر گروهی از آنان، طایفه‌ای کوچ نمی‌کند (و طایفه‌ای در مدینه بماند)، تا در دین (و معارف و احکام اسلام) آگاهی یابند و به هنگام بازگشت بسوی قوم خود، آنها را بیم دهند؟! شاید (از مخالفت فرمان پروردگار) بترسند، و خودداری کنند!'];
ayat[1358]=[9,123,'يَا أَيُّهَا الَّذِينَ آمَنُوا قَاتِلُوا الَّذِينَ يَلُونَكُم مِّنَ الْكُفَّارِ وَلْيَجِدُوا فِيكُمْ غِلْظَةً ۚ وَاعْلَمُوا أَنَّ اللَّهَ مَعَ الْمُتَّقِينَ','ای کسانی که ایمان آورده‌اید! با کافرانی که به شما نزدیکترند، پیکار کنید! (و دشمن دورتر، شما را از دشمنان نزدیک غافل نکند!) آنها باید در شما شدّت و خشونت (و قدرت) احساس کنند؛ و بدانید خداوند با پرهیزگاران است!'];
ayat[1359]=[9,124,'وَإِذَا مَا أُنزِلَتْ سُورَةٌ فَمِنْهُم مَّن يَقُولُ أَيُّكُمْ زَادَتْهُ هَٰذِهِ إِيمَانًا ۚ فَأَمَّا الَّذِينَ آمَنُوا فَزَادَتْهُمْ إِيمَانًا وَهُمْ يَسْتَبْشِرُونَ','و هنگامی که سوره‌ای نازل می‌شود، بعضی از آنان (به دیگران) می‌گویند: «این سوره، ایمان کدام یک از شما را افزون ساخت؟!» (به آنها بگو:) اما کسانی که ایمان آورده‌اند، بر ایمانشان افزوده؛ و آنها (به فضل و رحمت الهی) خوشحالند.'];
ayat[1360]=[9,125,'وَأَمَّا الَّذِينَ فِي قُلُوبِهِم مَّرَضٌ فَزَادَتْهُمْ رِجْسًا إِلَىٰ رِجْسِهِمْ وَمَاتُوا وَهُمْ كَافِرُونَ','و امّا آنها که در دلهایشان بیماری است، پلیدی بر پلیدیشان افزوده؛ و از دنیا رفتند در حالی که کافر بودند.'];
ayat[1361]=[9,126,'أَوَلَا يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِي كُلِّ عَامٍ مَّرَّةً أَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمْ يَذَّكَّرُونَ','آیا آنها نمی‌بینند که در هر سال، یک یا دو بار آزمایش می‌شوند؟! باز توبه نمی‌کنند، و متذکّر هم نمی‌گردند!'];
ayat[1362]=[9,127,'وَإِذَا مَا أُنزِلَتْ سُورَةٌ نَّظَرَ بَعْضُهُمْ إِلَىٰ بَعْضٍ هَلْ يَرَاكُم مِّنْ أَحَدٍ ثُمَّ انصَرَفُوا ۚ صَرَفَ اللَّهُ قُلُوبَهُم بِأَنَّهُمْ قَوْمٌ لَّا يَفْقَهُونَ','و هنگامی که سوره‌ای نازل می‌شود، بعضی از آنها [= منافقان‌] به یکدیگر نگاه می‌کنند و می‌گویند: «آیا کسی شما را می‌بیند؟ (اگر از حضور پیامبر بیرون رویم، کسی متوجّه ما نمی‌شود!)» سپس منصرف می‌شوند (و بیرون می‌روند)؛ خداوند دلهایشان را (از حق) منصرف ساخته؛ چرا که آنها، گروهی هستند که نمی‌فهمند (و بی‌دانشند)!'];
ayat[1363]=[9,128,'لَقَدْ جَاءَكُمْ رَسُولٌ مِّنْ أَنفُسِكُمْ عَزِيزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيصٌ عَلَيْكُم بِالْمُؤْمِنِينَ رَءُوفٌ رَّحِيمٌ','به یقین، رسولی از خود شما بسویتان آمد که رنجهای شما بر او سخت است؛ و اصرار بر هدایت شما دارد؛ و نسبت به مؤمنان، رئوف و مهربان است!'];
ayat[1364]=[9,129,'فَإِن تَوَلَّوْا فَقُلْ حَسْبِيَ اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۖ عَلَيْهِ تَوَكَّلْتُ ۖ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيمِ','اگر آنها (از حق) روی بگردانند، (نگران مباش!) بگو: «خداوند مرا کفایت می‌کند؛ هیچ معبودی جز او نیست؛ بر او توکّل کردم؛ و او صاحب عرش بزرگ است!»'];
ayat[1365]=[10,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ تِلْكَ آيَاتُ الْكِتَابِ الْحَكِيمِ','الر، این آیات کتاب استوار و حکمت آمیز است!'];
ayat[1366]=[10,2,'أَكَانَ لِلنَّاسِ عَجَبًا أَنْ أَوْحَيْنَا إِلَىٰ رَجُلٍ مِّنْهُمْ أَنْ أَنذِرِ النَّاسَ وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِندَ رَبِّهِمْ ۗ قَالَ الْكَافِرُونَ إِنَّ هَٰذَا لَسَاحِرٌ مُّبِينٌ','آیا برای مردم، موجب شگفتی بود که به مردی از آنها وحی فرستادیم که مردم را (از عواقب کفر و گناه) بترسان، و به کسانی که ایمان آورده‌اند بشارت ده که برای آنها، سابقه نیک (و پاداشهای مسلّم) نزد پروردگارشان است؟! (امّا) کافران گفتند: «این مرد، ساحر آشکاری است!»'];
ayat[1367]=[10,3,'إِنَّ رَبَّكُمُ اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۖ يُدَبِّرُ الْأَمْرَ ۖ مَا مِن شَفِيعٍ إِلَّا مِن بَعْدِ إِذْنِهِ ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ فَاعْبُدُوهُ ۚ أَفَلَا تَذَكَّرُونَ','پروردگار شما، خداوندی است که آسمانها و زمین را در شش روز [= شش دوران‌] آفرید؛ سپس بر تخت (قدرت) قرار گرفت، و به تدبیر کار (جهان) پرداخت؛ هیچ شفاعت کننده‌ای، جز با اذن او نیست؛ این است خداوند، پروردگار شما! پس او را پرستش کنید! آیا متذکّر نمی‌شوید؟!'];
ayat[1368]=[10,4,'إِلَيْهِ مَرْجِعُكُمْ جَمِيعًا ۖ وَعْدَ اللَّهِ حَقًّا ۚ إِنَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِ ۚ وَالَّذِينَ كَفَرُوا لَهُمْ شَرَابٌ مِّنْ حَمِيمٍ وَعَذَابٌ أَلِيمٌ بِمَا كَانُوا يَكْفُرُونَ','بازگشت همه شما بسوی اوست! خداوند وعده حقّی فرموده؛ او آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، تا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند، بعدالت جزا دهد؛ و برای کسانی که کافر شدند، نوشیدنی از مایع سوزان است؛ و عذابی دردناک، بخاطر آنکه کفر می‌ورزیدند!'];
ayat[1369]=[10,5,'هُوَ الَّذِي جَعَلَ الشَّمْسَ ضِيَاءً وَالْقَمَرَ نُورًا وَقَدَّرَهُ مَنَازِلَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ ۚ مَا خَلَقَ اللَّهُ ذَٰلِكَ إِلَّا بِالْحَقِّ ۚ يُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ','او کسی است که خورشید را روشنایی، و ماه را نور قرار داد؛ و برای آن منزلگاه‌هایی مقدّر کرد، تا عدد سالها و حساب (کارها) را بدانید؛ خداوند این را جز بحق نیافریده؛ او آیات (خود را) برای گروهی که اهل دانشند، شرح می‌دهد!'];
ayat[1370]=[10,6,'إِنَّ فِي اخْتِلَافِ اللَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللَّهُ فِي السَّمَاوَاتِ وَالْأَرْضِ لَآيَاتٍ لِّقَوْمٍ يَتَّقُونَ','مسلّماً در آمد و شد شب و روز، و آنچه خداوند در آسمانها و زمین آفریده، آیات (و نشانه‌هایی) است برای گروهی که پرهیزگارند (و حقایق را می‌بینند).'];
ayat[1371]=[10,7,'إِنَّ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا وَرَضُوا بِالْحَيَاةِ الدُّنْيَا وَاطْمَأَنُّوا بِهَا وَالَّذِينَ هُمْ عَنْ آيَاتِنَا غَافِلُونَ','آنها که ایمان به ملاقات ما (و روز رستاخیز) ندارند، و به زندگی دنیا خشنود شدند و بر آن تکیه کردند، و آنها که از آیات ما غافلند،'];
ayat[1372]=[10,8,'أُولَٰئِكَ مَأْوَاهُمُ النَّارُ بِمَا كَانُوا يَكْسِبُونَ','(همه) آنها جایگاهشان آتش است، بخاطر کارهایی که انجام می‌دادند!'];
ayat[1373]=[10,9,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ يَهْدِيهِمْ رَبُّهُم بِإِيمَانِهِمْ ۖ تَجْرِي مِن تَحْتِهِمُ الْأَنْهَارُ فِي جَنَّاتِ النَّعِيمِ','(ولی) کسانی که ایمان آوردند و کارهای شایسته انجام دادند، پروردگارشان آنها را در پرتو ایمانشان هدایت می‌کند؛ از زیر (قصرهای) آنها در باغهای بهشت، نهرها جاری است.'];
ayat[1374]=[10,10,'دَعْوَاهُمْ فِيهَا سُبْحَانَكَ اللَّهُمَّ وَتَحِيَّتُهُمْ فِيهَا سَلَامٌ ۚ وَآخِرُ دَعْوَاهُمْ أَنِ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ','گفتار (و دعای) آنها در بهشت این است که: «خداوندا، منزهی تو!» و تحیّت آنها در آنجا: سلام؛ و آخرین سخنشان این است که: «حمد، مخصوص پروردگار عالمیان است!»'];
ayat[1375]=[10,11,'۞ وَلَوْ يُعَجِّلُ اللَّهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُم بِالْخَيْرِ لَقُضِيَ إِلَيْهِمْ أَجَلُهُمْ ۖ فَنَذَرُ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا فِي طُغْيَانِهِمْ يَعْمَهُونَ','اگر همان گونه که مردم در به دست آوردن «خوبی» ها عجله دارند، خداوند در مجازاتشان شتاب می‌کرد، (بزودی) عمرشان به پایان می‌رسید (و همگی نابود می‌شدند)؛ ولی کسانی را که ایمان به لقای ما ندارند، به حال خود رها می‌کنیم تا در طغیانشان سرگردان شوند!'];
ayat[1376]=[10,12,'وَإِذَا مَسَّ الْإِنسَانَ الضُّرُّ دَعَانَا لِجَنبِهِ أَوْ قَاعِدًا أَوْ قَائِمًا فَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهُ مَرَّ كَأَن لَّمْ يَدْعُنَا إِلَىٰ ضُرٍّ مَّسَّهُ ۚ كَذَٰلِكَ زُيِّنَ لِلْمُسْرِفِينَ مَا كَانُوا يَعْمَلُونَ','هنگامی که به انسان زیان (و ناراحتی) رسد، ما را (در هر حال:) در حالی که به پهلو خوابیده، یا نشسته، یا ایستاده است، می‌خواند؛ امّا هنگامی که ناراحتی را از او برطرف ساختیم، چنان می‌رود که گویی هرگز ما را برای حل مشکلی که به او رسیده بود، نخوانده است! این گونه برای اسرافکاران، اعمالشان زینت داده شده است (که زشتی این عمل را درک نمی‌کنند)!'];
ayat[1377]=[10,13,'وَلَقَدْ أَهْلَكْنَا الْقُرُونَ مِن قَبْلِكُمْ لَمَّا ظَلَمُوا ۙ وَجَاءَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ وَمَا كَانُوا لِيُؤْمِنُوا ۚ كَذَٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ','ما امّتهای پیش از شما را، هنگامی که ظلم کردند، هلاک نمودیم؛ در حالی که پیامبرانشان دلایل روشن برای آنها آوردند، ولی آنها ایمان نیاوردند؛ این‌گونه گروه مجرمان را کیفر می‌دهیم!'];
ayat[1378]=[10,14,'ثُمَّ جَعَلْنَاكُمْ خَلَائِفَ فِي الْأَرْضِ مِن بَعْدِهِمْ لِنَنظُرَ كَيْفَ تَعْمَلُونَ','سپس شما را جانشینان آنها در روی زمین -پس از ایشان- قرار دادیم؛ تا ببینیم شما چگونه عمل می‌کنید!'];
ayat[1379]=[10,15,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ ۙ قَالَ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا ائْتِ بِقُرْآنٍ غَيْرِ هَٰذَا أَوْ بَدِّلْهُ ۚ قُلْ مَا يَكُونُ لِي أَنْ أُبَدِّلَهُ مِن تِلْقَاءِ نَفْسِي ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّ ۖ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ','و هنگامی که آیات روشن ما بر آنها خوانده می‌شود، کسانی که ایمان به لقای ما (و روز رستاخیز) ندارند می‌گویند: «قرآنی غیر از این بیاور، یا آن را تبدیل کن! (و آیات نکوهش بتها را بردار)» بگو: «من حق ندارم که از پیش خود آن را تغییر دهم؛ فقط از چیزی که بر من وحی می‌شود، پیروی می‌کنم! من اگر پروردگارم را نافرمانی کنم، از مجازات روز بزرگ (قیامت) می‌ترسم!»'];
ayat[1380]=[10,16,'قُل لَّوْ شَاءَ اللَّهُ مَا تَلَوْتُهُ عَلَيْكُمْ وَلَا أَدْرَاكُم بِهِ ۖ فَقَدْ لَبِثْتُ فِيكُمْ عُمُرًا مِّن قَبْلِهِ ۚ أَفَلَا تَعْقِلُونَ','بگو: «اگر خدا می‌خواست، من این آیات را بر شما نمی‌خواندم؛ و خداوند از آن آگاهتان نمی‌کرد؛ چه اینکه مدّتها پیش از این، در میان شما زندگی نمودم؛ (و هرگز آیه‌ای نیاوردم؛) آیا نمی‌فهمید؟!»'];
ayat[1381]=[10,17,'فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ ۚ إِنَّهُ لَا يُفْلِحُ الْمُجْرِمُونَ','چه کسی ستمکارتر است از آن کس که بر خدا دروغ می‌بندد، یا آیات او را تکذیب می‌کند؟! مسلماً مجرمان رستگار نخواهند شد!'];
ayat[1382]=[10,18,'وَيَعْبُدُونَ مِن دُونِ اللَّهِ مَا لَا يَضُرُّهُمْ وَلَا يَنفَعُهُمْ وَيَقُولُونَ هَٰؤُلَاءِ شُفَعَاؤُنَا عِندَ اللَّهِ ۚ قُلْ أَتُنَبِّئُونَ اللَّهَ بِمَا لَا يَعْلَمُ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ','آنها غیر از خدا، چیزهایی را می‌پرستند که نه به آنان زیان می‌رساند، و نه سودی می‌بخشد؛ و می‌گویند: «اینها شفیعان ما نزد خدا هستند!» بگو: «آیا خدا را به چیزی خبر می‌دهید که در آسمانها و زمین سراغ ندارد؟!» منزه است او، و برتر است از آن همتایانی که قرار می‌دهند!'];
ayat[1383]=[10,19,'وَمَا كَانَ النَّاسُ إِلَّا أُمَّةً وَاحِدَةً فَاخْتَلَفُوا ۚ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ لَقُضِيَ بَيْنَهُمْ فِيمَا فِيهِ يَخْتَلِفُونَ','(در آغاز) همه مردم امّت واحدی بودند؛ سپس اختلاف کردند؛ و اگر فرمانی از طرف پروردگارت (درباره عدم مجازات سریع آنان) از قبل صادر نشده بود، در میان آنها در آنچه اختلاف داشتند داوری می‌شد (و سپس همگی به مجازات می‌رسیدند).'];
ayat[1384]=[10,20,'وَيَقُولُونَ لَوْلَا أُنزِلَ عَلَيْهِ آيَةٌ مِّن رَّبِّهِ ۖ فَقُلْ إِنَّمَا الْغَيْبُ لِلَّهِ فَانتَظِرُوا إِنِّي مَعَكُم مِّنَ الْمُنتَظِرِينَ','می‌گویند: «چرا معجزه‌ای از پروردگارش بر او نازل نمی‌شود؟!» بگو: «غیب (و معجزات) تنها برای خدا (و به فرمان او) است! شما در انتظار باشید، من هم با شما در انتظارم! (شما در انتظار معجزات بهانه‌جویانه باشید، و من هم در انتظار مجازات شما!)»'];
ayat[1385]=[10,21,'وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً مِّن بَعْدِ ضَرَّاءَ مَسَّتْهُمْ إِذَا لَهُم مَّكْرٌ فِي آيَاتِنَا ۚ قُلِ اللَّهُ أَسْرَعُ مَكْرًا ۚ إِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَ','هنگامی که به مردم، پس از ناراحتی که به آنها رسیده است، رحمتی بچشانیم، در آیات ما نیرنگ می‌کنند (، و برای آن نعمت و رحمت توجیهات ناروا می‌کنند)؛ بگو: «خداوند سریعتر از شما مکر [= چاره‌جویی‌] می‌کند؛ و رسولان [= فرشتگان] ما، آنچه نیرنگ می‌کنید (و نقشه می‌کشید)، می‌نویسند!»'];
ayat[1386]=[10,22,'هُوَ الَّذِي يُسَيِّرُكُمْ فِي الْبَرِّ وَالْبَحْرِ ۖ حَتَّىٰ إِذَا كُنتُمْ فِي الْفُلْكِ وَجَرَيْنَ بِهِم بِرِيحٍ طَيِّبَةٍ وَفَرِحُوا بِهَا جَاءَتْهَا رِيحٌ عَاصِفٌ وَجَاءَهُمُ الْمَوْجُ مِن كُلِّ مَكَانٍ وَظَنُّوا أَنَّهُمْ أُحِيطَ بِهِمْ ۙ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ لَئِنْ أَنجَيْتَنَا مِنْ هَٰذِهِ لَنَكُونَنَّ مِنَ الشَّاكِرِينَ','او کسی است که شما را در خشکی و دریا سیر می‌دهد؛ زمانی که در کشتی قرارمی‌گیرید، و بادهای موافق آنان را (بسوی مقصد) حرکت میدهد و خوشحال می‌شوند، ناگهان طوفان شدیدی می‌وزد؛ و امواج از هر سو به سراغ آنها می‌آید؛ و گمان می‌کنند هلاک خواهند شد؛ در آن هنگام، خدا را از روی اخلاص می‌خوانند که: «اگر ما را از این گرفتاری نجات دهی، حتماً از سپاسگزاران خواهیم بود!»'];
ayat[1387]=[10,23,'فَلَمَّا أَنجَاهُمْ إِذَا هُمْ يَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ ۗ يَا أَيُّهَا النَّاسُ إِنَّمَا بَغْيُكُمْ عَلَىٰ أَنفُسِكُم ۖ مَّتَاعَ الْحَيَاةِ الدُّنْيَا ۖ ثُمَّ إِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','امّا هنگامی که خدا آنها را رهایی بخشید، (باز) به ناحق، در زمین ستم میکنند. ای مردم! ستمهای شما، به زیان خود شماست! از زندگی دنیا بهره (میبرید)، سپس بازگشت شما بسوی ماست؛ و ما، شما را به آنچه عمل میکردید، خبر میدهیم!'];
ayat[1388]=[10,24,'إِنَّمَا مَثَلُ الْحَيَاةِ الدُّنْيَا كَمَاءٍ أَنزَلْنَاهُ مِنَ السَّمَاءِ فَاخْتَلَطَ بِهِ نَبَاتُ الْأَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْأَنْعَامُ حَتَّىٰ إِذَا أَخَذَتِ الْأَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ أَهْلُهَا أَنَّهُمْ قَادِرُونَ عَلَيْهَا أَتَاهَا أَمْرُنَا لَيْلًا أَوْ نَهَارًا فَجَعَلْنَاهَا حَصِيدًا كَأَن لَّمْ تَغْنَ بِالْأَمْسِ ۚ كَذَٰلِكَ نُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَتَفَكَّرُونَ','مثل زندگی دنیا، همانند آبی است که از آسمان نازل کرده‌ایم؛ که در پی آن، گیاهان (گوناگون) زمین -که مردم و چهارپایان از آن می‌خورند- می‌روید؛ تا زمانی که زمین، زیبایی خود را یافته و آراسته می‌گردد، و اهل آن مطمئن می‌شوند که می‌توانند از آن بهره‌مند گردند، (ناگهان) فرمان ما، شب‌هنگام یا در روز، (برای نابودی آن) فرامی‌رسد؛ (سرما یا صاعقه‌ای را بر آن مسلّط می‌سازیم؛) و آنچنان آن را درو می‌کنیم که گویی دیروز هرگز (چنین کشتزاری) نبوده است! این گونه، آیات خود را برای گروهی که می‌اندیشند، شرح می‌دهیم!'];
ayat[1389]=[10,25,'وَاللَّهُ يَدْعُو إِلَىٰ دَارِ السَّلَامِ وَيَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','و خداوند به سرای صلح و سلامت دعوت می‌کند؛ و هر کس را بخواهد (و شایسته و لایق ببیند)، به راه راست هدایت می‌نماید.'];
ayat[1390]=[10,26,'۞ لِّلَّذِينَ أَحْسَنُوا الْحُسْنَىٰ وَزِيَادَةٌ ۖ وَلَا يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَلَا ذِلَّةٌ ۚ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ','کسانی که نیکی کردند، پاداش نیک و افزون بر آن دارند؛ و تاریکی و ذلّت، چهره‌هایشان را نمی‌پوشاند؛ آنها اهل بهشتند، و جاودانه در آن خواهند ماند.'];
ayat[1391]=[10,27,'وَالَّذِينَ كَسَبُوا السَّيِّئَاتِ جَزَاءُ سَيِّئَةٍ بِمِثْلِهَا وَتَرْهَقُهُمْ ذِلَّةٌ ۖ مَّا لَهُم مِّنَ اللَّهِ مِنْ عَاصِمٍ ۖ كَأَنَّمَا أُغْشِيَتْ وُجُوهُهُمْ قِطَعًا مِّنَ اللَّيْلِ مُظْلِمًا ۚ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','امّا کسانی که مرتکب گناهان شدند، جزای بدی بمقدار آن دارند؛ و ذلّت و خواری، چهره آنان را می‌پوشاند؛ و هیچ چیز نمی‌تواند آنها را از (مجازات) خدا نگه دارد! (چهره‌هایشان آنچنان تاریک است که) گویی با پاره‌هایی از شب تاریک، صورت آنها پوشیده شده! آنها اهل دوزخند؛ و جاودانه در آن خواهند ماند!'];
ayat[1392]=[10,28,'وَيَوْمَ نَحْشُرُهُمْ جَمِيعًا ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا مَكَانَكُمْ أَنتُمْ وَشُرَكَاؤُكُمْ ۚ فَزَيَّلْنَا بَيْنَهُمْ ۖ وَقَالَ شُرَكَاؤُهُم مَّا كُنتُمْ إِيَّانَا تَعْبُدُونَ','(به خاطر بیاورید) روزی را که همه آنها را جمع می‌کنیم، سپس به مشرکان می‌گوییم: «شما و معبودهایتان در جای خودتان باشید (تا به حسابتان رسیدگی شود!)» سپس آنها را از هم جدا می‌سازیم (و از هر یک جداگانه سؤال می‌کنیم). و معبودهایشان (به آنها) می‌گویند: «شما (هرگز) ما را عبادت نمی‌کردید!»'];
ayat[1393]=[10,29,'فَكَفَىٰ بِاللَّهِ شَهِيدًا بَيْنَنَا وَبَيْنَكُمْ إِن كُنَّا عَنْ عِبَادَتِكُمْ لَغَافِلِينَ','(آنها در پاسخ می‌گویند:) همین بس که خدا میان ما و شما گواه باشد، اگر ما از عبادت شما غافل بودیم!'];
ayat[1394]=[10,30,'هُنَالِكَ تَبْلُو كُلُّ نَفْسٍ مَّا أَسْلَفَتْ ۚ وَرُدُّوا إِلَى اللَّهِ مَوْلَاهُمُ الْحَقِّ ۖ وَضَلَّ عَنْهُم مَّا كَانُوا يَفْتَرُونَ','در آن جا، هر کس عملی را که قبلاً انجام داده است، می‌آزماید. و همگی بسوی «اللّه» -مولا و سرپرستِ حقیقی خود- بازگردانده می‌شوند؛ و چیزهایی را که بدروغ همتای خدا قرار داده بودند، گم و نابود می‌شوند!'];
ayat[1395]=[10,31,'قُلْ مَن يَرْزُقُكُم مِّنَ السَّمَاءِ وَالْأَرْضِ أَمَّن يَمْلِكُ السَّمْعَ وَالْأَبْصَارَ وَمَن يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَن يُدَبِّرُ الْأَمْرَ ۚ فَسَيَقُولُونَ اللَّهُ ۚ فَقُلْ أَفَلَا تَتَّقُونَ','بگو: «چه کسی شما را از آسمان و زمین روزی می‌دهد؟ یا چه کسی مالک (و خالق) گوش و چشمهاست؟ و چه کسی زنده را از مرده، و مرده را از زنده بیرون می‌آورد؟ و چه کسی امور (جهان) را تدبیر می‌کند؟» بزودی (در پاسخ) می‌گویند: «خدا»، بگو: «پس چرا تقوا پیشه نمی‌کنید (و از خدا نمی‌ترسید)؟!'];
ayat[1396]=[10,32,'فَذَٰلِكُمُ اللَّهُ رَبُّكُمُ الْحَقُّ ۖ فَمَاذَا بَعْدَ الْحَقِّ إِلَّا الضَّلَالُ ۖ فَأَنَّىٰ تُصْرَفُونَ','آن است خداوند، پروردگار حقّ شما (دارای همه این صفات)! با این حال، بعد از حق، چه چیزی جز گمراهی وجود دارد؟! پس چرا (از پرستش او) روی گردان می‌شوید؟!'];
ayat[1397]=[10,33,'كَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِينَ فَسَقُوا أَنَّهُمْ لَا يُؤْمِنُونَ','اینچنین فرمان پروردگارت بر فاسقان مسلّم شده که آنها (پس از این همه لجاجت و گناه)، ایمان نخواهند آورد!'];
ayat[1398]=[10,34,'قُلْ هَلْ مِن شُرَكَائِكُم مَّن يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ۚ قُلِ اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ۖ فَأَنَّىٰ تُؤْفَكُونَ','بگو: «آیا هیچ یک از معبودهای شما، آفرینش را ایجاد می‌کند و سپس بازمی‌گرداند؟!» بگو: «تنها خدا آفرینش را ایجاد کرده، سپس بازمی‌گرداند؛ با این حال، چرا از حق روی‌گردان می‌شوید؟!»'];
ayat[1399]=[10,35,'قُلْ هَلْ مِن شُرَكَائِكُم مَّن يَهْدِي إِلَى الْحَقِّ ۚ قُلِ اللَّهُ يَهْدِي لِلْحَقِّ ۗ أَفَمَن يَهْدِي إِلَى الْحَقِّ أَحَقُّ أَن يُتَّبَعَ أَمَّن لَّا يَهِدِّي إِلَّا أَن يُهْدَىٰ ۖ فَمَا لَكُمْ كَيْفَ تَحْكُمُونَ','بگو: «آیا هیچ یک از معبودهای شما، به سوی حق هدایت می‌کند؟! بگو: «تنها خدا به حق هدایت می‌کند! آیا کسی که هدایت به سوی حق می‌کند برای پیروی شایسته‌تر است، یا آن کس که خود هدایت نمی‌شود مگر هدایتش کنند؟ شما را چه می‌شود، چگونه داوری می‌کنید؟!»'];
ayat[1400]=[10,36,'وَمَا يَتَّبِعُ أَكْثَرُهُمْ إِلَّا ظَنًّا ۚ إِنَّ الظَّنَّ لَا يُغْنِي مِنَ الْحَقِّ شَيْئًا ۚ إِنَّ اللَّهَ عَلِيمٌ بِمَا يَفْعَلُونَ','و بیشتر آنها، جز از گمان (و پندارهای بی‌اساس)، پیروی نمی‌کنند؛ (در حالی که) گمان، هرگز انسان را از حقّ بی‌نیاز نمی‌سازد (و به حق نمی‌رساند)! به یقین، خداوند از آنچه انجام می‌دهند، آگاه است!'];
ayat[1401]=[10,37,'وَمَا كَانَ هَٰذَا الْقُرْآنُ أَن يُفْتَرَىٰ مِن دُونِ اللَّهِ وَلَٰكِن تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَتَفْصِيلَ الْكِتَابِ لَا رَيْبَ فِيهِ مِن رَّبِّ الْعَالَمِينَ','شایسته نبود (و امکان نداشت) که این قرآن، بدون وحی الهی به خدا نسبت داده شود؛ ولی تصدیقی است برای آنچه پیش از آن است (از کتب آسمانی)، و شرح و تفصیلی بر آنها است؛ شکّی در آن نیست، و از طرف پروردگار جهانیان است.'];
ayat[1402]=[10,38,'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ فَأْتُوا بِسُورَةٍ مِّثْلِهِ وَادْعُوا مَنِ اسْتَطَعْتُم مِّن دُونِ اللَّهِ إِن كُنتُمْ صَادِقِينَ','آیا آنها می‌گویند: «او قرآن را بدروغ به خدا نسبت داده است»؟! بگو: «اگر راست می گویید، یک سوره همانند آن بیاورید؛ و غیر از خدا، هر کس را می‌توانید (به یاری) طلبید!»'];
ayat[1403]=[10,39,'بَلْ كَذَّبُوا بِمَا لَمْ يُحِيطُوا بِعِلْمِهِ وَلَمَّا يَأْتِهِمْ تَأْوِيلُهُ ۚ كَذَٰلِكَ كَذَّبَ الَّذِينَ مِن قَبْلِهِمْ ۖ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِمِينَ','(ولی آنها از روی علم و دانش قرآن را انکار نکردند؛) بلکه چیزی را تکذیب کردند که آگاهی از آن نداشتند، و هنوز واقعیتش بر آنان روشن نشده است! پیشینیان آنها نیز همین گونه تکذیب کردند؛ پس بنگر عاقبت کار ظالمان چگونه بود!'];
ayat[1404]=[10,40,'وَمِنْهُم مَّن يُؤْمِنُ بِهِ وَمِنْهُم مَّن لَّا يُؤْمِنُ بِهِ ۚ وَرَبُّكَ أَعْلَمُ بِالْمُفْسِدِينَ','بعضی از آنها، به آن ایمان می‌آورند؛ و بعضی ایمان نمی‌آورند؛ و پروردگارت به مفسدان آگاهتر است (و آنها را بهتر می‌شناسد)!'];
ayat[1405]=[10,41,'وَإِن كَذَّبُوكَ فَقُل لِّي عَمَلِي وَلَكُمْ عَمَلُكُمْ ۖ أَنتُم بَرِيئُونَ مِمَّا أَعْمَلُ وَأَنَا بَرِيءٌ مِّمَّا تَعْمَلُونَ','و اگر تو را تکذیب کردند، بگو: «عمل من برای من، و عمل شما برای شماست! شما از آنچه من انجام می‌دهم بیزارید و من (نیز) از آنچه شماانجام می‌دهید بیزارم!»'];
ayat[1406]=[10,42,'وَمِنْهُم مَّن يَسْتَمِعُونَ إِلَيْكَ ۚ أَفَأَنتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوا لَا يَعْقِلُونَ','گروهی از آنان، بسوی تو گوش فرامی‌دهند (؛ امّا گویی هیچ نمی‌شنوند و کرند)! آیا تو می‌توانی سخن خود را به گوش کران برسانی، هر چند نفهمند؟!'];
ayat[1407]=[10,43,'وَمِنْهُم مَّن يَنظُرُ إِلَيْكَ ۚ أَفَأَنتَ تَهْدِي الْعُمْيَ وَلَوْ كَانُوا لَا يُبْصِرُونَ','و گروهی از آنان، به سوی تو می‌نگرند (امّا گویی هیچ نمی‌بینند)! آیا تو می‌توانی نابینایان را هدایت کنی، هر چند نبینند؟!'];
ayat[1408]=[10,44,'إِنَّ اللَّهَ لَا يَظْلِمُ النَّاسَ شَيْئًا وَلَٰكِنَّ النَّاسَ أَنفُسَهُمْ يَظْلِمُونَ','خداوند هیچ به مردم ستم نمی‌کند؛ ولی این مردمند که به خویشتن ستم می‌کند!'];
ayat[1409]=[10,45,'وَيَوْمَ يَحْشُرُهُمْ كَأَن لَّمْ يَلْبَثُوا إِلَّا سَاعَةً مِّنَ النَّهَارِ يَتَعَارَفُونَ بَيْنَهُمْ ۚ قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقَاءِ اللَّهِ وَمَا كَانُوا مُهْتَدِينَ','(به یاد آور) روزی را که (خداوند) آنها را جمع (و محشور) می‌سازد؛ آنچنان که (احساس می‌کنند) گویی جز ساعتی از روز، (در دنیا) توقّف نکردند؛ به آن مقدار که یکدیگر را (ببینند و) بشناسند! مسلّماً آنها که لقای خداوند (و روز رستاخیز) را تکذیب کردند، زیان بردند و هدایت نیافتند!'];
ayat[1410]=[10,46,'وَإِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللَّهُ شَهِيدٌ عَلَىٰ مَا يَفْعَلُونَ','اگر ما، پاره‌ای از مجازاتهایی را که به آنها وعده داده‌ایم، (در حال حیات تو (به تو نشان دهیم، و یا) پیش از آنکه گرفتار عذاب شوند،) تو را از دنیا ببریم، در هر حال، بازگشتشان به سوی ماست؛ سپس خداوند بر آنچه آنها انجام می‌دادند گواه است!'];
ayat[1411]=[10,47,'وَلِكُلِّ أُمَّةٍ رَّسُولٌ ۖ فَإِذَا جَاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُم بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَ','برای هر امّتی، رسولی است؛ هنگامی که رسولشان به سوی آنان بیاید، بعدالت در میان آنها داوری می‌شود؛ و ستمی به آنها نخواهد شد!'];
ayat[1412]=[10,48,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِن كُنتُمْ صَادِقِينَ','و می‌گویند: «اگر راست می‌گوئی، این وعده (مجازات) کی خواهد بود؟»'];
ayat[1413]=[10,49,'قُل لَّا أَمْلِكُ لِنَفْسِي ضَرًّا وَلَا نَفْعًا إِلَّا مَا شَاءَ اللَّهُ ۗ لِكُلِّ أُمَّةٍ أَجَلٌ ۚ إِذَا جَاءَ أَجَلُهُمْ فَلَا يَسْتَأْخِرُونَ سَاعَةً ۖ وَلَا يَسْتَقْدِمُونَ','بگو: «من (حتّی) برای خودم زیان و سودی را مالک نیستم، (تا چه رسد برای شما!) مگر آنچه خدا بخواهد. (این مقدار می‌دانم که) برای هر قوم و ملّتی، سرآمدی است؛ هنگامی که اجل آنها فرا رسد، (و فرمان مجازات یا مرگشان صادر شود،) نه ساعتی تأخیر می‌کنند، و نه پیشی می‌گیرند!'];
ayat[1414]=[10,50,'قُلْ أَرَأَيْتُمْ إِنْ أَتَاكُمْ عَذَابُهُ بَيَاتًا أَوْ نَهَارًا مَّاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ','بگو: «اگر مجازات او، شب‌هنگام یا در روز به سراغ شما آید (، آیا می‌توانید آن را از خود دفع کنید؟!)» پس مجرمان برای چه عجله می‌کنند؟!'];
ayat[1415]=[10,51,'أَثُمَّ إِذَا مَا وَقَعَ آمَنتُم بِهِ ۚ آلْآنَ وَقَدْ كُنتُم بِهِ تَسْتَعْجِلُونَ','یا اینکه آنگاه که واقع شد، به آن ایمان می‌آورید! (به شما گفته می‌شود:) حالا؟! در حالی که قبلاً برای آن عجله می‌کردید! (ولی اکنون چه سود!)'];
ayat[1416]=[10,52,'ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ الْخُلْدِ هَلْ تُجْزَوْنَ إِلَّا بِمَا كُنتُمْ تَكْسِبُونَ','سپس به کسانی که ستم کردند گفته می‌شود: عذاب ابدی را بچشید! آیا جز به آنچه انجام می‌دادید کیفر داده می‌شوید؟!»'];
ayat[1417]=[10,53,'۞ وَيَسْتَنبِئُونَكَ أَحَقٌّ هُوَ ۖ قُلْ إِي وَرَبِّي إِنَّهُ لَحَقٌّ ۖ وَمَا أَنتُم بِمُعْجِزِينَ','از تو می‌پرسند: «آیا آن (وعده مجازات الهی) حقّ است؟» بگو: «آری، به پروردگارم سوگند، قطعاً حقّ است؛ و شما نمی‌توانید از آن جلوگیری کنید!»'];
ayat[1418]=[10,54,'وَلَوْ أَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِي الْأَرْضِ لَافْتَدَتْ بِهِ ۗ وَأَسَرُّوا النَّدَامَةَ لَمَّا رَأَوُا الْعَذَابَ ۖ وَقُضِيَ بَيْنَهُم بِالْقِسْطِ ۚ وَهُمْ لَا يُظْلَمُونَ','و هر کس که ستم کرده، اگر تمامی آنچه روی زمین است در اختیار داشته باشد، (همه را از هول عذاب،) برای نجات خویش می‌دهد! و هنگامی که عذاب را ببینند، (پشیمان می‌شوند؛ امّا) پشیمانی خود را کتمان می‌کنند (، مبادا رسواتر شوند)! و در میان آنها، بعدالت داوری می‌شود؛ و ستمی بر آنها نخواهد شد!'];
ayat[1419]=[10,55,'أَلَا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۗ أَلَا إِنَّ وَعْدَ اللَّهِ حَقٌّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','آگاه باشید آنچه در آسمانها و زمین است، از آن خداست! آگاه باشید وعده خدا حقّ است، ولی بیشتر آنها نمی‌دانند!'];
ayat[1420]=[10,56,'هُوَ يُحْيِي وَيُمِيتُ وَإِلَيْهِ تُرْجَعُونَ','اوست که زنده می‌کند و می‌میراند، و به سوی او بازگردانده می‌شوید!'];
ayat[1421]=[10,57,'يَا أَيُّهَا النَّاسُ قَدْ جَاءَتْكُم مَّوْعِظَةٌ مِّن رَّبِّكُمْ وَشِفَاءٌ لِّمَا فِي الصُّدُورِ وَهُدًى وَرَحْمَةٌ لِّلْمُؤْمِنِينَ','ای مردم! اندرزی از سوی پروردگارتان برای شما آمده است؛ و درمانی برای آنچه در سینه‌هاست؛ (درمانی برای دلهای شما؛) و هدایت و رحمتی است برای مؤمنان!'];
ayat[1422]=[10,58,'قُلْ بِفَضْلِ اللَّهِ وَبِرَحْمَتِهِ فَبِذَٰلِكَ فَلْيَفْرَحُوا هُوَ خَيْرٌ مِّمَّا يَجْمَعُونَ','بگو: «به فضل و رحمت خدا باید خوشحال شوند؛ که این، از تمام آنچه گردآوری کرده‌اند، بهتر است!»'];
ayat[1423]=[10,59,'قُلْ أَرَأَيْتُم مَّا أَنزَلَ اللَّهُ لَكُم مِّن رِّزْقٍ فَجَعَلْتُم مِّنْهُ حَرَامًا وَحَلَالًا قُلْ آللَّهُ أَذِنَ لَكُمْ ۖ أَمْ عَلَى اللَّهِ تَفْتَرُونَ','بگو: «آیا روزیهایی را که خداوند بر شما نازل کرده دیده‌اید، که بعضی از آن را حلال، و بعضی را حرام نموده‌اید؟!» بگو: «آیا خداوند به شما اجازه داده، یا بر خدا افترا می بندید (و از پیش خود، حلال و حرام می‌کنید؟!)»'];
ayat[1424]=[10,60,'وَمَا ظَنُّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ يَوْمَ الْقِيَامَةِ ۗ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَ','آنها که بر خدا افترا می‌بندند، درباره (مجازات) روز رستاخیز، چه می‌اندیشند؟! خداوند نسبت به همه مردم فضل (و بخشش) دارد، امّا اکثر آنها سپاسگزاری نمی‌کنند!'];
ayat[1425]=[10,61,'وَمَا تَكُونُ فِي شَأْنٍ وَمَا تَتْلُو مِنْهُ مِن قُرْآنٍ وَلَا تَعْمَلُونَ مِنْ عَمَلٍ إِلَّا كُنَّا عَلَيْكُمْ شُهُودًا إِذْ تُفِيضُونَ فِيهِ ۚ وَمَا يَعْزُبُ عَن رَّبِّكَ مِن مِّثْقَالِ ذَرَّةٍ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ وَلَا أَصْغَرَ مِن ذَٰلِكَ وَلَا أَكْبَرَ إِلَّا فِي كِتَابٍ مُّبِينٍ','در هیچ حال (و اندیشه‌ای) نیستی، و هیچ قسمتی از قرآن را تلاوت نمی‌کنی، و هیچ عملی را انجام نمی‌دهید، مگر اینکه ما گواه بر شما هستیم در آن هنگام که وارد آن می شوید! و هیچ چیز در زمین و آسمان، از پروردگار تو مخفی نمی‌ماند؛ حتّی به اندازه سنگینی ذرّه‌ای، و نه کوچکتر از آن و نه بزرگتر، مگر اینکه (همه آنها) در کتاب آشکار (و لوح محفوظ علم خداوند) ثبت است!'];
ayat[1426]=[10,62,'أَلَا إِنَّ أَوْلِيَاءَ اللَّهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','آگاه باشید (دوستان و) اولیای خدا، نه ترسی دارند و نه غمگین می‌شوند!'];
ayat[1427]=[10,63,'الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ','همانها که ایمان آوردند، و (از مخالفت فرمان خدا) پرهیز می‌کردند.'];
ayat[1428]=[10,64,'لَهُمُ الْبُشْرَىٰ فِي الْحَيَاةِ الدُّنْيَا وَفِي الْآخِرَةِ ۚ لَا تَبْدِيلَ لِكَلِمَاتِ اللَّهِ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','در زندگی دنیا و در آخرت، شاد (و مسرور) ند؛ وعده‌های الهی تخلّف ناپذیر است! این است آن رستگاری بزرگ!'];
ayat[1429]=[10,65,'وَلَا يَحْزُنكَ قَوْلُهُمْ ۘ إِنَّ الْعِزَّةَ لِلَّهِ جَمِيعًا ۚ هُوَ السَّمِيعُ الْعَلِيمُ','سخن آنها تو را غمگین نسازد! تمام عزّت (و قدرت)، از آن خداست؛ و او شنوا و داناست!'];
ayat[1430]=[10,66,'أَلَا إِنَّ لِلَّهِ مَن فِي السَّمَاوَاتِ وَمَن فِي الْأَرْضِ ۗ وَمَا يَتَّبِعُ الَّذِينَ يَدْعُونَ مِن دُونِ اللَّهِ شُرَكَاءَ ۚ إِن يَتَّبِعُونَ إِلَّا الظَّنَّ وَإِنْ هُمْ إِلَّا يَخْرُصُونَ','آگاه باشید تمام کسانی که در آسمانها و زمین هستند، از آن خدا می‌باشند! و آنها که غیر خدا را همتای او می‌خوانند، (از منطق و دلیلی) پیروی نمی‌کنند؛ آنها فقط از پندار بی اساس پیروی می‌کنند؛ و آنها فقط دروغ می‌گویند!'];
ayat[1431]=[10,67,'هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَسْمَعُونَ','او کسی است که شب را برای شما آفرید، تا در آن آرامش بیابید؛ و روز را روشنی بخش (تا به تلاش زندگی پردازید) در اینها نشانه‌هایی است برای کسانی که گوش شنوا دارند!'];
ayat[1432]=[10,68,'قَالُوا اتَّخَذَ اللَّهُ وَلَدًا ۗ سُبْحَانَهُ ۖ هُوَ الْغَنِيُّ ۖ لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ إِنْ عِندَكُم مِّن سُلْطَانٍ بِهَٰذَا ۚ أَتَقُولُونَ عَلَى اللَّهِ مَا لَا تَعْلَمُونَ','گفتند: «خداوند فرزندی برای خود انتخاب کرده است»! (از هر عیب و نقص و احتیاجی) منزه است! او بی‌نیاز است! از آن اوست آنچه در آسمانها و آنچه در زمین است! شما هیچ‌گونه دلیلی بر این ادعا ندارید! آیا به خدا نسبتی می‌دهید که نمی‌دانید؟!'];
ayat[1433]=[10,69,'قُلْ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لَا يُفْلِحُونَ','بگو: «آنها که به خدا دروغ می‌بندند، (هرگز) رستگار نمی‌شوند!'];
ayat[1434]=[10,70,'مَتَاعٌ فِي الدُّنْيَا ثُمَّ إِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيقُهُمُ الْعَذَابَ الشَّدِيدَ بِمَا كَانُوا يَكْفُرُونَ','بهره‌ای (ناچیز) از دنیا دارند؛ سپس بازگشتشان بسوی ماست؛ و بعد، به آنها مجازات شدید به سزای کفرشان می‌چشانیم!'];
ayat[1435]=[10,71,'۞ وَاتْلُ عَلَيْهِمْ نَبَأَ نُوحٍ إِذْ قَالَ لِقَوْمِهِ يَا قَوْمِ إِن كَانَ كَبُرَ عَلَيْكُم مَّقَامِي وَتَذْكِيرِي بِآيَاتِ اللَّهِ فَعَلَى اللَّهِ تَوَكَّلْتُ فَأَجْمِعُوا أَمْرَكُمْ وَشُرَكَاءَكُمْ ثُمَّ لَا يَكُنْ أَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوا إِلَيَّ وَلَا تُنظِرُونِ','سرگذشت نوح را بر آنها بخوان! در آن هنگام که به قوم خود گفت: «ای قوم من! اگر تذکّرات من نسبت به آیات الهی، بر شما سنگین (و غیر قابل تحمّل) است، (هر کار از دستتان ساخته است بکنید.) من بر خدا توکّل کرده‌ام! فکر خود، و قدرت معبودهایتان را جمع کنید؛ سپس هیچ چیز بر شما پوشیده نماند؛ (تمام جوانب کارتان را بنگرید؛) سپس به حیات من پایان دهید، و (لحظه‌ای) مهلتم ندهید! (امّا توانایی ندارید!)'];
ayat[1436]=[10,72,'فَإِن تَوَلَّيْتُمْ فَمَا سَأَلْتُكُم مِّنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَى اللَّهِ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ','و اگر از قبول دعوتم روی بگردانید، (کار نادرستی کرده‌اید؛ چه اینکه) من از شما مزدی نمی‌خواهم؛ مزد من، تنها بر خداست! و من مأمورم که از مسلمین [= تسلیم شدگان در برابر فرمان خدا] باشم!»'];
ayat[1437]=[10,73,'فَكَذَّبُوهُ فَنَجَّيْنَاهُ وَمَن مَّعَهُ فِي الْفُلْكِ وَجَعَلْنَاهُمْ خَلَائِفَ وَأَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۖ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنذَرِينَ','امّا آنها او را تکذیب کردند! و ما، او و کسانی را که با او در کشتی بودند، نجات دادیم؛ و آنان را جانشین (و وارث کافران) قرار دادیم؛ و کسانی را که آیات ما را تکذیب کردند، غرق نمودیم! پس ببین عاقبت کار کسانی که انذار شدند (و به انذار الهی اهمیّت ندادند)، چگونه بود!'];
ayat[1438]=[10,74,'ثُمَّ بَعَثْنَا مِن بَعْدِهِ رُسُلًا إِلَىٰ قَوْمِهِمْ فَجَاءُوهُم بِالْبَيِّنَاتِ فَمَا كَانُوا لِيُؤْمِنُوا بِمَا كَذَّبُوا بِهِ مِن قَبْلُ ۚ كَذَٰلِكَ نَطْبَعُ عَلَىٰ قُلُوبِ الْمُعْتَدِينَ','سپس بعد از نوح، رسولانی به سوی قومشان فرستادیم؛ آنان دلایل روشن برایشان آوردند؛ امّا آنها، به چیزی که پیش از آن تکذیب کرده بودند، ایمان نیاوردند! اینچنین بر دلهای تجاوزکاران مهر می‌نهیم (تا چیزی را درک نکنند)!'];
ayat[1439]=[10,75,'ثُمَّ بَعَثْنَا مِن بَعْدِهِم مُّوسَىٰ وَهَارُونَ إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ بِآيَاتِنَا فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا مُّجْرِمِينَ','بعد از آنها، موسی و هارون را با آیات خود به سوی فرعون و اطرافیانش فرستادیم؛ اما آنها تکبّر کردند (و زیر بار حق نرفتند؛ چرا که) آنها گروهی مجرم بودند!'];
ayat[1440]=[10,76,'فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِندِنَا قَالُوا إِنَّ هَٰذَا لَسِحْرٌ مُّبِينٌ','و هنگامی که حق از نزد ما بسراغ آنها آمد، گفتند: «این، سحری است آشکار!»'];
ayat[1441]=[10,77,'قَالَ مُوسَىٰ أَتَقُولُونَ لِلْحَقِّ لَمَّا جَاءَكُمْ ۖ أَسِحْرٌ هَٰذَا وَلَا يُفْلِحُ السَّاحِرُونَ','موسی گفت: «آیا درباره حقّ، هنگامی که به سوی شما آمد، (چنین) می‌گویید؟! آیا این سحر است؟! در حالی که ساحران (هرگز) رستگار (و پیروز) نمی‌شوند!'];
ayat[1442]=[10,78,'قَالُوا أَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ آبَاءَنَا وَتَكُونَ لَكُمَا الْكِبْرِيَاءُ فِي الْأَرْضِ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِينَ','گفتند: «آیا آمده‌ای که ما را، از آنچه پدرانمان را بر آن یافتیم، منصرف سازی؛ و بزرگی (و ریاست) در روی زمین، از آن شما دو تن باشد؟! ما (هرگز) به شما ایمان نمی‌آوریم!»'];
ayat[1443]=[10,79,'وَقَالَ فِرْعَوْنُ ائْتُونِي بِكُلِّ سَاحِرٍ عَلِيمٍ','فرعون گفت: «(بروید و) هر جادوگر (و ساحر) دانایی را نزد من آورید!»'];
ayat[1444]=[10,80,'فَلَمَّا جَاءَ السَّحَرَةُ قَالَ لَهُم مُّوسَىٰ أَلْقُوا مَا أَنتُم مُّلْقُونَ','هنگامی که ساحران (به میدان مبارزه) آمدند، موسی به آنها گفت: «آنچه (از وسایل سحر) را می‌توانید بیفکنید، بیفکنید!»'];
ayat[1445]=[10,81,'فَلَمَّا أَلْقَوْا قَالَ مُوسَىٰ مَا جِئْتُم بِهِ السِّحْرُ ۖ إِنَّ اللَّهَ سَيُبْطِلُهُ ۖ إِنَّ اللَّهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِدِينَ','هنگامی که افکندند، موسی گفت: «آنچه شما آوردید، سحر است؛ که خداوند بزودی آن را باطل می‌کند؛ چرا که خداوند (هرگز) عمل مفسدان را اصلاح نمی‌کند!'];
ayat[1446]=[10,82,'وَيُحِقُّ اللَّهُ الْحَقَّ بِكَلِمَاتِهِ وَلَوْ كَرِهَ الْمُجْرِمُونَ','او حق را به وعده خویش، تحقق می‌بخشد؛ هر چند مجرمان کراهت داشته باشند!»'];
ayat[1447]=[10,83,'فَمَا آمَنَ لِمُوسَىٰ إِلَّا ذُرِّيَّةٌ مِّن قَوْمِهِ عَلَىٰ خَوْفٍ مِّن فِرْعَوْنَ وَمَلَئِهِمْ أَن يَفْتِنَهُمْ ۚ وَإِنَّ فِرْعَوْنَ لَعَالٍ فِي الْأَرْضِ وَإِنَّهُ لَمِنَ الْمُسْرِفِينَ','(در آغاز،) هیچ کس به موسی ایمان نیاورد، مگر گروهی از فرزندان قوم او؛ (آن هم) با ترس از فرعون و اطرافیانش، مبادا آنها را شکنجه کنند؛ زیرا فرعون، برتری‌جویی در زمین داشت؛ و از اسرافکاران بود!'];
ayat[1448]=[10,84,'وَقَالَ مُوسَىٰ يَا قَوْمِ إِن كُنتُمْ آمَنتُم بِاللَّهِ فَعَلَيْهِ تَوَكَّلُوا إِن كُنتُم مُّسْلِمِينَ','موسی گفت: «ای قوم من! اگر شما به خدا ایمان آورده‌اید، بر او توکّل کنید اگر تسلیم فرمان او هستید!»'];
ayat[1449]=[10,85,'فَقَالُوا عَلَى اللَّهِ تَوَكَّلْنَا رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلْقَوْمِ الظَّالِمِينَ','گفتند: «تنها بر خدا توکل داریم؛ پروردگارا! ما را مورد شکنجه گروه ستمگر قرار مده!'];
ayat[1450]=[10,86,'وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكَافِرِينَ','و ما را با رحمتت از (دست) قوم کافر رهایی بخش!'];
ayat[1451]=[10,87,'وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ وَأَخِيهِ أَن تَبَوَّآ لِقَوْمِكُمَا بِمِصْرَ بُيُوتًا وَاجْعَلُوا بُيُوتَكُمْ قِبْلَةً وَأَقِيمُوا الصَّلَاةَ ۗ وَبَشِّرِ الْمُؤْمِنِينَ','و به موسی و برادرش وحی کردیم که: «برای قوم خود، خانه‌هایی در سرزمین مصر انتخاب کنید؛ و خانه‌هایتان را مقابل یکدیگر (و متمرکز) قرار دهید! و نماز را برپا دارید! و به مؤمنان بشارت ده (که سرانجام پیروز می‌شوند!)»'];
ayat[1452]=[10,88,'وَقَالَ مُوسَىٰ رَبَّنَا إِنَّكَ آتَيْتَ فِرْعَوْنَ وَمَلَأَهُ زِينَةً وَأَمْوَالًا فِي الْحَيَاةِ الدُّنْيَا رَبَّنَا لِيُضِلُّوا عَن سَبِيلِكَ ۖ رَبَّنَا اطْمِسْ عَلَىٰ أَمْوَالِهِمْ وَاشْدُدْ عَلَىٰ قُلُوبِهِمْ فَلَا يُؤْمِنُوا حَتَّىٰ يَرَوُا الْعَذَابَ الْأَلِيمَ','موسی گفت: «پروردگارا! تو فرعون و اطرافیانش را زینت و اموالی (سرشار) در زندگی دنیا داده‌ای، پروردگارا! در نتیجه (بندگانت را) از راه تو گمراه می‌سازند! پروردگارا! اموالشان را نابود کن! و (بجرم گناهانشان،) دلهایشان را سخت و سنگین ساز، به گونه‌ای که ایمان نیاورند تا عذاب دردناک را ببینند!»'];
ayat[1453]=[10,89,'قَالَ قَدْ أُجِيبَت دَّعْوَتُكُمَا فَاسْتَقِيمَا وَلَا تَتَّبِعَانِّ سَبِيلَ الَّذِينَ لَا يَعْلَمُونَ','فرمود: «دعای شما پذیرفته شد! استقامت به خرج دهید؛ و از راه (و رسم) کسانی که نمی‌دانند، تبعیت نکنید!»'];
ayat[1454]=[10,90,'۞ وَجَاوَزْنَا بِبَنِي إِسْرَائِيلَ الْبَحْرَ فَأَتْبَعَهُمْ فِرْعَوْنُ وَجُنُودُهُ بَغْيًا وَعَدْوًا ۖ حَتَّىٰ إِذَا أَدْرَكَهُ الْغَرَقُ قَالَ آمَنتُ أَنَّهُ لَا إِلَٰهَ إِلَّا الَّذِي آمَنَتْ بِهِ بَنُو إِسْرَائِيلَ وَأَنَا مِنَ الْمُسْلِمِينَ','(سرانجام) بنی اسرائیل را از دریا [= رود عظیم نیل‌] عبور دادیم؛ و فرعون و لشکرش از سر ظلم و تجاوز، به دنبال آنها رفتند؛ هنگامی که غرقاب دامن او را گرفت، گفت: «ایمان آوردم که هیچ معبودی، جز کسی که بنی اسرائیل به او ایمان آورده‌اند، وجود ندارد؛ و من از مسلمین هستم!»'];
ayat[1455]=[10,91,'آلْآنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنتَ مِنَ الْمُفْسِدِينَ','(امّا به او خطاب شد:) الآن؟!! در حالی که قبلاً عصیان کردی، و از مفسدان بودی!'];
ayat[1456]=[10,92,'فَالْيَوْمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ آيَةً ۚ وَإِنَّ كَثِيرًا مِّنَ النَّاسِ عَنْ آيَاتِنَا لَغَافِلُونَ','ولی امروز، بدنت را (از آب) نجات می‌دهیم، تا عبرتی برای آیندگان باشی! و بسیاری از مردم، از آیات ما غافلند!'];
ayat[1457]=[10,93,'وَلَقَدْ بَوَّأْنَا بَنِي إِسْرَائِيلَ مُبَوَّأَ صِدْقٍ وَرَزَقْنَاهُم مِّنَ الطَّيِّبَاتِ فَمَا اخْتَلَفُوا حَتَّىٰ جَاءَهُمُ الْعِلْمُ ۚ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ','(سپس) بنی اسرائیل را در جایگاه صدق (و راستی) منزل دادیم؛ و از روزیهای پاکیزه به آنها عطا کردیم؛ (امّا آنها به نزاع و اختلاف برخاستند!) و اختلاف نکردند، مگر بعد از آنکه علم و آگاهی به سراغشان آمد! پروردگار تو روز قیامت، در آنچه اختلاف می‌کردند، میان آنها داوری می‌کند!'];
ayat[1458]=[10,94,'فَإِن كُنتَ فِي شَكٍّ مِّمَّا أَنزَلْنَا إِلَيْكَ فَاسْأَلِ الَّذِينَ يَقْرَءُونَ الْكِتَابَ مِن قَبْلِكَ ۚ لَقَدْ جَاءَكَ الْحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ','و اگر در آنچه بر تو نازل کرده‌ایم تردیدی داری، از کسانی که پیش از تو کتاب آسمانی را می‌خواندند بپرس، به یقین، «حق» از طرف پروردگارت به تو رسیده است؛ بنابر این، هرگز از تردیدکنندگان مباش! [مسلماً او تردیدی نداشت! این درسی برای مردم بود!]'];
ayat[1459]=[10,95,'وَلَا تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ فَتَكُونَ مِنَ الْخَاسِرِينَ','و از آنها مباش که آیات خدا را تکذیب کردند، که از زیانکاران خواهی بود!'];
ayat[1460]=[10,96,'إِنَّ الَّذِينَ حَقَّتْ عَلَيْهِمْ كَلِمَتُ رَبِّكَ لَا يُؤْمِنُونَ','(و بدان) آنها که فرمان پروردگار تو بر آنان تحقق یافته، (و بجرم اعمالشان، توفیق هدایت را از آنها گرفته هرگز) ایمان نمی‌آورند،'];
ayat[1461]=[10,97,'وَلَوْ جَاءَتْهُمْ كُلُّ آيَةٍ حَتَّىٰ يَرَوُا الْعَذَابَ الْأَلِيمَ','هر چند تمام آیات (و نشانه‌های الهی) به آنان برسد، تا زمانی که عذاب دردناک را ببینند! (زیرا تاریکی گناه، قلبهایشان را فرا گرفته، و راهی به روشنایی ندارند!)'];
ayat[1462]=[10,98,'فَلَوْلَا كَانَتْ قَرْيَةٌ آمَنَتْ فَنَفَعَهَا إِيمَانُهَا إِلَّا قَوْمَ يُونُسَ لَمَّا آمَنُوا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِي الْحَيَاةِ الدُّنْيَا وَمَتَّعْنَاهُمْ إِلَىٰ حِينٍ','چرا هیچ یک از شهرها و آبادیها ایمان نیاوردند که (ایمانشان بموقع باشد، و) به حالشان مفید افتد؟! مگر قوم یونس، هنگامی که آنها ایمان آوردند، عذاب رسوا کننده را در زندگی دنیا از آنان برطرف ساختیم؛ و تا مدّت معیّنی [= پایان زندگی و اجلشان‌] آنها را بهره‌مند ساختیم.'];
ayat[1463]=[10,99,'وَلَوْ شَاءَ رَبُّكَ لَآمَنَ مَن فِي الْأَرْضِ كُلُّهُمْ جَمِيعًا ۚ أَفَأَنتَ تُكْرِهُ النَّاسَ حَتَّىٰ يَكُونُوا مُؤْمِنِينَ','و اگر پروردگار تو می‌خواست، تمام کسانی که روی زمین هستند، همگی به (اجبار) ایمان می‌آوردند؛ آیا تو می‌خواهی مردم را مجبور سازی که ایمان بیاورند؟! (ایمان اجباری چه سودی دارد؟!)'];
ayat[1464]=[10,100,'وَمَا كَانَ لِنَفْسٍ أَن تُؤْمِنَ إِلَّا بِإِذْنِ اللَّهِ ۚ وَيَجْعَلُ الرِّجْسَ عَلَى الَّذِينَ لَا يَعْقِلُونَ','(اما) هیچ کس نمی‌تواند ایمان بیاورد، جز به فرمان خدا (و توفیق و یاری و هدایت او)! و پلیدی (کفر و گناه) را بر کسانی قرارمی‌دهد که نمی‌اندیشند.'];
ayat[1465]=[10,101,'قُلِ انظُرُوا مَاذَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَمَا تُغْنِي الْآيَاتُ وَالنُّذُرُ عَن قَوْمٍ لَّا يُؤْمِنُونَ','بگو: «نگاه کنید چه چیز (از آیات خدا و نشانه‌های توحیدش) در آسمانها و زمین است!» اما این آیات و انذارها به حال کسانی که (به خاطر لجاجت) ایمان نمی‌آورند مفید نخواهد بود!'];
ayat[1466]=[10,102,'فَهَلْ يَنتَظِرُونَ إِلَّا مِثْلَ أَيَّامِ الَّذِينَ خَلَوْا مِن قَبْلِهِمْ ۚ قُلْ فَانتَظِرُوا إِنِّي مَعَكُم مِّنَ الْمُنتَظِرِينَ','آیا آنها (چیزی) جز همانند روزهای پیشینیان (و بلاها و مجازاتهایشان) را انتظار می‌کشند؟! بگو: «شما انتظار بکشید، من نیز با شما انتظار می‌کشم!»'];
ayat[1467]=[10,103,'ثُمَّ نُنَجِّي رُسُلَنَا وَالَّذِينَ آمَنُوا ۚ كَذَٰلِكَ حَقًّا عَلَيْنَا نُنجِ الْمُؤْمِنِينَ','سپس (هنگام نزول بلا و مجازات،) فرستادگان خود و کسانی را که (به آنان) ایمان می‌آورند، نجات می‌دهیم و همین گونه، بر ما حق است که مؤمنان (به تو) را (نیز) رهایی بخشیم!'];
ayat[1468]=[10,104,'قُلْ يَا أَيُّهَا النَّاسُ إِن كُنتُمْ فِي شَكٍّ مِّن دِينِي فَلَا أَعْبُدُ الَّذِينَ تَعْبُدُونَ مِن دُونِ اللَّهِ وَلَٰكِنْ أَعْبُدُ اللَّهَ الَّذِي يَتَوَفَّاكُمْ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُؤْمِنِينَ','بگو: «ای مردم! اگر در عقیده من شک دارید، من آنهایی را که جز خدا می‌پرستید، نمی‌پرستم! تنها خداوندی را پرستش می‌کنم که شما را می‌میراند! و من مأمورم که از مؤمنان باشم!'];
ayat[1469]=[10,105,'وَأَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ','و (به من دستور داده شده که:) روی خود را به آیینی متوجه ساز که از هر گونه شرک، خالی است؛ و از مشرکان مباش!'];
ayat[1470]=[10,106,'وَلَا تَدْعُ مِن دُونِ اللَّهِ مَا لَا يَنفَعُكَ وَلَا يَضُرُّكَ ۖ فَإِن فَعَلْتَ فَإِنَّكَ إِذًا مِّنَ الظَّالِمِينَ','و جز خدا، چیزی را که نه سودی به تو می‌رساند و نه زیانی، مخوان! که اگر چنین کنی، از ستمکاران خواهی بود!'];
ayat[1471]=[10,107,'وَإِن يَمْسَسْكَ اللَّهُ بِضُرٍّ فَلَا كَاشِفَ لَهُ إِلَّا هُوَ ۖ وَإِن يُرِدْكَ بِخَيْرٍ فَلَا رَادَّ لِفَضْلِهِ ۚ يُصِيبُ بِهِ مَن يَشَاءُ مِنْ عِبَادِهِ ۚ وَهُوَ الْغَفُورُ الرَّحِيمُ','و اگر خداوند، (برای امتحان یا کیفر گناه،) زیانی به تو رساند، هیچ کس جز او آن را برطرف نمی‌سازد؛ و اگر اراده خیری برای تو کند، هیچ کس مانع فضل او نخواهد شد! آنرا به هر کس از بندگانش بخواهد می‌رساند؛ و او غفور و رحیم است!»'];
ayat[1472]=[10,108,'قُلْ يَا أَيُّهَا النَّاسُ قَدْ جَاءَكُمُ الْحَقُّ مِن رَّبِّكُمْ ۖ فَمَنِ اهْتَدَىٰ فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ ۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۖ وَمَا أَنَا عَلَيْكُم بِوَكِيلٍ','بگو: «ای مردم! حق از طرف پروردگارتان به سراغ شما آمده؛ هر کس (در پرتو آن) هدایت یابد، برای خود هدایت شده؛ و هر کس گمراه گردد، به زیان خود گمراه می‌گردد؛ و من مأمور (به اجبار) شما نیستم!»'];
ayat[1473]=[10,109,'وَاتَّبِعْ مَا يُوحَىٰ إِلَيْكَ وَاصْبِرْ حَتَّىٰ يَحْكُمَ اللَّهُ ۚ وَهُوَ خَيْرُ الْحَاكِمِينَ','و از آنچه بر تو وحی می‌شود پیروی کن، و شکیبا باش (و استقامت نما)، تا خداوند فرمان (پیروزی) را صادر کند؛ و او بهترین حاکمان است!'];
ayat[1474]=[11,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ كِتَابٌ أُحْكِمَتْ آيَاتُهُ ثُمَّ فُصِّلَتْ مِن لَّدُنْ حَكِيمٍ خَبِيرٍ','الر، این کتابی است که آیاتش استحکام یافته؛ سپس تشریح شده و از نزد خداوند حکیم و آگاه (نازل گردیده) است!'];
ayat[1475]=[11,2,'أَلَّا تَعْبُدُوا إِلَّا اللَّهَ ۚ إِنَّنِي لَكُم مِّنْهُ نَذِيرٌ وَبَشِيرٌ','(دعوت من این است) که: جز «اللّه» را نپرستید! من از سوی او برای شما بیم دهنده و بشارت دهنده‌ام!'];
ayat[1476]=[11,3,'وَأَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُمَتِّعْكُم مَّتَاعًا حَسَنًا إِلَىٰ أَجَلٍ مُّسَمًّى وَيُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ ۖ وَإِن تَوَلَّوْا فَإِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَبِيرٍ','و اینکه: از پروردگار خویش آمرزش بطلبید؛ سپس بسوی او بازگردید؛ تا شما را تا مدّت معیّنی، (از مواهب زندگی این جهان،) به خوبی بهره‌مند سازد؛ و به هر صاحب فضیلتی، به مقدار فضیلتش ببخشد! و اگر (از این فرمان) روی گردان شوید، من بر شما از عذاب روز بزرگی بیمناکم!'];
ayat[1477]=[11,4,'إِلَى اللَّهِ مَرْجِعُكُمْ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','(بدانید) بازگشت شما بسوی «اللّه» است، و او بر هر چیز تواناست!'];
ayat[1478]=[11,5,'أَلَا إِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُ ۚ أَلَا حِينَ يَسْتَغْشُونَ ثِيَابَهُمْ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','آگاه باشید، آنها (سرها را به هم نزدیک ساخته، و) سینه‌هاشان را در کنار هم قرارمی‌دهند، تا خود (و سخنان خویش) را از او [= پیامبر] پنهان دارند! آگاه باشید، آنگاه که آنها لباسهایشان را به خود می‌پیچند و خویش را در آن پنهان می‌کنند، (خداوند) می‌داند آنچه را پنهان می‌کنند و آنچه را آشکار می‌سازند؛ چرا که او، از اسرار درون سینه‌ها، آگاه است!'];
ayat[1479]=[11,6,'۞ وَمَا مِن دَابَّةٍ فِي الْأَرْضِ إِلَّا عَلَى اللَّهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا ۚ كُلٌّ فِي كِتَابٍ مُّبِينٍ','هیچ جنبنده‌ای در زمین نیست مگر اینکه روزی او بر خداست! او قرارگاه و محل نقل و انتقالش را می‌داند؛ همه اینها در کتاب آشکاری ثبت است! [= در لوح محفوظ، در کتاب علم خدا]'];
ayat[1480]=[11,7,'وَهُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ وَكَانَ عَرْشُهُ عَلَى الْمَاءِ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا ۗ وَلَئِن قُلْتَ إِنَّكُم مَّبْعُوثُونَ مِن بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا سِحْرٌ مُّبِينٌ','او کسی است که آسمانها و زمین را در شش روز [= شش دوران‌] آفرید؛ و عرش (حکومت) او، بر آب قرار داشت؛ (بخاطر این آفرید) تا شما را بیازماید که کدامیک عملتان بهتر است! و اگر (به آنها) بگویی: «شما بعد از مرگ، برانگیخته می‌شوید! «، مسلّماً» کافران می‌گویند: «این سحری آشکار است!»'];
ayat[1481]=[11,8,'وَلَئِنْ أَخَّرْنَا عَنْهُمُ الْعَذَابَ إِلَىٰ أُمَّةٍ مَّعْدُودَةٍ لَّيَقُولُنَّ مَا يَحْبِسُهُ ۗ أَلَا يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفًا عَنْهُمْ وَحَاقَ بِهِم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','و اگر مجازات را تا زمان محدودی از آنها به تأخیر اندازیم، (از روی استهزا می‌گویند: «چه چیز مانع آن شده است؟!» آگاه باشید، آن روز که (عذاب) به سراغشان آید، از آنها بازگردانده نخواهد شد؛ (و هیچ قدرتی مانع آن نخواهد بود؛) و آنچه را مسخره می‌کردند، دامانشان را می‌گیرد!'];
ayat[1482]=[11,9,'وَلَئِنْ أَذَقْنَا الْإِنسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنَاهَا مِنْهُ إِنَّهُ لَيَئُوسٌ كَفُورٌ','و اگر از جانب خویش، نعمتی به انسان بچشانیم، سپس آن را از او بگیریم، بسیار نومید و ناسپاس خواهد بود!'];
ayat[1483]=[11,10,'وَلَئِنْ أَذَقْنَاهُ نَعْمَاءَ بَعْدَ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّئَاتُ عَنِّي ۚ إِنَّهُ لَفَرِحٌ فَخُورٌ','و اگر بعد از شدّت و رنجی که به او رسیده، نعمتهایی به او بچشانیم، می‌گوید: «مشکلات از من برطرف شد، و دیگر باز نخواهد گشت!» و غرق شادی و غفلت و فخرفروشی می‌شود...'];
ayat[1484]=[11,11,'إِلَّا الَّذِينَ صَبَرُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ لَهُم مَّغْفِرَةٌ وَأَجْرٌ كَبِيرٌ','مگر آنها که (در سایه ایمان راستین،) صبر و استقامت ورزیدند و کارهای شایسته انجام دادند؛ که برای آنها، آمرزش و اجر بزرگی است!'];
ayat[1485]=[11,12,'فَلَعَلَّكَ تَارِكٌ بَعْضَ مَا يُوحَىٰ إِلَيْكَ وَضَائِقٌ بِهِ صَدْرُكَ أَن يَقُولُوا لَوْلَا أُنزِلَ عَلَيْهِ كَنزٌ أَوْ جَاءَ مَعَهُ مَلَكٌ ۚ إِنَّمَا أَنتَ نَذِيرٌ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ وَكِيلٌ','شاید (ابلاغ) بعض آیاتی را که به تو وحی می‌شود، (بخاطر عدم پذیرش آنها) ترک کنی (و به تأخیر اندازی)؛ و سینه‌ات از این جهت تنگ (و ناراحت) شود که می‌گویند: «چرا گنجی بر او نازل نشده؟! و یا چرا فرشته‌ای همراه او نیامده است؟!» (ابلاغ کن، و نگران و ناراحت مباش! چرا که) تو فقط بیم دهنده‌ای؛ و خداوند، نگاهبان و ناظر بر همه چیز است (؛ و به حساب آنان می‌رسد)!'];
ayat[1486]=[11,13,'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِّثْلِهِ مُفْتَرَيَاتٍ وَادْعُوا مَنِ اسْتَطَعْتُم مِّن دُونِ اللَّهِ إِن كُنتُمْ صَادِقِينَ','آنها می‌گویند: «او به دروغ این (قرآن) را (به خدا) نسبت داده (و ساختگی است)!» بگو: «اگر راست می‌گویید، شما هم ده سوره ساختگی همانند این قرآن بیاورید؛ و تمام کسانی را که می‌توانید -غیر از خدا- (برای این کار) دعوت کنید!»'];
ayat[1487]=[11,14,'فَإِلَّمْ يَسْتَجِيبُوا لَكُمْ فَاعْلَمُوا أَنَّمَا أُنزِلَ بِعِلْمِ اللَّهِ وَأَن لَّا إِلَٰهَ إِلَّا هُوَ ۖ فَهَلْ أَنتُم مُّسْلِمُونَ','و اگر آنها دعوت شما را نپذیرفتند، بدانید (قرآن) تنها با علم الهی نازل شده؛ و هیچ معبودی جز او نیست! آیا با این حال، تسلیم می‌شوید؟'];
ayat[1488]=[11,15,'مَن كَانَ يُرِيدُ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَالَهُمْ فِيهَا وَهُمْ فِيهَا لَا يُبْخَسُونَ','کسانی که زندگی دنیا و زینت آن را بخواهند، (نتیجه) اعمالشان را در همین دنیا بطور کامل به آنها می‌دهیم؛ و چیزی کم و کاست از آنها نخواهد شد!'];
ayat[1489]=[11,16,'أُولَٰئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الْآخِرَةِ إِلَّا النَّارُ ۖ وَحَبِطَ مَا صَنَعُوا فِيهَا وَبَاطِلٌ مَّا كَانُوا يَعْمَلُونَ','(ولی) آنها در آخرت، جز آتش، (سهمی) نخواهند داشت؛ و آنچه را در دنیا (برای غیر خدا) انجام دادند، بر باد می‌رود؛ و آنچه را عمل می‌کردند، باطل و بی‌اثر می‌شود!'];
ayat[1490]=[11,17,'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٍ مِّن رَّبِّهِ وَيَتْلُوهُ شَاهِدٌ مِّنْهُ وَمِن قَبْلِهِ كِتَابُ مُوسَىٰ إِمَامًا وَرَحْمَةً ۚ أُولَٰئِكَ يُؤْمِنُونَ بِهِ ۚ وَمَن يَكْفُرْ بِهِ مِنَ الْأَحْزَابِ فَالنَّارُ مَوْعِدُهُ ۚ فَلَا تَكُ فِي مِرْيَةٍ مِّنْهُ ۚ إِنَّهُ الْحَقُّ مِن رَّبِّكَ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ','آیا آن کس که دلیل آشکاری از پروردگار خویش دارد، و بدنبال آن، شاهدی از سوی او می‌باشد، و پیش از آن، کتاب موسی که پیشوا و رحمت بود (گواهی بر آن می‌دهد، همچون کسی است که چنین نباشد)؟! آنها [= حق‌طلبان و حقیقت‌جویان‌] به او (که دارای این ویژگیهاست،) ایمان می‌آورند! و هر کس از گروه‌های مختلف به او کافر شود، آتش وعده‌گاه اوست! پس، تردیدی در آن نداشته باش که آن حق است از پروردگارت! ولی بیشتر مردم ایمان نمی‌آورند!'];
ayat[1491]=[11,18,'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا ۚ أُولَٰئِكَ يُعْرَضُونَ عَلَىٰ رَبِّهِمْ وَيَقُولُ الْأَشْهَادُ هَٰؤُلَاءِ الَّذِينَ كَذَبُوا عَلَىٰ رَبِّهِمْ ۚ أَلَا لَعْنَةُ اللَّهِ عَلَى الظَّالِمِينَ','چه کسی ستمکارتر است از کسانی که بر خدا افترا می‌بندند؟! آنان (روز رستاخیز) بر پروردگارشان عرضه می‌شوند، در حالی که شاهدان [= پیامبران و فرشتگان‌] می‌گویند: «اینها همانها هستند که به پروردگارشان دروغ بستند! ای لعنت خدا بر ظالمان باد!»'];
ayat[1492]=[11,19,'الَّذِينَ يَصُدُّونَ عَن سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا وَهُم بِالْآخِرَةِ هُمْ كَافِرُونَ','همانها که (مردم را) از راه خدا بازمی‌دارند؛ و راه حق را کج و معوج نشان می‌دهند؛ و به سرای آخرت کافرند!'];
ayat[1493]=[11,20,'أُولَٰئِكَ لَمْ يَكُونُوا مُعْجِزِينَ فِي الْأَرْضِ وَمَا كَانَ لَهُم مِّن دُونِ اللَّهِ مِنْ أَوْلِيَاءَ ۘ يُضَاعَفُ لَهُمُ الْعَذَابُ ۚ مَا كَانُوا يَسْتَطِيعُونَ السَّمْعَ وَمَا كَانُوا يُبْصِرُونَ','آنها هیچ گاه توانایی فرار در زمین را ندارند؛ و جز خدا، پشتیبانهایی نمی‌یابند! عذاب خدا برای آنها مضاعف خواهد بود؛ (چرا که هم خودشان گمراه بودند، و هم دیگران را گمراه ساختند؛) آنها هرگز توانایی شنیدن (حق را) نداشتند؛ و (حقیقت را) نمی‌دیدند!'];
ayat[1494]=[11,21,'أُولَٰئِكَ الَّذِينَ خَسِرُوا أَنفُسَهُمْ وَضَلَّ عَنْهُم مَّا كَانُوا يَفْتَرُونَ','آنان کسانی هستند که سرمایه وجود خود را از دست داده‌اند؛ و تمام معبودهای دروغین از نظرشان گم شدند...'];
ayat[1495]=[11,22,'لَا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ','(به ناچار) آنها در سرای آخرت، قطعاً از همه زیانکارترند!'];
ayat[1496]=[11,23,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَأَخْبَتُوا إِلَىٰ رَبِّهِمْ أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ ۖ هُمْ فِيهَا خَالِدُونَ','کسانی که ایمان آوردند و کارهای شایسته انجام دادند و در برابر پروردگارشان خضوع و خشوع کردند، آنها اهل بهشتند؛ و جاودانه در آن خواهند ماند!'];
ayat[1497]=[11,24,'۞ مَثَلُ الْفَرِيقَيْنِ كَالْأَعْمَىٰ وَالْأَصَمِّ وَالْبَصِيرِ وَالسَّمِيعِ ۚ هَلْ يَسْتَوِيَانِ مَثَلًا ۚ أَفَلَا تَذَكَّرُونَ','حال این دو گروه [= مؤمنان و منکران‌]، حال «نابینا و کر» و «بینا و شنوا» است؛ آیا این دو، همانند یکدیگرند؟! آیا پند نمی‌گیرند؟!'];
ayat[1498]=[11,25,'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ إِنِّي لَكُمْ نَذِيرٌ مُّبِينٌ','ما نوح را بسوی قومش فرستادیم (؛ نخستین بار به آنها گفت): «من برای شما بیم‌دهنده‌ای آشکارم!'];
ayat[1499]=[11,26,'أَن لَّا تَعْبُدُوا إِلَّا اللَّهَ ۖ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ أَلِيمٍ','جز «الله» [= خدای یگانه یکتا] را نپرستید؛ زیرا بر شما از عذاب روز دردناکی می‌ترسم!'];
ayat[1500]=[11,27,'فَقَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِن قَوْمِهِ مَا نَرَاكَ إِلَّا بَشَرًا مِّثْلَنَا وَمَا نَرَاكَ اتَّبَعَكَ إِلَّا الَّذِينَ هُمْ أَرَاذِلُنَا بَادِيَ الرَّأْيِ وَمَا نَرَىٰ لَكُمْ عَلَيْنَا مِن فَضْلٍ بَلْ نَظُنُّكُمْ كَاذِبِينَ','اشراف کافر قومش (در پاسخ او) گفتند: «ما تو را جز بشری همچون خودمان نمی‌بینیم! و کسانی را که از تو پیروی کرده‌اند، جز گروهی اراذل ساده‌لوح، مشاهده نمی‌کنیم؛ و برای شما فضیلتی نسبت به خود نمی‌بینیم؛ بلکه شما را دروغگو تصور می‌کنیم!»'];
ayat[1501]=[11,28,'قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِن كُنتُ عَلَىٰ بَيِّنَةٍ مِّن رَّبِّي وَآتَانِي رَحْمَةً مِّنْ عِندِهِ فَعُمِّيَتْ عَلَيْكُمْ أَنُلْزِمُكُمُوهَا وَأَنتُمْ لَهَا كَارِهُونَ','(نوح) گفت: «اگر من دلیل روشنی از پروردگارم داشته باشم، و از نزد خودش رحمتی به من داده باشد -و بر شما مخفی مانده- (آیا باز هم رسالت مرا انکار می‌کنید)؟! آیا ما می‌توانیم شما را به پذیرش این دلیل روشن مجبور سازیم، با اینکه شما کراهت دارید؟!'];
ayat[1502]=[11,29,'وَيَا قَوْمِ لَا أَسْأَلُكُمْ عَلَيْهِ مَالًا ۖ إِنْ أَجْرِيَ إِلَّا عَلَى اللَّهِ ۚ وَمَا أَنَا بِطَارِدِ الَّذِينَ آمَنُوا ۚ إِنَّهُم مُّلَاقُو رَبِّهِمْ وَلَٰكِنِّي أَرَاكُمْ قَوْمًا تَجْهَلُونَ','ای قوم! من به خاطر این دعوت، اجر و پاداشی از شما نمی‌طلبم؛ اجر من، تنها بر خداست! و من، آنها را که ایمان آورده‌اند، (بخاطر شما) از خود طرد نمی‌کنم؛ چرا که آنها پروردگارشان را ملاقات خواهند کرد؛ (اگر آنها را از خود برانم، در دادگاه قیامت، خصم من خواهند بود؛) ولی شما را قوم جاهلی می‌بینم!'];
ayat[1503]=[11,30,'وَيَا قَوْمِ مَن يَنصُرُنِي مِنَ اللَّهِ إِن طَرَدتُّهُمْ ۚ أَفَلَا تَذَكَّرُونَ','ای قوم! چه کسی مرا در برابر (مجازات) خدا یاری می‌دهد اگر آنان را طرد کنم؟! آیا اندیشه نمی‌کنید؟!'];
ayat[1504]=[11,31,'وَلَا أَقُولُ لَكُمْ عِندِي خَزَائِنُ اللَّهِ وَلَا أَعْلَمُ الْغَيْبَ وَلَا أَقُولُ إِنِّي مَلَكٌ وَلَا أَقُولُ لِلَّذِينَ تَزْدَرِي أَعْيُنُكُمْ لَن يُؤْتِيَهُمُ اللَّهُ خَيْرًا ۖ اللَّهُ أَعْلَمُ بِمَا فِي أَنفُسِهِمْ ۖ إِنِّي إِذًا لَّمِنَ الظَّالِمِينَ','من هرگز به شما نمی‌گویم خزائن الهی نزد من است! و غیب هم نمی‌دانم! و نمی‌گویم من فرشته‌ام! و (نیز) نمی‌گویم کسانی که در نظر شما خوار می‌آیند، خداوند خیری به آنها نخواهد داد؛ خدا از دل آنان آگاهتر است! (با این حال، اگر آنها را برانم،) در این صورت از ستمکاران خواهم بود!»'];
ayat[1505]=[11,32,'قَالُوا يَا نُوحُ قَدْ جَادَلْتَنَا فَأَكْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ الصَّادِقِينَ','گفتند: «ای نوح! با ما جر و بحث کردی، و زیاد هم جر و بحث کردی! (بس است!) اکنون اگر راستی می‌گویی، آنچه را (از عذاب الهی) به ما وعده می‌دهی بیاور!»'];
ayat[1506]=[11,33,'قَالَ إِنَّمَا يَأْتِيكُم بِهِ اللَّهُ إِن شَاءَ وَمَا أَنتُم بِمُعْجِزِينَ','(نوح) گفت: «اگر خدا اراده کند، خواهد آورد؛ و شما قدرت فرار (از آن را) نخواهید داشت!'];
ayat[1507]=[11,34,'وَلَا يَنفَعُكُمْ نُصْحِي إِنْ أَرَدتُّ أَنْ أَنصَحَ لَكُمْ إِن كَانَ اللَّهُ يُرِيدُ أَن يُغْوِيَكُمْ ۚ هُوَ رَبُّكُمْ وَإِلَيْهِ تُرْجَعُونَ','(اما چه سود که) هرگاه خدا بخواهد شما را (بخاطر گناهانتان) گمراه سازد، و من بخواهم شما را اندرز دهم، اندرز من سودی به حالتان نخواهد داشت! او پروردگار شماست؛ و بسوی او بازگشت داده می‌شوید.»'];
ayat[1508]=[11,35,'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ إِنِ افْتَرَيْتُهُ فَعَلَيَّ إِجْرَامِي وَأَنَا بَرِيءٌ مِّمَّا تُجْرِمُونَ','(مشرکان) می‌گویند: «او [= محمد (ص)] این سخنان را بدروغ به خدا نسبت داده است! «بگو: «اگر من اینها را از پیش خود ساخته باشم و به او نسبت دهم، گناهش بر عهده من است؛ ولی من از گناهان شما بیزارم!»'];
ayat[1509]=[11,36,'وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُ لَن يُؤْمِنَ مِن قَوْمِكَ إِلَّا مَن قَدْ آمَنَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَفْعَلُونَ','به نوح وحی شد که: «جز آنها که (تاکنون) ایمان آورده‌اند، دیگر هیچ کس از قوم تو ایمان نخواهد آورد! پس، از کارهایی که می‌کردند، غمگین مباش!'];
ayat[1510]=[11,37,'وَاصْنَعِ الْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْنِي فِي الَّذِينَ ظَلَمُوا ۚ إِنَّهُم مُّغْرَقُونَ','و (اکنون) در حضور ما و طبق وحی ما، کشتی بساز! و درباره آنها که ستم کردند شفاعت مکن، که (همه) آنها غرق شدنی هستند!»'];
ayat[1511]=[11,38,'وَيَصْنَعُ الْفُلْكَ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَأٌ مِّن قَوْمِهِ سَخِرُوا مِنْهُ ۚ قَالَ إِن تَسْخَرُوا مِنَّا فَإِنَّا نَسْخَرُ مِنكُمْ كَمَا تَسْخَرُونَ','او مشغول ساختن کشتی بود، و هر زمان گروهی از اشراف قومش بر او می‌گذشتند، او را مسخره می‌کردند؛ (ولی نوح) گفت: «اگر ما را مسخره می‌کنید، ما نیز شما را همین‌گونه مسخره خواهیم کرد!'];
ayat[1512]=[11,39,'فَسَوْفَ تَعْلَمُونَ مَن يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيمٌ','بزودی خواهید دانست چه کسی عذاب خوارکننده به سراغش خواهد آمد، و مجازات جاودان بر او وارد خواهد شد!»'];
ayat[1513]=[11,40,'حَتَّىٰ إِذَا جَاءَ أَمْرُنَا وَفَارَ التَّنُّورُ قُلْنَا احْمِلْ فِيهَا مِن كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلَّا مَن سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ آمَنَ ۚ وَمَا آمَنَ مَعَهُ إِلَّا قَلِيلٌ','(این وضع همچنان ادامه یافت) تا آن زمان که فرمان ما فرا رسید، و تنور جوشیدن گرفت؛ (به نوح) گفتیم: «از هر جفتی از حیوانات (از نر و ماده) یک زوج در آن (کشتی) حمل کن! همچنین خاندانت را (بر آن سوار کن) -مگر آنها که قبلاً وعده هلاک آنان داده شده [= همسر و یکی از فرزندانت‌]- و همچنین مؤمنان را!» اما جز عده کمی همراه او ایمان نیاوردند!'];
ayat[1514]=[11,41,'۞ وَقَالَ ارْكَبُوا فِيهَا بِسْمِ اللَّهِ مَجْرَاهَا وَمُرْسَاهَا ۚ إِنَّ رَبِّي لَغَفُورٌ رَّحِيمٌ','او گفت: «به نام خدا بر آن سوار شوید! و هنگام حرکت و توقف کشتی، یاد او کنید، که پروردگارم آمرزنده و مهربان است!»'];
ayat[1515]=[11,42,'وَهِيَ تَجْرِي بِهِمْ فِي مَوْجٍ كَالْجِبَالِ وَنَادَىٰ نُوحٌ ابْنَهُ وَكَانَ فِي مَعْزِلٍ يَا بُنَيَّ ارْكَب مَّعَنَا وَلَا تَكُن مَّعَ الْكَافِرِينَ','و آن کشتی، آنها را از میان امواجی همچون کوه‌ها حرکت میداد؛ (در این هنگام،) نوح فرزندش را که در گوشه‌ای بود صدا زد: «پسرم! همراه ما سوار شو، و با کافران مباش!»'];
ayat[1516]=[11,43,'قَالَ سَآوِي إِلَىٰ جَبَلٍ يَعْصِمُنِي مِنَ الْمَاءِ ۚ قَالَ لَا عَاصِمَ الْيَوْمَ مِنْ أَمْرِ اللَّهِ إِلَّا مَن رَّحِمَ ۚ وَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِينَ','گفت: «بزودی به کوهی پناه میبرم تا مرا از آب حفظ کند!» (نوح) گفت: «امروز هیچ نگهداری در برابر فرمان خدا نیسست؛ مگر آن کس را که او رحم کند!» در این هنگام، موج در میان آن دو حایل شد؛ و او در زمره غرق‌شدگان قرار گرفت!'];
ayat[1517]=[11,44,'وَقِيلَ يَا أَرْضُ ابْلَعِي مَاءَكِ وَيَا سَمَاءُ أَقْلِعِي وَغِيضَ الْمَاءُ وَقُضِيَ الْأَمْرُ وَاسْتَوَتْ عَلَى الْجُودِيِّ ۖ وَقِيلَ بُعْدًا لِّلْقَوْمِ الظَّالِمِينَ','و گفته شد: «ای زمین، آبت را فرو بر! و ای آسمان، خودداری کن! و آب فرو نشست و کار پایان یافت و (کشتی) بر (دامنه کوه) جودی، پهلو گرفت؛ و (در این هنگام،) گفته شد:» دور باد قوم ستمگر (از سعادت و نجات و رحمت خدا!)»'];
ayat[1518]=[11,45,'وَنَادَىٰ نُوحٌ رَّبَّهُ فَقَالَ رَبِّ إِنَّ ابْنِي مِنْ أَهْلِي وَإِنَّ وَعْدَكَ الْحَقُّ وَأَنتَ أَحْكَمُ الْحَاكِمِينَ','نوح به پروردگارش عرض کرد: «پروردگارا! پسرم از خاندان من است؛ و وعده تو (در مورد نجات خاندانم) حق است؛ و تو از همه حکم‌کنندگان برتری!»'];
ayat[1519]=[11,46,'قَالَ يَا نُوحُ إِنَّهُ لَيْسَ مِنْ أَهْلِكَ ۖ إِنَّهُ عَمَلٌ غَيْرُ صَالِحٍ ۖ فَلَا تَسْأَلْنِ مَا لَيْسَ لَكَ بِهِ عِلْمٌ ۖ إِنِّي أَعِظُكَ أَن تَكُونَ مِنَ الْجَاهِلِينَ','فرمود: «ای نوح! او از اهل تو نیست! او عمل غیر صالحی است [= فرد ناشایسته‌ای است‌]! پس، آنچه را از آن آگاه نیستی، از من مخواه! من به تو اندرز می‌دهم تا از جاهلان نباشی!!»'];
ayat[1520]=[11,47,'قَالَ رَبِّ إِنِّي أَعُوذُ بِكَ أَنْ أَسْأَلَكَ مَا لَيْسَ لِي بِهِ عِلْمٌ ۖ وَإِلَّا تَغْفِرْ لِي وَتَرْحَمْنِي أَكُن مِّنَ الْخَاسِرِينَ','عرض کرد: «پروردگارا! من به تو پناه می‌برم که از تو چیزی بخواهم که از آن آگاهی ندارم! و اگر مرا نبخشی، و بر من رحم نکنی، از زیانکاران خواهم بود!»'];
ayat[1521]=[11,48,'قِيلَ يَا نُوحُ اهْبِطْ بِسَلَامٍ مِّنَّا وَبَرَكَاتٍ عَلَيْكَ وَعَلَىٰ أُمَمٍ مِّمَّن مَّعَكَ ۚ وَأُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُم مِّنَّا عَذَابٌ أَلِيمٌ','(به نوح) گفته شد: «ای نوح! با سلامت و برکاتی از ناحیه ما بر تو و بر تمام امتهایی که با تواند، فرود آی! و امتهای نیز هستند که ما آنها را از نعمتها بهره‌مند خواهیم ساخت، سپس عذاب دردناکی از سوی ما به آنها می‌رسد (، چرا که این نعمتها را کفران می‌کنند!)»'];
ayat[1522]=[11,49,'تِلْكَ مِنْ أَنبَاءِ الْغَيْبِ نُوحِيهَا إِلَيْكَ ۖ مَا كُنتَ تَعْلَمُهَا أَنتَ وَلَا قَوْمُكَ مِن قَبْلِ هَٰذَا ۖ فَاصْبِرْ ۖ إِنَّ الْعَاقِبَةَ لِلْمُتَّقِينَ','اینها از خبرهای غیب است که به تو (ای پیامبر) وحی می‌کنیم؛ نه تو، و نه قومت، اینها را پیش از این نمی‌دانستید! بنابر این، صبر و استقامت کن، که عاقبت از آن پرهیزگاران است!'];
ayat[1523]=[11,50,'وَإِلَىٰ عَادٍ أَخَاهُمْ هُودًا ۚ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ إِنْ أَنتُمْ إِلَّا مُفْتَرُونَ','(ما) به سوی (قوم) عاد، برادرشان «هود» را فرستادیم؛ (به آنها) گفت: «ای قوم من! خدا را پرستش کنید، که معبودی جز او برای شما نیست! شما فقط تهمت می‌زنید (و بتها را شریک او می‌خوانید)!'];
ayat[1524]=[11,51,'يَا قَوْمِ لَا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا ۖ إِنْ أَجْرِيَ إِلَّا عَلَى الَّذِي فَطَرَنِي ۚ أَفَلَا تَعْقِلُونَ','ای قوم من! من از شما برای این (رسالت)، پاداشی نمی‌طلبم؛ پاداش من، تنها بر کسی است که مرا آفریده است؛ آیا نمی‌فهمید؟!'];
ayat[1525]=[11,52,'وَيَا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُرْسِلِ السَّمَاءَ عَلَيْكُم مِّدْرَارًا وَيَزِدْكُمْ قُوَّةً إِلَىٰ قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِمِينَ','و ای قوم من! از پروردگارتان طلب آمرزش کنید، سپس به سوی او بازگردید، تا (باران) آسمان را پی در پی بر شما بفرستد؛ و نیرویی بر نیرویتان بیفزاید! و گنهکارانه، روی (از حق) بر نتابید!»'];
ayat[1526]=[11,53,'قَالُوا يَا هُودُ مَا جِئْتَنَا بِبَيِّنَةٍ وَمَا نَحْنُ بِتَارِكِي آلِهَتِنَا عَن قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِينَ','گفتند: «ای هود! تو دلیل روشنی برای ما نیاورده‌ای! و ما خدایان خود را بخاطر حرف تو، رها نخواهیم کرد! و ما (اصلاً) به تو ایمان نمی‌آوریم!'];
ayat[1527]=[11,54,'إِن نَّقُولُ إِلَّا اعْتَرَاكَ بَعْضُ آلِهَتِنَا بِسُوءٍ ۗ قَالَ إِنِّي أُشْهِدُ اللَّهَ وَاشْهَدُوا أَنِّي بَرِيءٌ مِّمَّا تُشْرِكُونَ','ما (درباره تو) فقط می‌گوییم: بعضی از خدایان ما، به تو زیان رسانده (و عقلت را ربوده) اند!» (هود) گفت: «من خدا را به شهادت می‌طلبم، شما نیز گواه باشید که من بیزارم از آنچه شریک (خدا) قرارمی‌دهید...'];
ayat[1528]=[11,55,'مِن دُونِهِ ۖ فَكِيدُونِي جَمِيعًا ثُمَّ لَا تُنظِرُونِ','از آنچه غیر او (می‌پرستید)! حال که چنین است، همگی برای من نقشه بکشید؛ و مرا مهلت ندهید! (اما بدانید کاری از دست شما ساخته نیست!)'];
ayat[1529]=[11,56,'إِنِّي تَوَكَّلْتُ عَلَى اللَّهِ رَبِّي وَرَبِّكُم ۚ مَّا مِن دَابَّةٍ إِلَّا هُوَ آخِذٌ بِنَاصِيَتِهَا ۚ إِنَّ رَبِّي عَلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','من، بر «الله» که پروردگار من و شماست، توکل کرده‌ام! هیچ جنبنده‌ای نیست مگر اینکه او بر آن تسلط دارد؛ (اما سلطه‌ای با عدالت! چرا که) پروردگار من بر راه راست است!'];
ayat[1530]=[11,57,'فَإِن تَوَلَّوْا فَقَدْ أَبْلَغْتُكُم مَّا أُرْسِلْتُ بِهِ إِلَيْكُمْ ۚ وَيَسْتَخْلِفُ رَبِّي قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّونَهُ شَيْئًا ۚ إِنَّ رَبِّي عَلَىٰ كُلِّ شَيْءٍ حَفِيظٌ','پس اگر روی برگردانید، من رسالتی را که مأمور بودم به شما رساندم؛ و پروردگارم گروه دیگری را جانشین شما می‌کند؛ و شما کمترین ضرری به او نمی‌رسایند؛ پروردگارم حافظ و نگاهبان هر چیز است!»'];
ayat[1531]=[11,58,'وَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا هُودًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِّنَّا وَنَجَّيْنَاهُم مِّنْ عَذَابٍ غَلِيظٍ','و هنگامی که فرمان ما فرا رسید، «هود» و کسانی را که با او ایمان آورده بودند، به رحمت خود نجات دادیم؛ و آنها را از عذاب شدید، رهایی بخشیدیم!'];
ayat[1532]=[11,59,'وَتِلْكَ عَادٌ ۖ جَحَدُوا بِآيَاتِ رَبِّهِمْ وَعَصَوْا رُسُلَهُ وَاتَّبَعُوا أَمْرَ كُلِّ جَبَّارٍ عَنِيدٍ','و این قوم «عاد» بود که آیات پروردگارشان را انکار کردند؛ و پیامبران او را معصیت نمودند؛ و از فرمان هر ستمگر دشمن حق، پیروی کردند!'];
ayat[1533]=[11,60,'وَأُتْبِعُوا فِي هَٰذِهِ الدُّنْيَا لَعْنَةً وَيَوْمَ الْقِيَامَةِ ۗ أَلَا إِنَّ عَادًا كَفَرُوا رَبَّهُمْ ۗ أَلَا بُعْدًا لِّعَادٍ قَوْمِ هُودٍ','آنان، در این دنیا و روز قیامت، لعنت (و نام ننگینی) بدنبال دارند! بدانید «عاد» نسبت به پروردگارشان کفر ورزیدند! دور باد «عاد» -قوم هود- (از رحمت خدا، و خیر و سعادت)!'];
ayat[1534]=[11,61,'۞ وَإِلَىٰ ثَمُودَ أَخَاهُمْ صَالِحًا ۚ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ هُوَ أَنشَأَكُم مِّنَ الْأَرْضِ وَاسْتَعْمَرَكُمْ فِيهَا فَاسْتَغْفِرُوهُ ثُمَّ تُوبُوا إِلَيْهِ ۚ إِنَّ رَبِّي قَرِيبٌ مُّجِيبٌ','و بسوی قوم «ثمود»، برادرشان «صالح» را (فرستادیم)؛ گفت: «ای قوم من! خدا را پرستش کنید، که معبودی جز او برای شما نیست! اوست که شما را از زمین آفرید، و آبادی آن را به شما واگذاشت! از او آمرزش بطلبید، سپس به سوی او بازگردید، که پروردگارم (به بندگان خود) نزدیک، و اجابت‌کننده (خواسته‌های آنها) است!»'];
ayat[1535]=[11,62,'قَالُوا يَا صَالِحُ قَدْ كُنتَ فِينَا مَرْجُوًّا قَبْلَ هَٰذَا ۖ أَتَنْهَانَا أَن نَّعْبُدَ مَا يَعْبُدُ آبَاؤُنَا وَإِنَّنَا لَفِي شَكٍّ مِّمَّا تَدْعُونَا إِلَيْهِ مُرِيبٍ','گفتند: «ای صالح! تو پیش از این، مایه امید ما بودی! آیا ما را از پرستش آنچه پدرانمان می‌پرستیدند، نهی می‌کنی؟! در حالی که ما، در مورد آنچه به سوی آن دعوتمان می‌کنی، در شک و تردید هستیم!»'];
ayat[1536]=[11,63,'قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِن كُنتُ عَلَىٰ بَيِّنَةٍ مِّن رَّبِّي وَآتَانِي مِنْهُ رَحْمَةً فَمَن يَنصُرُنِي مِنَ اللَّهِ إِنْ عَصَيْتُهُ ۖ فَمَا تَزِيدُونَنِي غَيْرَ تَخْسِيرٍ','گفت: «ای قوم! اگر من دلیل آشکاری از پروردگارم داشته باشم، و رحمتی از جانب خود به من داده باشد (، می‌توانم از ابلاغ رسالت او سرپیچی کنم)؟! اگر من نافرمانی او کنم، چه کسی می‌تواند مرا در برابر وی یاری دهد؟! پس، (سخنان) شما، جز اطمینان به زیانکار بودنتان، چیزی بر من نمی‌افزاید!'];
ayat[1537]=[11,64,'وَيَا قَوْمِ هَٰذِهِ نَاقَةُ اللَّهِ لَكُمْ آيَةً فَذَرُوهَا تَأْكُلْ فِي أَرْضِ اللَّهِ وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيبٌ','ای قوم من! این «ناقه» خداوند است، که برای شما نشانه‌ای است؛ بگذارید در زمین خدا به چرا مشغول شود؛ هیچ گونه آزاری به آن نرسانید، که بزودی عذاب خدا شما را خواهد گرفت!»'];
ayat[1538]=[11,65,'فَعَقَرُوهَا فَقَالَ تَمَتَّعُوا فِي دَارِكُمْ ثَلَاثَةَ أَيَّامٍ ۖ ذَٰلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ','(اما) آنها آن (ناقه) را از پای در آوردند! و (صالح به آنها) گفت: «(مهلت شما تمام شد!) سه روز در خانه‌هایتان بهره‌مند گردید؛ (و بعد از آن، عذاب الهی فرا خواهد رسید؛) این وعده‌ای است که دروغ نخواهد بود!»'];
ayat[1539]=[11,66,'فَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا صَالِحًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِّنَّا وَمِنْ خِزْيِ يَوْمِئِذٍ ۗ إِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيزُ','و هنگامی که فرمان (مجازات) ما فرا رسید، صالح و کسانی را که با او ایمان آورده بودند، به رحمت خود (از آن عذاب) و از رسوایی آن روز، رهایی بخشیدیم؛ چرا که پروردگارت قوی و شکست ناپذیر است!'];
ayat[1540]=[11,67,'وَأَخَذَ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيَارِهِمْ جَاثِمِينَ','و کسانی را که ستم کرده بودند، صیحه (آسمانی) فروگرفت؛ و در خانه‌هایشان به روی افتادند و مردند...'];
ayat[1541]=[11,68,'كَأَن لَّمْ يَغْنَوْا فِيهَا ۗ أَلَا إِنَّ ثَمُودَ كَفَرُوا رَبَّهُمْ ۗ أَلَا بُعْدًا لِّثَمُودَ','آنچنان که گویی هرگز ساکن آن دیار نبودند! بدانید قوم ثمود، پروردگارشان را انکار کردند! دور باد قوم ثمود (از رحمت پروردگار)!'];
ayat[1542]=[11,69,'وَلَقَدْ جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَىٰ قَالُوا سَلَامًا ۖ قَالَ سَلَامٌ ۖ فَمَا لَبِثَ أَن جَاءَ بِعِجْلٍ حَنِيذٍ','فرستادگان ما [= فرشتگان‌] برای ابراهیم بشارت آوردند؛ گفتند: «سلام!» (او نیز) گفت: «سلام!» و طولی نکشید که گوساله بریانی (برای آنها) آورد.'];
ayat[1543]=[11,70,'فَلَمَّا رَأَىٰ أَيْدِيَهُمْ لَا تَصِلُ إِلَيْهِ نَكِرَهُمْ وَأَوْجَسَ مِنْهُمْ خِيفَةً ۚ قَالُوا لَا تَخَفْ إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمِ لُوطٍ','(اما) هنگامی که دید دست آنها به آن نمی‌رسد (و از آن نمی‌خورند، کار) آنها را زشت شمرد؛ و در دل احساس ترس نمود. به او گفتند: «نترس! ما به سوی قوم لوط فرستاده شده‌ایم!»'];
ayat[1544]=[11,71,'وَامْرَأَتُهُ قَائِمَةٌ فَضَحِكَتْ فَبَشَّرْنَاهَا بِإِسْحَاقَ وَمِن وَرَاءِ إِسْحَاقَ يَعْقُوبَ','و همسرش ایستاده بود، (از خوشحالی) خندید؛ پس او را بشارت به اسحاق، و بعد از او یعقوب دادیم.'];
ayat[1545]=[11,72,'قَالَتْ يَا وَيْلَتَىٰ أَأَلِدُ وَأَنَا عَجُوزٌ وَهَٰذَا بَعْلِي شَيْخًا ۖ إِنَّ هَٰذَا لَشَيْءٌ عَجِيبٌ','گفت: «ای وای بر من! آیا من فرزند می‌آورم در حالی که پیرزنم، و این شوهرم پیرمردی است؟! این راستی چیز عجیبی است!»'];
ayat[1546]=[11,73,'قَالُوا أَتَعْجَبِينَ مِنْ أَمْرِ اللَّهِ ۖ رَحْمَتُ اللَّهِ وَبَرَكَاتُهُ عَلَيْكُمْ أَهْلَ الْبَيْتِ ۚ إِنَّهُ حَمِيدٌ مَّجِيدٌ','گفتند: «آیا از فرمان خدا تعجب میکنی؟! این رحمت خدا و برکاتش بر شما خانواده است؛ چرا که او ستوده و والا است!»'];
ayat[1547]=[11,74,'فَلَمَّا ذَهَبَ عَنْ إِبْرَاهِيمَ الرَّوْعُ وَجَاءَتْهُ الْبُشْرَىٰ يُجَادِلُنَا فِي قَوْمِ لُوطٍ','هنگامی که ترس ابراهیم فرو نشست، و بشارت به او رسید، درباره قوم لوط با ما مجادله می‌کرد...'];
ayat[1548]=[11,75,'إِنَّ إِبْرَاهِيمَ لَحَلِيمٌ أَوَّاهٌ مُّنِيبٌ','چرا که ابراهیم، بردبار و دلسوز و بازگشت‌کننده (بسوی خدا) بود!'];
ayat[1549]=[11,76,'يَا إِبْرَاهِيمُ أَعْرِضْ عَنْ هَٰذَا ۖ إِنَّهُ قَدْ جَاءَ أَمْرُ رَبِّكَ ۖ وَإِنَّهُمْ آتِيهِمْ عَذَابٌ غَيْرُ مَرْدُودٍ','ای ابراهیم! از این (درخواست) صرف‌نظر کن، که فرمان پروردگارت فرا رسیده؛ و بطور قطع عذاب (الهی) به سراغ آنها می‌آید؛ و برگشت ندارد!'];
ayat[1550]=[11,77,'وَلَمَّا جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالَ هَٰذَا يَوْمٌ عَصِيبٌ','و هنگامی که رسولان ما [= فرشتگان عذاب‌] به سراغ لوط آمدند، از آمدنشان ناراحت شد؛ و قلبش پریشان گشت؛ و گفت: «امروز روز سختی است!» (زیرا آنها را نشناخت؛ و ترسید قوم تبهکار مزاحم آنها شوند.)'];
ayat[1551]=[11,78,'وَجَاءَهُ قَوْمُهُ يُهْرَعُونَ إِلَيْهِ وَمِن قَبْلُ كَانُوا يَعْمَلُونَ السَّيِّئَاتِ ۚ قَالَ يَا قَوْمِ هَٰؤُلَاءِ بَنَاتِي هُنَّ أَطْهَرُ لَكُمْ ۖ فَاتَّقُوا اللَّهَ وَلَا تُخْزُونِ فِي ضَيْفِي ۖ أَلَيْسَ مِنكُمْ رَجُلٌ رَّشِيدٌ','قوم او (بقصد مزاحمت میهمانان) بسرعت به سراغ او آمدند -و قبلاً کارهای بد انجام می‌دادند- گفت: «ای قوم من! اینها دختران منند؛ برای شما پاکیزه‌ترند! (با آنها ازدواج کنید؛ و از زشتکاری چشم بپوشید!) از خدا بترسید؛ و مرا در مورد میهمانانم رسوا نسازید! آیا در میان شما یک مرد فهمیده و آگاه وجود ندارد؟!»'];
ayat[1552]=[11,79,'قَالُوا لَقَدْ عَلِمْتَ مَا لَنَا فِي بَنَاتِكَ مِنْ حَقٍّ وَإِنَّكَ لَتَعْلَمُ مَا نُرِيدُ','گفتند: «تو که می‌دانی ما تمایلی به دختران تو نداریم؛ و خوب می‌دانی ما چه می‌خواهیم!»'];
ayat[1553]=[11,80,'قَالَ لَوْ أَنَّ لِي بِكُمْ قُوَّةً أَوْ آوِي إِلَىٰ رُكْنٍ شَدِيدٍ','گفت: «(افسوس!) ای کاش در برابر شما قدرتی داشتم؛ یا تکیه‌گاه و پشتیبان محکمی در اختیار من بود! (آنگاه می‌دانستم با شما زشت‌سیرتان ددمنش چه کنم!)»'];
ayat[1554]=[11,81,'قَالُوا يَا لُوطُ إِنَّا رُسُلُ رَبِّكَ لَن يَصِلُوا إِلَيْكَ ۖ فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِّنَ اللَّيْلِ وَلَا يَلْتَفِتْ مِنكُمْ أَحَدٌ إِلَّا امْرَأَتَكَ ۖ إِنَّهُ مُصِيبُهَا مَا أَصَابَهُمْ ۚ إِنَّ مَوْعِدَهُمُ الصُّبْحُ ۚ أَلَيْسَ الصُّبْحُ بِقَرِيبٍ','(فرشتگان عذاب) گفتند: «ای لوط! ما فرستادگان پروردگار توایم! آنها هرگز دسترسی به تو پیدا نخواهند کرد! در دل شب، خانواده‌ات را (از این شهر) حرکت ده! و هیچ یک از شما پشت سرش را نگاه نکند؛ مگر همسرت، که او هم به همان بلایی که آنها گرفتار می‌شوند، گرفتار خواهد شد! موعد آنها صبح است؛ آیا صبح نزدیک نیست؟!»'];
ayat[1555]=[11,82,'فَلَمَّا جَاءَ أَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهَا حِجَارَةً مِّن سِجِّيلٍ مَّنضُودٍ','و هنگامی که فرمان ما فرا رسید، آن (شهر و دیار) را زیر و رو کردیم؛ و بارانی از سنگ [= گِلهای متحجر] متراکم بر روی هم، بر آنها نازل نمودیم...'];
ayat[1556]=[11,83,'مُّسَوَّمَةً عِندَ رَبِّكَ ۖ وَمَا هِيَ مِنَ الظَّالِمِينَ بِبَعِيدٍ','(سنگهایی که) نزد پروردگارت نشاندار بود؛ و آن، (از سایر) ستمگران دور نیست!'];
ayat[1557]=[11,84,'۞ وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا ۚ قَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ وَلَا تَنقُصُوا الْمِكْيَالَ وَالْمِيزَانَ ۚ إِنِّي أَرَاكُم بِخَيْرٍ وَإِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُّحِيطٍ','و بسوی «مدین» برادرشان شعیب را (فرستادیم)؛ گفت: «ای قوم من! خدا را پرستش کنید، که جز او، معبود دیگری برای شما نیست! پیمانه و وزن را کم نکنید (و دست به کم‌فروشی نزنید)! من (هم اکنون) شما را در نعمت می‌بینم؛ (ولی) از عذاب روز فراگیر، بر شما بیمناکم!'];
ayat[1558]=[11,85,'وَيَا قَوْمِ أَوْفُوا الْمِكْيَالَ وَالْمِيزَانَ بِالْقِسْطِ ۖ وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ','و ای قوم من! پیمانه و وزن را با عدالت، تمام دهید! و بر اشیاء (و اجناس) مردم، عیب نگذارید؛ و از حق آنان نکاهید! و در زمین به فساد نکوشید!'];
ayat[1559]=[11,86,'بَقِيَّتُ اللَّهِ خَيْرٌ لَّكُمْ إِن كُنتُم مُّؤْمِنِينَ ۚ وَمَا أَنَا عَلَيْكُم بِحَفِيظٍ','آنچه خداوند برای شما باقی گذارده (از سرمایه‌های حلال)، برایتان بهتر است اگر ایمان داشته باشید! و من، پاسدار شما (و مأمور بر اجبارتان به ایمان) نیستم!'];
ayat[1560]=[11,87,'قَالُوا يَا شُعَيْبُ أَصَلَاتُكَ تَأْمُرُكَ أَن نَّتْرُكَ مَا يَعْبُدُ آبَاؤُنَا أَوْ أَن نَّفْعَلَ فِي أَمْوَالِنَا مَا نَشَاءُ ۖ إِنَّكَ لَأَنتَ الْحَلِيمُ الرَّشِيدُ','گفتند: «ای شعیب! آیا نمازت به تو دستور می‌دهد که آنچه را پدرانمان می‌پرستیدند، ترک کنیم؛ یا آنچه را می‌خواهیم در اموالمان انجام ندهیم؟! تو که مرد بردبار و فهمیده‌ای هستی!»'];
ayat[1561]=[11,88,'قَالَ يَا قَوْمِ أَرَأَيْتُمْ إِن كُنتُ عَلَىٰ بَيِّنَةٍ مِّن رَّبِّي وَرَزَقَنِي مِنْهُ رِزْقًا حَسَنًا ۚ وَمَا أُرِيدُ أَنْ أُخَالِفَكُمْ إِلَىٰ مَا أَنْهَاكُمْ عَنْهُ ۚ إِنْ أُرِيدُ إِلَّا الْإِصْلَاحَ مَا اسْتَطَعْتُ ۚ وَمَا تَوْفِيقِي إِلَّا بِاللَّهِ ۚ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ','گفت: «ای قوم! به من بگویید، هرگاه من دلیل آشکاری از پروردگارم داشته باشم، و رزق (و موهبت) خوبی به من داده باشد، (آیا می‌توانم بر خلاف فرمان او رفتار کنم؟!) من هرگز نمی‌خواهم چیزی که شما را از آن باز می‌دارم، خودم مرتکب شوم! من جز اصلاح -تا آنجا که توانایی دارم- نمی‌خواهم! و توفیق من، جز به خدا نیست! بر او توکّل کردم؛ و به سوی او بازمی‌گردم!'];
ayat[1562]=[11,89,'وَيَا قَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِي أَن يُصِيبَكُم مِّثْلُ مَا أَصَابَ قَوْمَ نُوحٍ أَوْ قَوْمَ هُودٍ أَوْ قَوْمَ صَالِحٍ ۚ وَمَا قَوْمُ لُوطٍ مِّنكُم بِبَعِيدٍ','و ای قوم من! دشمنی و مخالفت با من، سبب نشود که شما به همان سرنوشتی که قوم نوح یا قوم هود یا قوم صالح گرفتار شدند، گرفتار شوید! و قوم لوط از شما چندان دور نیست!'];
ayat[1563]=[11,90,'وَاسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ ۚ إِنَّ رَبِّي رَحِيمٌ وَدُودٌ','از پروردگار خود، آمرزش بطلبید؛ و به سوی او بازگردید؛ که پروردگارم مهربان و دوستدار (بندگان توبه‌کار) است!»'];
ayat[1564]=[11,91,'قَالُوا يَا شُعَيْبُ مَا نَفْقَهُ كَثِيرًا مِّمَّا تَقُولُ وَإِنَّا لَنَرَاكَ فِينَا ضَعِيفًا ۖ وَلَوْلَا رَهْطُكَ لَرَجَمْنَاكَ ۖ وَمَا أَنتَ عَلَيْنَا بِعَزِيزٍ','گفتند: «ای شعیب! بسیاری از آنچه را می‌گویی، ما نمی‌فهمیم! و ما تو را در میان خود، ضعیف می‌یابیم؛ و اگر (بخاطر) قبیله کوچکت نبود، تو را سنگسار می‌کردیم؛ و تو در برابر ما قدرتی نداری!»'];
ayat[1565]=[11,92,'قَالَ يَا قَوْمِ أَرَهْطِي أَعَزُّ عَلَيْكُم مِّنَ اللَّهِ وَاتَّخَذْتُمُوهُ وَرَاءَكُمْ ظِهْرِيًّا ۖ إِنَّ رَبِّي بِمَا تَعْمَلُونَ مُحِيطٌ','گفت: «ای قوم! آیا قبیله کوچک من، نزد شما عزیزتر از خداوند است؟! در حالی که (فرمان) او را پشت سر انداخته‌اید! پروردگارم به آنچه انجام می‌دهید، احاطه دارد (و آگاه است)!'];
ayat[1566]=[11,93,'وَيَا قَوْمِ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنِّي عَامِلٌ ۖ سَوْفَ تَعْلَمُونَ مَن يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَمَنْ هُوَ كَاذِبٌ ۖ وَارْتَقِبُوا إِنِّي مَعَكُمْ رَقِيبٌ','ای قوم! هر کاری از دستتان ساخته است، انجام دهید، من هم کار خود را خواهم کرد؛ و بزودی خواهید دانست چه کسی عذاب خوارکننده به سراغش می‌آید، و چه کسی دروغگوست! شما انتظار بکشید، من هم در انتظارم!»'];
ayat[1567]=[11,94,'وَلَمَّا جَاءَ أَمْرُنَا نَجَّيْنَا شُعَيْبًا وَالَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِّنَّا وَأَخَذَتِ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيَارِهِمْ جَاثِمِينَ','و هنگامی که فرمان ما فرا رسید، شعیب و کسانی را که با او ایمان آورده بودند، به رحمت خود نجات دادیم؛ و آنها را که ستم کردند، صیحه (آسمانی) فرو گرفت؛ و در دیار خود، به رو افتادند (و مردند)...'];
ayat[1568]=[11,95,'كَأَن لَّمْ يَغْنَوْا فِيهَا ۗ أَلَا بُعْدًا لِّمَدْيَنَ كَمَا بَعِدَتْ ثَمُودُ','آنچنان که گویی هرگز از ساکنان آن (دیار) نبودند! دور باد مدین (و اهل آن) از رحمت خدا، همان گونه که قوم ثمود دور شدند!'];
ayat[1569]=[11,96,'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا وَسُلْطَانٍ مُّبِينٍ','ما، موسی را با آیات خود و دلیل آشکاری فرستادیم...'];
ayat[1570]=[11,97,'إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَاتَّبَعُوا أَمْرَ فِرْعَوْنَ ۖ وَمَا أَمْرُ فِرْعَوْنَ بِرَشِيدٍ','بسوی فرعون و اطرافیانش؛ اما آنها از فرمان فرعون پیروی کردند؛ در حالی که فرمان فرعون، مایه رشد و نجات نبود!'];
ayat[1571]=[11,98,'يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيَامَةِ فَأَوْرَدَهُمُ النَّارَ ۖ وَبِئْسَ الْوِرْدُ الْمَوْرُودُ','روز قیامت، او در پیشاپیش قومش خواهد بود؛ و (به جای چشمه‌های زلال بهشت) آنها را وارد آتش می‌کند! و چه بد آبشخوری است (آتش)، که بر آن وارد می‌شوند!'];
ayat[1572]=[11,99,'وَأُتْبِعُوا فِي هَٰذِهِ لَعْنَةً وَيَوْمَ الْقِيَامَةِ ۚ بِئْسَ الرِّفْدُ الْمَرْفُودُ','آنان در این جهان و روز قیامت، لعنتی بدنبال دارند؛ و چه بد عطایی است (لعن و دوری از رحمت خدا)، که نصیب آنان می‌شود!'];
ayat[1573]=[11,100,'ذَٰلِكَ مِنْ أَنبَاءِ الْقُرَىٰ نَقُصُّهُ عَلَيْكَ ۖ مِنْهَا قَائِمٌ وَحَصِيدٌ','این از اخبار شهرها و آبادیهاست که ما برای تو بازگو می‌کنیم؛ که بعضی (هنوز) برپا هستند، و بعضی درو شده‌اند (و از میان رفته‌اند)!'];
ayat[1574]=[11,101,'وَمَا ظَلَمْنَاهُمْ وَلَٰكِن ظَلَمُوا أَنفُسَهُمْ ۖ فَمَا أَغْنَتْ عَنْهُمْ آلِهَتُهُمُ الَّتِي يَدْعُونَ مِن دُونِ اللَّهِ مِن شَيْءٍ لَّمَّا جَاءَ أَمْرُ رَبِّكَ ۖ وَمَا زَادُوهُمْ غَيْرَ تَتْبِيبٍ','ما به آنها ستم نکردیم؛ بلکه آنها خودشان بر خویشتن ستم روا داشتند! و هنگامی که فرمان مجازات الهی فرا رسید، معبودانی را که غیر از خدا می‌خواندند، آنها را یاری نکردند؛ و جز بر هلاکت آنان نیفزودند!'];
ayat[1575]=[11,102,'وَكَذَٰلِكَ أَخْذُ رَبِّكَ إِذَا أَخَذَ الْقُرَىٰ وَهِيَ ظَالِمَةٌ ۚ إِنَّ أَخْذَهُ أَلِيمٌ شَدِيدٌ','و اینچنین است مجازات پروردگار تو، هنگامی که شهرها و آبادیهای ظالم را مجازات می‌کند! (آری،) مجازات او، دردناک و شدید است!'];
ayat[1576]=[11,103,'إِنَّ فِي ذَٰلِكَ لَآيَةً لِّمَنْ خَافَ عَذَابَ الْآخِرَةِ ۚ ذَٰلِكَ يَوْمٌ مَّجْمُوعٌ لَّهُ النَّاسُ وَذَٰلِكَ يَوْمٌ مَّشْهُودٌ','در این، نشانه‌ای است برای کسی که از عذاب آخرت می‌ترسد؛ همان روزی است که مردم در آن جمع می‌شوند، و روزی که همه آن را مشاهده می‌کنند.'];
ayat[1577]=[11,104,'وَمَا نُؤَخِّرُهُ إِلَّا لِأَجَلٍ مَّعْدُودٍ','و ما آن (مجازات) را، جز تا زمان محدودی، تأخیر نمی‌اندازیم!'];
ayat[1578]=[11,105,'يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ إِلَّا بِإِذْنِهِ ۚ فَمِنْهُمْ شَقِيٌّ وَسَعِيدٌ','آن روز که (قیامت و زمان مجازات) فرا رسد، هیچ کس جز به اجازه او سخن نمی‌گوید؛ گروهی بدبختند و گروهی خوشبخت!'];
ayat[1579]=[11,106,'فَأَمَّا الَّذِينَ شَقُوا فَفِي النَّارِ لَهُمْ فِيهَا زَفِيرٌ وَشَهِيقٌ','امّا آنها که بدبخت شدند، در آتشند؛ و برای آنان در آنجا، «زفیر» و «شهیق» [= ناله‌های طولانی دم و بازدم‌] است...'];
ayat[1580]=[11,107,'خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالْأَرْضُ إِلَّا مَا شَاءَ رَبُّكَ ۚ إِنَّ رَبَّكَ فَعَّالٌ لِّمَا يُرِيدُ','جاودانه در آن خواهند ماند، تا آسمانها و زمین برپاست؛ مگر آنچه پروردگارت بخواهد! پروردگارت هر چه را بخواهد انجام می‌دهد!'];
ayat[1581]=[11,108,'۞ وَأَمَّا الَّذِينَ سُعِدُوا فَفِي الْجَنَّةِ خَالِدِينَ فِيهَا مَا دَامَتِ السَّمَاوَاتُ وَالْأَرْضُ إِلَّا مَا شَاءَ رَبُّكَ ۖ عَطَاءً غَيْرَ مَجْذُوذٍ','امّا آنها که خوشبخت و سعادتمند شدند، جاودانه در بهشت خواهند ماند، تا آسمانها و زمین برپاست، مگر آنچه پروردگارت بخواهد! بخششی است قطع نشدنی!'];
ayat[1582]=[11,109,'فَلَا تَكُ فِي مِرْيَةٍ مِّمَّا يَعْبُدُ هَٰؤُلَاءِ ۚ مَا يَعْبُدُونَ إِلَّا كَمَا يَعْبُدُ آبَاؤُهُم مِّن قَبْلُ ۚ وَإِنَّا لَمُوَفُّوهُمْ نَصِيبَهُمْ غَيْرَ مَنقُوصٍ','پس شکّ و تردیدی (در باطل بودن) معبودهایی که آنها می‌پرستند، به خود راه مده! آنها همان‌گونه این معبودها را پرستش می‌کنند که پدرانشان قبلاً می‌پرستیدند، و ما نصیب آنان را بی‌کم و کاست خواهیم داد!'];
ayat[1583]=[11,110,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ فِيهِ ۚ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِي شَكٍّ مِّنْهُ مُرِيبٍ','ما به موسی کتاب آسمانی دادیم؛ سپس در آن اختلاف شد؛ و اگر فرمان قبلی خدا (در زمینه آزمایش و اتمام حجّت بر آنها) نبود، در میان آنان داوری می‌شد! و آنها (هنوز) در شکّ‌اند، شکّی آمیخته به بدگمانی!'];
ayat[1584]=[11,111,'وَإِنَّ كُلًّا لَّمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ أَعْمَالَهُمْ ۚ إِنَّهُ بِمَا يَعْمَلُونَ خَبِيرٌ','و پروردگارت اعمال هر یک را بی‌کم و کاست به آنها خواهد داد؛ او به آنچه عمل می‌کنند آگاه است!'];
ayat[1585]=[11,112,'فَاسْتَقِمْ كَمَا أُمِرْتَ وَمَن تَابَ مَعَكَ وَلَا تَطْغَوْا ۚ إِنَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','پس همان‌گونه که فرمان یافته‌ای، استقامت کن؛ و همچنین کسانی که با تو بسوی خدا آمده‌اند (باید استقامت کنند)! و طغیان نکنید، که خداوند آنچه را انجام می‌دهید می‌بیند!'];
ayat[1586]=[11,113,'وَلَا تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا فَتَمَسَّكُمُ النَّارُ وَمَا لَكُم مِّن دُونِ اللَّهِ مِنْ أَوْلِيَاءَ ثُمَّ لَا تُنصَرُونَ','و بر ظالمان تکیه ننمایید، که موجب می‌شود آتش شما را فرا گیرد؛ و در آن حال، هیچ ولیّ و سرپرستی جز خدا نخواهید داشت؛ و یاری نمی‌شوید!'];
ayat[1587]=[11,114,'وَأَقِمِ الصَّلَاةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِّنَ اللَّيْلِ ۚ إِنَّ الْحَسَنَاتِ يُذْهِبْنَ السَّيِّئَاتِ ۚ ذَٰلِكَ ذِكْرَىٰ لِلذَّاكِرِينَ','در دو طرف روز، و اوایل شب، نماز را برپا دار؛ چرا که حسنات، سیئات (و آثار آنها را) از بین می‌برند؛ این تذکّری است برای کسانی که اهل تذکّرند!'];
ayat[1588]=[11,115,'وَاصْبِرْ فَإِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ','و شکیبایی کن، که خداوند پاداش نیکوکاران را ضایع نخواهد کرد!'];
ayat[1589]=[11,116,'فَلَوْلَا كَانَ مِنَ الْقُرُونِ مِن قَبْلِكُمْ أُولُو بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسَادِ فِي الْأَرْضِ إِلَّا قَلِيلًا مِّمَّنْ أَنجَيْنَا مِنْهُمْ ۗ وَاتَّبَعَ الَّذِينَ ظَلَمُوا مَا أُتْرِفُوا فِيهِ وَكَانُوا مُجْرِمِينَ','چرا در قرون (و اقوام) قبل از شما، دانشمندان صاحب قدرتی نبودند که از فساد در زمین جلوگیری کنند؟! مگر اندکی از آنها، که نجاتشان دادیم! و آنان که ستم می‌کردند، از تنعّم و کامجوئی پیروی کردند؛ و گناهکار بودند (و نابود شدند)!'];
ayat[1590]=[11,117,'وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرَىٰ بِظُلْمٍ وَأَهْلُهَا مُصْلِحُونَ','و چنین نبود که پروردگارت آبادیها را بظلم و ستم نابود کند در حالی که اهلش در صدد اصلاح بوده باشند!'];
ayat[1591]=[11,118,'وَلَوْ شَاءَ رَبُّكَ لَجَعَلَ النَّاسَ أُمَّةً وَاحِدَةً ۖ وَلَا يَزَالُونَ مُخْتَلِفِينَ','و اگر پروردگارت می‌خواست، همه مردم را یک امّت (بدون هیچ گونه اختلاف) قرار می‌داد؛ ولی آنها همواره مختلفند...'];
ayat[1592]=[11,119,'إِلَّا مَن رَّحِمَ رَبُّكَ ۚ وَلِذَٰلِكَ خَلَقَهُمْ ۗ وَتَمَّتْ كَلِمَةُ رَبِّكَ لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ','مگر کسی را که پروردگارت رحم کند! و برای همین (پذیرش رحمت) آنها را آفرید! و فرمان پروردگارت قطعی شده که: جهنّم را از همه (سرکشان و طاغیان) جنّ و انس پر خواهم کرد!'];
ayat[1593]=[11,120,'وَكُلًّا نَّقُصُّ عَلَيْكَ مِنْ أَنبَاءِ الرُّسُلِ مَا نُثَبِّتُ بِهِ فُؤَادَكَ ۚ وَجَاءَكَ فِي هَٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَذِكْرَىٰ لِلْمُؤْمِنِينَ','ما از هر یک از سرگذشتهای انبیا برای تو بازگو کردیم، تا به وسیله آن، قلبت را آرامش بخشیم؛ و اراده‌ات قوّی گردد. و در این (اخبار و سرگذشتها،) برای تو حقّ، و برای مؤمنان موعظه و تذکّر آمده است.'];
ayat[1594]=[11,121,'وَقُل لِّلَّذِينَ لَا يُؤْمِنُونَ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنَّا عَامِلُونَ','و به آنها که ایمان نمی‌آورند، بگو: «هر چه در قدرت دارید، انجام دهید! ما هم انجام می‌دهیم!'];
ayat[1595]=[11,122,'وَانتَظِرُوا إِنَّا مُنتَظِرُونَ','و انتظار بکشید! ما هم منتظریم!'];
ayat[1596]=[11,123,'وَلِلَّهِ غَيْبُ السَّمَاوَاتِ وَالْأَرْضِ وَإِلَيْهِ يُرْجَعُ الْأَمْرُ كُلُّهُ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِ ۚ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ','و (آگاهی از) غیب (و اسرار نهان) آسمانها و زمین، تنها از آن خداست؛ و همه کارها به سوی او بازمی‌گردد! پس او را پرستش کن! و بر او توکّل نما! و پروردگارت از کارهایی که می‌کنید، هرگز غافل نیست!'];
ayat[1597]=[12,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ','الر، آن آیات کتاب آشکار است!'];
ayat[1598]=[12,2,'إِنَّا أَنزَلْنَاهُ قُرْآنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُونَ','ما آن را قرآنی عربی نازل کردیم، شاید شما درک کنید (و بیندیشید)!'];
ayat[1599]=[12,3,'نَحْنُ نَقُصُّ عَلَيْكَ أَحْسَنَ الْقَصَصِ بِمَا أَوْحَيْنَا إِلَيْكَ هَٰذَا الْقُرْآنَ وَإِن كُنتَ مِن قَبْلِهِ لَمِنَ الْغَافِلِينَ','ما بهترین سرگذشتها را از طریق این قرآن -که به تو وحی کردیم- بر تو بازگو می‌کنیم؛ و مسلّماً پیش از این، از آن خبر نداشتی!'];
ayat[1600]=[12,4,'إِذْ قَالَ يُوسُفُ لِأَبِيهِ يَا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَبًا وَالشَّمْسَ وَالْقَمَرَ رَأَيْتُهُمْ لِي سَاجِدِينَ','(به خاطر بیاور) هنگامی را که یوسف به پدرش گفت: «پدرم! من در خواب دیدم که یازده ستاره، و خورشید و ماه در برابرم سجده می‌کنند!»'];
ayat[1601]=[12,5,'قَالَ يَا بُنَيَّ لَا تَقْصُصْ رُؤْيَاكَ عَلَىٰ إِخْوَتِكَ فَيَكِيدُوا لَكَ كَيْدًا ۖ إِنَّ الشَّيْطَانَ لِلْإِنسَانِ عَدُوٌّ مُّبِينٌ','گفت: «فرزندم! خواب خود را برای برادرانت بازگو مکن، که برای تو نقشه (خطرناکی) می‌کشند؛ چرا که شیطان، دشمن آشکار انسان است!'];
ayat[1602]=[12,6,'وَكَذَٰلِكَ يَجْتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِن تَأْوِيلِ الْأَحَادِيثِ وَيُتِمُّ نِعْمَتَهُ عَلَيْكَ وَعَلَىٰ آلِ يَعْقُوبَ كَمَا أَتَمَّهَا عَلَىٰ أَبَوَيْكَ مِن قَبْلُ إِبْرَاهِيمَ وَإِسْحَاقَ ۚ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٌ','و این گونه پروردگارت تو را برمی‌گزیند؛ و از تعبیر خوابها به تو می‌آموزد؛ و نعمتش را بر تو و بر خاندان یعقوب تمام و کامل می‌کند، همان‌گونه که پیش از این، بر پدرانت ابراهیم و اسحاق تمام کرد؛ به یقین، پروردگار تو دانا و حکیم است!»'];
ayat[1603]=[12,7,'۞ لَّقَدْ كَانَ فِي يُوسُفَ وَإِخْوَتِهِ آيَاتٌ لِّلسَّائِلِينَ','در (داستان) یوسف و برادرانش، نشانه‌ها (ی هدایت) برای سؤال‌کنندگان بود!'];
ayat[1604]=[12,8,'إِذْ قَالُوا لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰ أَبِينَا مِنَّا وَنَحْنُ عُصْبَةٌ إِنَّ أَبَانَا لَفِي ضَلَالٍ مُّبِينٍ','هنگامی که (برادران) گفتند: «یوسف و برادرش [= بنیامین‌] نزد پدر، از ما محبوبترند؛ در حالی که ما گروه نیرومندی هستیم! مسلّماً پدر ما، در گمراهی آشکاری است!'];
ayat[1605]=[12,9,'اقْتُلُوا يُوسُفَ أَوِ اطْرَحُوهُ أَرْضًا يَخْلُ لَكُمْ وَجْهُ أَبِيكُمْ وَتَكُونُوا مِن بَعْدِهِ قَوْمًا صَالِحِينَ','یوسف را بکشید؛ یا او را به سرزمین دوردستی بیفکنید؛ تا توجه پدر، فقط به شما باشد؛ و بعد از آن، (از گناه خود توبه می‌کنید؛ و) افراد صالحی خواهید بود!'];
ayat[1606]=[12,10,'قَالَ قَائِلٌ مِّنْهُمْ لَا تَقْتُلُوا يُوسُفَ وَأَلْقُوهُ فِي غَيَابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ إِن كُنتُمْ فَاعِلِينَ','یکی از آنها گفت: «یوسف را نکشید! و اگر می‌خواهید کاری انجام دهید، او را در نهانگاه چاه بیفکنید؛ تا بعضی از قافله‌ها او را برگیرند (و با خود به مکان دوری ببرند)!»'];
ayat[1607]=[12,11,'قَالُوا يَا أَبَانَا مَا لَكَ لَا تَأْمَنَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُ لَنَاصِحُونَ','(و برای انجام این کار، برادران نزد پدر آمدند و) گفتند: «پدرجان! چرا تو درباره (برادرمان) یوسف، به ما اطمینان نمی‌کنی؟! در حالی که ما خیرخواه او هستیم!'];
ayat[1608]=[12,12,'أَرْسِلْهُ مَعَنَا غَدًا يَرْتَعْ وَيَلْعَبْ وَإِنَّا لَهُ لَحَافِظُونَ','فردا او را با ما (به خارج شهر) بفرست، تا غذای کافی بخورد و تفریح کند؛ و ما نگهبان او هستیم!»'];
ayat[1609]=[12,13,'قَالَ إِنِّي لَيَحْزُنُنِي أَن تَذْهَبُوا بِهِ وَأَخَافُ أَن يَأْكُلَهُ الذِّئْبُ وَأَنتُمْ عَنْهُ غَافِلُونَ','(پدر) گفت: «من از بردن او غمگین می‌شوم؛ و از این می‌ترسم که گرگ او را بخورد، و شما از او غافل باشید!»'];
ayat[1610]=[12,14,'قَالُوا لَئِنْ أَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ إِنَّا إِذًا لَّخَاسِرُونَ','گفتند: «با اینکه ما گروه نیرومندی هستیم، اگر گرگ او را بخورد، ما از زیانکاران خواهیم بود (و هرگز چنین چیزی) ممکن نیست!)»'];
ayat[1611]=[12,15,'فَلَمَّا ذَهَبُوا بِهِ وَأَجْمَعُوا أَن يَجْعَلُوهُ فِي غَيَابَتِ الْجُبِّ ۚ وَأَوْحَيْنَا إِلَيْهِ لَتُنَبِّئَنَّهُم بِأَمْرِهِمْ هَٰذَا وَهُمْ لَا يَشْعُرُونَ','هنگامی که او را با خود بردند، و تصمیم گرفتند وی را در مخفی‌گاه چاه قرار دهند، (سرانجام مقصد خود را عملی ساختند؛) و به او وحی فرستادیم که آنها را در آینده از این کارشان با خبر خواهی ساخت؛ در حالی که آنها نمی‌دانند!'];
ayat[1612]=[12,16,'وَجَاءُوا أَبَاهُمْ عِشَاءً يَبْكُونَ','(برادران یوسف) شب هنگام، گریان به سراغ پدر آمدند.'];
ayat[1613]=[12,17,'قَالُوا يَا أَبَانَا إِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوسُفَ عِندَ مَتَاعِنَا فَأَكَلَهُ الذِّئْبُ ۖ وَمَا أَنتَ بِمُؤْمِنٍ لَّنَا وَلَوْ كُنَّا صَادِقِينَ','گفتند: «ای پدر! ما رفتیم و مشغول مسابقه شدیم، و یوسف را نزد اثاث خود گذاردیم؛ و گرگ او را خورد! تو هرگز سخن ما را باور نخواهی کرد، هر چند راستگو باشیم!»'];
ayat[1614]=[12,18,'وَجَاءُوا عَلَىٰ قَمِيصِهِ بِدَمٍ كَذِبٍ ۚ قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنفُسُكُمْ أَمْرًا ۖ فَصَبْرٌ جَمِيلٌ ۖ وَاللَّهُ الْمُسْتَعَانُ عَلَىٰ مَا تَصِفُونَ','و پیراهن او را با خونی دروغین (آغشته ساخته، نزد پدر) آوردند؛ گفت: «هوسهای نفسانی شما این کار را برایتان آراسته! من صبر جمیل (و شکیبائی خالی از ناسپاسی) خواهم داشت؛ و در برابر آنچه می‌گویید، از خداوند یاری می‌طلبم!»'];
ayat[1615]=[12,19,'وَجَاءَتْ سَيَّارَةٌ فَأَرْسَلُوا وَارِدَهُمْ فَأَدْلَىٰ دَلْوَهُ ۖ قَالَ يَا بُشْرَىٰ هَٰذَا غُلَامٌ ۚ وَأَسَرُّوهُ بِضَاعَةً ۚ وَاللَّهُ عَلِيمٌ بِمَا يَعْمَلُونَ','و (در همین حال) کاروانی فرا رسید؛ و مأمور آب را (به سراغ آب) فرستادند؛ او دلو خود را در چاه افکند؛ (ناگهان) صدا زد: «مژده باد! این کودکی است (زیبا و دوست داشتنی!)» و این امر را بعنوان یک سرمایه از دیگران مخفی داشتند. و خداوند به آنچه آنها انجام می‌دادند، آگاه بود.'];
ayat[1616]=[12,20,'وَشَرَوْهُ بِثَمَنٍ بَخْسٍ دَرَاهِمَ مَعْدُودَةٍ وَكَانُوا فِيهِ مِنَ الزَّاهِدِينَ','و (سرانجام،) او را به بهای کمی -چند درهم- فروختند؛ و نسبت به (فروختن) او، بی رغبت بودند (؛ چرا که می‌ترسیدند رازشان فاش شود).'];
ayat[1617]=[12,21,'وَقَالَ الَّذِي اشْتَرَاهُ مِن مِّصْرَ لِامْرَأَتِهِ أَكْرِمِي مَثْوَاهُ عَسَىٰ أَن يَنفَعَنَا أَوْ نَتَّخِذَهُ وَلَدًا ۚ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ وَلِنُعَلِّمَهُ مِن تَأْوِيلِ الْأَحَادِيثِ ۚ وَاللَّهُ غَالِبٌ عَلَىٰ أَمْرِهِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','و آن کس که او را از سرزمین مصر خرید [= عزیز مصر]، به همسرش گفت: «مقام وی را گرامی دار، شاید برای ما سودمند باشد؛ و یا او را بعنوان فرزند انتخاب کنیم!» و اینچنین یوسف را در آن سرزمین متمکّن ساختیم! (ما این کار را کردیم، تا او را بزرگ داریم؛ و) از علم تعبیر خواب به او بیاموزیم؛ خداوند بر کار خود پیروز است، ولی بیشتر مردم نمی‌دانند!'];
ayat[1618]=[12,22,'وَلَمَّا بَلَغَ أَشُدَّهُ آتَيْنَاهُ حُكْمًا وَعِلْمًا ۚ وَكَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','و هنگامی که به بلوغ و قوّت رسید، ما «حکم» [= نبوّت‌] و «علم» به او دادیم؛ و اینچنین نیکوکاران را پاداش می‌دهیم!'];
ayat[1619]=[12,23,'وَرَاوَدَتْهُ الَّتِي هُوَ فِي بَيْتِهَا عَن نَّفْسِهِ وَغَلَّقَتِ الْأَبْوَابَ وَقَالَتْ هَيْتَ لَكَ ۚ قَالَ مَعَاذَ اللَّهِ ۖ إِنَّهُ رَبِّي أَحْسَنَ مَثْوَايَ ۖ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ','و آن زن که یوسف در خانه او بود، از او تمنّای کامجویی کرد؛ درها را بست و گفت: «بیا (بسوی آنچه برای تو مهیاست!)» (یوسف) گفت: «پناه می‌برم به خدا! او [= عزیز مصر] صاحب نعمت من است؛ مقام مرا گرامی داشته؛ (آیا ممکن است به او ظلم و خیانت کنم؟!) مسلّماً ظالمان رستگار نمی‌شوند!»'];
ayat[1620]=[12,24,'وَلَقَدْ هَمَّتْ بِهِ ۖ وَهَمَّ بِهَا لَوْلَا أَن رَّأَىٰ بُرْهَانَ رَبِّهِ ۚ كَذَٰلِكَ لِنَصْرِفَ عَنْهُ السُّوءَ وَالْفَحْشَاءَ ۚ إِنَّهُ مِنْ عِبَادِنَا الْمُخْلَصِينَ','آن زن قصد او کرد؛ و او نیز -اگر برهان پروردگار را نمی‌دید- قصد وی می‌نمود! اینچنین کردیم تا بدی و فحشا را از او دور سازیم؛ چرا که او از بندگان مخلص ما بود!'];
ayat[1621]=[12,25,'وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيصَهُ مِن دُبُرٍ وَأَلْفَيَا سَيِّدَهَا لَدَى الْبَابِ ۚ قَالَتْ مَا جَزَاءُ مَنْ أَرَادَ بِأَهْلِكَ سُوءًا إِلَّا أَن يُسْجَنَ أَوْ عَذَابٌ أَلِيمٌ','و هر دو به سوی در، دویدند (در حالی که همسر عزیز، یوسف را تعقیب می‌کرد)؛ و پیراهن او را از پشت (کشید و) پاره کرد. و در این هنگام، آقای آن زن را دم در یافتند! آن زن گفت: «کیفر کسی که بخواهد نسبت به اهل تو خیانت کند، جز زندان و یا عذاب دردناک، چه خواهد بود؟!»'];
ayat[1622]=[12,26,'قَالَ هِيَ رَاوَدَتْنِي عَن نَّفْسِي ۚ وَشَهِدَ شَاهِدٌ مِّنْ أَهْلِهَا إِن كَانَ قَمِيصُهُ قُدَّ مِن قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكَاذِبِينَ','(یوسف) گفت: «او مرا با اصرار به سوی خود دعوت کرد!» و در این هنگام، شاهدی از خانواده آن زن شهادت داد که: «اگر پیراهن او از پیش رو پاره شده، آن آن راست می‌گوید، و او از دروغگویان است.'];
ayat[1623]=[12,27,'وَإِن كَانَ قَمِيصُهُ قُدَّ مِن دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصَّادِقِينَ','و اگر پیراهنش از پشت پاره شده، آن زن دروغ می‌گوید، و او از راستگویان است.»'];
ayat[1624]=[12,28,'فَلَمَّا رَأَىٰ قَمِيصَهُ قُدَّ مِن دُبُرٍ قَالَ إِنَّهُ مِن كَيْدِكُنَّ ۖ إِنَّ كَيْدَكُنَّ عَظِيمٌ','هنگامی که (عزیز مصر) دید پیراهن او [= یوسف‌] از پشت پاره شده، گفت: «این از مکر و حیله شما زنان است؛ که مکر و حیله شما زنان، عظیم است!'];
ayat[1625]=[12,29,'يُوسُفُ أَعْرِضْ عَنْ هَٰذَا ۚ وَاسْتَغْفِرِي لِذَنبِكِ ۖ إِنَّكِ كُنتِ مِنَ الْخَاطِئِينَ','یوسف از این موضوع، صرف‌نظر کن! و تو ای زن نیز از گناهت استغفار کن، که از خطاکاران بودی!»'];
ayat[1626]=[12,30,'۞ وَقَالَ نِسْوَةٌ فِي الْمَدِينَةِ امْرَأَتُ الْعَزِيزِ تُرَاوِدُ فَتَاهَا عَن نَّفْسِهِ ۖ قَدْ شَغَفَهَا حُبًّا ۖ إِنَّا لَنَرَاهَا فِي ضَلَالٍ مُّبِينٍ','(این جریان در شهر منعکس شد؛) گروهی از زنان شهر گفتند: «همسر عزیز، جوانش [= غلامش‌] را بسوی خود دعوت می‌کند! عشق این جوان، در اعماق قلبش نفوذ کرده، ما او را در گمراهی آشکاری می‌بینیم!»'];
ayat[1627]=[12,31,'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ أَرْسَلَتْ إِلَيْهِنَّ وَأَعْتَدَتْ لَهُنَّ مُتَّكَأً وَآتَتْ كُلَّ وَاحِدَةٍ مِّنْهُنَّ سِكِّينًا وَقَالَتِ اخْرُجْ عَلَيْهِنَّ ۖ فَلَمَّا رَأَيْنَهُ أَكْبَرْنَهُ وَقَطَّعْنَ أَيْدِيَهُنَّ وَقُلْنَ حَاشَ لِلَّهِ مَا هَٰذَا بَشَرًا إِنْ هَٰذَا إِلَّا مَلَكٌ كَرِيمٌ','هنگامی که (همسر عزیز) از فکر آنها باخبر شد، به سراغشان فرستاد (و از آنها دعوت کرد)؛ و برای آنها پشتی (گرانبها، و مجلس باشکوهی) فراهم ساخت؛ و به دست هر کدام، چاقویی (برای بریدن میوه) داد؛ و در این موقع (به یوسف) گفت: «وارد مجلس آنان شو!» هنگامی که چشمشان به او افتاد، او را بسیار بزرگ (و زیبا) شمردند؛ و (بی‌توجه) دستهای خود را بریدند؛ و گفتند: «منزّه است خدا! این بشر نیست؛ این یک فرشته بزرگوار است!»'];
ayat[1628]=[12,32,'قَالَتْ فَذَٰلِكُنَّ الَّذِي لُمْتُنَّنِي فِيهِ ۖ وَلَقَدْ رَاوَدتُّهُ عَن نَّفْسِهِ فَاسْتَعْصَمَ ۖ وَلَئِن لَّمْ يَفْعَلْ مَا آمُرُهُ لَيُسْجَنَنَّ وَلَيَكُونًا مِّنَ الصَّاغِرِينَ','(همسر عزیز) گفت: «این همان کسی است که بخاطر (عشق) او مرا سرزنش کردید! (آری،) من او را به خویشتن دعوت کردم؛ و او خودداری کرد! و اگر آنچه را دستور می‌دهم انجام ندهد، به زندان خواهد افتاد؛ و مسلّماً خوار و ذلیل خواهد شد!»'];
ayat[1629]=[12,33,'قَالَ رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِي إِلَيْهِ ۖ وَإِلَّا تَصْرِفْ عَنِّي كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ وَأَكُن مِّنَ الْجَاهِلِينَ','(یوسف) گفت: «پروردگارا! زندان نزد من محبوبتر است از آنچه اینها مرا بسوی آن می‌خوانند! و اگر مکر و نیرنگ آنها را از من باز نگردانی، بسوی آنان متمایل خواهم شد و از جاهلان خواهم بود!»'];
ayat[1630]=[12,34,'فَاسْتَجَابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّ ۚ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ','پروردگارش دعای او را اجابت کرد؛ و مکر آنان را از او بگردانید؛ چرا که او شنوا و داناست!'];
ayat[1631]=[12,35,'ثُمَّ بَدَا لَهُم مِّن بَعْدِ مَا رَأَوُا الْآيَاتِ لَيَسْجُنُنَّهُ حَتَّىٰ حِينٍ','و بعد از آنکه نشانه‌های (پاکی یوسف) را دیدند، تصمیم گرفتند او را تا مدّتی زندانی کنند!'];
ayat[1632]=[12,36,'وَدَخَلَ مَعَهُ السِّجْنَ فَتَيَانِ ۖ قَالَ أَحَدُهُمَا إِنِّي أَرَانِي أَعْصِرُ خَمْرًا ۖ وَقَالَ الْآخَرُ إِنِّي أَرَانِي أَحْمِلُ فَوْقَ رَأْسِي خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ ۖ نَبِّئْنَا بِتَأْوِيلِهِ ۖ إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ','و دو جوان، همراه او وارد زندان شدند؛ یکی از آن دو گفت: «من در خواب دیدم که (انگور برای) شراب می‌فشارم!» و دیگری گفت: «من در خواب دیدم که نان بر سرم حمل می‌کنم؛ و پرندگان از آن می‌خورند؛ ما را از تعبیر این خواب آگاه کن که تو را از نیکوکاران می‌بینیم.»'];
ayat[1633]=[12,37,'قَالَ لَا يَأْتِيكُمَا طَعَامٌ تُرْزَقَانِهِ إِلَّا نَبَّأْتُكُمَا بِتَأْوِيلِهِ قَبْلَ أَن يَأْتِيَكُمَا ۚ ذَٰلِكُمَا مِمَّا عَلَّمَنِي رَبِّي ۚ إِنِّي تَرَكْتُ مِلَّةَ قَوْمٍ لَّا يُؤْمِنُونَ بِاللَّهِ وَهُم بِالْآخِرَةِ هُمْ كَافِرُونَ','(یوسف) گفت: «پیش از آنکه جیره غذایی شما فرا رسد، شما را از تعبیر خوابتان آگاه خواهم ساخت. این، از دانشی است که پروردگارم به من آموخته است. من آیین قومی را که به خدا ایمان ندارند، و به سرای دیگر کافرند، ترک گفتم (و شایسته چنین موهبتی شدم)!'];
ayat[1634]=[12,38,'وَاتَّبَعْتُ مِلَّةَ آبَائِي إِبْرَاهِيمَ وَإِسْحَاقَ وَيَعْقُوبَ ۚ مَا كَانَ لَنَا أَن نُّشْرِكَ بِاللَّهِ مِن شَيْءٍ ۚ ذَٰلِكَ مِن فَضْلِ اللَّهِ عَلَيْنَا وَعَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ','من از آیین پدرانم ابراهیم و اسحاق و یعقوب پیروی کردم! برای ما شایسته نبود چیزی را همتای خدا قرار دهیم؛ این از فضل خدا بر ما و بر مردم است؛ ولی بیشتر مردم شکرگزاری نمی‌کنند!'];
ayat[1635]=[12,39,'يَا صَاحِبَيِ السِّجْنِ أَأَرْبَابٌ مُّتَفَرِّقُونَ خَيْرٌ أَمِ اللَّهُ الْوَاحِدُ الْقَهَّارُ','ای دوستان زندانی من! آیا خدایان پراکنده بهترند، یا خداوند یکتای پیروز؟!'];
ayat[1636]=[12,40,'مَا تَعْبُدُونَ مِن دُونِهِ إِلَّا أَسْمَاءً سَمَّيْتُمُوهَا أَنتُمْ وَآبَاؤُكُم مَّا أَنزَلَ اللَّهُ بِهَا مِن سُلْطَانٍ ۚ إِنِ الْحُكْمُ إِلَّا لِلَّهِ ۚ أَمَرَ أَلَّا تَعْبُدُوا إِلَّا إِيَّاهُ ۚ ذَٰلِكَ الدِّينُ الْقَيِّمُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','این معبودهایی که غیر از خدا می‌پرستید، چیزی جز اسمهائی (بی‌مسمّا) که شما و پدرانتان آنها را خدا نامیده‌اید، نیست؛ خداوند هیچ دلیلی بر آن نازل نکرده؛ حکم تنها از آن خداست؛ فرمان داده که غیر از او را نپرستید! این است آیین پابرجا؛ ولی بیشتر مردم نمی‌دانند!'];
ayat[1637]=[12,41,'يَا صَاحِبَيِ السِّجْنِ أَمَّا أَحَدُكُمَا فَيَسْقِي رَبَّهُ خَمْرًا ۖ وَأَمَّا الْآخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِن رَّأْسِهِ ۚ قُضِيَ الْأَمْرُ الَّذِي فِيهِ تَسْتَفْتِيَانِ','ای دوستان زندانی من! امّا یکی از شما (دو نفر، آزاد می‌شود؛ و) ساقی شراب برای صاحب خود خواهد شد؛ و امّا دیگری به دار آویخته می‌شود؛ و پرندگان از سر او می‌خورند! و مطلبی که درباره آن (از من) نظر خواستید، قطعی و حتمی است!»'];
ayat[1638]=[12,42,'وَقَالَ لِلَّذِي ظَنَّ أَنَّهُ نَاجٍ مِّنْهُمَا اذْكُرْنِي عِندَ رَبِّكَ فَأَنسَاهُ الشَّيْطَانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ','و به آن یکی از آن دو نفر، که می‌دانست رهایی می‌یابد، گفت: «مرا نزد صاحبت [= سلطان مصر] یادآوری کن!» ولی شیطان یادآوری او را نزد صاحبش از خاطر وی برد؛ و بدنبال آن، (یوسف) چند سال در زندان باقی ماند.'];
ayat[1639]=[12,43,'وَقَالَ الْمَلِكُ إِنِّي أَرَىٰ سَبْعَ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعَ سُنبُلَاتٍ خُضْرٍ وَأُخَرَ يَابِسَاتٍ ۖ يَا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي رُؤْيَايَ إِن كُنتُمْ لِلرُّؤْيَا تَعْبُرُونَ','پادشاه گفت: «من در خواب دیدم هفت گاو چاق را که هفت گاو لاغر آنها را می‌خورند؛ و هفت خوشه سبز و هفت خوشه خشکیده؛ (که خشکیده‌ها بر سبزها پیچیدند؛ و آنها را از بین بردند.) ای جمعیّت اشراف! درباره خواب من نظر دهید، اگر خواب را تعبیر می‌کنید!»'];
ayat[1640]=[12,44,'قَالُوا أَضْغَاثُ أَحْلَامٍ ۖ وَمَا نَحْنُ بِتَأْوِيلِ الْأَحْلَامِ بِعَالِمِينَ','گفتند: «خوابهای پریشان و پراکنده‌ای است؛ و ما از تعبیر این گونه خوابها آگاه نیستیم!»'];
ayat[1641]=[12,45,'وَقَالَ الَّذِي نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ أُمَّةٍ أَنَا أُنَبِّئُكُم بِتَأْوِيلِهِ فَأَرْسِلُونِ','و یکی از آن دو که نجات یافته بود -و بعد از مدّتی به خاطرش آمد- گفت: «من تأویل آن را به شما خبر می‌دهم؛ مرا (به سراغ آن جوان زندانی) بفرستید!»'];
ayat[1642]=[12,46,'يُوسُفُ أَيُّهَا الصِّدِّيقُ أَفْتِنَا فِي سَبْعِ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَسَبْعِ سُنبُلَاتٍ خُضْرٍ وَأُخَرَ يَابِسَاتٍ لَّعَلِّي أَرْجِعُ إِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُونَ','(او به زندان آمد، و چنین گفت:) یوسف، ای مرد بسیار راستگو! درباره این خواب اظهار نظر کن که هفت گاو چاق را هفت گاو لاغر می‌خورند؛ و هفت خوشه تر، و هفت خوشه خشکیده؛ تا من بسوی مردم بازگردم، شاید (از تعبیر این خواب) آگاه شوند!'];
ayat[1643]=[12,47,'قَالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأَبًا فَمَا حَصَدتُّمْ فَذَرُوهُ فِي سُنبُلِهِ إِلَّا قَلِيلًا مِّمَّا تَأْكُلُونَ','گفت: «هفت سال با جدیّت زراعت می‌کنید؛ و آنچه را درو کردید، جز کمی که می‌خورید، در خوشه‌های خود باقی بگذارید (و ذخیره نمایید).'];
ayat[1644]=[12,48,'ثُمَّ يَأْتِي مِن بَعْدِ ذَٰلِكَ سَبْعٌ شِدَادٌ يَأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ إِلَّا قَلِيلًا مِّمَّا تُحْصِنُونَ','پس از آن، هفت سال سخت (و خشکی و قحطی) می‌آید، که آنچه را برای آن سالها ذخیره کرده‌اید، می‌خورند؛ جز کمی که (برای بذر) ذخیره خواهید کرد.'];
ayat[1645]=[12,49,'ثُمَّ يَأْتِي مِن بَعْدِ ذَٰلِكَ عَامٌ فِيهِ يُغَاثُ النَّاسُ وَفِيهِ يَعْصِرُونَ','سپس سالی فرامی‌رسد که باران فراوان نصیب مردم می‌شود؛ و در آن سال، مردم عصاره (میوه‌ها و دانه‌های روغنی را) می‌گیرند (و سال پر برکتی است.)»'];
ayat[1646]=[12,50,'وَقَالَ الْمَلِكُ ائْتُونِي بِهِ ۖ فَلَمَّا جَاءَهُ الرَّسُولُ قَالَ ارْجِعْ إِلَىٰ رَبِّكَ فَاسْأَلْهُ مَا بَالُ النِّسْوَةِ اللَّاتِي قَطَّعْنَ أَيْدِيَهُنَّ ۚ إِنَّ رَبِّي بِكَيْدِهِنَّ عَلِيمٌ','پادشاه گفت: «او را نزد من آورید!» ولی هنگامی که فرستاده او نزد وی [= یوسف‌] آمد گفت: «به سوی صاحبت بازگرد، و از او بپرس ماجرای زنانی که دستهای خود را بریدند چه بود؟ که خدای من به نیرنگ آنها آگاه است.»'];
ayat[1647]=[12,51,'قَالَ مَا خَطْبُكُنَّ إِذْ رَاوَدتُّنَّ يُوسُفَ عَن نَّفْسِهِ ۚ قُلْنَ حَاشَ لِلَّهِ مَا عَلِمْنَا عَلَيْهِ مِن سُوءٍ ۚ قَالَتِ امْرَأَتُ الْعَزِيزِ الْآنَ حَصْحَصَ الْحَقُّ أَنَا رَاوَدتُّهُ عَن نَّفْسِهِ وَإِنَّهُ لَمِنَ الصَّادِقِينَ','(پادشاه آن زنان را طلبید و) گفت: «به هنگامی که یوسف را به سوی خویش دعوت کردید، جریان کار شما چه بود؟» گفتند: «منزّه است خدا، ما هیچ عیبی در او نیافتیم!» (در این هنگام) همسر عزیز گفت: «الآن حق آشکار گشت! من بودم که او را به سوی خود دعوت کردم؛ و او از راستگویان است!'];
ayat[1648]=[12,52,'ذَٰلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ وَأَنَّ اللَّهَ لَا يَهْدِي كَيْدَ الْخَائِنِينَ','این سخن را بخاطر آن گفتم تا بداند من در غیاب به او خیانت نکردم؛ و خداوند مکر خائنان را هدایت نمی‌کند!'];
ayat[1649]=[12,53,'۞ وَمَا أُبَرِّئُ نَفْسِي ۚ إِنَّ النَّفْسَ لَأَمَّارَةٌ بِالسُّوءِ إِلَّا مَا رَحِمَ رَبِّي ۚ إِنَّ رَبِّي غَفُورٌ رَّحِيمٌ','من هرگز خودم را تبرئه نمی‌کنم، که نفس (سرکش) بسیار به بدیها امر می‌کند؛ مگر آنچه را پروردگارم رحم کند! پروردگارم آمرزنده و مهربان است.»'];
ayat[1650]=[12,54,'وَقَالَ الْمَلِكُ ائْتُونِي بِهِ أَسْتَخْلِصْهُ لِنَفْسِي ۖ فَلَمَّا كَلَّمَهُ قَالَ إِنَّكَ الْيَوْمَ لَدَيْنَا مَكِينٌ أَمِينٌ','پادشاه گفت: «او [= یوسف‌] را نزد من آورید، تا وی را مخصوص خود گردانم!» هنگامی که (یوسف نزد وی آمد و) با او صحبت کرد، (پادشاه به عقل و درایت او پی برد؛ و) گفت: «تو امروز نزد ما جایگاهی والا داری، و مورد اعتماد هستی!»'];
ayat[1651]=[12,55,'قَالَ اجْعَلْنِي عَلَىٰ خَزَائِنِ الْأَرْضِ ۖ إِنِّي حَفِيظٌ عَلِيمٌ','(یوسف) گفت: «مرا سرپرست خزائن سرزمین (مصر) قرار ده، که نگهدارنده و آگاهم!»'];
ayat[1652]=[12,56,'وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي الْأَرْضِ يَتَبَوَّأُ مِنْهَا حَيْثُ يَشَاءُ ۚ نُصِيبُ بِرَحْمَتِنَا مَن نَّشَاءُ ۖ وَلَا نُضِيعُ أَجْرَ الْمُحْسِنِينَ','و این‌گونه ما به یوسف در سرزمین (مصر) قدرت دادیم، که هر جا می‌خواست در آن منزل می‌گزید (و تصرّف می‌کرد)! ما رحمت خود را به هر کس بخواهیم (و شایسته بدانیم) میبخشیم؛ و پاداش نیکوکاران را ضایع نمی‌کنیم!'];
ayat[1653]=[12,57,'وَلَأَجْرُ الْآخِرَةِ خَيْرٌ لِّلَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ','(امّا) پاداش آخرت، برای کسانی که ایمان آورده و پرهیزگاری داشتند، بهتر است!'];
ayat[1654]=[12,58,'وَجَاءَ إِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهُ مُنكِرُونَ','(سرزمین کنعان را قحطی فرا گرفت؛) برادران یوسف (در پی موادّ غذایی به مصر) آمدند؛ و بر او وارد شدند. او آنان را شناخت؛ ولی آنها او را نشناختند.'];
ayat[1655]=[12,59,'وَلَمَّا جَهَّزَهُم بِجَهَازِهِمْ قَالَ ائْتُونِي بِأَخٍ لَّكُم مِّنْ أَبِيكُمْ ۚ أَلَا تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ وَأَنَا خَيْرُ الْمُنزِلِينَ','و هنگامی که (یوسف) بارهای آنان را آماده ساخت، گفت: «(نوبت آینده) آن برادری را که از پدر دارید، نزد من آورید! آیا نمی‌بینید من حق پیمانه را ادا می‌کنم، و من بهترین میزبانان هستم؟!'];
ayat[1656]=[12,60,'فَإِن لَّمْ تَأْتُونِي بِهِ فَلَا كَيْلَ لَكُمْ عِندِي وَلَا تَقْرَبُونِ','و اگر او را نزد من نیاورید، نه کیل (و پیمانه‌ای از غلّه) نزد من خواهید داشت؛ و نه (اصلاً) به من نزدیک شوید!»'];
ayat[1657]=[12,61,'قَالُوا سَنُرَاوِدُ عَنْهُ أَبَاهُ وَإِنَّا لَفَاعِلُونَ','گفتند: «ما با پدرش گفتگو خواهیم کرد؛ (و سعی می‌کنیم موافقتش را جلب نمائیم؛) و ما این کار را خواهیم کرد!»'];
ayat[1658]=[12,62,'وَقَالَ لِفِتْيَانِهِ اجْعَلُوا بِضَاعَتَهُمْ فِي رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُونَهَا إِذَا انقَلَبُوا إِلَىٰ أَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ','(سپس) به کارگزاران خود گفت: «آنچه را بعنوان قیمت پرداخته‌اند، در بارهایشان بگذارید! شاید پس از بازگشت به خانواده خویش، آن را بشناسند؛ و شاید برگردند!»'];
ayat[1659]=[12,63,'فَلَمَّا رَجَعُوا إِلَىٰ أَبِيهِمْ قَالُوا يَا أَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَأَرْسِلْ مَعَنَا أَخَانَا نَكْتَلْ وَإِنَّا لَهُ لَحَافِظُونَ','هنگامی که به سوی پدرشان بازگشتند، گفتند: «ای پدر! دستور داده شده که (بدون حضور برادرمان بنیامین) پیمانه‌ای (از غلّه) به ما ندهند؛ پس برادرمان را با ما بفرست، تا سهمی (از غلّه) دریافت داریم؛ و ما او را محافظت خواهیم کرد!»'];
ayat[1660]=[12,64,'قَالَ هَلْ آمَنُكُمْ عَلَيْهِ إِلَّا كَمَا أَمِنتُكُمْ عَلَىٰ أَخِيهِ مِن قَبْلُ ۖ فَاللَّهُ خَيْرٌ حَافِظًا ۖ وَهُوَ أَرْحَمُ الرَّاحِمِينَ','گفت: «آیا نسبت به او به شما اطمینان کنم همان‌گونه که نسبت به برادرش (یوسف) اطمینان کردم (و دیدید چه شد)؟! و (در هر حال،) خداوند بهترین حافظ، و مهربانترین مهربانان است»'];
ayat[1661]=[12,65,'وَلَمَّا فَتَحُوا مَتَاعَهُمْ وَجَدُوا بِضَاعَتَهُمْ رُدَّتْ إِلَيْهِمْ ۖ قَالُوا يَا أَبَانَا مَا نَبْغِي ۖ هَٰذِهِ بِضَاعَتُنَا رُدَّتْ إِلَيْنَا ۖ وَنَمِيرُ أَهْلَنَا وَنَحْفَظُ أَخَانَا وَنَزْدَادُ كَيْلَ بَعِيرٍ ۖ ذَٰلِكَ كَيْلٌ يَسِيرٌ','و هنگامی که متاع خود را گشودند، دیدند سرمایه آنها به آنها بازگردانده شده! گفتند: «پدر! ما دیگر چه میخواهیم؟! این سرمایه ماست که به ما باز پس گردانده شده است! (پس چه بهتر که برادر را با ما بفرستی؛) و ما برای خانواده خویش موادّ غذایی می‌آوریم؛ و برادرمان را حفظ خواهیم کرد؛ و یک بار شتر زیادتر دریافت خواهیم داشت؛ این پیمانه (بار) کوچکی است!»'];
ayat[1662]=[12,66,'قَالَ لَنْ أُرْسِلَهُ مَعَكُمْ حَتَّىٰ تُؤْتُونِ مَوْثِقًا مِّنَ اللَّهِ لَتَأْتُنَّنِي بِهِ إِلَّا أَن يُحَاطَ بِكُمْ ۖ فَلَمَّا آتَوْهُ مَوْثِقَهُمْ قَالَ اللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٌ','گفت: «من هرگز او را با شما نخواهم فرستاد، تا پیمان مؤکّد الهی بدهید که او را حتماً نزد من خواهید آورد! مگر اینکه (بر اثر مرگ یا علّت دیگر،) قدرت از شما سلب گردد. و هنگامی که آنها پیمان استوار خود را در اختیار او گذاردند، گفت: «خداوند، نسبت به آنچه می‌گوییم، ناظر و نگهبان است!»'];
ayat[1663]=[12,67,'وَقَالَ يَا بَنِيَّ لَا تَدْخُلُوا مِن بَابٍ وَاحِدٍ وَادْخُلُوا مِنْ أَبْوَابٍ مُّتَفَرِّقَةٍ ۖ وَمَا أُغْنِي عَنكُم مِّنَ اللَّهِ مِن شَيْءٍ ۖ إِنِ الْحُكْمُ إِلَّا لِلَّهِ ۖ عَلَيْهِ تَوَكَّلْتُ ۖ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ','و (هنگامی که می‌خواستند حرکت کنند، یعقوب) گفت: «فرزندان من! از یک در وارد نشوید؛ بلکه از درهای متفرّق وارد گردید (تا توجه مردم به سوی شما جلب نشود)! و (من با این دستور،) نمی‌توانم حادثه‌ای را که از سوی خدا حتمی است، از شما دفع کنم! حکم و فرمان، تنها از آنِ خداست! بر او توکّل کرده‌ام؛ و همه متوکّلان باید بر او توکّل کنند!»'];
ayat[1664]=[12,68,'وَلَمَّا دَخَلُوا مِنْ حَيْثُ أَمَرَهُمْ أَبُوهُم مَّا كَانَ يُغْنِي عَنْهُم مِّنَ اللَّهِ مِن شَيْءٍ إِلَّا حَاجَةً فِي نَفْسِ يَعْقُوبَ قَضَاهَا ۚ وَإِنَّهُ لَذُو عِلْمٍ لِّمَا عَلَّمْنَاهُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','و هنگامی که از همان طریق که پدر به آنها دستور داده بود وارد شدند، این کار هیچ حادثه حتمی الهی را نمی‌توانست از آنها دور سازد، جز حاجتی در دل یعقوب (که از این طریق) انجام شد (و خاطرش آرام گرفت)؛ و او به خاطر تعلیمی که ما به او دادیم، علم فراوانی داشت؛ ولی بیشتر مردم نمی‌دانند!'];
ayat[1665]=[12,69,'وَلَمَّا دَخَلُوا عَلَىٰ يُوسُفَ آوَىٰ إِلَيْهِ أَخَاهُ ۖ قَالَ إِنِّي أَنَا أَخُوكَ فَلَا تَبْتَئِسْ بِمَا كَانُوا يَعْمَلُونَ','هنگامی که (برادران) بر یوسف وارد شدند، برادرش را نزد خود جای داد و گفت: «من برادر تو هستم، از آنچه آنها انجام می‌دادند، غمگین و ناراحت نباش!»'];
ayat[1666]=[12,70,'فَلَمَّا جَهَّزَهُم بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ فِي رَحْلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا الْعِيرُ إِنَّكُمْ لَسَارِقُونَ','و هنگامی که (مأمور یوسف) بارهای آنها را بست، ظرف آبخوری پادشاه را در بارِ برادرش گذاشت؛ سپس کسی صدا زد؛ «ای اهل قافله، شما دزد هستید!»'];
ayat[1667]=[12,71,'قَالُوا وَأَقْبَلُوا عَلَيْهِم مَّاذَا تَفْقِدُونَ','آنها رو به سوی او کردند و گفتند: «چه چیز گم کرده‌اید؟»'];
ayat[1668]=[12,72,'قَالُوا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَن جَاءَ بِهِ حِمْلُ بَعِيرٍ وَأَنَا بِهِ زَعِيمٌ','گفتند: «پیمانه پادشاه را! و هر کس آن را بیاورد، یک بار شتر (غلّه) به او داده می‌شود؛ و من ضامن این (پاداش) هستم!»'];
ayat[1669]=[12,73,'قَالُوا تَاللَّهِ لَقَدْ عَلِمْتُم مَّا جِئْنَا لِنُفْسِدَ فِي الْأَرْضِ وَمَا كُنَّا سَارِقِينَ','گفتند: «به خدا سوگند شما می‌دانید ما نیامده‌ایم که در این سرزمین فساد کنیم؛ و ما (هرگز) دزد نبوده‌ایم!»'];
ayat[1670]=[12,74,'قَالُوا فَمَا جَزَاؤُهُ إِن كُنتُمْ كَاذِبِينَ','آنها گفتند: «اگر دروغگو باشید، کیفرش چیست؟»'];
ayat[1671]=[12,75,'قَالُوا جَزَاؤُهُ مَن وُجِدَ فِي رَحْلِهِ فَهُوَ جَزَاؤُهُ ۚ كَذَٰلِكَ نَجْزِي الظَّالِمِينَ','گفتند: «هر کس (آن پیمانه) در بارِ او پیدا شود، خودش کیفر آن خواهد بود؛ (و بخاطر این کار، برده شما خواهد شد؛) ما این‌گونه ستمگران را کیفر می‌دهیم!»'];
ayat[1672]=[12,76,'فَبَدَأَ بِأَوْعِيَتِهِمْ قَبْلَ وِعَاءِ أَخِيهِ ثُمَّ اسْتَخْرَجَهَا مِن وِعَاءِ أَخِيهِ ۚ كَذَٰلِكَ كِدْنَا لِيُوسُفَ ۖ مَا كَانَ لِيَأْخُذَ أَخَاهُ فِي دِينِ الْمَلِكِ إِلَّا أَن يَشَاءَ اللَّهُ ۚ نَرْفَعُ دَرَجَاتٍ مَّن نَّشَاءُ ۗ وَفَوْقَ كُلِّ ذِي عِلْمٍ عَلِيمٌ','در این هنگام، (یوسف) قبل از بار برادرش، به کاوش بارهای آنها پرداخت؛ سپس آن را از بارِ برادرش بیرون آورد؛ این گونه راه چاره را به یوسف یاد دادیم! او هرگز نمی‌توانست برادرش را مطابق آیین پادشاه (مصر) بگیرد، مگر آنکه خدا بخواهد! درجات هر کس را بخواهیم بالا می‌بریم؛ و برتر از هر صاحب علمی، عالمی است!'];
ayat[1673]=[12,77,'۞ قَالُوا إِن يَسْرِقْ فَقَدْ سَرَقَ أَخٌ لَّهُ مِن قَبْلُ ۚ فَأَسَرَّهَا يُوسُفُ فِي نَفْسِهِ وَلَمْ يُبْدِهَا لَهُمْ ۚ قَالَ أَنتُمْ شَرٌّ مَّكَانًا ۖ وَاللَّهُ أَعْلَمُ بِمَا تَصِفُونَ','(برادران) گفتند: «اگر او [بنیامین‌] دزدی کند، (جای تعجب نیست؛) برادرش (یوسف) نیز قبل از او دزدی کرد» یوسف (سخت ناراحت شد، و) این (ناراحتی) را در درون خود پنهان داشت، و برای آنها آشکار نکرد؛ (همین اندازه) گفت: «شما (از دیدگاه من،) از نظر منزلت بدّترین مردمید! و خدا از آنچه توصیف می‌کنید، آگاهتر است!»'];
ayat[1674]=[12,78,'قَالُوا يَا أَيُّهَا الْعَزِيزُ إِنَّ لَهُ أَبًا شَيْخًا كَبِيرًا فَخُذْ أَحَدَنَا مَكَانَهُ ۖ إِنَّا نَرَاكَ مِنَ الْمُحْسِنِينَ','گفتند: «ای عزیز! او پدر پیری دارد (که سخت ناراحت می‌شود)؛ یکی از ما را به جای او بگیر؛ ما تو را از نیکوکاران می‌بینیم!»'];
ayat[1675]=[12,79,'قَالَ مَعَاذَ اللَّهِ أَن نَّأْخُذَ إِلَّا مَن وَجَدْنَا مَتَاعَنَا عِندَهُ إِنَّا إِذًا لَّظَالِمُونَ','گفت: «پناه بر خدا که ما غیر از آن کس که متاع خود را نزد او یافته‌ایم بگیریم؛ در آن صورت، از ظالمان خواهیم بود!»'];
ayat[1676]=[12,80,'فَلَمَّا اسْتَيْأَسُوا مِنْهُ خَلَصُوا نَجِيًّا ۖ قَالَ كَبِيرُهُمْ أَلَمْ تَعْلَمُوا أَنَّ أَبَاكُمْ قَدْ أَخَذَ عَلَيْكُم مَّوْثِقًا مِّنَ اللَّهِ وَمِن قَبْلُ مَا فَرَّطتُمْ فِي يُوسُفَ ۖ فَلَنْ أَبْرَحَ الْأَرْضَ حَتَّىٰ يَأْذَنَ لِي أَبِي أَوْ يَحْكُمَ اللَّهُ لِي ۖ وَهُوَ خَيْرُ الْحَاكِمِينَ','هنگامی که (برادران) از او مأیوس شدند، به کناری رفتند و با هم به نجوا پرداختند؛ (برادر) بزرگشان گفت: «آیا نمی‌دانید پدرتان از شما پیمان الهی گرفته؛ و پیش از این درباره یوسف کوتاهی کردید؟! من از این سرزمین حرکت نمی‌کنم، تا پدرم به من اجازه دهد؛ یا خدا درباره من داوری کند، که او بهترین حکم‌کنندگان است!'];
ayat[1677]=[12,81,'ارْجِعُوا إِلَىٰ أَبِيكُمْ فَقُولُوا يَا أَبَانَا إِنَّ ابْنَكَ سَرَقَ وَمَا شَهِدْنَا إِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حَافِظِينَ','شما به سوی پدرتان بازگردید و بگویید: پدر (جان)، پسرت دزدی کرد! و ما جز به آنچه می‌دانستیم گواهی ندادیم؛ و ما از غیب آگاه نبودیم!'];
ayat[1678]=[12,82,'وَاسْأَلِ الْقَرْيَةَ الَّتِي كُنَّا فِيهَا وَالْعِيرَ الَّتِي أَقْبَلْنَا فِيهَا ۖ وَإِنَّا لَصَادِقُونَ','(و اگر اطمینان نداری،) از آن شهر که در آن بودیم سؤال کن، و نیز از آن قافله که با آن آمدیم (بپرس)! و ما (در گفتار خود) صادق هستیم!»'];
ayat[1679]=[12,83,'قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنفُسُكُمْ أَمْرًا ۖ فَصَبْرٌ جَمِيلٌ ۖ عَسَى اللَّهُ أَن يَأْتِيَنِي بِهِمْ جَمِيعًا ۚ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ','(یعقوب) گفت: «(هوای) نفس شما، مسأله را چنین در نظرتان آراسته است! من صبر می‌کنم، صبری زیبا (و خالی از کفران)! امیدوارم خداوند همه آنها را به من بازگرداند؛ چرا که او دانا و حکیم است!'];
ayat[1680]=[12,84,'وَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَا أَسَفَىٰ عَلَىٰ يُوسُفَ وَابْيَضَّتْ عَيْنَاهُ مِنَ الْحُزْنِ فَهُوَ كَظِيمٌ','و از آنها روی برگرداند و گفت: «وا اسفا بر یوسف!» و چشمان او از اندوه سفید شد، اما خشم خود را فرو می‌برد (و هرگز کفران نمی‌کرد)!'];
ayat[1681]=[12,85,'قَالُوا تَاللَّهِ تَفْتَأُ تَذْكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوْ تَكُونَ مِنَ الْهَالِكِينَ','گفتند: «به خدا تو آنقدر یاد یوسف می‌کنی تا در آستانه مرگ قرار گیری، یا هلاک گردی!»'];
ayat[1682]=[12,86,'قَالَ إِنَّمَا أَشْكُو بَثِّي وَحُزْنِي إِلَى اللَّهِ وَأَعْلَمُ مِنَ اللَّهِ مَا لَا تَعْلَمُونَ','گفت: «من غم و اندوهم را تنها به خدا می‌گویم (و شکایت نزد او می‌برم)! و از خدا چیزهایی می‌دانم که شما نمی‌دانید!'];
ayat[1683]=[12,87,'يَا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِن يُوسُفَ وَأَخِيهِ وَلَا تَيْأَسُوا مِن رَّوْحِ اللَّهِ ۖ إِنَّهُ لَا يَيْأَسُ مِن رَّوْحِ اللَّهِ إِلَّا الْقَوْمُ الْكَافِرُونَ','پسرانم! بروید، و از یوسف و برادرش جستجو کنید؛ و از رحمت خدا مأیوس نشوید؛ که تنها گروه کافران، از رحمت خدا مأیوس می‌شوند!»'];
ayat[1684]=[12,88,'فَلَمَّا دَخَلُوا عَلَيْهِ قَالُوا يَا أَيُّهَا الْعَزِيزُ مَسَّنَا وَأَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُّزْجَاةٍ فَأَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَا ۖ إِنَّ اللَّهَ يَجْزِي الْمُتَصَدِّقِينَ','هنگامی که آنها بر او [= یوسف‌] وارد شدند، گفتند: «ای عزیز! ما و خاندان ما را ناراحتی فرا گرفته، و متاع کمی (برای خرید موادّ غذایی) با خود آورده‌ایم؛ پیمانه را برای ما کامل کن؛ و بر ما تصدّق و بخشش نما، که خداوند بخشندگان را پاداش می‌دهد!»'];
ayat[1685]=[12,89,'قَالَ هَلْ عَلِمْتُم مَّا فَعَلْتُم بِيُوسُفَ وَأَخِيهِ إِذْ أَنتُمْ جَاهِلُونَ','گفت: «آیا دانستید با یوسف و برادرش چه کردید، آنگاه که جاهل بودید؟!»'];
ayat[1686]=[12,90,'قَالُوا أَإِنَّكَ لَأَنتَ يُوسُفُ ۖ قَالَ أَنَا يُوسُفُ وَهَٰذَا أَخِي ۖ قَدْ مَنَّ اللَّهُ عَلَيْنَا ۖ إِنَّهُ مَن يَتَّقِ وَيَصْبِرْ فَإِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ','گفتند: «آیا تو همان یوسفی؟!» گفت: «(آری،) من یوسفم، و این برادر من است! خداوند بر ما منّت گذارد؛ هر کس تقوا پیشه کند، و شکیبایی و استقامت نماید، (سرانجام پیروز می‌شود؛) چرا که خداوند پاداش نیکوکاران را ضایع نمی‌کند!»'];
ayat[1687]=[12,91,'قَالُوا تَاللَّهِ لَقَدْ آثَرَكَ اللَّهُ عَلَيْنَا وَإِن كُنَّا لَخَاطِئِينَ','گفتند: «به خدا سوگند، خداوند تو را بر ما برتری بخشیده؛ و ما خطاکار بودیم!»'];
ayat[1688]=[12,92,'قَالَ لَا تَثْرِيبَ عَلَيْكُمُ الْيَوْمَ ۖ يَغْفِرُ اللَّهُ لَكُمْ ۖ وَهُوَ أَرْحَمُ الرَّاحِمِينَ','(یوسف) گفت: «امروز ملامت و توبیخی بر شما نیست! خداوند شما را می‌بخشد؛ و او مهربانترین مهربانان است!'];
ayat[1689]=[12,93,'اذْهَبُوا بِقَمِيصِي هَٰذَا فَأَلْقُوهُ عَلَىٰ وَجْهِ أَبِي يَأْتِ بَصِيرًا وَأْتُونِي بِأَهْلِكُمْ أَجْمَعِينَ','این پیراهن مرا ببرید، و بر صورت پدرم بیندازید، بینا می‌شود! و همه نزدیکان خود را نزد من بیاورید!»'];
ayat[1690]=[12,94,'وَلَمَّا فَصَلَتِ الْعِيرُ قَالَ أَبُوهُمْ إِنِّي لَأَجِدُ رِيحَ يُوسُفَ ۖ لَوْلَا أَن تُفَنِّدُونِ','هنگامی که کاروان (از سرزمین مصر) جدا شد، پدرشان [= یعقوب‌] گفت: «من بوی یوسف را احساس می‌کنم، اگر مرا به نادانی و کم عقلی نسبت ندهید!»'];
ayat[1691]=[12,95,'قَالُوا تَاللَّهِ إِنَّكَ لَفِي ضَلَالِكَ الْقَدِيمِ','گفتند: «به خدا تو در همان گمراهی سابقت هستی!»'];
ayat[1692]=[12,96,'فَلَمَّا أَن جَاءَ الْبَشِيرُ أَلْقَاهُ عَلَىٰ وَجْهِهِ فَارْتَدَّ بَصِيرًا ۖ قَالَ أَلَمْ أَقُل لَّكُمْ إِنِّي أَعْلَمُ مِنَ اللَّهِ مَا لَا تَعْلَمُونَ','امّا هنگامی که بشارت دهنده فرا رسید، آن (پیراهن) را بر صورت او افکند؛ ناگهان بینا شد! گفت: «آیا به شما نگفتم من از خدا چیزهایی می‌دانم که شما نمی‌دانید؟!»'];
ayat[1693]=[12,97,'قَالُوا يَا أَبَانَا اسْتَغْفِرْ لَنَا ذُنُوبَنَا إِنَّا كُنَّا خَاطِئِينَ','گفتند: «پدر! از خدا آمرزش گناهان ما را بخواه، که ما خطاکار بودیم!»'];
ayat[1694]=[12,98,'قَالَ سَوْفَ أَسْتَغْفِرُ لَكُمْ رَبِّي ۖ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ','گفت: «بزودی برای شما از پروردگارم آمرزش می‌طلبم، که او آمرزنده و مهربان است!»'];
ayat[1695]=[12,99,'فَلَمَّا دَخَلُوا عَلَىٰ يُوسُفَ آوَىٰ إِلَيْهِ أَبَوَيْهِ وَقَالَ ادْخُلُوا مِصْرَ إِن شَاءَ اللَّهُ آمِنِينَ','و هنگامی که بر یوسف وارد شدند، او پدر و مادر خود را در آغوش گرفت، و گفت: «همگی داخل مصر شوید، که انشاء الله در امن و امان خواهید بود!»'];
ayat[1696]=[12,100,'وَرَفَعَ أَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوا لَهُ سُجَّدًا ۖ وَقَالَ يَا أَبَتِ هَٰذَا تَأْوِيلُ رُؤْيَايَ مِن قَبْلُ قَدْ جَعَلَهَا رَبِّي حَقًّا ۖ وَقَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَجَاءَ بِكُم مِّنَ الْبَدْوِ مِن بَعْدِ أَن نَّزَغَ الشَّيْطَانُ بَيْنِي وَبَيْنَ إِخْوَتِي ۚ إِنَّ رَبِّي لَطِيفٌ لِّمَا يَشَاءُ ۚ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ','و پدر و مادر خود را بر تخت نشاند؛ و همگی بخاطر او به سجده افتادند؛ و گفت: «پدر! این تعبیر خوابی است که قبلاً دیدم؛ پروردگارم آن را حقّ قرار داد! و او به من نیکی کرد هنگامی که مرا از زندان بیرون آورد، و شما را از آن بیابان (به اینجا) آورد بعد از آنکه شیطان، میان من و برادرانم فساد کرد. پروردگارم نسبت به آنچه می‌خواهد (و شایسته می‌داند،) صاحب لطف است؛ چرا که او دانا و حکیم است!'];
ayat[1697]=[12,101,'۞ رَبِّ قَدْ آتَيْتَنِي مِنَ الْمُلْكِ وَعَلَّمْتَنِي مِن تَأْوِيلِ الْأَحَادِيثِ ۚ فَاطِرَ السَّمَاوَاتِ وَالْأَرْضِ أَنتَ وَلِيِّي فِي الدُّنْيَا وَالْآخِرَةِ ۖ تَوَفَّنِي مُسْلِمًا وَأَلْحِقْنِي بِالصَّالِحِينَ','پروردگارا! بخشی (عظیم) از حکومت به من بخشیدی، و مرا از علم تعبیر خوابها آگاه ساختی! ای آفریننده آسمانها و زمین! تو ولیّ و سرپرست من در دنیا و آخرت هستی، مرا مسلمان بمیران؛ و به صالحان ملحق فرما!»'];
ayat[1698]=[12,102,'ذَٰلِكَ مِنْ أَنبَاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ ۖ وَمَا كُنتَ لَدَيْهِمْ إِذْ أَجْمَعُوا أَمْرَهُمْ وَهُمْ يَمْكُرُونَ','این از خبرهای غیب است که به تو وحی می‌فرستیم! تو (هرگز) نزد آنها نبودی هنگامی که تصمیم می‌گرفتند و نقشه می‌کشیدند!'];
ayat[1699]=[12,103,'وَمَا أَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِينَ','و بیشتر مردم، هر چند اصرار داشته باشی، ایمان نمی‌آورند!'];
ayat[1700]=[12,104,'وَمَا تَسْأَلُهُمْ عَلَيْهِ مِنْ أَجْرٍ ۚ إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَالَمِينَ','و تو (هرگز) از آنها پاداشی نمی‌طلبی؛ آن نیست مگر تذکّری برای جهانیان!'];
ayat[1701]=[12,105,'وَكَأَيِّن مِّنْ آيَةٍ فِي السَّمَاوَاتِ وَالْأَرْضِ يَمُرُّونَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُونَ','و چه بسیار نشانه‌ای (از خدا) در آسمانها و زمین که آنها از کنارش می‌گذرند، و از آن رویگردانند!'];
ayat[1702]=[12,106,'وَمَا يُؤْمِنُ أَكْثَرُهُم بِاللَّهِ إِلَّا وَهُم مُّشْرِكُونَ','و بیشتر آنها که مدعی ایمان به خدا هستند، مشرکند!'];
ayat[1703]=[12,107,'أَفَأَمِنُوا أَن تَأْتِيَهُمْ غَاشِيَةٌ مِّنْ عَذَابِ اللَّهِ أَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَهُمْ لَا يَشْعُرُونَ','آیا ایمن از آنند که عذاب فراگیری از سوی خدا به سراغ آنان بیاید، یا ساعت رستاخیز ناگهان فرارسد، در حالی که متوجّه نیستند؟!'];
ayat[1704]=[12,108,'قُلْ هَٰذِهِ سَبِيلِي أَدْعُو إِلَى اللَّهِ ۚ عَلَىٰ بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي ۖ وَسُبْحَانَ اللَّهِ وَمَا أَنَا مِنَ الْمُشْرِكِينَ','بگو: «این راه من است من و پیروانم، و با بصیرت کامل، همه مردم را به سوی خدا دعوت می‌کنیم! منزّه است خدا! و من از مشرکان نیستم!»'];
ayat[1705]=[12,109,'وَمَا أَرْسَلْنَا مِن قَبْلِكَ إِلَّا رِجَالًا نُّوحِي إِلَيْهِم مِّنْ أَهْلِ الْقُرَىٰ ۗ أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِن قَبْلِهِمْ ۗ وَلَدَارُ الْآخِرَةِ خَيْرٌ لِّلَّذِينَ اتَّقَوْا ۗ أَفَلَا تَعْقِلُونَ','و ما نفرستادیم پیش از تو، جز مردانی از اهل آبادیها که به آنها وحی می‌کردیم! آیا (مخالفان دعوت تو،) در زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنها بودند چه شد؟! و سرای آخرت برای پرهیزکاران بهتر است! آیا فکر نمی‌کنید؟!'];
ayat[1706]=[12,110,'حَتَّىٰ إِذَا اسْتَيْأَسَ الرُّسُلُ وَظَنُّوا أَنَّهُمْ قَدْ كُذِبُوا جَاءَهُمْ نَصْرُنَا فَنُجِّيَ مَن نَّشَاءُ ۖ وَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِينَ','(پیامبران به دعوت خود، و دشمنان آنها به مخالفت خود همچنان ادامه دادند) تا آنگاه که رسولان مأیوس شدند، و (مردم) گمان کردند که به آنان دروغ گفته شده است؛ در این هنگام، یاری ما به سراغ آنها آمد؛ آنان را که خواستیم نجات یافتند؛ و مجازات و عذاب ما از قوم گنهکار بازگردانده نمی‌شود!'];
ayat[1707]=[12,111,'لَقَدْ كَانَ فِي قَصَصِهِمْ عِبْرَةٌ لِّأُولِي الْأَلْبَابِ ۗ مَا كَانَ حَدِيثًا يُفْتَرَىٰ وَلَٰكِن تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَتَفْصِيلَ كُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً لِّقَوْمٍ يُؤْمِنُونَ','در سرگذشت آنها درس عبرتی برای صاحبان اندیشه بود! اینها داستان دروغین نبود؛ بلکه (وحی آسمانی است، و) هماهنگ است با آنچه پیش روی او (از کتب آسمانی پیشین) قرار دارد؛ و شرح هر چیزی (که پایه سعادت انسان است)؛ و هدایت و رحمتی است برای گروهی که ایمان می‌آورند!'];
ayat[1708]=[13,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ المر ۚ تِلْكَ آيَاتُ الْكِتَابِ ۗ وَالَّذِي أُنزِلَ إِلَيْكَ مِن رَّبِّكَ الْحَقُّ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ','المر، اینها آیات کتاب (آسمانی) است؛ و آنچه از طرف پروردگارت بر تو نازل شده، حقّ است؛ ولی بیشتر مردم ایمان نمی‌آورند!'];
ayat[1709]=[13,2,'اللَّهُ الَّذِي رَفَعَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ۖ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۖ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ ۖ كُلٌّ يَجْرِي لِأَجَلٍ مُّسَمًّى ۚ يُدَبِّرُ الْأَمْرَ يُفَصِّلُ الْآيَاتِ لَعَلَّكُم بِلِقَاءِ رَبِّكُمْ تُوقِنُونَ','خدا همان کسی است که آسمانها را، بدون ستونهایی که برای شما دیدنی باشد، برافراشت، سپس بر عرش استیلا یافت (و زمام تدبیر جهان را در کف قدرت گرفت)؛ و خورشید و ماه را مسخّر ساخت، که هر کدام تا زمان معینی حرکت دارند! کارها را او تدبیر می‌کند؛ آیات را (برای شما) تشریح می‌نماید؛ شاید به لقای پروردگارتان یقین پیدا کنید!'];
ayat[1710]=[13,3,'وَهُوَ الَّذِي مَدَّ الْأَرْضَ وَجَعَلَ فِيهَا رَوَاسِيَ وَأَنْهَارًا ۖ وَمِن كُلِّ الثَّمَرَاتِ جَعَلَ فِيهَا زَوْجَيْنِ اثْنَيْنِ ۖ يُغْشِي اللَّيْلَ النَّهَارَ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَتَفَكَّرُونَ','و او کسی است که زمین را گسترد؛ و در آن کوه‌ها و نهرهایی قرار داد؛ و در آن از تمام میوه‌ها دو جفت آفرید؛ (پرده سیاه) شب را بر روز می‌پوشاند؛ در اینها آیاتی است برای گروهی که تفکر می‌کنند!'];
ayat[1711]=[13,4,'وَفِي الْأَرْضِ قِطَعٌ مُّتَجَاوِرَاتٌ وَجَنَّاتٌ مِّنْ أَعْنَابٍ وَزَرْعٌ وَنَخِيلٌ صِنْوَانٌ وَغَيْرُ صِنْوَانٍ يُسْقَىٰ بِمَاءٍ وَاحِدٍ وَنُفَضِّلُ بَعْضَهَا عَلَىٰ بَعْضٍ فِي الْأُكُلِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَعْقِلُونَ','و در روی زمین، قطعاتی در کنار هم قرار دارد که با هم متفاوتند؛ و (نیز) باغهایی از انگور و زراعت و نخلها، (و درختان میوه گوناگون) که گاه بر یک پایه می‌رویند و گاه بر دو پایه؛ (و عجیب‌تر آنکه) همه آنها از یک آب سیراب می‌شوند! و با این حال، بعضی از آنها را از جهت میوه بر دیگری برتری می‌دهیم؛ در اینها نشانه‌هایی است برای گروهی که عقل خویش را به کار می‌گیرند!'];
ayat[1712]=[13,5,'۞ وَإِن تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ أَإِذَا كُنَّا تُرَابًا أَإِنَّا لَفِي خَلْقٍ جَدِيدٍ ۗ أُولَٰئِكَ الَّذِينَ كَفَرُوا بِرَبِّهِمْ ۖ وَأُولَٰئِكَ الْأَغْلَالُ فِي أَعْنَاقِهِمْ ۖ وَأُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','و اگر (از چیزی) تعجب می‌کنی، عجیب گفتار آنهاست که می‌گویند: «آیا هنگامی که ما خاک شدیم، (بار دیگر زنده می‌شویم و) به خلقت جدیدی بازمی‌گردیم؟!» آنها کسانی هستند که به پروردگارشان کافر شده‌اند؛ و آنان غل و زنجیرها در گردنشان است؛ و آنها اهل دوزخند، و جاودانه در آن خواهند ماند!'];
ayat[1713]=[13,6,'وَيَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِن قَبْلِهِمُ الْمَثُلَاتُ ۗ وَإِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِّلنَّاسِ عَلَىٰ ظُلْمِهِمْ ۖ وَإِنَّ رَبَّكَ لَشَدِيدُ الْعِقَابِ','آنها پیش از حسنه (و رحمت)، از تو تقاضای شتاب در سیّئه (و عذاب) می‌کنند؛ با اینکه پیش از آنها بلاهای عبرت انگیز نازل شده است! و پروردگار تو نسبت به مردم -با اینکه ظلم می‌کنند- دارای مغفرت است؛ و (در عین حال،) پروردگارت دارای عذاب شدید است!'];
ayat[1714]=[13,7,'وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلَا أُنزِلَ عَلَيْهِ آيَةٌ مِّن رَّبِّهِ ۗ إِنَّمَا أَنتَ مُنذِرٌ ۖ وَلِكُلِّ قَوْمٍ هَادٍ','کسانی که کافر شدند می‌گویند: «چرا آیه (و معجزه‌ای) از پروردگارش بر او نازل نشده؟!» تو فقط بیم دهنده‌ای! و برای هر گروهی هدایت کننده‌ای است (؛ و اینها همه بهانه است، نه برای جستجوی حقیقت)!'];
ayat[1715]=[13,8,'اللَّهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ أُنثَىٰ وَمَا تَغِيضُ الْأَرْحَامُ وَمَا تَزْدَادُ ۖ وَكُلُّ شَيْءٍ عِندَهُ بِمِقْدَارٍ','خدا از جنین‌هایی که هر (انسان یا حیوان) مادّه‌ای حمل می‌کند آگاه است؛ و نیز از آنچه رحمها کم می‌کنند (و پیش از موعد مقرّر می‌زایند)، و هم از آنچه افزون می‌کنند (و بعد از موقع میزایند)؛ و هر چیز نزد او مقدار معینی دارد.'];
ayat[1716]=[13,9,'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيرُ الْمُتَعَالِ','او از غیب و شهود آگاه، و بزرگ و متعالی است!'];
ayat[1717]=[13,10,'سَوَاءٌ مِّنكُم مَّنْ أَسَرَّ الْقَوْلَ وَمَن جَهَرَ بِهِ وَمَنْ هُوَ مُسْتَخْفٍ بِاللَّيْلِ وَسَارِبٌ بِالنَّهَارِ','برای او یکسان است کسانی از شما که پنهانی سخن بگویند، یا آن را آشکار سازند؛ و کسانی که شبانگاه مخفیانه حرکت می‌کنند، یا در روشنایی روز.'];
ayat[1718]=[13,11,'لَهُ مُعَقِّبَاتٌ مِّن بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللَّهِ ۗ إِنَّ اللَّهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتَّىٰ يُغَيِّرُوا مَا بِأَنفُسِهِمْ ۗ وَإِذَا أَرَادَ اللَّهُ بِقَوْمٍ سُوءًا فَلَا مَرَدَّ لَهُ ۚ وَمَا لَهُم مِّن دُونِهِ مِن وَالٍ','برای انسان، مأمورانی است که پی در پی، از پیش رو، و از پشت سرش او را از فرمان خدا [= حوادث غیر حتمی‌] حفظ می‌کنند؛ (امّا) خداوند سرنوشت هیچ قوم (و ملّتی) را تغییر نمی‌دهد مگر آنکه آنان آنچه را در خودشان است تغییر دهند! و هنگامی که خدا اراده سوئی به قومی (بخاطر اعمالشان) کند، هیچ چیز مانع آن نخواهد شد؛ و جز خدا، سرپرستی نخواهند داشت!'];
ayat[1719]=[13,12,'هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنشِئُ السَّحَابَ الثِّقَالَ','او کسی است که برق را به شما نشان می‌دهد، که هم مایه ترس است و هم مایه امید؛ و ابرهای سنگین‌بار ایجاد می‌کند!'];
ayat[1720]=[13,13,'وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلَائِكَةُ مِنْ خِيفَتِهِ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيبُ بِهَا مَن يَشَاءُ وَهُمْ يُجَادِلُونَ فِي اللَّهِ وَهُوَ شَدِيدُ الْمِحَالِ','و رعد، تسبیح و حمد او می‌گوید؛ و (نیز) فرشتگان از ترس او! و صاعقه‌ها را می‌فرستد؛ و هر کس را بخواهد گرفتار آن می‌سازد، (در حالی که آنها با مشاهده این همه آیات الهی، باز هم) درباره خدا به مجادله مشغولند! و او قدرتی بی‌انتها (و مجازاتی دردناک) دارد!'];
ayat[1721]=[13,14,'لَهُ دَعْوَةُ الْحَقِّ ۖ وَالَّذِينَ يَدْعُونَ مِن دُونِهِ لَا يَسْتَجِيبُونَ لَهُم بِشَيْءٍ إِلَّا كَبَاسِطِ كَفَّيْهِ إِلَى الْمَاءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهِ ۚ وَمَا دُعَاءُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ','دعوت حق از آن اوست! و کسانی را که (مشرکان) غیر از خدا می‌خوانند، (هرگز) به دعوت آنها پاسخ نمی‌گویند! آنها همچون کسی هستند که کفهای (دست) خود را به سوی آب می‌گشاید تا آب به دهانش برسد، و هرگز نخواهد رسید! و دعای کافران، جز در ضلال (و گمراهی) نیست!'];
ayat[1722]=[13,15,'وَلِلَّهِ يَسْجُدُ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ طَوْعًا وَكَرْهًا وَظِلَالُهُم بِالْغُدُوِّ وَالْآصَالِ ۩','تمام کسانی که در آسمانها و زمین هستند -از روی اطاعت یا اکراه- و همچنین سایه‌هایشان، هر صبح و عصر برای خدا سجده می‌کنند.'];
ayat[1723]=[13,16,'قُلْ مَن رَّبُّ السَّمَاوَاتِ وَالْأَرْضِ قُلِ اللَّهُ ۚ قُلْ أَفَاتَّخَذْتُم مِّن دُونِهِ أَوْلِيَاءَ لَا يَمْلِكُونَ لِأَنفُسِهِمْ نَفْعًا وَلَا ضَرًّا ۚ قُلْ هَلْ يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ أَمْ هَلْ تَسْتَوِي الظُّلُمَاتُ وَالنُّورُ ۗ أَمْ جَعَلُوا لِلَّهِ شُرَكَاءَ خَلَقُوا كَخَلْقِهِ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْ ۚ قُلِ اللَّهُ خَالِقُ كُلِّ شَيْءٍ وَهُوَ الْوَاحِدُ الْقَهَّارُ','بگو: «چه کسی پروردگار آسمانها و زمین است؟» بگو: «اللّه!» (سپس) بگو: «آیا اولیا (و خدایانی) غیر از او برای خود برگزیده‌اید که (حتّی) مالک سود و زیان خود نیستند (تا چه رسد به شما؟!)» بگو: «آیا نابینا و بینا یکسانند؟! یا ظلمتها و نور برابرند؟! آیا آنها همتایانی برای خدا قرار دادند بخاطر اینکه آنان همانند خدا آفرینشی داشتند، و این آفرینشها بر آنها مشتبه شده است؟!» بگو: «خدا خالق همه چیز است؛ و اوست یکتا و پیروز!»'];
ayat[1724]=[13,17,'أَنزَلَ مِنَ السَّمَاءِ مَاءً فَسَالَتْ أَوْدِيَةٌ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَّابِيًا ۚ وَمِمَّا يُوقِدُونَ عَلَيْهِ فِي النَّارِ ابْتِغَاءَ حِلْيَةٍ أَوْ مَتَاعٍ زَبَدٌ مِّثْلُهُ ۚ كَذَٰلِكَ يَضْرِبُ اللَّهُ الْحَقَّ وَالْبَاطِلَ ۚ فَأَمَّا الزَّبَدُ فَيَذْهَبُ جُفَاءً ۖ وَأَمَّا مَا يَنفَعُ النَّاسَ فَيَمْكُثُ فِي الْأَرْضِ ۚ كَذَٰلِكَ يَضْرِبُ اللَّهُ الْأَمْثَالَ','خداوند از آسمان آبی فرستاد؛ و از هر درّه و رودخانه‌ای به اندازه آنها سیلابی جاری شد؛ سپس سیل بر روی خود کفی حمل کرد؛ و از آنچه (در کوره‌ها،) برای به دست آوردن زینت آلات یا وسایل زندگی، آتش روی آن روشن می‌کنند نیز کفهایی مانند آن به وجود می‌آید -خداوند، حق و باطل را چنین مثل می‌زند!- امّا کفها به بیرون پرتاب می‌شوند، ولی آنچه به مردم سود می‌رساند [= آب یا فلز خالص] در زمین می‌ماند؛ خداوند اینچنین مثال می‌زند!'];
ayat[1725]=[13,18,'لِلَّذِينَ اسْتَجَابُوا لِرَبِّهِمُ الْحُسْنَىٰ ۚ وَالَّذِينَ لَمْ يَسْتَجِيبُوا لَهُ لَوْ أَنَّ لَهُم مَّا فِي الْأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِهِ ۚ أُولَٰئِكَ لَهُمْ سُوءُ الْحِسَابِ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمِهَادُ','برای آنها که دعوت پروردگارشان را اجابت کردند، (سرانجام و) نتیجه نیکوتر است؛ و کسانی که دعوت او را اجابت نکردند، (آنچنان در وحشت عذاب الهی فرو می‌روند، که) اگر تمام آنچه روی زمین است، و همانندش، از آن آنها باشد، همه را برای رهایی از عذاب می‌دهند! (ولی از آنها پذیرفته نخواهد شد!) برای آنها حساب بدی است؛ و جایگاهشان جهنم، و چه بد جایگاهی است!'];
ayat[1726]=[13,19,'۞ أَفَمَن يَعْلَمُ أَنَّمَا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ الْحَقُّ كَمَنْ هُوَ أَعْمَىٰ ۚ إِنَّمَا يَتَذَكَّرُ أُولُو الْأَلْبَابِ','آیا کسی که می‌داند آنچه از طرف پروردگارت بر تو نازل شده حق است، همانند کسی است که نابیناست؟! تنها صاحبان اندیشه متذکّر می‌شوند...'];
ayat[1727]=[13,20,'الَّذِينَ يُوفُونَ بِعَهْدِ اللَّهِ وَلَا يَنقُضُونَ الْمِيثَاقَ','آنها که به عهد الهی وفا می‌کنند، و پیمان را نمی‌شکنند...'];
ayat[1728]=[13,21,'وَالَّذِينَ يَصِلُونَ مَا أَمَرَ اللَّهُ بِهِ أَن يُوصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُونَ سُوءَ الْحِسَابِ','و آنها که پیوندهایی را که خدا دستور به برقراری آن داده، برقرار میدارند؛ و از پروردگارشان می‌ترسند؛ و از بدی حساب (روز قیامت) بیم دارند...'];
ayat[1729]=[13,22,'وَالَّذِينَ صَبَرُوا ابْتِغَاءَ وَجْهِ رَبِّهِمْ وَأَقَامُوا الصَّلَاةَ وَأَنفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً وَيَدْرَءُونَ بِالْحَسَنَةِ السَّيِّئَةَ أُولَٰئِكَ لَهُمْ عُقْبَى الدَّارِ','و آنها که بخاطر ذات (پاک) پروردگارشان شکیبایی می‌کنند؛ و نماز را برپا می‌دارند؛ و از آنچه به آنها روزی داده‌ایم، در پنهان و آشکار، انفاق می‌کنند؛ و با حسنات، سیئات را از میان می‌برند؛ پایان نیک سرای دیگر، از آن آنهاست...'];
ayat[1730]=[13,23,'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا وَمَن صَلَحَ مِنْ آبَائِهِمْ وَأَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ ۖ وَالْمَلَائِكَةُ يَدْخُلُونَ عَلَيْهِم مِّن كُلِّ بَابٍ','(همان) باغهای جاویدان بهشتی که وارد آن می‌شوند؛ و همچنین پدران و همسران و فرزندان صالح آنها؛ و فرشتگان از هر دری بر آنان وارد می‌گردند...'];
ayat[1731]=[13,24,'سَلَامٌ عَلَيْكُم بِمَا صَبَرْتُمْ ۚ فَنِعْمَ عُقْبَى الدَّارِ','(و به آنان می‌گویند:) سلام بر شما بخاطر صبر و استقامتتان! چه نیکوست سرانجام آن سرا (ی جاویدان)!'];
ayat[1732]=[13,25,'وَالَّذِينَ يَنقُضُونَ عَهْدَ اللَّهِ مِن بَعْدِ مِيثَاقِهِ وَيَقْطَعُونَ مَا أَمَرَ اللَّهُ بِهِ أَن يُوصَلَ وَيُفْسِدُونَ فِي الْأَرْضِ ۙ أُولَٰئِكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوءُ الدَّارِ','آنها که عهد الهی را پس از محکم کردن می‌شکنند، و پیوندهایی را که خدا دستور به برقراری آن داده قطع می‌کنند، و در روی زمین فساد می‌نمایند، لعنت برای آنهاست؛ و بدی (و مجازات) سرای آخرت!'];
ayat[1733]=[13,26,'اللَّهُ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ وَيَقْدِرُ ۚ وَفَرِحُوا بِالْحَيَاةِ الدُّنْيَا وَمَا الْحَيَاةُ الدُّنْيَا فِي الْآخِرَةِ إِلَّا مَتَاعٌ','خدا روزی را برای هر کس بخواهد (و شایسته بداند) وسیع، برای هر کس بخواهد (و مصلحت بداند،) تنگ قرارمی‌دهد؛ ولی آنها [= کافران‌] به زندگی دنیا، شاد (و خوشحال) شدند؛ در حالی که زندگی دنیا در برابر آخرت، متاع ناچیزی است!'];
ayat[1734]=[13,27,'وَيَقُولُ الَّذِينَ كَفَرُوا لَوْلَا أُنزِلَ عَلَيْهِ آيَةٌ مِّن رَّبِّهِ ۗ قُلْ إِنَّ اللَّهَ يُضِلُّ مَن يَشَاءُ وَيَهْدِي إِلَيْهِ مَنْ أَنَابَ','کسانی که کافر شدند می‌گویند: «چرا آیه (و معجزه) ای از پروردگارش بر او نازل نشده است؟!» بگو: «خداوند هر کس را بخواهد گمراه، و هر کس را که بازگردد، به سوی خودش هدایت می‌کند! (کمبودی در معجزه‌ها نیست؛ لجاجت آنها مانع است!)»'];
ayat[1735]=[13,28,'الَّذِينَ آمَنُوا وَتَطْمَئِنُّ قُلُوبُهُم بِذِكْرِ اللَّهِ ۗ أَلَا بِذِكْرِ اللَّهِ تَطْمَئِنُّ الْقُلُوبُ','آنها کسانی هستند که ایمان آورده‌اند، و دلهایشان به یاد خدا مطمئن (و آرام) است؛ آگاه باشید، تنها با یاد خدا دلها آرامش می‌یابد!'];
ayat[1736]=[13,29,'الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ طُوبَىٰ لَهُمْ وَحُسْنُ مَآبٍ','آنها که ایمان آوردند و کارهای شایسته انجام دادند، پاکیزه‌ترین (زندگی) نصیبشان است؛ و بهترین سرانجامها!'];
ayat[1737]=[13,30,'كَذَٰلِكَ أَرْسَلْنَاكَ فِي أُمَّةٍ قَدْ خَلَتْ مِن قَبْلِهَا أُمَمٌ لِّتَتْلُوَ عَلَيْهِمُ الَّذِي أَوْحَيْنَا إِلَيْكَ وَهُمْ يَكْفُرُونَ بِالرَّحْمَٰنِ ۚ قُلْ هُوَ رَبِّي لَا إِلَٰهَ إِلَّا هُوَ عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ مَتَابِ','همان گونه (که پیامبران پیشین را مبعوث کردیم،) تو را به میان امّتی فرستادیم که پیش از آنها امّتهای دیگری آمدند و رفتند، تا آنچه را به تو وحی نموده‌ایم بر آنان بخوانی، در حالی که به رحمان [= خداوندی که رحمتش همگان را فراگرفته‌] کفر می‌ورزند؛ بگو: «او پروردگار من است! معبودی جز او نیست! بر او توکّل کردم؛ و بازگشتم بسوی اوست!»'];
ayat[1738]=[13,31,'وَلَوْ أَنَّ قُرْآنًا سُيِّرَتْ بِهِ الْجِبَالُ أَوْ قُطِّعَتْ بِهِ الْأَرْضُ أَوْ كُلِّمَ بِهِ الْمَوْتَىٰ ۗ بَل لِّلَّهِ الْأَمْرُ جَمِيعًا ۗ أَفَلَمْ يَيْأَسِ الَّذِينَ آمَنُوا أَن لَّوْ يَشَاءُ اللَّهُ لَهَدَى النَّاسَ جَمِيعًا ۗ وَلَا يَزَالُ الَّذِينَ كَفَرُوا تُصِيبُهُم بِمَا صَنَعُوا قَارِعَةٌ أَوْ تَحُلُّ قَرِيبًا مِّن دَارِهِمْ حَتَّىٰ يَأْتِيَ وَعْدُ اللَّهِ ۚ إِنَّ اللَّهَ لَا يُخْلِفُ الْمِيعَادَ','اگر بوسیله قرآن، کوه‌ها به حرکت درآیند یا زمینها قطعه قطعه شوند، یا بوسیله آن با مردگان سخن گفته شود، (باز هم ایمان نخواهند آورد!) ولی همه کارها در اختیار خداست! آیا آنها که ایمان آورده‌اند نمی‌دانند که اگر خدا بخواهد همه مردم را (به اجبار) هدایت می‌کند (امّا هدایت اجباری سودی ندارد)! و پیوسته بلاهای کوبنده‌ای بر کافران بخاطر اعمالشان وارد می‌شود، و یا بنزدیکی خانه آنها فرود می‌آید، تا وعده (نهایی) خدا فرا رسد؛ به یقین خداوند در وعد؛ خود تخلّف نمی‌کند!'];
ayat[1739]=[13,32,'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّن قَبْلِكَ فَأَمْلَيْتُ لِلَّذِينَ كَفَرُوا ثُمَّ أَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ عِقَابِ','(تنها تو را استهزا نکردند،) پیامبران پیش از تو را نیز مورد استهزا قرار دادند؛ من به کافران مهلت دادم؛ سپس آنها را گرفتم؛ دیدی مجازات من چگونه بود؟!'];
ayat[1740]=[13,33,'أَفَمَنْ هُوَ قَائِمٌ عَلَىٰ كُلِّ نَفْسٍ بِمَا كَسَبَتْ ۗ وَجَعَلُوا لِلَّهِ شُرَكَاءَ قُلْ سَمُّوهُمْ ۚ أَمْ تُنَبِّئُونَهُ بِمَا لَا يَعْلَمُ فِي الْأَرْضِ أَم بِظَاهِرٍ مِّنَ الْقَوْلِ ۗ بَلْ زُيِّنَ لِلَّذِينَ كَفَرُوا مَكْرُهُمْ وَصُدُّوا عَنِ السَّبِيلِ ۗ وَمَن يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ','آیا کسی که بالای سر همه ایستاده، (و حافظ و نگهبان و مراقب همه است،) و اعمال همه را می‌بییند (، همچون کسی است که هیچ یک از این صفات را ندارد)؟! آنان برای خدا همتایانی قرار دادند؛ بگو: «آنها را نام ببرید! آیا چیزی را به او خبر می‌دهید که از وجود آن در روی زمین بی‌خبر است، یا سخنان ظاهری (و تو خالی) می‌گویید؟!» (نه، شریکی برای خدا وجود ندارد؛) بلکه در نظر کافران، دروغهایشان زینت داده شده، (و بر اثر ناپاکی درون، چنین می‌پندارند که واقعیتی دارد؛) و آنها از راه (خدا) بازداشته شده‌اند؛ و هر کس را خدا گمراه کند، راهنمایی برای او وجود نخواهد داشت!'];
ayat[1741]=[13,34,'لَّهُمْ عَذَابٌ فِي الْحَيَاةِ الدُّنْيَا ۖ وَلَعَذَابُ الْآخِرَةِ أَشَقُّ ۖ وَمَا لَهُم مِّنَ اللَّهِ مِن وَاقٍ','در دنیا، برای آنها عذابی (دردناک) است؛ و عذاب آخرت سخت‌تر است؛ و در برابر (عذاب) خدا، هیچ کس نمی‌تواند آنها را نگه دارد!'];
ayat[1742]=[13,35,'۞ مَّثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ ۖ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۖ أُكُلُهَا دَائِمٌ وَظِلُّهَا ۚ تِلْكَ عُقْبَى الَّذِينَ اتَّقَوا ۖ وَّعُقْبَى الْكَافِرِينَ النَّارُ','توصیف بهشتی که به پرهیزگاران وعده داده شده، (این است که) نهرهای آب از زیر درختانش جاری است، میوه آن همیشگی، و سایه‌اش دائمی است؛ این سرانجام کسانی است که پرهیزگاری پیشه کردند؛ و سرانجام کافران، آتش است!'];
ayat[1743]=[13,36,'وَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يَفْرَحُونَ بِمَا أُنزِلَ إِلَيْكَ ۖ وَمِنَ الْأَحْزَابِ مَن يُنكِرُ بَعْضَهُ ۚ قُلْ إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ وَلَا أُشْرِكَ بِهِ ۚ إِلَيْهِ أَدْعُو وَإِلَيْهِ مَآبِ','کسانی که کتاب آسمانی به آنان داده‌ایم، از آنچه بر تو نازل شده، خوشحالند؛ و بعضی از احزاب (و گروه‌ها)، قسمتی از آن را انکار می‌کنند؛ بگو: «من مأمورم که «اللّه» را بپرستم؛ و شریکی برای او قائل نشوم! به سوی او دعوت می‌کنم؛ و بازگشت من بسوی اوست!»'];
ayat[1744]=[13,37,'وَكَذَٰلِكَ أَنزَلْنَاهُ حُكْمًا عَرَبِيًّا ۚ وَلَئِنِ اتَّبَعْتَ أَهْوَاءَهُم بَعْدَمَا جَاءَكَ مِنَ الْعِلْمِ مَا لَكَ مِنَ اللَّهِ مِن وَلِيٍّ وَلَا وَاقٍ','همان‌گونه (که به پیامبران پیشین کتاب آسمانی دادیم،) بر تو نیز این (قرآن) را بعنوان فرمان روشن و صریحی نازل کردیم؛ و اگر از هوسهای آنان -بعد از آنکه آگاهی برای تو آمده- پیروی کنی، هیچ کس در برابر خدا، از تو حمایت و جلوگیری نخواهد کرد.'];
ayat[1745]=[13,38,'وَلَقَدْ أَرْسَلْنَا رُسُلًا مِّن قَبْلِكَ وَجَعَلْنَا لَهُمْ أَزْوَاجًا وَذُرِّيَّةً ۚ وَمَا كَانَ لِرَسُولٍ أَن يَأْتِيَ بِآيَةٍ إِلَّا بِإِذْنِ اللَّهِ ۗ لِكُلِّ أَجَلٍ كِتَابٌ','ما پیش از تو (نیز) رسولانی فرستادیم؛ و برای آنها همسران و فرزندانی قرار دادیم؛ و هیچ رسولی نمی‌توانست ا (ز پیش خود) معجزه‌ای بیاورد، مگر بفرمان خدا! هر زمانی نوشته‌ای دارد (و برای هر کاری، موعدی مقرّر است)!'];
ayat[1746]=[13,39,'يَمْحُو اللَّهُ مَا يَشَاءُ وَيُثْبِتُ ۖ وَعِندَهُ أُمُّ الْكِتَابِ','خداوند هر چه را بخواهد محو، و هر چه را بخواهد اثبات می‌کند؛ و «امّ الکتاب» [= لوح محفوظ] نزد اوست!'];
ayat[1747]=[13,40,'وَإِن مَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيْكَ الْبَلَاغُ وَعَلَيْنَا الْحِسَابُ','و اگر پاره‌ای از مجازاتها را که به آنها وعده می‌دهیم به تو نشان دهیم، یا (پیش از فرا رسیدن این مجازاتها) تو را بمیرانیم، در هر حال تو فقط مأمور ابلاغ هستی؛ و حساب (آنها) برماست.'];
ayat[1748]=[13,41,'أَوَلَمْ يَرَوْا أَنَّا نَأْتِي الْأَرْضَ نَنقُصُهَا مِنْ أَطْرَافِهَا ۚ وَاللَّهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهِ ۚ وَهُوَ سَرِيعُ الْحِسَابِ','آیا ندیدند که ما پیوسته به سراغ زمین می‌آییم و از اطراف (و جوانب) آن کم می‌کنیم؟! (و جامعه‌ها، تمدّنها، و دانشمندان تدریجاً از میان می‌روند.) و خداوند حکومت می‌کند؛ و هیچ کس را یارای جلوگیری یا ردّ احکام او نیست؛ و او سریع الحساب است!'];
ayat[1749]=[13,42,'وَقَدْ مَكَرَ الَّذِينَ مِن قَبْلِهِمْ فَلِلَّهِ الْمَكْرُ جَمِيعًا ۖ يَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍ ۗ وَسَيَعْلَمُ الْكُفَّارُ لِمَنْ عُقْبَى الدَّارِ','پیش از آنان نیز کسانی طرحها و نقشه‌ها کشیدند؛ ولی تمام طرحها و نقشه‌ها از آن خداست! او از کار هر کس آگاه است؛ و بزودی کفّار می‌دانند سرانجام (نیک و بد) در سرای دیگر از آن کیست!'];
ayat[1750]=[13,43,'وَيَقُولُ الَّذِينَ كَفَرُوا لَسْتَ مُرْسَلًا ۚ قُلْ كَفَىٰ بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ وَمَنْ عِندَهُ عِلْمُ الْكِتَابِ','آنها که کافر شدند می‌گویند: «تو پیامبر نیستی!» بگو: «کافی است که خداوند، و کسی که علم کتاب (و آگاهی بر قرآن) نزد اوست، میان من و شما گواه باشند!»'];
ayat[1751]=[14,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ كِتَابٌ أَنزَلْنَاهُ إِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ بِإِذْنِ رَبِّهِمْ إِلَىٰ صِرَاطِ الْعَزِيزِ الْحَمِيدِ','الر، (این) کتابی است که بر تو نازل کردیم، تا مردم را از تاریکیها (ی شرک و ظلم و جهل،) به سوی روشنایی (ایمان و عدل و آگاهی،) بفرمان پروردگارشان در آوری، بسوی راه خداوند عزیز و حمید.'];
ayat[1752]=[14,2,'اللَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَوَيْلٌ لِّلْكَافِرِينَ مِنْ عَذَابٍ شَدِيدٍ','همان خدایی که آنچه در آسمانها و آنچه در زمین است، از آن اوست؛ وای بر کافران از مجازات شدید (الهی)!'];
ayat[1753]=[14,3,'الَّذِينَ يَسْتَحِبُّونَ الْحَيَاةَ الدُّنْيَا عَلَى الْآخِرَةِ وَيَصُدُّونَ عَن سَبِيلِ اللَّهِ وَيَبْغُونَهَا عِوَجًا ۚ أُولَٰئِكَ فِي ضَلَالٍ بَعِيدٍ','همانها که زندگی دنیا را بر آخرت ترجیح می‌دهند؛ و (مردم را) از راه خدا باز می‌دارند؛ و می‌خواهند راه حق را منحرف سازند؛ آنها در گمراهی دوری هستند!'];
ayat[1754]=[14,4,'وَمَا أَرْسَلْنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوْمِهِ لِيُبَيِّنَ لَهُمْ ۖ فَيُضِلُّ اللَّهُ مَن يَشَاءُ وَيَهْدِي مَن يَشَاءُ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','ما هیچ پیامبری را، جز به زبان قومش، نفرستادیم؛ تا (حقایق را) برای آنها آشکار سازد؛ سپس خدا هر کس را بخواهد (و مستحق بداند) گمراه، و هر کس را بخواهد (و شایسته بداند) هدایت می‌کند؛ و او توانا و حکیم است.'];
ayat[1755]=[14,5,'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا أَنْ أَخْرِجْ قَوْمَكَ مِنَ الظُّلُمَاتِ إِلَى النُّورِ وَذَكِّرْهُم بِأَيَّامِ اللَّهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ','ما موسی را با آیات خود فرستادیم؛ (و دستور دادیم:) قومت را از ظلمات به نور بیرون آر! و «ایّام اللّه» را به آنان یاد آور! در این، نشانه‌هایی است برای هر صبر کننده شکرگزار!'];
ayat[1756]=[14,6,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ أَنجَاكُم مِّنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذَابِ وَيُذَبِّحُونَ أَبْنَاءَكُمْ وَيَسْتَحْيُونَ نِسَاءَكُمْ ۚ وَفِي ذَٰلِكُم بَلَاءٌ مِّن رَّبِّكُمْ عَظِيمٌ','و (به خاطر بیاور) هنگامی را که موسی به قومش گفت: «نعمت خدا را بر خود به یاد داشته باشید، زمانی که شما را از (چنگال) آل فرعون رهایی بخشید! همانها که شما را به بدترین وجهی عذاب می‌کردند؛ پسرانتان را سر می‌بریدند، و زنانتان را (برای خدمتگاری) زنده می‌گذاشتند؛ و در این، آزمایش بزرگی از طرف پروردگارتان برای شما بود!»'];
ayat[1757]=[14,7,'وَإِذْ تَأَذَّنَ رَبُّكُمْ لَئِن شَكَرْتُمْ لَأَزِيدَنَّكُمْ ۖ وَلَئِن كَفَرْتُمْ إِنَّ عَذَابِي لَشَدِيدٌ','و (همچنین به خاطر بیاورید) هنگامی را که پروردگارتان اعلام داشت: «اگر شکرگزاری کنید، (نعمت خود را) بر شما خواهم افزود؛ و اگر ناسپاسی کنید، مجازاتم شدید است!»'];
ayat[1758]=[14,8,'وَقَالَ مُوسَىٰ إِن تَكْفُرُوا أَنتُمْ وَمَن فِي الْأَرْضِ جَمِيعًا فَإِنَّ اللَّهَ لَغَنِيٌّ حَمِيدٌ','و موسی (به بنی اسرائیل) گفت: «اگر شما و همه مردم روی زمین کافر شوید، (به خدا زیانی نمی‌رسد؛ چرا که) خداوند، بی‌نیاز و شایسته ستایش است!»'];
ayat[1759]=[14,9,'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ مِن قَبْلِكُمْ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ ۛ وَالَّذِينَ مِن بَعْدِهِمْ ۛ لَا يَعْلَمُهُمْ إِلَّا اللَّهُ ۚ جَاءَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ فَرَدُّوا أَيْدِيَهُمْ فِي أَفْوَاهِهِمْ وَقَالُوا إِنَّا كَفَرْنَا بِمَا أُرْسِلْتُم بِهِ وَإِنَّا لَفِي شَكٍّ مِّمَّا تَدْعُونَنَا إِلَيْهِ مُرِيبٍ','آیا خبر کسانی که پیش از شما بودند، به شما نرسید؟! «قوم نوح» و «عاد» و «ثمود» و آنها که پس از ایشان بودند؛ همانها که جز خداوند از آنان آگاه نیست؛ پیامبرانشان دلایل روشن برای آنان آوردند، ولی آنها (از روی تعجّب و استهزا) دست بر دهان گرفتند و گفتند: «ما به آنچه شما به آن فرستاده شده‌اید، کافریم! و نسبت به آنچه ما را به سوی آن می‌خوانید، شکّ و تردید داریم!»'];
ayat[1760]=[14,10,'۞ قَالَتْ رُسُلُهُمْ أَفِي اللَّهِ شَكٌّ فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ ۖ يَدْعُوكُمْ لِيَغْفِرَ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ إِلَىٰ أَجَلٍ مُّسَمًّى ۚ قَالُوا إِنْ أَنتُمْ إِلَّا بَشَرٌ مِّثْلُنَا تُرِيدُونَ أَن تَصُدُّونَا عَمَّا كَانَ يَعْبُدُ آبَاؤُنَا فَأْتُونَا بِسُلْطَانٍ مُّبِينٍ','رسولان آنها گفتند: «آیا در خدا شکّ است؟! خدایی که آسمانها و زمین را آفریده؛ او شما را دعوت می‌کند تا گناهانتان را ببخشد، و تا موعد مقرّری شما را باقی گذارد!» آنها گفتند: «(ما اینها را نمی‌فهمیم! همین اندازه می‌دانیم که) شما انسانهایی همانند ما هستید، می‌خواهید ما را از آنچه پدرانمان می‌پرستیدند بازدارید؛ شما دلیل و معجزه روشنی برای ما بیاورید!»'];
ayat[1761]=[14,11,'قَالَتْ لَهُمْ رُسُلُهُمْ إِن نَّحْنُ إِلَّا بَشَرٌ مِّثْلُكُمْ وَلَٰكِنَّ اللَّهَ يَمُنُّ عَلَىٰ مَن يَشَاءُ مِنْ عِبَادِهِ ۖ وَمَا كَانَ لَنَا أَن نَّأْتِيَكُم بِسُلْطَانٍ إِلَّا بِإِذْنِ اللَّهِ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','پیامبرانشان به آنها گفتند: «درست است که ما بشری همانند شما هستیم، ولی خداوند بر هر کس از بندگانش بخواهد (و شایسته بداند)، نعمت می‌بخشد (و مقام رسالت عطا می‌کند)! و ما هرگز نمی‌توانیم معجزه‌ای جز بفرمان خدا بیاوریم! (و از تهدیدهای شما نمی‌هراسیم؛) افراد باایمان باید تنها بر خدا توکّل کنند!'];
ayat[1762]=[14,12,'وَمَا لَنَا أَلَّا نَتَوَكَّلَ عَلَى اللَّهِ وَقَدْ هَدَانَا سُبُلَنَا ۚ وَلَنَصْبِرَنَّ عَلَىٰ مَا آذَيْتُمُونَا ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ','و چرا بر خدا توکّل نکنیم، با اینکه ما را به راه‌های (سعادت) رهبری کرده است؟! و ما بطور مسلّم در برابر آزارهای شما صبر خواهیم کرد (و دست از رسالت خویش بر نمی‌داریم)! و توکّل‌کنندگان، باید فقط بر خدا توکّل کنند!»'];
ayat[1763]=[14,13,'وَقَالَ الَّذِينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُم مِّنْ أَرْضِنَا أَوْ لَتَعُودُنَّ فِي مِلَّتِنَا ۖ فَأَوْحَىٰ إِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظَّالِمِينَ','(ولی) کافران به پیامبران خود گفتند: «ما قطعاً شما را از سرزمین خود بیرون خواهیم کرد، مگر اینکه به آیین ما بازگردید!» در این حال، پروردگارشان به آنها وحی فرستاد که: «ما ظالمان را هلاک می‌کنیم!'];
ayat[1764]=[14,14,'وَلَنُسْكِنَنَّكُمُ الْأَرْضَ مِن بَعْدِهِمْ ۚ ذَٰلِكَ لِمَنْ خَافَ مَقَامِي وَخَافَ وَعِيدِ','و شما را بعد از آنان در زمین سکوت خواهیم داد، این (موفقیّت)، برای کسی است که از مقام (عدالت) من بترسد؛ و از عذاب (من) بیمناک باشد!»'];
ayat[1765]=[14,15,'وَاسْتَفْتَحُوا وَخَابَ كُلُّ جَبَّارٍ عَنِيدٍ','و آنها (از خدا) تقاضای فتح و پیروزی (بر کفار) کردند؛ و (سرانجام) هر گردنکش منحرفی نومید و نابود شد!'];
ayat[1766]=[14,16,'مِّن وَرَائِهِ جَهَنَّمُ وَيُسْقَىٰ مِن مَّاءٍ صَدِيدٍ','به دنبال او جهنم خواهد بود؛ و از آب بد بوی متعفّنی نوشانده می‌شود!'];
ayat[1767]=[14,17,'يَتَجَرَّعُهُ وَلَا يَكَادُ يُسِيغُهُ وَيَأْتِيهِ الْمَوْتُ مِن كُلِّ مَكَانٍ وَمَا هُوَ بِمَيِّتٍ ۖ وَمِن وَرَائِهِ عَذَابٌ غَلِيظٌ','بزحمت جرعه جرعه آن را سرمی‌کشد؛ و هرگز حاضر نیست به میل خود آن را بیاشامد؛ و مرگ از هرجا به سراغ او می‌آید؛ ولی با این همه نمی‌میرد! و بدنبال آن، عذاب شدیدی است!'];
ayat[1768]=[14,18,'مَّثَلُ الَّذِينَ كَفَرُوا بِرَبِّهِمْ ۖ أَعْمَالُهُمْ كَرَمَادٍ اشْتَدَّتْ بِهِ الرِّيحُ فِي يَوْمٍ عَاصِفٍ ۖ لَّا يَقْدِرُونَ مِمَّا كَسَبُوا عَلَىٰ شَيْءٍ ۚ ذَٰلِكَ هُوَ الضَّلَالُ الْبَعِيدُ','اعمال کسانی که به پروردگارشان کافر شدند، همچون خاکستری است در برابر تندباد در یک روز طوفانی! آنها توانایی ندارند کمترین چیزی از آنچه را انجام داده‌اند، به دست آورند؛ و این همان گمراهی دور و دراز است!'];
ayat[1769]=[14,19,'أَلَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۚ إِن يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ','آیا ندیدی خداوند، آسمانها و زمین را بحق آفریده است؟! اگر بخواهد، شما را می‌برد و خلق تازه‌ای می‌آورد!'];
ayat[1770]=[14,20,'وَمَا ذَٰلِكَ عَلَى اللَّهِ بِعَزِيزٍ','و این کار برای خدا مشکل نیست!'];
ayat[1771]=[14,21,'وَبَرَزُوا لِلَّهِ جَمِيعًا فَقَالَ الضُّعَفَاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ أَنتُم مُّغْنُونَ عَنَّا مِنْ عَذَابِ اللَّهِ مِن شَيْءٍ ۚ قَالُوا لَوْ هَدَانَا اللَّهُ لَهَدَيْنَاكُمْ ۖ سَوَاءٌ عَلَيْنَا أَجَزِعْنَا أَمْ صَبَرْنَا مَا لَنَا مِن مَّحِيصٍ','و (در قیامت)، همه آنها در برابر خدا ظاهر می‌شوند؛ در این هنگام، ضعفا [= دنباله‌روان نادان‌] به مستکبران (و رهبران گمراه) می‌گویند: «ما پیروان شما بودیم! آیا (اکنون که بخاطر پیروی از شما گرفتار مجازات الهی شده‌ایم،) شما حاضرید سهمی از عذاب الهی را بپذیرید و از ما بردارید؟» آنها می‌گویند: «اگر خدا ما را هدایت کرده بود، ما نیز شما را هدایت می‌کردیم! (ولی کار از اینها گذشته است،) چه بیتابی کنیم و چه شکیبایی، تفاوتی برای ما ندارد؛ راه گریزی برای ما نیست!»'];
ayat[1772]=[14,22,'وَقَالَ الشَّيْطَانُ لَمَّا قُضِيَ الْأَمْرُ إِنَّ اللَّهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدتُّكُمْ فَأَخْلَفْتُكُمْ ۖ وَمَا كَانَ لِيَ عَلَيْكُم مِّن سُلْطَانٍ إِلَّا أَن دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي ۖ فَلَا تَلُومُونِي وَلُومُوا أَنفُسَكُم ۖ مَّا أَنَا بِمُصْرِخِكُمْ وَمَا أَنتُم بِمُصْرِخِيَّ ۖ إِنِّي كَفَرْتُ بِمَا أَشْرَكْتُمُونِ مِن قَبْلُ ۗ إِنَّ الظَّالِمِينَ لَهُمْ عَذَابٌ أَلِيمٌ','و شیطان، هنگامی که کار تمام می‌شود، می‌گوید: «خداوند به شما وعده حق داد؛ و من به شما وعده (باطل) دادم، و تخلّف کردم! من بر شما تسلّطی نداشتم، جز اینکه دعوتتان کردم و شما دعوت مرا پذیرفتید! بنابر این، مرا سرزنش نکنید؛ خود را سرزنش کنید! نه من فریادرس شما هستم، و نه شما فریادرس من! من نسبت به شرک شما درباره خود، که از قبل داشتید، (و اطاعت مرا همردیف اطاعت خدا قرار دادید) بیزار و کافرم!» مسلّماً ستمکاران عذاب دردناکی دارند!'];
ayat[1773]=[14,23,'وَأُدْخِلَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا بِإِذْنِ رَبِّهِمْ ۖ تَحِيَّتُهُمْ فِيهَا سَلَامٌ','و کسانی را که ایمان آوردند و اعمال صالح انجام دادند، به باغهای بهشت وارد می‌کنند؛ باغهایی که نهرها از زیر درختانش جاری است؛ به اذن پروردگارشان، جاودانه در آن می‌مانند؛ و تحیّت آنها در آن، «سلام» است.'];
ayat[1774]=[14,24,'أَلَمْ تَرَ كَيْفَ ضَرَبَ اللَّهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ أَصْلُهَا ثَابِتٌ وَفَرْعُهَا فِي السَّمَاءِ','آیا ندیدی چگونه خداوند «کلمه طیبه» (و گفتار پاکیزه) را به درخت پاکیزه‌ای تشبیه کرده که ریشه آن (در زمین) ثابت، و شاخه آن در آسمان است؟!'];
ayat[1775]=[14,25,'تُؤْتِي أُكُلَهَا كُلَّ حِينٍ بِإِذْنِ رَبِّهَا ۗ وَيَضْرِبُ اللَّهُ الْأَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ','هر زمان میوه خود را به اذن پروردگارش می‌دهد. و خداوند برای مردم مثلها می‌زند، شاید متذکّر شوند (و پند گیرند)!'];
ayat[1776]=[14,26,'وَمَثَلُ كَلِمَةٍ خَبِيثَةٍ كَشَجَرَةٍ خَبِيثَةٍ اجْتُثَّتْ مِن فَوْقِ الْأَرْضِ مَا لَهَا مِن قَرَارٍ','(همچنین) «کلمه خبیثه» (و سخن آلوده) را به درخت ناپاکی تشبیه کرده که از روی زمین برکنده شده، و قرار و ثباتی ندارد.'];
ayat[1777]=[14,27,'يُثَبِّتُ اللَّهُ الَّذِينَ آمَنُوا بِالْقَوْلِ الثَّابِتِ فِي الْحَيَاةِ الدُّنْيَا وَفِي الْآخِرَةِ ۖ وَيُضِلُّ اللَّهُ الظَّالِمِينَ ۚ وَيَفْعَلُ اللَّهُ مَا يَشَاءُ','خداوند کسانی را که ایمان آوردند، به خاطر گفتار و اعتقاد ثابتشان، استوار می‌دارد؛ هم در این جهان، و هم در سرای دیگر! و ستمگران را گمراه می‌سازد، (و لطف خود را از آنها برمی‌گیرد)؛ خداوند هر کار را بخواهد (و مصلحت بداند) انجام می‌دهد!'];
ayat[1778]=[14,28,'۞ أَلَمْ تَرَ إِلَى الَّذِينَ بَدَّلُوا نِعْمَتَ اللَّهِ كُفْرًا وَأَحَلُّوا قَوْمَهُمْ دَارَ الْبَوَارِ','آیا ندیدی کسانی را که نعمت خدا را به کفران تبدیل کردند، و قوم خود را به سرای نیستی و نابودی کشاندند؟!'];
ayat[1779]=[14,29,'جَهَنَّمَ يَصْلَوْنَهَا ۖ وَبِئْسَ الْقَرَارُ','(سرای نیستی و نابودی، همان) جهنم است که آنها در آتش آن وارد می‌شوند؛ و بد قرارگاهی است!'];
ayat[1780]=[14,30,'وَجَعَلُوا لِلَّهِ أَندَادًا لِّيُضِلُّوا عَن سَبِيلِهِ ۗ قُلْ تَمَتَّعُوا فَإِنَّ مَصِيرَكُمْ إِلَى النَّارِ','آنها برای خدا همتایانی قرار داده‌اند، تا (مردم را) از راه او (منحرف و) گمراه سازند؛ بگو: «(چند روزی از زندگی دنیا و لذّات آن) بهره گیرید؛ امّا عاقبت کار شما به سوی آتش (دوزخ) است!»'];
ayat[1781]=[14,31,'قُل لِّعِبَادِيَ الَّذِينَ آمَنُوا يُقِيمُوا الصَّلَاةَ وَيُنفِقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً مِّن قَبْلِ أَن يَأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيهِ وَلَا خِلَالٌ','به بندگان من که ایمان آورده‌اند بگو نماز را برپا دارند؛ و از آنچه به آنها روزی داده‌ایم، پنهان و آشکار، انفاق کنند؛ پیش از آنکه روزی فرا رسد که نه در آن خرید و فروش است، و نه دوستی! (نه با مال می‌توانند از کیفر خدا رهایی یابند، و نه با پیوندهای مادی!)'];
ayat[1782]=[14,32,'اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَأَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرَاتِ رِزْقًا لَّكُمْ ۖ وَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِأَمْرِهِ ۖ وَسَخَّرَ لَكُمُ الْأَنْهَارَ','خداوند همان کسی است که آسمانها و زمین را آفرید؛ و از آسمان، آبی نازل کرد؛ و با آن، میوه‌ها (ی مختلف) را برای روزی شما (از زمین) بیرون آورد؛ و کشتی‌ها را مسخّر شما گردانید، تا بر صفحه دریا به فرمان او حرکت کنند؛ و نهرها را (نیز) مسخّر شما نمود؛'];
ayat[1783]=[14,33,'وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَائِبَيْنِ ۖ وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهَارَ','و خورشید و ماه را -که با برنامه منظّمی درکارند- به تسخیر شما درآورد؛ و شب و روز را (نیز) مسخّر شما ساخت؛'];
ayat[1784]=[14,34,'وَآتَاكُم مِّن كُلِّ مَا سَأَلْتُمُوهُ ۚ وَإِن تَعُدُّوا نِعْمَتَ اللَّهِ لَا تُحْصُوهَا ۗ إِنَّ الْإِنسَانَ لَظَلُومٌ كَفَّارٌ','و از هر چیزی که از او خواستید، به شما داد؛ و اگر نعمتهای خدا را بشمارید، هرگز آنها را شماره نتوانید کرد! انسان، ستمگر و ناسپاس است!'];
ayat[1785]=[14,35,'وَإِذْ قَالَ إِبْرَاهِيمُ رَبِّ اجْعَلْ هَٰذَا الْبَلَدَ آمِنًا وَاجْنُبْنِي وَبَنِيَّ أَن نَّعْبُدَ الْأَصْنَامَ','(به یاد آورید) زمانی را که ابراهیم گفت: «پروردگارا! این شهر [= مکّه‌] را شهر امنی قرار ده! و من و فرزندانم را از پرستش بتها دور نگاه دار!'];
ayat[1786]=[14,36,'رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيرًا مِّنَ النَّاسِ ۖ فَمَن تَبِعَنِي فَإِنَّهُ مِنِّي ۖ وَمَنْ عَصَانِي فَإِنَّكَ غَفُورٌ رَّحِيمٌ','پروردگارا! آنها [= بتها] بسیاری از مردم را گمراه ساختند! هر کس از من پیروی کند از من است؛ و هر کس نافرمانی من کند، تو بخشنده و مهربانی!'];
ayat[1787]=[14,37,'رَّبَّنَا إِنِّي أَسْكَنتُ مِن ذُرِّيَّتِي بِوَادٍ غَيْرِ ذِي زَرْعٍ عِندَ بَيْتِكَ الْمُحَرَّمِ رَبَّنَا لِيُقِيمُوا الصَّلَاةَ فَاجْعَلْ أَفْئِدَةً مِّنَ النَّاسِ تَهْوِي إِلَيْهِمْ وَارْزُقْهُم مِّنَ الثَّمَرَاتِ لَعَلَّهُمْ يَشْكُرُونَ','پروردگارا! من بعضی از فرزندانم را در سرزمین بی‌آب و علفی، در کنار خانه‌ای که حرم توست، ساکن ساختم تا نماز را برپا دارند؛ تو دلهای گروهی از مردم را متوجّه آنها ساز؛ و از ثمرات به آنها روزی ده؛ شاید آنان شکر تو را بجای آورند!'];
ayat[1788]=[14,38,'رَبَّنَا إِنَّكَ تَعْلَمُ مَا نُخْفِي وَمَا نُعْلِنُ ۗ وَمَا يَخْفَىٰ عَلَى اللَّهِ مِن شَيْءٍ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ','پروردگارا! تو می‌دانی آنچه را ما پنهان و یا آشکار می‌کنیم؛ و چیزی در زمین و آسمان بر خدا پنهان نیست!'];
ayat[1789]=[14,39,'الْحَمْدُ لِلَّهِ الَّذِي وَهَبَ لِي عَلَى الْكِبَرِ إِسْمَاعِيلَ وَإِسْحَاقَ ۚ إِنَّ رَبِّي لَسَمِيعُ الدُّعَاءِ','حمد خدای را که در پیری، اسماعیل و اسحاق را به من بخشید؛ مسلّماً پروردگار من، شنونده (و اجابت کننده) دعاست.'];
ayat[1790]=[14,40,'رَبِّ اجْعَلْنِي مُقِيمَ الصَّلَاةِ وَمِن ذُرِّيَّتِي ۚ رَبَّنَا وَتَقَبَّلْ دُعَاءِ','پروردگارا: مرا برپا کننده نماز قرار ده، و از فرزندانم (نیز چنین فرما)، پروردگارا: دعای مرا بپذیر!'];
ayat[1791]=[14,41,'رَبَّنَا اغْفِرْ لِي وَلِوَالِدَيَّ وَلِلْمُؤْمِنِينَ يَوْمَ يَقُومُ الْحِسَابُ','پروردگارا! من و پدر و مادرم و همه مؤمنان را، در آن روز که حساب برپا می‌شود، بیامرز!'];
ayat[1792]=[14,42,'وَلَا تَحْسَبَنَّ اللَّهَ غَافِلًا عَمَّا يَعْمَلُ الظَّالِمُونَ ۚ إِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيهِ الْأَبْصَارُ','گمان مبر که خدا، از آنچه ظالمان انجام می‌دهند، غافل است! (نه، بلکه کیفر) آنها را برای روزی تأخیر انداخته است که چشمها در آن (به خاطر ترس و وحشت) از حرکت بازمی‌ایستد...'];
ayat[1793]=[14,43,'مُهْطِعِينَ مُقْنِعِي رُءُوسِهِمْ لَا يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ ۖ وَأَفْئِدَتُهُمْ هَوَاءٌ','گردنها را کشیده، سرها را به آسمان بلندکرده، حتّی پلک چشمهایشان از حرکت بازمی‌ماند؛ (زیرا به هر طرف نگاه کنند، آثار عذاب آشکار است!) و (در این حال) دلهایشان (فرومی‌ریزد؛ و از اندیشه و امید،) خالی می‌گردد!'];
ayat[1794]=[14,44,'وَأَنذِرِ النَّاسَ يَوْمَ يَأْتِيهِمُ الْعَذَابُ فَيَقُولُ الَّذِينَ ظَلَمُوا رَبَّنَا أَخِّرْنَا إِلَىٰ أَجَلٍ قَرِيبٍ نُّجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَ ۗ أَوَلَمْ تَكُونُوا أَقْسَمْتُم مِّن قَبْلُ مَا لَكُم مِّن زَوَالٍ','و مردم را از روزی که عذاب الهی به سراغشان می‌آید، بترسان! آن روز که ظالمان می‌گویند: «پروردگارا! مدّت کوتاهی ما را مهلت ده، تا دعوت تو را بپذیریم و از پیامبران پیروی کنیم!» (امّا پاسخ می‌شنوند که:) مگر قبلاً سوگند یاد نکرده بودید که زوال و فنایی برای شما نیست؟!'];
ayat[1795]=[14,45,'وَسَكَنتُمْ فِي مَسَاكِنِ الَّذِينَ ظَلَمُوا أَنفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْأَمْثَالَ','(آری شما بودید که) در منازل (و کاخهای) کسانی که به خویشتن ستم کردند، ساکن شدید؛ و برای شما آشکار شد چگونه با آنان رفتار کردیم؛ و برای شما، مثلها (از سرگذشت پیشینیان) زدیم (باز هم بیدار نشدید)!'];
ayat[1796]=[14,46,'وَقَدْ مَكَرُوا مَكْرَهُمْ وَعِندَ اللَّهِ مَكْرُهُمْ وَإِن كَانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبَالُ','آنها نهایت مکر (و نیرنگ) خود را به کار زدند؛ و همه مکرها (و توطئه‌هایشان) نزد خدا آشکار است، هر چند مکرشان چنان باشد که کوه‌ها را از جا برکند!'];
ayat[1797]=[14,47,'فَلَا تَحْسَبَنَّ اللَّهَ مُخْلِفَ وَعْدِهِ رُسُلَهُ ۗ إِنَّ اللَّهَ عَزِيزٌ ذُو انتِقَامٍ','پس گمان مبر که خدا وعده‌ای را که به پیامبرانش داده، تخلّف کند! چرا که خداوند قادر و انتقام گیرنده است.'];
ayat[1798]=[14,48,'يَوْمَ تُبَدَّلُ الْأَرْضُ غَيْرَ الْأَرْضِ وَالسَّمَاوَاتُ ۖ وَبَرَزُوا لِلَّهِ الْوَاحِدِ الْقَهَّارِ','در آن روز که این زمین به زمین دیگر، و آسمانها (به آسمانهای دیگری) مبدل می‌شود، و آنان در پیشگاه خداوند واحد قهار ظاهر می‌گردند!'];
ayat[1799]=[14,49,'وَتَرَى الْمُجْرِمِينَ يَوْمَئِذٍ مُّقَرَّنِينَ فِي الْأَصْفَادِ','و در آن روز، مجرمان را با هم در غل و زنجیر می‌بینی! (که دستها و گردنهایشان را به هم بسته است!)'];
ayat[1800]=[14,50,'سَرَابِيلُهُم مِّن قَطِرَانٍ وَتَغْشَىٰ وُجُوهَهُمُ النَّارُ','لباسهایشان از قطران [= ماده چسبنده بد بوی قابل اشتعال‌] است؛ و صورتهایشان را آتش می‌پوشاند...'];
ayat[1801]=[14,51,'لِيَجْزِيَ اللَّهُ كُلَّ نَفْسٍ مَّا كَسَبَتْ ۚ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ','تا خداوند هر کس را، هر آنچه انجام داده، جزا دهد! به یقین، خداوند سریع الحساب است!'];
ayat[1802]=[14,52,'هَٰذَا بَلَاغٌ لِّلنَّاسِ وَلِيُنذَرُوا بِهِ وَلِيَعْلَمُوا أَنَّمَا هُوَ إِلَٰهٌ وَاحِدٌ وَلِيَذَّكَّرَ أُولُو الْأَلْبَابِ','این (قرآن،) پیام (و ابلاغی) برای (عموم) مردم است؛ تا همه به وسیله آن انذار شوند، و بدانند او خدا یکتاست؛ و تا صاحبان مغز (و اندیشه) پند گیرند!'];
ayat[1803]=[15,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الر ۚ تِلْكَ آيَاتُ الْكِتَابِ وَقُرْآنٍ مُّبِينٍ','الر، این آیات کتاب، و قرآن مبین است.'];
ayat[1804]=[15,2,'رُّبَمَا يَوَدُّ الَّذِينَ كَفَرُوا لَوْ كَانُوا مُسْلِمِينَ','کافران (هنگامی که آثار شوم اعمال خود را ببینند،) چه بسا آرزو می‌کنند که ای کاش مسلمان بودند!'];
ayat[1805]=[15,3,'ذَرْهُمْ يَأْكُلُوا وَيَتَمَتَّعُوا وَيُلْهِهِمُ الْأَمَلُ ۖ فَسَوْفَ يَعْلَمُونَ','بگذار آنها بخورند، و بهره گیرند، و آرزوها آنان را غافل سازد؛ ولی بزودی خواهند فهمید!'];
ayat[1806]=[15,4,'وَمَا أَهْلَكْنَا مِن قَرْيَةٍ إِلَّا وَلَهَا كِتَابٌ مَّعْلُومٌ','ما اهل هیچ شهر و دیاری را هلاک نکردیم مگر اینکه اجل معیّن (و زمان تغییر ناپذیری) داشتند!'];
ayat[1807]=[15,5,'مَّا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَأْخِرُونَ','هیچ گروهی از اجل خود پیشی نمی‌گیرد؛ و از آن عقب نخواهد افتاد!'];
ayat[1808]=[15,6,'وَقَالُوا يَا أَيُّهَا الَّذِي نُزِّلَ عَلَيْهِ الذِّكْرُ إِنَّكَ لَمَجْنُونٌ','و گفتند: «ای کسی که «ذکر» [= قرآن‌] بر او نازل شده، مسلماً تو دیوانه‌ای!'];
ayat[1809]=[15,7,'لَّوْ مَا تَأْتِينَا بِالْمَلَائِكَةِ إِن كُنتَ مِنَ الصَّادِقِينَ','اگر راست می‌گویی، چرا فرشتگان را نزد ما نمی‌زوری؟!»'];
ayat[1810]=[15,8,'مَا نُنَزِّلُ الْمَلَائِكَةَ إِلَّا بِالْحَقِّ وَمَا كَانُوا إِذًا مُّنظَرِينَ','(امّا اینها باید بدانند) ما فرشتگان را، جز بحق، نازل نمی‌کنیم،؛ و هرگاه نازل شوند، دیگر به اینها مهلت داده نمی‌شود (؛ و در صورت انکار، به عذاب الهی نابود می‌گردند)!'];
ayat[1811]=[15,9,'إِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَإِنَّا لَهُ لَحَافِظُونَ','ما قرآن را نازل کردیم؛ و ما بطور قطع نگهدار آنیم!'];
ayat[1812]=[15,10,'وَلَقَدْ أَرْسَلْنَا مِن قَبْلِكَ فِي شِيَعِ الْأَوَّلِينَ','ما پیش از تو (نیز) پیامبرانی در میان امّتهای نخستین فرستادیم.'];
ayat[1813]=[15,11,'وَمَا يَأْتِيهِم مِّن رَّسُولٍ إِلَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','هیچ پیامبری به سراغ آنها نمی‌آمد مگر اینکه او را مسخره می‌کردند.'];
ayat[1814]=[15,12,'كَذَٰلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ','ما اینچنین (و از هر طریق ممکن) قرآن را به درون دلهای مجرمان راه می‌دهیم!'];
ayat[1815]=[15,13,'لَا يُؤْمِنُونَ بِهِ ۖ وَقَدْ خَلَتْ سُنَّةُ الْأَوَّلِينَ','(اما با این حال،) آنها به آن ایمان نمی‌آورند؛ روش اقوام پیشین نیز چنین بود!'];
ayat[1816]=[15,14,'وَلَوْ فَتَحْنَا عَلَيْهِم بَابًا مِّنَ السَّمَاءِ فَظَلُّوا فِيهِ يَعْرُجُونَ','و اگر دری از آسمان به روی آنان بگشاییم، و آنها پیوسته در آن بالا روند...'];
ayat[1817]=[15,15,'لَقَالُوا إِنَّمَا سُكِّرَتْ أَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَّسْحُورُونَ','باز می‌گویند: «ما را چشم‌بندی کرده‌اند؛ بلکه ما (سر تا پا) سحر شده‌ایم!»'];
ayat[1818]=[15,16,'وَلَقَدْ جَعَلْنَا فِي السَّمَاءِ بُرُوجًا وَزَيَّنَّاهَا لِلنَّاظِرِينَ','ما در آسمان برجهایی قرار دادیم؛ و آن را برای بینندگان آراستیم.'];
ayat[1819]=[15,17,'وَحَفِظْنَاهَا مِن كُلِّ شَيْطَانٍ رَّجِيمٍ','و آن را از هر شیطان رانده شده‌ای حفظ کردیم؛'];
ayat[1820]=[15,18,'إِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَأَتْبَعَهُ شِهَابٌ مُّبِينٌ','مگر آن کس که استراق سمع کند (و دزدانه گوش فرا دهد) که «شهاب مبین» او را تعقیب می‌کند (و می‌راند)'];
ayat[1821]=[15,19,'وَالْأَرْضَ مَدَدْنَاهَا وَأَلْقَيْنَا فِيهَا رَوَاسِيَ وَأَنبَتْنَا فِيهَا مِن كُلِّ شَيْءٍ مَّوْزُونٍ','و زمین را گستردیم؛ و در آن کوه‌های ثابتی افکندیم؛ و از هر گیاه موزون، در آن رویاندیم؛'];
ayat[1822]=[15,20,'وَجَعَلْنَا لَكُمْ فِيهَا مَعَايِشَ وَمَن لَّسْتُمْ لَهُ بِرَازِقِينَ','و برای شما انواع وسایل زندگی در آن قرار دادیم؛ همچنین برای کسانی که شما نمی‌توانید به آنها روزی دهید!'];
ayat[1823]=[15,21,'وَإِن مِّن شَيْءٍ إِلَّا عِندَنَا خَزَائِنُهُ وَمَا نُنَزِّلُهُ إِلَّا بِقَدَرٍ مَّعْلُومٍ','و خزائن همه چیز، تنها نزد ماست؛ ولی ما جز به اندازه معیّن آن را نازل نمی‌کنیم!'];
ayat[1824]=[15,22,'وَأَرْسَلْنَا الرِّيَاحَ لَوَاقِحَ فَأَنزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَسْقَيْنَاكُمُوهُ وَمَا أَنتُمْ لَهُ بِخَازِنِينَ','ما بادها را برای بارور ساختن (ابرها و گیاهان) فرستادیم؛ و از آسمان آبی نازل کردیم، و شما را با آن سیراب ساختیم؛ در حالی که شما توانایی حفظ و نگهداری آن را نداشتید!'];
ayat[1825]=[15,23,'وَإِنَّا لَنَحْنُ نُحْيِي وَنُمِيتُ وَنَحْنُ الْوَارِثُونَ','ماییم که زنده می‌کنیم و می‌میرانیم؛ و ماییم وارث (همه جهان)!'];
ayat[1826]=[15,24,'وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِينَ مِنكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِينَ','ما، هم پیشینیان شما را دانستیم؛ و هم متأخّران را!'];
ayat[1827]=[15,25,'وَإِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ ۚ إِنَّهُ حَكِيمٌ عَلِيمٌ','پروردگار تو، قطعاً آنها را (در قیامت) جمع و محشور می‌کند؛ چرا که او حکیم و داناست!'];
ayat[1828]=[15,26,'وَلَقَدْ خَلَقْنَا الْإِنسَانَ مِن صَلْصَالٍ مِّنْ حَمَإٍ مَّسْنُونٍ','ما انسان را از گِل خشکیده‌ای (همچون سفال) که از گِل بد بوی (تیره رنگی) گرفته شده بود آفریدیم!'];
ayat[1829]=[15,27,'وَالْجَانَّ خَلَقْنَاهُ مِن قَبْلُ مِن نَّارِ السَّمُومِ','و جن را پیش از آن، از آتش گرم و سوزان خلق کردیم!'];
ayat[1830]=[15,28,'وَإِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي خَالِقٌ بَشَرًا مِّن صَلْصَالٍ مِّنْ حَمَإٍ مَّسْنُونٍ','و (به خاطر بیاور) هنگامی که پروردگارت به فرشتگان گفت: «من بشری را از گل خشکیده‌ای که از گل بدبویی گرفته شده، می‌آفرینم.'];
ayat[1831]=[15,29,'فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِن رُّوحِي فَقَعُوا لَهُ سَاجِدِينَ','هنگامی که کار آن را به پایان رساندم، و در او از روح خود (یک روح شایسته و بزرگ) دمیدم، همگی برای او سجده کنید!»'];
ayat[1832]=[15,30,'فَسَجَدَ الْمَلَائِكَةُ كُلُّهُمْ أَجْمَعُونَ','همه فرشتگان، بی استثنا، سجده کردند...'];
ayat[1833]=[15,31,'إِلَّا إِبْلِيسَ أَبَىٰ أَن يَكُونَ مَعَ السَّاجِدِينَ','جز ابلیس، که ابا کرد از اینکه با سجده‌کنندگان باشد.'];
ayat[1834]=[15,32,'قَالَ يَا إِبْلِيسُ مَا لَكَ أَلَّا تَكُونَ مَعَ السَّاجِدِينَ','(خداوند) فرمود: «ای ابلیس! چرا با سجده‌کنندگان نیستی؟!»'];
ayat[1835]=[15,33,'قَالَ لَمْ أَكُن لِّأَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِن صَلْصَالٍ مِّنْ حَمَإٍ مَّسْنُونٍ','گفت: «من هرگز برای بشری که او را از گل خشکیده‌ای که از گل بدبویی گرفته شده است آفریده‌ای، سجده نخواهم کرد!»'];
ayat[1836]=[15,34,'قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ','فرمود: «از صف آنها [= فرشتگان‌] بیرون رو، که رانده‌شده‌ای (از درگاه ما!).'];
ayat[1837]=[15,35,'وَإِنَّ عَلَيْكَ اللَّعْنَةَ إِلَىٰ يَوْمِ الدِّينِ','و لعنت (و دوری از رحمت حق) تا روز قیامت بر تو خواهد بود!»'];
ayat[1838]=[15,36,'قَالَ رَبِّ فَأَنظِرْنِي إِلَىٰ يَوْمِ يُبْعَثُونَ','گفت: «پروردگارا! مرا تا روز رستاخیز مهلت ده (و زنده بگذار!)»'];
ayat[1839]=[15,37,'قَالَ فَإِنَّكَ مِنَ الْمُنظَرِينَ','فرمود: «تو از مهلت یافتگانی!'];
ayat[1840]=[15,38,'إِلَىٰ يَوْمِ الْوَقْتِ الْمَعْلُومِ','(امّا نه تا روز رستاخیز، بلکه) تا روز وقت معیّنی.»'];
ayat[1841]=[15,39,'قَالَ رَبِّ بِمَا أَغْوَيْتَنِي لَأُزَيِّنَنَّ لَهُمْ فِي الْأَرْضِ وَلَأُغْوِيَنَّهُمْ أَجْمَعِينَ','گفت: «پروردگارا! چون مرا گمراه ساختی، من (نعمتهای مادّی را) در زمین در نظر آنها زینت می‌دهم، و همگی را گمراه خواهم ساخت،'];
ayat[1842]=[15,40,'إِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِينَ','مگر بندگان مخلصت را.»'];
ayat[1843]=[15,41,'قَالَ هَٰذَا صِرَاطٌ عَلَيَّ مُسْتَقِيمٌ','فرمود: «این راه مستقیمی است که بر عهده من است (و سنّت همیشگیم)...'];
ayat[1844]=[15,42,'إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ إِلَّا مَنِ اتَّبَعَكَ مِنَ الْغَاوِينَ','که بر بندگانم تسلّط نخواهی یافت؛ مگر گمراهانی که از تو پیروی می‌کنند؛'];
ayat[1845]=[15,43,'وَإِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ أَجْمَعِينَ','و دوزخ، میعادگاه همه آنهاست!'];
ayat[1846]=[15,44,'لَهَا سَبْعَةُ أَبْوَابٍ لِّكُلِّ بَابٍ مِّنْهُمْ جُزْءٌ مَّقْسُومٌ','هفت در دارد؛ و برای هر دری، گروه معیّنی از آنها تقسیم شده‌اند!'];
ayat[1847]=[15,45,'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَعُيُونٍ','به یقین، پرهیزگاران در باغها (ی سرسبز بهشت) و در کنار چشمه‌ها هستند.'];
ayat[1848]=[15,46,'ادْخُلُوهَا بِسَلَامٍ آمِنِينَ','(فرشتگان به آنها می‌گویند:) داخل این باغها شوید با سلامت و امنیّت!'];
ayat[1849]=[15,47,'وَنَزَعْنَا مَا فِي صُدُورِهِم مِّنْ غِلٍّ إِخْوَانًا عَلَىٰ سُرُرٍ مُّتَقَابِلِينَ','هر گونه غلِّ [= حسد و کینه و دشمنی‌] را از سینه آنها برمی‌کنیم (و روحشان را پاک می‌سازیم)؛ در حالی که همه برابرند، و بر تختها روبه‌روی یکدیگر قرار دارند.'];
ayat[1850]=[15,48,'لَا يَمَسُّهُمْ فِيهَا نَصَبٌ وَمَا هُم مِّنْهَا بِمُخْرَجِينَ','هیچ خستگی و تعبی در آنجا به آنها نمی‌رسد، و هیچ گاه از آن اخراج نمی‌گردند!'];
ayat[1851]=[15,49,'۞ نَبِّئْ عِبَادِي أَنِّي أَنَا الْغَفُورُ الرَّحِيمُ','بندگانم را آگاه کن که من بخشنده مهربانم!'];
ayat[1852]=[15,50,'وَأَنَّ عَذَابِي هُوَ الْعَذَابُ الْأَلِيمُ','و (اینکه) عذاب و کیفر من، همان عذاب دردناک است!'];
ayat[1853]=[15,51,'وَنَبِّئْهُمْ عَن ضَيْفِ إِبْرَاهِيمَ','و به آنها از مهمانهای ابراهیم خبر ده!'];
ayat[1854]=[15,52,'إِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَامًا قَالَ إِنَّا مِنكُمْ وَجِلُونَ','هنگامی که بر او وارد شدند و سلام کردند؛ (ابراهیم) گفت: «ما از شما بیمناکیم!»'];
ayat[1855]=[15,53,'قَالُوا لَا تَوْجَلْ إِنَّا نُبَشِّرُكَ بِغُلَامٍ عَلِيمٍ','گفتند: «نترس، ما تو را به پسری دانا بشارت می‌دهیم!»'];
ayat[1856]=[15,54,'قَالَ أَبَشَّرْتُمُونِي عَلَىٰ أَن مَّسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ','گفت: «آیا به من (چنین) بشارت می‌دهید با اینکه پیر شده‌ام؟! به چه چیز بشارت می‌دهید؟!»'];
ayat[1857]=[15,55,'قَالُوا بَشَّرْنَاكَ بِالْحَقِّ فَلَا تَكُن مِّنَ الْقَانِطِينَ','گفتند: «تو را به حق بشارت دادیم؛ از مأیوسان مباش!»'];
ayat[1858]=[15,56,'قَالَ وَمَن يَقْنَطُ مِن رَّحْمَةِ رَبِّهِ إِلَّا الضَّالُّونَ','گفت: «جز گمراهان، چه کسی از رحمت پروردگارش مأیوس می‌شود؟!»'];
ayat[1859]=[15,57,'قَالَ فَمَا خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ','(سپس) گفت: «مأموریت شما چیست ای فرستادگان خدا؟»'];
ayat[1860]=[15,58,'قَالُوا إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمٍ مُّجْرِمِينَ','گفتند: «ما به سوی قومی گنهکار مأموریت یافته‌ایم (تا آنها را هلاک کنیم)!'];
ayat[1861]=[15,59,'إِلَّا آلَ لُوطٍ إِنَّا لَمُنَجُّوهُمْ أَجْمَعِينَ','مگر خاندان لوط، که همگی آنها را نجات خواهیم داد...'];
ayat[1862]=[15,60,'إِلَّا امْرَأَتَهُ قَدَّرْنَا ۙ إِنَّهَا لَمِنَ الْغَابِرِينَ','بجز همسرش، که مقدّر داشتیم از بازماندگان (در شهر، و هلاک‌شوندگان) باشد!»'];
ayat[1863]=[15,61,'فَلَمَّا جَاءَ آلَ لُوطٍ الْمُرْسَلُونَ','هنگامی که فرستادگان (خدا) به سراغ خاندان لوط آمدند...'];
ayat[1864]=[15,62,'قَالَ إِنَّكُمْ قَوْمٌ مُّنكَرُونَ','(لوط) گفت: «شما گروه ناشناسی هستید!»'];
ayat[1865]=[15,63,'قَالُوا بَلْ جِئْنَاكَ بِمَا كَانُوا فِيهِ يَمْتَرُونَ','گفتند: «ما همان چیزی را برای تو آورده‌ایم که آنها [= کافران‌] در آن تردید داشتند (آری، ما مأمور عذابیم)!'];
ayat[1866]=[15,64,'وَأَتَيْنَاكَ بِالْحَقِّ وَإِنَّا لَصَادِقُونَ','ما واقعیّت مسلّمی را برای تو آورده‌ایم؛ و راست می‌گوییم!'];
ayat[1867]=[15,65,'فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِّنَ اللَّيْلِ وَاتَّبِعْ أَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنكُمْ أَحَدٌ وَامْضُوا حَيْثُ تُؤْمَرُونَ','پس، خانواده‌ات را در اواخر شب با خود بردار، و از اینجا ببر؛ و خودت به دنبال آنها حرکت کن؛ و کسی از شما به پشت سر خویش ننگرد؛ به همانجا که مأمور هستید بروید!»'];
ayat[1868]=[15,66,'وَقَضَيْنَا إِلَيْهِ ذَٰلِكَ الْأَمْرَ أَنَّ دَابِرَ هَٰؤُلَاءِ مَقْطُوعٌ مُّصْبِحِينَ','و ما به لوط این موضوع را وحی فرستادیم که صبحگاهان، همه آنها ریشه‌کن خواهند شد.'];
ayat[1869]=[15,67,'وَجَاءَ أَهْلُ الْمَدِينَةِ يَسْتَبْشِرُونَ','(از سوی دیگر،) اهل شهر (از ورود میهمانان با خبر شدند، و بطرف خانه لوط) آمدند در حالی که شادمان بودند.'];
ayat[1870]=[15,68,'قَالَ إِنَّ هَٰؤُلَاءِ ضَيْفِي فَلَا تَفْضَحُونِ','(لوط) گفت: «اینها میهمانان منند؛ آبروی مرا نریزید!'];
ayat[1871]=[15,69,'وَاتَّقُوا اللَّهَ وَلَا تُخْزُونِ','و از خدا بترسید، و مرا شرمنده نسازید!»'];
ayat[1872]=[15,70,'قَالُوا أَوَلَمْ نَنْهَكَ عَنِ الْعَالَمِينَ','گفتند: «مگر ما تو را از جهانیان نهی نکردیم (و نگفتیم کسی را به میهمانی نپذیر؟!»'];
ayat[1873]=[15,71,'قَالَ هَٰؤُلَاءِ بَنَاتِي إِن كُنتُمْ فَاعِلِينَ','گفت: «دختران من حاضرند، اگر می‌خواهید کار صحیحی انجام دهید (با آنها ازدواج کنید، و از گناه و آلودگی بپرهیزید!)»'];
ayat[1874]=[15,72,'لَعَمْرُكَ إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ','به جان تو سوگند، اینها در مستی خود سرگردانند (و عقل و شعور خود را از دست داده‌اند)!'];
ayat[1875]=[15,73,'فَأَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِينَ','سرانجام، هنگام طلوع آفتاب، صیحه (مرگبار -بصورت صاعقه یا زمین‌لرزه-) آنها را فراگرفت!'];
ayat[1876]=[15,74,'فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِّن سِجِّيلٍ','سپس (شهر و آبادی آنها را زیر و رو کردیم؛) بالای آن را پایین قرار دادیم؛ و بارانی از سنگ بر آنها فرو ریختیم!'];
ayat[1877]=[15,75,'إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّلْمُتَوَسِّمِينَ','در این (سرگذشت عبرت‌انگیز)، نشانه‌هایی است برای هوشیاران!'];
ayat[1878]=[15,76,'وَإِنَّهَا لَبِسَبِيلٍ مُّقِيمٍ','و ویرانه‌های سرزمین آنها، بر سر راه (کاروانها)، همواره ثابت و برقرار است؛'];
ayat[1879]=[15,77,'إِنَّ فِي ذَٰلِكَ لَآيَةً لِّلْمُؤْمِنِينَ','در این، نشانه‌ای است برای مؤمنان!'];
ayat[1880]=[15,78,'وَإِن كَانَ أَصْحَابُ الْأَيْكَةِ لَظَالِمِينَ','«اصحاب الأیکه» [= صاحبان سرزمینهای پردرخت = قوم شعیب‌] مسلماً قوم ستمگری بودند!'];
ayat[1881]=[15,79,'فَانتَقَمْنَا مِنْهُمْ وَإِنَّهُمَا لَبِإِمَامٍ مُّبِينٍ','ما از آنها انتقام گرفتیم؛ و (شهرهای ویران شده) این دو [= قوم لوط و اصحاب الأیکه‌] بر سر راه (شما در سفرهای شام)، آشکار است!'];
ayat[1882]=[15,80,'وَلَقَدْ كَذَّبَ أَصْحَابُ الْحِجْرِ الْمُرْسَلِينَ','و «اصحاب حجر» [= قوم ثمود] پیامبران را تکذیب کردند!'];
ayat[1883]=[15,81,'وَآتَيْنَاهُمْ آيَاتِنَا فَكَانُوا عَنْهَا مُعْرِضِينَ','ما آیات خود را به آنان دادیم؛ ولی آنها از آن روی گرداندند!'];
ayat[1884]=[15,82,'وَكَانُوا يَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتًا آمِنِينَ','آنها خانه‌های امن در دل کوه‌ها می‌تراشیدند.'];
ayat[1885]=[15,83,'فَأَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِينَ','امّا سرانجام صیحه (مرگبار)، صبحگاهان آنان را فرا گرفت؛'];
ayat[1886]=[15,84,'فَمَا أَغْنَىٰ عَنْهُم مَّا كَانُوا يَكْسِبُونَ','و آنچه را به دست آورده بودند، آنها را از عذاب الهی نجات نداد!'];
ayat[1887]=[15,85,'وَمَا خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إِلَّا بِالْحَقِّ ۗ وَإِنَّ السَّاعَةَ لَآتِيَةٌ ۖ فَاصْفَحِ الصَّفْحَ الْجَمِيلَ','ما آسمانها و زمین و آنچه را میان آن دو است، جز بحق نیافریدیم؛ و ساعت موعود [= قیامت‌] قطعاً فرا خواهد رسید (و جزای هر کس به او می‌رسد)! پس، از آنها به طرز شایسته‌ای صرف‌نظر کن (و آنها را بر نادانیهایشان ملامت ننما)!'];
ayat[1888]=[15,86,'إِنَّ رَبَّكَ هُوَ الْخَلَّاقُ الْعَلِيمُ','به یقین، پروردگار تو، آفریننده آگاه است!'];
ayat[1889]=[15,87,'وَلَقَدْ آتَيْنَاكَ سَبْعًا مِّنَ الْمَثَانِي وَالْقُرْآنَ الْعَظِيمَ','ما به تو سوره حمد و قرآن عظیم دادیم!'];
ayat[1890]=[15,88,'لَا تَمُدَّنَّ عَيْنَيْكَ إِلَىٰ مَا مَتَّعْنَا بِهِ أَزْوَاجًا مِّنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِينَ','(بنابر این،) هرگز چشم خود را به نعمتهای (مادّی)، که به گروه‌هایی از آنها [= کفّار] دادیم، میفکن! و بخاطر آنچه آنها دارند، غمگین مباش! و بال (عطوفت) خود را برای مؤمنین فرود آر!'];
ayat[1891]=[15,89,'وَقُلْ إِنِّي أَنَا النَّذِيرُ الْمُبِينُ','و بگو: «من انذارکننده آشکارم!»'];
ayat[1892]=[15,90,'كَمَا أَنزَلْنَا عَلَى الْمُقْتَسِمِينَ','(ما بر آنها عذابی می‌فرستیم) همان گونه که بر تجزیه‌گران (آیات الهی) فرستادیم!'];
ayat[1893]=[15,91,'الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ','همانها که قرآن را تقسیم کردند (؛ آنچه را به سودشان بود پذیرفتند، و آنچه را بر خلاف هوسهایشان بود رها نمودند)!'];
ayat[1894]=[15,92,'فَوَرَبِّكَ لَنَسْأَلَنَّهُمْ أَجْمَعِينَ','به پروردگارت سوگند، (در قیامت) از همه آنها سؤال خواهیم کرد...'];
ayat[1895]=[15,93,'عَمَّا كَانُوا يَعْمَلُونَ','از آنچه عمل می‌کردند!'];
ayat[1896]=[15,94,'فَاصْدَعْ بِمَا تُؤْمَرُ وَأَعْرِضْ عَنِ الْمُشْرِكِينَ','آنچه را مأموریت داری، آشکارا بیان کن! و از مشرکان روی گردان (و به آنها اعتنا نکن)!'];
ayat[1897]=[15,95,'إِنَّا كَفَيْنَاكَ الْمُسْتَهْزِئِينَ','ما شرّ استهزاکنندگان را از تو دفع خواهیم کرد؛'];
ayat[1898]=[15,96,'الَّذِينَ يَجْعَلُونَ مَعَ اللَّهِ إِلَٰهًا آخَرَ ۚ فَسَوْفَ يَعْلَمُونَ','همانها که معبود دیگری با خدا قرار دادند؛ امّا بزودی می‌فهمند!'];
ayat[1899]=[15,97,'وَلَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِمَا يَقُولُونَ','ما می‌دانیم سینه‌ات از آنچه آنها می‌گویند تنگ می‌شود (و تو را سخت ناراحت می‌کنند).'];
ayat[1900]=[15,98,'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَكُن مِّنَ السَّاجِدِينَ','(برای دفع ناراحتی آنان) پروردگارت را تسبیح و حمد گو! و از سجده‌کنندگان باش!'];
ayat[1901]=[15,99,'وَاعْبُدْ رَبَّكَ حَتَّىٰ يَأْتِيَكَ الْيَقِينُ','و پروردگارت را عبادت کن تا یقین [= مرگ‌] تو فرا رسد!'];
ayat[1902]=[16,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَتَىٰ أَمْرُ اللَّهِ فَلَا تَسْتَعْجِلُوهُ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ','فرمان خدا (برای مجازات مشرکان و مجرمان،) فرا رسیده است؛ برای آن عجله نکنید! منزّه و برتر است خداوند از آنچه همتای او قرارمی‌دهند!'];
ayat[1903]=[16,2,'يُنَزِّلُ الْمَلَائِكَةَ بِالرُّوحِ مِنْ أَمْرِهِ عَلَىٰ مَن يَشَاءُ مِنْ عِبَادِهِ أَنْ أَنذِرُوا أَنَّهُ لَا إِلَٰهَ إِلَّا أَنَا فَاتَّقُونِ','فرشتگان را با روح (الهی) بفرمانش بر هر کس از بندگانش بخواهد نازل می‌کند؛ (و به آنها دستور می‌دهد) که مردم را انذار کنید؛ (و بگویید:) معبودی جز من نیست؛ از (مخالفت دستور) من، بپرهیزید!'];
ayat[1904]=[16,3,'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۚ تَعَالَىٰ عَمَّا يُشْرِكُونَ','آسمانها و زمین را بحق آفرید؛ او برتر است از اینکه همتایی برای او قرار می‌دهند!'];
ayat[1905]=[16,4,'خَلَقَ الْإِنسَانَ مِن نُّطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُّبِينٌ','انسان را از نطفه بی‌ارزشی آفرید؛ و سرانجام (او موجودی فصیح، و) مدافع آشکار از خویشتن گردید!'];
ayat[1906]=[16,5,'وَالْأَنْعَامَ خَلَقَهَا ۗ لَكُمْ فِيهَا دِفْءٌ وَمَنَافِعُ وَمِنْهَا تَأْكُلُونَ','و چهارپایان را آفرید؛ در حالی که در آنها، برای شما وسیله پوشش، و منافع دیگری است؛ و از گوشت آنها می‌خورید!'];
ayat[1907]=[16,6,'وَلَكُمْ فِيهَا جَمَالٌ حِينَ تُرِيحُونَ وَحِينَ تَسْرَحُونَ','و در آنها برای شما زینت و شکوه است به هنگامی که آنها را به استراحتگاهشان بازمی‌گردانید، و هنگامی که (صبحگاهان) به صحرا می‌فرستید!'];
ayat[1908]=[16,7,'وَتَحْمِلُ أَثْقَالَكُمْ إِلَىٰ بَلَدٍ لَّمْ تَكُونُوا بَالِغِيهِ إِلَّا بِشِقِّ الْأَنفُسِ ۚ إِنَّ رَبَّكُمْ لَرَءُوفٌ رَّحِيمٌ','آنها بارهای سنگین شما را به شهری حمل می‌کنند که جز با مشقّت زیاد، به آن نمی‌رسیدید؛ پروردگارتان رؤوف و رحیم است (که این وسایل حیات را در اختیارتان قرار داده)!'];
ayat[1909]=[16,8,'وَالْخَيْلَ وَالْبِغَالَ وَالْحَمِيرَ لِتَرْكَبُوهَا وَزِينَةً ۚ وَيَخْلُقُ مَا لَا تَعْلَمُونَ','همچنین اسبها و استرها و الاغها را آفرید؛ تا بر آنها سوار شوید و زینت شما باشد، و چیزهایی می‌آفریند که نمی‌دانید.'];
ayat[1910]=[16,9,'وَعَلَى اللَّهِ قَصْدُ السَّبِيلِ وَمِنْهَا جَائِرٌ ۚ وَلَوْ شَاءَ لَهَدَاكُمْ أَجْمَعِينَ','و بر خداست که راه راست را (به بندگان) نشان دهد؛ امّا بعضی از راه‌ها بیراهه است! و اگر خدا بخواهد، همه شما را (به اجبار) هدایت می‌کند (؛ ولی اجبار سودی ندارد).'];
ayat[1911]=[16,10,'هُوَ الَّذِي أَنزَلَ مِنَ السَّمَاءِ مَاءً ۖ لَّكُم مِّنْهُ شَرَابٌ وَمِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ','او کسی است که از آسمان، آبی فرستاد، که نوشیدن شما از آن است؛ و (همچنین) گیاهان و درختانی که حیوانات خود را در آن به چرا می‌برید، نیز از آن است.'];
ayat[1912]=[16,11,'يُنبِتُ لَكُم بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخِيلَ وَالْأَعْنَابَ وَمِن كُلِّ الثَّمَرَاتِ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّقَوْمٍ يَتَفَكَّرُونَ','خداوند با آن (آب باران)، برای شما زراعت و زیتون و نخل و انگور، و از همه میوه‌ها می‌رویاند؛ مسلماً در این، نشانه روشنی برای اندیشمندان است.'];
ayat[1913]=[16,12,'وَسَخَّرَ لَكُمُ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ ۖ وَالنُّجُومُ مُسَخَّرَاتٌ بِأَمْرِهِ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَعْقِلُونَ','او شب و روز و خورشید و ماه را مسخّر شما ساخت؛ و ستارگان نیز به فرمان او مسخّر شمایند؛ در این، نشانه‌هایی است (از عظمت خدا،) برای گروهی که عقل خود را به کار می‌گیرند!'];
ayat[1914]=[16,13,'وَمَا ذَرَأَ لَكُمْ فِي الْأَرْضِ مُخْتَلِفًا أَلْوَانُهُ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّقَوْمٍ يَذَّكَّرُونَ','(علاوه بر این،) مخلوقاتی را که در زمین به رنگهای گوناگون آفریده نیز مسخّر (فرمان شما) ساخت؛ در این، نشانه روشنی است برای گروهی که متذکّر می‌شوند!'];
ayat[1915]=[16,14,'وَهُوَ الَّذِي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهَا وَتَرَى الْفُلْكَ مَوَاخِرَ فِيهِ وَلِتَبْتَغُوا مِن فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ','او کسی است که دریا را مسخّر (شما) ساخت تا از آن، گوشت تازه بخورید؛ و زیوری برای پوشیدن (مانند مروارید) از آن استخراج کنید؛ و کشتیها را می‌بینی که سینه دریا را می‌شکافند تا شما (به تجارت پردازید و) از فضل خدا بهره گیرید؛ شاید شکر نعمتهای او را بجا آورید!'];
ayat[1916]=[16,15,'وَأَلْقَىٰ فِي الْأَرْضِ رَوَاسِيَ أَن تَمِيدَ بِكُمْ وَأَنْهَارًا وَسُبُلًا لَّعَلَّكُمْ تَهْتَدُونَ','و در زمین، کوه‌های ثابت و محکمی افکند تا لرزش آن را نسبت به شما بگیرد؛ و نهرها و راه‌هایی ایجاد کرد، تا هدایت شوید.'];
ayat[1917]=[16,16,'وَعَلَامَاتٍ ۚ وَبِالنَّجْمِ هُمْ يَهْتَدُونَ','و (نیز) علاماتی قرار داد؛ و (شب هنگام) به وسیله ستارگان هدایت می‌شوند.'];
ayat[1918]=[16,17,'أَفَمَن يَخْلُقُ كَمَن لَّا يَخْلُقُ ۗ أَفَلَا تَذَكَّرُونَ','آیا کسی که (این گونه مخلوقات را) می‌آفریند، همچون کسی است که نمی‌آفریند؟! آیا متذکّر نمی‌شوید؟!'];
ayat[1919]=[16,18,'وَإِن تَعُدُّوا نِعْمَةَ اللَّهِ لَا تُحْصُوهَا ۗ إِنَّ اللَّهَ لَغَفُورٌ رَّحِيمٌ','و اگر نعمتهای خدا را بشمارید، هرگز نمی‌توانید آنها را احصا کنید؛ خداوند بخشنده و مهربان است!'];
ayat[1920]=[16,19,'وَاللَّهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ','خداوند آنچه را پنهان می‌دارید و آنچه را آشکار می‌سازید، می‌داند.'];
ayat[1921]=[16,20,'وَالَّذِينَ يَدْعُونَ مِن دُونِ اللَّهِ لَا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ','معبودهایی را که غیر از خدا می‌خوانند، چیزی را خلق نمی‌کنند؛ بلکه خودشان هم مخلوقند!'];
ayat[1922]=[16,21,'أَمْوَاتٌ غَيْرُ أَحْيَاءٍ ۖ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ','آنها مردگانی هستند که هرگز استعداد حیات ندارند؛ و نمی‌دانند (عبادت‌کنندگانشان) در چه زمانی محشور می‌شوند!'];
ayat[1923]=[16,22,'إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۚ فَالَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ قُلُوبُهُم مُّنكِرَةٌ وَهُم مُّسْتَكْبِرُونَ','معبود شما خداوند یگانه است؛ امّا کسانی که به آخرت ایمان نمی‌آورند، دلهایشان (حق را) انکار می‌کند و مستکبرند.'];
ayat[1924]=[16,23,'لَا جَرَمَ أَنَّ اللَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ ۚ إِنَّهُ لَا يُحِبُّ الْمُسْتَكْبِرِينَ','قطعاً خداوند از آنچه پنهان می‌دارند و آنچه آشکار می‌سازند با خبر است؛ او مستکبران را دوست نمی‌دارد!'];
ayat[1925]=[16,24,'وَإِذَا قِيلَ لَهُم مَّاذَا أَنزَلَ رَبُّكُمْ ۙ قَالُوا أَسَاطِيرُ الْأَوَّلِينَ','و هنگامی که به آنها گفته شود: «پروردگار شما چه نازل کرده است؟» می‌گویند: «اینها (وحی الهی نیست؛) همان افسانه‌های دروغین پیشینیان است!»'];
ayat[1926]=[16,25,'لِيَحْمِلُوا أَوْزَارَهُمْ كَامِلَةً يَوْمَ الْقِيَامَةِ ۙ وَمِنْ أَوْزَارِ الَّذِينَ يُضِلُّونَهُم بِغَيْرِ عِلْمٍ ۗ أَلَا سَاءَ مَا يَزِرُونَ','آنها باید روز قیامت، (هم) بار گناهان خود را بطور کامل بر دوش کشند؛ و هم سهمی از گناهان کسانی که بخاطر جهل، گمراهشان می‌سازند! بدانید آنها بار سنگین بدی بر دوش می‌کشند!'];
ayat[1927]=[16,26,'قَدْ مَكَرَ الَّذِينَ مِن قَبْلِهِمْ فَأَتَى اللَّهُ بُنْيَانَهُم مِّنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِن فَوْقِهِمْ وَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ','کسانی که قبل از ایشان بودند (نیز) از این توطئه‌ها داشتند؛ ولی خداوند به سراغ شالوده (زندگی) آنها رفت؛ و آن را از اساس ویران کرد؛ و سقف از بالا بر سرشان فرو ریخت؛ و عذاب (الهی) از آن جایی که نمی‌دانستند به سراغشان آمد!'];
ayat[1928]=[16,27,'ثُمَّ يَوْمَ الْقِيَامَةِ يُخْزِيهِمْ وَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنتُمْ تُشَاقُّونَ فِيهِمْ ۚ قَالَ الَّذِينَ أُوتُوا الْعِلْمَ إِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوءَ عَلَى الْكَافِرِينَ','سپس روز قیامت خدا آنها را رسوا می‌سازد؛ و می‌گوید: «شریکانی که شما برای من ساختید، و بخاطر آنها با دیگران دشمنی می‌کردید، کجا هستید؟!» (در این هنگام،) کسانی که به آنها علم داده شده می‌گویند: «رسوایی و بدبختی، امروز بر کافران است!»'];
ayat[1929]=[16,28,'الَّذِينَ تَتَوَفَّاهُمُ الْمَلَائِكَةُ ظَالِمِي أَنفُسِهِمْ ۖ فَأَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِن سُوءٍ ۚ بَلَىٰ إِنَّ اللَّهَ عَلِيمٌ بِمَا كُنتُمْ تَعْمَلُونَ','همانها که فرشتگان (مرگ) روحشان را می‌گیرند در حالی که به خود ظلم کرده بودند! در این موقع آنها تسلیم می‌شوند (و بدروغ می‌گویند:) ما کار بدی انجام نمی‌دادیم! آری، خداوند به آنچه انجام می‌دادید عالم است!'];
ayat[1930]=[16,29,'فَادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا ۖ فَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ','(به آنها گفته می‌شود:) اکنون از درهای جهنم وارد شوید در حالی که جاودانه در آن خواهید بود! چه جای بدی است جایگاه مستکبران!'];
ayat[1931]=[16,30,'۞ وَقِيلَ لِلَّذِينَ اتَّقَوْا مَاذَا أَنزَلَ رَبُّكُمْ ۚ قَالُوا خَيْرًا ۗ لِّلَّذِينَ أَحْسَنُوا فِي هَٰذِهِ الدُّنْيَا حَسَنَةٌ ۚ وَلَدَارُ الْآخِرَةِ خَيْرٌ ۚ وَلَنِعْمَ دَارُ الْمُتَّقِينَ','(ولی هنگامی که) به پرهیزگاران گفته می‌شد: «پروردگار شما چه چیز نازل کرده است؟» می‌گفتند: «خیر (و سعادت)» (آری،) برای کسانی که نیکی کردند، در این دنیا نیکی است؛ و سرای آخرت از آن هم بهتر است؛ و چه خوب است سرای پرهیزگاران!'];
ayat[1932]=[16,31,'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۖ لَهُمْ فِيهَا مَا يَشَاءُونَ ۚ كَذَٰلِكَ يَجْزِي اللَّهُ الْمُتَّقِينَ','باغهایی از بهشت جاویدان است که همگی وارد آن می‌شوند؛ نهرها از زیر درختانش می‌گذرد؛ هر چه بخواهند در آنجا هست؛ خداوند پرهیزگاران را چنین پاداش می‌دهد!'];
ayat[1933]=[16,32,'الَّذِينَ تَتَوَفَّاهُمُ الْمَلَائِكَةُ طَيِّبِينَ ۙ يَقُولُونَ سَلَامٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنتُمْ تَعْمَلُونَ','همانها که فرشتگان (مرگ) روحشان را می‌گیرند در حالی که پاک و پاکیزه‌اند؛ به آنها می‌گویند: «سلام بر شما! وارد بهشت شوید به خاطر اعمالی که انجام می‌دادید!»'];
ayat[1934]=[16,33,'هَلْ يَنظُرُونَ إِلَّا أَن تَأْتِيَهُمُ الْمَلَائِكَةُ أَوْ يَأْتِيَ أَمْرُ رَبِّكَ ۚ كَذَٰلِكَ فَعَلَ الَّذِينَ مِن قَبْلِهِمْ ۚ وَمَا ظَلَمَهُمُ اللَّهُ وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','آیا آنها انتظاری جز این دارند که فرشتگان (قبض ارواح) به سراغشان بیایند، یا فرمان پروردگارت (برای مجازاتشان) فرا رسد (آنگاه توبه کنند؟! ولی توبه آنها در آن زمان بی اثر است! آری،) کسانی که پیش از ایشان بودند نیز چنین کردند! خداوند به آنها ستم نکرد؛ ولی آنان به خویشتن ستم می‌نمودند!'];
ayat[1935]=[16,34,'فَأَصَابَهُمْ سَيِّئَاتُ مَا عَمِلُوا وَحَاقَ بِهِم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','و سرانجام بدیهای اعمالشان به آنها رسید؛ و آنچه را (از وعده‌های عذاب) استهزا می‌کردند، بر آنان وارد شد.'];
ayat[1936]=[16,35,'وَقَالَ الَّذِينَ أَشْرَكُوا لَوْ شَاءَ اللَّهُ مَا عَبَدْنَا مِن دُونِهِ مِن شَيْءٍ نَّحْنُ وَلَا آبَاؤُنَا وَلَا حَرَّمْنَا مِن دُونِهِ مِن شَيْءٍ ۚ كَذَٰلِكَ فَعَلَ الَّذِينَ مِن قَبْلِهِمْ ۚ فَهَلْ عَلَى الرُّسُلِ إِلَّا الْبَلَاغُ الْمُبِينُ','مشرکان گفتند: «اگر خدا می‌خواست، نه ما و نه پدران ما، غیر او را پرستش نمی‌کردیم؛ و چیزی را بدون اجازه او حرام نمی‌ساختیم!» (آری،) کسانی که پیش از ایشان بودند نیز همین کارها را انجام دادند؛ ولی آیا پیامبران وظیفه‌ای جز ابلاغ آشکار دارند؟!'];
ayat[1937]=[16,36,'وَلَقَدْ بَعَثْنَا فِي كُلِّ أُمَّةٍ رَّسُولًا أَنِ اعْبُدُوا اللَّهَ وَاجْتَنِبُوا الطَّاغُوتَ ۖ فَمِنْهُم مَّنْ هَدَى اللَّهُ وَمِنْهُم مَّنْ حَقَّتْ عَلَيْهِ الضَّلَالَةُ ۚ فَسِيرُوا فِي الْأَرْضِ فَانظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ','ما در هر امتی رسولی برانگیختیم که: «خدای یکتا را بپرستید؛ و از طاغوت اجتناب کنید!» خداوند گروهی را هدایت کرد؛ و گروهی ضلالت و گمراهی دامانشان را گرفت؛ پس در روی زمین بگردید و ببینید عاقبت تکذیب‌کنندگان چگونه بود!'];
ayat[1938]=[16,37,'إِن تَحْرِصْ عَلَىٰ هُدَاهُمْ فَإِنَّ اللَّهَ لَا يَهْدِي مَن يُضِلُّ ۖ وَمَا لَهُم مِّن نَّاصِرِينَ','هر قدر بر هدایت آنها حریص باشی، (سودی ندارد؛ چرا) که خداوند کسی را که گمراه ساخت، هدایت نمی‌کند؛ و آنها یاورانی نخواهند داشت!'];
ayat[1939]=[16,38,'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ ۙ لَا يَبْعَثُ اللَّهُ مَن يَمُوتُ ۚ بَلَىٰ وَعْدًا عَلَيْهِ حَقًّا وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','آنها سوگندهای شدید به خدا یاد کردند که: «هرگز خداوند کسی را که می‌میرد، برنمی‌انگیزد!» آری، این وعده قطعی خداست (که همه مردگان را برای جزا بازمی‌گرداند)؛ ولی بیشتر مردم نمی‌دانند!'];
ayat[1940]=[16,39,'لِيُبَيِّنَ لَهُمُ الَّذِي يَخْتَلِفُونَ فِيهِ وَلِيَعْلَمَ الَّذِينَ كَفَرُوا أَنَّهُمْ كَانُوا كَاذِبِينَ','هدف این است که آنچه را در آن اختلاف داشتند، برای آنها روشن سازد؛ و کسانی که منکر شدند، بدانند دروغ می‌گفتند!'];
ayat[1941]=[16,40,'إِنَّمَا قَوْلُنَا لِشَيْءٍ إِذَا أَرَدْنَاهُ أَن نَّقُولَ لَهُ كُن فَيَكُونُ','(رستاخیز مردگان برای ما مشکل نیست؛ زیرا) وقتی چیزی را اراده می‌کنیم، فقط به آن می‌گوییم: «موجود باش!» بلافاصله موجود می‌شود.'];
ayat[1942]=[16,41,'وَالَّذِينَ هَاجَرُوا فِي اللَّهِ مِن بَعْدِ مَا ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيَا حَسَنَةً ۖ وَلَأَجْرُ الْآخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا يَعْلَمُونَ','آنها که پس از ستم دیدن در راه خدا، هجرت کردند، در این دنیا جایگاه (و مقام) خوبی به آنها می‌دهیم؛ و پاداش آخرت، از آن هم بزرگتر است اگر می‌دانستند!'];
ayat[1943]=[16,42,'الَّذِينَ صَبَرُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ','آنها کسانی هستند که صبر و استقامت پیشه کردند، و تنها بر پروردگارشان توکّل می‌کنند.'];
ayat[1944]=[16,43,'وَمَا أَرْسَلْنَا مِن قَبْلِكَ إِلَّا رِجَالًا نُّوحِي إِلَيْهِمْ ۚ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِن كُنتُمْ لَا تَعْلَمُونَ','و پیش از تو، جز مردانی که به آنها وحی می‌کردیم، نفرستادیم! اگر نمی‌دانید، از آگاهان بپرسید (تا تعجب نکنید از اینکه پیامبر اسلام از میان همین مردان برانگیخته شده است)!'];
ayat[1945]=[16,44,'بِالْبَيِّنَاتِ وَالزُّبُرِ ۗ وَأَنزَلْنَا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ إِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُونَ','(از آنها بپرسید که) از دلایل روشن و کتب (پیامبران پیشین آگاهند!) و ما این ذکر [= قرآن‌] را بر تو نازل کردیم، تا آنچه به سوی مردم نازل شده است برای آنها روشن سازی؛ و شاید اندیشه کنند!'];
ayat[1946]=[16,45,'أَفَأَمِنَ الَّذِينَ مَكَرُوا السَّيِّئَاتِ أَن يَخْسِفَ اللَّهُ بِهِمُ الْأَرْضَ أَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ','آیا توطئه‌گران از این ایمن گشتند که ممکن است خدا آنها را در زمین فروبرد، و یا مجازات (الهی)، از آن جا که انتظارش را ندارند، به سراغشان آید؟!...'];
ayat[1947]=[16,46,'أَوْ يَأْخُذَهُمْ فِي تَقَلُّبِهِمْ فَمَا هُم بِمُعْجِزِينَ','یا به هنگامی (که برای کسب مال و ثروت افزونتر) در رفت و آمدند، دامانشان را بگیرد در حالی که قادر به فرار نیستند؟!...'];
ayat[1948]=[16,47,'أَوْ يَأْخُذَهُمْ عَلَىٰ تَخَوُّفٍ فَإِنَّ رَبَّكُمْ لَرَءُوفٌ رَّحِيمٌ','یا بطور تدریجی، با هشدارهای خوف‌انگیز آنان را گرفتار سازد؟! چرا که پروردگار شما، رؤوف و رحیم است.'];
ayat[1949]=[16,48,'أَوَلَمْ يَرَوْا إِلَىٰ مَا خَلَقَ اللَّهُ مِن شَيْءٍ يَتَفَيَّأُ ظِلَالُهُ عَنِ الْيَمِينِ وَالشَّمَائِلِ سُجَّدًا لِّلَّهِ وَهُمْ دَاخِرُونَ','آیا آنها مخلوقات خدا را ندیدند که سایه‌هایشان از راست و چپ حرکت دارند، و با خضوع برای خدا سجده می‌کنند؟!'];
ayat[1950]=[16,49,'وَلِلَّهِ يَسْجُدُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ مِن دَابَّةٍ وَالْمَلَائِكَةُ وَهُمْ لَا يَسْتَكْبِرُونَ','(نه تنها سایه‌ها، بلکه) تمام آنچه در آسمانها و زمین از جنبندگان وجود دارد، و همچنین فرشتگان، برای خدا سجده می‌کنند و تکبّر نمی‌ورزند.'];
ayat[1951]=[16,50,'يَخَافُونَ رَبَّهُم مِّن فَوْقِهِمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ ۩','آنها (تنها) از (مخالفت) پروردگارشان، که حاکم بر آنهاست، می‌ترسند؛ و آنچه را مأموریت دارند انجام می‌دهند.'];
ayat[1952]=[16,51,'۞ وَقَالَ اللَّهُ لَا تَتَّخِذُوا إِلَٰهَيْنِ اثْنَيْنِ ۖ إِنَّمَا هُوَ إِلَٰهٌ وَاحِدٌ ۖ فَإِيَّايَ فَارْهَبُونِ','خداوند فرمان داده: «دو معبود (برای خود) انتخاب نکنید؛ معبود (شما) همان خدای یگانه است؛ تنها از (کیفر) من بترسید!»'];
ayat[1953]=[16,52,'وَلَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَلَهُ الدِّينُ وَاصِبًا ۚ أَفَغَيْرَ اللَّهِ تَتَّقُونَ','آنچه در آسمانها و زمین است، از آن اوست؛ و دین خالص (نیز) همواره از آن او می‌باشد؛ آیا از غیر او می‌ترسید؟!'];
ayat[1954]=[16,53,'وَمَا بِكُم مِّن نِّعْمَةٍ فَمِنَ اللَّهِ ۖ ثُمَّ إِذَا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْأَرُونَ','آنچه از نعمتها دارید، همه از سوی خداست! و هنگامی که ناراحتی به شما رسد، فقط او را می‌خوانید!'];
ayat[1955]=[16,54,'ثُمَّ إِذَا كَشَفَ الضُّرَّ عَنكُمْ إِذَا فَرِيقٌ مِّنكُم بِرَبِّهِمْ يُشْرِكُونَ','(امّا) هنگامی که ناراحتی و رنج را از شما برطرف می‌سازد، ناگاه گروهی از شما برای پروردگارشان همتا قائل می‌شوند.'];
ayat[1956]=[16,55,'لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ ۚ فَتَمَتَّعُوا ۖ فَسَوْفَ تَعْلَمُونَ','(بگذار) تا نعمتهایی را که به آنها داده‌ایم کفران کنند! (اکنون) چند روزی (از متاع دنیا) بهره گیرید، امّا بزودی خواهید دانست (سرانجام کارتان به کجا خواهد کشید)!'];
ayat[1957]=[16,56,'وَيَجْعَلُونَ لِمَا لَا يَعْلَمُونَ نَصِيبًا مِّمَّا رَزَقْنَاهُمْ ۗ تَاللَّهِ لَتُسْأَلُنَّ عَمَّا كُنتُمْ تَفْتَرُونَ','آنان برای بتهایی که هیچ گونه سود و زیانی از آنها سراغ ندارند، سهمی از آنچه به آنان روزی داده‌ایم قرارمی‌دهند؛ به خدا سوگند، (در دادگاه قیامت،) از این افتراها که می‌بندید، بازپرسی خواهید شد!'];
ayat[1958]=[16,57,'وَيَجْعَلُونَ لِلَّهِ الْبَنَاتِ سُبْحَانَهُ ۙ وَلَهُم مَّا يَشْتَهُونَ','آنها (در پندار خود،) برای خداوند دختران قرارمی‌دهند؛ -منزّه است (از اینکه فرزندی داشته باشد)- ولی برای خودشان، آنچه را میل دارند قائل می‌شوند...'];
ayat[1959]=[16,58,'وَإِذَا بُشِّرَ أَحَدُهُم بِالْأُنثَىٰ ظَلَّ وَجْهُهُ مُسْوَدًّا وَهُوَ كَظِيمٌ','در حالی که هرگاه به یکی از آنها بشارت دهند دختر نصیب تو شده، صورتش (از فرط ناراحتی) سیاه می‌شود؛ و به شدّت خشمگین می‌گردد؛...'];
ayat[1960]=[16,59,'يَتَوَارَىٰ مِنَ الْقَوْمِ مِن سُوءِ مَا بُشِّرَ بِهِ ۚ أَيُمْسِكُهُ عَلَىٰ هُونٍ أَمْ يَدُسُّهُ فِي التُّرَابِ ۗ أَلَا سَاءَ مَا يَحْكُمُونَ','بخاطر بشارت بدی که به او داده شده، از قوم و قبیله خود متواری می‌گردد؛ (و نمی‌داند) آیا او را با قبول ننگ نگهدارد، یا در خاک پنهانش کند؟! چه بد حکم می‌کنند!'];
ayat[1961]=[16,60,'لِلَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ مَثَلُ السَّوْءِ ۖ وَلِلَّهِ الْمَثَلُ الْأَعْلَىٰ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','برای آنها که به سرای آخرت ایمان ندارند، صفات زشت است؛ و برای خدا، صفات عالی است؛ و او قدرتمند و حکیم است.'];
ayat[1962]=[16,61,'وَلَوْ يُؤَاخِذُ اللَّهُ النَّاسَ بِظُلْمِهِم مَّا تَرَكَ عَلَيْهَا مِن دَابَّةٍ وَلَٰكِن يُؤَخِّرُهُمْ إِلَىٰ أَجَلٍ مُّسَمًّى ۖ فَإِذَا جَاءَ أَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً ۖ وَلَا يَسْتَقْدِمُونَ','و اگر خداوند مردم را بخاطر ظلمشان مجازات می‌کرد، جنبنده‌ای را بر پشت زمین باقی نمی‌گذارد؛ ولی آنها را تا زمان معیّنی به تأخیر می‌اندازد. و هنگامی که اجلشان فرا رسد، نه ساعتی تأخیر می‌کنند، و نه ساعتی پیشی می‌گیرند.'];
ayat[1963]=[16,62,'وَيَجْعَلُونَ لِلَّهِ مَا يَكْرَهُونَ وَتَصِفُ أَلْسِنَتُهُمُ الْكَذِبَ أَنَّ لَهُمُ الْحُسْنَىٰ ۖ لَا جَرَمَ أَنَّ لَهُمُ النَّارَ وَأَنَّهُم مُّفْرَطُونَ','آنها برای خدا چیزهایی قرارمی‌دهند که خودشان از آن کراهت دارند [= فرزندان دختر]؛ با این حال زبانشان به دروغ می‌گوید سرانجام نیکی دارند! از این رو برای آنان آتش است؛ و آنها از پیشگامان (دوزخ) اند.'];
ayat[1964]=[16,63,'تَاللَّهِ لَقَدْ أَرْسَلْنَا إِلَىٰ أُمَمٍ مِّن قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ أَلِيمٌ','به خدا سوگند، به سوی امتهای پیش از تو پیامبرانی فرستادیم؛ امّا شیطان اعمالشان را در نظرشان آراست؛ و امروز او ولیّ و سرپرستشان است؛ و مجازات دردناکی برای آنهاست!'];
ayat[1965]=[16,64,'وَمَا أَنزَلْنَا عَلَيْكَ الْكِتَابَ إِلَّا لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا فِيهِ ۙ وَهُدًى وَرَحْمَةً لِّقَوْمٍ يُؤْمِنُونَ','ما قرآن را بر تو نازل نکردیم مگر برای اینکه آنچه را در آن اختلاف دارند، برای آنها روشن کنی؛ و (این قرآن) مایه هدایت و رحمت است برای قومی که ایمان می‌آورند!'];
ayat[1966]=[16,65,'وَاللَّهُ أَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّقَوْمٍ يَسْمَعُونَ','خداوند از آسمان، آبی فرستاد؛ و زمین را، پس از آنکه مرده بود، حیات بخشید! در این، نشانه روشنی است برای جمعیّتی که گوش شنوا دارند!'];
ayat[1967]=[16,66,'وَإِنَّ لَكُمْ فِي الْأَنْعَامِ لَعِبْرَةً ۖ نُّسْقِيكُم مِّمَّا فِي بُطُونِهِ مِن بَيْنِ فَرْثٍ وَدَمٍ لَّبَنًا خَالِصًا سَائِغًا لِّلشَّارِبِينَ','و در وجود چهارپایان، برای شما (درسهای) عبرتی است: از درون شکم آنها، از میان غذاهای هضم‌شده و خون، شیر خالص و گوارا به شما می‌نوشانیم!'];
ayat[1968]=[16,67,'وَمِن ثَمَرَاتِ النَّخِيلِ وَالْأَعْنَابِ تَتَّخِذُونَ مِنْهُ سَكَرًا وَرِزْقًا حَسَنًا ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّقَوْمٍ يَعْقِلُونَ','و از میوه‌های درختان نخل و انگور، مسکرات (ناپاک) و روزی خوب و پاکیزه می‌گیرید؛ در این، نشانه روشنی است برای جمعیّتی که اندیشه می‌کنند!'];
ayat[1969]=[16,68,'وَأَوْحَىٰ رَبُّكَ إِلَى النَّحْلِ أَنِ اتَّخِذِي مِنَ الْجِبَالِ بُيُوتًا وَمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُونَ','و پروردگار تو به زنبور عسل «وحی» (و الهام غریزی) نمود که: «از کوه‌ها و درختان و داربستهایی که مردم می‌سازند، خانه‌هایی برگزین!'];
ayat[1970]=[16,69,'ثُمَّ كُلِي مِن كُلِّ الثَّمَرَاتِ فَاسْلُكِي سُبُلَ رَبِّكِ ذُلُلًا ۚ يَخْرُجُ مِن بُطُونِهَا شَرَابٌ مُّخْتَلِفٌ أَلْوَانُهُ فِيهِ شِفَاءٌ لِّلنَّاسِ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّقَوْمٍ يَتَفَكَّرُونَ','سپس از تمام ثمرات (و شیره گلها) بخور و راه‌هایی را که پروردگارت برای تو تعیین کرده است، براحتی بپیما! «از درون شکم آنها، نوشیدنی با رنگهای مختلف خارج می‌شود که در آن، شفا برای مردم است؛ به یقین در این امر، نشانه روشنی است برای جمعیّتی که می‌اندیشند.'];
ayat[1971]=[16,70,'وَاللَّهُ خَلَقَكُمْ ثُمَّ يَتَوَفَّاكُمْ ۚ وَمِنكُم مَّن يُرَدُّ إِلَىٰ أَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْئًا ۚ إِنَّ اللَّهَ عَلِيمٌ قَدِيرٌ','خداوند شما را آفرید؛ سپس شما را می‌میراند؛ بعضی از شما به نامطلوب‌ترین سنین بالای عمر می‌رسند، تا بعد از علم و آگاهی، چیزی ندانند (و همه چیز را فراموش کنند)؛ خداوند دانا و تواناست!'];
ayat[1972]=[16,71,'وَاللَّهُ فَضَّلَ بَعْضَكُمْ عَلَىٰ بَعْضٍ فِي الرِّزْقِ ۚ فَمَا الَّذِينَ فُضِّلُوا بِرَادِّي رِزْقِهِمْ عَلَىٰ مَا مَلَكَتْ أَيْمَانُهُمْ فَهُمْ فِيهِ سَوَاءٌ ۚ أَفَبِنِعْمَةِ اللَّهِ يَجْحَدُونَ','خداوند بعضی از شما را بر بعضی دیگر از نظر روزی برتری داد (چرا که استعدادها و تلاشهایتان متفاوت است)! اما آنها که برتری داده شده‌اند، حاضر نیستند از روزی خود به بردگانشان بدهند و همگی در آن مساوی گردند؛ آیا آنان نعمت خدا را انکار می‌نمایند (که شکر او را ادا نمی‌کنند)؟!'];
ayat[1973]=[16,72,'وَاللَّهُ جَعَلَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَاجًا وَجَعَلَ لَكُم مِّنْ أَزْوَاجِكُم بَنِينَ وَحَفَدَةً وَرَزَقَكُم مِّنَ الطَّيِّبَاتِ ۚ أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَتِ اللَّهِ هُمْ يَكْفُرُونَ','خداوند برای شما از جنس خودتان همسرانی قرار داد؛ و از همسرانتان برای شما فرزندان و نوه‌هایی به وجود آورد؛ و از پاکیزه‌ها به شما روزی داد؛ آیا به باطل ایمان می‌آورند، و نعمت خدا را انکار می‌کنند؟!'];
ayat[1974]=[16,73,'وَيَعْبُدُونَ مِن دُونِ اللَّهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقًا مِّنَ السَّمَاوَاتِ وَالْأَرْضِ شَيْئًا وَلَا يَسْتَطِيعُونَ','آنها غیر از خدا، موجوداتی را می‌پرستند که هیچ رزقی را برای آنان از آسمانها و زمین در اختیار ندارند؛ و توان این کار را نیز ندارند.'];
ayat[1975]=[16,74,'فَلَا تَضْرِبُوا لِلَّهِ الْأَمْثَالَ ۚ إِنَّ اللَّهَ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ','پس، برای خدا امثال (و شبیه‌ها) قائل نشوید! خدا می‌داند، و شما نمی‌دانید.'];
ayat[1976]=[16,75,'۞ ضَرَبَ اللَّهُ مَثَلًا عَبْدًا مَّمْلُوكًا لَّا يَقْدِرُ عَلَىٰ شَيْءٍ وَمَن رَّزَقْنَاهُ مِنَّا رِزْقًا حَسَنًا فَهُوَ يُنفِقُ مِنْهُ سِرًّا وَجَهْرًا ۖ هَلْ يَسْتَوُونَ ۚ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ','خداوند مثالی زده: برده مملوکی را که قادر بر هیچ چیز نیست؛ و انسان (با ایمانی) را که از جانب خود، رزقی نیکو به او بخشیده‌ایم، و او پنهان و آشکار از آنچه خدا به او داده، انفاق می‌کند؛ آیا این دو نفر یکسانند؟! شکر مخصوص خداست، ولی اکثر آنها نمی‌دانند!'];
ayat[1977]=[16,76,'وَضَرَبَ اللَّهُ مَثَلًا رَّجُلَيْنِ أَحَدُهُمَا أَبْكَمُ لَا يَقْدِرُ عَلَىٰ شَيْءٍ وَهُوَ كَلٌّ عَلَىٰ مَوْلَاهُ أَيْنَمَا يُوَجِّههُّ لَا يَأْتِ بِخَيْرٍ ۖ هَلْ يَسْتَوِي هُوَ وَمَن يَأْمُرُ بِالْعَدْلِ ۙ وَهُوَ عَلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','خداوند مثالی (دیگر) زده است: دو نفر را، که یکی از آن دو، گنگ مادرزاد است؛ و قادر بر هیچ کاری نیست؛ و سربار صاحبش می‌باشد؛ او را در پی هر کاری بفرستد، خوب انجام نمی‌دهد؛ آیا چنین انسانی، با کسی که امر به عدل و داد می‌کند، و بر راهی راست قرار دارد، برابر است؟!'];
ayat[1978]=[16,77,'وَلِلَّهِ غَيْبُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَمَا أَمْرُ السَّاعَةِ إِلَّا كَلَمْحِ الْبَصَرِ أَوْ هُوَ أَقْرَبُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','غیب آسمانها و زمین، مخصوص خداست (و او همه را می‌داند)؛ و امر قیامت (بقدری نزدیک و آسان است) درست همانند چشم برهم زدن، و یا از آن هم نزدیکتر؛ چرا که خدا بر هر چیزی تواناست!'];
ayat[1979]=[16,78,'وَاللَّهُ أَخْرَجَكُم مِّن بُطُونِ أُمَّهَاتِكُمْ لَا تَعْلَمُونَ شَيْئًا وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۙ لَعَلَّكُمْ تَشْكُرُونَ','و خداوند شما را از شکم مادرانتان خارج نمود در حالی که هیچ چیز نمی‌دانستید؛ و برای شما، گوش و چشم و عقل قرار داد، تا شکر نعمت او را بجا آورید!'];
ayat[1980]=[16,79,'أَلَمْ يَرَوْا إِلَى الطَّيْرِ مُسَخَّرَاتٍ فِي جَوِّ السَّمَاءِ مَا يُمْسِكُهُنَّ إِلَّا اللَّهُ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يُؤْمِنُونَ','آیا آنها به پرندگانی که بر فراز آسمانها نگه‌داشته شده، نظر نیفکندند؟ هیچ کس جز خدا آنها را نگاه نمی‌دارد؛ در این امر، نشانه‌هایی (از عظمت و قدرت خدا) است برای کسانی که ایمان می‌آورند!'];
ayat[1981]=[16,80,'وَاللَّهُ جَعَلَ لَكُم مِّن بُيُوتِكُمْ سَكَنًا وَجَعَلَ لَكُم مِّن جُلُودِ الْأَنْعَامِ بُيُوتًا تَسْتَخِفُّونَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ إِقَامَتِكُمْ ۙ وَمِنْ أَصْوَافِهَا وَأَوْبَارِهَا وَأَشْعَارِهَا أَثَاثًا وَمَتَاعًا إِلَىٰ حِينٍ','و خدا برای شما از خانه‌هایتان محل سکونت (و آرامش) قرار داد؛ و از پوست چهارپایان نیز برای شما خانه‌هایی قرار داد که روز کوچ کردن و روز اقامتتان، به آسانی می‌توانید آنها را جا به جا کنید؛ و از پشم و کرک و موی آنها، برای شما اثاث و متاع (و وسایل مختلف زندگی) تا زمان معیّنی قرار داد.'];
ayat[1982]=[16,81,'وَاللَّهُ جَعَلَ لَكُم مِّمَّا خَلَقَ ظِلَالًا وَجَعَلَ لَكُم مِّنَ الْجِبَالِ أَكْنَانًا وَجَعَلَ لَكُمْ سَرَابِيلَ تَقِيكُمُ الْحَرَّ وَسَرَابِيلَ تَقِيكُم بَأْسَكُمْ ۚ كَذَٰلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ','و (نیز) خداوند از آنچه آفریده است سایه‌هایی برای شما قرار داده؛ و از کوه‌ها پناهگاه‌هایی؛ و برای شما پیراهنهایی آفریده، که شما را از گرما (و سرما) حفظ می‌کند؛ و پیراهنهایی که به هنگام جنگ، حافظ شماست؛ این گونه نعمتهایش را بر شما کامل می‌کند، شاید تسلیم فرمان او شوید!'];
ayat[1983]=[16,82,'فَإِن تَوَلَّوْا فَإِنَّمَا عَلَيْكَ الْبَلَاغُ الْمُبِينُ','(با این همه،) اگر روی برتابند، (نگران مباش؛) تو فقط وظیفه ابلاغ آشکار داری.'];
ayat[1984]=[16,83,'يَعْرِفُونَ نِعْمَتَ اللَّهِ ثُمَّ يُنكِرُونَهَا وَأَكْثَرُهُمُ الْكَافِرُونَ','آنها نعمت خدا را می‌شناسند؛ سپس آن را انکار می‌کنند؛ و اکثرشان کافرند!'];
ayat[1985]=[16,84,'وَيَوْمَ نَبْعَثُ مِن كُلِّ أُمَّةٍ شَهِيدًا ثُمَّ لَا يُؤْذَنُ لِلَّذِينَ كَفَرُوا وَلَا هُمْ يُسْتَعْتَبُونَ','(به خاطر بیاورید) روزی را که از هر امّتی گواهی بر آنان برمی‌انگیزیم؛ سپس به آنان که کفر ورزیدند، اجازه (سخن گفتن) داده نمی‌شود؛ (بلکه دست و پا و گوش و چشم، حتّی پوست تن آنها گواهی می‌دهند!) و (نیز) اجازه عذرخواهی و تقاضای عفو به آنان نمی‌دهند!'];
ayat[1986]=[16,85,'وَإِذَا رَأَى الَّذِينَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنظَرُونَ','و هنگامی که ظالمان عذاب را ببینند، نه به آنها تخفیف داده می‌شود، و نه مهلت!'];
ayat[1987]=[16,86,'وَإِذَا رَأَى الَّذِينَ أَشْرَكُوا شُرَكَاءَهُمْ قَالُوا رَبَّنَا هَٰؤُلَاءِ شُرَكَاؤُنَا الَّذِينَ كُنَّا نَدْعُو مِن دُونِكَ ۖ فَأَلْقَوْا إِلَيْهِمُ الْقَوْلَ إِنَّكُمْ لَكَاذِبُونَ','و هنگامی که مشرکان معبودهایی را که همتای خدا قرار دادند می‌بینند، می‌گویند: «پروردگارا! اینها همتایانی هستند که ما به جای تو، آنها را می‌خواندیم! «در این هنگام، معبودان به آنها می‌گویند: «شما دروغگو هستید! (شما هوای نفس خود را پرستش می‌کردید!)»'];
ayat[1988]=[16,87,'وَأَلْقَوْا إِلَى اللَّهِ يَوْمَئِذٍ السَّلَمَ ۖ وَضَلَّ عَنْهُم مَّا كَانُوا يَفْتَرُونَ','و در آن روز، همگی (ناگزیر) در پیشگاه خدا تسلیم می‌شوند؛ و تمام آنچه را (نسبت به خدا) دروغ می‌بستند، گم و نابود می‌شود!'];
ayat[1989]=[16,88,'الَّذِينَ كَفَرُوا وَصَدُّوا عَن سَبِيلِ اللَّهِ زِدْنَاهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوا يُفْسِدُونَ','کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند، بخاطر فسادی که می‌کردند، عذابی بر عذابشان می‌افزاییم!'];
ayat[1990]=[16,89,'وَيَوْمَ نَبْعَثُ فِي كُلِّ أُمَّةٍ شَهِيدًا عَلَيْهِم مِّنْ أَنفُسِهِمْ ۖ وَجِئْنَا بِكَ شَهِيدًا عَلَىٰ هَٰؤُلَاءِ ۚ وَنَزَّلْنَا عَلَيْكَ الْكِتَابَ تِبْيَانًا لِّكُلِّ شَيْءٍ وَهُدًى وَرَحْمَةً وَبُشْرَىٰ لِلْمُسْلِمِينَ','(به یاد آورید) روزی را که از هر امتی، گواهی از خودشان بر آنها برمی‌انگیزیم؛ و تو را گواه بر آنان قرارمی‌دهیم! و ما این کتاب را بر تو نازل کردیم که بیانگر همه چیز، و مایه هدایت و رحمت و بشارت برای مسلمانان است!'];
ayat[1991]=[16,90,'۞ إِنَّ اللَّهَ يَأْمُرُ بِالْعَدْلِ وَالْإِحْسَانِ وَإِيتَاءِ ذِي الْقُرْبَىٰ وَيَنْهَىٰ عَنِ الْفَحْشَاءِ وَالْمُنكَرِ وَالْبَغْيِ ۚ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ','خداوند به عدل و احسان و بخشش به نزدیکان فرمان می‌دهد؛ و از فحشا و منکر و ستم، نهی می‌کند؛ خداوند به شما اندرز می‌دهد، شاید متذکّر شوید!'];
ayat[1992]=[16,91,'وَأَوْفُوا بِعَهْدِ اللَّهِ إِذَا عَاهَدتُّمْ وَلَا تَنقُضُوا الْأَيْمَانَ بَعْدَ تَوْكِيدِهَا وَقَدْ جَعَلْتُمُ اللَّهَ عَلَيْكُمْ كَفِيلًا ۚ إِنَّ اللَّهَ يَعْلَمُ مَا تَفْعَلُونَ','و هنگامی که با خدا عهد بستید، به عهد او وفا کنید! و سوگندها را بعد از محکم ساختن نشکنید، در حالی که خدا را کفیل و ضامن بر (سوگند) خود قرار داده‌اید، به یقین خداوند از آنچه انجام می‌دهید، آگاه است!'];
ayat[1993]=[16,92,'وَلَا تَكُونُوا كَالَّتِي نَقَضَتْ غَزْلَهَا مِن بَعْدِ قُوَّةٍ أَنكَاثًا تَتَّخِذُونَ أَيْمَانَكُمْ دَخَلًا بَيْنَكُمْ أَن تَكُونَ أُمَّةٌ هِيَ أَرْبَىٰ مِنْ أُمَّةٍ ۚ إِنَّمَا يَبْلُوكُمُ اللَّهُ بِهِ ۚ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيَامَةِ مَا كُنتُمْ فِيهِ تَخْتَلِفُونَ','همانند آن زن (سبک مغز) نباشید که پشمهای تابیده خود را، پس از استحکام، وامی‌تابید! در حالی که (سوگند و پیمان) خود را وسیله خیانت و فساد قرار می‌دهید؛ بخاطر اینکه گروهی، جمعیّتشان از گروه دیگر بیشتر است (و کثرت دشمن را بهانه‌ای برای شکستن بیعت با پیامبر می‌شمرید)! خدا فقط شما را با این وسیله آزمایش می‌کند؛ و به یقین روز قیامت، آنچه را در آن اختلاف داشتید، برای شما روشن می‌سازد!'];
ayat[1994]=[16,93,'وَلَوْ شَاءَ اللَّهُ لَجَعَلَكُمْ أُمَّةً وَاحِدَةً وَلَٰكِن يُضِلُّ مَن يَشَاءُ وَيَهْدِي مَن يَشَاءُ ۚ وَلَتُسْأَلُنَّ عَمَّا كُنتُمْ تَعْمَلُونَ','و اگر خدا می‌خواست، همه شما را امت واحدی قرارمی‌داد؛ (و همه را به اجبار وادار به ایمان می‌کرد؛ امّا ایمان اجباری فایده‌ای ندارد!) ولی خدا هر کس را بخواهد (و شایسته بداند) گمراه، و هر کس را بخواهد (و لایق بداند) هدایت می‌کند! (به گروهی توفیق هدایت داده، و از گروهی سلب می‌کند!) و یقیناً شما از آنچه انجام می‌دادید، بازپرسی خواهید شد!'];
ayat[1995]=[16,94,'وَلَا تَتَّخِذُوا أَيْمَانَكُمْ دَخَلًا بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِهَا وَتَذُوقُوا السُّوءَ بِمَا صَدَدتُّمْ عَن سَبِيلِ اللَّهِ ۖ وَلَكُمْ عَذَابٌ عَظِيمٌ','سوگندهایتان را وسیله تقلّب و خیانت در میان خود قرار ندهید، مبادا گامی بعد از ثابت‌گشتن (بر ایمان) متزلزل شود؛ و به خاطر بازداشتن (مردم) از راه خدا، آثار سوء آن را بچشید! و برای شما، عذاب عظیمی خواهد بود!'];
ayat[1996]=[16,95,'وَلَا تَشْتَرُوا بِعَهْدِ اللَّهِ ثَمَنًا قَلِيلًا ۚ إِنَّمَا عِندَ اللَّهِ هُوَ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ','و (هرگز) پیمان الهی را با بهای کمی مبادله نکنید (و هر بهایی در برابر آن ناچیز است!) آنچه نزد خداست، برای شما بهتر است اگر می‌دانستید.'];
ayat[1997]=[16,96,'مَا عِندَكُمْ يَنفَدُ ۖ وَمَا عِندَ اللَّهِ بَاقٍ ۗ وَلَنَجْزِيَنَّ الَّذِينَ صَبَرُوا أَجْرَهُم بِأَحْسَنِ مَا كَانُوا يَعْمَلُونَ','آنچه نزد شماست فانی می‌شود؛ امّا آنچه نزد خداست باقی است؛ و به کسانی که صبر و استقامت پیشه کنند، مطابق بهترین اعمالی که انجام می‌دادند پاداش خواهیم داد.'];
ayat[1998]=[16,97,'مَنْ عَمِلَ صَالِحًا مِّن ذَكَرٍ أَوْ أُنثَىٰ وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيَاةً طَيِّبَةً ۖ وَلَنَجْزِيَنَّهُمْ أَجْرَهُم بِأَحْسَنِ مَا كَانُوا يَعْمَلُونَ','هر کس کار شایسته‌ای انجام دهد، خواه مرد باشد یا زن، در حالی که مؤمن است، او را به حیاتی پاک زنده می‌داریم؛ و پاداش آنها را به بهترین اعمالی که انجام می‌دادند، خواهیم داد.'];
ayat[1999]=[16,98,'فَإِذَا قَرَأْتَ الْقُرْآنَ فَاسْتَعِذْ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ','هنگامی که قرآن می‌خوانی، از شرّ شیطان مطرود، به خدا پناه بر!'];
ayat[2000]=[16,99,'إِنَّهُ لَيْسَ لَهُ سُلْطَانٌ عَلَى الَّذِينَ آمَنُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ','چرا که او، بر کسانی که ایمان دارند و بر پروردگارشان توکّل می‌کنند، تسلّطی ندارد.'];
ayat[2001]=[16,100,'إِنَّمَا سُلْطَانُهُ عَلَى الَّذِينَ يَتَوَلَّوْنَهُ وَالَّذِينَ هُم بِهِ مُشْرِكُونَ','تسلّط او تنها بر کسانی است که او را به سرپرستی خود برگزیده‌اند، و آنها که نسبت به او [= خدا] شرک می‌ورزند (و فرمان شیطان را به جای فرمان خدا، گردن می‌نهند)'];
ayat[2002]=[16,101,'وَإِذَا بَدَّلْنَا آيَةً مَّكَانَ آيَةٍ ۙ وَاللَّهُ أَعْلَمُ بِمَا يُنَزِّلُ قَالُوا إِنَّمَا أَنتَ مُفْتَرٍ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ','و هنگامی که آیه‌ای را به آیه دیگر مبدّل کنیم [= حکمی را نسخ نماییم‌] -و خدا بهتر می‌داند چه حکمی را نازل کند- آنها می‌گویند: «تو افترا می‌بندی!» امّا بیشترشان (حقیقت را) نمی‌دانند!'];
ayat[2003]=[16,102,'قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِن رَّبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِينَ آمَنُوا وَهُدًى وَبُشْرَىٰ لِلْمُسْلِمِينَ','بگو، روح القدس آن را از جانب پروردگارت بحقّ نازل کرده، تا افراد باایمان را ثابت‌قدم گرداند؛ و هدایت و بشارتی است برای عموم مسلمانان!'];
ayat[2004]=[16,103,'وَلَقَدْ نَعْلَمُ أَنَّهُمْ يَقُولُونَ إِنَّمَا يُعَلِّمُهُ بَشَرٌ ۗ لِّسَانُ الَّذِي يُلْحِدُونَ إِلَيْهِ أَعْجَمِيٌّ وَهَٰذَا لِسَانٌ عَرَبِيٌّ مُّبِينٌ','ما می‌دانیم که آنها می‌گویند: «این آیات را انسانی به او تعلیم می‌دهد!» در حالی که زبان کسی که اینها را به او نسبت می‌دهند عجمی است؛ ولی این (قرآن)، زبان عربی آشکار است!'];
ayat[2005]=[16,104,'إِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِآيَاتِ اللَّهِ لَا يَهْدِيهِمُ اللَّهُ وَلَهُمْ عَذَابٌ أَلِيمٌ','به یقین، کسانی که به آیات الهی ایمان نمی‌آورند، خدا آنها را هدایت نمی‌کند؛ و برای آنان عذاب دردناکی است.'];
ayat[2006]=[16,105,'إِنَّمَا يَفْتَرِي الْكَذِبَ الَّذِينَ لَا يُؤْمِنُونَ بِآيَاتِ اللَّهِ ۖ وَأُولَٰئِكَ هُمُ الْكَاذِبُونَ','تنها کسانی دروغ می‌بندند که به آیات خدا ایمان ندارند؛ (آری،) دروغگویان واقعی آنها هستند!'];
ayat[2007]=[16,106,'مَن كَفَرَ بِاللَّهِ مِن بَعْدِ إِيمَانِهِ إِلَّا مَنْ أُكْرِهَ وَقَلْبُهُ مُطْمَئِنٌّ بِالْإِيمَانِ وَلَٰكِن مَّن شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِّنَ اللَّهِ وَلَهُمْ عَذَابٌ عَظِيمٌ','کسانی که بعد از ایمان کافر شوند -بجز آنها که تحت فشار واقع شده‌اند در حالی که قلبشان آرام و با ایمان است- آری، آنها که سینه خود را برای پذیرش کفر گشوده‌اند، غضب خدا بر آنهاست؛ و عذاب عظیمی در انتظارشان!'];
ayat[2008]=[16,107,'ذَٰلِكَ بِأَنَّهُمُ اسْتَحَبُّوا الْحَيَاةَ الدُّنْيَا عَلَى الْآخِرَةِ وَأَنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْكَافِرِينَ','این به خاطر آن است که زندگی دنیا (و پست را) بر آخرت ترجیح دادند؛ و خداوند افراد بی‌ایمان (لجوج) را هدایت نمی‌کند.'];
ayat[2009]=[16,108,'أُولَٰئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَسَمْعِهِمْ وَأَبْصَارِهِمْ ۖ وَأُولَٰئِكَ هُمُ الْغَافِلُونَ','آنها کسانی هستند که (بر اثر فزونی گناه،) خدا بر قلب و گوش و چشمانشان مهر نهاده؛ (به همین دلیل نمی‌فهمند،) و غافلان واقعی همانها هستند!'];
ayat[2010]=[16,109,'لَا جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْخَاسِرُونَ','و ناچار آنها در آخرت زیانکارند.'];
ayat[2011]=[16,110,'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُوا مِن بَعْدِ مَا فُتِنُوا ثُمَّ جَاهَدُوا وَصَبَرُوا إِنَّ رَبَّكَ مِن بَعْدِهَا لَغَفُورٌ رَّحِيمٌ','امّا پروردگار تو نسبت به کسانی که بعد از فریب‌خوردن، (به ایمان بازگشتند و) هجرت کردند؛ سپس جهاد کردند و در راه خدا استقامت نمودند؛ پروردگارت، بعد از انجام این کارها، بخشنده و مهربان است (و آنها را مشمول رحمت خود می‌سازد).'];
ayat[2012]=[16,111,'۞ يَوْمَ تَأْتِي كُلُّ نَفْسٍ تُجَادِلُ عَن نَّفْسِهَا وَتُوَفَّىٰ كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُمْ لَا يُظْلَمُونَ','(به یاد آورید) روزی را که هر کس (در فکر خویشتن است؛ و تنها) به دفاع از خود برمی‌خیزد؛ و نتیجه اعمال هر کسی، بی‌کم و کاست، به او داده می‌شود؛ و به آنها ظلم نخواهد شد!'];
ayat[2013]=[16,112,'وَضَرَبَ اللَّهُ مَثَلًا قَرْيَةً كَانَتْ آمِنَةً مُّطْمَئِنَّةً يَأْتِيهَا رِزْقُهَا رَغَدًا مِّن كُلِّ مَكَانٍ فَكَفَرَتْ بِأَنْعُمِ اللَّهِ فَأَذَاقَهَا اللَّهُ لِبَاسَ الْجُوعِ وَالْخَوْفِ بِمَا كَانُوا يَصْنَعُونَ','خداوند (برای آنان که کفران نعمت می‌کنند،) مثلی زده است: منطقه آبادی که امن و آرام و مطمئن بود؛ و همواره روزیش از هر جا می‌رسید؛ امّا به نعمتهای خدا ناسپاسی کردند؛ و خداوند به خاطر اعمالی که انجام می‌دادند، لباس گرسنگی و ترس را بر اندامشان پوشانید!'];
ayat[2014]=[16,113,'وَلَقَدْ جَاءَهُمْ رَسُولٌ مِّنْهُمْ فَكَذَّبُوهُ فَأَخَذَهُمُ الْعَذَابُ وَهُمْ ظَالِمُونَ','پیامبری از خودشان به سراغ آنها آمد، او را تکذیب کردند؛ از این رو عذاب الهی آنها را فراگرفت در حالی که ظالم بودند!'];
ayat[2015]=[16,114,'فَكُلُوا مِمَّا رَزَقَكُمُ اللَّهُ حَلَالًا طَيِّبًا وَاشْكُرُوا نِعْمَتَ اللَّهِ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ','پس، از آنچه خدا روزیتان کرده است، حلال و پاکیزه بخورید؛ و شکر نعمت خدا را بجا آورید اگر او را می‌پرستید!'];
ayat[2016]=[16,115,'إِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنزِيرِ وَمَا أُهِلَّ لِغَيْرِ اللَّهِ بِهِ ۖ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَلَا عَادٍ فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','خداوند، تنها مردار، خون، گوشت خوک و آنچه را با نام غیر خدا سر بریده‌اند، بر شما حرام کرده است؛ امّا کسانی که ناچار شوند، در حالی که تجاوز و تعدّی از حدّ ننمایند، (خدا آنها را می‌بخشد؛ چرا که) خدا بخشنده و مهربان است.'];
ayat[2017]=[16,116,'وَلَا تَقُولُوا لِمَا تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ هَٰذَا حَلَالٌ وَهَٰذَا حَرَامٌ لِّتَفْتَرُوا عَلَى اللَّهِ الْكَذِبَ ۚ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللَّهِ الْكَذِبَ لَا يُفْلِحُونَ','به خاطر دروغی که بر زبانتان جاری می‌شود (و چیزی را مجاز و چیزی را ممنوع می‌کنید،) نگویید: «این حلال است و آن حرام»، تا بر خدا افترا ببندید به یقین کسانی که به خدا دروغ می‌بندند، رستگار نخواهند شد!'];
ayat[2018]=[16,117,'مَتَاعٌ قَلِيلٌ وَلَهُمْ عَذَابٌ أَلِيمٌ','بهره کمی است (که در این دنیا نصیبشان می‌شود)؛ و عذاب دردناکی در انتظار آنان است!'];
ayat[2019]=[16,118,'وَعَلَى الَّذِينَ هَادُوا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِن قَبْلُ ۖ وَمَا ظَلَمْنَاهُمْ وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','چیزهایی را که پیش از این برای تو شرح دادیم، بر یهود حرام کردیم؛ ما به آنها ستم نکردیم، اما آنها به خودشان ظلم و ستم می‌کردند!'];
ayat[2020]=[16,119,'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهَالَةٍ ثُمَّ تَابُوا مِن بَعْدِ ذَٰلِكَ وَأَصْلَحُوا إِنَّ رَبَّكَ مِن بَعْدِهَا لَغَفُورٌ رَّحِيمٌ','امّا پروردگارت نسبت به آنها که از روی جهالت، بدی کرده‌اند، سپس توبه کرده و در مقام جبران برآمده‌اند، پروردگارت بعد از آن آمرزنده و مهربان است.'];
ayat[2021]=[16,120,'إِنَّ إِبْرَاهِيمَ كَانَ أُمَّةً قَانِتًا لِّلَّهِ حَنِيفًا وَلَمْ يَكُ مِنَ الْمُشْرِكِينَ','ابراهیم (به تنهایی) امّتی بود مطیع فرمان خدا؛ خالی از هر گونه انحراف؛ و از مشرکان نبود؛'];
ayat[2022]=[16,121,'شَاكِرًا لِّأَنْعُمِهِ ۚ اجْتَبَاهُ وَهَدَاهُ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','شکرگزار نعمتهای پروردگار بود؛ خدا او را برگزید؛ و به راهی راست هدایت نمود!'];
ayat[2023]=[16,122,'وَآتَيْنَاهُ فِي الدُّنْيَا حَسَنَةً ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ','ما در دنیا به او (همّت) نیکویی دادیم؛ و در آخرت از نیکان است!'];
ayat[2024]=[16,123,'ثُمَّ أَوْحَيْنَا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْرَاهِيمَ حَنِيفًا ۖ وَمَا كَانَ مِنَ الْمُشْرِكِينَ','سپس به تو وحی فرستادیم که از آیین ابراهیم -که ایمانی خالص داشت و از مشرکان نبود- پیروی کن!'];
ayat[2025]=[16,124,'إِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِينَ اخْتَلَفُوا فِيهِ ۚ وَإِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ','(تحریمهای) روز شنبه (برای یهود) فقط بعنوان یک مجازات بود، که در آن هم اختلاف کردند؛ و پروردگارت روز قیامت، در آنچه اختلاف داشتند، میان آنها داوری می‌کند!'];
ayat[2026]=[16,125,'ادْعُ إِلَىٰ سَبِيلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ ۖ وَجَادِلْهُم بِالَّتِي هِيَ أَحْسَنُ ۚ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِ ۖ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ','با حکمت و اندرز نیکو، به راه پروردگارت دعوت نما! و با آنها به روشی که نیکوتر است، استدلال و مناظره کن! پروردگارت، از هر کسی بهتر می‌داند چه کسی از راه او گمراه شده است؛ و او به هدایت‌یافتگان داناتر است.'];
ayat[2027]=[16,126,'وَإِنْ عَاقَبْتُمْ فَعَاقِبُوا بِمِثْلِ مَا عُوقِبْتُم بِهِ ۖ وَلَئِن صَبَرْتُمْ لَهُوَ خَيْرٌ لِّلصَّابِرِينَ','و هر گاه خواستید مجازات کنید، تنها بمقداری که به شما تعدّی شده کیفر دهید! و اگر شکیبایی کنید، این کار برای شکیبایان بهتر است.'];
ayat[2028]=[16,127,'وَاصْبِرْ وَمَا صَبْرُكَ إِلَّا بِاللَّهِ ۚ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ فِي ضَيْقٍ مِّمَّا يَمْكُرُونَ','صبر کن، و صبر تو فقط برای خدا و به توفیق خدا باشد! و بخاطر (کارهای) آنها، اندوهگین و دلسرد مشو! و از توطئه‌های آنها، در تنگنا قرار مگیر!'];
ayat[2029]=[16,128,'إِنَّ اللَّهَ مَعَ الَّذِينَ اتَّقَوا وَّالَّذِينَ هُم مُّحْسِنُونَ','خداوند با کسانی است که تقوا پیشه کرده‌اند، و کسانی که نیکوکارند.'];
ayat[2030]=[17,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سُبْحَانَ الَّذِي أَسْرَىٰ بِعَبْدِهِ لَيْلًا مِّنَ الْمَسْجِدِ الْحَرَامِ إِلَى الْمَسْجِدِ الْأَقْصَى الَّذِي بَارَكْنَا حَوْلَهُ لِنُرِيَهُ مِنْ آيَاتِنَا ۚ إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ','پاک و منزّه است خدایی که بنده‌اش را در یک شب، از مسجد الحرام به مسجد الاقصی -که گرداگردش را پربرکت ساخته‌ایم- برد، تا برخی از آیات خود را به او نشان دهیم؛ چرا که او شنوا و بیناست.'];
ayat[2031]=[17,2,'وَآتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَاهُ هُدًى لِّبَنِي إِسْرَائِيلَ أَلَّا تَتَّخِذُوا مِن دُونِي وَكِيلًا','ما به موسی کتاب آسمانی دادیم؛ و آن را وسیله هدایت بنی اسرائیل ساختیم؛ (و گفتیم:) غیر ما را تکیه‌گاه خود قرار ندهید!'];
ayat[2032]=[17,3,'ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوحٍ ۚ إِنَّهُ كَانَ عَبْدًا شَكُورًا','ای فرزندان کسانی که با نوح (بر کشتی) سوار کردیم! او بنده شکرگزاری بود. (شما هم مانند او باشید، تا نجات یابید!)'];
ayat[2033]=[17,4,'وَقَضَيْنَا إِلَىٰ بَنِي إِسْرَائِيلَ فِي الْكِتَابِ لَتُفْسِدُنَّ فِي الْأَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيرًا','ما به بنی اسرائیل در کتاب (تورات) اعلام کردیم که دوبار در زمین فساد خواهید کرد، و برتری‌جویی بزرگی خواهید نمود.'];
ayat[2034]=[17,5,'فَإِذَا جَاءَ وَعْدُ أُولَاهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَّنَا أُولِي بَأْسٍ شَدِيدٍ فَجَاسُوا خِلَالَ الدِّيَارِ ۚ وَكَانَ وَعْدًا مَّفْعُولًا','هنگامی که نخستین وعده فرا رسد، گروهی از بندگان پیکارجوی خود را بر ضدّ شما میانگیزیم (تا شما را سخت در هم کوبند؛ حتی برای به دست آوردن مجرمان)، خانه‌ها را جستجو می‌کنند؛ و این وعده‌ای است قطعی!'];
ayat[2035]=[17,6,'ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَأَمْدَدْنَاكُم بِأَمْوَالٍ وَبَنِينَ وَجَعَلْنَاكُمْ أَكْثَرَ نَفِيرًا','سپس شما را بر آنها چیره می‌کنیم؛ و شما را به وسیله داراییها و فرزندانی کمک خواهیم کرد؛ و نفرات شما را بیشتر (از دشمن) قرارمی‌دهیم.'];
ayat[2036]=[17,7,'إِنْ أَحْسَنتُمْ أَحْسَنتُمْ لِأَنفُسِكُمْ ۖ وَإِنْ أَسَأْتُمْ فَلَهَا ۚ فَإِذَا جَاءَ وَعْدُ الْآخِرَةِ لِيَسُوءُوا وُجُوهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٍ وَلِيُتَبِّرُوا مَا عَلَوْا تَتْبِيرًا','اگر نیکی کنید، به خودتان نیکی می‌کنید؛ و اگر بدی کنید باز هم به خود می‌کنید. و هنگامی که وعده دوم فرا رسد، (آنچنان دشمن بر شما سخت خواهد گرفت که) آثار غم و اندوه در صورتهایتان ظاهر می‌شود؛ و داخل مسجد (الاقصی) می‌شوند همان گونه که بار اول وارد شدند؛ و آنچه را زیر سلطه خود می‌گیرند، در هم می‌کوبند.'];
ayat[2037]=[17,8,'عَسَىٰ رَبُّكُمْ أَن يَرْحَمَكُمْ ۚ وَإِنْ عُدتُّمْ عُدْنَا ۘ وَجَعَلْنَا جَهَنَّمَ لِلْكَافِرِينَ حَصِيرًا','امید است پروردگارتان به شما رحم کند! هرگاه برگردید، ما هم بازمی‌گردیم؛ و جهنّم را برای کافران، زندان سختی قرار دادیم.'];
ayat[2038]=[17,9,'إِنَّ هَٰذَا الْقُرْآنَ يَهْدِي لِلَّتِي هِيَ أَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحَاتِ أَنَّ لَهُمْ أَجْرًا كَبِيرًا','این قرآن، به راهی که استوارترین راه‌هاست، هدایت می‌کند؛ و به مؤمنانی که اعمال صالح انجام می‌دهند، بشارت می‌دهد که برای آنها پاداش بزرگی است.'];
ayat[2039]=[17,10,'وَأَنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ أَعْتَدْنَا لَهُمْ عَذَابًا أَلِيمًا','و اینکه آنها که به قیامت ایمان نمی‌آورند، عذاب دردناکی برای آنان آماده ساخته‌ایم.'];
ayat[2040]=[17,11,'وَيَدْعُ الْإِنسَانُ بِالشَّرِّ دُعَاءَهُ بِالْخَيْرِ ۖ وَكَانَ الْإِنسَانُ عَجُولًا','انسان (بر اثر شتابزدگی)، بدیها را طلب می‌کند آن گونه که نیکیها را می‌طلبد؛ و انسان، همیشه عجول بوده است!'];
ayat[2041]=[17,12,'وَجَعَلْنَا اللَّيْلَ وَالنَّهَارَ آيَتَيْنِ ۖ فَمَحَوْنَا آيَةَ اللَّيْلِ وَجَعَلْنَا آيَةَ النَّهَارِ مُبْصِرَةً لِّتَبْتَغُوا فَضْلًا مِّن رَّبِّكُمْ وَلِتَعْلَمُوا عَدَدَ السِّنِينَ وَالْحِسَابَ ۚ وَكُلَّ شَيْءٍ فَصَّلْنَاهُ تَفْصِيلًا','ما شب و روز را دو نشانه توحید و عظمت خود قرار دادیم؛ سپس نشانه شب را محو کرده، و نشانه روز را روشنی‌بخش ساختیم تا (در پرتو آن،) فضل پروردگارتان را بطلبید (و به تلاش زندگی برخیزید)، و عدد سالها و حساب را بدانید؛ و هر چیزی را بطور مشخّص و آشکار، بیان کردیم.'];
ayat[2042]=[17,13,'وَكُلَّ إِنسَانٍ أَلْزَمْنَاهُ طَائِرَهُ فِي عُنُقِهِ ۖ وَنُخْرِجُ لَهُ يَوْمَ الْقِيَامَةِ كِتَابًا يَلْقَاهُ مَنشُورًا','و هر انسانی، اعمالش را بر گردنش آویخته‌ایم؛ و روز قیامت، کتابی برای او بیرون می‌آوریم که آن را در برابر خود، گشوده می‌بیند! (این همان نامه اعمال اوست!)'];
ayat[2043]=[17,14,'اقْرَأْ كِتَابَكَ كَفَىٰ بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيبًا','(و به او می‌گوییم:) کتابت را بخوان، کافی است که امروز، خود حسابگر خویش باشی!'];
ayat[2044]=[17,15,'مَّنِ اهْتَدَىٰ فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ ۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۚ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبْعَثَ رَسُولًا','هر کس هدایت شود، برای خود هدایت یافته؛ و آن کس که گمراه گردد، به زیان خود گمراه شده است؛ و هیچ کس بار گناه دیگری را به دوش نمی‌کشد؛ و ما هرگز (قومی را) مجازات نخواهیم کرد، مگر آنکه پیامبری مبعوث کرده باشیم (تا وظایفشان را بیان کند.)'];
ayat[2045]=[17,16,'وَإِذَا أَرَدْنَا أَن نُّهْلِكَ قَرْيَةً أَمَرْنَا مُتْرَفِيهَا فَفَسَقُوا فِيهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنَاهَا تَدْمِيرًا','و هنگامی که بخواهیم شهر و دیاری را هلاک کنیم، نخست اوامر خود را برای «مترفین» (و ثروتمندان مست شهوت) آنجا، بیان می‌داریم، سپس هنگامی که به مخالفت برخاستند و استحقاق مجازات یافتند، آنها را به شدّت درهم می‌کوبیم.'];
ayat[2046]=[17,17,'وَكَمْ أَهْلَكْنَا مِنَ الْقُرُونِ مِن بَعْدِ نُوحٍ ۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِ خَبِيرًا بَصِيرًا','چه بسیار مردمی که در قرون بعد از نوح، زندگی می‌کردند؛ (و طبق همین سنت،) آنها را هلاک کردیم! و کافی است که پروردگارت از گناهان بندگانش آگاه، و نسبت به آن بیناست.'];
ayat[2047]=[17,18,'مَّن كَانَ يُرِيدُ الْعَاجِلَةَ عَجَّلْنَا لَهُ فِيهَا مَا نَشَاءُ لِمَن نُّرِيدُ ثُمَّ جَعَلْنَا لَهُ جَهَنَّمَ يَصْلَاهَا مَذْمُومًا مَّدْحُورًا','آن کس که (تنها) زندگی زودگذر (دنیا) را می‌طلبد، آن مقدار از آن را که بخواهیم -و به هر کس اراده کنیم- می‌دهیم؛ سپس دوزخ را برای او قرار خواهیم داد، که در آتش سوزانش می‌سوزد در حالی که نکوهیده و رانده (درگاه خدا) است.'];
ayat[2048]=[17,19,'وَمَنْ أَرَادَ الْآخِرَةَ وَسَعَىٰ لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَأُولَٰئِكَ كَانَ سَعْيُهُم مَّشْكُورًا','و آن کس که سرای آخرت را بطلبد، و برای آن سعی و کوشش کند -در حالی که ایمان داشته باشد- سعی و تلاش او، (از سوی خدا) پاداش داده خواهد شد.'];
ayat[2049]=[17,20,'كُلًّا نُّمِدُّ هَٰؤُلَاءِ وَهَٰؤُلَاءِ مِنْ عَطَاءِ رَبِّكَ ۚ وَمَا كَانَ عَطَاءُ رَبِّكَ مَحْظُورًا','هر یک از این دو گروه را از عطای پروردگارت، بهره و کمک می‌دهیم؛ و عطای پروردگارت هرگز (از کسی) منع نشده است.'];
ayat[2050]=[17,21,'انظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلَىٰ بَعْضٍ ۚ وَلَلْآخِرَةُ أَكْبَرُ دَرَجَاتٍ وَأَكْبَرُ تَفْضِيلًا','ببین چگونه بعضی را (در دنیا بخاطر تلاششان) بر بعضی دیگر برتری بخشیده‌ایم؛ درجات آخرت و برتریهایش، از این هم بیشتر است!'];
ayat[2051]=[17,22,'لَّا تَجْعَلْ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَتَقْعُدَ مَذْمُومًا مَّخْذُولًا','هرگز معبود دیگری را با خدا قرار مده، که نکوهیده و بی‌یار و یاور خواهی نشست!'];
ayat[2052]=[17,23,'۞ وَقَضَىٰ رَبُّكَ أَلَّا تَعْبُدُوا إِلَّا إِيَّاهُ وَبِالْوَالِدَيْنِ إِحْسَانًا ۚ إِمَّا يَبْلُغَنَّ عِندَكَ الْكِبَرَ أَحَدُهُمَا أَوْ كِلَاهُمَا فَلَا تَقُل لَّهُمَا أُفٍّ وَلَا تَنْهَرْهُمَا وَقُل لَّهُمَا قَوْلًا كَرِيمًا','و پروردگارت فرمان داده: جز او را نپرستید! و به پدر و مادر نیکی کنید! هرگاه یکی از آن دو، یا هر دوی آنها، نزد تو به سن پیری رسند، کمترین اهانتی به آنها روا مدار! و بر آنها فریاد مزن! و گفتار لطیف و سنجیده و بزرگوارانه به آنها بگو!'];
ayat[2053]=[17,24,'وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُل رَّبِّ ارْحَمْهُمَا كَمَا رَبَّيَانِي صَغِيرًا','و بالهای تواضع خویش را از محبّت و لطف، در برابر آنان فرود آر! و بگو: «پروردگارا! همان‌گونه که آنها مرا در کوچکی تربیت کردند، مشمول رحمتشان قرار ده!»'];
ayat[2054]=[17,25,'رَّبُّكُمْ أَعْلَمُ بِمَا فِي نُفُوسِكُمْ ۚ إِن تَكُونُوا صَالِحِينَ فَإِنَّهُ كَانَ لِلْأَوَّابِينَ غَفُورًا','پروردگار شما از درون دلهایتان آگاهتر است؛ (اگر لغزشی در این زمینه داشتید) هر گاه صالح باشید (و جبران کنید) او بازگشت‌کنندگان را می‌بخشد.'];
ayat[2055]=[17,26,'وَآتِ ذَا الْقُرْبَىٰ حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ وَلَا تُبَذِّرْ تَبْذِيرًا','و حقّ نزدیکان را بپرداز، و (همچنین حق) مستمند و وامانده در راه را! و هرگز اسراف و تبذیر مکن،'];
ayat[2056]=[17,27,'إِنَّ الْمُبَذِّرِينَ كَانُوا إِخْوَانَ الشَّيَاطِينِ ۖ وَكَانَ الشَّيْطَانُ لِرَبِّهِ كَفُورًا','چرا که تبذیرکنندگان، برادران شیاطینند؛ و شیطان در برابر پروردگارش، بسیار ناسپاس بود!'];
ayat[2057]=[17,28,'وَإِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاءَ رَحْمَةٍ مِّن رَّبِّكَ تَرْجُوهَا فَقُل لَّهُمْ قَوْلًا مَّيْسُورًا','و هرگاه از آنان [= مستمندان‌] روی برتابی، و انتظار رحمت پروردگارت را داشته باشی (تا گشایشی در کارت پدید آید و به آنها کمک کنی)، با گفتار نرم و آمیخته با لطف با آنها سخن بگو!'];
ayat[2058]=[17,29,'وَلَا تَجْعَلْ يَدَكَ مَغْلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُومًا مَّحْسُورًا','هرگز دستت را بر گردنت زنجیر مکن، (و ترک انفاق و بخشش منما) و بیش از حدّ (نیز) دست خود را مگشای، تا مورد سرزنش قرار گیری و از کار فرومانی!'];
ayat[2059]=[17,30,'إِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ وَيَقْدِرُ ۚ إِنَّهُ كَانَ بِعِبَادِهِ خَبِيرًا بَصِيرًا','به یقین، پروردگارت روزی را برای هر کس بخواهد، گشاده یا تنگ می‌دارد؛ او نسبت به بندگانش، آگاه و بیناست.'];
ayat[2060]=[17,31,'وَلَا تَقْتُلُوا أَوْلَادَكُمْ خَشْيَةَ إِمْلَاقٍ ۖ نَّحْنُ نَرْزُقُهُمْ وَإِيَّاكُمْ ۚ إِنَّ قَتْلَهُمْ كَانَ خِطْئًا كَبِيرًا','و فرزندانتان را از ترس فقر، نکشید! ما آنها و شما را روزی می‌دهیم؛ مسلماً کشتن آنها گناه بزرگی است!'];
ayat[2061]=[17,32,'وَلَا تَقْرَبُوا الزِّنَا ۖ إِنَّهُ كَانَ فَاحِشَةً وَسَاءَ سَبِيلًا','و نزدیک زنا نشوید، که کار بسیار زشت، و بد راهی است!'];
ayat[2062]=[17,33,'وَلَا تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بِالْحَقِّ ۗ وَمَن قُتِلَ مَظْلُومًا فَقَدْ جَعَلْنَا لِوَلِيِّهِ سُلْطَانًا فَلَا يُسْرِف فِّي الْقَتْلِ ۖ إِنَّهُ كَانَ مَنصُورًا','و کسی را که خداوند خونش را حرام شمرده، نکشید، جز بحق! و آن کس که مظلوم کشته شده، برای ولیش سلطه (و حق قصاص) قرار دادیم؛ اما در قتل اسراف نکند، چرا که او مورد حمایت است!'];
ayat[2063]=[17,34,'وَلَا تَقْرَبُوا مَالَ الْيَتِيمِ إِلَّا بِالَّتِي هِيَ أَحْسَنُ حَتَّىٰ يَبْلُغَ أَشُدَّهُ ۚ وَأَوْفُوا بِالْعَهْدِ ۖ إِنَّ الْعَهْدَ كَانَ مَسْئُولًا','و به مال یتیم، جز به بهترین راه نزدیک نشوید، تا به سر حد بلوغ رسد! و به عهد (خود) وفا کنید، که از عهد سؤال می‌شود!'];
ayat[2064]=[17,35,'وَأَوْفُوا الْكَيْلَ إِذَا كِلْتُمْ وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ ۚ ذَٰلِكَ خَيْرٌ وَأَحْسَنُ تَأْوِيلًا','و هنگامی که پیمانه می‌کنید، حق پیمانه را ادا نمایید، و با ترازوی درست وزن کنید! این برای شما بهتر، و عاقبتش نیکوتر است.'];
ayat[2065]=[17,36,'وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ ۚ إِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ أُولَٰئِكَ كَانَ عَنْهُ مَسْئُولًا','از آنچه به آن آگاهی نداری، پیروی مکن، چرا که گوش و چشم و دل، همه مسؤولند.'];
ayat[2066]=[17,37,'وَلَا تَمْشِ فِي الْأَرْضِ مَرَحًا ۖ إِنَّكَ لَن تَخْرِقَ الْأَرْضَ وَلَن تَبْلُغَ الْجِبَالَ طُولًا','و روی زمین، با تکبر راه مرو! تو نمی‌توانی زمین را بشکافی، و طول قامتت هرگز به کوه‌ها نمی‌رسد!'];
ayat[2067]=[17,38,'كُلُّ ذَٰلِكَ كَانَ سَيِّئُهُ عِندَ رَبِّكَ مَكْرُوهًا','همه اینها گناهش نزد پروردگار تو ناپسند است.'];
ayat[2068]=[17,39,'ذَٰلِكَ مِمَّا أَوْحَىٰ إِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِ ۗ وَلَا تَجْعَلْ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَتُلْقَىٰ فِي جَهَنَّمَ مَلُومًا مَّدْحُورًا','این (احکام)، از حکمتهایی است که پروردگارت به تو وحی فرستاده؛ و هرگز معبودی با خدا قرار مده، که در جهنم افکنده می‌شوی، در حالی که سرزنش شده، و رانده (درگاه خدا) خواهی بود!'];
ayat[2069]=[17,40,'أَفَأَصْفَاكُمْ رَبُّكُم بِالْبَنِينَ وَاتَّخَذَ مِنَ الْمَلَائِكَةِ إِنَاثًا ۚ إِنَّكُمْ لَتَقُولُونَ قَوْلًا عَظِيمًا','آیا پروردگارتان فرزندان پسر را مخصوص شما ساخته، و خودش دخترانی از فرشتگان برگزیده است؟! شما سخن بزرگ (و بسیار زشتی) می‌گویید!'];
ayat[2070]=[17,41,'وَلَقَدْ صَرَّفْنَا فِي هَٰذَا الْقُرْآنِ لِيَذَّكَّرُوا وَمَا يَزِيدُهُمْ إِلَّا نُفُورًا','ما در این قرآن، انواع بیانات مؤثر را آوردیم تا متذکر شوند! ولی (گروهی از کوردلان،) جز بر نفرتشان نمی‌افزاید.'];
ayat[2071]=[17,42,'قُل لَّوْ كَانَ مَعَهُ آلِهَةٌ كَمَا يَقُولُونَ إِذًا لَّابْتَغَوْا إِلَىٰ ذِي الْعَرْشِ سَبِيلًا','بگو: «اگر آنچنان که آنها می‌گویند با او خدایانی بود، در این صورت، (خدایان) سعی می‌کردند راهی به سوی (خداوند) صاحب عرش پیدا کنند.»'];
ayat[2072]=[17,43,'سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يَقُولُونَ عُلُوًّا كَبِيرًا','او پاک و برتر است از آنچه آنها می‌گویند، بسیار برتر و منزه‌تر!'];
ayat[2073]=[17,44,'تُسَبِّحُ لَهُ السَّمَاوَاتُ السَّبْعُ وَالْأَرْضُ وَمَن فِيهِنَّ ۚ وَإِن مِّن شَيْءٍ إِلَّا يُسَبِّحُ بِحَمْدِهِ وَلَٰكِن لَّا تَفْقَهُونَ تَسْبِيحَهُمْ ۗ إِنَّهُ كَانَ حَلِيمًا غَفُورًا','آسمانهای هفتگانه و زمین و کسانی که در آنها هستند، همه تسبیح او می‌گویند؛ و هر موجودی، تسبیح و حمد او می‌گوید؛ ولی شما تسبیح آنها را نمی‌فهمید؛ او بردبار و آمرزنده است.'];
ayat[2074]=[17,45,'وَإِذَا قَرَأْتَ الْقُرْآنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ حِجَابًا مَّسْتُورًا','و هنگامی که قرآن می‌خوانی، میان تو و آنها که به آخرت ایمان نمی‌آورند، حجاب ناپیدایی قرارمی‌دهیم؛'];
ayat[2075]=[17,46,'وَجَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَن يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا ۚ وَإِذَا ذَكَرْتَ رَبَّكَ فِي الْقُرْآنِ وَحْدَهُ وَلَّوْا عَلَىٰ أَدْبَارِهِمْ نُفُورًا','و بر دلهایشان پوششهایی، تا آن را نفهمند؛ و در گوشهایشان سنگینی؛ و هنگامی که پروردگارت را در قرآن به یگانگی یاد می‌کنی، آنها پشت می‌کنند و از تو روی بر می‌گردانند.'];
ayat[2076]=[17,47,'نَّحْنُ أَعْلَمُ بِمَا يَسْتَمِعُونَ بِهِ إِذْ يَسْتَمِعُونَ إِلَيْكَ وَإِذْ هُمْ نَجْوَىٰ إِذْ يَقُولُ الظَّالِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلًا مَّسْحُورًا','هنگامی که به سخنان تو گوش فرامی‌دهند، ما بهتر می‌دانیم برای چه گوش فرا می‌دهند؛ (و همچنین) در آن هنگام که با هم نجوا می‌کنند؛ آنگاه که ستمگران می‌گویند: «شما جز از انسانی که افسون شده، پیروی نمی‌کنید!»'];
ayat[2077]=[17,48,'انظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثَالَ فَضَلُّوا فَلَا يَسْتَطِيعُونَ سَبِيلًا','ببین چگونه برای تو مثلها زدند! در نتیجه گمراه شدند، و نمی‌توانند راه حق را پیدا کنند.'];
ayat[2078]=[17,49,'وَقَالُوا أَإِذَا كُنَّا عِظَامًا وَرُفَاتًا أَإِنَّا لَمَبْعُوثُونَ خَلْقًا جَدِيدًا','و گفتند: «آیا هنگامی که ما، استخوانهای پوسیده و پراکنده‌ای شدیم، دگر بار آفرینش تازه‌ای خواهیم یافت؟!»'];
ayat[2079]=[17,50,'۞ قُلْ كُونُوا حِجَارَةً أَوْ حَدِيدًا','بگو: «شما سنگ باشید یا آهن،'];
ayat[2080]=[17,51,'أَوْ خَلْقًا مِّمَّا يَكْبُرُ فِي صُدُورِكُمْ ۚ فَسَيَقُولُونَ مَن يُعِيدُنَا ۖ قُلِ الَّذِي فَطَرَكُمْ أَوَّلَ مَرَّةٍ ۚ فَسَيُنْغِضُونَ إِلَيْكَ رُءُوسَهُمْ وَيَقُولُونَ مَتَىٰ هُوَ ۖ قُلْ عَسَىٰ أَن يَكُونَ قَرِيبًا','یا هر مخلوقی که در نظر شما، از آن هم سخت‌تر است (و از حیات و زندگی دورتر می‌باشد، باز خدا قادر است شما را به زندگی مجدد بازگرداند). آنها بزودی می گویند: «چه کسی ما را بازمی‌گرداند؟!» بگو: «همان کسی که نخستین بار شما را آفرید.» آنان سر خود را (از روی تعجب و انکار،) به سوی تو خم می‌کنند و می‌گویند: «در چه زمانی خواهد بود؟!» بگو: «شاید نزدیک باشد!'];
ayat[2081]=[17,52,'يَوْمَ يَدْعُوكُمْ فَتَسْتَجِيبُونَ بِحَمْدِهِ وَتَظُنُّونَ إِن لَّبِثْتُمْ إِلَّا قَلِيلًا','همان روز که شما را (از قبرهایتان) فرامی‌خواند؛ شما هم اجابت می‌کنید در حالی که حمد او را می‌گویید؛ می‌پندارید تنها مدت کوتاهی (در جهان برزخ) درنگ کرده‌اید!»'];
ayat[2082]=[17,53,'وَقُل لِّعِبَادِي يَقُولُوا الَّتِي هِيَ أَحْسَنُ ۚ إِنَّ الشَّيْطَانَ يَنزَغُ بَيْنَهُمْ ۚ إِنَّ الشَّيْطَانَ كَانَ لِلْإِنسَانِ عَدُوًّا مُّبِينًا','به بندگانم بگو: «سخنی بگویند که بهترین باشد! چرا که (شیطان بوسیله سخنان ناموزون)، میان آنها فتنه و فساد می‌کند؛ همیشه شیطان دشمن آشکاری برای انسان بوده است!'];
ayat[2083]=[17,54,'رَّبُّكُمْ أَعْلَمُ بِكُمْ ۖ إِن يَشَأْ يَرْحَمْكُمْ أَوْ إِن يَشَأْ يُعَذِّبْكُمْ ۚ وَمَا أَرْسَلْنَاكَ عَلَيْهِمْ وَكِيلًا','پروردگار شما، از (نیات و اعمال) شما آگاهتر است؛ اگر بخواهد (و شایسته بداند)، شما را مشمول رحمت خود می‌سازد؛ و اگر بخواهد، مجازات می‌کند؛ و ما تو را بعنوان مأمور بر آنان نفرستاده‌ایم (که آنان را مجبور به ایمان کنی!)'];
ayat[2084]=[17,55,'وَرَبُّكَ أَعْلَمُ بِمَن فِي السَّمَاوَاتِ وَالْأَرْضِ ۗ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيِّينَ عَلَىٰ بَعْضٍ ۖ وَآتَيْنَا دَاوُودَ زَبُورًا','پروردگار تو، از حال همه کسانی که در آسمانها و زمین هستند، آگاهتر است؛ و (اگر تو را بر دیگران برتری دادیم، بخاطر شایستگی توست،) ما بعضی از پیامبران را بر بعضی دیگر برتری دادیم؛ و به داوود، زبور بخشیدیم.'];
ayat[2085]=[17,56,'قُلِ ادْعُوا الَّذِينَ زَعَمْتُم مِّن دُونِهِ فَلَا يَمْلِكُونَ كَشْفَ الضُّرِّ عَنكُمْ وَلَا تَحْوِيلًا','بگو: «کسانی را که غیر از خدا (معبود خود) می‌پندارید، بخوانید! آنها نه می‌توانند مشکلی را از شما برطرف سازند، و نه تغییری در آن ایجاد کنند.»'];
ayat[2086]=[17,57,'أُولَٰئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلَىٰ رَبِّهِمُ الْوَسِيلَةَ أَيُّهُمْ أَقْرَبُ وَيَرْجُونَ رَحْمَتَهُ وَيَخَافُونَ عَذَابَهُ ۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُورًا','کسانی را که آنان می‌خوانند، خودشان وسیله‌ای (برای تقرب) به پروردگارشان می‌جویند، وسیله‌ای هر چه نزدیکتر؛ و به رحمت او امیدوارند؛ و از عذاب او می‌ترسند؛ چرا که عذاب پروردگارت، همواره در خور پرهیز و وحشت است!'];
ayat[2087]=[17,58,'وَإِن مِّن قَرْيَةٍ إِلَّا نَحْنُ مُهْلِكُوهَا قَبْلَ يَوْمِ الْقِيَامَةِ أَوْ مُعَذِّبُوهَا عَذَابًا شَدِيدًا ۚ كَانَ ذَٰلِكَ فِي الْكِتَابِ مَسْطُورًا','هیچ شهر و آبادی نیست مگر اینکه آن را پیش از روز قیامت هلاک می‌کنیم؛ یا (اگر گناهکارند،) به عذاب شدیدی گرفتارشان خواهیم ساخت؛ این، در کتاب الهی [= لوح محفوظ] ثبت است.'];
ayat[2088]=[17,59,'وَمَا مَنَعَنَا أَن نُّرْسِلَ بِالْآيَاتِ إِلَّا أَن كَذَّبَ بِهَا الْأَوَّلُونَ ۚ وَآتَيْنَا ثَمُودَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوا بِهَا ۚ وَمَا نُرْسِلُ بِالْآيَاتِ إِلَّا تَخْوِيفًا','هیچ چیز مانع ما نبود که این معجزات (درخواستی بهانه‌جویان) را بفرستیم جز اینکه پیشینیان (که همین درخواستها را داشتند، و با ایشان هماهنگ بودند)، آن را تکذیب کردند؛ (از جمله،) ما به (قوم) ثمود، ناقه دادیم؛ (معجزه‌ای) که روشنگر بود؛ اما بر آن ستم کردند (و ناقه را کشتند). ما معجزات را فقط برای بیم دادن (و اتمام حجت) می‌فرستیم.'];
ayat[2089]=[17,60,'وَإِذْ قُلْنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِالنَّاسِ ۚ وَمَا جَعَلْنَا الرُّؤْيَا الَّتِي أَرَيْنَاكَ إِلَّا فِتْنَةً لِّلنَّاسِ وَالشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْآنِ ۚ وَنُخَوِّفُهُمْ فَمَا يَزِيدُهُمْ إِلَّا طُغْيَانًا كَبِيرًا','(به یاد آور) زمانی را که به تو گفتیم: «پروردگارت احاطه کامل به مردم دارد؛ (و از وضعشان کاملاً آگاه است.) و ما آن رؤیایی را که به تو نشان دادیم، فقط برای آزمایش مردم بود؛ همچنین شجره ملعونه [= درخت نفرین شده‌] را که در قرآن ذکر کرده‌ایم. ما آنها را بیم داده (و انذار) می‌کنیم؛ اما جز طغیان عظیم، چیزی بر آنها نمی‌افزاید!»'];
ayat[2090]=[17,61,'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ قَالَ أَأَسْجُدُ لِمَنْ خَلَقْتَ طِينًا','(به یاد آورید) زمانی را که به فرشتگان گفتیم: «برای آدم سجده کنید!» آنها همگی سجده کردند، جز ابلیس که گفت: «آیا برای کسی سجده کنم که او را از خاک آفریده‌ای؟!»'];
ayat[2091]=[17,62,'قَالَ أَرَأَيْتَكَ هَٰذَا الَّذِي كَرَّمْتَ عَلَيَّ لَئِنْ أَخَّرْتَنِ إِلَىٰ يَوْمِ الْقِيَامَةِ لَأَحْتَنِكَنَّ ذُرِّيَّتَهُ إِلَّا قَلِيلًا','(سپس) گفت: «به من بگو، این کسی را که بر من برتری داده‌ای (به چه دلیل بوده است؟) اگر مرا تا روز قیامت زنده بگذاری، همه فرزندانش را، جز عده کمی، گمراه و ریشه‌کن خواهم ساخت!»'];
ayat[2092]=[17,63,'قَالَ اذْهَبْ فَمَن تَبِعَكَ مِنْهُمْ فَإِنَّ جَهَنَّمَ جَزَاؤُكُمْ جَزَاءً مَّوْفُورًا','فرمود: «برو! هر کس از آنان از تو تبعیت کند، جهنم کیفر شماست، کیفری است فراوان!'];
ayat[2093]=[17,64,'وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُم بِصَوْتِكَ وَأَجْلِبْ عَلَيْهِم بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِي الْأَمْوَالِ وَالْأَوْلَادِ وَعِدْهُمْ ۚ وَمَا يَعِدُهُمُ الشَّيْطَانُ إِلَّا غُرُورًا','هر کدام از آنها را می‌توانی با صدایت تحریک کن! و لشکر سواره و پیاده‌ات را بر آنها گسیل دار! و در ثروت و فرزندانشان شرکت جوی! و آنان را با وعده‌ها سرگرم کن! -ولی شیطان، جز فریب و دروغ، وعده‌ای به آنها نمی‌دهد-'];
ayat[2094]=[17,65,'إِنَّ عِبَادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطَانٌ ۚ وَكَفَىٰ بِرَبِّكَ وَكِيلًا','(اما بدان) تو هرگز سلطه‌ای بر بندگان من، نخواهی یافت (و آنها هیچ‌گاه به دام تو گرفتار نمی‌شوند)! همین قدر کافی است که پروردگارت حافظ آنها باشد.»'];
ayat[2095]=[17,66,'رَّبُّكُمُ الَّذِي يُزْجِي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِن فَضْلِهِ ۚ إِنَّهُ كَانَ بِكُمْ رَحِيمًا','پروردگارتان کسی است که کشتی را در دریا برای شما به حرکت درمی‌آورد، تا از نعمت او بهره‌مند شوید؛ او نسبت به شما مهربان است.'];
ayat[2096]=[17,67,'وَإِذَا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَن تَدْعُونَ إِلَّا إِيَّاهُ ۖ فَلَمَّا نَجَّاكُمْ إِلَى الْبَرِّ أَعْرَضْتُمْ ۚ وَكَانَ الْإِنسَانُ كَفُورًا','و هنگامی که در دریا ناراحتی به شما برسد، جز او، تمام کسانی را که (برای حل مشکلات خود) می‌خوانید، فراموش می‌کنید؛ اما هنگامی که شما را به خشکی نجات دهد، روی می‌گردانید؛ و انسان، بسیار ناسپاس است!'];
ayat[2097]=[17,68,'أَفَأَمِنتُمْ أَن يَخْسِفَ بِكُمْ جَانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لَا تَجِدُوا لَكُمْ وَكِيلًا','آیا از این ایمن هستید که در خشکی (با یک زلزله شدید) شما را در زمین فرو ببرد، یا طوفانی از سنگریزه بر شما بفرستد (و در آن مدفونتان کند)، سپس حافظ (و یاوری) برای خود نیابید؟!'];
ayat[2098]=[17,69,'أَمْ أَمِنتُمْ أَن يُعِيدَكُمْ فِيهِ تَارَةً أُخْرَىٰ فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِّنَ الرِّيحِ فَيُغْرِقَكُم بِمَا كَفَرْتُمْ ۙ ثُمَّ لَا تَجِدُوا لَكُمْ عَلَيْنَا بِهِ تَبِيعًا','یا اینکه ایمن هستید که بار دیگر شما را به دریا بازگرداند، و تندباد کوبنده‌ای بر شما بفرستد، و شما را بخاطر کفرتان غرق کند، سپس دادخواه و خونخواهی در برابر ما پیدا نکنید؟!'];
ayat[2099]=[17,70,'۞ وَلَقَدْ كَرَّمْنَا بَنِي آدَمَ وَحَمَلْنَاهُمْ فِي الْبَرِّ وَالْبَحْرِ وَرَزَقْنَاهُم مِّنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلَىٰ كَثِيرٍ مِّمَّنْ خَلَقْنَا تَفْضِيلًا','ما آدمیزادگان را گرامی داشتیم؛ و آنها را در خشکی و دریا، (بر مرکبهای راهوار) حمل کردیم؛ و از انواع روزیهای پاکیزه به آنان روزی دادیم؛ و آنها را بر بسیاری از موجوداتی که خلق کرده‌ایم، برتری بخشیدیم.'];
ayat[2100]=[17,71,'يَوْمَ نَدْعُو كُلَّ أُنَاسٍ بِإِمَامِهِمْ ۖ فَمَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَأُولَٰئِكَ يَقْرَءُونَ كِتَابَهُمْ وَلَا يُظْلَمُونَ فَتِيلًا','(به یاد آورید) روزی را که هر گروهی را با پیشوایشان می‌خوانیم! کسانی که نامه عملشان به دست راستشان داده شود، آن را (با شادی و سرور) می‌خوانند؛ و بقدر رشته شکاف هسته خرمایی به آنان ستم نمی‌شود!'];
ayat[2101]=[17,72,'وَمَن كَانَ فِي هَٰذِهِ أَعْمَىٰ فَهُوَ فِي الْآخِرَةِ أَعْمَىٰ وَأَضَلُّ سَبِيلًا','اما کسی که در این جهان (از دیدن چهره حق) نابینا بوده است، در آخرت نیز نابینا و گمراهتر است!'];
ayat[2102]=[17,73,'وَإِن كَادُوا لَيَفْتِنُونَكَ عَنِ الَّذِي أَوْحَيْنَا إِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهُ ۖ وَإِذًا لَّاتَّخَذُوكَ خَلِيلًا','نزدیک بود آنها تو را (با وسوسه‌های خود) از آنچه بر تو وحی کرده‌ایم بفریبند، تا غیر آن را به ما نسبت دهی؛ و در آن صورت، تو را به دوستی خود برمیگزینند!'];
ayat[2103]=[17,74,'وَلَوْلَا أَن ثَبَّتْنَاكَ لَقَدْ كِدتَّ تَرْكَنُ إِلَيْهِمْ شَيْئًا قَلِيلًا','و اگر ما تو را ثابت قدم نمی‌ساختیم (و در پرتو مقام عصمت، مصون از انحراف نبودی)، نزدیک بود به آنان تمایل کنی.'];
ayat[2104]=[17,75,'إِذًا لَّأَذَقْنَاكَ ضِعْفَ الْحَيَاةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَصِيرًا','اگر چنین می‌کردی، ما دو برابر مجازات (مشرکان) در زندگی دنیا، و دو برابر (مجازات) آنها را بعد از مرگ، به تو می‌چشاندیم؛ سپس در برابر ما، یاوری برای خود نمی‌یافتی!'];
ayat[2105]=[17,76,'وَإِن كَادُوا لَيَسْتَفِزُّونَكَ مِنَ الْأَرْضِ لِيُخْرِجُوكَ مِنْهَا ۖ وَإِذًا لَّا يَلْبَثُونَ خِلَافَكَ إِلَّا قَلِيلًا','و نزدیک بود (با نیرنگ و توطئه) تو را از این سرزمین بلغزانند، تا از آن بیرونت کنند! و هرگاه چنین می‌کردند، (گرفتار مجازات سخت الهی شده،) و پس از تو، جز مدت کمی باقی نمی‌ماندند!'];
ayat[2106]=[17,77,'سُنَّةَ مَن قَدْ أَرْسَلْنَا قَبْلَكَ مِن رُّسُلِنَا ۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحْوِيلًا','این سنت (ما در مورد) پیامبرانی است که پیش از تو فرستادیم؛ و هرگز برای سنت ما تغییر و دگرگونی نخواهی یافت!'];
ayat[2107]=[17,78,'أَقِمِ الصَّلَاةَ لِدُلُوكِ الشَّمْسِ إِلَىٰ غَسَقِ اللَّيْلِ وَقُرْآنَ الْفَجْرِ ۖ إِنَّ قُرْآنَ الْفَجْرِ كَانَ مَشْهُودًا','نماز را از زوال خورشید (هنگام ظهر) تا نهایت تاریکی شب [= نیمه شب‌] برپا دار؛ و همچنین قرآن فجر [= نماز صبح‌] را؛ چرا که قرآن فجر، مشهود (فرشتگان شب و روز) است!'];
ayat[2108]=[17,79,'وَمِنَ اللَّيْلِ فَتَهَجَّدْ بِهِ نَافِلَةً لَّكَ عَسَىٰ أَن يَبْعَثَكَ رَبُّكَ مَقَامًا مَّحْمُودًا','و پاسی از شب را (از خواب برخیز، و) قرآن (و نماز) بخوان! این یک وظیفه اضافی برای توست؛ امید است پروردگارت تو را به مقامی در خور ستایش برانگیزد!'];
ayat[2109]=[17,80,'وَقُل رَّبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ وَأَخْرِجْنِي مُخْرَجَ صِدْقٍ وَاجْعَل لِّي مِن لَّدُنكَ سُلْطَانًا نَّصِيرًا','و بگو: «پروردگارا! مرا (در هر کار،) با صداقت وارد کن، و با صداقت خارج ساز! و از سوی خود، حجتی یاری کننده برایم قرار ده!»'];
ayat[2110]=[17,81,'وَقُلْ جَاءَ الْحَقُّ وَزَهَقَ الْبَاطِلُ ۚ إِنَّ الْبَاطِلَ كَانَ زَهُوقًا','و بگو: «حق آمد، و باطل نابود شد؛ یقیناً باطل نابود شدنی است!»'];
ayat[2111]=[17,82,'وَنُنَزِّلُ مِنَ الْقُرْآنِ مَا هُوَ شِفَاءٌ وَرَحْمَةٌ لِّلْمُؤْمِنِينَ ۙ وَلَا يَزِيدُ الظَّالِمِينَ إِلَّا خَسَارًا','و از قرآن، آنچه شفا و رحمت است برای مؤمنان، نازل می‌کنیم؛ و ستمگران را جز خسران (و زیان) نمی‌افزاید.'];
ayat[2112]=[17,83,'وَإِذَا أَنْعَمْنَا عَلَى الْإِنسَانِ أَعْرَضَ وَنَأَىٰ بِجَانِبِهِ ۖ وَإِذَا مَسَّهُ الشَّرُّ كَانَ يَئُوسًا','هنگامی که به انسان نعمت می‌بخشیم، (از حق) روی می‌گرداند و متکبرانه دور می‌شود؛ و هنگامی که (کمترین) بدی به او می‌رسد، (از همه چیز) مایوس می‌گردد!'];
ayat[2113]=[17,84,'قُلْ كُلٌّ يَعْمَلُ عَلَىٰ شَاكِلَتِهِ فَرَبُّكُمْ أَعْلَمُ بِمَنْ هُوَ أَهْدَىٰ سَبِيلًا','بگو: «هر کس طبق روش (و خلق و خوی) خود عمل می‌کند؛ و پروردگارتان کسانی را که راهشان نیکوتر است، بهتر می‌شناسد.»'];
ayat[2114]=[17,85,'وَيَسْأَلُونَكَ عَنِ الرُّوحِ ۖ قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي وَمَا أُوتِيتُم مِّنَ الْعِلْمِ إِلَّا قَلِيلًا','و از تو درباره «روح» سؤال می‌کنند، بگو: «روح از فرمان پروردگار من است؛ و جز اندکی از دانش، به شما داده نشده است!»'];
ayat[2115]=[17,86,'وَلَئِن شِئْنَا لَنَذْهَبَنَّ بِالَّذِي أَوْحَيْنَا إِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِهِ عَلَيْنَا وَكِيلًا','و اگر بخواهیم، آنچه را بر تو وحی فرستاده‌ایم، از تو می‌گیریم؛ سپس کسی را نمی‌یابی که در برابر ما، از تو دفاع کند...'];
ayat[2116]=[17,87,'إِلَّا رَحْمَةً مِّن رَّبِّكَ ۚ إِنَّ فَضْلَهُ كَانَ عَلَيْكَ كَبِيرًا','مگر رحمت پروردگارت (شامل حالت گردد،) که فضل پروردگارت بر تو بزرگ بوده است!'];
ayat[2117]=[17,88,'قُل لَّئِنِ اجْتَمَعَتِ الْإِنسُ وَالْجِنُّ عَلَىٰ أَن يَأْتُوا بِمِثْلِ هَٰذَا الْقُرْآنِ لَا يَأْتُونَ بِمِثْلِهِ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيرًا','بگو: «اگر انسانها و پریان (جن و انس) اتفاق کنند که همانند این قرآن را بیاورند، همانند آن را نخواهند آورد؛ هر چند یکدیگر را (در این کار) کمک کنند.'];
ayat[2118]=[17,89,'وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِي هَٰذَا الْقُرْآنِ مِن كُلِّ مَثَلٍ فَأَبَىٰ أَكْثَرُ النَّاسِ إِلَّا كُفُورًا','ما در این قرآن، برای مردم از هر چیز نمونه‌ای آوردیم (و همه معارف در آن جمع است)؛ اما بیشتر مردم (در برابر آن، از هر کاری) جز انکار، ابا داشتند!'];
ayat[2119]=[17,90,'وَقَالُوا لَن نُّؤْمِنَ لَكَ حَتَّىٰ تَفْجُرَ لَنَا مِنَ الْأَرْضِ يَنبُوعًا','و گفتند: «ما هرگز به تو ایمان نمی‌آوریم تا اینکه چشمه‌جوشانی از این سرزمین (خشک و سوزان) برای ما خارج سازی...'];
ayat[2120]=[17,91,'أَوْ تَكُونَ لَكَ جَنَّةٌ مِّن نَّخِيلٍ وَعِنَبٍ فَتُفَجِّرَ الْأَنْهَارَ خِلَالَهَا تَفْجِيرًا','یا باغی از نخل و انگور از آن تو باشد؛ و نهرها در لابه‌لای آن جاری کنی...'];
ayat[2121]=[17,92,'أَوْ تُسْقِطَ السَّمَاءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا أَوْ تَأْتِيَ بِاللَّهِ وَالْمَلَائِكَةِ قَبِيلًا','یا قطعات (سنگهای) آسمان را -آنچنان که می‌پنداری- بر سر ما فرود آری؛ یا خداوند و فرشتگان را در برابر ما بیاوری...'];
ayat[2122]=[17,93,'أَوْ يَكُونَ لَكَ بَيْتٌ مِّن زُخْرُفٍ أَوْ تَرْقَىٰ فِي السَّمَاءِ وَلَن نُّؤْمِنَ لِرُقِيِّكَ حَتَّىٰ تُنَزِّلَ عَلَيْنَا كِتَابًا نَّقْرَؤُهُ ۗ قُلْ سُبْحَانَ رَبِّي هَلْ كُنتُ إِلَّا بَشَرًا رَّسُولًا','یا برای تو خانه‌ای پر نقش و نگار از طلا باشد؛ یا به آسمان بالا روی؛ حتی اگر به آسمان روی، ایمان نمی‌آوریم مگر آنکه نامه‌ای بر ما فرود آوری که آن را بخوانیم! «بگو:» منزه است پروردگارم (از این سخنان بی‌معنی)! مگر من جز انسانی فرستاده خدا هستم؟!'];
ayat[2123]=[17,94,'وَمَا مَنَعَ النَّاسَ أَن يُؤْمِنُوا إِذْ جَاءَهُمُ الْهُدَىٰ إِلَّا أَن قَالُوا أَبَعَثَ اللَّهُ بَشَرًا رَّسُولًا','تنها چیزی که بعد از آمدن هدایت مانع شد مردم ایمان بیاورند، این بود (که از روی نادانی و بی‌خبری) گفتند: «آیا خداوند بشری را بعنوان رسول فرستاده است؟!»'];
ayat[2124]=[17,95,'قُل لَّوْ كَانَ فِي الْأَرْضِ مَلَائِكَةٌ يَمْشُونَ مُطْمَئِنِّينَ لَنَزَّلْنَا عَلَيْهِم مِّنَ السَّمَاءِ مَلَكًا رَّسُولًا','بگو: «(حتی) اگر در روی زمین فرشتگانی (زندگی می‌کردند، و) با آرامش گام برمی‌داشتند، ما فرشته‌ای را به عنوان رسول، بر آنها می‌فرستادیم!» (چرا که رهنمای هر گروهی باید از جنس خودشان باشد).'];
ayat[2125]=[17,96,'قُلْ كَفَىٰ بِاللَّهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ ۚ إِنَّهُ كَانَ بِعِبَادِهِ خَبِيرًا بَصِيرًا','بگو: «همین کافی است که خداوند، میان من و شما گواه باشد؛ چرا که او نسبت به بندگانش آگاه و بیناست!'];
ayat[2126]=[17,97,'وَمَن يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ ۖ وَمَن يُضْلِلْ فَلَن تَجِدَ لَهُمْ أَوْلِيَاءَ مِن دُونِهِ ۖ وَنَحْشُرُهُمْ يَوْمَ الْقِيَامَةِ عَلَىٰ وُجُوهِهِمْ عُمْيًا وَبُكْمًا وَصُمًّا ۖ مَّأْوَاهُمْ جَهَنَّمُ ۖ كُلَّمَا خَبَتْ زِدْنَاهُمْ سَعِيرًا','هر کس را خدا هدایت کند، هدایت یافته واقعی اوست؛ و هر کس را (بخاطر اعمالش) گمراه سازد، هادیان و سرپرستانی غیر خدا برای او نخواهی یافت؛ و روز قیامت، آنها را بر صورتهایشان محشور می‌کنیم، در حالی که نابینا و گنگ و کرند؛ جایگاهشان دوزخ است؛ هر زمان آتش آن فرونشیند، شعله تازه‌ای بر آنان می‌افزاییم!'];
ayat[2127]=[17,98,'ذَٰلِكَ جَزَاؤُهُم بِأَنَّهُمْ كَفَرُوا بِآيَاتِنَا وَقَالُوا أَإِذَا كُنَّا عِظَامًا وَرُفَاتًا أَإِنَّا لَمَبْعُوثُونَ خَلْقًا جَدِيدًا','این کیفر آنهاست، بخاطر اینکه نسبت به آیات ما کافر شدند و گفتند: «آیا هنگامی که ما استخوانهای پوسیده و خاکهای پراکنده‌ای شدیم، بار دیگر آفرینش تازه‌ای خواهیم یافت؟!»'];
ayat[2128]=[17,99,'۞ أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ قَادِرٌ عَلَىٰ أَن يَخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ أَجَلًا لَّا رَيْبَ فِيهِ فَأَبَى الظَّالِمُونَ إِلَّا كُفُورًا','آیا نمی‌دانند خدایی که آسمانها و زمین را آفریده، قادر است مثل آنان را بیافریند (و به زندگی جدید بازشان گرداند)؟! و برای آنان سرآمدی قطعی -که شکّی در آن نیست- قرار داده؛ امّا ظالمان، جز کفر و انکار را پذیرا نیستند!'];
ayat[2129]=[17,100,'قُل لَّوْ أَنتُمْ تَمْلِكُونَ خَزَائِنَ رَحْمَةِ رَبِّي إِذًا لَّأَمْسَكْتُمْ خَشْيَةَ الْإِنفَاقِ ۚ وَكَانَ الْإِنسَانُ قَتُورًا','بگو: «اگر شما مالک خزائن رحمت پروردگار من بودید. در آن صورت، (بخاطر تنگ نظری) امساک می‌کردید، مبادا انفاق، مایه تنگدستی شما شود» و انسان تنگ نظر است!'];
ayat[2130]=[17,101,'وَلَقَدْ آتَيْنَا مُوسَىٰ تِسْعَ آيَاتٍ بَيِّنَاتٍ ۖ فَاسْأَلْ بَنِي إِسْرَائِيلَ إِذْ جَاءَهُمْ فَقَالَ لَهُ فِرْعَوْنُ إِنِّي لَأَظُنُّكَ يَا مُوسَىٰ مَسْحُورًا','ما به موسی نه معجزه روشن دادیم؛ پس از بنی اسرائیل سؤال کن آن زمان که این (معجزات نه گانه) به سراغ آنها آمد (چگونه بودند)؟! فرعون به او گفت: «ای موسی! گمان می‌کنم تو دیوانه (یا ساحری)!»'];
ayat[2131]=[17,102,'قَالَ لَقَدْ عَلِمْتَ مَا أَنزَلَ هَٰؤُلَاءِ إِلَّا رَبُّ السَّمَاوَاتِ وَالْأَرْضِ بَصَائِرَ وَإِنِّي لَأَظُنُّكَ يَا فِرْعَوْنُ مَثْبُورًا','(موسی) گفت: «تو می‌دانی این آیات را جز پروردگار آسمانها و زمین -برای روشنی دلها- نفرستاده؛ و من گمان می‌کنم ای فرعون، تو (بزودی) هلاک خواهی شد!»'];
ayat[2132]=[17,103,'فَأَرَادَ أَن يَسْتَفِزَّهُم مِّنَ الْأَرْضِ فَأَغْرَقْنَاهُ وَمَن مَّعَهُ جَمِيعًا','پس (فرعون) تصمیم گرفت آنان را از آن سرزمین ریشه کن سازد؛ ولی ما، او و تمام کسانی را که با او بودند، غرق کردیم.'];
ayat[2133]=[17,104,'وَقُلْنَا مِن بَعْدِهِ لِبَنِي إِسْرَائِيلَ اسْكُنُوا الْأَرْضَ فَإِذَا جَاءَ وَعْدُ الْآخِرَةِ جِئْنَا بِكُمْ لَفِيفًا','و بعد از آن به بنی اسرائیل گفتیم: «در این سرزمین [= مصر و شام‌] ساکن شوید! امّا هنگامی که وعده آخرت فرا رسد، همه شما را دسته جمعی (به آن دادگاه عدل) می‌آوریم»'];
ayat[2134]=[17,105,'وَبِالْحَقِّ أَنزَلْنَاهُ وَبِالْحَقِّ نَزَلَ ۗ وَمَا أَرْسَلْنَاكَ إِلَّا مُبَشِّرًا وَنَذِيرًا','و ما قرآن را بحق نازل کردیم؛ و بحق نازل شد؛ و تو را، جز بعنوان بشارت‌دهنده و بیم‌دهنده، نفرستادیم!'];
ayat[2135]=[17,106,'وَقُرْآنًا فَرَقْنَاهُ لِتَقْرَأَهُ عَلَى النَّاسِ عَلَىٰ مُكْثٍ وَنَزَّلْنَاهُ تَنزِيلًا','و قرآنی که آیاتش را از هم جدا کردیم، تا آن را با درنگ بر مردم بخوانی؛ و آن را بتدریج نازل کردیم.'];
ayat[2136]=[17,107,'قُلْ آمِنُوا بِهِ أَوْ لَا تُؤْمِنُوا ۚ إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِن قَبْلِهِ إِذَا يُتْلَىٰ عَلَيْهِمْ يَخِرُّونَ لِلْأَذْقَانِ سُجَّدًا','بگو: «خواه به آن ایمان بیاورید، و خواه ایمان نیاورید، کسانی که پیش از آن به آنها دانش داده شده، هنگامی که (این آیات) بر آنان خوانده می‌شود، سجده‌کنان به خاک می‌افتند...'];
ayat[2137]=[17,108,'وَيَقُولُونَ سُبْحَانَ رَبِّنَا إِن كَانَ وَعْدُ رَبِّنَا لَمَفْعُولًا','و می‌گویند: «منزّه است پروردگار ما، که وعده‌هایش به یقین انجام‌شدنی است!»'];
ayat[2138]=[17,109,'وَيَخِرُّونَ لِلْأَذْقَانِ يَبْكُونَ وَيَزِيدُهُمْ خُشُوعًا ۩','آنها (بی‌اختیار) به زمین می‌افتند و گریه می‌کنند؛ و (تلاوت این آیات، همواره) بر خشوعشان می‌افزاید.'];
ayat[2139]=[17,110,'قُلِ ادْعُوا اللَّهَ أَوِ ادْعُوا الرَّحْمَٰنَ ۖ أَيًّا مَّا تَدْعُوا فَلَهُ الْأَسْمَاءُ الْحُسْنَىٰ ۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذَٰلِكَ سَبِيلًا','بگو: ««اللّه» را بخوانید یا «رحمان» را، هر کدام را بخوانید، (ذات پاکش یکی است؛ و) برای او بهترین نامهاست!» و نمازت را زیاد بلند، یا خیلی آهسته نخوان؛ و در میان آن دو، راهی (معتدل) انتخاب کن!'];
ayat[2140]=[17,111,'وَقُلِ الْحَمْدُ لِلَّهِ الَّذِي لَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُن لَّهُ شَرِيكٌ فِي الْمُلْكِ وَلَمْ يَكُن لَّهُ وَلِيٌّ مِّنَ الذُّلِّ ۖ وَكَبِّرْهُ تَكْبِيرًا','و بگو: «ستایش برای خداوندی است که نه فرزندی برای خود انتخاب کرده، و نه شریکی در حکومت دارد، و نه بخاطر ضعف و ذلّت، (حامی و) سرپرستی برای اوست!» و او را بسیار بزرگ بشمار!'];
ayat[2141]=[18,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ الَّذِي أَنزَلَ عَلَىٰ عَبْدِهِ الْكِتَابَ وَلَمْ يَجْعَل لَّهُ عِوَجًا ۜ','حمد مخصوص خدایی است که این کتاب (آسمانی) را بر بنده (برگزیده) اش نازل کرد، و هیچ گونه کژی در آن قرار نداد...'];
ayat[2142]=[18,2,'قَيِّمًا لِّيُنذِرَ بَأْسًا شَدِيدًا مِّن لَّدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصَّالِحَاتِ أَنَّ لَهُمْ أَجْرًا حَسَنًا','در حالی که ثابت و مستقیم و نگاهبان کتابهای (آسمانی) دیگر است؛ تا (بدکاران را) از عذاب شدید او بترساند؛ و مؤمنانی را که کارهای شایسته انجام می‌دهند، بشارت دهد که پاداش نیکویی برای آنهاست...'];
ayat[2143]=[18,3,'مَّاكِثِينَ فِيهِ أَبَدًا','(همان بهشت برین) که جاودانه در آن خواهند ماند!'];
ayat[2144]=[18,4,'وَيُنذِرَ الَّذِينَ قَالُوا اتَّخَذَ اللَّهُ وَلَدًا','و (نیز) آنها را که گفتند: «خداوند، فرزندی (برای خود) انتخاب کرده است»، انذار کند.'];
ayat[2145]=[18,5,'مَّا لَهُم بِهِ مِنْ عِلْمٍ وَلَا لِآبَائِهِمْ ۚ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ أَفْوَاهِهِمْ ۚ إِن يَقُولُونَ إِلَّا كَذِبًا','نه آنها (هرگز) به این سخن یقین دارند، و نه پدرانشان! سخن بزرگی از دهانشان خارج می‌شود! آنها فقط دروغ می‌گویند!'];
ayat[2146]=[18,6,'فَلَعَلَّكَ بَاخِعٌ نَّفْسَكَ عَلَىٰ آثَارِهِمْ إِن لَّمْ يُؤْمِنُوا بِهَٰذَا الْحَدِيثِ أَسَفًا','] گویی می‌خواهی بخاطر اعمال آنان، خود را از غم و اندوه هلاک کنی اگر به این گفتار ایمان نیاورند!'];
ayat[2147]=[18,7,'إِنَّا جَعَلْنَا مَا عَلَى الْأَرْضِ زِينَةً لَّهَا لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلًا','ما آنچه را روی زمین است زینت آن قرار دادیم، تا آنها را بیازماییم که کدامینشان بهتر عمل می‌کنند!'];
ayat[2148]=[18,8,'وَإِنَّا لَجَاعِلُونَ مَا عَلَيْهَا صَعِيدًا جُرُزًا','(ولی) این زرق و برقها پایدار نیست، و ما (سرانجام) قشر روی زمین را خاک بی گیاهی قرار می‌دهیم!'];
ayat[2149]=[18,9,'أَمْ حَسِبْتَ أَنَّ أَصْحَابَ الْكَهْفِ وَالرَّقِيمِ كَانُوا مِنْ آيَاتِنَا عَجَبًا','آیا گمان کردی اصحاب کهف و رقیم از آیات عجیب ما بودند؟!'];
ayat[2150]=[18,10,'إِذْ أَوَى الْفِتْيَةُ إِلَى الْكَهْفِ فَقَالُوا رَبَّنَا آتِنَا مِن لَّدُنكَ رَحْمَةً وَهَيِّئْ لَنَا مِنْ أَمْرِنَا رَشَدًا','زمانی را به خاطر بیاور که آن جوانان به غار پناه بردند، و گفتند: «پروردگارا! ما را از سوی خودت رحمتی عطا کن، و راه نجاتی برای ما فراهم ساز!»'];
ayat[2151]=[18,11,'فَضَرَبْنَا عَلَىٰ آذَانِهِمْ فِي الْكَهْفِ سِنِينَ عَدَدًا','ما (پرده خواب را) در غار بر گوششان زدیم، و سالها در خواب فرو رفتند.'];
ayat[2152]=[18,12,'ثُمَّ بَعَثْنَاهُمْ لِنَعْلَمَ أَيُّ الْحِزْبَيْنِ أَحْصَىٰ لِمَا لَبِثُوا أَمَدًا','سپس آنان را برانگیختیم تا بدانیم (و این امر آشکار گردد که) کدام یک از آن دو گروه، مدّت خواب خود را بهتر حساب کرده‌اند.'];
ayat[2153]=[18,13,'نَّحْنُ نَقُصُّ عَلَيْكَ نَبَأَهُم بِالْحَقِّ ۚ إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ وَزِدْنَاهُمْ هُدًى','ما داستان آنان را بحق برای تو بازگو می‌کنیم؛ آنها جوانانی بودند که به پروردگارشان ایمان آوردند، و ما بر هدایتشان افزودیم.'];
ayat[2154]=[18,14,'وَرَبَطْنَا عَلَىٰ قُلُوبِهِمْ إِذْ قَامُوا فَقَالُوا رَبُّنَا رَبُّ السَّمَاوَاتِ وَالْأَرْضِ لَن نَّدْعُوَ مِن دُونِهِ إِلَٰهًا ۖ لَّقَدْ قُلْنَا إِذًا شَطَطًا','و دلهایشان را محکم ساختیم در آن موقع که قیام کردند و گفتند: «پروردگار ما، پروردگار آسمانها و زمین است؛ هرگز غیر او معبودی را نمی‌خوانیم؛ که اگر چنین کنیم، سخنی بگزاف گفته‌ایم.'];
ayat[2155]=[18,15,'هَٰؤُلَاءِ قَوْمُنَا اتَّخَذُوا مِن دُونِهِ آلِهَةً ۖ لَّوْلَا يَأْتُونَ عَلَيْهِم بِسُلْطَانٍ بَيِّنٍ ۖ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا','این قوم ما هستند که معبودهایی جز خدا انتخاب کرده‌اند؛ چرا دلیل آشکاری (بر این کار) نمی‌آورند؟! و چه کسی ظالمتر است از آن کس که بر خدا دروغ ببندد؟!»'];
ayat[2156]=[18,16,'وَإِذِ اعْتَزَلْتُمُوهُمْ وَمَا يَعْبُدُونَ إِلَّا اللَّهَ فَأْوُوا إِلَى الْكَهْفِ يَنشُرْ لَكُمْ رَبُّكُم مِّن رَّحْمَتِهِ وَيُهَيِّئْ لَكُم مِّنْ أَمْرِكُم مِّرْفَقًا','و (به آنها گفتیم:) هنگامی که از آنان و آنچه جز خدا می‌پرستند کناره‌گیری کردید، به غار پناه برید؛ که پروردگارتان (سایه) رحمتش را بر شما می‌گستراند؛ و در این امر، آرامشی برای شما فراهم می‌سازد!'];
ayat[2157]=[18,17,'۞ وَتَرَى الشَّمْسَ إِذَا طَلَعَت تَّزَاوَرُ عَن كَهْفِهِمْ ذَاتَ الْيَمِينِ وَإِذَا غَرَبَت تَّقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِي فَجْوَةٍ مِّنْهُ ۚ ذَٰلِكَ مِنْ آيَاتِ اللَّهِ ۗ مَن يَهْدِ اللَّهُ فَهُوَ الْمُهْتَدِ ۖ وَمَن يُضْلِلْ فَلَن تَجِدَ لَهُ وَلِيًّا مُّرْشِدًا','و (اگر در آنجا بودی) خورشید را می‌دیدی که به هنگام طلوع، به سمت راست غارشان متمایل می‌گردد؛ و به هنگام غروب، به سمت چپ؛ و آنها در محل وسیعی از آن (غار) قرار داشتند؛ این از آیات خداست! هر کس را خدا هدایت کند، هدایت یافته واقعی اوست؛ و هر کس را گمراه نماید، هرگز ولیّ و راهنمایی برای او نخواهی یافت!'];
ayat[2158]=[18,18,'وَتَحْسَبُهُمْ أَيْقَاظًا وَهُمْ رُقُودٌ ۚ وَنُقَلِّبُهُمْ ذَاتَ الْيَمِينِ وَذَاتَ الشِّمَالِ ۖ وَكَلْبُهُم بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيدِ ۚ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَلَمُلِئْتَ مِنْهُمْ رُعْبًا','و (اگر به آنها نگاه می‌کردی) می‌پنداشتی بیدارند؛ در حالی که در خواب فرو رفته بودند! و ما آنها را به سمت راست و چپ میگرداندیم (تا بدنشان سالم بماند). و سگ آنها دستهای خود را بر دهانه غار گشوده بود (و نگهبانی می‌کرد). اگر نگاهشان می‌کردی، از آنان می‌گریختی؛ و سر تا پای تو از ترس و وحشت پر می‌شد!'];
ayat[2159]=[18,19,'وَكَذَٰلِكَ بَعَثْنَاهُمْ لِيَتَسَاءَلُوا بَيْنَهُمْ ۚ قَالَ قَائِلٌ مِّنْهُمْ كَمْ لَبِثْتُمْ ۖ قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ ۚ قَالُوا رَبُّكُمْ أَعْلَمُ بِمَا لَبِثْتُمْ فَابْعَثُوا أَحَدَكُم بِوَرِقِكُمْ هَٰذِهِ إِلَى الْمَدِينَةِ فَلْيَنظُرْ أَيُّهَا أَزْكَىٰ طَعَامًا فَلْيَأْتِكُم بِرِزْقٍ مِّنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ أَحَدًا','این‌گونه آنها را (از خواب) برانگیختیم تا از یکدیگر سؤال کنند؛ یکی از آنها گفت: «چه مدّت خوابیدید؟!» گفتند: «یک روز، یا بخشی از یک روز!» (و چون نتوانستند مدّت خوابشان را دقیقاً بدانند) گفتند: «پروردگارتان از مدّت خوابتان آگاهتر است! اکنون یک نفر از خودتان را با این سکّه‌ای که دارید به شهر بفرستید، تا بنگرد کدام یک از آنها غذای پاکیزه‌تری دارند، و مقداری از آن برای روزی شما بیاورد. امّا باید دقّت کند، و هیچ کس را از وضع شما آگاه نسازد...'];
ayat[2160]=[18,20,'إِنَّهُمْ إِن يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ أَوْ يُعِيدُوكُمْ فِي مِلَّتِهِمْ وَلَن تُفْلِحُوا إِذًا أَبَدًا','چرا که اگر آنان از وضع شما آگاه شوند، سنگسارتان می‌کنند؛ یا شما را به آیین خویش بازمی‌گردانند؛ و در آن صورت، هرگز روی رستگاری را نخواهید دید!»'];
ayat[2161]=[18,21,'وَكَذَٰلِكَ أَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوا أَنَّ وَعْدَ اللَّهِ حَقٌّ وَأَنَّ السَّاعَةَ لَا رَيْبَ فِيهَا إِذْ يَتَنَازَعُونَ بَيْنَهُمْ أَمْرَهُمْ ۖ فَقَالُوا ابْنُوا عَلَيْهِم بُنْيَانًا ۖ رَّبُّهُمْ أَعْلَمُ بِهِمْ ۚ قَالَ الَّذِينَ غَلَبُوا عَلَىٰ أَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِم مَّسْجِدًا','و اینچنین مردم را متوّجه حال آنها کردیم، تا بدانند که وعده خداوند (در مورد رستاخیز) حقّ است؛ و در پایان جهان و قیام قیامت شکّی نیست! در آن هنگام که میان خود درباره کار خویش نزاع داشتند، گروهی می‌گفتند: «بنایی بر آنان بسازید (تا برای همیشه از نظر پنهان شوند! و از آنها سخن نگویید که) پروردگارشان از وضع آنها آگاهتر است!» ولی آنها که از رازشان آگاهی یافتند (و آن را دلیلی بر رستاخیز دیدند) گفتند: «ما مسجدی در کنار (مدفن) آنها می‌سازیم (تا خاطره آنان فراموش نشود.)»'];
ayat[2162]=[18,22,'سَيَقُولُونَ ثَلَاثَةٌ رَّابِعُهُمْ كَلْبُهُمْ وَيَقُولُونَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًا بِالْغَيْبِ ۖ وَيَقُولُونَ سَبْعَةٌ وَثَامِنُهُمْ كَلْبُهُمْ ۚ قُل رَّبِّي أَعْلَمُ بِعِدَّتِهِم مَّا يَعْلَمُهُمْ إِلَّا قَلِيلٌ ۗ فَلَا تُمَارِ فِيهِمْ إِلَّا مِرَاءً ظَاهِرًا وَلَا تَسْتَفْتِ فِيهِم مِّنْهُمْ أَحَدًا','گروهی خواهند گفت: «آنها سه نفر بودند، که چهارمین آنها سگشان بود!» و گروهی می‌گویند: «پنچ نفر بودند، که ششمین آنها سگشان بود.» -همه اینها سخنانی بی‌دلیل است- و گروهی می‌گویند: «آنها هفت نفر بودند، و هشتمین آنها سگشان بود.» بگو: «پروردگار من از تعدادشان آگاهتر است!» جز گروه کمی، تعداد آنها را نمی‌دانند. پس درباره آنان جز با دلیل سخن مگو؛ و از هیچ کس درباره آنها سؤال مکن!'];
ayat[2163]=[18,23,'وَلَا تَقُولَنَّ لِشَيْءٍ إِنِّي فَاعِلٌ ذَٰلِكَ غَدًا','و هرگز در مورد کاری نگو: «من فردا آن را انجام می‌دهم»...'];
ayat[2164]=[18,24,'إِلَّا أَن يَشَاءَ اللَّهُ ۚ وَاذْكُر رَّبَّكَ إِذَا نَسِيتَ وَقُلْ عَسَىٰ أَن يَهْدِيَنِ رَبِّي لِأَقْرَبَ مِنْ هَٰذَا رَشَدًا','مگر اینکه خدا بخواهد! و هرگاه فراموش کردی، (جبران کن) و پروردگارت را به خاطر بیاور؛ و بگو: «امیدوارم که پروردگارم مرا به راهی روشنتر از این هدایت کند!»'];
ayat[2165]=[18,25,'وَلَبِثُوا فِي كَهْفِهِمْ ثَلَاثَ مِائَةٍ سِنِينَ وَازْدَادُوا تِسْعًا','آنها در غارشان سیصد سال درنگ کردند، و نه سال (نیز) بر آن افزودند.'];
ayat[2166]=[18,26,'قُلِ اللَّهُ أَعْلَمُ بِمَا لَبِثُوا ۖ لَهُ غَيْبُ السَّمَاوَاتِ وَالْأَرْضِ ۖ أَبْصِرْ بِهِ وَأَسْمِعْ ۚ مَا لَهُم مِّن دُونِهِ مِن وَلِيٍّ وَلَا يُشْرِكُ فِي حُكْمِهِ أَحَدًا','بگو: «خداوند از مدّت توقفشان آگاهتر است؛ غیب آسمانها و زمین از آن اوست! راستی چه بینا و شنواست! آنها هیچ ولیّ و سرپرستی جز او ندارند! و او هیچ کس را در حکم خود شرکت نمی‌دهد!»'];
ayat[2167]=[18,27,'وَاتْلُ مَا أُوحِيَ إِلَيْكَ مِن كِتَابِ رَبِّكَ ۖ لَا مُبَدِّلَ لِكَلِمَاتِهِ وَلَن تَجِدَ مِن دُونِهِ مُلْتَحَدًا','آنچه را از کتاب پروردگارت به تو وحی شده تلاوت کن! هیچ چیز سخنان او را دگرگون نمی‌سازد؛ و هرگز پناهگاهی جز او نمی‌یابی!'];
ayat[2168]=[18,28,'وَاصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُم بِالْغَدَاةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُ ۖ وَلَا تَعْدُ عَيْنَاكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَيَاةِ الدُّنْيَا ۖ وَلَا تُطِعْ مَنْ أَغْفَلْنَا قَلْبَهُ عَن ذِكْرِنَا وَاتَّبَعَ هَوَاهُ وَكَانَ أَمْرُهُ فُرُطًا','با کسانی باش که پروردگار خود را صبح و عصر می‌خوانند، و تنها رضای او را می‌طلبند! و هرگز بخاطر زیورهای دنیا، چشمان خود را از آنها برمگیر! و از کسانی که قلبشان را از یاد خود غافل ساختیم اطاعت مکن! همانها که از هوای نفس پیروی کردند، و کارهایشان افراطی است.'];
ayat[2169]=[18,29,'وَقُلِ الْحَقُّ مِن رَّبِّكُمْ ۖ فَمَن شَاءَ فَلْيُؤْمِن وَمَن شَاءَ فَلْيَكْفُرْ ۚ إِنَّا أَعْتَدْنَا لِلظَّالِمِينَ نَارًا أَحَاطَ بِهِمْ سُرَادِقُهَا ۚ وَإِن يَسْتَغِيثُوا يُغَاثُوا بِمَاءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَ ۚ بِئْسَ الشَّرَابُ وَسَاءَتْ مُرْتَفَقًا','بگو: «این حقّ است از سوی پروردگارتان! هر کس می‌خواهد ایمان بیاورد (و این حقیقت را پذیرا شود)، و هر کس میخواهد کافر گردد!» ما برای ستمگران آتشی آماده کردیم که سراپرده‌اش آنان را از هر سو احاطه کرده است! و اگر تقاضای آب کنند، آبی برای آنان میاورند که همچون فلز گداخته صورتها را بریان می‌کند! چه بد نوشیدنی، و چه بد محل اجتماعی است!'];
ayat[2170]=[18,30,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ إِنَّا لَا نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلًا','مسلّماً کسانی که ایمان آوردند و کارهای شایسته انجام دادند، ما پاداش نیکوکاران را ضایع نخواهیم کرد!'];
ayat[2171]=[18,31,'أُولَٰئِكَ لَهُمْ جَنَّاتُ عَدْنٍ تَجْرِي مِن تَحْتِهِمُ الْأَنْهَارُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِن ذَهَبٍ وَيَلْبَسُونَ ثِيَابًا خُضْرًا مِّن سُندُسٍ وَإِسْتَبْرَقٍ مُّتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ ۚ نِعْمَ الثَّوَابُ وَحَسُنَتْ مُرْتَفَقًا','آنها کسانی هستند که بهشت جاودان برای آنان است؛ باغهایی از بهشت که نهرها از زیر درختان و قصرهایش جاری است؛ در آنجا با دستبندهایی از طلا آراسته می‌شوند؛ و لباسهایی (فاخر) به رنگ سبز، از حریر نازک و ضخیم، دربر می‌کنند؛ در حالی که بر تختها تکیه کرده‌اند. چه پاداش خوبی، و چه جمع نیکویی!'];
ayat[2172]=[18,32,'۞ وَاضْرِبْ لَهُم مَّثَلًا رَّجُلَيْنِ جَعَلْنَا لِأَحَدِهِمَا جَنَّتَيْنِ مِنْ أَعْنَابٍ وَحَفَفْنَاهُمَا بِنَخْلٍ وَجَعَلْنَا بَيْنَهُمَا زَرْعًا','(ای پیامبر!) برای آنان مثالی بزن: آن دو مرد، که برای یکی از آنها دو باغ از انواع انگورها قرار دادیم؛ و گرداگرد آن دو (باغ) را با درختان نخل پوشاندیم؛ و در میانشان زراعت پر برکتی قراردادیم.'];
ayat[2173]=[18,33,'كِلْتَا الْجَنَّتَيْنِ آتَتْ أُكُلَهَا وَلَمْ تَظْلِم مِّنْهُ شَيْئًا ۚ وَفَجَّرْنَا خِلَالَهُمَا نَهَرًا','هر دو باغ، میوه آورده بود، (میوه‌های فراوان،) و چیزی فروگذار نکرده بود؛ و میان آن دو، نهر بزرگی جاری ساخته بودیم.'];
ayat[2174]=[18,34,'وَكَانَ لَهُ ثَمَرٌ فَقَالَ لِصَاحِبِهِ وَهُوَ يُحَاوِرُهُ أَنَا أَكْثَرُ مِنكَ مَالًا وَأَعَزُّ نَفَرًا','صاحب این باغ، درآمد فراوانی داشت؛ به همین جهت، به دوستش -در حالی که با او گفتگو می‌کرد- چنین گفت: «من از نظر ثروت از تو برتر، و از نظر نفرات نیرومندترم!»'];
ayat[2175]=[18,35,'وَدَخَلَ جَنَّتَهُ وَهُوَ ظَالِمٌ لِّنَفْسِهِ قَالَ مَا أَظُنُّ أَن تَبِيدَ هَٰذِهِ أَبَدًا','و در حالی که نسبت به خود ستمکار بود، در باغ خویش گام نهاد، و گفت: «من گمان نمی‌کنم هرگز این باغ نابود شود!'];
ayat[2176]=[18,36,'وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِن رُّدِدتُّ إِلَىٰ رَبِّي لَأَجِدَنَّ خَيْرًا مِّنْهَا مُنقَلَبًا','و باور نمی‌کنم قیامت برپا گردد! و اگر به سوی پروردگارم بازگردانده شوم (و قیامتی در کار باشد)، جایگاهی بهتر از این جا خواهم یافت!»'];
ayat[2177]=[18,37,'قَالَ لَهُ صَاحِبُهُ وَهُوَ يُحَاوِرُهُ أَكَفَرْتَ بِالَّذِي خَلَقَكَ مِن تُرَابٍ ثُمَّ مِن نُّطْفَةٍ ثُمَّ سَوَّاكَ رَجُلًا','دوست (با ایمان) وی -در حالی که با او گفتگو می‌کرد- گفت: «آیا به خدایی که تو را از خاک، و سپس از نطفه آفرید، و پس از آن تو را مرد کاملی قرار داد، کافر شدی؟!'];
ayat[2178]=[18,38,'لَّٰكِنَّا هُوَ اللَّهُ رَبِّي وَلَا أُشْرِكُ بِرَبِّي أَحَدًا','ولی من کسی هستم که «اللّه» پروردگار من است؛ و هیچ کس را شریک پروردگارم قرارنمی‌دهم!'];
ayat[2179]=[18,39,'وَلَوْلَا إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاءَ اللَّهُ لَا قُوَّةَ إِلَّا بِاللَّهِ ۚ إِن تَرَنِ أَنَا أَقَلَّ مِنكَ مَالًا وَوَلَدًا','چرا هنگامی که وارد باغت شدی، نگفتی این نعمتی است که خدا خواسته است؟! قوّت (و نیرویی) جز از ناحیه خدا نیست! و اگر می‌بینی من از نظر مال و فرزند از تو کمترم (مطلب مهمّی نیست)!'];
ayat[2180]=[18,40,'فَعَسَىٰ رَبِّي أَن يُؤْتِيَنِ خَيْرًا مِّن جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَانًا مِّنَ السَّمَاءِ فَتُصْبِحَ صَعِيدًا زَلَقًا','شاید پروردگارم بهتر از باغ تو به من بدهد؛ و مجازات حساب شده‌ای از آسمان بر باغ تو فروفرستد، بگونه‌ای که آن را به زمین بی‌گیاه لغزنده‌ای مبدّل کند!'];
ayat[2181]=[18,41,'أَوْ يُصْبِحَ مَاؤُهَا غَوْرًا فَلَن تَسْتَطِيعَ لَهُ طَلَبًا','و یا آب آن در اعمال زمین فرو رود، آن گونه که هرگز نتوانی آن را به دست آوری!»'];
ayat[2182]=[18,42,'وَأُحِيطَ بِثَمَرِهِ فَأَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلَىٰ مَا أَنفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَيَقُولُ يَا لَيْتَنِي لَمْ أُشْرِكْ بِرَبِّي أَحَدًا','(به هر حال عذاب الهی فرا رسید،) و تمام میوه‌های آن نابود شد؛ و او بخاطر هزینه هایی که در آن صرف کرده بود، پیوسته دستهای خود را به هم می‌مالید -در حالی که تمام باغ بر داربستهایش فرو ریخته بود- و می‌گفت: «ای کاش کسی را همتای پروردگارم قرار نداده بودم!»'];
ayat[2183]=[18,43,'وَلَمْ تَكُن لَّهُ فِئَةٌ يَنصُرُونَهُ مِن دُونِ اللَّهِ وَمَا كَانَ مُنتَصِرًا','و گروهی نداشت که او را در برابر (عذاب) خداوند یاری دهند؛ و از خودش (نیز) نمی‌توانست یاری گیرد.'];
ayat[2184]=[18,44,'هُنَالِكَ الْوَلَايَةُ لِلَّهِ الْحَقِّ ۚ هُوَ خَيْرٌ ثَوَابًا وَخَيْرٌ عُقْبًا','در آنجا ثابت شد که ولایت (و قدرت) از آن خداوند بر حق است! اوست که برترین ثواب، و بهترین عاقبت را (برای مطیعان) دارد!'];
ayat[2185]=[18,45,'وَاضْرِبْ لَهُم مَّثَلَ الْحَيَاةِ الدُّنْيَا كَمَاءٍ أَنزَلْنَاهُ مِنَ السَّمَاءِ فَاخْتَلَطَ بِهِ نَبَاتُ الْأَرْضِ فَأَصْبَحَ هَشِيمًا تَذْرُوهُ الرِّيَاحُ ۗ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ مُّقْتَدِرًا','(ای پیامبر!) زندگی دنیا را برای آنان به آبی تشبیه کن که از آسمان فرو می‌فرستیم؛ و بوسیله آن، گیاهان زمین (سرسبز می‌شود و) در هم فرومیرود. امّا بعد از مدتی می‌خشکد؛ و بادها آن را به هر سو پراکنده می‌کند؛ و خداوند بر همه چیز تواناست!'];
ayat[2186]=[18,46,'الْمَالُ وَالْبَنُونَ زِينَةُ الْحَيَاةِ الدُّنْيَا ۖ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِندَ رَبِّكَ ثَوَابًا وَخَيْرٌ أَمَلًا','مال و فرزند، زینت زندگی دنیاست؛ و باقیات صالحات [= ارزشهای پایدار و شایسته‌] ثوابش نزد پروردگارت بهتر و امیدبخش‌تر است!'];
ayat[2187]=[18,47,'وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْأَرْضَ بَارِزَةً وَحَشَرْنَاهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ أَحَدًا','و روزی را به خاطر بیاور که کوه‌ها را به حرکت درآوریم؛ و زمین را آشکار (و مسطح) می‌بینی؛ و همه آنان [= انسانها] را برمی‌انگیزیم، و احدی از ایشان را فروگذار نخواهیم کرد!'];
ayat[2188]=[18,48,'وَعُرِضُوا عَلَىٰ رَبِّكَ صَفًّا لَّقَدْ جِئْتُمُونَا كَمَا خَلَقْنَاكُمْ أَوَّلَ مَرَّةٍ ۚ بَلْ زَعَمْتُمْ أَلَّن نَّجْعَلَ لَكُم مَّوْعِدًا','آنها همه در یک صف به (پیشگاه) پروردگارت عرضه می‌شوند؛ (و به آنان گفته می‌شود:) همگی نزد ما آمدید، همان گونه که نخستین بار شما را آفریدیم! اما شما گمان می‌کردید ما هرگز موعدی برایتان قرار نخواهیم داد!'];
ayat[2189]=[18,49,'وَوُضِعَ الْكِتَابُ فَتَرَى الْمُجْرِمِينَ مُشْفِقِينَ مِمَّا فِيهِ وَيَقُولُونَ يَا وَيْلَتَنَا مَالِ هَٰذَا الْكِتَابِ لَا يُغَادِرُ صَغِيرَةً وَلَا كَبِيرَةً إِلَّا أَحْصَاهَا ۚ وَوَجَدُوا مَا عَمِلُوا حَاضِرًا ۗ وَلَا يَظْلِمُ رَبُّكَ أَحَدًا','و کتاب [= کتابی که نامه اعمال همه انسانهاست‌] در آن جا گذارده می‌شود، پس گنهکاران را می‌بینی که از آنچه در آن است، ترسان و هراسانند؛ و می‌گویند: «ای وای بر ما! این چه کتابی است که هیچ عمل کوچک و بزرگی را فرونگذاشته مگر اینکه آن را به شمار آورده است؟! و (این در حالی است که) همه اعمال خود را حاضر می‌بینند؛ و پروردگارت به هیچ کس ستم نمی‌کند.'];
ayat[2190]=[18,50,'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ أَمْرِ رَبِّهِ ۗ أَفَتَتَّخِذُونَهُ وَذُرِّيَّتَهُ أَوْلِيَاءَ مِن دُونِي وَهُمْ لَكُمْ عَدُوٌّ ۚ بِئْسَ لِلظَّالِمِينَ بَدَلًا','به یاد آرید زمانی را که به فرشتگان گفتیم: «برای آدم سجده کنید!» آنها همگی سجده کردند جز ابلیس -که از جن بود- و از فرمان پروردگارش بیرون شد آیا (با این حال،) او و فرزندانش را به جای من اولیای خود انتخاب می‌کنید، در حالی که آنها دشمن شما هستند؟! (فرمانبرداری از شیطان و فرزندانش به جای اطاعت خدا،) چه جایگزینی بدی است برای ستمکاران!'];
ayat[2191]=[18,51,'۞ مَّا أَشْهَدتُّهُمْ خَلْقَ السَّمَاوَاتِ وَالْأَرْضِ وَلَا خَلْقَ أَنفُسِهِمْ وَمَا كُنتُ مُتَّخِذَ الْمُضِلِّينَ عَضُدًا','من هرگز آنها [= ابلیس و فرزندانش‌] را به هنگام آفرینش آسمانها و زمین، و نه به هنگام آفرینش خودشان، حاضر نساختم! و من هیچ گاه گمراه‌کنندگان را دستیار خود قرار نمی‌دهم!'];
ayat[2192]=[18,52,'وَيَوْمَ يَقُولُ نَادُوا شُرَكَائِيَ الَّذِينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَجَعَلْنَا بَيْنَهُم مَّوْبِقًا','به خاطر بیاورید روزی را که (خداوند) می‌گوید: «همتایانی را که برای من می‌پنداشتید، بخوانید (تا به کمک شما بشتابند!)» ولی هر چه آنها را می‌خوانند، جوابشان نمی‌دهند؛ و در میان این دو گروه، کانون هلاکتی قرارداده‌ایم!'];
ayat[2193]=[18,53,'وَرَأَى الْمُجْرِمُونَ النَّارَ فَظَنُّوا أَنَّهُم مُّوَاقِعُوهَا وَلَمْ يَجِدُوا عَنْهَا مَصْرِفًا','و گنهکاران، آتش (دوزخ) را می‌بینند؛ و یقین می‌کنند که با آن درمی‌آمیزند، و هیچ گونه راه گریزی از آن نخواهند یافت.'];
ayat[2194]=[18,54,'وَلَقَدْ صَرَّفْنَا فِي هَٰذَا الْقُرْآنِ لِلنَّاسِ مِن كُلِّ مَثَلٍ ۚ وَكَانَ الْإِنسَانُ أَكْثَرَ شَيْءٍ جَدَلًا','و در این قرآن، از هر گونه مثلی برای مردم بیان کرده‌ایم؛ ولی انسان بیش از هر چیز، به مجادله می‌پردازد!'];
ayat[2195]=[18,55,'وَمَا مَنَعَ النَّاسَ أَن يُؤْمِنُوا إِذْ جَاءَهُمُ الْهُدَىٰ وَيَسْتَغْفِرُوا رَبَّهُمْ إِلَّا أَن تَأْتِيَهُمْ سُنَّةُ الْأَوَّلِينَ أَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلًا','و چیزی مردم را بازنداشت از اینکه -وقتی هدایت به سراغشان آمد- ایمان بیاورند و از پروردگارشان طلب آمرزش کنند، جز اینکه (خیره‌سری کردند؛ گویی می‌خواستند) سرنوشت پیشینیان برای آنان بیاید، یا عذاب (الهی) در برابرشان قرار گیرد!'];
ayat[2196]=[18,56,'وَمَا نُرْسِلُ الْمُرْسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَ ۚ وَيُجَادِلُ الَّذِينَ كَفَرُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ ۖ وَاتَّخَذُوا آيَاتِي وَمَا أُنذِرُوا هُزُوًا','ما پیامبران را، جز بعنوان بشارت دهنده و انذار کننده، نمی‌فرستیم؛ اما کافران همواره مجادله به باطل می‌کنند، تا (به گمان خود،) حق را بوسیله آن از میان بردارند! و آیات ما، و مجازاتهایی را که به آنان وعده داده شده است، به باد مسخره گرفتند!'];
ayat[2197]=[18,57,'وَمَنْ أَظْلَمُ مِمَّن ذُكِّرَ بِآيَاتِ رَبِّهِ فَأَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُ ۚ إِنَّا جَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَن يَفْقَهُوهُ وَفِي آذَانِهِمْ وَقْرًا ۖ وَإِن تَدْعُهُمْ إِلَى الْهُدَىٰ فَلَن يَهْتَدُوا إِذًا أَبَدًا','چه کسی ستمکارتر است از آن کس که آیات پروردگارش به او تذکّر داده شده، و از آن روی گرداند، و آنچه را با دستهای خود پیش فرستاد فراموش کرد؟! ما بر دلهای اینها پرده‌هایی افکنده‌ایم تا نفهمند؛ و در گوشهایشان سنگینی قرار داده‌ایم (تا صدای حق را نشنوند)! و از این رو اگر آنها را به سوی هدایت بخوانی، هرگز هدایت نمی‌شوند!'];
ayat[2198]=[18,58,'وَرَبُّكَ الْغَفُورُ ذُو الرَّحْمَةِ ۖ لَوْ يُؤَاخِذُهُم بِمَا كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذَابَ ۚ بَل لَّهُم مَّوْعِدٌ لَّن يَجِدُوا مِن دُونِهِ مَوْئِلًا','و پروردگارت، آمرزنده و صاحب رحمت است؛ اگر می‌خواست آنان را به خاطر اعمالشان مجازات کند، عذاب را هر چه زودتر برای آنها می‌فرستاد؛ ولی برای آنان موعدی است که هرگز از آن راه فراری نخواهند داشت!'];
ayat[2199]=[18,59,'وَتِلْكَ الْقُرَىٰ أَهْلَكْنَاهُمْ لَمَّا ظَلَمُوا وَجَعَلْنَا لِمَهْلِكِهِم مَّوْعِدًا','این شهرها و آبادیهایی است که ما آنها را هنگامی که ستم کردند هلاک نمودیم؛ و برای هلاکتشان موعدی قرار دادیم! (آنها ویرانه‌هایش را با چشم می‌بینند، و عبرت نمی‌گیرند!)'];
ayat[2200]=[18,60,'وَإِذْ قَالَ مُوسَىٰ لِفَتَاهُ لَا أَبْرَحُ حَتَّىٰ أَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ أَوْ أَمْضِيَ حُقُبًا','به خاطر بیاور هنگامی را که موسی به دوست خود گفت: دست از جستجو برنمی‌دارم تا به محل تلاقی دو دریا برسم؛ هر چند مدت طولانی به راه خود ادامه دهم!'];
ayat[2201]=[18,61,'فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوتَهُمَا فَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ سَرَبًا','(ولی) هنگامی که به محل تلاقی آن دو دریا رسیدند، ماهی خود را (که برای تغذیه همراه داشتند) فراموش کردند؛ و ماهی راه خود را در دریا پیش گرفت (و روان شد).'];
ayat[2202]=[18,62,'فَلَمَّا جَاوَزَا قَالَ لِفَتَاهُ آتِنَا غَدَاءَنَا لَقَدْ لَقِينَا مِن سَفَرِنَا هَٰذَا نَصَبًا','هنگامی که از آن جا گذشتند، (موسی) به یار همسفرش گفت: «غذای ما را بیاور، که سخت از این سفر خسته شده‌ایم!»'];
ayat[2203]=[18,63,'قَالَ أَرَأَيْتَ إِذْ أَوَيْنَا إِلَى الصَّخْرَةِ فَإِنِّي نَسِيتُ الْحُوتَ وَمَا أَنسَانِيهُ إِلَّا الشَّيْطَانُ أَنْ أَذْكُرَهُ ۚ وَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ عَجَبًا','گفت: «به خاطر داری هنگامی که ما (برای استراحت) به کنار آن صخره پناه بردیم، من (در آن جا) فراموش کردم جریان ماهی را بازگو کنم -و فقط شیطان بود که آن را از خاطر من برد- و ماهی بطرز شگفت‌آوری راه خود را در دریا پیش گرفت!»'];
ayat[2204]=[18,64,'قَالَ ذَٰلِكَ مَا كُنَّا نَبْغِ ۚ فَارْتَدَّا عَلَىٰ آثَارِهِمَا قَصَصًا','(موسی) گفت: «آن همان بود که ما می‌خواستیم!» سپس از همان راه بازگشتند، در حالی که پی‌جویی می‌کردند.'];
ayat[2205]=[18,65,'فَوَجَدَا عَبْدًا مِّنْ عِبَادِنَا آتَيْنَاهُ رَحْمَةً مِّنْ عِندِنَا وَعَلَّمْنَاهُ مِن لَّدُنَّا عِلْمًا','(در آن جا) بنده‌ای از بندگان ما را یافتند که رحمت (و موهبت عظیمی) از سوی خود به او داده، و علم فراوانی از نزد خود به او آموخته بودیم.'];
ayat[2206]=[18,66,'قَالَ لَهُ مُوسَىٰ هَلْ أَتَّبِعُكَ عَلَىٰ أَن تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا','موسی به او گفت: «آیا از تو پیروی کنم تا از آنچه به تو تعلیم داده شده و مایه رشد و صلاح است، به من بیاموزی؟»'];
ayat[2207]=[18,67,'قَالَ إِنَّكَ لَن تَسْتَطِيعَ مَعِيَ صَبْرًا','گفت: «تو هرگز نمی‌توانی با من شکیبایی کنی!'];
ayat[2208]=[18,68,'وَكَيْفَ تَصْبِرُ عَلَىٰ مَا لَمْ تُحِطْ بِهِ خُبْرًا','و چگونه می‌توانی در برابر چیزی که از رموزش آگاه نیستی شکیبا باشی؟!'];
ayat[2209]=[18,69,'قَالَ سَتَجِدُنِي إِن شَاءَ اللَّهُ صَابِرًا وَلَا أَعْصِي لَكَ أَمْرًا','(موسی) گفت: «به خواست خدا مرا شکیبا خواهی یافت؛ و در هیچ کاری مخالفت فرمان تو نخواهم کرد!»'];
ayat[2210]=[18,70,'قَالَ فَإِنِ اتَّبَعْتَنِي فَلَا تَسْأَلْنِي عَن شَيْءٍ حَتَّىٰ أُحْدِثَ لَكَ مِنْهُ ذِكْرًا','(خضر) گفت: «پس اگر می‌خواهی بدنبال من بیایی، از هیچ چیز مپرس تا خودم (به موقع) آن را برای تو بازگو کنم.»'];
ayat[2211]=[18,71,'فَانطَلَقَا حَتَّىٰ إِذَا رَكِبَا فِي السَّفِينَةِ خَرَقَهَا ۖ قَالَ أَخَرَقْتَهَا لِتُغْرِقَ أَهْلَهَا لَقَدْ جِئْتَ شَيْئًا إِمْرًا','آن دو به راه افتادند؛ تا آن که سوار کشتی شدند، (خضر) کشتی را سوراخ کرد. (موسی) گفت: «آیا آن را سوراخ کردی که اهلش را غرق کنی؟! راستی که چه کار بدی انجام دادی!»'];
ayat[2212]=[18,72,'قَالَ أَلَمْ أَقُلْ إِنَّكَ لَن تَسْتَطِيعَ مَعِيَ صَبْرًا','گفت: «آیا نگفتم تو هرگز نمی‌توانی با من شکیبایی کنی؟!»'];
ayat[2213]=[18,73,'قَالَ لَا تُؤَاخِذْنِي بِمَا نَسِيتُ وَلَا تُرْهِقْنِي مِنْ أَمْرِي عُسْرًا','(موسی) گفت: «مرا بخاطر این فراموشکاریم مؤاخذه مکن و از این کارم بر من سخت مگیر!»'];
ayat[2214]=[18,74,'فَانطَلَقَا حَتَّىٰ إِذَا لَقِيَا غُلَامًا فَقَتَلَهُ قَالَ أَقَتَلْتَ نَفْسًا زَكِيَّةً بِغَيْرِ نَفْسٍ لَّقَدْ جِئْتَ شَيْئًا نُّكْرًا','باز به راه خود ادامه دادند، تا اینکه نوجوانی را دیدند؛ و او آن نوجوان را کشت. (موسی) گفت: «آیا انسان پاکی را، بی آنکه قتلی کرده باشد، کشتی؟! براستی کار زشتی انجام دادی!»'];
ayat[2215]=[18,75,'۞ قَالَ أَلَمْ أَقُل لَّكَ إِنَّكَ لَن تَسْتَطِيعَ مَعِيَ صَبْرًا','(باز آن مرد عالم) گفت: «آیا به تو نگفتم که تو هرگز نمی‌توانی با من صبر کنی؟!»'];
ayat[2216]=[18,76,'قَالَ إِن سَأَلْتُكَ عَن شَيْءٍ بَعْدَهَا فَلَا تُصَاحِبْنِي ۖ قَدْ بَلَغْتَ مِن لَّدُنِّي عُذْرًا','(موسی) گفت: «بعد از این اگر درباره چیزی از تو سؤال کردم، دیگر با من همراهی نکن؛ (زیرا) از سوی من معذور خواهی بود!»'];
ayat[2217]=[18,77,'فَانطَلَقَا حَتَّىٰ إِذَا أَتَيَا أَهْلَ قَرْيَةٍ اسْتَطْعَمَا أَهْلَهَا فَأَبَوْا أَن يُضَيِّفُوهُمَا فَوَجَدَا فِيهَا جِدَارًا يُرِيدُ أَن يَنقَضَّ فَأَقَامَهُ ۖ قَالَ لَوْ شِئْتَ لَاتَّخَذْتَ عَلَيْهِ أَجْرًا','باز به راه خود ادامه دادند تا به مردم قریه‌ای رسیدند؛ از آنان خواستند که به ایشان غذا دهند؛ ولی آنان از مهمان کردنشان خودداری نمودند؛ (با این حال) در آن جا دیواری یافتند که می‌خواست فروریزد؛ و (آن مرد عالم) آن را برپا داشت. (موسی) گفت: «(لااقل) می‌خواستی در مقابل این کار مزدی بگیری!»'];
ayat[2218]=[18,78,'قَالَ هَٰذَا فِرَاقُ بَيْنِي وَبَيْنِكَ ۚ سَأُنَبِّئُكَ بِتَأْوِيلِ مَا لَمْ تَسْتَطِع عَّلَيْهِ صَبْرًا','او گفت: «اینک زمان جدایی من و تو فرا رسیده؛ اما بزودی راز آنچه را که نتوانستی در برابر آن صبر کنی، به تو خبر می‌دهم.'];
ayat[2219]=[18,79,'أَمَّا السَّفِينَةُ فَكَانَتْ لِمَسَاكِينَ يَعْمَلُونَ فِي الْبَحْرِ فَأَرَدتُّ أَنْ أَعِيبَهَا وَكَانَ وَرَاءَهُم مَّلِكٌ يَأْخُذُ كُلَّ سَفِينَةٍ غَصْبًا','اما آن کشتی مال گروهی از مستمندان بود که با آن در دریا کار می‌کردند؛ و من خواستم آن را معیوب کنم؛ (چرا که) پشت سرشان پادشاهی (ستمگر) بود که هر کشتی (سالمی) را بزور میگرفت!'];
ayat[2220]=[18,80,'وَأَمَّا الْغُلَامُ فَكَانَ أَبَوَاهُ مُؤْمِنَيْنِ فَخَشِينَا أَن يُرْهِقَهُمَا طُغْيَانًا وَكُفْرًا','و اما آن نوجوان، پدر و مادرش با ایمان بودند؛ و بیم داشتیم که آنان را به طغیان و کفر وادارد!'];
ayat[2221]=[18,81,'فَأَرَدْنَا أَن يُبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِّنْهُ زَكَاةً وَأَقْرَبَ رُحْمًا','از این رو، خواستیم که پروردگارشان به جای او، فرزندی پاکتر و با محبت‌تر به آن دو بدهد!'];
ayat[2222]=[18,82,'وَأَمَّا الْجِدَارُ فَكَانَ لِغُلَامَيْنِ يَتِيمَيْنِ فِي الْمَدِينَةِ وَكَانَ تَحْتَهُ كَنزٌ لَّهُمَا وَكَانَ أَبُوهُمَا صَالِحًا فَأَرَادَ رَبُّكَ أَن يَبْلُغَا أَشُدَّهُمَا وَيَسْتَخْرِجَا كَنزَهُمَا رَحْمَةً مِّن رَّبِّكَ ۚ وَمَا فَعَلْتُهُ عَنْ أَمْرِي ۚ ذَٰلِكَ تَأْوِيلُ مَا لَمْ تَسْطِع عَّلَيْهِ صَبْرًا','و اما آن دیوار، از آن دو نوجوان یتیم در آن شهر بود؛ و زیر آن، گنجی متعلق به آن دو وجود داشت؛ و پدرشان مرد صالحی بود؛ و پروردگار تو می‌خواست آنها به حد بلوغ برسند و گنجشان را استخراج کنند؛ این رحمتی از پروردگارت بود؛ و من آن (کارها) را خودسرانه انجام ندادم؛ این بود راز کارهایی که نتوانستی در برابر آنها شکیبایی به خرج دهی!»'];
ayat[2223]=[18,83,'وَيَسْأَلُونَكَ عَن ذِي الْقَرْنَيْنِ ۖ قُلْ سَأَتْلُو عَلَيْكُم مِّنْهُ ذِكْرًا','و از تو درباره «ذو القرنین» می‌پرسند؛ بگو: «بزودی بخشی از سرگذشت او را برای شما بازگو خواهم کرد.»'];
ayat[2224]=[18,84,'إِنَّا مَكَّنَّا لَهُ فِي الْأَرْضِ وَآتَيْنَاهُ مِن كُلِّ شَيْءٍ سَبَبًا','ما به او در روی زمین، قدرت و حکومت دادیم؛ و اسباب هر چیز را در اختیارش گذاشتیم.'];
ayat[2225]=[18,85,'فَأَتْبَعَ سَبَبًا','او از این اسباب، (پیروی و استفاده) کرد...'];
ayat[2226]=[18,86,'حَتَّىٰ إِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِي عَيْنٍ حَمِئَةٍ وَوَجَدَ عِندَهَا قَوْمًا ۗ قُلْنَا يَا ذَا الْقَرْنَيْنِ إِمَّا أَن تُعَذِّبَ وَإِمَّا أَن تَتَّخِذَ فِيهِمْ حُسْنًا','تا به غروبگاه آفتاب رسید؛ (در آن جا) احساس کرد (و در نظرش مجسّم شد) که خورشید در چشمه تیره و گل‌آلودی فرو می‌رود؛ و در آن جا قومی را یافت؛ گفتیم: «ای ذو القرنین! آیا می‌خواهی (آنان) را مجازات کنی، و یا روش نیکویی در مورد آنها انتخاب نمایی؟»'];
ayat[2227]=[18,87,'قَالَ أَمَّا مَن ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ إِلَىٰ رَبِّهِ فَيُعَذِّبُهُ عَذَابًا نُّكْرًا','گفت: «اما کسی را که ستم کرده است، مجازات خواهیم کرد؛ سپس به سوی پروردگارش بازمی‌گردد، و خدا او را مجازات شدیدی خواهد کرد!'];
ayat[2228]=[18,88,'وَأَمَّا مَنْ آمَنَ وَعَمِلَ صَالِحًا فَلَهُ جَزَاءً الْحُسْنَىٰ ۖ وَسَنَقُولُ لَهُ مِنْ أَمْرِنَا يُسْرًا','و اما کسی که ایمان آورد و عمل صالح انجام دهد، پاداشی نیکوتر خواهد داشت؛ و ما دستور آسانی به او خواهیم داد.»'];
ayat[2229]=[18,89,'ثُمَّ أَتْبَعَ سَبَبًا','سپس (بار دیگر) از اسبابی (که در اختیار داشت) بهره گرفت...'];
ayat[2230]=[18,90,'حَتَّىٰ إِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلَىٰ قَوْمٍ لَّمْ نَجْعَل لَّهُم مِّن دُونِهَا سِتْرًا','تا به خاستگاه خورشید رسید؛ (در آن جا) دید خورشید بر جمعیّتی طلوع می‌کند که در برابر (تابش) آفتاب، پوششی برای آنها قرار نداده بودیم (و هیچ گونه سایبانی نداشتند).'];
ayat[2231]=[18,91,'كَذَٰلِكَ وَقَدْ أَحَطْنَا بِمَا لَدَيْهِ خُبْرًا','(آری) اینچنین بود (کار ذو القرنین)! و ما بخوبی از امکاناتی که نزد او بود آگاه بودیم!'];
ayat[2232]=[18,92,'ثُمَّ أَتْبَعَ سَبَبًا','(باز) از اسباب مهمّی (که در اختیار داشت) استفاده کرد...'];
ayat[2233]=[18,93,'حَتَّىٰ إِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِن دُونِهِمَا قَوْمًا لَّا يَكَادُونَ يَفْقَهُونَ قَوْلًا','(و همچنان به راه خود ادامه داد) تا به میان دو کوه رسید؛ و در کنار آن دو (کوه) قومی را یافت که هیچ سخنی را نمی‌فهمیدند (و زبانشان مخصوص خودشان بود)!'];
ayat[2234]=[18,94,'قَالُوا يَا ذَا الْقَرْنَيْنِ إِنَّ يَأْجُوجَ وَمَأْجُوجَ مُفْسِدُونَ فِي الْأَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا عَلَىٰ أَن تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا','(آن گروه به او) گفتند: «ای ذو القرنین یأجوج و مأجوج در این سرزمین فساد می‌کنند؛ آیا ممکن است ما هزینه‌ای برای تو قرار دهیم، که میان ما و آنها سدّی ایجاد کنی؟!»'];
ayat[2235]=[18,95,'قَالَ مَا مَكَّنِّي فِيهِ رَبِّي خَيْرٌ فَأَعِينُونِي بِقُوَّةٍ أَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا','(ذو القرنین) گفت: «آنچه پروردگارم در اختیار من گذارده، بهتر است (از آنچه شما پیشنهاد می‌کنید)! مرا با نیرویی یاری دهید، تا میان شما و آنها سدّ محکمی قرار دهم!'];
ayat[2236]=[18,96,'آتُونِي زُبَرَ الْحَدِيدِ ۖ حَتَّىٰ إِذَا سَاوَىٰ بَيْنَ الصَّدَفَيْنِ قَالَ انفُخُوا ۖ حَتَّىٰ إِذَا جَعَلَهُ نَارًا قَالَ آتُونِي أُفْرِغْ عَلَيْهِ قِطْرًا','قطعات بزرگ آهن برایم بیاورید (و آنها را روی هم بچینید)!» تا وقتی که کاملاً میان دو کوه را پوشانید، گفت: «(در اطراف آن آتش بیفروزید، و) در آن بدمید!» (آنها دمیدند) تا قطعات آهن را سرخ و گداخته کرد، و گفت: «(اکنون) مس مذاب برایم بیاورید تا بر روی آن بریزم!»'];
ayat[2237]=[18,97,'فَمَا اسْطَاعُوا أَن يَظْهَرُوهُ وَمَا اسْتَطَاعُوا لَهُ نَقْبًا','(سرانجام چنان سدّ نیرومندی ساخت) که آنها [= طایفه یأجوج و مأجوج‌] قادر نبودند از آن بالا روند؛ و نمی‌توانستند نقبی در آن ایجاد کنند.'];
ayat[2238]=[18,98,'قَالَ هَٰذَا رَحْمَةٌ مِّن رَّبِّي ۖ فَإِذَا جَاءَ وَعْدُ رَبِّي جَعَلَهُ دَكَّاءَ ۖ وَكَانَ وَعْدُ رَبِّي حَقًّا','(آنگاه) گفت: «این از رحمت پروردگار من است! امّا هنگامی که وعده پروردگارم فرا رسد، آن را در هم می‌کوبد؛ و وعده پروردگارم حق است!»'];
ayat[2239]=[18,99,'۞ وَتَرَكْنَا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ فِي بَعْضٍ ۖ وَنُفِخَ فِي الصُّورِ فَجَمَعْنَاهُمْ جَمْعًا','و در آن روز (که جهان پایان می‌گیرد)، ما آنان را چنان رها می‌کنیم که درهم موج می‌زنند؛ و در صور [= شیپور] دمیده می‌شود؛ و ما همه را جمع می‌کنیم!'];
ayat[2240]=[18,100,'وَعَرَضْنَا جَهَنَّمَ يَوْمَئِذٍ لِّلْكَافِرِينَ عَرْضًا','در آن روز، جهنم را بر کافران عرضه می‌داریم!'];
ayat[2241]=[18,101,'الَّذِينَ كَانَتْ أَعْيُنُهُمْ فِي غِطَاءٍ عَن ذِكْرِي وَكَانُوا لَا يَسْتَطِيعُونَ سَمْعًا','همانها که پرده‌ای چشمانشان را از یاد من پوشانده بود، و قدرت شنوایی نداشتند!'];
ayat[2242]=[18,102,'أَفَحَسِبَ الَّذِينَ كَفَرُوا أَن يَتَّخِذُوا عِبَادِي مِن دُونِي أَوْلِيَاءَ ۚ إِنَّا أَعْتَدْنَا جَهَنَّمَ لِلْكَافِرِينَ نُزُلًا','آیا کافران پنداشتند می‌توانند بندگانم را به جای من اولیای خود انتخاب کنند؟! ما جهنم را برای پذیرایی کافران آماده کرده‌ایم!'];
ayat[2243]=[18,103,'قُلْ هَلْ نُنَبِّئُكُم بِالْأَخْسَرِينَ أَعْمَالًا','بگو: «آیا به شما خبر دهیم که زیانکارترین (مردم) در کارها، چه کسانی هستند؟'];
ayat[2244]=[18,104,'الَّذِينَ ضَلَّ سَعْيُهُمْ فِي الْحَيَاةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صُنْعًا','آنها که تلاشهایشان در زندگی دنیا گم (و نابود) شده؛ با این حال، می‌پندارند کار نیک انجام می‌دهند!»'];
ayat[2245]=[18,105,'أُولَٰئِكَ الَّذِينَ كَفَرُوا بِآيَاتِ رَبِّهِمْ وَلِقَائِهِ فَحَبِطَتْ أَعْمَالُهُمْ فَلَا نُقِيمُ لَهُمْ يَوْمَ الْقِيَامَةِ وَزْنًا','آنها کسانی هستند که به آیات پروردگارشان و لقای او کافر شدند؛ به همین جهت، اعمالشان حبط و نابود شد! از این رو روز قیامت، میزانی برای آنها برپا نخواهیم کرد!'];
ayat[2246]=[18,106,'ذَٰلِكَ جَزَاؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوا وَاتَّخَذُوا آيَاتِي وَرُسُلِي هُزُوًا','(آری،) این گونه است! کیفرشان دوزخ است، بخاطر آنکه کافر شدند، و آیات من و پیامبرانم را به سخریه گرفتند!'];
ayat[2247]=[18,107,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَانَتْ لَهُمْ جَنَّاتُ الْفِرْدَوْسِ نُزُلًا','امّا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، باغهای بهشت برین محل پذیرایی آنان خواهد بود.'];
ayat[2248]=[18,108,'خَالِدِينَ فِيهَا لَا يَبْغُونَ عَنْهَا حِوَلًا','آنها جاودانه در آن خواهند ماند؛ و هرگز تقاضای نقل مکان از آن جا نمی‌کنند!'];
ayat[2249]=[18,109,'قُل لَّوْ كَانَ الْبَحْرُ مِدَادًا لِّكَلِمَاتِ رَبِّي لَنَفِدَ الْبَحْرُ قَبْلَ أَن تَنفَدَ كَلِمَاتُ رَبِّي وَلَوْ جِئْنَا بِمِثْلِهِ مَدَدًا','بگو: «اگر دریاها برای (نوشتن) کلمات پروردگارم مرکّب شود، دریاها پایان می‌گیرد. پیش از آنکه کلمات پروردگارم پایان یابد؛ هر چند همانند آن (دریاها) را کمک آن قرار دهیم!»'];
ayat[2250]=[18,110,'قُلْ إِنَّمَا أَنَا بَشَرٌ مِّثْلُكُمْ يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ فَمَن كَانَ يَرْجُو لِقَاءَ رَبِّهِ فَلْيَعْمَلْ عَمَلًا صَالِحًا وَلَا يُشْرِكْ بِعِبَادَةِ رَبِّهِ أَحَدًا','بگو: «من فقط بشری هستم مثل شما؛ (امتیازم این است که) به من وحی می‌شود که تنها معبودتان معبود یگانه است؛ پس هر که به لقای پروردگارش امید دارد، باید کاری شایسته انجام دهد، و هیچ کس را در عبادت پروردگارش شریک نکند!'];
ayat[2251]=[19,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ كهيعص','کهیعص‌'];
ayat[2252]=[19,2,'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُ زَكَرِيَّا','(این) یادی است از رحمت پروردگار تو نسبت به بنده‌اش زکریا...'];
ayat[2253]=[19,3,'إِذْ نَادَىٰ رَبَّهُ نِدَاءً خَفِيًّا','در آن هنگام که پروردگارش را در خلوتگاه (عبادت) پنهان خواند...'];
ayat[2254]=[19,4,'قَالَ رَبِّ إِنِّي وَهَنَ الْعَظْمُ مِنِّي وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَلَمْ أَكُن بِدُعَائِكَ رَبِّ شَقِيًّا','گفت: «پروردگارا! استخوانم سست شده؛ و شعله پیری تمام سرم را فراگرفته؛ و من هرگز در دعای تو، از اجابت محروم نبوده‌ام!'];
ayat[2255]=[19,5,'وَإِنِّي خِفْتُ الْمَوَالِيَ مِن وَرَائِي وَكَانَتِ امْرَأَتِي عَاقِرًا فَهَبْ لِي مِن لَّدُنكَ وَلِيًّا','و من از بستگانم بعد از خودم بیمناکم (که حق پاسداری از آیین تو را نگاه ندارند)! و (از طرفی) همسرم نازا و عقیم است؛ تو از نزد خود جانشینی به من ببخش...'];
ayat[2256]=[19,6,'يَرِثُنِي وَيَرِثُ مِنْ آلِ يَعْقُوبَ ۖ وَاجْعَلْهُ رَبِّ رَضِيًّا','که وارث من و دودمان یعقوب باشد؛ و او را مورد رضایتت قرار ده!»'];
ayat[2257]=[19,7,'يَا زَكَرِيَّا إِنَّا نُبَشِّرُكَ بِغُلَامٍ اسْمُهُ يَحْيَىٰ لَمْ نَجْعَل لَّهُ مِن قَبْلُ سَمِيًّا','ای زکریا! ما تو را به فرزندی بشارت می‌دهیم که نامش «یحیی» است؛ و پیش از این، همنامی برای او قرار نداده‌ایم!'];
ayat[2258]=[19,8,'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِي غُلَامٌ وَكَانَتِ امْرَأَتِي عَاقِرًا وَقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا','گفت: «پروردگارا! چگونه برای من فرزندی خواهد بود؟! در حالی که همسرم نازا و عقیم است، و من نیز از شدّت پیری افتاده شده‌ام!»'];
ayat[2259]=[19,9,'قَالَ كَذَٰلِكَ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَقَدْ خَلَقْتُكَ مِن قَبْلُ وَلَمْ تَكُ شَيْئًا','فرمود: «پروردگارت این گونه گفته (و اراده کرده)! این بر من آسان است؛ و قبلاً تو را آفریدم در حالی که چیزی نبودی!»'];
ayat[2260]=[19,10,'قَالَ رَبِّ اجْعَل لِّي آيَةً ۚ قَالَ آيَتُكَ أَلَّا تُكَلِّمَ النَّاسَ ثَلَاثَ لَيَالٍ سَوِيًّا','عرض کرد: «پروردگارا! نشانه‌ای برای من قرار ده!» فرمود: «نشانه تو این است که سه شبانه روز قدرت تکلّم (با مردم) نخواهی داشت؛ در حالی که زبانت سالم است!»'];
ayat[2261]=[19,11,'فَخَرَجَ عَلَىٰ قَوْمِهِ مِنَ الْمِحْرَابِ فَأَوْحَىٰ إِلَيْهِمْ أَن سَبِّحُوا بُكْرَةً وَعَشِيًّا','او از محراب عبادتش به سوی مردم بیرون آمد؛ و با اشاره به آنها گفت: «(بشکرانه این موهبت،) صبح و شام خدا را تسبیح گویید!»'];
ayat[2262]=[19,12,'يَا يَحْيَىٰ خُذِ الْكِتَابَ بِقُوَّةٍ ۖ وَآتَيْنَاهُ الْحُكْمَ صَبِيًّا','ای یحیی! کتاب (خدا) را با قوّت بگیر! و ما فرمان نبوّت (و عقل کافی) در کودکی به او دادیم!'];
ayat[2263]=[19,13,'وَحَنَانًا مِّن لَّدُنَّا وَزَكَاةً ۖ وَكَانَ تَقِيًّا','و رحمت و محبتّی از ناحیه خود به او بخشیدیم، و پاکی (دل و جان)! و او پرهیزگار بود!'];
ayat[2264]=[19,14,'وَبَرًّا بِوَالِدَيْهِ وَلَمْ يَكُن جَبَّارًا عَصِيًّا','او نسبت به پدر و مادرش نیکوکار بود؛ و جبّار (و متکّبر) و عصیانگر نبود!'];
ayat[2265]=[19,15,'وَسَلَامٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوتُ وَيَوْمَ يُبْعَثُ حَيًّا','سلام بر او، آن روز که تولّد یافت، و آن روز که می‌میرد، و آن روز که زنده برانگیخته می‌شود!'];
ayat[2266]=[19,16,'وَاذْكُرْ فِي الْكِتَابِ مَرْيَمَ إِذِ انتَبَذَتْ مِنْ أَهْلِهَا مَكَانًا شَرْقِيًّا','و در این کتاب (آسمانی)، مریم را یاد کن، آن هنگام که از خانواده‌اش جدا شد، و در ناحیه شرقی (بیت المقدس) قرار گرفت؛'];
ayat[2267]=[19,17,'فَاتَّخَذَتْ مِن دُونِهِمْ حِجَابًا فَأَرْسَلْنَا إِلَيْهَا رُوحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا','و میان خود و آنان حجابی افکند (تا خلوتگاهش از هر نظر برای عبادت آماده باشد). در این هنگام، ما روح خود را بسوی او فرستادیم؛ و او در شکل انسانی بی‌عیب و نقص، بر مریم ظاهر شد!'];
ayat[2268]=[19,18,'قَالَتْ إِنِّي أَعُوذُ بِالرَّحْمَٰنِ مِنكَ إِن كُنتَ تَقِيًّا','او (سخت ترسید و) گفت: «من از شرّ تو، به خدای رحمان پناه می‌برم اگر پرهیزگاری!'];
ayat[2269]=[19,19,'قَالَ إِنَّمَا أَنَا رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلَامًا زَكِيًّا','گفت: «من فرستاده پروردگار توام؛ (آمده‌ام) تا پسر پاکیزه‌ای به تو ببخشم!»'];
ayat[2270]=[19,20,'قَالَتْ أَنَّىٰ يَكُونُ لِي غُلَامٌ وَلَمْ يَمْسَسْنِي بَشَرٌ وَلَمْ أَكُ بَغِيًّا','گفت: «چگونه ممکن است فرزندی برای من باشد؟! در حالی که تاکنون انسانی با من تماس نداشته، و زن آلوده‌ای هم نبوده‌ام!»'];
ayat[2271]=[19,21,'قَالَ كَذَٰلِكِ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌ ۖ وَلِنَجْعَلَهُ آيَةً لِّلنَّاسِ وَرَحْمَةً مِّنَّا ۚ وَكَانَ أَمْرًا مَّقْضِيًّا','گفت: «مطلب همین است! پروردگارت فرموده: این کار بر من آسان است! (ما او را می‌آفرینیم، تا قدرت خویش را آشکار سازیم؛) و او را برای مردم نشانه‌ای قرار دهیم؛ و رحمتی باشد از سوی ما! و این امری است پایان یافته (و جای گفتگو ندارد)!»'];
ayat[2272]=[19,22,'۞ فَحَمَلَتْهُ فَانتَبَذَتْ بِهِ مَكَانًا قَصِيًّا','سرانجام (مریم) به او باردار شد؛ و او را به نقطه دور دستی برد (و خلوت گزید)'];
ayat[2273]=[19,23,'فَأَجَاءَهَا الْمَخَاضُ إِلَىٰ جِذْعِ النَّخْلَةِ قَالَتْ يَا لَيْتَنِي مِتُّ قَبْلَ هَٰذَا وَكُنتُ نَسْيًا مَّنسِيًّا','درد زایمان او را به کنار تنه درخت خرمایی کشاند؛ (آنقدر ناراحت شد که) گفت: «ای کاش پیش از این مرده بودم، و بکلّی فراموش می‌شدم!»'];
ayat[2274]=[19,24,'فَنَادَاهَا مِن تَحْتِهَا أَلَّا تَحْزَنِي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا','ناگهان از طرف پایین پایش او را صدا زد که: «غمگین مباش! پروردگارت زیر پای تو چشمه آبی (گوارا) قرار داده است!'];
ayat[2275]=[19,25,'وَهُزِّي إِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسَاقِطْ عَلَيْكِ رُطَبًا جَنِيًّا','و این تنه نخل را به طرف خود تکان ده، رطب تازه‌ای بر تو فرو می‌ریزد!'];
ayat[2276]=[19,26,'فَكُلِي وَاشْرَبِي وَقَرِّي عَيْنًا ۖ فَإِمَّا تَرَيِنَّ مِنَ الْبَشَرِ أَحَدًا فَقُولِي إِنِّي نَذَرْتُ لِلرَّحْمَٰنِ صَوْمًا فَلَنْ أُكَلِّمَ الْيَوْمَ إِنسِيًّا','(از این غذای لذیذ) بخور؛ و (از آن آب گوارا) بنوش؛ و چشمت را (به این مولود جدید) روشن دار! و هرگاه کسی از انسانها را دیدی، (با اشاره) بگو: من برای خداوند رحمان روزه‌ای نذر کرده‌ام؛ بنابراین امروز با هیچ انسانی هیچ سخن نمی‌گویم! (و بدان که این نوزاد، خودش از تو دفاع خواهد کرد!)»'];
ayat[2277]=[19,27,'فَأَتَتْ بِهِ قَوْمَهَا تَحْمِلُهُ ۖ قَالُوا يَا مَرْيَمُ لَقَدْ جِئْتِ شَيْئًا فَرِيًّا','(مریم) در حالی که او را در آغوش گرفته بود، نزد قومش آورد؛ گفتند: «ای مریم! کار بسیار عجیب و بدی انجام دادی!'];
ayat[2278]=[19,28,'يَا أُخْتَ هَارُونَ مَا كَانَ أَبُوكِ امْرَأَ سَوْءٍ وَمَا كَانَتْ أُمُّكِ بَغِيًّا','ای خواهر هارون! نه پدرت مرد بدی بود، و نه مادرت زن بد کاره‌ای!!»'];
ayat[2279]=[19,29,'فَأَشَارَتْ إِلَيْهِ ۖ قَالُوا كَيْفَ نُكَلِّمُ مَن كَانَ فِي الْمَهْدِ صَبِيًّا','(مریم) به او اشاره کرد؛ گفتند: «چگونه با کودکی که در گاهواره است سخن بگوییم؟!»'];
ayat[2280]=[19,30,'قَالَ إِنِّي عَبْدُ اللَّهِ آتَانِيَ الْكِتَابَ وَجَعَلَنِي نَبِيًّا','(ناگهان عیسی زبان به سخن گشود و) گفت: «من بنده خدایم؛ او کتاب (آسمانی) به من داده؛ و مرا پیامبر قرار داده است!'];
ayat[2281]=[19,31,'وَجَعَلَنِي مُبَارَكًا أَيْنَ مَا كُنتُ وَأَوْصَانِي بِالصَّلَاةِ وَالزَّكَاةِ مَا دُمْتُ حَيًّا','و مرا -هر جا که باشم- وجودی پربرکت قرار داده؛ و تا زمانی که زنده‌ام، مرا به نماز و زکات توصیه کرده است!'];
ayat[2282]=[19,32,'وَبَرًّا بِوَالِدَتِي وَلَمْ يَجْعَلْنِي جَبَّارًا شَقِيًّا','و مرا نسبت به مادرم نیکوکار قرار داده؛ و جبّار و شقی قرار نداده است!'];
ayat[2283]=[19,33,'وَالسَّلَامُ عَلَيَّ يَوْمَ وُلِدتُّ وَيَوْمَ أَمُوتُ وَيَوْمَ أُبْعَثُ حَيًّا','و سلام (خدا) بر من، در آن روز که متولّد شدم، و در آن روز که می‌میرم، و آن روز که زنده برانگیخته خواهم شد!»'];
ayat[2284]=[19,34,'ذَٰلِكَ عِيسَى ابْنُ مَرْيَمَ ۚ قَوْلَ الْحَقِّ الَّذِي فِيهِ يَمْتَرُونَ','این است عیسی پسر مریم؛ گفتار حقّی که در آن تردید می‌کنند!'];
ayat[2285]=[19,35,'مَا كَانَ لِلَّهِ أَن يَتَّخِذَ مِن وَلَدٍ ۖ سُبْحَانَهُ ۚ إِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُن فَيَكُونُ','هرگز برای خدا شایسته نبود که فرزندی اختیار کند! منزّه است او! هرگاه چیزی را فرمان دهد، می‌گوید: «موجود باش!» همان دم موجود می‌شود!'];
ayat[2286]=[19,36,'وَإِنَّ اللَّهَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ ۚ هَٰذَا صِرَاطٌ مُّسْتَقِيمٌ','و خداوند، پروردگار من و شماست! او را پرستش کنید؛ این است راه راست!'];
ayat[2287]=[19,37,'فَاخْتَلَفَ الْأَحْزَابُ مِن بَيْنِهِمْ ۖ فَوَيْلٌ لِّلَّذِينَ كَفَرُوا مِن مَّشْهَدِ يَوْمٍ عَظِيمٍ','ولی (بعد از او) گروه‌هایی از میان پیروانش اختلاف کردند؛ وای به حال کافران از مشاهده روز بزرگ (رستاخیز)!'];
ayat[2288]=[19,38,'أَسْمِعْ بِهِمْ وَأَبْصِرْ يَوْمَ يَأْتُونَنَا ۖ لَٰكِنِ الظَّالِمُونَ الْيَوْمَ فِي ضَلَالٍ مُّبِينٍ','در آن روز که نزد ما می‌آیند، چه گوشهای شنوا و چه چشمهای بینایی پیدا می‌کنند! ولی این ستمگران امروز در گمراهی آشکارند!'];
ayat[2289]=[19,39,'وَأَنذِرْهُمْ يَوْمَ الْحَسْرَةِ إِذْ قُضِيَ الْأَمْرُ وَهُمْ فِي غَفْلَةٍ وَهُمْ لَا يُؤْمِنُونَ','آنان را از روز حسرت [= روز رستاخیز که برای همه مایه تأسف است‌] بترسان، در آن هنگام که همه چیز پایان می‌یابد! و آنها در غفلتند و ایمان نمی‌آورند!'];
ayat[2290]=[19,40,'إِنَّا نَحْنُ نَرِثُ الْأَرْضَ وَمَنْ عَلَيْهَا وَإِلَيْنَا يُرْجَعُونَ','ما، زمین و تمام کسانی را که بر آن هستند، به ارث می‌بریم؛ و همگی بسوی ما بازگردانده می‌شوند!'];
ayat[2291]=[19,41,'وَاذْكُرْ فِي الْكِتَابِ إِبْرَاهِيمَ ۚ إِنَّهُ كَانَ صِدِّيقًا نَّبِيًّا','در این کتاب، ابراهیم را یاد کن، که او بسیار راستگو، و پیامبر (خدا) بود!'];
ayat[2292]=[19,42,'إِذْ قَالَ لِأَبِيهِ يَا أَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْنِي عَنكَ شَيْئًا','هنگامی که به پدرش گفت: «ای پدر! چرا چیزی را می‌پرستی که نه می‌شنود، و نه می‌بیند، و نه هیچ مشکلی را از تو حلّ می‌کند؟!'];
ayat[2293]=[19,43,'يَا أَبَتِ إِنِّي قَدْ جَاءَنِي مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِي أَهْدِكَ صِرَاطًا سَوِيًّا','ای پدر! دانشی برای من آمده که برای تو نیامده است؛ بنابر این از من پیروی کن، تا تو را به راه راست هدایت کنم!'];
ayat[2294]=[19,44,'يَا أَبَتِ لَا تَعْبُدِ الشَّيْطَانَ ۖ إِنَّ الشَّيْطَانَ كَانَ لِلرَّحْمَٰنِ عَصِيًّا','ای پدر! شیطان را پرستش مکن، که شیطان نسبت به خداوند رحمان، عصیانگر بود!'];
ayat[2295]=[19,45,'يَا أَبَتِ إِنِّي أَخَافُ أَن يَمَسَّكَ عَذَابٌ مِّنَ الرَّحْمَٰنِ فَتَكُونَ لِلشَّيْطَانِ وَلِيًّا','ای پدر! من از این می‌ترسم که از سوی خداوند رحمان عذابی به تو رسد، در نتیجه از دوستان شیطان باشی!»'];
ayat[2296]=[19,46,'قَالَ أَرَاغِبٌ أَنتَ عَنْ آلِهَتِي يَا إِبْرَاهِيمُ ۖ لَئِن لَّمْ تَنتَهِ لَأَرْجُمَنَّكَ ۖ وَاهْجُرْنِي مَلِيًّا','گفت: «ای ابراهیم! آیا تو از معبودهای من روی گردانی؟! اگر (از این کار) دست برنداری، تو را سنگسار می‌کنم! و برای مدّتی طولانی از من دور شو!»'];
ayat[2297]=[19,47,'قَالَ سَلَامٌ عَلَيْكَ ۖ سَأَسْتَغْفِرُ لَكَ رَبِّي ۖ إِنَّهُ كَانَ بِي حَفِيًّا','(ابراهیم) گفت: «سلام بر تو! من بزودی از پروردگارم برایت تقاضای عفو می‌کنم؛ چرا که او همواره نسبت به من مهربان بوده است!'];
ayat[2298]=[19,48,'وَأَعْتَزِلُكُمْ وَمَا تَدْعُونَ مِن دُونِ اللَّهِ وَأَدْعُو رَبِّي عَسَىٰ أَلَّا أَكُونَ بِدُعَاءِ رَبِّي شَقِيًّا','و از شما، و آنچه غیر خدا می‌خوانید، کناره‌گیری می‌کنم؛ و پروردگارم را می‌خوانم؛ و امیدوارم در خواندن پروردگارم بی‌پاسخ نمانم!»'];
ayat[2299]=[19,49,'فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُونَ مِن دُونِ اللَّهِ وَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ ۖ وَكُلًّا جَعَلْنَا نَبِيًّا','هنگامی که از آنان و آنچه غیر خدا می‌پرستیدند کناره‌گیری کرد، ما اسحاق و یعقوب را به او بخشیدیم؛ و هر یک را پیامبری (بزرگ) قرار دادیم!'];
ayat[2300]=[19,50,'وَوَهَبْنَا لَهُم مِّن رَّحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا','و از رحمت خود به آنان عطا کردیم؛ و برای آنها نام نیک و مقام برجسته‌ای (در میان همه امّتها) قرار دادیم!'];
ayat[2301]=[19,51,'وَاذْكُرْ فِي الْكِتَابِ مُوسَىٰ ۚ إِنَّهُ كَانَ مُخْلَصًا وَكَانَ رَسُولًا نَّبِيًّا','و در این کتاب (آسمانی) از موسی یاد کن، که او مخلص بود، و رسول و پیامبری والا مقام!'];
ayat[2302]=[19,52,'وَنَادَيْنَاهُ مِن جَانِبِ الطُّورِ الْأَيْمَنِ وَقَرَّبْنَاهُ نَجِيًّا','ما او را از طرف راست (کوه) طور فراخواندیم؛ و نجواکنان او را (به خود) نزدیک ساختیم؛'];
ayat[2303]=[19,53,'وَوَهَبْنَا لَهُ مِن رَّحْمَتِنَا أَخَاهُ هَارُونَ نَبِيًّا','و ما از رحمت خود، برادرش هارون را -که پیامبر بود- به او بخشیدیم.'];
ayat[2304]=[19,54,'وَاذْكُرْ فِي الْكِتَابِ إِسْمَاعِيلَ ۚ إِنَّهُ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُولًا نَّبِيًّا','و در این کتاب (آسمانی) از اسماعیل (نیز) یاد کن، که او در وعده‌هایش صادق، و رسول و پیامبری (بزرگ) بود!'];
ayat[2305]=[19,55,'وَكَانَ يَأْمُرُ أَهْلَهُ بِالصَّلَاةِ وَالزَّكَاةِ وَكَانَ عِندَ رَبِّهِ مَرْضِيًّا','او همواره خانواده‌اش را به نماز و زکات فرمان می‌داد؛ و همواره مورد رضایت پروردگارش بود.'];
ayat[2306]=[19,56,'وَاذْكُرْ فِي الْكِتَابِ إِدْرِيسَ ۚ إِنَّهُ كَانَ صِدِّيقًا نَّبِيًّا','و در این کتاب، از ادریس (نیز) یاد کن، او بسیار راستگو و پیامبر (بزرگی) بود.'];
ayat[2307]=[19,57,'وَرَفَعْنَاهُ مَكَانًا عَلِيًّا','و ما او را به مقام والایی رساندیم.'];
ayat[2308]=[19,58,'أُولَٰئِكَ الَّذِينَ أَنْعَمَ اللَّهُ عَلَيْهِم مِّنَ النَّبِيِّينَ مِن ذُرِّيَّةِ آدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوحٍ وَمِن ذُرِّيَّةِ إِبْرَاهِيمَ وَإِسْرَائِيلَ وَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَا ۚ إِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُ الرَّحْمَٰنِ خَرُّوا سُجَّدًا وَبُكِيًّا ۩','آنها پیامبرانی بودند که خداوند مشمول نعمتشان قرار داده بود، از فرزندان آدم، و از کسانی که با نوح بر کشتی سوار کردیم، و از دودمان ابراهیم و یعقوب، و از کسانی که هدایت کردیم و برگزیدیم. آنها کسانی بودند که وقتی آیات خداوند رحمان بر آنان خوانده می‌شد به خاک می‌افتادند، در حالی که سجده می‌کردند و گریان بودند.'];
ayat[2309]=[19,59,'۞ فَخَلَفَ مِن بَعْدِهِمْ خَلْفٌ أَضَاعُوا الصَّلَاةَ وَاتَّبَعُوا الشَّهَوَاتِ ۖ فَسَوْفَ يَلْقَوْنَ غَيًّا','امّا پس از آنان، فرزندان ناشایسته‌ای روی کار آمدند که نماز را تباه کردند، و از شهوات پیروی نمودند؛ و بزودی (مجازات) گمراهی خود را خواهند دید!'];
ayat[2310]=[19,60,'إِلَّا مَن تَابَ وَآمَنَ وَعَمِلَ صَالِحًا فَأُولَٰئِكَ يَدْخُلُونَ الْجَنَّةَ وَلَا يُظْلَمُونَ شَيْئًا','مگر آنان که توبه کنند، و ایمان بیاورند، و کار شایسته انجام دهند؛ چنین کسانی داخل بهشت می‌شوند، و کمترین ستمی به آنان نخواهد شد.'];
ayat[2311]=[19,61,'جَنَّاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمَٰنُ عِبَادَهُ بِالْغَيْبِ ۚ إِنَّهُ كَانَ وَعْدُهُ مَأْتِيًّا','وارد باغهایی جاودانی می‌شوند که خداوند رحمان بندگانش را به آن وعده داده است؛ هر چند آن را ندیده‌اند؛ مسلماً وعده خدا تحقق یافتنی است!'];
ayat[2312]=[19,62,'لَّا يَسْمَعُونَ فِيهَا لَغْوًا إِلَّا سَلَامًا ۖ وَلَهُمْ رِزْقُهُمْ فِيهَا بُكْرَةً وَعَشِيًّا','در آن جا هرگز گفتار لغو و بیهوده‌ای نمی‌شنوند؛ و جز سلام در آن جا سخنی نیست؛ و هر صبح و شام، روزی آنان در بهشت مقرّر است.'];
ayat[2313]=[19,63,'تِلْكَ الْجَنَّةُ الَّتِي نُورِثُ مِنْ عِبَادِنَا مَن كَانَ تَقِيًّا','این همان بهشتی است که به بندگان پرهیزگار خود، به ارث می‌دهیم.'];
ayat[2314]=[19,64,'وَمَا نَتَنَزَّلُ إِلَّا بِأَمْرِ رَبِّكَ ۖ لَهُ مَا بَيْنَ أَيْدِينَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذَٰلِكَ ۚ وَمَا كَانَ رَبُّكَ نَسِيًّا','(پس از تأخیر وحی، جبرئیل به پیامبر عرض کرد:) ما جز بفرمان پروردگار تو، نازل نمی‌شویم؛ آنچه پیش روی ما، و پشت سر ما، و آنچه میان این دو می‌باشد، همه از آن اوست؛ و پروردگارت هرگز فراموشکار نبوده (و نیست)!'];
ayat[2315]=[19,65,'رَّبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهِ ۚ هَلْ تَعْلَمُ لَهُ سَمِيًّا','همان پروردگار آسمانها و زمین، و آنچه میان آن دو قرار دارد! او را پرستش کن؛ و در راه عبادتش شکیبا باش! آیا مثل و مانندی برای او می‌یابی؟!'];
ayat[2316]=[19,66,'وَيَقُولُ الْإِنسَانُ أَإِذَا مَا مِتُّ لَسَوْفَ أُخْرَجُ حَيًّا','انسان می‌گوید: «آیا پس از مردن، زنده (از قبر) بیرون خواهم آمد؟!»'];
ayat[2317]=[19,67,'أَوَلَا يَذْكُرُ الْإِنسَانُ أَنَّا خَلَقْنَاهُ مِن قَبْلُ وَلَمْ يَكُ شَيْئًا','آیا انسان به خاطر نمی‌آورد که ما پیش از این او را آفریدیم در حالی که چیزی نبود؟!'];
ayat[2318]=[19,68,'فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيَاطِينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا','سوگند به پروردگارت که همه آنها را همراه با شیاطین در قیامت جمع می‌کنیم؛ سپس همه را -در حالی که به زانو درآمده‌اند- گرداگرد جهنم حاضر می‌سازیم.'];
ayat[2319]=[19,69,'ثُمَّ لَنَنزِعَنَّ مِن كُلِّ شِيعَةٍ أَيُّهُمْ أَشَدُّ عَلَى الرَّحْمَٰنِ عِتِيًّا','سپس از هر گروه و جمعیّتی، کسانی را که در برابر خداوند رحمان از همه سرکش تر بوده‌اند، جدا می‌کنیم.'];
ayat[2320]=[19,70,'ثُمَّ لَنَحْنُ أَعْلَمُ بِالَّذِينَ هُمْ أَوْلَىٰ بِهَا صِلِيًّا','بعد از آن، ما بخوبی از کسانی که برای سوختن در آتش سزاوارترند، آگاهتریم!'];
ayat[2321]=[19,71,'وَإِن مِّنكُمْ إِلَّا وَارِدُهَا ۚ كَانَ عَلَىٰ رَبِّكَ حَتْمًا مَّقْضِيًّا','و همه شما (بدون استثنا) وارد جهنم می‌شوید؛ این امری است حتمی و قطعی بر پروردگارت!'];
ayat[2322]=[19,72,'ثُمَّ نُنَجِّي الَّذِينَ اتَّقَوا وَّنَذَرُ الظَّالِمِينَ فِيهَا جِثِيًّا','سپس آنها را که تقوا پیشه کردند از آن رهایی می‌بخشیم؛ و ظالمان را -در حالی که (از ضعف و ذلّت) به زانو درآمده‌اند- در آن رها می‌سازیم.'];
ayat[2323]=[19,73,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَيُّ الْفَرِيقَيْنِ خَيْرٌ مَّقَامًا وَأَحْسَنُ نَدِيًّا','و هنگامی که آیات روشن ما بر آنان خوانده می‌شود، کافران به مؤمنان می‌گویند: «کدام یک از دو گروه [= ما و شما] جایگاهش بهتر، و جلسات انس و مشورتش زیباتر، و بخشش او بیشتر است؟!»'];
ayat[2324]=[19,74,'وَكَمْ أَهْلَكْنَا قَبْلَهُم مِّن قَرْنٍ هُمْ أَحْسَنُ أَثَاثًا وَرِئْيًا','چه بسیار اقوامی را پیش از آنان نابود کردیم که هم مال و ثروتشان از آنها بهتر بود، و هم ظاهرشان آراسته‌تر!'];
ayat[2325]=[19,75,'قُلْ مَن كَانَ فِي الضَّلَالَةِ فَلْيَمْدُدْ لَهُ الرَّحْمَٰنُ مَدًّا ۚ حَتَّىٰ إِذَا رَأَوْا مَا يُوعَدُونَ إِمَّا الْعَذَابَ وَإِمَّا السَّاعَةَ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَّكَانًا وَأَضْعَفُ جُندًا','بگو: «کسی که در گمراهی است، باید خداوند به او مهلت دهد تا زمانی که وعده الهی را با چشم خود ببینند: یا عذاب (این دنیا)، یا (عذاب) قیامت! (آن روز) خواهند دانست چه کسی جایش بدتر، و لشکرش ناتوانتر است!»'];
ayat[2326]=[19,76,'وَيَزِيدُ اللَّهُ الَّذِينَ اهْتَدَوْا هُدًى ۗ وَالْبَاقِيَاتُ الصَّالِحَاتُ خَيْرٌ عِندَ رَبِّكَ ثَوَابًا وَخَيْرٌ مَّرَدًّا','(امّا) کسانی که در راه هدایت گام نهادند، خداوند بر هدایتشان می‌افزاید؛ و آثار شایسته‌ای که (از انسان) باقی می‌ماند، ثوابش در پیشگاه پروردگارت بهتر، و عاقبتش خوبتر است!'];
ayat[2327]=[19,77,'أَفَرَأَيْتَ الَّذِي كَفَرَ بِآيَاتِنَا وَقَالَ لَأُوتَيَنَّ مَالًا وَوَلَدًا','آیا دیدی کسی را که به آیات ما کافر شد، و گفت: «اموال و فرزندان فراوانی به من داده خواهد شد»؟!'];
ayat[2328]=[19,78,'أَطَّلَعَ الْغَيْبَ أَمِ اتَّخَذَ عِندَ الرَّحْمَٰنِ عَهْدًا','آیا او از غیب آگاه گشته، یا نزد خدا عهد و پیمانی گرفته است؟!'];
ayat[2329]=[19,79,'كَلَّا ۚ سَنَكْتُبُ مَا يَقُولُ وَنَمُدُّ لَهُ مِنَ الْعَذَابِ مَدًّا','هرگز چنین نیست! ما بزودی آنچه را می‌گوید می‌نویسیم و عذاب را بر او مستمرّ خواهیم داشت!'];
ayat[2330]=[19,80,'وَنَرِثُهُ مَا يَقُولُ وَيَأْتِينَا فَرْدًا','آنچه را او می‌گوید (از اموال و فرزندان)، از او به ارث می‌بریم، و به صورت تنها نزد ما خواهد آمد!'];
ayat[2331]=[19,81,'وَاتَّخَذُوا مِن دُونِ اللَّهِ آلِهَةً لِّيَكُونُوا لَهُمْ عِزًّا','و آنان غیر از خدا، معبودانی را برای خود برگزیدند تا مایه عزّتشان باشد! (چه پندار خامی!)'];
ayat[2332]=[19,82,'كَلَّا ۚ سَيَكْفُرُونَ بِعِبَادَتِهِمْ وَيَكُونُونَ عَلَيْهِمْ ضِدًّا','هرگز چنین نیست! به زودی (معبودها) منکر عبادت آنان خواهند شد؛ (بلکه) بر ضدّشان قیام می‌کنند!'];
ayat[2333]=[19,83,'أَلَمْ تَرَ أَنَّا أَرْسَلْنَا الشَّيَاطِينَ عَلَى الْكَافِرِينَ تَؤُزُّهُمْ أَزًّا','آیا ندیدی که ما شیاطین را بسوی کافران فرستادیم تا آنان را شدیداً تحریک کنند؟!'];
ayat[2334]=[19,84,'فَلَا تَعْجَلْ عَلَيْهِمْ ۖ إِنَّمَا نَعُدُّ لَهُمْ عَدًّا','پس درباره آنان شتاب مکن؛ ما آنها (و اعمالشان) را به دقت شماره میکنیم!'];
ayat[2335]=[19,85,'يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمَٰنِ وَفْدًا','در آن روز که پرهیزگاران را دسته جمعی بسوی خداوند رحمان (و پاداشهای او) محشور می‌کنیم...'];
ayat[2336]=[19,86,'وَنَسُوقُ الْمُجْرِمِينَ إِلَىٰ جَهَنَّمَ وِرْدًا','و مجرمان را (همچون شتران تشنه‌کامی که به سوی آبگاه می‌روند) به جهنّم می‌رانیم...'];
ayat[2337]=[19,87,'لَّا يَمْلِكُونَ الشَّفَاعَةَ إِلَّا مَنِ اتَّخَذَ عِندَ الرَّحْمَٰنِ عَهْدًا','آنان هرگز مالک شفاعت نیستند؛ مگر کسی که نزد خداوند رحمان، عهد و پیمانی دارد.'];
ayat[2338]=[19,88,'وَقَالُوا اتَّخَذَ الرَّحْمَٰنُ وَلَدًا','و گفتند: «خداوند رحمان فرزندی برای خود برگزیده است».'];
ayat[2339]=[19,89,'لَّقَدْ جِئْتُمْ شَيْئًا إِدًّا','راستی مطلب زشت و زننده‌ای گفتید!'];
ayat[2340]=[19,90,'تَكَادُ السَّمَاوَاتُ يَتَفَطَّرْنَ مِنْهُ وَتَنشَقُّ الْأَرْضُ وَتَخِرُّ الْجِبَالُ هَدًّا','نزدیک است به خاطر این سخن آسمانها از هم متلاشی گردد، و زمین شکافته شود، و کوه‌ها بشدّت فرو ریزد...'];
ayat[2341]=[19,91,'أَن دَعَوْا لِلرَّحْمَٰنِ وَلَدًا','از این رو که برای خداوند رحمان فرزندی قائل شدند!'];
ayat[2342]=[19,92,'وَمَا يَنبَغِي لِلرَّحْمَٰنِ أَن يَتَّخِذَ وَلَدًا','در حالی که هرگز برای خداوند رحمان سزاوار نیست که فرزندی برگزیند!'];
ayat[2343]=[19,93,'إِن كُلُّ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ إِلَّا آتِي الرَّحْمَٰنِ عَبْدًا','تمام کسانی که در آسمانها و زمین هستند، بنده اویند!'];
ayat[2344]=[19,94,'لَّقَدْ أَحْصَاهُمْ وَعَدَّهُمْ عَدًّا','خداوند همه آنها را احصا کرده، و به دقّت شمرده است!'];
ayat[2345]=[19,95,'وَكُلُّهُمْ آتِيهِ يَوْمَ الْقِيَامَةِ فَرْدًا','و همگی روز رستاخیز، تک و تنها نزد او حاضر می‌شوند!'];
ayat[2346]=[19,96,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَيَجْعَلُ لَهُمُ الرَّحْمَٰنُ وُدًّا','مسلّماً کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، خداوند رحمان محبّتی برای آنان در دلها قرار می‌دهد!'];
ayat[2347]=[19,97,'فَإِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِينَ وَتُنذِرَ بِهِ قَوْمًا لُّدًّا','و ما فقط آن [= قرآن‌] را بر زبان تو آسان ساختیم تا پرهیزگاران را بوسیله آن بشارت دهی، و دشمنان سرسخت را با آنان انذار کنی.'];
ayat[2348]=[19,98,'وَكَمْ أَهْلَكْنَا قَبْلَهُم مِّن قَرْنٍ هَلْ تُحِسُّ مِنْهُم مِّنْ أَحَدٍ أَوْ تَسْمَعُ لَهُمْ رِكْزًا','چه بسیار اقوام (بی‌ایمان و گنهکاری) را که پیش از آنان هلاک کردیم؛ آیا احدی از آنها را احساس می‌کنی؟! یا کمترین صدایی از آنان می‌شنوی؟!'];
ayat[2349]=[20,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طه','طه‌'];
ayat[2350]=[20,2,'مَا أَنزَلْنَا عَلَيْكَ الْقُرْآنَ لِتَشْقَىٰ','ما قرآن را بر تو نازل نکردیم که خود را به زحمت بیفکنی!'];
ayat[2351]=[20,3,'إِلَّا تَذْكِرَةً لِّمَن يَخْشَىٰ','آن را فقط برای یادآوری کسانی که (از خدا) می‌ترسند نازل ساختیم.'];
ayat[2352]=[20,4,'تَنزِيلًا مِّمَّنْ خَلَقَ الْأَرْضَ وَالسَّمَاوَاتِ الْعُلَى','(این قرآن) از سوی کسی نازل شده که زمین و آسمانهای بلند را آفریده است.'];
ayat[2353]=[20,5,'الرَّحْمَٰنُ عَلَى الْعَرْشِ اسْتَوَىٰ','همان بخشنده‌ای که بر عرش مسلّط است.'];
ayat[2354]=[20,6,'لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرَىٰ','از آن اوست آنچه در آسمانها، و آنچه در زمین، و آنچه میان آن دو، و آنچه در زیر خاک (پنهان) است!'];
ayat[2355]=[20,7,'وَإِن تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَأَخْفَى','اگر سخن آشکارا بگویی (یا مخفی کنی)، او اسرار -و حتی پنهان‌تر از آن- را نیز می‌داند!'];
ayat[2356]=[20,8,'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۖ لَهُ الْأَسْمَاءُ الْحُسْنَىٰ','او خداوندی است که معبودی جز او نیست؛ و نامهای نیکوتر از آن اوست!'];
ayat[2357]=[20,9,'وَهَلْ أَتَاكَ حَدِيثُ مُوسَىٰ','و آیا خبر موسی به تو رسیده است؟'];
ayat[2358]=[20,10,'إِذْ رَأَىٰ نَارًا فَقَالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نَارًا لَّعَلِّي آتِيكُم مِّنْهَا بِقَبَسٍ أَوْ أَجِدُ عَلَى النَّارِ هُدًى','هنگامی که (از دور) آتشی مشاهده کرد، و به خانواده خود گفت: «(اندکی) درنگ کنید که من آتشی دیدم! شاید شعله‌ای از آن برای شما بیاورم؛ یا بوسیله این آتش راه را پیدا کنم!»'];
ayat[2359]=[20,11,'فَلَمَّا أَتَاهَا نُودِيَ يَا مُوسَىٰ','هنگامی که نزد آتش آمد، ندا داده شد که: «ای موسی!'];
ayat[2360]=[20,12,'إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ ۖ إِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى','من پروردگار توام! کفشهایت را بیرون آر، که تو در سرزمین مقدّس «طوی» هستی!'];
ayat[2361]=[20,13,'وَأَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوحَىٰ','و من تو را (برای مقام رسالت) برگزیدم؛ اکنون به آنچه بر تو وحی می‌شود، گوش فراده!'];
ayat[2362]=[20,14,'إِنَّنِي أَنَا اللَّهُ لَا إِلَٰهَ إِلَّا أَنَا فَاعْبُدْنِي وَأَقِمِ الصَّلَاةَ لِذِكْرِي','من «اللّه» هستم؛ معبودی جز من نیست! مرا بپرست، و نماز را برای یاد من بپادار!'];
ayat[2363]=[20,15,'إِنَّ السَّاعَةَ آتِيَةٌ أَكَادُ أُخْفِيهَا لِتُجْزَىٰ كُلُّ نَفْسٍ بِمَا تَسْعَىٰ','بطور قطع رستاخیز خواهد آمد! می‌خواهم آن را پنهان کنم، تا هر کس در برابر سعی و کوشش خود، جزا داده شود!'];
ayat[2364]=[20,16,'فَلَا يَصُدَّنَّكَ عَنْهَا مَن لَّا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوَاهُ فَتَرْدَىٰ','پس مبادا کسی که به آن ایمان ندارد و از هوسهای خویش پیروی می‌کند، تو را از آن بازدارد؛ که هلاک خواهی شد!'];
ayat[2365]=[20,17,'وَمَا تِلْكَ بِيَمِينِكَ يَا مُوسَىٰ','و آن چیست در دست راست تو، ای موسی؟!'];
ayat[2366]=[20,18,'قَالَ هِيَ عَصَايَ أَتَوَكَّأُ عَلَيْهَا وَأَهُشُّ بِهَا عَلَىٰ غَنَمِي وَلِيَ فِيهَا مَآرِبُ أُخْرَىٰ','گفت: «این عصای من است؛ بر آن تکیه می‌کنم، برگ درختان را با آن برای گوسفندانم فرومی‌ریزم؛ و مرا با آن کارها و نیازهای دیگری است.'];
ayat[2367]=[20,19,'قَالَ أَلْقِهَا يَا مُوسَىٰ','گفت: «ای موسی! آن را بیفکن.»'];
ayat[2368]=[20,20,'فَأَلْقَاهَا فَإِذَا هِيَ حَيَّةٌ تَسْعَىٰ','پس موسی آن (عصا) را افکند، که ناگهان اژدهایی شد که به هر سو می‌شتافت.'];
ayat[2369]=[20,21,'قَالَ خُذْهَا وَلَا تَخَفْ ۖ سَنُعِيدُهَا سِيرَتَهَا الْأُولَىٰ','گفت: «آن را بگیر و نترس، ما آن را به صورت اولش بازمی‌گردانیم.'];
ayat[2370]=[20,22,'وَاضْمُمْ يَدَكَ إِلَىٰ جَنَاحِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ آيَةً أُخْرَىٰ','و دستت را به گریبانت ببر، تا سفید و بی‌عیب بیرون آید؛ این نشانه دیگری (از سوی خداوند) است.'];
ayat[2371]=[20,23,'لِنُرِيَكَ مِنْ آيَاتِنَا الْكُبْرَى','تا از نشانه‌های بزرگ خویش به تو نشان دهیم.'];
ayat[2372]=[20,24,'اذْهَبْ إِلَىٰ فِرْعَوْنَ إِنَّهُ طَغَىٰ','اینک به سوی فرعون برو، که او طغیان کرده است.»'];
ayat[2373]=[20,25,'قَالَ رَبِّ اشْرَحْ لِي صَدْرِي','(موسی) گفت: «پروردگارا! سینه‌ام را گشاده کن؛'];
ayat[2374]=[20,26,'وَيَسِّرْ لِي أَمْرِي','و کارم را برایم آسان گردان!'];
ayat[2375]=[20,27,'وَاحْلُلْ عُقْدَةً مِّن لِّسَانِي','و گره از زبانم بگشای؛'];
ayat[2376]=[20,28,'يَفْقَهُوا قَوْلِي','تا سخنان مرا بفهمند!'];
ayat[2377]=[20,29,'وَاجْعَل لِّي وَزِيرًا مِّنْ أَهْلِي','و وزیری از خاندانم برای من قرار ده...'];
ayat[2378]=[20,30,'هَارُونَ أَخِي','برادرم هارون را!'];
ayat[2379]=[20,31,'اشْدُدْ بِهِ أَزْرِي','با او پشتم را محکم کن؛'];
ayat[2380]=[20,32,'وَأَشْرِكْهُ فِي أَمْرِي','و او را در کارم شریک ساز؛'];
ayat[2381]=[20,33,'كَيْ نُسَبِّحَكَ كَثِيرًا','تا تو را بسیار تسبیح گوییم؛'];
ayat[2382]=[20,34,'وَنَذْكُرَكَ كَثِيرًا','و تو را بسیار یاد کنیم؛'];
ayat[2383]=[20,35,'إِنَّكَ كُنتَ بِنَا بَصِيرًا','چرا که تو همیشه از حال ما آگاه بوده‌ای!»'];
ayat[2384]=[20,36,'قَالَ قَدْ أُوتِيتَ سُؤْلَكَ يَا مُوسَىٰ','فرمود: «ای موسی! آنچه را خواستی به تو داده شد!'];
ayat[2385]=[20,37,'وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً أُخْرَىٰ','و ما بار دیگر تو را مشمول نعمت خود ساختیم...'];
ayat[2386]=[20,38,'إِذْ أَوْحَيْنَا إِلَىٰ أُمِّكَ مَا يُوحَىٰ','آن زمان که به مادرت آنچه لازم بود الهام کردیم...'];
ayat[2387]=[20,39,'أَنِ اقْذِفِيهِ فِي التَّابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِّي وَعَدُوٌّ لَّهُ ۚ وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِّنِّي وَلِتُصْنَعَ عَلَىٰ عَيْنِي','که: «او را در صندوقی بیفکن، و آن صندوق را به دریا بینداز، تا دریا آن را به ساحل افکند؛ و دشمن من و دشمن او، آن را برگیرد!» و من محبّتی از خودم بر تو افکندم، تا در برابر دیدگان [= علم‌] من، ساخته شوی (و پرورش یابی)!'];
ayat[2388]=[20,40,'إِذْ تَمْشِي أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلَىٰ مَن يَكْفُلُهُ ۖ فَرَجَعْنَاكَ إِلَىٰ أُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ۚ وَقَتَلْتَ نَفْسًا فَنَجَّيْنَاكَ مِنَ الْغَمِّ وَفَتَنَّاكَ فُتُونًا ۚ فَلَبِثْتَ سِنِينَ فِي أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلَىٰ قَدَرٍ يَا مُوسَىٰ','در آن هنگام که خواهرت (در نزدیکی کاخ فرعون) راه می‌رفت و می‌گفت: «آیا کسی را به شما نشان دهم که این نوزاد را کفالت می‌کند (و دایه خوبی برای او خواهد بود)!» پس تو را به مادرت بازگرداندیم، تا چشمش به تو روشن شود؛ و غمگین نگردد! و تو یکی (از فرعونیان) را کشتی؛ اما ما تو را از اندوه نجات دادیم! و بارها تو را آزمودیم! پس از آن، سالیانی در میان مردم «مدین» توقف نمودی؛ سپس در زمان مقدّر (برای فرمان رسالت) به این جا آمدی، ای موسی!'];
ayat[2389]=[20,41,'وَاصْطَنَعْتُكَ لِنَفْسِي','و من تو را برای خودم ساختم (و پرورش دادم)!'];
ayat[2390]=[20,42,'اذْهَبْ أَنتَ وَأَخُوكَ بِآيَاتِي وَلَا تَنِيَا فِي ذِكْرِي','(اکنون) تو و برادرت با آیات من بروید، و در یاد من کوتاهی نکنید!'];
ayat[2391]=[20,43,'اذْهَبَا إِلَىٰ فِرْعَوْنَ إِنَّهُ طَغَىٰ','بسوی فرعون بروید؛ که طغیان کرده است!'];
ayat[2392]=[20,44,'فَقُولَا لَهُ قَوْلًا لَّيِّنًا لَّعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشَىٰ','اما بنرمی با او سخن بگویید؛ شاید متذکّر شود، یا (از خدا) بترسد!'];
ayat[2393]=[20,45,'قَالَا رَبَّنَا إِنَّنَا نَخَافُ أَن يَفْرُطَ عَلَيْنَا أَوْ أَن يَطْغَىٰ','(موسی و هارون) گفتند: «پروردگارا! از این می‌ترسیم که بر ما پیشی گیرد (و قبل از بیان حق، ما را آزار دهد)؛ یا طغیان کند (و نپذیرد)!»'];
ayat[2394]=[20,46,'قَالَ لَا تَخَافَا ۖ إِنَّنِي مَعَكُمَا أَسْمَعُ وَأَرَىٰ','فرمود: «نترسید! من با شما هستم؛ (همه چیز را) می‌شنوم و می‌بینم!'];
ayat[2395]=[20,47,'فَأْتِيَاهُ فَقُولَا إِنَّا رَسُولَا رَبِّكَ فَأَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ وَلَا تُعَذِّبْهُمْ ۖ قَدْ جِئْنَاكَ بِآيَةٍ مِّن رَّبِّكَ ۖ وَالسَّلَامُ عَلَىٰ مَنِ اتَّبَعَ الْهُدَىٰ','به سراغ او بروید و بگویید: «ما فرستادگان پروردگار توئیم! بنی اسرائیل را با ما بفرست؛ و آنان را شکنجه و آزار مکن! ما نشانه روشنی از سوی پروردگارت برای تو آورده‌ایم! و درود بر آن کس باد که از هدایت پیروی می‌کند!'];
ayat[2396]=[20,48,'إِنَّا قَدْ أُوحِيَ إِلَيْنَا أَنَّ الْعَذَابَ عَلَىٰ مَن كَذَّبَ وَتَوَلَّىٰ','به ما وحی شده که عذاب بر کسی است که (آیات الهی را) تکذیب کند و سرپیچی نماید!»'];
ayat[2397]=[20,49,'قَالَ فَمَن رَّبُّكُمَا يَا مُوسَىٰ','(فرعون) گفت: «پروردگار شما کیست، ای موسی؟!»'];
ayat[2398]=[20,50,'قَالَ رَبُّنَا الَّذِي أَعْطَىٰ كُلَّ شَيْءٍ خَلْقَهُ ثُمَّ هَدَىٰ','گفت: «پروردگار ما همان کسی است که به هر موجودی، آنچه را لازمه آفرینش او بوده داده؛ سپس هدایت کرده است!»'];
ayat[2399]=[20,51,'قَالَ فَمَا بَالُ الْقُرُونِ الْأُولَىٰ','گفت: «پس تکلیف نسلهای گذشته (که به اینها ایمان نداشتند) چه خواهد شد؟!»'];
ayat[2400]=[20,52,'قَالَ عِلْمُهَا عِندَ رَبِّي فِي كِتَابٍ ۖ لَّا يَضِلُّ رَبِّي وَلَا يَنسَى','گفت: «آگاهی مربوط به آنها، نزد پروردگارم در کتابی ثبت است؛ پروردگارم هرگز گمراه نمی‌شود، و فراموش نمی‌کند (و آنچه شایسته آنهاست به ایشان می‌دهد)!'];
ayat[2401]=[20,53,'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْدًا وَسَلَكَ لَكُمْ فِيهَا سُبُلًا وَأَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ أَزْوَاجًا مِّن نَّبَاتٍ شَتَّىٰ','همان خداوندی که زمین را برای شما محل آسایش قرار داد؛ و راه‌هایی در آن ایجاد کرد؛ و از آسمان، آبی فرستاد!» که با آن، انواع گوناگون گیاهان را (از خاک تیره) برآوردیم.'];
ayat[2402]=[20,54,'كُلُوا وَارْعَوْا أَنْعَامَكُمْ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّأُولِي النُّهَىٰ','هم خودتان بخورید؛ و هم چهارپایانتان را در آن به چرا برید! مسلّماً در اینها نشانه‌های روشنی برای خردمندان است!'];
ayat[2403]=[20,55,'۞ مِنْهَا خَلَقْنَاكُمْ وَفِيهَا نُعِيدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً أُخْرَىٰ','ما شما را از آن [= زمین‌] آفریدیم؛ و در آن بازمی‌گردانیم؛ و بار دیگر (در قیامت) شما را از آن بیرون می‌آوریم!'];
ayat[2404]=[20,56,'وَلَقَدْ أَرَيْنَاهُ آيَاتِنَا كُلَّهَا فَكَذَّبَ وَأَبَىٰ','ما همه آیات خود را به او نشان دادیم؛ اما او تکذیب کرد و سرباز زد!'];
ayat[2405]=[20,57,'قَالَ أَجِئْتَنَا لِتُخْرِجَنَا مِنْ أَرْضِنَا بِسِحْرِكَ يَا مُوسَىٰ','گفت: «ای موسی! آیا آمده‌ای که با سحر خود، ما را از سرزمینمان بیرون کنی؟!'];
ayat[2406]=[20,58,'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِّثْلِهِ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لَّا نُخْلِفُهُ نَحْنُ وَلَا أَنتَ مَكَانًا سُوًى','قطعاً ما هم سحری همانند آن برای تو خواهیم آورد! هم اکنون (تاریخش را تعیین کن، و) موعدی میان ما و خودت قرار ده که نه ما و نه تو، از آن تخلّف نکنیم؛ آن هم در مکانی که نسبت به همه یکسان باشد!»'];
ayat[2407]=[20,59,'قَالَ مَوْعِدُكُمْ يَوْمُ الزِّينَةِ وَأَن يُحْشَرَ النَّاسُ ضُحًى','گفت: «میعاد ما و شما روز زینت [= روز عید] است؛ به شرط اینکه همه مردم، هنگامی که روز، بالا می‌آید، جمع شوند!»'];
ayat[2408]=[20,60,'فَتَوَلَّىٰ فِرْعَوْنُ فَجَمَعَ كَيْدَهُ ثُمَّ أَتَىٰ','فرعون آن مجلس را ترک گفت؛ و تمام مکر و فریب خود را جمع کرد؛ و سپس همه را (در روز موعود) آورد.'];
ayat[2409]=[20,61,'قَالَ لَهُم مُّوسَىٰ وَيْلَكُمْ لَا تَفْتَرُوا عَلَى اللَّهِ كَذِبًا فَيُسْحِتَكُم بِعَذَابٍ ۖ وَقَدْ خَابَ مَنِ افْتَرَىٰ','موسی به آنان گفت: «وای بر شما! دروغ بر خدا نبندید، که شما را با عذابی نابود می‌سازد! و هر کس که (بر خدا) دروغ ببندد، نومید (و شکست خورده) می‌شود!»'];
ayat[2410]=[20,62,'فَتَنَازَعُوا أَمْرَهُم بَيْنَهُمْ وَأَسَرُّوا النَّجْوَىٰ','آنها در میان خود، در مورد ادامه راهشان به نزاع برخاستند؛ و مخفیانه و درگوشی با هم سخن گفتند.'];
ayat[2411]=[20,63,'قَالُوا إِنْ هَٰذَانِ لَسَاحِرَانِ يُرِيدَانِ أَن يُخْرِجَاكُم مِّنْ أَرْضِكُم بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيقَتِكُمُ الْمُثْلَىٰ','گفتند: «این دو (نفر) مسلّماً ساحرند! می‌خواهند با سحرشان شما را از سرزمینتان بیرون کنند و راه و رسم نمونه شما را از بین ببرند!'];
ayat[2412]=[20,64,'فَأَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفًّا ۚ وَقَدْ أَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلَىٰ','اکنون که چنین است، تمام نیرو و نقشه خود را جمع کنید، و در یک صف (به میدان مبارزه) بیایید؛ امروز رستگاری از آن کسی است که برتری خود را اثبات کند!'];
ayat[2413]=[20,65,'قَالُوا يَا مُوسَىٰ إِمَّا أَن تُلْقِيَ وَإِمَّا أَن نَّكُونَ أَوَّلَ مَنْ أَلْقَىٰ','(ساحران) گفتند: «ای موسی! آیا تو اول (عصای خود را) می‌افکنی، یا ما کسانی باشیم که اول بیفکنیم؟!»'];
ayat[2414]=[20,66,'قَالَ بَلْ أَلْقُوا ۖ فَإِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ إِلَيْهِ مِن سِحْرِهِمْ أَنَّهَا تَسْعَىٰ','گفت: «شما اول بیفکنید!» در این هنگام طنابها و عصاهای آنان بر اثر سحرشان چنان به نظر می‌رسید که حرکت می‌کند!'];
ayat[2415]=[20,67,'فَأَوْجَسَ فِي نَفْسِهِ خِيفَةً مُّوسَىٰ','موسی ترس خفیفی در دل احساس کرد (مبادا مردم گمراه شوند)!'];
ayat[2416]=[20,68,'قُلْنَا لَا تَخَفْ إِنَّكَ أَنتَ الْأَعْلَىٰ','گفتیم: «نترس! تو مسلّماً (پیروز و) برتری!'];
ayat[2417]=[20,69,'وَأَلْقِ مَا فِي يَمِينِكَ تَلْقَفْ مَا صَنَعُوا ۖ إِنَّمَا صَنَعُوا كَيْدُ سَاحِرٍ ۖ وَلَا يُفْلِحُ السَّاحِرُ حَيْثُ أَتَىٰ','و آنچه را در دست راست داری بیفکن، تمام آنچه را ساخته‌اند می‌بلعد! آنچه ساخته‌اند تنها مکر ساحر است؛ و ساحر هر جا رود رستگار نخواهد شد!»'];
ayat[2418]=[20,70,'فَأُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوا آمَنَّا بِرَبِّ هَارُونَ وَمُوسَىٰ','(موسی عصای خود را افکند، و آنچه را که آنها ساخته بودند بلعید.) ساحران همگی به سجده افتادند و گفتند: «ما به پروردگار هارون و موسی ایمان آوردیم!»'];
ayat[2419]=[20,71,'قَالَ آمَنتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ ۖ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ ۖ فَلَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُم مِّنْ خِلَافٍ وَلَأُصَلِّبَنَّكُمْ فِي جُذُوعِ النَّخْلِ وَلَتَعْلَمُنَّ أَيُّنَا أَشَدُّ عَذَابًا وَأَبْقَىٰ','(فرعون) گفت: «آیا پیش از آنکه به شما اذن دهم به او ایمان آوردید؟! مسلّماً او بزرگ شماست که به شما سحر آموخته است! به یقین دستها و پاهایتان را بطور مخالف قطع می‌کنم؛ و شما را از تنه‌های نخل به دار می‌آویزم؛ و خواهید دانست مجازات کدام یک از ما دردناکتر و پایدارتر است!»'];
ayat[2420]=[20,72,'قَالُوا لَن نُّؤْثِرَكَ عَلَىٰ مَا جَاءَنَا مِنَ الْبَيِّنَاتِ وَالَّذِي فَطَرَنَا ۖ فَاقْضِ مَا أَنتَ قَاضٍ ۖ إِنَّمَا تَقْضِي هَٰذِهِ الْحَيَاةَ الدُّنْيَا','گفتند: «سوگند به آن کسی که ما را آفریده، هرگز تو را بر دلایل روشنی که برای ما آمده، مقدّم نخواهیم داشت! هر حکمی می‌خواهی بکن؛ تو تنها در این زندگی دنیا می‌توانی حکم کنی!'];
ayat[2421]=[20,73,'إِنَّا آمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطَايَانَا وَمَا أَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِ ۗ وَاللَّهُ خَيْرٌ وَأَبْقَىٰ','ما به پروردگارمان ایمان آوردیم تا گناهانمان و آنچه را از سحر بر ما تحمیل کردی ببخشاید؛ و خدا بهتر و پایدارتر است!»'];
ayat[2422]=[20,74,'إِنَّهُ مَن يَأْتِ رَبَّهُ مُجْرِمًا فَإِنَّ لَهُ جَهَنَّمَ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ','هر کس در محضر پروردگارش خطاکار حاضر شود، آتش دوزخ برای اوست؛ در آن جا، نه می‌میرد و نه زندگی می‌کند!'];
ayat[2423]=[20,75,'وَمَن يَأْتِهِ مُؤْمِنًا قَدْ عَمِلَ الصَّالِحَاتِ فَأُولَٰئِكَ لَهُمُ الدَّرَجَاتُ الْعُلَىٰ','و هر کس با ایمان نزد او آید، و اعمال صالح انجام داده باشد، چنین کسانی درجات عالی دارند...'];
ayat[2424]=[20,76,'جَنَّاتُ عَدْنٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ مَن تَزَكَّىٰ','باغهای جاویدان بهشت، که نهرها از زیر درختانش جاری است، در حالی که همیشه در آن خواهند بود؛ این است پاداش کسی که خود را پاک نماید!'];
ayat[2425]=[20,77,'وَلَقَدْ أَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَسْرِ بِعِبَادِي فَاضْرِبْ لَهُمْ طَرِيقًا فِي الْبَحْرِ يَبَسًا لَّا تَخَافُ دَرَكًا وَلَا تَخْشَىٰ','ما به موسی وحی فرستادیم که: «شبانه بندگانم را (از مصر) با خود ببر؛ و برای آنها راهی خشک در دریا بگشا؛ که نه از تعقیب (فرعونیان) خواهی ترسید، و نه از غرق شدن در دریا!»'];
ayat[2426]=[20,78,'فَأَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِهِ فَغَشِيَهُم مِّنَ الْيَمِّ مَا غَشِيَهُمْ','(به این ترتیب) فرعون با لشکریانش آنها را دنبال کردند؛ و دریا آنان را (در میان امواج خروشان خود) بطور کامل پوشانید!'];
ayat[2427]=[20,79,'وَأَضَلَّ فِرْعَوْنُ قَوْمَهُ وَمَا هَدَىٰ','فرعون قوم خود را گمراه ساخت؛ و هرگز هدایت نکرد!'];
ayat[2428]=[20,80,'يَا بَنِي إِسْرَائِيلَ قَدْ أَنجَيْنَاكُم مِّنْ عَدُوِّكُمْ وَوَاعَدْنَاكُمْ جَانِبَ الطُّورِ الْأَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوَىٰ','ای بنی اسرائیل! ما شما را از چنگال دشمنتان نجات دادیم؛ و در طرف راست کوه طور، با شما وعده گذاردیم؛ و «منّ» و «سلوی» بر شما نازل کردیم!'];
ayat[2429]=[20,81,'كُلُوا مِن طَيِّبَاتِ مَا رَزَقْنَاكُمْ وَلَا تَطْغَوْا فِيهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِي ۖ وَمَن يَحْلِلْ عَلَيْهِ غَضَبِي فَقَدْ هَوَىٰ','بخورید از روزیهای پاکیزه‌ای که به شما داده‌ایم؛ و در آن طغیان نکنید، که غضب من بر شما وارد شود و هر کس غضبم بر او وارد شود، سقوب می‌کند!'];
ayat[2430]=[20,82,'وَإِنِّي لَغَفَّارٌ لِّمَن تَابَ وَآمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدَىٰ','و من هر که را توبه کند، و ایمان آورد، و عمل صالح انجام دهد، سپس هدایت شود، می‌آمرزم!'];
ayat[2431]=[20,83,'۞ وَمَا أَعْجَلَكَ عَن قَوْمِكَ يَا مُوسَىٰ','ای موسی! چه چیز سبب شد که از قومت پیشی گیری، و (برای آمدن به کوه طور) عجله کنی؟!'];
ayat[2432]=[20,84,'قَالَ هُمْ أُولَاءِ عَلَىٰ أَثَرِي وَعَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضَىٰ','عرض کرد: «پروردگارا! آنان در پی منند؛ و من به سوی تو شتاب کردم، تا از من خشنود شوی!»'];
ayat[2433]=[20,85,'قَالَ فَإِنَّا قَدْ فَتَنَّا قَوْمَكَ مِن بَعْدِكَ وَأَضَلَّهُمُ السَّامِرِيُّ','فرمود: «ما قوم تو را بعد از تو، آزمودیم و سامری آنها را گمراه ساخت!»'];
ayat[2434]=[20,86,'فَرَجَعَ مُوسَىٰ إِلَىٰ قَوْمِهِ غَضْبَانَ أَسِفًا ۚ قَالَ يَا قَوْمِ أَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ۚ أَفَطَالَ عَلَيْكُمُ الْعَهْدُ أَمْ أَرَدتُّمْ أَن يَحِلَّ عَلَيْكُمْ غَضَبٌ مِّن رَّبِّكُمْ فَأَخْلَفْتُم مَّوْعِدِي','موسی خشمگین و اندوهناک به سوی قوم خود بازگشت و گفت: «ای قوم من! مگر پروردگارتان وعده نیکویی به شما نداد؟! آیا مدّت جدایی من از شما به طول انجامید، یا می‌خواستید غضب پروردگارتان بر شما نازل شود که با وعده من مخالفت کردید؟!»'];
ayat[2435]=[20,87,'قَالُوا مَا أَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلَٰكِنَّا حُمِّلْنَا أَوْزَارًا مِّن زِينَةِ الْقَوْمِ فَقَذَفْنَاهَا فَكَذَٰلِكَ أَلْقَى السَّامِرِيُّ','گفتند: «ما به میل و اراده خود از وعده تو تخلّف نکردیم؛ بلکه مقداری از زیورهای قوم را که با خود داشتیم افکندیم!» و سامری اینچنین القا کرد...'];
ayat[2436]=[20,88,'فَأَخْرَجَ لَهُمْ عِجْلًا جَسَدًا لَّهُ خُوَارٌ فَقَالُوا هَٰذَا إِلَٰهُكُمْ وَإِلَٰهُ مُوسَىٰ فَنَسِيَ','و برای آنان مجسّمه گوساله‌ای که صدایی همچون صدای گوساله (واقعی) داشت پدید آورد؛ و (به یکدیگر) گفتند: «این خدای شما، و خدای موسی است!» و او فراموش کرد (پیمانی را که با خدا بسته بود)!'];
ayat[2437]=[20,89,'أَفَلَا يَرَوْنَ أَلَّا يَرْجِعُ إِلَيْهِمْ قَوْلًا وَلَا يَمْلِكُ لَهُمْ ضَرًّا وَلَا نَفْعًا','آیا نمی‌بینند که (این گوساله) هیچ پاسخی به آنان نمی‌دهد، و مالک هیچ گونه سود و زیانی برای آنها نیست؟!'];
ayat[2438]=[20,90,'وَلَقَدْ قَالَ لَهُمْ هَارُونُ مِن قَبْلُ يَا قَوْمِ إِنَّمَا فُتِنتُم بِهِ ۖ وَإِنَّ رَبَّكُمُ الرَّحْمَٰنُ فَاتَّبِعُونِي وَأَطِيعُوا أَمْرِي','و پیش از آن، هارون به آنها گفته بود: «ای قوم من! شما به این وسیله مورد آزمایش قرار گرفته‌اید! پروردگار شما خداوند رحمان است! پس، از من پیروی کنید، و فرمانم را اطاعت نمایید!»'];
ayat[2439]=[20,91,'قَالُوا لَن نَّبْرَحَ عَلَيْهِ عَاكِفِينَ حَتَّىٰ يَرْجِعَ إِلَيْنَا مُوسَىٰ','ولی آنها گفتند: «ما همچنان گرد آن می‌گردیم (و به پرستش گوساله ادامه می‌دهیم) تا موسی به سوی ما بازگردد!»'];
ayat[2440]=[20,92,'قَالَ يَا هَارُونُ مَا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضَلُّوا','(موسی) گفت: «ای هارون! چرا هنگامی که دیدی آنها گمراه شدند...'];
ayat[2441]=[20,93,'أَلَّا تَتَّبِعَنِ ۖ أَفَعَصَيْتَ أَمْرِي','از من پیروی نکردی؟! آیا فرمان مرا عصیان نمودی؟!»'];
ayat[2442]=[20,94,'قَالَ يَا ابْنَ أُمَّ لَا تَأْخُذْ بِلِحْيَتِي وَلَا بِرَأْسِي ۖ إِنِّي خَشِيتُ أَن تَقُولَ فَرَّقْتَ بَيْنَ بَنِي إِسْرَائِيلَ وَلَمْ تَرْقُبْ قَوْلِي','(هارون) گفت: «ای فرزند مادرم! [= ای برادر!] ریش و سر مرا مگیر! من ترسیدم بگویی تو میان بنی اسرائیل تفرقه انداختی، و سفارش مرا به کار نبستی!»'];
ayat[2443]=[20,95,'قَالَ فَمَا خَطْبُكَ يَا سَامِرِيُّ','(موسی رو به سامری کرد و) گفت: «تو چرا این کار را کردی، ای سامری؟!»'];
ayat[2444]=[20,96,'قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوا بِهِ فَقَبَضْتُ قَبْضَةً مِّنْ أَثَرِ الرَّسُولِ فَنَبَذْتُهَا وَكَذَٰلِكَ سَوَّلَتْ لِي نَفْسِي','گفت: «من چیزی دیدم که آنها ندیدند؛ من قسمتی از آثار رسول (و فرستاده خدا) را گرفتم، سپس آن را افکندم، و اینچنین (هوای) نفس من این کار را در نظرم جلوه داد!»'];
ayat[2445]=[20,97,'قَالَ فَاذْهَبْ فَإِنَّ لَكَ فِي الْحَيَاةِ أَن تَقُولَ لَا مِسَاسَ ۖ وَإِنَّ لَكَ مَوْعِدًا لَّن تُخْلَفَهُ ۖ وَانظُرْ إِلَىٰ إِلَٰهِكَ الَّذِي ظَلْتَ عَلَيْهِ عَاكِفًا ۖ لَّنُحَرِّقَنَّهُ ثُمَّ لَنَنسِفَنَّهُ فِي الْيَمِّ نَسْفًا','(موسی) گفت: «برو، که بهره تو در زندگی دنیا این است که (هر کس با تو نزدیک شود) بگوئی «با من تماس نگیر!» و تو میعادی (از عذاب خدا) داری، که هرگز تخلّف نخواهد شد! (اکنون) بنگر به این معبودت که پیوسته آن را پرستش می‌کردی! و ببین ما آن را نخست می‌سوزانیم؛ سپس ذرّات آن را به دریا می‌پاشیم!'];
ayat[2446]=[20,98,'إِنَّمَا إِلَٰهُكُمُ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ ۚ وَسِعَ كُلَّ شَيْءٍ عِلْمًا','معبود شما تنها خداوندی است که جز او معبودی نیست؛ و علم او همه چیز را فرا گرفته است!»'];
ayat[2447]=[20,99,'كَذَٰلِكَ نَقُصُّ عَلَيْكَ مِنْ أَنبَاءِ مَا قَدْ سَبَقَ ۚ وَقَدْ آتَيْنَاكَ مِن لَّدُنَّا ذِكْرًا','این گونه بخشی از اخبار پیشین را برای تو بازگو می‌کنیم؛ و ما از نزد خود، ذکر (و قرآنی) به تو دادیم!'];
ayat[2448]=[20,100,'مَّنْ أَعْرَضَ عَنْهُ فَإِنَّهُ يَحْمِلُ يَوْمَ الْقِيَامَةِ وِزْرًا','هر کس از آن روی گردان شود، روز قیامت بار سنگینی (از گناه و مسؤولیّت) بر دوش خواهد داشت!'];
ayat[2449]=[20,101,'خَالِدِينَ فِيهِ ۖ وَسَاءَ لَهُمْ يَوْمَ الْقِيَامَةِ حِمْلًا','در حالی که جاودانه در آن خواهند ماند؛ و بد باری است برای آنها در روز قیامت!'];
ayat[2450]=[20,102,'يَوْمَ يُنفَخُ فِي الصُّورِ ۚ وَنَحْشُرُ الْمُجْرِمِينَ يَوْمَئِذٍ زُرْقًا','همان روزی که در «صور» دمیده می‌شود؛ و مجرمان را با بدنهای کبود، در آن روز جمع می‌کنیم!'];
ayat[2451]=[20,103,'يَتَخَافَتُونَ بَيْنَهُمْ إِن لَّبِثْتُمْ إِلَّا عَشْرًا','آنها آهسته با هم گفتگو می‌کنند؛ (بعضی می‌گویند:) شما فقط ده (شبانه روز در عالم برزخ) توقّف کردید! (و نمی‌دانند چقدر طولانی بوده است!)'];
ayat[2452]=[20,104,'نَّحْنُ أَعْلَمُ بِمَا يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طَرِيقَةً إِن لَّبِثْتُمْ إِلَّا يَوْمًا','ما به آنچه آنها می‌گویند آگاهتریم، هنگامی که نیکوروش‌ترین آنها می‌گوید: «شما تنها یک روز درنگ کردید!»'];
ayat[2453]=[20,105,'وَيَسْأَلُونَكَ عَنِ الْجِبَالِ فَقُلْ يَنسِفُهَا رَبِّي نَسْفًا','و از تو درباره کوه‌ها سؤال می‌کنند؛ بگو: «پروردگارم آنها را (متلاشی کرده) برباد می‌دهد!'];
ayat[2454]=[20,106,'فَيَذَرُهَا قَاعًا صَفْصَفًا','سپس زمین را صاف و هموار و بی‌آب و گیاه رها می‌سازد...'];
ayat[2455]=[20,107,'لَّا تَرَىٰ فِيهَا عِوَجًا وَلَا أَمْتًا','به گونه‌ای که در آن، هیچ پستی و بلندی نمی‌بینی!»'];
ayat[2456]=[20,108,'يَوْمَئِذٍ يَتَّبِعُونَ الدَّاعِيَ لَا عِوَجَ لَهُ ۖ وَخَشَعَتِ الْأَصْوَاتُ لِلرَّحْمَٰنِ فَلَا تَسْمَعُ إِلَّا هَمْسًا','در آن روز، همه از دعوت کننده الهی پیروی نموده، و قدرت بر مخالفت او نخواهند داشت (و همگی از قبرها برمی‌خیزند)؛ و همه صداها در برابر (عظمت) خداوند رحمان، خاضع می‌شود؛ و جز صدای آهسته چیزی نمی‌شنوی!'];
ayat[2457]=[20,109,'يَوْمَئِذٍ لَّا تَنفَعُ الشَّفَاعَةُ إِلَّا مَنْ أَذِنَ لَهُ الرَّحْمَٰنُ وَرَضِيَ لَهُ قَوْلًا','در آن روز، شفاعت هیچ کس سودی نمی‌بخشد، جز کسی که خداوند رحمان به او اجازه داده، و به گفتار او راضی است.'];
ayat[2458]=[20,110,'يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيطُونَ بِهِ عِلْمًا','آنچه را پیش رو دارند، و آنچه را (در دنیا) پشت سرگذاشته‌اند می‌داند؛ ولی آنها به (علم) او احاطه ندارند!'];
ayat[2459]=[20,111,'۞ وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ ۖ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا','و (در آن روز) همه چهره‌ها در برابر خداوند حیّ قیوم، خاضع می‌شود؛ و مأیوس (و زیانکار) است آن که بار ستمی بر دوش دارد!'];
ayat[2460]=[20,112,'وَمَن يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا يَخَافُ ظُلْمًا وَلَا هَضْمًا','(امّا) آن کس که کارهای شایسته انجام دهد، در حالی که مؤمن باشد، نه از ظلمی می‌ترسد، و نه از نقصان حقش.'];
ayat[2461]=[20,113,'وَكَذَٰلِكَ أَنزَلْنَاهُ قُرْآنًا عَرَبِيًّا وَصَرَّفْنَا فِيهِ مِنَ الْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لَهُمْ ذِكْرًا','و این گونه آن را قرآنی عربی [= فصیح و گویا] نازل کردیم، و انواع وعیدها (و انذارها) را در آن بازگو نمودیم، شاید تقوا پیشه کنند؛ یا برای آنان تذکّری پدید آورد!'];
ayat[2462]=[20,114,'فَتَعَالَى اللَّهُ الْمَلِكُ الْحَقُّ ۗ وَلَا تَعْجَلْ بِالْقُرْآنِ مِن قَبْلِ أَن يُقْضَىٰ إِلَيْكَ وَحْيُهُ ۖ وَقُل رَّبِّ زِدْنِي عِلْمًا','پس بلندمرتبه است خداوندی که سلطان حقّ است! پس نسبت به (تلاوت) قرآن عجله مکن، پیش از آنکه وحی آن بر تو تمام شود؛ و بگو: «پروردگارا! علم مرا افزون کن!»'];
ayat[2463]=[20,115,'وَلَقَدْ عَهِدْنَا إِلَىٰ آدَمَ مِن قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهُ عَزْمًا','پیش از این، از آدم پیمان گرفته بودیم؛ امّا او فراموش کرد؛ و عزم استواری برای او نیافتیم!'];
ayat[2464]=[20,116,'وَإِذْ قُلْنَا لِلْمَلَائِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلَّا إِبْلِيسَ أَبَىٰ','و به یاد آور هنگامی را که به فرشتگان گفتیم: «برای آدم سجده کنید!» همگی سجده کردند؛ جز ابلیس که سرباز زد (و سجده نکرد)!'];
ayat[2465]=[20,117,'فَقُلْنَا يَا آدَمُ إِنَّ هَٰذَا عَدُوٌّ لَّكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقَىٰ','پس گفتیم: «ای آدم! این (ابلیس) دشمن تو و (دشمن) همسر توست! مبادا شما را از بهشت بیرون کند؛ که به زحمت و رنج خواهی افتاد!'];
ayat[2466]=[20,118,'إِنَّ لَكَ أَلَّا تَجُوعَ فِيهَا وَلَا تَعْرَىٰ','(امّا تو در بهشت راحت هستی! و مزیّتش) برای تو این است که در آن گرسنه و برهنه نخواهی شد؛'];
ayat[2467]=[20,119,'وَأَنَّكَ لَا تَظْمَأُ فِيهَا وَلَا تَضْحَىٰ','و در آن تشنه نمی‌شوی، و حرارت آفتاب آزارت نمی‌دهد!»'];
ayat[2468]=[20,120,'فَوَسْوَسَ إِلَيْهِ الشَّيْطَانُ قَالَ يَا آدَمُ هَلْ أَدُلُّكَ عَلَىٰ شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَّا يَبْلَىٰ','ولی شیطان او را وسوسه کرد و گفت: «ای آدم! آیا می‌خواهی تو را به درخت زندگی جاوید، و ملکی بی‌زوال راهنمایی کنم؟!»'];
ayat[2469]=[20,121,'فَأَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْآتُهُمَا وَطَفِقَا يَخْصِفَانِ عَلَيْهِمَا مِن وَرَقِ الْجَنَّةِ ۚ وَعَصَىٰ آدَمُ رَبَّهُ فَغَوَىٰ','سرانجام هر دو از آن خوردند، (و لباس بهشتیشان فرو ریخت،) و عورتشان آشکار گشت و برای پوشاندن خود، از برگهای (درختان) بهشتی جامه دوختند! (آری) آدم پروردگارش را نافرمانی کرد، و از پاداش او محروم شد!'];
ayat[2470]=[20,122,'ثُمَّ اجْتَبَاهُ رَبُّهُ فَتَابَ عَلَيْهِ وَهَدَىٰ','سپس پروردگارش او را برگزید، و توبه‌اش را پذیرفت، و هدایتش نمود.'];
ayat[2471]=[20,123,'قَالَ اهْبِطَا مِنْهَا جَمِيعًا ۖ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۖ فَإِمَّا يَأْتِيَنَّكُم مِّنِّي هُدًى فَمَنِ اتَّبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشْقَىٰ','(خداوند) فرمود: «هر دو از آن (بهشت) فرود آیید، در حالی که دشمن یکدیگر خواهید بود! ولی هرگاه هدایت من به سراغ شما آید، هر کس از هدایت من پیروی کند، نه گمراه می‌شود، و نه در رنج خواهد بود!'];
ayat[2472]=[20,124,'وَمَنْ أَعْرَضَ عَن ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنكًا وَنَحْشُرُهُ يَوْمَ الْقِيَامَةِ أَعْمَىٰ','و هر کس از یاد من روی گردان شود، زندگی (سخت و) تنگی خواهد داشت؛ و روز قیامت، او را نابینا محشور می‌کنیم!»'];
ayat[2473]=[20,125,'قَالَ رَبِّ لِمَ حَشَرْتَنِي أَعْمَىٰ وَقَدْ كُنتُ بَصِيرًا','می‌گوید: «پروردگارا! چرا نابینا محشورم کردی؟! من که بینا بودم!»'];
ayat[2474]=[20,126,'قَالَ كَذَٰلِكَ أَتَتْكَ آيَاتُنَا فَنَسِيتَهَا ۖ وَكَذَٰلِكَ الْيَوْمَ تُنسَىٰ','می‌فرماید: «آن گونه که آیات من برای تو آمد، و تو آنها را فراموش کردی؛ امروز نیز تو فراموش خواهی شد!»'];
ayat[2475]=[20,127,'وَكَذَٰلِكَ نَجْزِي مَنْ أَسْرَفَ وَلَمْ يُؤْمِن بِآيَاتِ رَبِّهِ ۚ وَلَعَذَابُ الْآخِرَةِ أَشَدُّ وَأَبْقَىٰ','و این گونه جزا می‌دهیم کسی را که اسراف کند، و به آیات پروردگارش ایمان نیاورد! و عذاب آخرت، شدیدتر و پایدارتر است!'];
ayat[2476]=[20,128,'أَفَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا قَبْلَهُم مِّنَ الْقُرُونِ يَمْشُونَ فِي مَسَاكِنِهِمْ ۗ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّأُولِي النُّهَىٰ','آیا برای هدایت آنان کافی نیست که بسیاری از نسلهای پیشین را (که طغیان و فساد کردند) هلاک نمودیم، و اینها در مسکنهای (ویران شده) آنان راه می‌روند! مسلّماً در این امر، نشانه‌های روشنی برای خردمندان است.'];
ayat[2477]=[20,129,'وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ لَكَانَ لِزَامًا وَأَجَلٌ مُّسَمًّى','و اگر سنّت و تقدیر پروردگارت و ملاحظه زمان مقرّر نبود، عذاب الهی بزودی دامان آنان را می‌گرفت!'];
ayat[2478]=[20,130,'فَاصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ غُرُوبِهَا ۖ وَمِنْ آنَاءِ اللَّيْلِ فَسَبِّحْ وَأَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضَىٰ','پس در برابر آنچه می‌گویند، صبر کن! و پیش از طلوع آفتاب، و قبل از غروب آن؛ تسبیح و حمد پروردگارت را بجا آور؛ و همچنین (برخی) از ساعات شب و اطراف روز (پروردگارت را) تسبیح گوی؛ باشد که (از الطاف الهی) خشنود شوی!'];
ayat[2479]=[20,131,'وَلَا تَمُدَّنَّ عَيْنَيْكَ إِلَىٰ مَا مَتَّعْنَا بِهِ أَزْوَاجًا مِّنْهُمْ زَهْرَةَ الْحَيَاةِ الدُّنْيَا لِنَفْتِنَهُمْ فِيهِ ۚ وَرِزْقُ رَبِّكَ خَيْرٌ وَأَبْقَىٰ','و هرگز چشمان خود را به نعمتهای مادّی، که به گروه‌هایی از آنان داده‌ایم، میفکن! اینها شکوفه‌های زندگی دنیاست؛ تا آنان را در آن بیازماییم؛ و روزی پروردگارت بهتر و پایدارتر است!'];
ayat[2480]=[20,132,'وَأْمُرْ أَهْلَكَ بِالصَّلَاةِ وَاصْطَبِرْ عَلَيْهَا ۖ لَا نَسْأَلُكَ رِزْقًا ۖ نَّحْنُ نَرْزُقُكَ ۗ وَالْعَاقِبَةُ لِلتَّقْوَىٰ','خانواده خود را به نماز فرمان ده؛ و بر انجام آن شکیبا باش! از تو روزی نمی‌خواهیم؛ (بلکه) ما به تو روزی می‌دهیم؛ و عاقبت نیک برای تقواست!'];
ayat[2481]=[20,133,'وَقَالُوا لَوْلَا يَأْتِينَا بِآيَةٍ مِّن رَّبِّهِ ۚ أَوَلَمْ تَأْتِهِم بَيِّنَةُ مَا فِي الصُّحُفِ الْأُولَىٰ','گفتند: «چرا (پیامبر) معجزه و نشانه‌ای از سوی پروردگارش برای ما نمی‌آورد؟! (بگو:) آیا خبرهای روشنی که در کتابهای (آسمانی) نخستین بوده، برای آنها نیامد؟!'];
ayat[2482]=[20,134,'وَلَوْ أَنَّا أَهْلَكْنَاهُم بِعَذَابٍ مِّن قَبْلِهِ لَقَالُوا رَبَّنَا لَوْلَا أَرْسَلْتَ إِلَيْنَا رَسُولًا فَنَتَّبِعَ آيَاتِكَ مِن قَبْلِ أَن نَّذِلَّ وَنَخْزَىٰ','اگر ما آنان را پیش از آن (که قرآن نازل شود) با عذابی هلاک می‌کردیم، (در قیامت) می‌گفتند: «پروردگارا! چرا پیامبری برای ما نفرستادی تا از آیات تو پیروی کنیم، پیش از آنکه ذلیل و رسوا شویم!»'];
ayat[2483]=[20,135,'قُلْ كُلٌّ مُّتَرَبِّصٌ فَتَرَبَّصُوا ۖ فَسَتَعْلَمُونَ مَنْ أَصْحَابُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدَىٰ','بگو: «همه (ما و شما) در انتظاریم! (ما در انتظار وعده پیروزی، و شما در انتظار شکست ما!) حال که چنین است، انتظار بکشید! امّا بزودی می‌دانید چه کسی از اصحاب صراط مستقیم، و چه کسی هدایت یافته است!'];
ayat[2484]=[21,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ اقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِي غَفْلَةٍ مُّعْرِضُونَ','حساب مردم به آنان نزدیک شده، در حالی که در غفلتند و روی گردانند!'];
ayat[2485]=[21,2,'مَا يَأْتِيهِم مِّن ذِكْرٍ مِّن رَّبِّهِم مُّحْدَثٍ إِلَّا اسْتَمَعُوهُ وَهُمْ يَلْعَبُونَ','هیچ یادآوری تازه‌ای از طرف پروردگارشان برای آنها نمی‌آید، مگر آنکه با بازی (و شوخی) به آن گوش می‌دهند!'];
ayat[2486]=[21,3,'لَاهِيَةً قُلُوبُهُمْ ۗ وَأَسَرُّوا النَّجْوَى الَّذِينَ ظَلَمُوا هَلْ هَٰذَا إِلَّا بَشَرٌ مِّثْلُكُمْ ۖ أَفَتَأْتُونَ السِّحْرَ وَأَنتُمْ تُبْصِرُونَ','این در حالی است که دلهایشان در لهو و بی‌خبری فرو رفته است! و ستمگران پنهانی نجوا کردند (و گفتند): «آیا جز این است که او بشری همانند شماست؟! آیا به سراغ سحر می‌روید، با اینکه (چشم دارید و) می‌بینید؟!'];
ayat[2487]=[21,4,'قَالَ رَبِّي يَعْلَمُ الْقَوْلَ فِي السَّمَاءِ وَالْأَرْضِ ۖ وَهُوَ السَّمِيعُ الْعَلِيمُ','(پیامبر) گفت: «پروردگارم همه سخنان را، چه در آسمان باشد و چه در زمین، می‌داند؛ و او شنوا و داناست!»'];
ayat[2488]=[21,5,'بَلْ قَالُوا أَضْغَاثُ أَحْلَامٍ بَلِ افْتَرَاهُ بَلْ هُوَ شَاعِرٌ فَلْيَأْتِنَا بِآيَةٍ كَمَا أُرْسِلَ الْأَوَّلُونَ','آنها گفتند: «(آنچه محمّد (ص) آورده وحی نیست؛) بلکه خوابهایی آشفته است! اصلاً آن را بدروغ به خدا بسته؛ نه، بلکه او یک شاعر است! (اگر راست می‌گوید) باید معجزه‌ای برای ما بیاورد؛ همان گونه که پیامبران پیشین (با معجزات) فرستاده شدند!»'];
ayat[2489]=[21,6,'مَا آمَنَتْ قَبْلَهُم مِّن قَرْيَةٍ أَهْلَكْنَاهَا ۖ أَفَهُمْ يُؤْمِنُونَ','تمام آبادیهایی که پیش از اینها هلاک کردیم (تقاضای معجزات گوناگون کردند، و خواسته آنان عملی شد، ولی) هرگز ایمان نیاوردند؛ آیا اینها ایمان می‌آورند؟!'];
ayat[2490]=[21,7,'وَمَا أَرْسَلْنَا قَبْلَكَ إِلَّا رِجَالًا نُّوحِي إِلَيْهِمْ ۖ فَاسْأَلُوا أَهْلَ الذِّكْرِ إِن كُنتُمْ لَا تَعْلَمُونَ','ما پیش از تو، جز مردانی که به آنان وحی می‌کردیم، نفرستادیم! (همه انسان بودند، و از جنس بشر!) اگر نمی‌دانید، از آگاهان بپرسید.'];
ayat[2491]=[21,8,'وَمَا جَعَلْنَاهُمْ جَسَدًا لَّا يَأْكُلُونَ الطَّعَامَ وَمَا كَانُوا خَالِدِينَ','آنان را پیکرهایی که غذا نخورند قرار ندادیم! عمر جاویدان هم نداشتند!'];
ayat[2492]=[21,9,'ثُمَّ صَدَقْنَاهُمُ الْوَعْدَ فَأَنجَيْنَاهُمْ وَمَن نَّشَاءُ وَأَهْلَكْنَا الْمُسْرِفِينَ','سپس وعده‌ای را که به آنان داده بودیم، وفا کردیم! آنها و هر کس را که می‌خواستیم (از چنگ دشمنانشان) نجات دادیم؛ و مسرفان را هلاک نمودیم!'];
ayat[2493]=[21,10,'لَقَدْ أَنزَلْنَا إِلَيْكُمْ كِتَابًا فِيهِ ذِكْرُكُمْ ۖ أَفَلَا تَعْقِلُونَ','ما بر شما کتابی نازل کردیم که وسیله تذکّر (و بیداری) شما در آن است! آیا نمی‌فهمید؟!'];
ayat[2494]=[21,11,'وَكَمْ قَصَمْنَا مِن قَرْيَةٍ كَانَتْ ظَالِمَةً وَأَنشَأْنَا بَعْدَهَا قَوْمًا آخَرِينَ','چه بسیار آبادیهای ستمگری را در هم شکستیم؛ و بعد از آنها، قوم دیگری روی کار آوردیم!'];
ayat[2495]=[21,12,'فَلَمَّا أَحَسُّوا بَأْسَنَا إِذَا هُم مِّنْهَا يَرْكُضُونَ','هنگامی که عذاب ما را احساس کردند، ناگهان پا به فرار گذاشتند!'];
ayat[2496]=[21,13,'لَا تَرْكُضُوا وَارْجِعُوا إِلَىٰ مَا أُتْرِفْتُمْ فِيهِ وَمَسَاكِنِكُمْ لَعَلَّكُمْ تُسْأَلُونَ','(گفتیم:) فرار نکنید؛ و به زندگی پر ناز و نعمت، و به مسکنهای پر زرق و برقتان بازگردید! شاید (سائلان بیایند و) از شما تقاضا کنند (شما هم آنان را محروم بازگردانید)!'];
ayat[2497]=[21,14,'قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ','گفتند: «ای وای بر ما! به یقین ما ستمگر بودیم!»'];
ayat[2498]=[21,15,'فَمَا زَالَت تِّلْكَ دَعْوَاهُمْ حَتَّىٰ جَعَلْنَاهُمْ حَصِيدًا خَامِدِينَ','و همچنان این سخن را تکرار می‌کردند، تا آنها را درو کرده و خاموش ساختیم!'];
ayat[2499]=[21,16,'وَمَا خَلَقْنَا السَّمَاءَ وَالْأَرْضَ وَمَا بَيْنَهُمَا لَاعِبِينَ','ما آسمان و زمین، و آنچه را در میان آنهاست از روی بازی نیافریدیم!'];
ayat[2500]=[21,17,'لَوْ أَرَدْنَا أَن نَّتَّخِذَ لَهْوًا لَّاتَّخَذْنَاهُ مِن لَّدُنَّا إِن كُنَّا فَاعِلِينَ','(بفرض محال) اگر می‌خواستیم سرگرمی انتخاب کنیم، چیزی متناسب خود انتخاب می‌کردیم!'];
ayat[2501]=[21,18,'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهُ فَإِذَا هُوَ زَاهِقٌ ۚ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُونَ','بلکه ما حق را بر سر باطل می‌کوبیم تا آن را هلاک سازد؛ و این گونه، باطل محو و نابود می‌شود! امّا وای بر شما از توصیفی که (درباره خدا و هدف آفرینش) می‌کنید!'];
ayat[2502]=[21,19,'وَلَهُ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَمَنْ عِندَهُ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِ وَلَا يَسْتَحْسِرُونَ','از آن اوست آنان که در آسمانها و زمینند! و آنها که نزد اویند [= فرشتگان‌] هیچ گاه از عبادتش استکبار نمی‌ورزند، و هرگز خسته نمی‌شوند.'];
ayat[2503]=[21,20,'يُسَبِّحُونَ اللَّيْلَ وَالنَّهَارَ لَا يَفْتُرُونَ','(تمام) شب و روز را تسبیح می‌گویند؛ و سست نمی‌گردند.'];
ayat[2504]=[21,21,'أَمِ اتَّخَذُوا آلِهَةً مِّنَ الْأَرْضِ هُمْ يُنشِرُونَ','آیا آنها خدایانی از زمین برگزیدند که (خلق می‌کنند و) منتشر می‌سازند؟!'];
ayat[2505]=[21,22,'لَوْ كَانَ فِيهِمَا آلِهَةٌ إِلَّا اللَّهُ لَفَسَدَتَا ۚ فَسُبْحَانَ اللَّهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ','اگر در آسمان و زمین، جز «اللّه» خدایان دیگری بود، فاسد می‌شدند (و نظام جهان به هم می‌خورد)! منزه است خداوند پروردگار عرش، از توصیفی که آنها می‌کنند!'];
ayat[2506]=[21,23,'لَا يُسْأَلُ عَمَّا يَفْعَلُ وَهُمْ يُسْأَلُونَ','هیچ کس نمی‌تواند بر کار او خرده بگیرد؛ ولی در کارهای آنها، جای سؤال و ایراد است!'];
ayat[2507]=[21,24,'أَمِ اتَّخَذُوا مِن دُونِهِ آلِهَةً ۖ قُلْ هَاتُوا بُرْهَانَكُمْ ۖ هَٰذَا ذِكْرُ مَن مَّعِيَ وَذِكْرُ مَن قَبْلِي ۗ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ الْحَقَّ ۖ فَهُم مُّعْرِضُونَ','آیا آنها معبودانی جز خدا برگزیدند؟! بگو: «دلیلتان را بیاورید! این سخن کسانی است که با من هستند، و سخن کسانی [= پیامبرانی‌] است که پیش از من بودند!» امّا بیشتر آنها حق را نمی‌دانند؛ و به همین دلیل (از آن) روی گردانند.'];
ayat[2508]=[21,25,'وَمَا أَرْسَلْنَا مِن قَبْلِكَ مِن رَّسُولٍ إِلَّا نُوحِي إِلَيْهِ أَنَّهُ لَا إِلَٰهَ إِلَّا أَنَا فَاعْبُدُونِ','ما پیش از تو هیچ پیامبری را نفرستادیم مگر اینکه به او وحی کردیم که: «معبودی جز من نیست؛ پس تنها مرا پرستش کنید.»'];
ayat[2509]=[21,26,'وَقَالُوا اتَّخَذَ الرَّحْمَٰنُ وَلَدًا ۗ سُبْحَانَهُ ۚ بَلْ عِبَادٌ مُّكْرَمُونَ','آنها گفتند: «خداوند رحمان فرزندی برای خود انتخاب کرده است»! او منزه است (از این عیب و نقص)؛ آنها [= فرشتگان‌] بندگان شایسته اویند.'];
ayat[2510]=[21,27,'لَا يَسْبِقُونَهُ بِالْقَوْلِ وَهُم بِأَمْرِهِ يَعْمَلُونَ','هرگز در سخن بر او پیشی نمی‌گیرند؛ و (پیوسته) به فرمان او عمل می‌کنند.'];
ayat[2511]=[21,28,'يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُونَ إِلَّا لِمَنِ ارْتَضَىٰ وَهُم مِّنْ خَشْيَتِهِ مُشْفِقُونَ','او اعمال امروز و آینده و اعمال گذشته آنها را می‌داند؛ و آنها جز برای کسی که خدا راضی (به شفاعت برای او) است شفاعت نمی‌کنند؛ و از ترس او بیمناکند.'];
ayat[2512]=[21,29,'۞ وَمَن يَقُلْ مِنْهُمْ إِنِّي إِلَٰهٌ مِّن دُونِهِ فَذَٰلِكَ نَجْزِيهِ جَهَنَّمَ ۚ كَذَٰلِكَ نَجْزِي الظَّالِمِينَ','و هر کس از آنها بگوید: «من جز خدا، معبودی دیگرم»، کیفر او را جهنم می‌دهیم! و ستمگران را این گونه کیفر خواهیم داد.'];
ayat[2513]=[21,30,'أَوَلَمْ يَرَ الَّذِينَ كَفَرُوا أَنَّ السَّمَاوَاتِ وَالْأَرْضَ كَانَتَا رَتْقًا فَفَتَقْنَاهُمَا ۖ وَجَعَلْنَا مِنَ الْمَاءِ كُلَّ شَيْءٍ حَيٍّ ۖ أَفَلَا يُؤْمِنُونَ','آیا کافران ندیدند که آسمانها و زمین به هم پیوسته بودند، و ما آنها را از یکدیگر باز کردیم؛ و هر چیز زنده‌ای را از آب قرار دادیم؟! آیا ایمان نمی‌آورند؟!'];
ayat[2514]=[21,31,'وَجَعَلْنَا فِي الْأَرْضِ رَوَاسِيَ أَن تَمِيدَ بِهِمْ وَجَعَلْنَا فِيهَا فِجَاجًا سُبُلًا لَّعَلَّهُمْ يَهْتَدُونَ','و در زمین، کوه‌های ثابت و پابرجایی قرار دادیم، مبادا آنها را بلرزاند! و در آن، درّه‌ها و راه‌هایی قرار دادیم تا هدایت شوند!'];
ayat[2515]=[21,32,'وَجَعَلْنَا السَّمَاءَ سَقْفًا مَّحْفُوظًا ۖ وَهُمْ عَنْ آيَاتِهَا مُعْرِضُونَ','و آسمان را سقف محفوظی قرار دادیم؛ ولی آنها از آیات آن روی‌گردانند.'];
ayat[2516]=[21,33,'وَهُوَ الَّذِي خَلَقَ اللَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَ ۖ كُلٌّ فِي فَلَكٍ يَسْبَحُونَ','او کسی است که شب و روز و خورشید و ماه را آفرید؛ هر یک در مداری در حرکتند!'];
ayat[2517]=[21,34,'وَمَا جَعَلْنَا لِبَشَرٍ مِّن قَبْلِكَ الْخُلْدَ ۖ أَفَإِن مِّتَّ فَهُمُ الْخَالِدُونَ','پیش از تو (نیز) برای هیچ انسانی جاودانگی قرار ندادیم؛ (وانگهی آنها که انتظار مرگ تو را می‌کشند،) آیا اگر تو بمیری، آنان جاوید خواهند بود؟!'];
ayat[2518]=[21,35,'كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ۗ وَنَبْلُوكُم بِالشَّرِّ وَالْخَيْرِ فِتْنَةً ۖ وَإِلَيْنَا تُرْجَعُونَ','هر انسانی طعم مرگ را می‌چشد! و شما را با بدیها و خوبیها آزمایش می‌کنیم؛ و سرانجام بسوی ما بازگردانده می‌شوید!'];
ayat[2519]=[21,36,'وَإِذَا رَآكَ الَّذِينَ كَفَرُوا إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا الَّذِي يَذْكُرُ آلِهَتَكُمْ وَهُم بِذِكْرِ الرَّحْمَٰنِ هُمْ كَافِرُونَ','هنگامی که کافران تو را می‌بینند، کاری جز استهزا کردن تو ندارند؛ (و می‌گویند:) آیا این همان کسی است که سخن از خدایان شما می‌گوید؟! در حالی که خودشان ذکر خداوند رحمان را انکار می‌کنند.'];
ayat[2520]=[21,37,'خُلِقَ الْإِنسَانُ مِنْ عَجَلٍ ۚ سَأُرِيكُمْ آيَاتِي فَلَا تَسْتَعْجِلُونِ','(آری،) انسان از عجله آفریده شده؛ ولی عجله نکنید؛ بزودی آیاتم را به شما نشان خواهم داد!'];
ayat[2521]=[21,38,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِن كُنتُمْ صَادِقِينَ','آنها می‌گویند: «اگر راست میگویید، این وعده (قیامت) کی فرا می‌رسد؟!»'];
ayat[2522]=[21,39,'لَوْ يَعْلَمُ الَّذِينَ كَفَرُوا حِينَ لَا يَكُفُّونَ عَن وُجُوهِهِمُ النَّارَ وَلَا عَن ظُهُورِهِمْ وَلَا هُمْ يُنصَرُونَ','ولی اگر کافران می‌دانستند زمانی که (فرا می‌رسد) نمی‌توانند شعله‌های آتش را از صورت و از پشتهای خود دور کنند، و هیچ کس آنان را یاری نمی‌کند (این قدر درباره قیامت شتاب نمی‌کردند)!'];
ayat[2523]=[21,40,'بَلْ تَأْتِيهِم بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَطِيعُونَ رَدَّهَا وَلَا هُمْ يُنظَرُونَ','(آری، این مجازات الهی) بطور ناگهانی به سراغشان می‌آید و مبهوتشان می‌کند؛ آنچنان که توانایی دفع آن را ندارند، و به آنها مهلت داده نمی‌شود!'];
ayat[2524]=[21,41,'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّن قَبْلِكَ فَحَاقَ بِالَّذِينَ سَخِرُوا مِنْهُم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','(اگر تو را استهزا کنند نگران نباش،) پیامبران پیش از تو را (نیز) استهزا کردند؛ امّا سرانجام، آنچه را استهزا می‌کردند دامان مسخره‌کنندگان را گرفت (و مجازات الهی آنها را در هم کوبید)!'];
ayat[2525]=[21,42,'قُلْ مَن يَكْلَؤُكُم بِاللَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمَٰنِ ۗ بَلْ هُمْ عَن ذِكْرِ رَبِّهِم مُّعْرِضُونَ','بگو: «چه کسی شما را در شب و روز از (مجازات) خداوند بخشنده نگاه می‌دارد؟!» ولی آنان از یاد پروردگارشان روی‌گردانند!'];
ayat[2526]=[21,43,'أَمْ لَهُمْ آلِهَةٌ تَمْنَعُهُم مِّن دُونِنَا ۚ لَا يَسْتَطِيعُونَ نَصْرَ أَنفُسِهِمْ وَلَا هُم مِّنَّا يُصْحَبُونَ','آیا آنها خدایانی دارند که می‌توانند در برابر ما از آنان دفاع کنند؟! (این خدایان ساختگی، حتّی) نمی‌توانند خودشان را یاری دهند (تا چه رسد به دیگران)؛ و نه از ناحیه ما با نیرویی یاری می‌شوند!'];
ayat[2527]=[21,44,'بَلْ مَتَّعْنَا هَٰؤُلَاءِ وَآبَاءَهُمْ حَتَّىٰ طَالَ عَلَيْهِمُ الْعُمُرُ ۗ أَفَلَا يَرَوْنَ أَنَّا نَأْتِي الْأَرْضَ نَنقُصُهَا مِنْ أَطْرَافِهَا ۚ أَفَهُمُ الْغَالِبُونَ','ما آنها و پدرانشان را (از نعمتها) بهره‌مند ساختیم، تا آنجا که عمر طولانی پیدا کردند (و مایه غرور و طغیانشان شد)؛ آیا نمی‌بینند که ما پیوسته به سراغ زمین آمده، و از آن (و اهلش) می‌کاهیم؟! آیا آنها غالبند (یا ما)؟!'];
ayat[2528]=[21,45,'قُلْ إِنَّمَا أُنذِرُكُم بِالْوَحْيِ ۚ وَلَا يَسْمَعُ الصُّمُّ الدُّعَاءَ إِذَا مَا يُنذَرُونَ','بگو: «من تنها بوسیله وحی شما را انذار می‌کنم!» ولی آنها که گوشهایشان کر است، هنگامی که انذار می‌شوند، سخنان را نمی‌شنوند!'];
ayat[2529]=[21,46,'وَلَئِن مَّسَّتْهُمْ نَفْحَةٌ مِّنْ عَذَابِ رَبِّكَ لَيَقُولُنَّ يَا وَيْلَنَا إِنَّا كُنَّا ظَالِمِينَ','اگر کمترین عذاب پروردگارت به آنان برسد، فریادشان بلند می‌شود که: «ای وای بر ما! ما همگی ستمگر بودیم!»'];
ayat[2530]=[21,47,'وَنَضَعُ الْمَوَازِينَ الْقِسْطَ لِيَوْمِ الْقِيَامَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْئًا ۖ وَإِن كَانَ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ أَتَيْنَا بِهَا ۗ وَكَفَىٰ بِنَا حَاسِبِينَ','ما ترازوهای عدل را در روز قیامت برپا می‌کنیم؛ پس به هیچ کس کمترین ستمی نمی‌شود؛ و اگر بمقدار سنگینی یک دانه خردل (کار نیک و بدی) باشد، ما آن را حاضر می‌کنیم؛ و کافی است که ما حساب‌کننده باشیم!'];
ayat[2531]=[21,48,'وَلَقَدْ آتَيْنَا مُوسَىٰ وَهَارُونَ الْفُرْقَانَ وَضِيَاءً وَذِكْرًا لِّلْمُتَّقِينَ','ما به موسی و هارون، «فرقان» [= وسیله جدا کردن حق از باطل‌] و نور، و آنچه مایه یادآوری برای پرهیزگاران است، دادیم.'];
ayat[2532]=[21,49,'الَّذِينَ يَخْشَوْنَ رَبَّهُم بِالْغَيْبِ وَهُم مِّنَ السَّاعَةِ مُشْفِقُونَ','همانان که از پروردگارشان در نهان می‌ترسند، و از قیامت بیم دارند!'];
ayat[2533]=[21,50,'وَهَٰذَا ذِكْرٌ مُّبَارَكٌ أَنزَلْنَاهُ ۚ أَفَأَنتُمْ لَهُ مُنكِرُونَ','و این (قرآن) ذکر مبارکی است که (بر شما) نازل کردیم؛ آیا شما آن را انکار می‌کنید؟!'];
ayat[2534]=[21,51,'۞ وَلَقَدْ آتَيْنَا إِبْرَاهِيمَ رُشْدَهُ مِن قَبْلُ وَكُنَّا بِهِ عَالِمِينَ','ما وسیله رشد ابراهیم را از قبل به او دادیم؛ و از (شایستگی) او آگاه بودیم...'];
ayat[2535]=[21,52,'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَا هَٰذِهِ التَّمَاثِيلُ الَّتِي أَنتُمْ لَهَا عَاكِفُونَ','آن هنگام که به پدرش (آزر) و قوم او گفت: «این مجسمه‌های بی‌روح چیست که شما همواره آنها را پرستش می‌کنید؟!»'];
ayat[2536]=[21,53,'قَالُوا وَجَدْنَا آبَاءَنَا لَهَا عَابِدِينَ','گفتند: «ما پدران خود را دیدیم که آنها را عبادت می‌کنند.»'];
ayat[2537]=[21,54,'قَالَ لَقَدْ كُنتُمْ أَنتُمْ وَآبَاؤُكُمْ فِي ضَلَالٍ مُّبِينٍ','گفت: «مسلّماً هم شما و هم پدرانتان، در گمراهی آشکاری بوده‌اید!»'];
ayat[2538]=[21,55,'قَالُوا أَجِئْتَنَا بِالْحَقِّ أَمْ أَنتَ مِنَ اللَّاعِبِينَ','گفتند: «آیا مطلب حقّی برای ما آورده‌ای، یا شوخی می‌کنی؟!»'];
ayat[2539]=[21,56,'قَالَ بَل رَّبُّكُمْ رَبُّ السَّمَاوَاتِ وَالْأَرْضِ الَّذِي فَطَرَهُنَّ وَأَنَا عَلَىٰ ذَٰلِكُم مِّنَ الشَّاهِدِينَ','گفت: «(کاملاً حقّ آورده‌ام) پروردگار شما همان پروردگار آسمانها و زمین است که آنها را ایجاد کرده؛ و من بر این امر، از گواهانم!'];
ayat[2540]=[21,57,'وَتَاللَّهِ لَأَكِيدَنَّ أَصْنَامَكُم بَعْدَ أَن تُوَلُّوا مُدْبِرِينَ','و به خدا سوگند، در غیاب شما، نقشه‌ای برای نابودی بتهایتان می‌کشم!»'];
ayat[2541]=[21,58,'فَجَعَلَهُمْ جُذَاذًا إِلَّا كَبِيرًا لَّهُمْ لَعَلَّهُمْ إِلَيْهِ يَرْجِعُونَ','سرانجام (با استفاده از یک فرصت مناسب)، همه آنها -جز بت بزرگشان- را قطعه قطعه کرد؛ شاید سراغ او بیایند (و او حقایق را بازگو کند)!'];
ayat[2542]=[21,59,'قَالُوا مَن فَعَلَ هَٰذَا بِآلِهَتِنَا إِنَّهُ لَمِنَ الظَّالِمِينَ','(هنگامی که منظره بتها را دیدند،) گفتند: «هر کس با خدایان ما چنین کرده، قطعاً از ستمگران است (و باید کیفر سخت ببیند)!»'];
ayat[2543]=[21,60,'قَالُوا سَمِعْنَا فَتًى يَذْكُرُهُمْ يُقَالُ لَهُ إِبْرَاهِيمُ','(گروهی) گفتند: «شنیدیم نوجوانی از (مخالفت با) بتها سخن می‌گفت که او را ابراهیم می‌گویند.»'];
ayat[2544]=[21,61,'قَالُوا فَأْتُوا بِهِ عَلَىٰ أَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُونَ','(جمعیّت) گفتند: «او را در برابر دیدگان مردم بیاورید، تا گواهی دهند!»'];
ayat[2545]=[21,62,'قَالُوا أَأَنتَ فَعَلْتَ هَٰذَا بِآلِهَتِنَا يَا إِبْرَاهِيمُ','(هنگامی که ابراهیم را حاضر کردند،) گفتند: «تو این کار را با خدایان ما کرده‌ای، ای ابراهیم؟!»'];
ayat[2546]=[21,63,'قَالَ بَلْ فَعَلَهُ كَبِيرُهُمْ هَٰذَا فَاسْأَلُوهُمْ إِن كَانُوا يَنطِقُونَ','گفت: «بلکه این کار را بزرگشان کرده است! از آنها بپرسید اگر سخن می‌گویند!»'];
ayat[2547]=[21,64,'فَرَجَعُوا إِلَىٰ أَنفُسِهِمْ فَقَالُوا إِنَّكُمْ أَنتُمُ الظَّالِمُونَ','آنها به وجدان خویش بازگشتند؛ و (به خود) گفتند: «حقّا که شما ستمگرید!»'];
ayat[2548]=[21,65,'ثُمَّ نُكِسُوا عَلَىٰ رُءُوسِهِمْ لَقَدْ عَلِمْتَ مَا هَٰؤُلَاءِ يَنطِقُونَ','سپس بر سرهایشان واژگونه شدند؛ (و حکم وجدان را بکلّی فراموش کردند و گفتند:) تو می‌دانی که اینها سخن نمی‌گویند!'];
ayat[2549]=[21,66,'قَالَ أَفَتَعْبُدُونَ مِن دُونِ اللَّهِ مَا لَا يَنفَعُكُمْ شَيْئًا وَلَا يَضُرُّكُمْ','(ابراهیم) گفت: «آیا جز خدا چیزی را می‌پرستید که نه کمترین سودی برای شما دارد، و نه زیانی به شما می‌رساند! (نه امیدی به سودشان دارید، و نه ترسی از زیانشان!)'];
ayat[2550]=[21,67,'أُفٍّ لَّكُمْ وَلِمَا تَعْبُدُونَ مِن دُونِ اللَّهِ ۖ أَفَلَا تَعْقِلُونَ','اف بر شما و بر آنچه جز خدا می‌پرستید! آیا اندیشه نمی‌کنید (و عقل ندارید)؟!'];
ayat[2551]=[21,68,'قَالُوا حَرِّقُوهُ وَانصُرُوا آلِهَتَكُمْ إِن كُنتُمْ فَاعِلِينَ','گفتند: «او را بسوزانید و خدایان خود را یاری کنید، اگر کاری از شما ساخته است!»'];
ayat[2552]=[21,69,'قُلْنَا يَا نَارُ كُونِي بَرْدًا وَسَلَامًا عَلَىٰ إِبْرَاهِيمَ','(سرانجام او را به آتش افکندند؛ ولی ما) گفتیم: «ای آتش! بر ابراهیم سرد و سالم باش!»'];
ayat[2553]=[21,70,'وَأَرَادُوا بِهِ كَيْدًا فَجَعَلْنَاهُمُ الْأَخْسَرِينَ','آنها می‌خواستند ابراهیم را با این نقشه نابود کنند؛ ولی ما آنها را زیانکارترین مردم قرار دادیم!'];
ayat[2554]=[21,71,'وَنَجَّيْنَاهُ وَلُوطًا إِلَى الْأَرْضِ الَّتِي بَارَكْنَا فِيهَا لِلْعَالَمِينَ','و او و لوط را به سرزمین (شام) -که آن را برای همه جهانیان پربرکت ساختیم- نجات دادیم!'];
ayat[2555]=[21,72,'وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ نَافِلَةً ۖ وَكُلًّا جَعَلْنَا صَالِحِينَ','و اسحاق، و علاوه بر او، یعقوب را به وی بخشیدیم؛ و همه آنان را مردانی صالح قرار دادیم!'];
ayat[2556]=[21,73,'وَجَعَلْنَاهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا وَأَوْحَيْنَا إِلَيْهِمْ فِعْلَ الْخَيْرَاتِ وَإِقَامَ الصَّلَاةِ وَإِيتَاءَ الزَّكَاةِ ۖ وَكَانُوا لَنَا عَابِدِينَ','و آنان را پیشوایانی قرار دادیم که به فرمان ما، (مردم را) هدایت می‌کردند؛ و انجام کارهای نیک و برپاداشتن نماز و ادای زکات را به آنها وحی کردیم؛ و تنها ما را عبادت می‌کردند.'];
ayat[2557]=[21,74,'وَلُوطًا آتَيْنَاهُ حُكْمًا وَعِلْمًا وَنَجَّيْنَاهُ مِنَ الْقَرْيَةِ الَّتِي كَانَت تَّعْمَلُ الْخَبَائِثَ ۗ إِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَاسِقِينَ','و لوط را (به یاد آور) که به او حکومت و علم دادیم؛ و از شهری که اعمال زشت و کثیف انجام می‌دادند، رهایی بخشیدیم؛ چرا که آنها مردم بد و فاسقی بودند!'];
ayat[2558]=[21,75,'وَأَدْخَلْنَاهُ فِي رَحْمَتِنَا ۖ إِنَّهُ مِنَ الصَّالِحِينَ','و او را در رحمت خود داخل کردیم؛ و او از صالحان بود.'];
ayat[2559]=[21,76,'وَنُوحًا إِذْ نَادَىٰ مِن قَبْلُ فَاسْتَجَبْنَا لَهُ فَنَجَّيْنَاهُ وَأَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ','و نوح را (به یاد آور) هنگامی که پیش از آن (زمان، پروردگار خود را) خواند! ما دعای او را مستجاب کردیم؛ و او و خاندانش را از اندوه بزرگ نجات دادیم؛'];
ayat[2560]=[21,77,'وَنَصَرْنَاهُ مِنَ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا ۚ إِنَّهُمْ كَانُوا قَوْمَ سَوْءٍ فَأَغْرَقْنَاهُمْ أَجْمَعِينَ','و او را در برابر جمعیّتی که آیات ما را تکذیب کرده بودند یاری دادیم؛ چرا که قوم بدی بودند؛ از این رو همه آنها را غرق کردیم!'];
ayat[2561]=[21,78,'وَدَاوُودَ وَسُلَيْمَانَ إِذْ يَحْكُمَانِ فِي الْحَرْثِ إِذْ نَفَشَتْ فِيهِ غَنَمُ الْقَوْمِ وَكُنَّا لِحُكْمِهِمْ شَاهِدِينَ','و داوود و سلیمان را (به خاطر بیاور) هنگامی که درباره کشتزاری که گوسفندان بی شبان قوم، شبانگاه در آن چریده (و آن را تباه کرده) بودند، داوری می‌کردند؛ و ما بر حکم آنان شاهد بودیم.'];
ayat[2562]=[21,79,'فَفَهَّمْنَاهَا سُلَيْمَانَ ۚ وَكُلًّا آتَيْنَا حُكْمًا وَعِلْمًا ۚ وَسَخَّرْنَا مَعَ دَاوُودَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَ ۚ وَكُنَّا فَاعِلِينَ','ما (حکم واقعی) آن را به سلیمان فهماندیم؛ و به هر یک از آنان (شایستگی) داوری، و علم فراوانی دادیم؛ و کوه‌ها و پرندگان را با داوود مسخّر ساختیم، که (همراه او) تسبیح (خدا) می‌گفتند؛ و ما این کار را انجام دادیم!'];
ayat[2563]=[21,80,'وَعَلَّمْنَاهُ صَنْعَةَ لَبُوسٍ لَّكُمْ لِتُحْصِنَكُم مِّن بَأْسِكُمْ ۖ فَهَلْ أَنتُمْ شَاكِرُونَ','و ساختن زره را بخاطر شما به او تعلیم دادیم، تا شما را در جنگهایتان حفظ کند؛ آیا شکرگزار (این نعمتهای خدا) هستید؟'];
ayat[2564]=[21,81,'وَلِسُلَيْمَانَ الرِّيحَ عَاصِفَةً تَجْرِي بِأَمْرِهِ إِلَى الْأَرْضِ الَّتِي بَارَكْنَا فِيهَا ۚ وَكُنَّا بِكُلِّ شَيْءٍ عَالِمِينَ','و تندباد را مسخّر سلیمان ساختیم، که بفرمان او بسوی سرزمینی که آن را پربرکت کرده بودیم جریان می‌یافت؛ و ما از همه چیز آگاه بوده‌ایم.'];
ayat[2565]=[21,82,'وَمِنَ الشَّيَاطِينِ مَن يَغُوصُونَ لَهُ وَيَعْمَلُونَ عَمَلًا دُونَ ذَٰلِكَ ۖ وَكُنَّا لَهُمْ حَافِظِينَ','و گروهی از شیاطین (را نیز مسخّر او قرار دادیم، که در دریا) برایش غوّاصی می‌کردند؛ و کارهایی غیر از این (نیز) برای او انجام می‌دادند؛ و ما آنها را (از سرکشی) حفظ می‌کردیم!'];
ayat[2566]=[21,83,'۞ وَأَيُّوبَ إِذْ نَادَىٰ رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ وَأَنتَ أَرْحَمُ الرَّاحِمِينَ','و ایّوب را (به یاد آور) هنگامی که پروردگارش را خواند (و عرضه داشت): «بدحالی و مشکلات به من روی آورده؛ و تو مهربانترین مهربانانی!»'];
ayat[2567]=[21,84,'فَاسْتَجَبْنَا لَهُ فَكَشَفْنَا مَا بِهِ مِن ضُرٍّ ۖ وَآتَيْنَاهُ أَهْلَهُ وَمِثْلَهُم مَّعَهُمْ رَحْمَةً مِّنْ عِندِنَا وَذِكْرَىٰ لِلْعَابِدِينَ','ما دعای او را مستجاب کردیم؛ و ناراحتیهایی را که داشت برطرف ساختیم؛ و خاندانش را به او بازگرداندیم؛ و همانندشان را بر آنها افزودیم؛ تا رحمتی از سوی ما و تذکّری برای عبادت‌کنندگان باشد.'];
ayat[2568]=[21,85,'وَإِسْمَاعِيلَ وَإِدْرِيسَ وَذَا الْكِفْلِ ۖ كُلٌّ مِّنَ الصَّابِرِينَ','و اسماعیل و ادریس و ذاالکفل را (به یاد آور) که همه از صابران بودند.'];
ayat[2569]=[21,86,'وَأَدْخَلْنَاهُمْ فِي رَحْمَتِنَا ۖ إِنَّهُم مِّنَ الصَّالِحِينَ','و ما آنان را در رحمت خود وارد ساختیم؛ چرا که آنها از صالحان بودند.'];
ayat[2570]=[21,87,'وَذَا النُّونِ إِذ ذَّهَبَ مُغَاضِبًا فَظَنَّ أَن لَّن نَّقْدِرَ عَلَيْهِ فَنَادَىٰ فِي الظُّلُمَاتِ أَن لَّا إِلَٰهَ إِلَّا أَنتَ سُبْحَانَكَ إِنِّي كُنتُ مِنَ الظَّالِمِينَ','و ذاالنون [= یونس‌] را (به یاد آور) در آن هنگام که خشمگین (از میان قوم خود) رفت؛ و چنین می‌پنداشت که ما بر او تنگ نخواهیم گرفت؛ (امّا موقعی که در کام نهنگ فرو رفت،) در آن ظلمتها (ی متراکم) صدا زد: «(خداوندا!) جز تو معبودی نیست! منزّهی تو! من از ستمکاران بودم!»'];
ayat[2571]=[21,88,'فَاسْتَجَبْنَا لَهُ وَنَجَّيْنَاهُ مِنَ الْغَمِّ ۚ وَكَذَٰلِكَ نُنجِي الْمُؤْمِنِينَ','ما دعای او را به اجابت رساندیم؛ و از آن اندوه نجاتش بخشیدیم؛ و این گونه مؤمنان را نجات می‌دهیم!'];
ayat[2572]=[21,89,'وَزَكَرِيَّا إِذْ نَادَىٰ رَبَّهُ رَبِّ لَا تَذَرْنِي فَرْدًا وَأَنتَ خَيْرُ الْوَارِثِينَ','و زکریا را (به یاد آور) در آن هنگام که پروردگارش را خواند (و عرض کرد): «پروردگار من! مرا تنها مگذار (و فرزند برومندی به من عطا کن)؛ و تو بهترین وارثانی!»'];
ayat[2573]=[21,90,'فَاسْتَجَبْنَا لَهُ وَوَهَبْنَا لَهُ يَحْيَىٰ وَأَصْلَحْنَا لَهُ زَوْجَهُ ۚ إِنَّهُمْ كَانُوا يُسَارِعُونَ فِي الْخَيْرَاتِ وَيَدْعُونَنَا رَغَبًا وَرَهَبًا ۖ وَكَانُوا لَنَا خَاشِعِينَ','ما هم دعای او را پذیرفتیم، و یحیی را به او بخشیدیم؛ و همسرش را (که نازا بود) برایش آماده (بارداری) کردیم؛ چرا که آنان (خاندانی بودند که) همواره در کارهای خیر بسرعت اقدام می‌کردند؛ و در حال بیم و امید ما را می‌خواندند؛ و پیوسته برای ما (خاضع و) خاشع بودند.'];
ayat[2574]=[21,91,'وَالَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهَا مِن رُّوحِنَا وَجَعَلْنَاهَا وَابْنَهَا آيَةً لِّلْعَالَمِينَ','و به یاد آور زنی را که دامان خود را پاک نگه داشت؛ و ما از روح خود در او دمیدیم؛ و او و فرزندش [= مسیح‌] را نشانه بزرگی برای جهانیان قرار دادیم!'];
ayat[2575]=[21,92,'إِنَّ هَٰذِهِ أُمَّتُكُمْ أُمَّةً وَاحِدَةً وَأَنَا رَبُّكُمْ فَاعْبُدُونِ','این (پیامبران بزرگ و پیروانشان) همه امّت واحدی بودند (و پیرو یک هدف)؛ و من پروردگار شما هستم؛ پس مرا پرستش کنید!'];
ayat[2576]=[21,93,'وَتَقَطَّعُوا أَمْرَهُم بَيْنَهُمْ ۖ كُلٌّ إِلَيْنَا رَاجِعُونَ','(گروهی از پیروان ناآگاه آنها) کار خود را به تفرقه در میان خود کشاندند؛ (ولی سرانجام) همگی بسوی ما بازمی‌گردند!'];
ayat[2577]=[21,94,'فَمَن يَعْمَلْ مِنَ الصَّالِحَاتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِهِ وَإِنَّا لَهُ كَاتِبُونَ','و هر کس چیزی از اعمال شایسته بجا آورد، در حالی که ایمان داشته باشد، کوشش او ناسپاسی نخواهد شد؛ و ما تمام اعمال او را (برای پاداش) می‌نویسیم.'];
ayat[2578]=[21,95,'وَحَرَامٌ عَلَىٰ قَرْيَةٍ أَهْلَكْنَاهَا أَنَّهُمْ لَا يَرْجِعُونَ','و حرام است بر شهرها و آبادیهایی که (بر اثر گناه) نابودشان کردیم (که به دنیا بازگردند؛) آنها هرگز باز نخواهند گشت!'];
ayat[2579]=[21,96,'حَتَّىٰ إِذَا فُتِحَتْ يَأْجُوجُ وَمَأْجُوجُ وَهُم مِّن كُلِّ حَدَبٍ يَنسِلُونَ','تا آن زمان که «یأجوج» و «مأجوج» گشوده شوند؛ و آنها از هر محلّ مرتفعی بسرعت عبور می‌کنند.'];
ayat[2580]=[21,97,'وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَإِذَا هِيَ شَاخِصَةٌ أَبْصَارُ الَّذِينَ كَفَرُوا يَا وَيْلَنَا قَدْ كُنَّا فِي غَفْلَةٍ مِّنْ هَٰذَا بَلْ كُنَّا ظَالِمِينَ','و وعده حقّ [= قیامت‌] نزدیک می‌شود؛ در آن هنگام چشمهای کافران از وحشت از حرکت بازمی‌ماند؛ (می‌گویند:) ای وای بر ما که از این (جریان) در غفلت بودیم؛ بلکه ما ستمکار بودیم!'];
ayat[2581]=[21,98,'إِنَّكُمْ وَمَا تَعْبُدُونَ مِن دُونِ اللَّهِ حَصَبُ جَهَنَّمَ أَنتُمْ لَهَا وَارِدُونَ','شما و آنچه غیر خدا می‌پرستید، هیزم جهنّم خواهید بود؛ و همگی در آن وارد می‌شوید.'];
ayat[2582]=[21,99,'لَوْ كَانَ هَٰؤُلَاءِ آلِهَةً مَّا وَرَدُوهَا ۖ وَكُلٌّ فِيهَا خَالِدُونَ','اگر اینها خدایانی بودند، هرگز وارد آن نمی‌شدند! در حالی که همگی در آن جاودانه خواهند بود.'];
ayat[2583]=[21,100,'لَهُمْ فِيهَا زَفِيرٌ وَهُمْ فِيهَا لَا يَسْمَعُونَ','برای آنان در آن [= دوزخ‌] ناله‌های دردناکی است و چیزی نمی‌شنوند.'];
ayat[2584]=[21,101,'إِنَّ الَّذِينَ سَبَقَتْ لَهُم مِّنَّا الْحُسْنَىٰ أُولَٰئِكَ عَنْهَا مُبْعَدُونَ','(امّا) کسانی که از قبل، وعده نیک از سوی ما به آنها داده شده [= مؤمنان صالح‌] از آن دور نگاهداشته می‌شوند.'];
ayat[2585]=[21,102,'لَا يَسْمَعُونَ حَسِيسَهَا ۖ وَهُمْ فِي مَا اشْتَهَتْ أَنفُسُهُمْ خَالِدُونَ','آنها صدای آتش دوزخ را نمی‌شوند؛ و در آنچه دلشان بخواهد، جاودانه متنعّم هستند.'];
ayat[2586]=[21,103,'لَا يَحْزُنُهُمُ الْفَزَعُ الْأَكْبَرُ وَتَتَلَقَّاهُمُ الْمَلَائِكَةُ هَٰذَا يَوْمُكُمُ الَّذِي كُنتُمْ تُوعَدُونَ','وحشت بزرگ، آنها را اندوهگین نمی‌کند؛ و فرشتگان به استقبالشان می‌آیند، (و می‌گویند:) این همان روزی است که به شما وعده داده می‌شد!'];
ayat[2587]=[21,104,'يَوْمَ نَطْوِي السَّمَاءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِ ۚ كَمَا بَدَأْنَا أَوَّلَ خَلْقٍ نُّعِيدُهُ ۚ وَعْدًا عَلَيْنَا ۚ إِنَّا كُنَّا فَاعِلِينَ','در آن روز که آسمان را چون طوماری در هم می‌پیچیم، (سپس) همان گونه که آفرینش را آغاز کردیم، آن را بازمی‌گردانیم؛ این وعده‌ای است بر ما، و قطعاً آن را انجام خواهیم داد.'];
ayat[2588]=[21,105,'وَلَقَدْ كَتَبْنَا فِي الزَّبُورِ مِن بَعْدِ الذِّكْرِ أَنَّ الْأَرْضَ يَرِثُهَا عِبَادِيَ الصَّالِحُونَ','در «زبور» بعد از ذکر (تورات) نوشتیم: «بندگان شایسته‌ام وارث (حکومت) زمین خواهند شد!»'];
ayat[2589]=[21,106,'إِنَّ فِي هَٰذَا لَبَلَاغًا لِّقَوْمٍ عَابِدِينَ','در این، ابلاغ روشنی است برای جمعیّت عبادت‌کنندگان!'];
ayat[2590]=[21,107,'وَمَا أَرْسَلْنَاكَ إِلَّا رَحْمَةً لِّلْعَالَمِينَ','ما تو را جز برای رحمت جهانیان نفرستادیم.'];
ayat[2591]=[21,108,'قُلْ إِنَّمَا يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ ۖ فَهَلْ أَنتُم مُّسْلِمُونَ','بگو: «تنها چیزی که به من وحی می‌شود این است که معبود شما خدای یگانه است؛ آیا (با این حال) تسلیم (حقّ) می‌شوید؟ (و بتها را کنار می‌گذارید؟)»'];
ayat[2592]=[21,109,'فَإِن تَوَلَّوْا فَقُلْ آذَنتُكُمْ عَلَىٰ سَوَاءٍ ۖ وَإِنْ أَدْرِي أَقَرِيبٌ أَم بَعِيدٌ مَّا تُوعَدُونَ','اگر باز (روی گردان شوند، بگو: «من به همه شما یکسان اعلام خطر می‌کنم؛ و نمی‌دانم آیا وعده (عذاب خدا) که به شما داده می‌شود نزدیک است یا دور!'];
ayat[2593]=[21,110,'إِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُونَ','او سخنان آشکار را می‌داند، و آنچه را کتمان می‌کنید (نیز) می‌داند (و چیزی بر او پوشیده نیست)!'];
ayat[2594]=[21,111,'وَإِنْ أَدْرِي لَعَلَّهُ فِتْنَةٌ لَّكُمْ وَمَتَاعٌ إِلَىٰ حِينٍ','و من نمی‌دانم شاید این آزمایشی برای شماست؛ و مایه بهره‌گیری تا مدّتی (معیّن)!'];
ayat[2595]=[21,112,'قَالَ رَبِّ احْكُم بِالْحَقِّ ۗ وَرَبُّنَا الرَّحْمَٰنُ الْمُسْتَعَانُ عَلَىٰ مَا تَصِفُونَ','(و پیامبر) گفت: «پروردگارا! بحق داوری فرما (و این طغیانگران را کیفر ده)! و پروردگار ما (خداوند) رحمان است که در برابر نسبتهای ناروای شما، از او استمداد می‌طلبم!»'];
ayat[2596]=[22,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ ۚ إِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيمٌ','ای مردم! از (عذاب) پروردگارتان بترسید، که زلزله رستاخیز امر عظیمی است!'];
ayat[2597]=[22,2,'يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّا أَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكَارَىٰ وَمَا هُم بِسُكَارَىٰ وَلَٰكِنَّ عَذَابَ اللَّهِ شَدِيدٌ','روزی که آن را می‌بینید، (آنچنان وحشت سراپای همه را فرامی‌گیرد که) هر مادر شیردهی، کودک شیرخوارش را فراموش می‌کند؛ و هر بارداری جنین خود را بر زمین می‌نهد؛ و مردم را مست می‌بینی، در حالی که مست نیستند؛ ولی عذاب خدا شدید است!'];
ayat[2598]=[22,3,'وَمِنَ النَّاسِ مَن يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّبِعُ كُلَّ شَيْطَانٍ مَّرِيدٍ','گروهی از مردم، بدون هیچ علم و دانشی، به مجادله درباره خدا برمی‌خیزند؛ و از هر شیطان سرکشی پیروی می‌کنند.'];
ayat[2599]=[22,4,'كُتِبَ عَلَيْهِ أَنَّهُ مَن تَوَلَّاهُ فَأَنَّهُ يُضِلُّهُ وَيَهْدِيهِ إِلَىٰ عَذَابِ السَّعِيرِ','بر او نوشته شده که هر کس ولایتش را بر گردن نهد، بطور مسلّم گمراهش می‌سازد، و به آتش سوزان راهنماییش می‌کند!'];
ayat[2600]=[22,5,'يَا أَيُّهَا النَّاسُ إِن كُنتُمْ فِي رَيْبٍ مِّنَ الْبَعْثِ فَإِنَّا خَلَقْنَاكُم مِّن تُرَابٍ ثُمَّ مِن نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِن مُّضْغَةٍ مُّخَلَّقَةٍ وَغَيْرِ مُخَلَّقَةٍ لِّنُبَيِّنَ لَكُمْ ۚ وَنُقِرُّ فِي الْأَرْحَامِ مَا نَشَاءُ إِلَىٰ أَجَلٍ مُّسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ ۖ وَمِنكُم مَّن يُتَوَفَّىٰ وَمِنكُم مَّن يُرَدُّ إِلَىٰ أَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِن بَعْدِ عِلْمٍ شَيْئًا ۚ وَتَرَى الْأَرْضَ هَامِدَةً فَإِذَا أَنزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ وَرَبَتْ وَأَنبَتَتْ مِن كُلِّ زَوْجٍ بَهِيجٍ','ای مردم! اگر در رستاخیز شک دارید، (به این نکته توجّه کنید که:) ما شما را از خاک آفریدیم، سپس از نطفه، و بعد از خون بسته شده، سپس از «مضغه» [= چیزی شبیه گوشت جویده شده‌]، که بعضی دارای شکل و خلقت است و بعضی بدون شکل؛ تا برای شما روشن سازیم (که بر هر چیز قادریم)! و جنین‌هایی را که بخواهیم تا مدّت معیّنی در رحم (مادران) قرارمی‌دهیم؛ (و آنچه را بخواهیم ساقظ می‌کنیم؛) بعد شما را بصورت طفل بیرون می‌آوریم؛ سپس هدف این است که به حدّ رشد و بلوغ خویش برسید. در این میان بعضی از شما می‌میرند؛ و بعضی آن قدر عمر می‌کنند که به بدترین مرحله زندگی (و پیری) می‌رسند؛ آنچنان که بعد از علم و آگاهی، چیزی نمی‌دانند! (از سوی دیگر،) زمین را (در فصل زمستان) خشک و مرده می‌بینی، اما هنگامی که آب باران بر آن فرو می‌فرستیم، به حرکت درمی‌آید و می‌روید؛ و از هر نوع گیاهان زیبا می‌رویاند!'];
ayat[2601]=[22,6,'ذَٰلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّهُ يُحْيِي الْمَوْتَىٰ وَأَنَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','این بخاطر آن است که (بدانید) خداوند حق است؛ و اوست که مردگان را زنده می‌کند؛ و بر هر چیزی تواناست.'];
ayat[2602]=[22,7,'وَأَنَّ السَّاعَةَ آتِيَةٌ لَّا رَيْبَ فِيهَا وَأَنَّ اللَّهَ يَبْعَثُ مَن فِي الْقُبُورِ','و اینکه رستاخیز آمدنی است، و شکّی در آن نیست؛ و خداوند تمام کسانی را که در قبرها هستند زنده می‌کند.'];
ayat[2603]=[22,8,'وَمِنَ النَّاسِ مَن يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُّنِيرٍ','و گروهی از مردم، بدون هیچ دانش و هیچ هدایت و کتاب روشنی بخشی، درباره خدا مجادله می‌کنند!'];
ayat[2604]=[22,9,'ثَانِيَ عِطْفِهِ لِيُضِلَّ عَن سَبِيلِ اللَّهِ ۖ لَهُ فِي الدُّنْيَا خِزْيٌ ۖ وَنُذِيقُهُ يَوْمَ الْقِيَامَةِ عَذَابَ الْحَرِيقِ','آنها با تکبّر و بی‌اعتنایی (نسبت به سخنان الهی)، می‌خواهند مردم را از راه خدا گمراه سازند! برای آنان در دنیا رسوایی است؛ و در قیامت، عذاب سوزان به آنها می‌چشانیم!'];
ayat[2605]=[22,10,'ذَٰلِكَ بِمَا قَدَّمَتْ يَدَاكَ وَأَنَّ اللَّهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيدِ','(و به آنان می‌گوییم:) این در برابر چیزی است که دستهایتان از پیش برای شما فرستاده؛ و خداوند هرگز به بندگان ظلم نمی‌کند!'];
ayat[2606]=[22,11,'وَمِنَ النَّاسِ مَن يَعْبُدُ اللَّهَ عَلَىٰ حَرْفٍ ۖ فَإِنْ أَصَابَهُ خَيْرٌ اطْمَأَنَّ بِهِ ۖ وَإِنْ أَصَابَتْهُ فِتْنَةٌ انقَلَبَ عَلَىٰ وَجْهِهِ خَسِرَ الدُّنْيَا وَالْآخِرَةَ ۚ ذَٰلِكَ هُوَ الْخُسْرَانُ الْمُبِينُ','بعضی از مردم خدا را تنها با زبان می‌پرستند (و ایمان قلبیشان بسیار ضعیف است)؛ همین که (دنیا به آنها رو کند و نفع و) خیری به آنان برسد، حالت اطمینان پیدا می‌کنند؛ اما اگر مصیبتی برای امتحان به آنها برسد، دگرگون می‌شوند (و به کفر رومی‌آورند)! (به این ترتیب) هم دنیا را از دست داده‌اند، و هم آخرت را؛ و این همان خسران و زیان آشکار است!'];
ayat[2607]=[22,12,'يَدْعُو مِن دُونِ اللَّهِ مَا لَا يَضُرُّهُ وَمَا لَا يَنفَعُهُ ۚ ذَٰلِكَ هُوَ الضَّلَالُ الْبَعِيدُ','او جز خدا کسی را می‌خواند که نه زیانی به او می‌رساند، و نه سودی؛ این همان گمراهی بسیار عمیق است.'];
ayat[2608]=[22,13,'يَدْعُو لَمَن ضَرُّهُ أَقْرَبُ مِن نَّفْعِهِ ۚ لَبِئْسَ الْمَوْلَىٰ وَلَبِئْسَ الْعَشِيرُ','او کسی را می‌خواند که زیانش از نفعش نزدیکتر است؛ چه بد مولا و یاوری، و چه بد مونس و معاشری!'];
ayat[2609]=[22,14,'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۚ إِنَّ اللَّهَ يَفْعَلُ مَا يُرِيدُ','خداوند کسانی را که ایمان آورده و اعمال صالح انجام داده‌اند، در باغهایی از بهشت وارد می‌کند که نهرها زیر درختانش جاری است؛ (آری،) خدا هر چه را اراده کند انجام می‌دهد!'];
ayat[2610]=[22,15,'مَن كَانَ يَظُنُّ أَن لَّن يَنصُرَهُ اللَّهُ فِي الدُّنْيَا وَالْآخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ إِلَى السَّمَاءِ ثُمَّ لْيَقْطَعْ فَلْيَنظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ مَا يَغِيظُ','هر کس گمان می‌کند که خدا پیامبرش را در دنیا و آخرت یاری نخواهد کرد (و از این نظر عصبانی است، هر کاری از دستش ساخته است بکند)، ریسمانی به سقف خانه خود بیاویزد، و خود را حلق آویز و نفس خود را قطع کند (و تا لبه پرتگاه مرگ پیش رود)؛ ببیند آیا این کار خشم او را فرو می‌نشاند؟!'];
ayat[2611]=[22,16,'وَكَذَٰلِكَ أَنزَلْنَاهُ آيَاتٍ بَيِّنَاتٍ وَأَنَّ اللَّهَ يَهْدِي مَن يُرِيدُ','این گونه ما آن [= قرآن‌] را بصورت آیات روشنی نازل کردیم؛ و خداوند هر کس را بخواهد هدایت می‌کند.'];
ayat[2612]=[22,17,'إِنَّ الَّذِينَ آمَنُوا وَالَّذِينَ هَادُوا وَالصَّابِئِينَ وَالنَّصَارَىٰ وَالْمَجُوسَ وَالَّذِينَ أَشْرَكُوا إِنَّ اللَّهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ','مسلّماً کسانی که ایمان آورده‌اند، و یهود و صابئان [= ستاره‌پرستان‌] و نصاری و مجوس و مشرکان، خداوند در میان آنان روز قیامت داوری می‌کند؛ (و حق را از باطل جدا می‌سازد؛) خداوند بر هر چیز گواه (و از همه چیز آگاه) است.'];
ayat[2613]=[22,18,'أَلَمْ تَرَ أَنَّ اللَّهَ يَسْجُدُ لَهُ مَن فِي السَّمَاوَاتِ وَمَن فِي الْأَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُومُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَابُّ وَكَثِيرٌ مِّنَ النَّاسِ ۖ وَكَثِيرٌ حَقَّ عَلَيْهِ الْعَذَابُ ۗ وَمَن يُهِنِ اللَّهُ فَمَا لَهُ مِن مُّكْرِمٍ ۚ إِنَّ اللَّهَ يَفْعَلُ مَا يَشَاءُ ۩','آیا ندیدی که تمام کسانی که در آسمانها و کسانی که در زمینند برای خدا سجده می‌کنند؟! و (همچنین) خورشید و ماه و ستارگان و کوه‌ها و درختان و جنبندگان، و بسیاری از مردم! امّا بسیاری (ابا دارند، و) فرمان عذاب درباره آنان حتمی است؛ و هر کس را خدا خوار کند، کسی او را گرامی نخواهد داشت! خداوند هر کار را بخواهد (و صلاح بداند) انجام می‌دهد!'];
ayat[2614]=[22,19,'۞ هَٰذَانِ خَصْمَانِ اخْتَصَمُوا فِي رَبِّهِمْ ۖ فَالَّذِينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيَابٌ مِّن نَّارٍ يُصَبُّ مِن فَوْقِ رُءُوسِهِمُ الْحَمِيمُ','اینان دو گروهند که درباره پروردگارشان به مخاصمه و جدال پرداختند؛ کسانی که کافر شدند، لباسهایی از آتش برای آنها بریده شده، و مایع سوزان و جوشان بر سرشان ریخته می‌شود؛'];
ayat[2615]=[22,20,'يُصْهَرُ بِهِ مَا فِي بُطُونِهِمْ وَالْجُلُودُ','آنچنان که هم درونشان با آن آب می‌شود، و هم پوستهایشان.'];
ayat[2616]=[22,21,'وَلَهُم مَّقَامِعُ مِنْ حَدِيدٍ','و برای آنان گرزهایی از آهن (سوزان) است.'];
ayat[2617]=[22,22,'كُلَّمَا أَرَادُوا أَن يَخْرُجُوا مِنْهَا مِنْ غَمٍّ أُعِيدُوا فِيهَا وَذُوقُوا عَذَابَ الْحَرِيقِ','هر گاه بخواهند از غم و اندوه‌های دوزخ خارج شوند، آنها را به آن بازمی‌گردانند؛ و (به آنان گفته می‌شود:) بچشید عذاب سوزان را!'];
ayat[2618]=[22,23,'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِن ذَهَبٍ وَلُؤْلُؤًا ۖ وَلِبَاسُهُمْ فِيهَا حَرِيرٌ','خداوند کسانی را که ایمان آورده و اعمال صالح انجام داده‌اند، در باغهایی از بهشت وارد می‌کند که از زیر درختانش نهرها جاری است؛ آنان با دستبندهایی از طلا و مروارید زینت می‌شوند؛ و در آنجا لباسهایشان از حریر است.'];
ayat[2619]=[22,24,'وَهُدُوا إِلَى الطَّيِّبِ مِنَ الْقَوْلِ وَهُدُوا إِلَىٰ صِرَاطِ الْحَمِيدِ','و بسوی سخنان پاکیزه هدایت می‌شوند، و به راه خداوند شایسته ستایش، راهنمایی می‌گردند.'];
ayat[2620]=[22,25,'إِنَّ الَّذِينَ كَفَرُوا وَيَصُدُّونَ عَن سَبِيلِ اللَّهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِي جَعَلْنَاهُ لِلنَّاسِ سَوَاءً الْعَاكِفُ فِيهِ وَالْبَادِ ۚ وَمَن يُرِدْ فِيهِ بِإِلْحَادٍ بِظُلْمٍ نُّذِقْهُ مِنْ عَذَابٍ أَلِيمٍ','کسانی که کافر شدند، و مؤمنان را از راه خدا بازداشتند، و (همچنین) از مسجد الحرام، که آن را برای همه مردم، برابر قرار دادیم، چه کسانی که در آنجا زندگی می‌کنند یا از نقاط دور وارد می‌شوند (، مستحقّ عذابی دردناکند)؛ و هر کس بخواهد در این سرزمین از راه حق منحرف گردد و دست به ستم زند، ما از عذابی دردناک به او می‌چشانیم!'];
ayat[2621]=[22,26,'وَإِذْ بَوَّأْنَا لِإِبْرَاهِيمَ مَكَانَ الْبَيْتِ أَن لَّا تُشْرِكْ بِي شَيْئًا وَطَهِّرْ بَيْتِيَ لِلطَّائِفِينَ وَالْقَائِمِينَ وَالرُّكَّعِ السُّجُودِ','(به خاطر بیاور) زمانی را که جای خانه (کعبه) را برای ابراهیم آماده ساختیم (تا خانه را بنا کند؛ و به او گفتیم:) چیزی را همتای من قرار مده! و خانه‌ام را برای طواف‌کنندگان و قیام‌کنندگان و رکوع‌کنندگان و سجودکنندگان (از آلودگی بتها و از هر گونه آلودگی) پاک ساز!'];
ayat[2622]=[22,27,'وَأَذِّن فِي النَّاسِ بِالْحَجِّ يَأْتُوكَ رِجَالًا وَعَلَىٰ كُلِّ ضَامِرٍ يَأْتِينَ مِن كُلِّ فَجٍّ عَمِيقٍ','و مردم را دعوت عمومی به حج کن؛ تا پیاده و سواره بر مرکبهای لاغر از هر راه دوری بسوی تو بیایند...'];
ayat[2623]=[22,28,'لِّيَشْهَدُوا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللَّهِ فِي أَيَّامٍ مَّعْلُومَاتٍ عَلَىٰ مَا رَزَقَهُم مِّن بَهِيمَةِ الْأَنْعَامِ ۖ فَكُلُوا مِنْهَا وَأَطْعِمُوا الْبَائِسَ الْفَقِيرَ','تا شاهد منافع گوناگون خویش (در این برنامه حیاتبخش) باشند؛ و در ایّام معیّنی نام خدا را، بر چهارپایانی که به آنان داده است، (به هنگام قربانی‌کردن) ببرند؛ پس از گوشت آنها بخورید؛ و بینوای فقیر را نیز اطعام نمایید!'];
ayat[2624]=[22,29,'ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَلْيُوفُوا نُذُورَهُمْ وَلْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ','سپس، باید آلودگیهایشان را برطرف سازند؛ و به نذرهای خود وفا کنند؛ و بر گرد خانه گرامی کعبه، طواف کنند.'];
ayat[2625]=[22,30,'ذَٰلِكَ وَمَن يُعَظِّمْ حُرُمَاتِ اللَّهِ فَهُوَ خَيْرٌ لَّهُ عِندَ رَبِّهِ ۗ وَأُحِلَّتْ لَكُمُ الْأَنْعَامُ إِلَّا مَا يُتْلَىٰ عَلَيْكُمْ ۖ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْأَوْثَانِ وَاجْتَنِبُوا قَوْلَ الزُّورِ','(مناسک حج) این است! و هر کس برنامه‌های الهی را بزرگ دارد، نزد پروردگارش برای او بهتر است! و چهارپایان برای شما حلال شده، مگر آنچه (ممنوع بودنش) بر شما خوانده می‌شود. از پلیدیهای بتها اجتناب کنید! و از سخن باطل بپرهیزید!'];
ayat[2626]=[22,31,'حُنَفَاءَ لِلَّهِ غَيْرَ مُشْرِكِينَ بِهِ ۚ وَمَن يُشْرِكْ بِاللَّهِ فَكَأَنَّمَا خَرَّ مِنَ السَّمَاءِ فَتَخْطَفُهُ الطَّيْرُ أَوْ تَهْوِي بِهِ الرِّيحُ فِي مَكَانٍ سَحِيقٍ','(برنامه و مناسک حج را انجام دهید) در حالی که همگی خالص برای خدا باشد! هیچ گونه همتایی برای او قائل نشوید! و هر کس همتایی برای خدا قرار دهد، گویی از آسمان سقوب کرده، و پرندگان (در وسط هوا) او را می‌ربایند؛ و یا تندباد او را به جای دوردستی پرتاب می‌کند!'];
ayat[2627]=[22,32,'ذَٰلِكَ وَمَن يُعَظِّمْ شَعَائِرَ اللَّهِ فَإِنَّهَا مِن تَقْوَى الْقُلُوبِ','این است (مناسک حج)! و هر کس شعائر الهی را بزرگ دارد، این کار نشانه تقوای دلهاست.'];
ayat[2628]=[22,33,'لَكُمْ فِيهَا مَنَافِعُ إِلَىٰ أَجَلٍ مُّسَمًّى ثُمَّ مَحِلُّهَا إِلَى الْبَيْتِ الْعَتِيقِ','در آن (حیوانات قربانی)، منافعی برای شماست تا زمان معیّنی [= روز ذبح آنها] سپس محل آن، خانه قدیمی و گرامی (کعبه) است.'];
ayat[2629]=[22,34,'وَلِكُلِّ أُمَّةٍ جَعَلْنَا مَنسَكًا لِّيَذْكُرُوا اسْمَ اللَّهِ عَلَىٰ مَا رَزَقَهُم مِّن بَهِيمَةِ الْأَنْعَامِ ۗ فَإِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ فَلَهُ أَسْلِمُوا ۗ وَبَشِّرِ الْمُخْبِتِينَ','برای هر امّتی قربانگاهی قرار دادیم، تا نام خدا را (به هنگام قربانی) بر چهارپایانی که به آنان روزی داده‌ایم ببرند، و خدای شما معبود واحدی است؛ در برابر (فرمان) او تسلیم شوید و بشارت ده متواضعان و تسلیم‌شوندگان را.'];
ayat[2630]=[22,35,'الَّذِينَ إِذَا ذُكِرَ اللَّهُ وَجِلَتْ قُلُوبُهُمْ وَالصَّابِرِينَ عَلَىٰ مَا أَصَابَهُمْ وَالْمُقِيمِي الصَّلَاةِ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ','همانها که چون نام خدا برده می‌شود، دلهایشان پر از خوف (پروردگار) می‌گردد؛ و شکیبایان در برابر مصیبتهایی که به آنان می‌رسد؛ و آنها که نماز را برپا می‌دارند، و از آنچه به آنان روزی داده‌ایم انفاق می‌کنند.'];
ayat[2631]=[22,36,'وَالْبُدْنَ جَعَلْنَاهَا لَكُم مِّن شَعَائِرِ اللَّهِ لَكُمْ فِيهَا خَيْرٌ ۖ فَاذْكُرُوا اسْمَ اللَّهِ عَلَيْهَا صَوَافَّ ۖ فَإِذَا وَجَبَتْ جُنُوبُهَا فَكُلُوا مِنْهَا وَأَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّ ۚ كَذَٰلِكَ سَخَّرْنَاهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ','و شترهای چاق و فربه را (در مراسم حج) برای شما از شعائر الهی قرار دادیم؛ در آنها برای شما خیر و برکت است؛ نام خدا را (هنگام قربانی کردن) در حالی که به صف ایستاده‌اند بر آنها ببرید؛ و هنگامی که پهلوهایشان آرام گرفت (و جان دادند)، از گوشت آنها بخورید، و مستمندان قانع و فقیران را نیز از آن اطعام کنید! این گونه ما آنها را مسخّرتان ساختیم، تا شکر خدا را بجا آورید.'];
ayat[2632]=[22,37,'لَن يَنَالَ اللَّهَ لُحُومُهَا وَلَا دِمَاؤُهَا وَلَٰكِن يَنَالُهُ التَّقْوَىٰ مِنكُمْ ۚ كَذَٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللَّهَ عَلَىٰ مَا هَدَاكُمْ ۗ وَبَشِّرِ الْمُحْسِنِينَ','نه گوشتها و نه خونهای آنها، هرگز به خدا نمی‌رسد. آنچه به او می‌رسد، تقوا و پرهیزگاری شماست. این گونه خداوند آنها را مسخّر شما ساخته، تا او را بخاطر آنکه شما را هدایت کرده است بزرگ بشمرید؛ و بشارت ده نیکوکاران را!'];
ayat[2633]=[22,38,'۞ إِنَّ اللَّهَ يُدَافِعُ عَنِ الَّذِينَ آمَنُوا ۗ إِنَّ اللَّهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُورٍ','خداوند از کسانی که ایمان آورده‌اند دفاع می‌کند؛ خداوند هیچ خیانتکار ناسپاسی را دوست ندارد!'];
ayat[2634]=[22,39,'أُذِنَ لِلَّذِينَ يُقَاتَلُونَ بِأَنَّهُمْ ظُلِمُوا ۚ وَإِنَّ اللَّهَ عَلَىٰ نَصْرِهِمْ لَقَدِيرٌ','به کسانی که جنگ بر آنان تحمیل گردیده، اجازه جهاد داده شده است؛ چرا که مورد ستم قرار گرفته‌اند؛ و خدا بر یاری آنها تواناست.'];
ayat[2635]=[22,40,'الَّذِينَ أُخْرِجُوا مِن دِيَارِهِم بِغَيْرِ حَقٍّ إِلَّا أَن يَقُولُوا رَبُّنَا اللَّهُ ۗ وَلَوْلَا دَفْعُ اللَّهِ النَّاسَ بَعْضَهُم بِبَعْضٍ لَّهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَصَلَوَاتٌ وَمَسَاجِدُ يُذْكَرُ فِيهَا اسْمُ اللَّهِ كَثِيرًا ۗ وَلَيَنصُرَنَّ اللَّهُ مَن يَنصُرُهُ ۗ إِنَّ اللَّهَ لَقَوِيٌّ عَزِيزٌ','همانها که از خانه و شهر خود، به ناحق رانده شدند، جز اینکه می‌گفتند: «پروردگار ما، خدای یکتاست!» و اگر خداوند بعضی از مردم را بوسیله بعضی دیگر دفع نکند، دیرها و صومعه‌ها، و معابد یهود و نصارا، و مساجدی که نام خدا در آن بسیار برده می‌شود، ویران می‌گردد! و خداوند کسانی را که یاری او کنند (و از آیینش دفاع نمایند) یاری می‌کند؛ خداوند قوی و شکست ناپذیر است.'];
ayat[2636]=[22,41,'الَّذِينَ إِن مَّكَّنَّاهُمْ فِي الْأَرْضِ أَقَامُوا الصَّلَاةَ وَآتَوُا الزَّكَاةَ وَأَمَرُوا بِالْمَعْرُوفِ وَنَهَوْا عَنِ الْمُنكَرِ ۗ وَلِلَّهِ عَاقِبَةُ الْأُمُورِ','همان کسانی که هر گاه در زمین به آنها قدرت بخشیدیم، نماز را برپا می‌دارند، و زکات می‌دهند، و امر به معروف و نهی از منکر می‌کنند، و پایان همه کارها از آن خداست!'];
ayat[2637]=[22,42,'وَإِن يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَثَمُودُ','اگر تو را تکذیب کنند، (امر تازه‌ای نیست؛) پیش از آنها قوم نوح و عاد و ثمود (پیامبرانشان را) تکذیب کردند.'];
ayat[2638]=[22,43,'وَقَوْمُ إِبْرَاهِيمَ وَقَوْمُ لُوطٍ','و همچنین قوم ابراهیم و قوم لوط؛'];
ayat[2639]=[22,44,'وَأَصْحَابُ مَدْيَنَ ۖ وَكُذِّبَ مُوسَىٰ فَأَمْلَيْتُ لِلْكَافِرِينَ ثُمَّ أَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ نَكِيرِ','و اصحاب مدین (قوم شعیب)؛ و نیز موسی (از سوی فرعونیان) تکذیب شد؛ امّا من به کافران مهلت دادم، سپس آنها را مجازات کردم. دیدی چگونه (عمل آنها را) انکار نمودم (و چگونه به آنان پاسخ گفتم)؟!'];
ayat[2640]=[22,45,'فَكَأَيِّن مِّن قَرْيَةٍ أَهْلَكْنَاهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَبِئْرٍ مُّعَطَّلَةٍ وَقَصْرٍ مَّشِيدٍ','چه بسیار شهرها و آبادیهایی که آنها را نابود و هلاک کردیم در حالی که (مردمش) ستمگر بودند، بگونه‌ای که بر سقفهای خود فروریخت! (نخست سقفها ویران گشت؛ و بعد دیوارها بر روی سقفها!) و چه بسیار چاه پر آب که بی‌صاحب ماند؛ و چه بسیار قصرهای محکم و مرتفع!'];
ayat[2641]=[22,46,'أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهَا أَوْ آذَانٌ يَسْمَعُونَ بِهَا ۖ فَإِنَّهَا لَا تَعْمَى الْأَبْصَارُ وَلَٰكِن تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ','آیا آنان در زمین سیر نکردند، تا دلهایی داشته باشند که حقیقت را با آن درک کنند؛ یا گوشهای شنوایی که با آن (ندای حق را) بشنوند؟! چرا که چشمهای ظاهر نابینا نمی‌شود، بلکه دلهایی که در سینه‌هاست کور می‌شود.'];
ayat[2642]=[22,47,'وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ وَلَن يُخْلِفَ اللَّهُ وَعْدَهُ ۚ وَإِنَّ يَوْمًا عِندَ رَبِّكَ كَأَلْفِ سَنَةٍ مِّمَّا تَعُدُّونَ','آنان از تو تقاضای شتاب در عذاب می‌کنند؛ در حالی که خداوند هرگز از وعده خود تخلّف نخواهد کرد! و یک روز نزد پروردگارت، همانند هزار سال از سالهایی است که شما می‌شمرید!'];
ayat[2643]=[22,48,'وَكَأَيِّن مِّن قَرْيَةٍ أَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ أَخَذْتُهَا وَإِلَيَّ الْمَصِيرُ','و چه بسیار شهرها و آبادیهایی که به آنها مهلت دادم، در حالی که ستمگر بودند؛ (امّا از این مهلت برای اصلاح خویش استفاده نکردند.) سپس آنها را مجازات کردم؛ و بازگشت، تنها بسوی من است!'];
ayat[2644]=[22,49,'قُلْ يَا أَيُّهَا النَّاسُ إِنَّمَا أَنَا لَكُمْ نَذِيرٌ مُّبِينٌ','بگو: «ای مردم! من برای شما بیم‌دهنده آشکاری هستم!'];
ayat[2645]=[22,50,'فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُم مَّغْفِرَةٌ وَرِزْقٌ كَرِيمٌ','آنها که ایمان آوردند و اعمال صالح انجام دادند، آمرزش و روزی پر ارزشی برای آنهاست!'];
ayat[2646]=[22,51,'وَالَّذِينَ سَعَوْا فِي آيَاتِنَا مُعَاجِزِينَ أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ','و آنها که در (محو) آیات ما تلاش کردند، و چنین می‌پنداشتند که می‌توانند بر اراده حتمی ما غالب شوند، اصحاب دوزخند!»'];
ayat[2647]=[22,52,'وَمَا أَرْسَلْنَا مِن قَبْلِكَ مِن رَّسُولٍ وَلَا نَبِيٍّ إِلَّا إِذَا تَمَنَّىٰ أَلْقَى الشَّيْطَانُ فِي أُمْنِيَّتِهِ فَيَنسَخُ اللَّهُ مَا يُلْقِي الشَّيْطَانُ ثُمَّ يُحْكِمُ اللَّهُ آيَاتِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','هیچ پیامبری را پیش از تو نفرستادیم مگر اینکه هرگاه آرزو می‌کرد (و طرحی برای پیشبرد اهداف الهی خود می‌ریخت)، شیطان القائاتی در آن می‌کرد؛ امّا خداوند القائات شیطان را از میان می‌برد، سپس آیات خود را استحکام می‌بخشید؛ و خداوند علیم و حکیم است.'];
ayat[2648]=[22,53,'لِّيَجْعَلَ مَا يُلْقِي الشَّيْطَانُ فِتْنَةً لِّلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ وَالْقَاسِيَةِ قُلُوبُهُمْ ۗ وَإِنَّ الظَّالِمِينَ لَفِي شِقَاقٍ بَعِيدٍ','هدف این بود که خداوند القای شیطان را آزمونی قرار دهد برای آنها که در دلهایشان بیماری است، و آنها که سنگدلند؛ و ظالمان در عداوت شدید دور از حقّ قرار گرفته‌اند!'];
ayat[2649]=[22,54,'وَلِيَعْلَمَ الَّذِينَ أُوتُوا الْعِلْمَ أَنَّهُ الْحَقُّ مِن رَّبِّكَ فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ ۗ وَإِنَّ اللَّهَ لَهَادِ الَّذِينَ آمَنُوا إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','و (نیز) هدف این بود که آگاهان بدانند این حقّی است از سوی پروردگارت، و در نتیجه به آن ایمان بیاورند، و دلهایشان در برابر آن خاضع گردد؛ و خداوند کسانی را که ایمان آوردند، بسوی صراط مستقیم هدایت می‌کند.'];
ayat[2650]=[22,55,'وَلَا يَزَالُ الَّذِينَ كَفَرُوا فِي مِرْيَةٍ مِّنْهُ حَتَّىٰ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً أَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيمٍ','کافران همواره در باره قرآن در شکّ هستند، تا آنکه روز قیامت بطور ناگهانی فرارسد، یا عذاب روز عقیم [= روزی که قادر بر جبران گذشته نیستند] به سراغشان آید!'];
ayat[2651]=[22,56,'الْمُلْكُ يَوْمَئِذٍ لِّلَّهِ يَحْكُمُ بَيْنَهُمْ ۚ فَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فِي جَنَّاتِ النَّعِيمِ','حکومت و فرمانروایی در آن روز از آن خداست؛ و میان آنها داوری می‌کند: کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند، در باغهای پرنعمت بهشتند؛'];
ayat[2652]=[22,57,'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا فَأُولَٰئِكَ لَهُمْ عَذَابٌ مُّهِينٌ','و کسانی که کافر شدند و آیات ما را تکذیب کردند، عذاب خوارکننده‌ای برای آنهاست!'];
ayat[2653]=[22,58,'وَالَّذِينَ هَاجَرُوا فِي سَبِيلِ اللَّهِ ثُمَّ قُتِلُوا أَوْ مَاتُوا لَيَرْزُقَنَّهُمُ اللَّهُ رِزْقًا حَسَنًا ۚ وَإِنَّ اللَّهَ لَهُوَ خَيْرُ الرَّازِقِينَ','و کسانی که در راه خدا هجرت کردند، سپس کشته شدند یا به مرگ طبیعی از دنیا رفتند، خداوند به آنها روزی نیکویی می‌دهد؛ که او بهترین روزی‌دهندگان است!'];
ayat[2654]=[22,59,'لَيُدْخِلَنَّهُم مُّدْخَلًا يَرْضَوْنَهُ ۗ وَإِنَّ اللَّهَ لَعَلِيمٌ حَلِيمٌ','خداوند آنان را در محلی وارد می‌کند که از آن خشنود خواهند بود؛ و خداوند دانا و بردبار است.'];
ayat[2655]=[22,60,'۞ ذَٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوقِبَ بِهِ ثُمَّ بُغِيَ عَلَيْهِ لَيَنصُرَنَّهُ اللَّهُ ۗ إِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ','(آری،) مطلب چنین است! و هر کس به همان مقدار که به او ستم شده مجازات کند، سپس مورد تعدّی قرار گیرد، خدا او را یاری خواهد کرد؛ یقیناً خداوند بخشنده و آمرزنده است!'];
ayat[2656]=[22,61,'ذَٰلِكَ بِأَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَأَنَّ اللَّهَ سَمِيعٌ بَصِيرٌ','این (وعده نصرت الهی) بخاطر آن است (که او بر هر چیز قادر است؛ خداوندی) که شب را در روز، و روز را در شب داخل می‌کند؛ و خداوند شنوا و بیناست!'];
ayat[2657]=[22,62,'ذَٰلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِن دُونِهِ هُوَ الْبَاطِلُ وَأَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ','این بخاطر آن است که خداوند حقّ است؛ و آنچه را غیر از او می‌خوانند باطل است؛ و خداوند بلندمقام و بزرگ است!'];
ayat[2658]=[22,63,'أَلَمْ تَرَ أَنَّ اللَّهَ أَنزَلَ مِنَ السَّمَاءِ مَاءً فَتُصْبِحُ الْأَرْضُ مُخْضَرَّةً ۗ إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ','آیا ندیدی خداوند از آسمان، آبی فرستاد، و زمین (بر اثر آن) سرسبز و خرّم می‌گردد؟! و خداوند لطیف و آگاه است.'];
ayat[2659]=[22,64,'لَّهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ وَإِنَّ اللَّهَ لَهُوَ الْغَنِيُّ الْحَمِيدُ','آنچه در آسمانها و آنچه در زمین است از آن اوست؛ و خداوند بی‌نیاز، و شایسته هر گونه ستایش است!'];
ayat[2660]=[22,65,'أَلَمْ تَرَ أَنَّ اللَّهَ سَخَّرَ لَكُم مَّا فِي الْأَرْضِ وَالْفُلْكَ تَجْرِي فِي الْبَحْرِ بِأَمْرِهِ وَيُمْسِكُ السَّمَاءَ أَن تَقَعَ عَلَى الْأَرْضِ إِلَّا بِإِذْنِهِ ۗ إِنَّ اللَّهَ بِالنَّاسِ لَرَءُوفٌ رَّحِيمٌ','آیا ندیدی که خداوند آنچه را در زمین است مسخّر شما کرد؛ و (نیز) کشتیهایی را که به فرمان او بر صفحه اقیانوسها حرکت می‌کنند؛ و آسمان [= کرات و سنگهای آسمانی‌] را نگه می‌دارد، تا جز بفرمان او، بر زمین فرو نیفتند؟ خداوند نسبت به مردم رحیم و مهربان است!'];
ayat[2661]=[22,66,'وَهُوَ الَّذِي أَحْيَاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ۗ إِنَّ الْإِنسَانَ لَكَفُورٌ','و او کسی است که شما را زنده کرد، سپس می‌میراند، بار دیگر زنده می‌کند، امّا این انسان بسیار ناسپاس است.'];
ayat[2662]=[22,67,'لِّكُلِّ أُمَّةٍ جَعَلْنَا مَنسَكًا هُمْ نَاسِكُوهُ ۖ فَلَا يُنَازِعُنَّكَ فِي الْأَمْرِ ۚ وَادْعُ إِلَىٰ رَبِّكَ ۖ إِنَّكَ لَعَلَىٰ هُدًى مُّسْتَقِيمٍ','برای هر امّتی عبادتی قرار دادیم، تا آن عبادت را (در پیشگاه خدا) انجام دهند؛ پس نباید در این امر با تو به نزاع برخیزند! بسوی پروردگارت دعوت کن، که بر هدایت مستقیم قرار داری (و راه راست همین است که تو می‌پویی).'];
ayat[2663]=[22,68,'وَإِن جَادَلُوكَ فَقُلِ اللَّهُ أَعْلَمُ بِمَا تَعْمَلُونَ','و اگر آنان با تو به جدال برخیزند، بگو: «خدا از کارهایی که شما انجام می‌دهید آگاهتر است!'];
ayat[2664]=[22,69,'اللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيَامَةِ فِيمَا كُنتُمْ فِيهِ تَخْتَلِفُونَ','و خداوند در روز قیامت، میان شما در آنچه اختلاف می‌کردید، داوری می‌کند!»'];
ayat[2665]=[22,70,'أَلَمْ تَعْلَمْ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاءِ وَالْأَرْضِ ۗ إِنَّ ذَٰلِكَ فِي كِتَابٍ ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ','آیا نمی‌دانستی خداوند آنچه را در آسمان و زمین است می‌داند؟! همه اینها در کتابی ثبت است (همان کتاب علم بی‌پایان پروردگار)؛ و این بر خداوند آسان است!'];
ayat[2666]=[22,71,'وَيَعْبُدُونَ مِن دُونِ اللَّهِ مَا لَمْ يُنَزِّلْ بِهِ سُلْطَانًا وَمَا لَيْسَ لَهُم بِهِ عِلْمٌ ۗ وَمَا لِلظَّالِمِينَ مِن نَّصِيرٍ','آنها غیر از خداوند، چیزهایی را می‌پرستند که او هیچ گونه دلیلی بر آن نازل نکرده است، و چیزهایی را که علم و آگاهی به آن ندارند! و برای ستمگران، یاور و راهنمایی نیست!'];
ayat[2667]=[22,72,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ تَعْرِفُ فِي وُجُوهِ الَّذِينَ كَفَرُوا الْمُنكَرَ ۖ يَكَادُونَ يَسْطُونَ بِالَّذِينَ يَتْلُونَ عَلَيْهِمْ آيَاتِنَا ۗ قُلْ أَفَأُنَبِّئُكُم بِشَرٍّ مِّن ذَٰلِكُمُ ۗ النَّارُ وَعَدَهَا اللَّهُ الَّذِينَ كَفَرُوا ۖ وَبِئْسَ الْمَصِيرُ','و هنگامی که آیات روشن ما بر آنان خوانده می‌شود، در چهره کافران آثار انکار مشاهده می‌کنی، آنچنان که نزدیک است برخیزند و با مشت به کسانی که آیات ما را بر آنها میخوانند حمله کنند! بگو: «آیا شما را به بدتر از این خبر دهم؟ همان آتش سوزنده [= دوزخ‌] که خدا به کافران وعده داده؛ و بد سرانجامی است!»'];
ayat[2668]=[22,73,'يَا أَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُ ۚ إِنَّ الَّذِينَ تَدْعُونَ مِن دُونِ اللَّهِ لَن يَخْلُقُوا ذُبَابًا وَلَوِ اجْتَمَعُوا لَهُ ۖ وَإِن يَسْلُبْهُمُ الذُّبَابُ شَيْئًا لَّا يَسْتَنقِذُوهُ مِنْهُ ۚ ضَعُفَ الطَّالِبُ وَالْمَطْلُوبُ','ای مردم! مثلی زده شده است، به آن گوش فرا دهید: کسانی را که غیر از خدا می‌خوانید، هرگز نمی‌توانند مگسی بیافرینند، هر چند برای این کار دست به دست هم دهند! و هرگاه مگس چیزی از آنها برباید، نمی‌توانند آن را باز پس گیرند! هم این طلب‌کنندگان ناتوانند، و هم آن مطلوبان (هم این عابدان، و هم آن معبودان)!'];
ayat[2669]=[22,74,'مَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ ۗ إِنَّ اللَّهَ لَقَوِيٌّ عَزِيزٌ','خدا را آن گونه که باید بشناسند نشناختند؛ خداوند قوّی و شکست‌ناپذیر است!'];
ayat[2670]=[22,75,'اللَّهُ يَصْطَفِي مِنَ الْمَلَائِكَةِ رُسُلًا وَمِنَ النَّاسِ ۚ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ','خداوند از فرشتگان رسولانی برمی‌گزیند، و همچنین از مردم؛ خداوند شنوا و بیناست!'];
ayat[2671]=[22,76,'يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ ۗ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ','آنچه را در پیش روی آنها و پشت سر آنهاست می‌داند؛ و همه امور بسوی خدا بازمی‌گردد.'];
ayat[2672]=[22,77,'يَا أَيُّهَا الَّذِينَ آمَنُوا ارْكَعُوا وَاسْجُدُوا وَاعْبُدُوا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَ ۩','ای کسانی که ایمان آورده‌اید! رکوع کنید، و سجود بجا آورید، و پروردگارتان را عبادت کنید، و کار نیک انجام دهید، شاید رستگار شوید!'];
ayat[2673]=[22,78,'وَجَاهِدُوا فِي اللَّهِ حَقَّ جِهَادِهِ ۚ هُوَ اجْتَبَاكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حَرَجٍ ۚ مِّلَّةَ أَبِيكُمْ إِبْرَاهِيمَ ۚ هُوَ سَمَّاكُمُ الْمُسْلِمِينَ مِن قَبْلُ وَفِي هَٰذَا لِيَكُونَ الرَّسُولُ شَهِيدًا عَلَيْكُمْ وَتَكُونُوا شُهَدَاءَ عَلَى النَّاسِ ۚ فَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَاعْتَصِمُوا بِاللَّهِ هُوَ مَوْلَاكُمْ ۖ فَنِعْمَ الْمَوْلَىٰ وَنِعْمَ النَّصِيرُ','و در راه خدا جهاد کنید، و حقّ جهادش را ادا نمایید! او شما را برگزید، و در دین (اسلام) کار سنگین و سختی بر شما قرار ندارد؛ از آیین پدرتان ابراهیم پیروی کنید؛ خداوند شما را در کتابهای پیشین و در این کتاب آسمانی «مسلمان» نامید، تا پیامبر گواه بر شما باشد، و شما گواهان بر مردم! پس نماز را برپا دارید، و زکات را بدهید، و به خدا تمسّک جویید، که او مولا و سرپرست شماست! چه مولای خوب، و چه یاور شایسته‌ای!'];
ayat[2674]=[23,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قَدْ أَفْلَحَ الْمُؤْمِنُونَ','مؤمنان رستگار شدند؛'];
ayat[2675]=[23,2,'الَّذِينَ هُمْ فِي صَلَاتِهِمْ خَاشِعُونَ','آنها که در نمازشان خشوع دارند؛'];
ayat[2676]=[23,3,'وَالَّذِينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَ','و آنها که از لغو و بیهودگی روی‌گردانند؛'];
ayat[2677]=[23,4,'وَالَّذِينَ هُمْ لِلزَّكَاةِ فَاعِلُونَ','و آنها که زکات را انجام می‌دهند؛'];
ayat[2678]=[23,5,'وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ','و آنها که دامان خود را (از آلوده‌شدن به بی‌عفتی) حفظ می‌کنند؛'];
ayat[2679]=[23,6,'إِلَّا عَلَىٰ أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ','تنها آمیزش جنسی با همسران و کنیزانشان دارند، که در بهره‌گیری از آنان ملامت نمی‌شوند؛'];
ayat[2680]=[23,7,'فَمَنِ ابْتَغَىٰ وَرَاءَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْعَادُونَ','و کسانی که غیر از این طریق را طلب کنند، تجاوزگرند!'];
ayat[2681]=[23,8,'وَالَّذِينَ هُمْ لِأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ','و آنها که امانتها و عهد خود را رعایت می‌کنند؛'];
ayat[2682]=[23,9,'وَالَّذِينَ هُمْ عَلَىٰ صَلَوَاتِهِمْ يُحَافِظُونَ','و آنها که بر نمازهایشان مواظبت می‌نمایند؛'];
ayat[2683]=[23,10,'أُولَٰئِكَ هُمُ الْوَارِثُونَ','(آری،) آنها وارثانند!'];
ayat[2684]=[23,11,'الَّذِينَ يَرِثُونَ الْفِرْدَوْسَ هُمْ فِيهَا خَالِدُونَ','(وارثانی) که بهشت برین را ارث می‌برند، و جاودانه در آن خواهند ماند!'];
ayat[2685]=[23,12,'وَلَقَدْ خَلَقْنَا الْإِنسَانَ مِن سُلَالَةٍ مِّن طِينٍ','و ما انسان را از عصاره‌ای از گِل آفریدیم؛'];
ayat[2686]=[23,13,'ثُمَّ جَعَلْنَاهُ نُطْفَةً فِي قَرَارٍ مَّكِينٍ','سپس او را نطفه‌ای در قرارگاه مطمئن [= رحم‌] قرار دادیم؛'];
ayat[2687]=[23,14,'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظَامًا فَكَسَوْنَا الْعِظَامَ لَحْمًا ثُمَّ أَنشَأْنَاهُ خَلْقًا آخَرَ ۚ فَتَبَارَكَ اللَّهُ أَحْسَنُ الْخَالِقِينَ','سپس نطفه را بصورت علقه [= خون بسته‌]، و علقه را بصورت مضغه [= چیزی شبیه گوشت جویده شده‌]، و مضغه را بصورت استخوانهایی درآوردیم؛ و بر استخوانها گوشت پوشاندیم؛ سپس آن را آفرینش تازه‌ای دادیم؛ پس بزرگ است خدایی که بهترین آفرینندگان است!'];
ayat[2688]=[23,15,'ثُمَّ إِنَّكُم بَعْدَ ذَٰلِكَ لَمَيِّتُونَ','سپس شما بعد از آن می‌میرید؛'];
ayat[2689]=[23,16,'ثُمَّ إِنَّكُمْ يَوْمَ الْقِيَامَةِ تُبْعَثُونَ','سپس در روز قیامت برانگیخته می‌شوید!'];
ayat[2690]=[23,17,'وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَائِقَ وَمَا كُنَّا عَنِ الْخَلْقِ غَافِلِينَ','ما بر بالای سر شما هفت راه [= طبقات هفتگانه آسمان‌] قرار دادیم؛ و ما (هرگز) از خلق (خود) غافل نبوده‌ایم!'];
ayat[2691]=[23,18,'وَأَنزَلْنَا مِنَ السَّمَاءِ مَاءً بِقَدَرٍ فَأَسْكَنَّاهُ فِي الْأَرْضِ ۖ وَإِنَّا عَلَىٰ ذَهَابٍ بِهِ لَقَادِرُونَ','و از آسمان، آبی به اندازه معیّن نازل کردیم؛ و آن را در زمین (در جایگاه مخصوصی) ساکن نمودیم؛ و ما بر از بین بردن آن کاملاً قادریم!'];
ayat[2692]=[23,19,'فَأَنشَأْنَا لَكُم بِهِ جَنَّاتٍ مِّن نَّخِيلٍ وَأَعْنَابٍ لَّكُمْ فِيهَا فَوَاكِهُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ','سپس بوسیله آن باغهایی از درختان نخل و انگور برای شما ایجاد کردیم؛ باغهایی که در آن میوه‌های بسیار است؛ و از آن میخورید!'];
ayat[2693]=[23,20,'وَشَجَرَةً تَخْرُجُ مِن طُورِ سَيْنَاءَ تَنبُتُ بِالدُّهْنِ وَصِبْغٍ لِّلْآكِلِينَ','و (نیز) درختی را که از طور سینا می‌روید [= درخت زیتون‌]، و از آن روغن و «نان خورش» برای خورندگان فراهم می‌گردد (آفریدیم)!'];
ayat[2694]=[23,21,'وَإِنَّ لَكُمْ فِي الْأَنْعَامِ لَعِبْرَةً ۖ نُّسْقِيكُم مِّمَّا فِي بُطُونِهَا وَلَكُمْ فِيهَا مَنَافِعُ كَثِيرَةٌ وَمِنْهَا تَأْكُلُونَ','و برای شما در چهارپایان عبرتی است؛ از آنچه در درون آنهاست [= از شیر] شما را سیراب می‌کنیم؛ و برای شما در آنها منافع بسیاری است؛ و از گوشت آنها می‌خورید؛'];
ayat[2695]=[23,22,'وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ','و بر آنها و بر کشتیها سوار می‌شوید!'];
ayat[2696]=[23,23,'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ أَفَلَا تَتَّقُونَ','و ما نوح را بسوی قومش فرستادیم؛ او به آنها گفت: «ای قوم من! خداوند یکتا را بپرستید، که جز او معبودی برای شما نیست! آیا (از پرستش بتها) پرهیز نمی‌کنید؟!'];
ayat[2697]=[23,24,'فَقَالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِن قَوْمِهِ مَا هَٰذَا إِلَّا بَشَرٌ مِّثْلُكُمْ يُرِيدُ أَن يَتَفَضَّلَ عَلَيْكُمْ وَلَوْ شَاءَ اللَّهُ لَأَنزَلَ مَلَائِكَةً مَّا سَمِعْنَا بِهَٰذَا فِي آبَائِنَا الْأَوَّلِينَ','جمعیّت اشرافی (و مغرور) از قوم نوح که کافر بودند گفتند: «این مرد جز بشری همچون شما نیست، که می‌خواهد بر شما برتری جوید! اگر خدا می‌خواست (پیامبری بفرستد) فرشتگانی نازل می‌کرد؛ ما چنین چیزی را هرگز در نیاکان خود نشنیده‌ایم!'];
ayat[2698]=[23,25,'إِنْ هُوَ إِلَّا رَجُلٌ بِهِ جِنَّةٌ فَتَرَبَّصُوا بِهِ حَتَّىٰ حِينٍ','او فقط مردی است که به نوعی جنون مبتلاست! پس مدّتی درباره او صبر کنید (تا مرگش فرا رسد، یا از این بیماری رهایی یابد!)»'];
ayat[2699]=[23,26,'قَالَ رَبِّ انصُرْنِي بِمَا كَذَّبُونِ','(نوح) گفت: «پروردگارا! مرا در برابر تکذیبهای آنان یاری کن!»'];
ayat[2700]=[23,27,'فَأَوْحَيْنَا إِلَيْهِ أَنِ اصْنَعِ الْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَا فَإِذَا جَاءَ أَمْرُنَا وَفَارَ التَّنُّورُ ۙ فَاسْلُكْ فِيهَا مِن كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَأَهْلَكَ إِلَّا مَن سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْ ۖ وَلَا تُخَاطِبْنِي فِي الَّذِينَ ظَلَمُوا ۖ إِنَّهُم مُّغْرَقُونَ','ما به نوح وحی کردیم که: «کشتی را در حضور ما، و مطابق وحی ما بساز. و هنگامی که فرمان ما (برای غرق آنان) فرا رسد، و آب از تنور بجوشد (که نشانه فرا رسیدن طوفان است)، از هر یک از انواع حیوانات یک جفت در کشتی سوار کن؛ و همچنین خانواده‌ات را، مگر آنانی که قبلاً وعده هلاکشان داده شده [= همسر و فرزند کافرت‌]؛ و دیگر درباره ستمگران با من سخن مگو، که آنان همگی هلاک خواهند شد.'];
ayat[2701]=[23,28,'فَإِذَا اسْتَوَيْتَ أَنتَ وَمَن مَّعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلَّهِ الَّذِي نَجَّانَا مِنَ الْقَوْمِ الظَّالِمِينَ','و هنگامی که تو و همه کسانی که با تو هستند بر کشتی سوار شدید، بگو: «ستایش برای خدایی است که ما را از قوم ستمگر نجات بخشید!»'];
ayat[2702]=[23,29,'وَقُل رَّبِّ أَنزِلْنِي مُنزَلًا مُّبَارَكًا وَأَنتَ خَيْرُ الْمُنزِلِينَ','و بگو: «پروردگارا! ما را در منزلگاهی پربرکت فرود آر، و تو بهترین فرودآورندگانی!»'];
ayat[2703]=[23,30,'إِنَّ فِي ذَٰلِكَ لَآيَاتٍ وَإِن كُنَّا لَمُبْتَلِينَ','(آری،) در این ماجرا (برای صاحبان عقل و اندیشه) آیات و نشانه‌هایی است؛ و ما مسلّماً همگان را آزمایش می‌کنیم!'];
ayat[2704]=[23,31,'ثُمَّ أَنشَأْنَا مِن بَعْدِهِمْ قَرْنًا آخَرِينَ','سپس جمعیّت دیگری را بعد از آنها به وجود آوردیم.'];
ayat[2705]=[23,32,'فَأَرْسَلْنَا فِيهِمْ رَسُولًا مِّنْهُمْ أَنِ اعْبُدُوا اللَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُ ۖ أَفَلَا تَتَّقُونَ','و در میان آنان رسولی از خودشان فرستادیم که: «خدا را بپرستید؛ جز او معبودی برای شما نیست؛ آیا (با این همه، از شرک و بت پرستی) پرهیز نمی‌کنید؟!»'];
ayat[2706]=[23,33,'وَقَالَ الْمَلَأُ مِن قَوْمِهِ الَّذِينَ كَفَرُوا وَكَذَّبُوا بِلِقَاءِ الْآخِرَةِ وَأَتْرَفْنَاهُمْ فِي الْحَيَاةِ الدُّنْيَا مَا هَٰذَا إِلَّا بَشَرٌ مِّثْلُكُمْ يَأْكُلُ مِمَّا تَأْكُلُونَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُونَ','ولی اشرافیان (خودخواه) از قوم او که کافر بودند، و دیدار آخرت را تکذیب می‌کردند، و در زندگی دنیا به آنان ناز و نعمت داده بودیم، گفتند: «این بشری است مثل شما؛ از آنچه می‌خورید می‌خورد؛ و از آنچه می‌نوشید می‌نوشد! (پس چگونه می‌تواند پیامبر باشد؟!)'];
ayat[2707]=[23,34,'وَلَئِنْ أَطَعْتُم بَشَرًا مِّثْلَكُمْ إِنَّكُمْ إِذًا لَّخَاسِرُونَ','و اگر از بشری همانند خودتان اطاعت کنید، مسلّماً زیانکارید.'];
ayat[2708]=[23,35,'أَيَعِدُكُمْ أَنَّكُمْ إِذَا مِتُّمْ وَكُنتُمْ تُرَابًا وَعِظَامًا أَنَّكُم مُّخْرَجُونَ','آیا او به شما وعده می‌دهد هنگامی که مردید و خاک و استخوانهایی (پوسیده) شدید، بار دیگر (از قبرها) بیرون آورده می‌شوید؟!'];
ayat[2709]=[23,36,'۞ هَيْهَاتَ هَيْهَاتَ لِمَا تُوعَدُونَ','هیهات، هیهات از این وعده‌هایی که به شما داده می‌شود!'];
ayat[2710]=[23,37,'إِنْ هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوثِينَ','مسلّماً غیر از این زندگی دنیای ما، چیزی در کار نیست؛ پیوسته گروهی از ما می‌میریم، و نسل دیگری جای ما را می‌گیرد؛ و ما هرگز برانگیخته نخواهیم شد!'];
ayat[2711]=[23,38,'إِنْ هُوَ إِلَّا رَجُلٌ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا وَمَا نَحْنُ لَهُ بِمُؤْمِنِينَ','او فقط مردی دروغگوست که بر خدا افترا بسته؛ و ما هرگز به او ایمان نخواهیم آورد!»'];
ayat[2712]=[23,39,'قَالَ رَبِّ انصُرْنِي بِمَا كَذَّبُونِ','(پیامبرشان) گفت: «پروردگارا! مرا در برابر تکذیبهای آنان یاری کن!»'];
ayat[2713]=[23,40,'قَالَ عَمَّا قَلِيلٍ لَّيُصْبِحُنَّ نَادِمِينَ','(خداوند) فرمود: «بزودی از کار خود پشیمان خواهند شد! (امّا زمانی که دیگر سودی به حالشان ندارد.)»'];
ayat[2714]=[23,41,'فَأَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنَاهُمْ غُثَاءً ۚ فَبُعْدًا لِّلْقَوْمِ الظَّالِمِينَ','سرانجام صیحه آسمانی آنها را بحق فرو گرفت؛ و ما آنها را همچون خاشاکی بر سیلاب قرار دادیم؛ دور باد قوم ستمگر (از رحمت خدا)!'];
ayat[2715]=[23,42,'ثُمَّ أَنشَأْنَا مِن بَعْدِهِمْ قُرُونًا آخَرِينَ','سپس اقوام دیگری را پس از آنها پدید آوردیم.'];
ayat[2716]=[23,43,'مَا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَأْخِرُونَ','هیچ امتی بر اجل و سر رسید حتمی خود پیشی نمی‌گیرد، و از آن تأخیر نیز نمی‌کند.'];
ayat[2717]=[23,44,'ثُمَّ أَرْسَلْنَا رُسُلَنَا تَتْرَىٰ ۖ كُلَّ مَا جَاءَ أُمَّةً رَّسُولُهَا كَذَّبُوهُ ۚ فَأَتْبَعْنَا بَعْضَهُم بَعْضًا وَجَعَلْنَاهُمْ أَحَادِيثَ ۚ فَبُعْدًا لِّقَوْمٍ لَّا يُؤْمِنُونَ','سپس رسولان خود را یکی پس از دیگری فرستادیم؛ هر زمان رسولی برای (هدایت) قومی می‌آمد، او را تکذیب می‌کردند؛ ولی ما این امّتهای سرکش را یکی پس از دیگری هلاک نمودیم، و آنها را احادیثی قرار دادیم (چنان محو شدند که تنها نام و گفتگویی از آنان باقی ماند.) دور باد (از رحمت خدا) قومی که ایمان نمی‌آورند!'];
ayat[2718]=[23,45,'ثُمَّ أَرْسَلْنَا مُوسَىٰ وَأَخَاهُ هَارُونَ بِآيَاتِنَا وَسُلْطَانٍ مُّبِينٍ','سپس موسی و برادرش هارون را با آیات خود و دلیلی روشن فرستادیم...'];
ayat[2719]=[23,46,'إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَاسْتَكْبَرُوا وَكَانُوا قَوْمًا عَالِينَ','بسوی فرعون و اطرافیان اشرافی او؛ امّا آنها تکبّر کردند، و آنها مردمی برتری‌جوی بودند.'];
ayat[2720]=[23,47,'فَقَالُوا أَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عَابِدُونَ','آنها گفتند: «آیا ما به دو انسان همانند خودمان ایمان بیاوریم، در حالی که قوم آنها بردگان ما هستند؟!»'];
ayat[2721]=[23,48,'فَكَذَّبُوهُمَا فَكَانُوا مِنَ الْمُهْلَكِينَ','(آری،) آنها این دو را تکذیب کردند؛ و سرانجام همگی هلاک شدند.'];
ayat[2722]=[23,49,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ لَعَلَّهُمْ يَهْتَدُونَ','و ما به موسی کتاب (آسمانی) دادیم؛ شاید آنان [= بنی اسرائیل‌] هدایت شوند.'];
ayat[2723]=[23,50,'وَجَعَلْنَا ابْنَ مَرْيَمَ وَأُمَّهُ آيَةً وَآوَيْنَاهُمَا إِلَىٰ رَبْوَةٍ ذَاتِ قَرَارٍ وَمَعِينٍ','و ما فرزند مریم [= عیسی‌] و مادرش را آیت و نشانه‌ای قرار دادیم؛ و آنها را در سرزمین مرتفعی که دارای امنیّت و آب جاری بود جای دادیم.'];
ayat[2724]=[23,51,'يَا أَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّبَاتِ وَاعْمَلُوا صَالِحًا ۖ إِنِّي بِمَا تَعْمَلُونَ عَلِيمٌ','ای پیامبران! از غذاهای پاکیزه بخورید، و عمل صالح انجام دهید، که من به آنچه انجام می‌دهید آگاهم.'];
ayat[2725]=[23,52,'وَإِنَّ هَٰذِهِ أُمَّتُكُمْ أُمَّةً وَاحِدَةً وَأَنَا رَبُّكُمْ فَاتَّقُونِ','و این امّت شما امّت واحدی است؛ و من پروردگار شما هستم؛ پس، از مخالفت فرمان من بپرهیزید!'];
ayat[2726]=[23,53,'فَتَقَطَّعُوا أَمْرَهُم بَيْنَهُمْ زُبُرًا ۖ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ','امّا آنها کارهای خود را در میان خویش به پراکندگی کشاندند، و هر گروهی به راهی رفتند؛ (و عجب اینکه) هر گروه به آنچه نزد خود دارند خوشحالند!'];
ayat[2727]=[23,54,'فَذَرْهُمْ فِي غَمْرَتِهِمْ حَتَّىٰ حِينٍ','آنها را در جهل و غفلتشان بگذار تا زمانی (که مرگشان فرا رسد یا گرفتار عذاب الهی شوند).'];
ayat[2728]=[23,55,'أَيَحْسَبُونَ أَنَّمَا نُمِدُّهُم بِهِ مِن مَّالٍ وَبَنِينَ','آنها گمان می‌کنند اموال و فرزندانی که بعنوان کمک به آنان می‌دهیم...'];
ayat[2729]=[23,56,'نُسَارِعُ لَهُمْ فِي الْخَيْرَاتِ ۚ بَل لَّا يَشْعُرُونَ','برای این است که درهای خیرات را با شتاب به روی آنها بگشاییم!! (چنین نیست) بلکه آنها نمی‌فهمند (که این وسیله امتحانشان است).'];
ayat[2730]=[23,57,'إِنَّ الَّذِينَ هُم مِّنْ خَشْيَةِ رَبِّهِم مُّشْفِقُونَ','مسلّماً کسانی که از خوف پروردگارشان بیمناکند،'];
ayat[2731]=[23,58,'وَالَّذِينَ هُم بِآيَاتِ رَبِّهِمْ يُؤْمِنُونَ','و آنان که به آیات پروردگارشان ایمان می‌آورند،'];
ayat[2732]=[23,59,'وَالَّذِينَ هُم بِرَبِّهِمْ لَا يُشْرِكُونَ','و آنها که به پروردگارشان شرک نمی‌ورزند،'];
ayat[2733]=[23,60,'وَالَّذِينَ يُؤْتُونَ مَا آتَوا وَّقُلُوبُهُمْ وَجِلَةٌ أَنَّهُمْ إِلَىٰ رَبِّهِمْ رَاجِعُونَ','و آنها که نهایت کوشش را در انجام طاعات به خرج می‌دهند و با این حال، دلهایشان هراسناک است از اینکه سرانجام بسوی پروردگارشان بازمی‌گردند،'];
ayat[2734]=[23,61,'أُولَٰئِكَ يُسَارِعُونَ فِي الْخَيْرَاتِ وَهُمْ لَهَا سَابِقُونَ','(آری) چنین کسانی در خیرات سرعت می‌کنند و از دیگران پیشی می‌گیرند (و مشمول عنایات ما هستند).'];
ayat[2735]=[23,62,'وَلَا نُكَلِّفُ نَفْسًا إِلَّا وُسْعَهَا ۖ وَلَدَيْنَا كِتَابٌ يَنطِقُ بِالْحَقِّ ۚ وَهُمْ لَا يُظْلَمُونَ','و ما هیچ کس را جز به اندازه تواناییش تکلیف نمی‌کنیم؛ و نزد ما کتابی است که (تمام اعمال بندگان را ثبت کرده و) بحق سخن می‌گوید؛ و به آنان هیچ ستمی نمی‌شود.'];
ayat[2736]=[23,63,'بَلْ قُلُوبُهُمْ فِي غَمْرَةٍ مِّنْ هَٰذَا وَلَهُمْ أَعْمَالٌ مِّن دُونِ ذَٰلِكَ هُمْ لَهَا عَامِلُونَ','ولی دلهای آنها از این نامه اعمال (و روز حساب و آیات قرآن) در بی‌خبری فرورفته؛ و اعمال (زشت) دیگری جز این دارند که پیوسته آن را انجام می‌دهند...'];
ayat[2737]=[23,64,'حَتَّىٰ إِذَا أَخَذْنَا مُتْرَفِيهِم بِالْعَذَابِ إِذَا هُمْ يَجْأَرُونَ','تا زمانی که متنعّمان مغرور آنها را در چنگال عذاب گرفتار سازیم؛ در این هنگام، ناله‌های دردناک و استغاثه‌آمیز سرمی‌دهند!'];
ayat[2738]=[23,65,'لَا تَجْأَرُوا الْيَوْمَ ۖ إِنَّكُم مِّنَّا لَا تُنصَرُونَ','(امّا به آنها گفته می‌شود:) امروز فریاد نکنید، زیرا از سوی ما یاری نخواهید شد!'];
ayat[2739]=[23,66,'قَدْ كَانَتْ آيَاتِي تُتْلَىٰ عَلَيْكُمْ فَكُنتُمْ عَلَىٰ أَعْقَابِكُمْ تَنكِصُونَ','(آیا فراموش کرده‌اید که) در گذشته آیات من پیوسته بر شما خوانده می‌شد؛ امّا شما اعراض کرده به عقب بازمی‌گشتید؟!'];
ayat[2740]=[23,67,'مُسْتَكْبِرِينَ بِهِ سَامِرًا تَهْجُرُونَ','در حالی که در برابر او [=پیامبر] استکبار می‌کردید، و شبها در جلسات خود به بدگویی می‌پرداختید؟!'];
ayat[2741]=[23,68,'أَفَلَمْ يَدَّبَّرُوا الْقَوْلَ أَمْ جَاءَهُم مَّا لَمْ يَأْتِ آبَاءَهُمُ الْأَوَّلِينَ','آیا آنها در این گفتار نیندیشیدند، یا اینکه چیزی برای آنان آمده که برای نیاکانشان نیامده است؟!'];
ayat[2742]=[23,69,'أَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنكِرُونَ','یا اینکه پیامبرشان را نشناختند (و از سوابق او آگاه نیستند)، از این رو او را انکار می‌کنند؟!'];
ayat[2743]=[23,70,'أَمْ يَقُولُونَ بِهِ جِنَّةٌ ۚ بَلْ جَاءَهُم بِالْحَقِّ وَأَكْثَرُهُمْ لِلْحَقِّ كَارِهُونَ','یا می‌گویند او دیوانه است؟! ولی او حق را برای آنان آورده؛ امّا بیشترشان از حق کراهت دارند (و گریزانند).'];
ayat[2744]=[23,71,'وَلَوِ اتَّبَعَ الْحَقُّ أَهْوَاءَهُمْ لَفَسَدَتِ السَّمَاوَاتُ وَالْأَرْضُ وَمَن فِيهِنَّ ۚ بَلْ أَتَيْنَاهُم بِذِكْرِهِمْ فَهُمْ عَن ذِكْرِهِم مُّعْرِضُونَ','و اگر حق از هوسهای آنها پیروی کند، آسمانها و زمین و همه کسانی که در آنها هستند تباه می‌شوند! ولی ما قرآنی به آنها دادیم که مایه یادآوری (و عزّت و شرف) برای آنهاست، امّا آنان از (آنچه مایه) یادآوریشان (است) رویگردانند!'];
ayat[2745]=[23,72,'أَمْ تَسْأَلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ ۖ وَهُوَ خَيْرُ الرَّازِقِينَ','یا اینکه تو از آنها مزد و هزینه‌ای (در برابر دعوتت) می‌خواهی؟ با اینکه مزد پروردگارت بهتر، و او بهترین روزی دهندگان است!'];
ayat[2746]=[23,73,'وَإِنَّكَ لَتَدْعُوهُمْ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','بطور قطع و یقین، تو آنان را به راه راست دعوت می‌کنی.'];
ayat[2747]=[23,74,'وَإِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُونَ','امّا کسانی که به آخرت ایمان ندارند از این راه منحرفند!'];
ayat[2748]=[23,75,'۞ وَلَوْ رَحِمْنَاهُمْ وَكَشَفْنَا مَا بِهِم مِّن ضُرٍّ لَّلَجُّوا فِي طُغْيَانِهِمْ يَعْمَهُونَ','و اگر به آنان رحم کنید و گرفتاریها و مشکلاتشان را برطرف سازیم، (نه تنها بیدار نمی‌شوند، بلکه) در طغیانشان لجاجت می‌ورزند و (در این وادی) سرگردان می‌مانند!'];
ayat[2749]=[23,76,'وَلَقَدْ أَخَذْنَاهُم بِالْعَذَابِ فَمَا اسْتَكَانُوا لِرَبِّهِمْ وَمَا يَتَضَرَّعُونَ','ما آنها را به عذاب و بلا گرفتار ساختیم (تا بیدار شوند)، امّا آنان نه در برابر پروردگارشان تواضع کردند، و نه به درگاهش تضرّع می‌کنند!'];
ayat[2750]=[23,77,'حَتَّىٰ إِذَا فَتَحْنَا عَلَيْهِم بَابًا ذَا عَذَابٍ شَدِيدٍ إِذَا هُمْ فِيهِ مُبْلِسُونَ','(این وضع همچنان ادامه می‌یابد) تا زمانی که دری از عذاب شدید به روی آنان بگشاییم، (و چنان گرفتار می‌شوند که) ناگهان بکلّی مأیوس گردند.'];
ayat[2751]=[23,78,'وَهُوَ الَّذِي أَنشَأَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۚ قَلِيلًا مَّا تَشْكُرُونَ','و او کسی است که برای شما گوش و چشم و قلب [=عقل‌] ایجاد کرد؛ امّا کمتر شکر او را بجا می‌آورید.'];
ayat[2752]=[23,79,'وَهُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ','و او کسی است که شما را در زمین آفرید؛ و به سوی او محشور می‌شوید!'];
ayat[2753]=[23,80,'وَهُوَ الَّذِي يُحْيِي وَيُمِيتُ وَلَهُ اخْتِلَافُ اللَّيْلِ وَالنَّهَارِ ۚ أَفَلَا تَعْقِلُونَ','و او کسی است که زنده می‌کند و می‌میراند؛ و رفت و آمد شب و روز از آن اوست؛ آیا اندیشه نمی‌کنید؟!'];
ayat[2754]=[23,81,'بَلْ قَالُوا مِثْلَ مَا قَالَ الْأَوَّلُونَ','(نه،) بلکه آنان نیز مثل آنچه پیشینیان گفته بودند گفتند.'];
ayat[2755]=[23,82,'قَالُوا أَإِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ','آنها گفتند: «آیا هنگامی که مردیم و خاک و استخوانهایی (پوسیده) شدیم، آیا بار دیگر برانگیخته خواهیم شد؟!'];
ayat[2756]=[23,83,'لَقَدْ وُعِدْنَا نَحْنُ وَآبَاؤُنَا هَٰذَا مِن قَبْلُ إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ','این وعده به ما و پدرانمان از قبل داده شده؛ این فقط افسانه‌های پیشینیان است!»'];
ayat[2757]=[23,84,'قُل لِّمَنِ الْأَرْضُ وَمَن فِيهَا إِن كُنتُمْ تَعْلَمُونَ','بگو: «زمین و کسانی که در آن هستند از آن کیست، اگر شما می‌دانید؟!»'];
ayat[2758]=[23,85,'سَيَقُولُونَ لِلَّهِ ۚ قُلْ أَفَلَا تَذَكَّرُونَ','بزودی (در پاسخ تو) می‌گویند: «همه از آن خداست!» بگو: «آیا متذکّر نمی‌شوید؟!»'];
ayat[2759]=[23,86,'قُلْ مَن رَّبُّ السَّمَاوَاتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيمِ','بگو: «چه کسی پروردگار آسمانهای هفتگانه، و پروردگار عرش عظیم است؟»'];
ayat[2760]=[23,87,'سَيَقُولُونَ لِلَّهِ ۚ قُلْ أَفَلَا تَتَّقُونَ','بزودی خواهند گفت: «همه اینها از آن خداست!» بگو: «آیا تقوا پیشه نمی‌کنید (و از خدا نمی‌ترسید و دست از شرک برنمی‌دارید)؟!»'];
ayat[2761]=[23,88,'قُلْ مَن بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَهُوَ يُجِيرُ وَلَا يُجَارُ عَلَيْهِ إِن كُنتُمْ تَعْلَمُونَ','بگو: «اگر می‌دانید، چه کسی حکومت همه موجودات را در دست دارد، و به بی پناهان پناه می‌دهد، و نیاز به پناه‌دادن ندارد؟!»'];
ayat[2762]=[23,89,'سَيَقُولُونَ لِلَّهِ ۚ قُلْ فَأَنَّىٰ تُسْحَرُونَ','خواهند گفت: «(همه اینها) از آن خداست» بگو: «با این حال چگونه می‌گویید سحر شده‌اید (و این سخنان سحر و افسون است)؟!»'];
ayat[2763]=[23,90,'بَلْ أَتَيْنَاهُم بِالْحَقِّ وَإِنَّهُمْ لَكَاذِبُونَ','نه، (واقع این است که) ما حقّ را برای آنها آوردیم؛ و آنان دروغ می‌گویند!'];
ayat[2764]=[23,91,'مَا اتَّخَذَ اللَّهُ مِن وَلَدٍ وَمَا كَانَ مَعَهُ مِنْ إِلَٰهٍ ۚ إِذًا لَّذَهَبَ كُلُّ إِلَٰهٍ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلَىٰ بَعْضٍ ۚ سُبْحَانَ اللَّهِ عَمَّا يَصِفُونَ','خدا هرگز فرزندی برای خود انتخاب نکرده؛ و معبود دیگری با او نیست؛ که اگر چنین می‌شد، هر یک از خدایان مخلوقات خود را تدبیر و اداره می‌کردند و بعضی بر بعضی دیگر برتری می‌جستند (و جهان هستی به تباهی کشیده می‌شد)؛ منزّه است خدا از آنچه آنان توصیف می‌کنند!'];
ayat[2765]=[23,92,'عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعَالَىٰ عَمَّا يُشْرِكُونَ','او دانای نهان و آشکار است؛ پس برتر است از آنچه برای او همتا قرار می‌دهند!'];
ayat[2766]=[23,93,'قُل رَّبِّ إِمَّا تُرِيَنِّي مَا يُوعَدُونَ','بگو: «پروردگار من! اگر عذابهایی را که به آنان وعده داده می‌شود به من نشان دهی (و در زندگیم آن را ببینم)...'];
ayat[2767]=[23,94,'رَبِّ فَلَا تَجْعَلْنِي فِي الْقَوْمِ الظَّالِمِينَ','پروردگار من! مرا (در این عذابها) با گروه ستمگران قرار مده!»'];
ayat[2768]=[23,95,'وَإِنَّا عَلَىٰ أَن نُّرِيَكَ مَا نَعِدُهُمْ لَقَادِرُونَ','و ما تواناییم که آنچه را به آنها وعده می‌دهیم به تو نشان دهیم!'];
ayat[2769]=[23,96,'ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ السَّيِّئَةَ ۚ نَحْنُ أَعْلَمُ بِمَا يَصِفُونَ','بدی را به بهترین راه و روش دفع کن (و پاسخ بدی را به نیکی ده)! ما به آنچه توصیف می‌کنند آگاهتریم!'];
ayat[2770]=[23,97,'وَقُل رَّبِّ أَعُوذُ بِكَ مِنْ هَمَزَاتِ الشَّيَاطِينِ','و بگو: «پروردگارا! از وسوسه‌های شیاطین به تو پناه می‌برم!'];
ayat[2771]=[23,98,'وَأَعُوذُ بِكَ رَبِّ أَن يَحْضُرُونِ','و از اینکه آنان نزد من حاضر شوند (نیز) -ای پروردگار من- به تو پناه می‌برم!»'];
ayat[2772]=[23,99,'حَتَّىٰ إِذَا جَاءَ أَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُونِ','(آنها همچنان به راه غلط خود ادامه می‌دهند) تا زمانی که مرگ یکی از آنان فرارسد، می‌گوید: «پروردگار من! مرا بازگردانید!'];
ayat[2773]=[23,100,'لَعَلِّي أَعْمَلُ صَالِحًا فِيمَا تَرَكْتُ ۚ كَلَّا ۚ إِنَّهَا كَلِمَةٌ هُوَ قَائِلُهَا ۖ وَمِن وَرَائِهِم بَرْزَخٌ إِلَىٰ يَوْمِ يُبْعَثُونَ','شاید در آنچه ترک کردم (و کوتاهی نمودم) عمل صالحی انجام دهم!» (ولی به او می‌گویند:) چنین نیست! این سخنی است که او به زبان می‌گوید (و اگر بازگردد، کارش همچون گذشته است)! و پشت سر آنان برزخی است تا روزی که برانگیخته شوند!'];
ayat[2774]=[23,101,'فَإِذَا نُفِخَ فِي الصُّورِ فَلَا أَنسَابَ بَيْنَهُمْ يَوْمَئِذٍ وَلَا يَتَسَاءَلُونَ','هنگامی که در «صور» دمیده شود، هیچ یک از پیوندهای خویشاوندی میان آنها در آن روز نخواهد بود؛ و از یکدیگر تقاضای کمک نمی‌کنند (چون کاری از کسی ساخته نیست)!'];
ayat[2775]=[23,102,'فَمَن ثَقُلَتْ مَوَازِينُهُ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','و کسانی که وزنه اعمالشان سنگین است، همان رستگارانند!'];
ayat[2776]=[23,103,'وَمَنْ خَفَّتْ مَوَازِينُهُ فَأُولَٰئِكَ الَّذِينَ خَسِرُوا أَنفُسَهُمْ فِي جَهَنَّمَ خَالِدُونَ','و آنان که وزنه اعمالشان سبک باشد، کسانی هستند که سرمایه وجود خود را از دست داده، در جهنم جاودانه خواهند ماند!'];
ayat[2777]=[23,104,'تَلْفَحُ وُجُوهَهُمُ النَّارُ وَهُمْ فِيهَا كَالِحُونَ','شعله‌های سوزان آتش همچون شمشیر به صورتهایشان نواخته می‌شود؛ و در دوزخ چهره‌ای عبوس دارند.'];
ayat[2778]=[23,105,'أَلَمْ تَكُنْ آيَاتِي تُتْلَىٰ عَلَيْكُمْ فَكُنتُم بِهَا تُكَذِّبُونَ','(به آنها گفته می‌شود:) آیا آیات من بر شما خوانده نمی‌شد، پس آن را تکذیب می‌کردید؟!'];
ayat[2779]=[23,106,'قَالُوا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًا ضَالِّينَ','می‌گویند: «پروردگارا! بدبختی ما بر ما چیره شد، و ما قوم گمراهی بودیم!'];
ayat[2780]=[23,107,'رَبَّنَا أَخْرِجْنَا مِنْهَا فَإِنْ عُدْنَا فَإِنَّا ظَالِمُونَ','پروردگارا! ما را از این (دوزخ) بیرون آر، اگر بار دیگر تکرار کردیم قطعاً ستمگریم (و مستحق عذاب)!»'];
ayat[2781]=[23,108,'قَالَ اخْسَئُوا فِيهَا وَلَا تُكَلِّمُونِ','(خداوند) می‌گوید: «دور شوید در دوزخ، و با من سخن مگویید!'];
ayat[2782]=[23,109,'إِنَّهُ كَانَ فَرِيقٌ مِّنْ عِبَادِي يَقُولُونَ رَبَّنَا آمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَأَنتَ خَيْرُ الرَّاحِمِينَ','(فراموش کرده‌اید) گروهی از بندگانم می‌گفتند: پروردگارا! ما ایمان آوردیم؛ ما را ببخش و بر ما رحم کن؛ و تو بهترین رحم کنندگانی!'];
ayat[2783]=[23,110,'فَاتَّخَذْتُمُوهُمْ سِخْرِيًّا حَتَّىٰ أَنسَوْكُمْ ذِكْرِي وَكُنتُم مِّنْهُمْ تَضْحَكُونَ','امّا شما آنها را به باد مسخره گرفتید تا شما را از یاد من غافل کردند؛ و شما به آنان می‌خندیدید!'];
ayat[2784]=[23,111,'إِنِّي جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوا أَنَّهُمْ هُمُ الْفَائِزُونَ','ولی من امروز آنها را بخاطر صبر و استقامتشان پاداش دادم؛ آنها پیروز و رستگارند!»'];
ayat[2785]=[23,112,'قَالَ كَمْ لَبِثْتُمْ فِي الْأَرْضِ عَدَدَ سِنِينَ','(خداوند) می‌گوید: «چند سال در روی زمین توقّف کردید؟»'];
ayat[2786]=[23,113,'قَالُوا لَبِثْنَا يَوْمًا أَوْ بَعْضَ يَوْمٍ فَاسْأَلِ الْعَادِّينَ','(در پاسخ) می‌گویند: «تنها به اندازه یک روز، یا قسمتی از یک روز! از آنها که می‌توانند بشمارند بپرس!»'];
ayat[2787]=[23,114,'قَالَ إِن لَّبِثْتُمْ إِلَّا قَلِيلًا ۖ لَّوْ أَنَّكُمْ كُنتُمْ تَعْلَمُونَ','می‌گوید: «(آری،) شما مقدار کمی توقّف نمودید اگر می‌دانستید!»'];
ayat[2788]=[23,115,'أَفَحَسِبْتُمْ أَنَّمَا خَلَقْنَاكُمْ عَبَثًا وَأَنَّكُمْ إِلَيْنَا لَا تُرْجَعُونَ','آیا گمان کردید شما را بیهوده آفریده‌ایم، و بسوی ما باز نمی‌گردید؟'];
ayat[2789]=[23,116,'فَتَعَالَى اللَّهُ الْمَلِكُ الْحَقُّ ۖ لَا إِلَٰهَ إِلَّا هُوَ رَبُّ الْعَرْشِ الْكَرِيمِ','پس برتر است خداوندی که فرمانروای حقّ است (از اینکه شما را بی‌هدف آفریده باشد)! معبودی جز او نیست؛ و او پروردگار عرش کریم است!'];
ayat[2790]=[23,117,'وَمَن يَدْعُ مَعَ اللَّهِ إِلَٰهًا آخَرَ لَا بُرْهَانَ لَهُ بِهِ فَإِنَّمَا حِسَابُهُ عِندَ رَبِّهِ ۚ إِنَّهُ لَا يُفْلِحُ الْكَافِرُونَ','و هر کس معبود دیگری را با خدا بخواند -و مسلّماً هیچ دلیلی بر آن نخواهد داشت- حساب او نزد پروردگارش خواهد بود؛ یقیناً کافران رستگار نخواهند شد!'];
ayat[2791]=[23,118,'وَقُل رَّبِّ اغْفِرْ وَارْحَمْ وَأَنتَ خَيْرُ الرَّاحِمِينَ','و بگو: «پروردگارا! مرا ببخش و رحمت کن؛ و تو بهترین رحم کنندگانی!'];
ayat[2792]=[24,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سُورَةٌ أَنزَلْنَاهَا وَفَرَضْنَاهَا وَأَنزَلْنَا فِيهَا آيَاتٍ بَيِّنَاتٍ لَّعَلَّكُمْ تَذَكَّرُونَ','(این) سوره‌ای است که آن را فرو فرستادیم، و (عمل به آن را) واجب نمودیم، و در آن آیات روشنی نازل کردیم، شاید شما متذکّر شوید!'];
ayat[2793]=[24,2,'الزَّانِيَةُ وَالزَّانِي فَاجْلِدُوا كُلَّ وَاحِدٍ مِّنْهُمَا مِائَةَ جَلْدَةٍ ۖ وَلَا تَأْخُذْكُم بِهِمَا رَأْفَةٌ فِي دِينِ اللَّهِ إِن كُنتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ وَلْيَشْهَدْ عَذَابَهُمَا طَائِفَةٌ مِّنَ الْمُؤْمِنِينَ','هر یک از زن و مرد زناکار را صد تازیانه بزنید؛ و نباید رأفت (و محبّت کاذب) نسبت به آن دو شما را از اجرای حکم الهی مانع شود، اگر به خدا و روز جزا ایمان دارید! و باید گروهی از مؤمنان مجازاتشان را مشاهده کنند!'];
ayat[2794]=[24,3,'الزَّانِي لَا يَنكِحُ إِلَّا زَانِيَةً أَوْ مُشْرِكَةً وَالزَّانِيَةُ لَا يَنكِحُهَا إِلَّا زَانٍ أَوْ مُشْرِكٌ ۚ وَحُرِّمَ ذَٰلِكَ عَلَى الْمُؤْمِنِينَ','مرد زناکار جز با زن زناکار یا مشرک ازدواج نمی‌کند؛ و زن زناکار را، جز مرد زناکار یا مشرک، به ازدواج خود درنمی‌آورد؛ و این کار بر مؤمنان حرام شده است!'];
ayat[2795]=[24,4,'وَالَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَدَاءَ فَاجْلِدُوهُمْ ثَمَانِينَ جَلْدَةً وَلَا تَقْبَلُوا لَهُمْ شَهَادَةً أَبَدًا ۚ وَأُولَٰئِكَ هُمُ الْفَاسِقُونَ','و کسانی که زنان پاکدامن را متهمّ می‌کنند، سپس چهار شاهد (بر مدّعای خود) نمی‌آورند، آنها را هشتاد تازیانه بزنید و شهادتشان را هرگز نپذیرید؛ و آنها همان فاسقانند!'];
ayat[2796]=[24,5,'إِلَّا الَّذِينَ تَابُوا مِن بَعْدِ ذَٰلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','مگر کسانی که بعد از آن توبه کنند و جبران نمایند (که خداوند آنها را می‌بخشد) زیرا خداوند آمرزنده و مهربان است.'];
ayat[2797]=[24,6,'وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ وَلَمْ يَكُن لَّهُمْ شُهَدَاءُ إِلَّا أَنفُسُهُمْ فَشَهَادَةُ أَحَدِهِمْ أَرْبَعُ شَهَادَاتٍ بِاللَّهِ ۙ إِنَّهُ لَمِنَ الصَّادِقِينَ','و کسانی که همسران خود را (به عمل منافی عفّت) متهمّ می‌کنند، و گواهانی جز خودشان ندارند، هر یک از آنها باید چهار مرتبه به نام خدا شهادت دهد که از راستگویان است؛'];
ayat[2798]=[24,7,'وَالْخَامِسَةُ أَنَّ لَعْنَتَ اللَّهِ عَلَيْهِ إِن كَانَ مِنَ الْكَاذِبِينَ','و در پنجمین بار بگوید که لعنت خدا بر او باد اگر از دروغگویان باشد.'];
ayat[2799]=[24,8,'وَيَدْرَأُ عَنْهَا الْعَذَابَ أَن تَشْهَدَ أَرْبَعَ شَهَادَاتٍ بِاللَّهِ ۙ إِنَّهُ لَمِنَ الْكَاذِبِينَ','آن زن نیز می‌تواند کیفر (زنا) را از خود دور کند، به این طریق که چهار بار خدا را به شهادت طلبد که آن مرد (در این نسبتی که به او می‌دهد) از دروغگویان است.'];
ayat[2800]=[24,9,'وَالْخَامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إِن كَانَ مِنَ الصَّادِقِينَ','و بار پنجم بگوید که غضب خدا بر او باد اگر آن مرد از راستگویان باشد.'];
ayat[2801]=[24,10,'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ تَوَّابٌ حَكِيمٌ','و اگر فضل و رحمت خدا شامل حال شما نبود و اینکه او توبه‌پذیر و حکیم است (بسیاری از شما گرفتار مجازات سخت الهی می‌شدید)!'];
ayat[2802]=[24,11,'إِنَّ الَّذِينَ جَاءُوا بِالْإِفْكِ عُصْبَةٌ مِّنكُمْ ۚ لَا تَحْسَبُوهُ شَرًّا لَّكُم ۖ بَلْ هُوَ خَيْرٌ لَّكُمْ ۚ لِكُلِّ امْرِئٍ مِّنْهُم مَّا اكْتَسَبَ مِنَ الْإِثْمِ ۚ وَالَّذِي تَوَلَّىٰ كِبْرَهُ مِنْهُمْ لَهُ عَذَابٌ عَظِيمٌ','مسلّماً کسانی که آن تهمت عظیم را عنوان کردند گروهی (متشکّل و توطئه‌گر) از شما بودند؛ امّا گمان نکنید این ماجرا برای شما بد است، بلکه خیر شما در آن است؛ آنها هر کدام سهم خود را از این گناهی که مرتکب شدند دارند؛ و از آنان کسی که بخش مهمّ آن را بر عهده داشت عذاب عظیمی برای اوست!'];
ayat[2803]=[24,12,'لَّوْلَا إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِأَنفُسِهِمْ خَيْرًا وَقَالُوا هَٰذَا إِفْكٌ مُّبِينٌ','چرا هنگامی که این (تهمت) را شنیدید، مردان و زنان با ایمان نسبت به خود (و کسی که همچون خود آنها بود) گمان خیر نبردند و نگفتند این دروغی بزرگ و آشکار است؟!'];
ayat[2804]=[24,13,'لَّوْلَا جَاءُوا عَلَيْهِ بِأَرْبَعَةِ شُهَدَاءَ ۚ فَإِذْ لَمْ يَأْتُوا بِالشُّهَدَاءِ فَأُولَٰئِكَ عِندَ اللَّهِ هُمُ الْكَاذِبُونَ','چرا چهار شاهد برای آن نیاوردند؟! اکنون که این گواهان را نیاوردند، آنان در پیشگاه خدا دروغگویانند!'];
ayat[2805]=[24,14,'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا وَالْآخِرَةِ لَمَسَّكُمْ فِي مَا أَفَضْتُمْ فِيهِ عَذَابٌ عَظِيمٌ','و اگر فضل و رحمت الهی در دنیا و آخرت شامل شما نمی‌شد، بخاطر این گناهی که کردید عذاب سختی به شما می‌رسید!'];
ayat[2806]=[24,15,'إِذْ تَلَقَّوْنَهُ بِأَلْسِنَتِكُمْ وَتَقُولُونَ بِأَفْوَاهِكُم مَّا لَيْسَ لَكُم بِهِ عِلْمٌ وَتَحْسَبُونَهُ هَيِّنًا وَهُوَ عِندَ اللَّهِ عَظِيمٌ','به خاطر بیاورید زمانی را که این شایعه را از زبان یکدیگر می‌گرفتید، و با دهان خود سخنی می‌گفتید که به آن یقین نداشتید؛ و آن را کوچک می‌پنداشتید در حالی که نزد خدا بزرگ است!'];
ayat[2807]=[24,16,'وَلَوْلَا إِذْ سَمِعْتُمُوهُ قُلْتُم مَّا يَكُونُ لَنَا أَن نَّتَكَلَّمَ بِهَٰذَا سُبْحَانَكَ هَٰذَا بُهْتَانٌ عَظِيمٌ','چرا هنگامی که آن را شنیدید نگفتید: «ما حق نداریم که به این سخن تکلّم کنیم؛ خداوندا منزّهی تو، این بهتان بزرگی است»؟!'];
ayat[2808]=[24,17,'يَعِظُكُمُ اللَّهُ أَن تَعُودُوا لِمِثْلِهِ أَبَدًا إِن كُنتُم مُّؤْمِنِينَ','خداوند شما را اندرز می‌دهد که هرگز چنین کاری را تکرار نکنید اگر ایمان دارید!'];
ayat[2809]=[24,18,'وَيُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ','و خداوند آیات را برای شما بیان می‌کند، و خدا دانا و حکیم است.'];
ayat[2810]=[24,19,'إِنَّ الَّذِينَ يُحِبُّونَ أَن تَشِيعَ الْفَاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذَابٌ أَلِيمٌ فِي الدُّنْيَا وَالْآخِرَةِ ۚ وَاللَّهُ يَعْلَمُ وَأَنتُمْ لَا تَعْلَمُونَ','کسانی که دوست دارند زشتیها در میان مردم با ایمان شیوع یابد، عذاب دردناکی برای آنان در دنیا و آخرت است؛ و خداوند می‌داند و شما نمی‌دانید!'];
ayat[2811]=[24,20,'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ رَءُوفٌ رَّحِيمٌ','و اگر فضل و رحمت الهی شامل حال شما نبود و اینکه خدا مهربان و رحیم است (مجازات سختی دامانتان را می‌گرفت)!'];
ayat[2812]=[24,21,'۞ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ وَمَن يَتَّبِعْ خُطُوَاتِ الشَّيْطَانِ فَإِنَّهُ يَأْمُرُ بِالْفَحْشَاءِ وَالْمُنكَرِ ۚ وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكَىٰ مِنكُم مِّنْ أَحَدٍ أَبَدًا وَلَٰكِنَّ اللَّهَ يُزَكِّي مَن يَشَاءُ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','ای کسانی که ایمان آورده‌اید! از گامهای شیطان پیروی نکنید! هر کس پیرو شیطان شود (گمراهش می‌سازد، زیرا) او به فحشا و منکر فرمان می‌دهد! و اگر فضل و رحمت الهی بر شما نبود، هرگز احدی از شما پاک نمی‌شد؛ ولی خداوند هر که را بخواهد تزکیه می‌کند، و خدا شنوا و داناست!'];
ayat[2813]=[24,22,'وَلَا يَأْتَلِ أُولُو الْفَضْلِ مِنكُمْ وَالسَّعَةِ أَن يُؤْتُوا أُولِي الْقُرْبَىٰ وَالْمَسَاكِينَ وَالْمُهَاجِرِينَ فِي سَبِيلِ اللَّهِ ۖ وَلْيَعْفُوا وَلْيَصْفَحُوا ۗ أَلَا تُحِبُّونَ أَن يَغْفِرَ اللَّهُ لَكُمْ ۗ وَاللَّهُ غَفُورٌ رَّحِيمٌ','آنها که از میان شما دارای برتری (مالی) و وسعت زندگی هستند نباید سوگند یاد کنند که از انفاق نسبت به نزدیکان و مستمندان و مهاجران در راه خدا دریغ نمایند؛ آنها باید عفو کنند و چشم بپوشند؛ آیا دوست نمی‌دارید خداوند شما را ببخشد؟! و خداوند آمرزنده و مهربان است!'];
ayat[2814]=[24,23,'إِنَّ الَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ الْغَافِلَاتِ الْمُؤْمِنَاتِ لُعِنُوا فِي الدُّنْيَا وَالْآخِرَةِ وَلَهُمْ عَذَابٌ عَظِيمٌ','کسانی که زنان پاکدامن و بی‌خبر (از هرگونه آلودگی) و مؤمن را متهم می‌سازند، در دنیا و آخرت از رحمت الهی بدورند و عذاب بزرگی برای آنهاست.'];
ayat[2815]=[24,24,'يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَأَيْدِيهِمْ وَأَرْجُلُهُم بِمَا كَانُوا يَعْمَلُونَ','در آن روز زبانها و دستها و پاهایشان بر ضدّ آنها به اعمالی که مرتکب می‌شدند گواهی می‌دهد!'];
ayat[2816]=[24,25,'يَوْمَئِذٍ يُوَفِّيهِمُ اللَّهُ دِينَهُمُ الْحَقَّ وَيَعْلَمُونَ أَنَّ اللَّهَ هُوَ الْحَقُّ الْمُبِينُ','آن روز، خداوند جزای واقعی آنان را بی‌کم و کاست می‌دهد؛ و می‌دانند که خداوند حق آشکار است!'];
ayat[2817]=[24,26,'الْخَبِيثَاتُ لِلْخَبِيثِينَ وَالْخَبِيثُونَ لِلْخَبِيثَاتِ ۖ وَالطَّيِّبَاتُ لِلطَّيِّبِينَ وَالطَّيِّبُونَ لِلطَّيِّبَاتِ ۚ أُولَٰئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَ ۖ لَهُم مَّغْفِرَةٌ وَرِزْقٌ كَرِيمٌ','زنان ناپاک از آن مردان ناپاکند، و مردان ناپاک نیز به زنان ناپاک تعلّق دارند؛ و زنان پاک از آن مردان پاک، و مردان پاک از آن زنان پاکند! اینان از نسبتهای ناروایی که (ناپاکان) به آنان می‌دهند مبرّا هستند؛ و برای آنان آمرزش (الهی) و روزی پرارزشی است!'];
ayat[2818]=[24,27,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَدْخُلُوا بُيُوتًا غَيْرَ بُيُوتِكُمْ حَتَّىٰ تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلَىٰ أَهْلِهَا ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ لَعَلَّكُمْ تَذَكَّرُونَ','ای کسانی که ایمان آورده‌اید! در خانه‌هایی غیر از خانه خود وارد نشوید تا اجازه بگیرید و بر اهل آن خانه سلام کنید؛ این برای شما بهتر است؛ شاید متذکّر شوید!'];
ayat[2819]=[24,28,'فَإِن لَّمْ تَجِدُوا فِيهَا أَحَدًا فَلَا تَدْخُلُوهَا حَتَّىٰ يُؤْذَنَ لَكُمْ ۖ وَإِن قِيلَ لَكُمُ ارْجِعُوا فَارْجِعُوا ۖ هُوَ أَزْكَىٰ لَكُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ','و اگر کسی را در آن نیافتید، وارد نشوید تا به شما اجازه داده شود؛ و اگر گفته شد: «بازگردید!» بازگردید؛ این برای شما پاکیزه‌تر است؛ و خداوند به آنچه انجام می‌دهید آگاه است!'];
ayat[2820]=[24,29,'لَّيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَدْخُلُوا بُيُوتًا غَيْرَ مَسْكُونَةٍ فِيهَا مَتَاعٌ لَّكُمْ ۚ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ','(ولی) گناهی بر شما نیست که وارد خانه‌های غیر مسکونی بشوید که در آن متاعی متعلّق به شما وجود دارد؛ و خدا آنچه را آشکار می‌کنید و آنچه را پنهان می‌دارید، می‌داند!'];
ayat[2821]=[24,30,'قُل لِّلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصَارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْ ۚ ذَٰلِكَ أَزْكَىٰ لَهُمْ ۗ إِنَّ اللَّهَ خَبِيرٌ بِمَا يَصْنَعُونَ','به مؤمنان بگو چشمهای خود را (از نگاه به نامحرمان) فروگیرند، و عفاف خود را حفظ کنند؛ این برای آنان پاکیزه‌تر است؛ خداوند از آنچه انجام می‌دهید آگاه است!'];
ayat[2822]=[24,31,'وَقُل لِّلْمُؤْمِنَاتِ يَغْضُضْنَ مِنْ أَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنْهَا ۖ وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّ ۖ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوْ آبَائِهِنَّ أَوْ آبَاءِ بُعُولَتِهِنَّ أَوْ أَبْنَائِهِنَّ أَوْ أَبْنَاءِ بُعُولَتِهِنَّ أَوْ إِخْوَانِهِنَّ أَوْ بَنِي إِخْوَانِهِنَّ أَوْ بَنِي أَخَوَاتِهِنَّ أَوْ نِسَائِهِنَّ أَوْ مَا مَلَكَتْ أَيْمَانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أُولِي الْإِرْبَةِ مِنَ الرِّجَالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلَىٰ عَوْرَاتِ النِّسَاءِ ۖ وَلَا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِينَ مِن زِينَتِهِنَّ ۚ وَتُوبُوا إِلَى اللَّهِ جَمِيعًا أَيُّهَ الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ','و به زنان با ایمان بگو چشمهای خود را (از نگاه هوس‌آلود) فروگیرند، و دامان خویش را حفظ کنند و زینت خود را -جز آن مقدار که نمایان است- آشکار ننمایند و (اطراف) روسری‌های خود را بر سینه خود افکنند (تا گردن و سینه با آن پوشانده شود)، و زینت خود را آشکار نسازند مگر برای شوهرانشان، یا پدرانشان، یا پدر شوهرانشان، یا پسرانشان، یا پسران همسرانشان، یا برادرانشان، یا پسران برادرانشان، یا پسران خواهرانشان، یا زنان هم‌کیششان، یا بردگانشان [=کنیزانشان‌]، یا افراد سفیه که تمایلی به زن ندارند، یا کودکانی که از امور جنسی مربوط به زنان آگاه نیستند؛ و هنگام راه رفتن پاهای خود را به زمین نزنند تا زینت پنهانیشان دانسته شود (و صدای خلخال که برپا دارند به گوش رسد). و همگی بسوی خدا بازگردید ای مؤمنان، تا رستگار شوید!'];
ayat[2823]=[24,32,'وَأَنكِحُوا الْأَيَامَىٰ مِنكُمْ وَالصَّالِحِينَ مِنْ عِبَادِكُمْ وَإِمَائِكُمْ ۚ إِن يَكُونُوا فُقَرَاءَ يُغْنِهِمُ اللَّهُ مِن فَضْلِهِ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ','مردان و زنان بی‌همسر خود را همسر دهید، همچنین غلامان و کنیزان صالح و درستکارتان را؛ اگر فقیر و تنگدست باشند، خداوند از فضل خود آنان را بی‌نیاز می‌سازد؛ خداوند گشایش‌دهنده و آگاه است!'];
ayat[2824]=[24,33,'وَلْيَسْتَعْفِفِ الَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغْنِيَهُمُ اللَّهُ مِن فَضْلِهِ ۗ وَالَّذِينَ يَبْتَغُونَ الْكِتَابَ مِمَّا مَلَكَتْ أَيْمَانُكُمْ فَكَاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْرًا ۖ وَآتُوهُم مِّن مَّالِ اللَّهِ الَّذِي آتَاكُمْ ۚ وَلَا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَاءِ إِنْ أَرَدْنَ تَحَصُّنًا لِّتَبْتَغُوا عَرَضَ الْحَيَاةِ الدُّنْيَا ۚ وَمَن يُكْرِههُّنَّ فَإِنَّ اللَّهَ مِن بَعْدِ إِكْرَاهِهِنَّ غَفُورٌ رَّحِيمٌ','و کسانی که امکانی برای ازدواج نمی‌یابند، باید پاکدامنی پیشه کنند تا خداوند از فضل خود آنان را بی‌نیاز گرداند! و آن بردگانتان که خواستار مکاتبه [=قرار داد مخصوص برای آزاد شدن‌] هستند، با آنان قرار داد ببندید اگر رشد و صلاح در آنان احساس می‌کنید (که بعد از آزادی، توانایی زندگی مستقل را دارند)؛ و چیزی از مال خدا را که به شما داده است به آنان بدهید! و کنیزان خود را برای دستیابی متاع ناپایدار زندگی دنیا مجبور به خود فروشی نکنید اگر خودشان می‌خواهند پاک بمانند! و هر کس آنها را (بر این کار) اجبار کند، (سپس پشیمان گردد،) خداوند بعد از این اجبار آنها غفور و رحیم است! (توبه کنید و بازگردید، تا خدا شما را ببخشد!)'];
ayat[2825]=[24,34,'وَلَقَدْ أَنزَلْنَا إِلَيْكُمْ آيَاتٍ مُّبَيِّنَاتٍ وَمَثَلًا مِّنَ الَّذِينَ خَلَوْا مِن قَبْلِكُمْ وَمَوْعِظَةً لِّلْمُتَّقِينَ','ما بر شما آیاتی فرستادیم که حقایق بسیاری را تبیین می‌کند، و اخباری از کسانی که پیش از شما بودند، و موعظه و اندرزی برای پرهیزگاران!'];
ayat[2826]=[24,35,'۞ اللَّهُ نُورُ السَّمَاوَاتِ وَالْأَرْضِ ۚ مَثَلُ نُورِهِ كَمِشْكَاةٍ فِيهَا مِصْبَاحٌ ۖ الْمِصْبَاحُ فِي زُجَاجَةٍ ۖ الزُّجَاجَةُ كَأَنَّهَا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِن شَجَرَةٍ مُّبَارَكَةٍ زَيْتُونَةٍ لَّا شَرْقِيَّةٍ وَلَا غَرْبِيَّةٍ يَكَادُ زَيْتُهَا يُضِيءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌ ۚ نُّورٌ عَلَىٰ نُورٍ ۗ يَهْدِي اللَّهُ لِنُورِهِ مَن يَشَاءُ ۚ وَيَضْرِبُ اللَّهُ الْأَمْثَالَ لِلنَّاسِ ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','خداوند نور آسمانها و زمین است؛ مثل نور خداوند همانند چراغدانی است که در آن چراغی (پر فروغ) باشد، آن چراغ در حبابی قرار گیرد، حبابی شفاف و درخشنده همچون یک ستاره فروزان، این چراغ با روغنی افروخته می‌شود که از درخت پربرکت زیتونی گرفته شده که نه شرقی است و نه غربی؛ (روغنش آنچنان صاف و خالص است که) نزدیک است بدون تماس با آتش شعله‌ور شود؛ نوری است بر فراز نوری؛ و خدا هر کس را بخواهد به نور خود هدایت می‌کند، و خداوند به هر چیزی داناست.'];
ayat[2827]=[24,36,'فِي بُيُوتٍ أَذِنَ اللَّهُ أَن تُرْفَعَ وَيُذْكَرَ فِيهَا اسْمُهُ يُسَبِّحُ لَهُ فِيهَا بِالْغُدُوِّ وَالْآصَالِ','(این چراغ پرفروغ) در خانه‌هایی قرار دارد که خداوند اذن فرموده دیوارهای آن را بالا برند (تا از دستبرد شیاطین و هوسبازان در امان باشد)؛ خانه‌هایی که نام خدا در آنها برده می‌شود، و صبح و شام در آنها تسبیح او می‌گویند...'];
ayat[2828]=[24,37,'رِجَالٌ لَّا تُلْهِيهِمْ تِجَارَةٌ وَلَا بَيْعٌ عَن ذِكْرِ اللَّهِ وَإِقَامِ الصَّلَاةِ وَإِيتَاءِ الزَّكَاةِ ۙ يَخَافُونَ يَوْمًا تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَالْأَبْصَارُ','مردانی که نه تجارت و نه معامله‌ای آنان را از یاد خدا و برپاداشتن نماز و ادای زکات غافل نمی‌کند؛ آنها از روزی می‌ترسند که در آن، دلها و چشمها زیر و رو می‌شود.'];
ayat[2829]=[24,38,'لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا عَمِلُوا وَيَزِيدَهُم مِّن فَضْلِهِ ۗ وَاللَّهُ يَرْزُقُ مَن يَشَاءُ بِغَيْرِ حِسَابٍ','(آنها به سراغ این کارها می‌روند) تا خداوند آنان را به بهترین اعمالی که انجام داده‌اند پاداش دهد، و از فضل خود بر پاداششان بیفزاید؛ و خداوند به هر کس بخواهد بی حساب روزی می‌دهد (و از مواهب بی‌انتهای خویش بهره‌مند می‌سازد).'];
ayat[2830]=[24,39,'وَالَّذِينَ كَفَرُوا أَعْمَالُهُمْ كَسَرَابٍ بِقِيعَةٍ يَحْسَبُهُ الظَّمْآنُ مَاءً حَتَّىٰ إِذَا جَاءَهُ لَمْ يَجِدْهُ شَيْئًا وَوَجَدَ اللَّهَ عِندَهُ فَوَفَّاهُ حِسَابَهُ ۗ وَاللَّهُ سَرِيعُ الْحِسَابِ','کسانی که کافر شدند، اعمالشان همچون سرابی است در یک کویر که انسان تشنه از دور آن را آب می‌پندارد؛ امّا هنگامی که به سراغ آن می‌آید چیزی نمی‌یابد، و خدا را نزد آن می‌یابد که حساب او را بطور کامل می‌دهد؛ و خداوند سریع الحساب است!'];
ayat[2831]=[24,40,'أَوْ كَظُلُمَاتٍ فِي بَحْرٍ لُّجِّيٍّ يَغْشَاهُ مَوْجٌ مِّن فَوْقِهِ مَوْجٌ مِّن فَوْقِهِ سَحَابٌ ۚ ظُلُمَاتٌ بَعْضُهَا فَوْقَ بَعْضٍ إِذَا أَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرَاهَا ۗ وَمَن لَّمْ يَجْعَلِ اللَّهُ لَهُ نُورًا فَمَا لَهُ مِن نُّورٍ','یا همچون ظلماتی در یک دریای عمیق و پهناور که موج آن را پوشانده، و بر فراز آن موج دیگری، و بر فراز آن ابری تاریک است؛ ظلمتهایی است یکی بر فراز دیگری، آن گونه که هر گاه دست خود را خارج کند ممکن نیست آن را ببیند! و کسی که خدا نوری برای او قرار نداده، نوری برای او نیست!'];
ayat[2832]=[24,41,'أَلَمْ تَرَ أَنَّ اللَّهَ يُسَبِّحُ لَهُ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ وَالطَّيْرُ صَافَّاتٍ ۖ كُلٌّ قَدْ عَلِمَ صَلَاتَهُ وَتَسْبِيحَهُ ۗ وَاللَّهُ عَلِيمٌ بِمَا يَفْعَلُونَ','آیا ندیدی تمام آنان که در آسمانها و زمینند برای خدا تسبیح می‌کنند، و همچنین پرندگان به هنگامی که بر فراز آسمان بال گسترده‌اند؟! هر یک از آنها نماز و تسبیح خود را می‌داند؛ و خداوند به آنچه انجام میدهند داناست!'];
ayat[2833]=[24,42,'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِلَى اللَّهِ الْمَصِيرُ','و از برای خداست حکومت و مالکیّت آسمانها و زمین؛ و بازگشت (تمامی موجودات) بسوی اوست!'];
ayat[2834]=[24,43,'أَلَمْ تَرَ أَنَّ اللَّهَ يُزْجِي سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِهِ وَيُنَزِّلُ مِنَ السَّمَاءِ مِن جِبَالٍ فِيهَا مِن بَرَدٍ فَيُصِيبُ بِهِ مَن يَشَاءُ وَيَصْرِفُهُ عَن مَّن يَشَاءُ ۖ يَكَادُ سَنَا بَرْقِهِ يَذْهَبُ بِالْأَبْصَارِ','آیا ندیدی که خداوند ابرهایی را به آرامی می‌راند، سپس میان آنها پیوند می‌دهد، و بعد آن را متراکم می‌سازد؟! در این حال، دانه‌های باران را می‌بینی که از لابه‌لای آن خارج می‌شود؛ و از آسمان -از کوه‌هایی که در آن است [=ابرهایی که همچون کوه‌ها انباشته شده‌اند]- دانه‌های تگرگ نازل می‌کند، و هر کس را بخواهد بوسیله آن زیان می‌رساند، و از هر کس بخواهد این زیان را برطرف می‌کند؛ نزدیک است درخشندگی برق آن (ابرها) چشمها را ببرد!'];
ayat[2835]=[24,44,'يُقَلِّبُ اللَّهُ اللَّيْلَ وَالنَّهَارَ ۚ إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِّأُولِي الْأَبْصَارِ','خداوند شب و روز را دگرگون می‌سازد؛ در این عبرتی است برای صاحبان بصیرت!'];
ayat[2836]=[24,45,'وَاللَّهُ خَلَقَ كُلَّ دَابَّةٍ مِّن مَّاءٍ ۖ فَمِنْهُم مَّن يَمْشِي عَلَىٰ بَطْنِهِ وَمِنْهُم مَّن يَمْشِي عَلَىٰ رِجْلَيْنِ وَمِنْهُم مَّن يَمْشِي عَلَىٰ أَرْبَعٍ ۚ يَخْلُقُ اللَّهُ مَا يَشَاءُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','و خداوند هر جنبنده‌ای را از آبی آفرید؛ گروهی از آنها بر شکم خود راه می‌روند، و گروهی بر دو پای خود، و گروهی بر چهار پا راه می‌روند؛ خداوند هر چه را بخواهد می‌آفریند، زیرا خدا بر همه چیز تواناست!'];
ayat[2837]=[24,46,'لَّقَدْ أَنزَلْنَا آيَاتٍ مُّبَيِّنَاتٍ ۚ وَاللَّهُ يَهْدِي مَن يَشَاءُ إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','ما آیات روشنگری نازل کردیم؛ و خدا هر که را بخواهد به صراط مستقیم هدایت می‌کند!'];
ayat[2838]=[24,47,'وَيَقُولُونَ آمَنَّا بِاللَّهِ وَبِالرَّسُولِ وَأَطَعْنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٌ مِّنْهُم مِّن بَعْدِ ذَٰلِكَ ۚ وَمَا أُولَٰئِكَ بِالْمُؤْمِنِينَ','آنها می‌گویند: «به خدا و پیامبر ایمان داریم و اطاعت می‌کنیم!» ولی بعد از این ادّعا، گروهی از آنان رویگردان می‌شوند؛ آنها (در حقیقت) مؤمن نیستند!'];
ayat[2839]=[24,48,'وَإِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ إِذَا فَرِيقٌ مِّنْهُم مُّعْرِضُونَ','و هنگامی که از آنان دعوت شود که بسوی خدا و پیامبرش بیایند تا در میانشان داوری کند، ناگهان گروهی از آنان رویگردان می‌شوند!'];
ayat[2840]=[24,49,'وَإِن يَكُن لَّهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ','ولی اگر حق داشته باشند (و داوری به نفع آنان شود) با سرعت و تسلیم بسوی او می‌آیند!'];
ayat[2841]=[24,50,'أَفِي قُلُوبِهِم مَّرَضٌ أَمِ ارْتَابُوا أَمْ يَخَافُونَ أَن يَحِيفَ اللَّهُ عَلَيْهِمْ وَرَسُولُهُ ۚ بَلْ أُولَٰئِكَ هُمُ الظَّالِمُونَ','آیا در دلهای آنان بیماری است، یا شکّ و تردید دارند، یا می‌ترسند خدا و رسولش بر آنان ستم کنند؟! نه، بلکه آنها خودشان ستمگرند!'];
ayat[2842]=[24,51,'إِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِينَ إِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَن يَقُولُوا سَمِعْنَا وَأَطَعْنَا ۚ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','سخن مؤمنان، هنگامی که بسوی خدا و رسولش دعوت شوند تا میان آنان داوری کند، تنها این است که می‌گویند: «شنیدیم و اطاعت کردیم!» و اینها همان رستگاران واقعی هستند.'];
ayat[2843]=[24,52,'وَمَن يُطِعِ اللَّهَ وَرَسُولَهُ وَيَخْشَ اللَّهَ وَيَتَّقْهِ فَأُولَٰئِكَ هُمُ الْفَائِزُونَ','و هر کس خدا و پیامبرش را اطاعت کند، و از خدا بترسد و از مخالفت فرمانش بپرهیزد، چنین کسانی همان پیروزمندان واقعی هستند!'];
ayat[2844]=[24,53,'۞ وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ ۖ قُل لَّا تُقْسِمُوا ۖ طَاعَةٌ مَّعْرُوفَةٌ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ','آنها با نهایت تأکید سوگند یاد کردند که اگر به آنان فرمان دهی، (از خانه و اموال خود) بیرون می‌روند (و جان را در طبق اخلاص گذارده تقدیم می‌کنند)؛ بگو: «سوگند یاد نکنید؛ شما طاعت خالصانه نشان دهید که خداوند به آنچه انجام می‌دهید آگاه است!»'];
ayat[2845]=[24,54,'قُلْ أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ ۖ فَإِن تَوَلَّوْا فَإِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُم مَّا حُمِّلْتُمْ ۖ وَإِن تُطِيعُوهُ تَهْتَدُوا ۚ وَمَا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ الْمُبِينُ','بگو: «خدا را اطاعت کنید، و از پیامبرش فرمان برید! و اگر سرپیچی نمایید، پیامبر مسؤول اعمال خویش است و شما مسؤول اعمال خود! امّا اگر از او اطاعت کنید، هدایت خواهید شد؛ و بر پیامبر چیزی جز رساندن آشکار نیست!»'];
ayat[2846]=[24,55,'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا مِنكُمْ وَعَمِلُوا الصَّالِحَاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْأَرْضِ كَمَا اسْتَخْلَفَ الَّذِينَ مِن قَبْلِهِمْ وَلَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ الَّذِي ارْتَضَىٰ لَهُمْ وَلَيُبَدِّلَنَّهُم مِّن بَعْدِ خَوْفِهِمْ أَمْنًا ۚ يَعْبُدُونَنِي لَا يُشْرِكُونَ بِي شَيْئًا ۚ وَمَن كَفَرَ بَعْدَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ','خداوند به کسانی از شما که ایمان آورده و کارهای شایسته انجام داده‌اند وعده می‌دهد که قطعاً آنان را حکمران روی زمین خواهد کرد، همان گونه که به پیشینیان آنها خلافت روی زمین را بخشید؛ و دین و آیینی را که برای آنان پسندیده، پابرجا و ریشه‌دار خواهد ساخت؛ و ترسشان را به امنیّت و آرامش مبدّل می‌کند، آنچنان که تنها مرا می پرستند و چیزی را شریک من نخواهند ساخت. و کسانی که پس از آن کافر شوند، آنها فاسقانند.'];
ayat[2847]=[24,56,'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ','و نماز را برپا دارید، و زکات را بدهید، و رسول (خدا) را اطاعت کنید تا مشمول رحمت (او) شوید.'];
ayat[2848]=[24,57,'لَا تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الْأَرْضِ ۚ وَمَأْوَاهُمُ النَّارُ ۖ وَلَبِئْسَ الْمَصِيرُ','گمان مبر کافران می‌توانند از چنگال مجازات الهی در زمین فرار کنند! جایگاه آنان آتش است، و چه بد جایگاهی است!'];
ayat[2849]=[24,58,'يَا أَيُّهَا الَّذِينَ آمَنُوا لِيَسْتَأْذِنكُمُ الَّذِينَ مَلَكَتْ أَيْمَانُكُمْ وَالَّذِينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنكُمْ ثَلَاثَ مَرَّاتٍ ۚ مِّن قَبْلِ صَلَاةِ الْفَجْرِ وَحِينَ تَضَعُونَ ثِيَابَكُم مِّنَ الظَّهِيرَةِ وَمِن بَعْدِ صَلَاةِ الْعِشَاءِ ۚ ثَلَاثُ عَوْرَاتٍ لَّكُمْ ۚ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌ بَعْدَهُنَّ ۚ طَوَّافُونَ عَلَيْكُم بَعْضُكُمْ عَلَىٰ بَعْضٍ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','ای کسانی که ایمان آورده‌اید! بردگان شما، و همچنین کودکانتان که به حدّ بلوغ نرسیده‌اند، در سه وقت باید از شما اجازه بگیرند: پیش از نماز صبح، و نیمروز هنگامی که لباسهای (معمولی) خود را بیرون می‌آورید، و بعد از نماز عشا؛ این سه وقت خصوصی برای شماست؛ امّا بعد از این سه وقت، گناهی بر شما و بر آنان نیست (که بدون اذن وارد شوند) و بر گرد یکدیگر بگردید (و با صفا و صمیمیّت به یکدیگر خدمت نمایید). این گونه خداوند آیات را برای شما بیان می‌کند، و خداوند دانا و حکیم است!'];
ayat[2850]=[24,59,'وَإِذَا بَلَغَ الْأَطْفَالُ مِنكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذِينَ مِن قَبْلِهِمْ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ','و هنگامی که اطفال شما به سنّ بلوغ رسند باید اجازه بگیرند، همان گونه که اشخاصی که پیش از آنان بودند اجازه می‌گرفتند؛ اینچنین خداوند آیاتش را برای شما بیان می‌کند، و خدا دانا و حکیم است!'];
ayat[2851]=[24,60,'وَالْقَوَاعِدُ مِنَ النِّسَاءِ اللَّاتِي لَا يَرْجُونَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ أَن يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَاتٍ بِزِينَةٍ ۖ وَأَن يَسْتَعْفِفْنَ خَيْرٌ لَّهُنَّ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ','و زنان از کارافتاده‌ای که امید به ازدواج ندارند، گناهی بر آنان نیست که لباسهای (رویین) خود را بر زمین بگذارند، بشرط اینکه در برابر مردم خودآرایی نکنند؛ و اگر خود را بپوشانند برای آنان بهتر است؛ و خداوند شنوا و داناست.'];
ayat[2852]=[24,61,'لَّيْسَ عَلَى الْأَعْمَىٰ حَرَجٌ وَلَا عَلَى الْأَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَرِيضِ حَرَجٌ وَلَا عَلَىٰ أَنفُسِكُمْ أَن تَأْكُلُوا مِن بُيُوتِكُمْ أَوْ بُيُوتِ آبَائِكُمْ أَوْ بُيُوتِ أُمَّهَاتِكُمْ أَوْ بُيُوتِ إِخْوَانِكُمْ أَوْ بُيُوتِ أَخَوَاتِكُمْ أَوْ بُيُوتِ أَعْمَامِكُمْ أَوْ بُيُوتِ عَمَّاتِكُمْ أَوْ بُيُوتِ أَخْوَالِكُمْ أَوْ بُيُوتِ خَالَاتِكُمْ أَوْ مَا مَلَكْتُم مَّفَاتِحَهُ أَوْ صَدِيقِكُمْ ۚ لَيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَأْكُلُوا جَمِيعًا أَوْ أَشْتَاتًا ۚ فَإِذَا دَخَلْتُم بُيُوتًا فَسَلِّمُوا عَلَىٰ أَنفُسِكُمْ تَحِيَّةً مِّنْ عِندِ اللَّهِ مُبَارَكَةً طَيِّبَةً ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ','بر نابینا و افراد لنگ و بیمار گناهی نیست (که با شما هم غذا شوند)، و بر شما نیز گناهی نیست که از خانه‌های خودتان [=خانه‌های فرزندان یا همسرانتان که خانه خود شما محسوب می‌شود بدون اجازه خاصّی‌] غذا بخورید؛ و همچنین خانه‌های پدرانتان، یا خانه‌های مادرانتان، یا خانه‌های برادرانتان، یا خانه‌های خواهرانتان، یا خانه‌های عموهایتان، یا خانه‌های عمّه‌هایتان، یا خانه‌های داییهایتان، یا خانه‌های خاله‌هایتان، یا خانه‌ای که کلیدش در اختیار شماست، یا خانه‌های دوستانتان، بر شما گناهی نیست که بطور دسته‌جمعی یا جداگانه غذا بخورید؛ و هنگامی که داخل خانه‌ای شدید، بر خویشتن سلام کنید، سلام و تحیّتی از سوی خداوند، سلامی پربرکت و پاکیزه! این گونه خداوند آیات را برای شما روشن می‌کند، باشد که بیندیشید!'];
ayat[2853]=[24,62,'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ وَإِذَا كَانُوا مَعَهُ عَلَىٰ أَمْرٍ جَامِعٍ لَّمْ يَذْهَبُوا حَتَّىٰ يَسْتَأْذِنُوهُ ۚ إِنَّ الَّذِينَ يَسْتَأْذِنُونَكَ أُولَٰئِكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ ۚ فَإِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَن لِّمَن شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','مؤمنان واقعی کسانی هستند که به خدا و رسولش ایمان آورده‌اند و هنگامی که در کار مهمّی با او باشند، بی‌اجازه او جایی نمی‌روند؛ کسانی که از تو اجازه می‌گیرند، براستی به خدا و پیامبرش ایمان آورده‌اند! در این صورت، هر گاه برای بعضی کارهای مهمّ خود از تو اجازه بخواهند، به هر یک از آنان که می‌خواهی (و صلاح می‌بینی) اجازه ده، و برایشان از خدا آمرزش بخواه که خداوند آمرزنده و مهربان است!'];
ayat[2854]=[24,63,'لَّا تَجْعَلُوا دُعَاءَ الرَّسُولِ بَيْنَكُمْ كَدُعَاءِ بَعْضِكُم بَعْضًا ۚ قَدْ يَعْلَمُ اللَّهُ الَّذِينَ يَتَسَلَّلُونَ مِنكُمْ لِوَاذًا ۚ فَلْيَحْذَرِ الَّذِينَ يُخَالِفُونَ عَنْ أَمْرِهِ أَن تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذَابٌ أَلِيمٌ','صدا کردن پیامبر را در میان خود، مانند صدا کردن یکدیگر قرار ندهی؛ خداوند کسانی از شما را که پشت سر دیگران پنهان می‌شوند، و یکی پس از دیگری فرار می‌کنند می‌داند! پس آنان که فرمان او را مخالفت می‌کنند، باید بترسند از اینکه فتنه‌ای دامنشان را بگیرد، یا عذابی دردناک به آنها برسد!'];
ayat[2855]=[24,64,'أَلَا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ قَدْ يَعْلَمُ مَا أَنتُمْ عَلَيْهِ وَيَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُم بِمَا عَمِلُوا ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','آگاه باشید که برای خداست آنچه در آسمانها و زمین است؛ او می‌داند آنچه را که شما بر آن هستید، و (می‌داند) روزی را که بسوی او بازمی‌گردند؛ و (در آن روز) آنها را از اعمالی که انجام دادند آگاه می‌سازد؛ و خداوند به هر چیزی داناست!'];
ayat[2856]=[25,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَارَكَ الَّذِي نَزَّلَ الْفُرْقَانَ عَلَىٰ عَبْدِهِ لِيَكُونَ لِلْعَالَمِينَ نَذِيرًا','زوال ناپذیر و پر برکت است کسی که قرآن را بر بنده‌اش نازل کرد تا بیم‌دهنده جهانیان باشد.'];
ayat[2857]=[25,2,'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُن لَّهُ شَرِيكٌ فِي الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْدِيرًا','خداوندی که حکومت آسمانها و زمین از آن اوست، و فرزندی برای خود انتخاب نکرد، و همتایی در حکومت و مالکیّت ندارد، و همه چیز را آفرید، و به دقّت اندازه‌گیری نمود!'];
ayat[2858]=[25,3,'وَاتَّخَذُوا مِن دُونِهِ آلِهَةً لَّا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ وَلَا يَمْلِكُونَ لِأَنفُسِهِمْ ضَرًّا وَلَا نَفْعًا وَلَا يَمْلِكُونَ مَوْتًا وَلَا حَيَاةً وَلَا نُشُورًا','آنان غیر از خداوند معبودانی برای خود برگزیدند؛ معبودانی که چیزی را نمی‌آفرینند، بلکه خودشان مخلوقند، و مالک زیان و سود خویش نیستند، و نه مالک مرگ و حیات و رستاخیز خویشند.'];
ayat[2859]=[25,4,'وَقَالَ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا إِفْكٌ افْتَرَاهُ وَأَعَانَهُ عَلَيْهِ قَوْمٌ آخَرُونَ ۖ فَقَدْ جَاءُوا ظُلْمًا وَزُورًا','و کافران گفتند: «این فقط دروغی است که او ساخته، و گروهی دیگر او را بر این کار یاری داده‌اند.» آنها (با این سخن،) ظلم و دروغ بزرگی را مرتکب شدند.'];
ayat[2860]=[25,5,'وَقَالُوا أَسَاطِيرُ الْأَوَّلِينَ اكْتَتَبَهَا فَهِيَ تُمْلَىٰ عَلَيْهِ بُكْرَةً وَأَصِيلًا','و گفتند: «این همان افسانه‌های پیشینیان است که وی آن را رونویس کرده، و هر صبح و شام بر او املا می‌شود.»'];
ayat[2861]=[25,6,'قُلْ أَنزَلَهُ الَّذِي يَعْلَمُ السِّرَّ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّهُ كَانَ غَفُورًا رَّحِيمًا','بگو: «کسی آن را نازل کرده که اسرار آسمانها و زمین را می‌داند؛ او (همیشه) آمرزنده و مهربان بوده است!»'];
ayat[2862]=[25,7,'وَقَالُوا مَالِ هَٰذَا الرَّسُولِ يَأْكُلُ الطَّعَامَ وَيَمْشِي فِي الْأَسْوَاقِ ۙ لَوْلَا أُنزِلَ إِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذِيرًا','و گفتند: «چرا این پیامبر غذا می‌خورد و در بازارها راه می‌رود؟! (نه سنّت فرشتگان را دارد و نه روش شاهان را!) چرا فرشته‌ای بر او نازل نشده که همراه وی مردم را انذار کند (و گواه صدق دعوی او باشد)؟!'];
ayat[2863]=[25,8,'أَوْ يُلْقَىٰ إِلَيْهِ كَنزٌ أَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْهَا ۚ وَقَالَ الظَّالِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلًا مَّسْحُورًا','یا گنجی (از آسمان) برای او فرستاده شود، یا باغی داشته باشد که از (میوه) آن بخورد (و امرار معاش کند)؟!» و ستمگران گفتند: «شما تنها از مردی مجنون پیروی می‌کنید!»'];
ayat[2864]=[25,9,'انظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثَالَ فَضَلُّوا فَلَا يَسْتَطِيعُونَ سَبِيلًا','ببین چگونه برای تو مثلها زدند و گمراه شدند، آن گونه که قدرت پیدا کردن راه را ندارند!'];
ayat[2865]=[25,10,'تَبَارَكَ الَّذِي إِن شَاءَ جَعَلَ لَكَ خَيْرًا مِّن ذَٰلِكَ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ وَيَجْعَل لَّكَ قُصُورًا','زوال ناپذیر و بزرگ است خدایی که اگر بخواهد برای تو بهتر از این قرار می‌دهد: باغهایی که نهرها از زیر درختانش جاری است، و (اگر بخواهد) برای تو کاخهایی مجلّل قرارمی‌دهد.'];
ayat[2866]=[25,11,'بَلْ كَذَّبُوا بِالسَّاعَةِ ۖ وَأَعْتَدْنَا لِمَن كَذَّبَ بِالسَّاعَةِ سَعِيرًا','(اینها همه بهانه است) بلکه آنان قیامت را تکذیب کرده‌اند؛ و ما برای کسی که قیامت را تکذیب کند، آتشی شعله‌ور و سوزان فراهم کرده‌ایم!'];
ayat[2867]=[25,12,'إِذَا رَأَتْهُم مِّن مَّكَانٍ بَعِيدٍ سَمِعُوا لَهَا تَغَيُّظًا وَزَفِيرًا','هنگامی که این آتش آنان را از مکانی دور ببیند، صدای وحشتناک و خشم آلودش را که با نفس‌زدن شدید همراه است می‌شنوند.'];
ayat[2868]=[25,13,'وَإِذَا أُلْقُوا مِنْهَا مَكَانًا ضَيِّقًا مُّقَرَّنِينَ دَعَوْا هُنَالِكَ ثُبُورًا','و هنگامی که در جای تنگ و محدودی از آن افکنده شوند در حالی که در غل و زنجیرند، فریاد واویلای آنان بلند می‌شود!'];
ayat[2869]=[25,14,'لَّا تَدْعُوا الْيَوْمَ ثُبُورًا وَاحِدًا وَادْعُوا ثُبُورًا كَثِيرًا','(به آنان گفته می‌شود:) امروز یک بار واویلا نگویید، بلکه بسیار واویلا بگویید!'];
ayat[2870]=[25,15,'قُلْ أَذَٰلِكَ خَيْرٌ أَمْ جَنَّةُ الْخُلْدِ الَّتِي وُعِدَ الْمُتَّقُونَ ۚ كَانَتْ لَهُمْ جَزَاءً وَمَصِيرًا','(ای پیامبر!) بگو: «آیا این بهتر است یا بهشت جاویدانی که به پرهیزگاران وعده داده شده؟! بهشتی که پاداش اعمال آنها، و قرارگاهشان است.»'];
ayat[2871]=[25,16,'لَّهُمْ فِيهَا مَا يَشَاءُونَ خَالِدِينَ ۚ كَانَ عَلَىٰ رَبِّكَ وَعْدًا مَّسْئُولًا','هر چه بخواهند در آنجا برایشان فراهم است؛ جاودانه در آن خواهند ماند؛ این وعده‌ای است مسلّم که پروردگارت بر عهده گرفته است!'];
ayat[2872]=[25,17,'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِن دُونِ اللَّهِ فَيَقُولُ أَأَنتُمْ أَضْلَلْتُمْ عِبَادِي هَٰؤُلَاءِ أَمْ هُمْ ضَلُّوا السَّبِيلَ','(به خاطر بیاور) روزی را که همه آنان و معبودهایی را که غیر از خدا می‌پرستند جمع می‌کند، آنگاه به آنها می‌گوید: «آیا شما این بندگان مرا گمراه کردید یا خود آنان راه را گم کردند؟!»'];
ayat[2873]=[25,18,'قَالُوا سُبْحَانَكَ مَا كَانَ يَنبَغِي لَنَا أَن نَّتَّخِذَ مِن دُونِكَ مِنْ أَوْلِيَاءَ وَلَٰكِن مَّتَّعْتَهُمْ وَآبَاءَهُمْ حَتَّىٰ نَسُوا الذِّكْرَ وَكَانُوا قَوْمًا بُورًا','(در پاسخ) می‌گویند: «منزّهی تو! برای ما شایسته نبود که غیر از تو اولیایی برگزینیم، ولی آنان و پدرانشان را از نعمتها برخوردار نمودی تا اینکه (به جای شکر نعمت) یاد تو را فراموش کردند و تباه و هلاک شدند.»'];
ayat[2874]=[25,19,'فَقَدْ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسْتَطِيعُونَ صَرْفًا وَلَا نَصْرًا ۚ وَمَن يَظْلِم مِّنكُمْ نُذِقْهُ عَذَابًا كَبِيرًا','(خداوند به آنان می‌گوید: ببینید) این معبودان، شما را در آنچه می‌گویید تکذیب کردند! اکنون نمی‌توانید عذاب الهی را برطرف بسازید، یا از کسی یاری بطلبید! و هر کس از شما ستم کند، عذاب شدیدی به او می‌چشانیم!'];
ayat[2875]=[25,20,'وَمَا أَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلَّا إِنَّهُمْ لَيَأْكُلُونَ الطَّعَامَ وَيَمْشُونَ فِي الْأَسْوَاقِ ۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَتَصْبِرُونَ ۗ وَكَانَ رَبُّكَ بَصِيرًا','ما هیچ یک از رسولان را پیش از تو نفرستادیم مگر اینکه غذا می‌خوردند و در بازارها راه می‌رفتند؛ و بعضی از شما را وسیله امتحان بعضی دیگر قراردادیم، آیا صبر و شکیبایی می‌کنید (و از عهده امتحان برمی‌آیید)؟! و پروردگار تو همواره بصیر و بینا بوده است.'];
ayat[2876]=[25,21,'۞ وَقَالَ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا لَوْلَا أُنزِلَ عَلَيْنَا الْمَلَائِكَةُ أَوْ نَرَىٰ رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوا فِي أَنفُسِهِمْ وَعَتَوْا عُتُوًّا كَبِيرًا','و کسانی که امیدی به دیدار ما ندارند (و رستاخیز را انکار می‌کنند) گفتند: «چرا فرشتگان بر ما نازل نشدند و یا پروردگارمان را با چشم خود نمی‌بینیم؟!» آنها درباره خود تکبّر ورزیدند و طغیان بزرگی کردند!'];
ayat[2877]=[25,22,'يَوْمَ يَرَوْنَ الْمَلَائِكَةَ لَا بُشْرَىٰ يَوْمَئِذٍ لِّلْمُجْرِمِينَ وَيَقُولُونَ حِجْرًا مَّحْجُورًا','(آنها به آرزوی خود می‌رسند،) امّا روزی که فرشتگان را می‌بینند، روز بشارت برای مجرمان نخواهد بود (بلکه روز مجازات و کیفر آنان است)! و می‌گویند: «ما را امان دهید، ما را معاف دارید!» (امّا سودی ندارد!)'];
ayat[2878]=[25,23,'وَقَدِمْنَا إِلَىٰ مَا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنَاهُ هَبَاءً مَّنثُورًا','و ما به سراغ اعمالی که انجام داده‌اند می‌رویم، و همه را همچون ذرّات غبار پراکنده در هوا قرار می‌دهیم!'];
ayat[2879]=[25,24,'أَصْحَابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُّسْتَقَرًّا وَأَحْسَنُ مَقِيلًا','بهشتیان در آن روز قرارگاهشان از همه بهتر، و استراحتگاهشان نیکوتر است!'];
ayat[2880]=[25,25,'وَيَوْمَ تَشَقَّقُ السَّمَاءُ بِالْغَمَامِ وَنُزِّلَ الْمَلَائِكَةُ تَنزِيلًا','و (بخاطر آور) روزی را که آسمان با ابرها شکافته می‌شود، و فرشتگان نازل می‌گردند.'];
ayat[2881]=[25,26,'الْمُلْكُ يَوْمَئِذٍ الْحَقُّ لِلرَّحْمَٰنِ ۚ وَكَانَ يَوْمًا عَلَى الْكَافِرِينَ عَسِيرًا','حکومت در آن روز از آن خداوند رحمان است؛ و آن روز، روز سختی برای کافران خواهد بود!'];
ayat[2882]=[25,27,'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلَىٰ يَدَيْهِ يَقُولُ يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلًا','و (به خاطر آور) روزی را که ستمکار دست خود را (از شدّت حسرت) به دندان می‌گزد و می‌گوید: «ای کاش با رسول (خدا) راهی برگزیده بودم!'];
ayat[2883]=[25,28,'يَا وَيْلَتَىٰ لَيْتَنِي لَمْ أَتَّخِذْ فُلَانًا خَلِيلًا','ای وای بر من، کاش فلان (شخص گمراه) را دوست خود انتخاب نکرده بودم!'];
ayat[2884]=[25,29,'لَّقَدْ أَضَلَّنِي عَنِ الذِّكْرِ بَعْدَ إِذْ جَاءَنِي ۗ وَكَانَ الشَّيْطَانُ لِلْإِنسَانِ خَذُولًا','او مرا از یادآوری (حق) گمراه ساخت بعد از آنکه (یاد حق) به سراغ من آمده بود!» و شیطان همیشه خوارکنند* انسان بوده است!'];
ayat[2885]=[25,30,'وَقَالَ الرَّسُولُ يَا رَبِّ إِنَّ قَوْمِي اتَّخَذُوا هَٰذَا الْقُرْآنَ مَهْجُورًا','و پیامبر عرضه داشت: «پروردگارا! قوم من قرآن را رها کردند».'];
ayat[2886]=[25,31,'وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِّنَ الْمُجْرِمِينَ ۗ وَكَفَىٰ بِرَبِّكَ هَادِيًا وَنَصِيرًا','(آری،) این گونه برای هر پیامبری دشمنی از مجرمان قرار دادیم؛ امّا (برای تو) همین بس که پروردگارت هادی و یاور (تو) باشد!'];
ayat[2887]=[25,32,'وَقَالَ الَّذِينَ كَفَرُوا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْآنُ جُمْلَةً وَاحِدَةً ۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِ فُؤَادَكَ ۖ وَرَتَّلْنَاهُ تَرْتِيلًا','و کافران گفتند: «چرا قرآن یکجا بر او نازل نمی‌شود؟!» این بخاطر آن است که قلب تو را بوسیله آن محکم داریم، و (از این رو) آن را به تدریج بر تو خواندیم.'];
ayat[2888]=[25,33,'وَلَا يَأْتُونَكَ بِمَثَلٍ إِلَّا جِئْنَاكَ بِالْحَقِّ وَأَحْسَنَ تَفْسِيرًا','آنان هیچ مثلی برای تو نمی‌آورند مگر اینکه ما حق را برای تو می‌آوریم، و تفسیری بهتر (و پاسخی دندان شکن که در برابر آن ناتوان شوند)!'];
ayat[2889]=[25,34,'الَّذِينَ يُحْشَرُونَ عَلَىٰ وُجُوهِهِمْ إِلَىٰ جَهَنَّمَ أُولَٰئِكَ شَرٌّ مَّكَانًا وَأَضَلُّ سَبِيلًا','(تو گمراه نیستی،) آنان که بر صورتهایشان بسوی جهنم محشور می‌شوند، بدترین محل را دارند و گمراه‌ترین افرادند!'];
ayat[2890]=[25,35,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُ أَخَاهُ هَارُونَ وَزِيرًا','و ما به موسی کتاب (آسمانی) دادیم؛ و برادرش هارون را یاور او قرار دادیم؛'];
ayat[2891]=[25,36,'فَقُلْنَا اذْهَبَا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَدَمَّرْنَاهُمْ تَدْمِيرًا','و گفتیم: «به سوی این قوم که آیات ما را تکذیب کردند بروید!» (امّا آن مردم به مخالفت برخاستند) و ما به شدّت آنان را درهم کوبیدیم!'];
ayat[2892]=[25,37,'وَقَوْمَ نُوحٍ لَّمَّا كَذَّبُوا الرُّسُلَ أَغْرَقْنَاهُمْ وَجَعَلْنَاهُمْ لِلنَّاسِ آيَةً ۖ وَأَعْتَدْنَا لِلظَّالِمِينَ عَذَابًا أَلِيمًا','و قوم نوح را هنگامی که رسولان (ما) را تکذیب کردند غرق نمودیم، و آنان را درس عبرتی برای مردم قرار دادیم؛ و برای ستمگران عذاب دردناکی فراهم ساخته‌ایم!'];
ayat[2893]=[25,38,'وَعَادًا وَثَمُودَ وَأَصْحَابَ الرَّسِّ وَقُرُونًا بَيْنَ ذَٰلِكَ كَثِيرًا','(همچنین) قوم عاد و ثمود و اصحاب الرّس [= گروهی که درختان صنوبر را می‌پرستیدند] و اقوام بسیار دیگری را که در این میان بودند، هلاک کردیم!'];
ayat[2894]=[25,39,'وَكُلًّا ضَرَبْنَا لَهُ الْأَمْثَالَ ۖ وَكُلًّا تَبَّرْنَا تَتْبِيرًا','و برای هر یک از آنها مثلها زدیم؛ و (چون سودی نداد،) همگی را نابود کردیم!'];
ayat[2895]=[25,40,'وَلَقَدْ أَتَوْا عَلَى الْقَرْيَةِ الَّتِي أُمْطِرَتْ مَطَرَ السَّوْءِ ۚ أَفَلَمْ يَكُونُوا يَرَوْنَهَا ۚ بَلْ كَانُوا لَا يَرْجُونَ نُشُورًا','آنها [= مشرکان مکّه‌] از کنار شهری که باران شرّ [= بارانی از سنگهای آسمانی‌] بر آن باریده بود [= دیار قوم لوط] گذشتند؛ آیا آن را نمی‌دیدند؟! (آری، می‌دیدند) ولی به رستاخیز ایمان نداشتند!'];
ayat[2896]=[25,41,'وَإِذَا رَأَوْكَ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا الَّذِي بَعَثَ اللَّهُ رَسُولًا','و هنگامی که تو را می‌بینند، تنها به باد استهزایت می‌گیرند (و می‌گویند:) آیا این همان کسی است که خدا او را بعنوان پیامبر برانگیخته است؟!'];
ayat[2897]=[25,42,'إِن كَادَ لَيُضِلُّنَا عَنْ آلِهَتِنَا لَوْلَا أَن صَبَرْنَا عَلَيْهَا ۚ وَسَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ الْعَذَابَ مَنْ أَضَلُّ سَبِيلًا','اگر ما بر پرستش خدایانمان استقامت نمی‌کردیم، بیم آن می‌رفت که ما را گمراه سازد! امّا هنگامی که عذاب الهی را ببینند، بزودی می‌فهمند چه کسی گمراهتر بوده است!'];
ayat[2898]=[25,43,'أَرَأَيْتَ مَنِ اتَّخَذَ إِلَٰهَهُ هَوَاهُ أَفَأَنتَ تَكُونُ عَلَيْهِ وَكِيلًا','آیا دیدی کسی را که هوای نفسش را معبود خود برگزیده است؟! آیا تو می‌توانی او را هدایت کنی (یا به دفاع از او برخیزی)؟!'];
ayat[2899]=[25,44,'أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ ۚ إِنْ هُمْ إِلَّا كَالْأَنْعَامِ ۖ بَلْ هُمْ أَضَلُّ سَبِيلًا','آیا گمان می‌بری بیشتر آنان می‌شنوند یا می‌فهمند؟! آنان فقط همچون چهارپایانند، بلکه گمراهترند!'];
ayat[2900]=[25,45,'أَلَمْ تَرَ إِلَىٰ رَبِّكَ كَيْفَ مَدَّ الظِّلَّ وَلَوْ شَاءَ لَجَعَلَهُ سَاكِنًا ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيلًا','آیا ندیدی چگونه پروردگارت سایه را گسترده ساخت؟! و اگر می‌خواست آن را ساکن قرار می‌داد؛ سپس خورشید را بر وجود آن دلیل قرار دادیم!'];
ayat[2901]=[25,46,'ثُمَّ قَبَضْنَاهُ إِلَيْنَا قَبْضًا يَسِيرًا','سپس آن را آهسته جمع می‌کنیم (و نظام سایه و آفتاب را حاکم می‌سازیم)!'];
ayat[2902]=[25,47,'وَهُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِبَاسًا وَالنَّوْمَ سُبَاتًا وَجَعَلَ النَّهَارَ نُشُورًا','او کسی است که شب را برای شما لباس قرار داد، و خواب را مایه استراحت، و روز را وسیله حرکت و حیات!'];
ayat[2903]=[25,48,'وَهُوَ الَّذِي أَرْسَلَ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۚ وَأَنزَلْنَا مِنَ السَّمَاءِ مَاءً طَهُورًا','او کسی است که بادها را بشارتگرانی پیش از رحمتش فرستاد، و از آسمان آبی پاک‌کننده نازل کردیم...'];
ayat[2904]=[25,49,'لِّنُحْيِيَ بِهِ بَلْدَةً مَّيْتًا وَنُسْقِيَهُ مِمَّا خَلَقْنَا أَنْعَامًا وَأَنَاسِيَّ كَثِيرًا','تا بوسیله آن، سرزمین مرده‌ای را زنده کنیم؛ و آن را به مخلوقاتی که آفریده‌ایم -چهارپایان و انسانهای بسیار- می‌نوشانیم.'];
ayat[2905]=[25,50,'وَلَقَدْ صَرَّفْنَاهُ بَيْنَهُمْ لِيَذَّكَّرُوا فَأَبَىٰ أَكْثَرُ النَّاسِ إِلَّا كُفُورًا','ما این آیات را بصورتهای گوناگون برای آنان بیان کردیم تا متذکّر شوند، ولی بیشتر مردم از هر کاری جز انکار و کفر ابا دارند.'];
ayat[2906]=[25,51,'وَلَوْ شِئْنَا لَبَعَثْنَا فِي كُلِّ قَرْيَةٍ نَّذِيرًا','و اگر می‌خواستیم، در هر شهر و دیاری بیم‌دهنده‌ای برمی‌انگیختیم (ولی این کار لزومی نداشت).'];
ayat[2907]=[25,52,'فَلَا تُطِعِ الْكَافِرِينَ وَجَاهِدْهُم بِهِ جِهَادًا كَبِيرًا','بنابر این از کافران اطاعت مکن، و بوسیله آن [= قرآن‌] با آنان جهاد بزرگی بنما!'];
ayat[2908]=[25,53,'۞ وَهُوَ الَّذِي مَرَجَ الْبَحْرَيْنِ هَٰذَا عَذْبٌ فُرَاتٌ وَهَٰذَا مِلْحٌ أُجَاجٌ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا وَحِجْرًا مَّحْجُورًا','او کسی است که دو دریا را در کنار هم قرار داد؛ یکی گوارا و شیرین، و دیگر شور و تلخ؛ و در میان آنها برزخی قرار داد تا با هم مخلوط نشوند (گویی هر یک به دیگری می‌گوید:) دور باش و نزدیک نیا!'];
ayat[2909]=[25,54,'وَهُوَ الَّذِي خَلَقَ مِنَ الْمَاءِ بَشَرًا فَجَعَلَهُ نَسَبًا وَصِهْرًا ۗ وَكَانَ رَبُّكَ قَدِيرًا','او کسی است که از آب، انسانی را آفرید؛ سپس او را نسب و سبب قرار داد (و نسل او را از این دو طریق گسترش داد)؛ و پروردگار تو همواره توانا بوده است.'];
ayat[2910]=[25,55,'وَيَعْبُدُونَ مِن دُونِ اللَّهِ مَا لَا يَنفَعُهُمْ وَلَا يَضُرُّهُمْ ۗ وَكَانَ الْكَافِرُ عَلَىٰ رَبِّهِ ظَهِيرًا','آنان جز خدا چیزهایی را می‌پرستند که نه به آنان سودی می‌رساند و نه زیانی؛ و کافران همیشه در برابر پروردگارشان (در طریق کفر) پشتیبان یکدیگرند.'];
ayat[2911]=[25,56,'وَمَا أَرْسَلْنَاكَ إِلَّا مُبَشِّرًا وَنَذِيرًا','(ای پیامبر!) ما تو را جز بعنوان بشارت دهنده و انذار کننده نفرستادیم!'];
ayat[2912]=[25,57,'قُلْ مَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلَّا مَن شَاءَ أَن يَتَّخِذَ إِلَىٰ رَبِّهِ سَبِيلًا','بگو: «من در برابر آن (ابلاغ آیین خدا) هیچ گونه پاداشی از شما نمی‌طلبم؛ مگر کسی که بخواهد راهی بسوی پروردگارش برگزیند (این پاداش من است.)»'];
ayat[2913]=[25,58,'وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِي لَا يَمُوتُ وَسَبِّحْ بِحَمْدِهِ ۚ وَكَفَىٰ بِهِ بِذُنُوبِ عِبَادِهِ خَبِيرًا','و توکّل کن بر آن زنده‌ای که هرگز نمی‌میرد؛ و تسبیح و حمد او را بجا آور؛ و همین بس که او از گناهان بندگانش آگاه است!'];
ayat[2914]=[25,59,'الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۚ الرَّحْمَٰنُ فَاسْأَلْ بِهِ خَبِيرًا','همان (خدایی) که آسمانها و زمین و آنچه را میان این دو وجود دارد، در شش روز [= شش دوران‌] آفرید؛ سپس بر عرش (قدرت) قرار گرفت (و به تدبیر جهان پرداخت، او خداوند) رحمان است؛ از او بخواه که از همه چیز آگاه است!'];
ayat[2915]=[25,60,'وَإِذَا قِيلَ لَهُمُ اسْجُدُوا لِلرَّحْمَٰنِ قَالُوا وَمَا الرَّحْمَٰنُ أَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُورًا ۩','و هنگامی که به آنان گفته شود: «برای خداوند رحمان سجده کنید!» می‌گویند: «رحمان چیست؟! (ما اصلاً رحمان را نمی‌شناسیم!) آیا برای چیزی سجده کنیم که تو به ما دستور می‌دهی؟!» (این سخن را می‌گویند) و بر نفرتشان افزوده می‌شود!'];
ayat[2916]=[25,61,'تَبَارَكَ الَّذِي جَعَلَ فِي السَّمَاءِ بُرُوجًا وَجَعَلَ فِيهَا سِرَاجًا وَقَمَرًا مُّنِيرًا','جاودان و پربرکت است آن (خدایی) که در آسمان منزلگاه‌هائی برای ستارگان قرار داد؛ و در میان آن، چراغ روشن و ماه تابانی آفرید!'];
ayat[2917]=[25,62,'وَهُوَ الَّذِي جَعَلَ اللَّيْلَ وَالنَّهَارَ خِلْفَةً لِّمَنْ أَرَادَ أَن يَذَّكَّرَ أَوْ أَرَادَ شُكُورًا','و او همان کسی است که شب و روز را جانشین یکدیگر قرار داد برای کسی که بخواهد متذکّر شود یا شکرگزاری کند (و آنچه را در روز کوتاهی کرده در شب انجام دهد و به عکس).'];
ayat[2918]=[25,63,'وَعِبَادُ الرَّحْمَٰنِ الَّذِينَ يَمْشُونَ عَلَى الْأَرْضِ هَوْنًا وَإِذَا خَاطَبَهُمُ الْجَاهِلُونَ قَالُوا سَلَامًا','بندگان (خاص خداوند) رحمان، کسانی هستند که با آرامش و بی‌تکبّر بر زمین راه می‌روند؛ و هنگامی که جاهلان آنها را مخاطب سازند (و سخنان نابخردانه گویند)، به آنها سلام می‌گویند (و با بی‌اعتنایی و بزرگواری می‌گذرند)؛'];
ayat[2919]=[25,64,'وَالَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّدًا وَقِيَامًا','کسانی که شبانگاه برای پروردگارشان سجده و قیام می‌کنند؛'];
ayat[2920]=[25,65,'وَالَّذِينَ يَقُولُونَ رَبَّنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَ ۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا','و کسانی که می‌گویند: «پروردگارا! عذاب جهنم را از ما برطرف گردان، که عذابش سخت و پر دوام است!'];
ayat[2921]=[25,66,'إِنَّهَا سَاءَتْ مُسْتَقَرًّا وَمُقَامًا','مسلّماً آن (جهنم)، بد جایگاه و بد محلّ اقامتی است!»'];
ayat[2922]=[25,67,'وَالَّذِينَ إِذَا أَنفَقُوا لَمْ يُسْرِفُوا وَلَمْ يَقْتُرُوا وَكَانَ بَيْنَ ذَٰلِكَ قَوَامًا','و کسانی که هرگاه انفاق کنند، نه اسراف می‌نمایند و نه سخت‌گیری؛ بلکه در میان این دو، حدّ اعتدالی دارند.'];
ayat[2923]=[25,68,'وَالَّذِينَ لَا يَدْعُونَ مَعَ اللَّهِ إِلَٰهًا آخَرَ وَلَا يَقْتُلُونَ النَّفْسَ الَّتِي حَرَّمَ اللَّهُ إِلَّا بِالْحَقِّ وَلَا يَزْنُونَ ۚ وَمَن يَفْعَلْ ذَٰلِكَ يَلْقَ أَثَامًا','و کسانی که معبود دیگری را با خداوند نمی‌خوانند؛ و انسانی را که خداوند خونش را حرام شمرده، جز بحق نمی‌کشند؛ و زنا نمی‌کنند؛ و هر کس چنین کند، مجازات سختی خواهد دید!'];
ayat[2924]=[25,69,'يُضَاعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيَامَةِ وَيَخْلُدْ فِيهِ مُهَانًا','عذاب او در قیامت مضاعف می‌گردد، و همیشه با خواری در آن خواهد ماند!'];
ayat[2925]=[25,70,'إِلَّا مَن تَابَ وَآمَنَ وَعَمِلَ عَمَلًا صَالِحًا فَأُولَٰئِكَ يُبَدِّلُ اللَّهُ سَيِّئَاتِهِمْ حَسَنَاتٍ ۗ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','مگر کسانی که توبه کنند و ایمان آورند و عمل صالح انجام دهند، که خداوند گناهان آنان را به حسنات مبدّل می‌کند؛ و خداوند همواره آمرزنده و مهربان بوده است!'];
ayat[2926]=[25,71,'وَمَن تَابَ وَعَمِلَ صَالِحًا فَإِنَّهُ يَتُوبُ إِلَى اللَّهِ مَتَابًا','و کسی که توبه کند و عمل صالح انجام دهد، بسوی خدا بازگشت می‌کند (و پاداش خود را از او می‌گیرد.)'];
ayat[2927]=[25,72,'وَالَّذِينَ لَا يَشْهَدُونَ الزُّورَ وَإِذَا مَرُّوا بِاللَّغْوِ مَرُّوا كِرَامًا','و کسانی که شهادت به باطل نمی‌دهند (و در مجالس باطل شرکت نمی‌کنند)؛ و هنگامی که با لغو و بیهودگی برخورد کنند، بزرگوارانه از آن می‌گذرند.'];
ayat[2928]=[25,73,'وَالَّذِينَ إِذَا ذُكِّرُوا بِآيَاتِ رَبِّهِمْ لَمْ يَخِرُّوا عَلَيْهَا صُمًّا وَعُمْيَانًا','و کسانی که هرگاه آیات پروردگارشان به آنان گوشزد شود، کر و کور روی آن نمی افتند.'];
ayat[2929]=[25,74,'وَالَّذِينَ يَقُولُونَ رَبَّنَا هَبْ لَنَا مِنْ أَزْوَاجِنَا وَذُرِّيَّاتِنَا قُرَّةَ أَعْيُنٍ وَاجْعَلْنَا لِلْمُتَّقِينَ إِمَامًا','و کسانی که می‌گویند: «پروردگارا! از همسران و فرزندانمان مایه روشنی چشم ما قرارده، و ما را برای پرهیزگاران پیشوا گردان!»'];
ayat[2930]=[25,75,'أُولَٰئِكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوا وَيُلَقَّوْنَ فِيهَا تَحِيَّةً وَسَلَامًا','(آری،) آنها هستند که درجات عالی بهشت در برابر شکیباییشان به آنان پاداش داده می‌شود؛ و در آن، با تحیّت و سلام روبه‌رو می‌شوند.'];
ayat[2931]=[25,76,'خَالِدِينَ فِيهَا ۚ حَسُنَتْ مُسْتَقَرًّا وَمُقَامًا','در حالی که جاودانه در آن خواهند ماند؛ چه قرارگاه و محلّ اقامت خوبی!'];
ayat[2932]=[25,77,'قُلْ مَا يَعْبَأُ بِكُمْ رَبِّي لَوْلَا دُعَاؤُكُمْ ۖ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُونُ لِزَامًا','بگو: «پروردگارم برای شما ارجی قائل نیست اگر دعای شما نباشد؛ شما (آیات خدا و پیامبران را) تکذیب کردید، و (این عمل) دامان شما را خواهد گرفت و از شما جدا نخواهد شد!» ظ'];
ayat[2933]=[26,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طسم','طسم‌'];
ayat[2934]=[26,2,'تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ','این آیات کتاب روشنگر است.'];
ayat[2935]=[26,3,'لَعَلَّكَ بَاخِعٌ نَّفْسَكَ أَلَّا يَكُونُوا مُؤْمِنِينَ','گویی می‌خواهی جان خود را از شدّت اندوه از دست دهی بخاطر اینکه آنها ایمان نمی‌آورند!'];
ayat[2936]=[26,4,'إِن نَّشَأْ نُنَزِّلْ عَلَيْهِم مِّنَ السَّمَاءِ آيَةً فَظَلَّتْ أَعْنَاقُهُمْ لَهَا خَاضِعِينَ','اگر ما اراده کنیم، از آسمان بر آنان آیه‌ای نازل می‌کنیم که گردنهایشان در برابر آن خاضع گردد!'];
ayat[2937]=[26,5,'وَمَا يَأْتِيهِم مِّن ذِكْرٍ مِّنَ الرَّحْمَٰنِ مُحْدَثٍ إِلَّا كَانُوا عَنْهُ مُعْرِضِينَ','و هیچ ذکر تازه‌ای از سوی خداوند مهربان برای آنها نمی‌آید مگر اینکه از آن روی‌گردان می‌شوند!'];
ayat[2938]=[26,6,'فَقَدْ كَذَّبُوا فَسَيَأْتِيهِمْ أَنبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ','آنان تکذیب کردند؛ امّا بزودی اخبار (کیفر) آنچه را استهزا می‌کردند به آنان می‌رسد!'];
ayat[2939]=[26,7,'أَوَلَمْ يَرَوْا إِلَى الْأَرْضِ كَمْ أَنبَتْنَا فِيهَا مِن كُلِّ زَوْجٍ كَرِيمٍ','آیا آنان به زمین نگاه نکردند که چقدر از انواع گیاهان پرارزش در آن رویاندیم؟!'];
ayat[2940]=[26,8,'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','در این، نشانه روشنی است (بر وجود خدا)؛ ولی بیشترشان هرگز مؤمن نبوده‌اند!'];
ayat[2941]=[26,9,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است!'];
ayat[2942]=[26,10,'وَإِذْ نَادَىٰ رَبُّكَ مُوسَىٰ أَنِ ائْتِ الْقَوْمَ الظَّالِمِينَ','(به خاطر بیاور) هنگامی را که پروردگارت موسی را ندا داد که به سراغ قوم ستمگر برو...'];
ayat[2943]=[26,11,'قَوْمَ فِرْعَوْنَ ۚ أَلَا يَتَّقُونَ','قوم فرعون، آیا آنان (از مخالفت فرمان پروردگار) پرهیز نمی‌کنند؟!'];
ayat[2944]=[26,12,'قَالَ رَبِّ إِنِّي أَخَافُ أَن يُكَذِّبُونِ','(موسی) عرض کرد: «پروردگارا! از آن بیم دارم که مرا تکذیب کنند،'];
ayat[2945]=[26,13,'وَيَضِيقُ صَدْرِي وَلَا يَنطَلِقُ لِسَانِي فَأَرْسِلْ إِلَىٰ هَارُونَ','و سینه‌ام تنگ شود، و زبانم بقدر کافی گویا نیست؛ (برادرم) هارون را نیز رسالت ده (تا مرا یاری کند)!'];
ayat[2946]=[26,14,'وَلَهُمْ عَلَيَّ ذَنبٌ فَأَخَافُ أَن يَقْتُلُونِ','و آنان (به اعتقاد خودشان) بر گردن من گناهی دارند؛ می‌ترسم مرا بکشند (و این رسالت به پایان نرسد)!'];
ayat[2947]=[26,15,'قَالَ كَلَّا ۖ فَاذْهَبَا بِآيَاتِنَا ۖ إِنَّا مَعَكُم مُّسْتَمِعُونَ','فرمود: «چنین نیست، (آنان کاری نمی‌توانند انجام دهند)! شما هر دو با آیات ما (برای هدایتشان) بروید؛ ما با شما هستیم و (سخنانتان را) می‌شنویم!'];
ayat[2948]=[26,16,'فَأْتِيَا فِرْعَوْنَ فَقُولَا إِنَّا رَسُولُ رَبِّ الْعَالَمِينَ','به سراغ فرعون بروید و بگویید: ما فرستاده پروردگار جهانیان هستیم؛'];
ayat[2949]=[26,17,'أَنْ أَرْسِلْ مَعَنَا بَنِي إِسْرَائِيلَ','بنی اسرائیل را با ما بفرست!» (آنها به سراغ فرعون آمدند)؛'];
ayat[2950]=[26,18,'قَالَ أَلَمْ نُرَبِّكَ فِينَا وَلِيدًا وَلَبِثْتَ فِينَا مِنْ عُمُرِكَ سِنِينَ','(فرعون) گفت: «آیا ما تو را در کودکی در میان خود پرورش ندادیم، و سالهایی از زندگیت را در میان ما نبودی؟!'];
ayat[2951]=[26,19,'وَفَعَلْتَ فَعْلَتَكَ الَّتِي فَعَلْتَ وَأَنتَ مِنَ الْكَافِرِينَ','و سرانجام، آن کارت را (که نمی‌بایست انجام دهی) انجام دادی (و یک نفر از ما را کشتی)، و تو از ناسپاسانی!»'];
ayat[2952]=[26,20,'قَالَ فَعَلْتُهَا إِذًا وَأَنَا مِنَ الضَّالِّينَ','(موسی) گفت: «من آن کار را انجام دادم در حالی که از بی‌خبران بودم!'];
ayat[2953]=[26,21,'فَفَرَرْتُ مِنكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِي رَبِّي حُكْمًا وَجَعَلَنِي مِنَ الْمُرْسَلِينَ','پس هنگامی که از شما ترسیدم فرار کردم؛ و پروردگارم به من حکمت و دانش بخشید، و مرا از پیامبران قرار داد!'];
ayat[2954]=[26,22,'وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ أَنْ عَبَّدتَّ بَنِي إِسْرَائِيلَ','آیا این منتّی است که تو بر من می‌گذاری که بنی اسرائیل را برده خود ساخته‌ای؟!»'];
ayat[2955]=[26,23,'قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعَالَمِينَ','فرعون گفت: «پروردگار عالمیان چیست؟!»'];
ayat[2956]=[26,24,'قَالَ رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ إِن كُنتُم مُّوقِنِينَ','(موسی) گفت: «پروردگار آسمانها و زمین و آنچه میان آن دو است، اگر اهل یقین هستید!».'];
ayat[2957]=[26,25,'قَالَ لِمَنْ حَوْلَهُ أَلَا تَسْتَمِعُونَ','(فرعون) به اطرافیانش گفت: «آیا نمی‌شنوید (این مرد چه می‌گوید)؟!»'];
ayat[2958]=[26,26,'قَالَ رَبُّكُمْ وَرَبُّ آبَائِكُمُ الْأَوَّلِينَ','(موسی) گفت: «او پروردگار شما و پروردگار نیاکان شماست!»'];
ayat[2959]=[26,27,'قَالَ إِنَّ رَسُولَكُمُ الَّذِي أُرْسِلَ إِلَيْكُمْ لَمَجْنُونٌ','(فرعون) گفت: «پیامبری که بسوی شما فرستاده شده مسلّماً دیوانه است!»'];
ayat[2960]=[26,28,'قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَا ۖ إِن كُنتُمْ تَعْقِلُونَ','(موسی) گفت: «او پروردگار مشرق و مغرب و آنچه میان آن دو است می‌باشد، اگر شما عقل و اندیشه خود را به کار می‌گرفتید!»'];
ayat[2961]=[26,29,'قَالَ لَئِنِ اتَّخَذْتَ إِلَٰهًا غَيْرِي لَأَجْعَلَنَّكَ مِنَ الْمَسْجُونِينَ','(فرعون خشمگین شد و) گفت: «اگر معبودی غیر از من برگزینی، تو را از زندانیان قرار خواهم داد!»'];
ayat[2962]=[26,30,'قَالَ أَوَلَوْ جِئْتُكَ بِشَيْءٍ مُّبِينٍ','(موسی) گفت: «حتّی اگر نشانه آشکاری برای تو بیاورم (باز ایمان نمی‌آوری)؟!»'];
ayat[2963]=[26,31,'قَالَ فَأْتِ بِهِ إِن كُنتَ مِنَ الصَّادِقِينَ','گفت: «اگر راست می‌گویی آن را بیاور!»'];
ayat[2964]=[26,32,'فَأَلْقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعْبَانٌ مُّبِينٌ','در این هنگام موسی عصای خود را افکند، و ناگهان مار عظیم و آشکاری شد؛'];
ayat[2965]=[26,33,'وَنَزَعَ يَدَهُ فَإِذَا هِيَ بَيْضَاءُ لِلنَّاظِرِينَ','و دست خود را (در گریبان فرو برد و) بیرون آورد، و در برابر بینندگان سفید و روشن بود.'];
ayat[2966]=[26,34,'قَالَ لِلْمَلَإِ حَوْلَهُ إِنَّ هَٰذَا لَسَاحِرٌ عَلِيمٌ','(فرعون) به گروهی که اطراف او بودند گفت: «این ساحر آگاه و ماهری است!'];
ayat[2967]=[26,35,'يُرِيدُ أَن يُخْرِجَكُم مِّنْ أَرْضِكُم بِسِحْرِهِ فَمَاذَا تَأْمُرُونَ','او می‌خواهد با سحرش شما را از سرزمینتان بیرون کند! شما چه نظر می‌دهید؟»'];
ayat[2968]=[26,36,'قَالُوا أَرْجِهْ وَأَخَاهُ وَابْعَثْ فِي الْمَدَائِنِ حَاشِرِينَ','گفتند: «او و برادرش را مهلت ده؛ و مأموران را برای بسیج به تمام شهرها اعزام کن،'];
ayat[2969]=[26,37,'يَأْتُوكَ بِكُلِّ سَحَّارٍ عَلِيمٍ','تا هر ساحر ماهر و دانایی را نزد تو آورند!»'];
ayat[2970]=[26,38,'فَجُمِعَ السَّحَرَةُ لِمِيقَاتِ يَوْمٍ مَّعْلُومٍ','سرانجام ساحران برای وعده‌گاه روز معیّنی جمع‌آوری شدند.'];
ayat[2971]=[26,39,'وَقِيلَ لِلنَّاسِ هَلْ أَنتُم مُّجْتَمِعُونَ','و به مردم گفته شد: «آیا شما نیز (در این صحنه) اجتماع می‌کنید...'];
ayat[2972]=[26,40,'لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ إِن كَانُوا هُمُ الْغَالِبِينَ','تا اگر ساحران پیروز شوند، از آنان پیروی کنیم؟!»'];
ayat[2973]=[26,41,'فَلَمَّا جَاءَ السَّحَرَةُ قَالُوا لِفِرْعَوْنَ أَئِنَّ لَنَا لَأَجْرًا إِن كُنَّا نَحْنُ الْغَالِبِينَ','هنگامی که ساحران آمدند، به فرعون گفتند: «آیا اگر ما پیروز شویم، پاداش مهمّی خواهیم داشت؟»'];
ayat[2974]=[26,42,'قَالَ نَعَمْ وَإِنَّكُمْ إِذًا لَّمِنَ الْمُقَرَّبِينَ','گفت: «(آری،) و در آن صورت شما از مقرّبان خواهید بود!»'];
ayat[2975]=[26,43,'قَالَ لَهُم مُّوسَىٰ أَلْقُوا مَا أَنتُم مُّلْقُونَ','(روز موعود فرا رسید و همگی جمع شدند؛) موسی به ساحران گفت: «آنچه را می‌خواهید بیفکنید، بیفکنید!»'];
ayat[2976]=[26,44,'فَأَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوا بِعِزَّةِ فِرْعَوْنَ إِنَّا لَنَحْنُ الْغَالِبُونَ','آنها طنابها و عصاهای خود را افکندند و گفتند: «به عزّت فرعون، ما قطعاً پیروزیم!»'];
ayat[2977]=[26,45,'فَأَلْقَىٰ مُوسَىٰ عَصَاهُ فَإِذَا هِيَ تَلْقَفُ مَا يَأْفِكُونَ','سپس موسی عصایش را افکند، ناگهان تمام وسایل دروغین آنها را بلعید!'];
ayat[2978]=[26,46,'فَأُلْقِيَ السَّحَرَةُ سَاجِدِينَ','فوراً همه ساحران به سجده افتادند.'];
ayat[2979]=[26,47,'قَالُوا آمَنَّا بِرَبِّ الْعَالَمِينَ','گفتند: «ما به پروردگار عالمیان ایمان آوردیم،'];
ayat[2980]=[26,48,'رَبِّ مُوسَىٰ وَهَارُونَ','پروردگار موسی و هارون!»'];
ayat[2981]=[26,49,'قَالَ آمَنتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ ۖ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَسَوْفَ تَعْلَمُونَ ۚ لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُم مِّنْ خِلَافٍ وَلَأُصَلِّبَنَّكُمْ أَجْمَعِينَ','(فرعون) گفت: «آیا پیش از اینکه به شما اجازه دهم به او ایمان آوردید؟! مسلّماً او بزرگ و استاد شماست که به شما سحر آموخته (و این یک توطئه است)! امّا بزودی خواهید دانست! دستها و پاهای شما را بعکس یکدیگر قطع می‌کنم، و همه شما را به دار می‌آویزم!»'];
ayat[2982]=[26,50,'قَالُوا لَا ضَيْرَ ۖ إِنَّا إِلَىٰ رَبِّنَا مُنقَلِبُونَ','گفتند: «مهّم نیست، (هر کاری از دستت ساخته است بکن)! ما بسوی پروردگارمان بازمی‌گردیم!'];
ayat[2983]=[26,51,'إِنَّا نَطْمَعُ أَن يَغْفِرَ لَنَا رَبُّنَا خَطَايَانَا أَن كُنَّا أَوَّلَ الْمُؤْمِنِينَ','ما امیدواریم که پروردگارمان خطاهای ما را ببخشد، چرا که ما نخستین ایمان‌آورندگان بودیم!»'];
ayat[2984]=[26,52,'۞ وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَسْرِ بِعِبَادِي إِنَّكُم مُّتَّبَعُونَ','و به موسی وحی کردیم که شبانه بندگانم را (از مصر) کوچ ده، زیرا شما مورد تعقیب هستید!»'];
ayat[2985]=[26,53,'فَأَرْسَلَ فِرْعَوْنُ فِي الْمَدَائِنِ حَاشِرِينَ','فرعون (از این ماجرا آگاه شد و) مأموران بسیج نیرو را به شهرها فرستاد،'];
ayat[2986]=[26,54,'إِنَّ هَٰؤُلَاءِ لَشِرْذِمَةٌ قَلِيلُونَ','(و گفت:) اینها مسلّماً گروهی اندکند؛'];
ayat[2987]=[26,55,'وَإِنَّهُمْ لَنَا لَغَائِظُونَ','و اینها ما را به خشم آورده‌اند؛'];
ayat[2988]=[26,56,'وَإِنَّا لَجَمِيعٌ حَاذِرُونَ','و ما همگی آماده پیکاریم!»'];
ayat[2989]=[26,57,'فَأَخْرَجْنَاهُم مِّن جَنَّاتٍ وَعُيُونٍ','(سرانجام فرعونیان مغلوب شدند،) و ما آنها را از باغها و چشمه‌ها بیرون راندیم،'];
ayat[2990]=[26,58,'وَكُنُوزٍ وَمَقَامٍ كَرِيمٍ','و از گنجها و قصرهای مجلّل!'];
ayat[2991]=[26,59,'كَذَٰلِكَ وَأَوْرَثْنَاهَا بَنِي إِسْرَائِيلَ','(آری،) اینچنین کردیم! و بنی اسرائیل را وارث آنها ساختیم!'];
ayat[2992]=[26,60,'فَأَتْبَعُوهُم مُّشْرِقِينَ','آنان به تعقیب بنی اسرائیل پرداختند، و به هنگام طلوع آفتاب به آنها رسیدند.'];
ayat[2993]=[26,61,'فَلَمَّا تَرَاءَى الْجَمْعَانِ قَالَ أَصْحَابُ مُوسَىٰ إِنَّا لَمُدْرَكُونَ','هنگامی که دو گروه یکدیگر را دیدند، یاران موسی گفتند: «ما در چنگال فرعونیان گرفتار شدیم!»'];
ayat[2994]=[26,62,'قَالَ كَلَّا ۖ إِنَّ مَعِيَ رَبِّي سَيَهْدِينِ','(موسی) گفت: «چنین نیست! یقیناً پروردگارم با من است، بزودی مرا هدایت خواهد کرد!»'];
ayat[2995]=[26,63,'فَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنِ اضْرِب بِّعَصَاكَ الْبَحْرَ ۖ فَانفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيمِ','و بدنبال آن به موسی وحی کردیم: «عصایت را به دریا بزن!» (عصایش را به دریا زد،) و دریا از هم شکافته شد، و هر بخشی همچون کوه عظیمی بود!'];
ayat[2996]=[26,64,'وَأَزْلَفْنَا ثَمَّ الْآخَرِينَ','و در آنجا دیگران [= لشکر فرعون‌] را نیز (به دریا) نزدیک ساختیم!'];
ayat[2997]=[26,65,'وَأَنجَيْنَا مُوسَىٰ وَمَن مَّعَهُ أَجْمَعِينَ','و موسی و تمام کسانی را که با او بودند نجات دادیم!'];
ayat[2998]=[26,66,'ثُمَّ أَغْرَقْنَا الْآخَرِينَ','سپس دیگران را غرق کردیم!'];
ayat[2999]=[26,67,'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','در این جریان، نشانه روشنی است ولی بیشترشان ایمان نیاوردند! (چرا که طالب حق نبودند)'];
ayat[3000]=[26,68,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگارت شکست‌ناپذیر و مهربان است!'];
ayat[3001]=[26,69,'وَاتْلُ عَلَيْهِمْ نَبَأَ إِبْرَاهِيمَ','و بر آنان خبر ابراهیم را بخوان،'];
ayat[3002]=[26,70,'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَا تَعْبُدُونَ','هنگامی که به پدر و قومش گفت: «چه چیز را می‌پرستید؟!»'];
ayat[3003]=[26,71,'قَالُوا نَعْبُدُ أَصْنَامًا فَنَظَلُّ لَهَا عَاكِفِينَ','گفتند: «بتهایی را می‌پرستیم، و همه روز ملازم عبادت آنهاییم.»'];
ayat[3004]=[26,72,'قَالَ هَلْ يَسْمَعُونَكُمْ إِذْ تَدْعُونَ','گفت: «آیا هنگامی که آنها را می‌خوانید صدای شما را می‌شنوند؟!'];
ayat[3005]=[26,73,'أَوْ يَنفَعُونَكُمْ أَوْ يَضُرُّونَ','یا سود و زیانی به شما می‌رسانند؟!»'];
ayat[3006]=[26,74,'قَالُوا بَلْ وَجَدْنَا آبَاءَنَا كَذَٰلِكَ يَفْعَلُونَ','گفتند: «ما فقط نیاکان خود را یافتیم که چنین می‌کنند.»'];
ayat[3007]=[26,75,'قَالَ أَفَرَأَيْتُم مَّا كُنتُمْ تَعْبُدُونَ','گفت: «آیا دیدید (این) چیزهایی را که پیوسته پرستش می‌کردید...'];
ayat[3008]=[26,76,'أَنتُمْ وَآبَاؤُكُمُ الْأَقْدَمُونَ','شما و پدران پیشین شما،'];
ayat[3009]=[26,77,'فَإِنَّهُمْ عَدُوٌّ لِّي إِلَّا رَبَّ الْعَالَمِينَ','همه آنها دشمن من هستند (و من دشمن آنها)، مگر پروردگار عالمیان!'];
ayat[3010]=[26,78,'الَّذِي خَلَقَنِي فَهُوَ يَهْدِينِ','همان کسی که مرا آفرید، و پیوسته راهنمائیم می‌کند،'];
ayat[3011]=[26,79,'وَالَّذِي هُوَ يُطْعِمُنِي وَيَسْقِينِ','و کسی که مرا غذا می‌دهد و سیراب می‌نماید،'];
ayat[3012]=[26,80,'وَإِذَا مَرِضْتُ فَهُوَ يَشْفِينِ','و هنگامی که بیمار شوم مرا شفا می‌دهد،'];
ayat[3013]=[26,81,'وَالَّذِي يُمِيتُنِي ثُمَّ يُحْيِينِ','و کسی که مرا می‌میراند و سپس زنده می‌کند،'];
ayat[3014]=[26,82,'وَالَّذِي أَطْمَعُ أَن يَغْفِرَ لِي خَطِيئَتِي يَوْمَ الدِّينِ','و کسی که امید دارم گناهم را در روز جزا ببخشد!'];
ayat[3015]=[26,83,'رَبِّ هَبْ لِي حُكْمًا وَأَلْحِقْنِي بِالصَّالِحِينَ','پروردگارا! به من علم و دانش ببخش، و مرا به صالحان ملحق کن!'];
ayat[3016]=[26,84,'وَاجْعَل لِّي لِسَانَ صِدْقٍ فِي الْآخِرِينَ','و برای من در میان امّتهای آینده، زبان صدق (و ذکر خیری) قرار ده!'];
ayat[3017]=[26,85,'وَاجْعَلْنِي مِن وَرَثَةِ جَنَّةِ النَّعِيمِ','و مرا وارثان بهشت پرنعمت گردان!'];
ayat[3018]=[26,86,'وَاغْفِرْ لِأَبِي إِنَّهُ كَانَ مِنَ الضَّالِّينَ','و پدرم [= عمویم‌] را بیامرز، که او از گمراهان بود!'];
ayat[3019]=[26,87,'وَلَا تُخْزِنِي يَوْمَ يُبْعَثُونَ','و در آن روز که مردم برانگیخته می‌شوند، مرا شرمنده و رسوا مکن!'];
ayat[3020]=[26,88,'يَوْمَ لَا يَنفَعُ مَالٌ وَلَا بَنُونَ','در آن روز که مال و فرزندان سودی نمی‌بخشد،'];
ayat[3021]=[26,89,'إِلَّا مَنْ أَتَى اللَّهَ بِقَلْبٍ سَلِيمٍ','مگر کسی که با قلب سلیم به پیشگاه خدا آید!»'];
ayat[3022]=[26,90,'وَأُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ','(در آن روز،) بهشت برای پرهیزکاران نزدیک می‌شود،'];
ayat[3023]=[26,91,'وَبُرِّزَتِ الْجَحِيمُ لِلْغَاوِينَ','و دوزخ برای گمراهان آشکار می‌گردد،'];
ayat[3024]=[26,92,'وَقِيلَ لَهُمْ أَيْنَ مَا كُنتُمْ تَعْبُدُونَ','و به آنان گفته می‌شود: «کجا هستند معبودانی که آنها را پرستش می‌کردید...'];
ayat[3025]=[26,93,'مِن دُونِ اللَّهِ هَلْ يَنصُرُونَكُمْ أَوْ يَنتَصِرُونَ','معبودهایی غیر از خدا؟! آیا آنها شما را یاری می‌کنند، یا کسی به یاری آنها می‌آید؟!»'];
ayat[3026]=[26,94,'فَكُبْكِبُوا فِيهَا هُمْ وَالْغَاوُونَ','در آن هنگام همه آن معبودان با عابدان گمراه به دوزخ افکنده می‌شوند؛'];
ayat[3027]=[26,95,'وَجُنُودُ إِبْلِيسَ أَجْمَعُونَ','و همچنین همگی لشکریان ابلیس!'];
ayat[3028]=[26,96,'قَالُوا وَهُمْ فِيهَا يَخْتَصِمُونَ','آنها در آنجا در حالی که به مخاصمه برخاسته‌اند می‌گویند:'];
ayat[3029]=[26,97,'تَاللَّهِ إِن كُنَّا لَفِي ضَلَالٍ مُّبِينٍ','«به خدا سوگند که ما در گمراهی آشکاری بودیم،'];
ayat[3030]=[26,98,'إِذْ نُسَوِّيكُم بِرَبِّ الْعَالَمِينَ','چون شما را با پروردگار عالمیان برابر می‌شمردیم!'];
ayat[3031]=[26,99,'وَمَا أَضَلَّنَا إِلَّا الْمُجْرِمُونَ','امّا کسی جز مجرمان ما را گمراه نکرد!'];
ayat[3032]=[26,100,'فَمَا لَنَا مِن شَافِعِينَ','(افسوس که امروز) شفاعت‌کنندگانی برای ما وجود ندارد،'];
ayat[3033]=[26,101,'وَلَا صَدِيقٍ حَمِيمٍ','و نه دوست گرم و پرمحبّتی!'];
ayat[3034]=[26,102,'فَلَوْ أَنَّ لَنَا كَرَّةً فَنَكُونَ مِنَ الْمُؤْمِنِينَ','ای کاش بار دیگر (به دنیا) بازگردیم و از مؤمنان باشیم!»'];
ayat[3035]=[26,103,'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','در این ماجرا، نشانه (و عبرتی) است؛ ولی بیشتر آنان مؤمن نبودند!'];
ayat[3036]=[26,104,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است!'];
ayat[3037]=[26,105,'كَذَّبَتْ قَوْمُ نُوحٍ الْمُرْسَلِينَ','قوم نوح رسولان را تکذیب کردند،'];
ayat[3038]=[26,106,'إِذْ قَالَ لَهُمْ أَخُوهُمْ نُوحٌ أَلَا تَتَّقُونَ','هنگامی که برادرشان نوح به آنان گفت: «آیا تقوا پیشه نمی‌کنید؟!'];
ayat[3039]=[26,107,'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ','مسلّماً من برای شما پیامبری امین هستم!'];
ayat[3040]=[26,108,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','تقوای الهی پیشه کنید و مرا اطاعت نمایید!'];
ayat[3041]=[26,109,'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ','من برای این دعوت، هیچ مزدی از شما نمی‌طلبم؛ اجر من تنها بر پروردگار عالمیان است!'];
ayat[3042]=[26,110,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس، تقوای الهی پیشه کنید و مرا اطاعت نمایید!»'];
ayat[3043]=[26,111,'۞ قَالُوا أَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْأَرْذَلُونَ','گفتند: «آیا ما به تو ایمان بیاوریم در حالی که افراد پست و بی‌ارزش از تو پیروی کرده‌اند؟!»'];
ayat[3044]=[26,112,'قَالَ وَمَا عِلْمِي بِمَا كَانُوا يَعْمَلُونَ','(نوح) گفت: «من چه می‌دانم آنها چه کاری داشته‌اند!'];
ayat[3045]=[26,113,'إِنْ حِسَابُهُمْ إِلَّا عَلَىٰ رَبِّي ۖ لَوْ تَشْعُرُونَ','حساب آنها تنها با پروردگار من است اگر شما می‌فهمیدید!'];
ayat[3046]=[26,114,'وَمَا أَنَا بِطَارِدِ الْمُؤْمِنِينَ','و من هرگز مؤمنان را طرد نخواهم کرد.'];
ayat[3047]=[26,115,'إِنْ أَنَا إِلَّا نَذِيرٌ مُّبِينٌ','من تنها انذارکننده‌ای آشکارم.»'];
ayat[3048]=[26,116,'قَالُوا لَئِن لَّمْ تَنتَهِ يَا نُوحُ لَتَكُونَنَّ مِنَ الْمَرْجُومِينَ','گفتند: «ای نوح! اگر (از حرفهایت) دست برنداری، سنگباران خواهی شد!»'];
ayat[3049]=[26,117,'قَالَ رَبِّ إِنَّ قَوْمِي كَذَّبُونِ','گفت: «پروردگارا! قوم من، مرا تکذیب کردند!'];
ayat[3050]=[26,118,'فَافْتَحْ بَيْنِي وَبَيْنَهُمْ فَتْحًا وَنَجِّنِي وَمَن مَّعِيَ مِنَ الْمُؤْمِنِينَ','اکنون میان من و آنها جدایی بیفکن؛ و مرا و مؤمنانی را که با من هستند رهایی بخش!»'];
ayat[3051]=[26,119,'فَأَنجَيْنَاهُ وَمَن مَّعَهُ فِي الْفُلْكِ الْمَشْحُونِ','ما، او و کسانی را که با او بودند، در آن کشتی که پر (از انسان و انواع حیوانات) بود، رهایی بخشیدیم!'];
ayat[3052]=[26,120,'ثُمَّ أَغْرَقْنَا بَعْدُ الْبَاقِينَ','سپس بقیه را غرق کردیم!'];
ayat[3053]=[26,121,'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','در این ماجرا نشانه روشنی است؛ امّا بیشتر آنان مؤمن نبودند.'];
ayat[3054]=[26,122,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است.'];
ayat[3055]=[26,123,'كَذَّبَتْ عَادٌ الْمُرْسَلِينَ','قوم عاد (نیز) رسولان (خدا) را تکذیب کردند،'];
ayat[3056]=[26,124,'إِذْ قَالَ لَهُمْ أَخُوهُمْ هُودٌ أَلَا تَتَّقُونَ','هنگامی که برادرشان هود گفت: «آیا تقوا پیشه نمی‌کنید؟!'];
ayat[3057]=[26,125,'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ','مسلماً من برای شما پیامبری امین هستم.'];
ayat[3058]=[26,126,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'];
ayat[3059]=[26,127,'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ','من در برابر این دعوت، هیچ اجر و پاداشی از شما نمی‌طلبم؛ اجر و پاداش من تنها بر پروردگار عالمیان است.'];
ayat[3060]=[26,128,'أَتَبْنُونَ بِكُلِّ رِيعٍ آيَةً تَعْبَثُونَ','آیا شما بر هر مکان مرتفعی نشانه‌ای از روی هوا و هوس می‌سازید؟!'];
ayat[3061]=[26,129,'وَتَتَّخِذُونَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُونَ','و قصرها و قلعه‌های زیبا و محکم بنا می‌کنید شاید در دنیا جاودانه بمانید؟!'];
ayat[3062]=[26,130,'وَإِذَا بَطَشْتُم بَطَشْتُمْ جَبَّارِينَ','و هنگامی که کسی را مجازات می‌کنید همچون جبّاران کیفر می‌دهید!'];
ayat[3063]=[26,131,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'];
ayat[3064]=[26,132,'وَاتَّقُوا الَّذِي أَمَدَّكُم بِمَا تَعْلَمُونَ','و از (نافرمانی) خدایی بپرهیزید که شما را به نعمتهایی که می‌دانید امداد کرده؛'];
ayat[3065]=[26,133,'أَمَدَّكُم بِأَنْعَامٍ وَبَنِينَ','شما را به چهارپایان و نیز پسران (لایق و برومند) امداد فرموده؛'];
ayat[3066]=[26,134,'وَجَنَّاتٍ وَعُيُونٍ','همچنین به باغها و چشمه‌ها!'];
ayat[3067]=[26,135,'إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ','(اگر کفران کنید،) من بر شما از عذاب روزی بزرگ می‌ترسم!»'];
ayat[3068]=[26,136,'قَالُوا سَوَاءٌ عَلَيْنَا أَوَعَظْتَ أَمْ لَمْ تَكُن مِّنَ الْوَاعِظِينَ','آنها [= قوم عاد] گفتند: «برای ما تفاوت نمی‌کند، چه ما را انذار کنی یا نکنی؛ (بیهوده خود را خسته مکن)!'];
ayat[3069]=[26,137,'إِنْ هَٰذَا إِلَّا خُلُقُ الْأَوَّلِينَ','این همان روش (و افسانه‌های) پیشینیان است!'];
ayat[3070]=[26,138,'وَمَا نَحْنُ بِمُعَذَّبِينَ','و ما هرگز مجازات نخواهیم شد!»'];
ayat[3071]=[26,139,'فَكَذَّبُوهُ فَأَهْلَكْنَاهُمْ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','آنان هود را تکذیب کردند، ما هم نابودشان کردیم؛ و در این، آیت و نشانه‌ای است (برای آگاهان)؛ ولی بیشتر آنان مؤمن نبودند!'];
ayat[3072]=[26,140,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است!'];
ayat[3073]=[26,141,'كَذَّبَتْ ثَمُودُ الْمُرْسَلِينَ','قوم ثمود رسولان (خدا) را تکذیب کردند،'];
ayat[3074]=[26,142,'إِذْ قَالَ لَهُمْ أَخُوهُمْ صَالِحٌ أَلَا تَتَّقُونَ','هنگامی که صالح به آنان گفت: «آیا تقوا پیشه نمی‌کنید؟!'];
ayat[3075]=[26,143,'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ','من برای شما پیامبری امین هستم!'];
ayat[3076]=[26,144,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'];
ayat[3077]=[26,145,'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ','من در برابر این دعوت، اجر و پاداشی از شما نمی‌طلبم؛ اجر من تنها بر پروردگار عالمیان است!'];
ayat[3078]=[26,146,'أَتُتْرَكُونَ فِي مَا هَاهُنَا آمِنِينَ','آیا شما تصوّر می‌کنید همیشه در نهایت امنیّت در نعمتهایی که اینجاست می‌مانید،'];
ayat[3079]=[26,147,'فِي جَنَّاتٍ وَعُيُونٍ','در این باغها و چشمه‌ها،'];
ayat[3080]=[26,148,'وَزُرُوعٍ وَنَخْلٍ طَلْعُهَا هَضِيمٌ','در این زراعتها و نخلهایی که میوه‌هایش شیرین و رسیده است؟!'];
ayat[3081]=[26,149,'وَتَنْحِتُونَ مِنَ الْجِبَالِ بُيُوتًا فَارِهِينَ','و از کوه‌ها خانه‌هایی می‌تراشید، و در آن به عیش و نوش می‌پردازید!'];
ayat[3082]=[26,150,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس از خدا بترسید و مرا اطاعت کنید!'];
ayat[3083]=[26,151,'وَلَا تُطِيعُوا أَمْرَ الْمُسْرِفِينَ','و فرمان مسرفان را اطاعت نکنید!'];
ayat[3084]=[26,152,'الَّذِينَ يُفْسِدُونَ فِي الْأَرْضِ وَلَا يُصْلِحُونَ','همانها که در زمین فساد می‌کنند و اصلاح نمی‌کنند!»'];
ayat[3085]=[26,153,'قَالُوا إِنَّمَا أَنتَ مِنَ الْمُسَحَّرِينَ','گفتند: «(ای صالح!) تو از افسون شدگانی (و عقل خود را از دست داده‌ای!)'];
ayat[3086]=[26,154,'مَا أَنتَ إِلَّا بَشَرٌ مِّثْلُنَا فَأْتِ بِآيَةٍ إِن كُنتَ مِنَ الصَّادِقِينَ','تو فقط بشری همچون مائی؛ اگر راست می‌گویی آیت و نشانه‌ای بیاور!»'];
ayat[3087]=[26,155,'قَالَ هَٰذِهِ نَاقَةٌ لَّهَا شِرْبٌ وَلَكُمْ شِرْبُ يَوْمٍ مَّعْلُومٍ','گفت: «این ناقه‌ای است (که آیت الهی است) برای او سهمی (از آب قریه)، و برای شما سهم روز معینّی است!'];
ayat[3088]=[26,156,'وَلَا تَمَسُّوهَا بِسُوءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيمٍ','کمترین آزاری به آن نرسانید، که عذاب روزی بزرگ شما را فرا خواهد گرفت!»'];
ayat[3089]=[26,157,'فَعَقَرُوهَا فَأَصْبَحُوا نَادِمِينَ','سرانجام بر آن (ناقه) حمله نموده آن را «پی» کردند؛ (و هلاک نمودند) سپس از کرده خود پشیمان شدند!'];
ayat[3090]=[26,158,'فَأَخَذَهُمُ الْعَذَابُ ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','و عذاب الهی آنان را فرا گرفت؛ در این، آیت و نشانه‌ای است؛ ولی بیشتر آنان مؤمن نبودند!'];
ayat[3091]=[26,159,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است!'];
ayat[3092]=[26,160,'كَذَّبَتْ قَوْمُ لُوطٍ الْمُرْسَلِينَ','قوم لوط فرستادگان (خدا) را تکذیب کردند،'];
ayat[3093]=[26,161,'إِذْ قَالَ لَهُمْ أَخُوهُمْ لُوطٌ أَلَا تَتَّقُونَ','هنگامی که برادرشان لوط به آنان گفت: «آیا تقوا پیشه نمی‌کنید؟!'];
ayat[3094]=[26,162,'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ','من برای شما پیامبری امین هستم!'];
ayat[3095]=[26,163,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'];
ayat[3096]=[26,164,'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ','من در برابر این دعوت، اجری از شما نمی‌طلبم، اجر من فقط بر پروردگار عالمیان است!'];
ayat[3097]=[26,165,'أَتَأْتُونَ الذُّكْرَانَ مِنَ الْعَالَمِينَ','آیا در میان جهانیان، شما به سراغ جنس ذکور می‌روید (و همجنس بازی می‌کنید، آیا این زشت و ننگین نیست؟!)'];
ayat[3098]=[26,166,'وَتَذَرُونَ مَا خَلَقَ لَكُمْ رَبُّكُم مِّنْ أَزْوَاجِكُم ۚ بَلْ أَنتُمْ قَوْمٌ عَادُونَ','و همسرانی را که پروردگارتان برای شما آفریده است رها می‌کنید؟! (حقّاً) شما قوم تجاوزگری هستید!»'];
ayat[3099]=[26,167,'قَالُوا لَئِن لَّمْ تَنتَهِ يَا لُوطُ لَتَكُونَنَّ مِنَ الْمُخْرَجِينَ','گفتند: «ای لوط! اگر (از این سخنان) دست برنداری، به یقین از اخراج شدگان خواهی بود!»'];
ayat[3100]=[26,168,'قَالَ إِنِّي لِعَمَلِكُم مِّنَ الْقَالِينَ','گفت: «من دشمن سرسخت اعمال شما هستم!'];
ayat[3101]=[26,169,'رَبِّ نَجِّنِي وَأَهْلِي مِمَّا يَعْمَلُونَ','پروردگارا! من و خاندانم را از آنچه اینها انجام می‌دهند رهایی بخش!»'];
ayat[3102]=[26,170,'فَنَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ','ما او و تمامی خاندانش را نجات دادیم،'];
ayat[3103]=[26,171,'إِلَّا عَجُوزًا فِي الْغَابِرِينَ','جز پیرزنی که در میان بازماندگان بود!'];
ayat[3104]=[26,172,'ثُمَّ دَمَّرْنَا الْآخَرِينَ','سپس دیگران را هلاک کردیم!'];
ayat[3105]=[26,173,'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًا ۖ فَسَاءَ مَطَرُ الْمُنذَرِينَ','و بارانی (از سنگ) بر آنها فرستادیم؛ چه باران بدی بود باران انذارشدگان!'];
ayat[3106]=[26,174,'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','در این (ماجرای قوم لوط و سرنوشت شوم آنها) آیتی است؛ امّا بیشترشان مؤمن نبودند.'];
ayat[3107]=[26,175,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است!'];
ayat[3108]=[26,176,'كَذَّبَ أَصْحَابُ الْأَيْكَةِ الْمُرْسَلِينَ','اصحاب ایکه [= شهری نزدیک مدین‌] رسولان (خدا) را تکذیب کردند،'];
ayat[3109]=[26,177,'إِذْ قَالَ لَهُمْ شُعَيْبٌ أَلَا تَتَّقُونَ','هنگامی که شعیب به آنها گفت: «آیا تقوا پیشه نمی‌کنید؟!'];
ayat[3110]=[26,178,'إِنِّي لَكُمْ رَسُولٌ أَمِينٌ','مسلّماً من برای شما پیامبری امین هستم!'];
ayat[3111]=[26,179,'فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','پس تقوای الهی پیشه کنید و مرا اطاعت نمایید!'];
ayat[3112]=[26,180,'وَمَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ ۖ إِنْ أَجْرِيَ إِلَّا عَلَىٰ رَبِّ الْعَالَمِينَ','من در برابر این دعوت، پاداشی از شما نمی‌طلبم؛ اجر من تنها بر پروردگار عالمیان است!'];
ayat[3113]=[26,181,'۞ أَوْفُوا الْكَيْلَ وَلَا تَكُونُوا مِنَ الْمُخْسِرِينَ','حق پیمانه را ادا کنید (و کم فروشی نکنید)، و دیگران را به خسارت نیفکنید!'];
ayat[3114]=[26,182,'وَزِنُوا بِالْقِسْطَاسِ الْمُسْتَقِيمِ','و با ترازوی صحیح وزن کنید!'];
ayat[3115]=[26,183,'وَلَا تَبْخَسُوا النَّاسَ أَشْيَاءَهُمْ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ','و حق مردم را کم نگذارید، و در زمین تلاش برای فساد نکنید!'];
ayat[3116]=[26,184,'وَاتَّقُوا الَّذِي خَلَقَكُمْ وَالْجِبِلَّةَ الْأَوَّلِينَ','و از (نافرمانی) کسی که شما و اقوام پیشین را آفرید بپرهیزید!»'];
ayat[3117]=[26,185,'قَالُوا إِنَّمَا أَنتَ مِنَ الْمُسَحَّرِينَ','آنها گفتند: «تو فقط از افسون‌شدگانی!'];
ayat[3118]=[26,186,'وَمَا أَنتَ إِلَّا بَشَرٌ مِّثْلُنَا وَإِن نَّظُنُّكَ لَمِنَ الْكَاذِبِينَ','تو بشری همچون مائی، تنها گمانی که درباره تو داریم این است که از دروغگویانی!'];
ayat[3119]=[26,187,'فَأَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ السَّمَاءِ إِن كُنتَ مِنَ الصَّادِقِينَ','اگر راست می‌گویی، سنگهایی از آسمان بر سر ما بباران!»'];
ayat[3120]=[26,188,'قَالَ رَبِّي أَعْلَمُ بِمَا تَعْمَلُونَ','(شعیب) گفت: «پروردگار من به اعمالی که شما انجام می‌دهید داناتر است!»'];
ayat[3121]=[26,189,'فَكَذَّبُوهُ فَأَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ ۚ إِنَّهُ كَانَ عَذَابَ يَوْمٍ عَظِيمٍ','سرانجام او را تکذیب کردند، و عذاب روز سایبان (سایبانی از ابر صاعقه‌خیز) آنها را فراگرفت؛ یقیناً آن عذاب روز بزرگی بود!'];
ayat[3122]=[26,190,'إِنَّ فِي ذَٰلِكَ لَآيَةً ۖ وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ','در این ماجرا، آیت و نشانه‌ای است؛ ولی بیشتر آنها مؤمن نبودند.'];
ayat[3123]=[26,191,'وَإِنَّ رَبَّكَ لَهُوَ الْعَزِيزُ الرَّحِيمُ','و پروردگار تو عزیز و رحیم است!'];
ayat[3124]=[26,192,'وَإِنَّهُ لَتَنزِيلُ رَبِّ الْعَالَمِينَ','مسلّماً این (قرآن) از سوی پروردگار جهانیان نازل شده است!'];
ayat[3125]=[26,193,'نَزَلَ بِهِ الرُّوحُ الْأَمِينُ','روح الامین آن را نازل کرده است...'];
ayat[3126]=[26,194,'عَلَىٰ قَلْبِكَ لِتَكُونَ مِنَ الْمُنذِرِينَ','بر قلب (پاک) تو، تا از انذارکنندگان باشی!'];
ayat[3127]=[26,195,'بِلِسَانٍ عَرَبِيٍّ مُّبِينٍ','آن را به زبان عربی آشکار (نازل کرد)!'];
ayat[3128]=[26,196,'وَإِنَّهُ لَفِي زُبُرِ الْأَوَّلِينَ','و توصیف آن در کتابهای پیشینیان نیز آمده است!'];
ayat[3129]=[26,197,'أَوَلَمْ يَكُن لَّهُمْ آيَةً أَن يَعْلَمَهُ عُلَمَاءُ بَنِي إِسْرَائِيلَ','آیا همین نشانه برای آنها کافی نیست که علمای بنی اسرائیل بخوبی از آن آگاهند؟!'];
ayat[3130]=[26,198,'وَلَوْ نَزَّلْنَاهُ عَلَىٰ بَعْضِ الْأَعْجَمِينَ','هرگاه ما آن را بر بعضی از عجم [= غیر عرب‌] ها نازل می‌کردیم...'];
ayat[3131]=[26,199,'فَقَرَأَهُ عَلَيْهِم مَّا كَانُوا بِهِ مُؤْمِنِينَ','و او آن را بر ایشان می‌خواند، به آن ایمان نمی‌آورند!'];
ayat[3132]=[26,200,'كَذَٰلِكَ سَلَكْنَاهُ فِي قُلُوبِ الْمُجْرِمِينَ','(آری،) این گونه (با بیانی رسا) قرآن را در دلهای مجرمان وارد می‌کنیم!'];
ayat[3133]=[26,201,'لَا يُؤْمِنُونَ بِهِ حَتَّىٰ يَرَوُا الْعَذَابَ الْأَلِيمَ','(امّا) به آن ایمان نمی‌آورند تا عذاب دردناک را با چشم خود ببینند!'];
ayat[3134]=[26,202,'فَيَأْتِيَهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ','ناگهان به سراغشان می‌آید، در حالی که توجّه ندارند!'];
ayat[3135]=[26,203,'فَيَقُولُوا هَلْ نَحْنُ مُنظَرُونَ','و (در آن هنگام) می‌گویند: «آیا به ما مهلتی داده خواهد شد؟!»'];
ayat[3136]=[26,204,'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ','آیا برای عذاب ما عجله می‌کنند؟!'];
ayat[3137]=[26,205,'أَفَرَأَيْتَ إِن مَّتَّعْنَاهُمْ سِنِينَ','به ما خبر ده، اگر (باز هم) سالیانی آنها را از این زندگی بهره‌مند سازیم...'];
ayat[3138]=[26,206,'ثُمَّ جَاءَهُم مَّا كَانُوا يُوعَدُونَ','سپس عذابی که به آنها وعده داده شده به سراغشان بیاید...'];
ayat[3139]=[26,207,'مَا أَغْنَىٰ عَنْهُم مَّا كَانُوا يُمَتَّعُونَ','این تمتع و بهره‌گیری از دنیا برای آنها سودی نخواهد داشت!'];
ayat[3140]=[26,208,'وَمَا أَهْلَكْنَا مِن قَرْيَةٍ إِلَّا لَهَا مُنذِرُونَ','ما هیچ شهر و دیاری را هلاک نکردیم مگر اینکه انذارکنندگانی (از پیامبران الهی) داشتند.'];
ayat[3141]=[26,209,'ذِكْرَىٰ وَمَا كُنَّا ظَالِمِينَ','تا متذکّر شوند؛ و ما هرگز ستمکار نبودیم! (که بدون اتمام حجّت مجازات کنیم)'];
ayat[3142]=[26,210,'وَمَا تَنَزَّلَتْ بِهِ الشَّيَاطِينُ','شیاطین و جنیّان (هرگز) این آیات را نازل نکردند!'];
ayat[3143]=[26,211,'وَمَا يَنبَغِي لَهُمْ وَمَا يَسْتَطِيعُونَ','و برای آنها سزاوار نیست؛ و قدرت ندارند!'];
ayat[3144]=[26,212,'إِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُولُونَ','آنها از استراق سمع (و شنیدن اخبار آسمانها) برکنارند!'];
ayat[3145]=[26,213,'فَلَا تَدْعُ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَتَكُونَ مِنَ الْمُعَذَّبِينَ','(ای پیامبر!) هیچ معبودی را با خداوند مخوان، که از معذّبین خواهی بود!'];
ayat[3146]=[26,214,'وَأَنذِرْ عَشِيرَتَكَ الْأَقْرَبِينَ','و خویشاوندان نزدیکت را انذار کن!'];
ayat[3147]=[26,215,'وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ','و بال و پر خود را برای مؤمنانی که از تو پیروی می‌کنند بگستر!'];
ayat[3148]=[26,216,'فَإِنْ عَصَوْكَ فَقُلْ إِنِّي بَرِيءٌ مِّمَّا تَعْمَلُونَ','اگر تو را نافرمانی کنند بگو: «من از آنچه شما انجام می‌دهید بیزارم!»'];
ayat[3149]=[26,217,'وَتَوَكَّلْ عَلَى الْعَزِيزِ الرَّحِيمِ','و بر خداوند عزیز و رحیم توکّل کن!'];
ayat[3150]=[26,218,'الَّذِي يَرَاكَ حِينَ تَقُومُ','همان کسی که تو را به هنگامی که (برای عبادت) برمی‌خیزی می‌بیند؛'];
ayat[3151]=[26,219,'وَتَقَلُّبَكَ فِي السَّاجِدِينَ','و (نیز) حرکت تو را در میان سجده‌کنندگان!'];
ayat[3152]=[26,220,'إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ','اوست خدای شنوا و دانا.'];
ayat[3153]=[26,221,'هَلْ أُنَبِّئُكُمْ عَلَىٰ مَن تَنَزَّلُ الشَّيَاطِينُ','آیا به شما خبر دهم که شیاطین بر چه کسی نازل می‌شوند؟!'];
ayat[3154]=[26,222,'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٍ','آنها بر هر دروغگوی گنهکار نازل می‌گردند؛'];
ayat[3155]=[26,223,'يُلْقُونَ السَّمْعَ وَأَكْثَرُهُمْ كَاذِبُونَ','آنچه را می‌شنوند (به دیگران) القا می‌کنند؛ و بیشترشان دروغگو هستند!'];
ayat[3156]=[26,224,'وَالشُّعَرَاءُ يَتَّبِعُهُمُ الْغَاوُونَ','(پیامبر اسلام شاعر نیست؛) شاعران کسانی هستند که گمراهان از آنان پیروی می‌کنند.'];
ayat[3157]=[26,225,'أَلَمْ تَرَ أَنَّهُمْ فِي كُلِّ وَادٍ يَهِيمُونَ','آیا نمی‌بینی آنها در هر وادی سرگردانند؟'];
ayat[3158]=[26,226,'وَأَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَ','و سخنانی می‌گویند که (به آنها) عمل نمی‌کنند؟!'];
ayat[3159]=[26,227,'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَذَكَرُوا اللَّهَ كَثِيرًا وَانتَصَرُوا مِن بَعْدِ مَا ظُلِمُوا ۗ وَسَيَعْلَمُ الَّذِينَ ظَلَمُوا أَيَّ مُنقَلَبٍ يَنقَلِبُونَ','مگر کسانی که ایمان آورده و کارهای شایسته انجام می‌دهند و خدا را بسیار یاد می کنند، و به هنگامی که مورد ستم واقع می‌شوند به دفاع از خویشتن (و مؤمنان) برمی‌خیزند (و از شعر در این راه کمک می‌گیرند)؛ آنها که ستم کردند به زودی می‌دانند که بازگشتشان به کجاست!'];
ayat[3160]=[27,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طس ۚ تِلْكَ آيَاتُ الْقُرْآنِ وَكِتَابٍ مُّبِينٍ','طس - این آیات قرآن و کتاب مبین است،'];
ayat[3161]=[27,2,'هُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ','وسیله هدایت و بشارت برای مؤمنان است؛'];
ayat[3162]=[27,3,'الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُم بِالْآخِرَةِ هُمْ يُوقِنُونَ','همان کسانی که نماز را برپا می‌دارند، و زکات را ادا می‌کنند، و آنان به آخرت یقین دارند.'];
ayat[3163]=[27,4,'إِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ زَيَّنَّا لَهُمْ أَعْمَالَهُمْ فَهُمْ يَعْمَهُونَ','کسانی که به آخرت ایمان ندارند، اعمال (بد) شان را برای آنان زینت می‌دهیم بطوری که سرگردان می‌شوند.'];
ayat[3164]=[27,5,'أُولَٰئِكَ الَّذِينَ لَهُمْ سُوءُ الْعَذَابِ وَهُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ','آنان کسانی هستند که عذاب بد (و دردناک) برای آنهاست؛ و آنها در آخرت، زیانکارترین مردمند!'];
ayat[3165]=[27,6,'وَإِنَّكَ لَتُلَقَّى الْقُرْآنَ مِن لَّدُنْ حَكِيمٍ عَلِيمٍ','به یقین این قرآن از سوی حکیم و دانایی بر تو القا می‌شود.'];
ayat[3166]=[27,7,'إِذْ قَالَ مُوسَىٰ لِأَهْلِهِ إِنِّي آنَسْتُ نَارًا سَآتِيكُم مِّنْهَا بِخَبَرٍ أَوْ آتِيكُم بِشِهَابٍ قَبَسٍ لَّعَلَّكُمْ تَصْطَلُونَ','(به خاطر بیاور) هنگامی را که موسی به خانواده خود گفت: «من آتشی از دور دیدم؛ (همین جا توقف کنید؛) بزودی خبری از آن برای شما می‌آورم، یا شعله آتشی تا گرم شوید.»'];
ayat[3167]=[27,8,'فَلَمَّا جَاءَهَا نُودِيَ أَن بُورِكَ مَن فِي النَّارِ وَمَنْ حَوْلَهَا وَسُبْحَانَ اللَّهِ رَبِّ الْعَالَمِينَ','هنگامی که نزد آتش آمد، ندایی برخاست که: «مبارک باد آن کس که در آتش است و کسی که در اطراف آن است [= فرشتگان و موسی‌] و منزّه است خداوندی که پروردگار جهانیان است!'];
ayat[3168]=[27,9,'يَا مُوسَىٰ إِنَّهُ أَنَا اللَّهُ الْعَزِيزُ الْحَكِيمُ','ای موسی! من خداوند عزیز و حکیمم!'];
ayat[3169]=[27,10,'وَأَلْقِ عَصَاكَ ۚ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّىٰ مُدْبِرًا وَلَمْ يُعَقِّبْ ۚ يَا مُوسَىٰ لَا تَخَفْ إِنِّي لَا يَخَافُ لَدَيَّ الْمُرْسَلُونَ','و عصایت را بیفکن! -هنگامی که (موسی) به آن نگاه کرد، دید (با سرعت) همچون ماری به هر سو می‌دود (ترسید و) به عقب برگشت، و حتی پشت سر خود را نگاه نکرد- ای موسی! نترس، که رسولان در نزد من نمی‌ترسند!'];
ayat[3170]=[27,11,'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسْنًا بَعْدَ سُوءٍ فَإِنِّي غَفُورٌ رَّحِيمٌ','مگر کسی که ستم کند؛ سپس بدی را به نیکی تبدیل نماید، که (توبه او را می‌پذیرم، و) من غفور و رحیمم!'];
ayat[3171]=[27,12,'وَأَدْخِلْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ ۖ فِي تِسْعِ آيَاتٍ إِلَىٰ فِرْعَوْنَ وَقَوْمِهِ ۚ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ','و دستت را در گریبانت داخل کن؛ هنگامی که خارج می‌شود، سفید و درخشنده است بی آنکه عیبی در آن باشد؛ این در زمره معجزات نه‌گانه‌ای است که تو با آنها بسوی فرعون و قومش فرستاده می‌شوی؛ آنان قومی فاسق و طغیانگرند!»'];
ayat[3172]=[27,13,'فَلَمَّا جَاءَتْهُمْ آيَاتُنَا مُبْصِرَةً قَالُوا هَٰذَا سِحْرٌ مُّبِينٌ','و هنگامی که آیات روشنی‌بخش ما به سراغ آنها آمد گفتند: «این سحری است آشکار!»'];
ayat[3173]=[27,14,'وَجَحَدُوا بِهَا وَاسْتَيْقَنَتْهَا أَنفُسُهُمْ ظُلْمًا وَعُلُوًّا ۚ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ','و آن را از روی ظلم و سرکشی انکار کردند، در حالی که در دل به آن یقین داشتند! پس بنگر سرانجام تبهکاران (و مفسدان) چگونه بود!'];
ayat[3174]=[27,15,'وَلَقَدْ آتَيْنَا دَاوُودَ وَسُلَيْمَانَ عِلْمًا ۖ وَقَالَا الْحَمْدُ لِلَّهِ الَّذِي فَضَّلَنَا عَلَىٰ كَثِيرٍ مِّنْ عِبَادِهِ الْمُؤْمِنِينَ','و ما به داوود و سلیمان، دانشی عظیم دادیم؛ و آنان گفتند: «ستایش از آن خداوندی است که ما را بر بسیاری از بندگان مؤمنش برتری بخشید.»'];
ayat[3175]=[27,16,'وَوَرِثَ سُلَيْمَانُ دَاوُودَ ۖ وَقَالَ يَا أَيُّهَا النَّاسُ عُلِّمْنَا مَنطِقَ الطَّيْرِ وَأُوتِينَا مِن كُلِّ شَيْءٍ ۖ إِنَّ هَٰذَا لَهُوَ الْفَضْلُ الْمُبِينُ','و سلیمان وارث داوود شد، و گفت: «ای مردم! زبان پرندگان به ما تعلیم داده شده، و از هر چیز به ما عطا گردیده؛ این فضیلت آشکاری است.»'];
ayat[3176]=[27,17,'وَحُشِرَ لِسُلَيْمَانَ جُنُودُهُ مِنَ الْجِنِّ وَالْإِنسِ وَالطَّيْرِ فَهُمْ يُوزَعُونَ','لشکریان سلیمان، از جنّ و انس و پرندگان، نزد او جمع شدند؛ آنقدر زیاد بودند که باید توقّف می‌کردند تا به هم ملحق شوند!'];
ayat[3177]=[27,18,'حَتَّىٰ إِذَا أَتَوْا عَلَىٰ وَادِ النَّمْلِ قَالَتْ نَمْلَةٌ يَا أَيُّهَا النَّمْلُ ادْخُلُوا مَسَاكِنَكُمْ لَا يَحْطِمَنَّكُمْ سُلَيْمَانُ وَجُنُودُهُ وَهُمْ لَا يَشْعُرُونَ','(آنها حرکت کردند) تا به سرزمین مورچگان رسیدند؛ مورچه‌ای گفت: «به لانه‌های خود بروید تا سلیمان و لشکرش شما را پایمال نکنند در حالی که نمی‌فهمند!»'];
ayat[3178]=[27,19,'فَتَبَسَّمَ ضَاحِكًا مِّن قَوْلِهَا وَقَالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَىٰ وَالِدَيَّ وَأَنْ أَعْمَلَ صَالِحًا تَرْضَاهُ وَأَدْخِلْنِي بِرَحْمَتِكَ فِي عِبَادِكَ الصَّالِحِينَ','سلیمان از سخن او تبسّمی کرد و خندید و گفت: «پروردگارا! شکر نعمتهایی را که بر من و پدر و مادرم ارزانی داشته‌ای به من الهام کن، و توفیق ده تا عمل صالحی که موجب رضای توست انجام دهم، و مرا برحمت خود در زمره بندگان صالحت وارد کن!»'];
ayat[3179]=[27,20,'وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَا أَرَى الْهُدْهُدَ أَمْ كَانَ مِنَ الْغَائِبِينَ','(سلیمان) در جستجوی آن پرنده [= هدهد] برآمد و گفت: «چرا هدهد را نمی‌بینم، یا اینکه او از غایبان است؟!'];
ayat[3180]=[27,21,'لَأُعَذِّبَنَّهُ عَذَابًا شَدِيدًا أَوْ لَأَذْبَحَنَّهُ أَوْ لَيَأْتِيَنِّي بِسُلْطَانٍ مُّبِينٍ','قطعاً او را کیفر شدیدی خواهم داد، یا او را ذبح می‌کنم، یا باید دلیل روشنی (برای غیبتش) برای من بیاورد!'];
ayat[3181]=[27,22,'فَمَكَثَ غَيْرَ بَعِيدٍ فَقَالَ أَحَطتُ بِمَا لَمْ تُحِطْ بِهِ وَجِئْتُكَ مِن سَبَإٍ بِنَبَإٍ يَقِينٍ','چندان درنگ نکرد (که هدهد آمد و) گفت: «من بر چیزی آگاهی یافتم که تو بر آن آگاهی نیافتی؛ من از سرزمین «سبا» یک خبر قطعی برای تو آورده‌ام!'];
ayat[3182]=[27,23,'إِنِّي وَجَدتُّ امْرَأَةً تَمْلِكُهُمْ وَأُوتِيَتْ مِن كُلِّ شَيْءٍ وَلَهَا عَرْشٌ عَظِيمٌ','من زنی را دیدم که بر آنان حکومت می‌کند، و همه چیز در اختیار دارد، و (به خصوص) تخت عظیمی دارد!'];
ayat[3183]=[27,24,'وَجَدتُّهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِن دُونِ اللَّهِ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ فَهُمْ لَا يَهْتَدُونَ','او و قومش را دیدم که برای غیر خدا -خورشید- سجده می‌کنند؛ و شیطان اعمالشان را در نظرشان جلوه داده، و آنها را از راه بازداشته؛ و از این رو هدایت نمی‌شوند!»'];
ayat[3184]=[27,25,'أَلَّا يَسْجُدُوا لِلَّهِ الَّذِي يُخْرِجُ الْخَبْءَ فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُخْفُونَ وَمَا تُعْلِنُونَ','چرا برای خداوندی سجده نمی‌کنند که آنچه را در آسمانها و زمین پنهان است خارج (و آشکار) می‌سازد، و آنچه را پنهان می‌دارید یا آشکار می‌کنید می‌داند؟!'];
ayat[3185]=[27,26,'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ رَبُّ الْعَرْشِ الْعَظِيمِ ۩','خداوندی که معبودی جز او نیست، و پروردگار عرش عظیم است!'];
ayat[3186]=[27,27,'۞ قَالَ سَنَنظُرُ أَصَدَقْتَ أَمْ كُنتَ مِنَ الْكَاذِبِينَ','(سلیمان) گفت: «ما تحقیق می‌کنیم ببینیم راست گفتی یا از دروغگویان هستی؟'];
ayat[3187]=[27,28,'اذْهَب بِّكِتَابِي هَٰذَا فَأَلْقِهْ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانظُرْ مَاذَا يَرْجِعُونَ','این نامه مرا ببر و بر آنان بیفکن؛ سپس برگرد (و در گوشه‌ای توقّف کن) ببین آنها چه عکس العملی نشان می‌دهند!'];
ayat[3188]=[27,29,'قَالَتْ يَا أَيُّهَا الْمَلَأُ إِنِّي أُلْقِيَ إِلَيَّ كِتَابٌ كَرِيمٌ','(ملکه سبا) گفت: «ای اشراف! نامه پرارزشی به سوی من افکنده شده!'];
ayat[3189]=[27,30,'إِنَّهُ مِن سُلَيْمَانَ وَإِنَّهُ بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ','این نامه از سلیمان است، و چنین می‌باشد: به نام خداوند بخشنده مهربان‌'];
ayat[3190]=[27,31,'أَلَّا تَعْلُوا عَلَيَّ وَأْتُونِي مُسْلِمِينَ','توصیه من این است که نسبت به من برتری‌جویی نکنید، و بسوی من آیید در حالی که تسلیم حقّ هستید!»'];
ayat[3191]=[27,32,'قَالَتْ يَا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي أَمْرِي مَا كُنتُ قَاطِعَةً أَمْرًا حَتَّىٰ تَشْهَدُونِ','(سپس) گفت: «ای اشراف (و ای بزرگان)! نظر خود را در این امر مهمّ به من بازگو کنید، که من هیچ کار مهمّی را بدون حضور (و مشورت) شما انجام نداده‌ام!'];
ayat[3192]=[27,33,'قَالُوا نَحْنُ أُولُو قُوَّةٍ وَأُولُو بَأْسٍ شَدِيدٍ وَالْأَمْرُ إِلَيْكِ فَانظُرِي مَاذَا تَأْمُرِينَ','گفتند: «ما دارای نیروی کافی و قدرت جنگی فراوان هستیم، ولی تصمیم نهایی با توست؛ ببین چه دستور می‌دهی!»'];
ayat[3193]=[27,34,'قَالَتْ إِنَّ الْمُلُوكَ إِذَا دَخَلُوا قَرْيَةً أَفْسَدُوهَا وَجَعَلُوا أَعِزَّةَ أَهْلِهَا أَذِلَّةً ۖ وَكَذَٰلِكَ يَفْعَلُونَ','گفت: پادشاهان هنگامی که وارد منطقه آبادی شوند آن را به فساد و تباهی می‌کشند، و عزیزان آنجا را ذلیل می‌کنند؛ (آری) کار آنان همین‌گونه است!'];
ayat[3194]=[27,35,'وَإِنِّي مُرْسِلَةٌ إِلَيْهِم بِهَدِيَّةٍ فَنَاظِرَةٌ بِمَ يَرْجِعُ الْمُرْسَلُونَ','و من (اکنون جنگ را صلاح نمی‌بینم،) هدیه گرانبهایی برای آنان می‌فرستم تا ببینم فرستادگان من چه خبر می‌آورند (و از این طریق آنها را بیازمایم)!»'];
ayat[3195]=[27,36,'فَلَمَّا جَاءَ سُلَيْمَانَ قَالَ أَتُمِدُّونَنِ بِمَالٍ فَمَا آتَانِيَ اللَّهُ خَيْرٌ مِّمَّا آتَاكُم بَلْ أَنتُم بِهَدِيَّتِكُمْ تَفْرَحُونَ','هنگامی که (فرستاده ملکه سبا) نزد سلیمان آمد، گفت: «می‌خواهید مرا با مال کمک کنید (و فریب دهید)؟! آنچه خدا به من داده، بهتر است از آنچه به شما داده است؛ بلکه شما هستید که به هدیه‌هایتان خوشحال می‌شوید!'];
ayat[3196]=[27,37,'ارْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُم بِجُنُودٍ لَّا قِبَلَ لَهُم بِهَا وَلَنُخْرِجَنَّهُم مِّنْهَا أَذِلَّةً وَهُمْ صَاغِرُونَ','بسوی آنان بازگرد (و اعلام کن) با لشکریانی به سراغ آنان می‌آییم که قدرت مقابله با آن را نداشته باشند؛ و آنان را از آن (سرزمین آباد) با ذلّت و خواری بیرون می‌رانیم!»'];
ayat[3197]=[27,38,'قَالَ يَا أَيُّهَا الْمَلَأُ أَيُّكُمْ يَأْتِينِي بِعَرْشِهَا قَبْلَ أَن يَأْتُونِي مُسْلِمِينَ','(سلیمان) گفت: «ای بزرگان! کدام یک از شما تخت او را برای من می‌آورد پیش از آنکه به حال تسلیم نزد من آیند؟»'];
ayat[3198]=[27,39,'قَالَ عِفْرِيتٌ مِّنَ الْجِنِّ أَنَا آتِيكَ بِهِ قَبْلَ أَن تَقُومَ مِن مَّقَامِكَ ۖ وَإِنِّي عَلَيْهِ لَقَوِيٌّ أَمِينٌ','عفریتی از جنّ گفت: «من آن را نزد تو می‌آورم پیش از آنکه از مجلست برخیزی و من نسبت به این امر، توانا و امینم!»'];
ayat[3199]=[27,40,'قَالَ الَّذِي عِندَهُ عِلْمٌ مِّنَ الْكِتَابِ أَنَا آتِيكَ بِهِ قَبْلَ أَن يَرْتَدَّ إِلَيْكَ طَرْفُكَ ۚ فَلَمَّا رَآهُ مُسْتَقِرًّا عِندَهُ قَالَ هَٰذَا مِن فَضْلِ رَبِّي لِيَبْلُوَنِي أَأَشْكُرُ أَمْ أَكْفُرُ ۖ وَمَن شَكَرَ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِ ۖ وَمَن كَفَرَ فَإِنَّ رَبِّي غَنِيٌّ كَرِيمٌ','(امّا) کسی که دانشی از کتاب (آسمانی) داشت گفت: «پیش از آنکه چشم بر هم زنی، آن را نزد تو خواهم آورد!» و هنگامی که (سلیمان) آن (تخت) را نزد خود ثابت و پابرجا دید گفت: «این از فضل پروردگار من است، تا مرا آزمایش کند که آیا شکر او را بجا می‌آورم یا کفران می‌کنم؟! و هر کس شکر کند، به نفع خود شکر می‌کند؛ و هر کس کفران نماید (بزیان خویش نموده است، که) پروردگار من، غنیّ و کریم است!»'];
ayat[3200]=[27,41,'قَالَ نَكِّرُوا لَهَا عَرْشَهَا نَنظُرْ أَتَهْتَدِي أَمْ تَكُونُ مِنَ الَّذِينَ لَا يَهْتَدُونَ','(سلیمان) گفت: «تخت او را برایش ناشناس سازید؛ ببینم آیا متوجّه می‌شود یا از کسانی است که هدایت نخواهند شد؟!'];
ayat[3201]=[27,42,'فَلَمَّا جَاءَتْ قِيلَ أَهَٰكَذَا عَرْشُكِ ۖ قَالَتْ كَأَنَّهُ هُوَ ۚ وَأُوتِينَا الْعِلْمَ مِن قَبْلِهَا وَكُنَّا مُسْلِمِينَ','هنگامی که آمد، به او گفته شد: «آیا تخت تو این گونه است؟» گفت: گویا خود آن است! و ما پیش از این هم آگاه بودیم و اسلام آورده بودیم!»'];
ayat[3202]=[27,43,'وَصَدَّهَا مَا كَانَت تَّعْبُدُ مِن دُونِ اللَّهِ ۖ إِنَّهَا كَانَتْ مِن قَوْمٍ كَافِرِينَ','و او را از آنچه غیر از خدا میپرستید بازداشت، که او [= ملکه سبا] از قوم کافران بود.'];
ayat[3203]=[27,44,'قِيلَ لَهَا ادْخُلِي الصَّرْحَ ۖ فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةً وَكَشَفَتْ عَن سَاقَيْهَا ۚ قَالَ إِنَّهُ صَرْحٌ مُّمَرَّدٌ مِّن قَوَارِيرَ ۗ قَالَتْ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي وَأَسْلَمْتُ مَعَ سُلَيْمَانَ لِلَّهِ رَبِّ الْعَالَمِينَ','به او گفته شد: «داخل حیاط (قصر) شو!» هنگامی که نظر به آن افکند، پنداشت نهر آبی است و ساق پاهای خود را برهنه کرد (تا از آب بگذرد؛ امّا سلیمان) گفت: «(این آب نیست،) بلکه قصری است از بلور صاف!» (ملکه سبا) گفت: «پروردگارا! من به خود ستم کردم؛ و (اینک) با سلیمان برای خداوندی که پروردگار عالمیان است اسلام آوردم!»'];
ayat[3204]=[27,45,'وَلَقَدْ أَرْسَلْنَا إِلَىٰ ثَمُودَ أَخَاهُمْ صَالِحًا أَنِ اعْبُدُوا اللَّهَ فَإِذَا هُمْ فَرِيقَانِ يَخْتَصِمُونَ','ما به سوی «ثمود»، برادرشان «صالح» را فرستادیم که: خدای یگانه را بپرستید! امّا آنان به دو گروه تقسیم شدند که به مخاصمه پرداختند.'];
ayat[3205]=[27,46,'قَالَ يَا قَوْمِ لِمَ تَسْتَعْجِلُونَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ ۖ لَوْلَا تَسْتَغْفِرُونَ اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ','(صالح) گفت: «ای قوم من! چرا برای بدی قبل از نیکی عجله می‌کنید (و عذاب الهی را می‌طلبید نه رحمت او را)؟! چرا از خداوند تقاضای آمرزش نمی‌کنید تا شاید مشمول رحمت (او) شوید؟!»'];
ayat[3206]=[27,47,'قَالُوا اطَّيَّرْنَا بِكَ وَبِمَن مَّعَكَ ۚ قَالَ طَائِرُكُمْ عِندَ اللَّهِ ۖ بَلْ أَنتُمْ قَوْمٌ تُفْتَنُونَ','آنها گفتند: «ما تو را و کسانی که با تو هستند به فال بد گرفتیم!» (صالح) گفت: «فال (نیک و) بد شما نزد خداست (و همه مقدّرات به قدرت او تعیین می‌گردد)؛ بلکه شما گروهی هستید فریب‌خورده!'];
ayat[3207]=[27,48,'وَكَانَ فِي الْمَدِينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِي الْأَرْضِ وَلَا يُصْلِحُونَ','و در آن شهر، نه گروهک بودند که در زمین فساد می‌کردند و اصلاح نمی‌کردند.'];
ayat[3208]=[27,49,'قَالُوا تَقَاسَمُوا بِاللَّهِ لَنُبَيِّتَنَّهُ وَأَهْلَهُ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِ مَا شَهِدْنَا مَهْلِكَ أَهْلِهِ وَإِنَّا لَصَادِقُونَ','آنها گفتند: «بیایید قسم یاد کنید به خدا که بر او [= صالح‌] و خانواده اش شبیخون می زنیم (و آنها را به قتل می‌رسانیم؛) سپس به ولیّ دم او می‌گوییم: ما هرگز از هلاکت خانواده او خبر نداشتیم و در این گفتار خود صادق هستیم!»'];
ayat[3209]=[27,50,'وَمَكَرُوا مَكْرًا وَمَكَرْنَا مَكْرًا وَهُمْ لَا يَشْعُرُونَ','آنها نقشه مهمّی کشیدند، و ما هم نقشه مهمّی؛ در حالی که آنها درک نمی‌کردند!'];
ayat[3210]=[27,51,'فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ أَنَّا دَمَّرْنَاهُمْ وَقَوْمَهُمْ أَجْمَعِينَ','بنگر عاقبت توطئه آنها چه شد، که ما آنها و قومشان همگی را نابود کردیم؛'];
ayat[3211]=[27,52,'فَتِلْكَ بُيُوتُهُمْ خَاوِيَةً بِمَا ظَلَمُوا ۗ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّقَوْمٍ يَعْلَمُونَ','این خانه‌های آنهاست که بخاطر ظلم و ستمشان خالی مانده؛ و در این نشانه روشنی است برای کسانی که آگاهند!'];
ayat[3212]=[27,53,'وَأَنجَيْنَا الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ','و کسانی را که ایمان آورده و تقوا پیشه کرده بودند نجات دادیم!'];
ayat[3213]=[27,54,'وَلُوطًا إِذْ قَالَ لِقَوْمِهِ أَتَأْتُونَ الْفَاحِشَةَ وَأَنتُمْ تُبْصِرُونَ','و لوط را (به یاد آور) هنگامی که به قومش گفت: «آیا شما به سراغ کار بسیار زشتی می‌روید در حالی که (نتایج شوم آن را) می‌بینید؟!'];
ayat[3214]=[27,55,'أَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ شَهْوَةً مِّن دُونِ النِّسَاءِ ۚ بَلْ أَنتُمْ قَوْمٌ تَجْهَلُونَ','آیا شما بجای زنان، از روی شهوت به سراغ مردان می‌روید؟! شما قومی نادانید!»'];
ayat[3215]=[27,56,'۞ فَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَن قَالُوا أَخْرِجُوا آلَ لُوطٍ مِّن قَرْيَتِكُمْ ۖ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ','آنها پاسخی جز این نداشتند که (به یکدیگر) گفتند: «خاندان لوط را از شهر و دیار خود بیرون کنید، که اینها افرادی پاکدامن هستند!»'];
ayat[3216]=[27,57,'فَأَنجَيْنَاهُ وَأَهْلَهُ إِلَّا امْرَأَتَهُ قَدَّرْنَاهَا مِنَ الْغَابِرِينَ','ما او و خانواده‌اش را نجات دادیم، بجز همسرش که مقدّر کردیم جزء باقی‌ماندگان (در آن شهر) باشد!'];
ayat[3217]=[27,58,'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًا ۖ فَسَاءَ مَطَرُ الْمُنذَرِينَ','سپس بارانی (از سنگ) بر سر آنها باراندیم (و همگی زیر آن مدفون شدند)؛ و چه بد است باران انذارشدگان!'];
ayat[3218]=[27,59,'قُلِ الْحَمْدُ لِلَّهِ وَسَلَامٌ عَلَىٰ عِبَادِهِ الَّذِينَ اصْطَفَىٰ ۗ آللَّهُ خَيْرٌ أَمَّا يُشْرِكُونَ','بگو: «حمد مخصوص خداست؛ و سلام بر بندگان برگزیده‌اش!» آیا خداوند بهتر است یا بتهایی که همتای او قرارمی‌دهند؟!'];
ayat[3219]=[27,60,'أَمَّنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَأَنزَلَ لَكُم مِّنَ السَّمَاءِ مَاءً فَأَنبَتْنَا بِهِ حَدَائِقَ ذَاتَ بَهْجَةٍ مَّا كَانَ لَكُمْ أَن تُنبِتُوا شَجَرَهَا ۗ أَإِلَٰهٌ مَّعَ اللَّهِ ۚ بَلْ هُمْ قَوْمٌ يَعْدِلُونَ','(آیا بتهایی که معبود شما هستند بهترند) یا کسی که آسمانها و زمین را آفریده؟! و برای شما از آسمان، آبی فرستاد که با آن، باغهایی زیبا و سرورانگیز رویاندیم؛ شما هرگز قدرت نداشتید درختان آن را برویانید! آیا معبود دیگری با خداست؟! نه، بلکه آنها گروهی هستند که (از روی نادانی، مخلوقات را) همطراز (پروردگارشان) قرار می‌دهند!'];
ayat[3220]=[27,61,'أَمَّن جَعَلَ الْأَرْضَ قَرَارًا وَجَعَلَ خِلَالَهَا أَنْهَارًا وَجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًا ۗ أَإِلَٰهٌ مَّعَ اللَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ','یا کسی که زمین را مستقرّ و آرام قرار داد، و میان آن نهرهایی روان ساخت، و برای آن کوه‌های ثابت و پابرجا ایجاد کرد، و میان دو دریا مانعی قرار داد (تا با هم مخلوط نشوند؛ با این حال) آیا معبودی با خداست؟! نه، بلکه بیشتر آنان نمی‌دانند (و جاهلند)!'];
ayat[3221]=[27,62,'أَمَّن يُجِيبُ الْمُضْطَرَّ إِذَا دَعَاهُ وَيَكْشِفُ السُّوءَ وَيَجْعَلُكُمْ خُلَفَاءَ الْأَرْضِ ۗ أَإِلَٰهٌ مَّعَ اللَّهِ ۚ قَلِيلًا مَّا تَذَكَّرُونَ','یا کسی که دعای مضطرّ را اجابت می‌کند و گرفتاری را برطرف می‌سازد، و شما را خلفای زمین قرارمی‌دهد؛ آیا معبودی با خداست؟! کمتر متذکّر می‌شوید!'];
ayat[3222]=[27,63,'أَمَّن يَهْدِيكُمْ فِي ظُلُمَاتِ الْبَرِّ وَالْبَحْرِ وَمَن يُرْسِلُ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۗ أَإِلَٰهٌ مَّعَ اللَّهِ ۚ تَعَالَى اللَّهُ عَمَّا يُشْرِكُونَ','یا کسی که شما را در تاریکیهای صحرا و دریا هدایت می‌کند، و کسی که بادها را بعنوان بشارت پیش از نزول رحمتش می‌فرستد؛ آیا معبودی با خداست؟! خداوند برتر است از آنچه برای او شریک قرارمی‌دهند!'];
ayat[3223]=[27,64,'أَمَّن يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ وَمَن يَرْزُقُكُم مِّنَ السَّمَاءِ وَالْأَرْضِ ۗ أَإِلَٰهٌ مَّعَ اللَّهِ ۚ قُلْ هَاتُوا بُرْهَانَكُمْ إِن كُنتُمْ صَادِقِينَ','یا کسی که آفرینش را آغاز کرد، سپس آن را تجدید می‌کند، و کسی که شما را از آسمان و زمین روزی می‌دهد؛ آیا معبودی با خداست؟! بگو: «دلیلتان را بیاورید اگر راست می‌گویید!»'];
ayat[3224]=[27,65,'قُل لَّا يَعْلَمُ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ الْغَيْبَ إِلَّا اللَّهُ ۚ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ','بگو: «کسانی که در آسمانها و زمین هستند غیب نمی‌دانند جز خدا، و نمی‌دانند کی برانگیخته می‌شوند!»'];
ayat[3225]=[27,66,'بَلِ ادَّارَكَ عِلْمُهُمْ فِي الْآخِرَةِ ۚ بَلْ هُمْ فِي شَكٍّ مِّنْهَا ۖ بَلْ هُم مِّنْهَا عَمُونَ','آنها [= مشرکان‌] اطلاع صحیحی درباره آخرت ندارند؛ بلکه در اصل آن شکّ دارند؛ بلکه نسبت به آن نابینایند!'];
ayat[3226]=[27,67,'وَقَالَ الَّذِينَ كَفَرُوا أَإِذَا كُنَّا تُرَابًا وَآبَاؤُنَا أَئِنَّا لَمُخْرَجُونَ','و کافران گفتند: «آیا هنگامی که ما و پدرانمان خاک شدیم، (زنده می‌شویم و) از دل خاک بیرون می‌آییم؟!'];
ayat[3227]=[27,68,'لَقَدْ وُعِدْنَا هَٰذَا نَحْنُ وَآبَاؤُنَا مِن قَبْلُ إِنْ هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ','این وعده‌ای است که به ما و پدرانمان از پیش داده شده؛ اینها همان افسانه‌های خرافی پیشینیان است!»'];
ayat[3228]=[27,69,'قُلْ سِيرُوا فِي الْأَرْضِ فَانظُرُوا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِينَ','بگو: «در روی زمین سیر کنید و ببینید عاقبت کار مجرمان به کجا رسید!»'];
ayat[3229]=[27,70,'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُن فِي ضَيْقٍ مِّمَّا يَمْكُرُونَ','از (تکذیب و انکار) آنان غمگین مباش، و سینه‌ات از توطئه آنان تنگ نشود!'];
ayat[3230]=[27,71,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِن كُنتُمْ صَادِقِينَ','آنها می‌گویند: «این وعده (عذاب که به ما می‌دهید) کی خواهد آمد اگر راست می‌گویید؟!»'];
ayat[3231]=[27,72,'قُلْ عَسَىٰ أَن يَكُونَ رَدِفَ لَكُم بَعْضُ الَّذِي تَسْتَعْجِلُونَ','بگو: «شاید پاره‌ای از آنچه درباره آن شتاب می‌کنید، نزدیک و در کنار شما باشد!»'];
ayat[3232]=[27,73,'وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَ','مسلّماً پروردگار تو نسبت به مردم، فضل (و رحمت) دارد؛ ولی بیشترشان شکرگزار نیستند!'];
ayat[3233]=[27,74,'وَإِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ','و پروردگارت آنچه را در سینه‌هایشان پنهان می‌دارند و آنچه را آشکار می‌کنند بخوبی می‌داند!'];
ayat[3234]=[27,75,'وَمَا مِنْ غَائِبَةٍ فِي السَّمَاءِ وَالْأَرْضِ إِلَّا فِي كِتَابٍ مُّبِينٍ','و هیچ موجود پنهانی در آسمان و زمین نیست مگر اینکه در کتاب مبین (در لوح محفوظ و علم بی‌پایان پروردگار) ثبت است!'];
ayat[3235]=[27,76,'إِنَّ هَٰذَا الْقُرْآنَ يَقُصُّ عَلَىٰ بَنِي إِسْرَائِيلَ أَكْثَرَ الَّذِي هُمْ فِيهِ يَخْتَلِفُونَ','این قرآن اکثر چیزهایی را که بنی اسرائیل در آن اختلاف دارند برای آنان بیان می‌کند؛'];
ayat[3236]=[27,77,'وَإِنَّهُ لَهُدًى وَرَحْمَةٌ لِّلْمُؤْمِنِينَ','و مایه هدایت و رحمت برای مؤمنان است!'];
ayat[3237]=[27,78,'إِنَّ رَبَّكَ يَقْضِي بَيْنَهُم بِحُكْمِهِ ۚ وَهُوَ الْعَزِيزُ الْعَلِيمُ','پروردگار تو میان آنها در قیامت به حکم خود داوری می‌کند؛ و اوست قادر دانا.'];
ayat[3238]=[27,79,'فَتَوَكَّلْ عَلَى اللَّهِ ۖ إِنَّكَ عَلَى الْحَقِّ الْمُبِينِ','پس بر خدا توکّل کن، که تو بر حقّ آشکار هستی!'];
ayat[3239]=[27,80,'إِنَّكَ لَا تُسْمِعُ الْمَوْتَىٰ وَلَا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ','مسلّماً تو نمی‌توانی سخنت را به گوش مردگان برسانی، و نمی‌توانی کران را هنگامی که روی برمی‌گردانند و پشت می‌کنند فراخوانی!'];
ayat[3240]=[27,81,'وَمَا أَنتَ بِهَادِي الْعُمْيِ عَن ضَلَالَتِهِمْ ۖ إِن تُسْمِعُ إِلَّا مَن يُؤْمِنُ بِآيَاتِنَا فَهُم مُّسْلِمُونَ','و نیز نمی‌توانی کوران را از گمراهیشان برهانی؛ تو فقط میتوانی سخن خود را به گوش کسانی برسانی که آماده پذیرش ایمان به آیات ما هستند و در برابر حق تسلیمند!'];
ayat[3241]=[27,82,'۞ وَإِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ أَخْرَجْنَا لَهُمْ دَابَّةً مِّنَ الْأَرْضِ تُكَلِّمُهُمْ أَنَّ النَّاسَ كَانُوا بِآيَاتِنَا لَا يُوقِنُونَ','و هنگامی که فرمان عذاب آنها رسد (و در آستانه رستاخیز قرار گیرند)، جنبنده‌ای را از زمین برای آنها خارج می‌کنیم که با آنان تکلّم می‌کند (و می‌گوید) که مردم به آیات ما ایمان نمی‌آوردند.'];
ayat[3242]=[27,83,'وَيَوْمَ نَحْشُرُ مِن كُلِّ أُمَّةٍ فَوْجًا مِّمَّن يُكَذِّبُ بِآيَاتِنَا فَهُمْ يُوزَعُونَ','(به خاطر آور) روزی را که ما از هر امّتی، گروهی را از کسانی که آیات ما را تکذیب می‌کردند محشور می‌کنیم؛ و آنها را نگه می‌داریم تا به یکدیگر ملحق شوند!'];
ayat[3243]=[27,84,'حَتَّىٰ إِذَا جَاءُوا قَالَ أَكَذَّبْتُم بِآيَاتِي وَلَمْ تُحِيطُوا بِهَا عِلْمًا أَمَّاذَا كُنتُمْ تَعْمَلُونَ','تا زمانی که (به پای حساب) می‌آیند، (به آنان) می‌گوید: «آیا آیات مرا تکذیب کردید و در صدد تحقیق برنیامدید؟! شما چه اعمالی انجام می‌دادید؟!»'];
ayat[3244]=[27,85,'وَوَقَعَ الْقَوْلُ عَلَيْهِم بِمَا ظَلَمُوا فَهُمْ لَا يَنطِقُونَ','در این هنگام، فرمان عذاب بخاطر ظلمشان بر آنها واقع می‌شود، و آنها سخنی ندارند که بگویند!'];
ayat[3245]=[27,86,'أَلَمْ يَرَوْا أَنَّا جَعَلْنَا اللَّيْلَ لِيَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يُؤْمِنُونَ','آیا ندیدند که ما شب را برای آرامش آنها قرار دادیم و روز را روشنی‌بخش؟! در این امور نشانه‌های روشنی است برای کسانی که ایمان می‌آورند (و آماده قبول حقند).'];
ayat[3246]=[27,87,'وَيَوْمَ يُنفَخُ فِي الصُّورِ فَفَزِعَ مَن فِي السَّمَاوَاتِ وَمَن فِي الْأَرْضِ إِلَّا مَن شَاءَ اللَّهُ ۚ وَكُلٌّ أَتَوْهُ دَاخِرِينَ','و (به خاطر آورید) روزی را که در «صور» دمیده می‌شود، و تمام کسانی که در آسمانها و زمین هستند در وحشت فرو می‌روند، جز کسانی که خدا خواسته؛ و همگی با خضوع در پیشگاه او حاضر می‌شوند!'];
ayat[3247]=[27,88,'وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحَابِ ۚ صُنْعَ اللَّهِ الَّذِي أَتْقَنَ كُلَّ شَيْءٍ ۚ إِنَّهُ خَبِيرٌ بِمَا تَفْعَلُونَ','کوه‌ها را می‌بینی، و آنها را ساکن و جامد می‌پنداری، در حالی که مانند ابر در حرکتند؛ این صنع و آفرینش خداوندی است که همه چیز را متقن آفریده؛ او از کارهایی که شما انجام می‌دهید مسلّماً آگاه است!'];
ayat[3248]=[27,89,'مَن جَاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِّنْهَا وَهُم مِّن فَزَعٍ يَوْمَئِذٍ آمِنُونَ','کسانی که کار نیکی انجام دهند پاداشی بهتر از آن خواهند داشت؛ و آنان از وحشت آن روز درامانند!'];
ayat[3249]=[27,90,'وَمَن جَاءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِي النَّارِ هَلْ تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ','و آنها که اعمال بدی انجام دهند، به صورت در آتش افکنده می‌شوند؛ آیا جزایی جز آنچه عمل می‌کردید خواهید داشت؟!'];
ayat[3250]=[27,91,'إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَٰذِهِ الْبَلْدَةِ الَّذِي حَرَّمَهَا وَلَهُ كُلُّ شَيْءٍ ۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ','(بگو:) من مأمورم پروردگار این شهر (مقدّس مکّه) را عبادت کنم، همان کسی که این شهر را حرمت بخشیده؛ در حالی که همه چیز از آن اوست! و من مأمورم که از مسلمین باشم؛'];
ayat[3251]=[27,92,'وَأَنْ أَتْلُوَ الْقُرْآنَ ۖ فَمَنِ اهْتَدَىٰ فَإِنَّمَا يَهْتَدِي لِنَفْسِهِ ۖ وَمَن ضَلَّ فَقُلْ إِنَّمَا أَنَا مِنَ الْمُنذِرِينَ','و اینکه قرآن را تلاوت کنم! هر کس هدایت شود بسود خود هدایت شده؛ و هر کس گمراه گردد (زیانش متوجّه خود اوست؛) بگو: «من فقط از انذارکنندگانم!»'];
ayat[3252]=[27,93,'وَقُلِ الْحَمْدُ لِلَّهِ سَيُرِيكُمْ آيَاتِهِ فَتَعْرِفُونَهَا ۚ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُونَ','بگو: «حمد و ستایش مخصوص ذات خداست؛ بزودی آیاتش را به شما نشان می‌دهد تا آن را بشناسید؛ و پروردگار تو از آنچه انجام می‌دهید غافل نیست!'];
ayat[3253]=[28,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ طسم','طسم!'];
ayat[3254]=[28,2,'تِلْكَ آيَاتُ الْكِتَابِ الْمُبِينِ','اینها از آیات کتاب مبین است!'];
ayat[3255]=[28,3,'نَتْلُو عَلَيْكَ مِن نَّبَإِ مُوسَىٰ وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُؤْمِنُونَ','ما از داستان موسی و فرعون بحقّ بر تو می‌خوانیم، برای گروهی که (طالب حقّند و) ایمان می‌آورند!'];
ayat[3256]=[28,4,'إِنَّ فِرْعَوْنَ عَلَا فِي الْأَرْضِ وَجَعَلَ أَهْلَهَا شِيَعًا يَسْتَضْعِفُ طَائِفَةً مِّنْهُمْ يُذَبِّحُ أَبْنَاءَهُمْ وَيَسْتَحْيِي نِسَاءَهُمْ ۚ إِنَّهُ كَانَ مِنَ الْمُفْسِدِينَ','فرعون در زمین برتری‌جویی کرد، و اهل آن را به گروه‌های مختلفی تقسیم نمود؛ گروهی را به ضعف و ناتوانی می‌کشاند، پسرانشان را سر می‌برید و زنانشان را (برای کنیزی و خدمت) زنده نگه می‌داشت؛ او به یقین از مفسدان بود!'];
ayat[3257]=[28,5,'وَنُرِيدُ أَن نَّمُنَّ عَلَى الَّذِينَ اسْتُضْعِفُوا فِي الْأَرْضِ وَنَجْعَلَهُمْ أَئِمَّةً وَنَجْعَلَهُمُ الْوَارِثِينَ','ما می‌خواهیم بر مستضعفان زمین منّت نهیم و آنان را پیشوایان و وارثان روی زمین قرار دهیم!'];
ayat[3258]=[28,6,'وَنُمَكِّنَ لَهُمْ فِي الْأَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا مِنْهُم مَّا كَانُوا يَحْذَرُونَ','و حکومتشان را در زمین پابرجا سازیم؛ و به فرعون و هامان و لشکریانشان، آنچه را از آنها [= بنی اسرائیل‌] بیم داشتند نشان دهیم!'];
ayat[3259]=[28,7,'وَأَوْحَيْنَا إِلَىٰ أُمِّ مُوسَىٰ أَنْ أَرْضِعِيهِ ۖ فَإِذَا خِفْتِ عَلَيْهِ فَأَلْقِيهِ فِي الْيَمِّ وَلَا تَخَافِي وَلَا تَحْزَنِي ۖ إِنَّا رَادُّوهُ إِلَيْكِ وَجَاعِلُوهُ مِنَ الْمُرْسَلِينَ','ما به مادر موسی الهام کردیم که: «او را شیر ده؛ و هنگامی که بر او ترسیدی، وی را در دریا (ی نیل) بیفکن؛ و نترس و غمگین مباش، که ما او را به تو بازمی‌گردانیم، و او را از رسولان قرار می‌دهیم!»'];
ayat[3260]=[28,8,'فَالْتَقَطَهُ آلُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَنًا ۗ إِنَّ فِرْعَوْنَ وَهَامَانَ وَجُنُودَهُمَا كَانُوا خَاطِئِينَ','(هنگامی که مادر بفرمان خدا او را به دریا افکند) خاندان فرعون او را از آب گرفتند، تا سرانجام دشمن آنان و مایه اندوهشان گردد! مسلّماً فرعون و هامان و لشکریانشان خطاکار بودند.'];
ayat[3261]=[28,9,'وَقَالَتِ امْرَأَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِّي وَلَكَ ۖ لَا تَقْتُلُوهُ عَسَىٰ أَن يَنفَعَنَا أَوْ نَتَّخِذَهُ وَلَدًا وَهُمْ لَا يَشْعُرُونَ','همسر فرعون (چون دید آنها قصد کشتن کودک را دارند) گفت: «نور چشم من و توست! او را نکشید شاید برای ما مفید باشد، یا او را بعنوان پسر خود برگزینیم!» و آنها نمی‌فهمیدند (که دشمن اصلی خود را در آغوش خویش می‌پرورانند)!'];
ayat[3262]=[28,10,'وَأَصْبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَارِغًا ۖ إِن كَادَتْ لَتُبْدِي بِهِ لَوْلَا أَن رَّبَطْنَا عَلَىٰ قَلْبِهَا لِتَكُونَ مِنَ الْمُؤْمِنِينَ','(سرانجام) قلب مادر موسی از همه چیز (جز یاد فرزندش) تهی گشت؛ و اگر دل او را (بوسیله ایمان و امید) محکم نکرده بودیم، نزدیک بود مطلب را افشا کند!'];
ayat[3263]=[28,11,'وَقَالَتْ لِأُخْتِهِ قُصِّيهِ ۖ فَبَصُرَتْ بِهِ عَن جُنُبٍ وَهُمْ لَا يَشْعُرُونَ','و (مادر موسی) به خواهر او گفت: «وضع حال او را پیگیری کن!» او نیز از دور ماجرا را مشاهده کرد در حالی که آنان بی‌خبر بودند.'];
ayat[3264]=[28,12,'۞ وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِن قَبْلُ فَقَالَتْ هَلْ أَدُلُّكُمْ عَلَىٰ أَهْلِ بَيْتٍ يَكْفُلُونَهُ لَكُمْ وَهُمْ لَهُ نَاصِحُونَ','ما همه زنان شیرده را از پیش بر او حرام کردیم (تا تنها به آغوش مادر بازگردد)؛ و خواهرش (که بیتابی مأموران را برای پیدا کردن دایه مشاهده کرد) گفت: «آیا شما را به خانواده‌ای راهنمایی کنم که می‌توانند این نوزاد را برای شما کفالت کنند و خیرخواه او باشند؟!»'];
ayat[3265]=[28,13,'فَرَدَدْنَاهُ إِلَىٰ أُمِّهِ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ أَنَّ وَعْدَ اللَّهِ حَقٌّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','ما او را به مادرش بازگرداندیم تا چشمش روشن شود و غمگین نباشد و بداند که وعده الهی حق است؛ ولی بیشتر آنان نمی‌دانند!'];
ayat[3266]=[28,14,'وَلَمَّا بَلَغَ أَشُدَّهُ وَاسْتَوَىٰ آتَيْنَاهُ حُكْمًا وَعِلْمًا ۚ وَكَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','و هنگامی که (موسی) نیرومند و کامل شد، حکمت و دانش به او دادیم؛ و این گونه نیکوکاران را جزا می‌دهیم!'];
ayat[3267]=[28,15,'وَدَخَلَ الْمَدِينَةَ عَلَىٰ حِينِ غَفْلَةٍ مِّنْ أَهْلِهَا فَوَجَدَ فِيهَا رَجُلَيْنِ يَقْتَتِلَانِ هَٰذَا مِن شِيعَتِهِ وَهَٰذَا مِنْ عَدُوِّهِ ۖ فَاسْتَغَاثَهُ الَّذِي مِن شِيعَتِهِ عَلَى الَّذِي مِنْ عَدُوِّهِ فَوَكَزَهُ مُوسَىٰ فَقَضَىٰ عَلَيْهِ ۖ قَالَ هَٰذَا مِنْ عَمَلِ الشَّيْطَانِ ۖ إِنَّهُ عَدُوٌّ مُّضِلٌّ مُّبِينٌ','او به هنگامی که اهل شهر در غفلت بودند وارد شهر شد؛ ناگهان دو مرد را دید که به جنگ و نزاع مشغولند؛ یکی از پیروان او بود (و از بنی اسرائیل)، و دیگری از دشمنانش، آن که از پیروان او بود در برابر دشمنش از وی تقاضای کمک نمود؛ موسی مشت محکمی بر سینه او زد و کار او را ساخت (و بر زمین افتاد و مرد)؛ موسی گفت: «این (نزاع شما) از عمل شیطان بود، که او دشمن و گمراه‌کننده آشکاری است»'];
ayat[3268]=[28,16,'قَالَ رَبِّ إِنِّي ظَلَمْتُ نَفْسِي فَاغْفِرْ لِي فَغَفَرَ لَهُ ۚ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ','(سپس) عرض کرد: «پروردگارا! من به خویشتن ستم کردم؛ مرا ببخش!» خداوند او را بخشید، که او غفور و رحیم است!'];
ayat[3269]=[28,17,'قَالَ رَبِّ بِمَا أَنْعَمْتَ عَلَيَّ فَلَنْ أَكُونَ ظَهِيرًا لِّلْمُجْرِمِينَ','عرض کرد: «پروردگارا! بشکرانه نعمتی که به من دادی، هرگز پشتیبان مجرمان نخواهم بود!»'];
ayat[3270]=[28,18,'فَأَصْبَحَ فِي الْمَدِينَةِ خَائِفًا يَتَرَقَّبُ فَإِذَا الَّذِي اسْتَنصَرَهُ بِالْأَمْسِ يَسْتَصْرِخُهُ ۚ قَالَ لَهُ مُوسَىٰ إِنَّكَ لَغَوِيٌّ مُّبِينٌ','موسی در شهر ترسان بود و هر لحظه در انتظار حادثه‌ای (و در جستجوی اخبار)؛ ناگهان دید همان کسی که دیروز از او یاری طلبیده بود فریاد می‌زند و از او کمک می‌خواهد، موسی به او گفت: «تو آشکارا انسان (ماجراجو و) گمراهی هستی!»'];
ayat[3271]=[28,19,'فَلَمَّا أَنْ أَرَادَ أَن يَبْطِشَ بِالَّذِي هُوَ عَدُوٌّ لَّهُمَا قَالَ يَا مُوسَىٰ أَتُرِيدُ أَن تَقْتُلَنِي كَمَا قَتَلْتَ نَفْسًا بِالْأَمْسِ ۖ إِن تُرِيدُ إِلَّا أَن تَكُونَ جَبَّارًا فِي الْأَرْضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ الْمُصْلِحِينَ','و هنگامی که خواست با کسی که دشمن هر دوی آنها بود درگیر شود و با قدرت مانع او گردد، (فریادش بلند شد،) گفت: «ای موسی می‌خواهی! مرا بکشی همان گونه که دیروز انسانی را کشتی؟! تو فقط می‌خواهی جبّاری در روی زمین باشی، و نمی‌خواهی از مصلحان باشی!»'];
ayat[3272]=[28,20,'وَجَاءَ رَجُلٌ مِّنْ أَقْصَى الْمَدِينَةِ يَسْعَىٰ قَالَ يَا مُوسَىٰ إِنَّ الْمَلَأَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَاخْرُجْ إِنِّي لَكَ مِنَ النَّاصِحِينَ','(در این هنگام) مردی با سرعت از دورترین نقطه شهر [= مرکز فرعونیان‌] آمد و گفت: «ای موسی! این جمعیّت برای کشتن تو به مشورت نشسته‌اند؛ فوراً از شهر خارج شو، که من از خیرخواهان توام!»'];
ayat[3273]=[28,21,'فَخَرَجَ مِنْهَا خَائِفًا يَتَرَقَّبُ ۖ قَالَ رَبِّ نَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ','موسی از شهر خارج شد در حالی که ترسان بود و هر لحظه در انتظار حادثه‌ای؛ عرض کرد: «پروردگارا! مرا از این قوم ظالم رهایی بخش!»'];
ayat[3274]=[28,22,'وَلَمَّا تَوَجَّهَ تِلْقَاءَ مَدْيَنَ قَالَ عَسَىٰ رَبِّي أَن يَهْدِيَنِي سَوَاءَ السَّبِيلِ','و هنگامی که متوجّه جانب مدین شد گفت: «امیدوارم پروردگارم مرا به راه راست هدایت کند!»'];
ayat[3275]=[28,23,'وَلَمَّا وَرَدَ مَاءَ مَدْيَنَ وَجَدَ عَلَيْهِ أُمَّةً مِّنَ النَّاسِ يَسْقُونَ وَوَجَدَ مِن دُونِهِمُ امْرَأَتَيْنِ تَذُودَانِ ۖ قَالَ مَا خَطْبُكُمَا ۖ قَالَتَا لَا نَسْقِي حَتَّىٰ يُصْدِرَ الرِّعَاءُ ۖ وَأَبُونَا شَيْخٌ كَبِيرٌ','و هنگامی که به (چاه) آب مدین رسید، گروهی از مردم را در آنجا دید که چهارپایان خود را سیراب می‌کنند؛ و در کنار آنان دو زن را دید که مراقب گوسفندان خویشند (و به چاه نزدیک نمی‌شوند؛ موسی) به آن دو گفت: «کار شما چیست؟ (چرا گوسفندان خود را آب نمی‌دهید؟!)» گفتند: «ما آنها را آب نمی‌دهیم تا چوپانها همگی خارج شوند؛ و پدر ما پیرمرد کهنسالی است (و قادر بر این کارها نیست.)!»'];
ayat[3276]=[28,24,'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰ إِلَى الظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَا أَنزَلْتَ إِلَيَّ مِنْ خَيْرٍ فَقِيرٌ','موسی برای (گوسفندان) آن دو آب کشید؛ سپس رو به سایه آورد و عرض کرد: «پروردگارا! هر خیر و نیکی بر من فرستی، به آن نیازمندم!»'];
ayat[3277]=[28,25,'فَجَاءَتْهُ إِحْدَاهُمَا تَمْشِي عَلَى اسْتِحْيَاءٍ قَالَتْ إِنَّ أَبِي يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ مَا سَقَيْتَ لَنَا ۚ فَلَمَّا جَاءَهُ وَقَصَّ عَلَيْهِ الْقَصَصَ قَالَ لَا تَخَفْ ۖ نَجَوْتَ مِنَ الْقَوْمِ الظَّالِمِينَ','ناگهان یکی از آن دو (زن) به سراغ او آمد در حالی که با نهایت حیا گام برمی‌داشت، گفت: «پدرم از تو دعوت می‌کند تا مزد آب دادن (به گوسفندان) را که برای ما انجام دادی به تو بپردازد.» هنگامی که موسی نزد او [= شعیب‌] آمد و سرگذشت خود را شرح داد، گفت: «نترس، از قوم ظالم نجات یافتی!»'];
ayat[3278]=[28,26,'قَالَتْ إِحْدَاهُمَا يَا أَبَتِ اسْتَأْجِرْهُ ۖ إِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْأَمِينُ','یکی از آن دو (دختر) گفت: «پدرم! او را استخدام کن، زیرا بهترین کسی را که می‌توانی استخدام کنی آن کسی است که قویّ و امین باشد (و او همین مرد است)!»'];
ayat[3279]=[28,27,'قَالَ إِنِّي أُرِيدُ أَنْ أُنكِحَكَ إِحْدَى ابْنَتَيَّ هَاتَيْنِ عَلَىٰ أَن تَأْجُرَنِي ثَمَانِيَ حِجَجٍ ۖ فَإِنْ أَتْمَمْتَ عَشْرًا فَمِنْ عِندِكَ ۖ وَمَا أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ ۚ سَتَجِدُنِي إِن شَاءَ اللَّهُ مِنَ الصَّالِحِينَ','(شعیب) گفت: «من می‌خواهم یکی از این دو دخترم را به همسری تو درآورم به این شرط که هشت سال برای من کار کنی؛ و اگر آن را تا ده سال افزایش دهی، محبّتی از ناحیه توست؛ من نمی‌خواهم کار سنگینی بر دوش تو بگذارم؛ و ان شاء الله مرا از صالحان خواهی یافت»'];
ayat[3280]=[28,28,'قَالَ ذَٰلِكَ بَيْنِي وَبَيْنَكَ ۖ أَيَّمَا الْأَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّ ۖ وَاللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٌ','(موسی) گفت: «(مانعی ندارد،) این قراردادی میان من و تو باشد؛ البته هر کدام از این دو مدّت را انجام دهم ستمی بر من نخواهد بود (و من در انتخاب آن آزادم)! و خدا بر آنچه ما می‌گوییم گواه است!»'];
ayat[3281]=[28,29,'۞ فَلَمَّا قَضَىٰ مُوسَى الْأَجَلَ وَسَارَ بِأَهْلِهِ آنَسَ مِن جَانِبِ الطُّورِ نَارًا قَالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نَارًا لَّعَلِّي آتِيكُم مِّنْهَا بِخَبَرٍ أَوْ جَذْوَةٍ مِّنَ النَّارِ لَعَلَّكُمْ تَصْطَلُونَ','هنگامی که موسی مدّت خود را به پایان رسانید و همراه خانواده‌اش (از مدین به سوی مصر) حرکت کرد، از جانب طور آتشی دید! به خانواده‌اش گفت: «درنگ کنید که من آتشی دیدم! (می‌روم) شاید خبری از آن برای شما بیاورم، یا شعله‌ای از آتش تا با آن گرم شوید!»'];
ayat[3282]=[28,30,'فَلَمَّا أَتَاهَا نُودِيَ مِن شَاطِئِ الْوَادِ الْأَيْمَنِ فِي الْبُقْعَةِ الْمُبَارَكَةِ مِنَ الشَّجَرَةِ أَن يَا مُوسَىٰ إِنِّي أَنَا اللَّهُ رَبُّ الْعَالَمِينَ','هنگامی که به سراغ آتش آمد، از کرانه راست درّه، در آن سرزمین پر برکت، از میان یک درخت ندا داده شد که: «ای موسی! منم خداوند، پروردگار جهانیان!'];
ayat[3283]=[28,31,'وَأَنْ أَلْقِ عَصَاكَ ۖ فَلَمَّا رَآهَا تَهْتَزُّ كَأَنَّهَا جَانٌّ وَلَّىٰ مُدْبِرًا وَلَمْ يُعَقِّبْ ۚ يَا مُوسَىٰ أَقْبِلْ وَلَا تَخَفْ ۖ إِنَّكَ مِنَ الْآمِنِينَ','عصایت را بیفکن!» هنگامی که (عصا را افکند و) دید همچون ماری با سرعت حرکت می‌کند، ترسید و به عقب برگشت، و حتّی پشت سر خود را نگاه نکرد! ندا آمد: «برگرد و نترس، تو در امان هستی!'];
ayat[3284]=[28,32,'اسْلُكْ يَدَكَ فِي جَيْبِكَ تَخْرُجْ بَيْضَاءَ مِنْ غَيْرِ سُوءٍ وَاضْمُمْ إِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ ۖ فَذَانِكَ بُرْهَانَانِ مِن رَّبِّكَ إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ ۚ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ','دستت را در گریبان خود فروبر، هنگامی که خارج می‌شود سفید و درخشنده است بدون عیب و نقص؛ و دستهایت را بر سینه‌ات بگذار، تا ترس و وحشت از تو دور شود! این دو [= معجزه عصا و ید بیضا] برهان روشن از پروردگارت بسوی فرعون و اطرافیان اوست، که آنان قوم فاسقی هستند!»'];
ayat[3285]=[28,33,'قَالَ رَبِّ إِنِّي قَتَلْتُ مِنْهُمْ نَفْسًا فَأَخَافُ أَن يَقْتُلُونِ','عرض کرد: «پروردگارا! من یک تن از آنان را کشته‌ام؛ می‌ترسم مرا به قتل برسانند!'];
ayat[3286]=[28,34,'وَأَخِي هَارُونُ هُوَ أَفْصَحُ مِنِّي لِسَانًا فَأَرْسِلْهُ مَعِيَ رِدْءًا يُصَدِّقُنِي ۖ إِنِّي أَخَافُ أَن يُكَذِّبُونِ','و برادرم هارون زبانش از من فصیحتر است؛ او را همراه من بفرست تا یاور من باشد و مرا تصدیق کند؛ می‌ترسم مرا تکذیب کنند!»'];
ayat[3287]=[28,35,'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجْعَلُ لَكُمَا سُلْطَانًا فَلَا يَصِلُونَ إِلَيْكُمَا ۚ بِآيَاتِنَا أَنتُمَا وَمَنِ اتَّبَعَكُمَا الْغَالِبُونَ','فرمود: «بزودی بازوان تو را بوسیله برادرت محکم (و نیرومند) می‌کنیم، و برای شما سلطه و برتری قرارمی‌دهیم؛ و به برکت آیات ما، بر شما دست نمی‌یابند؛ شما و پیروانتان پیروزید!»'];
ayat[3288]=[28,36,'فَلَمَّا جَاءَهُم مُّوسَىٰ بِآيَاتِنَا بَيِّنَاتٍ قَالُوا مَا هَٰذَا إِلَّا سِحْرٌ مُّفْتَرًى وَمَا سَمِعْنَا بِهَٰذَا فِي آبَائِنَا الْأَوَّلِينَ','هنگامی که موسی معجزات روشن ما را برای آنان آورد، گفتند: «این چیزی جز سحر نیست که بدروغ به خدا بسته شده؛ ما هرگز چنین چیزی را در نیاکان خود نشنیده‌ایم!»'];
ayat[3289]=[28,37,'وَقَالَ مُوسَىٰ رَبِّي أَعْلَمُ بِمَن جَاءَ بِالْهُدَىٰ مِنْ عِندِهِ وَمَن تَكُونُ لَهُ عَاقِبَةُ الدَّارِ ۖ إِنَّهُ لَا يُفْلِحُ الظَّالِمُونَ','موسی گفت: «پروردگارم از حال کسانی که هدایت را از نزد او آورده‌اند، و کسانی که عاقبت نیک سرا (ی دنیا و آخرت) از آن آنهاست آگاهتر است! مسلّماً ظالمان رستگار نخواهند شد!»'];
ayat[3290]=[28,38,'وَقَالَ فِرْعَوْنُ يَا أَيُّهَا الْمَلَأُ مَا عَلِمْتُ لَكُم مِّنْ إِلَٰهٍ غَيْرِي فَأَوْقِدْ لِي يَا هَامَانُ عَلَى الطِّينِ فَاجْعَل لِّي صَرْحًا لَّعَلِّي أَطَّلِعُ إِلَىٰ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُ مِنَ الْكَاذِبِينَ','فرعون گفت: «ای جمعیت اشراف! من خدایی جز خودم برای شما سراغ ندارم. (امّا برای تحقیق بیشتر،) ای هامان، برایم آتشی بر گل بیفروز (و آجرهای محکم بساز)، و برای من برج بلندی ترتیب ده تا از خدای موسی خبر گیرم؛ هر چند من گمان می‌کنم او از دروغگویان است!»'];
ayat[3291]=[28,39,'وَاسْتَكْبَرَ هُوَ وَجُنُودُهُ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوا أَنَّهُمْ إِلَيْنَا لَا يُرْجَعُونَ','(سرانجام) فرعون و لشکریانش بدون حقّ در زمین استکبار کردند، و پنداشتند بسوی ما بازگردانده نمی‌شوند!'];
ayat[3292]=[28,40,'فَأَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ ۖ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الظَّالِمِينَ','ما نیز او و لشکریانش را گرفتیم و به دریا افکندیم؛ اکنون بنگر پایان کار ظالمان چگونه بود!'];
ayat[3293]=[28,41,'وَجَعَلْنَاهُمْ أَئِمَّةً يَدْعُونَ إِلَى النَّارِ ۖ وَيَوْمَ الْقِيَامَةِ لَا يُنصَرُونَ','و آنان [= فرعونیان‌] را پیشوایانی قرار دادیم که به آتش (دوزخ) دعوت می‌کنند؛ و روز رستاخیز یاری نخواهند شد!'];
ayat[3294]=[28,42,'وَأَتْبَعْنَاهُمْ فِي هَٰذِهِ الدُّنْيَا لَعْنَةً ۖ وَيَوْمَ الْقِيَامَةِ هُم مِّنَ الْمَقْبُوحِينَ','و در این دنیا نیز لعنتی بدنبال آنان قرار دادیم؛ و روز قیامت از زشت‌رویانند!'];
ayat[3295]=[28,43,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ مِن بَعْدِ مَا أَهْلَكْنَا الْقُرُونَ الْأُولَىٰ بَصَائِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لَّعَلَّهُمْ يَتَذَكَّرُونَ','و ما به موسی کتاب آسمانی دادیم بعد از آنکه اقوام قرون نخستین را هلاک نمودیم؛ کتابی که برای مردم بصیرت‌آفرین بود، و مایه هدایت و رحمت؛ شاید متذکر شوند!'];
ayat[3296]=[28,44,'وَمَا كُنتَ بِجَانِبِ الْغَرْبِيِّ إِذْ قَضَيْنَا إِلَىٰ مُوسَى الْأَمْرَ وَمَا كُنتَ مِنَ الشَّاهِدِينَ','تو در جانب غربی نبودی هنگامی که ما فرمان نبوّت را به موسی دادیم؛ و تو از شاهدان نبودی (در آن هنگام که معجزات را در اختیار موسی گذاردیم)!'];
ayat[3297]=[28,45,'وَلَٰكِنَّا أَنشَأْنَا قُرُونًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُ ۚ وَمَا كُنتَ ثَاوِيًا فِي أَهْلِ مَدْيَنَ تَتْلُو عَلَيْهِمْ آيَاتِنَا وَلَٰكِنَّا كُنَّا مُرْسِلِينَ','ولی ما اقوامی را در اعصار مختلف خلق کردیم، و زمانهای طولانی بر آنها گذشت (که آثار انبیا از دلهایشان محو شد؛ پس تو را با کتاب آسمانیت فرستادیم)! تو هرگز در میان مردم مدین اقامت نداشتی تا (از وضع آنان آگاه باشی و) آیات ما را برای آنها [= مشرکان مکّه‌] بخوانی، ولی ما بودیم که تو را فرستادیم (و این آیات را در اختیارت قرار دادیم)!'];
ayat[3298]=[28,46,'وَمَا كُنتَ بِجَانِبِ الطُّورِ إِذْ نَادَيْنَا وَلَٰكِن رَّحْمَةً مِّن رَّبِّكَ لِتُنذِرَ قَوْمًا مَّا أَتَاهُم مِّن نَّذِيرٍ مِّن قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ','تو در کنار طور نبودی زمانی که ما ندا دادیم؛ ولی این رحمتی از سوی پروردگارت بود (که این اخبار را در اختیار تو نهاد) تا بوسیله آن قومی را انذار کنی که پیش از تو هیچ انذارکننده‌ای برای آنان نیامده است؛ شاید متذکّر شوند!'];
ayat[3299]=[28,47,'وَلَوْلَا أَن تُصِيبَهُم مُّصِيبَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَيَقُولُوا رَبَّنَا لَوْلَا أَرْسَلْتَ إِلَيْنَا رَسُولًا فَنَتَّبِعَ آيَاتِكَ وَنَكُونَ مِنَ الْمُؤْمِنِينَ','هرگاه (پیش از فرستادن پیامبری) مجازات و مصیبتی بر اثر اعمالشان به آنان می‌رسید، می‌گفتند: «پروردگارا! چرا رسولی برای ما نفرستادی تا از آیات تو پیروی کنیم و از مؤمنان باشیم؟!»'];
ayat[3300]=[28,48,'فَلَمَّا جَاءَهُمُ الْحَقُّ مِنْ عِندِنَا قَالُوا لَوْلَا أُوتِيَ مِثْلَ مَا أُوتِيَ مُوسَىٰ ۚ أَوَلَمْ يَكْفُرُوا بِمَا أُوتِيَ مُوسَىٰ مِن قَبْلُ ۖ قَالُوا سِحْرَانِ تَظَاهَرَا وَقَالُوا إِنَّا بِكُلٍّ كَافِرُونَ','ولی هنگامی که حقّ از نزد ما برای آنها آمد گفتند: «چرا مثل همان چیزی که به موسی داده شد به این پیامبر داده نشده است؟!» مگر بهانه‌جویانی همانند آنان، معجزاتی را که در گذشته به موسی داده شد، انکار نکردند و گفتند: «این دو نفر [= موسی و هارون‌] دو ساحرند که دست به دست هم داده‌اند (تا ما را گمراه کنند) و ما به هر دو کافریم»؟!'];
ayat[3301]=[28,49,'قُلْ فَأْتُوا بِكِتَابٍ مِّنْ عِندِ اللَّهِ هُوَ أَهْدَىٰ مِنْهُمَا أَتَّبِعْهُ إِن كُنتُمْ صَادِقِينَ','بگو: «اگر راست می‌گویید (که تورات و قرآن از سوی خدا نیست)، کتابی هدایت‌بخش‌تر از این دو از نزد خدا بیاورید، تا من از آن پیروی کنم!»'];
ayat[3302]=[28,50,'فَإِن لَّمْ يَسْتَجِيبُوا لَكَ فَاعْلَمْ أَنَّمَا يَتَّبِعُونَ أَهْوَاءَهُمْ ۚ وَمَنْ أَضَلُّ مِمَّنِ اتَّبَعَ هَوَاهُ بِغَيْرِ هُدًى مِّنَ اللَّهِ ۚ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','اگر این پیشنهاد تو را نپذیرند، بدان که آنان تنها از هوسهای خود پیروی می‌کنند! و آیا گمراهتر از آن کس که پیروی هوای نفس خویش کرده و هیچ هدایت الهی را نپذیرفته، کسی پیدا می‌شود؟! مسلّماً خداوند قوم ستمگر را هدایت نمی‌کند!'];
ayat[3303]=[28,51,'۞ وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَ','ما آیات قرآن را یکی پس از دیگری برای آنان آوردیم شاید متذکّر شوند!'];
ayat[3304]=[28,52,'الَّذِينَ آتَيْنَاهُمُ الْكِتَابَ مِن قَبْلِهِ هُم بِهِ يُؤْمِنُونَ','کسانی که قبلاً کتاب آسمانی به آنان داده‌ایم به آن [= قرآن‌] ایمان می‌آورند!'];
ayat[3305]=[28,53,'وَإِذَا يُتْلَىٰ عَلَيْهِمْ قَالُوا آمَنَّا بِهِ إِنَّهُ الْحَقُّ مِن رَّبِّنَا إِنَّا كُنَّا مِن قَبْلِهِ مُسْلِمِينَ','و هنگامی که بر آنان خوانده شود می‌گویند: «به آن ایمان آوردیم؛ اینها همه حق است و از سوی پروردگار ماست؛ ما پیش از این هم مسلمان بودیم!»'];
ayat[3306]=[28,54,'أُولَٰئِكَ يُؤْتَوْنَ أَجْرَهُم مَّرَّتَيْنِ بِمَا صَبَرُوا وَيَدْرَءُونَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ','آنها کسانی هستند که بخاطر شکیباییشان، اجر و پادششان را دو بار دریافت می‌دارند؛ و بوسیله نیکیها بدیها را دفع می‌کنند؛ و از آنچه به آنان روزی داده‌ایم انفاق می‌نمایند؛'];
ayat[3307]=[28,55,'وَإِذَا سَمِعُوا اللَّغْوَ أَعْرَضُوا عَنْهُ وَقَالُوا لَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ سَلَامٌ عَلَيْكُمْ لَا نَبْتَغِي الْجَاهِلِينَ','و هرگاه سخن لغو و بیهوده بشنوند، از آن روی می‌گردانند و می‌گویند: «اعمال ما از آن ماست و اعمال شما از آن خودتان؛ سلام بر شما (سلام وداع)؛ ما خواهان جاهلان نیستیم!»'];
ayat[3308]=[28,56,'إِنَّكَ لَا تَهْدِي مَنْ أَحْبَبْتَ وَلَٰكِنَّ اللَّهَ يَهْدِي مَن يَشَاءُ ۚ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ','تو نمی‌توانی کسی را که دوست داری هدایت کنی؛ ولی خداوند هر کس را بخواهد هدایت می‌کند؛ و او به هدایت یافتگان آگاهتر است!'];
ayat[3309]=[28,57,'وَقَالُوا إِن نَّتَّبِعِ الْهُدَىٰ مَعَكَ نُتَخَطَّفْ مِنْ أَرْضِنَا ۚ أَوَلَمْ نُمَكِّن لَّهُمْ حَرَمًا آمِنًا يُجْبَىٰ إِلَيْهِ ثَمَرَاتُ كُلِّ شَيْءٍ رِّزْقًا مِّن لَّدُنَّا وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','آنها گفتند: «ما اگر هدایت را همراه تو پذیرا شویم، ما را از سرزمینمان می‌ربایند!» آیا ما حرم امنی در اختیار آنها قرار ندادیم که ثمرات هر چیزی (از هر شهر و دیاری) بسوی آن آورده می‌شود؟! رزقی است از جانب ما؛ ولی بیشتر آنان نمی‌دانند!'];
ayat[3310]=[28,58,'وَكَمْ أَهْلَكْنَا مِن قَرْيَةٍ بَطِرَتْ مَعِيشَتَهَا ۖ فَتِلْكَ مَسَاكِنُهُمْ لَمْ تُسْكَن مِّن بَعْدِهِمْ إِلَّا قَلِيلًا ۖ وَكُنَّا نَحْنُ الْوَارِثِينَ','و چه بسیار از شهرها و آبادیهایی را که بر اثر فراوانی نعمت، مست و مغرور شده بودند هلاک کردیم! این خانه‌های آنهاست (که ویران شده)، و بعد از آنان جز اندکی کسی در آنها سکونت نکرد؛ و ما وارث آنان بودیم!'];
ayat[3311]=[28,59,'وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرَىٰ حَتَّىٰ يَبْعَثَ فِي أُمِّهَا رَسُولًا يَتْلُو عَلَيْهِمْ آيَاتِنَا ۚ وَمَا كُنَّا مُهْلِكِي الْقُرَىٰ إِلَّا وَأَهْلُهَا ظَالِمُونَ','و پروردگار تو هرگز شهرها و آبادیها را هلاک نمی‌کرد تا اینکه در کانون آنها پیامبری مبعوث کند که آیات ما را بر آنان بخواند؛ و ما هرگز آبادیها و شهرها را هلاک نکردیم مگر آنکه اهلش ظالم بودند!'];
ayat[3312]=[28,60,'وَمَا أُوتِيتُم مِّن شَيْءٍ فَمَتَاعُ الْحَيَاةِ الدُّنْيَا وَزِينَتُهَا ۚ وَمَا عِندَ اللَّهِ خَيْرٌ وَأَبْقَىٰ ۚ أَفَلَا تَعْقِلُونَ','آنچه به شما داده شده، متاع زندگی دنیا و زینت آن است؛ و آنچه نزد خداست بهتر و پایدارتر است؛ آیا اندیشه نمی‌کنید؟!'];
ayat[3313]=[28,61,'أَفَمَن وَعَدْنَاهُ وَعْدًا حَسَنًا فَهُوَ لَاقِيهِ كَمَن مَّتَّعْنَاهُ مَتَاعَ الْحَيَاةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيَامَةِ مِنَ الْمُحْضَرِينَ','آیا کسی که به او وعده نیکو داده‌ایم و به آن خواهد رسید، همانند کسی است که متاع زندگی دنیا به او داده‌ایم سپس روز قیامت (برای حساب و جزا) از احضارشدگان خواهد بود؟!'];
ayat[3314]=[28,62,'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنتُمْ تَزْعُمُونَ','روزی را (به خاطر بیاورید) که خداوند آنان را ندا میدهد و میگوید: «کجا هستند همتایانی که برای من می‌پنداشتید؟!»'];
ayat[3315]=[28,63,'قَالَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هَٰؤُلَاءِ الَّذِينَ أَغْوَيْنَا أَغْوَيْنَاهُمْ كَمَا غَوَيْنَا ۖ تَبَرَّأْنَا إِلَيْكَ ۖ مَا كَانُوا إِيَّانَا يَعْبُدُونَ','گروهی (از معبودان) که فرمان عذاب درباره آنها مسلم شده است میگویند: «پروردگارا! ما اینها [= عابدان‌] را گمراه کردیم؛ (آری) ما آنها را گمراه کردیم همان‌گونه که خودمان گمراه شدیم؛ ما از آنان به سوی تو بیزاری می‌جوییم؛ آنان در حقیقت ما را نمی‌پرستیدند (بلکه هوای نفس خود را پرستش می‌کردند)!»'];
ayat[3316]=[28,64,'وَقِيلَ ادْعُوا شُرَكَاءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَرَأَوُا الْعَذَابَ ۚ لَوْ أَنَّهُمْ كَانُوا يَهْتَدُونَ','و به آنها [= عابدان‌] گفته می‌شود: «معبودهایتان را که همتای خدا می‌پنداشتید بخوانید (تا شما را یاری کنند)!» معبودهایشان را می‌خوانند، ولی جوابی به آنان نمی‌دهند! و (در این هنگام) عذاب الهی را (با چشم خود) می‌بینند، و آرزو می‌کنند ای کاش هدایت یافته بودند!'];
ayat[3317]=[28,65,'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ مَاذَا أَجَبْتُمُ الْمُرْسَلِينَ','(به خاطر آورید) روزی را که خداوند آنان را ندا می‌دهد و می‌گوید: «چه پاسخی به پیامبران (من) گفتید؟!»'];
ayat[3318]=[28,66,'فَعَمِيَتْ عَلَيْهِمُ الْأَنبَاءُ يَوْمَئِذٍ فَهُمْ لَا يَتَسَاءَلُونَ','در آن روز، همه اخبار به آنان پوشیده می‌ماند، (حتی نمی‌توانند) از یکدیگر سؤالی کنند!'];
ayat[3319]=[28,67,'فَأَمَّا مَن تَابَ وَآمَنَ وَعَمِلَ صَالِحًا فَعَسَىٰ أَن يَكُونَ مِنَ الْمُفْلِحِينَ','اما کسی که توبه کند، و ایمان آورد و عمل صالحی انجام دهد، امید است از رستگاران باشد!'];
ayat[3320]=[28,68,'وَرَبُّكَ يَخْلُقُ مَا يَشَاءُ وَيَخْتَارُ ۗ مَا كَانَ لَهُمُ الْخِيَرَةُ ۚ سُبْحَانَ اللَّهِ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ','پروردگار تو هر چه بخواهد می‌آفریند، و هر چه بخواهد برمی‌گزیند؛ آنان (در برابر او) اختیاری ندارند؛ منزه است خداوند، و برتر است از همتایانی که برای او قائل می‌شوند!'];
ayat[3321]=[28,69,'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ','و پروردگار تو می‌داند آنچه را که سینه‌هایشان پنهان می‌دارد و آنچه را آشکار می‌سازند!'];
ayat[3322]=[28,70,'وَهُوَ اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۖ لَهُ الْحَمْدُ فِي الْأُولَىٰ وَالْآخِرَةِ ۖ وَلَهُ الْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ','و او خدایی است که معبودی جز او نیست؛ ستایش برای اوست در این جهان و در جهان دیگر؛ حاکمیت (نیز) از آن اوست؛ و همه شما به سوی او بازگردانده می‌شوید!'];
ayat[3323]=[28,71,'قُلْ أَرَأَيْتُمْ إِن جَعَلَ اللَّهُ عَلَيْكُمُ اللَّيْلَ سَرْمَدًا إِلَىٰ يَوْمِ الْقِيَامَةِ مَنْ إِلَٰهٌ غَيْرُ اللَّهِ يَأْتِيكُم بِضِيَاءٍ ۖ أَفَلَا تَسْمَعُونَ','بگو: «به من خبر دهید اگر خداوند شب را تا قیامت بر شما جاودان سازد، آیا معبودی جز خدا می‌تواند روشنایی برای شما بیاورد؟! آیا نمی‌شنوید؟!»'];
ayat[3324]=[28,72,'قُلْ أَرَأَيْتُمْ إِن جَعَلَ اللَّهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا إِلَىٰ يَوْمِ الْقِيَامَةِ مَنْ إِلَٰهٌ غَيْرُ اللَّهِ يَأْتِيكُم بِلَيْلٍ تَسْكُنُونَ فِيهِ ۖ أَفَلَا تُبْصِرُونَ','بگو: «به من خبر دهید اگر خداوند روز را تا قیامت بر شما جاودان کند، کدام معبود غیر از خداست که شبی برای شما بیاورد تا در آن آرامش یابید؟ آیا نمی‌بینید؟!»'];
ayat[3325]=[28,73,'وَمِن رَّحْمَتِهِ جَعَلَ لَكُمُ اللَّيْلَ وَالنَّهَارَ لِتَسْكُنُوا فِيهِ وَلِتَبْتَغُوا مِن فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ','و از رحمت اوست که برای شما شب و روز قرار داد تا هم در آن آرامش داشته باشید و هم برای بهره‌گیری از فضل خدا تلاش کنید، و شاید شکر نعمت او را بجا آورید!'];
ayat[3326]=[28,74,'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَائِيَ الَّذِينَ كُنتُمْ تَزْعُمُونَ','(به خاطر آورید) روزی را که آنها را ندا می‌دهد و می‌گوید: «کجایند همتایانی که برای من می‌پنداشتید؟!»'];
ayat[3327]=[28,75,'وَنَزَعْنَا مِن كُلِّ أُمَّةٍ شَهِيدًا فَقُلْنَا هَاتُوا بُرْهَانَكُمْ فَعَلِمُوا أَنَّ الْحَقَّ لِلَّهِ وَضَلَّ عَنْهُم مَّا كَانُوا يَفْتَرُونَ','(در آن روز) از هر امتی گواهی برمی‌گزینیم و (به مشرکان) می‌گوییم: «دلیل خود را بیاورید!» اما آنها می‌دانند که حق از آن خداست، و تمام آنچه را افترا می‌بستند از (نظر) آنها گم خواهد شد!'];
ayat[3328]=[28,76,'۞ إِنَّ قَارُونَ كَانَ مِن قَوْمِ مُوسَىٰ فَبَغَىٰ عَلَيْهِمْ ۖ وَآتَيْنَاهُ مِنَ الْكُنُوزِ مَا إِنَّ مَفَاتِحَهُ لَتَنُوءُ بِالْعُصْبَةِ أُولِي الْقُوَّةِ إِذْ قَالَ لَهُ قَوْمُهُ لَا تَفْرَحْ ۖ إِنَّ اللَّهَ لَا يُحِبُّ الْفَرِحِينَ','قارون از قوم موسی بود، اما بر آنان ستم کرد؛ ما آنقدر از گنجها به او داده بودیم که حمل کلیدهای آن برای یک گروه زورمند مشکل بود! (به خاطر آورید) هنگامی را که قومش به او گفتند: «این همه شادی مغرورانه مکن، که خداوند شادی‌کنندگان مغرور را دوست نمی‌دارد!'];
ayat[3329]=[28,77,'وَابْتَغِ فِيمَا آتَاكَ اللَّهُ الدَّارَ الْآخِرَةَ ۖ وَلَا تَنسَ نَصِيبَكَ مِنَ الدُّنْيَا ۖ وَأَحْسِن كَمَا أَحْسَنَ اللَّهُ إِلَيْكَ ۖ وَلَا تَبْغِ الْفَسَادَ فِي الْأَرْضِ ۖ إِنَّ اللَّهَ لَا يُحِبُّ الْمُفْسِدِينَ','و در آنچه خدا به تو داده، سرای آخرت را بطلب؛ و بهره‌ات را از دنیا فراموش مکن؛ و همان‌گونه که خدا به تو نیکی کرده نیکی کن؛ و هرگز در زمین در جستجوی فساد مباش، که خدا مفسدان را دوست ندارد!'];
ayat[3330]=[28,78,'قَالَ إِنَّمَا أُوتِيتُهُ عَلَىٰ عِلْمٍ عِندِي ۚ أَوَلَمْ يَعْلَمْ أَنَّ اللَّهَ قَدْ أَهْلَكَ مِن قَبْلِهِ مِنَ الْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةً وَأَكْثَرُ جَمْعًا ۚ وَلَا يُسْأَلُ عَن ذُنُوبِهِمُ الْمُجْرِمُونَ','(قارون) گفت: «این ثروت را بوسیله دانشی که نزد من است به دست آورده‌ام!» آیا او نمی‌دانست که خداوند اقوامی را پیش از او هلاک کرد که نیرومندتر و ثروتمندتر از او بودند؟! (و هنگامی که عذاب الهی فرا رسد،) مجرمان از گناهانشان سؤال نمی‌شوند.'];
ayat[3331]=[28,79,'فَخَرَجَ عَلَىٰ قَوْمِهِ فِي زِينَتِهِ ۖ قَالَ الَّذِينَ يُرِيدُونَ الْحَيَاةَ الدُّنْيَا يَا لَيْتَ لَنَا مِثْلَ مَا أُوتِيَ قَارُونُ إِنَّهُ لَذُو حَظٍّ عَظِيمٍ','(روزی قارون) با تمام زینت خود در برابر قومش ظاهر شد، آنها که خواهان زندگی دنیا بودند گفتند: «ای کاش همانند آنچه به قارون داده شده است ما نیز داشتیم! به راستی که او بهره عظیمی دارد!»'];
ayat[3332]=[28,80,'وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللَّهِ خَيْرٌ لِّمَنْ آمَنَ وَعَمِلَ صَالِحًا وَلَا يُلَقَّاهَا إِلَّا الصَّابِرُونَ','اما کسانی که علم و دانش به آنها داده شده بود گفتند: «وای بر شما ثواب الهی برای کسانی که ایمان آورده‌اند و عمل صالح انجام می‌دهند بهتر است، اما جز صابران آن را دریافت نمی‌کنند.»'];
ayat[3333]=[28,81,'فَخَسَفْنَا بِهِ وَبِدَارِهِ الْأَرْضَ فَمَا كَانَ لَهُ مِن فِئَةٍ يَنصُرُونَهُ مِن دُونِ اللَّهِ وَمَا كَانَ مِنَ الْمُنتَصِرِينَ','سپس ما، او و خانه‌اش را در زمین فرو بردیم، و گروهی نداشت که او را در برابر عذاب الهی یاری کنند، و خود نیز نمی‌توانست خویشتن را یاری دهد.'];
ayat[3334]=[28,82,'وَأَصْبَحَ الَّذِينَ تَمَنَّوْا مَكَانَهُ بِالْأَمْسِ يَقُولُونَ وَيْكَأَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ ۖ لَوْلَا أَن مَّنَّ اللَّهُ عَلَيْنَا لَخَسَفَ بِنَا ۖ وَيْكَأَنَّهُ لَا يُفْلِحُ الْكَافِرُونَ','و آنها که دیروز آرزو می‌کردند بجای او باشند (هنگامی که این صحنه را دیدند) گفتند: «وای بر ما! گویی خدا روزی را بر هر کس از بندگانش بخواهد گسترش می‌دهد یا تنگ می‌گیرد! اگر خدا بر ما منت ننهاده بود، ما را نیز به قعر زمین فرو می برد! ای وای گویی کافران هرگز رستگار نمی‌شوند!'];
ayat[3335]=[28,83,'تِلْكَ الدَّارُ الْآخِرَةُ نَجْعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوًّا فِي الْأَرْضِ وَلَا فَسَادًا ۚ وَالْعَاقِبَةُ لِلْمُتَّقِينَ','(آری،) این سرای آخرت را (تنها) برای کسانی قرارمی‌دهیم که اراده برتری‌جویی در زمین و فساد را ندارند؛ و عاقبت نیک برای پرهیزگاران است!'];
ayat[3336]=[28,84,'مَن جَاءَ بِالْحَسَنَةِ فَلَهُ خَيْرٌ مِّنْهَا ۖ وَمَن جَاءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذِينَ عَمِلُوا السَّيِّئَاتِ إِلَّا مَا كَانُوا يَعْمَلُونَ','کسی که کار نیکی انجام دهد، برای او پاداشی بهتر از آن است؛ و به کسانی که کارهای بد انجام دهند، مجازات بدکاران جز (به مقدار) اعمالشان نخواهد بود.'];
ayat[3337]=[28,85,'إِنَّ الَّذِي فَرَضَ عَلَيْكَ الْقُرْآنَ لَرَادُّكَ إِلَىٰ مَعَادٍ ۚ قُل رَّبِّي أَعْلَمُ مَن جَاءَ بِالْهُدَىٰ وَمَنْ هُوَ فِي ضَلَالٍ مُّبِينٍ','آن کس که قرآن را بر تو فرض کرد، تو را به جایگاهت [= زادگاهت‌] بازمی‌گرداند! بگو: «پروردگار من از همه بهتر می‌داند چه کسی (برنامه) هدایت آورده، و چه کسی در گمراهی آشکار است!»'];
ayat[3338]=[28,86,'وَمَا كُنتَ تَرْجُو أَن يُلْقَىٰ إِلَيْكَ الْكِتَابُ إِلَّا رَحْمَةً مِّن رَّبِّكَ ۖ فَلَا تَكُونَنَّ ظَهِيرًا لِّلْكَافِرِينَ','و تو هرگز امید نداشتی که این کتاب آسمانی به تو القا گردد؛ ولی رحمت پروردگارت چنین ایجاب کرد! اکنون که چنین است، هرگز از کافران پشتیبانی مکن!'];
ayat[3339]=[28,87,'وَلَا يَصُدُّنَّكَ عَنْ آيَاتِ اللَّهِ بَعْدَ إِذْ أُنزِلَتْ إِلَيْكَ ۖ وَادْعُ إِلَىٰ رَبِّكَ ۖ وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ','و هرگز (آنها) تو را از آیات خداوند، بعد از آنکه بر تو نازل گشت، بازندارند! و بسوی پروردگارت دعوت کن، و هرگز از مشرکان مباش!'];
ayat[3340]=[28,88,'وَلَا تَدْعُ مَعَ اللَّهِ إِلَٰهًا آخَرَ ۘ لَا إِلَٰهَ إِلَّا هُوَ ۚ كُلُّ شَيْءٍ هَالِكٌ إِلَّا وَجْهَهُ ۚ لَهُ الْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ','معبود دیگری را با خدا مخوان، که هیچ معبودی جز او نیست؛ همه چیز جز ذات (پاک) او فانی می‌شود؛ حاکمیت تنها از آن اوست؛ و همه بسوی او بازگردانده می شوید!'];
ayat[3341]=[29,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم','الم‌'];
ayat[3342]=[29,2,'أَحَسِبَ النَّاسُ أَن يُتْرَكُوا أَن يَقُولُوا آمَنَّا وَهُمْ لَا يُفْتَنُونَ','آیا مردم گمان کردند همین که بگویند: «ایمان آوردیم»، به حال خود رها می‌شوند و آزمایش نخواهند شد؟!'];
ayat[3343]=[29,3,'وَلَقَدْ فَتَنَّا الَّذِينَ مِن قَبْلِهِمْ ۖ فَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ صَدَقُوا وَلَيَعْلَمَنَّ الْكَاذِبِينَ','ما کسانی را که پیش از آنان بودند آزمودیم (و اینها را نیز امتحان می‌کنیم)؛ باید علم خدا درباره کسانی که راست می‌گویند و کسانی که دروغ می‌گویند تحقق یابد!'];
ayat[3344]=[29,4,'أَمْ حَسِبَ الَّذِينَ يَعْمَلُونَ السَّيِّئَاتِ أَن يَسْبِقُونَا ۚ سَاءَ مَا يَحْكُمُونَ','آیا کسانی که اعمال بد انجام می‌دهند گمان کردند بر قدرت ما چیره خواهند شد؟! چه بد داوری می‌کنند!'];
ayat[3345]=[29,5,'مَن كَانَ يَرْجُو لِقَاءَ اللَّهِ فَإِنَّ أَجَلَ اللَّهِ لَآتٍ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ','کسی که امید به لقاء الله (و رستاخیز) دارد (باید در اطاعت فرمان او بکوشد!) زیرا سرآمدی را که خدا تعیین کرده فرامی‌رسد؛ و او شنوا و داناست!'];
ayat[3346]=[29,6,'وَمَن جَاهَدَ فَإِنَّمَا يُجَاهِدُ لِنَفْسِهِ ۚ إِنَّ اللَّهَ لَغَنِيٌّ عَنِ الْعَالَمِينَ','کسی که جهاد و تلاش کند، برای خود جهاد می‌کند؛ چرا که خداوند از همه جهانیان بی نیاز است.'];
ayat[3347]=[29,7,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّئَاتِهِمْ وَلَنَجْزِيَنَّهُمْ أَحْسَنَ الَّذِي كَانُوا يَعْمَلُونَ','و کسانی که ایمان آورده و کارهای شایسته انجام دادند، گناهان آنان را می‌پوشانیم (و می‌بخشیم) و آنان را به بهترین اعمالی که انجام می‌دادند پاداش می‌دهیم.'];
ayat[3348]=[29,8,'وَوَصَّيْنَا الْإِنسَانَ بِوَالِدَيْهِ حُسْنًا ۖ وَإِن جَاهَدَاكَ لِتُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلَا تُطِعْهُمَا ۚ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','ما به انسان توصیه کردیم که به پدر و مادرش نیکی کند، و اگر آن دو (مشرک باشند و) تلاش کنند که برای من همتایی قائل شوی که به آن علم نداری، از آنها پیروی مکن! بازگشت شما به سوی من است، و شما را از آنچه انجام می‌دادید با خبر خواهم ساخت!'];
ayat[3349]=[29,9,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُدْخِلَنَّهُمْ فِي الصَّالِحِينَ','و کسانی که ایمان آورده و کارهای شایسته انجام دادند، آنها را در زمره صالحان وارد خواهیم کرد!'];
ayat[3350]=[29,10,'وَمِنَ النَّاسِ مَن يَقُولُ آمَنَّا بِاللَّهِ فَإِذَا أُوذِيَ فِي اللَّهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللَّهِ وَلَئِن جَاءَ نَصْرٌ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمْ ۚ أَوَلَيْسَ اللَّهُ بِأَعْلَمَ بِمَا فِي صُدُورِ الْعَالَمِينَ','و از مردم کسانی هستند که می‌گویند: «به خدا ایمان آورده‌ایم!» اما هنگامی که در راه خدا شکنجه و آزار می‌بینند، آزار مردم را همچون عذاب الهی می‌شمارند (و از آن سخت وحشت می‌کنند)؛ ولی هنگامی که پیروزی از سوی پروردگارت (برای شما) بیاید، می‌گویند: «ما هم با شما بودیم (و در این پیروزی شریکیم)»!! آیا خداوند به آنچه در سینه‌های جهانیان است آگاه‌تر نیست؟!'];
ayat[3351]=[29,11,'وَلَيَعْلَمَنَّ اللَّهُ الَّذِينَ آمَنُوا وَلَيَعْلَمَنَّ الْمُنَافِقِينَ','مسلماً خداوند مؤمنان را می‌شناسد، و به یقین منافقان را (نیز) می‌شناسد.'];
ayat[3352]=[29,12,'وَقَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا اتَّبِعُوا سَبِيلَنَا وَلْنَحْمِلْ خَطَايَاكُمْ وَمَا هُم بِحَامِلِينَ مِنْ خَطَايَاهُم مِّن شَيْءٍ ۖ إِنَّهُمْ لَكَاذِبُونَ','و کافران به مؤمنان گفتند: «شما از راه ما پیروی کنید، (و اگر گناهی دارد) ما گناهانتان را بر عهده خواهیم گرفت!» آنان هرگز چیزی از گناهان اینها را بر دوش نخواهند گرفت؛ آنان به یقین دروغگو هستند!'];
ayat[3353]=[29,13,'وَلَيَحْمِلُنَّ أَثْقَالَهُمْ وَأَثْقَالًا مَّعَ أَثْقَالِهِمْ ۖ وَلَيُسْأَلُنَّ يَوْمَ الْقِيَامَةِ عَمَّا كَانُوا يَفْتَرُونَ','آنها بار سنگین (گناهان) خویش را بر دوش می‌کشند، و (همچنین) بارهای سنگین دیگری را اضافه بر بارهای سنگین خود؛ و روز قیامت به یقین از تهمتهائی که می‌بستند سؤال خواهند شد!'];
ayat[3354]=[29,14,'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلَّا خَمْسِينَ عَامًا فَأَخَذَهُمُ الطُّوفَانُ وَهُمْ ظَالِمُونَ','و ما نوح را بسوی قومش فرستادیم؛ و او را در میان آنان هزار سال مگر پنجاه سال، درنگ کرد؛ اما سرانجام طوفان و سیلاب آنان را فراگرفت در حالی که ظالم بودند.'];
ayat[3355]=[29,15,'فَأَنجَيْنَاهُ وَأَصْحَابَ السَّفِينَةِ وَجَعَلْنَاهَا آيَةً لِّلْعَالَمِينَ','ما او و سرنشینان کشتی را رهایی بخشیدیم، و آن را آیتی برای جهانیان قرار دادیم!'];
ayat[3356]=[29,16,'وَإِبْرَاهِيمَ إِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ ۖ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ','ما ابراهیم را (نیز) فرستادیم، هنگامی که به قومش گفت: «خدا را پرستش کنید و از (عذاب) او بپرهیزید که این برای شما بهتر است اگر بدانید!'];
ayat[3357]=[29,17,'إِنَّمَا تَعْبُدُونَ مِن دُونِ اللَّهِ أَوْثَانًا وَتَخْلُقُونَ إِفْكًا ۚ إِنَّ الَّذِينَ تَعْبُدُونَ مِن دُونِ اللَّهِ لَا يَمْلِكُونَ لَكُمْ رِزْقًا فَابْتَغُوا عِندَ اللَّهِ الرِّزْقَ وَاعْبُدُوهُ وَاشْكُرُوا لَهُ ۖ إِلَيْهِ تُرْجَعُونَ','شما غیر از خدا فقط بتهایی (از سنگ و چوب) را می‌پرستید و دروغی به هم می‌بافید؛ آنهایی را که غیر از خدا پرستش می‌کنید، مالک هیچ رزقی برای شما نیستند؛ روزی را تنها نزد خدا بطلبید و او را پرستش کنید و شکر او را بجا آورید که بسوی او بازگشت داده می‌شوید!'];
ayat[3358]=[29,18,'وَإِن تُكَذِّبُوا فَقَدْ كَذَّبَ أُمَمٌ مِّن قَبْلِكُمْ ۖ وَمَا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ الْمُبِينُ','اگر شما (مرا) تکذیب کنید (جای تعجب نیست)، امتهایی پیش از شما نیز (پیامبرانشان را) تکذیب کردند؛ وظیفه فرستاده (خدا) جز ابلاغ آشکار نیست».'];
ayat[3359]=[29,19,'أَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللَّهُ الْخَلْقَ ثُمَّ يُعِيدُهُ ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ','آیا آنان ندیدند چگونه خداوند آفرینش را آغاز می‌کند، سپس بازمی‌گرداند؟! این کار برای خدا آسان است!'];
ayat[3360]=[29,20,'قُلْ سِيرُوا فِي الْأَرْضِ فَانظُرُوا كَيْفَ بَدَأَ الْخَلْقَ ۚ ثُمَّ اللَّهُ يُنشِئُ النَّشْأَةَ الْآخِرَةَ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','بگو: «در زمین بگردید و بنگرید خداوند چگونه آفرینش را آغاز کرده است؟ سپس خداوند (به همین‌گونه) جهان آخرت را ایجاد می‌کند؛ یقیناً خدا بر هر چیز توانا است!'];
ayat[3361]=[29,21,'يُعَذِّبُ مَن يَشَاءُ وَيَرْحَمُ مَن يَشَاءُ ۖ وَإِلَيْهِ تُقْلَبُونَ','هر کس را بخواهد (و مستحق بداند) مجازات می‌کند، و هر کس را بخواهد مورد رحمت قرارمی‌دهد؛ و شما را به سوی او بازمی‌گردانند.'];
ayat[3362]=[29,22,'وَمَا أَنتُم بِمُعْجِزِينَ فِي الْأَرْضِ وَلَا فِي السَّمَاءِ ۖ وَمَا لَكُم مِّن دُونِ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ','شما هرگز نمی‌توانید بر اراده خدا چیره شوید و از حوزه قدرت او در زمین و آسمان بگریزید؛ و برای شما جز خدا، ولی و یاوری نیست!»'];
ayat[3363]=[29,23,'وَالَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ وَلِقَائِهِ أُولَٰئِكَ يَئِسُوا مِن رَّحْمَتِي وَأُولَٰئِكَ لَهُمْ عَذَابٌ أَلِيمٌ','کسانی که به آیات خدا و دیدار او کافر شدند، از رحمت من مأیوسند؛ و برای آنها عذاب دردناکی است!'];
ayat[3364]=[29,24,'فَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَن قَالُوا اقْتُلُوهُ أَوْ حَرِّقُوهُ فَأَنجَاهُ اللَّهُ مِنَ النَّارِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يُؤْمِنُونَ','اما جواب قوم او [= ابراهیم‌] جز این نبود که گفتند: «او را بکشید یا بسوزانید!» ولی خداوند او را از آتش رهایی بخشید؛ در این ماجرا نشانه‌هایی است برای کسانی که ایمان می‌آورند.'];
ayat[3365]=[29,25,'وَقَالَ إِنَّمَا اتَّخَذْتُم مِّن دُونِ اللَّهِ أَوْثَانًا مَّوَدَّةَ بَيْنِكُمْ فِي الْحَيَاةِ الدُّنْيَا ۖ ثُمَّ يَوْمَ الْقِيَامَةِ يَكْفُرُ بَعْضُكُم بِبَعْضٍ وَيَلْعَنُ بَعْضُكُم بَعْضًا وَمَأْوَاكُمُ النَّارُ وَمَا لَكُم مِّن نَّاصِرِينَ','(ابراهیم) گفت: «شما غیر از خدا بتهایی برای خود انتخاب کرده‌اید که مایه دوستی و محبت میان شما در زندگی دنیا باشد؛ سپس روز قیامت از یکدیگر بیزاری می‌جویید و یکدیگر را لعن می‌کنید؛ و جایگاه (همه) شما آتش است و هیچ یار و یاوری برای شما نخواهد بود!»'];
ayat[3366]=[29,26,'۞ فَآمَنَ لَهُ لُوطٌ ۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّي ۖ إِنَّهُ هُوَ الْعَزِيزُ الْحَكِيمُ','و لوط به او [= ابراهیم‌] ایمان آورد، و (ابراهیم) گفت: «من بسوی پروردگارم هجرت می‌کنم که او صاحب قدرت و حکیم است!»'];
ayat[3367]=[29,27,'وَوَهَبْنَا لَهُ إِسْحَاقَ وَيَعْقُوبَ وَجَعَلْنَا فِي ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتَابَ وَآتَيْنَاهُ أَجْرَهُ فِي الدُّنْيَا ۖ وَإِنَّهُ فِي الْآخِرَةِ لَمِنَ الصَّالِحِينَ','و (در اواخر عمر،) اسحاق و یعقوب را به او بخشیدیم و نبوت و کتاب آسمانی را در دودمانش قرار دادیم و پاداش او را در دنیا دادیم و او در آخرت از صالحان است!'];
ayat[3368]=[29,28,'وَلُوطًا إِذْ قَالَ لِقَوْمِهِ إِنَّكُمْ لَتَأْتُونَ الْفَاحِشَةَ مَا سَبَقَكُم بِهَا مِنْ أَحَدٍ مِّنَ الْعَالَمِينَ','و لوط را فرستادیم هنگامی که به قوم خود گفت: «شما عمل بسیار زشتی انجام می‌دهید که هیچ یک از مردم جهان پیش از شما آن را انجام نداده است!'];
ayat[3369]=[29,29,'أَئِنَّكُمْ لَتَأْتُونَ الرِّجَالَ وَتَقْطَعُونَ السَّبِيلَ وَتَأْتُونَ فِي نَادِيكُمُ الْمُنكَرَ ۖ فَمَا كَانَ جَوَابَ قَوْمِهِ إِلَّا أَن قَالُوا ائْتِنَا بِعَذَابِ اللَّهِ إِن كُنتَ مِنَ الصَّادِقِينَ','آیا شما به سراغ مردان می‌روید و راه (تداوم نسل انسان) را قطع می‌کنید و در مجلستان اعمال ناپسند انجام می‌دهید؟! «اما پاسخ قومش جز این نبود که گفتند:» اگر راست می‌گویی عذاب الهی را برای ما بیاور!'];
ayat[3370]=[29,30,'قَالَ رَبِّ انصُرْنِي عَلَى الْقَوْمِ الْمُفْسِدِينَ','(لوط) عرض کرد: «پروردگارا! مرا در برابر این قوم تبهکار یاری فرما!»'];
ayat[3371]=[29,31,'وَلَمَّا جَاءَتْ رُسُلُنَا إِبْرَاهِيمَ بِالْبُشْرَىٰ قَالُوا إِنَّا مُهْلِكُو أَهْلِ هَٰذِهِ الْقَرْيَةِ ۖ إِنَّ أَهْلَهَا كَانُوا ظَالِمِينَ','و هنگامی که فرستادگان ما (از فرشتگان) بشارت (تولد فرزند) برای ابراهیم آوردند، گفتند: «ما اهل این شهر و آبادی را [و به شهرهای قوم لوط اشاره کردند] هلاک خواهیم کرد، چرا که اهل آن ستمگرند!»'];
ayat[3372]=[29,32,'قَالَ إِنَّ فِيهَا لُوطًا ۚ قَالُوا نَحْنُ أَعْلَمُ بِمَن فِيهَا ۖ لَنُنَجِّيَنَّهُ وَأَهْلَهُ إِلَّا امْرَأَتَهُ كَانَتْ مِنَ الْغَابِرِينَ','(ابراهیم) گفت: «در این آبادی لوط است!» گفتند: «ما به کسانی که در آن هستند آگاهتریم! او و خانواده‌اش را نجات می‌دهیم؛ جز همسرش که در میان قوم (گنهکار) باقی خواهد ماند.»'];
ayat[3373]=[29,33,'وَلَمَّا أَن جَاءَتْ رُسُلُنَا لُوطًا سِيءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالُوا لَا تَخَفْ وَلَا تَحْزَنْ ۖ إِنَّا مُنَجُّوكَ وَأَهْلَكَ إِلَّا امْرَأَتَكَ كَانَتْ مِنَ الْغَابِرِينَ','هنگامی که فرستادگان ما نزد لوط آمدند، از دیدن آنها بدحال و دلتنگ شد؛ گفتند: «نترس و غمگین مباش، ما تو و خانواده‌ات را نجات خواهیم داد، جز همسرت که در میان قوم باقی می‌ماند.'];
ayat[3374]=[29,34,'إِنَّا مُنزِلُونَ عَلَىٰ أَهْلِ هَٰذِهِ الْقَرْيَةِ رِجْزًا مِّنَ السَّمَاءِ بِمَا كَانُوا يَفْسُقُونَ','ما بر اهل این شهر و آبادی به خاطر گناهانشان، عذابی از آسمان فرو خواهیم ریخت!»'];
ayat[3375]=[29,35,'وَلَقَد تَّرَكْنَا مِنْهَا آيَةً بَيِّنَةً لِّقَوْمٍ يَعْقِلُونَ','و از این آبادی نشانه روشنی (و درس عبرتی) برای کسانی که می‌اندیشند باقی گذاردیم!'];
ayat[3376]=[29,36,'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا فَقَالَ يَا قَوْمِ اعْبُدُوا اللَّهَ وَارْجُوا الْيَوْمَ الْآخِرَ وَلَا تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ','و ما بسوی «مدین»، برادرشان «شعیب» را فرستادیم؛ گفت: «ای قوم من! خدا را بپرستید، و به روز بازپسین امیدوار باشید، و در زمین فساد نکنید!»'];
ayat[3377]=[29,37,'فَكَذَّبُوهُ فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دَارِهِمْ جَاثِمِينَ','(ولی) آنها او را تکذیب کردند، و به این سبب زلزله آنان را فراگرفت، و بامدادان در خانه‌های خود به رو در افتاده و مرده بودند!'];
ayat[3378]=[29,38,'وَعَادًا وَثَمُودَ وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَاكِنِهِمْ ۖ وَزَيَّنَ لَهُمُ الشَّيْطَانُ أَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيلِ وَكَانُوا مُسْتَبْصِرِينَ','ما طایفه «عاد» و «ثمود» را نیز (هلاک کردیم)، و مساکن (ویران شده) آنان برای شما آشکار است؛ شیطان اعمالشان را برای آنان آراسته بود، از این رو آنان را از راه (خدا) بازداشت در حالی که بینا بودند.'];
ayat[3379]=[29,39,'وَقَارُونَ وَفِرْعَوْنَ وَهَامَانَ ۖ وَلَقَدْ جَاءَهُم مُّوسَىٰ بِالْبَيِّنَاتِ فَاسْتَكْبَرُوا فِي الْأَرْضِ وَمَا كَانُوا سَابِقِينَ','و «قارون» و «فرعون» و «هامان» را نیز هلاک کردیم؛ موسی با دلایل روشن به سراغشان آمد، امّا آنان در زمین برتری جویی کردند، ولی نتوانستند بر خدا پیشی گیرند!'];
ayat[3380]=[29,40,'فَكُلًّا أَخَذْنَا بِذَنبِهِ ۖ فَمِنْهُم مَّنْ أَرْسَلْنَا عَلَيْهِ حَاصِبًا وَمِنْهُم مَّنْ أَخَذَتْهُ الصَّيْحَةُ وَمِنْهُم مَّنْ خَسَفْنَا بِهِ الْأَرْضَ وَمِنْهُم مَّنْ أَغْرَقْنَا ۚ وَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','ما هر یک از آنان را به گناهانشان گرفتیم، بر بعضی از آنها طوفانی از سنگریزه فرستادیم، و بعضی از آنان را صیحه آسمانی فروگرفت، و بعضی دیگر را در زمین فرو بردیم، و بعضی را غرق کردیم؛ خداوند هرگز به آنها ستم نکرد، ولی آنها خودشان بر خود ستم می‌کردند!'];
ayat[3381]=[29,41,'مَثَلُ الَّذِينَ اتَّخَذُوا مِن دُونِ اللَّهِ أَوْلِيَاءَ كَمَثَلِ الْعَنكَبُوتِ اتَّخَذَتْ بَيْتًا ۖ وَإِنَّ أَوْهَنَ الْبُيُوتِ لَبَيْتُ الْعَنكَبُوتِ ۖ لَوْ كَانُوا يَعْلَمُونَ','مثَل کسانی که غیر از خدا را اولیای خود برگزیدند، مثَل عنکبوت است که خانه‌ای برای خود انتخاب کرده؛ در حالی که سست‌ترین خانه‌های خانه عنکبوت است اگر می‌دانستند!'];
ayat[3382]=[29,42,'إِنَّ اللَّهَ يَعْلَمُ مَا يَدْعُونَ مِن دُونِهِ مِن شَيْءٍ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','خداوند آنچه را غیر از او می‌خوانند می‌داند، و او شکست‌ناپذیر و حکیم است.'];
ayat[3383]=[29,43,'وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ ۖ وَمَا يَعْقِلُهَا إِلَّا الْعَالِمُونَ','اینها مثالهایی است که ما برای مردم می‌زنیم، و جز دانایان آن را درک نمی‌کنند.'];
ayat[3384]=[29,44,'خَلَقَ اللَّهُ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّلْمُؤْمِنِينَ','خداوند، آسمانها و زمین را بحق آفرید؛ و در این آیتی است برای مؤمنان.'];
ayat[3385]=[29,45,'اتْلُ مَا أُوحِيَ إِلَيْكَ مِنَ الْكِتَابِ وَأَقِمِ الصَّلَاةَ ۖ إِنَّ الصَّلَاةَ تَنْهَىٰ عَنِ الْفَحْشَاءِ وَالْمُنكَرِ ۗ وَلَذِكْرُ اللَّهِ أَكْبَرُ ۗ وَاللَّهُ يَعْلَمُ مَا تَصْنَعُونَ','آنچه را از کتاب (آسمانی) به تو وحی شده تلاوت کن، و نماز را برپا دار، که نماز (انسان را) از زشتیها و گناه بازمی‌دارد، و یاد خدا بزرگتر است؛ و خداوند می‌داند شما چه کارهایی انجام می‌دهید!'];
ayat[3386]=[29,46,'۞ وَلَا تُجَادِلُوا أَهْلَ الْكِتَابِ إِلَّا بِالَّتِي هِيَ أَحْسَنُ إِلَّا الَّذِينَ ظَلَمُوا مِنْهُمْ ۖ وَقُولُوا آمَنَّا بِالَّذِي أُنزِلَ إِلَيْنَا وَأُنزِلَ إِلَيْكُمْ وَإِلَٰهُنَا وَإِلَٰهُكُمْ وَاحِدٌ وَنَحْنُ لَهُ مُسْلِمُونَ','با اهل کتاب جز به روشی که از همه نیکوتر است مجادله نکنید، مگر کسانی از آنان که ستم کردند؛ و (به آنها) بگویید: «ما به تمام آنچه از سوی خدا بر ما و شما نازل شده ایمان آورده‌ایم، و معبود ما و شما یکی است، و ما در برابر او تسلیم هستیم!»'];
ayat[3387]=[29,47,'وَكَذَٰلِكَ أَنزَلْنَا إِلَيْكَ الْكِتَابَ ۚ فَالَّذِينَ آتَيْنَاهُمُ الْكِتَابَ يُؤْمِنُونَ بِهِ ۖ وَمِنْ هَٰؤُلَاءِ مَن يُؤْمِنُ بِهِ ۚ وَمَا يَجْحَدُ بِآيَاتِنَا إِلَّا الْكَافِرُونَ','و این گونه، کتاب [= قرآن‌] را بر تو نازل کردیم، کسانی که کتاب (آسمانی) به آنها داده‌ایم به این کتاب ایمان می‌آورند؛ و بعضی از این گروه [= مشرکان‌] نیز به آن مؤمن می‌شوند؛ و آیات ما را جز کافران انکار نمی‌کنند.'];
ayat[3388]=[29,48,'وَمَا كُنتَ تَتْلُو مِن قَبْلِهِ مِن كِتَابٍ وَلَا تَخُطُّهُ بِيَمِينِكَ ۖ إِذًا لَّارْتَابَ الْمُبْطِلُونَ','تو هرگز پیش از این کتابی نمی‌خواندی، و با دست خود چیزی نمی‌نوشتی، مبادا کسانی که در صدد (تکذیب و) ابطال سخنان تو هستند، شک و تردید کنند!'];
ayat[3389]=[29,49,'بَلْ هُوَ آيَاتٌ بَيِّنَاتٌ فِي صُدُورِ الَّذِينَ أُوتُوا الْعِلْمَ ۚ وَمَا يَجْحَدُ بِآيَاتِنَا إِلَّا الظَّالِمُونَ','ولی این آیات روشنی است که در سینه دانشوران جای دارد؛ و آیات ما را جز ستمگران انکار نمی‌کنند!'];
ayat[3390]=[29,50,'وَقَالُوا لَوْلَا أُنزِلَ عَلَيْهِ آيَاتٌ مِّن رَّبِّهِ ۖ قُلْ إِنَّمَا الْآيَاتُ عِندَ اللَّهِ وَإِنَّمَا أَنَا نَذِيرٌ مُّبِينٌ','گفتند: «چرا معجزاتی از سوی پروردگارش بر او نازل نشده؟!» بگو: «معجزات همه نزد خداست (و به فرمان او نازل می‌شود، نه به میل من و شما)؛ من تنها بیم دهنده‌ای آشکارم!'];
ayat[3391]=[29,51,'أَوَلَمْ يَكْفِهِمْ أَنَّا أَنزَلْنَا عَلَيْكَ الْكِتَابَ يُتْلَىٰ عَلَيْهِمْ ۚ إِنَّ فِي ذَٰلِكَ لَرَحْمَةً وَذِكْرَىٰ لِقَوْمٍ يُؤْمِنُونَ','آیا برای آنان کافی نیست که این کتاب را بر تو نازل کردیم که پیوسته بر آنها تلاوت می‌شود؟! در این، رحمت و تذکّری است برای کسانی که ایمان می‌آورند (و این معجزه بسیار واضحی است).'];
ayat[3392]=[29,52,'قُلْ كَفَىٰ بِاللَّهِ بَيْنِي وَبَيْنَكُمْ شَهِيدًا ۖ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۗ وَالَّذِينَ آمَنُوا بِالْبَاطِلِ وَكَفَرُوا بِاللَّهِ أُولَٰئِكَ هُمُ الْخَاسِرُونَ','بگو: «همین بس که خدا میان من و شما گواه است؛ آنچه را در آسمانها و زمین است می‌داند؛ و کسانی که به باطل ایمان آوردند و به خدا کافر شدند زیانکاران واقعی هستند!'];
ayat[3393]=[29,53,'وَيَسْتَعْجِلُونَكَ بِالْعَذَابِ ۚ وَلَوْلَا أَجَلٌ مُّسَمًّى لَّجَاءَهُمُ الْعَذَابُ وَلَيَأْتِيَنَّهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ','آنان با شتاب از تو عذاب را می‌طلبند؛ و اگر موعد مقرّری تعیین نشده بود، عذاب (الهی) به سراغ آنان می‌آمد؛ و سرانجام این عذاب بطور ناگهانی بر آنها نازل می‌شود در حالی که نمی‌دانند (و غافلند).'];
ayat[3394]=[29,54,'يَسْتَعْجِلُونَكَ بِالْعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكَافِرِينَ','آنان با عجله از تو عذاب می‌طلبند، در حالی که جهنم به کافران احاطه دارد!'];
ayat[3395]=[29,55,'يَوْمَ يَغْشَاهُمُ الْعَذَابُ مِن فَوْقِهِمْ وَمِن تَحْتِ أَرْجُلِهِمْ وَيَقُولُ ذُوقُوا مَا كُنتُمْ تَعْمَلُونَ','آن روز که عذاب (الهی) آنها را از بالای سر و پایین پایشان فرامی‌گیرد و به آنها می‌گوید: «بچشید آنچه را عمل می‌کردید» (روز سخت و دردناکی برای آنهاست!)'];
ayat[3396]=[29,56,'يَا عِبَادِيَ الَّذِينَ آمَنُوا إِنَّ أَرْضِي وَاسِعَةٌ فَإِيَّايَ فَاعْبُدُونِ','ای بندگان من که ایمان آورده‌اید! زمین من وسیع است، پس تنها مرا بپرستید (و در برابر فشارهای دشمنان تسلیم نشوید)!'];
ayat[3397]=[29,57,'كُلُّ نَفْسٍ ذَائِقَةُ الْمَوْتِ ۖ ثُمَّ إِلَيْنَا تُرْجَعُونَ','هر انسانی مرگ را می‌چشد، سپس شما را بسوی ما بازمی‌گردانند.'];
ayat[3398]=[29,58,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَنُبَوِّئَنَّهُم مِّنَ الْجَنَّةِ غُرَفًا تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ نِعْمَ أَجْرُ الْعَامِلِينَ','و کسانی که ایمان آورده و کارهای شایسته انجام دادند، آنان را در غرفه‌هایی از بهشت جای می‌دهیم که نهرها در زیر آن جاری است؛ جاودانه در آن خواهند ماند؛ چه خوب است پاداش عمل‌کنندگان!'];
ayat[3399]=[29,59,'الَّذِينَ صَبَرُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ','همانها که (در برابر مشکلات) صبر (و استقامت) کردند و بر پروردگارشان توکّل می‌کنند.'];
ayat[3400]=[29,60,'وَكَأَيِّن مِّن دَابَّةٍ لَّا تَحْمِلُ رِزْقَهَا اللَّهُ يَرْزُقُهَا وَإِيَّاكُمْ ۚ وَهُوَ السَّمِيعُ الْعَلِيمُ','چه بسا جنبنده‌ای که قدرت حمل روزی خود را ندارد، خداوند او و شما را روزی می‌دهد؛ و او شنوا و داناست.'];
ayat[3401]=[29,61,'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُولُنَّ اللَّهُ ۖ فَأَنَّىٰ يُؤْفَكُونَ','و هر گاه از آنان بپرسی: «چه کسی آسمانها و زمین را آفریده، و خورشید و ماه را مسخّر کرده است؟» می‌گویند: «اللّه»! پس با این حال چگونه آنان را (از عبادت خدا) منحرف می‌سازند؟!'];
ayat[3402]=[29,62,'اللَّهُ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ لَهُ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ','خداوند روزی را برای هر کس از بندگانش بخواهد گسترده می‌کند، و برای هر کس بخواهد محدود می‌سازد؛ خداوند به همه چیز داناست!'];
ayat[3403]=[29,63,'وَلَئِن سَأَلْتَهُم مَّن نَّزَّلَ مِنَ السَّمَاءِ مَاءً فَأَحْيَا بِهِ الْأَرْضَ مِن بَعْدِ مَوْتِهَا لَيَقُولُنَّ اللَّهُ ۚ قُلِ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْقِلُونَ','و اگر از آنان بپرسی: «چه کسی از آسمان آبی فرستاد و بوسیله آن زمین را پس از مردنش زنده کرد؟ می‌گویند: «اللّه»! بگو: «حمد و ستایش مخصوص خداست!» امّا بیشتر آنها نمی‌دانند.'];
ayat[3404]=[29,64,'وَمَا هَٰذِهِ الْحَيَاةُ الدُّنْيَا إِلَّا لَهْوٌ وَلَعِبٌ ۚ وَإِنَّ الدَّارَ الْآخِرَةَ لَهِيَ الْحَيَوَانُ ۚ لَوْ كَانُوا يَعْلَمُونَ','این زندگی دنیا چیزی جز سرگرمی و بازی نیست؛ و زندگی واقعی سرای آخرت است، اگر می‌دانستند!'];
ayat[3405]=[29,65,'فَإِذَا رَكِبُوا فِي الْفُلْكِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ إِذَا هُمْ يُشْرِكُونَ','هنگامی که بر سوار بر کشتی شوند، خدا را با اخلاص می‌خوانند (و غیر او را فراموش می‌کنند)؛ امّا هنگامی که خدا آنان را به خشکی رساند و نجات داد، باز مشرک می‌شوند!'];
ayat[3406]=[29,66,'لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ وَلِيَتَمَتَّعُوا ۖ فَسَوْفَ يَعْلَمُونَ','(بگذار) آنچه را (از آیات) به آنها داده‌ایم انکار کنند و از لذّات زودگذر زندگی بهره گیرند؛ امّا بزودی خواهند فهمید!'];
ayat[3407]=[29,67,'أَوَلَمْ يَرَوْا أَنَّا جَعَلْنَا حَرَمًا آمِنًا وَيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْ ۚ أَفَبِالْبَاطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ اللَّهِ يَكْفُرُونَ','آیا ندیدند که ما حرم امنی (برای آنها) قرار دادیم در حالی که مردم را در اطراف آنان (در بیرون این حرم) می‌ربایند؟! آیا به باطل ایمان می‌آورند و نعمت خدا را کفران می‌کنند؟!'];
ayat[3408]=[29,68,'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاءَهُ ۚ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِّلْكَافِرِينَ','چه کسی ستمکارتر از آن کس است که بر خدا دروغ بسته یا حق را پس از آنکه به سراغش آمده تکذیب نماید؟! آیا جایگاه کافران در دوزخ نیست؟!'];
ayat[3409]=[29,69,'وَالَّذِينَ جَاهَدُوا فِينَا لَنَهْدِيَنَّهُمْ سُبُلَنَا ۚ وَإِنَّ اللَّهَ لَمَعَ الْمُحْسِنِينَ','و آنها که در راه ما (با خلوص نیّت) جهاد کنند، قطعاً به راه‌های خود، هدایتشان خواهیم کرد؛ و خداوند با نیکوکاران است.'];
ayat[3410]=[30,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم','الم‌'];
ayat[3411]=[30,2,'غُلِبَتِ الرُّومُ','رومیان مغلوب شدند!'];
ayat[3412]=[30,3,'فِي أَدْنَى الْأَرْضِ وَهُم مِّن بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَ','(و این شکست) در سرزمین نزدیکی رخ داد؛ امّا آنان پس از (این) مغلوبیّت بزودی غلبه خواهند کرد...'];
ayat[3413]=[30,4,'فِي بِضْعِ سِنِينَ ۗ لِلَّهِ الْأَمْرُ مِن قَبْلُ وَمِن بَعْدُ ۚ وَيَوْمَئِذٍ يَفْرَحُ الْمُؤْمِنُونَ','در چند سال همه کارها از آن خداست؛ چه قبل و چه بعد (از این شکست و پیروزی)؛ و در آن روز، مؤمنان (بخاطر پیروزی دیگری) خوشحال خواهند شد...'];
ayat[3414]=[30,5,'بِنَصْرِ اللَّهِ ۚ يَنصُرُ مَن يَشَاءُ ۖ وَهُوَ الْعَزِيزُ الرَّحِيمُ','به سبب یاری خداوند؛ و او هر کس را بخواهد یاری می‌دهد؛ و او صاحب قدرت و رحیم است!'];
ayat[3415]=[30,6,'وَعْدَ اللَّهِ ۖ لَا يُخْلِفُ اللَّهُ وَعْدَهُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','این وعده‌ای است که خدا کرده؛ و خداوند هرگز از وعده‌اش تخلّف نمی‌کند؛ ولی بیشتر مردم نمی‌دانند!'];
ayat[3416]=[30,7,'يَعْلَمُونَ ظَاهِرًا مِّنَ الْحَيَاةِ الدُّنْيَا وَهُمْ عَنِ الْآخِرَةِ هُمْ غَافِلُونَ','آنها فقط ظاهری از زندگی دنیا را می‌دانند، و از آخرت (و پایان کار) غافلند!'];
ayat[3417]=[30,8,'أَوَلَمْ يَتَفَكَّرُوا فِي أَنفُسِهِم ۗ مَّا خَلَقَ اللَّهُ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إِلَّا بِالْحَقِّ وَأَجَلٍ مُّسَمًّى ۗ وَإِنَّ كَثِيرًا مِّنَ النَّاسِ بِلِقَاءِ رَبِّهِمْ لَكَافِرُونَ','آیا آنان با خود نیندیشیدند که خداوند، آسمانها و زمین و آنچه را میان آن دو است جز بحق و برای زمان معیّنی نیافریده است؟! ولی بسیاری از مردم (رستاخیز و) لقای پروردگارشان را منکرند!'];
ayat[3418]=[30,9,'أَوَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِن قَبْلِهِمْ ۚ كَانُوا أَشَدَّ مِنْهُمْ قُوَّةً وَأَثَارُوا الْأَرْضَ وَعَمَرُوهَا أَكْثَرَ مِمَّا عَمَرُوهَا وَجَاءَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ ۖ فَمَا كَانَ اللَّهُ لِيَظْلِمَهُمْ وَلَٰكِن كَانُوا أَنفُسَهُمْ يَظْلِمُونَ','آیا در زمین گردش نکردند تا ببینند عاقبت کسانی که قبل از آنان بودند چگونه بود؟! آنها نیرومندتر از اینان بودند، و زمین را (برای زراعت و آبادی) بیش از اینان دگرگون ساختند و آباد کردند، و پیامبرانشان با دلایل روشن به سراغشان آمدند (امّا آنها انکار کردند و کیفر خود را دیدند)؛ خداوند هرگز به آنان ستم نکرد، آنها به خودشان ستم می‌کردند!'];
ayat[3419]=[30,10,'ثُمَّ كَانَ عَاقِبَةَ الَّذِينَ أَسَاءُوا السُّوأَىٰ أَن كَذَّبُوا بِآيَاتِ اللَّهِ وَكَانُوا بِهَا يَسْتَهْزِئُونَ','سپس سرانجام کسانی که اعمال بد مرتکب شدند به جایی رسید که آیات خدا را تکذیب کردند و آن را به مسخره گرفتند!'];
ayat[3420]=[30,11,'اللَّهُ يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ ثُمَّ إِلَيْهِ تُرْجَعُونَ','خداوند آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، سپس شما را بسوی او باز می‌گردانند!'];
ayat[3421]=[30,12,'وَيَوْمَ تَقُومُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُونَ','آن روز که قیامت برپا می‌شود، مجرمان در نومیدی و غم و اندوه فرو می‌روند!'];
ayat[3422]=[30,13,'وَلَمْ يَكُن لَّهُم مِّن شُرَكَائِهِمْ شُفَعَاءُ وَكَانُوا بِشُرَكَائِهِمْ كَافِرِينَ','و برای آنان شفیعانی از معبودانشان نخواهد بود، و نسبت به معبودهایی که آنها را همتای خدا قرار داده بودند کافر می‌شوند!'];
ayat[3423]=[30,14,'وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ','آن روز که قیامت برپا می‌گردد، (مردم) از هم جدا می‌شوند؛'];
ayat[3424]=[30,15,'فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَهُمْ فِي رَوْضَةٍ يُحْبَرُونَ','امّا آنان که ایمان آورده و اعمال صالح انجام دادند، در باغی از بهشت شاد و مسرور خواهند بود.'];
ayat[3425]=[30,16,'وَأَمَّا الَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا وَلِقَاءِ الْآخِرَةِ فَأُولَٰئِكَ فِي الْعَذَابِ مُحْضَرُونَ','و امّا آنان که به آیات ما و لقای آخرت کافر شدند، در عذاب الهی احضار می‌شوند.'];
ayat[3426]=[30,17,'فَسُبْحَانَ اللَّهِ حِينَ تُمْسُونَ وَحِينَ تُصْبِحُونَ','منزّه است خداوند به هنگامی که شام می‌کنید و صبح می‌کنید؛'];
ayat[3427]=[30,18,'وَلَهُ الْحَمْدُ فِي السَّمَاوَاتِ وَالْأَرْضِ وَعَشِيًّا وَحِينَ تُظْهِرُونَ','و حمد و ستایش مخصوص اوست در آسمان و زمین، و به هنگام عصر و هنگامی که ظهر می‌کنید.'];
ayat[3428]=[30,19,'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ وَكَذَٰلِكَ تُخْرَجُونَ','او زنده را از مرده بیرون می‌آورد، و مرده را از زنده، و زمین را پس از مردنش حیات می‌بخشد، و به همین گونه روز قیامت (از گورها) بیرون آورده می‌شوید!'];
ayat[3429]=[30,20,'وَمِنْ آيَاتِهِ أَنْ خَلَقَكُم مِّن تُرَابٍ ثُمَّ إِذَا أَنتُم بَشَرٌ تَنتَشِرُونَ','از نشانه‌های او این است که شما را از خاک آفرید، سپس بناگاه انسانهایی شدید و در روی زمین گسترش یافتید!'];
ayat[3430]=[30,21,'وَمِنْ آيَاتِهِ أَنْ خَلَقَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَاجًا لِّتَسْكُنُوا إِلَيْهَا وَجَعَلَ بَيْنَكُم مَّوَدَّةً وَرَحْمَةً ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَتَفَكَّرُونَ','و از نشانه‌های او اینکه همسرانی از جنس خودتان برای شما آفرید تا در کنار آنان آرامش یابید، و در میانتان مودّت و رحمت قرار داد؛ در این نشانه‌هایی است برای گروهی که تفکّر می‌کنند!'];
ayat[3431]=[30,22,'وَمِنْ آيَاتِهِ خَلْقُ السَّمَاوَاتِ وَالْأَرْضِ وَاخْتِلَافُ أَلْسِنَتِكُمْ وَأَلْوَانِكُمْ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّلْعَالِمِينَ','و از آیات او آفرینش آسمانها و زمین، و تفاوت زبانها و رنگهای شماست؛ در این نشانه‌هایی است برای عالمان!'];
ayat[3432]=[30,23,'وَمِنْ آيَاتِهِ مَنَامُكُم بِاللَّيْلِ وَالنَّهَارِ وَابْتِغَاؤُكُم مِّن فَضْلِهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَسْمَعُونَ','و از نشانه‌های او خواب شما در شب و روز است و تلاش و کوششتان برای بهره‌گیری از فضل پروردگار (و تأمین معاش)؛ در این امور نشانه‌هایی است برای آنان که گوش شنوا دارند!'];
ayat[3433]=[30,24,'وَمِنْ آيَاتِهِ يُرِيكُمُ الْبَرْقَ خَوْفًا وَطَمَعًا وَيُنَزِّلُ مِنَ السَّمَاءِ مَاءً فَيُحْيِي بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَعْقِلُونَ','و از آیات او این است که برق و رعد را به شما نشان می‌دهد که هم مایه ترس و هم امید است (ترس از صاعقه، و امید به نزول باران)، و از آسمان آبی فرو می‌فرستد که زمین را بعد از مردنش بوسیله آن زنده می‌کند؛ در این نشانه‌هایی است برای جمعیّتی که می‌اندیشند!'];
ayat[3434]=[30,25,'وَمِنْ آيَاتِهِ أَن تَقُومَ السَّمَاءُ وَالْأَرْضُ بِأَمْرِهِ ۚ ثُمَّ إِذَا دَعَاكُمْ دَعْوَةً مِّنَ الْأَرْضِ إِذَا أَنتُمْ تَخْرُجُونَ','و از آیات او این است که آسمان و زمین به فرمان او برپاست؛ سپس هنگامی که شما را (در قیامت) از زمین فراخواند، ناگهان همه خارج می‌شوید (و در صحنه محشر حضور می‌یابید)!'];
ayat[3435]=[30,26,'وَلَهُ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ كُلٌّ لَّهُ قَانِتُونَ','و از آن اوست تمام کسانی که در آسمانها و زمین‌اند و همگی در برابر او خاضع و مطیع‌اند!'];
ayat[3436]=[30,27,'وَهُوَ الَّذِي يَبْدَأُ الْخَلْقَ ثُمَّ يُعِيدُهُ وَهُوَ أَهْوَنُ عَلَيْهِ ۚ وَلَهُ الْمَثَلُ الْأَعْلَىٰ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','او کسی است که آفرینش را آغاز می‌کند، سپس آن را بازمی‌گرداند، و این کار برای او آسانتر می‌باشد؛ و برای اوست توصیف برتر در آسمانها و زمین؛ و اوست توانمند و حکیم!'];
ayat[3437]=[30,28,'ضَرَبَ لَكُم مَّثَلًا مِّنْ أَنفُسِكُمْ ۖ هَل لَّكُم مِّن مَّا مَلَكَتْ أَيْمَانُكُم مِّن شُرَكَاءَ فِي مَا رَزَقْنَاكُمْ فَأَنتُمْ فِيهِ سَوَاءٌ تَخَافُونَهُمْ كَخِيفَتِكُمْ أَنفُسَكُمْ ۚ كَذَٰلِكَ نُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْقِلُونَ','خداوند مثالی از خودتان، برای شما زده است: آیا (اگر مملوک و برده‌ای داشته باشید)، این برده‌های شما هرگز در روزیهایی که به شما داده‌ایم شریک شما می‌باشند؛ آنچنان که هر دو مساوی بوده و از تصرّف مستقل و بدون اجازه آنان بیم داشته باشید، آن گونه که در مورد شرکای آزاد خود بیم دارید؟! اینچنین آیات خود را برای کسانی که تعقّل می‌کنند شرح می‌دهیم.'];
ayat[3438]=[30,29,'بَلِ اتَّبَعَ الَّذِينَ ظَلَمُوا أَهْوَاءَهُم بِغَيْرِ عِلْمٍ ۖ فَمَن يَهْدِي مَنْ أَضَلَّ اللَّهُ ۖ وَمَا لَهُم مِّن نَّاصِرِينَ','ولی ظالمان بدون علم و آگاهی، از هوی و هوسهای خود پیروی کردند! پس چه کسی می‌تواند آنان را که خدا گمراه کرده است هدایت کند؟! و برای آنها هیچ یاوری نخواهد بود!'];
ayat[3439]=[30,30,'فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا ۚ فِطْرَتَ اللَّهِ الَّتِي فَطَرَ النَّاسَ عَلَيْهَا ۚ لَا تَبْدِيلَ لِخَلْقِ اللَّهِ ۚ ذَٰلِكَ الدِّينُ الْقَيِّمُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','پس روی خود را متوجّه آیین خالص پروردگار کن! این فطرتی است که خداوند، انسانها را بر آن آفریده؛ دگرگونی در آفرینش الهی نیست؛ این است آیین استوار؛ ولی اکثر مردم نمی‌دانند!'];
ayat[3440]=[30,31,'۞ مُنِيبِينَ إِلَيْهِ وَاتَّقُوهُ وَأَقِيمُوا الصَّلَاةَ وَلَا تَكُونُوا مِنَ الْمُشْرِكِينَ','این باید در حالی باشد که شما بسوی او بازگشت می‌کنید و از (مخالفت فرمان) او بپرهیزید، نماز را برپا دارید و از مشرکان نباشید...'];
ayat[3441]=[30,32,'مِنَ الَّذِينَ فَرَّقُوا دِينَهُمْ وَكَانُوا شِيَعًا ۖ كُلُّ حِزْبٍ بِمَا لَدَيْهِمْ فَرِحُونَ','از کسانی که دین خود را پراکنده ساختند و به دسته‌ها و گروه‌ها تقسیم شدند! و (عجب اینکه) هر گروهی به آنچه نزد آنهاست (دلبسته و) خوشحالند!'];
ayat[3442]=[30,33,'وَإِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُم مُّنِيبِينَ إِلَيْهِ ثُمَّ إِذَا أَذَاقَهُم مِّنْهُ رَحْمَةً إِذَا فَرِيقٌ مِّنْهُم بِرَبِّهِمْ يُشْرِكُونَ','هنگامی که رنج و زیانی به مردم برسد، پروردگار خود را می‌خوانند و توبه‌کنان بسوی او بازمی‌گردند؛ امّا همین که رحمتی از خودش به آنان بچشاند، بناگاه گروهی از آنان نسبت به پروردگارشان مشرک می‌شوند.'];
ayat[3443]=[30,34,'لِيَكْفُرُوا بِمَا آتَيْنَاهُمْ ۚ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ','(بگذار) نعمتهایی را که ما به آنها داده‌ایم کفران کنند! و (از نعمتهای زودگذر دنیا هر چه می‌توانید) بهره گیرید؛ امّا بزودی خواهید دانست (که نتیجه کفران و کامجوییهای بی حساب شما چه بوده است)!'];
ayat[3444]=[30,35,'أَمْ أَنزَلْنَا عَلَيْهِمْ سُلْطَانًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا بِهِ يُشْرِكُونَ','آیا ما دلیل محکمی بر آنان فرستادیم که از شرکشان سخن می‌گوید (و آن را موجّه می‌شمارد)؟!'];
ayat[3445]=[30,36,'وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوا بِهَا ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ إِذَا هُمْ يَقْنَطُونَ','و هنگامی که رحمتی به مردم بچشانیم، از آن خوشحال می‌شوند؛ و هرگاه رنج و مصیبتی بخاطر اعمالی که انجام داده‌اند به آنان رسد، ناگهان مأیوس می‌شوند!'];
ayat[3446]=[30,37,'أَوَلَمْ يَرَوْا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ وَيَقْدِرُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يُؤْمِنُونَ','آیا ندیدند که خداوند روزی را برای هر کس بخواهد گسترده یا تنگ می‌سازد؟! در این نشانه‌هایی است برای گروهی که ایمان می‌آورند.'];
ayat[3447]=[30,38,'فَآتِ ذَا الْقُرْبَىٰ حَقَّهُ وَالْمِسْكِينَ وَابْنَ السَّبِيلِ ۚ ذَٰلِكَ خَيْرٌ لِّلَّذِينَ يُرِيدُونَ وَجْهَ اللَّهِ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','پس حقّ نزدیکان و مسکینان و در راه‌ماندگان را ادا کن! این برای آنها که رضای خدا را می‌طلبند بهتر است، و چنین کسانی رستگارانند.'];
ayat[3448]=[30,39,'وَمَا آتَيْتُم مِّن رِّبًا لِّيَرْبُوَ فِي أَمْوَالِ النَّاسِ فَلَا يَرْبُو عِندَ اللَّهِ ۖ وَمَا آتَيْتُم مِّن زَكَاةٍ تُرِيدُونَ وَجْهَ اللَّهِ فَأُولَٰئِكَ هُمُ الْمُضْعِفُونَ','آنچه بعنوان ربا می‌پردازید تا در اموال مردم فزونی یابد، نزد خدا فزونی نخواهد یافت؛ و آنچه را بعنوان زکات می‌پردازید و تنها رضای خدا را می‌طلبید (مایه برکت است؛ و) کسانی که چنین می‌کنند دارای پاداش مضاعفند.'];
ayat[3449]=[30,40,'اللَّهُ الَّذِي خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ۖ هَلْ مِن شُرَكَائِكُم مَّن يَفْعَلُ مِن ذَٰلِكُم مِّن شَيْءٍ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ','خداوند همان کسی است که شما را آفرید، سپس روزی داد، بعد می‌میراند، سپس زنده می‌کند؛ آیا هیچ یک از همتایانی که برای خدا قرار داده‌اید چیزی از این کارها را می‌توانند انجام دهند؟! او منزّه و برتر است از آنچه همتای او قرار می‌دهند.'];
ayat[3450]=[30,41,'ظَهَرَ الْفَسَادُ فِي الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ أَيْدِي النَّاسِ لِيُذِيقَهُم بَعْضَ الَّذِي عَمِلُوا لَعَلَّهُمْ يَرْجِعُونَ','فساد، در خشکی و دریا بخاطر کارهایی که مردم انجام داده‌اند آشکار شده است؛ خدا می‌خواهد نتیجه بعضی از اعمالشان را به آنان بچشاند، شاید (بسوی حق) بازگردند!'];
ayat[3451]=[30,42,'قُلْ سِيرُوا فِي الْأَرْضِ فَانظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِن قَبْلُ ۚ كَانَ أَكْثَرُهُم مُّشْرِكِينَ','بگو: «در زمین سیر کنید و بنگرید عاقبت کسانی که قبل از شما بودند چگونه بود؟ بیشتر آنها مشرک بودند!'];
ayat[3452]=[30,43,'فَأَقِمْ وَجْهَكَ لِلدِّينِ الْقَيِّمِ مِن قَبْلِ أَن يَأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهُ مِنَ اللَّهِ ۖ يَوْمَئِذٍ يَصَّدَّعُونَ','روی خود را بسوی آیین مستقیم و پایدار بدار، پیش از آنکه روزی فرا رسد که هیچ کس نمی‌تواند آن را از خدا بازگرداند؛ در آن روز مردم به گروه‌هایی تقسیم می‌شوند:'];
ayat[3453]=[30,44,'مَن كَفَرَ فَعَلَيْهِ كُفْرُهُ ۖ وَمَنْ عَمِلَ صَالِحًا فَلِأَنفُسِهِمْ يَمْهَدُونَ','هر کس کافر شود، کفرش بر زیان خود اوست؛ و آنها که کار شایسته انجام دهند، به سود خودشان آماده می‌سازند.'];
ayat[3454]=[30,45,'لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِن فَضْلِهِ ۚ إِنَّهُ لَا يُحِبُّ الْكَافِرِينَ','این برای آن است که خداوند کسانی را که ایمان آورده و اعمال صالح انجام داده‌اند، از فضلش پاداش دهد؛ او کافران را دوست نمی‌دارد!'];
ayat[3455]=[30,46,'وَمِنْ آيَاتِهِ أَن يُرْسِلَ الرِّيَاحَ مُبَشِّرَاتٍ وَلِيُذِيقَكُم مِّن رَّحْمَتِهِ وَلِتَجْرِيَ الْفُلْكُ بِأَمْرِهِ وَلِتَبْتَغُوا مِن فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ','و از آیات (عظمت) خدا این است که بادها را بعنوان بشارتگرانی می‌فرستد تا شما را از رحمتش بچشاند (و سیراب کند) و کشتیها بفرمانش حرکت کنند و از فضل او بهره گیرید؛ شاید شکرگزاری کنید.'];
ayat[3456]=[30,47,'وَلَقَدْ أَرْسَلْنَا مِن قَبْلِكَ رُسُلًا إِلَىٰ قَوْمِهِمْ فَجَاءُوهُم بِالْبَيِّنَاتِ فَانتَقَمْنَا مِنَ الَّذِينَ أَجْرَمُوا ۖ وَكَانَ حَقًّا عَلَيْنَا نَصْرُ الْمُؤْمِنِينَ','و پیش از تو پیامبرانی را بسوی قومشان فرستادیم؛ آنها با دلایل روشن به سراغ قوم خود رفتند، ولی (هنگامی که اندرزها سودی نداد) از مجرمان انتقام گرفتیم (و مؤمنان را یاری کردیم)؛ و یاری مؤمنان، همواره حقّی است بر عهده ما!'];
ayat[3457]=[30,48,'اللَّهُ الَّذِي يُرْسِلُ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَيَبْسُطُهُ فِي السَّمَاءِ كَيْفَ يَشَاءُ وَيَجْعَلُهُ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِهِ ۖ فَإِذَا أَصَابَ بِهِ مَن يَشَاءُ مِنْ عِبَادِهِ إِذَا هُمْ يَسْتَبْشِرُونَ','خداوند همان کسی است که بادها را می‌فرستد تا ابرهایی را به حرکت در آورند، سپس آنها را در پهنه آسمان آن گونه که بخواهد می‌گستراند و متراکم می‌سازد؛ در این هنگام دانه‌های باران را می‌بینی که از لا به لای آن خارج می‌شود، هنگامی که این (باران حیاتبخش) را به هر کس از بندگانش که بخواهد می‌رساند، ناگهان خوشحال می‌شوند...'];
ayat[3458]=[30,49,'وَإِن كَانُوا مِن قَبْلِ أَن يُنَزَّلَ عَلَيْهِم مِّن قَبْلِهِ لَمُبْلِسِينَ','و قطعاً پیش از آنکه بر آنان نازل شود مایوس بودند!'];
ayat[3459]=[30,50,'فَانظُرْ إِلَىٰ آثَارِ رَحْمَتِ اللَّهِ كَيْفَ يُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ إِنَّ ذَٰلِكَ لَمُحْيِي الْمَوْتَىٰ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','به آثار رحمت الهی بنگر که چگونه زمین را بعد از مردنش زنده می‌کند؛ چنین کسی (که زمین مرده را زنده کرد) زنده‌کننده مردگان (در قیامت) است؛ و او بر همه چیز تواناست!'];
ayat[3460]=[30,51,'وَلَئِنْ أَرْسَلْنَا رِيحًا فَرَأَوْهُ مُصْفَرًّا لَّظَلُّوا مِن بَعْدِهِ يَكْفُرُونَ','و اگر ما بادی بفرستیم (داغ و سوزان)، و بر اثر آن زراعت و باغ خود را زرد و پژمرده ببینند، (مأیوس شده و) پس از آن راه کفران پیش می‌گیرند!'];
ayat[3461]=[30,52,'فَإِنَّكَ لَا تُسْمِعُ الْمَوْتَىٰ وَلَا تُسْمِعُ الصُّمَّ الدُّعَاءَ إِذَا وَلَّوْا مُدْبِرِينَ','تو نمی‌توانی صدای خود را به گوش مردگان برسانی، و نه سخنت را به گوش کران هنگامی که روی برگردانند و دور شوند!'];
ayat[3462]=[30,53,'وَمَا أَنتَ بِهَادِ الْعُمْيِ عَن ضَلَالَتِهِمْ ۖ إِن تُسْمِعُ إِلَّا مَن يُؤْمِنُ بِآيَاتِنَا فَهُم مُّسْلِمُونَ','و (نیز) نمی‌توانی نابینایان را از گمراهیشان هدایت کنی؛ تو تنها سخنت را به گوش کسانی می‌رسانی که ایمان به آیات ما می‌آورند و در برابر حق تسلیمند!'];
ayat[3463]=[30,54,'۞ اللَّهُ الَّذِي خَلَقَكُم مِّن ضَعْفٍ ثُمَّ جَعَلَ مِن بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِن بَعْدِ قُوَّةٍ ضَعْفًا وَشَيْبَةً ۚ يَخْلُقُ مَا يَشَاءُ ۖ وَهُوَ الْعَلِيمُ الْقَدِيرُ','خدا همان کسی است که شما را آفرید در حالی که ضعیف بودید؛ سپس بعد از ناتوانی، قوّت بخشید و باز بعد از قوّت، ضعف و پیری قرار داد؛ او هر چه بخواهد می‌آفریند، و دانا و تواناست.'];
ayat[3464]=[30,55,'وَيَوْمَ تَقُومُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُونَ مَا لَبِثُوا غَيْرَ سَاعَةٍ ۚ كَذَٰلِكَ كَانُوا يُؤْفَكُونَ','و روزی که قیامت برپا شود، مجرمان سوگند یاد می‌کنند که جز ساعتی (در عالم برزخ) درنگ نکردند! اینچنین از درک حقیقت بازگردانده می‌شوند.'];
ayat[3465]=[30,56,'وَقَالَ الَّذِينَ أُوتُوا الْعِلْمَ وَالْإِيمَانَ لَقَدْ لَبِثْتُمْ فِي كِتَابِ اللَّهِ إِلَىٰ يَوْمِ الْبَعْثِ ۖ فَهَٰذَا يَوْمُ الْبَعْثِ وَلَٰكِنَّكُمْ كُنتُمْ لَا تَعْلَمُونَ','ولی کسانی که علم و ایمان به آنان داده شده می‌گویند: «شما بفرمان خدا تا روز قیامت (در عالم برزخ) درنگ کردید، و اکنون روز رستاخیز است، امّا شما نمی‌دانستید!»'];
ayat[3466]=[30,57,'فَيَوْمَئِذٍ لَّا يَنفَعُ الَّذِينَ ظَلَمُوا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُونَ','آن روز عذرخواهی ظالمان سودی به حالشان ندارد، و توبه آنان پذیرفته نمی‌شود.'];
ayat[3467]=[30,58,'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِي هَٰذَا الْقُرْآنِ مِن كُلِّ مَثَلٍ ۚ وَلَئِن جِئْتَهُم بِآيَةٍ لَّيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ أَنتُمْ إِلَّا مُبْطِلُونَ','ما برای مردم در این قرآن از هر گونه مثال و مطلبی بیان کردیم؛ و اگر آیه‌ای برای آنان بیاوری، کافران می‌گویند: «شما اهل باطلید (و اینها سحر و جادو است)!»'];
ayat[3468]=[30,59,'كَذَٰلِكَ يَطْبَعُ اللَّهُ عَلَىٰ قُلُوبِ الَّذِينَ لَا يَعْلَمُونَ','این گونه خداوند بر دلهای آنان که آگاهی ندارند مهر می‌نهد!'];
ayat[3469]=[30,60,'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۖ وَلَا يَسْتَخِفَّنَّكَ الَّذِينَ لَا يُوقِنُونَ','اکنون که چنین است صبر پیشه کن که وعده خدا حق است؛ و هرگز کسانی که ایمان ندارند تو را خشمگین نسازند (و از راه خود منحرف نکنند)!'];
ayat[3470]=[31,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم','الم‌'];
ayat[3471]=[31,2,'تِلْكَ آيَاتُ الْكِتَابِ الْحَكِيمِ','این آیات کتاب حکیم است (کتابی پرمحتوا و استوار)!'];
ayat[3472]=[31,3,'هُدًى وَرَحْمَةً لِّلْمُحْسِنِينَ','مایه هدایت و رحمت برای نیکوکاران است.'];
ayat[3473]=[31,4,'الَّذِينَ يُقِيمُونَ الصَّلَاةَ وَيُؤْتُونَ الزَّكَاةَ وَهُم بِالْآخِرَةِ هُمْ يُوقِنُونَ','همانان که نماز را برپا می‌دارند، و زکات را می‌پردازند و آنها به آخرت یقین دارند.'];
ayat[3474]=[31,5,'أُولَٰئِكَ عَلَىٰ هُدًى مِّن رَّبِّهِمْ ۖ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','آنان بر طریق هدایت از پروردگارشانند، و آنانند رستگاران!'];
ayat[3475]=[31,6,'وَمِنَ النَّاسِ مَن يَشْتَرِي لَهْوَ الْحَدِيثِ لِيُضِلَّ عَن سَبِيلِ اللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّخِذَهَا هُزُوًا ۚ أُولَٰئِكَ لَهُمْ عَذَابٌ مُّهِينٌ','و بعضی از مردم سخنان بیهوده را می‌خرند تا مردم را از روی نادانی، از راه خدا گمراه سازند و آیات الهی را به استهزا گیرند؛ برای آنان عذابی خوارکننده است!'];
ayat[3476]=[31,7,'وَإِذَا تُتْلَىٰ عَلَيْهِ آيَاتُنَا وَلَّىٰ مُسْتَكْبِرًا كَأَن لَّمْ يَسْمَعْهَا كَأَنَّ فِي أُذُنَيْهِ وَقْرًا ۖ فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ','و هنگامی که آیات ما بر او خوانده می‌شود، مستکبرانه روی برمی‌گرداند، گویی آن را نشنیده است؛ گویی اصلاً گوشهایش سنگین است! او را به عذابی دردناک بشارت ده!'];
ayat[3477]=[31,8,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتُ النَّعِيمِ','(ولی) کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، باغهای پرنعمت بهشت از آن آنهاست؛'];
ayat[3478]=[31,9,'خَالِدِينَ فِيهَا ۖ وَعْدَ اللَّهِ حَقًّا ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','جاودانه در آن خواهند ماند؛ این وعده حتمی الهی است؛ و اوست عزیز و حکیم (شکست‌ناپذیر و دانا).'];
ayat[3479]=[31,10,'خَلَقَ السَّمَاوَاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ۖ وَأَلْقَىٰ فِي الْأَرْضِ رَوَاسِيَ أَن تَمِيدَ بِكُمْ وَبَثَّ فِيهَا مِن كُلِّ دَابَّةٍ ۚ وَأَنزَلْنَا مِنَ السَّمَاءِ مَاءً فَأَنبَتْنَا فِيهَا مِن كُلِّ زَوْجٍ كَرِيمٍ','(او) آسمانها را بدون ستونی که آن را ببینید آفرید، و در زمین کوه‌هایی افکند تا شما را نلرزاند (و جایگاه شما آرام باشد) و از هر گونه جنبنده‌ای روی آن منتشر ساخت؛ و از آسمان آبی نازل کردیم و بوسیله آن در روی زمین انواع گوناگونی از جفتهای گیاهان پر ارزش رویاندیم.'];
ayat[3480]=[31,11,'هَٰذَا خَلْقُ اللَّهِ فَأَرُونِي مَاذَا خَلَقَ الَّذِينَ مِن دُونِهِ ۚ بَلِ الظَّالِمُونَ فِي ضَلَالٍ مُّبِينٍ','این آفرینش خداست؛ امّا به من نشان دهید معبودانی غیر او چه چیز را آفریده‌اند؟! ولی ظالمان در گمراهی آشکارند.'];
ayat[3481]=[31,12,'وَلَقَدْ آتَيْنَا لُقْمَانَ الْحِكْمَةَ أَنِ اشْكُرْ لِلَّهِ ۚ وَمَن يَشْكُرْ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِ ۖ وَمَن كَفَرَ فَإِنَّ اللَّهَ غَنِيٌّ حَمِيدٌ','ما به لقمان حکمت دادیم؛ (و به او گفتیم:) شکر خدا را بجای آور هر کس شکرگزاری کند، تنها به سود خویش شکر کرده؛ و آن کس که کفران کند، (زیانی به خدا نمی‌رساند)؛ چرا که خداوند بی‌نیاز و ستوده است.'];
ayat[3482]=[31,13,'وَإِذْ قَالَ لُقْمَانُ لِابْنِهِ وَهُوَ يَعِظُهُ يَا بُنَيَّ لَا تُشْرِكْ بِاللَّهِ ۖ إِنَّ الشِّرْكَ لَظُلْمٌ عَظِيمٌ','(به خاطر بیاور) هنگامی را که لقمان به فرزندش -در حالی که او را موعظه می‌کرد- گفت: «پسرم! چیزی را همتای خدا قرار مده که شرک، ظلم بزرگی است.»'];
ayat[3483]=[31,14,'وَوَصَّيْنَا الْإِنسَانَ بِوَالِدَيْهِ حَمَلَتْهُ أُمُّهُ وَهْنًا عَلَىٰ وَهْنٍ وَفِصَالُهُ فِي عَامَيْنِ أَنِ اشْكُرْ لِي وَلِوَالِدَيْكَ إِلَيَّ الْمَصِيرُ','و ما به انسان درباره پدر و مادرش سفارش کردیم؛ مادرش او را با ناتوانی روی ناتوانی حمل کرد (به هنگام بارداری هر روز رنج و ناراحتی تازه‌ای را متحمّل می‌شد)، و دوران شیرخوارگی او در دو سال پایان می‌یابد؛ (آری به او توصیه کردم) که برای من و برای پدر و مادرت شکر بجا آور که بازگشت (همه شما) به سوی من است!'];
ayat[3484]=[31,15,'وَإِن جَاهَدَاكَ عَلَىٰ أَن تُشْرِكَ بِي مَا لَيْسَ لَكَ بِهِ عِلْمٌ فَلَا تُطِعْهُمَا ۖ وَصَاحِبْهُمَا فِي الدُّنْيَا مَعْرُوفًا ۖ وَاتَّبِعْ سَبِيلَ مَنْ أَنَابَ إِلَيَّ ۚ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','و هرگاه آن دو، تلاش کنند که تو چیزی را همتای من قرار دهی، که از آن آگاهی نداری (بلکه می‌دانی باطل است)، از ایشان اطاعت مکن، ولی با آن دو، در دنیا به طرز شایسته‌ای رفتار کن؛ و از راه کسانی پیروی کن که توبه‌کنان به سوی من آمده‌اند؛ سپس بازگشت همه شما به سوی من است و من شما را از آنچه عمل می‌کردید آگاه می‌کنم.'];
ayat[3485]=[31,16,'يَا بُنَيَّ إِنَّهَا إِن تَكُ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ فَتَكُن فِي صَخْرَةٍ أَوْ فِي السَّمَاوَاتِ أَوْ فِي الْأَرْضِ يَأْتِ بِهَا اللَّهُ ۚ إِنَّ اللَّهَ لَطِيفٌ خَبِيرٌ','پسرم! اگر به اندازه سنگینی دانه خردلی (کار نیک یا بد) باشد، و در دل سنگی یا در (گوشه‌ای از) آسمانها و زمین قرار گیرد، خداوند آن را (در قیامت برای حساب) می‌آورد؛ خداوند دقیق و آگاه است!'];
ayat[3486]=[31,17,'يَا بُنَيَّ أَقِمِ الصَّلَاةَ وَأْمُرْ بِالْمَعْرُوفِ وَانْهَ عَنِ الْمُنكَرِ وَاصْبِرْ عَلَىٰ مَا أَصَابَكَ ۖ إِنَّ ذَٰلِكَ مِنْ عَزْمِ الْأُمُورِ','پسرم! نماز را برپا دار، و امر به معروف و نهی از منکر کن، و در برابر مصایبی که به تو می‌رسد شکیبا باش که این از کارهای مهمّ است!'];
ayat[3487]=[31,18,'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِي الْأَرْضِ مَرَحًا ۖ إِنَّ اللَّهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ','(پسرم!) با بی‌اعتنایی از مردم روی مگردان، و مغرورانه بر زمین راه مرو که خداوند هیچ متکبّر مغروری را دوست ندارد.'];
ayat[3488]=[31,19,'وَاقْصِدْ فِي مَشْيِكَ وَاغْضُضْ مِن صَوْتِكَ ۚ إِنَّ أَنكَرَ الْأَصْوَاتِ لَصَوْتُ الْحَمِيرِ','(پسرم!) در راه‌رفتن، اعتدال را رعایت کن؛ از صدای خود بکاه (و هرگز فریاد مزن) که زشت‌ترین صداها صدای خران است.'];
ayat[3489]=[31,20,'أَلَمْ تَرَوْا أَنَّ اللَّهَ سَخَّرَ لَكُم مَّا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُ ظَاهِرَةً وَبَاطِنَةً ۗ وَمِنَ النَّاسِ مَن يُجَادِلُ فِي اللَّهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَابٍ مُّنِيرٍ','آیا ندیدید خداوند آنچه را در آسمانها و زمین است مسخّر شما کرده، و نعمتهای آشکار و پنهان خود را به طور فراوان بر شما ارزانی داشته است؟! ولی بعضی از مردم بدون هیچ دانش و هدایت و کتاب روشنگری درباره خدا مجادله می‌کنند!'];
ayat[3490]=[31,21,'وَإِذَا قِيلَ لَهُمُ اتَّبِعُوا مَا أَنزَلَ اللَّهُ قَالُوا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ آبَاءَنَا ۚ أَوَلَوْ كَانَ الشَّيْطَانُ يَدْعُوهُمْ إِلَىٰ عَذَابِ السَّعِيرِ','و هنگامی که به آنان گفته شود: «از آنچه خدا نازل کرده پیروی کنید!»، می‌گویند: «نه، بلکه ما از چیزی پیروی می‌کنیم که پدران خود را بر آن یافتیم!» آیا حتّی اگر شیطان آنان را دعوت به عذاب آتش فروزان کند (باز هم تبعیّت می‌کنند)؟!'];
ayat[3491]=[31,22,'۞ وَمَن يُسْلِمْ وَجْهَهُ إِلَى اللَّهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقَىٰ ۗ وَإِلَى اللَّهِ عَاقِبَةُ الْأُمُورِ','کسی که روی خود را تسلیم خدا کند در حالی که نیکوکار باشد، به دستگیره محکمی چنگ زده (و به تکیه‌گاه مطمئنی تکیه کرده است)؛ و عاقبت همه کارها به سوی خداست.'];
ayat[3492]=[31,23,'وَمَن كَفَرَ فَلَا يَحْزُنكَ كُفْرُهُ ۚ إِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُم بِمَا عَمِلُوا ۚ إِنَّ اللَّهَ عَلِيمٌ بِذَاتِ الصُّدُورِ','و کسی که کافر شود، کفر او تو را غمگین نسازد؛ بازگشت همه آنان به سوی ماست و ما آنها را از اعمالی که انجام داده‌اند (و نتایج شوم آن) آگاه خواهیم ساخت؛ خداوند به آنچه درون سینه‌هاست آگاه است.'];
ayat[3493]=[31,24,'نُمَتِّعُهُمْ قَلِيلًا ثُمَّ نَضْطَرُّهُمْ إِلَىٰ عَذَابٍ غَلِيظٍ','ما اندکی آنها را از متاع دنیا بهره‌مند می‌کنیم، سپس آنها را به تحمّل عذاب شدیدی وادار می‌سازیم!'];
ayat[3494]=[31,25,'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ لَيَقُولُنَّ اللَّهُ ۚ قُلِ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ','و هرگاه از آنان سؤال کنی: «چه کسی آسمانها و زمین را آفریده است؟» مسلّماً می‌گویند: «اللّه»، بگو: «الحمد للّه (که خود شما معترفید)!» ولی بیشتر آنان نمی‌دانند!'];
ayat[3495]=[31,26,'لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ','آنچه در آسمانها و زمین است از آن خداست، چرا که خداوند بی‌نیاز و شایسته ستایش است!'];
ayat[3496]=[31,27,'وَلَوْ أَنَّمَا فِي الْأَرْضِ مِن شَجَرَةٍ أَقْلَامٌ وَالْبَحْرُ يَمُدُّهُ مِن بَعْدِهِ سَبْعَةُ أَبْحُرٍ مَّا نَفِدَتْ كَلِمَاتُ اللَّهِ ۗ إِنَّ اللَّهَ عَزِيزٌ حَكِيمٌ','و اگر همه درختان روی زمین قلم شود، و دریا برای آن مرکّب گردد، و هفت دریاچه به آن افزوده شود، اینها همه تمام می‌شود ولی کلمات خدا پایان نمی‌گیرد؛ خداوند عزیز و حکیم است.'];
ayat[3497]=[31,28,'مَّا خَلْقُكُمْ وَلَا بَعْثُكُمْ إِلَّا كَنَفْسٍ وَاحِدَةٍ ۗ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ','آفرینش و برانگیختن (و زندگی دوباره) همه شما (در قیامت) همانند یک فرد بیش نیست؛ خداوند شنوا و بیناست!'];
ayat[3498]=[31,29,'أَلَمْ تَرَ أَنَّ اللَّهَ يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي إِلَىٰ أَجَلٍ مُّسَمًّى وَأَنَّ اللَّهَ بِمَا تَعْمَلُونَ خَبِيرٌ','آیا ندیدی که خداوند شب را در روز، و روز را در شب داخل می‌کند، و خورشید و ماه را مسخّر ساخته و هر کدام تا سرآمد معینی به حرکت خود ادامه می‌دهند؟! خداوند به آنچه انجام می‌دهید آگاه است.'];
ayat[3499]=[31,30,'ذَٰلِكَ بِأَنَّ اللَّهَ هُوَ الْحَقُّ وَأَنَّ مَا يَدْعُونَ مِن دُونِهِ الْبَاطِلُ وَأَنَّ اللَّهَ هُوَ الْعَلِيُّ الْكَبِيرُ','اینها همه دلیل بر آن است که خداوند حقّ است، و آنچه غیر از او می‌خوانند باطل است، و خداوند بلند مقام و بزرگ مرتبه است!'];
ayat[3500]=[31,31,'أَلَمْ تَرَ أَنَّ الْفُلْكَ تَجْرِي فِي الْبَحْرِ بِنِعْمَتِ اللَّهِ لِيُرِيَكُم مِّنْ آيَاتِهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ','آیا ندیدی کشتیها بر (صفحه) دریاها به فرمان خدا، و به (برکت) نعمت او حرکت می‌کنند تا بخشی از آیاتش را به شما نشان دهد؟! در اینها نشانه‌هایی است برای کسانی که شکیبا و شکرگزارند!'];
ayat[3501]=[31,32,'وَإِذَا غَشِيَهُم مَّوْجٌ كَالظُّلَلِ دَعَوُا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ فَلَمَّا نَجَّاهُمْ إِلَى الْبَرِّ فَمِنْهُم مُّقْتَصِدٌ ۚ وَمَا يَجْحَدُ بِآيَاتِنَا إِلَّا كُلُّ خَتَّارٍ كَفُورٍ','و هنگامی که (در سفر دریا) موجی همچون ابرها آنان را بپوشاند (و بالا رود و بالای سرشان قرار گیرد)، خدا را با اخلاص می‌خوانند؛ امّا وقتی آنها را به خشکی رساند و نجات داد، بعضی راه اعتدال را پیش می‌گیرند (و به ایمان خود وفادار می‌مانند، در حالی که بعضی دیگر فراموش کرده راه کفر پیش می‌گیرند)؛ ولی آیات ما را هیچ کس جز پیمان‌شکنان ناسپاس انکار نمی‌کنند!'];
ayat[3502]=[31,33,'يَا أَيُّهَا النَّاسُ اتَّقُوا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَّا يَجْزِي وَالِدٌ عَن وَلَدِهِ وَلَا مَوْلُودٌ هُوَ جَازٍ عَن وَالِدِهِ شَيْئًا ۚ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۖ فَلَا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا وَلَا يَغُرَّنَّكُم بِاللَّهِ الْغَرُورُ','ای مردم! تقوای الهی پیشه کنید و بترسید از روزی که نه پدر کیفر اعمال فرزندش را تحمّل می‌کند، و نه فرزند چیزی از کیفر (اعمال) پدرش را؛ به یقین وعده الهی حقّ است؛ پس مبادا زندگانی دنیا شما را بفریبد، و مبادا (شیطان) فریبکار شما را (به کرم) خدا مغرور سازد!'];
ayat[3503]=[31,34,'إِنَّ اللَّهَ عِندَهُ عِلْمُ السَّاعَةِ وَيُنَزِّلُ الْغَيْثَ وَيَعْلَمُ مَا فِي الْأَرْحَامِ ۖ وَمَا تَدْرِي نَفْسٌ مَّاذَا تَكْسِبُ غَدًا ۖ وَمَا تَدْرِي نَفْسٌ بِأَيِّ أَرْضٍ تَمُوتُ ۚ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ','آگاهی از زمان قیام قیامت مخصوص خداست، و اوست که باران را نازل می‌کند، و آنچه را که در رحم‌ها (ی مادران) است می‌داند، و هیچ کس نمی‌داند فردا چه به دست می‌آورد، و هیچ کس نمی‌داند در چه سرزمینی می‌میرد؟ خداوند عالم و آگاه است!'];
ayat[3504]=[32,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الم','الم‌'];
ayat[3505]=[32,2,'تَنزِيلُ الْكِتَابِ لَا رَيْبَ فِيهِ مِن رَّبِّ الْعَالَمِينَ','این کتابی است که از سوی پروردگار جهانیان نازل شده، و شک و تردیدی در آن نیست!'];
ayat[3506]=[32,3,'أَمْ يَقُولُونَ افْتَرَاهُ ۚ بَلْ هُوَ الْحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوْمًا مَّا أَتَاهُم مِّن نَّذِيرٍ مِّن قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ','ولی آنان می‌گویند: «(محمّد) آن را بدروغ به خدا بسته است»، امّا این سخن حقّی است از سوی پروردگارت تا گروهی را انذار کنی که پیش از تو هیچ انذارکننده‌ای برای آنان نیامده است، شاید (پند گیرند و) هدایت شوند!'];
ayat[3507]=[32,4,'اللَّهُ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۖ مَا لَكُم مِّن دُونِهِ مِن وَلِيٍّ وَلَا شَفِيعٍ ۚ أَفَلَا تَتَذَكَّرُونَ','خداوند کسی است که آسمانها و زمین و آنچه را میان این دو است در شش روز [= شش دوران‌] آفرید، سپس بر عرش (قدرت) قرار گرفت؛ هیچ سرپرست و شفاعت کننده‌ای برای شما جز او نیست؛ آیا متذکّر نمی‌شوید؟!'];
ayat[3508]=[32,5,'يُدَبِّرُ الْأَمْرَ مِنَ السَّمَاءِ إِلَى الْأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ أَلْفَ سَنَةٍ مِّمَّا تَعُدُّونَ','امور این جهان را از آسمان به سوی زمین تدبیر می‌کند؛ سپس در روزی که مقدار آن هزار سال از سالهایی است که شما می‌شمرید بسوی او بالا می‌رود (و دنیا پایان می‌یابد).'];
ayat[3509]=[32,6,'ذَٰلِكَ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الرَّحِيمُ','او خداوندی است که از پنهان و آشکار با خبر است، و شکست‌ناپذیر و مهربان است!'];
ayat[3510]=[32,7,'الَّذِي أَحْسَنَ كُلَّ شَيْءٍ خَلَقَهُ ۖ وَبَدَأَ خَلْقَ الْإِنسَانِ مِن طِينٍ','او همان کسی است که هر چه را آفرید نیکو آفرید؛ و آفرینش انسان را از گِل آغاز کرد؛'];
ayat[3511]=[32,8,'ثُمَّ جَعَلَ نَسْلَهُ مِن سُلَالَةٍ مِّن مَّاءٍ مَّهِينٍ','سپس نسل او را از عصاره‌ای از آب ناچیز و بی‌قدر آفرید.'];
ayat[3512]=[32,9,'ثُمَّ سَوَّاهُ وَنَفَخَ فِيهِ مِن رُّوحِهِ ۖ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۚ قَلِيلًا مَّا تَشْكُرُونَ','سپس (اندام) او را موزون ساخت و از روح خویش در وی دمید؛ و برای شما گوش و چشمها و دلها قرار داد؛ امّا کمتر شکر نعمتهای او را بجا می‌آورید!'];
ayat[3513]=[32,10,'وَقَالُوا أَإِذَا ضَلَلْنَا فِي الْأَرْضِ أَإِنَّا لَفِي خَلْقٍ جَدِيدٍ ۚ بَلْ هُم بِلِقَاءِ رَبِّهِمْ كَافِرُونَ','آنها گفتند: «آیا هنگامی که ما (مردیم و) در زمین گم شدیم، آفرینش تازه‌ای خواهیم یافت؟!» ولی آنان لقای پروردگارشان را انکار می‌کنند (و می‌خواهند با انکار معاد، آزادانه به هوسرانی خویش ادامه دهند).'];
ayat[3514]=[32,11,'۞ قُلْ يَتَوَفَّاكُم مَّلَكُ الْمَوْتِ الَّذِي وُكِّلَ بِكُمْ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ','بگو: «فرشته مرگ که بر شما مأمور شده، (روح) شما را می‌گیرد؛ سپس شما را بسوی پروردگارتان بازمی‌گردانند.»'];
ayat[3515]=[32,12,'وَلَوْ تَرَىٰ إِذِ الْمُجْرِمُونَ نَاكِسُو رُءُوسِهِمْ عِندَ رَبِّهِمْ رَبَّنَا أَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا إِنَّا مُوقِنُونَ','و اگر ببینی مجرمان را هنگامی که در پیشگاه پروردگارشان سر به زیر افکنده، می‌گویند: «پروردگارا! آنچه وعده کرده بودی دیدیم و شنیدیم؛ ما را بازگردان تا کار شایسته‌ای انجام دهیم؛ ما (به قیامت) یقین داریم!»'];
ayat[3516]=[32,13,'وَلَوْ شِئْنَا لَآتَيْنَا كُلَّ نَفْسٍ هُدَاهَا وَلَٰكِنْ حَقَّ الْقَوْلُ مِنِّي لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ أَجْمَعِينَ','و اگر می‌خواستیم به هر انسانی هدایت لازمش را (از روی اجبار بدهیم) می‌دادیم؛ ولی (من آنها را آزاد گذارده‌ام و) سخن و وعده‌ام حق است که دوزخ را (از افراد بی‌ایمان و گنهکار) از جنّ و انس همگی پر کنم!'];
ayat[3517]=[32,14,'فَذُوقُوا بِمَا نَسِيتُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا إِنَّا نَسِينَاكُمْ ۖ وَذُوقُوا عَذَابَ الْخُلْدِ بِمَا كُنتُمْ تَعْمَلُونَ','(و به آنها می‌گویم:) بچشید (عذاب جهنم را)! بخاطر اینکه دیدار امروزتان را فراموش کردید، ما نیز شما را فراموش کردیم؛ و بچشید عذاب جاودان را به خاطر اعمالی که انجام می‌دادید!'];
ayat[3518]=[32,15,'إِنَّمَا يُؤْمِنُ بِآيَاتِنَا الَّذِينَ إِذَا ذُكِّرُوا بِهَا خَرُّوا سُجَّدًا وَسَبَّحُوا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَ ۩','تنها کسانی که به آیات ما ایمان می‌آورند که هر وقت این آیات به آنان یادآوری شود به سجده می‌افتند و تسبیح و حمد پروردگارشان را بجا می‌آورند، و تکبّر نمی‌کنند.'];
ayat[3519]=[32,16,'تَتَجَافَىٰ جُنُوبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفًا وَطَمَعًا وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ','پهلوهایشان از بسترها در دل شب دور می‌شود (و بپا می‌خیزند و رو به درگاه خدا می آورند) و پروردگار خود را با بیم و امید می‌خوانند، و از آنچه به آنان روزی داده‌ایم انفاق می‌کنند!'];
ayat[3520]=[32,17,'فَلَا تَعْلَمُ نَفْسٌ مَّا أُخْفِيَ لَهُم مِّن قُرَّةِ أَعْيُنٍ جَزَاءً بِمَا كَانُوا يَعْمَلُونَ','هیچ کس نمی‌داند چه پاداشهای مهمّی که مایه روشنی چشمهاست برای آنها نهفته شده، این پاداش کارهائی است که انجام می‌دادند!'];
ayat[3521]=[32,18,'أَفَمَن كَانَ مُؤْمِنًا كَمَن كَانَ فَاسِقًا ۚ لَّا يَسْتَوُونَ','آیا کسی که باایمان باشد همچون کسی است که فاسق است؟! نه، هرگز این دو برابر نیستند.'];
ayat[3522]=[32,19,'أَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ جَنَّاتُ الْمَأْوَىٰ نُزُلًا بِمَا كَانُوا يَعْمَلُونَ','امّا کسانی که ایمان آوردند و کارهای شایسته انجام دادند، باغهای بهشت جاویدان از آن آنها خواهد بود، این وسیله پذیرایی (خداوند) از آنهاست به پاداش آنچه انجام می‌دادند.'];
ayat[3523]=[32,20,'وَأَمَّا الَّذِينَ فَسَقُوا فَمَأْوَاهُمُ النَّارُ ۖ كُلَّمَا أَرَادُوا أَن يَخْرُجُوا مِنْهَا أُعِيدُوا فِيهَا وَقِيلَ لَهُمْ ذُوقُوا عَذَابَ النَّارِ الَّذِي كُنتُم بِهِ تُكَذِّبُونَ','و امّا کسانی که فاسق شدند (و از اطاعت خدا سرباز زدند)، جایگاه همیشگی آنها آتش است؛ هر زمان بخواهند از آن خارج شوند، آنها را به آن بازمی‌گردانند و به آنان گفته می‌شود: «بچشید عذاب آتشی را که انکار می‌کردید!»'];
ayat[3524]=[32,21,'وَلَنُذِيقَنَّهُم مِّنَ الْعَذَابِ الْأَدْنَىٰ دُونَ الْعَذَابِ الْأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ','به آنان از عذاب نزدیک (عذاب این دنیا) پیش از عذاب بزرگ (آخرت) می‌چشانیم، شاید بازگردند!'];
ayat[3525]=[32,22,'وَمَنْ أَظْلَمُ مِمَّن ذُكِّرَ بِآيَاتِ رَبِّهِ ثُمَّ أَعْرَضَ عَنْهَا ۚ إِنَّا مِنَ الْمُجْرِمِينَ مُنتَقِمُونَ','چه کسی ستمکارتر است از آن کس که آیات پروردگارش به او یادآوری شده و او از آن اعراض کرده است؟! مسلّماً ما از مجرمان انتقام خواهیم گرفت!'];
ayat[3526]=[32,23,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَلَا تَكُن فِي مِرْيَةٍ مِّن لِّقَائِهِ ۖ وَجَعَلْنَاهُ هُدًى لِّبَنِي إِسْرَائِيلَ','ما به موسی کتاب آسمانی دادیم؛ و شک نداشته باش که او آیات الهی را دریافت داشت؛ و ما آن را وسیله هدایت بنی اسرائیل قرار دادیم!'];
ayat[3527]=[32,24,'وَجَعَلْنَا مِنْهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا لَمَّا صَبَرُوا ۖ وَكَانُوا بِآيَاتِنَا يُوقِنُونَ','و از آنان امامان (و پیشوایانی) قرار دادیم که به فرمان ما (مردم را) هدایت می‌کردند؛ چون شکیبایی نمودند، و به آیات ما یقین داشتند.'];
ayat[3528]=[32,25,'إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ','البتّه پروردگار تو میان آنان روز قیامت در آنچه اختلاف داشتند داوری می‌کند (و هر کس را به سزای اعمالش می‌رساند)!'];
ayat[3529]=[32,26,'أَوَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّنَ الْقُرُونِ يَمْشُونَ فِي مَسَاكِنِهِمْ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ ۖ أَفَلَا يَسْمَعُونَ','آیا برای هدایت آنها همین کافی نیست که افراد زیادی را که در قرن پیش از آنان زندگی داشتند هلاک کردیم؟! اینها در مساکن (ویران شده) آنان راه می‌روند؛ در این آیاتی است (از قدرت خداوند و مجازات دردناک مجرمان)؛ آیا نمی‌شنوند؟!'];
ayat[3530]=[32,27,'أَوَلَمْ يَرَوْا أَنَّا نَسُوقُ الْمَاءَ إِلَى الْأَرْضِ الْجُرُزِ فَنُخْرِجُ بِهِ زَرْعًا تَأْكُلُ مِنْهُ أَنْعَامُهُمْ وَأَنفُسُهُمْ ۖ أَفَلَا يُبْصِرُونَ','آیا ندیدند که ما آب را بسوی زمینهای خشک می‌رانیم و بوسیله آن زراعتهایی می‌رویانیم که هم چهارپایانشان از آن می‌خورند و هم خودشان تغذیه می‌کنند؛ آیا نمی بینند؟!'];
ayat[3531]=[32,28,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْفَتْحُ إِن كُنتُمْ صَادِقِينَ','آنان می‌گویند: «اگر راست می‌گویید، این پیروزی شما کی خواهد بود؟!»'];
ayat[3532]=[32,29,'قُلْ يَوْمَ الْفَتْحِ لَا يَنفَعُ الَّذِينَ كَفَرُوا إِيمَانُهُمْ وَلَا هُمْ يُنظَرُونَ','بگو: «روز پیروزی، ایمان آوردن، سودی به حال کافران نخواهد داشت؛ و به آنها هیچ مهلت داده نمی‌شود!»'];
ayat[3533]=[32,30,'فَأَعْرِضْ عَنْهُمْ وَانتَظِرْ إِنَّهُم مُّنتَظِرُونَ','حال که چنین است، از آنها روی بگردان و منتظر باش؛ آنها نیز منتظرند! (تو منتظر رحمت خدا و آنها هم منتظر عذاب او!)'];
ayat[3534]=[33,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ اتَّقِ اللَّهَ وَلَا تُطِعِ الْكَافِرِينَ وَالْمُنَافِقِينَ ۗ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا','ای پیامبر! تقوای الهی پیشه کن و از کافران و منافقان اطاعت مکن که خداوند عالم و حکیم است.'];
ayat[3535]=[33,2,'وَاتَّبِعْ مَا يُوحَىٰ إِلَيْكَ مِن رَّبِّكَ ۚ إِنَّ اللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا','و از آنچه از سوی پروردگارت به تو وحی می‌شود پیروی کن که خداوند به آنچه انجام می‌دهید آگاه است.'];
ayat[3536]=[33,3,'وَتَوَكَّلْ عَلَى اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا','و بر خدا توکّل کن، و همین بس که خداوند حافظ و مدافع (انسان) باشد!'];
ayat[3537]=[33,4,'مَّا جَعَلَ اللَّهُ لِرَجُلٍ مِّن قَلْبَيْنِ فِي جَوْفِهِ ۚ وَمَا جَعَلَ أَزْوَاجَكُمُ اللَّائِي تُظَاهِرُونَ مِنْهُنَّ أُمَّهَاتِكُمْ ۚ وَمَا جَعَلَ أَدْعِيَاءَكُمْ أَبْنَاءَكُمْ ۚ ذَٰلِكُمْ قَوْلُكُم بِأَفْوَاهِكُمْ ۖ وَاللَّهُ يَقُولُ الْحَقَّ وَهُوَ يَهْدِي السَّبِيلَ','خداوند برای هیچ کس دو دل در درونش نیافریده؛ و هرگز همسرانتان را که مورد «ظهار» قرارمی‌دهید مادران شما قرار نداده؛ و (نیز) فرزندخوانده‌های شما را فرزند حقیقی شما قرارنداده است؛ این سخن شماست که به دهان خود می‌گویید (سخنی باطل و بی‌پایه)؛ امّا خداوند حقّ را می‌گوید و او به راه راست هدایت می‌کند.'];
ayat[3538]=[33,5,'ادْعُوهُمْ لِآبَائِهِمْ هُوَ أَقْسَطُ عِندَ اللَّهِ ۚ فَإِن لَّمْ تَعْلَمُوا آبَاءَهُمْ فَإِخْوَانُكُمْ فِي الدِّينِ وَمَوَالِيكُمْ ۚ وَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيمَا أَخْطَأْتُم بِهِ وَلَٰكِن مَّا تَعَمَّدَتْ قُلُوبُكُمْ ۚ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','آنها را به نام پدرانشان بخوانید که این کار نزد خدا عادلانه‌تر است؛ و اگر پدرانشان را نمی‌شناسید، آنها برادران دینی و موالی شما هستند؛ امّا گناهی بر شما نیست در خطاهایی که از شما سرمی‌زند (و بی‌توجّه آنها را به نام دیگران صدا می‌زنید)، ولی آنچه را از روی عمد می‌گویید (مورد حساب قرار خواهد داد)؛ و خداوند آمرزنده و رحیم است.'];
ayat[3539]=[33,6,'النَّبِيُّ أَوْلَىٰ بِالْمُؤْمِنِينَ مِنْ أَنفُسِهِمْ ۖ وَأَزْوَاجُهُ أُمَّهَاتُهُمْ ۗ وَأُولُو الْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٍ فِي كِتَابِ اللَّهِ مِنَ الْمُؤْمِنِينَ وَالْمُهَاجِرِينَ إِلَّا أَن تَفْعَلُوا إِلَىٰ أَوْلِيَائِكُم مَّعْرُوفًا ۚ كَانَ ذَٰلِكَ فِي الْكِتَابِ مَسْطُورًا','پیامبر نسبت به مؤمنان از خودشان سزاوارتر است؛ و همسران او مادران آنها [= مؤمنان‌] محسوب می‌شوند؛ و خویشاوندان نسبت به یکدیگر از مؤمنان و مهاجران در آنچه خدا مقرّر داشته اولی هستند، مگر اینکه بخواهید نسبت به دوستانتان نیکی کنید (و سهمی از اموال خود را به آنها بدهید)؛ این حکم در کتاب (الهی) نوشته شده است.'];
ayat[3540]=[33,7,'وَإِذْ أَخَذْنَا مِنَ النَّبِيِّينَ مِيثَاقَهُمْ وَمِنكَ وَمِن نُّوحٍ وَإِبْرَاهِيمَ وَمُوسَىٰ وَعِيسَى ابْنِ مَرْيَمَ ۖ وَأَخَذْنَا مِنْهُم مِّيثَاقًا غَلِيظًا','(به خاطر آور) هنگامی را که از پیامبران پیمان گرفتیم، و (همچنین) از تو و از نوح و ابراهیم و موسی و عیسی بن مریم، و ما از همه آنان پیمان محکمی گرفتیم (که در ادای مسؤولیت تبلیغ و رسالت کوتاهی نکنند)!'];
ayat[3541]=[33,8,'لِّيَسْأَلَ الصَّادِقِينَ عَن صِدْقِهِمْ ۚ وَأَعَدَّ لِلْكَافِرِينَ عَذَابًا أَلِيمًا','به این منظور که خدا راستگویان را از صدقشان (در ایمان و عمل صالح) سؤال کند؛ و برای کافران عذابی دردناک آماده ساخته است.'];
ayat[3542]=[33,9,'يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَةَ اللَّهِ عَلَيْكُمْ إِذْ جَاءَتْكُمْ جُنُودٌ فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا وَجُنُودًا لَّمْ تَرَوْهَا ۚ وَكَانَ اللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا','ای کسانی که ایمان آورده‌اید! نعمت خدا را بر خود به یاد آورید در آن هنگام که لشکرهایی (عظیم) به سراغ شما آمدند؛ ولی ما باد و طوفان سختی بر آنان فرستادیم و لشکریانی که آنها را نمی‌دیدید (و به این وسیله آنها را در هم شکستیم)؛ و خداوند همیشه به آنچه انجام می‌دهید بینا بوده است.'];
ayat[3543]=[33,10,'إِذْ جَاءُوكُم مِّن فَوْقِكُمْ وَمِنْ أَسْفَلَ مِنكُمْ وَإِذْ زَاغَتِ الْأَبْصَارُ وَبَلَغَتِ الْقُلُوبُ الْحَنَاجِرَ وَتَظُنُّونَ بِاللَّهِ الظُّنُونَا','(به خاطر بیاورید) زمانی را که آنها از طرف بالا و پایین (شهر) بر شما وارد شدند (و مدینه را محاصره کردند) و زمانی را که چشمها از شدّت وحشت خیره شده و جانها به لب رسیده بود، و گمانهای گوناگون بدی به خدا می‌بردید.'];
ayat[3544]=[33,11,'هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُونَ وَزُلْزِلُوا زِلْزَالًا شَدِيدًا','آنجا بود که مؤمنان آزمایش شدند و تکان سختی خوردند!'];
ayat[3545]=[33,12,'وَإِذْ يَقُولُ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِم مَّرَضٌ مَّا وَعَدَنَا اللَّهُ وَرَسُولُهُ إِلَّا غُرُورًا','و (نیز) به خاطر آورید زمانی را که منافقان و بیماردلان می‌گفتند: «خدا و پیامبرش جز وعده‌های دروغین به ما نداده‌اند!»'];
ayat[3546]=[33,13,'وَإِذْ قَالَت طَّائِفَةٌ مِّنْهُمْ يَا أَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُوا ۚ وَيَسْتَأْذِنُ فَرِيقٌ مِّنْهُمُ النَّبِيَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوْرَةٌ وَمَا هِيَ بِعَوْرَةٍ ۖ إِن يُرِيدُونَ إِلَّا فِرَارًا','و (نیز) به خاطر آورید زمانی را که گروهی از آنها گفتند: «ای اهل یثرب (ای مردم مدینه)! اینجا جای توقف شما نیست؛ به خانه‌های خود بازگردید!» و گروهی از آنان از پیامبر اجازه بازگشت می‌خواستند و می‌گفتند: «خانه‌های ما بی‌حفاظ است!»، در حالی که بی‌حفاظ نبود؛ آنها فقط می‌خواستند (از جنگ) فرار کنند.'];
ayat[3547]=[33,14,'وَلَوْ دُخِلَتْ عَلَيْهِم مِّنْ أَقْطَارِهَا ثُمَّ سُئِلُوا الْفِتْنَةَ لَآتَوْهَا وَمَا تَلَبَّثُوا بِهَا إِلَّا يَسِيرًا','آنها چنان بودند که اگر دشمنان از اطراف مدینه بر آنان وارد می‌شدند و پیشنهاد بازگشت به سوی شرک به آنان می‌کردند می‌پذیرفتند، و جز مدّت کمی (برای انتخاب این راه) درنگ نمی‌کردند!'];
ayat[3548]=[33,15,'وَلَقَدْ كَانُوا عَاهَدُوا اللَّهَ مِن قَبْلُ لَا يُوَلُّونَ الْأَدْبَارَ ۚ وَكَانَ عَهْدُ اللَّهِ مَسْئُولًا','(در حالی که) آنان قبل از این با خدا عهد کرده بودند که پشت به دشمن نکنند؛ و عهد الهی مورد سؤال قرار خواهد گرفت (و در برابر آن مسؤولند)!'];
ayat[3549]=[33,16,'قُل لَّن يَنفَعَكُمُ الْفِرَارُ إِن فَرَرْتُم مِّنَ الْمَوْتِ أَوِ الْقَتْلِ وَإِذًا لَّا تُمَتَّعُونَ إِلَّا قَلِيلًا','بگو: «اگر از مرگ یا کشته‌شدن فرار کنید، سودی به حال شما نخواهد داشت؛ و در آن هنگام جز بهره کمی از زندگانی نخواهید گرفت!»'];
ayat[3550]=[33,17,'قُلْ مَن ذَا الَّذِي يَعْصِمُكُم مِّنَ اللَّهِ إِنْ أَرَادَ بِكُمْ سُوءًا أَوْ أَرَادَ بِكُمْ رَحْمَةً ۚ وَلَا يَجِدُونَ لَهُم مِّن دُونِ اللَّهِ وَلِيًّا وَلَا نَصِيرًا','بگو: «چه کسی می‌تواند شما را در برابر اراده خدا حفظ کند اگر او بدی یا رحمتی را برای شما اراده کند؟!» و آنها جز خدا هیچ سرپرست و یاوری برای خود نخواهند یافت.'];
ayat[3551]=[33,18,'۞ قَدْ يَعْلَمُ اللَّهُ الْمُعَوِّقِينَ مِنكُمْ وَالْقَائِلِينَ لِإِخْوَانِهِمْ هَلُمَّ إِلَيْنَا ۖ وَلَا يَأْتُونَ الْبَأْسَ إِلَّا قَلِيلًا','خداوند کسانی که مردم را از جنگ بازمی‌داشتند و کسانی را که به برادران خود می‌گفتند: «بسوی ما بیایید (و خود را از معرکه بیرون کشید)» بخوبی می‌شناسد؛ و آنها (مردمی ضعیفند و) جز اندکی پیکار نمی‌کنند!'];
ayat[3552]=[33,19,'أَشِحَّةً عَلَيْكُمْ ۖ فَإِذَا جَاءَ الْخَوْفُ رَأَيْتَهُمْ يَنظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَالَّذِي يُغْشَىٰ عَلَيْهِ مِنَ الْمَوْتِ ۖ فَإِذَا ذَهَبَ الْخَوْفُ سَلَقُوكُم بِأَلْسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى الْخَيْرِ ۚ أُولَٰئِكَ لَمْ يُؤْمِنُوا فَأَحْبَطَ اللَّهُ أَعْمَالَهُمْ ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا','آنها در همه چیز نسبت به شما بخیلند؛ و هنگامی که (لحظات) ترس (و بحرانی) پیش آید، می‌بینی آنچنان به تو نگاه می‌کنند، و چشمهایشان در حدقه می‌چرخد، که گویی می‌خواهند قالب تهی کنند! امّا وقتی حالت خوف و ترس فرو نشست، زبانهای تند و خشن خود را با انبوهی از خشم و عصبانیت بر شما می‌گشایند (و سهم خود را از غنایم مطالبه می‌کنند!) در حالی که در آن نیز حریص و بخیلند؛ آنها (هرگز) ایمان نیاورده‌اند، از این رو خداوند اعمالشان را حبط و نابود کرد؛ و این کار بر خدا آسان است.'];
ayat[3553]=[33,20,'يَحْسَبُونَ الْأَحْزَابَ لَمْ يَذْهَبُوا ۖ وَإِن يَأْتِ الْأَحْزَابُ يَوَدُّوا لَوْ أَنَّهُم بَادُونَ فِي الْأَعْرَابِ يَسْأَلُونَ عَنْ أَنبَائِكُمْ ۖ وَلَوْ كَانُوا فِيكُم مَّا قَاتَلُوا إِلَّا قَلِيلًا','آنها گمان می‌کنند هنوز لشکر احزاب نرفته‌اند؛ و اگر برگردند (از ترس آنان) دوست می‌دارند در میان اعراب بادیه‌نشین پراکنده (و پنهان) شوند و از اخبار شما جویا گردند؛ و اگر در میان شما باشند جز اندکی پیکار نمی‌کنند!'];
ayat[3554]=[33,21,'لَّقَدْ كَانَ لَكُمْ فِي رَسُولِ اللَّهِ أُسْوَةٌ حَسَنَةٌ لِّمَن كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ وَذَكَرَ اللَّهَ كَثِيرًا','مسلّماً برای شما در زندگی رسول خدا سرمشق نیکویی بود، برای آنها که امید به رحمت خدا و روز رستاخیز دارند و خدا را بسیار یاد می‌کنند.'];
ayat[3555]=[33,22,'وَلَمَّا رَأَى الْمُؤْمِنُونَ الْأَحْزَابَ قَالُوا هَٰذَا مَا وَعَدَنَا اللَّهُ وَرَسُولُهُ وَصَدَقَ اللَّهُ وَرَسُولُهُ ۚ وَمَا زَادَهُمْ إِلَّا إِيمَانًا وَتَسْلِيمًا','(امّا) مؤمنان وقتی لشکر احزاب را دیدند گفتند: «این همان است که خدا و رسولش به ما وعده داده، و خدا و رسولش راست گفته‌اند!» و این موضوع جز بر ایمان و تسلیم آنان نیفزود.'];
ayat[3556]=[33,23,'مِّنَ الْمُؤْمِنِينَ رِجَالٌ صَدَقُوا مَا عَاهَدُوا اللَّهَ عَلَيْهِ ۖ فَمِنْهُم مَّن قَضَىٰ نَحْبَهُ وَمِنْهُم مَّن يَنتَظِرُ ۖ وَمَا بَدَّلُوا تَبْدِيلًا','در میان مؤمنان مردانی هستند که بر سر عهدی که با خدا بستند صادقانه ایستاده‌اند؛ بعضی پیمان خود را به آخر بردند (و در راه او شربت شهادت نوشیدند)، و بعضی دیگر در انتظارند؛ و هرگز تغییر و تبدیلی در عهد و پیمان خود ندادند.'];
ayat[3557]=[33,24,'لِّيَجْزِيَ اللَّهُ الصَّادِقِينَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنَافِقِينَ إِن شَاءَ أَوْ يَتُوبَ عَلَيْهِمْ ۚ إِنَّ اللَّهَ كَانَ غَفُورًا رَّحِيمًا','هدف این است که خداوند صادقان را بخاطر صدقشان پاداش دهد، و منافقان را هرگاه اراده کند عذاب نماید یا (اگر توبه کنند) توبه آنها را بپذیرد؛ چرا که خداوند آمرزنده و رحیم است.'];
ayat[3558]=[33,25,'وَرَدَّ اللَّهُ الَّذِينَ كَفَرُوا بِغَيْظِهِمْ لَمْ يَنَالُوا خَيْرًا ۚ وَكَفَى اللَّهُ الْمُؤْمِنِينَ الْقِتَالَ ۚ وَكَانَ اللَّهُ قَوِيًّا عَزِيزًا','خدا کافران را با دلی پر از خشم بازگرداند بی‌آنکه نتیجه‌ای از کار خود گرفته باشند؛ و خداوند (در این میدان)، مؤمنان را از جنگ بی‌نیاز ساخت (و پیروزی را نصیبشان کرد)؛ و خدا قوّی و شکست‌ناپذیر است!'];
ayat[3559]=[33,26,'وَأَنزَلَ الَّذِينَ ظَاهَرُوهُم مِّنْ أَهْلِ الْكِتَابِ مِن صَيَاصِيهِمْ وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ فَرِيقًا تَقْتُلُونَ وَتَأْسِرُونَ فَرِيقًا','و خداوند گروهی از اهل کتاب [= یهود] را که از آنان [= مشرکان عرب‌] حمایت کردند از قلعه‌های محکمشان پایین کشید و در دلهایشان رعب افکند؛ (و کارشان به جایی رسید که) گروهی را به قتل می‌رساندید و گروهی را اسیر می‌کردید!'];
ayat[3560]=[33,27,'وَأَوْرَثَكُمْ أَرْضَهُمْ وَدِيَارَهُمْ وَأَمْوَالَهُمْ وَأَرْضًا لَّمْ تَطَئُوهَا ۚ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرًا','و زمینها و خانه‌ها و اموالشان را در اختیار شما گذاشت، و (همچنین) زمینی را که هرگز در آن گام ننهاده بودید؛ و خداوند بر هر چیز تواناست!'];
ayat[3561]=[33,28,'يَا أَيُّهَا النَّبِيُّ قُل لِّأَزْوَاجِكَ إِن كُنتُنَّ تُرِدْنَ الْحَيَاةَ الدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحًا جَمِيلًا','ای پیامبر! به همسرانت بگو: «اگر شما زندگی دنیا و زرق و برق آن را می‌خواهید بیایید با هدیه‌ای شما را بهره‌مند سازم و شما را بطرز نیکویی رها سازم!'];
ayat[3562]=[33,29,'وَإِن كُنتُنَّ تُرِدْنَ اللَّهَ وَرَسُولَهُ وَالدَّارَ الْآخِرَةَ فَإِنَّ اللَّهَ أَعَدَّ لِلْمُحْسِنَاتِ مِنكُنَّ أَجْرًا عَظِيمًا','و اگر شما خدا و پیامبرش و سرای آخرت را می‌خواهید، خداوند برای نیکوکاران شما پاداش عظیمی آماده ساخته است.»'];
ayat[3563]=[33,30,'يَا نِسَاءَ النَّبِيِّ مَن يَأْتِ مِنكُنَّ بِفَاحِشَةٍ مُّبَيِّنَةٍ يُضَاعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِ ۚ وَكَانَ ذَٰلِكَ عَلَى اللَّهِ يَسِيرًا','ای همسران پیامبر! هر کدام از شما گناه آشکار و فاحشی مرتکب شود، عذاب او دوچندان خواهد بود؛ و این برای خدا آسان است.'];
ayat[3564]=[33,31,'۞ وَمَن يَقْنُتْ مِنكُنَّ لِلَّهِ وَرَسُولِهِ وَتَعْمَلْ صَالِحًا نُّؤْتِهَا أَجْرَهَا مَرَّتَيْنِ وَأَعْتَدْنَا لَهَا رِزْقًا كَرِيمًا','و هر کس از شما برای خدا و پیامبرش خضوع کند و عمل صالح انجام دهد، پاداش او را دو چندان خواهیم ساخت، و روزی پرارزشی برای او آماده کرده‌ایم.'];
ayat[3565]=[33,32,'يَا نِسَاءَ النَّبِيِّ لَسْتُنَّ كَأَحَدٍ مِّنَ النِّسَاءِ ۚ إِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِي فِي قَلْبِهِ مَرَضٌ وَقُلْنَ قَوْلًا مَّعْرُوفًا','ای همسران پیامبر! شما همچون یکی از زنان معمولی نیستید اگر تقوا پیشه کنید؛ پس به گونه‌ای هوس‌انگیز سخن نگویید که بیماردلان در شما طمع کنند، و سخن شایسته بگویید!'];
ayat[3566]=[33,33,'وَقَرْنَ فِي بُيُوتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْأُولَىٰ ۖ وَأَقِمْنَ الصَّلَاةَ وَآتِينَ الزَّكَاةَ وَأَطِعْنَ اللَّهَ وَرَسُولَهُ ۚ إِنَّمَا يُرِيدُ اللَّهُ لِيُذْهِبَ عَنكُمُ الرِّجْسَ أَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيرًا','و در خانه‌های خود بمانید، و همچون دوران جاهلیّت نخستین (در میان مردم) ظاهر نشوید، و نماز را برپا دارید، و زکات را بپردازید، و خدا و رسولش را اطاعت کنید؛ خداوند فقط می‌خواهد پلیدی و گناه را از شما اهل بیت دور کند و کاملاً شما را پاک سازد.'];
ayat[3567]=[33,34,'وَاذْكُرْنَ مَا يُتْلَىٰ فِي بُيُوتِكُنَّ مِنْ آيَاتِ اللَّهِ وَالْحِكْمَةِ ۚ إِنَّ اللَّهَ كَانَ لَطِيفًا خَبِيرًا','آنچه را در خانه‌های شما از آیات خداوند و حکمت و دانش خوانده می‌شود یاد کنید؛ خداوند لطیف و خبیر است!'];
ayat[3568]=[33,35,'إِنَّ الْمُسْلِمِينَ وَالْمُسْلِمَاتِ وَالْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَالْقَانِتِينَ وَالْقَانِتَاتِ وَالصَّادِقِينَ وَالصَّادِقَاتِ وَالصَّابِرِينَ وَالصَّابِرَاتِ وَالْخَاشِعِينَ وَالْخَاشِعَاتِ وَالْمُتَصَدِّقِينَ وَالْمُتَصَدِّقَاتِ وَالصَّائِمِينَ وَالصَّائِمَاتِ وَالْحَافِظِينَ فُرُوجَهُمْ وَالْحَافِظَاتِ وَالذَّاكِرِينَ اللَّهَ كَثِيرًا وَالذَّاكِرَاتِ أَعَدَّ اللَّهُ لَهُم مَّغْفِرَةً وَأَجْرًا عَظِيمًا','به یقین، مردان مسلمان و زنان مسلمان، مردان با ایمان و زنان با ایمان، مردان مطیع فرمان خدا و زنان مطیع فرمان خدا، مردان راستگو و زنان راستگو، مردان صابر و شکیبا و زنان صابر و شکیبا، مردان با خشوع و زنان با خشوع، مردان انفاق کننده و زنان انفاق کننده، مردان روزه‌دار و زنان روزه‌دار، مردان پاکدامن و زنان پاکدامن و مردانی که بسیار به یاد خدا هستند و زنانی که بسیار یاد خدا می‌کنند، خداوند برای همه آنان مغفرت و پاداش عظیمی فراهم ساخته است.'];
ayat[3569]=[33,36,'وَمَا كَانَ لِمُؤْمِنٍ وَلَا مُؤْمِنَةٍ إِذَا قَضَى اللَّهُ وَرَسُولُهُ أَمْرًا أَن يَكُونَ لَهُمُ الْخِيَرَةُ مِنْ أَمْرِهِمْ ۗ وَمَن يَعْصِ اللَّهَ وَرَسُولَهُ فَقَدْ ضَلَّ ضَلَالًا مُّبِينًا','هیچ مرد و زن با ایمانی حق ندارد هنگامی که خدا و پیامبرش امری را لازم بدانند، اختیاری (در برابر فرمان خدا) داشته باشد؛ و هر کس نافرمانی خدا و رسولش را کند، به گمراهی آشکاری گرفتار شده است!'];
ayat[3570]=[33,37,'وَإِذْ تَقُولُ لِلَّذِي أَنْعَمَ اللَّهُ عَلَيْهِ وَأَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللَّهَ وَتُخْفِي فِي نَفْسِكَ مَا اللَّهُ مُبْدِيهِ وَتَخْشَى النَّاسَ وَاللَّهُ أَحَقُّ أَن تَخْشَاهُ ۖ فَلَمَّا قَضَىٰ زَيْدٌ مِّنْهَا وَطَرًا زَوَّجْنَاكَهَا لِكَيْ لَا يَكُونَ عَلَى الْمُؤْمِنِينَ حَرَجٌ فِي أَزْوَاجِ أَدْعِيَائِهِمْ إِذَا قَضَوْا مِنْهُنَّ وَطَرًا ۚ وَكَانَ أَمْرُ اللَّهِ مَفْعُولًا','(به خاطر بیاور) زمانی را که به آن کس که خداوند به او نعمت داده بود و تو نیز به او نعمت داده بودی [به فرزند خوانده‌ات «زید»] می‌گفتی: «همسرت را نگاه‌دار و از خدا بپرهیز!» (و پیوسته این امر را تکرار می‌کردی)؛ و در دل چیزی را پنهان می‌داشتی که خداوند آن را آشکار می‌کند؛ و از مردم می‌ترسیدی در حالی که خداوند سزاوارتر است که از او بترسی! هنگامی که زید نیازش را از آن زن به سرآورد (و از او جدا شد)، ما او را به همسری تو درآوردیم تا مشکلی برای مؤمنان در ازدواج با همسران پسر خوانده‌هایشان -هنگامی که طلاق گیرند- نباشد؛ و فرمان خدا انجام شدنی است (و سنّت غلط تحریم این زنان باید شکسته شود).'];
ayat[3571]=[33,38,'مَّا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيمَا فَرَضَ اللَّهُ لَهُ ۖ سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِن قَبْلُ ۚ وَكَانَ أَمْرُ اللَّهِ قَدَرًا مَّقْدُورًا','هیچ گونه منعی بر پیامبر در آنچه خدا بر او واجب کرده نیست؛ این سنّت الهی در مورد کسانی که پیش از این بوده‌اند نیز جاری بوده؛ و فرمان خدا روی حساب و برنامه دقیقی است!'];
ayat[3572]=[33,39,'الَّذِينَ يُبَلِّغُونَ رِسَالَاتِ اللَّهِ وَيَخْشَوْنَهُ وَلَا يَخْشَوْنَ أَحَدًا إِلَّا اللَّهَ ۗ وَكَفَىٰ بِاللَّهِ حَسِيبًا','(پیامبران) پیشین کسانی بودند که تبلیغ رسالتهای الهی می‌کردند و (تنها) از او می ترسیدند، و از هیچ کس جز خدا بیم نداشتند؛ و همین بس که خداوند حسابگر (و پاداش‌دهنده اعمال آنها) است!'];
ayat[3573]=[33,40,'مَّا كَانَ مُحَمَّدٌ أَبَا أَحَدٍ مِّن رِّجَالِكُمْ وَلَٰكِن رَّسُولَ اللَّهِ وَخَاتَمَ النَّبِيِّينَ ۗ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ عَلِيمًا','محمّد (ص) پدر هیچ یک از مردان شما نبوده و نیست؛ ولی رسول خدا و ختم‌کننده و آخرین پیامبران است؛ و خداوند به همه چیز آگاه است!'];
ayat[3574]=[33,41,'يَا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا اللَّهَ ذِكْرًا كَثِيرًا','ای کسانی که ایمان آورده‌اید! خدا را بسیار یاد کنید،'];
ayat[3575]=[33,42,'وَسَبِّحُوهُ بُكْرَةً وَأَصِيلًا','و صبح و شام او را تسبیح گویید!'];
ayat[3576]=[33,43,'هُوَ الَّذِي يُصَلِّي عَلَيْكُمْ وَمَلَائِكَتُهُ لِيُخْرِجَكُم مِّنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَكَانَ بِالْمُؤْمِنِينَ رَحِيمًا','او کسی است که بر شما درود و رحمت می‌فرستد، و فرشتگان او (نیز) برای شما تقاضای رحمت می‌کنند تا شما را از ظلمات (جهل و شرک گناه) به سوی نور (ایمان و علم و تقوا) رهنمون گردد؛ او نسبت به مؤمنان همواره مهربان بوده است!'];
ayat[3577]=[33,44,'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُ سَلَامٌ ۚ وَأَعَدَّ لَهُمْ أَجْرًا كَرِيمًا','تحیّت آنان در روزی که او را دیدار می‌کنند سلام است؛ و برای آنها پاداش پرارزشی فراهم ساخته است.'];
ayat[3578]=[33,45,'يَا أَيُّهَا النَّبِيُّ إِنَّا أَرْسَلْنَاكَ شَاهِدًا وَمُبَشِّرًا وَنَذِيرًا','ای پیامبر! ما تو را گواه فرستادیم و بشارت‌دهنده و انذارکننده!'];
ayat[3579]=[33,46,'وَدَاعِيًا إِلَى اللَّهِ بِإِذْنِهِ وَسِرَاجًا مُّنِيرًا','و تو را دعوت‌کننده بسوی خدا به فرمان او قرار دادیم، و چراغی روشنی‌بخش!'];
ayat[3580]=[33,47,'وَبَشِّرِ الْمُؤْمِنِينَ بِأَنَّ لَهُم مِّنَ اللَّهِ فَضْلًا كَبِيرًا','و مؤمنان را بشارت ده که برای آنان از سوی خدا فضل بزرگی است.'];
ayat[3581]=[33,48,'وَلَا تُطِعِ الْكَافِرِينَ وَالْمُنَافِقِينَ وَدَعْ أَذَاهُمْ وَتَوَكَّلْ عَلَى اللَّهِ ۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا','و از کافران و منافقان اطاعت مکن، و به آزارهای آنها اعتنا منما، و بر خدا توکّل کن، و همین بس که خدا حامی و مدافع (تو) است!'];
ayat[3582]=[33,49,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَكَحْتُمُ الْمُؤْمِنَاتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَهَا ۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحًا جَمِيلًا','ای کسانی که ایمان آورده‌اید! هنگامی که با زنان با ایمان ازدواج کردید و قبل از همبستر شدن طلاق دادید، عدّه‌ای برای شما بر آنها نیست که بخواهید حساب آن را نگاه دارید؛ آنها را با هدیه مناسبی بهره‌مند سازید و بطرز شایسته‌ای رهایشان کنید.'];
ayat[3583]=[33,50,'يَا أَيُّهَا النَّبِيُّ إِنَّا أَحْلَلْنَا لَكَ أَزْوَاجَكَ اللَّاتِي آتَيْتَ أُجُورَهُنَّ وَمَا مَلَكَتْ يَمِينُكَ مِمَّا أَفَاءَ اللَّهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّاتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَالَاتِكَ اللَّاتِي هَاجَرْنَ مَعَكَ وَامْرَأَةً مُّؤْمِنَةً إِن وَهَبَتْ نَفْسَهَا لِلنَّبِيِّ إِنْ أَرَادَ النَّبِيُّ أَن يَسْتَنكِحَهَا خَالِصَةً لَّكَ مِن دُونِ الْمُؤْمِنِينَ ۗ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِي أَزْوَاجِهِمْ وَمَا مَلَكَتْ أَيْمَانُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٌ ۗ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','ای پیامبر! ما همسران تو را که مهرشان را پرداخته‌ای برای تو حلال کردیم، و همچنین کنیزانی که از طریق غنایمی که خدا به تو بخشیده است مالک شده‌ای و دختران عموی تو، و دختران عمّه‌ها، و دختران دایی تو، و دختران خاله‌ها که با تو مهاجرت کردند (ازدواج با آنها برای تو حلال است) و هرگاه زن با ایمانی خود را به پیامبر ببخشد (و مهری برای خود نخواهد) چنانچه پیامبر بخواهد می‌تواند او را به همسری برگزیند؛ امّا چنین ازدواجی تنها برای تو مجاز است نه دیگر مؤمنان؛ ما می‌دانیم برای آنان در مورد همسرانشان و کنیزانشان چه حکمی مقرّر داشته‌ایم (و مصلحت آنان چه حکمی را ایجاب می‌کند)؛ این بخاطر آن است که مشکلی (در ادای رسالت) بر تو نباشد (و از این راه حامیان فزونتری فراهم سازی)؛ و خداوند آمرزنده و مهربان است!'];
ayat[3584]=[33,51,'۞ تُرْجِي مَن تَشَاءُ مِنْهُنَّ وَتُؤْوِي إِلَيْكَ مَن تَشَاءُ ۖ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَ ۚ ذَٰلِكَ أَدْنَىٰ أَن تَقَرَّ أَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَا آتَيْتَهُنَّ كُلُّهُنَّ ۚ وَاللَّهُ يَعْلَمُ مَا فِي قُلُوبِكُمْ ۚ وَكَانَ اللَّهُ عَلِيمًا حَلِيمًا','(موعد) هر یک از همسرانت را بخواهی می‌توانی به تأخیر اندازی، و هر کدام را بخواهی نزد خود جای دهی؛ و هرگاه بعضی از آنان را که برکنار ساخته‌ای بخواهی نزد خود جای دهی، گناهی بر تو نیست؛ این حکم الهی برای روشنی چشم آنان، و اینکه غمگین نباشند و به آنچه به آنان می‌دهی همگی راضی شوند نزدیکتر است؛ و خدا آنچه را در قلوب شماست می‌داند، و خداوند دانا و بردبار است (از مصالح بندگان خود با خبر است، و در کیفر آنها عجله نمی‌کند)!'];
ayat[3585]=[33,52,'لَّا يَحِلُّ لَكَ النِّسَاءُ مِن بَعْدُ وَلَا أَن تَبَدَّلَ بِهِنَّ مِنْ أَزْوَاجٍ وَلَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلَّا مَا مَلَكَتْ يَمِينُكَ ۗ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ رَّقِيبًا','بعد از این دیگر زنی بر تو حلال نیست، و نمی‌توانی همسرانت را به همسران دیگری مبدّل کنی [= بعضی را طلاق دهی و همسر دیگری به جای او برگزینی‌] هر چند جمال آنها مورد توجّه تو واقع شود، مگر آنچه که بصورت کنیز در ملک تو درآید! و خداوند ناظر و مراقب هر چیز است (و با این حکم فشار قبایل عرب را در اختیار همسر از آنان، از تو برداشتیم).'];
ayat[3586]=[33,53,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَدْخُلُوا بُيُوتَ النَّبِيِّ إِلَّا أَن يُؤْذَنَ لَكُمْ إِلَىٰ طَعَامٍ غَيْرَ نَاظِرِينَ إِنَاهُ وَلَٰكِنْ إِذَا دُعِيتُمْ فَادْخُلُوا فَإِذَا طَعِمْتُمْ فَانتَشِرُوا وَلَا مُسْتَأْنِسِينَ لِحَدِيثٍ ۚ إِنَّ ذَٰلِكُمْ كَانَ يُؤْذِي النَّبِيَّ فَيَسْتَحْيِي مِنكُمْ ۖ وَاللَّهُ لَا يَسْتَحْيِي مِنَ الْحَقِّ ۚ وَإِذَا سَأَلْتُمُوهُنَّ مَتَاعًا فَاسْأَلُوهُنَّ مِن وَرَاءِ حِجَابٍ ۚ ذَٰلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّ ۚ وَمَا كَانَ لَكُمْ أَن تُؤْذُوا رَسُولَ اللَّهِ وَلَا أَن تَنكِحُوا أَزْوَاجَهُ مِن بَعْدِهِ أَبَدًا ۚ إِنَّ ذَٰلِكُمْ كَانَ عِندَ اللَّهِ عَظِيمًا','ای کسانی که ایمان آورده‌اید! در خانه‌های پیامبر داخل نشوید مگر به شما برای صرف غذا اجازه داده شود، در حالی که (قبل از موعد نیایید و) در انتظار وقت غذا ننشینید؛ امّا هنگامی که دعوت شدید داخل شوید؛ و وقتی غذا خوردید پراکنده شوید، و (بعد از صرف غذا) به بحث و صحبت ننشینید؛ این عمل، پیامبر را ناراحت می‌نماید، ولی از شما شرم می‌کند (و چیزی نمی‌گوید)؛ امّا خداوند از (بیان) حق شرم ندارد! و هنگامی که چیزی از وسایل زندگی را (بعنوان عاریت) از آنان [= همسران پیامبر] می‌خواهید از پشت پرده بخواهید؛ این کار برای پاکی دلهای شما و آنها بهتر است! و شما حق ندارید رسول خدا را آزار دهید، و نه هرگز همسران او را بعد از او به همسری خود درآورید که این کار نزد خدا بزرگ است!'];
ayat[3587]=[33,54,'إِن تُبْدُوا شَيْئًا أَوْ تُخْفُوهُ فَإِنَّ اللَّهَ كَانَ بِكُلِّ شَيْءٍ عَلِيمًا','اگر چیزی را آشکار کنید یا آن را پنهان دارید، خداوند از همه چیز آگاه است!'];
ayat[3588]=[33,55,'لَّا جُنَاحَ عَلَيْهِنَّ فِي آبَائِهِنَّ وَلَا أَبْنَائِهِنَّ وَلَا إِخْوَانِهِنَّ وَلَا أَبْنَاءِ إِخْوَانِهِنَّ وَلَا أَبْنَاءِ أَخَوَاتِهِنَّ وَلَا نِسَائِهِنَّ وَلَا مَا مَلَكَتْ أَيْمَانُهُنَّ ۗ وَاتَّقِينَ اللَّهَ ۚ إِنَّ اللَّهَ كَانَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدًا','بر آنان [= همسران پیامبر] گناهی نیست در مورد پدران و فرزندان و برادران و فرزندان برادران و فرزندان خواهران خود و زنان مسلمان و بردگان خویش (که بدون حجاب و پرده با آنها تماس بگیرند)؛ و تقوای الهی را پیشه کنید که خداوند نسبت به هر چیزی شاهد و آگاه است.'];
ayat[3589]=[33,56,'إِنَّ اللَّهَ وَمَلَائِكَتَهُ يُصَلُّونَ عَلَى النَّبِيِّ ۚ يَا أَيُّهَا الَّذِينَ آمَنُوا صَلُّوا عَلَيْهِ وَسَلِّمُوا تَسْلِيمًا','خدا و فرشتگانش بر پیامبر درود می‌فرستند؛ ای کسانی که ایمان آورده‌اید، بر او درود فرستید و سلام گویید و کاملاً تسلیم (فرمان او) باشید.'];
ayat[3590]=[33,57,'إِنَّ الَّذِينَ يُؤْذُونَ اللَّهَ وَرَسُولَهُ لَعَنَهُمُ اللَّهُ فِي الدُّنْيَا وَالْآخِرَةِ وَأَعَدَّ لَهُمْ عَذَابًا مُّهِينًا','آنها که خدا و پیامبرش را آزار می‌دهند، خداوند آنان را از رحمت خود در دنیا و آخرت دور ساخته، و برای آنها عذاب خوارکننده‌ای آماده کرده است.'];
ayat[3591]=[33,58,'وَالَّذِينَ يُؤْذُونَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ بِغَيْرِ مَا اكْتَسَبُوا فَقَدِ احْتَمَلُوا بُهْتَانًا وَإِثْمًا مُّبِينًا','و آنان که مردان و زنان باایمان را به خاطر کاری که انجام نداده‌اند آزار می‌دهند؛ بار بهتان و گناه آشکاری را به دوش کشیده‌اند.'];
ayat[3592]=[33,59,'يَا أَيُّهَا النَّبِيُّ قُل لِّأَزْوَاجِكَ وَبَنَاتِكَ وَنِسَاءِ الْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِن جَلَابِيبِهِنَّ ۚ ذَٰلِكَ أَدْنَىٰ أَن يُعْرَفْنَ فَلَا يُؤْذَيْنَ ۗ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','ای پیامبر! به همسران و دخترانت و زنان مؤمنان بگو: «جلبابها [= روسری‌های بلند] خود را بر خویش فروافکنند، این کار برای اینکه شناخته شوند و مورد آزار قرار نگیرند بهتر است؛ (و اگر تاکنون خطا و کوتاهی از آنها سر زده توبه کنند) خداوند همواره آمرزنده رحیم است.'];
ayat[3593]=[33,60,'۞ لَّئِن لَّمْ يَنتَهِ الْمُنَافِقُونَ وَالَّذِينَ فِي قُلُوبِهِم مَّرَضٌ وَالْمُرْجِفُونَ فِي الْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَا إِلَّا قَلِيلًا','اگر منافقان و بیماردلان و آنها که اخبار دروغ و شایعات بی‌اساس در مدینه پخش می کنند دست از کار خود بر ندارند، تو را بر ضدّ آنان می‌شورانیم، سپس جز مدّت کوتاهی نمی‌توانند در کنار تو در این شهر بمانند!'];
ayat[3594]=[33,61,'مَّلْعُونِينَ ۖ أَيْنَمَا ثُقِفُوا أُخِذُوا وَقُتِّلُوا تَقْتِيلًا','و از همه جا طرد می‌شوند، و هر جا یافته شوند گرفته خواهند شد و به سختی به قتل خواهند رسید!'];
ayat[3595]=[33,62,'سُنَّةَ اللَّهِ فِي الَّذِينَ خَلَوْا مِن قَبْلُ ۖ وَلَن تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلًا','این سنّت خداوند در اقوام پیشین است، و برای سنّت الهی هیچ گونه تغییر نخواهی یافت!'];
ayat[3596]=[33,63,'يَسْأَلُكَ النَّاسُ عَنِ السَّاعَةِ ۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ اللَّهِ ۚ وَمَا يُدْرِيكَ لَعَلَّ السَّاعَةَ تَكُونُ قَرِيبًا','مردم از تو درباره (زمان قیام) قیامت سؤال می‌کنند، بگو: «علم آن تنها نزد خداست!» و چه می‌دانی شاید قیامت نزدیک باشد!'];
ayat[3597]=[33,64,'إِنَّ اللَّهَ لَعَنَ الْكَافِرِينَ وَأَعَدَّ لَهُمْ سَعِيرًا','خداوند کافران را لعن کرده (و از رحمت خود دور داشته) و برای آنان آتش سوزاننده‌ای آماده نموده است،'];
ayat[3598]=[33,65,'خَالِدِينَ فِيهَا أَبَدًا ۖ لَّا يَجِدُونَ وَلِيًّا وَلَا نَصِيرًا','در حالی که همواره در آن تا ابد می‌مانند، و ولیّ و یاوری نخواهند یافت!'];
ayat[3599]=[33,66,'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِي النَّارِ يَقُولُونَ يَا لَيْتَنَا أَطَعْنَا اللَّهَ وَأَطَعْنَا الرَّسُولَا','در آن روز که صورتهای آنان در آتش (دوزخ) دگرگون خواهد شد (از کار خویش پشیمان می‌شوند و) می‌گویند: «ای کاش خدا و پیامبر را اطاعت کرده بودیم!»'];
ayat[3600]=[33,67,'وَقَالُوا رَبَّنَا إِنَّا أَطَعْنَا سَادَتَنَا وَكُبَرَاءَنَا فَأَضَلُّونَا السَّبِيلَا','و می‌گویند: «پروردگارا! ما از سران و بزرگان خود اطاعت کردیم و ما را گمراه ساختند!'];
ayat[3601]=[33,68,'رَبَّنَا آتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَبِيرًا','پروردگارا! آنان را از عذاب، دو چندان ده و آنها را لعن بزرگی فرما!»'];
ayat[3602]=[33,69,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَكُونُوا كَالَّذِينَ آذَوْا مُوسَىٰ فَبَرَّأَهُ اللَّهُ مِمَّا قَالُوا ۚ وَكَانَ عِندَ اللَّهِ وَجِيهًا','ای کسانی که ایمان آورده‌اید! همانند کسانی نباشید که موسی را آزار دادند؛ و خداوند او را از آنچه در حق او می‌گفتند مبرا ساخت؛ و او نزد خداوند، آبرومند (و گرانقدر) بود!'];
ayat[3603]=[33,70,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَقُولُوا قَوْلًا سَدِيدًا','ای کسانی که ایمان آورده‌اید! تقوای الهی پیشه کنید و سخن حق بگویید...'];
ayat[3604]=[33,71,'يُصْلِحْ لَكُمْ أَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ ۗ وَمَن يُطِعِ اللَّهَ وَرَسُولَهُ فَقَدْ فَازَ فَوْزًا عَظِيمًا','تا خدا کارهای شما را اصلاح کند و گناهانتان را بیامرزد؛ و هر کس اطاعت خدا و رسولش کند، به رستگاری (و پیروزی) عظیمی دست یافته است!'];
ayat[3605]=[33,72,'إِنَّا عَرَضْنَا الْأَمَانَةَ عَلَى السَّمَاوَاتِ وَالْأَرْضِ وَالْجِبَالِ فَأَبَيْنَ أَن يَحْمِلْنَهَا وَأَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْإِنسَانُ ۖ إِنَّهُ كَانَ ظَلُومًا جَهُولًا','ما امانت (تعهّد، تکلیف، و ولایت الهیّه) را بر آسمانها و زمین و کوه‌ها عرضه داشتیم، آنها از حمل آن سر برتافتند، و از آن هراسیدند؛ امّا انسان آن را بر دوش کشید؛ او بسیار ظالم و جاهل بود، (چون قدر این مقام عظیم را نشناخت و به خود ستم کرد)!'];
ayat[3606]=[33,73,'لِّيُعَذِّبَ اللَّهُ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَاتِ وَيَتُوبَ اللَّهُ عَلَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ۗ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','هدف این بود که خداوند مردان و زنان منافق و مردان و زنان مشرک را (از مؤمنان جدا سازد و آنان را) عذاب کند، و خدا رحمت خود را بر مردان و زنان باایمان بفرستد؛ خداوند همواره آمرزنده و رحیم است!'];
ayat[3607]=[34,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ وَلَهُ الْحَمْدُ فِي الْآخِرَةِ ۚ وَهُوَ الْحَكِيمُ الْخَبِيرُ','حمد (و ستایش) مخصوص خداوندی است که تمام آنچه در آسمانها و زمین است از آن اوست؛ و (نیز) حمد (و سپاس) برای اوست در سرای آخرت؛ و او حکیم و آگاه است.'];
ayat[3608]=[34,2,'يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا ۚ وَهُوَ الرَّحِيمُ الْغَفُورُ','آنچه در زمین فرومی‌رود و آنچه را از آن برمی‌آید می‌داند، و (همچنین) آنچه از آسمان نازل می‌شود و آنچه بر آن بالا می‌رود؛ و او مهربان و آمرزنده است.'];
ayat[3609]=[34,3,'وَقَالَ الَّذِينَ كَفَرُوا لَا تَأْتِينَا السَّاعَةُ ۖ قُلْ بَلَىٰ وَرَبِّي لَتَأْتِيَنَّكُمْ عَالِمِ الْغَيْبِ ۖ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ وَلَا أَصْغَرُ مِن ذَٰلِكَ وَلَا أَكْبَرُ إِلَّا فِي كِتَابٍ مُّبِينٍ','کافران گفتند: «قیامت هرگز به سراغ ما نخواهد آمد!» بگو: «آری به پروردگارم سوگند که به سراغ شما خواهد آمد، خداوندی که از غیب آگاه است و به اندازه سنگینی ذرّه‌ای در آسمانها و زمین از علم او دور نخواهد ماند، و نه کوچکتر از آن و نه بزرگتر، مگر اینکه در کتابی آشکار ثبت است!»'];
ayat[3610]=[34,4,'لِّيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ ۚ أُولَٰئِكَ لَهُم مَّغْفِرَةٌ وَرِزْقٌ كَرِيمٌ','تا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند پاداش دهد؛ برای آنان مغفرت و روزی پرارزشی است!'];
ayat[3611]=[34,5,'وَالَّذِينَ سَعَوْا فِي آيَاتِنَا مُعَاجِزِينَ أُولَٰئِكَ لَهُمْ عَذَابٌ مِّن رِّجْزٍ أَلِيمٌ','و کسانی که سعی در (تکذیب) آیات ما داشتند و گمان کردند می‌توانند از حوزه قدرت ما بگریزند، عذابی بد و دردناک خواهند داشت!'];
ayat[3612]=[34,6,'وَيَرَى الَّذِينَ أُوتُوا الْعِلْمَ الَّذِي أُنزِلَ إِلَيْكَ مِن رَّبِّكَ هُوَ الْحَقَّ وَيَهْدِي إِلَىٰ صِرَاطِ الْعَزِيزِ الْحَمِيدِ','کسانی که به ایشان علم داده شده، آنچه را از سوی پروردگارت بر تو نازل شده حق می دانند و به راه خداوند عزیز و حمید هدایت می‌کند.'];
ayat[3613]=[34,7,'وَقَالَ الَّذِينَ كَفَرُوا هَلْ نَدُلُّكُمْ عَلَىٰ رَجُلٍ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِي خَلْقٍ جَدِيدٍ','و کافران گفتند: «آیا مردی را به شما نشان دهیم که به شما خبر می‌دهد هنگامی که (مُردید و) سخت از هم متلاشی شدید، (بار دیگر) آفرینش تازه‌ای خواهید یافت؟!'];
ayat[3614]=[34,8,'أَفْتَرَىٰ عَلَى اللَّهِ كَذِبًا أَم بِهِ جِنَّةٌ ۗ بَلِ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ فِي الْعَذَابِ وَالضَّلَالِ الْبَعِيدِ','آیا او بر خدا دروغ بسته یا به نوعی جنون گرفتار است؟!» (چنین نیست)، بلکه کسانی که به آخرت ایمان نمی‌آورند، در عذاب و گمراهی دوری هستند (و نشانه گمراهی آنها همین انکار شدید است).'];
ayat[3615]=[34,9,'أَفَلَمْ يَرَوْا إِلَىٰ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُم مِّنَ السَّمَاءِ وَالْأَرْضِ ۚ إِن نَّشَأْ نَخْسِفْ بِهِمُ الْأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِّنَ السَّمَاءِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَةً لِّكُلِّ عَبْدٍ مُّنِيبٍ','آیا به آنچه پیش رو و پشت سر آنان از آسمان و زمین قرار دارد نگاه نکردند (تا به قدرت خدا بر همه چیز واقف شوند)؟! اگر ما بخواهیم آنها را (با یک زمین‌لرزه) در زمین فرومی‌بریم، یا قطعه‌هایی از سنگهای آسمانی را بر آنها فرومی‌ریزیم؛ در این نشانه‌ای است (بر قدرت خدا) برای هر بنده توبه‌کار!'];
ayat[3616]=[34,10,'۞ وَلَقَدْ آتَيْنَا دَاوُودَ مِنَّا فَضْلًا ۖ يَا جِبَالُ أَوِّبِي مَعَهُ وَالطَّيْرَ ۖ وَأَلَنَّا لَهُ الْحَدِيدَ','و ما به داوود از سوی خود فضیلتی بزرگ بخشیدیم؛ (ما به کوه‌ها و پرندگان گفتیم:) ای کوه‌ها و ای پرندگان! با او هم‌آواز شوید و همراه او تسبیح خدا گویید! و آهن را برای او نرم کردیم.'];
ayat[3617]=[34,11,'أَنِ اعْمَلْ سَابِغَاتٍ وَقَدِّرْ فِي السَّرْدِ ۖ وَاعْمَلُوا صَالِحًا ۖ إِنِّي بِمَا تَعْمَلُونَ بَصِيرٌ','(و به او گفتیم:) زره‌های کامل و فراخ بساز، و حلقه‌ها را به اندازه و متناسب کن! و عمل صالح بجا آورید که من به آنچه انجام میدهید بینا هستم!'];
ayat[3618]=[34,12,'وَلِسُلَيْمَانَ الرِّيحَ غُدُوُّهَا شَهْرٌ وَرَوَاحُهَا شَهْرٌ ۖ وَأَسَلْنَا لَهُ عَيْنَ الْقِطْرِ ۖ وَمِنَ الْجِنِّ مَن يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِ ۖ وَمَن يَزِغْ مِنْهُمْ عَنْ أَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيرِ','و برای سلیمان باد را مسخّر ساختیم که صبحگاهان مسیر یک ماه را می‌پیمود و عصرگاهان مسیر یک ماه را؛ و چشمه مس (مذاب) را برای او روان ساختیم؛ و گروهی از جنّ پیش روی او به اذن پروردگارش کار می‌کردند؛ و هر کدام از آنها که از فرمان ما سرپیچی می‌کرد، او را عذاب آتش سوزان می‌چشاندیم!'];
ayat[3619]=[34,13,'يَعْمَلُونَ لَهُ مَا يَشَاءُ مِن مَّحَارِيبَ وَتَمَاثِيلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُورٍ رَّاسِيَاتٍ ۚ اعْمَلُوا آلَ دَاوُودَ شُكْرًا ۚ وَقَلِيلٌ مِّنْ عِبَادِيَ الشَّكُورُ','آنها هر چه سلیمان می‌خواست برایش درست می‌کردند: معبدها، تمثالها، ظروف بزرگ غذا همانند حوضها، و دیگهای ثابت (که از بزرگی قابل حمل و نقل نبود؛ و به آنان گفتیم:) ای آل داوود! شکر (این همه نعمت را) بجا آورید؛ ولی عده کمی از بندگان من شکرگزارند!'];
ayat[3620]=[34,14,'فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلَىٰ مَوْتِهِ إِلَّا دَابَّةُ الْأَرْضِ تَأْكُلُ مِنسَأَتَهُ ۖ فَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ أَن لَّوْ كَانُوا يَعْلَمُونَ الْغَيْبَ مَا لَبِثُوا فِي الْعَذَابِ الْمُهِينِ','(با این همه جلال و شکوه سلیمان) هنگامی که مرگ را بر او مقرّر داشتیم، کسی آنها را از مرگ وی آگاه نساخت مگر جنبنده زمین [= موریانه‌] که عصای او را می‌خورد (تا شکست و پیکر سلیمان فرو افتاد)؛ هنگامی که بر زمین افتاد جنّیان فهمیدند که اگر از غیب آگاه بودند در عذاب خوارکننده باقی نمی‌ماندند!'];
ayat[3621]=[34,15,'لَقَدْ كَانَ لِسَبَإٍ فِي مَسْكَنِهِمْ آيَةٌ ۖ جَنَّتَانِ عَن يَمِينٍ وَشِمَالٍ ۖ كُلُوا مِن رِّزْقِ رَبِّكُمْ وَاشْكُرُوا لَهُ ۚ بَلْدَةٌ طَيِّبَةٌ وَرَبٌّ غَفُورٌ','برای قوم «سبا» در محل سکونتشان نشانه‌ای (از قدرت الهی) بود: دو باغ (بزرگ و گسترده) از راست و چپ (رودخانه عظیم با میوه‌های فراوان؛ و به آنها گفتیم:) از روزی پروردگارتان بخورید و شکر او را بجا آورید؛ شهری است پاک و پاکیزه، و پروردگاری آمرزنده (و مهربان)!'];
ayat[3622]=[34,16,'فَأَعْرَضُوا فَأَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنَاهُم بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ أُكُلٍ خَمْطٍ وَأَثْلٍ وَشَيْءٍ مِّن سِدْرٍ قَلِيلٍ','امّا آنها (از خدا) روی‌گردان شدند، و ما سیل ویرانگر را بر آنان فرستادیم، و دو باغ (پربرکت) شان را به دو باغ (بی‌ارزش) با میوه‌های تلخ و درختان شوره گز و اندکی درخت سدر مبدّل ساختیم!'];
ayat[3623]=[34,17,'ذَٰلِكَ جَزَيْنَاهُم بِمَا كَفَرُوا ۖ وَهَلْ نُجَازِي إِلَّا الْكَفُورَ','این کیفر را بخاطر کفرانشان به آنها دادیم؛ و آیا جز کفران کننده را کیفر می‌دهیم؟!'];
ayat[3624]=[34,18,'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِي بَارَكْنَا فِيهَا قُرًى ظَاهِرَةً وَقَدَّرْنَا فِيهَا السَّيْرَ ۖ سِيرُوا فِيهَا لَيَالِيَ وَأَيَّامًا آمِنِينَ','و میان آنها و شهرهایی که برکت داده بودیم، آبادیهای آشکاری قرار دادیم؛ و سفر در میان آنها را بطور متناسب (با فاصله نزدیک) مقرّر داشتیم؛ (و به آنان گفتیم:) شبها و روزها در این آبادیها با ایمنی (کامل) سفر کنید!'];
ayat[3625]=[34,19,'فَقَالُوا رَبَّنَا بَاعِدْ بَيْنَ أَسْفَارِنَا وَظَلَمُوا أَنفُسَهُمْ فَجَعَلْنَاهُمْ أَحَادِيثَ وَمَزَّقْنَاهُمْ كُلَّ مُمَزَّقٍ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ','ولی (این ناسپاس مردم) گفتند: «پروردگارا! میان سفرهای ما دوری بیفکن» (تا بینوایان نتوانند دوش به دوش اغنیا سفر کنند! و به این طریق) آنها به خویشتن ستم کردند! و ما آنان را داستانهایی (برای عبرت دیگران) قرار دادیم و جمعیّتشان را متلاشی ساختیم؛ در این ماجرا، نشانه‌های عبرتی برای هر صابر شکرگزار است.'];
ayat[3626]=[34,20,'وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُ فَاتَّبَعُوهُ إِلَّا فَرِيقًا مِّنَ الْمُؤْمِنِينَ','(آری) بیقین، ابلیس گمان خود را درباره آنها محقّق یافت که همگی از او پیروی کردند جز گروه اندکی از مؤمنان!'];
ayat[3627]=[34,21,'وَمَا كَانَ لَهُ عَلَيْهِم مِّن سُلْطَانٍ إِلَّا لِنَعْلَمَ مَن يُؤْمِنُ بِالْآخِرَةِ مِمَّنْ هُوَ مِنْهَا فِي شَكٍّ ۗ وَرَبُّكَ عَلَىٰ كُلِّ شَيْءٍ حَفِيظٌ','او سلطه بر آنان نداشت جز برای اینکه مؤمنان به آخرت را از آنها که در شکّ هستند باز شناسیم؛ و پروردگار تو، نگاهبان همه چیز است!'];
ayat[3628]=[34,22,'قُلِ ادْعُوا الَّذِينَ زَعَمْتُم مِّن دُونِ اللَّهِ ۖ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ وَمَا لَهُمْ فِيهِمَا مِن شِرْكٍ وَمَا لَهُ مِنْهُم مِّن ظَهِيرٍ','بگو: «کسانی را که غیر از خدا (معبود خود) می‌پندارید بخوانید! (آنها هرگز گرهی از کار شما نمی‌گشایند، چرا که) آنها به اندازه ذرّه‌ای در آسمانها و زمین مالک نیستند، و نه در (خلقت و مالکیّت) آنها شریکند، و نه یاور او (در آفرینش) بودند.'];
ayat[3629]=[34,23,'وَلَا تَنفَعُ الشَّفَاعَةُ عِندَهُ إِلَّا لِمَنْ أَذِنَ لَهُ ۚ حَتَّىٰ إِذَا فُزِّعَ عَن قُلُوبِهِمْ قَالُوا مَاذَا قَالَ رَبُّكُمْ ۖ قَالُوا الْحَقَّ ۖ وَهُوَ الْعَلِيُّ الْكَبِيرُ','هیچ شفاعتی نزد او، جز برای کسانی که اذن داده، سودی ندارد! (در آن روز همه در اضطرابند) تا زمانی که اضطراب از دلهای آنان زایل گردد (و فرمان از ناحیه او صادر شود؛ در این هنگام مجرمان به شفیعان) می‌گویند: «پروردگارتان چه دستوری داده؟» می‌گویند: «حقّ را (بیان کرد و اجازه شفاعت درباره مستحقّان داد)؛ و اوست بلندمقام و بزرگ‌مرتبه!»'];
ayat[3630]=[34,24,'۞ قُلْ مَن يَرْزُقُكُم مِّنَ السَّمَاوَاتِ وَالْأَرْضِ ۖ قُلِ اللَّهُ ۖ وَإِنَّا أَوْ إِيَّاكُمْ لَعَلَىٰ هُدًى أَوْ فِي ضَلَالٍ مُّبِينٍ','بگو: «چه کسی شما را از آسمانها و زمین روزی می‌دهد؟» بگو: «اللّه! و ما یا شما بر (طریق) هدایت یا در ضلالت آشکاری هستیم!»'];
ayat[3631]=[34,25,'قُل لَّا تُسْأَلُونَ عَمَّا أَجْرَمْنَا وَلَا نُسْأَلُ عَمَّا تَعْمَلُونَ','بگو: «شما از گناهی که ما کرده‌ایم سؤال نخواهید شد، (همان گونه که) ما در برابر اعمال شما مسؤول نیستیم!»'];
ayat[3632]=[34,26,'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّ وَهُوَ الْفَتَّاحُ الْعَلِيمُ','بگو: «پروردگار ما همه ما را جمع می‌کند، سپس در میان ما بحق داوری می‌نماید (و صفوف مجرمان را از نیکوکاران جدا می‌سازد)، و اوست داور (و جداکننده) آگاه!»'];
ayat[3633]=[34,27,'قُلْ أَرُونِيَ الَّذِينَ أَلْحَقْتُم بِهِ شُرَكَاءَ ۖ كَلَّا ۚ بَلْ هُوَ اللَّهُ الْعَزِيزُ الْحَكِيمُ','بگو: «کسانی را که بعنوان شریک به او ملحق ساخته‌اید به من نشان دهید! هرگز چنین نیست! (او شریک و شبیهی ندارد)، بلکه او خداوند عزیز و حکیم است!'];
ayat[3634]=[34,28,'وَمَا أَرْسَلْنَاكَ إِلَّا كَافَّةً لِّلنَّاسِ بَشِيرًا وَنَذِيرًا وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','و ما تو را جز برای همه مردم نفرستادیم تا (آنها را به پاداشهای الهی) بشارت دهی و (از عذاب او) بترسانی؛ ولی بیشتر مردم نمی‌دانند!'];
ayat[3635]=[34,29,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِن كُنتُمْ صَادِقِينَ','می‌گویند: «اگر راست می‌گویید، این وعده (رستاخیز) کی خواهد بود؟!»'];
ayat[3636]=[34,30,'قُل لَّكُم مِّيعَادُ يَوْمٍ لَّا تَسْتَأْخِرُونَ عَنْهُ سَاعَةً وَلَا تَسْتَقْدِمُونَ','بگو: «وعده شما روزی خواهد بود که نه ساعتی از آن تأخیر می‌کنید و نه (بر آن) پیشی خواهید گرفت!»'];
ayat[3637]=[34,31,'وَقَالَ الَّذِينَ كَفَرُوا لَن نُّؤْمِنَ بِهَٰذَا الْقُرْآنِ وَلَا بِالَّذِي بَيْنَ يَدَيْهِ ۗ وَلَوْ تَرَىٰ إِذِ الظَّالِمُونَ مَوْقُوفُونَ عِندَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلَىٰ بَعْضٍ الْقَوْلَ يَقُولُ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا لَوْلَا أَنتُمْ لَكُنَّا مُؤْمِنِينَ','کافران گفتند: «ما هرگز به این قرآن و کتابهای دیگری که پیش از آن بوده ایمان نخواهیم آورد!» اگر ببینی هنگامی که این ستمگران در پیشگاه پروردگارشان (برای حساب و جزا) نگه داشته شده‌اند در حالی که هر کدام گناه خود را به گردن دیگری می‌اندازد (از وضع آنها تعجّب می‌کنی)! مستضعفان به مستکبران می‌گویند: «اگر شما نبودید ما مؤمن بودیم!»'];
ayat[3638]=[34,32,'قَالَ الَّذِينَ اسْتَكْبَرُوا لِلَّذِينَ اسْتُضْعِفُوا أَنَحْنُ صَدَدْنَاكُمْ عَنِ الْهُدَىٰ بَعْدَ إِذْ جَاءَكُم ۖ بَلْ كُنتُم مُّجْرِمِينَ','(امّا) مستکبران به مستضعفان پاسخ می‌دهند: «آیا ما شما را از هدایت بازداشتیم بعد از آنکه به سراغ شما آمد (و آن را بخوبی دریافتید)؟! بلکه شما خود مجرم بودید!»'];
ayat[3639]=[34,33,'وَقَالَ الَّذِينَ اسْتُضْعِفُوا لِلَّذِينَ اسْتَكْبَرُوا بَلْ مَكْرُ اللَّيْلِ وَالنَّهَارِ إِذْ تَأْمُرُونَنَا أَن نَّكْفُرَ بِاللَّهِ وَنَجْعَلَ لَهُ أَندَادًا ۚ وَأَسَرُّوا النَّدَامَةَ لَمَّا رَأَوُا الْعَذَابَ وَجَعَلْنَا الْأَغْلَالَ فِي أَعْنَاقِ الَّذِينَ كَفَرُوا ۚ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُوا يَعْمَلُونَ','و مستضعفان به مستکبران می‌گویند: «وسوسه‌های فریبکارانه شما در شب و روز (مایه گمراهی ما شد)، هنگامی که به ما دستور می‌دادید که به خداوند کافر شویم و همتایانی برای او قرار دهیم!» و آنان هنگامی که عذاب (الهی) را می‌بینند پشیمانی خود را پنهان می‌کنند (تا بیشتر رسوا نشوند)! و ما غل و زنجیرها در گردن کافران می نهیم؛ آیا جز آنچه عمل می‌کردند به آنها جزا داده می‌شود؟!'];
ayat[3640]=[34,34,'وَمَا أَرْسَلْنَا فِي قَرْيَةٍ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا بِمَا أُرْسِلْتُم بِهِ كَافِرُونَ','و ما در هیچ شهر و دیاری پیامبری بیم‌دهنده نفرستادیم مگر اینکه مترفین آنها (که مست ناز و نعمت بودند) گفتند: «ما به آنچه فرستاده شده‌اید کافریم!»'];
ayat[3641]=[34,35,'وَقَالُوا نَحْنُ أَكْثَرُ أَمْوَالًا وَأَوْلَادًا وَمَا نَحْنُ بِمُعَذَّبِينَ','و گفتند: «اموال و اولاد ما (از همه) بیشتر است (و این نشانه علاقه خدا به ماست!)؛ و ما هرگز مجازات نخواهیم شد!»'];
ayat[3642]=[34,36,'قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ وَيَقْدِرُ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','بگو: «پروردگار من روزی را برای هر کس بخواهد وسیع یا تنگ می‌کند، (این ربطی به قرب در درگاه او ندارد)؛ ولی بیشتر مردم نمی‌دانند!'];
ayat[3643]=[34,37,'وَمَا أَمْوَالُكُمْ وَلَا أَوْلَادُكُم بِالَّتِي تُقَرِّبُكُمْ عِندَنَا زُلْفَىٰ إِلَّا مَنْ آمَنَ وَعَمِلَ صَالِحًا فَأُولَٰئِكَ لَهُمْ جَزَاءُ الضِّعْفِ بِمَا عَمِلُوا وَهُمْ فِي الْغُرُفَاتِ آمِنُونَ','اموال و فرزندانتان هرگز شما را نزد ما مقرّب نمی‌سازد، جز کسانی که ایمان بیاورند و عمل صالحی انجام دهند که برای آنان پاداش مضاعف در برابر کارهایی است که انجام داده‌اند؛ و آنها در غرفه‌های (بهشتی) در (نهایت) امنیّت خواهند بود!'];
ayat[3644]=[34,38,'وَالَّذِينَ يَسْعَوْنَ فِي آيَاتِنَا مُعَاجِزِينَ أُولَٰئِكَ فِي الْعَذَابِ مُحْضَرُونَ','و کسانی که برای انکار و ابطال آیات ما تلاش می‌کنند و می‌پندارند از چنگ قدرت ما فرار خواهند کرد، در عذاب (الهی) احضار می‌شوند!'];
ayat[3645]=[34,39,'قُلْ إِنَّ رَبِّي يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ مِنْ عِبَادِهِ وَيَقْدِرُ لَهُ ۚ وَمَا أَنفَقْتُم مِّن شَيْءٍ فَهُوَ يُخْلِفُهُ ۖ وَهُوَ خَيْرُ الرَّازِقِينَ','بگو: «پروردگارم روزی را برای هر کس بخواهد وسعت می‌بخشد، و برای هر کس بخواهد تنگ (و محدود) می‌سازد؛ و هر چیزی را (در راه او) انفاق کنید، عوض آن را می‌دهد (و جای آن را پر می‌کند)؛ و او بهترین روزی‌دهندگان است!»'];
ayat[3646]=[34,40,'وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا ثُمَّ يَقُولُ لِلْمَلَائِكَةِ أَهَٰؤُلَاءِ إِيَّاكُمْ كَانُوا يَعْبُدُونَ','(به خاطر بیاور) روزی را که خداوند همه آنان را بر می‌انگیزد، سپس به فرشتگان می‌گوید: «آیا اینها شما را پرستش می‌کردند؟!»'];
ayat[3647]=[34,41,'قَالُوا سُبْحَانَكَ أَنتَ وَلِيُّنَا مِن دُونِهِم ۖ بَلْ كَانُوا يَعْبُدُونَ الْجِنَّ ۖ أَكْثَرُهُم بِهِم مُّؤْمِنُونَ','آنها می‌گویند: «منزّهی (از اینکه همتایی داشته باشی)! تنها تو ولیّ مائی، نه آنها؛ (آنها ما را پرستش نمی‌کردند) بلکه جنّ را پرستش می‌نمودند؛ و اکثرشان به آنها ایمان داشتند!»'];
ayat[3648]=[34,42,'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَّفْعًا وَلَا ضَرًّا وَنَقُولُ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذَابَ النَّارِ الَّتِي كُنتُم بِهَا تُكَذِّبُونَ','(آری) امروز هیچ یک از شما نسبت به دیگری مالک سود و زیانی نیست! و به ظالمان می‌گوییم: «بچشید عذاب آتشی را که تکذیب می‌کردید!»'];
ayat[3649]=[34,43,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالُوا مَا هَٰذَا إِلَّا رَجُلٌ يُرِيدُ أَن يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ آبَاؤُكُمْ وَقَالُوا مَا هَٰذَا إِلَّا إِفْكٌ مُّفْتَرًى ۚ وَقَالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جَاءَهُمْ إِنْ هَٰذَا إِلَّا سِحْرٌ مُّبِينٌ','و هنگامی که آیات روشنگر ما بر آنان خوانده می‌شود، می‌گویند: «او فقط مردی است که می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند بازدارد!» و می‌گویند: «این جز دروغ بزرگی که (به خدا) بسته شده چیز دیگری نیست!» و کافران هنگامی که حق به سراغشان آمد گفتند: «این، جز افسونی آشکار نیست!»'];
ayat[3650]=[34,44,'وَمَا آتَيْنَاهُم مِّن كُتُبٍ يَدْرُسُونَهَا ۖ وَمَا أَرْسَلْنَا إِلَيْهِمْ قَبْلَكَ مِن نَّذِيرٍ','ما (قبلا) ً چیزی از کتابهای آسمانی را به آنان نداده‌ایم که آن را بخوانند (و به اتکّای آن سخنان تو را تکذیب کنند)، و پیش از تو هیچ بیم‌دهنده [= پیامبری] برای آنان نفرستادیم!'];
ayat[3651]=[34,45,'وَكَذَّبَ الَّذِينَ مِن قَبْلِهِمْ وَمَا بَلَغُوا مِعْشَارَ مَا آتَيْنَاهُمْ فَكَذَّبُوا رُسُلِي ۖ فَكَيْفَ كَانَ نَكِيرِ','کسانی که پیش از آنان بودند (نیز آیات الهی را) تکذیب کردند، در حالی که اینها به یک دهم آنچه به آنان دادیم نمی‌رسند! (آری) آنها رسولان مرا تکذیب کردند؛ پس ببین مجازات من (نسبت به آنها) چگونه بود!'];
ayat[3652]=[34,46,'۞ قُلْ إِنَّمَا أَعِظُكُم بِوَاحِدَةٍ ۖ أَن تَقُومُوا لِلَّهِ مَثْنَىٰ وَفُرَادَىٰ ثُمَّ تَتَفَكَّرُوا ۚ مَا بِصَاحِبِكُم مِّن جِنَّةٍ ۚ إِنْ هُوَ إِلَّا نَذِيرٌ لَّكُم بَيْنَ يَدَيْ عَذَابٍ شَدِيدٍ','بگو: «شما را تنها به یک چیز اندرز می‌دهم، و آن اینکه: دو نفر دو نفر یا یک نفر یک نفر برای خدا قیام کنید، سپس بیندیشید این دوست و همنشین شما [= محمّد] هیچ گونه جنونی ندارد؛ او فقط بیم‌دهنده شما در برابر عذاب شدید (الهی) است!»'];
ayat[3653]=[34,47,'قُلْ مَا سَأَلْتُكُم مِّنْ أَجْرٍ فَهُوَ لَكُمْ ۖ إِنْ أَجْرِيَ إِلَّا عَلَى اللَّهِ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ','بگو: «هر اجر و پاداشی از شما خواسته‌ام برای خود شماست؛ اجر من تنها بر خداوند است، و او بر همه چیز گواه است!»'];
ayat[3654]=[34,48,'قُلْ إِنَّ رَبِّي يَقْذِفُ بِالْحَقِّ عَلَّامُ الْغُيُوبِ','بگو: «پروردگار من حق را (بر دل پیامبران خود) می‌افکند، که او دانای غیبها (و اسرار نهان) است.»'];
ayat[3655]=[34,49,'قُلْ جَاءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيدُ','بگو: «حق آمد! و باطل (کاری از آن ساخته نیست و) نمی‌تواند آغازگر چیزی باشد و نه تجدیدکننده آن!»'];
ayat[3656]=[34,50,'قُلْ إِن ضَلَلْتُ فَإِنَّمَا أَضِلُّ عَلَىٰ نَفْسِي ۖ وَإِنِ اهْتَدَيْتُ فَبِمَا يُوحِي إِلَيَّ رَبِّي ۚ إِنَّهُ سَمِيعٌ قَرِيبٌ','بگو: «اگر من گمراه شوم، از ناحیه خود گمراه می‌شوم؛ و اگر هدایت یابم، به وسیله آنچه پروردگارم به من وحی می‌کند هدایت می‌یابم؛ او شنوای نزدیک است!»'];
ayat[3657]=[34,51,'وَلَوْ تَرَىٰ إِذْ فَزِعُوا فَلَا فَوْتَ وَأُخِذُوا مِن مَّكَانٍ قَرِيبٍ','اگر ببینی هنگامی که فریادشان بلند می‌شود امّا نمی‌توانند (از عذاب الهی) بگریزند، و آنها را از جای نزدیکی (که حتّی انتظارش را ندارند) می‌گیرند (از درماندگی آنها تعجّب خواهی کرد)!'];
ayat[3658]=[34,52,'وَقَالُوا آمَنَّا بِهِ وَأَنَّىٰ لَهُمُ التَّنَاوُشُ مِن مَّكَانٍ بَعِيدٍ','و (در آن حال) می‌گویند: «به حقّ ایمان آوردیم!»، ولی چگونه می‌توانند از فاصله دور به آن دسترسی پیدا کنند!'];
ayat[3659]=[34,53,'وَقَدْ كَفَرُوا بِهِ مِن قَبْلُ ۖ وَيَقْذِفُونَ بِالْغَيْبِ مِن مَّكَانٍ بَعِيدٍ','آنها پیش از این (که در نهایت آزادی بودند) به آن کافر شدند و دورا دور، و غائبانه (و بدون آگاهی). نسبت‌های ناروا می‌دادند.'];
ayat[3660]=[34,54,'وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِأَشْيَاعِهِم مِّن قَبْلُ ۚ إِنَّهُمْ كَانُوا فِي شَكٍّ مُّرِيبٍ','(سرانجام) میان آنها و خواسته‌هایشان جدایی افکنده شد، همان گونه که با پیروان (و هم‌مسلکان) آنها از قبل عمل شد، چرا که آنها در شک و تردید بودند!'];
ayat[3661]=[35,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَمْدُ لِلَّهِ فَاطِرِ السَّمَاوَاتِ وَالْأَرْضِ جَاعِلِ الْمَلَائِكَةِ رُسُلًا أُولِي أَجْنِحَةٍ مَّثْنَىٰ وَثُلَاثَ وَرُبَاعَ ۚ يَزِيدُ فِي الْخَلْقِ مَا يَشَاءُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','ستایش مخصوص خداوندی است آفریننده آسمانها و زمین، که فرشتگان را رسولانی قرار داد دارای بالهای دوگانه و سه‌گانه و چهارگانه، او هر چه بخواهد در آفرینش می‌افزاید، و او بر هر چیزی تواناست!'];
ayat[3662]=[35,2,'مَّا يَفْتَحِ اللَّهُ لِلنَّاسِ مِن رَّحْمَةٍ فَلَا مُمْسِكَ لَهَا ۖ وَمَا يُمْسِكْ فَلَا مُرْسِلَ لَهُ مِن بَعْدِهِ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','هر رحمتی را خدا به روی مردم بگشاید، کسی نمی‌تواند جلو آن را بگیرد؛ و هر چه را امساک کند، کسی غیر از او قادر به فرستادن آن نیست؛ و او عزیز و حکیم است!'];
ayat[3663]=[35,3,'يَا أَيُّهَا النَّاسُ اذْكُرُوا نِعْمَتَ اللَّهِ عَلَيْكُمْ ۚ هَلْ مِنْ خَالِقٍ غَيْرُ اللَّهِ يَرْزُقُكُم مِّنَ السَّمَاءِ وَالْأَرْضِ ۚ لَا إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ','ای مردم! به یاد آورید نعمت خدا را بر شما؛ آیا آفریننده‌ای جز خدا هست که شما را از آسمان و زمین روزی دهد؟! هیچ معبودی جز او نیست؛ با این حال چگونه به سوی باطل منحرف می‌شوید؟!'];
ayat[3664]=[35,4,'وَإِن يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِّن قَبْلِكَ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ','اگر تو را تکذیب کنند (غم مخور، موضوع تازه‌ای نیست) پیامبران پیش از تو نیز تکذیب شدند؛ و همه کارها بسوی خدا بازمی‌گردد.'];
ayat[3665]=[35,5,'يَا أَيُّهَا النَّاسُ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۖ فَلَا تَغُرَّنَّكُمُ الْحَيَاةُ الدُّنْيَا ۖ وَلَا يَغُرَّنَّكُم بِاللَّهِ الْغَرُورُ','ای مردم! وعده خداوند حقّ است؛ مبادا زندگی دنیا شما را بفریبد، و مبادا شیطان شما را فریب دهد و به (کرم) خدا مغرور سازد!'];
ayat[3666]=[35,6,'إِنَّ الشَّيْطَانَ لَكُمْ عَدُوٌّ فَاتَّخِذُوهُ عَدُوًّا ۚ إِنَّمَا يَدْعُو حِزْبَهُ لِيَكُونُوا مِنْ أَصْحَابِ السَّعِيرِ','البتّه شیطان دشمن شماست، پس او را دشمن بدانید؛ او فقط حزبش را به این دعوت می‌کند که اهل آتش سوزان (جهنّم) باشند!'];
ayat[3667]=[35,7,'الَّذِينَ كَفَرُوا لَهُمْ عَذَابٌ شَدِيدٌ ۖ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُم مَّغْفِرَةٌ وَأَجْرٌ كَبِيرٌ','کسانی که راه کفر پیش گرفتند، برای آنان عذابی سخت است؛ و کسانی که ایمان آوردند و کارهای شایسته انجام دادند، آمرزش و پاداش بزرگ از آن آنهاست!'];
ayat[3668]=[35,8,'أَفَمَن زُيِّنَ لَهُ سُوءُ عَمَلِهِ فَرَآهُ حَسَنًا ۖ فَإِنَّ اللَّهَ يُضِلُّ مَن يَشَاءُ وَيَهْدِي مَن يَشَاءُ ۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَاتٍ ۚ إِنَّ اللَّهَ عَلِيمٌ بِمَا يَصْنَعُونَ','آیا کسی که عمل بدش برای او آراسته شده و آن را خوب و زیبا می‌بیند (همانند کسی است که واقع را آنچنان که هست می‌یابد)؟! خداوند هر کس را بخواهد گمراه می سازد و هر کس را بخواهد هدایت می‌کند؛ پس جانت به خاطر شدّت تأسف بر آنان از دست نرود؛ خداوند به آنچه انجام می‌دهند داناست!'];
ayat[3669]=[35,9,'وَاللَّهُ الَّذِي أَرْسَلَ الرِّيَاحَ فَتُثِيرُ سَحَابًا فَسُقْنَاهُ إِلَىٰ بَلَدٍ مَّيِّتٍ فَأَحْيَيْنَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا ۚ كَذَٰلِكَ النُّشُورُ','خداوند کسی است که بادها را فرستاد تا ابرهایی را به حرکت درآورند؛ سپس ما این ابرها را به سوی زمین مرده‌ای راندیم و به وسیله آن، زمین را پس از مردنش زنده می‌کنیم؛ رستاخیز نیز همین گونه است!'];
ayat[3670]=[35,10,'مَن كَانَ يُرِيدُ الْعِزَّةَ فَلِلَّهِ الْعِزَّةُ جَمِيعًا ۚ إِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهُ ۚ وَالَّذِينَ يَمْكُرُونَ السَّيِّئَاتِ لَهُمْ عَذَابٌ شَدِيدٌ ۖ وَمَكْرُ أُولَٰئِكَ هُوَ يَبُورُ','کسی که خواهان عزّت است (باید از خدا بخواهد چرا که) تمام عزّت برای خداست؛ سخنان پاکیزه به سوی او صعود می‌کند، و عمل صالح را بالا می‌برد؛ و آنها که نقشه‌های بد می‌کشند، عذاب سختی برای آنهاست و مکر (و تلاش افسادگرانه) آنان نابود می‌شود (و به جایی نمی‌رسد)!'];
ayat[3671]=[35,11,'وَاللَّهُ خَلَقَكُم مِّن تُرَابٍ ثُمَّ مِن نُّطْفَةٍ ثُمَّ جَعَلَكُمْ أَزْوَاجًا ۚ وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِ ۚ وَمَا يُعَمَّرُ مِن مُّعَمَّرٍ وَلَا يُنقَصُ مِنْ عُمُرِهِ إِلَّا فِي كِتَابٍ ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ','خداوند شما را از خاکی آفرید، سپس از نطفه‌ای؛ سپس شما را بصورت زوجهایی قرار داد؛ هیچ جنس ماده‌ای باردار نمی‌شود و وضع حمل نمی‌کند مگر به علم او، و هیچ کس عمر طولانی نمی‌کند، یا از عمرش کاسته نمی‌شود مگر اینکه در کتاب (علم خداوند) ثبت است؛ اینها همه برای خداوند آسان است.'];
ayat[3672]=[35,12,'وَمَا يَسْتَوِي الْبَحْرَانِ هَٰذَا عَذْبٌ فُرَاتٌ سَائِغٌ شَرَابُهُ وَهَٰذَا مِلْحٌ أُجَاجٌ ۖ وَمِن كُلٍّ تَأْكُلُونَ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَهَا ۖ وَتَرَى الْفُلْكَ فِيهِ مَوَاخِرَ لِتَبْتَغُوا مِن فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ','دو دریا یکسان نیستند: این یکی دریایی است که آبش گوارا و شیرین و نوشیدنش خوشگوار است، و آن یکی شور و تلخ و گلوگیر؛ (امّا) از هر دو گوشتی تازه می خورید و وسایل زینتی استخراج کرده می‌پوشید؛ و کشتیها را در آن می‌بینی که آنها را می‌شکافند (و به سوی مقصد پیش می‌روند) تا از فضل خداوند بهره‌گیرید، و شاید شکر (نعمتهای او را) بجا آورید!'];
ayat[3673]=[35,13,'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُّسَمًّى ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ ۚ وَالَّذِينَ تَدْعُونَ مِن دُونِهِ مَا يَمْلِكُونَ مِن قِطْمِيرٍ','او شب را در روز داخل می‌کند و روز را در شب؛ و خورشید و ماه را مسخّر (شما) کرده، هر یک تا سرآمد معیّنی به حرکت خود ادامه می‌دهد؛ این است خداوند، پروردگار شما؛ حاکمیّت (در سراسر عالم) از آن اوست: و کسانی را که جز او می‌خوانید (و می‌پرستید) حتی به اندازه پوست نازک هسته خرما مالک نیستند!'];
ayat[3674]=[35,14,'إِن تَدْعُوهُمْ لَا يَسْمَعُوا دُعَاءَكُمْ وَلَوْ سَمِعُوا مَا اسْتَجَابُوا لَكُمْ ۖ وَيَوْمَ الْقِيَامَةِ يَكْفُرُونَ بِشِرْكِكُمْ ۚ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيرٍ','اگر آنها را بخوانید صدای شما را نمی‌شنوند، و اگر بشنوند به شما پاسخ نمی‌گویند؛ و روز قیامت، شرک (و پرستش) شما را منکر می‌شوند، و هیچ کس مانند (خداوند آگاه و) خبیر تو را (از حقایق) با خبر نمی‌سازد!'];
ayat[3675]=[35,15,'۞ يَا أَيُّهَا النَّاسُ أَنتُمُ الْفُقَرَاءُ إِلَى اللَّهِ ۖ وَاللَّهُ هُوَ الْغَنِيُّ الْحَمِيدُ','ای مردم شما (همگی) نیازمند به خدائید؛ تنها خداوند است که بی‌نیاز و شایسته هر گونه حمد و ستایش است!'];
ayat[3676]=[35,16,'إِن يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ','اگر بخواهد شما را می‌برد و خلق جدیدی می‌آورد؛'];
ayat[3677]=[35,17,'وَمَا ذَٰلِكَ عَلَى اللَّهِ بِعَزِيزٍ','و این برای خداوند مشکل نیست!'];
ayat[3678]=[35,18,'وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۚ وَإِن تَدْعُ مُثْقَلَةٌ إِلَىٰ حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَلَوْ كَانَ ذَا قُرْبَىٰ ۗ إِنَّمَا تُنذِرُ الَّذِينَ يَخْشَوْنَ رَبَّهُم بِالْغَيْبِ وَأَقَامُوا الصَّلَاةَ ۚ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفْسِهِ ۚ وَإِلَى اللَّهِ الْمَصِيرُ','هیچ گنهکاری بار گناه دیگری را بر دوش نمی‌کشد؛ و اگر شخص سنگین‌باری دیگری را برای حمل گناه خود بخواند، چیزی از آن را بر دوش نخواهد گرفت، هر چند از نزدیکان او باشد! تو فقط کسانی را بیم‌می‌دهی که از پروردگار خود در پنهانی می‌ترسند و نماز را برپا می‌دارند؛ و هر کس پاکی (و تقوا) پیشه کند، نتیجه آن به خودش بازمی‌گردد؛ و بازگشت (همگان) به سوی خداست!'];
ayat[3679]=[35,19,'وَمَا يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ','و نابینا و بینا هرگز برابر نیستند،'];
ayat[3680]=[35,20,'وَلَا الظُّلُمَاتُ وَلَا النُّورُ','و نه ظلمتها و روشنایی،'];
ayat[3681]=[35,21,'وَلَا الظِّلُّ وَلَا الْحَرُورُ','و نه سایه (آرامبخش) و باد داغ و سوزان!'];
ayat[3682]=[35,22,'وَمَا يَسْتَوِي الْأَحْيَاءُ وَلَا الْأَمْوَاتُ ۚ إِنَّ اللَّهَ يُسْمِعُ مَن يَشَاءُ ۖ وَمَا أَنتَ بِمُسْمِعٍ مَّن فِي الْقُبُورِ','و هرگز مردگان و زندگان یکسان نیستند! خداوند پیام خود را به گوش هر کس بخواهد می‌رساند، و تو نمی‌توانی سخن خود را به گوش آنان که در گور خفته‌اند برسانی!'];
ayat[3683]=[35,23,'إِنْ أَنتَ إِلَّا نَذِيرٌ','تو فقط انذارکننده‌ای، (اگر ایمان نیاورند نگران نباش، وظیفه‌ات را انجام ده.)'];
ayat[3684]=[35,24,'إِنَّا أَرْسَلْنَاكَ بِالْحَقِّ بَشِيرًا وَنَذِيرًا ۚ وَإِن مِّنْ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٌ','ما تو را بحق برای بشارت و انذار فرستادیم؛ و هر امّتی در گذشته انذارکننده‌ای داشته است!'];
ayat[3685]=[35,25,'وَإِن يُكَذِّبُوكَ فَقَدْ كَذَّبَ الَّذِينَ مِن قَبْلِهِمْ جَاءَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ وَبِالزُّبُرِ وَبِالْكِتَابِ الْمُنِيرِ','اگر تو را تکذیب کنند (عجیب نیست)؛ کسانی که پیش از آنان بودند (نیز پیامبران خود را) تکذیب کردند؛ آنها با دلایل روشن و کتابهای پند و موعظه و کتب آسمانی روشنگر (مشتمل بر معارف و احکام) به سراغ آنان آمدند (امّا کوردلان ایمان نیاوردند).'];
ayat[3686]=[35,26,'ثُمَّ أَخَذْتُ الَّذِينَ كَفَرُوا ۖ فَكَيْفَ كَانَ نَكِيرِ','سپس من کافران را (بعد از اتمام حجّت) گرفتم (و سخت مجازات کردم)؛ مجازات من نسبت به آنان چگونه بود؟!'];
ayat[3687]=[35,27,'أَلَمْ تَرَ أَنَّ اللَّهَ أَنزَلَ مِنَ السَّمَاءِ مَاءً فَأَخْرَجْنَا بِهِ ثَمَرَاتٍ مُّخْتَلِفًا أَلْوَانُهَا ۚ وَمِنَ الْجِبَالِ جُدَدٌ بِيضٌ وَحُمْرٌ مُّخْتَلِفٌ أَلْوَانُهَا وَغَرَابِيبُ سُودٌ','آیا ندیدی خداوند از آسمان آبی فرو فرستاد که بوسیله آن میوه‌هایی رنگارنگ (از زمین) خارج ساختیم و از کوه‌ها نیز (به لطف پروردگار) جاده‌هایی آفریده شده سفید و سرخ و به رنگهای مختلف و گاه به رنگ کاملاً سیاه!'];
ayat[3688]=[35,28,'وَمِنَ النَّاسِ وَالدَّوَابِّ وَالْأَنْعَامِ مُخْتَلِفٌ أَلْوَانُهُ كَذَٰلِكَ ۗ إِنَّمَا يَخْشَى اللَّهَ مِنْ عِبَادِهِ الْعُلَمَاءُ ۗ إِنَّ اللَّهَ عَزِيزٌ غَفُورٌ','و از انسانها و جنبندگان و چهارپایان انواعی با رنگهای مختلف، (آری) حقیقت چنین است: از میان بندگان خدا، تنها دانشمندان از او می‌ترسند؛ خداوند عزیز و غفور است!'];
ayat[3689]=[35,29,'إِنَّ الَّذِينَ يَتْلُونَ كِتَابَ اللَّهِ وَأَقَامُوا الصَّلَاةَ وَأَنفَقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً يَرْجُونَ تِجَارَةً لَّن تَبُورَ','کسانی که کتاب الهی را تلاوت می‌کنند و نماز را برپا می‌دارند و از آنچه به آنان روزی داده‌ایم پنهان و آشکار انفاق می‌کنند، تجارتی (پرسود و) بی‌زیان و خالی از کساد را امید دارند.'];
ayat[3690]=[35,30,'لِيُوَفِّيَهُمْ أُجُورَهُمْ وَيَزِيدَهُم مِّن فَضْلِهِ ۚ إِنَّهُ غَفُورٌ شَكُورٌ','(آنها این اعمال صالح را انجام می‌دهند) تا خداوند اجر و پاداش کامل به آنها دهد و از فضلش بر آنها بیفزاید که او آمرزنده و شکرگزار است!'];
ayat[3691]=[35,31,'وَالَّذِي أَوْحَيْنَا إِلَيْكَ مِنَ الْكِتَابِ هُوَ الْحَقُّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ ۗ إِنَّ اللَّهَ بِعِبَادِهِ لَخَبِيرٌ بَصِيرٌ','و آنچه از کتاب به تو وحی کردیم حق است و تصدیق‌کننده و هماهنگ با کتب پیش از آن؛ خداوند نسبت به بندگانش خبیر و بیناست!'];
ayat[3692]=[35,32,'ثُمَّ أَوْرَثْنَا الْكِتَابَ الَّذِينَ اصْطَفَيْنَا مِنْ عِبَادِنَا ۖ فَمِنْهُمْ ظَالِمٌ لِّنَفْسِهِ وَمِنْهُم مُّقْتَصِدٌ وَمِنْهُمْ سَابِقٌ بِالْخَيْرَاتِ بِإِذْنِ اللَّهِ ۚ ذَٰلِكَ هُوَ الْفَضْلُ الْكَبِيرُ','سپس این کتاب (آسمانی) را به گروهی از بندگان برگزیده خود به میراث دادیم؛ (امّا) از میان آنها عده‌ای بر خود ستم کردند، و عده‌ای میانه رو بودند، و گروهی به اذن خدا در نیکیها (از همه) پیشی گرفتند، و این، همان فضیلت بزرگ است!'];
ayat[3693]=[35,33,'جَنَّاتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِن ذَهَبٍ وَلُؤْلُؤًا ۖ وَلِبَاسُهُمْ فِيهَا حَرِيرٌ','(پاداش آنان) باغهای جاویدان بهشت است که در آن وارد می‌شوند در حالی که با دستبندهایی از طلا و مروارید آراسته‌اند، و لباسشان در آنجا حریر است!'];
ayat[3694]=[35,34,'وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي أَذْهَبَ عَنَّا الْحَزَنَ ۖ إِنَّ رَبَّنَا لَغَفُورٌ شَكُورٌ','آنها می‌گویند: «حمد (و ستایش) برای خداوندی است که اندوه را از ما برطرف ساخت؛ پروردگار ما آمرزنده و سپاسگزار است!'];
ayat[3695]=[35,35,'الَّذِي أَحَلَّنَا دَارَ الْمُقَامَةِ مِن فَضْلِهِ لَا يَمَسُّنَا فِيهَا نَصَبٌ وَلَا يَمَسُّنَا فِيهَا لُغُوبٌ','همان کسی که با فضل خود ما را در این سرای اقامت (جاویدان) جای داد که نه در آن رنجی به ما می‌رسد و نه سستی و واماندگی!'];
ayat[3696]=[35,36,'وَالَّذِينَ كَفَرُوا لَهُمْ نَارُ جَهَنَّمَ لَا يُقْضَىٰ عَلَيْهِمْ فَيَمُوتُوا وَلَا يُخَفَّفُ عَنْهُم مِّنْ عَذَابِهَا ۚ كَذَٰلِكَ نَجْزِي كُلَّ كَفُورٍ','و کسانی که کافر شدند، آتش دوزخ برای آنهاست؛ هرگز فرمان مرگشان صادر نمی‌شود تا بمیرند، و نه چیزی از عذابش از آنان تخفیف داده می‌شود؛ این گونه هر کفران‌کننده‌ای را کیفر می‌دهیم!'];
ayat[3697]=[35,37,'وَهُمْ يَصْطَرِخُونَ فِيهَا رَبَّنَا أَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِي كُنَّا نَعْمَلُ ۚ أَوَلَمْ نُعَمِّرْكُم مَّا يَتَذَكَّرُ فِيهِ مَن تَذَكَّرَ وَجَاءَكُمُ النَّذِيرُ ۖ فَذُوقُوا فَمَا لِلظَّالِمِينَ مِن نَّصِيرٍ','آنها در دوزخ فریاد می‌زنند: «پروردگارا! ما را خارج کن تا عمل صالحی انجام دهیم غیر از آنچه انجام می‌دادیم!» (در پاسخ به آنان گفته می‌شود:) آیا شما را به اندازه‌ای که هر کس اهل تذکّر است در آن متذکّر می‌شود عمر ندادیم، و انذارکننده (الهی) به سراغ شما نیامد؟! اکنون بچشید که برای ظالمان هیچ یاوری نیست!'];
ayat[3698]=[35,38,'إِنَّ اللَّهَ عَالِمُ غَيْبِ السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','خداوند از غیب آسمانها و زمین آگاه است، و آنچه را در درون دلهاست می‌داند!'];
ayat[3699]=[35,39,'هُوَ الَّذِي جَعَلَكُمْ خَلَائِفَ فِي الْأَرْضِ ۚ فَمَن كَفَرَ فَعَلَيْهِ كُفْرُهُ ۖ وَلَا يَزِيدُ الْكَافِرِينَ كُفْرُهُمْ عِندَ رَبِّهِمْ إِلَّا مَقْتًا ۖ وَلَا يَزِيدُ الْكَافِرِينَ كُفْرُهُمْ إِلَّا خَسَارًا','اوست که شما را جانشینانی در زمین قرار داد؛ هر کس کافر شود، کفر او به زیان خودش خواهد بود، و کافران را کفرشان جز خشم و غضب در نزد پروردگار چیزی نمی‌افزاید، و (نیز) کفرشان جز زیان و خسران چیزی بر آنها اضافه نمی‌کند!'];
ayat[3700]=[35,40,'قُلْ أَرَأَيْتُمْ شُرَكَاءَكُمُ الَّذِينَ تَدْعُونَ مِن دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا مِنَ الْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّمَاوَاتِ أَمْ آتَيْنَاهُمْ كِتَابًا فَهُمْ عَلَىٰ بَيِّنَتٍ مِّنْهُ ۚ بَلْ إِن يَعِدُ الظَّالِمُونَ بَعْضُهُم بَعْضًا إِلَّا غُرُورًا','بگو: «این معبودانی را که جز خدا می‌خوانید به من نشان دهید چه چیزی از زمین را آفریده‌اند، یا اینکه شرکتی در (آفرینش و مالکیّت) آسمانها دارند؟! یا به آنان کتابی (آسمانی) داده‌ایم و دلیلی از آن برای (شرک) خود دارند؟!» نه هیچ یک از اینها نیست، ظالمان فقط وعده‌های دروغین به یکدیگر می‌دهند!'];
ayat[3701]=[35,41,'۞ إِنَّ اللَّهَ يُمْسِكُ السَّمَاوَاتِ وَالْأَرْضَ أَن تَزُولَا ۚ وَلَئِن زَالَتَا إِنْ أَمْسَكَهُمَا مِنْ أَحَدٍ مِّن بَعْدِهِ ۚ إِنَّهُ كَانَ حَلِيمًا غَفُورًا','خداوند آسمانها و زمین را نگاه می‌دارد تا از نظام خود منحرف نشوند؛ و هرگاه منحرف گردند، کسی جز او نمی‌تواند آنها را نگاه دارد، او بردبار و غفور است!'];
ayat[3702]=[35,42,'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِن جَاءَهُمْ نَذِيرٌ لَّيَكُونُنَّ أَهْدَىٰ مِنْ إِحْدَى الْأُمَمِ ۖ فَلَمَّا جَاءَهُمْ نَذِيرٌ مَّا زَادَهُمْ إِلَّا نُفُورًا','آنان با نهایت تأکید به خدا سوگند خوردند که اگر پیامبری انذارکننده به سراغشان آید، هدایت یافته‌ترین امّتها خواهند بود؛ امّا چون پیامبری برای آنان آمد، جز فرار و فاصله‌گرفتن از (حق) چیزی بر آنها نیفزود!'];
ayat[3703]=[35,43,'اسْتِكْبَارًا فِي الْأَرْضِ وَمَكْرَ السَّيِّئِ ۚ وَلَا يَحِيقُ الْمَكْرُ السَّيِّئُ إِلَّا بِأَهْلِهِ ۚ فَهَلْ يَنظُرُونَ إِلَّا سُنَّتَ الْأَوَّلِينَ ۚ فَلَن تَجِدَ لِسُنَّتِ اللَّهِ تَبْدِيلًا ۖ وَلَن تَجِدَ لِسُنَّتِ اللَّهِ تَحْوِيلًا','اینها همه بخاطر استکبار در زمین و نیرنگهای بدشان بود؛ امّا این نیرنگها تنها دامان صاحبانش را می‌گیرد؛ آیا آنها چیزی جز سنّت پیشینیان و (عذابهای دردناک آنان) را انتظار دارند؟! هرگز برای سنّت خدا تبدیل نخواهی یافت، و هرگز برای سنّت الهی تغییری نمی‌یابی!'];
ayat[3704]=[35,44,'أَوَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِن قَبْلِهِمْ وَكَانُوا أَشَدَّ مِنْهُمْ قُوَّةً ۚ وَمَا كَانَ اللَّهُ لِيُعْجِزَهُ مِن شَيْءٍ فِي السَّمَاوَاتِ وَلَا فِي الْأَرْضِ ۚ إِنَّهُ كَانَ عَلِيمًا قَدِيرًا','آیا آنان در زمین نگشتند تا ببینند عاقبت کسانی که پیش از آنها بودند چگونه بود؟! همانها که از اینان قویتر (و نیرومندتر) بودند؛ نه چیزی در آسمانها و نه چیزی در زمین از حوزه قدرت او بیرون نخواهد رفت؛ او دانا و تواناست!'];
ayat[3705]=[35,45,'وَلَوْ يُؤَاخِذُ اللَّهُ النَّاسَ بِمَا كَسَبُوا مَا تَرَكَ عَلَىٰ ظَهْرِهَا مِن دَابَّةٍ وَلَٰكِن يُؤَخِّرُهُمْ إِلَىٰ أَجَلٍ مُّسَمًّى ۖ فَإِذَا جَاءَ أَجَلُهُمْ فَإِنَّ اللَّهَ كَانَ بِعِبَادِهِ بَصِيرًا','اگر خداوند مردم را به سبب کارهایی که انجام داده‌اند مجازات کند، جنبنده‌ای را بر پشت زمین باقی نخواهد گذاشت! ولی (به لطفش) آنها را تا سرآمد معیّنی تأخیر می‌اندازد (و مهلت اصلاح می‌دهد) امّا هنگامی که اجل آنان فرا رسد، (خداوند هر کس را به مقتضای عملش جزا می‌دهد) او نسبت به بندگانش بیناست (و از اعمال و نیّات همه آگاه است)!'];
ayat[3706]=[36,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يس','یس.'];
ayat[3707]=[36,2,'وَالْقُرْآنِ الْحَكِيمِ','سوگند به قرآن حکیم.'];
ayat[3708]=[36,3,'إِنَّكَ لَمِنَ الْمُرْسَلِينَ','که تو قطعاً از رسولان (خداوند) هستی،'];
ayat[3709]=[36,4,'عَلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','بر راهی راست (قرار داری)؛'];
ayat[3710]=[36,5,'تَنزِيلَ الْعَزِيزِ الرَّحِيمِ','این قرآنی است که از سوی خداوند عزیز و رحیم نازل شده است‌'];
ayat[3711]=[36,6,'لِتُنذِرَ قَوْمًا مَّا أُنذِرَ آبَاؤُهُمْ فَهُمْ غَافِلُونَ','تا قومی را بیم دهی که پدرانشان انذار نشدند، از این رو آنان غافلند!'];
ayat[3712]=[36,7,'لَقَدْ حَقَّ الْقَوْلُ عَلَىٰ أَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُونَ','فرمان (الهی) درباره بیشتر آنها تحقق یافته، به همین جهت ایمان نمی‌آورند!'];
ayat[3713]=[36,8,'إِنَّا جَعَلْنَا فِي أَعْنَاقِهِمْ أَغْلَالًا فَهِيَ إِلَى الْأَذْقَانِ فَهُم مُّقْمَحُونَ','ما در گردنهای آنان غلهایی قرار دادیم که تا چانه‌ها ادامه دارد و سرهای آنان را به بالا نگاه داشته است!'];
ayat[3714]=[36,9,'وَجَعَلْنَا مِن بَيْنِ أَيْدِيهِمْ سَدًّا وَمِنْ خَلْفِهِمْ سَدًّا فَأَغْشَيْنَاهُمْ فَهُمْ لَا يُبْصِرُونَ','و در پیش روی آنان سدّی قرار دادیم، و در پشت سرشان سدّی؛ و چشمانشان را پوشانده‌ایم، لذا نمی‌بینند!'];
ayat[3715]=[36,10,'وَسَوَاءٌ عَلَيْهِمْ أَأَنذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَ','برای آنان یکسان است: چه انذارشان کنی یا نکنی، ایمان نمی‌آورند!'];
ayat[3716]=[36,11,'إِنَّمَا تُنذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمَٰنَ بِالْغَيْبِ ۖ فَبَشِّرْهُ بِمَغْفِرَةٍ وَأَجْرٍ كَرِيمٍ','تو فقط کسی را انذار می‌کنی که از این یادآوری (الهی) پیروی کند و از خداوند رحمان در نهان بترسد؛ چنین کسی را به آمرزش و پاداشی پرارزش بشارت ده!'];
ayat[3717]=[36,12,'إِنَّا نَحْنُ نُحْيِي الْمَوْتَىٰ وَنَكْتُبُ مَا قَدَّمُوا وَآثَارَهُمْ ۚ وَكُلَّ شَيْءٍ أَحْصَيْنَاهُ فِي إِمَامٍ مُّبِينٍ','به یقین ما مردگان را زنده می‌کنیم و آنچه را از پیش فرستاده‌اند و تمام آثار آنها را می نویسیم؛ و همه چیز را در کتاب آشکار کننده‌ای برشمرده‌ایم!'];
ayat[3718]=[36,13,'وَاضْرِبْ لَهُم مَّثَلًا أَصْحَابَ الْقَرْيَةِ إِذْ جَاءَهَا الْمُرْسَلُونَ','و برای آنها، اصحاب قریه (انطاکیه) را مثال بزن هنگامی که فرستادگان خدا به سوی آنان آمدند؛'];
ayat[3719]=[36,14,'إِذْ أَرْسَلْنَا إِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوا إِنَّا إِلَيْكُم مُّرْسَلُونَ','هنگامی که دو نفر از رسولان را بسوی آنها فرستادیم، امّا آنان رسولان (ما) را تکذیب کردند؛ پس برای تقویت آن دو، شخص سوّمی فرستادیم، آنها همگی گفتند: «ما فرستادگان (خدا) به سوی شما هستیم!»'];
ayat[3720]=[36,15,'قَالُوا مَا أَنتُمْ إِلَّا بَشَرٌ مِّثْلُنَا وَمَا أَنزَلَ الرَّحْمَٰنُ مِن شَيْءٍ إِنْ أَنتُمْ إِلَّا تَكْذِبُونَ','امّا آنان (در جواب) گفتند: «شما جز بشری همانند ما نیستید، و خداوند رحمان چیزی نازل نکرده، شما فقط دروغ می‌گویید!»'];
ayat[3721]=[36,16,'قَالُوا رَبُّنَا يَعْلَمُ إِنَّا إِلَيْكُمْ لَمُرْسَلُونَ','(رسولان ما) گفتند: «پروردگار ما آگاه است که ما قطعاً فرستادگان (او) به سوی شما هستیم،'];
ayat[3722]=[36,17,'وَمَا عَلَيْنَا إِلَّا الْبَلَاغُ الْمُبِينُ','و بر عهده ما چیزی جز ابلاغ آشکار نیست!»'];
ayat[3723]=[36,18,'قَالُوا إِنَّا تَطَيَّرْنَا بِكُمْ ۖ لَئِن لَّمْ تَنتَهُوا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِيمٌ','آنان گفتند: «ما شما را به فال بد گرفته‌ایم (و وجود شما را شوم می‌دانیم)، و اگر (از این سخنان) دست برندارید شما را سنگسار خواهیم کرد و شکنجه دردناکی از ما به شما خواهد رسید!'];
ayat[3724]=[36,19,'قَالُوا طَائِرُكُم مَّعَكُمْ ۚ أَئِن ذُكِّرْتُم ۚ بَلْ أَنتُمْ قَوْمٌ مُّسْرِفُونَ','(رسولان) گفتند: «شومی شما از خودتان است اگر درست بیندیشید، بلکه شما گروهی اسرافکارید!»'];
ayat[3725]=[36,20,'وَجَاءَ مِنْ أَقْصَى الْمَدِينَةِ رَجُلٌ يَسْعَىٰ قَالَ يَا قَوْمِ اتَّبِعُوا الْمُرْسَلِينَ','و مردی (با ایمان) از دورترین نقطه شهر با شتاب فرا رسید، گفت: «ای قوم من! از فرستادگان (خدا) پیروی کنید!'];
ayat[3726]=[36,21,'اتَّبِعُوا مَن لَّا يَسْأَلُكُمْ أَجْرًا وَهُم مُّهْتَدُونَ','از کسانی پیروی کنید که از شما مزدی نمی‌خواهند و خود هدایت یافته‌اند!'];
ayat[3727]=[36,22,'وَمَا لِيَ لَا أَعْبُدُ الَّذِي فَطَرَنِي وَإِلَيْهِ تُرْجَعُونَ','من چرا کسی را پرستش نکنم که مرا آفریده، و همگی به سوی او بازگشت داده می‌شوید؟!'];
ayat[3728]=[36,23,'أَأَتَّخِذُ مِن دُونِهِ آلِهَةً إِن يُرِدْنِ الرَّحْمَٰنُ بِضُرٍّ لَّا تُغْنِ عَنِّي شَفَاعَتُهُمْ شَيْئًا وَلَا يُنقِذُونِ','آیا غیر از او معبودانی را انتخاب کنم که اگر خداوند رحمان بخواهد زیانی به من برساند، شفاعت آنها کمترین فایده‌ای برای من ندارد و مرا (از مجازات او) نجات نخواهند داد!'];
ayat[3729]=[36,24,'إِنِّي إِذًا لَّفِي ضَلَالٍ مُّبِينٍ','اگر چنین کنم، من در گمراهی آشکاری خواهم بود!'];
ayat[3730]=[36,25,'إِنِّي آمَنتُ بِرَبِّكُمْ فَاسْمَعُونِ','(به همین دلیل) من به پروردگارتان ایمان آوردم؛ پس به سخنان من گوش فرا دهید!»'];
ayat[3731]=[36,26,'قِيلَ ادْخُلِ الْجَنَّةَ ۖ قَالَ يَا لَيْتَ قَوْمِي يَعْلَمُونَ','(سرانجام او را شهید کردند و) به او گفته شد: «وارد بهشت شو!» گفت: «ای کاش قوم من می‌دانستند...'];
ayat[3732]=[36,27,'بِمَا غَفَرَ لِي رَبِّي وَجَعَلَنِي مِنَ الْمُكْرَمِينَ','که پروردگارم مرا آمرزیده و از گرامی‌داشتگان قرار داده است!»'];
ayat[3733]=[36,28,'۞ وَمَا أَنزَلْنَا عَلَىٰ قَوْمِهِ مِن بَعْدِهِ مِن جُندٍ مِّنَ السَّمَاءِ وَمَا كُنَّا مُنزِلِينَ','و ما بعد از او بر قومش هیچ لشکری از آسمان نفرستادیم، و هرگز سنت ما بر این نبود؛'];
ayat[3734]=[36,29,'إِن كَانَتْ إِلَّا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ خَامِدُونَ','(بلکه) فقط یک صیحه آسمانی بود، ناگهان همگی خاموش شدند!'];
ayat[3735]=[36,30,'يَا حَسْرَةً عَلَى الْعِبَادِ ۚ مَا يَأْتِيهِم مِّن رَّسُولٍ إِلَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','افسوس بر این بندگان که هیچ پیامبری برای هدایت آنان نیامد مگر اینکه او را استهزا می‌کردند!'];
ayat[3736]=[36,31,'أَلَمْ يَرَوْا كَمْ أَهْلَكْنَا قَبْلَهُم مِّنَ الْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لَا يَرْجِعُونَ','آیا ندیدند چقدر از اقوام پیش از آنان را (بخاطر گناهانشان) هلاک کردیم، آنها هرگز به سوی ایشان بازنمی‌گردند (و زنده نمی‌شوند)!'];
ayat[3737]=[36,32,'وَإِن كُلٌّ لَّمَّا جَمِيعٌ لَّدَيْنَا مُحْضَرُونَ','و همه آنان (روز قیامت) نزد ما احضار می‌شوند!'];
ayat[3738]=[36,33,'وَآيَةٌ لَّهُمُ الْأَرْضُ الْمَيْتَةُ أَحْيَيْنَاهَا وَأَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُونَ','زمین مرده برای آنها آیتی است، ما آن را زنده کردیم و دانه‌های (غذایی) از آن خارج ساختیم که از آن می‌خورند؛'];
ayat[3739]=[36,34,'وَجَعَلْنَا فِيهَا جَنَّاتٍ مِّن نَّخِيلٍ وَأَعْنَابٍ وَفَجَّرْنَا فِيهَا مِنَ الْعُيُونِ','و در آن باغهایی از نخلها و انگورها قرار دادیم و چشمه‌هایی از آن جاری ساختیم،'];
ayat[3740]=[36,35,'لِيَأْكُلُوا مِن ثَمَرِهِ وَمَا عَمِلَتْهُ أَيْدِيهِمْ ۖ أَفَلَا يَشْكُرُونَ','تا از میوه آن بخورند در حالی که دست آنان هیچ دخالتی در ساختن آن نداشته است! آیا شکر خدا را بجا نمی‌آورند؟!'];
ayat[3741]=[36,36,'سُبْحَانَ الَّذِي خَلَقَ الْأَزْوَاجَ كُلَّهَا مِمَّا تُنبِتُ الْأَرْضُ وَمِنْ أَنفُسِهِمْ وَمِمَّا لَا يَعْلَمُونَ','منزه است کسی که تمام زوجها را آفرید، از آنچه زمین می‌رویاند، و از خودشان، و از آنچه نمی‌دانند!'];
ayat[3742]=[36,37,'وَآيَةٌ لَّهُمُ اللَّيْلُ نَسْلَخُ مِنْهُ النَّهَارَ فَإِذَا هُم مُّظْلِمُونَ','شب (نیز) برای آنها نشانه‌ای است (از عظمت خدا)؛ ما روز را از آن برمی‌گیریم، ناگهان تاریکی آنان را فرا می‌گیرد!'];
ayat[3743]=[36,38,'وَالشَّمْسُ تَجْرِي لِمُسْتَقَرٍّ لَّهَا ۚ ذَٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ','و خورشید (نیز برای آنها آیتی است) که پیوسته بسوی قرارگاهش در حرکت است؛ این تقدیر خداوند قادر و داناست.'];
ayat[3744]=[36,39,'وَالْقَمَرَ قَدَّرْنَاهُ مَنَازِلَ حَتَّىٰ عَادَ كَالْعُرْجُونِ الْقَدِيمِ','و برای ماه منزلگاه‌هایی قرار دادیم، (و هنگامی که این منازل را طی کرد) سرانجام بصورت «شاخه کهنه قوسی شکل و زرد رنگ خرما» در می‌آید.'];
ayat[3745]=[36,40,'لَا الشَّمْسُ يَنبَغِي لَهَا أَن تُدْرِكَ الْقَمَرَ وَلَا اللَّيْلُ سَابِقُ النَّهَارِ ۚ وَكُلٌّ فِي فَلَكٍ يَسْبَحُونَ','نه خورشید را سزاست که به ماه رسد، و نه شب بر روز پیشی می‌گیرد؛ و هر کدام در مسیر خود شناورند.'];
ayat[3746]=[36,41,'وَآيَةٌ لَّهُمْ أَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِي الْفُلْكِ الْمَشْحُونِ','نشانه‌ای (دیگر از عظمت پروردگار) برای آنان است که ما فرزندانشان را در کشتیهایی پر (از وسایل و بارها) حمل کردیم.'];
ayat[3747]=[36,42,'وَخَلَقْنَا لَهُم مِّن مِّثْلِهِ مَا يَرْكَبُونَ','و برای آنها مرکبهای دیگری همانند آن آفریدیم.'];
ayat[3748]=[36,43,'وَإِن نَّشَأْ نُغْرِقْهُمْ فَلَا صَرِيخَ لَهُمْ وَلَا هُمْ يُنقَذُونَ','و اگر بخواهیم آنها را غرق می‌کنیم بطوری که نه فریادرسی داشته باشند و نه نجات داده شوند!'];
ayat[3749]=[36,44,'إِلَّا رَحْمَةً مِّنَّا وَمَتَاعًا إِلَىٰ حِينٍ','مگر اینکه رحمت ما شامل حال آنان شود، و تا زمان معیّنی از این زندگی بهره گیرند!'];
ayat[3750]=[36,45,'وَإِذَا قِيلَ لَهُمُ اتَّقُوا مَا بَيْنَ أَيْدِيكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ','و هرگاه به آنها گفته شود: «از آنچه پیش رو و پشت سر شماست [= از عذابهای الهی‌] بترسید تا مشمول رحمت الهی شوید!» (اعتنا نمی‌کنند).'];
ayat[3751]=[36,46,'وَمَا تَأْتِيهِم مِّنْ آيَةٍ مِّنْ آيَاتِ رَبِّهِمْ إِلَّا كَانُوا عَنْهَا مُعْرِضِينَ','و هیچ آیه‌ای از آیات پروردگارشان برای آنها نمی‌آید مگر اینکه از آن روی‌گردان می شوند.'];
ayat[3752]=[36,47,'وَإِذَا قِيلَ لَهُمْ أَنفِقُوا مِمَّا رَزَقَكُمُ اللَّهُ قَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَنُطْعِمُ مَن لَّوْ يَشَاءُ اللَّهُ أَطْعَمَهُ إِنْ أَنتُمْ إِلَّا فِي ضَلَالٍ مُّبِينٍ','و هنگامی که به آنان گفته شود: «از آنچه خدا به شما روزی کرده انفاق کنید!»، کافران به مؤمنان می‌گویند: «آیا ما کسی را اطعام کنیم که اگر خدا می‌خواست او را اطعام می‌کرد؟! (پس خدا خواسته است او گرسنه باشد)، شما فقط در گمراهی آشکارید»!'];
ayat[3753]=[36,48,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِن كُنتُمْ صَادِقِينَ','آنها می‌گویند: «اگر راست می‌گویید، این وعده (قیامت) کی خواهد بود؟!'];
ayat[3754]=[36,49,'مَا يَنظُرُونَ إِلَّا صَيْحَةً وَاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ','(امّا) جز این انتظار نمی‌کشند که یک صیحه عظیم (آسمانی) آنها را فراگیرد، در حالی که مشغول جدال (در امور دنیا) هستند.'];
ayat[3755]=[36,50,'فَلَا يَسْتَطِيعُونَ تَوْصِيَةً وَلَا إِلَىٰ أَهْلِهِمْ يَرْجِعُونَ','(چنان غافلگیر می‌شوند که حتّی) نمی‌توانند وصیّتی کنند یا به سوی خانواده خود بازگردند!'];
ayat[3756]=[36,51,'وَنُفِخَ فِي الصُّورِ فَإِذَا هُم مِّنَ الْأَجْدَاثِ إِلَىٰ رَبِّهِمْ يَنسِلُونَ','(بار دیگر) در «صور» دمیده می‌شود، ناگهان آنها از قبرها، شتابان به سوی (دادگاه) پروردگارشان می‌روند!'];
ayat[3757]=[36,52,'قَالُوا يَا وَيْلَنَا مَن بَعَثَنَا مِن مَّرْقَدِنَا ۜ ۗ هَٰذَا مَا وَعَدَ الرَّحْمَٰنُ وَصَدَقَ الْمُرْسَلُونَ','می‌گویند: «ای وای بر ما! چه کسی ما را از خوابگاهمان برانگیخت؟! (آری) این همان است که خداوند رحمان وعده داده، و فرستادگان (او) راست گفتند!»'];
ayat[3758]=[36,53,'إِن كَانَتْ إِلَّا صَيْحَةً وَاحِدَةً فَإِذَا هُمْ جَمِيعٌ لَّدَيْنَا مُحْضَرُونَ','صیحه واحدی بیش نیست، (فریادی عظیم برمی‌خیزد) ناگهان همگی نزد ما احضار می‌شوند!'];
ayat[3759]=[36,54,'فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْئًا وَلَا تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ','(و به آنها گفته می‌شود:) امروز به هیچ کس ذرّه‌ای ستم نمی‌شود، و جز آنچه را عمل می‌کردید جزا داده نمی‌شوید!'];
ayat[3760]=[36,55,'إِنَّ أَصْحَابَ الْجَنَّةِ الْيَوْمَ فِي شُغُلٍ فَاكِهُونَ','بهشتیان، امروز به نعمتهای خدا مشغول و مسرورند.'];
ayat[3761]=[36,56,'هُمْ وَأَزْوَاجُهُمْ فِي ظِلَالٍ عَلَى الْأَرَائِكِ مُتَّكِئُونَ','آنها و همسرانشان در سایه‌های (قصرها و درختان بهشتی) بر تختها تکیه زده‌اند.'];
ayat[3762]=[36,57,'لَهُمْ فِيهَا فَاكِهَةٌ وَلَهُم مَّا يَدَّعُونَ','برای آنها در بهشت میوه بسیار لذّت‌بخشی است، و هر چه بخواهند در اختیار آنان خواهد بود!'];
ayat[3763]=[36,58,'سَلَامٌ قَوْلًا مِّن رَّبٍّ رَّحِيمٍ','بر آنها سلام (و درود الهی) است؛ این سخنی است از سوی پروردگاری مهربان!'];
ayat[3764]=[36,59,'وَامْتَازُوا الْيَوْمَ أَيُّهَا الْمُجْرِمُونَ','(و به آنها می‌گویند:) جدا شوید امروز ای گنهکاران!'];
ayat[3765]=[36,60,'۞ أَلَمْ أَعْهَدْ إِلَيْكُمْ يَا بَنِي آدَمَ أَن لَّا تَعْبُدُوا الشَّيْطَانَ ۖ إِنَّهُ لَكُمْ عَدُوٌّ مُّبِينٌ','آیا با شما عهد نکردم ای فرزندان آدم که شیطان را نپرستید، که او برای شما دشمن آشکاری است؟!'];
ayat[3766]=[36,61,'وَأَنِ اعْبُدُونِي ۚ هَٰذَا صِرَاطٌ مُّسْتَقِيمٌ','و اینکه مرا بپرستید که راه مستقیم این است؟!'];
ayat[3767]=[36,62,'وَلَقَدْ أَضَلَّ مِنكُمْ جِبِلًّا كَثِيرًا ۖ أَفَلَمْ تَكُونُوا تَعْقِلُونَ','او گروه زیادی از شما را گمراه کرد، آیا اندیشه نکردید؟!'];
ayat[3768]=[36,63,'هَٰذِهِ جَهَنَّمُ الَّتِي كُنتُمْ تُوعَدُونَ','این همان دوزخی است که به شما وعده داده می‌شد!'];
ayat[3769]=[36,64,'اصْلَوْهَا الْيَوْمَ بِمَا كُنتُمْ تَكْفُرُونَ','امروز وارد آن شوید و به خاطر کفری که داشتید به آتش آن بسوزید!'];
ayat[3770]=[36,65,'الْيَوْمَ نَخْتِمُ عَلَىٰ أَفْوَاهِهِمْ وَتُكَلِّمُنَا أَيْدِيهِمْ وَتَشْهَدُ أَرْجُلُهُم بِمَا كَانُوا يَكْسِبُونَ','امروز بر دهانشان مُهر می‌نهیم، و دستهایشان با ما سخن می‌گویند و پاهایشان کارهایی را که انجام می‌دادند شهادت می‌دهند!'];
ayat[3771]=[36,66,'وَلَوْ نَشَاءُ لَطَمَسْنَا عَلَىٰ أَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَأَنَّىٰ يُبْصِرُونَ','و اگر بخواهیم چشمانشان را محو کنیم؛ سپس برای عبور از راه، می‌خواهند بر یکدیگر پیشی بگیرند، امّا چگونه می‌توانند ببینند؟!'];
ayat[3772]=[36,67,'وَلَوْ نَشَاءُ لَمَسَخْنَاهُمْ عَلَىٰ مَكَانَتِهِمْ فَمَا اسْتَطَاعُوا مُضِيًّا وَلَا يَرْجِعُونَ','و اگر بخواهیم آنها را در جای خود مسخ می‌کنیم (و به مجسمه‌هایی بی‌روح مبدّل می‌سازیم) تا نتوانند راه خود را ادامه دهند یا به عقب برگردند!'];
ayat[3773]=[36,68,'وَمَن نُّعَمِّرْهُ نُنَكِّسْهُ فِي الْخَلْقِ ۖ أَفَلَا يَعْقِلُونَ','هر کس را طول عمر دهیم، در آفرینش واژگونه‌اش می‌کنیم (و به ناتوانی کودکی باز می‌گردانیم)؛ آیا اندیشه نمی‌کنند؟!'];
ayat[3774]=[36,69,'وَمَا عَلَّمْنَاهُ الشِّعْرَ وَمَا يَنبَغِي لَهُ ۚ إِنْ هُوَ إِلَّا ذِكْرٌ وَقُرْآنٌ مُّبِينٌ','ما هرگز شعر به او [= پیامبر] نیاموختیم، و شایسته او نیست (شاعر باشد)؛ این (کتاب آسمانی) فقط ذکر و قرآن مبین است!'];
ayat[3775]=[36,70,'لِّيُنذِرَ مَن كَانَ حَيًّا وَيَحِقَّ الْقَوْلُ عَلَى الْكَافِرِينَ','تا افرادی را که زنده‌اند بیم دهد (و بر کافران اتمام حجّت شود) و فرمان عذاب بر آنان مسلّم گردد!'];
ayat[3776]=[36,71,'أَوَلَمْ يَرَوْا أَنَّا خَلَقْنَا لَهُم مِّمَّا عَمِلَتْ أَيْدِينَا أَنْعَامًا فَهُمْ لَهَا مَالِكُونَ','آیا ندیدند که از آنچه با قدرت خود به عمل آورده‌ایم چهارپایانی برای آنان آفریدیم که آنان مالک آن هستند؟!'];
ayat[3777]=[36,72,'وَذَلَّلْنَاهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ','و آنها را رام ایشان ساختیم، هم مرکب آنان از آن است و هم از آن تغذیه می‌کنند؛'];
ayat[3778]=[36,73,'وَلَهُمْ فِيهَا مَنَافِعُ وَمَشَارِبُ ۖ أَفَلَا يَشْكُرُونَ','و برای آنان بهره‌های دیگری در آن (حیوانات) است و نوشیدنیهائی گوارا؛ آیا با این حال شکرگزاری نمی‌کنند؟!'];
ayat[3779]=[36,74,'وَاتَّخَذُوا مِن دُونِ اللَّهِ آلِهَةً لَّعَلَّهُمْ يُنصَرُونَ','آنان غیر از خدا معبودانی برای خویش برگزیدند به این امید که یاری شوند!'];
ayat[3780]=[36,75,'لَا يَسْتَطِيعُونَ نَصْرَهُمْ وَهُمْ لَهُمْ جُندٌ مُّحْضَرُونَ','ولی آنها قادر به یاری ایشان نیستند، و این (عبادت‌کنندگان در قیامت) لشکری برای آنها خواهند بود که در آتش دوزخ احضار می‌شوند!'];
ayat[3781]=[36,76,'فَلَا يَحْزُنكَ قَوْلُهُمْ ۘ إِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ','پس سخنانشان تو را غمگین نسازد، ما آنچه را پنهان می‌دارند و آنچه را آشکار می‌کنند می‌دانیم!'];
ayat[3782]=[36,77,'أَوَلَمْ يَرَ الْإِنسَانُ أَنَّا خَلَقْنَاهُ مِن نُّطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُّبِينٌ','آیا انسان نمی‌داند که ما او را از نطفه‌ای بی‌ارزش آفریدیم؟! و او (چنان صاحب قدرت و شعور و نطق شد که) به مخاصمه آشکار (با ما) برخاست!'];
ayat[3783]=[36,78,'وَضَرَبَ لَنَا مَثَلًا وَنَسِيَ خَلْقَهُ ۖ قَالَ مَن يُحْيِي الْعِظَامَ وَهِيَ رَمِيمٌ','و برای ما مثالی زد و آفرینش خود را فراموش کرد و گفت: «چه کسی این استخوانها را زنده می‌کند در حالی که پوسیده است؟!»'];
ayat[3784]=[36,79,'قُلْ يُحْيِيهَا الَّذِي أَنشَأَهَا أَوَّلَ مَرَّةٍ ۖ وَهُوَ بِكُلِّ خَلْقٍ عَلِيمٌ','بگو: «همان کسی آن را زنده می‌کند که نخستین بار آن را آفرید؛ و او به هر مخلوقی داناست!'];
ayat[3785]=[36,80,'الَّذِي جَعَلَ لَكُم مِّنَ الشَّجَرِ الْأَخْضَرِ نَارًا فَإِذَا أَنتُم مِّنْهُ تُوقِدُونَ','همان کسی که برای شما از درخت سبز، آتش آفرید و شما بوسیله آن، آتش می‌افروزید!»'];
ayat[3786]=[36,81,'أَوَلَيْسَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِقَادِرٍ عَلَىٰ أَن يَخْلُقَ مِثْلَهُم ۚ بَلَىٰ وَهُوَ الْخَلَّاقُ الْعَلِيمُ','آیا کسی که آسمانها و زمین را آفرید، نمی‌تواند همانند آنان [= انسانهای خاک شده‌] را بیافریند؟! آری (می‌تواند)، و او آفریدگار داناست!'];
ayat[3787]=[36,82,'إِنَّمَا أَمْرُهُ إِذَا أَرَادَ شَيْئًا أَن يَقُولَ لَهُ كُن فَيَكُونُ','فرمان او چنین است که هرگاه چیزی را اراده کند، تنها به آن می‌گوید: «موجود باش!»، آن نیز بی‌درنگ موجود می‌شود!'];
ayat[3788]=[36,83,'فَسُبْحَانَ الَّذِي بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَإِلَيْهِ تُرْجَعُونَ','پس منزّه است خداوندی که مالکیّت و حاکمیّت همه چیز در دست اوست؛ و شما را به سوی او بازمی‌گردانند!'];
ayat[3789]=[37,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالصَّافَّاتِ صَفًّا','سوگند به (فرشتگان) صف‌کشیده (و منظّم)'];
ayat[3790]=[37,2,'فَالزَّاجِرَاتِ زَجْرًا','و به نهی‌کنندگان و (بازدارندگان)'];
ayat[3791]=[37,3,'فَالتَّالِيَاتِ ذِكْرًا','و تلاوت‌کنندگان پیاپی آیات الهی...'];
ayat[3792]=[37,4,'إِنَّ إِلَٰهَكُمْ لَوَاحِدٌ','که معبود شما یگانه است؛'];
ayat[3793]=[37,5,'رَّبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِ','پروردگار آسمانها و زمین و آنچه میان آنهاست، و پروردگار مشرقها!'];
ayat[3794]=[37,6,'إِنَّا زَيَّنَّا السَّمَاءَ الدُّنْيَا بِزِينَةٍ الْكَوَاكِبِ','ما آسمان نزدیک [= پایین‌] را با ستارگان آراستیم،'];
ayat[3795]=[37,7,'وَحِفْظًا مِّن كُلِّ شَيْطَانٍ مَّارِدٍ','تا آن را از هر شیطان خبیثی حفظ کنیم!'];
ayat[3796]=[37,8,'لَّا يَسَّمَّعُونَ إِلَى الْمَلَإِ الْأَعْلَىٰ وَيُقْذَفُونَ مِن كُلِّ جَانِبٍ','آنها نمی‌توانند به (سخنان) فرشتگان عالم بالا گوش فرادهند، (و هرگاه چنین کنند) از هر سو هدف قرارمی‌گیرند!'];
ayat[3797]=[37,9,'دُحُورًا ۖ وَلَهُمْ عَذَابٌ وَاصِبٌ','آنها به شدّت به عقب رانده می‌شوند؛ و برای آنان مجازاتی دائم است!'];
ayat[3798]=[37,10,'إِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَأَتْبَعَهُ شِهَابٌ ثَاقِبٌ','مگر آنها که در لحظه‌ای کوتاه برای استراق سمع به آسمان نزدیک شوند، که «شهاب ثاقب» آنها را تعقیب می‌کند!'];
ayat[3799]=[37,11,'فَاسْتَفْتِهِمْ أَهُمْ أَشَدُّ خَلْقًا أَم مَّنْ خَلَقْنَا ۚ إِنَّا خَلَقْنَاهُم مِّن طِينٍ لَّازِبٍ','از آنان بپرس: «آیا آفرینش (و معاد) آنان سخت‌تر است یا آفرینش فرشتگان (و آسمانها و زمین)؟! ما آنان را از گل چسبنده‌ای آفریدیم!'];
ayat[3800]=[37,12,'بَلْ عَجِبْتَ وَيَسْخَرُونَ','تو از انکارشان تعجّب می‌کنی، ولی آنها مسخره می‌کنند!'];
ayat[3801]=[37,13,'وَإِذَا ذُكِّرُوا لَا يَذْكُرُونَ','و هنگامی که به آنان تذکّر داده شود، هرگز متذکّر نمی‌شوند!'];
ayat[3802]=[37,14,'وَإِذَا رَأَوْا آيَةً يَسْتَسْخِرُونَ','و هنگامی که معجزه‌ای را ببینند، دیگران را نیز به استهزا دعوت می‌کنند!'];
ayat[3803]=[37,15,'وَقَالُوا إِنْ هَٰذَا إِلَّا سِحْرٌ مُّبِينٌ','و می‌گویند: «این فقط سحری آشکار است!'];
ayat[3804]=[37,16,'أَإِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ','آیا هنگامی که ما مُردیم و به خاک و استخوان مبدّل شدیم، بار دیگر برانگیخته خواهیم شد؟!'];
ayat[3805]=[37,17,'أَوَآبَاؤُنَا الْأَوَّلُونَ','یا پدران نخستین ما (بازمی‌گردند)؟!»'];
ayat[3806]=[37,18,'قُلْ نَعَمْ وَأَنتُمْ دَاخِرُونَ','بگو: «آری، همه شما زنده می‌شوید در حالی که خوار و کوچک خواهید بود!'];
ayat[3807]=[37,19,'فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ فَإِذَا هُمْ يَنظُرُونَ','تنها یک صیحه عظیم واقع می‌شود، ناگهان همه (از قبرها برمی‌خیزند و) نگاه می‌کنند!'];
ayat[3808]=[37,20,'وَقَالُوا يَا وَيْلَنَا هَٰذَا يَوْمُ الدِّينِ','و می‌گویند: «ای وای بر ما، این روز جزاست!»'];
ayat[3809]=[37,21,'هَٰذَا يَوْمُ الْفَصْلِ الَّذِي كُنتُم بِهِ تُكَذِّبُونَ','(آری) این همان روز جدایی (حقّ از باطل) است که شما آن را تکذیب می‌کردید!'];
ayat[3810]=[37,22,'۞ احْشُرُوا الَّذِينَ ظَلَمُوا وَأَزْوَاجَهُمْ وَمَا كَانُوا يَعْبُدُونَ','(در این هنگام به فرشتگان دستور داده می‌شود:) ظالمان و همردیفانشان و آنچه را می‌پرستیدند...'];
ayat[3811]=[37,23,'مِن دُونِ اللَّهِ فَاهْدُوهُمْ إِلَىٰ صِرَاطِ الْجَحِيمِ','(آری آنچه را) جز خدا می‌پرستیدند جمع کنید و بسوی راه دوزخ هدایتشان کنید!'];
ayat[3812]=[37,24,'وَقِفُوهُمْ ۖ إِنَّهُم مَّسْئُولُونَ','آنها را نگهدارید که باید بازپرسی شوند!'];
ayat[3813]=[37,25,'مَا لَكُمْ لَا تَنَاصَرُونَ','شما را چه شده که از هم یاری نمی‌طلبید؟!'];
ayat[3814]=[37,26,'بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُونَ','ولی آنان در آن روز تسلیم قدرت خداوندند!'];
ayat[3815]=[37,27,'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَاءَلُونَ','(و در این حال) رو به یکدیگر کرده و از هم می‌پرسند...'];
ayat[3816]=[37,28,'قَالُوا إِنَّكُمْ كُنتُمْ تَأْتُونَنَا عَنِ الْيَمِينِ','گروهی (می‌گویند: «شما رهبران گمراهی بودید که به ظاهر) از طریق خیرخواهی و نیکی وارد شدید امّا جز فریب چیزی در کارتان نبود)!»'];
ayat[3817]=[37,29,'قَالُوا بَل لَّمْ تَكُونُوا مُؤْمِنِينَ','(آنها در جواب) می‌گویند: «شما خودتان اهل ایمان نبودید (تقصیر ما چیست)؟!'];
ayat[3818]=[37,30,'وَمَا كَانَ لَنَا عَلَيْكُم مِّن سُلْطَانٍ ۖ بَلْ كُنتُمْ قَوْمًا طَاغِينَ','ما هیچ گونه سلطه‌ای بر شما نداشتیم، بلکه شما خود قومی طغیانگر بودید!'];
ayat[3819]=[37,31,'فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَا ۖ إِنَّا لَذَائِقُونَ','اکنون فرمان پروردگارمان بر همه ما مسلّم شده، و همگی از عذاب او می‌چشیم!'];
ayat[3820]=[37,32,'فَأَغْوَيْنَاكُمْ إِنَّا كُنَّا غَاوِينَ','ما شما را گمراه کردیم، همان گونه که خود گمراه بودیم!'];
ayat[3821]=[37,33,'فَإِنَّهُمْ يَوْمَئِذٍ فِي الْعَذَابِ مُشْتَرِكُونَ','(آری) همه آنها [= پیشوایان و پیروان گمراه‌] در آن روز در عذاب الهی مشترکند!'];
ayat[3822]=[37,34,'إِنَّا كَذَٰلِكَ نَفْعَلُ بِالْمُجْرِمِينَ','ما این گونه با مجرمان رفتار می‌کنیم!'];
ayat[3823]=[37,35,'إِنَّهُمْ كَانُوا إِذَا قِيلَ لَهُمْ لَا إِلَٰهَ إِلَّا اللَّهُ يَسْتَكْبِرُونَ','چرا که وقتی به آنها گفته می‌شد: «معبودی جز خدا وجود ندارد»، تکبّر و سرکشی می‌کردند...'];
ayat[3824]=[37,36,'وَيَقُولُونَ أَئِنَّا لَتَارِكُو آلِهَتِنَا لِشَاعِرٍ مَّجْنُونٍ','و پیوسته می‌گفتند: «آیا ما معبودان خود را بخاطر شاعری دیوانه رها کنیم؟!»'];
ayat[3825]=[37,37,'بَلْ جَاءَ بِالْحَقِّ وَصَدَّقَ الْمُرْسَلِينَ','چنین نیست، او حقّ را آورده و پیامبران پیشین را تصدیق کرده است!'];
ayat[3826]=[37,38,'إِنَّكُمْ لَذَائِقُو الْعَذَابِ الْأَلِيمِ','اما شما (مستکبران کوردل) بطور مسلّم عذاب دردناک (الهی) را خواهید چشید!'];
ayat[3827]=[37,39,'وَمَا تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ','و جز به آنچه انجام می‌دادید کیفر داده نمی‌شوید،'];
ayat[3828]=[37,40,'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ','جز بندگان مخلص خدا (که از این کیفرها برکنارند)!'];
ayat[3829]=[37,41,'أُولَٰئِكَ لَهُمْ رِزْقٌ مَّعْلُومٌ','برای آنان [= بندگان مخلص‌] روزی معیّن و ویژه‌ای است،'];
ayat[3830]=[37,42,'فَوَاكِهُ ۖ وَهُم مُّكْرَمُونَ','میوه‌ها (ی گوناگون پر ارزش)، و آنها گرامی داشته می‌شوند...'];
ayat[3831]=[37,43,'فِي جَنَّاتِ النَّعِيمِ','در باغهای پر نعمت بهشت؛'];
ayat[3832]=[37,44,'عَلَىٰ سُرُرٍ مُّتَقَابِلِينَ','در حالی که بر تختها رو به روی یکدیگر تکیه زده‌اند،'];
ayat[3833]=[37,45,'يُطَافُ عَلَيْهِم بِكَأْسٍ مِّن مَّعِينٍ','و گرداگردشان قدحهای لبریز از شراب طهور را می‌گردانند؛'];
ayat[3834]=[37,46,'بَيْضَاءَ لَذَّةٍ لِّلشَّارِبِينَ','شرابی سفید و درخشنده، و لذّتبخش برای نوشندگان؛'];
ayat[3835]=[37,47,'لَا فِيهَا غَوْلٌ وَلَا هُمْ عَنْهَا يُنزَفُونَ','شرابی که نه در آن مایه تباهی عقل است و نه از آن مست می‌شوند!'];
ayat[3836]=[37,48,'وَعِندَهُمْ قَاصِرَاتُ الطَّرْفِ عِينٌ','و نزد آنها همسرانی زیبا چشم است که جز به شوهران خود عشق نمی‌ورزند.'];
ayat[3837]=[37,49,'كَأَنَّهُنَّ بَيْضٌ مَّكْنُونٌ','گویی از (لطافت و سفیدی) همچون تخم‌مرغهایی هستند که (در زیر بال و پر مرغ) پنهان مانده (و دست انسانی هرگز آن را لمس نکرده است)!'];
ayat[3838]=[37,50,'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَاءَلُونَ','(در حالی که آنها غرق گفتگو هستند) بعضی رو به بعضی دیگر کرده می‌پرسند...'];
ayat[3839]=[37,51,'قَالَ قَائِلٌ مِّنْهُمْ إِنِّي كَانَ لِي قَرِينٌ','کسی از آنها می‌گوید: «من همنشینی داشتم...'];
ayat[3840]=[37,52,'يَقُولُ أَإِنَّكَ لَمِنَ الْمُصَدِّقِينَ','که پیوسته می‌گفت: آیا (به راستی) تو این سخن را باور کرده‌ای...'];
ayat[3841]=[37,53,'أَإِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَدِينُونَ','که وقتی ما مُردیم و به خاک و استخوان مبدّل شدیم، (بار دیگر) زنده می‌شویم و جزا داده خواهیم شد؟!»'];
ayat[3842]=[37,54,'قَالَ هَلْ أَنتُم مُّطَّلِعُونَ','(سپس) می‌گوید: «آیا شما می‌توانید از او خبری بگیرید؟»'];
ayat[3843]=[37,55,'فَاطَّلَعَ فَرَآهُ فِي سَوَاءِ الْجَحِيمِ','اینجاست که نگاهی می‌کند، ناگهان او را در میان دوزخ می‌بیند.'];
ayat[3844]=[37,56,'قَالَ تَاللَّهِ إِن كِدتَّ لَتُرْدِينِ','می‌گوید: «به خدا سوگند نزدیک بود مرا (نیز) به هلاکت بکشانی!'];
ayat[3845]=[37,57,'وَلَوْلَا نِعْمَةُ رَبِّي لَكُنتُ مِنَ الْمُحْضَرِينَ','و اگر نعمت پروردگارم نبود، من نیز از احضارشدگان (در دوزخ) بودم!'];
ayat[3846]=[37,58,'أَفَمَا نَحْنُ بِمَيِّتِينَ','(سپس به یاران خود می‌گوید: ای دوستان!) آیا ما هرگز نمی‌میریم (و در بهشت جاودانه خواهیم بود)،'];
ayat[3847]=[37,59,'إِلَّا مَوْتَتَنَا الْأُولَىٰ وَمَا نَحْنُ بِمُعَذَّبِينَ','و جز همان مرگ اول، مرگی به سراغ ما نخواهد آمد، و ما هرگز عذاب نخواهیم شد.'];
ayat[3848]=[37,60,'إِنَّ هَٰذَا لَهُوَ الْفَوْزُ الْعَظِيمُ','راستی این همان پیروزی بزرگ است!'];
ayat[3849]=[37,61,'لِمِثْلِ هَٰذَا فَلْيَعْمَلِ الْعَامِلُونَ','آری، برای مثل این، باید عمل‌کنندگان عمل کنند!'];
ayat[3850]=[37,62,'أَذَٰلِكَ خَيْرٌ نُّزُلًا أَمْ شَجَرَةُ الزَّقُّومِ','آیا این (نعمتهای جاویدان بهشتی) بهتر است یا درخت (نفرت‌انگیز) زقّوم؟!'];
ayat[3851]=[37,63,'إِنَّا جَعَلْنَاهَا فِتْنَةً لِّلظَّالِمِينَ','ما آن را مایه درد و رنج ظالمان قرار دادیم!'];
ayat[3852]=[37,64,'إِنَّهَا شَجَرَةٌ تَخْرُجُ فِي أَصْلِ الْجَحِيمِ','آن درختی است که از قعر جهنّم می‌روید!'];
ayat[3853]=[37,65,'طَلْعُهَا كَأَنَّهُ رُءُوسُ الشَّيَاطِينِ','شکوفه آن مانند سرهای شیاطین است!'];
ayat[3854]=[37,66,'فَإِنَّهُمْ لَآكِلُونَ مِنْهَا فَمَالِئُونَ مِنْهَا الْبُطُونَ','آنها [= مجرمان‌] از آن می‌خورند و شکمها را از آن پر می‌کنند!'];
ayat[3855]=[37,67,'ثُمَّ إِنَّ لَهُمْ عَلَيْهَا لَشَوْبًا مِّنْ حَمِيمٍ','سپس روی آن آب داغ متعفّنی می‌نوشند!'];
ayat[3856]=[37,68,'ثُمَّ إِنَّ مَرْجِعَهُمْ لَإِلَى الْجَحِيمِ','سپس بازگشت آنها به سوی جهنّم است!'];
ayat[3857]=[37,69,'إِنَّهُمْ أَلْفَوْا آبَاءَهُمْ ضَالِّينَ','چرا که آنها پدران خود را گمراه یافتند،'];
ayat[3858]=[37,70,'فَهُمْ عَلَىٰ آثَارِهِمْ يُهْرَعُونَ','با این حال به سرعت بدنبال آنان کشانده می‌شوند!'];
ayat[3859]=[37,71,'وَلَقَدْ ضَلَّ قَبْلَهُمْ أَكْثَرُ الْأَوَّلِينَ','و قبل از آنها بیشتر پیشینیان (نیز) گمراه شدند!'];
ayat[3860]=[37,72,'وَلَقَدْ أَرْسَلْنَا فِيهِم مُّنذِرِينَ','ما در میان آنها انذارکنندگانی فرستادیم،'];
ayat[3861]=[37,73,'فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنذَرِينَ','ولی بنگر عاقبت انذارشوندگان چگونه بود!'];
ayat[3862]=[37,74,'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ','مگر بندگان مخلص خدا!'];
ayat[3863]=[37,75,'وَلَقَدْ نَادَانَا نُوحٌ فَلَنِعْمَ الْمُجِيبُونَ','و نوح، ما را خواند (و ما دعای او را اجابت کردیم)؛ و چه خوب اجابت کننده‌ای هستیم!'];
ayat[3864]=[37,76,'وَنَجَّيْنَاهُ وَأَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ','و او و خاندانش را از اندوه بزرگ رهایی بخشیدیم،'];
ayat[3865]=[37,77,'وَجَعَلْنَا ذُرِّيَّتَهُ هُمُ الْبَاقِينَ','و فرزندانش را همان بازماندگان (روی زمین) قرار دادیم،'];
ayat[3866]=[37,78,'وَتَرَكْنَا عَلَيْهِ فِي الْآخِرِينَ','و نام نیک او را در میان امّتهای بعد باقی نهادیم.'];
ayat[3867]=[37,79,'سَلَامٌ عَلَىٰ نُوحٍ فِي الْعَالَمِينَ','سلام بر نوح در میان جهانیان باد!'];
ayat[3868]=[37,80,'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','ما این گونه نیکوکاران را پاداش می‌دهیم!'];
ayat[3869]=[37,81,'إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ','چرا که او از بندگان باایمان ما بود!'];
ayat[3870]=[37,82,'ثُمَّ أَغْرَقْنَا الْآخَرِينَ','سپس دیگران [= دشمنان او] را غرق کردیم!'];
ayat[3871]=[37,83,'۞ وَإِنَّ مِن شِيعَتِهِ لَإِبْرَاهِيمَ','و از پیروان او ابراهیم بود؛'];
ayat[3872]=[37,84,'إِذْ جَاءَ رَبَّهُ بِقَلْبٍ سَلِيمٍ','(به خاطر بیاور) هنگامی را که با قلب سلیم به پیشگاه پروردگارش آمد؛'];
ayat[3873]=[37,85,'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِ مَاذَا تَعْبُدُونَ','هنگامی که به پدر و قومش گفت: «اینها چیست که می‌پرستید؟!'];
ayat[3874]=[37,86,'أَئِفْكًا آلِهَةً دُونَ اللَّهِ تُرِيدُونَ','آیا غیر از خدا به سراغ این معبودان دروغین می‌روید؟!'];
ayat[3875]=[37,87,'فَمَا ظَنُّكُم بِرَبِّ الْعَالَمِينَ','شما درباره پروردگار عالمیان چه گمان می‌برید؟!'];
ayat[3876]=[37,88,'فَنَظَرَ نَظْرَةً فِي النُّجُومِ','(سپس) نگاهی به ستارگان افکند...'];
ayat[3877]=[37,89,'فَقَالَ إِنِّي سَقِيمٌ','و گفت: «من بیمارم (و با شما به مراسم جشن نمی‌آیم)!»'];
ayat[3878]=[37,90,'فَتَوَلَّوْا عَنْهُ مُدْبِرِينَ','آنها از او روی برتافته و به او پشت کردند (و بسرعت دور شدند.)'];
ayat[3879]=[37,91,'فَرَاغَ إِلَىٰ آلِهَتِهِمْ فَقَالَ أَلَا تَأْكُلُونَ','(او وارد بتخانه شد) مخفیانه نگاهی به معبودانشان کرد و از روی تمسخر گفت: «چرا (از این غذاها) نمی‌خورید؟!'];
ayat[3880]=[37,92,'مَا لَكُمْ لَا تَنطِقُونَ','(اصلاً) چرا سخن نمی‌گویید؟!»'];
ayat[3881]=[37,93,'فَرَاغَ عَلَيْهِمْ ضَرْبًا بِالْيَمِينِ','سپس بسوی آنها رفت و ضربه‌ای محکم با دست راست بر پیکر آنها فرود آورد (و جز بت بزرگ، همه را درهم شکست).'];
ayat[3882]=[37,94,'فَأَقْبَلُوا إِلَيْهِ يَزِفُّونَ','آنها با سرعت به او روی آوردند.'];
ayat[3883]=[37,95,'قَالَ أَتَعْبُدُونَ مَا تَنْحِتُونَ','گفت: «آیا چیزی را می‌پرستید که با دست خود می‌تراشید؟!'];
ayat[3884]=[37,96,'وَاللَّهُ خَلَقَكُمْ وَمَا تَعْمَلُونَ','با اینکه خداوند هم شما را آفریده و هم بتهایی که می‌سازید!»'];
ayat[3885]=[37,97,'قَالُوا ابْنُوا لَهُ بُنْيَانًا فَأَلْقُوهُ فِي الْجَحِيمِ','(بت‌پرستان) گفتند: «بنای مرتفعی برای او بسازید و او را در جهنّمی از آتش بیفکنید!»'];
ayat[3886]=[37,98,'فَأَرَادُوا بِهِ كَيْدًا فَجَعَلْنَاهُمُ الْأَسْفَلِينَ','آنها طرحی برای نابودی ابراهیم ریخته بودند، ولی ما آنان را پست و مغلوب ساختیم!'];
ayat[3887]=[37,99,'وَقَالَ إِنِّي ذَاهِبٌ إِلَىٰ رَبِّي سَيَهْدِينِ','(او از این مهلکه بسلامت بیرون آمد) و گفت: «من به سوی پروردگارم می‌روم، او مرا هدایت خواهد کرد!'];
ayat[3888]=[37,100,'رَبِّ هَبْ لِي مِنَ الصَّالِحِينَ','پروردگارا! به من از صالحان [= فرزندان صالح‌] ببخش!»'];
ayat[3889]=[37,101,'فَبَشَّرْنَاهُ بِغُلَامٍ حَلِيمٍ','ما او [= ابراهیم‌] را به نوجوانی بردبار و صبور بشارت دادیم!'];
ayat[3890]=[37,102,'فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قَالَ يَا بُنَيَّ إِنِّي أَرَىٰ فِي الْمَنَامِ أَنِّي أَذْبَحُكَ فَانظُرْ مَاذَا تَرَىٰ ۚ قَالَ يَا أَبَتِ افْعَلْ مَا تُؤْمَرُ ۖ سَتَجِدُنِي إِن شَاءَ اللَّهُ مِنَ الصَّابِرِينَ','هنگامی که با او به مقام سعی و کوشش رسید، گفت: «پسرم! من در خواب دیدم که تو را ذبح می‌کنم، نظر تو چیست؟» گفت «پدرم! هر چه دستور داری اجرا کن، به خواست خدا مرا از صابران خواهی یافت!»'];
ayat[3891]=[37,103,'فَلَمَّا أَسْلَمَا وَتَلَّهُ لِلْجَبِينِ','هنگامی که هر دو تسلیم شدند و ابراهیم جبین او را بر خاک نهاد...'];
ayat[3892]=[37,104,'وَنَادَيْنَاهُ أَن يَا إِبْرَاهِيمُ','او را ندا دادیم که: «ای ابراهیم!'];
ayat[3893]=[37,105,'قَدْ صَدَّقْتَ الرُّؤْيَا ۚ إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','آن رؤیا را تحقق بخشیدی (و به مأموریت خود عمل کردی)!» ما این گونه، نیکوکاران را جزا می‌دهیم!'];
ayat[3894]=[37,106,'إِنَّ هَٰذَا لَهُوَ الْبَلَاءُ الْمُبِينُ','این مسلّماً همان امتحان آشکار است!'];
ayat[3895]=[37,107,'وَفَدَيْنَاهُ بِذِبْحٍ عَظِيمٍ','ما ذبح عظیمی را فدای او کردیم،'];
ayat[3896]=[37,108,'وَتَرَكْنَا عَلَيْهِ فِي الْآخِرِينَ','و نام نیک او را در امّتهای بعد باقی نهادیم!'];
ayat[3897]=[37,109,'سَلَامٌ عَلَىٰ إِبْرَاهِيمَ','سلام بر ابراهیم!'];
ayat[3898]=[37,110,'كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','این گونه نیکوکاران را پاداش می‌دهیم!'];
ayat[3899]=[37,111,'إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ','او از بندگان باایمان ما است!'];
ayat[3900]=[37,112,'وَبَشَّرْنَاهُ بِإِسْحَاقَ نَبِيًّا مِّنَ الصَّالِحِينَ','ما او را به اسحاق -پیامبری از شایستگان- بشارت دادیم!'];
ayat[3901]=[37,113,'وَبَارَكْنَا عَلَيْهِ وَعَلَىٰ إِسْحَاقَ ۚ وَمِن ذُرِّيَّتِهِمَا مُحْسِنٌ وَظَالِمٌ لِّنَفْسِهِ مُبِينٌ','ما به او و اسحاق برکت دادیم؛ و از دودمان آن دو، افرادی بودند نیکوکار و افرادی آشکارا به خود ستم کردند!'];
ayat[3902]=[37,114,'وَلَقَدْ مَنَنَّا عَلَىٰ مُوسَىٰ وَهَارُونَ','ما به موسی و هارون نعمت بخشیدیم!'];
ayat[3903]=[37,115,'وَنَجَّيْنَاهُمَا وَقَوْمَهُمَا مِنَ الْكَرْبِ الْعَظِيمِ','و آن دو و قومشان را از اندوه بزرگ نجات دادیم!'];
ayat[3904]=[37,116,'وَنَصَرْنَاهُمْ فَكَانُوا هُمُ الْغَالِبِينَ','و آنها را یاری کردیم تا بر دشمنان خود پیروز شدند!'];
ayat[3905]=[37,117,'وَآتَيْنَاهُمَا الْكِتَابَ الْمُسْتَبِينَ','ما به آن دو، کتاب روشنگر دادیم،'];
ayat[3906]=[37,118,'وَهَدَيْنَاهُمَا الصِّرَاطَ الْمُسْتَقِيمَ','و آن دو را به راه راست هدایت نمودیم!'];
ayat[3907]=[37,119,'وَتَرَكْنَا عَلَيْهِمَا فِي الْآخِرِينَ','و نام نیکشان را در اقوام بعد باقی گذاردیم!'];
ayat[3908]=[37,120,'سَلَامٌ عَلَىٰ مُوسَىٰ وَهَارُونَ','سلام بر موسی و هارون!'];
ayat[3909]=[37,121,'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','ما این گونه نیکوکاران را پاداش می‌دهیم!'];
ayat[3910]=[37,122,'إِنَّهُمَا مِنْ عِبَادِنَا الْمُؤْمِنِينَ','آن دو از بندگان مؤمن ما بودند!'];
ayat[3911]=[37,123,'وَإِنَّ إِلْيَاسَ لَمِنَ الْمُرْسَلِينَ','و الیاس از رسولان (ما) بود!'];
ayat[3912]=[37,124,'إِذْ قَالَ لِقَوْمِهِ أَلَا تَتَّقُونَ','به خاطر بیاور هنگامی را که به قومش گفت: «آیا تقوا پیشه نمی‌کنید؟!'];
ayat[3913]=[37,125,'أَتَدْعُونَ بَعْلًا وَتَذَرُونَ أَحْسَنَ الْخَالِقِينَ','آیا بت «بعل» را می‌خوانید و بهترین آفریدگارها را رها می‌سازید؟!'];
ayat[3914]=[37,126,'اللَّهَ رَبَّكُمْ وَرَبَّ آبَائِكُمُ الْأَوَّلِينَ','خدایی که پروردگار شما و پروردگار نیاکان شماست!»'];
ayat[3915]=[37,127,'فَكَذَّبُوهُ فَإِنَّهُمْ لَمُحْضَرُونَ','امّا آنها او را تکذیب کردند؛ ولی به یقین همگی (در دادگاه عدل الهی) احضار می‌شوند!'];
ayat[3916]=[37,128,'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ','مگر بندگان مخلص خدا!'];
ayat[3917]=[37,129,'وَتَرَكْنَا عَلَيْهِ فِي الْآخِرِينَ','ما نام نیک او را در میان امّتهای بعد باقی گذاردیم!'];
ayat[3918]=[37,130,'سَلَامٌ عَلَىٰ إِلْ يَاسِينَ','سلام بر الیاسین!'];
ayat[3919]=[37,131,'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','ما این گونه نیکوکاران را پاداش می‌دهیم!'];
ayat[3920]=[37,132,'إِنَّهُ مِنْ عِبَادِنَا الْمُؤْمِنِينَ','او از بندگان مؤمن ما است!'];
ayat[3921]=[37,133,'وَإِنَّ لُوطًا لَّمِنَ الْمُرْسَلِينَ','و لوط از رسولان (ما) است!'];
ayat[3922]=[37,134,'إِذْ نَجَّيْنَاهُ وَأَهْلَهُ أَجْمَعِينَ','و به خاطر بیاور زمانی را که او و خاندانش را همگی نجات دادیم،'];
ayat[3923]=[37,135,'إِلَّا عَجُوزًا فِي الْغَابِرِينَ','مگر پیرزنی که از بازماندگان بود (و به سرنوشت آنان گرفتار شد)!'];
ayat[3924]=[37,136,'ثُمَّ دَمَّرْنَا الْآخَرِينَ','سپس بقیه را نابود کردیم!'];
ayat[3925]=[37,137,'وَإِنَّكُمْ لَتَمُرُّونَ عَلَيْهِم مُّصْبِحِينَ','و شما پیوسته صبحگاهان از کنار (ویرانه‌های شهرهای) آنها می‌گذرید...'];
ayat[3926]=[37,138,'وَبِاللَّيْلِ ۗ أَفَلَا تَعْقِلُونَ','و (همچنین) شبانگاه؛ آیا نمی‌اندیشید؟!'];
ayat[3927]=[37,139,'وَإِنَّ يُونُسَ لَمِنَ الْمُرْسَلِينَ','و یونس از رسولان (ما) است!'];
ayat[3928]=[37,140,'إِذْ أَبَقَ إِلَى الْفُلْكِ الْمَشْحُونِ','به خاطر بیاور زمانی را که به سوی کشتی پر (از جمعیّت و بار) فرار کرد.'];
ayat[3929]=[37,141,'فَسَاهَمَ فَكَانَ مِنَ الْمُدْحَضِينَ','و با آنها قرعه افکند، (و قرعه به نام او افتاد و) مغلوب شد!'];
ayat[3930]=[37,142,'فَالْتَقَمَهُ الْحُوتُ وَهُوَ مُلِيمٌ','(او را به دریا افکندند) و ماهی عظیمی او را بلعید، در حالی که مستحقّ سرزنش بود!'];
ayat[3931]=[37,143,'فَلَوْلَا أَنَّهُ كَانَ مِنَ الْمُسَبِّحِينَ','و اگر او از تسبیح‌کنندگان نبود...'];
ayat[3932]=[37,144,'لَلَبِثَ فِي بَطْنِهِ إِلَىٰ يَوْمِ يُبْعَثُونَ','تا روز قیامت در شکم ماهی می‌ماند!'];
ayat[3933]=[37,145,'۞ فَنَبَذْنَاهُ بِالْعَرَاءِ وَهُوَ سَقِيمٌ','(به هر حال ما او را رهایی بخشیدیم و) او را در یک سرزمین خشک خالی از گیاه افکندیم در حالی که بیمار بود!'];
ayat[3934]=[37,146,'وَأَنبَتْنَا عَلَيْهِ شَجَرَةً مِّن يَقْطِينٍ','و بوته کدوئی بر او رویاندیم (تا در سایه برگهای پهن و مرطوبش آرامش یابد)!'];
ayat[3935]=[37,147,'وَأَرْسَلْنَاهُ إِلَىٰ مِائَةِ أَلْفٍ أَوْ يَزِيدُونَ','و او را به سوی جمعیّت یکصد هزار نفری -یا بیشتر- فرستادیم!'];
ayat[3936]=[37,148,'فَآمَنُوا فَمَتَّعْنَاهُمْ إِلَىٰ حِينٍ','آنها ایمان آوردند، از این رو تا مدّت معلومی آنان را از مواهب زندگی بهره‌مند ساختیم!'];
ayat[3937]=[37,149,'فَاسْتَفْتِهِمْ أَلِرَبِّكَ الْبَنَاتُ وَلَهُمُ الْبَنُونَ','از آنان بپرس: آیا پروردگارت دخترانی دارد و پسران از آن آنهاست؟!'];
ayat[3938]=[37,150,'أَمْ خَلَقْنَا الْمَلَائِكَةَ إِنَاثًا وَهُمْ شَاهِدُونَ','آیا ما فرشتگان را مؤنث آفریدیم و آنها ناظر بودند؟!'];
ayat[3939]=[37,151,'أَلَا إِنَّهُم مِّنْ إِفْكِهِمْ لَيَقُولُونَ','بدانید آنها با این تهمت بزرگشان می‌گویند:'];
ayat[3940]=[37,152,'وَلَدَ اللَّهُ وَإِنَّهُمْ لَكَاذِبُونَ','«خداوند فرزند آورده!» ولی آنها به یقین دروغ می‌گویند!'];
ayat[3941]=[37,153,'أَصْطَفَى الْبَنَاتِ عَلَى الْبَنِينَ','آیا دختران را بر پسران ترجیح داده است؟!'];
ayat[3942]=[37,154,'مَا لَكُمْ كَيْفَ تَحْكُمُونَ','شما را چه شده است؟! چگونه حکم می‌کنید؟! (هیچ می‌فهمید چه می‌گویید؟!)'];
ayat[3943]=[37,155,'أَفَلَا تَذَكَّرُونَ','آیا متذکّر نمی‌شوید؟!'];
ayat[3944]=[37,156,'أَمْ لَكُمْ سُلْطَانٌ مُّبِينٌ','یا شما دلیل روشنی در این باره دارید؟'];
ayat[3945]=[37,157,'فَأْتُوا بِكِتَابِكُمْ إِن كُنتُمْ صَادِقِينَ','کتابتان را بیاورید اگر راست می‌گویید!'];
ayat[3946]=[37,158,'وَجَعَلُوا بَيْنَهُ وَبَيْنَ الْجِنَّةِ نَسَبًا ۚ وَلَقَدْ عَلِمَتِ الْجِنَّةُ إِنَّهُمْ لَمُحْضَرُونَ','آنها [= مشرکان‌] میان او [= خداوند] و جنّ، (خویشاوندی و) نسبتی قائل شدند؛ در حالی که جنّیان بخوبی می‌دانند که این بت‌پرستان در دادگاه الهی احضار می‌شوند!'];
ayat[3947]=[37,159,'سُبْحَانَ اللَّهِ عَمَّا يَصِفُونَ','منزّه است خداوند از آنچه توصیف می‌کنند،'];
ayat[3948]=[37,160,'إِلَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ','مگر بندگان مخلص خدا!'];
ayat[3949]=[37,161,'فَإِنَّكُمْ وَمَا تَعْبُدُونَ','شما و آنچه را پرستش می‌کنید،'];
ayat[3950]=[37,162,'مَا أَنتُمْ عَلَيْهِ بِفَاتِنِينَ','هرگز نمی‌توانید کسی را (با آن) فریب دهید،'];
ayat[3951]=[37,163,'إِلَّا مَنْ هُوَ صَالِ الْجَحِيمِ','مگر آنها که در آتش دوزخ وارد می‌شوند!'];
ayat[3952]=[37,164,'وَمَا مِنَّا إِلَّا لَهُ مَقَامٌ مَّعْلُومٌ','و هیچ یک از ما نیست جز آنکه مقام معلومی دارد؛'];
ayat[3953]=[37,165,'وَإِنَّا لَنَحْنُ الصَّافُّونَ','و ما همگی (برای اطاعت فرمان خداوند) به صف ایستاده‌ایم؛'];
ayat[3954]=[37,166,'وَإِنَّا لَنَحْنُ الْمُسَبِّحُونَ','و ما همه تسبیح‌گوی او هستیم!'];
ayat[3955]=[37,167,'وَإِن كَانُوا لَيَقُولُونَ','آنها پیوسته می‌گفتند:'];
ayat[3956]=[37,168,'لَوْ أَنَّ عِندَنَا ذِكْرًا مِّنَ الْأَوَّلِينَ','«اگر یکی از کتابهای پیشینیان نزد ما بود،'];
ayat[3957]=[37,169,'لَكُنَّا عِبَادَ اللَّهِ الْمُخْلَصِينَ','به یقین، ما بندگان مخلص خدا بودیم!»'];
ayat[3958]=[37,170,'فَكَفَرُوا بِهِ ۖ فَسَوْفَ يَعْلَمُونَ','(امّا هنگامی که این کتاب بزرگ آسمانی بر آنها نازل شد،) به آن کافر شدند؛ ولی بزودی (نتیجه کار خود را) خواهند دانست!'];
ayat[3959]=[37,171,'وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَلِينَ','وعده قطعی ما برای بندگان فرستاده ما از پیش مسلّم شده...'];
ayat[3960]=[37,172,'إِنَّهُمْ لَهُمُ الْمَنصُورُونَ','که آنان یاری شدگانند،'];
ayat[3961]=[37,173,'وَإِنَّ جُندَنَا لَهُمُ الْغَالِبُونَ','و لشکر ما پیروزند!'];
ayat[3962]=[37,174,'فَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍ','از آنها [= کافران‌] روی بگردان تا زمان معیّنی (که فرمان جهاد فرارسد)!'];
ayat[3963]=[37,175,'وَأَبْصِرْهُمْ فَسَوْفَ يُبْصِرُونَ','و وضع آنها را بنگر (چه بی‌محتواست) اما بزودی (نتیجه اعمال خود را) می‌بینند!'];
ayat[3964]=[37,176,'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ','آیا آنها برای عذاب ما شتاب می‌کنند؟!'];
ayat[3965]=[37,177,'فَإِذَا نَزَلَ بِسَاحَتِهِمْ فَسَاءَ صَبَاحُ الْمُنذَرِينَ','امّا هنگامی که عذاب ما در آستانه خانه‌هایشان فرود آید، انذارشدگان صبحگاه بدی خواهند داشت!'];
ayat[3966]=[37,178,'وَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍ','از آنان روی بگردان تا زمان معیّنی!'];
ayat[3967]=[37,179,'وَأَبْصِرْ فَسَوْفَ يُبْصِرُونَ','و وضع کارشان را ببین؛ آنها نیز به زودی (نتیجه اعمال خود را) می‌بینند!'];
ayat[3968]=[37,180,'سُبْحَانَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُونَ','منزّه است پروردگار تو، پروردگار عزّت (و قدرت) از آنچه آنان توصیف می‌کنند.'];
ayat[3969]=[37,181,'وَسَلَامٌ عَلَى الْمُرْسَلِينَ','و سلام بر رسولان!'];
ayat[3970]=[37,182,'وَالْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ','حمد و ستایش مخصوص خداوندی است که پروردگار جهانیان است!'];
ayat[3971]=[38,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ ص ۚ وَالْقُرْآنِ ذِي الذِّكْرِ','ص، سوگند به قرآنی که دارای ذکر است (که این کتاب، معجزه الهی است).'];
ayat[3972]=[38,2,'بَلِ الَّذِينَ كَفَرُوا فِي عِزَّةٍ وَشِقَاقٍ','ولی کافران گرفتار غرور اختلافند!'];
ayat[3973]=[38,3,'كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّن قَرْنٍ فَنَادَوا وَّلَاتَ حِينَ مَنَاصٍ','چه بسیار اقوامی را که پیش از آنها هلاک کردیم؛ و به هنگام نزول عذاب فریاد می‌زدند (و کمک می‌خواستند) ولی وقت نجات گذشته بود!'];
ayat[3974]=[38,4,'وَعَجِبُوا أَن جَاءَهُم مُّنذِرٌ مِّنْهُمْ ۖ وَقَالَ الْكَافِرُونَ هَٰذَا سَاحِرٌ كَذَّابٌ','آنها تعجّب کردند که پیامبر بیم‌دهنده‌ای از میان آنان به سویشان آمده؛ و کافران گفتند: این ساحر بسیار دروغگویی است!'];
ayat[3975]=[38,5,'أَجَعَلَ الْآلِهَةَ إِلَٰهًا وَاحِدًا ۖ إِنَّ هَٰذَا لَشَيْءٌ عُجَابٌ','آیا او بجای اینهمه خدایان، خدای واحدی قرار داده؟! این براستی چیز عجیبی است!»'];
ayat[3976]=[38,6,'وَانطَلَقَ الْمَلَأُ مِنْهُمْ أَنِ امْشُوا وَاصْبِرُوا عَلَىٰ آلِهَتِكُمْ ۖ إِنَّ هَٰذَا لَشَيْءٌ يُرَادُ','سرکردگان آنها بیرون آمدند و گفتند: «بروید و خدایانتان را محکم بچسبید، این چیزی است که خواسته‌اند (شما را گمراه کنند)!'];
ayat[3977]=[38,7,'مَا سَمِعْنَا بِهَٰذَا فِي الْمِلَّةِ الْآخِرَةِ إِنْ هَٰذَا إِلَّا اخْتِلَاقٌ','ما هرگز چنین چیزی در آیین دیگری نشنیده‌ایم؛ این تنها یک آئین ساختگی است!'];
ayat[3978]=[38,8,'أَأُنزِلَ عَلَيْهِ الذِّكْرُ مِن بَيْنِنَا ۚ بَلْ هُمْ فِي شَكٍّ مِّن ذِكْرِي ۖ بَل لَّمَّا يَذُوقُوا عَذَابِ','آیا از میان همه ما، قرآن تنها بر او [= محمّد] نازل شده؟!» آنها در حقیقت در اصل وحی من تردید دارند، بلکه آنان هنوز عذاب الهی را نچشیده‌اند (که این چنین گستاخانه سخن می‌گویند)!'];
ayat[3979]=[38,9,'أَمْ عِندَهُمْ خَزَائِنُ رَحْمَةِ رَبِّكَ الْعَزِيزِ الْوَهَّابِ','مگر خزاین رحمت پروردگار توانا و بخشنده‌ات نزد آنهاست (تا به هر کس میل دارند بدهند)؟!'];
ayat[3980]=[38,10,'أَمْ لَهُم مُّلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ فَلْيَرْتَقُوا فِي الْأَسْبَابِ','یا اینکه مالکیّت و حاکمیّت آسمانها و زمین و آنچه میان این دو است از آن آنهاست؟! (اگر چنین است) با هر وسیله ممکن به آسمانها بروند (و جلو نزول وحی را بر قلب پاک محمّد بگیرند)!'];
ayat[3981]=[38,11,'جُندٌ مَّا هُنَالِكَ مَهْزُومٌ مِّنَ الْأَحْزَابِ','(آری) اینها لشکر کوچک شکست‌خورده‌ای از احزابند!'];
ayat[3982]=[38,12,'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَفِرْعَوْنُ ذُو الْأَوْتَادِ','پیش از آنان قوم نوح و عاد و فرعون صاحب قدرت (پیامبران ما را) تکذیب کردند!'];
ayat[3983]=[38,13,'وَثَمُودُ وَقَوْمُ لُوطٍ وَأَصْحَابُ الْأَيْكَةِ ۚ أُولَٰئِكَ الْأَحْزَابُ','و (نیز) قوم ثمود و لوط و اصحاب الأیکه [= قوم شعیب‌]، اینها احزابی بودند (که به تکذیب پیامبران برخاستند)!'];
ayat[3984]=[38,14,'إِن كُلٌّ إِلَّا كَذَّبَ الرُّسُلَ فَحَقَّ عِقَابِ','هر یک (از این گروه‌ها) رسولان را تکذیب کردند، و عذاب الهی درباره آنان تحقق یافت!'];
ayat[3985]=[38,15,'وَمَا يَنظُرُ هَٰؤُلَاءِ إِلَّا صَيْحَةً وَاحِدَةً مَّا لَهَا مِن فَوَاقٍ','اینها (با این اعمالشان) جز یک صیحه آسمانی را انتظار نمی‌کشند که هیچ مهلت و بازگشتی برای آن وجود ندارد (و همگی را نابود می‌سازد)!'];
ayat[3986]=[38,16,'وَقَالُوا رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبْلَ يَوْمِ الْحِسَابِ','آنها (از روی خیره‌سری) گفتند: «پروردگارا! بهره ما را از عذاب هر چه زودتر قبل از روز حساب به ما ده!»'];
ayat[3987]=[38,17,'اصْبِرْ عَلَىٰ مَا يَقُولُونَ وَاذْكُرْ عَبْدَنَا دَاوُودَ ذَا الْأَيْدِ ۖ إِنَّهُ أَوَّابٌ','در برابر آنچه می‌گویند شکیبا باش، و به خاطر بیاور بنده ما داوود صاحب قدرت را، که او بسیار توبه‌کننده بود!'];
ayat[3988]=[38,18,'إِنَّا سَخَّرْنَا الْجِبَالَ مَعَهُ يُسَبِّحْنَ بِالْعَشِيِّ وَالْإِشْرَاقِ','ما کوه‌ها را مسخّر او ساختیم که هر شامگاه و صبحگاه با او تسبیح می‌گفتند!'];
ayat[3989]=[38,19,'وَالطَّيْرَ مَحْشُورَةً ۖ كُلٌّ لَّهُ أَوَّابٌ','پرندگان را نیز دسته جمعی مسخّر او کردیم (تا همراه او تسبیح خدا گویند)؛ و همه اینها بازگشت‌کننده به سوی او بودند!'];
ayat[3990]=[38,20,'وَشَدَدْنَا مُلْكَهُ وَآتَيْنَاهُ الْحِكْمَةَ وَفَصْلَ الْخِطَابِ','و حکومت او را استحکام بخشیدیم، (هم) دانش به او دادیم و (هم) داوری عادلانه!'];
ayat[3991]=[38,21,'۞ وَهَلْ أَتَاكَ نَبَأُ الْخَصْمِ إِذْ تَسَوَّرُوا الْمِحْرَابَ','آیا داستان شاکیان هنگامی که از محراب (داوود) بالا رفتند به تو رسیده است؟!'];
ayat[3992]=[38,22,'إِذْ دَخَلُوا عَلَىٰ دَاوُودَ فَفَزِعَ مِنْهُمْ ۖ قَالُوا لَا تَخَفْ ۖ خَصْمَانِ بَغَىٰ بَعْضُنَا عَلَىٰ بَعْضٍ فَاحْكُم بَيْنَنَا بِالْحَقِّ وَلَا تُشْطِطْ وَاهْدِنَا إِلَىٰ سَوَاءِ الصِّرَاطِ','در آن هنگام که (بی‌مقدمه) بر او وارد شدند و او از دیدن آنها وحشت کرد؛ گفتند: «نترس، دو نفر شاکی هستیم که یکی از ما بر دیگری ستم کرده؛ اکنون در میان ما بحق داوری کن و ستم روا مدار و ما را به راه راست هدایت کن!'];
ayat[3993]=[38,23,'إِنَّ هَٰذَا أَخِي لَهُ تِسْعٌ وَتِسْعُونَ نَعْجَةً وَلِيَ نَعْجَةٌ وَاحِدَةٌ فَقَالَ أَكْفِلْنِيهَا وَعَزَّنِي فِي الْخِطَابِ','این برادر من است؛ و او نود و نه میش دارد و من یکی بیش ندارم امّا او اصرار می‌کند که: این یکی را هم به من واگذار؛ و در سخن بر من غلبه کرده است!»'];
ayat[3994]=[38,24,'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ إِلَىٰ نِعَاجِهِ ۖ وَإِنَّ كَثِيرًا مِّنَ الْخُلَطَاءِ لَيَبْغِي بَعْضُهُمْ عَلَىٰ بَعْضٍ إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَقَلِيلٌ مَّا هُمْ ۗ وَظَنَّ دَاوُودُ أَنَّمَا فَتَنَّاهُ فَاسْتَغْفَرَ رَبَّهُ وَخَرَّ رَاكِعًا وَأَنَابَ ۩','(داوود) گفت: «مسلّماً او با درخواست یک میش تو برای افزودن آن به میشهایش، بر تو ستم نموده؛ و بسیاری از شریکان (و دوستان) به یکدیگر ستم می‌کنند، مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند؛ امّا عدّه آنان کم است!» داوود دانست که ما او را (با این ماجرا) آزموده‌ایم، از این رو از پروردگارش طلب آمرزش نمود و به سجده افتاد و توبه کرد.'];
ayat[3995]=[38,25,'فَغَفَرْنَا لَهُ ذَٰلِكَ ۖ وَإِنَّ لَهُ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَآبٍ','ما این عمل را بر او بخشیدیم؛ و او نزد ما دارای مقامی والا و سرانجامی نیکوست!'];
ayat[3996]=[38,26,'يَا دَاوُودُ إِنَّا جَعَلْنَاكَ خَلِيفَةً فِي الْأَرْضِ فَاحْكُم بَيْنَ النَّاسِ بِالْحَقِّ وَلَا تَتَّبِعِ الْهَوَىٰ فَيُضِلَّكَ عَن سَبِيلِ اللَّهِ ۚ إِنَّ الَّذِينَ يَضِلُّونَ عَن سَبِيلِ اللَّهِ لَهُمْ عَذَابٌ شَدِيدٌ بِمَا نَسُوا يَوْمَ الْحِسَابِ','ای داوود! ما تو را خلیفه و (نماینده خود) در زمین قرار دادیم؛ پس در میان مردم بحق داوری کن، و از هوای نفس پیروی مکن که تو را از راه خدا منحرف سازد؛ کسانی که از راه خدا گمراه شوند، عذاب شدیدی بخاطر فراموش کردن روز حساب دارند!'];
ayat[3997]=[38,27,'وَمَا خَلَقْنَا السَّمَاءَ وَالْأَرْضَ وَمَا بَيْنَهُمَا بَاطِلًا ۚ ذَٰلِكَ ظَنُّ الَّذِينَ كَفَرُوا ۚ فَوَيْلٌ لِّلَّذِينَ كَفَرُوا مِنَ النَّارِ','ما آسمان و زمین و آنچه را میان آنهاست بیهوده نیافریدیم؛ این گمان کافران است؛ وای بر کافران از آتش (دوزخ)!'];
ayat[3998]=[38,28,'أَمْ نَجْعَلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ كَالْمُفْسِدِينَ فِي الْأَرْضِ أَمْ نَجْعَلُ الْمُتَّقِينَ كَالْفُجَّارِ','آیا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند همچون مفسدان در زمین قرار می‌دهیم، یا پرهیزگاران را همچون فاجران؟!'];
ayat[3999]=[38,29,'كِتَابٌ أَنزَلْنَاهُ إِلَيْكَ مُبَارَكٌ لِّيَدَّبَّرُوا آيَاتِهِ وَلِيَتَذَكَّرَ أُولُو الْأَلْبَابِ','این کتابی است پربرکت که بر تو نازل کرده‌ایم تا در آیات آن تدبّر کنند و خردمندان متذکّر شوند!'];
ayat[4000]=[38,30,'وَوَهَبْنَا لِدَاوُودَ سُلَيْمَانَ ۚ نِعْمَ الْعَبْدُ ۖ إِنَّهُ أَوَّابٌ','ما سلیمان را به داوود بخشیدیم؛ چه بنده خوبی! زیرا همواره به سوی خدا بازگشت می‌کرد (و به یاد او بود)!'];
ayat[4001]=[38,31,'إِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصَّافِنَاتُ الْجِيَادُ','به خاطر بیاور هنگامی را که عصرگاهان اسبان چابک تندرو را بر او عرضه داشتند،'];
ayat[4002]=[38,32,'فَقَالَ إِنِّي أَحْبَبْتُ حُبَّ الْخَيْرِ عَن ذِكْرِ رَبِّي حَتَّىٰ تَوَارَتْ بِالْحِجَابِ','گفت: «من این اسبان را بخاطر پروردگارم دوست دارم (و می‌خواهم از آنها در جهاد استفاده کنم»، او همچنان به آنها نگاه می‌کرد) تا از دیدگانش پنهان شدند.'];
ayat[4003]=[38,33,'رُدُّوهَا عَلَيَّ ۖ فَطَفِقَ مَسْحًا بِالسُّوقِ وَالْأَعْنَاقِ','(آنها به قدری جالب بودند که گفت:) بار دیگر آنها را نزد من بازگردانید! و دست به ساقها و گردنهای آنها کشید (و آنها را نوازش داد).'];
ayat[4004]=[38,34,'وَلَقَدْ فَتَنَّا سُلَيْمَانَ وَأَلْقَيْنَا عَلَىٰ كُرْسِيِّهِ جَسَدًا ثُمَّ أَنَابَ','ما سلیمان را آزمودیم و بر تخت او جسدی افکندیم؛ سپس او به درگاه خداوند توبه کرد.'];
ayat[4005]=[38,35,'قَالَ رَبِّ اغْفِرْ لِي وَهَبْ لِي مُلْكًا لَّا يَنبَغِي لِأَحَدٍ مِّن بَعْدِي ۖ إِنَّكَ أَنتَ الْوَهَّابُ','گفت: پروردگارا! مرا ببخش و حکومتی به من عطا کن که بعد از من سزاوار هیچ کس نباشد، که تو بسیار بخشنده‌ای!'];
ayat[4006]=[38,36,'فَسَخَّرْنَا لَهُ الرِّيحَ تَجْرِي بِأَمْرِهِ رُخَاءً حَيْثُ أَصَابَ','پس ما باد را مسخّر او ساختیم تا به فرمانش بنرمی حرکت کند و به هر جا او می‌خواهد برود!'];
ayat[4007]=[38,37,'وَالشَّيَاطِينَ كُلَّ بَنَّاءٍ وَغَوَّاصٍ','و شیاطین را مسخّر او کردیم، هر بنّا و غوّاصی از آنها را!'];
ayat[4008]=[38,38,'وَآخَرِينَ مُقَرَّنِينَ فِي الْأَصْفَادِ','و گروه دیگری (از شیاطین) را در غل و زنجیر (تحت سلطه او) قرار دادیم،'];
ayat[4009]=[38,39,'هَٰذَا عَطَاؤُنَا فَامْنُنْ أَوْ أَمْسِكْ بِغَيْرِ حِسَابٍ','(و به او گفتیم:) این عطای ما است، به هر کس می‌خواهی (و صلاح می‌بینی) ببخش، و از هر کس می‌خواهی امساک کن، و حسابی بر تو نیست (تو امین هستی)!'];
ayat[4010]=[38,40,'وَإِنَّ لَهُ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَآبٍ','و برای او [= سلیمان‌] نزد ما مقامی ارجمند و سرانجامی نیکوست!'];
ayat[4011]=[38,41,'وَاذْكُرْ عَبْدَنَا أَيُّوبَ إِذْ نَادَىٰ رَبَّهُ أَنِّي مَسَّنِيَ الشَّيْطَانُ بِنُصْبٍ وَعَذَابٍ','و به خاطر بیاور بنده ما ایّوب را، هنگامی که پروردگارش را خواند (و گفت: پروردگارا!) شیطان مرا به رنج و عذاب افکنده است.'];
ayat[4012]=[38,42,'ارْكُضْ بِرِجْلِكَ ۖ هَٰذَا مُغْتَسَلٌ بَارِدٌ وَشَرَابٌ','(به او گفتیم:) پای خود را بر زمین بکوب! این چشمه آبی خنک برای شستشو و نوشیدن است!'];
ayat[4013]=[38,43,'وَوَهَبْنَا لَهُ أَهْلَهُ وَمِثْلَهُم مَّعَهُمْ رَحْمَةً مِّنَّا وَذِكْرَىٰ لِأُولِي الْأَلْبَابِ','و خانواده‌اش را به او بخشیدیم، و همانند آنها را بر آنان افزودیم، تا رحمتی از سوی ما باشد و تذکّری برای اندیشمندان.'];
ayat[4014]=[38,44,'وَخُذْ بِيَدِكَ ضِغْثًا فَاضْرِب بِّهِ وَلَا تَحْنَثْ ۗ إِنَّا وَجَدْنَاهُ صَابِرًا ۚ نِّعْمَ الْعَبْدُ ۖ إِنَّهُ أَوَّابٌ','(و به او گفتیم:) بسته‌ای از ساقه‌های گندم (یا مانند آن) را برگیر و با آن (همسرت را) بزن و سوگند خود را مشکن! ما او را شکیبا یافتیم؛ چه بنده خوبی که بسیار بازگشت‌کننده (به سوی خدا) بود!'];
ayat[4015]=[38,45,'وَاذْكُرْ عِبَادَنَا إِبْرَاهِيمَ وَإِسْحَاقَ وَيَعْقُوبَ أُولِي الْأَيْدِي وَالْأَبْصَارِ','و به خاطر بیاور بندگان ما ابراهیم و اسحاق و یعقوب را، صاحبان دستها (ی نیرومند) و چشمها (ی بینا)!'];
ayat[4016]=[38,46,'إِنَّا أَخْلَصْنَاهُم بِخَالِصَةٍ ذِكْرَى الدَّارِ','ما آنها را با خلوص ویژه‌ای خالص کردیم، و آن یادآوری سرای آخرت بود!'];
ayat[4017]=[38,47,'وَإِنَّهُمْ عِندَنَا لَمِنَ الْمُصْطَفَيْنَ الْأَخْيَارِ','و آنها نزد ما از برگزیدگان و نیکانند!'];
ayat[4018]=[38,48,'وَاذْكُرْ إِسْمَاعِيلَ وَالْيَسَعَ وَذَا الْكِفْلِ ۖ وَكُلٌّ مِّنَ الْأَخْيَارِ','و به خاطر بیاور «اسماعیل» و «الیسع» و «ذا الکفل» را که همه از نیکان بودند!'];
ayat[4019]=[38,49,'هَٰذَا ذِكْرٌ ۚ وَإِنَّ لِلْمُتَّقِينَ لَحُسْنَ مَآبٍ','این یک یادآوری است، و برای پرهیزکاران فرجام نیکویی است:'];
ayat[4020]=[38,50,'جَنَّاتِ عَدْنٍ مُّفَتَّحَةً لَّهُمُ الْأَبْوَابُ','باغهای جاویدان بهشتی که درهایش به روی آنان گشوده است،'];
ayat[4021]=[38,51,'مُتَّكِئِينَ فِيهَا يَدْعُونَ فِيهَا بِفَاكِهَةٍ كَثِيرَةٍ وَشَرَابٍ','در حالی که در آن بر تختها تکیه کرده‌اند و میوه‌های بسیار و نوشیدنیها در اختیار آنان است!'];
ayat[4022]=[38,52,'۞ وَعِندَهُمْ قَاصِرَاتُ الطَّرْفِ أَتْرَابٌ','و نزد آنان همسرانی است که تنها چشم به شوهرانشان دوخته‌اند، و همسن و سالند!'];
ayat[4023]=[38,53,'هَٰذَا مَا تُوعَدُونَ لِيَوْمِ الْحِسَابِ','این همان است که برای روز حساب به شما وعده داده می‌شود (وعده‌ای تخلّف ناپذیر)!'];
ayat[4024]=[38,54,'إِنَّ هَٰذَا لَرِزْقُنَا مَا لَهُ مِن نَّفَادٍ','این روزی ما است که هرگز آن را پایانی نیست!'];
ayat[4025]=[38,55,'هَٰذَا ۚ وَإِنَّ لِلطَّاغِينَ لَشَرَّ مَآبٍ','این (پاداش پرهیزگاران است)، و برای طغیانگران بدترین محل بازگشت است:'];
ayat[4026]=[38,56,'جَهَنَّمَ يَصْلَوْنَهَا فَبِئْسَ الْمِهَادُ','دوزخ، که در آن وارد می‌شوند؛ و چه بستر بدی است!'];
ayat[4027]=[38,57,'هَٰذَا فَلْيَذُوقُوهُ حَمِيمٌ وَغَسَّاقٌ','این نوشابه «حمیم» و «غسّاق» است [= دو مایع سوزان و تیره رنگ‌] که باید از آن بچشند!'];
ayat[4028]=[38,58,'وَآخَرُ مِن شَكْلِهِ أَزْوَاجٌ','و جز اینها کیفرهای دیگری همانند آن دارند!'];
ayat[4029]=[38,59,'هَٰذَا فَوْجٌ مُّقْتَحِمٌ مَّعَكُمْ ۖ لَا مَرْحَبًا بِهِمْ ۚ إِنَّهُمْ صَالُو النَّارِ','(به آنان گفته می‌شود:) این گروهی است که همراه شما وارد دوزخ می‌شوند (اینها همان سران گمراهیند)؛ خوشامد بر آنها مباد، همگی در آتش خواهند سوخت!'];
ayat[4030]=[38,60,'قَالُوا بَلْ أَنتُمْ لَا مَرْحَبًا بِكُمْ ۖ أَنتُمْ قَدَّمْتُمُوهُ لَنَا ۖ فَبِئْسَ الْقَرَارُ','آنها (به رؤسای خود) می‌گویند: «بلکه خوشامد بر شما مباد که این عذاب را شما برای ما فراهم ساختید! چه بد قرارگاهی است اینجا!»'];
ayat[4031]=[38,61,'قَالُوا رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدْهُ عَذَابًا ضِعْفًا فِي النَّارِ','(سپس) می‌گویند: «پروردگارا! هر کس این عذاب را برای ما فراهم ساخته، عذابی مضاعف در آتش بر او بیفزا!»'];
ayat[4032]=[38,62,'وَقَالُوا مَا لَنَا لَا نَرَىٰ رِجَالًا كُنَّا نَعُدُّهُم مِّنَ الْأَشْرَارِ','آنها می‌گویند: «چرا مردانی را که ما از اشرار می‌شمردیم (در اینجا، در آتش دوزخ) نمی‌بینیم؟!'];
ayat[4033]=[38,63,'أَتَّخَذْنَاهُمْ سِخْرِيًّا أَمْ زَاغَتْ عَنْهُمُ الْأَبْصَارُ','آیا ما آنان را به مسخره گرفتیم یا (به اندازه‌ای حقیرند که) چشمها آنها را نمی‌بیند؟!'];
ayat[4034]=[38,64,'إِنَّ ذَٰلِكَ لَحَقٌّ تَخَاصُمُ أَهْلِ النَّارِ','این یک واقعیت است گفتگوهای خصمانه دوزخیان!'];
ayat[4035]=[38,65,'قُلْ إِنَّمَا أَنَا مُنذِرٌ ۖ وَمَا مِنْ إِلَٰهٍ إِلَّا اللَّهُ الْوَاحِدُ الْقَهَّارُ','بگو: «من تنها یک بیم‌دهنده‌ام؛ و هیچ معبودی جز خداوند یگانه قهّار نیست!'];
ayat[4036]=[38,66,'رَبُّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا الْعَزِيزُ الْغَفَّارُ','پروردگار آسمانها و زمین و آنچه میان آن دو است، پروردگار عزیز و غفّار!»'];
ayat[4037]=[38,67,'قُلْ هُوَ نَبَأٌ عَظِيمٌ','بگو: «این خبری بزرگ است،'];
ayat[4038]=[38,68,'أَنتُمْ عَنْهُ مُعْرِضُونَ','که شما از آن روی‌گردانید!'];
ayat[4039]=[38,69,'مَا كَانَ لِيَ مِنْ عِلْمٍ بِالْمَلَإِ الْأَعْلَىٰ إِذْ يَخْتَصِمُونَ','من از ملأ اعلی (و فرشتگان عالم بالا) به هنگامی که (درباره آفرینش آدم) مخاصمه می‌کردند خبر ندارم!'];
ayat[4040]=[38,70,'إِن يُوحَىٰ إِلَيَّ إِلَّا أَنَّمَا أَنَا نَذِيرٌ مُّبِينٌ','تنها چیزی که به من وحی می‌شود این است که من انذارکننده آشکاری هستم!»'];
ayat[4041]=[38,71,'إِذْ قَالَ رَبُّكَ لِلْمَلَائِكَةِ إِنِّي خَالِقٌ بَشَرًا مِّن طِينٍ','و به خاطر بیاور هنگامی را که پروردگارت به فرشتگان گفت: «من بشری را از گل می‌آفرینم!'];
ayat[4042]=[38,72,'فَإِذَا سَوَّيْتُهُ وَنَفَخْتُ فِيهِ مِن رُّوحِي فَقَعُوا لَهُ سَاجِدِينَ','هنگامی که آن را نظام بخشیدم و از روح خود در آن دمیدم، برای او به سجده افتید!»'];
ayat[4043]=[38,73,'فَسَجَدَ الْمَلَائِكَةُ كُلُّهُمْ أَجْمَعُونَ','در آن هنگام همه فرشتگان سجده کردند،'];
ayat[4044]=[38,74,'إِلَّا إِبْلِيسَ اسْتَكْبَرَ وَكَانَ مِنَ الْكَافِرِينَ','جز ابلیس که تکبّر ورزید و از کافران بود!'];
ayat[4045]=[38,75,'قَالَ يَا إِبْلِيسُ مَا مَنَعَكَ أَن تَسْجُدَ لِمَا خَلَقْتُ بِيَدَيَّ ۖ أَسْتَكْبَرْتَ أَمْ كُنتَ مِنَ الْعَالِينَ','گفت: «ای ابلیس! چه چیز مانع تو شد که بر مخلوقی که با قدرت خود او را آفریدم سجده کنی؟! آیا تکبّر کردی یا از برترینها بودی؟! (برتر از اینکه فرمان سجود به تو داده شود!)»'];
ayat[4046]=[38,76,'قَالَ أَنَا خَيْرٌ مِّنْهُ ۖ خَلَقْتَنِي مِن نَّارٍ وَخَلَقْتَهُ مِن طِينٍ','گفت: «من از او بهترم؛ مرا از آتش آفریده‌ای و او را از گل!»'];
ayat[4047]=[38,77,'قَالَ فَاخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ','فرمود: «از آسمانها (و صفوف ملائکه) خارج شو، که تو رانده درگاه منی!'];
ayat[4048]=[38,78,'وَإِنَّ عَلَيْكَ لَعْنَتِي إِلَىٰ يَوْمِ الدِّينِ','و مسلّماً لعنت من بر تو تا روز قیامت خواهد بود!'];
ayat[4049]=[38,79,'قَالَ رَبِّ فَأَنظِرْنِي إِلَىٰ يَوْمِ يُبْعَثُونَ','گفت: «پروردگارا! مرا تا روزی که انسانها برانگیخته می‌شوند مهلت ده!»'];
ayat[4050]=[38,80,'قَالَ فَإِنَّكَ مِنَ الْمُنظَرِينَ','فرمود: «تو از مهلت داده‌شدگانی،'];
ayat[4051]=[38,81,'إِلَىٰ يَوْمِ الْوَقْتِ الْمَعْلُومِ','ولی تا روز و زمان معیّن!»'];
ayat[4052]=[38,82,'قَالَ فَبِعِزَّتِكَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ','گفت: «به عزّتت سوگند، همه آنان را گمراه خواهم کرد،'];
ayat[4053]=[38,83,'إِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِينَ','مگر بندگان خالص تو، از میان آنها!»'];
ayat[4054]=[38,84,'قَالَ فَالْحَقُّ وَالْحَقَّ أَقُولُ','فرمود: «به حق سوگند، و حق می‌گویم،'];
ayat[4055]=[38,85,'لَأَمْلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنْهُمْ أَجْمَعِينَ','که جهنّم را از تو و هر کدام از آنان که از تو پیروی کند، پر خواهم کرد!»'];
ayat[4056]=[38,86,'قُلْ مَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ وَمَا أَنَا مِنَ الْمُتَكَلِّفِينَ','(ای پیامبر!) بگو: «من برای دعوت نبوّت هیچ پاداشی از شما نمی‌طلبم، و من از متکلّفین نیستم! (سخنانم روشن و همراه با دلیل است!)»'];
ayat[4057]=[38,87,'إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَالَمِينَ','این (قرآن) تذکّری برای همه جهانیان است؛'];
ayat[4058]=[38,88,'وَلَتَعْلَمُنَّ نَبَأَهُ بَعْدَ حِينٍ','و خبر آن را بعد از مدّتی می‌شنوید!'];
ayat[4059]=[39,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَنزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ','این کتابی است که از سوی خداوند عزیز و حکیم نازل شده است.'];
ayat[4060]=[39,2,'إِنَّا أَنزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ فَاعْبُدِ اللَّهَ مُخْلِصًا لَّهُ الدِّينَ','ما این کتاب را بحقّ بر تو نازل کردیم؛ پس خدا را پرستش کن و دین خود را برای او خالص گردان!'];
ayat[4061]=[39,3,'أَلَا لِلَّهِ الدِّينُ الْخَالِصُ ۚ وَالَّذِينَ اتَّخَذُوا مِن دُونِهِ أَوْلِيَاءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَا إِلَى اللَّهِ زُلْفَىٰ إِنَّ اللَّهَ يَحْكُمُ بَيْنَهُمْ فِي مَا هُمْ فِيهِ يَخْتَلِفُونَ ۗ إِنَّ اللَّهَ لَا يَهْدِي مَنْ هُوَ كَاذِبٌ كَفَّارٌ','آگاه باشید که دین خالص از آن خداست، و آنها که غیر خدا را اولیای خود قرار دادند و دلیلشان این بود که: «اینها را نمی‌پرستیم مگر بخاطر اینکه ما را به خداوند نزدیک کنند»، خداوند روز قیامت میان آنان در آنچه اختلاف داشتند داوری می‌کند؛ خداوند آن کس را که دروغگو و کفران‌کننده است هرگز هدایت نمی‌کند!'];
ayat[4062]=[39,4,'لَّوْ أَرَادَ اللَّهُ أَن يَتَّخِذَ وَلَدًا لَّاصْطَفَىٰ مِمَّا يَخْلُقُ مَا يَشَاءُ ۚ سُبْحَانَهُ ۖ هُوَ اللَّهُ الْوَاحِدُ الْقَهَّارُ','اگر (بفرض محال) خدا می‌خواست فرزندی انتخاب کند، از میان مخلوقاتش آنچه را می‌خواست برمیگزید؛ منزّه است (از اینکه فرزندی داشته باشد)! او خداوند یکتای پیروز است!'];
ayat[4063]=[39,5,'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ ۖ يُكَوِّرُ اللَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى اللَّيْلِ ۖ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ ۖ كُلٌّ يَجْرِي لِأَجَلٍ مُّسَمًّى ۗ أَلَا هُوَ الْعَزِيزُ الْغَفَّارُ','آسمانها و زمین را بحقّ آفرید؛ شب را بر روز می‌پیچد و روز را بر شب؛ و خورشید و ماه را مسخّر فرمان خویش قرار داد؛ هر کدام تا سرآمد معیّنی به حرکت خود ادامه می‌دهند؛ آگاه باشید که او قادر و آمرزنده است!'];
ayat[4064]=[39,6,'خَلَقَكُم مِّن نَّفْسٍ وَاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَأَنزَلَ لَكُم مِّنَ الْأَنْعَامِ ثَمَانِيَةَ أَزْوَاجٍ ۚ يَخْلُقُكُمْ فِي بُطُونِ أُمَّهَاتِكُمْ خَلْقًا مِّن بَعْدِ خَلْقٍ فِي ظُلُمَاتٍ ثَلَاثٍ ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ لَهُ الْمُلْكُ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُصْرَفُونَ','او شما را از یک نفس آفرید، و همسرش را از (باقیمانده گِل) او خلق کرد؛ و برای شما هشت زوج از چهارپایان ایجاد کرد؛ او شما را در شکم مادرانتان آفرینشی بعد از آفرینش دیگر، در میان تاریکیهای سه گانه، می‌بخشد! این است خداوند، پروردگار شما که حکومت (عالم هستی) از آن اوست؛ هیچ معبودی جز او نیست؛ پس چگونه از راه حق منحرف می‌شوید؟!'];
ayat[4065]=[39,7,'إِن تَكْفُرُوا فَإِنَّ اللَّهَ غَنِيٌّ عَنكُمْ ۖ وَلَا يَرْضَىٰ لِعِبَادِهِ الْكُفْرَ ۖ وَإِن تَشْكُرُوا يَرْضَهُ لَكُمْ ۗ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ۗ ثُمَّ إِلَىٰ رَبِّكُم مَّرْجِعُكُمْ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','اگر کفران کنید، خداوند از شما بی‌نیاز است و هرگز کفران را برای بندگانش نمی پسندد؛ و اگر شکر او را بجا آورید آن را برای شما می‌پسندد! و هیچ گنهکاری گناه دیگری را بر دوش نمی‌کشد! سپس بازگشت همه شما به سوی پروردگارتان است، و شما را از آنچه انجام می‌دادید آگاه می‌سازد؛ چرا که او به آنچه در سینه‌هاست آگاه است!'];
ayat[4066]=[39,8,'۞ وَإِذَا مَسَّ الْإِنسَانَ ضُرٌّ دَعَا رَبَّهُ مُنِيبًا إِلَيْهِ ثُمَّ إِذَا خَوَّلَهُ نِعْمَةً مِّنْهُ نَسِيَ مَا كَانَ يَدْعُو إِلَيْهِ مِن قَبْلُ وَجَعَلَ لِلَّهِ أَندَادًا لِّيُضِلَّ عَن سَبِيلِهِ ۚ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيلًا ۖ إِنَّكَ مِنْ أَصْحَابِ النَّارِ','هنگامی که انسان را زیانی رسد، پروردگار خود را می‌خواند و بسوی او باز می گردد؛ امّا هنگامی که نعمتی از خود به او عطا کند، آنچه را به خاطر آن قبلاً خدا را می خواند از یاد می‌برد و برای خداوند همتایانی قرارمی‌دهد تا مردم را از راه او منحرف سازد؛ بگو: «چند روزی از کفرت بهره‌گیر که از دوزخیانی!»'];
ayat[4067]=[39,9,'أَمَّنْ هُوَ قَانِتٌ آنَاءَ اللَّيْلِ سَاجِدًا وَقَائِمًا يَحْذَرُ الْآخِرَةَ وَيَرْجُو رَحْمَةَ رَبِّهِ ۗ قُلْ هَلْ يَسْتَوِي الَّذِينَ يَعْلَمُونَ وَالَّذِينَ لَا يَعْلَمُونَ ۗ إِنَّمَا يَتَذَكَّرُ أُولُو الْأَلْبَابِ','(آیا چنین کسی با ارزش است) یا کسی که در ساعات شب به عبادت مشغول است و در حال سجده و قیام، از عذاب آخرت می‌ترسد و به رحمت پروردگارش امیدوار است؟! بگو: «آیا کسانی که می‌دانند با کسانی که نمی‌دانند یکسانند؟! تنها خردمندان متذکّر می‌شوند!»'];
ayat[4068]=[39,10,'قُلْ يَا عِبَادِ الَّذِينَ آمَنُوا اتَّقُوا رَبَّكُمْ ۚ لِلَّذِينَ أَحْسَنُوا فِي هَٰذِهِ الدُّنْيَا حَسَنَةٌ ۗ وَأَرْضُ اللَّهِ وَاسِعَةٌ ۗ إِنَّمَا يُوَفَّى الصَّابِرُونَ أَجْرَهُم بِغَيْرِ حِسَابٍ','بگو: «ای بندگان من که ایمان آورده‌اید! از (مخالفت) پروردگارتان بپرهیزید! برای کسانی که در این دنیا نیکی کرده‌اند پاداش نیکی است! و زمین خدا وسیع است، (اگر تحت فشار سران کفر بودید مهاجرت کنید) که صابران اجر و پاداش خود را بی‌حساب دریافت می‌دارند!'];
ayat[4069]=[39,11,'قُلْ إِنِّي أُمِرْتُ أَنْ أَعْبُدَ اللَّهَ مُخْلِصًا لَّهُ الدِّينَ','بگو: «من مأمورم که خدا را پرستش کنم در حالی که دینم را برای او خالص کرده باشم،'];
ayat[4070]=[39,12,'وَأُمِرْتُ لِأَنْ أَكُونَ أَوَّلَ الْمُسْلِمِينَ','و مأمورم که نخستین مسلمان باشم!'];
ayat[4071]=[39,13,'قُلْ إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ','بگو: «من اگر نافرمانی پروردگارم کنم، از عذاب روز بزرگ (قیامت) می‌ترسم!»'];
ayat[4072]=[39,14,'قُلِ اللَّهَ أَعْبُدُ مُخْلِصًا لَّهُ دِينِي','بگو: «من تنها خدا را می‌پرستم در حالی که دینم را برای او خالص می‌کنم.'];
ayat[4073]=[39,15,'فَاعْبُدُوا مَا شِئْتُم مِّن دُونِهِ ۗ قُلْ إِنَّ الْخَاسِرِينَ الَّذِينَ خَسِرُوا أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ الْقِيَامَةِ ۗ أَلَا ذَٰلِكَ هُوَ الْخُسْرَانُ الْمُبِينُ','شما هر چه را جز او می‌خواهید بپرستید!» بگو: «زیانکاران واقعی آنانند که سرمایه وجود خویش و بستگانشان را در روز قیامت از دست داده‌اند! آگاه باشید زیان آشکار همین است!»'];
ayat[4074]=[39,16,'لَهُم مِّن فَوْقِهِمْ ظُلَلٌ مِّنَ النَّارِ وَمِن تَحْتِهِمْ ظُلَلٌ ۚ ذَٰلِكَ يُخَوِّفُ اللَّهُ بِهِ عِبَادَهُ ۚ يَا عِبَادِ فَاتَّقُونِ','برای آنان از بالای سرشان سایبانهایی از آتش، و در زیر پایشان نیز سایبانهایی از آتش است؛ این چیزی است که خداوند با آن بندگانش را می‌ترساند! ای بندگان من! از نافرمانی من بپرهیزید!'];
ayat[4075]=[39,17,'وَالَّذِينَ اجْتَنَبُوا الطَّاغُوتَ أَن يَعْبُدُوهَا وَأَنَابُوا إِلَى اللَّهِ لَهُمُ الْبُشْرَىٰ ۚ فَبَشِّرْ عِبَادِ','و کسانی که از عبادت طاغوت پرهیز کردند و به سوی خداوند بازگشتند، بشارت از آن آنهاست؛ پس بندگان مرا بشارت ده!'];
ayat[4076]=[39,18,'الَّذِينَ يَسْتَمِعُونَ الْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُ ۚ أُولَٰئِكَ الَّذِينَ هَدَاهُمُ اللَّهُ ۖ وَأُولَٰئِكَ هُمْ أُولُو الْأَلْبَابِ','همان کسانی که سخنان را می‌شنوند و از نیکوترین آنها پیروی می‌کنند؛ آنان کسانی هستند که خدا هدایتشان کرده، و آنها خردمندانند.'];
ayat[4077]=[39,19,'أَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِي النَّارِ','آیا تو می‌توانی کسی را که فرمان عذاب درباره او قطعی شده رهایی بخشی؟! آیا تو میتوانی کسی را که در درون آتش است برگیری و نجات دهی؟!'];
ayat[4078]=[39,20,'لَٰكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِّن فَوْقِهَا غُرَفٌ مَّبْنِيَّةٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۖ وَعْدَ اللَّهِ ۖ لَا يُخْلِفُ اللَّهُ الْمِيعَادَ','ولی آنها که تقوای الهی پیشه کردند، غرفه‌هایی در بهشت دارند که بر فراز آنها غرفه‌های دیگری بنا شده و از زیر آنها نهرها جاری است این وعده الهی است، و خداوند در وعده خود تخلّف نمی‌کند!'];
ayat[4079]=[39,21,'أَلَمْ تَرَ أَنَّ اللَّهَ أَنزَلَ مِنَ السَّمَاءِ مَاءً فَسَلَكَهُ يَنَابِيعَ فِي الْأَرْضِ ثُمَّ يُخْرِجُ بِهِ زَرْعًا مُّخْتَلِفًا أَلْوَانُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرًّا ثُمَّ يَجْعَلُهُ حُطَامًا ۚ إِنَّ فِي ذَٰلِكَ لَذِكْرَىٰ لِأُولِي الْأَلْبَابِ','آیا ندیدی که خداوند از آسمان آبی فرستاد و آن را بصورت چشمه‌هایی در زمین وارد نمود، سپس با آن زراعتی را خارج می‌سازد که رنگهای مختلف دارد؛ بعد آن گیاه خشک می‌شود، بگونه‌ای که آن را زرد و بی‌روح می‌بینی؛ سپس آن را در هم می‌شکند و خرد می‌کند؛ در این مثال تذکّری است برای خردمندان (از ناپایداری دنیا)!'];
ayat[4080]=[39,22,'أَفَمَن شَرَحَ اللَّهُ صَدْرَهُ لِلْإِسْلَامِ فَهُوَ عَلَىٰ نُورٍ مِّن رَّبِّهِ ۚ فَوَيْلٌ لِّلْقَاسِيَةِ قُلُوبُهُم مِّن ذِكْرِ اللَّهِ ۚ أُولَٰئِكَ فِي ضَلَالٍ مُّبِينٍ','آیا کسی که خدا سینه‌اش را برای اسلام گشاده است و بر فراز مرکبی از نور الهی قرار گرفته (همچون کوردلان گمراه است؟!) وای بر آنان که قلبهایی سخت در برابر ذکر خدا دارند! آنها در گمراهی آشکاری هستند!'];
ayat[4081]=[39,23,'اللَّهُ نَزَّلَ أَحْسَنَ الْحَدِيثِ كِتَابًا مُّتَشَابِهًا مَّثَانِيَ تَقْشَعِرُّ مِنْهُ جُلُودُ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ ثُمَّ تَلِينُ جُلُودُهُمْ وَقُلُوبُهُمْ إِلَىٰ ذِكْرِ اللَّهِ ۚ ذَٰلِكَ هُدَى اللَّهِ يَهْدِي بِهِ مَن يَشَاءُ ۚ وَمَن يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ','خداوند بهترین سخن را نازل کرده، کتابی که آیاتش (در لطف و زیبایی و عمق و محتوا) همانند یکدیگر است؛ آیاتی مکرّر دارد (با تکراری شوق‌انگیز) که از شنیدن آیاتش لرزه بر اندام کسانی که از پروردگارشان می‌ترسند می‌افتد؛ سپس برون و درونشان نرم و متوجّه ذکر خدا می‌شود؛ این هدایت الهی است که هر کس را بخواهد با آن راهنمایی می‌کند؛ و هر کس را خداوند گمراه سازد، راهنمایی برای او نخواهد بود!'];
ayat[4082]=[39,24,'أَفَمَن يَتَّقِي بِوَجْهِهِ سُوءَ الْعَذَابِ يَوْمَ الْقِيَامَةِ ۚ وَقِيلَ لِلظَّالِمِينَ ذُوقُوا مَا كُنتُمْ تَكْسِبُونَ','آیا کسی که با صورت خود عذاب دردناک (الهی) را در روز قیامت دور می‌سازد (همانند کسی است که هرگز آتش دوزخ به او نمی‌رسد)؟! و به ظالمان گفته می شود: «بچشید آنچه را به دست می‌آوردید (و انجام می‌دادید)!»'];
ayat[4083]=[39,25,'كَذَّبَ الَّذِينَ مِن قَبْلِهِمْ فَأَتَاهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ','کسانی که قبل از آنها بودند نیز (آیات ما را) تکذیب نمودند، و عذاب (الهی) از جایی که فکر نمی‌کردند به سراغشان آمد!'];
ayat[4084]=[39,26,'فَأَذَاقَهُمُ اللَّهُ الْخِزْيَ فِي الْحَيَاةِ الدُّنْيَا ۖ وَلَعَذَابُ الْآخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا يَعْلَمُونَ','پس خداوند خواری را در زندگی این دنیا به آنها چشانید، و عذاب آخرت شدیدتر است اگر می‌دانستند.'];
ayat[4085]=[39,27,'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِي هَٰذَا الْقُرْآنِ مِن كُلِّ مَثَلٍ لَّعَلَّهُمْ يَتَذَكَّرُونَ','ما برای مردم در این قرآن از هر نوع مثَلی زدیم، شاید متذکّر شوند.'];
ayat[4086]=[39,28,'قُرْآنًا عَرَبِيًّا غَيْرَ ذِي عِوَجٍ لَّعَلَّهُمْ يَتَّقُونَ','قرآنی است فصیح و خالی از هر گونه کجی و نادرستی، شاید آنان پرهیزگاری پیشه کنند!'];
ayat[4087]=[39,29,'ضَرَبَ اللَّهُ مَثَلًا رَّجُلًا فِيهِ شُرَكَاءُ مُتَشَاكِسُونَ وَرَجُلًا سَلَمًا لِّرَجُلٍ هَلْ يَسْتَوِيَانِ مَثَلًا ۚ الْحَمْدُ لِلَّهِ ۚ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ','خداوند مثالی زده است: مردی را که مملوک شریکانی است که درباره او پیوسته با هم به مشاجره مشغولند، و مردی که تنها تسلیم یک نفر است؛ آیا این دو یکسانند؟! حمد، مخصوص خداست، ولی بیشتر آنان نمی‌دانند.'];
ayat[4088]=[39,30,'إِنَّكَ مَيِّتٌ وَإِنَّهُم مَّيِّتُونَ','تو می‌میری و آنها نیز خواهند مرد!'];
ayat[4089]=[39,31,'ثُمَّ إِنَّكُمْ يَوْمَ الْقِيَامَةِ عِندَ رَبِّكُمْ تَخْتَصِمُونَ','سپس شما روز قیامت نزد پروردگارتان مخاصمه می‌کنید.'];
ayat[4090]=[39,32,'۞ فَمَنْ أَظْلَمُ مِمَّن كَذَبَ عَلَى اللَّهِ وَكَذَّبَ بِالصِّدْقِ إِذْ جَاءَهُ ۚ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِّلْكَافِرِينَ','پس چه کسی ستمکارتر است از آن کسی که بر خدا دروغ ببندد و سخن راست را هنگامی که به سراغ او آمده تکذیب کند؟! آیا در جهنّم جایگاهی برای کافران نیست؟!'];
ayat[4091]=[39,33,'وَالَّذِي جَاءَ بِالصِّدْقِ وَصَدَّقَ بِهِ ۙ أُولَٰئِكَ هُمُ الْمُتَّقُونَ','امّا کسی که سخن راست بیاورد و کسی که آن را تصدیق کند، آنان پرهیزگارانند!'];
ayat[4092]=[39,34,'لَهُم مَّا يَشَاءُونَ عِندَ رَبِّهِمْ ۚ ذَٰلِكَ جَزَاءُ الْمُحْسِنِينَ','آنچه بخواهند نزد پروردگارشان برای آنان موجود است؛ و این است جزای نیکوکاران!'];
ayat[4093]=[39,35,'لِيُكَفِّرَ اللَّهُ عَنْهُمْ أَسْوَأَ الَّذِي عَمِلُوا وَيَجْزِيَهُمْ أَجْرَهُم بِأَحْسَنِ الَّذِي كَانُوا يَعْمَلُونَ','تا خداوند بدترین اعمالی را که انجام داده‌اند (در سایه ایمان و صداقت آنها) بیامرزد، و آنها را به بهترین اعمالی که انجام می‌دادند پاداش دهد!'];
ayat[4094]=[39,36,'أَلَيْسَ اللَّهُ بِكَافٍ عَبْدَهُ ۖ وَيُخَوِّفُونَكَ بِالَّذِينَ مِن دُونِهِ ۚ وَمَن يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ','آیا خداوند برای (نجات و دفاع از) بنده‌اش کافی نیست؟! امّا آنها تو را از غیر او می‌ترسانند. و هر کس را خداوند گمراه کند، هیچ هدایت‌کننده‌ای ندارد!'];
ayat[4095]=[39,37,'وَمَن يَهْدِ اللَّهُ فَمَا لَهُ مِن مُّضِلٍّ ۗ أَلَيْسَ اللَّهُ بِعَزِيزٍ ذِي انتِقَامٍ','و هر کس را خدا هدایت کند، هیچ گمراه‌کننده‌ای نخواهد داشت آیا خداوند توانا و دارای مجازات نیست؟!'];
ayat[4096]=[39,38,'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ لَيَقُولُنَّ اللَّهُ ۚ قُلْ أَفَرَأَيْتُم مَّا تَدْعُونَ مِن دُونِ اللَّهِ إِنْ أَرَادَنِيَ اللَّهُ بِضُرٍّ هَلْ هُنَّ كَاشِفَاتُ ضُرِّهِ أَوْ أَرَادَنِي بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكَاتُ رَحْمَتِهِ ۚ قُلْ حَسْبِيَ اللَّهُ ۖ عَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُونَ','و اگر از آنها بپرسی: «چه کسی آسمانها و زمین را آفریده؟» حتماً می‌گویند: «خدا!» بگو: «آیا هیچ درباره معبودانی که غیر از خدا می‌خوانید اندیشه می‌کنید که اگر خدا زیانی برای من بخواهد، آیا آنها می‌توانند گزند او را برطرف سازند؟! و یا اگر رحمتی برای من بخواهد، آیا آنها می‌توانند جلو رحمت او را بگیرند؟!» بگو: «خدا مرا کافی است؛ و همه متوکّلان تنها بر او توکّل می‌کنند!»'];
ayat[4097]=[39,39,'قُلْ يَا قَوْمِ اعْمَلُوا عَلَىٰ مَكَانَتِكُمْ إِنِّي عَامِلٌ ۖ فَسَوْفَ تَعْلَمُونَ','بگو: «ای قوم من! شما هر چه در توان دارید انجام دهید، من نیز به وظیفه خود عمل می‌کنم؛ امّا بزودی خواهید دانست...'];
ayat[4098]=[39,40,'مَن يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيمٌ','چه کسی عذاب خوارکننده‌ای (در دنیا) به سراغش می‌آید، و (سپس) عذابی جاویدان (در آخرت) بر او وارد می‌گردد!»'];
ayat[4099]=[39,41,'إِنَّا أَنزَلْنَا عَلَيْكَ الْكِتَابَ لِلنَّاسِ بِالْحَقِّ ۖ فَمَنِ اهْتَدَىٰ فَلِنَفْسِهِ ۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا ۖ وَمَا أَنتَ عَلَيْهِم بِوَكِيلٍ','ما این کتاب (آسمانی) را برای مردم بحق بر تو نازل کردیم؛ هر کس هدایت را پذیرد به نفع خود اوست؛ و هر کس گمراهی را برگزیند، تنها به زیان خود گمراه می‌گردد؛ و تو مأمور اجبار آنها به هدایت نیستی.'];
ayat[4100]=[39,42,'اللَّهُ يَتَوَفَّى الْأَنفُسَ حِينَ مَوْتِهَا وَالَّتِي لَمْ تَمُتْ فِي مَنَامِهَا ۖ فَيُمْسِكُ الَّتِي قَضَىٰ عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْأُخْرَىٰ إِلَىٰ أَجَلٍ مُّسَمًّى ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَتَفَكَّرُونَ','خداوند ارواح را به هنگام مرگ قبض می‌کند، و ارواحی را که نمرده‌اند نیز به هنگام خواب می‌گیرد؛ سپس ارواح کسانی که فرمان مرگشان را صادر کرده نگه می‌دارد و ارواح دیگری را (که باید زنده بمانند) بازمی‌گرداند تا سرآمدی معیّن؛ در این امر نشانه‌های روشنی است برای کسانی که اندیشه می‌کنند!'];
ayat[4101]=[39,43,'أَمِ اتَّخَذُوا مِن دُونِ اللَّهِ شُفَعَاءَ ۚ قُلْ أَوَلَوْ كَانُوا لَا يَمْلِكُونَ شَيْئًا وَلَا يَعْقِلُونَ','آیا آنان غیر از خدا شفیعانی گرفته‌اند؟! به آنان بگو: «آیا (از آنها شفاعت می‌طلبید) هر چند مالک چیزی نباشند و درک و شعوری برای آنها نباشد؟!»'];
ayat[4102]=[39,44,'قُل لِّلَّهِ الشَّفَاعَةُ جَمِيعًا ۖ لَّهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ ثُمَّ إِلَيْهِ تُرْجَعُونَ','بگو: «تمام شفاعت از آن خداست، (زیرا) حاکمیّت آسمانها و زمین از آن اوست و سپس همه شما را به سوی او بازمی‌گردانند!»'];
ayat[4103]=[39,45,'وَإِذَا ذُكِرَ اللَّهُ وَحْدَهُ اشْمَأَزَّتْ قُلُوبُ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ ۖ وَإِذَا ذُكِرَ الَّذِينَ مِن دُونِهِ إِذَا هُمْ يَسْتَبْشِرُونَ','هنگامی که خداوند به یگانگی یاد می‌شود، دلهای کسانی که به آخرت ایمان ندارند مشمئزّ (و متنفّر) می‌گردد؛ امّا هنگامی که از معبودهای دیگر یاد می‌شود، آنان خوشحال می‌شوند.'];
ayat[4104]=[39,46,'قُلِ اللَّهُمَّ فَاطِرَ السَّمَاوَاتِ وَالْأَرْضِ عَالِمَ الْغَيْبِ وَالشَّهَادَةِ أَنتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِي مَا كَانُوا فِيهِ يَخْتَلِفُونَ','بگو: «خداوندا! ای آفریننده آسمانها و زمین، و آگاه از اسرار نهان و آشکار، تو در میان بندگانت در آنچه اختلاف داشتند داوری خواهی کرد!»'];
ayat[4105]=[39,47,'وَلَوْ أَنَّ لِلَّذِينَ ظَلَمُوا مَا فِي الْأَرْضِ جَمِيعًا وَمِثْلَهُ مَعَهُ لَافْتَدَوْا بِهِ مِن سُوءِ الْعَذَابِ يَوْمَ الْقِيَامَةِ ۚ وَبَدَا لَهُم مِّنَ اللَّهِ مَا لَمْ يَكُونُوا يَحْتَسِبُونَ','اگر ستمکاران تمام آنچه را روی زمین است مالک باشند و همانند آن بر آن افزوده شود، حاضرند همه را فدا کنند تا از عذاب شدید روز قیامت رهایی یابند؛ و از سوی خدا برای آنها اموری ظاهر می‌شود که هرگز گمان نمی‌کردند!'];
ayat[4106]=[39,48,'وَبَدَا لَهُمْ سَيِّئَاتُ مَا كَسَبُوا وَحَاقَ بِهِم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','در آن روز اعمال بدی را که انجام داده‌اند برای آنها آشکار می‌شود، و آنچه را استهزا می‌کردند بر آنها واقع می‌گردد.'];
ayat[4107]=[39,49,'فَإِذَا مَسَّ الْإِنسَانَ ضُرٌّ دَعَانَا ثُمَّ إِذَا خَوَّلْنَاهُ نِعْمَةً مِّنَّا قَالَ إِنَّمَا أُوتِيتُهُ عَلَىٰ عِلْمٍ ۚ بَلْ هِيَ فِتْنَةٌ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','هنگامی که انسان را زیانی رسد، ما را (برای حلّ مشکلش) می‌خواند؛ سپس هنگامی که از جانب خود به او نعمتی دهیم، می‌گوید: «این نعمت را بخاطر کاردانی خودم به من داده‌اند»؛ ولی این وسیله آزمایش (آنها) است، امّا بیشترشان نمی‌دانند.'];
ayat[4108]=[39,50,'قَدْ قَالَهَا الَّذِينَ مِن قَبْلِهِمْ فَمَا أَغْنَىٰ عَنْهُم مَّا كَانُوا يَكْسِبُونَ','این سخن را کسانی که قبل از آنها بودند نیز گفتند، ولی آنچه را به دست می‌آوردند برای آنها سودی نداشت!'];
ayat[4109]=[39,51,'فَأَصَابَهُمْ سَيِّئَاتُ مَا كَسَبُوا ۚ وَالَّذِينَ ظَلَمُوا مِنْ هَٰؤُلَاءِ سَيُصِيبُهُمْ سَيِّئَاتُ مَا كَسَبُوا وَمَا هُم بِمُعْجِزِينَ','سپس بدیهای اعمالشان به آنها رسید؛ و ظالمان این گروه [= اهل مکّه‌] نیز بزودی گرفتار بدیهای اعمالی که انجام داده‌اند خواهند شد، و هرگز نمی‌توانند از چنگال عذاب الهی بگریزند.'];
ayat[4110]=[39,52,'أَوَلَمْ يَعْلَمُوا أَنَّ اللَّهَ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ وَيَقْدِرُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يُؤْمِنُونَ','آیا آنها ندانستند که خداوند روزی را برای هر کس بخواهد گسترده یا تنگ می‌سازد؟! در این، آیات و نشانه‌هایی است برای گروهی که ایمان می‌آورند.'];
ayat[4111]=[39,53,'۞ قُلْ يَا عِبَادِيَ الَّذِينَ أَسْرَفُوا عَلَىٰ أَنفُسِهِمْ لَا تَقْنَطُوا مِن رَّحْمَةِ اللَّهِ ۚ إِنَّ اللَّهَ يَغْفِرُ الذُّنُوبَ جَمِيعًا ۚ إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ','بگو: «ای بندگان من که بر خود اسراف و ستم کرده‌اید! از رحمت خداوند نومید نشوید که خدا همه گناهان را می‌آمرزد، زیرا او بسیار آمرزنده و مهربان است.'];
ayat[4112]=[39,54,'وَأَنِيبُوا إِلَىٰ رَبِّكُمْ وَأَسْلِمُوا لَهُ مِن قَبْلِ أَن يَأْتِيَكُمُ الْعَذَابُ ثُمَّ لَا تُنصَرُونَ','و به درگاه پروردگارتان بازگردید و در برابر او تسلیم شوید، پیش از آنکه عذاب به سراغ شما آید، سپس از سوی هیچ کس یاری نشوید!'];
ayat[4113]=[39,55,'وَاتَّبِعُوا أَحْسَنَ مَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُم مِّن قَبْلِ أَن يَأْتِيَكُمُ الْعَذَابُ بَغْتَةً وَأَنتُمْ لَا تَشْعُرُونَ','و از بهترین دستورهائی که از سوی پروردگارتان بر شما نازل شده پیروی کنید پیش از آنکه عذاب (الهی) ناگهان به سراغ شما آید در حالی که از آن خبر ندارید!»'];
ayat[4114]=[39,56,'أَن تَقُولَ نَفْسٌ يَا حَسْرَتَا عَلَىٰ مَا فَرَّطتُ فِي جَنبِ اللَّهِ وَإِن كُنتُ لَمِنَ السَّاخِرِينَ','(این دستورها برای آن است که) مبادا کسی روز قیامت بگوید: «افسوس بر من از کوتاهیهایی که در اطاعت فرمان خدا کردم و از مسخره‌کنندگان (آیات او) بودم!»'];
ayat[4115]=[39,57,'أَوْ تَقُولَ لَوْ أَنَّ اللَّهَ هَدَانِي لَكُنتُ مِنَ الْمُتَّقِينَ','یا بگوید: «اگر خداوند مرا هدایت می‌کرد، از پرهیزگاران بودم!»'];
ayat[4116]=[39,58,'أَوْ تَقُولَ حِينَ تَرَى الْعَذَابَ لَوْ أَنَّ لِي كَرَّةً فَأَكُونَ مِنَ الْمُحْسِنِينَ','یا هنگامی که عذاب را می‌بیند بگوید: «ای کاش بار دیگر (به دنیا) بازمی‌گشتم و از نیکوکاران بودم!»'];
ayat[4117]=[39,59,'بَلَىٰ قَدْ جَاءَتْكَ آيَاتِي فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وَكُنتَ مِنَ الْكَافِرِينَ','آری، آیات من به سراغ تو آمد، امّا آن را تکذیب کردی و تکبّر نمودی و از کافران بودی!'];
ayat[4118]=[39,60,'وَيَوْمَ الْقِيَامَةِ تَرَى الَّذِينَ كَذَبُوا عَلَى اللَّهِ وُجُوهُهُم مُّسْوَدَّةٌ ۚ أَلَيْسَ فِي جَهَنَّمَ مَثْوًى لِّلْمُتَكَبِّرِينَ','و روز قیامت کسانی را که بر خدا دروغ بستند می‌بینی که صورتهایشان سیاه است؛ آیا در جهنّم جایگاهی برای متکبّران نیست؟!'];
ayat[4119]=[39,61,'وَيُنَجِّي اللَّهُ الَّذِينَ اتَّقَوْا بِمَفَازَتِهِمْ لَا يَمَسُّهُمُ السُّوءُ وَلَا هُمْ يَحْزَنُونَ','و خداوند کسانی را که تقوا پیشه کردند با رستگاری رهایی می‌بخشد؛ هیچ بدی به آنان نمی‌رسد و هرگز غمگین نخواهند شد.'];
ayat[4120]=[39,62,'اللَّهُ خَالِقُ كُلِّ شَيْءٍ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ وَكِيلٌ','خداوند آفریدگار همه چیز است و حافظ و ناظر بر همه اشیا است.'];
ayat[4121]=[39,63,'لَّهُ مَقَالِيدُ السَّمَاوَاتِ وَالْأَرْضِ ۗ وَالَّذِينَ كَفَرُوا بِآيَاتِ اللَّهِ أُولَٰئِكَ هُمُ الْخَاسِرُونَ','کلیدهای آسمانها و زمین از آن اوست؛ و کسانی که به آیات خداوند کافر شدند زیانکارانند.'];
ayat[4122]=[39,64,'قُلْ أَفَغَيْرَ اللَّهِ تَأْمُرُونِّي أَعْبُدُ أَيُّهَا الْجَاهِلُونَ','بگو: «آیا به من دستور می‌دهید که غیر خدا را بپرستم ای جاهلان؟!»'];
ayat[4123]=[39,65,'وَلَقَدْ أُوحِيَ إِلَيْكَ وَإِلَى الَّذِينَ مِن قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ الْخَاسِرِينَ','به تو و همه پیامبران پیشین وحی شده که اگر مشرک شوی، تمام اعمالت تباه می‌شود و از زیانکاران خواهی بود!'];
ayat[4124]=[39,66,'بَلِ اللَّهَ فَاعْبُدْ وَكُن مِّنَ الشَّاكِرِينَ','بلکه تنها خداوند را عبادت کن و از شکرگزاران باش!'];
ayat[4125]=[39,67,'وَمَا قَدَرُوا اللَّهَ حَقَّ قَدْرِهِ وَالْأَرْضُ جَمِيعًا قَبْضَتُهُ يَوْمَ الْقِيَامَةِ وَالسَّمَاوَاتُ مَطْوِيَّاتٌ بِيَمِينِهِ ۚ سُبْحَانَهُ وَتَعَالَىٰ عَمَّا يُشْرِكُونَ','آنها خدا را آن گونه که شایسته است نشناختند، در حالی که تمام زمین در روز قیامت در قبضه اوست و آسمانها پیچیده در دست او؛ خداوند منزّه و بلندمقام است از شریکیهایی که برای او می‌پندارند'];
ayat[4126]=[39,68,'وَنُفِخَ فِي الصُّورِ فَصَعِقَ مَن فِي السَّمَاوَاتِ وَمَن فِي الْأَرْضِ إِلَّا مَن شَاءَ اللَّهُ ۖ ثُمَّ نُفِخَ فِيهِ أُخْرَىٰ فَإِذَا هُمْ قِيَامٌ يَنظُرُونَ','و در «صور» دمیده می‌شود، پس همه کسانی که در آسمانها و زمینند می‌میرند، مگر کسانی که خدا بخواهد؛ سپس بار دیگر در «صور» دمیده می‌شود، ناگهان همگی به پا می‌خیزند و در انتظار (حساب و جزا) هستند.'];
ayat[4127]=[39,69,'وَأَشْرَقَتِ الْأَرْضُ بِنُورِ رَبِّهَا وَوُضِعَ الْكِتَابُ وَجِيءَ بِالنَّبِيِّينَ وَالشُّهَدَاءِ وَقُضِيَ بَيْنَهُم بِالْحَقِّ وَهُمْ لَا يُظْلَمُونَ','و زمین (در آن روز) به نور پروردگارش روشن می‌شود، و نامه‌های اعمال را پیش می‌نهند و پیامبران و گواهان را حاضر می‌سازند، و میان آنها بحق داوری می‌شود و به آنان ستم نخواهد شد!'];
ayat[4128]=[39,70,'وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُوَ أَعْلَمُ بِمَا يَفْعَلُونَ','و به هر کس آنچه انجام داده است بی‌کم و کاست داده می‌شود؛ و او نسبت به آنچه انجام می‌دادند از همه آگاهتر است.'];
ayat[4129]=[39,71,'وَسِيقَ الَّذِينَ كَفَرُوا إِلَىٰ جَهَنَّمَ زُمَرًا ۖ حَتَّىٰ إِذَا جَاءُوهَا فُتِحَتْ أَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ رُسُلٌ مِّنكُمْ يَتْلُونَ عَلَيْكُمْ آيَاتِ رَبِّكُمْ وَيُنذِرُونَكُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا ۚ قَالُوا بَلَىٰ وَلَٰكِنْ حَقَّتْ كَلِمَةُ الْعَذَابِ عَلَى الْكَافِرِينَ','و کسانی که کافر شدند گروه گروه به سوی جهنّم رانده می‌شوند؛ وقتی به دوزخ می‌رسند، درهای آن گشوده می‌شود و نگهبانان دوزخ به آنها می‌گویند: «آیا رسولانی از میان شما به سویتان نیامدند که آیات پروردگارتان را برای شما بخوانند و از ملاقات این روز شما را بر حذر دارند؟!» می‌گویند: «آری، (پیامبران آمدند و آیات الهی را بر ما خواندند، و ما مخالفت کردیم!) ولی فرمان عذاب الهی بر کافران مسلّم شده است.'];
ayat[4130]=[39,72,'قِيلَ ادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ','به آنان گفته می‌شود: «از درهای جهنّم وارد شوید، جاودانه در آن بمانید؛ چه بد جایگاهی است جایگاه متکبّران!»'];
ayat[4131]=[39,73,'وَسِيقَ الَّذِينَ اتَّقَوْا رَبَّهُمْ إِلَى الْجَنَّةِ زُمَرًا ۖ حَتَّىٰ إِذَا جَاءُوهَا وَفُتِحَتْ أَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلَامٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوهَا خَالِدِينَ','و کسانی که تقوای الهی پیشه کردند گروه گروه به سوی بهشت برده می‌شوند؛ هنگامی که به آن می‌رسند درهای بهشت گشوده می‌شود و نگهبانان به آنان می‌گویند: «سلام بر شما! گوارایتان باد این نعمتها! داخل بهشت شوید و جاودانه بمانید!»'];
ayat[4132]=[39,74,'وَقَالُوا الْحَمْدُ لِلَّهِ الَّذِي صَدَقَنَا وَعْدَهُ وَأَوْرَثَنَا الْأَرْضَ نَتَبَوَّأُ مِنَ الْجَنَّةِ حَيْثُ نَشَاءُ ۖ فَنِعْمَ أَجْرُ الْعَامِلِينَ','آنها می‌گویند: «حمد و ستایش مخصوص خداوندی است که به وعده خویش درباره ما وفا کرد و زمین (بهشت) را میراث ما قرار داد که هر جا را بخواهیم منزلگاه خود قرار دهیم؛ چه نیکوست پاداش عمل کنندگان!»'];
ayat[4133]=[39,75,'وَتَرَى الْمَلَائِكَةَ حَافِّينَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ ۖ وَقُضِيَ بَيْنَهُم بِالْحَقِّ وَقِيلَ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ','(در آن روز) فرشتگان را می‌بینی که بر گرد عرش خدا حلقه زده‌اند و با ستایش پروردگارشان تسبیح می‌گویند؛ و در میان بندگان بحق داوری می‌شود؛ و (سرانجام) گفته خواهد شد: «حمد مخصوص خدا پروردگار جهانیان است!»'];
ayat[4134]=[40,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم.'];
ayat[4135]=[40,2,'تَنزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْعَلِيمِ','این کتابی است که از سوی خداوند قادر و دانا نازل شده است.'];
ayat[4136]=[40,3,'غَافِرِ الذَّنبِ وَقَابِلِ التَّوْبِ شَدِيدِ الْعِقَابِ ذِي الطَّوْلِ ۖ لَا إِلَٰهَ إِلَّا هُوَ ۖ إِلَيْهِ الْمَصِيرُ','خداوندی که آمرزنده گناه، پذیرنده توبه، دارای مجازات سخت، و صاحب نعمت فراوان است؛ هیچ معبودی جز او نیست؛ و بازگشت (همه شما) تنها بسوی اوست.'];
ayat[4137]=[40,4,'مَا يُجَادِلُ فِي آيَاتِ اللَّهِ إِلَّا الَّذِينَ كَفَرُوا فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِي الْبِلَادِ','تنها کسانی در آیات ما مجادله می‌کنند که (از روی عناد) کافر شده‌اند؛ پس مبادا رفت و آمد آنان در شهرها (و قدرت‌نمایی آنان) تو را بفریبد!'];
ayat[4138]=[40,5,'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَالْأَحْزَابُ مِن بَعْدِهِمْ ۖ وَهَمَّتْ كُلُّ أُمَّةٍ بِرَسُولِهِمْ لِيَأْخُذُوهُ ۖ وَجَادَلُوا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَأَخَذْتُهُمْ ۖ فَكَيْفَ كَانَ عِقَابِ','پیش از آنها قوم نوح و اقوامی که بعد از ایشان بودند (پیامبرانشان را) تکذیب کردند؛ و هر امّتی در پی آن بود که توطئه کند و پیامبرش را بگیرد (و آزار دهد)، و برای محو حق به مجادله باطل دست زدند؛ امّا من آنها را گرفتم (و سخت مجازات کردم)؛ ببین که مجازات من چگونه بود!'];
ayat[4139]=[40,6,'وَكَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِينَ كَفَرُوا أَنَّهُمْ أَصْحَابُ النَّارِ','و این گونه فرمان پروردگارت درباره کسانی که کافر شدند مسلّم شده که آنها همه اهل آتشند!'];
ayat[4140]=[40,7,'الَّذِينَ يَحْمِلُونَ الْعَرْشَ وَمَنْ حَوْلَهُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِهِ وَيَسْتَغْفِرُونَ لِلَّذِينَ آمَنُوا رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَّحْمَةً وَعِلْمًا فَاغْفِرْ لِلَّذِينَ تَابُوا وَاتَّبَعُوا سَبِيلَكَ وَقِهِمْ عَذَابَ الْجَحِيمِ','فرشتگانی که حاملان عرشند و آنها که گرداگرد آن (طواف می‌کنند) تسبیح و حمد پروردگارشان را می‌گویند و به او ایمان دارند و برای مؤمنان استغفار می‌کنند (و می گویند:) پروردگارا! رحمت و علم تو همه چیز را فراگرفته است؛ پس کسانی را که توبه کرده و راه تو را پیروی می‌کنند بیامرز، و آنان را از عذاب دوزخ نگاه دار!'];
ayat[4141]=[40,8,'رَبَّنَا وَأَدْخِلْهُمْ جَنَّاتِ عَدْنٍ الَّتِي وَعَدتَّهُمْ وَمَن صَلَحَ مِنْ آبَائِهِمْ وَأَزْوَاجِهِمْ وَذُرِّيَّاتِهِمْ ۚ إِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ','پروردگارا! آنها را در باغهای جاویدان بهشت که به آنها وعده فرموده‌ای وارد کن، همچنین از پدران و همسران و فرزندانشان هر کدام که صالح بودند، که تو توانا و حکیمی!'];
ayat[4142]=[40,9,'وَقِهِمُ السَّيِّئَاتِ ۚ وَمَن تَقِ السَّيِّئَاتِ يَوْمَئِذٍ فَقَدْ رَحِمْتَهُ ۚ وَذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','و آنان را از بدیها نگاه دار، و هر کس را که در آن روز از بدیها نگاه داری، مشمول رحمتت ساخته‌ای؛ و این است همان رستگاری عظیم!'];
ayat[4143]=[40,10,'إِنَّ الَّذِينَ كَفَرُوا يُنَادَوْنَ لَمَقْتُ اللَّهِ أَكْبَرُ مِن مَّقْتِكُمْ أَنفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى الْإِيمَانِ فَتَكْفُرُونَ','کسانی را که کافر شدند روز قیامت صدا می‌زنند که عداوت و خشم خداوند نسبت به شما از عداوت و خشم خودتان نسبت به خودتان بیشتر است، چرا که بسوی ایمان دعوت می‌شدید، ولی انکار می‌کردید!'];
ayat[4144]=[40,11,'قَالُوا رَبَّنَا أَمَتَّنَا اثْنَتَيْنِ وَأَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنَا بِذُنُوبِنَا فَهَلْ إِلَىٰ خُرُوجٍ مِّن سَبِيلٍ','آنها می‌گویند: «پروردگارا! ما را دو بار میراندی و دو بار زنده کردی؛ اکنون به گناهان خود معترفیم؛ آیا راهی برای خارج شدن (از دوزخ) وجود دارد؟»'];
ayat[4145]=[40,12,'ذَٰلِكُم بِأَنَّهُ إِذَا دُعِيَ اللَّهُ وَحْدَهُ كَفَرْتُمْ ۖ وَإِن يُشْرَكْ بِهِ تُؤْمِنُوا ۚ فَالْحُكْمُ لِلَّهِ الْعَلِيِّ الْكَبِيرِ','این بخاطر آن است که وقتی خداوند به یگانگی خوانده می‌شد انکار می‌کردید، و اگر برای او همتایی می‌پنداشتند ایمان می‌آوردید؛ اکنون داوری مخصوص خداوند بلندمرتبه و بزرگ است (و شما را مطابق عدل خود کیفر می‌دهد).'];
ayat[4146]=[40,13,'هُوَ الَّذِي يُرِيكُمْ آيَاتِهِ وَيُنَزِّلُ لَكُم مِّنَ السَّمَاءِ رِزْقًا ۚ وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ','او کسی است که آیات خود را به شما نشان می‌دهد و از آسمان برای شما روزی (با ارزشی) می‌فرستد؛ تنها کسانی متذکّر این حقایق می‌شوند که بسوی خدا باز می‌گردند.'];
ayat[4147]=[40,14,'فَادْعُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ وَلَوْ كَرِهَ الْكَافِرُونَ','(تنها) خدا را بخوانید و دین خود را برای او خالص کنید، هرچند کافران ناخشنود باشند!'];
ayat[4148]=[40,15,'رَفِيعُ الدَّرَجَاتِ ذُو الْعَرْشِ يُلْقِي الرُّوحَ مِنْ أَمْرِهِ عَلَىٰ مَن يَشَاءُ مِنْ عِبَادِهِ لِيُنذِرَ يَوْمَ التَّلَاقِ','او درجات (بندگان صالح) را بالا می‌برد، او صاحب عرش است، روح (مقدّس) را به فرمانش بر هر کس از بندگانش که بخواهد القاء می‌کند تا (مردم را) از روز ملاقات [= روز رستاخیز] بیم دهد!'];
ayat[4149]=[40,16,'يَوْمَ هُم بَارِزُونَ ۖ لَا يَخْفَىٰ عَلَى اللَّهِ مِنْهُمْ شَيْءٌ ۚ لِّمَنِ الْمُلْكُ الْيَوْمَ ۖ لِلَّهِ الْوَاحِدِ الْقَهَّارِ','روزی که همه آنان آشکار می‌شوند و چیزی از آنها بر خدا پنهان نخواهد ماند؛ (و گفته می‌شود:) حکومت امروز برای کیست؟ برای خداوند یکتای قهّار است!'];
ayat[4150]=[40,17,'الْيَوْمَ تُجْزَىٰ كُلُّ نَفْسٍ بِمَا كَسَبَتْ ۚ لَا ظُلْمَ الْيَوْمَ ۚ إِنَّ اللَّهَ سَرِيعُ الْحِسَابِ','امروز هر کس در برابر کاری که انجام داده است پاداش داده می‌شود؛ امروز هیچ ظلمی نیست؛ خداوند سریع الحساب است!'];
ayat[4151]=[40,18,'وَأَنذِرْهُمْ يَوْمَ الْآزِفَةِ إِذِ الْقُلُوبُ لَدَى الْحَنَاجِرِ كَاظِمِينَ ۚ مَا لِلظَّالِمِينَ مِنْ حَمِيمٍ وَلَا شَفِيعٍ يُطَاعُ','و آنها را از روز نزدیک بترسان، هنگامی که از شدّت وحشت دلها به گلوگاه می‌رسد و تمامی وجود آنها مملوّ از اندوه می‌گردد؛ برای ستمکاران دوستی وجود ندارد، و نه شفاعت کننده‌ای که شفاعتش پذیرفته شود.'];
ayat[4152]=[40,19,'يَعْلَمُ خَائِنَةَ الْأَعْيُنِ وَمَا تُخْفِي الصُّدُورُ','او چشمهایی را که به خیانت می‌گردد و آنچه را سینه‌ها پنهان می‌دارند، می‌داند.'];
ayat[4153]=[40,20,'وَاللَّهُ يَقْضِي بِالْحَقِّ ۖ وَالَّذِينَ يَدْعُونَ مِن دُونِهِ لَا يَقْضُونَ بِشَيْءٍ ۗ إِنَّ اللَّهَ هُوَ السَّمِيعُ الْبَصِيرُ','خداوند بحقّ داوری می‌کند، و معبودهایی را که غیر از او می‌خوانند هیچ گونه داوری ندارند؛ خداوند شنوا و بیناست!'];
ayat[4154]=[40,21,'۞ أَوَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ كَانُوا مِن قَبْلِهِمْ ۚ كَانُوا هُمْ أَشَدَّ مِنْهُمْ قُوَّةً وَآثَارًا فِي الْأَرْضِ فَأَخَذَهُمُ اللَّهُ بِذُنُوبِهِمْ وَمَا كَانَ لَهُم مِّنَ اللَّهِ مِن وَاقٍ','آیا آنها روی زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنان بودند چگونه بود؟! آنها در قدرت و ایجاد آثار مهمّ در زمین از اینها برتر بودند؛ ولی خداوند ایشان را به گناهانشان گرفت، و در برابر عذاب او مدافعی نداشتند!'];
ayat[4155]=[40,22,'ذَٰلِكَ بِأَنَّهُمْ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِالْبَيِّنَاتِ فَكَفَرُوا فَأَخَذَهُمُ اللَّهُ ۚ إِنَّهُ قَوِيٌّ شَدِيدُ الْعِقَابِ','این برای آن بود که پیامبرانشان پیوسته با دلایل روشن به سراغشان می‌آمدند، ولی آنها انکار می‌کردند؛ خداوند هم آنان را گرفت (و کیفر داد) که او قوّی و مجازاتش شدید است!'];
ayat[4156]=[40,23,'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا وَسُلْطَانٍ مُّبِينٍ','ما موسی را با آیات خود و دلیل روشن فرستادیم...'];
ayat[4157]=[40,24,'إِلَىٰ فِرْعَوْنَ وَهَامَانَ وَقَارُونَ فَقَالُوا سَاحِرٌ كَذَّابٌ','بسوی فرعون و هامان و قارون؛ ولی آنها گفتند: «او ساحری بسیار دروغگو است!»'];
ayat[4158]=[40,25,'فَلَمَّا جَاءَهُم بِالْحَقِّ مِنْ عِندِنَا قَالُوا اقْتُلُوا أَبْنَاءَ الَّذِينَ آمَنُوا مَعَهُ وَاسْتَحْيُوا نِسَاءَهُمْ ۚ وَمَا كَيْدُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ','و هنگامی که حقّ را از سوی ما برای آنها آورد، گفتند: «پسران کسانی را که با موسی ایمان آورده‌اند بکشید و زنانشان را (برای اسارت و خدمت) زنده بگذارید!» امّا نقشه کافران جز در گمراهی نیست (و نقش بر آب می‌شود).'];
ayat[4159]=[40,26,'وَقَالَ فِرْعَوْنُ ذَرُونِي أَقْتُلْ مُوسَىٰ وَلْيَدْعُ رَبَّهُ ۖ إِنِّي أَخَافُ أَن يُبَدِّلَ دِينَكُمْ أَوْ أَن يُظْهِرَ فِي الْأَرْضِ الْفَسَادَ','و فرعون گفت: «بگذارید موسی را بکشم، و او پروردگارش را بخواند (تا نجاتش دهد)! زیرا من می‌ترسم که آیین شما را دگرگون سازد، و یا در این سرزمین فساد بر پا کند!»'];
ayat[4160]=[40,27,'وَقَالَ مُوسَىٰ إِنِّي عُذْتُ بِرَبِّي وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٍ لَّا يُؤْمِنُ بِيَوْمِ الْحِسَابِ','موسی گفت: «من به پروردگارم و پروردگار شما پناه می‌برم از هر متکبّری که به روز حساب ایمان نمی‌آورد!»'];
ayat[4161]=[40,28,'وَقَالَ رَجُلٌ مُّؤْمِنٌ مِّنْ آلِ فِرْعَوْنَ يَكْتُمُ إِيمَانَهُ أَتَقْتُلُونَ رَجُلًا أَن يَقُولَ رَبِّيَ اللَّهُ وَقَدْ جَاءَكُم بِالْبَيِّنَاتِ مِن رَّبِّكُمْ ۖ وَإِن يَكُ كَاذِبًا فَعَلَيْهِ كَذِبُهُ ۖ وَإِن يَكُ صَادِقًا يُصِبْكُم بَعْضُ الَّذِي يَعِدُكُمْ ۖ إِنَّ اللَّهَ لَا يَهْدِي مَنْ هُوَ مُسْرِفٌ كَذَّابٌ','و مرد مؤمنی از آل فرعون که ایمان خود را پنهان می‌داشت گفت: «آیا می‌خواهید مردی را بکشید بخاطر اینکه می‌گوید: پروردگار من «اللّه» است، در حالی که دلایل روشنی از سوی پروردگارتان برای شما آورده است؟! اگر دروغگو باشد، دروغش دامن خودش را خواهد گرفت؛ و اگر راستگو باشد، (لااقل) بعضی از عذابهایی را که وعده می‌دهد به شما خواهد رسید؛ خداوند کسی را که اسرافکار و بسیار دروغگوست هدایت نمی‌کند.'];
ayat[4162]=[40,29,'يَا قَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظَاهِرِينَ فِي الْأَرْضِ فَمَن يَنصُرُنَا مِن بَأْسِ اللَّهِ إِن جَاءَنَا ۚ قَالَ فِرْعَوْنُ مَا أُرِيكُمْ إِلَّا مَا أَرَىٰ وَمَا أَهْدِيكُمْ إِلَّا سَبِيلَ الرَّشَادِ','ای قوم من! امروز حکومت از آن شماست و در این سرزمین پیروزید؛ اگر عذاب خدا به سراغ ما آید، چه کسی ما را یاری خواهد کرد؟!» فرعون گفت: «من جز آنچه را معتقدم به شما ارائه نمی‌دهم، و شما را جز به راه صحیح راهنمایی نمی‌کنم! (دستور، همان قتل موسی است!)»'];
ayat[4163]=[40,30,'وَقَالَ الَّذِي آمَنَ يَا قَوْمِ إِنِّي أَخَافُ عَلَيْكُم مِّثْلَ يَوْمِ الْأَحْزَابِ','آن مرد باایمان گفت: «ای قوم من! من بر شما از روزی همانند روز (عذاب) اقوام پیشین بیمناکم!'];
ayat[4164]=[40,31,'مِثْلَ دَأْبِ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَالَّذِينَ مِن بَعْدِهِمْ ۚ وَمَا اللَّهُ يُرِيدُ ظُلْمًا لِّلْعِبَادِ','و از عادتی همچون عادت قوم نوح و عاد و ثمود و کسانی که بعد از آنان بودند (از شرک و کفر و طغیان) می‌ترسم؛ و خداوند ظلم و ستمی بر بندگانش نمی‌خواهد.'];
ayat[4165]=[40,32,'وَيَا قَوْمِ إِنِّي أَخَافُ عَلَيْكُمْ يَوْمَ التَّنَادِ','ای قوم من! من بر شما از روزی که مردم یکدیگر را صدا می‌زنند (و از هم یاری می‌طلبند و صدایشان به جایی نمی‌رسد) بیمناکم!'];
ayat[4166]=[40,33,'يَوْمَ تُوَلُّونَ مُدْبِرِينَ مَا لَكُم مِّنَ اللَّهِ مِنْ عَاصِمٍ ۗ وَمَن يُضْلِلِ اللَّهُ فَمَا لَهُ مِنْ هَادٍ','همان روزی که روی می‌گردانید و فرار می‌کنید؛ امّا هیچ پناهگاهی در برابر عذاب خداوند برای شما نیست؛ و هر کس را خداوند (بخاطر اعمالش) گمراه سازد، هدایت‌کننده‌ای برای او نیست!'];
ayat[4167]=[40,34,'وَلَقَدْ جَاءَكُمْ يُوسُفُ مِن قَبْلُ بِالْبَيِّنَاتِ فَمَا زِلْتُمْ فِي شَكٍّ مِّمَّا جَاءَكُم بِهِ ۖ حَتَّىٰ إِذَا هَلَكَ قُلْتُمْ لَن يَبْعَثَ اللَّهُ مِن بَعْدِهِ رَسُولًا ۚ كَذَٰلِكَ يُضِلُّ اللَّهُ مَنْ هُوَ مُسْرِفٌ مُّرْتَابٌ','پیش از این یوسف دلایل روشن برای شما آورد، ولی شما همچنان در آنچه او برای شما آورده بود تردید داشتید؛ تا زمانی که از دنیا رفت، گفتید: هرگز خداوند بعد از او پیامبری مبعوث نخواهد کرد! این گونه خداوند هر اسرافکار تردیدکننده‌ای را گمراه می‌سازد!'];
ayat[4168]=[40,35,'الَّذِينَ يُجَادِلُونَ فِي آيَاتِ اللَّهِ بِغَيْرِ سُلْطَانٍ أَتَاهُمْ ۖ كَبُرَ مَقْتًا عِندَ اللَّهِ وَعِندَ الَّذِينَ آمَنُوا ۚ كَذَٰلِكَ يَطْبَعُ اللَّهُ عَلَىٰ كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ','همانها که در آیات خدا بی‌آنکه دلیلی برایشان آمده باشد به مجادله برمی‌خیزند؛ (این کارشان) خشم عظیمی نزد خداوند و نزد آنان که ایمان آورده‌اند به بار می‌آورد؛ این گونه خداوند بر دل هر متکبّر جبّاری مُهر می‌نهد!»'];
ayat[4169]=[40,36,'وَقَالَ فِرْعَوْنُ يَا هَامَانُ ابْنِ لِي صَرْحًا لَّعَلِّي أَبْلُغُ الْأَسْبَابَ','فرعون گفت: «ای هامان! برای من بنای مرتفعی بساز، شاید به وسایلی دست یابم،'];
ayat[4170]=[40,37,'أَسْبَابَ السَّمَاوَاتِ فَأَطَّلِعَ إِلَىٰ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُ كَاذِبًا ۚ وَكَذَٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوءُ عَمَلِهِ وَصُدَّ عَنِ السَّبِيلِ ۚ وَمَا كَيْدُ فِرْعَوْنَ إِلَّا فِي تَبَابٍ','وسایل (صعود به) آسمانها تا از خدای موسی آگاه شوم؛ هر چند گمان می‌کنم او دروغگو باشد!» اینچنین اعمال بد فرعون در نظرش آراسته جلوه کرد و از راه حق باز داشته شد؛ و توطئه فرعون (و همفکران او) جز به نابودی نمی‌انجامد!'];
ayat[4171]=[40,38,'وَقَالَ الَّذِي آمَنَ يَا قَوْمِ اتَّبِعُونِ أَهْدِكُمْ سَبِيلَ الرَّشَادِ','کسی که (از قوم فرعون) ایمان آورده بود گفت: «ای قوم من! از من پیروی کنید تا شما را به راه درست هدایت کنم.'];
ayat[4172]=[40,39,'يَا قَوْمِ إِنَّمَا هَٰذِهِ الْحَيَاةُ الدُّنْيَا مَتَاعٌ وَإِنَّ الْآخِرَةَ هِيَ دَارُ الْقَرَارِ','ای قوم من! این زندگی دنیا، تنها متاع زودگذری است؛ و آخرت سرای همیشگی است!'];
ayat[4173]=[40,40,'مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزَىٰ إِلَّا مِثْلَهَا ۖ وَمَنْ عَمِلَ صَالِحًا مِّن ذَكَرٍ أَوْ أُنثَىٰ وَهُوَ مُؤْمِنٌ فَأُولَٰئِكَ يَدْخُلُونَ الْجَنَّةَ يُرْزَقُونَ فِيهَا بِغَيْرِ حِسَابٍ','هر کس بدی کند، جز بمانند آن کیفر داده نمی‌شود؛ ولی هر کس کار شایسته‌ای انجام دهد -خواه مرد یا زن- در حالی که مؤمن باشد آنها وارد بهشت می‌شوند و در آن روزی بی‌حسابی به آنها داده خواهد شد.'];
ayat[4174]=[40,41,'۞ وَيَا قَوْمِ مَا لِي أَدْعُوكُمْ إِلَى النَّجَاةِ وَتَدْعُونَنِي إِلَى النَّارِ','ای قوم من! چرا من شما را به سوی نجات دعوت می‌کنم، امّا شما مرا بسوی آتش فرا می‌خوانید؟!'];
ayat[4175]=[40,42,'تَدْعُونَنِي لِأَكْفُرَ بِاللَّهِ وَأُشْرِكَ بِهِ مَا لَيْسَ لِي بِهِ عِلْمٌ وَأَنَا أَدْعُوكُمْ إِلَى الْعَزِيزِ الْغَفَّارِ','مرا دعوت می‌کنید که به خداوند یگانه کافر شوم و همتایی که به آن علم ندارم برای او قرار دهم، در حالی که من شما را بسوی خداوند عزیز غفّار دعوت می‌کنم!'];
ayat[4176]=[40,43,'لَا جَرَمَ أَنَّمَا تَدْعُونَنِي إِلَيْهِ لَيْسَ لَهُ دَعْوَةٌ فِي الدُّنْيَا وَلَا فِي الْآخِرَةِ وَأَنَّ مَرَدَّنَا إِلَى اللَّهِ وَأَنَّ الْمُسْرِفِينَ هُمْ أَصْحَابُ النَّارِ','قطعاً آنچه مرا بسوی آن می‌خوانید، نه دعوت (و حاکمیّتی) در دنیا دارد و نه در آخرت؛ و تنها بازگشت ما در قیامت بسوی خداست؛ و مسرفان اهل آتشند!'];
ayat[4177]=[40,44,'فَسَتَذْكُرُونَ مَا أَقُولُ لَكُمْ ۚ وَأُفَوِّضُ أَمْرِي إِلَى اللَّهِ ۚ إِنَّ اللَّهَ بَصِيرٌ بِالْعِبَادِ','و بزودی آنچه را به شما می‌گویم به خاطر خواهید آورد! من کار خود را به خدا واگذارم که خداوند نسبت به بندگانش بیناست!»'];
ayat[4178]=[40,45,'فَوَقَاهُ اللَّهُ سَيِّئَاتِ مَا مَكَرُوا ۖ وَحَاقَ بِآلِ فِرْعَوْنَ سُوءُ الْعَذَابِ','خداوند او را از نقشه‌های سوء آنها نگه داشت، و عذاب شدید بر آل فرعون وارد شد!'];
ayat[4179]=[40,46,'النَّارُ يُعْرَضُونَ عَلَيْهَا غُدُوًّا وَعَشِيًّا ۖ وَيَوْمَ تَقُومُ السَّاعَةُ أَدْخِلُوا آلَ فِرْعَوْنَ أَشَدَّ الْعَذَابِ','عذاب آنها آتش است که هر صبح و شام بر آن عرضه می‌شوند؛ و روزی که قیامت برپا شود (می‌فرماید:) «آل فرعون را در سخت‌ترین عذابها وارد کنید!»'];
ayat[4180]=[40,47,'وَإِذْ يَتَحَاجُّونَ فِي النَّارِ فَيَقُولُ الضُّعَفَاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ أَنتُم مُّغْنُونَ عَنَّا نَصِيبًا مِّنَ النَّارِ','به خاطر بیاور هنگامی را که در آتش دوزخ با هم محاجّه می‌کنند؛ ضعیفان به مستکبران می‌گویند: «ما پیرو شما بودیم، آیا شما (امروز) سهمی از آتش را بجای ما پذیرا می‌شوید؟!»'];
ayat[4181]=[40,48,'قَالَ الَّذِينَ اسْتَكْبَرُوا إِنَّا كُلٌّ فِيهَا إِنَّ اللَّهَ قَدْ حَكَمَ بَيْنَ الْعِبَادِ','مستکبران می‌گویند: «ما همگی در آن هستیم، زیرا خداوند در میان بندگانش (بعدالت) حکم کرده است!»'];
ayat[4182]=[40,49,'وَقَالَ الَّذِينَ فِي النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًا مِّنَ الْعَذَابِ','و آنها که در آتشند به مأموران دوزخ می‌گویند: «از پروردگارتان بخواهید یک روز عذاب را از ما بردارد!»'];
ayat[4183]=[40,50,'قَالُوا أَوَلَمْ تَكُ تَأْتِيكُمْ رُسُلُكُم بِالْبَيِّنَاتِ ۖ قَالُوا بَلَىٰ ۚ قَالُوا فَادْعُوا ۗ وَمَا دُعَاءُ الْكَافِرِينَ إِلَّا فِي ضَلَالٍ','آنها می‌گویند: «آیا پیامبران شما دلایل روشن برایتان نیاوردند؟!» می‌گویند: «آری!» آنها می‌گویند: «پس هر چه می‌خواهید (خدا را) بخوانید؛ ولی دعای کافران (به جایی نمی‌رسد و) جز در ضلالت نیست!»'];
ayat[4184]=[40,51,'إِنَّا لَنَنصُرُ رُسُلَنَا وَالَّذِينَ آمَنُوا فِي الْحَيَاةِ الدُّنْيَا وَيَوْمَ يَقُومُ الْأَشْهَادُ','ما به یقین پیامبران خود و کسانی را که ایمان آورده‌اند، در زندگی دنیا و (در آخرت) روزی که گواهان به پا می‌خیزند یاری می‌دهیم!'];
ayat[4185]=[40,52,'يَوْمَ لَا يَنفَعُ الظَّالِمِينَ مَعْذِرَتُهُمْ ۖ وَلَهُمُ اللَّعْنَةُ وَلَهُمْ سُوءُ الدَّارِ','روزی که عذرخواهی ظالمان سودی به حالشان نمی‌بخشد؛ و لعنت خدا برای آنها، و خانه (و جایگاه) بد نیز برای آنان است.'];
ayat[4186]=[40,53,'وَلَقَدْ آتَيْنَا مُوسَى الْهُدَىٰ وَأَوْرَثْنَا بَنِي إِسْرَائِيلَ الْكِتَابَ','و ما به موسی هدایت بخشیدیم، و بنی اسرائیل را وارثان کتاب (تورات) قرار دادیم.'];
ayat[4187]=[40,54,'هُدًى وَذِكْرَىٰ لِأُولِي الْأَلْبَابِ','کتابی که مایه هدایت و تذکّر برای صاحبان عقل بود!'];
ayat[4188]=[40,55,'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَاسْتَغْفِرْ لِذَنبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْإِبْكَارِ','پس (ای پیامبر!) صبر و شکیبایی پیشه کن که وعده خدا حقّ است، و برای گناهت استغفار کن، و هر صبح و شام تسبیح و حمد پروردگارت را بجا آور!'];
ayat[4189]=[40,56,'إِنَّ الَّذِينَ يُجَادِلُونَ فِي آيَاتِ اللَّهِ بِغَيْرِ سُلْطَانٍ أَتَاهُمْ ۙ إِن فِي صُدُورِهِمْ إِلَّا كِبْرٌ مَّا هُم بِبَالِغِيهِ ۚ فَاسْتَعِذْ بِاللَّهِ ۖ إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ','کسانی که در آیات خداوند بدون دلیلی که برای آنها آمده باشد ستیزه‌جویی می‌کنند، در سینه‌هایشان فقط تکبّر (و غرور) است، و هرگز به خواسته خود نخواهند رسید، پس به خدا پناه بر که او شنوا و بیناست!'];
ayat[4190]=[40,57,'لَخَلْقُ السَّمَاوَاتِ وَالْأَرْضِ أَكْبَرُ مِنْ خَلْقِ النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','آفرینش آسمانها و زمین از آفرینش انسانها مهمتر است، ولی بیشتر مردم نمی‌دانند.'];
ayat[4191]=[40,58,'وَمَا يَسْتَوِي الْأَعْمَىٰ وَالْبَصِيرُ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَلَا الْمُسِيءُ ۚ قَلِيلًا مَّا تَتَذَكَّرُونَ','هرگز نابینا و بینا یکسان نیستند؛ همچنین کسانی که ایمان آورده، و اعمال صالح انجام داده‌اند با بدکاران یکسان نخواهند بود؛ امّا کمتر متذکّر می‌شوید!'];
ayat[4192]=[40,59,'إِنَّ السَّاعَةَ لَآتِيَةٌ لَّا رَيْبَ فِيهَا وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يُؤْمِنُونَ','روز قیامت به یقین آمدنی است، و شکّی در آن نیست؛ ولی اکثر مردم ایمان نمی آورند!'];
ayat[4193]=[40,60,'وَقَالَ رَبُّكُمُ ادْعُونِي أَسْتَجِبْ لَكُمْ ۚ إِنَّ الَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبَادَتِي سَيَدْخُلُونَ جَهَنَّمَ دَاخِرِينَ','پروردگار شما گفته است: «مرا بخوانید تا (دعای) شما را بپذیرم! کسانی که از عبادت من تکبّر می‌ورزند به زودی با ذلّت وارد دوزخ می‌شوند!»'];
ayat[4194]=[40,61,'اللَّهُ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَالنَّهَارَ مُبْصِرًا ۚ إِنَّ اللَّهَ لَذُو فَضْلٍ عَلَى النَّاسِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَشْكُرُونَ','خداوند کسی است که شب را برای شما آفرید تا در آن بیاسایید، و روز را روشنی‌بخش قرار داد؛ خداوند نسبت به مردم صاحب فضل و کرم است؛ ولی بیشتر مردم شکرگزاری نمی‌کنند!'];
ayat[4195]=[40,62,'ذَٰلِكُمُ اللَّهُ رَبُّكُمْ خَالِقُ كُلِّ شَيْءٍ لَّا إِلَٰهَ إِلَّا هُوَ ۖ فَأَنَّىٰ تُؤْفَكُونَ','این است خداوند، پروردگار شما که آفریننده همه چیز است؛ هیچ معبودی جز او نیست؛ با این حال چگونه از راه حق منحرف می‌شوید؟!'];
ayat[4196]=[40,63,'كَذَٰلِكَ يُؤْفَكُ الَّذِينَ كَانُوا بِآيَاتِ اللَّهِ يَجْحَدُونَ','اینچنین کسانی که آیات خدا را انکار می‌کردند (از راه راست) منحرف می‌شوند!'];
ayat[4197]=[40,64,'اللَّهُ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ قَرَارًا وَالسَّمَاءَ بِنَاءً وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَرَزَقَكُم مِّنَ الطَّيِّبَاتِ ۚ ذَٰلِكُمُ اللَّهُ رَبُّكُمْ ۖ فَتَبَارَكَ اللَّهُ رَبُّ الْعَالَمِينَ','خداوند کسی است که زمین را برای شما جایگاه امن و آرامش قرار داد و آسمان را همچون سقفی (بالای سرتان)؛ و شما را صورتگری کرد، و صورتتان را نیکو آفرید؛ و از چیزهایی پاکیزه به شما روزی داد؛ این است خداوند پروردگار شما! جاوید و پربرکت است خداوندی که پروردگار عالمیان است!'];
ayat[4198]=[40,65,'هُوَ الْحَيُّ لَا إِلَٰهَ إِلَّا هُوَ فَادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ ۗ الْحَمْدُ لِلَّهِ رَبِّ الْعَالَمِينَ','زنده (واقعی) اوست؛ معبودی جز او نیست؛ پس او را بخوانید در حالی که دین خود را برای او خالص کرده‌اید! ستایش مخصوص خداوندی است که پروردگار جهانیان است!'];
ayat[4199]=[40,66,'۞ قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِن دُونِ اللَّهِ لَمَّا جَاءَنِيَ الْبَيِّنَاتُ مِن رَّبِّي وَأُمِرْتُ أَنْ أُسْلِمَ لِرَبِّ الْعَالَمِينَ','بگو: «من نهی شده‌ام از اینکه معبودهایی را که شما غیر از خدا می‌خوانید بپرستم، چون دلایل روشن از جانب پروردگارم برای من آمده است؛ و مأمورم که تنها در برابر پروردگار عالمیان تسلیم باشم!»'];
ayat[4200]=[40,67,'هُوَ الَّذِي خَلَقَكُم مِّن تُرَابٍ ثُمَّ مِن نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ ثُمَّ لِتَكُونُوا شُيُوخًا ۚ وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبْلُ ۖ وَلِتَبْلُغُوا أَجَلًا مُّسَمًّى وَلَعَلَّكُمْ تَعْقِلُونَ','او کسی است که شما را از خاک آفرید، سپس از نطفه، سپس از علقه (خون بسته شده)، سپس شما را بصورت طفلی (از شکم مادر) بیرون می‌فرستد، بعد به مرحله کمال قوّت خود می‌رسید، و بعد از آن پیر می‌شوید و (در این میان) گروهی از شما پیش از رسیدن به این مرحله می‌میرند و در نهایت به سرآمد عمر خود می‌رسید؛ و شاید تعقّل کنید!'];
ayat[4201]=[40,68,'هُوَ الَّذِي يُحْيِي وَيُمِيتُ ۖ فَإِذَا قَضَىٰ أَمْرًا فَإِنَّمَا يَقُولُ لَهُ كُن فَيَكُونُ','او کسی است که زنده می‌کند و می‌میراند؛ و هنگامی که کاری را مقرّر کند، تنها به آن می‌گوید: «موجود باش!» بی‌درنگ موجود می‌شود!'];
ayat[4202]=[40,69,'أَلَمْ تَرَ إِلَى الَّذِينَ يُجَادِلُونَ فِي آيَاتِ اللَّهِ أَنَّىٰ يُصْرَفُونَ','آیا ندیدی کسانی را که در آیات خدا مجادله می‌کنند، چگونه از راه حقّ منحرف می شوند؟!'];
ayat[4203]=[40,70,'الَّذِينَ كَذَّبُوا بِالْكِتَابِ وَبِمَا أَرْسَلْنَا بِهِ رُسُلَنَا ۖ فَسَوْفَ يَعْلَمُونَ','همان کسانی که کتاب (آسمانی) و آنچه رسولان خود را بدان فرستاده‌ایم تکذیب کردند؛ امّا بزودی (نتیجه کار خود را) می‌دانند!'];
ayat[4204]=[40,71,'إِذِ الْأَغْلَالُ فِي أَعْنَاقِهِمْ وَالسَّلَاسِلُ يُسْحَبُونَ','در آن هنگام که غل و زنجیرها بر گردن آنان قرار گرفته و آنها را می‌کشند...'];
ayat[4205]=[40,72,'فِي الْحَمِيمِ ثُمَّ فِي النَّارِ يُسْجَرُونَ','و در آب جوشان وارد می‌کنند؛ سپس در آتش دوزخ افروخته می‌شوند!'];
ayat[4206]=[40,73,'ثُمَّ قِيلَ لَهُمْ أَيْنَ مَا كُنتُمْ تُشْرِكُونَ','سپس به آنها گفته می‌شود: «کجایند آنچه را همتای خدا قرار می‌دادید،'];
ayat[4207]=[40,74,'مِن دُونِ اللَّهِ ۖ قَالُوا ضَلُّوا عَنَّا بَل لَّمْ نَكُن نَّدْعُو مِن قَبْلُ شَيْئًا ۚ كَذَٰلِكَ يُضِلُّ اللَّهُ الْكَافِرِينَ','همان معبودهایی را که جز خدا پرستش می‌کردید؟!» آنها می‌گویند: «همه از نظر ما پنهان و گم شدند؛ بلکه ما اصلاً پیش از این چیزی را پرستش نمی‌کردیم»! این گونه خداوند کافران را گمراه می‌سازد!'];
ayat[4208]=[40,75,'ذَٰلِكُم بِمَا كُنتُمْ تَفْرَحُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنتُمْ تَمْرَحُونَ','این (عذاب) بخاطر آن است که بناحقّ در زمین شادی می‌کردید و از روی غرور و مستی به خوشحالی می‌پرداختید!'];
ayat[4209]=[40,76,'ادْخُلُوا أَبْوَابَ جَهَنَّمَ خَالِدِينَ فِيهَا ۖ فَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ','از درهای جهنّم وارد شوید و جاودانه در آن بمانید؛ و چه بد است جایگاه متکبّران!'];
ayat[4210]=[40,77,'فَاصْبِرْ إِنَّ وَعْدَ اللَّهِ حَقٌّ ۚ فَإِمَّا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا يُرْجَعُونَ','پس (ای پیامبر) صبر کن که وعده خدا حقّ است؛ و هرگاه قسمتی از مجازاتهایی را که به آنها وعده داده‌ایم در حال حیاتت به تو ارائه دهیم، یا تو را (پیش از آن) از دنیا ببریم (مهمّ نیست)؛ چرا که همه آنان را تنها بسوی ما باز می‌گردانند!'];
ayat[4211]=[40,78,'وَلَقَدْ أَرْسَلْنَا رُسُلًا مِّن قَبْلِكَ مِنْهُم مَّن قَصَصْنَا عَلَيْكَ وَمِنْهُم مَّن لَّمْ نَقْصُصْ عَلَيْكَ ۗ وَمَا كَانَ لِرَسُولٍ أَن يَأْتِيَ بِآيَةٍ إِلَّا بِإِذْنِ اللَّهِ ۚ فَإِذَا جَاءَ أَمْرُ اللَّهِ قُضِيَ بِالْحَقِّ وَخَسِرَ هُنَالِكَ الْمُبْطِلُونَ','ما پیش از تو رسولانی فرستادیم؛ سرگذشت گروهی از آنان را برای تو بازگفته، و گروهی را برای تو بازگو نکرده‌ایم؛ و هیچ پیامبری حق نداشت معجزه‌ای جز بفرمان خدا بیاورد و هنگامی که فرمان خداوند (برای مجازات آنها) صادر شود، بحق داوری خواهد شد؛ و آنجا اهل باطل زیان خواهند کرد!'];
ayat[4212]=[40,79,'اللَّهُ الَّذِي جَعَلَ لَكُمُ الْأَنْعَامَ لِتَرْكَبُوا مِنْهَا وَمِنْهَا تَأْكُلُونَ','خداوند کسی است که چهارپایان را برای شما آفرید تا بعضی را سوار شوید و از بعضی تغذیه کنید.'];
ayat[4213]=[40,80,'وَلَكُمْ فِيهَا مَنَافِعُ وَلِتَبْلُغُوا عَلَيْهَا حَاجَةً فِي صُدُورِكُمْ وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُونَ','و برای شما در آنها منافع بسیاری (جز اینها) است، تا بوسیله آنها به مقصدی که در دل دارید برسید؛ و بر آنها و بر کشتیها سوار می‌شوید.'];
ayat[4214]=[40,81,'وَيُرِيكُمْ آيَاتِهِ فَأَيَّ آيَاتِ اللَّهِ تُنكِرُونَ','او آیاتش را همواره به شما نشان می‌دهد؛ پس کدام یک از آیات او را انکار می‌کنید؟!'];
ayat[4215]=[40,82,'أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِن قَبْلِهِمْ ۚ كَانُوا أَكْثَرَ مِنْهُمْ وَأَشَدَّ قُوَّةً وَآثَارًا فِي الْأَرْضِ فَمَا أَغْنَىٰ عَنْهُم مَّا كَانُوا يَكْسِبُونَ','آیا روی زمین سیر نکردند تا ببینند عاقبت کسانی که پیش از آنها بودند چه شد؟! همانها که نفراتشان از اینها بیشتر، و نیرو و آثارشان در زمین فزونتر بود؛ امّا هرگز آنچه را به دست می‌آوردند نتوانست آنها را بی‌نیاز سازد (و عذاب الهی را از آنان دور کند)!'];
ayat[4216]=[40,83,'فَلَمَّا جَاءَتْهُمْ رُسُلُهُم بِالْبَيِّنَاتِ فَرِحُوا بِمَا عِندَهُم مِّنَ الْعِلْمِ وَحَاقَ بِهِم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','هنگامی که رسولانشان دلایل روشنی برای آنان آوردند، به دانشی که خود داشتند خوشحال بودند (و غیر آن را هیچ می‌شمردند)؛ ولی آنچه را (از عذاب) به تمسخر می‌گرفتند آنان را فراگرفت!'];
ayat[4217]=[40,84,'فَلَمَّا رَأَوْا بَأْسَنَا قَالُوا آمَنَّا بِاللَّهِ وَحْدَهُ وَكَفَرْنَا بِمَا كُنَّا بِهِ مُشْرِكِينَ','هنگامی که عذاب (شدید) ما را دیدند گفتند: «هم اکنون به خداوند یگانه ایمان آوردیم و به معبودهایی که همتای او می‌شمردیم کافر شدیم!»'];
ayat[4218]=[40,85,'فَلَمْ يَكُ يَنفَعُهُمْ إِيمَانُهُمْ لَمَّا رَأَوْا بَأْسَنَا ۖ سُنَّتَ اللَّهِ الَّتِي قَدْ خَلَتْ فِي عِبَادِهِ ۖ وَخَسِرَ هُنَالِكَ الْكَافِرُونَ','امّا هنگامی که عذاب ما را مشاهده کردند، ایمانشان برای آنها سودی نداشت! این سنّت خداوند است که همواره در میان بندگانش اجرا شده، و آنجا کافران زیانکار شدند!'];
ayat[4219]=[41,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم‌'];
ayat[4220]=[41,2,'تَنزِيلٌ مِّنَ الرَّحْمَٰنِ الرَّحِيمِ','این کتابی است که از سوی خداوند رحمان و رحیم نازل شده است؛'];
ayat[4221]=[41,3,'كِتَابٌ فُصِّلَتْ آيَاتُهُ قُرْآنًا عَرَبِيًّا لِّقَوْمٍ يَعْلَمُونَ','کتابی که آیاتش هر مطلبی را در جای خود بازگو کرده، در حالی که فصیح و گویاست برای جمعیّتی که آگاهند!'];
ayat[4222]=[41,4,'بَشِيرًا وَنَذِيرًا فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ','قرآنی که بشارت دهنده و بیم دهنده است؛ ولی بیشتر آنان روی گردان شدند؛ از این رو چیزی نمی‌شنوند!'];
ayat[4223]=[41,5,'وَقَالُوا قُلُوبُنَا فِي أَكِنَّةٍ مِّمَّا تَدْعُونَا إِلَيْهِ وَفِي آذَانِنَا وَقْرٌ وَمِن بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَاعْمَلْ إِنَّنَا عَامِلُونَ','آنها گفتند: «قلبهای ما نسبت به آنچه ما را به آن دعوت می‌کنی در پوششهایی قرار گرفته و در گوشهای ما سنگینی است، و میان ما و تو حجابی وجود دارد؛ پس تو بدنبال عمل خود باش، ما هم برای خود عمل می‌کنیم!»'];
ayat[4224]=[41,6,'قُلْ إِنَّمَا أَنَا بَشَرٌ مِّثْلُكُمْ يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمْ إِلَٰهٌ وَاحِدٌ فَاسْتَقِيمُوا إِلَيْهِ وَاسْتَغْفِرُوهُ ۗ وَوَيْلٌ لِّلْمُشْرِكِينَ','بگو: من فقط انسانی مثل شما هستم؛ این حقیقت بر من وحی می‌شود که معبود شما معبودی یگانه است؛ پس تمام توجّه خویش را به او کنید و از وی آمرزش طلبید؛ وای بر مشرکان!'];
ayat[4225]=[41,7,'الَّذِينَ لَا يُؤْتُونَ الزَّكَاةَ وَهُم بِالْآخِرَةِ هُمْ كَافِرُونَ','همانها که زکات را نمی‌پردازند، و آخرت را منکرند!'];
ayat[4226]=[41,8,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ','امّا کسانی که ایمان آورده و کارهای شایسته انجام دادند، پاداشی دائمی دارند!'];
ayat[4227]=[41,9,'۞ قُلْ أَئِنَّكُمْ لَتَكْفُرُونَ بِالَّذِي خَلَقَ الْأَرْضَ فِي يَوْمَيْنِ وَتَجْعَلُونَ لَهُ أَندَادًا ۚ ذَٰلِكَ رَبُّ الْعَالَمِينَ','بگو: آیا شما به آن کس که زمین را در دو روز آفرید کافر هستید و برای او همانندهایی قرارمی‌دهید؟! او پروردگار جهانیان است!'];
ayat[4228]=[41,10,'وَجَعَلَ فِيهَا رَوَاسِيَ مِن فَوْقِهَا وَبَارَكَ فِيهَا وَقَدَّرَ فِيهَا أَقْوَاتَهَا فِي أَرْبَعَةِ أَيَّامٍ سَوَاءً لِّلسَّائِلِينَ','او در زمین کوه‌های استواری قرار داد و برکاتی در آن آفرید و موادّ غذایی آن را مقدّر فرمود، - ینها همه در چهار روز بود- درست به اندازه نیاز تقاضا کنندگان!'];
ayat[4229]=[41,11,'ثُمَّ اسْتَوَىٰ إِلَى السَّمَاءِ وَهِيَ دُخَانٌ فَقَالَ لَهَا وَلِلْأَرْضِ ائْتِيَا طَوْعًا أَوْ كَرْهًا قَالَتَا أَتَيْنَا طَائِعِينَ','سپس به آفرینش آسمان پرداخت، در حالی که بصورت دود بود؛ به آن و به زمین دستور داد: «به وجود آیید (و شکل گیرید)، خواه از روی اطاعت و خواه اکراه!» آنها گفتند: «ما از روی طاعت می‌آییم (و شکل می‌گیریم)!»'];
ayat[4230]=[41,12,'فَقَضَاهُنَّ سَبْعَ سَمَاوَاتٍ فِي يَوْمَيْنِ وَأَوْحَىٰ فِي كُلِّ سَمَاءٍ أَمْرَهَا ۚ وَزَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَحِفْظًا ۚ ذَٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ','در این هنگام آنها را بصورت هفت آسمان در دو روز آفرید، و در هر آسمانی کار آن (آسمان) را وحی (و مقرّر) فرمود، و آسمان پایین را با چراغهایی [= ستارگان‌] زینت بخشیدیم، و (با شهابها از رخنه شیاطین) حفظ کردیم، این است تقدیر خداوند توانا و دانا!'];
ayat[4231]=[41,13,'فَإِنْ أَعْرَضُوا فَقُلْ أَنذَرْتُكُمْ صَاعِقَةً مِّثْلَ صَاعِقَةِ عَادٍ وَثَمُودَ','اگر آنها روی‌گردان شوند، بگو: من شما را از صاعقه‌ای همانند صاعقه عاد و ثمود می‌ترسانم!'];
ayat[4232]=[41,14,'إِذْ جَاءَتْهُمُ الرُّسُلُ مِن بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ أَلَّا تَعْبُدُوا إِلَّا اللَّهَ ۖ قَالُوا لَوْ شَاءَ رَبُّنَا لَأَنزَلَ مَلَائِكَةً فَإِنَّا بِمَا أُرْسِلْتُم بِهِ كَافِرُونَ','در آن هنگام که رسولان از پیش رو و پشت سر (و از هر سو) به سراغشان آمدند (و آنان را دعوت کردند) که جز خدا را نپرستید آنها گفتند: «اگر پروردگار ما می‌خواست فرشتگانی نازل می‌کرد؛ از این رو ما به آنچه شما مبعوث به آن هستید کافریم!»'];
ayat[4233]=[41,15,'فَأَمَّا عَادٌ فَاسْتَكْبَرُوا فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَقَالُوا مَنْ أَشَدُّ مِنَّا قُوَّةً ۖ أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةً ۖ وَكَانُوا بِآيَاتِنَا يَجْحَدُونَ','امّا قوم عاد بناحق در زمین تکبّر ورزیدند و گفتند: «چه کسی از ما نیرومندتر است؟!» آیا نمی‌دانستند خداوندی که آنان را آفریده از آنها قویتر است؟ و (به خاطر این پندار) پیوسته آیات ما را انکار می‌کردند.'];
ayat[4234]=[41,16,'فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِي أَيَّامٍ نَّحِسَاتٍ لِّنُذِيقَهُمْ عَذَابَ الْخِزْيِ فِي الْحَيَاةِ الدُّنْيَا ۖ وَلَعَذَابُ الْآخِرَةِ أَخْزَىٰ ۖ وَهُمْ لَا يُنصَرُونَ','سرانجام تندبادی شدید و هول‌انگیز و سرد و سخت در روزهایی شوم و پرغبار بر آنها فرستادیم تا عذاب خوارکننده را در زندگی دنیا به آنها بچشانیم؛ و عذاب آخرت از آن هم خوارکننده‌تر است، و (از هیچ طرف) یاری نمی‌شوند!'];
ayat[4235]=[41,17,'وَأَمَّا ثَمُودُ فَهَدَيْنَاهُمْ فَاسْتَحَبُّوا الْعَمَىٰ عَلَى الْهُدَىٰ فَأَخَذَتْهُمْ صَاعِقَةُ الْعَذَابِ الْهُونِ بِمَا كَانُوا يَكْسِبُونَ','امّا ثمود را هدایت کردیم، ولی آنها نابینایی را بر هدایت ترجیح دادند؛ به همین جهت صاعقه -آن عذاب خوارکننده- به خاطر اعمالی که انجام می‌دادند آنها را فروگرفت!'];
ayat[4236]=[41,18,'وَنَجَّيْنَا الَّذِينَ آمَنُوا وَكَانُوا يَتَّقُونَ','و کسانی را که ایمان آوردند و پرهیزگار بودند نجات بخشیدیم!'];
ayat[4237]=[41,19,'وَيَوْمَ يُحْشَرُ أَعْدَاءُ اللَّهِ إِلَى النَّارِ فَهُمْ يُوزَعُونَ','به خاطر بیاورید روزی را که دشمنان خدا را جمع کرده به سوی دوزخ می‌برند، و صفوف پیشین را نگه می‌دارند (تا صفهای بعد به آنها ملحق شوند!)'];
ayat[4238]=[41,20,'حَتَّىٰ إِذَا مَا جَاءُوهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَأَبْصَارُهُمْ وَجُلُودُهُم بِمَا كَانُوا يَعْمَلُونَ','وقتی به آن می‌رسند، گوشها و چشمها و پوستهای تنشان به آنچه می‌کردند گواهی می‌دهند.'];
ayat[4239]=[41,21,'وَقَالُوا لِجُلُودِهِمْ لِمَ شَهِدتُّمْ عَلَيْنَا ۖ قَالُوا أَنطَقَنَا اللَّهُ الَّذِي أَنطَقَ كُلَّ شَيْءٍ وَهُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍ وَإِلَيْهِ تُرْجَعُونَ','آنها به پوستهای تنشان می‌گویند: «چرا بر ضدّ ما گواهی دادید؟!» آنها جواب می‌دهند: «همان خدایی که هر موجودی را به نطق درآورده ما را گویا ساخته؛ و او شما را نخستین بار آفرید، و بازگشتتان بسوی اوست!'];
ayat[4240]=[41,22,'وَمَا كُنتُمْ تَسْتَتِرُونَ أَن يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَا أَبْصَارُكُمْ وَلَا جُلُودُكُمْ وَلَٰكِن ظَنَنتُمْ أَنَّ اللَّهَ لَا يَعْلَمُ كَثِيرًا مِّمَّا تَعْمَلُونَ','شما اگر گناهانتان را مخفی می‌کردید نه بخاطر این بود که از شهادت گوش و چشمها و پوستهای تنتان بیم داشتید، بلکه شما گمان می‌کردید که خداوند بسیاری از اعمالی را که انجام می‌دهید نمی‌داند!'];
ayat[4241]=[41,23,'وَذَٰلِكُمْ ظَنُّكُمُ الَّذِي ظَنَنتُم بِرَبِّكُمْ أَرْدَاكُمْ فَأَصْبَحْتُم مِّنَ الْخَاسِرِينَ','آری این گمان بدی بود که درباره پروردگارتان داشتید و همان موجب هلاکت شما گردید، و سرانجام از زیانکاران شدید!'];
ayat[4242]=[41,24,'فَإِن يَصْبِرُوا فَالنَّارُ مَثْوًى لَّهُمْ ۖ وَإِن يَسْتَعْتِبُوا فَمَا هُم مِّنَ الْمُعْتَبِينَ','اگر صبر کنند (یا نکنند، به هر حال) دوزخ جایگاه آنهاست؛ و اگر تقاضای عفو کنند، مورد عفو قرار نمی‌گیرند!'];
ayat[4243]=[41,25,'۞ وَقَيَّضْنَا لَهُمْ قُرَنَاءَ فَزَيَّنُوا لَهُم مَّا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ الْقَوْلُ فِي أُمَمٍ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ الْجِنِّ وَالْإِنسِ ۖ إِنَّهُمْ كَانُوا خَاسِرِينَ','ما برای آنها همنشینانی (زشت‌سیرت) قرار دادیم که زشتیها را از پیش رو و پشت سر آنها در نظرشان جلوه دادند؛ و فرمان الهی در باره آنان تحقق یافت و به سرنوشت اقوام گمراهی از جنّ و انس که قبل از آنها بودند گرفتار شدند؛ آنها مسلّماً زیانکار بودند!'];
ayat[4244]=[41,26,'وَقَالَ الَّذِينَ كَفَرُوا لَا تَسْمَعُوا لِهَٰذَا الْقُرْآنِ وَالْغَوْا فِيهِ لَعَلَّكُمْ تَغْلِبُونَ','کافران گفتند: «گوش به این قرآن فراندهید؛ و به هنگام تلاوت آن جنجال کنید، شاید پیروز شوید!»'];
ayat[4245]=[41,27,'فَلَنُذِيقَنَّ الَّذِينَ كَفَرُوا عَذَابًا شَدِيدًا وَلَنَجْزِيَنَّهُمْ أَسْوَأَ الَّذِي كَانُوا يَعْمَلُونَ','به یقین به کافران عذاب شدیدی می‌چشمانیم، و آنها را به بدترین اعمالی که انجام می دادند کیفر می‌دهیم!'];
ayat[4246]=[41,28,'ذَٰلِكَ جَزَاءُ أَعْدَاءِ اللَّهِ النَّارُ ۖ لَهُمْ فِيهَا دَارُ الْخُلْدِ ۖ جَزَاءً بِمَا كَانُوا بِآيَاتِنَا يَجْحَدُونَ','این آتش کیفر دشمنان خدا است، سرای جاویدشان در آن خواهد بود، کیفری است بخاطر اینکه آیات ما را انکار می‌کردند.'];
ayat[4247]=[41,29,'وَقَالَ الَّذِينَ كَفَرُوا رَبَّنَا أَرِنَا اللَّذَيْنِ أَضَلَّانَا مِنَ الْجِنِّ وَالْإِنسِ نَجْعَلْهُمَا تَحْتَ أَقْدَامِنَا لِيَكُونَا مِنَ الْأَسْفَلِينَ','کافران گفتند: «پروردگارا! آنهایی که از جنّ و انس ما را گمراه کردند به ما نشان ده تا زیر پای خود نهیم (و لگدمالشان کنیم) تا از پست ترین مردم باشند!»'];
ayat[4248]=[41,30,'إِنَّ الَّذِينَ قَالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقَامُوا تَتَنَزَّلُ عَلَيْهِمُ الْمَلَائِكَةُ أَلَّا تَخَافُوا وَلَا تَحْزَنُوا وَأَبْشِرُوا بِالْجَنَّةِ الَّتِي كُنتُمْ تُوعَدُونَ','به یقین کسانی که گفتند: «پروردگار ما خداوند یگانه است!» سپس استقامت کردند، فرشتگان بر آنان نازل می‌شوند که: «نترسید و غمگین مباشید، و بشارت باد بر شما به آن بهشتی که به شما وعده داده شده است!'];
ayat[4249]=[41,31,'نَحْنُ أَوْلِيَاؤُكُمْ فِي الْحَيَاةِ الدُّنْيَا وَفِي الْآخِرَةِ ۖ وَلَكُمْ فِيهَا مَا تَشْتَهِي أَنفُسُكُمْ وَلَكُمْ فِيهَا مَا تَدَّعُونَ','ما یاران و مددکاران شما در زندگی دنیا و آخرت هستیم؛ و برای شما هر چه دلتان بخواهد در بهشت فراهم است، و هر چه طلب کنید به شما داده می‌شود!'];
ayat[4250]=[41,32,'نُزُلًا مِّنْ غَفُورٍ رَّحِيمٍ','اینها وسیله پذیرایی از سوی خداوند غفور و رحیم است!»'];
ayat[4251]=[41,33,'وَمَنْ أَحْسَنُ قَوْلًا مِّمَّن دَعَا إِلَى اللَّهِ وَعَمِلَ صَالِحًا وَقَالَ إِنَّنِي مِنَ الْمُسْلِمِينَ','چه کسی خوش گفتارتر است از آن کس که دعوت به سوی خدا می‌کند و عمل صالح انجام می‌دهد و می‌گوید: «من از مسلمانانم»؟!'];
ayat[4252]=[41,34,'وَلَا تَسْتَوِي الْحَسَنَةُ وَلَا السَّيِّئَةُ ۚ ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ فَإِذَا الَّذِي بَيْنَكَ وَبَيْنَهُ عَدَاوَةٌ كَأَنَّهُ وَلِيٌّ حَمِيمٌ','هرگز نیکی و بدی یکسان نیست؛ بدی را با نیکی دفع کن، ناگاه (خواهی دید) همان کس که میان تو و او دشمنی است، گویی دوستی گرم و صمیمی است!'];
ayat[4253]=[41,35,'وَمَا يُلَقَّاهَا إِلَّا الَّذِينَ صَبَرُوا وَمَا يُلَقَّاهَا إِلَّا ذُو حَظٍّ عَظِيمٍ','امّا جز کسانی که دارای صبر و استقامتند به این مقام نمی‌رسند، و جز کسانی که بهره عظیمی (از ایمان و تقوا) دارند به آن نایل نمی‌گردند!'];
ayat[4254]=[41,36,'وَإِمَّا يَنزَغَنَّكَ مِنَ الشَّيْطَانِ نَزْغٌ فَاسْتَعِذْ بِاللَّهِ ۖ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ','و هرگاه وسوسه‌هایی از شیطان متوجّه تو گردد، از خدا پناه بخواه که او شنوده و داناست!'];
ayat[4255]=[41,37,'وَمِنْ آيَاتِهِ اللَّيْلُ وَالنَّهَارُ وَالشَّمْسُ وَالْقَمَرُ ۚ لَا تَسْجُدُوا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَاسْجُدُوا لِلَّهِ الَّذِي خَلَقَهُنَّ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ','و از نشانه‌های او، شب و روز و خورشید و ماه است؛ برای خورشید و ماه سجده نکنید، برای خدایی که آفریننده آنهاست سجده کنید اگر می‌خواهید او را بپرستید!'];
ayat[4256]=[41,38,'فَإِنِ اسْتَكْبَرُوا فَالَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُ بِاللَّيْلِ وَالنَّهَارِ وَهُمْ لَا يَسْأَمُونَ ۩','و اگر (از عبادت پروردگار) تکبّر کنند، کسانی که نزد پروردگار تو هستند شب و روز برای او تسبیح می‌گویند و خسته نمی‌شوند!'];
ayat[4257]=[41,39,'وَمِنْ آيَاتِهِ أَنَّكَ تَرَى الْأَرْضَ خَاشِعَةً فَإِذَا أَنزَلْنَا عَلَيْهَا الْمَاءَ اهْتَزَّتْ وَرَبَتْ ۚ إِنَّ الَّذِي أَحْيَاهَا لَمُحْيِي الْمَوْتَىٰ ۚ إِنَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','و از آیات او این است که زمین را خشک (و بی‌جان) می‌بینی، امّا هنگامی که آب (باران) بر آن می‌فرستیم به جنبش درمی‌آید و نموّ می‌کند؛ همان کسی که آن را زنده کرد، مردگان را نیز زنده می‌کند؛ او بر هر چیز تواناست!'];
ayat[4258]=[41,40,'إِنَّ الَّذِينَ يُلْحِدُونَ فِي آيَاتِنَا لَا يَخْفَوْنَ عَلَيْنَا ۗ أَفَمَن يُلْقَىٰ فِي النَّارِ خَيْرٌ أَم مَّن يَأْتِي آمِنًا يَوْمَ الْقِيَامَةِ ۚ اعْمَلُوا مَا شِئْتُمْ ۖ إِنَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','کسانی که آیات ما را تحریف می‌کنند بر ما پوشیده نخواهند بود! آیا کسی که در آتش افکنده می‌شود بهتر است یا کسی که در نهایت امن و امان در قیامت به عرصه محشر می‌آید؟! هر کاری می‌خواهید بکنید، او به آنچه انجام می‌دهید بیناست!'];
ayat[4259]=[41,41,'إِنَّ الَّذِينَ كَفَرُوا بِالذِّكْرِ لَمَّا جَاءَهُمْ ۖ وَإِنَّهُ لَكِتَابٌ عَزِيزٌ','کسانی که به این ذکر [= قرآن‌] هنگامی که به سراغشان آمد کافر شدند (نیز بر ما مخفی نخواهد ماند)! و این کتابی است قطعاً شکست ناپذیر...'];
ayat[4260]=[41,42,'لَّا يَأْتِيهِ الْبَاطِلُ مِن بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهِ ۖ تَنزِيلٌ مِّنْ حَكِيمٍ حَمِيدٍ','که هیچ گونه باطلی، نه از پیش رو و نه از پشت سر، به سراغ آن نمی‌آید؛ چرا که از سوی خداوند حکیم و شایسته ستایش نازل شده است!'];
ayat[4261]=[41,43,'مَّا يُقَالُ لَكَ إِلَّا مَا قَدْ قِيلَ لِلرُّسُلِ مِن قَبْلِكَ ۚ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ وَذُو عِقَابٍ أَلِيمٍ','آنچه به ناروا درباره تو می‌گویند همان است که درباره پیامبران قبل از تو نیز گفته شده؛ پروردگار تو دارای مغفرت و (هم) دارای مجازات دردناکی است!'];
ayat[4262]=[41,44,'وَلَوْ جَعَلْنَاهُ قُرْآنًا أَعْجَمِيًّا لَّقَالُوا لَوْلَا فُصِّلَتْ آيَاتُهُ ۖ أَأَعْجَمِيٌّ وَعَرَبِيٌّ ۗ قُلْ هُوَ لِلَّذِينَ آمَنُوا هُدًى وَشِفَاءٌ ۖ وَالَّذِينَ لَا يُؤْمِنُونَ فِي آذَانِهِمْ وَقْرٌ وَهُوَ عَلَيْهِمْ عَمًى ۚ أُولَٰئِكَ يُنَادَوْنَ مِن مَّكَانٍ بَعِيدٍ','هرگاه آن را قرآنی عجمی قرار می‌دادیم حتماً می‌گفتند: «چرا آیاتش روشن نیست؟! قرآن عجمی از پیغمبری عربی؟!» بگو: «این (کتاب) برای کسانی که ایمان آورده‌اند هدایت و درمان است؛ ولی کسانی که ایمان نمی‌آورند، در گوشهایشان سنگینی است و گویی نابینا هستند و آن را نمی‌بینند؛ آنها (همچون کسانی هستند که گوئی) از راه دور صدا زده می‌شوند!»'];
ayat[4263]=[41,45,'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ فَاخْتُلِفَ فِيهِ ۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِي شَكٍّ مِّنْهُ مُرِيبٍ','ما به موسی کتاب آسمانی دادیم؛ سپس در آن اختلاف شد؛ و اگر فرمانی از ناحیه پروردگارت در این باره صادر نشده بود (که باید به آنان مهلت داد تا اتمام حجّت شود)، در میان آنها داوری می‌شد (و به کیفر می‌رسیدند)؛ ولی آنها هنوز درباره آن شکّی تهمت‌انگیز دارند!'];
ayat[4264]=[41,46,'مَّنْ عَمِلَ صَالِحًا فَلِنَفْسِهِ ۖ وَمَنْ أَسَاءَ فَعَلَيْهَا ۗ وَمَا رَبُّكَ بِظَلَّامٍ لِّلْعَبِيدِ','کسی که عمل صالحی انجام دهد، سودش برای خود اوست؛ و هر کس بدی کند، به خویشتن بدی کرده است؛ و پروردگارت هرگز به بندگان ستم نمی‌کند!'];
ayat[4265]=[41,47,'۞ إِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِ ۚ وَمَا تَخْرُجُ مِن ثَمَرَاتٍ مِّنْ أَكْمَامِهَا وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِ ۚ وَيَوْمَ يُنَادِيهِمْ أَيْنَ شُرَكَائِي قَالُوا آذَنَّاكَ مَا مِنَّا مِن شَهِيدٍ','علم به قیامت (و لحظه وقوع آن) تنها به خدا بازمی‌گردد؛ هیچ میوه‌ای از غلاف خود خارج نمی‌شود، و هیچ زنی باردار نمی‌گردد و وضع حمل نمی‌کند مگر به علم او؛ و آن روز که آنها را ندا می‌دهد (و می‌گوید:) کجایند شریکانی که برای من می‌پنداشتید؟! می‌گویند: «(پروردگارا!) ما عرضه داشتیم که هیچ گواهی بر گفته خود نداریم!»'];
ayat[4266]=[41,48,'وَضَلَّ عَنْهُم مَّا كَانُوا يَدْعُونَ مِن قَبْلُ ۖ وَظَنُّوا مَا لَهُم مِّن مَّحِيصٍ','و همه معبودانی را که قبلاً می‌خواندند محو و گم می‌شوند؛ و می‌دانند هیچ گریزگاهی ندارند!'];
ayat[4267]=[41,49,'لَّا يَسْأَمُ الْإِنسَانُ مِن دُعَاءِ الْخَيْرِ وَإِن مَّسَّهُ الشَّرُّ فَيَئُوسٌ قَنُوطٌ','انسان هرگز از تقاضای نیکی (و نعمت) خسته نمی‌شود؛ و هرگاه شرّ و بدی به او رسد، بسیار مأیوس و نومید می‌گردد!'];
ayat[4268]=[41,50,'وَلَئِنْ أَذَقْنَاهُ رَحْمَةً مِّنَّا مِن بَعْدِ ضَرَّاءَ مَسَّتْهُ لَيَقُولَنَّ هَٰذَا لِي وَمَا أَظُنُّ السَّاعَةَ قَائِمَةً وَلَئِن رُّجِعْتُ إِلَىٰ رَبِّي إِنَّ لِي عِندَهُ لَلْحُسْنَىٰ ۚ فَلَنُنَبِّئَنَّ الَّذِينَ كَفَرُوا بِمَا عَمِلُوا وَلَنُذِيقَنَّهُم مِّنْ عَذَابٍ غَلِيظٍ','و هرگاه او را رحمتی از سوی خود بعد از ناراحتی که به او رسیده بچشانیم می‌گوید: «این بخاطر شایستگی و استحقاق من بوده، و گمان نمی‌کنم قیامت برپا شود؛ و (بفرض که قیامتی باشد،) هرگاه بسوی پروردگارم بازگردانده شوم، برای من نزد او پاداشهای نیک است. ما کافران را از اعمالی که انجام داده‌اند (بزودی) آگاه خواهیم کرد و از عذاب شدید به آنها می‌چشانیم.'];
ayat[4269]=[41,51,'وَإِذَا أَنْعَمْنَا عَلَى الْإِنسَانِ أَعْرَضَ وَنَأَىٰ بِجَانِبِهِ وَإِذَا مَسَّهُ الشَّرُّ فَذُو دُعَاءٍ عَرِيضٍ','و هرگاه به انسان (غافل و بی‌خبر) نعمت دهیم، روی می‌گرداند و به حال تکبّر از حق دور می‌شود؛ ولی هرگاه مختصر ناراحتی به او رسد، تقاضای فراوان و مستمرّ (برای بر طرف شدن آن) دارد!'];
ayat[4270]=[41,52,'قُلْ أَرَأَيْتُمْ إِن كَانَ مِنْ عِندِ اللَّهِ ثُمَّ كَفَرْتُم بِهِ مَنْ أَضَلُّ مِمَّنْ هُوَ فِي شِقَاقٍ بَعِيدٍ','بگو: «به من خبر دهید اگر این قرآن از سوی خداوند باشد و شما به آن کافر شوید، چه کسی گمراهتر خواهد بود از کسی که در مخالفت شدیدی قرار دارد؟!'];
ayat[4271]=[41,53,'سَنُرِيهِمْ آيَاتِنَا فِي الْآفَاقِ وَفِي أَنفُسِهِمْ حَتَّىٰ يَتَبَيَّنَ لَهُمْ أَنَّهُ الْحَقُّ ۗ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ','به زودی نشانه‌های خود را در اطراف جهان و در درون جانشان به آنها نشان می‌دهیم تا برای آنان آشکار گردد که او حق است؛ آیا کافی نیست که پروردگارت بر همه چیز شاهد و گواه است؟!'];
ayat[4272]=[41,54,'أَلَا إِنَّهُمْ فِي مِرْيَةٍ مِّن لِّقَاءِ رَبِّهِمْ ۗ أَلَا إِنَّهُ بِكُلِّ شَيْءٍ مُّحِيطٌ','آگاه باشید که آنها از لقای پروردگارشان در شکّ و تردیدند؛ و آگاه باشید که خداوند به همه چیز احاطه دارد!'];
ayat[4273]=[42,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم.'];
ayat[4274]=[42,2,'عسق','عسق.'];
ayat[4275]=[42,3,'كَذَٰلِكَ يُوحِي إِلَيْكَ وَإِلَى الَّذِينَ مِن قَبْلِكَ اللَّهُ الْعَزِيزُ الْحَكِيمُ','این گونه خداوند عزیز و حکیم به تو و پیامبرانی که پیش از تو بودند وحی می‌کند.'];
ayat[4276]=[42,4,'لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَلِيُّ الْعَظِيمُ','آنچه در آسمانها و آنچه در زمین است از آن اوست؛ و او بلندمرتبه و بزرگ است!'];
ayat[4277]=[42,5,'تَكَادُ السَّمَاوَاتُ يَتَفَطَّرْنَ مِن فَوْقِهِنَّ ۚ وَالْمَلَائِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُونَ لِمَن فِي الْأَرْضِ ۗ أَلَا إِنَّ اللَّهَ هُوَ الْغَفُورُ الرَّحِيمُ','نزدیک است آسمانها (بخاطر نسبتهای ناروای مشرکان) از بالا متلاشی شوند و فرشتگان پیوسته تسبیح و حمد پروردگارشان را بجا می‌آورند و برای کسانی که در زمین هستند استغفار می‌کنند؛ آگاه باشید خداوند آمرزنده و مهربان است.'];
ayat[4278]=[42,6,'وَالَّذِينَ اتَّخَذُوا مِن دُونِهِ أَوْلِيَاءَ اللَّهُ حَفِيظٌ عَلَيْهِمْ وَمَا أَنتَ عَلَيْهِم بِوَكِيلٍ','کسانی که غیر خدا را ولیّ خود انتخاب کردند، خداوند حساب همه اعمال آنها را نگه می‌دارد؛ و تو مأمور نیستی که آنان را مجبور به قبول حقّ کنی!'];
ayat[4279]=[42,7,'وَكَذَٰلِكَ أَوْحَيْنَا إِلَيْكَ قُرْآنًا عَرَبِيًّا لِّتُنذِرَ أُمَّ الْقُرَىٰ وَمَنْ حَوْلَهَا وَتُنذِرَ يَوْمَ الْجَمْعِ لَا رَيْبَ فِيهِ ۚ فَرِيقٌ فِي الْجَنَّةِ وَفَرِيقٌ فِي السَّعِيرِ','و این گونه قرآنی عربی [= فصیح و گویا] را بر تو وحی کردیم تا «امّ‌القری» [= مکّه‌] و مردم پیرامون آن را انذار کنی و آنها را از روزی که همه خلایق در آن روز جمع می‌شوند و شکّ و تردید در آن نیست بترسانی؛ گروهی در بهشتند و گروهی در آتش سوزان!'];
ayat[4280]=[42,8,'وَلَوْ شَاءَ اللَّهُ لَجَعَلَهُمْ أُمَّةً وَاحِدَةً وَلَٰكِن يُدْخِلُ مَن يَشَاءُ فِي رَحْمَتِهِ ۚ وَالظَّالِمُونَ مَا لَهُم مِّن وَلِيٍّ وَلَا نَصِيرٍ','و اگر خدا می‌خواست همه آنها را امّت واحدی قرار می‌داد (و به زور هدایت می‌کرد، ولی هدایت اجباری سودی ندارد)؛ امّا خداوند هر کس را بخواهد در رحمتش وارد می‌کند، و برای ظالمان ولیّ و یاوری نیست.'];
ayat[4281]=[42,9,'أَمِ اتَّخَذُوا مِن دُونِهِ أَوْلِيَاءَ ۖ فَاللَّهُ هُوَ الْوَلِيُّ وَهُوَ يُحْيِي الْمَوْتَىٰ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آیا آنها غیر از خدا را ولیّ خود برگزیدند؟! در حالی که «ولیّ» فقط خداوند است و اوست که مردگان را زنده می‌کند، و اوست که بر هر چیزی تواناست!'];
ayat[4282]=[42,10,'وَمَا اخْتَلَفْتُمْ فِيهِ مِن شَيْءٍ فَحُكْمُهُ إِلَى اللَّهِ ۚ ذَٰلِكُمُ اللَّهُ رَبِّي عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ','در هر چیز اختلاف کنید، داوریش با خداست؛ این است خداوند، پروردگار من، بر او توکّل کرده‌ام و به سوی او بازمی‌گردم!'];
ayat[4283]=[42,11,'فَاطِرُ السَّمَاوَاتِ وَالْأَرْضِ ۚ جَعَلَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَاجًا وَمِنَ الْأَنْعَامِ أَزْوَاجًا ۖ يَذْرَؤُكُمْ فِيهِ ۚ لَيْسَ كَمِثْلِهِ شَيْءٌ ۖ وَهُوَ السَّمِيعُ الْبَصِيرُ','او آفریننده آسمانها و زمین است و از جنس شما همسرانی برای شما قرار داد و جفتهایی از چهارپایان آفرید؛ و شما را به این وسیله [= بوسیله همسران‌] زیاد می کند؛ هیچ چیز همانند او نیست و او شنوا و بیناست!'];
ayat[4284]=[42,12,'لَهُ مَقَالِيدُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يَبْسُطُ الرِّزْقَ لِمَن يَشَاءُ وَيَقْدِرُ ۚ إِنَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','کلیدهای آسمانها و زمین از آن اوست؛ روزی را برای هر کس بخواهد گسترش می دهد یا محدود می‌سازد؛ او به همه چیز داناست.'];
ayat[4285]=[42,13,'۞ شَرَعَ لَكُم مِّنَ الدِّينِ مَا وَصَّىٰ بِهِ نُوحًا وَالَّذِي أَوْحَيْنَا إِلَيْكَ وَمَا وَصَّيْنَا بِهِ إِبْرَاهِيمَ وَمُوسَىٰ وَعِيسَىٰ ۖ أَنْ أَقِيمُوا الدِّينَ وَلَا تَتَفَرَّقُوا فِيهِ ۚ كَبُرَ عَلَى الْمُشْرِكِينَ مَا تَدْعُوهُمْ إِلَيْهِ ۚ اللَّهُ يَجْتَبِي إِلَيْهِ مَن يَشَاءُ وَيَهْدِي إِلَيْهِ مَن يُنِيبُ','آیینی را برای شما تشریع کرد که به نوح توصیه کرده بود؛ و آنچه را بر تو وحی فرستادیم و به ابراهیم و موسی و عیسی سفارش کردیم این بود که: دین را برپا دارید و در آن تفرقه ایجاد نکنید! و بر مشرکان گران است آنچه شما آنان را به سویش دعوت می کنید! خداوند هر کس را بخواهد برمی‌گزیند، و کسی را که به سوی او بازگردد هدایت می‌کند.'];
ayat[4286]=[42,14,'وَمَا تَفَرَّقُوا إِلَّا مِن بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ ۚ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ إِلَىٰ أَجَلٍ مُّسَمًّى لَّقُضِيَ بَيْنَهُمْ ۚ وَإِنَّ الَّذِينَ أُورِثُوا الْكِتَابَ مِن بَعْدِهِمْ لَفِي شَكٍّ مِّنْهُ مُرِيبٍ','آنان پراکنده نشدند مگر بعد از آنکه علم و آگاهی به سراغشان آمد؛ و این تفرقه جویی بخاطر انحراف از حق (و عداوت و حسد) بود؛ و اگر فرمانی از سوی پروردگارت صادر نشده بود که تا سرآمد معیّنی (زنده و آزاد) باشند، در میان آنها داوری می‌شد؛ و کسانی که بعد از آنها وارثان کتاب شدند نسبت به آن در شک و تردیدند، شکی همراه با بدبینی!'];
ayat[4287]=[42,15,'فَلِذَٰلِكَ فَادْعُ ۖ وَاسْتَقِمْ كَمَا أُمِرْتَ ۖ وَلَا تَتَّبِعْ أَهْوَاءَهُمْ ۖ وَقُلْ آمَنتُ بِمَا أَنزَلَ اللَّهُ مِن كِتَابٍ ۖ وَأُمِرْتُ لِأَعْدِلَ بَيْنَكُمُ ۖ اللَّهُ رَبُّنَا وَرَبُّكُمْ ۖ لَنَا أَعْمَالُنَا وَلَكُمْ أَعْمَالُكُمْ ۖ لَا حُجَّةَ بَيْنَنَا وَبَيْنَكُمُ ۖ اللَّهُ يَجْمَعُ بَيْنَنَا ۖ وَإِلَيْهِ الْمَصِيرُ','پس به همین خاطر تو نیز آنان را به سوی این آیین واحد الهی دعوت کن و آنچنان که مأمور شده‌ای استقامت نما، و از هوی و هوسهای آنان پیروی مکن، و بگو: «به هر کتابی که خدا نازل کرده ایمان آورده‌ام و مأمورم در میان شما عدالت کنم؛ خداوند پروردگار ما و شماست؛ نتیجه اعمال ما از آن ما است و نتیجه اعمال شما از آن شما، خصومت شخصی در میان ما نیست؛ و خداوند ما و شما را در یکجا جمع می‌کند، و بازگشت (همه) به سوی اوست!»'];
ayat[4288]=[42,16,'وَالَّذِينَ يُحَاجُّونَ فِي اللَّهِ مِن بَعْدِ مَا اسْتُجِيبَ لَهُ حُجَّتُهُمْ دَاحِضَةٌ عِندَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَلَهُمْ عَذَابٌ شَدِيدٌ','کسانی که (از روی لجاجت) درباره خدا بعد از پذیرفتن (و ایمان به) او، محاجّه می کنند، دلیلشان نزد پروردگارشان باطل و بی‌پایه است؛ و غضب بر آنهاست و عذابی شدید دارند.'];
ayat[4289]=[42,17,'اللَّهُ الَّذِي أَنزَلَ الْكِتَابَ بِالْحَقِّ وَالْمِيزَانَ ۗ وَمَا يُدْرِيكَ لَعَلَّ السَّاعَةَ قَرِيبٌ','خداوند کسی است که کتاب را بحق نازل کرد و میزان (سنجش حق و باطل و خبر قیامت) را نیز؛ تو چه می‌دانی شاید ساعت (قیام قیامت) نزدیک باشد!'];
ayat[4290]=[42,18,'يَسْتَعْجِلُ بِهَا الَّذِينَ لَا يُؤْمِنُونَ بِهَا ۖ وَالَّذِينَ آمَنُوا مُشْفِقُونَ مِنْهَا وَيَعْلَمُونَ أَنَّهَا الْحَقُّ ۗ أَلَا إِنَّ الَّذِينَ يُمَارُونَ فِي السَّاعَةِ لَفِي ضَلَالٍ بَعِيدٍ','کسانی که به قیامت ایمان ندارند درباره آن شتاب می‌کنند؛ ولی آنها که ایمان آورده‌اند پیوسته از آن هراسانند، و می‌دانند آن حق است؛ آگاه باشید کسانی که در قیامت تردید می‌کنند، در گمراهی عمیقی هستند.'];
ayat[4291]=[42,19,'اللَّهُ لَطِيفٌ بِعِبَادِهِ يَرْزُقُ مَن يَشَاءُ ۖ وَهُوَ الْقَوِيُّ الْعَزِيزُ','خداوند نسبت به بندگانش لطف (و آگاهی) دارد؛ هر کس را بخواهد روزی می‌دهد و او قوّی و شکست‌ناپذیر است!'];
ayat[4292]=[42,20,'مَن كَانَ يُرِيدُ حَرْثَ الْآخِرَةِ نَزِدْ لَهُ فِي حَرْثِهِ ۖ وَمَن كَانَ يُرِيدُ حَرْثَ الدُّنْيَا نُؤْتِهِ مِنْهَا وَمَا لَهُ فِي الْآخِرَةِ مِن نَّصِيبٍ','کسی که زراعت آخرت را بخواهد، به کشت او برکت و افزایش می‌دهیم و بر محصولش می‌افزاییم؛ و کسی که فقط کشت دنیا را بطلبد، کمی از آن به او می‌دهیم امّا در آخرت هیچ بهره‌ای ندارد!'];
ayat[4293]=[42,21,'أَمْ لَهُمْ شُرَكَاءُ شَرَعُوا لَهُم مِّنَ الدِّينِ مَا لَمْ يَأْذَن بِهِ اللَّهُ ۚ وَلَوْلَا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْ ۗ وَإِنَّ الظَّالِمِينَ لَهُمْ عَذَابٌ أَلِيمٌ','آیا معبودانی دارند که بی‌اذن خداوند آیینی برای آنها ساخته‌اند؟! اگر مهلت معیّنی برای آنها نبود، در میانشان داوری می‌شد (و دستور عذاب صادر می‌گشت) و برای ظالمان عذاب دردناکی است!'];
ayat[4294]=[42,22,'تَرَى الظَّالِمِينَ مُشْفِقِينَ مِمَّا كَسَبُوا وَهُوَ وَاقِعٌ بِهِمْ ۗ وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فِي رَوْضَاتِ الْجَنَّاتِ ۖ لَهُم مَّا يَشَاءُونَ عِندَ رَبِّهِمْ ۚ ذَٰلِكَ هُوَ الْفَضْلُ الْكَبِيرُ','(در آن روز) ستمگران را می‌بینی که از اعمالی که انجام داده‌اند سخت بیمناکند، ولی آنها را فرامی‌گیرد! امّا کسانی که ایمان آورده و کارهای شایسته انجام داده‌اند در باغهای بهشتند و هر چه بخواهند نزد پروردگارشان برای آنها فراهم است؛ این است فضل (و بخشش) بزرگ!'];
ayat[4295]=[42,23,'ذَٰلِكَ الَّذِي يُبَشِّرُ اللَّهُ عِبَادَهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ ۗ قُل لَّا أَسْأَلُكُمْ عَلَيْهِ أَجْرًا إِلَّا الْمَوَدَّةَ فِي الْقُرْبَىٰ ۗ وَمَن يَقْتَرِفْ حَسَنَةً نَّزِدْ لَهُ فِيهَا حُسْنًا ۚ إِنَّ اللَّهَ غَفُورٌ شَكُورٌ','این همان چیزی است که خداوند بندگانش را که ایمان آورده و اعمال صالح انجام داده‌اند به آن نوید می‌دهد! بگو: «من هیچ پاداشی از شما بر رسالتم درخواست نمی‌کنم جز دوست‌داشتن نزدیکانم [= اهل بیتم‌]؛ و هر کس کار نیکی انجام دهد، بر نیکی‌اش می‌افزاییم؛ چرا که خداوند آمرزنده و سپاسگزار است.'];
ayat[4296]=[42,24,'أَمْ يَقُولُونَ افْتَرَىٰ عَلَى اللَّهِ كَذِبًا ۖ فَإِن يَشَإِ اللَّهُ يَخْتِمْ عَلَىٰ قَلْبِكَ ۗ وَيَمْحُ اللَّهُ الْبَاطِلَ وَيُحِقُّ الْحَقَّ بِكَلِمَاتِهِ ۚ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','آیا می‌گویند: «او بر خدا دروغ بسته است»؟! در حالی که اگر خدا بخواهد بر قلب تو مُهر می‌نهد (و اگر خلاف بگوئی قدرت اظهار این آیات را از تو می‌گیرد) و باطل را محو می‌کند و حقّ را بفرمانش پابرجا می‌سازد؛ چرا که او از آنچه درون سینه‌هاست آگاه است.'];
ayat[4297]=[42,25,'وَهُوَ الَّذِي يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهِ وَيَعْفُو عَنِ السَّيِّئَاتِ وَيَعْلَمُ مَا تَفْعَلُونَ','او کسی است که توبه را از بندگانش می‌پذیرد و بدیها را می‌بخشد، و آنچه را انجام می‌دهید می‌داند.'];
ayat[4298]=[42,26,'وَيَسْتَجِيبُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَيَزِيدُهُم مِّن فَضْلِهِ ۚ وَالْكَافِرُونَ لَهُمْ عَذَابٌ شَدِيدٌ','و درخواست کسانی را که ایمان آورده و کارهای نیک انجام داده‌اند می‌پذیرد و از فضل خود بر آنها می‌افزاید؛ امّا برای کافران عذاب شدیدی است!'];
ayat[4299]=[42,27,'۞ وَلَوْ بَسَطَ اللَّهُ الرِّزْقَ لِعِبَادِهِ لَبَغَوْا فِي الْأَرْضِ وَلَٰكِن يُنَزِّلُ بِقَدَرٍ مَّا يَشَاءُ ۚ إِنَّهُ بِعِبَادِهِ خَبِيرٌ بَصِيرٌ','هرگاه خداوند روزی را برای بندگانش وسعت بخشد، در زمین طغیان و ستم می‌کنند؛ از این‌رو بمقداری که می‌خواهد (و مصلحت می‌داند) نازل می‌کند، که نسبه به بندگانش آگاه و بیناست!'];
ayat[4300]=[42,28,'وَهُوَ الَّذِي يُنَزِّلُ الْغَيْثَ مِن بَعْدِ مَا قَنَطُوا وَيَنشُرُ رَحْمَتَهُ ۚ وَهُوَ الْوَلِيُّ الْحَمِيدُ','او کسی است که باران سودمند را پس از آنکه مأیوس شدند نازل می‌کند و رحمت خویش را می‌گستراند؛ و او ولیّ و (سرپرست) و ستوده است!'];
ayat[4301]=[42,29,'وَمِنْ آيَاتِهِ خَلْقُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَثَّ فِيهِمَا مِن دَابَّةٍ ۚ وَهُوَ عَلَىٰ جَمْعِهِمْ إِذَا يَشَاءُ قَدِيرٌ','و از آیات اوست آفرینش آسمانها و زمین و آنچه از جنبندگان در آنها منتشر نموده؛ و او هرگاه بخواهد بر جمع آنها تواناست!'];
ayat[4302]=[42,30,'وَمَا أَصَابَكُم مِّن مُّصِيبَةٍ فَبِمَا كَسَبَتْ أَيْدِيكُمْ وَيَعْفُو عَن كَثِيرٍ','هر مصیبتی به شما رسد بخاطر اعمالی است که انجام داده‌اید، و بسیاری را نیز عفو می‌کند!'];
ayat[4303]=[42,31,'وَمَا أَنتُم بِمُعْجِزِينَ فِي الْأَرْضِ ۖ وَمَا لَكُم مِّن دُونِ اللَّهِ مِن وَلِيٍّ وَلَا نَصِيرٍ','و شما هرگز نمی‌توانید در زمین از قدرت خداوند فرار کنید؛ و غیر از خدا هیچ ولیّ و یاوری برای شما نیست.'];
ayat[4304]=[42,32,'وَمِنْ آيَاتِهِ الْجَوَارِ فِي الْبَحْرِ كَالْأَعْلَامِ','از نشانه‌های او کشتیهایی است که در دریا همچون کوه‌ها به نظر می‌رسند!'];
ayat[4305]=[42,33,'إِن يَشَأْ يُسْكِنِ الرِّيحَ فَيَظْلَلْنَ رَوَاكِدَ عَلَىٰ ظَهْرِهِ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ','اگر او اراده کند، باد را ساکن می‌سازد تا آنها بر پشت دریا بی‌حرکت بمانند؛ در این نشانه‌هایی است برای هر صبرکننده شکرگزار!'];
ayat[4306]=[42,34,'أَوْ يُوبِقْهُنَّ بِمَا كَسَبُوا وَيَعْفُ عَن كَثِيرٍ','یا اگر بخواهد آنها را بخاطر اعمالی که سرنشینانش مرتکب شده‌اند نابود می‌سازد؛ و در عین حال بسیاری را می‌بخشد.'];
ayat[4307]=[42,35,'وَيَعْلَمَ الَّذِينَ يُجَادِلُونَ فِي آيَاتِنَا مَا لَهُم مِّن مَّحِيصٍ','کسانی که در آیات ما مجادله می‌کنند بدانند هیچ گریزگاهی ندارند!'];
ayat[4308]=[42,36,'فَمَا أُوتِيتُم مِّن شَيْءٍ فَمَتَاعُ الْحَيَاةِ الدُّنْيَا ۖ وَمَا عِندَ اللَّهِ خَيْرٌ وَأَبْقَىٰ لِلَّذِينَ آمَنُوا وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ','آنچه به شما عطا شده متاع زودگذر زندگی دنیاست، و آنچه نزد خداست برای کسانی که ایمان آورده و بر پروردگارشان توکّل می‌کنند بهتر و پایدارتر است.'];
ayat[4309]=[42,37,'وَالَّذِينَ يَجْتَنِبُونَ كَبَائِرَ الْإِثْمِ وَالْفَوَاحِشَ وَإِذَا مَا غَضِبُوا هُمْ يَغْفِرُونَ','همان کسانی که از گناهان بزرگ و اعمال زشت اجتناب می‌ورزند، و هنگامی که خشمگین شوند عفو می‌کنند.'];
ayat[4310]=[42,38,'وَالَّذِينَ اسْتَجَابُوا لِرَبِّهِمْ وَأَقَامُوا الصَّلَاةَ وَأَمْرُهُمْ شُورَىٰ بَيْنَهُمْ وَمِمَّا رَزَقْنَاهُمْ يُنفِقُونَ','و کسانی که دعوت پروردگارشان را اجابت کرده و نماز را برپا می‌دارند و کارهایشان به صورت مشورت در میان آنهاست و از آنچه به آنها روزی داده‌ایم انفاق می‌کنند،'];
ayat[4311]=[42,39,'وَالَّذِينَ إِذَا أَصَابَهُمُ الْبَغْيُ هُمْ يَنتَصِرُونَ','و کسانی که هرگاه ستمی به آنها رسد، (تسلیم ظلم نمی‌شوند و) یاری می‌طلبند!'];
ayat[4312]=[42,40,'وَجَزَاءُ سَيِّئَةٍ سَيِّئَةٌ مِّثْلُهَا ۖ فَمَنْ عَفَا وَأَصْلَحَ فَأَجْرُهُ عَلَى اللَّهِ ۚ إِنَّهُ لَا يُحِبُّ الظَّالِمِينَ','کیفر بدی، مجازاتی است همانند آن؛ و هر کس عفو و اصلاح کند، پاداش او با خداست؛ خداوند ظالمان را دوست ندارد!'];
ayat[4313]=[42,41,'وَلَمَنِ انتَصَرَ بَعْدَ ظُلْمِهِ فَأُولَٰئِكَ مَا عَلَيْهِم مِّن سَبِيلٍ','و کسی که بعد از مظلوم‌شدن یاری طلبد، ایرادی بر او نیست؛'];
ayat[4314]=[42,42,'إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَظْلِمُونَ النَّاسَ وَيَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ ۚ أُولَٰئِكَ لَهُمْ عَذَابٌ أَلِيمٌ','ایراد و مجازات بر کسانی است که به مردم ستم می‌کنند و در زمین بناحق ظلم روا می‌دارند؛ برای آنان عذاب دردناکی است!'];
ayat[4315]=[42,43,'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنْ عَزْمِ الْأُمُورِ','امّا کسانی که شکیبایی و عفو کنند، این از کارهای پرارزش است!'];
ayat[4316]=[42,44,'وَمَن يُضْلِلِ اللَّهُ فَمَا لَهُ مِن وَلِيٍّ مِّن بَعْدِهِ ۗ وَتَرَى الظَّالِمِينَ لَمَّا رَأَوُا الْعَذَابَ يَقُولُونَ هَلْ إِلَىٰ مَرَدٍّ مِّن سَبِيلٍ','کسی را که خدا گمراه کند، ولیّ و یاوری جز او نخواهد داشت؛ و ظالمان را (روز قیامت) می‌بینی هنگامی که عذاب الهی را مشاهده می‌کنند میگویند: «آیا راهی به سوی بازگشت (و جبران) وجود دارد؟!»'];
ayat[4317]=[42,45,'وَتَرَاهُمْ يُعْرَضُونَ عَلَيْهَا خَاشِعِينَ مِنَ الذُّلِّ يَنظُرُونَ مِن طَرْفٍ خَفِيٍّ ۗ وَقَالَ الَّذِينَ آمَنُوا إِنَّ الْخَاسِرِينَ الَّذِينَ خَسِرُوا أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ الْقِيَامَةِ ۗ أَلَا إِنَّ الظَّالِمِينَ فِي عَذَابٍ مُّقِيمٍ','و آنها را می‌بینی که بر آتش عرضه می‌شوند در حالی که از شدّت مذلّت خاشعند و زیر چشمی (به آن) نگاه می‌کنند؛ و کسانی که ایمان آورده‌اند می‌گویند: «زیانکاران واقعی آنانند که خود و خانواده خویش را روز قیامت از دست داده‌اند؛ آگاه باشید که ظالمان (آن روز) در عذاب دائمند!»'];
ayat[4318]=[42,46,'وَمَا كَانَ لَهُم مِّنْ أَوْلِيَاءَ يَنصُرُونَهُم مِّن دُونِ اللَّهِ ۗ وَمَن يُضْلِلِ اللَّهُ فَمَا لَهُ مِن سَبِيلٍ','آنها جز خدا اولیا و یاورانی ندارند که یاریشان کنند؛ و هر کس را خدا گمراه سازد، هیچ راه نجاتی برای او نیست!'];
ayat[4319]=[42,47,'اسْتَجِيبُوا لِرَبِّكُم مِّن قَبْلِ أَن يَأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهُ مِنَ اللَّهِ ۚ مَا لَكُم مِّن مَّلْجَإٍ يَوْمَئِذٍ وَمَا لَكُم مِّن نَّكِيرٍ','اجابت کنید دعوت پروردگار خود را پیش از آنکه روزی فرا رسد که بازگشتی برای آن در برابر اراده خدا نیست؛ و در آن روز، نه پناهگاهی دارید و نه مدافعی!'];
ayat[4320]=[42,48,'فَإِنْ أَعْرَضُوا فَمَا أَرْسَلْنَاكَ عَلَيْهِمْ حَفِيظًا ۖ إِنْ عَلَيْكَ إِلَّا الْبَلَاغُ ۗ وَإِنَّا إِذَا أَذَقْنَا الْإِنسَانَ مِنَّا رَحْمَةً فَرِحَ بِهَا ۖ وَإِن تُصِبْهُمْ سَيِّئَةٌ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَإِنَّ الْإِنسَانَ كَفُورٌ','و اگر روی‌گردان شوند (غمگین مباش)، ما تو را حافظ آنان (و مأمور اجبارشان) قرار نداده‌ایم؛ وظیف‌ه تو تنها ابلاغ رسالت است! و هنگامی که ما رحمتی از سوی خود به انسان بچشانیم به آن دلخوش می‌شود، و اگر بلایی بخاطر اعمالی که انجام داده‌اند به آنها رسد (به کفران می‌پردازند)، چرا که انسان بسیار کفران‌کننده است!'];
ayat[4321]=[42,49,'لِّلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ يَخْلُقُ مَا يَشَاءُ ۚ يَهَبُ لِمَن يَشَاءُ إِنَاثًا وَيَهَبُ لِمَن يَشَاءُ الذُّكُورَ','مالکیّت و حاکمیّت آسمانها و زمین از آن خداست؛ هر چه را بخواهد می‌آفریند؛ به هر کس اراده کند دختر می‌بخشد و به هر کس بخواهد پسر،'];
ayat[4322]=[42,50,'أَوْ يُزَوِّجُهُمْ ذُكْرَانًا وَإِنَاثًا ۖ وَيَجْعَلُ مَن يَشَاءُ عَقِيمًا ۚ إِنَّهُ عَلِيمٌ قَدِيرٌ','یا (اگر بخواهد) پسر و دختر -هر دو- را برای آنان جمع میکند و هر کس را بخواهد عقیم می‌گذارد؛ زیرا که او دانا و قادر است.'];
ayat[4323]=[42,51,'۞ وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ اللَّهُ إِلَّا وَحْيًا أَوْ مِن وَرَاءِ حِجَابٍ أَوْ يُرْسِلَ رَسُولًا فَيُوحِيَ بِإِذْنِهِ مَا يَشَاءُ ۚ إِنَّهُ عَلِيٌّ حَكِيمٌ','و شایسته هیچ انسانی نیست که خدا با او سخن گوید، مگر از راه وحی یا از پشت حجاب، یا رسولی می‌فرستد و بفرمان او آنچه را بخواهد وحی می‌کند؛ چرا که او بلندمقام و حکیم است!'];
ayat[4324]=[42,52,'وَكَذَٰلِكَ أَوْحَيْنَا إِلَيْكَ رُوحًا مِّنْ أَمْرِنَا ۚ مَا كُنتَ تَدْرِي مَا الْكِتَابُ وَلَا الْإِيمَانُ وَلَٰكِن جَعَلْنَاهُ نُورًا نَّهْدِي بِهِ مَن نَّشَاءُ مِنْ عِبَادِنَا ۚ وَإِنَّكَ لَتَهْدِي إِلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','همان گونه (که بر پیامبران پیشین وحی فرستادیم) بر تو نیز روحی را بفرمان خود وحی کردیم؛ تو پیش از این نمی‌دانستی کتاب و ایمان چیست (و از محتوای قرآن آگاه نبودی)؛ ولی ما آن را نوری قرار دادیم که بوسیله آن هر کس از بندگان خویش را بخواهیم هدایت می‌کنیم؛ و تو مسلّماً به سوی راه راست هدایت می‌کنی.'];
ayat[4325]=[42,53,'صِرَاطِ اللَّهِ الَّذِي لَهُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۗ أَلَا إِلَى اللَّهِ تَصِيرُ الْأُمُورُ','راه خداوندی که تمامی آنچه در آسمانها و آنچه در زمین است از آن اوست؛ آگاه باشید که همه کارها تنها بسوی خدا بازمی‌گردد!'];
ayat[4326]=[43,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم.'];
ayat[4327]=[43,2,'وَالْكِتَابِ الْمُبِينِ','سوگند به کتاب مبین (و روشنگر)،'];
ayat[4328]=[43,3,'إِنَّا جَعَلْنَاهُ قُرْآنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُونَ','که ما آن را قرآنی فصیح و عربی قرار دادیم، شاید شما (آن را) درک کنید!'];
ayat[4329]=[43,4,'وَإِنَّهُ فِي أُمِّ الْكِتَابِ لَدَيْنَا لَعَلِيٌّ حَكِيمٌ','و آن در «امّ‌الکتاب» [= لوح محفوظ] نزد ما بلندپایه و استوار است!'];
ayat[4330]=[43,5,'أَفَنَضْرِبُ عَنكُمُ الذِّكْرَ صَفْحًا أَن كُنتُمْ قَوْمًا مُّسْرِفِينَ','آیا این ذکر [= قرآن‌] را از شما بازگیریم بخاطر اینکه قومی اسرافکارید؟!'];
ayat[4331]=[43,6,'وَكَمْ أَرْسَلْنَا مِن نَّبِيٍّ فِي الْأَوَّلِينَ','چه بسیار پیامبرانی که (برای هدایت) در میان اقوام پیشین فرستادیم؛'];
ayat[4332]=[43,7,'وَمَا يَأْتِيهِم مِّن نَّبِيٍّ إِلَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','ولی هیچ پیامبری به سوی آنها نمی‌آمد مگر اینکه او را استهزا می‌کردند.'];
ayat[4333]=[43,8,'فَأَهْلَكْنَا أَشَدَّ مِنْهُم بَطْشًا وَمَضَىٰ مَثَلُ الْأَوَّلِينَ','ولی ما کسانی را که نیرومندتر از آنها بودند هلاک کردیم، و داستان پیشینیان گذشت.'];
ayat[4334]=[43,9,'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ الْعَزِيزُ الْعَلِيمُ','هر گاه از آنان بپرسی: «چه کسی آسمانها و زمین را آفریده است؟» مسلّماً می‌گویند: «خداوند قادر و دانا آنها را آفریده است»!'];
ayat[4335]=[43,10,'الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْدًا وَجَعَلَ لَكُمْ فِيهَا سُبُلًا لَّعَلَّكُمْ تَهْتَدُونَ','همان کسی که زمین را محل آرامش شما قرار داد، و برای شما در آن راه‌هایی آفرید باشد، که هدایت شوید (و به مقصد برسید)!'];
ayat[4336]=[43,11,'وَالَّذِي نَزَّلَ مِنَ السَّمَاءِ مَاءً بِقَدَرٍ فَأَنشَرْنَا بِهِ بَلْدَةً مَّيْتًا ۚ كَذَٰلِكَ تُخْرَجُونَ','همان کسی که از آسمان آبی فرستاد بمقدار معیّن، و بوسیله آن سرزمین مرده را حیات بخشیدیم؛ همین گونه (در قیامت از قبرها) شما را خارج می‌سازند!'];
ayat[4337]=[43,12,'وَالَّذِي خَلَقَ الْأَزْوَاجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ الْفُلْكِ وَالْأَنْعَامِ مَا تَرْكَبُونَ','و همان کسی که همه زوجها را آفرید، و برای شما از کشتیها و چهارپایان مرکبهایی قرارداد که بر آن سوار می‌شوید،'];
ayat[4338]=[43,13,'لِتَسْتَوُوا عَلَىٰ ظُهُورِهِ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ إِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا سُبْحَانَ الَّذِي سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ','تا بر پشت آنها بخوبی قرار گیرید؛ سپس هنگامی که بر آنها سوار شدید، نعمت پروردگارتان را متذکّر شوید و بگویید: «پاک و منزّه است کسی که این را مسخّر ما ساخت، وگرنه ما توانایی تسخیر آن را نداشتیم؛'];
ayat[4339]=[43,14,'وَإِنَّا إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ','و ما به سوی پروردگارمان بازمی‌گردیم!»'];
ayat[4340]=[43,15,'وَجَعَلُوا لَهُ مِنْ عِبَادِهِ جُزْءًا ۚ إِنَّ الْإِنسَانَ لَكَفُورٌ مُّبِينٌ','آنها برای خداوند از میان بندگانش جزئی قرار دادند (و ملائکه را دختران خدا خواندند)؛ انسان کفران‌کننده آشکاری است!'];
ayat[4341]=[43,16,'أَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَنَاتٍ وَأَصْفَاكُم بِالْبَنِينَ','آیا از میان مخلوقاتش دختران را برای خود انتخاب کرده و پسران را برای شما برگزیده است؟!'];
ayat[4342]=[43,17,'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحْمَٰنِ مَثَلًا ظَلَّ وَجْهُهُ مُسْوَدًّا وَهُوَ كَظِيمٌ','در حالی که هرگاه یکی از آنها را به همان چیزی که برای خداوند رحمان شبیه قرار داده [= به تولّد دختر] بشارت دهند، صورتش (از فرط ناراحتی) سیاه می‌شود و خشمگین می‌گردد!'];
ayat[4343]=[43,18,'أَوَمَن يُنَشَّأُ فِي الْحِلْيَةِ وَهُوَ فِي الْخِصَامِ غَيْرُ مُبِينٍ','آیا کسی را که در لابلای زینتها پرورش می‌یابد و به هنگام جدال قادر به تبیین مقصود خود نیست (فرزند خدا می‌خوانید)؟!'];
ayat[4344]=[43,19,'وَجَعَلُوا الْمَلَائِكَةَ الَّذِينَ هُمْ عِبَادُ الرَّحْمَٰنِ إِنَاثًا ۚ أَشَهِدُوا خَلْقَهُمْ ۚ سَتُكْتَبُ شَهَادَتُهُمْ وَيُسْأَلُونَ','آنها فرشتگان را که بندگان خداوند رحمانند مؤنّث پنداشتند؛ آیا شاهد آفرینش آنها بوده اند؟! گواهی آنان نوشته می‌شود و (از آن) بازخواست خواهند شد!'];
ayat[4345]=[43,20,'وَقَالُوا لَوْ شَاءَ الرَّحْمَٰنُ مَا عَبَدْنَاهُم ۗ مَّا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَخْرُصُونَ','آنان گفتند: «اگر خداوند رحمان می‌خواست ما آنها را پرستش نمی‌کردیم!» ولی به این امر هیچ گونه علم و یقین ندارند و جز دروغ چیزی نمی‌گویند!'];
ayat[4346]=[43,21,'أَمْ آتَيْنَاهُمْ كِتَابًا مِّن قَبْلِهِ فَهُم بِهِ مُسْتَمْسِكُونَ','یا اینکه ما کتابی پیش از این به آنان داده‌ایم و آنها به آن تمسّک می‌جویند؟!'];
ayat[4347]=[43,22,'بَلْ قَالُوا إِنَّا وَجَدْنَا آبَاءَنَا عَلَىٰ أُمَّةٍ وَإِنَّا عَلَىٰ آثَارِهِم مُّهْتَدُونَ','بلکه آنها می‌گویند: «ما نیاکان خود را بر آئینی یافتیم، و ما نیز به پیروی آنان هدایت یافته‌ایم.»'];
ayat[4348]=[43,23,'وَكَذَٰلِكَ مَا أَرْسَلْنَا مِن قَبْلِكَ فِي قَرْيَةٍ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا وَجَدْنَا آبَاءَنَا عَلَىٰ أُمَّةٍ وَإِنَّا عَلَىٰ آثَارِهِم مُّقْتَدُونَ','و این‌گونه در هیچ شهر و دیاری پیش از تو پیامبر انذارکننده‌ای نفرستادیم مگر اینکه ثروتمندان مست و مغرور آن گفتند: «ما پدران خود را بر آئینی یافتیم و به آثار آنان اقتدا می‌کنیم.»'];
ayat[4349]=[43,24,'۞ قَالَ أَوَلَوْ جِئْتُكُم بِأَهْدَىٰ مِمَّا وَجَدتُّمْ عَلَيْهِ آبَاءَكُمْ ۖ قَالُوا إِنَّا بِمَا أُرْسِلْتُم بِهِ كَافِرُونَ','(پیامبرشان) گفت: «آیا اگر من آیینی هدایت‌بخش‌تر از آنچه پدرانتان را بر آن یافتید آورده باشم (باز هم انکار می‌کنید)؟!» گفتند: «(آری،) ما به آنچه شما به آن فرستاده شده‌اید کافریم!»'];
ayat[4350]=[43,25,'فَانتَقَمْنَا مِنْهُمْ ۖ فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ','به همین جهت از آنها انتقام گرفتیم؛ بنگر پایان کار تکذیب‌کنندگان چگونه بود!'];
ayat[4351]=[43,26,'وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ وَقَوْمِهِ إِنَّنِي بَرَاءٌ مِّمَّا تَعْبُدُونَ','و به خاطر بیاور هنگامی را که ابراهیم به پدرش [= عمویش آزر] و قومش گفت: «من از آنچه شما می‌پرستید بیزارم.'];
ayat[4352]=[43,27,'إِلَّا الَّذِي فَطَرَنِي فَإِنَّهُ سَيَهْدِينِ','مگر آن کسی که مرا آفریده، که او هدایتم خواهد کرد!»'];
ayat[4353]=[43,28,'وَجَعَلَهَا كَلِمَةً بَاقِيَةً فِي عَقِبِهِ لَعَلَّهُمْ يَرْجِعُونَ','او کلمه توحید را کلمه پاینده‌ای در نسلهای بعد از خود قرار داد، شاید به سوی خدا باز گردند!'];
ayat[4354]=[43,29,'بَلْ مَتَّعْتُ هَٰؤُلَاءِ وَآبَاءَهُمْ حَتَّىٰ جَاءَهُمُ الْحَقُّ وَرَسُولٌ مُّبِينٌ','ولی من این گروه و پدرانشان را از مواهب دنیا بهره‌مند ساختم تا حق و فرستاده آشکار (الهی) به سراغشان آمد؛'];
ayat[4355]=[43,30,'وَلَمَّا جَاءَهُمُ الْحَقُّ قَالُوا هَٰذَا سِحْرٌ وَإِنَّا بِهِ كَافِرُونَ','هنگامی که حق به سراغشان آمد؛ گفتند: «این سحر است، و ما نسبت به آن کافریم»!'];
ayat[4356]=[43,31,'وَقَالُوا لَوْلَا نُزِّلَ هَٰذَا الْقُرْآنُ عَلَىٰ رَجُلٍ مِّنَ الْقَرْيَتَيْنِ عَظِيمٍ','و گفتند: «چرا این قرآن بر مرد بزرگ (و ثروتمندی) از این دو شهر (مکه و طائف) ناز ل نشده است؟!»'];
ayat[4357]=[43,32,'أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ ۚ نَحْنُ قَسَمْنَا بَيْنَهُم مَّعِيشَتَهُمْ فِي الْحَيَاةِ الدُّنْيَا ۚ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِّيَتَّخِذَ بَعْضُهُم بَعْضًا سُخْرِيًّا ۗ وَرَحْمَتُ رَبِّكَ خَيْرٌ مِّمَّا يَجْمَعُونَ','آیا آنان رحمت پروردگارت را تقسیم می‌کنند؟! ما معیشت آنها را در حیات دنیا در میانشان تقسیم کردیم و بعضی را بر بعضی برتری دادیم تا یکدیگر را مسخر کرده (و با هم تعاون نمایند)؛ و رحمت پروردگارت از تمام آنچه جمع‌آوری می‌کنند بهتر است!'];
ayat[4358]=[43,33,'وَلَوْلَا أَن يَكُونَ النَّاسُ أُمَّةً وَاحِدَةً لَّجَعَلْنَا لِمَن يَكْفُرُ بِالرَّحْمَٰنِ لِبُيُوتِهِمْ سُقُفًا مِّن فِضَّةٍ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَ','اگر (تمکّن کفّار از مواهب مادی) سبب نمی‌شد که همه مردم امت واحد (گمراهی) شوند، ما برای کسانی که به (خداوند) رحمان کافر می‌شدند خانه‌هایی قرار می‌دادیم با سقفهایی از نقره و نردبانهایی که از آن بالا روند،'];
ayat[4359]=[43,34,'وَلِبُيُوتِهِمْ أَبْوَابًا وَسُرُرًا عَلَيْهَا يَتَّكِئُونَ','و برای خانه‌هایشان درها و تختهایی (زیبا و نقره‌ای) قرار می‌دادیم که بر آن تکیه کنند؛'];
ayat[4360]=[43,35,'وَزُخْرُفًا ۚ وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَاعُ الْحَيَاةِ الدُّنْيَا ۚ وَالْآخِرَةُ عِندَ رَبِّكَ لِلْمُتَّقِينَ','و انواع زیورها؛ ولی تمام اینها بهره زندگی دنیاست، و آخرت نزد پروردگارت از آن پرهیزگاران است!'];
ayat[4361]=[43,36,'وَمَن يَعْشُ عَن ذِكْرِ الرَّحْمَٰنِ نُقَيِّضْ لَهُ شَيْطَانًا فَهُوَ لَهُ قَرِينٌ','و هر کس از یاد خدا روی‌گردان شود شیطان را به سراغ او میفرستیم پس همواره قرین اوست!'];
ayat[4362]=[43,37,'وَإِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ السَّبِيلِ وَيَحْسَبُونَ أَنَّهُم مُّهْتَدُونَ','و آنها [= شیاطین‌] این گروه را از راه خدا بازمی‌دارند، در حالی که گمان می‌کنند هدایت‌یافتگان حقیقی آنها هستند!'];
ayat[4363]=[43,38,'حَتَّىٰ إِذَا جَاءَنَا قَالَ يَا لَيْتَ بَيْنِي وَبَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَرِينُ','تا زمانی که (در قیامت) نزد ما حاضر شود می‌گوید: ای کاش میان من و تو فاصله مشرق و مغرب بود؛ چه بد همنشینی بودی!'];
ayat[4364]=[43,39,'وَلَن يَنفَعَكُمُ الْيَوْمَ إِذ ظَّلَمْتُمْ أَنَّكُمْ فِي الْعَذَابِ مُشْتَرِكُونَ','(ولی به آنها می‌گوییم:) هرگز این گفتگوها امروز به حال شما سودی ندارد، چرا که ظلم کردید؛ و همه در عذاب مشترکید!'];
ayat[4365]=[43,40,'أَفَأَنتَ تُسْمِعُ الصُّمَّ أَوْ تَهْدِي الْعُمْيَ وَمَن كَانَ فِي ضَلَالٍ مُّبِينٍ','(ای پیامبر!) آیا تو می‌توانی سخن خود را به گوش کران برسانی، یا کوران و کسانی را که در گمراهی آشکاری هستند هدایت کنی؟!'];
ayat[4366]=[43,41,'فَإِمَّا نَذْهَبَنَّ بِكَ فَإِنَّا مِنْهُم مُّنتَقِمُونَ','و هرگاه تو را از میان آنها ببریم، حتماً از آنان انتقام خواهیم گرفت؛'];
ayat[4367]=[43,42,'أَوْ نُرِيَنَّكَ الَّذِي وَعَدْنَاهُمْ فَإِنَّا عَلَيْهِم مُّقْتَدِرُونَ','یا اگر (زنده بمانی و) و آنچه را (از عذاب) به آنان وعده داده‌ایم به تو نشان دهیم، باز ما بر آنها مسلّطیم!'];
ayat[4368]=[43,43,'فَاسْتَمْسِكْ بِالَّذِي أُوحِيَ إِلَيْكَ ۖ إِنَّكَ عَلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','آنچه را بر تو وحی شده محکم بگیر که تو بر صراط مستقیمی.'];
ayat[4369]=[43,44,'وَإِنَّهُ لَذِكْرٌ لَّكَ وَلِقَوْمِكَ ۖ وَسَوْفَ تُسْأَلُونَ','و این مایه یادآوری (و عظمت) تو و قوم تو است و بزودی سؤال خواهید شد.'];
ayat[4370]=[43,45,'وَاسْأَلْ مَنْ أَرْسَلْنَا مِن قَبْلِكَ مِن رُّسُلِنَا أَجَعَلْنَا مِن دُونِ الرَّحْمَٰنِ آلِهَةً يُعْبَدُونَ','از رسولانی که پیش از تو فرستادیم بپرس: آیا غیر از خداوند رحمان معبودانی برای پرستش قرار دادیم؟!'];
ayat[4371]=[43,46,'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِآيَاتِنَا إِلَىٰ فِرْعَوْنَ وَمَلَئِهِ فَقَالَ إِنِّي رَسُولُ رَبِّ الْعَالَمِينَ','ما موسی را با آیات خود به سوی فرعون و درباریان او فرستادیم؛ (موسی به آنها) گفت: «من فرستاده پروردگار جهانیانم»'];
ayat[4372]=[43,47,'فَلَمَّا جَاءَهُم بِآيَاتِنَا إِذَا هُم مِّنْهَا يَضْحَكُونَ','ولی هنگامی که او آیات ما را برای آنها آورد، به آن می‌خندیدند!'];
ayat[4373]=[43,48,'وَمَا نُرِيهِم مِّنْ آيَةٍ إِلَّا هِيَ أَكْبَرُ مِنْ أُخْتِهَا ۖ وَأَخَذْنَاهُم بِالْعَذَابِ لَعَلَّهُمْ يَرْجِعُونَ','ما هیچ آیه (و معجزه‌ای) به آنان نشان نمی‌دادیم مگر اینکه از دیگری بزرگتر (و مهمتر) بود؛ و آنها را به (انواع) عذاب گرفتار کردیم شاید بازگردند!'];
ayat[4374]=[43,49,'وَقَالُوا يَا أَيُّهَ السَّاحِرُ ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهْتَدُونَ','(وقتی گرفتار بلا می‌شدند می) گفتند: «ای ساحر! پروردگارت را به عهدی که با تو کرده بخوان (تا ما را از این بلا برهاند) که ما هدایت خواهیم یافت (و ایمان می‌آوریم)!»'];
ayat[4375]=[43,50,'فَلَمَّا كَشَفْنَا عَنْهُمُ الْعَذَابَ إِذَا هُمْ يَنكُثُونَ','امّا هنگامی که عذاب را از آنها برطرف می‌ساختیم پیمان خود را می‌شکستند!'];
ayat[4376]=[43,51,'وَنَادَىٰ فِرْعَوْنُ فِي قَوْمِهِ قَالَ يَا قَوْمِ أَلَيْسَ لِي مُلْكُ مِصْرَ وَهَٰذِهِ الْأَنْهَارُ تَجْرِي مِن تَحْتِي ۖ أَفَلَا تُبْصِرُونَ','فرعون در میان قوم خود ندا داد و گفت: «ای قوم من! آیا حکومت مصر از آن من نیست، و این نهرها تحت فرمان من جریان ندارد؟ آیا نمی‌بینید؟'];
ayat[4377]=[43,52,'أَمْ أَنَا خَيْرٌ مِّنْ هَٰذَا الَّذِي هُوَ مَهِينٌ وَلَا يَكَادُ يُبِينُ','مگر نه این است که من از این مردی که از خانواده و طبقه پستی است و هرگز نمی‌تواند فصیح سخن بگوید برترم؟'];
ayat[4378]=[43,53,'فَلَوْلَا أُلْقِيَ عَلَيْهِ أَسْوِرَةٌ مِّن ذَهَبٍ أَوْ جَاءَ مَعَهُ الْمَلَائِكَةُ مُقْتَرِنِينَ','(اگر راست می‌گوید) چرا دستبندهای طلا به او داده نشده، یا اینکه چرا فرشتگان دوشادوش او نیامده‌اند (تا گفتارش را تأیید کنند)؟!'];
ayat[4379]=[43,54,'فَاسْتَخَفَّ قَوْمَهُ فَأَطَاعُوهُ ۚ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ','(فرعون) قوم خود را سبک شمرد، در نتیجه از او اطاعت کردند؛ آنان قومی فاسق بودند!'];
ayat[4380]=[43,55,'فَلَمَّا آسَفُونَا انتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَاهُمْ أَجْمَعِينَ','امّا هنگامی که ما را به خشم آوردند، از آنها انتقام گرفتیم و همه را غرق کردیم.'];
ayat[4381]=[43,56,'فَجَعَلْنَاهُمْ سَلَفًا وَمَثَلًا لِّلْآخِرِينَ','و آنها را پیشگامان (در عذاب) و عبرتی برای دیگران قرار دادیم.'];
ayat[4382]=[43,57,'۞ وَلَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلًا إِذَا قَوْمُكَ مِنْهُ يَصِدُّونَ','و هنگامی که درباره فرزند مریم مثلی زده شد، ناگهان قوم تو بخاطر آن داد و فریاد راه انداختند.'];
ayat[4383]=[43,58,'وَقَالُوا أَآلِهَتُنَا خَيْرٌ أَمْ هُوَ ۚ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلًا ۚ بَلْ هُمْ قَوْمٌ خَصِمُونَ','و گفتند: «آیا خدایان ما بهترند یا او [= مسیح‌]؟! (اگر معبودان ما در دوزخند، مسیح نیز در دوزخ است، چرا که معبود واقع شده)!» ولی آنها این مثل را جز از طریق جدال (و لجاج) برای تو نزدند؛ آنان گروهی کینه‌توز و پرخاشگرند!'];
ayat[4384]=[43,59,'إِنْ هُوَ إِلَّا عَبْدٌ أَنْعَمْنَا عَلَيْهِ وَجَعَلْنَاهُ مَثَلًا لِّبَنِي إِسْرَائِيلَ','مسیح فقط بنده‌ای بود که ما نعمت به او بخشیدیم و او را نمونه و الگوئی برای بنی اسرائیل قرار دادیم.'];
ayat[4385]=[43,60,'وَلَوْ نَشَاءُ لَجَعَلْنَا مِنكُم مَّلَائِكَةً فِي الْأَرْضِ يَخْلُفُونَ','و هرگاه بخواهیم به جای شما در زمین فرشتگانی قرار می‌دهیم که جانشین (شما) گردند!'];
ayat[4386]=[43,61,'وَإِنَّهُ لَعِلْمٌ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَاتَّبِعُونِ ۚ هَٰذَا صِرَاطٌ مُّسْتَقِيمٌ','و او [= مسیح‌] سبب آگاهی بر روز قیامت است. (زیرا نزول عیسی گواه نزدیکی رستاخیز است)؛ هرگز در آن تردید نکنید؛ و از من پیروی کنید که این راه مستقیم است!'];
ayat[4387]=[43,62,'وَلَا يَصُدَّنَّكُمُ الشَّيْطَانُ ۖ إِنَّهُ لَكُمْ عَدُوٌّ مُّبِينٌ','و شیطان شما را (از راه خدا) باز ندارد، که او دشمن آشکار شماست!'];
ayat[4388]=[43,63,'وَلَمَّا جَاءَ عِيسَىٰ بِالْبَيِّنَاتِ قَالَ قَدْ جِئْتُكُم بِالْحِكْمَةِ وَلِأُبَيِّنَ لَكُم بَعْضَ الَّذِي تَخْتَلِفُونَ فِيهِ ۖ فَاتَّقُوا اللَّهَ وَأَطِيعُونِ','و هنگامی که عیسی دلایل روشن (برای آنها) آورد گفت: «من برای شما حکمت آورده‌ام، و آمده‌ام تا برخی از آنچه را که در آن اختلاف دارید روشن کنم؛ پس تقوای الهی پیشه کنید و از من اطاعت نمایید!'];
ayat[4389]=[43,64,'إِنَّ اللَّهَ هُوَ رَبِّي وَرَبُّكُمْ فَاعْبُدُوهُ ۚ هَٰذَا صِرَاطٌ مُّسْتَقِيمٌ','خداوند پروردگار من و پروردگار شماست؛ (تنها) او را پرستش کنید که راه راست همین است!»'];
ayat[4390]=[43,65,'فَاخْتَلَفَ الْأَحْزَابُ مِن بَيْنِهِمْ ۖ فَوَيْلٌ لِّلَّذِينَ ظَلَمُوا مِنْ عَذَابِ يَوْمٍ أَلِيمٍ','ولی گروه‌هایی از میان آنها (درباره مسیح) اختلاف کردند (و بعضی او را خدا پنداشتند)؛ وای بر کسانی که ستم کردند از عذاب روزی دردناک!'];
ayat[4391]=[43,66,'هَلْ يَنظُرُونَ إِلَّا السَّاعَةَ أَن تَأْتِيَهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ','آیا جز این انتظار دارند که قیامت ناگهان به سراغشان آید در حالی که نمی‌فهمند؟'];
ayat[4392]=[43,67,'الْأَخِلَّاءُ يَوْمَئِذٍ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلَّا الْمُتَّقِينَ','دوستان در آن روز دشمن یکدیگرند، مگر پرهیزگاران!'];
ayat[4393]=[43,68,'يَا عِبَادِ لَا خَوْفٌ عَلَيْكُمُ الْيَوْمَ وَلَا أَنتُمْ تَحْزَنُونَ','ای بندگان من! امروز نه ترسی بر شماست و نه اندوهگین می‌شوید!'];
ayat[4394]=[43,69,'الَّذِينَ آمَنُوا بِآيَاتِنَا وَكَانُوا مُسْلِمِينَ','همان کسانی که به آیات ما ایمان آوردند و تسلیم بودند.'];
ayat[4395]=[43,70,'ادْخُلُوا الْجَنَّةَ أَنتُمْ وَأَزْوَاجُكُمْ تُحْبَرُونَ','(به آنها خطاب می‌شود:) شما و همسرانتان در نهایت شادمانی وارد بهشت شوید!'];
ayat[4396]=[43,71,'يُطَافُ عَلَيْهِم بِصِحَافٍ مِّن ذَهَبٍ وَأَكْوَابٍ ۖ وَفِيهَا مَا تَشْتَهِيهِ الْأَنفُسُ وَتَلَذُّ الْأَعْيُنُ ۖ وَأَنتُمْ فِيهَا خَالِدُونَ','(این در حالی است که) ظرفها (ی غذا) و جامهای طلائی (شراب طهور) را گرداگرد آنها می‌گردانند؛ و در آن (بهشت) آنچه دلها می‌خواهد و چشمها از آن لذت می‌برد موجود است؛ و شما همیشه در آن خواهید ماند!'];
ayat[4397]=[43,72,'وَتِلْكَ الْجَنَّةُ الَّتِي أُورِثْتُمُوهَا بِمَا كُنتُمْ تَعْمَلُونَ','این بهشتی است که شما وارث آن می‌شوید بخاطر اعمالی که انجام می‌دادید!'];
ayat[4398]=[43,73,'لَكُمْ فِيهَا فَاكِهَةٌ كَثِيرَةٌ مِّنْهَا تَأْكُلُونَ','و در آن برای شما میوه‌های فراوان است که از آن می‌خورید.'];
ayat[4399]=[43,74,'إِنَّ الْمُجْرِمِينَ فِي عَذَابِ جَهَنَّمَ خَالِدُونَ','(ولی) مجرمان در عذاب دوزخ جاودانه می‌مانند.'];
ayat[4400]=[43,75,'لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيهِ مُبْلِسُونَ','هرگز عذاب آنان تخفیف نمی‌یابد، و در آنجا از همه چیز مأیوسند.'];
ayat[4401]=[43,76,'وَمَا ظَلَمْنَاهُمْ وَلَٰكِن كَانُوا هُمُ الظَّالِمِينَ','ما به آنها ستم نکردیم، آنان خود ستمکار بودند!'];
ayat[4402]=[43,77,'وَنَادَوْا يَا مَالِكُ لِيَقْضِ عَلَيْنَا رَبُّكَ ۖ قَالَ إِنَّكُم مَّاكِثُونَ','آنها فریاد می‌کشند: «ای مالک دوزخ! (ای کاش) پروردگارت ما را بمیراند (تا آسوده شویم)!» می‌گوید: «شما در این جا ماندنی هستید!»'];
ayat[4403]=[43,78,'لَقَدْ جِئْنَاكُم بِالْحَقِّ وَلَٰكِنَّ أَكْثَرَكُمْ لِلْحَقِّ كَارِهُونَ','ما حق را برای شما آوردیم؛ ولی بیشتر شما از حق کراهت داشتید!'];
ayat[4404]=[43,79,'أَمْ أَبْرَمُوا أَمْرًا فَإِنَّا مُبْرِمُونَ','بلکه آنها تصمیم محکم بر توطئه گرفتند؛ ما نیز اراده محکمی (درباره آنها) داریم!'];
ayat[4405]=[43,80,'أَمْ يَحْسَبُونَ أَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوَاهُم ۚ بَلَىٰ وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُونَ','آیا آنان می‌پندارند که ما اسرار نهانی و سخنان درگوشی آنان را نمی‌شنویم؟ آری، رسولان (و فرشتگان) ما نزد آنها هستند و می‌نویسند!'];
ayat[4406]=[43,81,'قُلْ إِن كَانَ لِلرَّحْمَٰنِ وَلَدٌ فَأَنَا أَوَّلُ الْعَابِدِينَ','بگو: «اگر برای خداوند فرزندی بود، من نخستین پرستنده او بودم!»'];
ayat[4407]=[43,82,'سُبْحَانَ رَبِّ السَّمَاوَاتِ وَالْأَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُونَ','منزه است پروردگار آسمانها و زمین، پروردگار عرش، از توصیفی که آنها می‌کنند!'];
ayat[4408]=[43,83,'فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتَّىٰ يُلَاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ','آنان را به حال خود واگذار تا در باطل غوطه‌ور باشند و سرگرم بازی شوند تا روزی را که به آنها وعده داده شده است ملاقات کنند (و نتیجه کار خود را ببینند)!'];
ayat[4409]=[43,84,'وَهُوَ الَّذِي فِي السَّمَاءِ إِلَٰهٌ وَفِي الْأَرْضِ إِلَٰهٌ ۚ وَهُوَ الْحَكِيمُ الْعَلِيمُ','او کسی است که در آسمان معبود است و در زمین معبود؛ و او حکیم و علیم است!'];
ayat[4410]=[43,85,'وَتَبَارَكَ الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا وَعِندَهُ عِلْمُ السَّاعَةِ وَإِلَيْهِ تُرْجَعُونَ','پر برکت و پایدار است کسی که حکومت آسمانها و زمین و آنچه در میان آن دو است از آن اوست؛ و آگاهی از قیام قیامت نزد اوست و به سوی او بازگردانده می‌شوید!'];
ayat[4411]=[43,86,'وَلَا يَمْلِكُ الَّذِينَ يَدْعُونَ مِن دُونِهِ الشَّفَاعَةَ إِلَّا مَن شَهِدَ بِالْحَقِّ وَهُمْ يَعْلَمُونَ','کسانی را که غیر از او می‌خوانند قادر بر شفاعت نیستند؛ مگر آنها که شهادت به حق داده‌اند و بخوبی آگاهند!'];
ayat[4412]=[43,87,'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَهُمْ لَيَقُولُنَّ اللَّهُ ۖ فَأَنَّىٰ يُؤْفَكُونَ','و اگر از آنها بپرسی چه کسی آنان را آفریده، قطعاً می‌گویند: خدا؛ پس چگونه از عبادت او منحرف می‌شوند؟!'];
ayat[4413]=[43,88,'وَقِيلِهِ يَا رَبِّ إِنَّ هَٰؤُلَاءِ قَوْمٌ لَّا يُؤْمِنُونَ','آنها چگونه از شکایت پیامبر که می‌گوید: «پروردگارا! اینها قومی هستند که ایمان نمی آورند» (غافل می‌شوند؟!)'];
ayat[4414]=[43,89,'فَاصْفَحْ عَنْهُمْ وَقُلْ سَلَامٌ ۚ فَسَوْفَ يَعْلَمُونَ','پس (اکنون که چنین است) از آنان روی برگردان و بگو: «سلام بر شما»، امّا بزودی خواهند دانست!'];
ayat[4415]=[44,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم‌'];
ayat[4416]=[44,2,'وَالْكِتَابِ الْمُبِينِ','سوگند به این کتاب روشنگر،'];
ayat[4417]=[44,3,'إِنَّا أَنزَلْنَاهُ فِي لَيْلَةٍ مُّبَارَكَةٍ ۚ إِنَّا كُنَّا مُنذِرِينَ','که ما آن را در شبی پر برکت نازل کردیم؛ ما همواره انذارکننده بوده‌ایم!'];
ayat[4418]=[44,4,'فِيهَا يُفْرَقُ كُلُّ أَمْرٍ حَكِيمٍ','در آن شب هر امری بر اساس حکمت (الهی) تدبیر و جدا می‌گردد.'];
ayat[4419]=[44,5,'أَمْرًا مِّنْ عِندِنَا ۚ إِنَّا كُنَّا مُرْسِلِينَ','(آری، نزول قرآن) فرمانی بود از سوی ما؛ ما (محمد (ص) را) فرستادیم!'];
ayat[4420]=[44,6,'رَحْمَةً مِّن رَّبِّكَ ۚ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ','اینها همه بخاطر رحمتی است از سوی پروردگارت، که شنونده و داناست!'];
ayat[4421]=[44,7,'رَبِّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا ۖ إِن كُنتُم مُّوقِنِينَ','(همان) پروردگار آسمانها و زمین و آنچه در میان آنهاست، اگر اهل یقین هستید!'];
ayat[4422]=[44,8,'لَا إِلَٰهَ إِلَّا هُوَ يُحْيِي وَيُمِيتُ ۖ رَبُّكُمْ وَرَبُّ آبَائِكُمُ الْأَوَّلِينَ','هیچ معبودی جز او نیست؛ زنده می‌کند و می‌میراند؛ او پروردگار شما و پروردگار پدران نخستین شماست!'];
ayat[4423]=[44,9,'بَلْ هُمْ فِي شَكٍّ يَلْعَبُونَ','ولی آنها در شکند و (با حقایق) بازی می‌کنند.'];
ayat[4424]=[44,10,'فَارْتَقِبْ يَوْمَ تَأْتِي السَّمَاءُ بِدُخَانٍ مُّبِينٍ','پس منتظر روزی باش که آسمان دود آشکاری پدید آورد...'];
ayat[4425]=[44,11,'يَغْشَى النَّاسَ ۖ هَٰذَا عَذَابٌ أَلِيمٌ','که همه مردم را فرامی‌گیرد؛ این عذاب دردناکی است!'];
ayat[4426]=[44,12,'رَّبَّنَا اكْشِفْ عَنَّا الْعَذَابَ إِنَّا مُؤْمِنُونَ','(می‌گویند:) پروردگارا! عذاب را از ما برطرف کن که ایمان می‌آوریم.'];
ayat[4427]=[44,13,'أَنَّىٰ لَهُمُ الذِّكْرَىٰ وَقَدْ جَاءَهُمْ رَسُولٌ مُّبِينٌ','چگونه: و از کجا متذکر می‌شوند با اینکه رسول روشنگر (با معجزات و منطق روشن) به سراغشان آمد!'];
ayat[4428]=[44,14,'ثُمَّ تَوَلَّوْا عَنْهُ وَقَالُوا مُعَلَّمٌ مَّجْنُونٌ','سپس از او روی گردان شدند و گفتند: «او تعلیم یافته‌ای دیوانه است!»'];
ayat[4429]=[44,15,'إِنَّا كَاشِفُو الْعَذَابِ قَلِيلًا ۚ إِنَّكُمْ عَائِدُونَ','ما عذاب را کمی برطرف می‌سازیم، ولی باز به کارهای خود بازمی‌گردید!'];
ayat[4430]=[44,16,'يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرَىٰ إِنَّا مُنتَقِمُونَ','(ما از آنها انتقام می‌گیریم) در آن روز که آنها را با قدرت خواهیم گرفت؛ آری ما انتقام گیرنده‌ایم!'];
ayat[4431]=[44,17,'۞ وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَاءَهُمْ رَسُولٌ كَرِيمٌ','ما پیش از اینها قوم فرعون را آزمودیم و رسول بزرگواری به سراغشان آمد،'];
ayat[4432]=[44,18,'أَنْ أَدُّوا إِلَيَّ عِبَادَ اللَّهِ ۖ إِنِّي لَكُمْ رَسُولٌ أَمِينٌ','(و به آنان گفت: امور) بندگان خدا را به من واگذارید که من فرستاده امینی برای شما هستم!'];
ayat[4433]=[44,19,'وَأَن لَّا تَعْلُوا عَلَى اللَّهِ ۖ إِنِّي آتِيكُم بِسُلْطَانٍ مُّبِينٍ','و در برابر خداوند تکبر نورزید که من برای شما دلیل روشنی آورده‌ام!'];
ayat[4434]=[44,20,'وَإِنِّي عُذْتُ بِرَبِّي وَرَبِّكُمْ أَن تَرْجُمُونِ','و من به پروردگار خود و پروردگار شما پناه می‌برم از اینکه مرا متهم کنید!'];
ayat[4435]=[44,21,'وَإِن لَّمْ تُؤْمِنُوا لِي فَاعْتَزِلُونِ','و اگر به من ایمان نمی‌آورید، از من کناره‌گیری کنید (و مانع ایمان آوردن مردم نشوید)!'];
ayat[4436]=[44,22,'فَدَعَا رَبَّهُ أَنَّ هَٰؤُلَاءِ قَوْمٌ مُّجْرِمُونَ','(آنها هیچ یک از این پندها را نپذیرفتند، و موسی) به پیشگاه پروردگارش عرضه داشت: اینها قومی مجرمند!'];
ayat[4437]=[44,23,'فَأَسْرِ بِعِبَادِي لَيْلًا إِنَّكُم مُّتَّبَعُونَ','(به او دستور داده شد:) بندگان مرا شبانه حرکت ده که شما تعقیب می‌شوید!'];
ayat[4438]=[44,24,'وَاتْرُكِ الْبَحْرَ رَهْوًا ۖ إِنَّهُمْ جُندٌ مُّغْرَقُونَ','(هنگامی که از دریا گذشتید) دریا را آرام و گشاده بگذار (و بگذر) که آنها لشکری غرق‌شده خواهند بود!'];
ayat[4439]=[44,25,'كَمْ تَرَكُوا مِن جَنَّاتٍ وَعُيُونٍ','(سرانجام همگی نابود شدند و) چه بسیار باغها و چشمه‌ها که از خود به جای گذاشتند،'];
ayat[4440]=[44,26,'وَزُرُوعٍ وَمَقَامٍ كَرِيمٍ','و زراعتها و قصرهای زیبا و گرانقیمت،'];
ayat[4441]=[44,27,'وَنَعْمَةٍ كَانُوا فِيهَا فَاكِهِينَ','و نعمتهای فراوان دیگر که در آن (غرق) بودند!'];
ayat[4442]=[44,28,'كَذَٰلِكَ ۖ وَأَوْرَثْنَاهَا قَوْمًا آخَرِينَ','اینچنین بود ماجرای آنان! و ما (اموال و حکومت) اینها را میراث برای اقوام دیگری قرار دادیم!'];
ayat[4443]=[44,29,'فَمَا بَكَتْ عَلَيْهِمُ السَّمَاءُ وَالْأَرْضُ وَمَا كَانُوا مُنظَرِينَ','نه آسمان بر آنان گریست و نه زمین، و نه به آنها مهلتی داده شد!'];
ayat[4444]=[44,30,'وَلَقَدْ نَجَّيْنَا بَنِي إِسْرَائِيلَ مِنَ الْعَذَابِ الْمُهِينِ','ما بنی اسرائیل را از عذاب ذلّت‌بار رهایی بخشیدیم؛'];
ayat[4445]=[44,31,'مِن فِرْعَوْنَ ۚ إِنَّهُ كَانَ عَالِيًا مِّنَ الْمُسْرِفِينَ','از فرعون که مردی متکبر و از اسرافکاران بود!'];
ayat[4446]=[44,32,'وَلَقَدِ اخْتَرْنَاهُمْ عَلَىٰ عِلْمٍ عَلَى الْعَالَمِينَ','ما آنها را با علم (خویش) بر جهانیان برگزیدیم و برتری دادیم،'];
ayat[4447]=[44,33,'وَآتَيْنَاهُم مِّنَ الْآيَاتِ مَا فِيهِ بَلَاءٌ مُّبِينٌ','و آیاتی (از قدرت خویش) را به آنها دادیم که آزمایش آشکاری در آن بود (ولی آنان کفران کردند و مجازات شدند)!'];
ayat[4448]=[44,34,'إِنَّ هَٰؤُلَاءِ لَيَقُولُونَ','اینها [= مشرکان‌] می‌گویند:'];
ayat[4449]=[44,35,'إِنْ هِيَ إِلَّا مَوْتَتُنَا الْأُولَىٰ وَمَا نَحْنُ بِمُنشَرِينَ','«مرگ ما جز همان مرگ اول نیست و هرگز برانگیخته نخواهیم شد!'];
ayat[4450]=[44,36,'فَأْتُوا بِآبَائِنَا إِن كُنتُمْ صَادِقِينَ','اگر راست می‌گویید پدران ما را (زنده کنید و) بیاورید (تا گواهی دهند)!»'];
ayat[4451]=[44,37,'أَهُمْ خَيْرٌ أَمْ قَوْمُ تُبَّعٍ وَالَّذِينَ مِن قَبْلِهِمْ ۚ أَهْلَكْنَاهُمْ ۖ إِنَّهُمْ كَانُوا مُجْرِمِينَ','آیا آنان بهترند یا قوم «تبّع» و کسانی که پیش از آنها بودند؟! ما آنان را هلاک کردیم، چرا که مجرم بودند!'];
ayat[4452]=[44,38,'وَمَا خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا لَاعِبِينَ','ما آسمانها و زمین و آنچه را که در میان این دو است به بازی (و بی‌هدف) نیافریدیم!'];
ayat[4453]=[44,39,'مَا خَلَقْنَاهُمَا إِلَّا بِالْحَقِّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','ما آن دو را جز بحق نیافریدیم؛ ولی بیشتر آنان نمی‌دانند!'];
ayat[4454]=[44,40,'إِنَّ يَوْمَ الْفَصْلِ مِيقَاتُهُمْ أَجْمَعِينَ','روز جدایی (حق از باطل) وعده‌گاه همه آنهاست!'];
ayat[4455]=[44,41,'يَوْمَ لَا يُغْنِي مَوْلًى عَن مَّوْلًى شَيْئًا وَلَا هُمْ يُنصَرُونَ','روزی که هیچ دوستی کمترین کمکی به دوستش نمی‌کند، و از هیچ‌سو یاری نمی‌شوند؛'];
ayat[4456]=[44,42,'إِلَّا مَن رَّحِمَ اللَّهُ ۚ إِنَّهُ هُوَ الْعَزِيزُ الرَّحِيمُ','مگر کسی که خدا او را مورد رحمت قرار داده، چرا که او عزیز و رحیم است!'];
ayat[4457]=[44,43,'إِنَّ شَجَرَتَ الزَّقُّومِ','مسلّماً درخت زقّوم...'];
ayat[4458]=[44,44,'طَعَامُ الْأَثِيمِ','غذای گنهکاران است،'];
ayat[4459]=[44,45,'كَالْمُهْلِ يَغْلِي فِي الْبُطُونِ','همانند فلز گداخته در شکمها می‌جوشد؛'];
ayat[4460]=[44,46,'كَغَلْيِ الْحَمِيمِ','جوششی همچون آب سوزان!'];
ayat[4461]=[44,47,'خُذُوهُ فَاعْتِلُوهُ إِلَىٰ سَوَاءِ الْجَحِيمِ','(آنگاه به مأموران دوزخ خطاب می‌شود:) این کافر مجرم را بگیرید و به میان دوزخ پرتابش کنید!'];
ayat[4462]=[44,48,'ثُمَّ صُبُّوا فَوْقَ رَأْسِهِ مِنْ عَذَابِ الْحَمِيمِ','سپس بر سر او از عذاب جوشان بریزید!'];
ayat[4463]=[44,49,'ذُقْ إِنَّكَ أَنتَ الْعَزِيزُ الْكَرِيمُ','(به او گفته می‌شود:) بچش که (به پندار خود) بسیار قدرتمند و محترم بودی!'];
ayat[4464]=[44,50,'إِنَّ هَٰذَا مَا كُنتُم بِهِ تَمْتَرُونَ','این همان چیزی است که پیوسته در آن تردید می‌کردید!'];
ayat[4465]=[44,51,'إِنَّ الْمُتَّقِينَ فِي مَقَامٍ أَمِينٍ','(ولی) پرهیزگاران در جایگاه امنی قرار دارند،'];
ayat[4466]=[44,52,'فِي جَنَّاتٍ وَعُيُونٍ','در میان باغها و چشمه‌ها؛'];
ayat[4467]=[44,53,'يَلْبَسُونَ مِن سُندُسٍ وَإِسْتَبْرَقٍ مُّتَقَابِلِينَ','آنها لباسهایی از حریر نازک و ضخیم می‌پوشند و در مقابل یکدیگر می‌نشینند؛'];
ayat[4468]=[44,54,'كَذَٰلِكَ وَزَوَّجْنَاهُم بِحُورٍ عِينٍ','اینچنین‌اند بهشتیان؛ و آنها را با «حور العین» تزویج می‌کنیم!'];
ayat[4469]=[44,55,'يَدْعُونَ فِيهَا بِكُلِّ فَاكِهَةٍ آمِنِينَ','آنها در آنجا هر نوع میوه‌ای را بخواهند در اختیارشان قرارمی‌گیرد، و در نهایت امنیّت به سر می‌برند!'];
ayat[4470]=[44,56,'لَا يَذُوقُونَ فِيهَا الْمَوْتَ إِلَّا الْمَوْتَةَ الْأُولَىٰ ۖ وَوَقَاهُمْ عَذَابَ الْجَحِيمِ','هرگز مرگی جز همان مرگ اوّل (که در دنیا چشیده‌اند) نخواهند چشید، و خداوند آنها را از عذاب دوزخ حفظ می‌کند؛'];
ayat[4471]=[44,57,'فَضْلًا مِّن رَّبِّكَ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','این فضل و بخششی است از سوی پروردگارت، این همان رستگاری بزرگ است!'];
ayat[4472]=[44,58,'فَإِنَّمَا يَسَّرْنَاهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ','ما آن [= قرآن‌] را بر زبان تو آسان ساختیم، شاید آنان متذکّر شوند!'];
ayat[4473]=[44,59,'فَارْتَقِبْ إِنَّهُم مُّرْتَقِبُونَ','(امّا اگر نپذیرفتند) منتظر باش، آنها نیز منتظرند (تو منتظر پیروزی الهی و آنها منتظر عذاب و شکست)!'];
ayat[4474]=[45,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم‌'];
ayat[4475]=[45,2,'تَنزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ','این کتاب از سوی خداوند عزیز و حکیم نازل شده است!'];
ayat[4476]=[45,3,'إِنَّ فِي السَّمَاوَاتِ وَالْأَرْضِ لَآيَاتٍ لِّلْمُؤْمِنِينَ','بی‌شکّ در آسمانها و زمین نشانه‌های (فراوانی) برای مؤمنان وجود دارد؛'];
ayat[4477]=[45,4,'وَفِي خَلْقِكُمْ وَمَا يَبُثُّ مِن دَابَّةٍ آيَاتٌ لِّقَوْمٍ يُوقِنُونَ','و نیز در آفرینش شما و جنبندگانی که (در سراسر زمین) پراکنده ساخته، نشانه‌هایی است برای جمعیّتی که اهل یقینند.'];
ayat[4478]=[45,5,'وَاخْتِلَافِ اللَّيْلِ وَالنَّهَارِ وَمَا أَنزَلَ اللَّهُ مِنَ السَّمَاءِ مِن رِّزْقٍ فَأَحْيَا بِهِ الْأَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيفِ الرِّيَاحِ آيَاتٌ لِّقَوْمٍ يَعْقِلُونَ','و نیز در آمد و شد شب و روز، و رزق (و بارانی) که خداوند از آسمان نازل کرده و بوسیله آن زمین را بعد از مردنش حیات بخشیده و همچنین در وزش بادها، نشانه‌های روشنی است برای گروهی که اهل تفکّرند!'];
ayat[4479]=[45,6,'تِلْكَ آيَاتُ اللَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّ ۖ فَبِأَيِّ حَدِيثٍ بَعْدَ اللَّهِ وَآيَاتِهِ يُؤْمِنُونَ','اینها آیات خداوند است که ما آن را بحقّ بر تو تلاوت می‌کنیم؛ اگر آنها به این آیات ایمان نیاورند، به کدام سخن بعد از سخن خدا و آیاتش ایمان می‌آورند؟!'];
ayat[4480]=[45,7,'وَيْلٌ لِّكُلِّ أَفَّاكٍ أَثِيمٍ','وای بر هر دروغگوی گنهکار...'];
ayat[4481]=[45,8,'يَسْمَعُ آيَاتِ اللَّهِ تُتْلَىٰ عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًا كَأَن لَّمْ يَسْمَعْهَا ۖ فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ','که پیوسته آیات خدا را می‌شنود که بر او تلاوت می‌شود، امّا از روی تکبّر اصرار بر مخالفت دارد؛ گویی اصلاً آن را هیچ نشنیده است؛ چنین کسی را به عذابی دردناک بشارت ده!'];
ayat[4482]=[45,9,'وَإِذَا عَلِمَ مِنْ آيَاتِنَا شَيْئًا اتَّخَذَهَا هُزُوًا ۚ أُولَٰئِكَ لَهُمْ عَذَابٌ مُّهِينٌ','و هرگاه از بعضی آیات ما آگاه شود، آن را به باد استهزا می‌گیرد؛ برای آنان عذاب خوارکننده‌ای است!'];
ayat[4483]=[45,10,'مِّن وَرَائِهِمْ جَهَنَّمُ ۖ وَلَا يُغْنِي عَنْهُم مَّا كَسَبُوا شَيْئًا وَلَا مَا اتَّخَذُوا مِن دُونِ اللَّهِ أَوْلِيَاءَ ۖ وَلَهُمْ عَذَابٌ عَظِيمٌ','و پشت سرشان دوزخ است؛ و هرگز آنچه را به دست آورده‌اند آنها را (از عذاب الهی) رهایی نمی‌بخشد، و نه اولیایی که غیر از خدا برای خود برگزیدند (مایه نجاتشان خواهند بود)؛ و عذاب بزرگی برای آنهاست!'];
ayat[4484]=[45,11,'هَٰذَا هُدًى ۖ وَالَّذِينَ كَفَرُوا بِآيَاتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِّن رِّجْزٍ أَلِيمٌ','این (قرآن) مایه هدایت است، و کسانی که به آیات پروردگارشان کافر شدند، عذابی سخت و دردناک دارند!'];
ayat[4485]=[45,12,'۞ اللَّهُ الَّذِي سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ فِيهِ بِأَمْرِهِ وَلِتَبْتَغُوا مِن فَضْلِهِ وَلَعَلَّكُمْ تَشْكُرُونَ','خداوند همان کسی است که دریا را مسخّر شما کرد تا کشتیها بفرمانش در آن حرکت کنند و بتوانند از فضل او بهره گیرید، و شاید شکر نعمتهایش را بجا آورید!'];
ayat[4486]=[45,13,'وَسَخَّرَ لَكُم مَّا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ جَمِيعًا مِّنْهُ ۚ إِنَّ فِي ذَٰلِكَ لَآيَاتٍ لِّقَوْمٍ يَتَفَكَّرُونَ','او آنچه در آسمانها و آنچه در زمین است همه را از سوی خودش مسخّر شما ساخته؛ در این نشانه‌های (مهمّی) است برای کسانی که اندیشه می‌کنند!'];
ayat[4487]=[45,14,'قُل لِّلَّذِينَ آمَنُوا يَغْفِرُوا لِلَّذِينَ لَا يَرْجُونَ أَيَّامَ اللَّهِ لِيَجْزِيَ قَوْمًا بِمَا كَانُوا يَكْسِبُونَ','به مؤمنان بگو: «کسانی را که امید به ایّام اللّه [= روز رستاخیز] ندارند مورد عفو قرار دهند تا خداوند هر قومی را به اعمالی که انجام می‌دادند جزا دهد»!'];
ayat[4488]=[45,15,'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهِ ۖ وَمَنْ أَسَاءَ فَعَلَيْهَا ۖ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ','هر کس کار شایسته‌ای بجا آورد، برای خود بجا آورده است؛ و کسی که کار بد می‌کند، به زیان خود اوست؛ سپس همه شما به سوی پروردگارتان بازگردانده می‌شوید!'];
ayat[4489]=[45,16,'وَلَقَدْ آتَيْنَا بَنِي إِسْرَائِيلَ الْكِتَابَ وَالْحُكْمَ وَالنُّبُوَّةَ وَرَزَقْنَاهُم مِّنَ الطَّيِّبَاتِ وَفَضَّلْنَاهُمْ عَلَى الْعَالَمِينَ','ما بنی اسرائیل را کتاب (آسمانی) و حکومت و نبّوت بخشیدیم و از روزیهای پاکیزه به آنها عطا کردیم و آنان را بر جهانیان (و مردم عصر خویش) برتری بخشیدیم؛'];
ayat[4490]=[45,17,'وَآتَيْنَاهُم بَيِّنَاتٍ مِّنَ الْأَمْرِ ۖ فَمَا اخْتَلَفُوا إِلَّا مِن بَعْدِ مَا جَاءَهُمُ الْعِلْمُ بَغْيًا بَيْنَهُمْ ۚ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيَامَةِ فِيمَا كَانُوا فِيهِ يَخْتَلِفُونَ','و دلایل روشنی از امر نبّوت و شریعت در اختیارشان قرار دادیم؛ آنها اختلاف نکردند مگر بعد از علم و آگاهی؛ و این اختلاف بخاطر ستم و برتری‌جویی آنان بود؛ امّا پروردگارت روز قیامت در میان آنها در آنچه اختلاف داشتند داوری می‌کند.'];
ayat[4491]=[45,18,'ثُمَّ جَعَلْنَاكَ عَلَىٰ شَرِيعَةٍ مِّنَ الْأَمْرِ فَاتَّبِعْهَا وَلَا تَتَّبِعْ أَهْوَاءَ الَّذِينَ لَا يَعْلَمُونَ','سپس تو را بر شریعت و آیین حقّی قرار دادیم؛ از آن پیروی کن و از هوسهای کسانی که آگاهی ندارند پیروی مکن!'];
ayat[4492]=[45,19,'إِنَّهُمْ لَن يُغْنُوا عَنكَ مِنَ اللَّهِ شَيْئًا ۚ وَإِنَّ الظَّالِمِينَ بَعْضُهُمْ أَوْلِيَاءُ بَعْضٍ ۖ وَاللَّهُ وَلِيُّ الْمُتَّقِينَ','آنها هرگز نمی‌توانند تو را در برابر خداوند بی‌نیاز کنند (و از عذابش برهانند)؛ و ظالمان یار و یاور یکدیگرند، امّا خداوند یار و یاور پرهیزگاران است!'];
ayat[4493]=[45,20,'هَٰذَا بَصَائِرُ لِلنَّاسِ وَهُدًى وَرَحْمَةٌ لِّقَوْمٍ يُوقِنُونَ','این (قرآن و شریعت آسمانی) وسایل بینایی و مایه هدایت و رحمت است برای مردمی که (به آن) یقین دارند!'];
ayat[4494]=[45,21,'أَمْ حَسِبَ الَّذِينَ اجْتَرَحُوا السَّيِّئَاتِ أَن نَّجْعَلَهُمْ كَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ سَوَاءً مَّحْيَاهُمْ وَمَمَاتُهُمْ ۚ سَاءَ مَا يَحْكُمُونَ','آیا کسانی که مرتکب بدیها و گناهان شدند گمان کردند که ما آنها را همچون کسانی قرارمی‌دهیم که ایمان آورده و اعمال صالح انجام داده‌اند که حیات و مرگشان یکسان باشد؟! چه بد داوری می‌کنند!'];
ayat[4495]=[45,22,'وَخَلَقَ اللَّهُ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَلِتُجْزَىٰ كُلُّ نَفْسٍ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ','و خداوند آسمانها و زمین را بحقّ آفریده است تا هر کس در برابر اعمالی که انجام داده است جزا داده شود؛ و به آنها ستمی نخواهد شد!'];
ayat[4496]=[45,23,'أَفَرَأَيْتَ مَنِ اتَّخَذَ إِلَٰهَهُ هَوَاهُ وَأَضَلَّهُ اللَّهُ عَلَىٰ عِلْمٍ وَخَتَمَ عَلَىٰ سَمْعِهِ وَقَلْبِهِ وَجَعَلَ عَلَىٰ بَصَرِهِ غِشَاوَةً فَمَن يَهْدِيهِ مِن بَعْدِ اللَّهِ ۚ أَفَلَا تَذَكَّرُونَ','آیا دیدی کسی را که معبود خود را هوای نفس خویش قرار داده و خداوند او را با آگاهی (بر اینکه شایسته هدایت نیست) گمراه ساخته و بر گوش و قلبش مُهر زده و بر چشمش پرده‌ای افکنده است؟! با این حال چه کسی می‌تواند غیر از خدا او را هدایت کند؟! آیا متذکّر نمی‌شوید؟!'];
ayat[4497]=[45,24,'وَقَالُوا مَا هِيَ إِلَّا حَيَاتُنَا الدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا يُهْلِكُنَا إِلَّا الدَّهْرُ ۚ وَمَا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ ۖ إِنْ هُمْ إِلَّا يَظُنُّونَ','آنها گفتند: «چیزی جز همین زندگی دنیای ما در کار نیست؛ گروهی از ما می‌میرند و گروهی جای آنها را می‌گیرند؛ و جز طبیعت و روزگار ما را هلاک نمی‌کند!» آنان به این سخن که می‌گویند علمی ندارند، بلکه تنها حدس می‌زنند (و گمانی بی‌پایه دارند)!'];
ayat[4498]=[45,25,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ مَّا كَانَ حُجَّتَهُمْ إِلَّا أَن قَالُوا ائْتُوا بِآبَائِنَا إِن كُنتُمْ صَادِقِينَ','و هنگامی که آیات روشن ما بر آنها خوانده می‌شود، دلیلی در برابر آن ندارند جز اینکه می‌گویند: «اگر راست می‌گویید پدران ما را (زنده کنید) و بیاورید (تا گواهی دهند)!»'];
ayat[4499]=[45,26,'قُلِ اللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ إِلَىٰ يَوْمِ الْقِيَامَةِ لَا رَيْبَ فِيهِ وَلَٰكِنَّ أَكْثَرَ النَّاسِ لَا يَعْلَمُونَ','بگو: «خداوند شما را زنده می‌کند، سپس می‌میراند، بار دیگر در روز قیامت که در آن تردیدی نیست گردآوری می‌کند؛ ولی بیشتر مردم نمی‌دانند.»'];
ayat[4500]=[45,27,'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَيَوْمَ تَقُومُ السَّاعَةُ يَوْمَئِذٍ يَخْسَرُ الْمُبْطِلُونَ','مالکیّت و حاکمیّت آسمانها و زمین برای خداست؛ و آن روز که قیامت برپا شود اهل باطل زیان می‌بینند!'];
ayat[4501]=[45,28,'وَتَرَىٰ كُلَّ أُمَّةٍ جَاثِيَةً ۚ كُلُّ أُمَّةٍ تُدْعَىٰ إِلَىٰ كِتَابِهَا الْيَوْمَ تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ','در آن روز هر امّتی را می‌بینی (که از شدّت ترس و وحشت) بر زانو نشسته؛ هر امّتی بسوی کتابش خوانده می‌شود، و (به آنها می‌گویند:) امروز جزای آنچه را انجام می‌دادید به شما می‌دهند!'];
ayat[4502]=[45,29,'هَٰذَا كِتَابُنَا يَنطِقُ عَلَيْكُم بِالْحَقِّ ۚ إِنَّا كُنَّا نَسْتَنسِخُ مَا كُنتُمْ تَعْمَلُونَ','این کتاب ما است که بحق با شما سخن می‌گوید (و اعمال شما را بازگو می‌کند)؛ ما آنچه را انجام می‌دادید می‌نوشتیم!'];
ayat[4503]=[45,30,'فَأَمَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِي رَحْمَتِهِ ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْمُبِينُ','امّا کسانی که ایمان آوردند و اعمال صالح انجام دادند، پروردگارشان آنها را در رحمت خود وارد می‌کند؛ این همان پیروزی بزرگ است!'];
ayat[4504]=[45,31,'وَأَمَّا الَّذِينَ كَفَرُوا أَفَلَمْ تَكُنْ آيَاتِي تُتْلَىٰ عَلَيْكُمْ فَاسْتَكْبَرْتُمْ وَكُنتُمْ قَوْمًا مُّجْرِمِينَ','امّا کسانی که کافر شدند (به آنها گفته می‌شود:) مگر آیات من بر شما خوانده نمی‌شد و شما استکبار کردید و قوم مجرمی بودید؟!'];
ayat[4505]=[45,32,'وَإِذَا قِيلَ إِنَّ وَعْدَ اللَّهِ حَقٌّ وَالسَّاعَةُ لَا رَيْبَ فِيهَا قُلْتُم مَّا نَدْرِي مَا السَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنًّا وَمَا نَحْنُ بِمُسْتَيْقِنِينَ','و هنگامی که گفته می‌شد: «وعده خداوند حقّ است، و در قیامت هیچ شکّی نیست»، شما می‌گفتید: «ما نمی‌دانیم قیامت چیست؟ ما تنها گمانی در این باره داریم، و به هیچ‌وجه یقین نداریم!»'];
ayat[4506]=[45,33,'وَبَدَا لَهُمْ سَيِّئَاتُ مَا عَمِلُوا وَحَاقَ بِهِم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','و بدیهای اعمالشان برای آنان آشکار می‌شود، و سرانجام آنچه را استهزا می‌کردند آنها را فرامی‌گیرد!'];
ayat[4507]=[45,34,'وَقِيلَ الْيَوْمَ نَنسَاكُمْ كَمَا نَسِيتُمْ لِقَاءَ يَوْمِكُمْ هَٰذَا وَمَأْوَاكُمُ النَّارُ وَمَا لَكُم مِّن نَّاصِرِينَ','و به آنها گفته می‌شود: «امروز شما را فراموش می‌کنیم همان‌گونه که شما دیدار امروزتان را فراموش کردید؛ و جایگاه شما دوزخ است و هیچ یاوری ندارید!'];
ayat[4508]=[45,35,'ذَٰلِكُم بِأَنَّكُمُ اتَّخَذْتُمْ آيَاتِ اللَّهِ هُزُوًا وَغَرَّتْكُمُ الْحَيَاةُ الدُّنْيَا ۚ فَالْيَوْمَ لَا يُخْرَجُونَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُونَ','این بخاطر آن است که شما آیات خدا را به مسخره گرفتید و زندگی دنیا شما را فریب داد! «امروز نه آنان را از دوزخ بیرون می‌آورند، و نه هیچ‌گونه عذری از آنها پذیرفته می‌شود!'];
ayat[4509]=[45,36,'فَلِلَّهِ الْحَمْدُ رَبِّ السَّمَاوَاتِ وَرَبِّ الْأَرْضِ رَبِّ الْعَالَمِينَ','پس حمد و ستایش مخصوص خداست، پروردگار آسمانها و پروردگار زمین و پروردگار همه جهانیان!'];
ayat[4510]=[45,37,'وَلَهُ الْكِبْرِيَاءُ فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ','و برای اوست کبریا و عظمت در آسمانها و زمین، و اوست عزیز و حکیم!'];
ayat[4511]=[46,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ حم','حم‌'];
ayat[4512]=[46,2,'تَنزِيلُ الْكِتَابِ مِنَ اللَّهِ الْعَزِيزِ الْحَكِيمِ','این کتاب از سوی خداوند عزیز و حکیم نازل شده است!'];
ayat[4513]=[46,3,'مَا خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا إِلَّا بِالْحَقِّ وَأَجَلٍ مُّسَمًّى ۚ وَالَّذِينَ كَفَرُوا عَمَّا أُنذِرُوا مُعْرِضُونَ','ما آسمانها و زمین و آنچه را در میان این دو است جز بحق و برای سرآمد معیّنی نیافریدیم؛ امّا کافران از آنچه انذار می‌شوند روی گردانند!'];
ayat[4514]=[46,4,'قُلْ أَرَأَيْتُم مَّا تَدْعُونَ مِن دُونِ اللَّهِ أَرُونِي مَاذَا خَلَقُوا مِنَ الْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِي السَّمَاوَاتِ ۖ ائْتُونِي بِكِتَابٍ مِّن قَبْلِ هَٰذَا أَوْ أَثَارَةٍ مِّنْ عِلْمٍ إِن كُنتُمْ صَادِقِينَ','به آنان بگو: «این معبودهایی را که غیر از خدا پرستش می‌کنید به من نشان دهید چه چیزی از زمین را آفریده‌اند، یا شرکتی در آفرینش آسمانها دارند؟ کتابی آسمانی پیش از این، یا اثر علمی از گذشتگان برای من بیاورید (که دلیل صدق گفتار شما باشد) اگر راست می‌گویید!»'];
ayat[4515]=[46,5,'وَمَنْ أَضَلُّ مِمَّن يَدْعُو مِن دُونِ اللَّهِ مَن لَّا يَسْتَجِيبُ لَهُ إِلَىٰ يَوْمِ الْقِيَامَةِ وَهُمْ عَن دُعَائِهِمْ غَافِلُونَ','چه کسی گمراهتر است از آن کس که معبودی غیر خدا را می‌خواند که تا قیامت هم به او پاسخ نمی‌گوید و از خواندن آنها (کاملا) ً بی‌خبر است؟! (و صدای آنها را هیچ نمی‌شنود!)'];
ayat[4516]=[46,6,'وَإِذَا حُشِرَ النَّاسُ كَانُوا لَهُمْ أَعْدَاءً وَكَانُوا بِعِبَادَتِهِمْ كَافِرِينَ','و هنگامی که مردم محشور می‌شوند، معبودهای آنها دشمنانشان خواهند بود؛ حتّی عبادت آنها را انکار می‌کنند!'];
ayat[4517]=[46,7,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ قَالَ الَّذِينَ كَفَرُوا لِلْحَقِّ لَمَّا جَاءَهُمْ هَٰذَا سِحْرٌ مُّبِينٌ','هنگامی که آیات روشن ما بر آنان خوانده می‌شود، کافران در برابر حقّی که برای آنها آمده می‌گویند: «این سحری آشکار است!»'];
ayat[4518]=[46,8,'أَمْ يَقُولُونَ افْتَرَاهُ ۖ قُلْ إِنِ افْتَرَيْتُهُ فَلَا تَمْلِكُونَ لِي مِنَ اللَّهِ شَيْئًا ۖ هُوَ أَعْلَمُ بِمَا تُفِيضُونَ فِيهِ ۖ كَفَىٰ بِهِ شَهِيدًا بَيْنِي وَبَيْنَكُمْ ۖ وَهُوَ الْغَفُورُ الرَّحِيمُ','بلکه می‌گویند: «این آیات را بر خدا افترا بسته است!» بگو: «اگر من آن را بدروغ به خدا نسبت داده باشم (لازم است مرا رسوا کند و) شما نمی‌توانید در برابر خداوند از من دفاع کنید! او کارهایی را که شما در آن وارد می‌شوید بهتر می‌داند؛ همین بس که خداوند گواه میان من و شما باشد؛ و او آمرزنده و مهربان است!»'];
ayat[4519]=[46,9,'قُلْ مَا كُنتُ بِدْعًا مِّنَ الرُّسُلِ وَمَا أَدْرِي مَا يُفْعَلُ بِي وَلَا بِكُمْ ۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّ وَمَا أَنَا إِلَّا نَذِيرٌ مُّبِينٌ','بگو: «من پیامبر نوظهوری نیستم؛ و نمی‌دانم با من و شما چه خواهد شد؛ من تنها از آنچه بر من وحی می‌شود پیروی می‌کنم، و جز بیم‌دهنده آشکاری نیستم!»'];
ayat[4520]=[46,10,'قُلْ أَرَأَيْتُمْ إِن كَانَ مِنْ عِندِ اللَّهِ وَكَفَرْتُم بِهِ وَشَهِدَ شَاهِدٌ مِّن بَنِي إِسْرَائِيلَ عَلَىٰ مِثْلِهِ فَآمَنَ وَاسْتَكْبَرْتُمْ ۖ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','بگو: «به من خبر دهید اگر این قرآن از سوی خدا باشد و شما به آن کافر شوید، در حالی که شاهدی از بنی اسرائیل بر آن شهادت دهد، و او ایمان آورد و شما استکبار کنید (چه کسی گمراهتر از شما خواهد بود)؟! خداوند گروه ستمگر را هدایت نمی‌کند!»'];
ayat[4521]=[46,11,'وَقَالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا لَوْ كَانَ خَيْرًا مَّا سَبَقُونَا إِلَيْهِ ۚ وَإِذْ لَمْ يَهْتَدُوا بِهِ فَسَيَقُولُونَ هَٰذَا إِفْكٌ قَدِيمٌ','کافران درباره مؤمنان چنین گفتند: «اگر (اسلام) چیز خوبی بود، هرگز آنها (در پذیرش آن) بر ما پیشی نمی‌گرفتند!» و چون خودشان بوسیله آن هدایت نشدند می‌گویند: «این یک دروغ قدیمی است!»'];
ayat[4522]=[46,12,'وَمِن قَبْلِهِ كِتَابُ مُوسَىٰ إِمَامًا وَرَحْمَةً ۚ وَهَٰذَا كِتَابٌ مُّصَدِّقٌ لِّسَانًا عَرَبِيًّا لِّيُنذِرَ الَّذِينَ ظَلَمُوا وَبُشْرَىٰ لِلْمُحْسِنِينَ','و پیش از آن، کتاب موسی که پیشوا و رحمت بود (نشانه‌های آن را بیان کرده)، و این کتاب هماهنگ با نشانه‌های تورات است در حالی که به زبان عربی و فصیح و گویاست، تا ظالمان را بیم دهد و برای نیکوکاران بشارتی باشد!'];
ayat[4523]=[46,13,'إِنَّ الَّذِينَ قَالُوا رَبُّنَا اللَّهُ ثُمَّ اسْتَقَامُوا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ','کسانی که گفتند: «پروردگار ما اللّه است»، سپس استقامت کردند، نه ترسی برای آنان است و نه اندوهگین می‌شوند.'];
ayat[4524]=[46,14,'أُولَٰئِكَ أَصْحَابُ الْجَنَّةِ خَالِدِينَ فِيهَا جَزَاءً بِمَا كَانُوا يَعْمَلُونَ','آنها اهل بهشتند و جاودانه در آن می‌مانند؛ این پاداش اعمالی است که انجام می‌دادند.'];
ayat[4525]=[46,15,'وَوَصَّيْنَا الْإِنسَانَ بِوَالِدَيْهِ إِحْسَانًا ۖ حَمَلَتْهُ أُمُّهُ كُرْهًا وَوَضَعَتْهُ كُرْهًا ۖ وَحَمْلُهُ وَفِصَالُهُ ثَلَاثُونَ شَهْرًا ۚ حَتَّىٰ إِذَا بَلَغَ أَشُدَّهُ وَبَلَغَ أَرْبَعِينَ سَنَةً قَالَ رَبِّ أَوْزِعْنِي أَنْ أَشْكُرَ نِعْمَتَكَ الَّتِي أَنْعَمْتَ عَلَيَّ وَعَلَىٰ وَالِدَيَّ وَأَنْ أَعْمَلَ صَالِحًا تَرْضَاهُ وَأَصْلِحْ لِي فِي ذُرِّيَّتِي ۖ إِنِّي تُبْتُ إِلَيْكَ وَإِنِّي مِنَ الْمُسْلِمِينَ','ما به انسان توصیه کردیم که به پدر و مادرش نیکی کند، مادرش او را با ناراحتی حمل می‌کند و با ناراحتی بر زمین می‌گذارد؛ و دوران حمل و از شیر بازگرفتنش سی ماه است؛ تا زمانی که به کمال قدرت و رشد برسد و به چهل سالگی بالغ گردد می‌گوید: «پروردگارا! مرا توفیق ده تا شکر نعمتی را که به من و پدر و مادرم دادی بجا آورم و کار شایسته‌ای انجام دهم که از آن خشنود باشی، و فرزندان مرا صالح گردان؛ من به سوی تو بازمی‌گردم و توبه می‌کنم، و من از مسلمانانم!»'];
ayat[4526]=[46,16,'أُولَٰئِكَ الَّذِينَ نَتَقَبَّلُ عَنْهُمْ أَحْسَنَ مَا عَمِلُوا وَنَتَجَاوَزُ عَن سَيِّئَاتِهِمْ فِي أَصْحَابِ الْجَنَّةِ ۖ وَعْدَ الصِّدْقِ الَّذِي كَانُوا يُوعَدُونَ','آنها کسانی هستند که ما بهترین اعمالشان را قبول می‌کنیم و از گناهانشان می‌گذریم و در میان بهشتیان جای دارند؛ این وعده راستی است که وعده داده می‌شدند.'];
ayat[4527]=[46,17,'وَالَّذِي قَالَ لِوَالِدَيْهِ أُفٍّ لَّكُمَا أَتَعِدَانِنِي أَنْ أُخْرَجَ وَقَدْ خَلَتِ الْقُرُونُ مِن قَبْلِي وَهُمَا يَسْتَغِيثَانِ اللَّهَ وَيْلَكَ آمِنْ إِنَّ وَعْدَ اللَّهِ حَقٌّ فَيَقُولُ مَا هَٰذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ','و کسی که به پدر و مادرش می‌گوید: «اف بر شما! آیا به من وعده می‌دهید که من روز قیامت مبعوث می‌شوم؟! در حالی که پیش از من اقوام زیادی بودند (و هرگز مبعوث نشدند)! و آن دو پیوسته فریاد می‌کشند و خدا را به یاری می‌طلبند که: وای بر تو، ایمان بیاور که وعده خدا حق است امّا او پیوسته می‌گوید:» اینها چیزی جز افسانه‌های پیشینیان نیست!'];
ayat[4528]=[46,18,'أُولَٰئِكَ الَّذِينَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِي أُمَمٍ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ الْجِنِّ وَالْإِنسِ ۖ إِنَّهُمْ كَانُوا خَاسِرِينَ','آنها کسانی هستند که فرمان عذاب درباره آنان همراه اقوام (کافری) که پیش از آنان از جنّ و انس بودند مسلّم شده، چرا که همگی زیانکار بودند!'];
ayat[4529]=[46,19,'وَلِكُلٍّ دَرَجَاتٌ مِّمَّا عَمِلُوا ۖ وَلِيُوَفِّيَهُمْ أَعْمَالَهُمْ وَهُمْ لَا يُظْلَمُونَ','و برای هر کدام از آنها درجاتی است بر طبق اعمالی که انجام داده‌اند، تا خداوند کارهایشان را بی‌کم و کاست به آنان تحویل دهد؛ و به آنها هیچ ستمی نخواهد شد!'];
ayat[4530]=[46,20,'وَيَوْمَ يُعْرَضُ الَّذِينَ كَفَرُوا عَلَى النَّارِ أَذْهَبْتُمْ طَيِّبَاتِكُمْ فِي حَيَاتِكُمُ الدُّنْيَا وَاسْتَمْتَعْتُم بِهَا فَالْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُونِ بِمَا كُنتُمْ تَسْتَكْبِرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنتُمْ تَفْسُقُونَ','آن روز که کافران را بر آتش عرضه می‌کنند (به آنها گفته می‌شود:) از طیّبات و لذائذ در زندگی دنیا خود استفاده کردید و از آن بهره گرفتید؛ اما امروز عذاب ذلّت‌بار بخاطر استکباری که در زمین بناحق کردید و بخاطر گناهانی که انجام می‌دادید؛ جزای شما خواهد بود!'];
ayat[4531]=[46,21,'۞ وَاذْكُرْ أَخَا عَادٍ إِذْ أَنذَرَ قَوْمَهُ بِالْأَحْقَافِ وَقَدْ خَلَتِ النُّذُرُ مِن بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ أَلَّا تَعْبُدُوا إِلَّا اللَّهَ إِنِّي أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ','(سرگذشت هود) برادر قوم عاد را یاد کن، آن زمان که قومش را در سرزمین «احقاف» بیم داد در حالی که پیامبران زیادی قبل از او در گذشته‌های دور و نزدیک آمده بودند که: جز خدای یگانه را نپرستید! (و گفت:) من بر شما از عذاب روزی بزرگ می‌ترسم!'];
ayat[4532]=[46,22,'قَالُوا أَجِئْتَنَا لِتَأْفِكَنَا عَنْ آلِهَتِنَا فَأْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ الصَّادِقِينَ','آنها گفتند: «آیا آمده‌ای که ما را (با دروغهایت) از معبودانمان بازگردانی؟! اگر راست می‌گویی عذابی را که به ما وعده می‌دهی بیاور!»'];
ayat[4533]=[46,23,'قَالَ إِنَّمَا الْعِلْمُ عِندَ اللَّهِ وَأُبَلِّغُكُم مَّا أُرْسِلْتُ بِهِ وَلَٰكِنِّي أَرَاكُمْ قَوْمًا تَجْهَلُونَ','گفت: «علم (آن) تنها نزد خداست (و او می‌داند چه زمانی شما را مجازات کند)؛ من آنچه را به آن فرستاده شده‌ام به شما می‌رسانم، (وظیفه من همین است!) ولی شما را قومی می‌بینیم که پیوسته در نادانی هستید!»'];
ayat[4534]=[46,24,'فَلَمَّا رَأَوْهُ عَارِضًا مُّسْتَقْبِلَ أَوْدِيَتِهِمْ قَالُوا هَٰذَا عَارِضٌ مُّمْطِرُنَا ۚ بَلْ هُوَ مَا اسْتَعْجَلْتُم بِهِ ۖ رِيحٌ فِيهَا عَذَابٌ أَلِيمٌ','هنگامی که آن (عذاب الهی) را بصورت ابر گسترده‌ای دیدند که بسوی درّه‌ها و آبگیرهای آنان در حرکت است (خوشحال شدند) گفتند: «این ابری است که بر ما می‌بارد!» (ولی به آنها گفته شد:) این همان چیزی است که برای آمدنش شتاب می‌کردید، تندبادی است (وحشتناک) که عذاب دردناکی در آن است!'];
ayat[4535]=[46,25,'تُدَمِّرُ كُلَّ شَيْءٍ بِأَمْرِ رَبِّهَا فَأَصْبَحُوا لَا يُرَىٰ إِلَّا مَسَاكِنُهُمْ ۚ كَذَٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ','همه چیز را بفرمان پروردگارش در هم میکوبد و نابود میکند (آری) آنها صبح کردند در حالی که چیزی جز خانه‌هایشان به چشم نمی‌خورد؛ ما این‌گونه گروه مجرمان را کیفر می‌دهیم!'];
ayat[4536]=[46,26,'وَلَقَدْ مَكَّنَّاهُمْ فِيمَا إِن مَّكَّنَّاكُمْ فِيهِ وَجَعَلْنَا لَهُمْ سَمْعًا وَأَبْصَارًا وَأَفْئِدَةً فَمَا أَغْنَىٰ عَنْهُمْ سَمْعُهُمْ وَلَا أَبْصَارُهُمْ وَلَا أَفْئِدَتُهُم مِّن شَيْءٍ إِذْ كَانُوا يَجْحَدُونَ بِآيَاتِ اللَّهِ وَحَاقَ بِهِم مَّا كَانُوا بِهِ يَسْتَهْزِئُونَ','ما به آنها [= قوم عاد] قدرتی دادیم که به شما ندادیم، و برای آنان گوش و چشم و دل قرار دادیم؛ (امّا به هنگام نزول عذاب) نه گوشها و چشمها و نه عقلهایشان برای آنان هیچ سودی نداشت، چرا که آیات خدا را انکار می‌کردند؛ و سرانجام آنچه را استهزا می‌کردند بر آنها وارد شد!'];
ayat[4537]=[46,27,'وَلَقَدْ أَهْلَكْنَا مَا حَوْلَكُم مِّنَ الْقُرَىٰ وَصَرَّفْنَا الْآيَاتِ لَعَلَّهُمْ يَرْجِعُونَ','ما آبادیهایی را که پیرامون شما بودند نابود ساختیم، و آیات خود را بصورتهای گوناگون (برای مردم آنها) بیان کردیم شاید بازگردند!'];
ayat[4538]=[46,28,'فَلَوْلَا نَصَرَهُمُ الَّذِينَ اتَّخَذُوا مِن دُونِ اللَّهِ قُرْبَانًا آلِهَةً ۖ بَلْ ضَلُّوا عَنْهُمْ ۚ وَذَٰلِكَ إِفْكُهُمْ وَمَا كَانُوا يَفْتَرُونَ','پس چرا معبودانی را که غیر از خدا برگزیدند -به گمان اینکه به خدا نزدیکشان سازد- آنها را یاری نکردند؟! بلکه از میانشان گم شدند! این بود نتیجه دروغ آنها و آنچه افترا می‌بستند!'];
ayat[4539]=[46,29,'وَإِذْ صَرَفْنَا إِلَيْكَ نَفَرًا مِّنَ الْجِنِّ يَسْتَمِعُونَ الْقُرْآنَ فَلَمَّا حَضَرُوهُ قَالُوا أَنصِتُوا ۖ فَلَمَّا قُضِيَ وَلَّوْا إِلَىٰ قَوْمِهِم مُّنذِرِينَ','(به یاد آور) هنگامی که گروهی از جنّ را به سوی تو متوجّه ساختیم که قرآن را بشنوند؛ وقتی حضور یافتند به یکدیگر گفتند: «خاموش باشید و بشنوید!» و هنگامی که پایان گرفت، به سوی قوم خود بازگشتند و آنها را بیم دادند!'];
ayat[4540]=[46,30,'قَالُوا يَا قَوْمَنَا إِنَّا سَمِعْنَا كِتَابًا أُنزِلَ مِن بَعْدِ مُوسَىٰ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ يَهْدِي إِلَى الْحَقِّ وَإِلَىٰ طَرِيقٍ مُّسْتَقِيمٍ','گفتند: «ای قوم ما! ما کتابی را شنیدیم که بعد از موسی نازل شده، هماهنگ با نشانه‌های کتابهای پیش از آن، که به سوی حقّ و راه راست هدایت می‌کند.'];
ayat[4541]=[46,31,'يَا قَوْمَنَا أَجِيبُوا دَاعِيَ اللَّهِ وَآمِنُوا بِهِ يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُجِرْكُم مِّنْ عَذَابٍ أَلِيمٍ','ای قوم ما! دعوت کننده الهی را اجابت کنید و به او ایمان آورید تا گناهانتان را ببخشد و شما را از عذابی دردناک پناه دهد!'];
ayat[4542]=[46,32,'وَمَن لَّا يُجِبْ دَاعِيَ اللَّهِ فَلَيْسَ بِمُعْجِزٍ فِي الْأَرْضِ وَلَيْسَ لَهُ مِن دُونِهِ أَوْلِيَاءُ ۚ أُولَٰئِكَ فِي ضَلَالٍ مُّبِينٍ','و هر کس به دعوت کننده الهی پاسخ نگوید، هرگز نمی‌تواند از چنگال عذاب الهی در زمین فرار کند، و غیر از خدا یار و یاوری برای او نیست؛ چنین کسانی در گمراهی آشکارند!»'];
ayat[4543]=[46,33,'أَوَلَمْ يَرَوْا أَنَّ اللَّهَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ وَلَمْ يَعْيَ بِخَلْقِهِنَّ بِقَادِرٍ عَلَىٰ أَن يُحْيِيَ الْمَوْتَىٰ ۚ بَلَىٰ إِنَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آیا آنها نمی‌دانند خداوندی که آسمانها و زمین را آفریده و از آفرینش آنها ناتوان نشده است، می‌تواند مُردگان را زنده کند؟! آری او بر هر چیز تواناست!'];
ayat[4544]=[46,34,'وَيَوْمَ يُعْرَضُ الَّذِينَ كَفَرُوا عَلَى النَّارِ أَلَيْسَ هَٰذَا بِالْحَقِّ ۖ قَالُوا بَلَىٰ وَرَبِّنَا ۚ قَالَ فَذُوقُوا الْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَ','روزی را به یاد آور که کافران را بر آتش عرضه می‌دارند (و به آنها گفته می‌شود:) آیا این حقّ نیست؟! می‌گویند: «آری، به پروردگارمان سوگند (که حقّ است)!» (در این هنگام خداوند) می‌گوید: «پس عذاب را بخاطر کفرتان بچشید!»'];
ayat[4545]=[46,35,'فَاصْبِرْ كَمَا صَبَرَ أُولُو الْعَزْمِ مِنَ الرُّسُلِ وَلَا تَسْتَعْجِل لَّهُمْ ۚ كَأَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوعَدُونَ لَمْ يَلْبَثُوا إِلَّا سَاعَةً مِّن نَّهَارٍ ۚ بَلَاغٌ ۚ فَهَلْ يُهْلَكُ إِلَّا الْقَوْمُ الْفَاسِقُونَ','پس صبر کن آن‌گونه که پیامبران «اُولو العزم» صبر کردند، و برای (عذاب) آنان شتاب مکن! هنگامی که وعده‌هایی را که به آنها داده می‌شود ببینند، احساس می‌کنند که گویی فقط ساعتی از یک روز (در دنیا) توقّف داشتند؛ این ابلاغی است برای همگان؛ آیا جز قوم فاسق هلاک می‌شوند؟!'];
ayat[4546]=[47,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الَّذِينَ كَفَرُوا وَصَدُّوا عَن سَبِيلِ اللَّهِ أَضَلَّ أَعْمَالَهُمْ','کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند، (خداوند) اعمالشان را نابود می‌کند!'];
ayat[4547]=[47,2,'وَالَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَآمَنُوا بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٍ وَهُوَ الْحَقُّ مِن رَّبِّهِمْ ۙ كَفَّرَ عَنْهُمْ سَيِّئَاتِهِمْ وَأَصْلَحَ بَالَهُمْ','و کسانی که ایمان آوردند و کارهای شایسته انجام دادند و به آنچه بر محمد (ص) نازل شده -و همه حقّ است و از سوی پروردگارشان- نیز ایمان آوردند، خداوند گناهانشان را می‌بخشد و کارشان را اصلاح می‌کند!'];
ayat[4548]=[47,3,'ذَٰلِكَ بِأَنَّ الَّذِينَ كَفَرُوا اتَّبَعُوا الْبَاطِلَ وَأَنَّ الَّذِينَ آمَنُوا اتَّبَعُوا الْحَقَّ مِن رَّبِّهِمْ ۚ كَذَٰلِكَ يَضْرِبُ اللَّهُ لِلنَّاسِ أَمْثَالَهُمْ','این بخاطر آن است که کافران از باطل پیروی کردند، و مؤمنان از حقّی که از سوی پروردگارشان بود تبعیّت نمودند؛ این‌گونه خداوند برای مردم مثَلهای (زندگی) آنان را بیان می‌کند!'];
ayat[4549]=[47,4,'فَإِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا فَضَرْبَ الرِّقَابِ حَتَّىٰ إِذَا أَثْخَنتُمُوهُمْ فَشُدُّوا الْوَثَاقَ فَإِمَّا مَنًّا بَعْدُ وَإِمَّا فِدَاءً حَتَّىٰ تَضَعَ الْحَرْبُ أَوْزَارَهَا ۚ ذَٰلِكَ وَلَوْ يَشَاءُ اللَّهُ لَانتَصَرَ مِنْهُمْ وَلَٰكِن لِّيَبْلُوَ بَعْضَكُم بِبَعْضٍ ۗ وَالَّذِينَ قُتِلُوا فِي سَبِيلِ اللَّهِ فَلَن يُضِلَّ أَعْمَالَهُمْ','و هنگامی که با کافران (جنایت‌پیشه) در میدان جنگ روبه‌رو شدید گردنهایشان را بزنید، (و این کار را همچنان ادامه دهید) تا به اندازه کافی دشمن را در هم بکوبید؛ در این هنگام اسیران را محکم ببندید؛ سپس یا بر آنان منّت گذارید (و آزادشان کنید) یا در برابر آزادی از آنان فدیه [= غرامت‌] بگیرید؛ (و این وضع باید همچنان ادامه یابد) تا جنگ بار سنگین خود را بر زمین نهد، (آری) برنامه این است! و اگر خدا می‌خواست خودش آنها را مجازات می‌کرد، اما می‌خواهد بعضی از شما را با بعضی دیگر بیازماید؛ و کسانی که در راه خدا کشته شدند، خداوند هرگز اعمالشان را از بین نمی‌برد!'];
ayat[4550]=[47,5,'سَيَهْدِيهِمْ وَيُصْلِحُ بَالَهُمْ','بزودی آنان را هدایت نموده و کارشان را اصلاح می‌کند؛'];
ayat[4551]=[47,6,'وَيُدْخِلُهُمُ الْجَنَّةَ عَرَّفَهَا لَهُمْ','و آنها را در بهشت (جاویدانش) که اوصاف آن را برای آنان بازگو کرده وارد می‌کند.'];
ayat[4552]=[47,7,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِن تَنصُرُوا اللَّهَ يَنصُرْكُمْ وَيُثَبِّتْ أَقْدَامَكُمْ','ای کسانی که ایمان آورده‌اید! اگر (آیین) خدا را یاری کنید، شما را یاری می‌کند و گامهایتان را استوار می‌دارد.'];
ayat[4553]=[47,8,'وَالَّذِينَ كَفَرُوا فَتَعْسًا لَّهُمْ وَأَضَلَّ أَعْمَالَهُمْ','و کسانی که کافر شدند، مرگ بر آنان! و اعمالشان نابود باد!'];
ayat[4554]=[47,9,'ذَٰلِكَ بِأَنَّهُمْ كَرِهُوا مَا أَنزَلَ اللَّهُ فَأَحْبَطَ أَعْمَالَهُمْ','این بخاطر آن است که از آنچه خداوند نازل کرده کراهت داشتند؛ از این رو خدا اعمالشان را حبط و نابود کرد!'];
ayat[4555]=[47,10,'۞ أَفَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنظُرُوا كَيْفَ كَانَ عَاقِبَةُ الَّذِينَ مِن قَبْلِهِمْ ۚ دَمَّرَ اللَّهُ عَلَيْهِمْ ۖ وَلِلْكَافِرِينَ أَمْثَالُهَا','آیا در زمین سیر نکردند تا ببینند عاقبت کسانی که قبل از آنان بودند چگونه بود؟! خداوند آنها را هلاک کرد؛ و برای کافران امثال این مجازاتها خواهد بود!'];
ayat[4556]=[47,11,'ذَٰلِكَ بِأَنَّ اللَّهَ مَوْلَى الَّذِينَ آمَنُوا وَأَنَّ الْكَافِرِينَ لَا مَوْلَىٰ لَهُمْ','این برای آن است که خداوند مولا و سرپرست کسانی است که ایمان آوردند؛ امّا کافران مولایی ندارند!'];
ayat[4557]=[47,12,'إِنَّ اللَّهَ يُدْخِلُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۖ وَالَّذِينَ كَفَرُوا يَتَمَتَّعُونَ وَيَأْكُلُونَ كَمَا تَأْكُلُ الْأَنْعَامُ وَالنَّارُ مَثْوًى لَّهُمْ','خداوند کسانی را که ایمان آوردند و اعمال صالح انجام دادند وارد باغهایی از بهشت می‌کند که نهرها از زیر (درختانش) جاری است؛ در حالی که کافران از متاع زودگذر دنیا بهره می‌گیرند و همچون چهارپایان می‌خورند، و سرانجام آتش دوزخ جایگاه آنهاست!'];
ayat[4558]=[47,13,'وَكَأَيِّن مِّن قَرْيَةٍ هِيَ أَشَدُّ قُوَّةً مِّن قَرْيَتِكَ الَّتِي أَخْرَجَتْكَ أَهْلَكْنَاهُمْ فَلَا نَاصِرَ لَهُمْ','و چه بسیار شهرهایی که از شهری که تو را بیرون کرد نیرومندتر بودند؛ ما همه آنها را نابود کردیم و هیچ یاوری نداشتند!'];
ayat[4559]=[47,14,'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٍ مِّن رَّبِّهِ كَمَن زُيِّنَ لَهُ سُوءُ عَمَلِهِ وَاتَّبَعُوا أَهْوَاءَهُم','آیا کسی که دلیل روشنی از سوی پروردگارش دارد، همانند کسی است که زشتی اعمالش در نظرش آراسته شده و از هوای نفسشان پیروی می‌کنند؟!'];
ayat[4560]=[47,15,'مَّثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ ۖ فِيهَا أَنْهَارٌ مِّن مَّاءٍ غَيْرِ آسِنٍ وَأَنْهَارٌ مِّن لَّبَنٍ لَّمْ يَتَغَيَّرْ طَعْمُهُ وَأَنْهَارٌ مِّنْ خَمْرٍ لَّذَّةٍ لِّلشَّارِبِينَ وَأَنْهَارٌ مِّنْ عَسَلٍ مُّصَفًّى ۖ وَلَهُمْ فِيهَا مِن كُلِّ الثَّمَرَاتِ وَمَغْفِرَةٌ مِّن رَّبِّهِمْ ۖ كَمَنْ هُوَ خَالِدٌ فِي النَّارِ وَسُقُوا مَاءً حَمِيمًا فَقَطَّعَ أَمْعَاءَهُمْ','توصیف بهشتی که به پرهیزگاران وعده داده شده، چنین است: در آن نهرهایی از آب صاف و خالص که بدبو نشده، و نهرهایی از شیر که طعم آن دگرگون نگشته، و نهرهایی از شراب (طهور) که مایه لذّت نوشندگان است، و نهرهایی از عسل مصفّاست، و برای آنها در آن از همه انواع میوه‌ها وجود دارد؛ و (از همه بالاتر) آمرزشی است از سوی پروردگارشان! آیا اینها همانند کسانی هستند که همیشه در آتش دوزخند و از آب جوشان نوشانده می‌شوند که اندرونشان را از هم متلاشی می‌کند؟!'];
ayat[4561]=[47,16,'وَمِنْهُم مَّن يَسْتَمِعُ إِلَيْكَ حَتَّىٰ إِذَا خَرَجُوا مِنْ عِندِكَ قَالُوا لِلَّذِينَ أُوتُوا الْعِلْمَ مَاذَا قَالَ آنِفًا ۚ أُولَٰئِكَ الَّذِينَ طَبَعَ اللَّهُ عَلَىٰ قُلُوبِهِمْ وَاتَّبَعُوا أَهْوَاءَهُمْ','گروهی از آنان به سخنانت گوش فرامی‌دهند، امّا هنگامی که از نزد تو خارج می‌شوند به کسانی که علم و دانش به آنان بخشیده شده (از روی استهزا) می‌گویند: «(این مرد) الان چه گفت؟!» آنها کسانی هستند که خداوند بر دلهایشان مُهر نهاده و از هوای نفسشان پیروی کرده‌اند (از این رو چیزی نمی‌فهمند)!'];
ayat[4562]=[47,17,'وَالَّذِينَ اهْتَدَوْا زَادَهُمْ هُدًى وَآتَاهُمْ تَقْوَاهُمْ','کسانی که هدایت یافته‌اند، خداوند بر هدایتشان می‌افزاید و روح تقوا به آنان می‌بخشد!'];
ayat[4563]=[47,18,'فَهَلْ يَنظُرُونَ إِلَّا السَّاعَةَ أَن تَأْتِيَهُم بَغْتَةً ۖ فَقَدْ جَاءَ أَشْرَاطُهَا ۚ فَأَنَّىٰ لَهُمْ إِذَا جَاءَتْهُمْ ذِكْرَاهُمْ','آیا آنها [= کافران‌] جز این انتظاری دارند که قیامت ناگهان فرا رسد (آنگاه ایمان آورند)، در حالی که هم‌اکنون نشانه‌های آن آمده است؛ اما هنگامی که بیاید، تذکّر (و ایمان) آنها سودی نخواهد داشت!'];
ayat[4564]=[47,19,'فَاعْلَمْ أَنَّهُ لَا إِلَٰهَ إِلَّا اللَّهُ وَاسْتَغْفِرْ لِذَنبِكَ وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ۗ وَاللَّهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوَاكُمْ','پس بدان که معبودی جز «اللّه» نیست؛ و برای گناه خود و مردان و زنان باایمان استغفار کن! و خداوند محل حرکت و قرارگاه شما را می‌داند!'];
ayat[4565]=[47,20,'وَيَقُولُ الَّذِينَ آمَنُوا لَوْلَا نُزِّلَتْ سُورَةٌ ۖ فَإِذَا أُنزِلَتْ سُورَةٌ مُّحْكَمَةٌ وَذُكِرَ فِيهَا الْقِتَالُ ۙ رَأَيْتَ الَّذِينَ فِي قُلُوبِهِم مَّرَضٌ يَنظُرُونَ إِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِ ۖ فَأَوْلَىٰ لَهُمْ','کسانی که ایمان آورده‌اند می‌گویند: «چرا سوره‌ای نازل نمی‌شود (که در آن فرمان جهاد باشد)؟!» امّا هنگامی که سوره واضح و روشنی نازل می‌گردد که در آن سخنی از جنگ است، منافقان بیماردل را می‌بینی که همچون کسی که در آستانه مرگ قرار گرفته به تو نگاه می‌کنند؛ پس مرگ و نابودی برای آنان سزاوارتر است!'];
ayat[4566]=[47,21,'طَاعَةٌ وَقَوْلٌ مَّعْرُوفٌ ۚ فَإِذَا عَزَمَ الْأَمْرُ فَلَوْ صَدَقُوا اللَّهَ لَكَانَ خَيْرًا لَّهُمْ','(ولی) اطاعت و سخن سنجیده برای آنان بهتر است؛ و اگر هنگامی که فرمان جهاد قطعی می‌شود به خدا راست گویند (و از در صدق و صفا درآیند) برای آنها بهتر می‌باشد!'];
ayat[4567]=[47,22,'فَهَلْ عَسَيْتُمْ إِن تَوَلَّيْتُمْ أَن تُفْسِدُوا فِي الْأَرْضِ وَتُقَطِّعُوا أَرْحَامَكُمْ','اگر (از این دستورها) روی گردان شوید، جز این انتظار می‌رود که در زمین فساد و قطع پیوند خویشاوندی کنید؟!'];
ayat[4568]=[47,23,'أُولَٰئِكَ الَّذِينَ لَعَنَهُمُ اللَّهُ فَأَصَمَّهُمْ وَأَعْمَىٰ أَبْصَارَهُمْ','آنها کسانی هستند که خداوند از رحمت خویش دورشان ساخته، گوشهایشان را کر و چشمهایشان را کور کرده است!'];
ayat[4569]=[47,24,'أَفَلَا يَتَدَبَّرُونَ الْقُرْآنَ أَمْ عَلَىٰ قُلُوبٍ أَقْفَالُهَا','آیا آنها در قرآن تدبّر نمی‌کنند، یا بر دلهایشان قفل نهاده شده است؟!'];
ayat[4570]=[47,25,'إِنَّ الَّذِينَ ارْتَدُّوا عَلَىٰ أَدْبَارِهِم مِّن بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَى ۙ الشَّيْطَانُ سَوَّلَ لَهُمْ وَأَمْلَىٰ لَهُمْ','کسانی که بعد از روشن شدن هدایت برای آنها، پشت به حق کردند، شیطان اعمال زشتشان را در نظرشان زینت داده و آنان را با آرزوهای طولانی فریفته است!'];
ayat[4571]=[47,26,'ذَٰلِكَ بِأَنَّهُمْ قَالُوا لِلَّذِينَ كَرِهُوا مَا نَزَّلَ اللَّهُ سَنُطِيعُكُمْ فِي بَعْضِ الْأَمْرِ ۖ وَاللَّهُ يَعْلَمُ إِسْرَارَهُمْ','این بخاطر آن است که آنان به کسانی که نزول وحی الهی را کراهت داشتند گفتند: «ما در بعضی از امور از شما پیروی می‌کنیم؟» در حالی که خداوند پنهانکاری آنان را می‌داند.'];
ayat[4572]=[47,27,'فَكَيْفَ إِذَا تَوَفَّتْهُمُ الْمَلَائِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَارَهُمْ','حال آنها چگونه خواهد بود هنگامی که فرشتگان (مرگ) بر صورت و پشت آنان می‌زنند و جانشان را می‌گیرند؟!'];
ayat[4573]=[47,28,'ذَٰلِكَ بِأَنَّهُمُ اتَّبَعُوا مَا أَسْخَطَ اللَّهَ وَكَرِهُوا رِضْوَانَهُ فَأَحْبَطَ أَعْمَالَهُمْ','این بخاطر آن است که آنها از آنچه خداوند را به خشم می‌آورد پیروی کردند، و آنچه را موجب خشنودی اوست کراهت داشتند؛ از این رو (خداوند) اعمالشان را نابود کرد!'];
ayat[4574]=[47,29,'أَمْ حَسِبَ الَّذِينَ فِي قُلُوبِهِم مَّرَضٌ أَن لَّن يُخْرِجَ اللَّهُ أَضْغَانَهُمْ','آیا کسانی که در دلهایشان بیماری است گمان کردند خدا کینه‌هایشان را آشکار نمی‌کند؟!'];
ayat[4575]=[47,30,'وَلَوْ نَشَاءُ لَأَرَيْنَاكَهُمْ فَلَعَرَفْتَهُم بِسِيمَاهُمْ ۚ وَلَتَعْرِفَنَّهُمْ فِي لَحْنِ الْقَوْلِ ۚ وَاللَّهُ يَعْلَمُ أَعْمَالَكُمْ','و اگر ما بخواهیم آنها را به تو نشان می‌دهیم تا آنان را با قیافه‌هایشان بشناسی، هر چند می‌توانی آنها را از طرز سخنانشان بشناسی؛ و خداوند اعمال شما را می‌داند!'];
ayat[4576]=[47,31,'وَلَنَبْلُوَنَّكُمْ حَتَّىٰ نَعْلَمَ الْمُجَاهِدِينَ مِنكُمْ وَالصَّابِرِينَ وَنَبْلُوَ أَخْبَارَكُمْ','ما همه شما را قطعاً می‌آزمائیم تا معلوم شود مجاهدان واقعی و صابران از میان شما کیانند، و اخبار شما را بیازماییم!'];
ayat[4577]=[47,32,'إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَن سَبِيلِ اللَّهِ وَشَاقُّوا الرَّسُولَ مِن بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَىٰ لَن يَضُرُّوا اللَّهَ شَيْئًا وَسَيُحْبِطُ أَعْمَالَهُمْ','آنان که کافر شدند و (مردم را) از راه خدا بازداشتند و بعد از روشن‌شدن هدایت برای آنان (باز) به مخالفت با رسول (خدا) برخاستند، هرگز زیانی به خدا نمی‌رسانند و (خداوند) بزودی اعمالشان را نابود می‌کند!'];
ayat[4578]=[47,33,'۞ يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ وَلَا تُبْطِلُوا أَعْمَالَكُمْ','ای کسانی که ایمان آورده‌اید! اطاعت کنید خدا را، و اطاعت کنید رسول (خدا) را، و اعمال خود را باطل نسازید!'];
ayat[4579]=[47,34,'إِنَّ الَّذِينَ كَفَرُوا وَصَدُّوا عَن سَبِيلِ اللَّهِ ثُمَّ مَاتُوا وَهُمْ كُفَّارٌ فَلَن يَغْفِرَ اللَّهُ لَهُمْ','کسانی که کافر شدند و (مردم را) از راه خدا بازداشتند سپس در حال کفر از دنیا رفتند، خدا هرگز آنها را نخواهد بخشید.'];
ayat[4580]=[47,35,'فَلَا تَهِنُوا وَتَدْعُوا إِلَى السَّلْمِ وَأَنتُمُ الْأَعْلَوْنَ وَاللَّهُ مَعَكُمْ وَلَن يَتِرَكُمْ أَعْمَالَكُمْ','پس هرگز سست نشوید و (دشمنان را) به صلح (ذلّت‌بار) دعوت نکنید در حالی که شما برترید، و خداوند با شماست و چیزی از (ثواب) اعمالتان را کم نمی‌کند!'];
ayat[4581]=[47,36,'إِنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ ۚ وَإِن تُؤْمِنُوا وَتَتَّقُوا يُؤْتِكُمْ أُجُورَكُمْ وَلَا يَسْأَلْكُمْ أَمْوَالَكُمْ','زندگی دنیا تنها بازی و سرگرمی است؛ و اگر ایمان آورید و تقوا پیشه کنید، پاداشهای شما را می‌دهد و اموال شما را نمی‌طلبد،'];
ayat[4582]=[47,37,'إِن يَسْأَلْكُمُوهَا فَيُحْفِكُمْ تَبْخَلُوا وَيُخْرِجْ أَضْغَانَكُمْ','چرا که هر گاه اموال شما را مطالبه کند و حتّی اصرار نماید، بخل می‌ورزید؛ و کینه و خشم شما را آشکار می‌سازد!'];
ayat[4583]=[47,38,'هَا أَنتُمْ هَٰؤُلَاءِ تُدْعَوْنَ لِتُنفِقُوا فِي سَبِيلِ اللَّهِ فَمِنكُم مَّن يَبْخَلُ ۖ وَمَن يَبْخَلْ فَإِنَّمَا يَبْخَلُ عَن نَّفْسِهِ ۚ وَاللَّهُ الْغَنِيُّ وَأَنتُمُ الْفُقَرَاءُ ۚ وَإِن تَتَوَلَّوْا يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ ثُمَّ لَا يَكُونُوا أَمْثَالَكُم','آری، شما همان گروهی هستید که برای انفاق در راه خدا دعوت می‌شوید، بعضی از شما بخل می‌ورزند؛ و هر کس بخل ورزد، نسبت به خود بخل کرده است؛ و خداوند بی‌نیاز است و شما همه نیازمندید؛ و هرگاه سرپیچی کنید، خداوند گروه دیگری را جای شما می‌آورد پس آنها مانند شما نخواهند بود (و سخاوتمندانه در راه خدا انفاق می‌کنند).'];
ayat[4584]=[48,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا فَتَحْنَا لَكَ فَتْحًا مُّبِينًا','ما برای تو پیروزی آشکاری فراهم ساختیم!...'];
ayat[4585]=[48,2,'لِّيَغْفِرَ لَكَ اللَّهُ مَا تَقَدَّمَ مِن ذَنبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعْمَتَهُ عَلَيْكَ وَيَهْدِيَكَ صِرَاطًا مُّسْتَقِيمًا','تا خداوند گناهان گذشته و آینده‌ای را که به تو نسبت می‌دادند ببخشد (و حقّانیت تو را ثابت نموده) و نعمتش را بر تو تمام کند و به راه راست هدایتت فرماید؛'];
ayat[4586]=[48,3,'وَيَنصُرَكَ اللَّهُ نَصْرًا عَزِيزًا','و پیروزی شکست‌ناپذیری نصیب تو کند.'];
ayat[4587]=[48,4,'هُوَ الَّذِي أَنزَلَ السَّكِينَةَ فِي قُلُوبِ الْمُؤْمِنِينَ لِيَزْدَادُوا إِيمَانًا مَّعَ إِيمَانِهِمْ ۗ وَلِلَّهِ جُنُودُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَكَانَ اللَّهُ عَلِيمًا حَكِيمًا','او کسی است که آرامش را در دلهای مؤمنان نازل کرد تا ایمانی بر ایمانشان بیفزایند؛ لشکریان آسمانها و زمین از آن خداست، و خداوند دانا و حکیم است.'];
ayat[4588]=[48,5,'لِّيُدْخِلَ الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا وَيُكَفِّرَ عَنْهُمْ سَيِّئَاتِهِمْ ۚ وَكَانَ ذَٰلِكَ عِندَ اللَّهِ فَوْزًا عَظِيمًا','هدف (دیگر از آن فتح مبین) این بود که مردان و زنان با ایمان را در باغهایی (از بهشت) وارد کند که نهرها از زیر (درختانش) جاری است، در حالی که جاودانه در آن می‌مانند، و گناهانشان را می‌بخشد، و این نزد خدا رستگاری بزرگی است!'];
ayat[4589]=[48,6,'وَيُعَذِّبَ الْمُنَافِقِينَ وَالْمُنَافِقَاتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَاتِ الظَّانِّينَ بِاللَّهِ ظَنَّ السَّوْءِ ۚ عَلَيْهِمْ دَائِرَةُ السَّوْءِ ۖ وَغَضِبَ اللَّهُ عَلَيْهِمْ وَلَعَنَهُمْ وَأَعَدَّ لَهُمْ جَهَنَّمَ ۖ وَسَاءَتْ مَصِيرًا','و (نیز) مردان و زنان منافق و مردان و زنان مشرک را که به خدا گمان بد می‌برند مجازات کند؛ (آری) حوادث ناگواری (که برای مؤمنان انتظار می‌کشند) تنها بر خودشان نازل می‌شود! خداوند بر آنان غضب کرده و از رحمت خود دورشان ساخته و جهنم را برای آنان آماده کرده؛ و چه بد سرانجامی است!'];
ayat[4590]=[48,7,'وَلِلَّهِ جُنُودُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا','لشکریان آسمانها و زمین تنها از آن خداست؛ و خداوند شکست‌ناپذیر و حکیم است.!'];
ayat[4591]=[48,8,'إِنَّا أَرْسَلْنَاكَ شَاهِدًا وَمُبَشِّرًا وَنَذِيرًا','به یقین ما تو را گواه (بر اعمال آنها) و بشارت‌دهنده و بیم‌دهنده فرستادیم،'];
ayat[4592]=[48,9,'لِّتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُ وَتُسَبِّحُوهُ بُكْرَةً وَأَصِيلًا','تا (شما مردم) به خدا و رسولش ایمان بیاورید و از او دفاع کنید و او را بزرگ دارید، و خدا را صبح و شام تسبیح گویید.'];
ayat[4593]=[48,10,'إِنَّ الَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ اللَّهَ يَدُ اللَّهِ فَوْقَ أَيْدِيهِمْ ۚ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفْسِهِ ۖ وَمَنْ أَوْفَىٰ بِمَا عَاهَدَ عَلَيْهُ اللَّهَ فَسَيُؤْتِيهِ أَجْرًا عَظِيمًا','کسانی که با تو بیعت می‌کنند (در حقیّقت) تنها با خدا بیعت می‌نمایند، و دست خدا بالای دست آنهاست؛ پس هر کس پیمان‌شکنی کند، تنها به زیان خود پیمان شکسته است؛ و آن کس که نسبت به عهدی که با خدا بسته وفا کند، بزودی پاداش عظیمی به او خواهد داد.'];
ayat[4594]=[48,11,'سَيَقُولُ لَكَ الْمُخَلَّفُونَ مِنَ الْأَعْرَابِ شَغَلَتْنَا أَمْوَالُنَا وَأَهْلُونَا فَاسْتَغْفِرْ لَنَا ۚ يَقُولُونَ بِأَلْسِنَتِهِم مَّا لَيْسَ فِي قُلُوبِهِمْ ۚ قُلْ فَمَن يَمْلِكُ لَكُم مِّنَ اللَّهِ شَيْئًا إِنْ أَرَادَ بِكُمْ ضَرًّا أَوْ أَرَادَ بِكُمْ نَفْعًا ۚ بَلْ كَانَ اللَّهُ بِمَا تَعْمَلُونَ خَبِيرًا','بزودی متخلّفان از اعراب بادیه‌نشین (عذرتراشی کرده) می‌گویند: «(حفظ) اموال و خانواده‌های ما، ما را به خود مشغول داشت (و نتوانستیم در سفر حدیبیّه تو را همراهی کنیم)، برای ما طلب آمرزش کن!» آنها به زبان خود چیزی می‌گویند که در دل ندارند! بگو: «چه کسی می‌تواند در برابر خداوند از شما دفاع کند هرگاه زیانی برای شما بخواهد، و یا اگر نفعی اراده کند (مانع گردد)؟! و خداوند به همه کارهایی که انجام می‌دهید آگاه است!»'];
ayat[4595]=[48,12,'بَلْ ظَنَنتُمْ أَن لَّن يَنقَلِبَ الرَّسُولُ وَالْمُؤْمِنُونَ إِلَىٰ أَهْلِيهِمْ أَبَدًا وَزُيِّنَ ذَٰلِكَ فِي قُلُوبِكُمْ وَظَنَنتُمْ ظَنَّ السَّوْءِ وَكُنتُمْ قَوْمًا بُورًا','ولی شما گمان کردید پیامبر و مؤمنان هرگز به خانواده‌های خود بازنخواهند گشت؛ و این (پندار غلط) در دلهای شما زینت یافته بود و گمان بد کردید؛ و سرانجام (در دام شیطان افتادید و) هلاک شدید!'];
ayat[4596]=[48,13,'وَمَن لَّمْ يُؤْمِن بِاللَّهِ وَرَسُولِهِ فَإِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَعِيرًا','آن کس که به خدا و پیامبرش ایمان نیاورده (سرنوشتش دوزخ است)، چرا که ما برای کافران آتش فروزان آماده کرده‌ایم!'];
ayat[4597]=[48,14,'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ يَغْفِرُ لِمَن يَشَاءُ وَيُعَذِّبُ مَن يَشَاءُ ۚ وَكَانَ اللَّهُ غَفُورًا رَّحِيمًا','مالکیت و حاکمیت آسمانها و زمین از آن خداست؛ هر کس را بخواهد (و شایسته بداند) می‌بخشد، و هر کس را بخواهد مجازات می‌کند؛ و خداوند آمرزنده و رحیم است.'];
ayat[4598]=[48,15,'سَيَقُولُ الْمُخَلَّفُونَ إِذَا انطَلَقْتُمْ إِلَىٰ مَغَانِمَ لِتَأْخُذُوهَا ذَرُونَا نَتَّبِعْكُمْ ۖ يُرِيدُونَ أَن يُبَدِّلُوا كَلَامَ اللَّهِ ۚ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمْ قَالَ اللَّهُ مِن قَبْلُ ۖ فَسَيَقُولُونَ بَلْ تَحْسُدُونَنَا ۚ بَلْ كَانُوا لَا يَفْقَهُونَ إِلَّا قَلِيلًا','هنگامی که شما برای به دست آوردن غنایمی حرکت کنید، متخلّفان (حدیبیّه) می‌گویند: «بگذارید ما هم در پی شما بیائیم، آنها می‌خواهند کلام خدا را تغییر دهند؛ بگو: «هرگز نباید بدنبال ما بیایید؛ این گونه خداوند از قبل گفته است!» آنها به زودی می‌گویند: «شما نسبت به ما حسد می‌ورزید!» ولی آنها جز اندکی نمی‌فهمند!'];
ayat[4599]=[48,16,'قُل لِّلْمُخَلَّفِينَ مِنَ الْأَعْرَابِ سَتُدْعَوْنَ إِلَىٰ قَوْمٍ أُولِي بَأْسٍ شَدِيدٍ تُقَاتِلُونَهُمْ أَوْ يُسْلِمُونَ ۖ فَإِن تُطِيعُوا يُؤْتِكُمُ اللَّهُ أَجْرًا حَسَنًا ۖ وَإِن تَتَوَلَّوْا كَمَا تَوَلَّيْتُم مِّن قَبْلُ يُعَذِّبْكُمْ عَذَابًا أَلِيمًا','به متخلّفان از اعراب بگو: «بزودی از شما دعوت می‌شود که بسوی قومی نیرومند و جنگجو بروید و با آنها پیکار کنید تا اسلام بیاورند؛ اگر اطاعت کنید، خداوند پاداش نیکی به شما می‌دهد؛ و اگر سرپیچی نمایید -همان گونه که در گذشته نیز سرپیچی کردید- شما را با عذاب دردناکی کیفر می‌دهد!»'];
ayat[4600]=[48,17,'لَّيْسَ عَلَى الْأَعْمَىٰ حَرَجٌ وَلَا عَلَى الْأَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَرِيضِ حَرَجٌ ۗ وَمَن يُطِعِ اللَّهَ وَرَسُولَهُ يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۖ وَمَن يَتَوَلَّ يُعَذِّبْهُ عَذَابًا أَلِيمًا','بر نابینا و لنگ و بیمار گناهی نیست (اگر در میدان جهاد شرکت نکنند)؛ و هر کس خدا و رسولش را اطاعت نماید، او را در باغهای (از بهشت) وارد می‌کند که نهرها از زیر (درختانش) جاری است؛ و آن کس که سرپیچی کند، او را به عذاب دردناکی گرفتار می‌سازد!'];
ayat[4601]=[48,18,'۞ لَّقَدْ رَضِيَ اللَّهُ عَنِ الْمُؤْمِنِينَ إِذْ يُبَايِعُونَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِمْ فَأَنزَلَ السَّكِينَةَ عَلَيْهِمْ وَأَثَابَهُمْ فَتْحًا قَرِيبًا','خداوند از مؤمنان -هنگامی که در زیر آن درخت با تو بیعت کردند- راضی و خشنود شد؛ خدا آنچه را در درون دلهایشان (از ایمان و صداقت) نهفته بود می‌دانست؛ از این رو آرامش را بر دلهایشان نازل کرد و پیروزی نزدیکی بعنوان پاداش نصیب آنها فرمود؛'];
ayat[4602]=[48,19,'وَمَغَانِمَ كَثِيرَةً يَأْخُذُونَهَا ۗ وَكَانَ اللَّهُ عَزِيزًا حَكِيمًا','و (همچنین) غنایم بسیاری که آن را به دست می‌آورید؛ و خداوند شکست ناپذیر و حکیم است!'];
ayat[4603]=[48,20,'وَعَدَكُمُ اللَّهُ مَغَانِمَ كَثِيرَةً تَأْخُذُونَهَا فَعَجَّلَ لَكُمْ هَٰذِهِ وَكَفَّ أَيْدِيَ النَّاسِ عَنكُمْ وَلِتَكُونَ آيَةً لِّلْمُؤْمِنِينَ وَيَهْدِيَكُمْ صِرَاطًا مُّسْتَقِيمًا','خداوند غنایم فراوانی به شما وعده داده بود که آنها را به دست می‌آورید، ولی این یکی را زودتر برای شما فراهم ساخت؛ و دست تعدّی مردم [= دشمنان‌] را از شما بازداشت تا نشانه‌ای برای مؤمنان باشد و شما را به راه راست هدایت کند!'];
ayat[4604]=[48,21,'وَأُخْرَىٰ لَمْ تَقْدِرُوا عَلَيْهَا قَدْ أَحَاطَ اللَّهُ بِهَا ۚ وَكَانَ اللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرًا','و نیز غنایم و فتوحات دیگری (نصیبتان می‌کند) که شما توانایی آن را ندارید، ولی قدرت خدا به آن احاطه دارد؛ و خداوند بر همه چیز تواناست!'];
ayat[4605]=[48,22,'وَلَوْ قَاتَلَكُمُ الَّذِينَ كَفَرُوا لَوَلَّوُا الْأَدْبَارَ ثُمَّ لَا يَجِدُونَ وَلِيًّا وَلَا نَصِيرًا','و اگر کافران (در سرزمین حدیبیّه) با شما پیکار می‌کردند بزودی فرار می‌کردند، سپس ولیّ و یاوری نمی‌یافتند!'];
ayat[4606]=[48,23,'سُنَّةَ اللَّهِ الَّتِي قَدْ خَلَتْ مِن قَبْلُ ۖ وَلَن تَجِدَ لِسُنَّةِ اللَّهِ تَبْدِيلًا','این سنّت الهی است که در گذشته نیز بوده است؛ و هرگز برای سنّت الهی تغییر و تبدیلی نخواهی یافت!'];
ayat[4607]=[48,24,'وَهُوَ الَّذِي كَفَّ أَيْدِيَهُمْ عَنكُمْ وَأَيْدِيَكُمْ عَنْهُم بِبَطْنِ مَكَّةَ مِن بَعْدِ أَنْ أَظْفَرَكُمْ عَلَيْهِمْ ۚ وَكَانَ اللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا','او کسی است که دست آنها را از شما، و دست شما را از آنان در دل مکّه کوتاه کرد، بعد از آنکه شما را بر آنها پیروز ساخت؛ و خداوند به آنچه انجام می‌دهید بیناست!'];
ayat[4608]=[48,25,'هُمُ الَّذِينَ كَفَرُوا وَصَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ وَالْهَدْيَ مَعْكُوفًا أَن يَبْلُغَ مَحِلَّهُ ۚ وَلَوْلَا رِجَالٌ مُّؤْمِنُونَ وَنِسَاءٌ مُّؤْمِنَاتٌ لَّمْ تَعْلَمُوهُمْ أَن تَطَئُوهُمْ فَتُصِيبَكُم مِّنْهُم مَّعَرَّةٌ بِغَيْرِ عِلْمٍ ۖ لِّيُدْخِلَ اللَّهُ فِي رَحْمَتِهِ مَن يَشَاءُ ۚ لَوْ تَزَيَّلُوا لَعَذَّبْنَا الَّذِينَ كَفَرُوا مِنْهُمْ عَذَابًا أَلِيمًا','آنها کسانی هستند که کافر شدند و شما را از (زیارت) مسجد الحرام و رسیدن قربانیهایتان به محل قربانگاه بازداشتند؛ و هرگاه مردان و زنان با ایمانی در این میان بدون آگاهی شما، زیر دست و پا، از بین نمی‌رفتند که از این راه عیب و عاری ناآگاهانه به شما می‌رسید، (خداوند هرگز مانع این جنگ نمی‌شد)! هدف این بود که خدا هر کس را می‌خواهد در رحمت خود وارد کند؛ و اگر مؤمنان و کفّار (در مکه) از هم جدا می‌شدند، کافران را عذاب دردناکی می‌کردیم!'];
ayat[4609]=[48,26,'إِذْ جَعَلَ الَّذِينَ كَفَرُوا فِي قُلُوبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجَاهِلِيَّةِ فَأَنزَلَ اللَّهُ سَكِينَتَهُ عَلَىٰ رَسُولِهِ وَعَلَى الْمُؤْمِنِينَ وَأَلْزَمَهُمْ كَلِمَةَ التَّقْوَىٰ وَكَانُوا أَحَقَّ بِهَا وَأَهْلَهَا ۚ وَكَانَ اللَّهُ بِكُلِّ شَيْءٍ عَلِيمًا','(به خاطر بیاورید) هنگامی را که کافران در دلهای خود خشم و نخوت جاهلیّت داشتند؛ و (در مقابل،) خداوند آرامش و سکینه خود را بر فرستاده خویش و مؤمنان نازل فرمود و آنها را به حقیقت تقوا ملزم ساخت، و آنان از هر کس شایسته‌تر و اهل آن بودند؛ و خداوند به همه چیز دانا است.'];
ayat[4610]=[48,27,'لَّقَدْ صَدَقَ اللَّهُ رَسُولَهُ الرُّؤْيَا بِالْحَقِّ ۖ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرَامَ إِن شَاءَ اللَّهُ آمِنِينَ مُحَلِّقِينَ رُءُوسَكُمْ وَمُقَصِّرِينَ لَا تَخَافُونَ ۖ فَعَلِمَ مَا لَمْ تَعْلَمُوا فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتْحًا قَرِيبًا','خداوند آنچه را به پیامبرش در عالم خواب نشان داد راست گفت؛ بطور قطع همه شما بخواست خدا وارد مسجد الحرام می‌شوید در نهایت امنیّت و در حالی که سرهای خود را تراشیده یا کوتاه کرده‌اید و از هیچ کس ترس و وحشتی ندارید؛ ولی خداوند چیزهایی را می‌دانست که شما نمی‌دانستید (و در این تأخیر حکمتی بود)؛ و قبل از آن، فتح نزدیکی (برای شما) قرار داده است.'];
ayat[4611]=[48,28,'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ ۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا','او کسی است که رسولش را با هدایت و دین حق فرستاده تا آن را بر همه ادایان پیروز کند؛ و کافی است که خدا گواه این موضوع باشد!'];
ayat[4612]=[48,29,'مُّحَمَّدٌ رَّسُولُ اللَّهِ ۚ وَالَّذِينَ مَعَهُ أَشِدَّاءُ عَلَى الْكُفَّارِ رُحَمَاءُ بَيْنَهُمْ ۖ تَرَاهُمْ رُكَّعًا سُجَّدًا يَبْتَغُونَ فَضْلًا مِّنَ اللَّهِ وَرِضْوَانًا ۖ سِيمَاهُمْ فِي وُجُوهِهِم مِّنْ أَثَرِ السُّجُودِ ۚ ذَٰلِكَ مَثَلُهُمْ فِي التَّوْرَاةِ ۚ وَمَثَلُهُمْ فِي الْإِنجِيلِ كَزَرْعٍ أَخْرَجَ شَطْأَهُ فَآزَرَهُ فَاسْتَغْلَظَ فَاسْتَوَىٰ عَلَىٰ سُوقِهِ يُعْجِبُ الزُّرَّاعَ لِيَغِيظَ بِهِمُ الْكُفَّارَ ۗ وَعَدَ اللَّهُ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنْهُم مَّغْفِرَةً وَأَجْرًا عَظِيمًا','محمّد (ص) فرستاده خداست؛ و کسانی که با او هستند در برابر کفّار سرسخت و شدید، و در میان خود مهربانند؛ پیوسته آنها را در حال رکوع و سجود می‌بینی در حالی که همواره فضل خدا و رضای او را می‌طلبند؛ نشانه آنها در صورتشان از اثر سجده نمایان است؛ این توصیف آنان در تورات و توصیف آنان در انجیل است، همانند زراعتی که جوانه‌های خود را خارج ساخته، سپس به تقویت آن پرداخته تا محکم شده و بر پای خود ایستاده است و بقدری نموّ و رشد کرده که زارعان را به شگفتی وامی‌دارد؛ این برای آن است که کافران را به خشم آورد (ولی) کسانی از آنها را که ایمان آورده و کارهای شایسته انجام داده‌اند، خداوند وعده آمرزش و اجر عظیمی داده است.'];
ayat[4613]=[49,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُقَدِّمُوا بَيْنَ يَدَيِ اللَّهِ وَرَسُولِهِ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ سَمِيعٌ عَلِيمٌ','ای کسانی که ایمان آورده‌اید! چیزی را بر خدا و رسولش مقدّم نشمرید (و پیشی مگیرید)، و تقوای الهی پیشه کنید که خداوند شنوا و داناست!'];
ayat[4614]=[49,2,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَرْفَعُوا أَصْوَاتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلَا تَجْهَرُوا لَهُ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ أَن تَحْبَطَ أَعْمَالُكُمْ وَأَنتُمْ لَا تَشْعُرُونَ','ای کسانی که ایمان آورده‌اید! صدای خود را فراتر از صدای پیامبر نکنید، و در برابر او بلند سخن مگویید (و داد و فریاد نزنید) آن گونه که بعضی از شما در برابر بعضی بلند صدا می‌کنند، مبادا اعمال شما نابود گردد در حالی که نمی‌دانید!'];
ayat[4615]=[49,3,'إِنَّ الَّذِينَ يَغُضُّونَ أَصْوَاتَهُمْ عِندَ رَسُولِ اللَّهِ أُولَٰئِكَ الَّذِينَ امْتَحَنَ اللَّهُ قُلُوبَهُمْ لِلتَّقْوَىٰ ۚ لَهُم مَّغْفِرَةٌ وَأَجْرٌ عَظِيمٌ','آنها که صدای خود را نزد رسول خدا کوتاه می‌کنند همان کسانی هستند که خداوند دلهایشان را برای تقوا خالص نموده، و برای آنان آمرزش و پاداش عظیمی است!'];
ayat[4616]=[49,4,'إِنَّ الَّذِينَ يُنَادُونَكَ مِن وَرَاءِ الْحُجُرَاتِ أَكْثَرُهُمْ لَا يَعْقِلُونَ','(ولی) کسانی که تو را از پشت حجره‌ها بلند صدا می‌زنند، بیشترشان نمی‌فهمند!'];
ayat[4617]=[49,5,'وَلَوْ أَنَّهُمْ صَبَرُوا حَتَّىٰ تَخْرُجَ إِلَيْهِمْ لَكَانَ خَيْرًا لَّهُمْ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','اگر آنها صبر می‌کردند تا خود به سراغشان آیی، برای آنان بهتر بود؛ و خداوند آمرزنده و رحیم است!'];
ayat[4618]=[49,6,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِن جَاءَكُمْ فَاسِقٌ بِنَبَإٍ فَتَبَيَّنُوا أَن تُصِيبُوا قَوْمًا بِجَهَالَةٍ فَتُصْبِحُوا عَلَىٰ مَا فَعَلْتُمْ نَادِمِينَ','ای کسانی که ایمان آورده‌اید! اگر شخص فاسقی خبری برای شما بیاورد، درباره آن تحقیق کنید، مبادا به گروهی از روی نادانی آسیب برسانید و از کرده خود پشیمان شوید!'];
ayat[4619]=[49,7,'وَاعْلَمُوا أَنَّ فِيكُمْ رَسُولَ اللَّهِ ۚ لَوْ يُطِيعُكُمْ فِي كَثِيرٍ مِّنَ الْأَمْرِ لَعَنِتُّمْ وَلَٰكِنَّ اللَّهَ حَبَّبَ إِلَيْكُمُ الْإِيمَانَ وَزَيَّنَهُ فِي قُلُوبِكُمْ وَكَرَّهَ إِلَيْكُمُ الْكُفْرَ وَالْفُسُوقَ وَالْعِصْيَانَ ۚ أُولَٰئِكَ هُمُ الرَّاشِدُونَ','و بدانید رسول خدا در میان شماست؛ هرگاه در بسیاری از کارها از شما اطاعت کند، به مشقّت خواهید افتاد؛ ولی خداوند ایمان را محبوب شما قرار داده و آن را در دلهایتان زینت بخشیده، و (به عکس) کفر و فسق و گناه را منفورتان قرار داده است؛ کسانی که دارای این صفاتند هدایت یافتگانند!'];
ayat[4620]=[49,8,'فَضْلًا مِّنَ اللَّهِ وَنِعْمَةً ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ','(و این برای شما بعنوان) فضل و نعمتی از سوی خداست؛ و خداوند دانا و حکیم است.!'];
ayat[4621]=[49,9,'وَإِن طَائِفَتَانِ مِنَ الْمُؤْمِنِينَ اقْتَتَلُوا فَأَصْلِحُوا بَيْنَهُمَا ۖ فَإِن بَغَتْ إِحْدَاهُمَا عَلَى الْأُخْرَىٰ فَقَاتِلُوا الَّتِي تَبْغِي حَتَّىٰ تَفِيءَ إِلَىٰ أَمْرِ اللَّهِ ۚ فَإِن فَاءَتْ فَأَصْلِحُوا بَيْنَهُمَا بِالْعَدْلِ وَأَقْسِطُوا ۖ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ','و هرگاه دو گروه از مؤمنان با هم به نزاع و جنگ پردازند، آنها را آشتی دهید؛ و اگر یکی از آن دو بر دیگری تجاوز کند، با گروه متجاوز پیکار کنید تا به فرمان خدا بازگردد؛ و هرگاه بازگشت (و زمینه صلح فراهم شد)، در میان آن دو به عدالت صلح برقرار سازید؛ و عدالت پیشه کنید که خداوند عدالت پیشگان را دوست می‌دارد.'];
ayat[4622]=[49,10,'إِنَّمَا الْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا بَيْنَ أَخَوَيْكُمْ ۚ وَاتَّقُوا اللَّهَ لَعَلَّكُمْ تُرْحَمُونَ','مؤمنان برادر یکدیگرند؛ پس دو برادر خود را صلح و آشتی دهید و تقوای الهی پیشه کنید، باشد که مشمول رحمت او شوید!'];
ayat[4623]=[49,11,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا يَسْخَرْ قَوْمٌ مِّن قَوْمٍ عَسَىٰ أَن يَكُونُوا خَيْرًا مِّنْهُمْ وَلَا نِسَاءٌ مِّن نِّسَاءٍ عَسَىٰ أَن يَكُنَّ خَيْرًا مِّنْهُنَّ ۖ وَلَا تَلْمِزُوا أَنفُسَكُمْ وَلَا تَنَابَزُوا بِالْأَلْقَابِ ۖ بِئْسَ الِاسْمُ الْفُسُوقُ بَعْدَ الْإِيمَانِ ۚ وَمَن لَّمْ يَتُبْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ','ای کسانی که ایمان آورده‌اید! نباید گروهی از مردان شما گروه دیگر را مسخره کنند، شاید آنها از اینها بهتر باشند؛ و نه زنانی زنان دیگر را، شاید آنان بهتر از اینان باشند؛ و یکدیگر را مورد طعن و عیبجویی قرار ندهید و با القاب زشت و ناپسند یکدیگر را یاد نکنید، بسیار بد است که بر کسی پس از ایمان نام کفرآمیز بگذارید؛ و آنها که توبه نکنند، ظالم و ستمگرند!'];
ayat[4624]=[49,12,'يَا أَيُّهَا الَّذِينَ آمَنُوا اجْتَنِبُوا كَثِيرًا مِّنَ الظَّنِّ إِنَّ بَعْضَ الظَّنِّ إِثْمٌ ۖ وَلَا تَجَسَّسُوا وَلَا يَغْتَب بَّعْضُكُم بَعْضًا ۚ أَيُحِبُّ أَحَدُكُمْ أَن يَأْكُلَ لَحْمَ أَخِيهِ مَيْتًا فَكَرِهْتُمُوهُ ۚ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ تَوَّابٌ رَّحِيمٌ','ای کسانی که ایمان آورده‌اید! از بسیاری از گمانها بپرهیزید، چرا که بعضی از گمانها گناه است؛ و هرگز (در کار دیگران) تجسّس نکنید؛ و هیچ یک از شما دیگری را غیبت نکند، آیا کسی از شما دوست دارد که گوشت برادر مرده خود را بخورد؟! (به یقین) همه شما از این امر کراهت دارید؛ تقوای الهی پیشه کنید که خداوند توبه‌پذیر و مهربان است!'];
ayat[4625]=[49,13,'يَا أَيُّهَا النَّاسُ إِنَّا خَلَقْنَاكُم مِّن ذَكَرٍ وَأُنثَىٰ وَجَعَلْنَاكُمْ شُعُوبًا وَقَبَائِلَ لِتَعَارَفُوا ۚ إِنَّ أَكْرَمَكُمْ عِندَ اللَّهِ أَتْقَاكُمْ ۚ إِنَّ اللَّهَ عَلِيمٌ خَبِيرٌ','ای مردم! ما شما را از یک مرد و زن آفریدیم و شما را تیره‌ها و قبیله‌ها قرار دادیم تا یکدیگر را بشناسید؛ (اینها ملاک امتیاز نیست،) گرامی‌ترین شما نزد خداوند با تقواترین شماست؛ خداوند دانا و آگاه است!'];
ayat[4626]=[49,14,'۞ قَالَتِ الْأَعْرَابُ آمَنَّا ۖ قُل لَّمْ تُؤْمِنُوا وَلَٰكِن قُولُوا أَسْلَمْنَا وَلَمَّا يَدْخُلِ الْإِيمَانُ فِي قُلُوبِكُمْ ۖ وَإِن تُطِيعُوا اللَّهَ وَرَسُولَهُ لَا يَلِتْكُم مِّنْ أَعْمَالِكُمْ شَيْئًا ۚ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','عربهای بادیه‌نشین گفتند: «ایمان آورده‌ایم» بگو: «شما ایمان نیاورده‌اید، ولی بگویید اسلام آورده‌ایم، امّا هنوز ایمان وارد قلب شما نشده است! و اگر از خدا و رسولش اطاعت کنید، چیزی از پاداش کارهای شما را فروگذار نمی‌کند، خداوند، آمرزنده مهربان است.»'];
ayat[4627]=[49,15,'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ ثُمَّ لَمْ يَرْتَابُوا وَجَاهَدُوا بِأَمْوَالِهِمْ وَأَنفُسِهِمْ فِي سَبِيلِ اللَّهِ ۚ أُولَٰئِكَ هُمُ الصَّادِقُونَ','مؤمنان واقعی تنها کسانی هستند که به خدا و رسولش ایمان آورده‌اند، سپس هرگز شکّ و تردیدی به خود راه نداده و با اموال و جانهای خود در راه خدا جهاد کرده‌اند؛ آنها راستگویانند.'];
ayat[4628]=[49,16,'قُلْ أَتُعَلِّمُونَ اللَّهَ بِدِينِكُمْ وَاللَّهُ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۚ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','بگو: «آیا خدا را از ایمان خود با خبر می‌سازید؟! او تمام آنچه را در آسمانها و زمین است می‌داند؛ و خداوند از همه چیز آگاه است!»'];
ayat[4629]=[49,17,'يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا ۖ قُل لَّا تَمُنُّوا عَلَيَّ إِسْلَامَكُم ۖ بَلِ اللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَدَاكُمْ لِلْإِيمَانِ إِن كُنتُمْ صَادِقِينَ','آنها بر تو منّت می‌نهند که اسلام آورده‌اند؛ بگو: «اسلام آوردن خود را بر من منّت نگذارید، بلکه خداوند بر شما منّت می‌نهد که شما را به سوی ایمان هدایت کرده است، اگر (در ادّعای ایمان) راستگو هستید!'];
ayat[4630]=[49,18,'إِنَّ اللَّهَ يَعْلَمُ غَيْبَ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَاللَّهُ بَصِيرٌ بِمَا تَعْمَلُونَ','خداوند غیبت آسمانها و زمین را می‌داند و نسبت به آنچه انجام می‌دهید بیناست!»'];
ayat[4631]=[50,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ ق ۚ وَالْقُرْآنِ الْمَجِيدِ','ق، سوگند به قرآن مجید (که قیامت و رستاخیز حقّ است)!'];
ayat[4632]=[50,2,'بَلْ عَجِبُوا أَن جَاءَهُم مُّنذِرٌ مِّنْهُمْ فَقَالَ الْكَافِرُونَ هَٰذَا شَيْءٌ عَجِيبٌ','آنها تعجّب کردند که پیامبری انذارگر از میان خودشان آمده؛ و کافران گفتند: «این چیز عجیبی است!'];
ayat[4633]=[50,3,'أَإِذَا مِتْنَا وَكُنَّا تُرَابًا ۖ ذَٰلِكَ رَجْعٌ بَعِيدٌ','آیا هنگامی که مُردیم و خاک شدیم (دوباره به زندگی بازمی‌گردیم)؟! این بازگشتی بعید است!»'];
ayat[4634]=[50,4,'قَدْ عَلِمْنَا مَا تَنقُصُ الْأَرْضُ مِنْهُمْ ۖ وَعِندَنَا كِتَابٌ حَفِيظٌ','ولی ما می‌دانیم آنچه را زمین از بدن آنها می‌کاهد؛ و نزد ما کتابی است که همه چیز در آن محفوظ است!'];
ayat[4635]=[50,5,'بَلْ كَذَّبُوا بِالْحَقِّ لَمَّا جَاءَهُمْ فَهُمْ فِي أَمْرٍ مَّرِيجٍ','آنها حقّ را هنگامی که به سراغشان آمد تکذیب کردند؛ از این رو پیوسته در کار پراکنده خود متحیّرند!'];
ayat[4636]=[50,6,'أَفَلَمْ يَنظُرُوا إِلَى السَّمَاءِ فَوْقَهُمْ كَيْفَ بَنَيْنَاهَا وَزَيَّنَّاهَا وَمَا لَهَا مِن فُرُوجٍ','آیا آنان به آسمان بالای سرشان نگاه نکردند که چگونه ما آن را بنا کرده‌ایم، و چگونه آن را (بوسیله ستارگان) زینت بخشیده‌ایم و هیچ شکاف و شکستی در آن نیست؟!'];
ayat[4637]=[50,7,'وَالْأَرْضَ مَدَدْنَاهَا وَأَلْقَيْنَا فِيهَا رَوَاسِيَ وَأَنبَتْنَا فِيهَا مِن كُلِّ زَوْجٍ بَهِيجٍ','و زمین را گسترش دادیم و در آن کوه‌هائی عظیم و استوار افکندیم و از هر نوع گیاه بهجت‌انگیز در آن رویاندیم،'];
ayat[4638]=[50,8,'تَبْصِرَةً وَذِكْرَىٰ لِكُلِّ عَبْدٍ مُّنِيبٍ','تا وسیله بینایی و یادآوری برای هر بنده توبه کاری باشد!'];
ayat[4639]=[50,9,'وَنَزَّلْنَا مِنَ السَّمَاءِ مَاءً مُّبَارَكًا فَأَنبَتْنَا بِهِ جَنَّاتٍ وَحَبَّ الْحَصِيدِ','و از آسمان، آبی پربرکت نازل کردیم، و بوسیله آن باغها و دانه‌هایی را که درو می‌کند رویاندیم،'];
ayat[4640]=[50,10,'وَالنَّخْلَ بَاسِقَاتٍ لَّهَا طَلْعٌ نَّضِيدٌ','و نخلهای بلندقامت که میوه‌های متراکم دارند؛'];
ayat[4641]=[50,11,'رِّزْقًا لِّلْعِبَادِ ۖ وَأَحْيَيْنَا بِهِ بَلْدَةً مَّيْتًا ۚ كَذَٰلِكَ الْخُرُوجُ','همه اینها برای روزی‌بخشیدن به بندگان است و بوسیله باران سرزمین مرده را زنده کردیم؛ (آری) زنده‌شدن مُردگان نیز همین گونه است!'];
ayat[4642]=[50,12,'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَأَصْحَابُ الرَّسِّ وَثَمُودُ','پیش از آنان قوم نوح و «اصحاب الرسّ» [= قومی که در یمامه زندگی می‌کردند و پیامبری به نام حنظله داشتند] و قوم ثمود (پیامبرانشان را) تکذیب کردند،'];
ayat[4643]=[50,13,'وَعَادٌ وَفِرْعَوْنُ وَإِخْوَانُ لُوطٍ','و همچنین قوم عاد و فرعون و قوم لوط،'];
ayat[4644]=[50,14,'وَأَصْحَابُ الْأَيْكَةِ وَقَوْمُ تُبَّعٍ ۚ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيدِ','و «اصحاب الایکه» [= قوم شعیب‌] و قوم تبّع (که در سرزمین یمن زندگی می‌کردند)، هر یک از آنها فرستادگان الهی را تکذیب کردند و وعده عذاب درباره آنان تحقّق یافت!'];
ayat[4645]=[50,15,'أَفَعَيِينَا بِالْخَلْقِ الْأَوَّلِ ۚ بَلْ هُمْ فِي لَبْسٍ مِّنْ خَلْقٍ جَدِيدٍ','آیا ما از آفرینش نخستین عاجز ماندیم (که قادر بر آفرینش رستاخیز نباشیم)؟! ولی آنها (با این همه دلایل روشن) باز در آفرینش جدید تردید دارند!'];
ayat[4646]=[50,16,'وَلَقَدْ خَلَقْنَا الْإِنسَانَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهِ نَفْسُهُ ۖ وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ الْوَرِيدِ','ما انسان را آفریدیم و وسوسه‌های نفس او را می‌دانیم، و ما به او از رگ قلبش نزدیکتریم!'];
ayat[4647]=[50,17,'إِذْ يَتَلَقَّى الْمُتَلَقِّيَانِ عَنِ الْيَمِينِ وَعَنِ الشِّمَالِ قَعِيدٌ','(به خاطر بیاورید) هنگامی را که دو فرشته راست و چپ که ملازم انسانند اعمال او را دریافت می‌دارند؛'];
ayat[4648]=[50,18,'مَّا يَلْفِظُ مِن قَوْلٍ إِلَّا لَدَيْهِ رَقِيبٌ عَتِيدٌ','انسان هیچ سخنی را بر زبان نمی‌آورد مگر اینکه همان دم، فرشته‌ای مراقب و آماده برای انجام مأموریت (و ضبط آن) است!'];
ayat[4649]=[50,19,'وَجَاءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ ۖ ذَٰلِكَ مَا كُنتَ مِنْهُ تَحِيدُ','و سرانجام، سکرات (و بیخودی در آستانه) مرگ بحق فرامی‌رسد (و به انسان گفته می‌شود:) این همان چیزی است که تو از آن می‌گریختی!'];
ayat[4650]=[50,20,'وَنُفِخَ فِي الصُّورِ ۚ ذَٰلِكَ يَوْمُ الْوَعِيدِ','و در «صور» دمیده می‌شود؛ آن روز، روز تحقّق وعده وحشتناک است!'];
ayat[4651]=[50,21,'وَجَاءَتْ كُلُّ نَفْسٍ مَّعَهَا سَائِقٌ وَشَهِيدٌ','هر انسانی وارد محشر می‌گردد در حالی که همراه او حرکت دهنده و گواهی است!'];
ayat[4652]=[50,22,'لَّقَدْ كُنتَ فِي غَفْلَةٍ مِّنْ هَٰذَا فَكَشَفْنَا عَنكَ غِطَاءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيدٌ','(به او خطاب می‌شود:) تو از این صحنه (و دادگاه بزرگ) غافل بودی و ما پرده را از چشم تو کنار زدیم، و امروز چشمت کاملاً تیزبین است!'];
ayat[4653]=[50,23,'وَقَالَ قَرِينُهُ هَٰذَا مَا لَدَيَّ عَتِيدٌ','فرشته همنشین او می‌گوید: «این نامه اعمال اوست که نزد من حاضر و آماده است!»'];
ayat[4654]=[50,24,'أَلْقِيَا فِي جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٍ','(خداوند فرمان می‌دهد:) هر کافر متکبّر لجوج را در جهنّم افکنید!'];
ayat[4655]=[50,25,'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ مُّرِيبٍ','آن کسی که به شدّت مانع خیر و متجاوز و در شکّ و تردید است (حتی دیگران را به تردید می افکند)؛'];
ayat[4656]=[50,26,'الَّذِي جَعَلَ مَعَ اللَّهِ إِلَٰهًا آخَرَ فَأَلْقِيَاهُ فِي الْعَذَابِ الشَّدِيدِ','همان کسی که معبود دیگری با خدا قرار داده، (آری) او را در عذاب شدید بیفکنید!'];
ayat[4657]=[50,27,'۞ قَالَ قَرِينُهُ رَبَّنَا مَا أَطْغَيْتُهُ وَلَٰكِن كَانَ فِي ضَلَالٍ بَعِيدٍ','و همنشینش (از شیاطین) می‌گوید: «پروردگارا! من او را به طغیان وانداشتم، لکن او خود در گمراهی دور و درازی بود!»'];
ayat[4658]=[50,28,'قَالَ لَا تَخْتَصِمُوا لَدَيَّ وَقَدْ قَدَّمْتُ إِلَيْكُم بِالْوَعِيدِ','(خداوند) می‌گوید: «نزد من جدال و مخاصمه نکنید؛ من پیشتر به شما هشدار داده‌ام (و اتمام حجّت کرده‌ام)!'];
ayat[4659]=[50,29,'مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَمَا أَنَا بِظَلَّامٍ لِّلْعَبِيدِ','سخن من تغییر ناپذیر است، و من هرگز به بندگان ستم نخواهم کرد!»'];
ayat[4660]=[50,30,'يَوْمَ نَقُولُ لِجَهَنَّمَ هَلِ امْتَلَأْتِ وَتَقُولُ هَلْ مِن مَّزِيدٍ','(به خاطر بیاورید) روزی را که به جهنّم می‌گوییم: «آیا پر شده‌ای؟» و او می‌گوید: «آیا افزون بر این هم هست؟!»'];
ayat[4661]=[50,31,'وَأُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِينَ غَيْرَ بَعِيدٍ','(در آن روز) بهشت را به پرهیزگاران نزدیک می‌کنند، و فاصله‌ای از آنان ندارد!'];
ayat[4662]=[50,32,'هَٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٍ','این چیزی است که به شما وعده داده می‌شود، و برای کسانی است که بسوی خدا بازمی‌گردند و پیمانها و احکام او را حفظ می‌کنند،'];
ayat[4663]=[50,33,'مَّنْ خَشِيَ الرَّحْمَٰنَ بِالْغَيْبِ وَجَاءَ بِقَلْبٍ مُّنِيبٍ','آن کس که از خداوند رحمان در نهان بترسد و با قلبی پرانابه در محضر او حاضر شود!'];
ayat[4664]=[50,34,'ادْخُلُوهَا بِسَلَامٍ ۖ ذَٰلِكَ يَوْمُ الْخُلُودِ','(به آنان گفته می‌شود:) بسلامت وارد بهشت شوید، امروز روز جاودانگی است!'];
ayat[4665]=[50,35,'لَهُم مَّا يَشَاءُونَ فِيهَا وَلَدَيْنَا مَزِيدٌ','هر چه بخواهند در آنجا برای آنها هست، و نزد ما نعمتهای بیشتری است (که به فکر هیچ کس نمی‌رسد)!'];
ayat[4666]=[50,36,'وَكَمْ أَهْلَكْنَا قَبْلَهُم مِّن قَرْنٍ هُمْ أَشَدُّ مِنْهُم بَطْشًا فَنَقَّبُوا فِي الْبِلَادِ هَلْ مِن مَّحِيصٍ','چه بسیار اقوامی را که پیش از آنها هلاک کردیم، اقوامی که از آنان قویتر بودند و شهرها (و کشورها) را گشودند؛ آیا راه فراری (از عذاب الهی) وجود دارد!'];
ayat[4667]=[50,37,'إِنَّ فِي ذَٰلِكَ لَذِكْرَىٰ لِمَن كَانَ لَهُ قَلْبٌ أَوْ أَلْقَى السَّمْعَ وَهُوَ شَهِيدٌ','در این تذکّری است برای آن کس که عقل دارد، یا گوش دل فرادهد در حالی که حاضر باشد!'];
ayat[4668]=[50,38,'وَلَقَدْ خَلَقْنَا السَّمَاوَاتِ وَالْأَرْضَ وَمَا بَيْنَهُمَا فِي سِتَّةِ أَيَّامٍ وَمَا مَسَّنَا مِن لُّغُوبٍ','ما آسمانها و زمین و آنچه را در میان آنهاست در شش روز [= شش دوران‌] آفریدیم، و هیچ گونه رنج و سختی به ما نرسید! (با این حال چگونه زنده‌کردن مُردگان برای ما مشکل است؟!)'];
ayat[4669]=[50,39,'فَاصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَقَبْلَ الْغُرُوبِ','در برابر آنچه آنها می‌گویند شکیبا باش، و پیش از طلوع آفتاب و پیش از غروب تسبیح و حمد پروردگارت را بجا آور،'];
ayat[4670]=[50,40,'وَمِنَ اللَّيْلِ فَسَبِّحْهُ وَأَدْبَارَ السُّجُودِ','و در بخشی از شب او را تسبیح کن، و بعد از سجده‌ها!'];
ayat[4671]=[50,41,'وَاسْتَمِعْ يَوْمَ يُنَادِ الْمُنَادِ مِن مَّكَانٍ قَرِيبٍ','و گوش فرا ده و منتظر روزی باش که منادی از مکانی نزدیک ندا می‌دهد،'];
ayat[4672]=[50,42,'يَوْمَ يَسْمَعُونَ الصَّيْحَةَ بِالْحَقِّ ۚ ذَٰلِكَ يَوْمُ الْخُرُوجِ','روزی که همگان صیحه رستاخیز را بحق می‌شنوند؛ آن روز، روز خروج (از قبرها) است!'];
ayat[4673]=[50,43,'إِنَّا نَحْنُ نُحْيِي وَنُمِيتُ وَإِلَيْنَا الْمَصِيرُ','ماییم که زنده می‌کنیم و می‌میرانیم، و بازگشت تنها بسوی ماست!'];
ayat[4674]=[50,44,'يَوْمَ تَشَقَّقُ الْأَرْضُ عَنْهُمْ سِرَاعًا ۚ ذَٰلِكَ حَشْرٌ عَلَيْنَا يَسِيرٌ','روزی که زمین به سرعت از روی آنها شکافته می‌شود و (از قبرها) خارج می‌گردند؛ و این جمع کردن برای ما آسان است!'];
ayat[4675]=[50,45,'نَّحْنُ أَعْلَمُ بِمَا يَقُولُونَ ۖ وَمَا أَنتَ عَلَيْهِم بِجَبَّارٍ ۖ فَذَكِّرْ بِالْقُرْآنِ مَن يَخَافُ وَعِيدِ','ما به آنچه آنها می‌گویند آگاهتریم، و تو مأمور به اجبار آنها (به ایمان) نیستی؛ پس بوسیله قرآن، کسانی را که از عذاب من می‌ترسند متذکّر ساز (وظیفه تو همین است)!'];
ayat[4676]=[51,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالذَّارِيَاتِ ذَرْوًا','سوگند به بادهایی که (ابرها را) به حرکت درمی‌آورند،'];
ayat[4677]=[51,2,'فَالْحَامِلَاتِ وِقْرًا','سوگند به آن ابرها که بار سنگینی (از باران را) با خود حمل می‌کنند،'];
ayat[4678]=[51,3,'فَالْجَارِيَاتِ يُسْرًا','و سوگند به کشتیهایی که به آسانی به حرکت درمی‌آیند،'];
ayat[4679]=[51,4,'فَالْمُقَسِّمَاتِ أَمْرًا','و سوگند به فرشتگانی که کارها را تقسیم می‌کنند،'];
ayat[4680]=[51,5,'إِنَّمَا تُوعَدُونَ لَصَادِقٌ','(آری سوگند به همه اینها) که آنچه به شما وعده شده قطعاً راست است؛'];
ayat[4681]=[51,6,'وَإِنَّ الدِّينَ لَوَاقِعٌ','و بی‌شکّ (رستاخیز) و جزای اعمال واقع‌شدنی است!'];
ayat[4682]=[51,7,'وَالسَّمَاءِ ذَاتِ الْحُبُكِ','قسم به آسمان که دارای چین و شکنهای زیباست،'];
ayat[4683]=[51,8,'إِنَّكُمْ لَفِي قَوْلٍ مُّخْتَلِفٍ','که شما (درباره قیامت) در گفتاری مختلف و گوناگونید!'];
ayat[4684]=[51,9,'يُؤْفَكُ عَنْهُ مَنْ أُفِكَ','(تنها) کسی از ایمان به آن منحرف می‌شود که از قبول حقّ سرباز می‌زند!'];
ayat[4685]=[51,10,'قُتِلَ الْخَرَّاصُونَ','کشته باد دروغگویان (و مرگ بر آنها)!'];
ayat[4686]=[51,11,'الَّذِينَ هُمْ فِي غَمْرَةٍ سَاهُونَ','همانها که در جهل و غفلت فرو رفته‌اند،'];
ayat[4687]=[51,12,'يَسْأَلُونَ أَيَّانَ يَوْمُ الدِّينِ','و پیوسته سؤال می‌کنند: «روز جزا چه موقع است؟!»'];
ayat[4688]=[51,13,'يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُونَ','(آری) همان روزی است که آنها را بر آتش می‌سوزانند!'];
ayat[4689]=[51,14,'ذُوقُوا فِتْنَتَكُمْ هَٰذَا الَّذِي كُنتُم بِهِ تَسْتَعْجِلُونَ','(و گفته می‌شود:) بچشید عذاب خود را، این همان چیزی است که برای آن شتاب داشتید!'];
ayat[4690]=[51,15,'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَعُيُونٍ','به یقین، پرهیزگاران در باغهای بهشت و در میان چشمه‌ها قرار دارند،'];
ayat[4691]=[51,16,'آخِذِينَ مَا آتَاهُمْ رَبُّهُمْ ۚ إِنَّهُمْ كَانُوا قَبْلَ ذَٰلِكَ مُحْسِنِينَ','و آنچه پروردگارشان به آنها بخشیده دریافت می‌دارند، زیرا پیش از آن (در سرای دنیا) از نیکوکاران بودند!'];
ayat[4692]=[51,17,'كَانُوا قَلِيلًا مِّنَ اللَّيْلِ مَا يَهْجَعُونَ','آنها کمی از شب را می‌خوابیدند،'];
ayat[4693]=[51,18,'وَبِالْأَسْحَارِ هُمْ يَسْتَغْفِرُونَ','و در سحرگاهان استغفار می‌کردند،'];
ayat[4694]=[51,19,'وَفِي أَمْوَالِهِمْ حَقٌّ لِّلسَّائِلِ وَالْمَحْرُومِ','و در اموال آنها حقّی برای سائل و محروم بود!'];
ayat[4695]=[51,20,'وَفِي الْأَرْضِ آيَاتٌ لِّلْمُوقِنِينَ','و در زمین آیاتی برای جویندگان یقین است،'];
ayat[4696]=[51,21,'وَفِي أَنفُسِكُمْ ۚ أَفَلَا تُبْصِرُونَ','و در وجود خود شما (نیز آیاتی است)؛ آیا نمی‌بینید؟!'];
ayat[4697]=[51,22,'وَفِي السَّمَاءِ رِزْقُكُمْ وَمَا تُوعَدُونَ','و روزی شما در آسمان است و آنچه به شما وعده داده می‌شود!'];
ayat[4698]=[51,23,'فَوَرَبِّ السَّمَاءِ وَالْأَرْضِ إِنَّهُ لَحَقٌّ مِّثْلَ مَا أَنَّكُمْ تَنطِقُونَ','سوگند به پروردگار آسمان و زمین که این مطلب حقّ است همان گونه که شما سخن می‌گویید!'];
ayat[4699]=[51,24,'هَلْ أَتَاكَ حَدِيثُ ضَيْفِ إِبْرَاهِيمَ الْمُكْرَمِينَ','آیا خبر مهمانهای بزرگوار ابراهیم به تو رسیده است؟'];
ayat[4700]=[51,25,'إِذْ دَخَلُوا عَلَيْهِ فَقَالُوا سَلَامًا ۖ قَالَ سَلَامٌ قَوْمٌ مُّنكَرُونَ','در آن زمان که بر او وارد شدند و گفتند: «سلام بر تو!» او گفت: «سلام بر شما که جمعیّتی ناشناخته‌اید!»'];
ayat[4701]=[51,26,'فَرَاغَ إِلَىٰ أَهْلِهِ فَجَاءَ بِعِجْلٍ سَمِينٍ','سپس پنهانی به سوی خانواده خود رفت و گوساله فربه (و بریان شده‌ای را برای آنها) آورد،'];
ayat[4702]=[51,27,'فَقَرَّبَهُ إِلَيْهِمْ قَالَ أَلَا تَأْكُلُونَ','و نزدیک آنها گذارد، (ولی با تعجّب دید دست بسوی غذا نمی‌برند) گفت: «آیا شما غذا نمی‌خورید؟!»'];
ayat[4703]=[51,28,'فَأَوْجَسَ مِنْهُمْ خِيفَةً ۖ قَالُوا لَا تَخَفْ ۖ وَبَشَّرُوهُ بِغُلَامٍ عَلِيمٍ','و از آنها احساس وحشت کرد، گفتند: «نترس (ما رسولان و فرشتگان پروردگار توایم)!» و او را بشارت به تولّد پسری دانا دادند.'];
ayat[4704]=[51,29,'فَأَقْبَلَتِ امْرَأَتُهُ فِي صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوزٌ عَقِيمٌ','در این هنگام همسرش جلو آمد در حالی که (از خوشحالی و تعجّب) فریاد می‌کشید به صورت خود زد و گفت: «(آیا پسری خواهم آورد در حالی که) پیرزنی نازا هستم؟!»'];
ayat[4705]=[51,30,'قَالُوا كَذَٰلِكِ قَالَ رَبُّكِ ۖ إِنَّهُ هُوَ الْحَكِيمُ الْعَلِيمُ','گفتند: «پروردگارت چنین گفته است، و او حکیم و داناست!»'];
ayat[4706]=[51,31,'۞ قَالَ فَمَا خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ','(ابراهیم) گفت: «مأموریت شما چیست ای فرستادگان (خدا)؟»'];
ayat[4707]=[51,32,'قَالُوا إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمٍ مُّجْرِمِينَ','گفتند: «ما به سوی قوم مجرمی فرستاده شده‌ایم...'];
ayat[4708]=[51,33,'لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِّن طِينٍ','تا بارانی از «سنگ - گِل» بر آنها بفرستیم؛'];
ayat[4709]=[51,34,'مُّسَوَّمَةً عِندَ رَبِّكَ لِلْمُسْرِفِينَ','سنگهایی که از ناحیه پروردگارت برای اسرافکاران نشان گذاشته شده است!»'];
ayat[4710]=[51,35,'فَأَخْرَجْنَا مَن كَانَ فِيهَا مِنَ الْمُؤْمِنِينَ','ما مؤمنانی را که در آن شهرها (ی قوم لوط) زندگی می‌کردند (قبل از نزول عذاب) خارج کردیم،'];
ayat[4711]=[51,36,'فَمَا وَجَدْنَا فِيهَا غَيْرَ بَيْتٍ مِّنَ الْمُسْلِمِينَ','ولی جز یک خانواده باایمان در تمام آنها نیافتیم!'];
ayat[4712]=[51,37,'وَتَرَكْنَا فِيهَا آيَةً لِّلَّذِينَ يَخَافُونَ الْعَذَابَ الْأَلِيمَ','و در آن (شهرهای بلا دیده) نشانه‌ای روشن برای کسانی که از عذاب دردناک می‌ترسند به جای گذاردیم.'];
ayat[4713]=[51,38,'وَفِي مُوسَىٰ إِذْ أَرْسَلْنَاهُ إِلَىٰ فِرْعَوْنَ بِسُلْطَانٍ مُّبِينٍ','و در (زندگی) موسی نیز (نشانه و درس عبرتی بود) هنگامی که او را با دلیلی آشکار به سوی فرعون فرستادیم؛'];
ayat[4714]=[51,39,'فَتَوَلَّىٰ بِرُكْنِهِ وَقَالَ سَاحِرٌ أَوْ مَجْنُونٌ','امّا او با تمام وجودش از وی روی برتافت و گفت: «این مرد یا ساحر است یا دیوانه!»'];
ayat[4715]=[51,40,'فَأَخَذْنَاهُ وَجُنُودَهُ فَنَبَذْنَاهُمْ فِي الْيَمِّ وَهُوَ مُلِيمٌ','از این رو ما او و لشکریانش را گرفتیم و به دریا افکندیم در حالی که در خور سرزنش بود!'];
ayat[4716]=[51,41,'وَفِي عَادٍ إِذْ أَرْسَلْنَا عَلَيْهِمُ الرِّيحَ الْعَقِيمَ','و (همچنین) در سرگذشت «عاد» (آیتی است) در آن هنگام که تندبادی بی‌باران بر آنها فرستادیم،'];
ayat[4717]=[51,42,'مَا تَذَرُ مِن شَيْءٍ أَتَتْ عَلَيْهِ إِلَّا جَعَلَتْهُ كَالرَّمِيمِ','که بر هیچ چیز نمی‌گذشت مگر اینکه آن را همچون استخوانهای پوسیده می‌ساخت.'];
ayat[4718]=[51,43,'وَفِي ثَمُودَ إِذْ قِيلَ لَهُمْ تَمَتَّعُوا حَتَّىٰ حِينٍ','و نیز در سرگذشت قوم «ثمود» عبرتی است در آن هنگام که به آنان گفته شد: «مدّتی کوتاه بهره‌مند باشید (و سپس منتظر عذاب)!»'];
ayat[4719]=[51,44,'فَعَتَوْا عَنْ أَمْرِ رَبِّهِمْ فَأَخَذَتْهُمُ الصَّاعِقَةُ وَهُمْ يَنظُرُونَ','آنها از فرمان پروردگارشان سرباز زدند، و صاعقه آنان را فراگرفت در حالی که (خیره خیره) نگاه می‌کردند (بی‌آنکه قدرت دفاع داشته باشند)!'];
ayat[4720]=[51,45,'فَمَا اسْتَطَاعُوا مِن قِيَامٍ وَمَا كَانُوا مُنتَصِرِينَ','چنان بر زمین افتادند که توان برخاستن نداشتند و نتوانستند از کسی یاری طلبند!'];
ayat[4721]=[51,46,'وَقَوْمَ نُوحٍ مِّن قَبْلُ ۖ إِنَّهُمْ كَانُوا قَوْمًا فَاسِقِينَ','همچنین قوم نوح را پیش از آنها هلاک کردیم، چرا که قوم فاسقی بودند!'];
ayat[4722]=[51,47,'وَالسَّمَاءَ بَنَيْنَاهَا بِأَيْدٍ وَإِنَّا لَمُوسِعُونَ','و ما آسمان را با قدرت بنا کردیم، و همواره آن را وسعت می‌بخشیم!'];
ayat[4723]=[51,48,'وَالْأَرْضَ فَرَشْنَاهَا فَنِعْمَ الْمَاهِدُونَ','و زمین را گستردیم، و چه خوب گستراننده‌ای هستیم!'];
ayat[4724]=[51,49,'وَمِن كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ','و از هر چیز دو جفت آفریدیم، شاید متذکّر شوید!'];
ayat[4725]=[51,50,'فَفِرُّوا إِلَى اللَّهِ ۖ إِنِّي لَكُم مِّنْهُ نَذِيرٌ مُّبِينٌ','پس به سوی خدا بگریزید، که من از سوی او برای شما بیم‌دهنده‌ای آشکارم!'];
ayat[4726]=[51,51,'وَلَا تَجْعَلُوا مَعَ اللَّهِ إِلَٰهًا آخَرَ ۖ إِنِّي لَكُم مِّنْهُ نَذِيرٌ مُّبِينٌ','و با خدا معبود دیگری قرار ندهید، که من برای شما از سوی او بیم‌دهنده‌ای آشکارم!'];
ayat[4727]=[51,52,'كَذَٰلِكَ مَا أَتَى الَّذِينَ مِن قَبْلِهِم مِّن رَّسُولٍ إِلَّا قَالُوا سَاحِرٌ أَوْ مَجْنُونٌ','این گونه است که هیچ پیامبری قبل از اینها بسوی قومی فرستاده نشد مگر اینکه گفتند: «او ساحر است یا دیوانه!»'];
ayat[4728]=[51,53,'أَتَوَاصَوْا بِهِ ۚ بَلْ هُمْ قَوْمٌ طَاغُونَ','آیا یکدیگر را به آن سفارش می‌کردند (که همه چنین تهمتی بزنند)؟! نه، بلکه آنها قومی طغیانگرند.'];
ayat[4729]=[51,54,'فَتَوَلَّ عَنْهُمْ فَمَا أَنتَ بِمَلُومٍ','حال که چنین است از آنها روی بگردان که هرگز در خور ملامت نخواهی بود؛'];
ayat[4730]=[51,55,'وَذَكِّرْ فَإِنَّ الذِّكْرَىٰ تَنفَعُ الْمُؤْمِنِينَ','و پیوسته تذکّر ده، زیرا تذکّر مؤمنان را سود می‌بخشد.'];
ayat[4731]=[51,56,'وَمَا خَلَقْتُ الْجِنَّ وَالْإِنسَ إِلَّا لِيَعْبُدُونِ','من جنّ و انس را نیافریدم جز برای اینکه عبادتم کنند (و از این راه تکامل یابند و به من نزدیک شوند)!'];
ayat[4732]=[51,57,'مَا أُرِيدُ مِنْهُم مِّن رِّزْقٍ وَمَا أُرِيدُ أَن يُطْعِمُونِ','هرگز از آنها روزی نمی‌خواهم، و نمی‌خواهم مرا اطعام کنند!'];
ayat[4733]=[51,58,'إِنَّ اللَّهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَتِينُ','خداوند روزی‌دهنده و صاحب قوّت و قدرت است!'];
ayat[4734]=[51,59,'فَإِنَّ لِلَّذِينَ ظَلَمُوا ذَنُوبًا مِّثْلَ ذَنُوبِ أَصْحَابِهِمْ فَلَا يَسْتَعْجِلُونِ','و برای کسانی که ستم کردند، سهم بزرگی از عذاب است همانند سهم یارانشان (از اقوام ستمگر پیشین)؛ بنابر این عجله نکنند!'];
ayat[4735]=[51,60,'فَوَيْلٌ لِّلَّذِينَ كَفَرُوا مِن يَوْمِهِمُ الَّذِي يُوعَدُونَ','پس وای بر کسانی که کافر شدند از روزی که به آنها وعده داده می‌شود!'];
ayat[4736]=[52,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالطُّورِ','سوگند به کوه طور،'];
ayat[4737]=[52,2,'وَكِتَابٍ مَّسْطُورٍ','و کتابی که نوشته شده،'];
ayat[4738]=[52,3,'فِي رَقٍّ مَّنشُورٍ','در صفحه‌ای گسترده،'];
ayat[4739]=[52,4,'وَالْبَيْتِ الْمَعْمُورِ','و سوگند به «بیت المعمور»،'];
ayat[4740]=[52,5,'وَالسَّقْفِ الْمَرْفُوعِ','و سقف برافراشته،'];
ayat[4741]=[52,6,'وَالْبَحْرِ الْمَسْجُورِ','و دریای مملوّ و برافروخته،'];
ayat[4742]=[52,7,'إِنَّ عَذَابَ رَبِّكَ لَوَاقِعٌ','که عذاب پروردگارت واقع می‌شود،'];
ayat[4743]=[52,8,'مَّا لَهُ مِن دَافِعٍ','و چیزی از آن مانع نخواهد بود!'];
ayat[4744]=[52,9,'يَوْمَ تَمُورُ السَّمَاءُ مَوْرًا','(این عذاب الهی) در آن روزی است که آسمان به شدّت به حرکت درمی‌آید،'];
ayat[4745]=[52,10,'وَتَسِيرُ الْجِبَالُ سَيْرًا','و کوه‌ها از جا کنده و متحرّک می‌شوند!'];
ayat[4746]=[52,11,'فَوَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان،'];
ayat[4747]=[52,12,'الَّذِينَ هُمْ فِي خَوْضٍ يَلْعَبُونَ','همانها که در سخنان باطل به بازی مشغولند!'];
ayat[4748]=[52,13,'يَوْمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا','در آن روز که آنها را بزور به سوی آتش دوزخ می‌رانند!'];
ayat[4749]=[52,14,'هَٰذِهِ النَّارُ الَّتِي كُنتُم بِهَا تُكَذِّبُونَ','(به آنها می‌گویند:) این همان آتشی است که آن را انکار می‌کردید.'];
ayat[4750]=[52,15,'أَفَسِحْرٌ هَٰذَا أَمْ أَنتُمْ لَا تُبْصِرُونَ','آیا این سحر است یا شما نمی‌بینید؟!'];
ayat[4751]=[52,16,'اصْلَوْهَا فَاصْبِرُوا أَوْ لَا تَصْبِرُوا سَوَاءٌ عَلَيْكُمْ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ','در آن وارد شوید و بسوزید؛ می‌خواهید صبر کنید یا نکنید، برای شما یکسان است؛ چرا که تنها به اعمالتان جزا داده می‌شوید!'];
ayat[4752]=[52,17,'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَنَعِيمٍ','ولی پرهیزگاران در میان باغهای بهشت و نعمتهای فراوان جای دارند،'];
ayat[4753]=[52,18,'فَاكِهِينَ بِمَا آتَاهُمْ رَبُّهُمْ وَوَقَاهُمْ رَبُّهُمْ عَذَابَ الْجَحِيمِ','و از آنچه پروردگارشان به آنها داده و آنان را از عذاب دوزخ نگاه داشته است شاد و مسرورند!'];
ayat[4754]=[52,19,'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا كُنتُمْ تَعْمَلُونَ','(به آنها گفته می‌شود:) بخورید و بیاشامید گوارا؛ اینها در برابر اعمالی است که انجام می‌دادید!'];
ayat[4755]=[52,20,'مُتَّكِئِينَ عَلَىٰ سُرُرٍ مَّصْفُوفَةٍ ۖ وَزَوَّجْنَاهُم بِحُورٍ عِينٍ','این در حالی که بر تختهای صف‌کشیده در کنار هم تکیه می‌زنند، و «حور العین» را به همسری آنها درمی‌آوریم!'];
ayat[4756]=[52,21,'وَالَّذِينَ آمَنُوا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُم بِإِيمَانٍ أَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَا أَلَتْنَاهُم مِّنْ عَمَلِهِم مِّن شَيْءٍ ۚ كُلُّ امْرِئٍ بِمَا كَسَبَ رَهِينٌ','کسانی که ایمان آوردند و فرزندانشان به پیروی از آنان ایمان اختیار کردند، فرزندانشان را (در بهشت) به آنان ملحق می‌کنیم؛ و از (پاداش) عملشان چیزی نمی‌کاهیم؛ و هر کس در گرو اعمال خویش است!'];
ayat[4757]=[52,22,'وَأَمْدَدْنَاهُم بِفَاكِهَةٍ وَلَحْمٍ مِّمَّا يَشْتَهُونَ','و همواره از انواع میوه‌ها و گوشتها -از هر نوع که بخواهند- در اختیارشان می‌گذاریم!'];
ayat[4758]=[52,23,'يَتَنَازَعُونَ فِيهَا كَأْسًا لَّا لَغْوٌ فِيهَا وَلَا تَأْثِيمٌ','آنها در بهشت جامهای پر از شراب طهور را که نه بیهوده‌گویی در آن است و نه گناه، از یکدیگر می‌گیرند!'];
ayat[4759]=[52,24,'۞ وَيَطُوفُ عَلَيْهِمْ غِلْمَانٌ لَّهُمْ كَأَنَّهُمْ لُؤْلُؤٌ مَّكْنُونٌ','و پیوسته بر گردشان نوجوانانی برای (خدمت) آنان گردش می‌کنند که همچون مرواریدهای درون صدفند!'];
ayat[4760]=[52,25,'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَاءَلُونَ','در این هنگام رو به یکدیگر کرده (از گذشته) سؤال می‌نمایند؛'];
ayat[4761]=[52,26,'قَالُوا إِنَّا كُنَّا قَبْلُ فِي أَهْلِنَا مُشْفِقِينَ','می‌گویند: «ما در میان خانواده خود ترسان بودیم (مبادا گناهان آنها دامن ما را بگیرد)!'];
ayat[4762]=[52,27,'فَمَنَّ اللَّهُ عَلَيْنَا وَوَقَانَا عَذَابَ السَّمُومِ','امّا خداوند بر ما منّت نهاد و از عذاب‌کشنده ما را حفظ کرد!'];
ayat[4763]=[52,28,'إِنَّا كُنَّا مِن قَبْلُ نَدْعُوهُ ۖ إِنَّهُ هُوَ الْبَرُّ الرَّحِيمُ','ما از پیش او را می‌خواندیم (و می‌پرستیدیم)، که اوست نیکوکار و مهربان!»'];
ayat[4764]=[52,29,'فَذَكِّرْ فَمَا أَنتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَلَا مَجْنُونٍ','پس تذکّر ده، که به لطف پروردگارت تو کاهن و مجنون نیستی!'];
ayat[4765]=[52,30,'أَمْ يَقُولُونَ شَاعِرٌ نَّتَرَبَّصُ بِهِ رَيْبَ الْمَنُونِ','بلکه آنها می‌گویند: «او شاعری است که ما انتظار مرگش را می‌کشیم!»'];
ayat[4766]=[52,31,'قُلْ تَرَبَّصُوا فَإِنِّي مَعَكُم مِّنَ الْمُتَرَبِّصِينَ','بگو: «انتظار بکشید که من هم با (شما انتظار می‌کشم شما انتظار مرگ مرا، و من انتظار نابودی شما را با عذاب الهی)!»'];
ayat[4767]=[52,32,'أَمْ تَأْمُرُهُمْ أَحْلَامُهُم بِهَٰذَا ۚ أَمْ هُمْ قَوْمٌ طَاغُونَ','آیا عقلهایشان آنها را به این اعمال دستور می‌دهد، یا قومی طغیانگرند؟'];
ayat[4768]=[52,33,'أَمْ يَقُولُونَ تَقَوَّلَهُ ۚ بَل لَّا يُؤْمِنُونَ','یا می‌گویند: «قرآن را به خدا افترا بسته»، ولی آنان ایمان ندارند.'];
ayat[4769]=[52,34,'فَلْيَأْتُوا بِحَدِيثٍ مِّثْلِهِ إِن كَانُوا صَادِقِينَ','اگر راست می‌گویند سخنی همانند آن بیاورند!'];
ayat[4770]=[52,35,'أَمْ خُلِقُوا مِنْ غَيْرِ شَيْءٍ أَمْ هُمُ الْخَالِقُونَ','یا آنها بی هیچ آفریده شده‌اند، یا خود خالق خویشند؟!'];
ayat[4771]=[52,36,'أَمْ خَلَقُوا السَّمَاوَاتِ وَالْأَرْضَ ۚ بَل لَّا يُوقِنُونَ','آیا آنها آسمانها و زمین را آفریده‌اند؟! بلکه آنها جویای یقین نیستند!'];
ayat[4772]=[52,37,'أَمْ عِندَهُمْ خَزَائِنُ رَبِّكَ أَمْ هُمُ الْمُصَيْطِرُونَ','آیا خزاین پروردگارت نزد آنهاست؟! یا بر همه چیز عالم سیطره دارند؟!'];
ayat[4773]=[52,38,'أَمْ لَهُمْ سُلَّمٌ يَسْتَمِعُونَ فِيهِ ۖ فَلْيَأْتِ مُسْتَمِعُهُم بِسُلْطَانٍ مُّبِينٍ','آیا نردبانی دارند (که به آسمان بالا می‌روند) و بوسیله آن اسرار وحی را می‌شنوند؟! کسی که از آنها این ادّعا را دارد دلیل روشنی بیاورد!'];
ayat[4774]=[52,39,'أَمْ لَهُ الْبَنَاتُ وَلَكُمُ الْبَنُونَ','آیا سهم خدا دختران است و سهم شما پسران (که فرشتگان را دختران خدا می‌نامید)؟!'];
ayat[4775]=[52,40,'أَمْ تَسْأَلُهُمْ أَجْرًا فَهُم مِّن مَّغْرَمٍ مُّثْقَلُونَ','آیا تو از آنها پاداشی می‌طلبی که در زیر بار گران آن قرار دارند؟!'];
ayat[4776]=[52,41,'أَمْ عِندَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ','آیا اسرار غیب نزد آنهاست و از روی آن می‌نویسند؟!'];
ayat[4777]=[52,42,'أَمْ يُرِيدُونَ كَيْدًا ۖ فَالَّذِينَ كَفَرُوا هُمُ الْمَكِيدُونَ','آیا می‌خواهند نقشه شیطانی برای تو بکشند؟! ولی بدانند خود کافران در دام این نقشه‌ها گرفتار می‌شوند!'];
ayat[4778]=[52,43,'أَمْ لَهُمْ إِلَٰهٌ غَيْرُ اللَّهِ ۚ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ','یا معبودی غیر خداوند دارند (که قول یاری به آنها داده)؟! منزّه است خدا از آنچه همتای او قرارمی‌دهند!'];
ayat[4779]=[52,44,'وَإِن يَرَوْا كِسْفًا مِّنَ السَّمَاءِ سَاقِطًا يَقُولُوا سَحَابٌ مَّرْكُومٌ','آنها (چنان لجوجند که) اگر ببینند قطعه سنگی از آسمان (برای عذابشان) سقوط می‌کند می‌گویند: «این ابر متراکمی است!»'];
ayat[4780]=[52,45,'فَذَرْهُمْ حَتَّىٰ يُلَاقُوا يَوْمَهُمُ الَّذِي فِيهِ يُصْعَقُونَ','حال که چنین است آنها را رها کن تا روز مرگ خود را ملاقات کنند؛'];
ayat[4781]=[52,46,'يَوْمَ لَا يُغْنِي عَنْهُمْ كَيْدُهُمْ شَيْئًا وَلَا هُمْ يُنصَرُونَ','روزی که نقشه‌های آنان سودی به حالشان نخواهد داشت و (از هیچ سو) یاری نمی‌شوند!'];
ayat[4782]=[52,47,'وَإِنَّ لِلَّذِينَ ظَلَمُوا عَذَابًا دُونَ ذَٰلِكَ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ','و برای ستمگران عذابی قبل از آن است (در همین جهان)؛ ولی بیشترشان نمی‌دانند!'];
ayat[4783]=[52,48,'وَاصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنَا ۖ وَسَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ','در راه ابلاغ حکم پروردگارت صبر و استقامت کن، چرا که تو در حفاظت کامل ما قرار داری! و هنگامی که برمی‌خیزی پروردگارت را تسبیح و حمد گوی!'];
ayat[4784]=[52,49,'وَمِنَ اللَّيْلِ فَسَبِّحْهُ وَإِدْبَارَ النُّجُومِ','(همچنین) به هنگام شب او را تسبیح کن و به هنگام پشت کردن ستارگان (و طلوع صبح)!'];
ayat[4785]=[53,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالنَّجْمِ إِذَا هَوَىٰ','سوگند به ستاره هنگامی که افول می‌کند،'];
ayat[4786]=[53,2,'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوَىٰ','که هرگز دوست شما [= محمّد «ص»] منحرف نشده و مقصد را گم نکرده است،'];
ayat[4787]=[53,3,'وَمَا يَنطِقُ عَنِ الْهَوَىٰ','و هرگز از روی هوای نفس سخن نمی‌گوید!'];
ayat[4788]=[53,4,'إِنْ هُوَ إِلَّا وَحْيٌ يُوحَىٰ','آنچه می‌گوید چیزی جز وحی که بر او نازل شده نیست!'];
ayat[4789]=[53,5,'عَلَّمَهُ شَدِيدُ الْقُوَىٰ','آن کس که قدرت عظیمی دارد [= جبرئیل امین‌] او را تعلیم داده است؛'];
ayat[4790]=[53,6,'ذُو مِرَّةٍ فَاسْتَوَىٰ','همان کس که توانایی فوق العاده دارد؛ او سلطه یافت...'];
ayat[4791]=[53,7,'وَهُوَ بِالْأُفُقِ الْأَعْلَىٰ','در حالی که در اُفق اعلی قرار داشت!'];
ayat[4792]=[53,8,'ثُمَّ دَنَا فَتَدَلَّىٰ','سپس نزدیکتر و نزدیکتر شد...'];
ayat[4793]=[53,9,'فَكَانَ قَابَ قَوْسَيْنِ أَوْ أَدْنَىٰ','تا آنکه فاصله او (با پیامبر) به اندازه فاصله دو کمان یا کمتر بود؛'];
ayat[4794]=[53,10,'فَأَوْحَىٰ إِلَىٰ عَبْدِهِ مَا أَوْحَىٰ','در اینجا خداوند آنچه را وحی کردنی بود به بنده‌اش وحی نمود.'];
ayat[4795]=[53,11,'مَا كَذَبَ الْفُؤَادُ مَا رَأَىٰ','قلب (پاک او) در آنچه دید هرگز دروغ نگفت.'];
ayat[4796]=[53,12,'أَفَتُمَارُونَهُ عَلَىٰ مَا يَرَىٰ','آیا با او درباره آنچه (با چشم خود) دیده مجادله می‌کنید؟!'];
ayat[4797]=[53,13,'وَلَقَدْ رَآهُ نَزْلَةً أُخْرَىٰ','و بار دیگر نیز او را مشاهده کرد،'];
ayat[4798]=[53,14,'عِندَ سِدْرَةِ الْمُنتَهَىٰ','نزد «سدرة المنتهی»،'];
ayat[4799]=[53,15,'عِندَهَا جَنَّةُ الْمَأْوَىٰ','که «جنت المأوی» در آنجاست!'];
ayat[4800]=[53,16,'إِذْ يَغْشَى السِّدْرَةَ مَا يَغْشَىٰ','در آن هنگام که چیزی [= نور خیره‌کننده‌ای‌] سدرة المنتهی را پوشانده بود،'];
ayat[4801]=[53,17,'مَا زَاغَ الْبَصَرُ وَمَا طَغَىٰ','چشم او هرگز منحرف نشد و طغیان نکرد (آنچه دید واقعیّت بود)!'];
ayat[4802]=[53,18,'لَقَدْ رَأَىٰ مِنْ آيَاتِ رَبِّهِ الْكُبْرَىٰ','او پاره‌ای از آیات و نشانه‌های بزرگ پروردگارش را دید!'];
ayat[4803]=[53,19,'أَفَرَأَيْتُمُ اللَّاتَ وَالْعُزَّىٰ','به من خبر دهید آیا بتهای «لات» و «عزّی»...'];
ayat[4804]=[53,20,'وَمَنَاةَ الثَّالِثَةَ الْأُخْرَىٰ','و «منات» که سوّمین آنهاست (دختران خدا هستند)؟!'];
ayat[4805]=[53,21,'أَلَكُمُ الذَّكَرُ وَلَهُ الْأُنثَىٰ','آیا سهم شما پسر است و سهم او دختر؟! (در حالی که بزعم شما دختران کم ارزش‌ترند!)'];
ayat[4806]=[53,22,'تِلْكَ إِذًا قِسْمَةٌ ضِيزَىٰ','در این صورت این تقسیمی ناعادلانه است!'];
ayat[4807]=[53,23,'إِنْ هِيَ إِلَّا أَسْمَاءٌ سَمَّيْتُمُوهَا أَنتُمْ وَآبَاؤُكُم مَّا أَنزَلَ اللَّهُ بِهَا مِن سُلْطَانٍ ۚ إِن يَتَّبِعُونَ إِلَّا الظَّنَّ وَمَا تَهْوَى الْأَنفُسُ ۖ وَلَقَدْ جَاءَهُم مِّن رَّبِّهِمُ الْهُدَىٰ','اینها فقط نامهایی است که شما و پدرانتان بر آنها گذاشته‌اید (نامهایی بی‌محتوا و اسمهایی بی مسمّا)، و هرگز خداوند دلیل و حجتی بر آن نازل نکرده؛ آنان فقط از گمانهای بی‌اساس و هوای نفس پیروی می‌کنند در حالی که هدایت از سوی پروردگارشان برای آنها آمده است!'];
ayat[4808]=[53,24,'أَمْ لِلْإِنسَانِ مَا تَمَنَّىٰ','یا آنچه انسان تمنّا دارد به آن می‌رسد؟!'];
ayat[4809]=[53,25,'فَلِلَّهِ الْآخِرَةُ وَالْأُولَىٰ','در حالی که آخرت و دنیا از آن خداست!'];
ayat[4810]=[53,26,'۞ وَكَم مِّن مَّلَكٍ فِي السَّمَاوَاتِ لَا تُغْنِي شَفَاعَتُهُمْ شَيْئًا إِلَّا مِن بَعْدِ أَن يَأْذَنَ اللَّهُ لِمَن يَشَاءُ وَيَرْضَىٰ','و چه بسیار فرشتگان آسمانها که شفاعت آنها سودی نمی‌بخشد مگر پس از آنکه خدا برای هر کس بخواهد و راضی باشد اجازه (شفاعت) دهد!'];
ayat[4811]=[53,27,'إِنَّ الَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ لَيُسَمُّونَ الْمَلَائِكَةَ تَسْمِيَةَ الْأُنثَىٰ','کسانی که به آخرت ایمان ندارند، فرشتگان را دختر (خدا) نامگذاری می‌کنند!'];
ayat[4812]=[53,28,'وَمَا لَهُم بِهِ مِنْ عِلْمٍ ۖ إِن يَتَّبِعُونَ إِلَّا الظَّنَّ ۖ وَإِنَّ الظَّنَّ لَا يُغْنِي مِنَ الْحَقِّ شَيْئًا','آنها هرگز به این سخن دانشی ندارند، تنها از گمان بی‌پایه پیروی می‌کنند با اینکه «گمان» هرگز انسان را از حقّ بی‌نیاز نمی‌کند!'];
ayat[4813]=[53,29,'فَأَعْرِضْ عَن مَّن تَوَلَّىٰ عَن ذِكْرِنَا وَلَمْ يُرِدْ إِلَّا الْحَيَاةَ الدُّنْيَا','حال که چنین است از کسی که از یاد ما روی می‌گرداند و جز زندگی مادی دنیا را نمی‌طلبد، اعراض کن!'];
ayat[4814]=[53,30,'ذَٰلِكَ مَبْلَغُهُم مِّنَ الْعِلْمِ ۚ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِ وَهُوَ أَعْلَمُ بِمَنِ اهْتَدَىٰ','این آخرین حدّ آگاهی آنهاست؛ پروردگار تو کسانی را که از راه او گمراه شده‌اند بهتر می‌شناسد، و (همچنین) هدایت‌یافتگان را از همه بهتر می‌شناسد!'];
ayat[4815]=[53,31,'وَلِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ لِيَجْزِيَ الَّذِينَ أَسَاءُوا بِمَا عَمِلُوا وَيَجْزِيَ الَّذِينَ أَحْسَنُوا بِالْحُسْنَى','و برای خداست آنچه در آسمانها و آنچه در زمین است تا بدکاران را به کیفر کارهای بدشان برساند و نیکوکاران را در برابر اعمال نیکشان پاداش دهد!'];
ayat[4816]=[53,32,'الَّذِينَ يَجْتَنِبُونَ كَبَائِرَ الْإِثْمِ وَالْفَوَاحِشَ إِلَّا اللَّمَمَ ۚ إِنَّ رَبَّكَ وَاسِعُ الْمَغْفِرَةِ ۚ هُوَ أَعْلَمُ بِكُمْ إِذْ أَنشَأَكُم مِّنَ الْأَرْضِ وَإِذْ أَنتُمْ أَجِنَّةٌ فِي بُطُونِ أُمَّهَاتِكُمْ ۖ فَلَا تُزَكُّوا أَنفُسَكُمْ ۖ هُوَ أَعْلَمُ بِمَنِ اتَّقَىٰ','همانها که از گناهان بزرگ و اعمال زشت دوری می‌کنند، جز گناهان صغیره (که گاه آلوده آن می‌شوند)؛ آمرزش پروردگار تو گسترده است؛ او نسبت به شما از همه آگاهتر است از آن هنگام که شما را از زمین آفرید و در آن موقع که بصورت جنینهایی در شکم مادرانتان بودید؛ پس خودستایی نکنید، او پرهیزگاران را بهتر می‌شناسد!'];
ayat[4817]=[53,33,'أَفَرَأَيْتَ الَّذِي تَوَلَّىٰ','آیا دیدی آن کس را که (از اسلام -یا انفاق-) روی گردان شد؟!'];
ayat[4818]=[53,34,'وَأَعْطَىٰ قَلِيلًا وَأَكْدَىٰ','و کمی عطا کرد، و از بیشتر امساک نمود!'];
ayat[4819]=[53,35,'أَعِندَهُ عِلْمُ الْغَيْبِ فَهُوَ يَرَىٰ','آیا نزد او علم غیب است و می‌بیند (که دیگران می‌توانند گناهان او را بر دوش گیرند)؟!'];
ayat[4820]=[53,36,'أَمْ لَمْ يُنَبَّأْ بِمَا فِي صُحُفِ مُوسَىٰ','یا از آنچه در کتب موسی نازل گردیده با خبر نشده است؟!'];
ayat[4821]=[53,37,'وَإِبْرَاهِيمَ الَّذِي وَفَّىٰ','و در کتب ابراهیم، همان کسی که وظیفه خود را بطور کامل ادا کرد،'];
ayat[4822]=[53,38,'أَلَّا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ','که هیچ کس بار گناه دیگری را بر دوش نمی‌گیرد،'];
ayat[4823]=[53,39,'وَأَن لَّيْسَ لِلْإِنسَانِ إِلَّا مَا سَعَىٰ','و اینکه برای انسان بهره‌ای جز سعی و کوشش او نیست،'];
ayat[4824]=[53,40,'وَأَنَّ سَعْيَهُ سَوْفَ يُرَىٰ','و اینکه تلاش او بزودی دیده می‌شود،'];
ayat[4825]=[53,41,'ثُمَّ يُجْزَاهُ الْجَزَاءَ الْأَوْفَىٰ','سپس به او جزای کافی داده خواهد شد!'];
ayat[4826]=[53,42,'وَأَنَّ إِلَىٰ رَبِّكَ الْمُنتَهَىٰ','(و آیا از کتب پیشین انبیا به او نرسیده است) که همه امور به پروردگارت منتهی می‌گردد؟!'];
ayat[4827]=[53,43,'وَأَنَّهُ هُوَ أَضْحَكَ وَأَبْكَىٰ','و اینکه اوست که خنداند و گریاند،'];
ayat[4828]=[53,44,'وَأَنَّهُ هُوَ أَمَاتَ وَأَحْيَا','و اوست که میراند و زنده کرد،'];
ayat[4829]=[53,45,'وَأَنَّهُ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنثَىٰ','و اوست که دو زوج نر و مادّه را آفرید...'];
ayat[4830]=[53,46,'مِن نُّطْفَةٍ إِذَا تُمْنَىٰ','از نطفه‌ای هنگامی که خارج می‌شود (و در رحم می‌ریزد)،'];
ayat[4831]=[53,47,'وَأَنَّ عَلَيْهِ النَّشْأَةَ الْأُخْرَىٰ','و اینکه بر خداست ایجاد عالم دیگر (تا عدالت اجرا گردد)،'];
ayat[4832]=[53,48,'وَأَنَّهُ هُوَ أَغْنَىٰ وَأَقْنَىٰ','و اینکه اوست که بی‌نیاز کرد و سرمایه باقی بخشید،'];
ayat[4833]=[53,49,'وَأَنَّهُ هُوَ رَبُّ الشِّعْرَىٰ','و اینکه اوست پروردگار ستاره «شعرا»!'];
ayat[4834]=[53,50,'وَأَنَّهُ أَهْلَكَ عَادًا الْأُولَىٰ','(و آیا به انسان نرسیده است که در کتب انبیای پیشین آمده) که خداوند قوم «عاد نخستین» را هلاک کرد؟!'];
ayat[4835]=[53,51,'وَثَمُودَ فَمَا أَبْقَىٰ','و همچنین قوم «ثمود» را، و کسی از آنان را باقی نگذارد!'];
ayat[4836]=[53,52,'وَقَوْمَ نُوحٍ مِّن قَبْلُ ۖ إِنَّهُمْ كَانُوا هُمْ أَظْلَمَ وَأَطْغَىٰ','و نیز قوم نوح را پیش از آنها، چرا که آنان از همه ظالمتر و طغیانگرتر بودند!'];
ayat[4837]=[53,53,'وَالْمُؤْتَفِكَةَ أَهْوَىٰ','و نیز شهرهای زیر و رو شده (قوم لوط) را فرو کوبید،'];
ayat[4838]=[53,54,'فَغَشَّاهَا مَا غَشَّىٰ','سپس آنها را با عذاب سنگین پوشانید!'];
ayat[4839]=[53,55,'فَبِأَيِّ آلَاءِ رَبِّكَ تَتَمَارَىٰ','(بگو:) در کدام یک از نعمتهای پروردگارت تردید داری؟!'];
ayat[4840]=[53,56,'هَٰذَا نَذِيرٌ مِّنَ النُّذُرِ الْأُولَىٰ','این (پیامبر) بیم‌دهنده‌ای از بیم‌دهندگان پیشین است!'];
ayat[4841]=[53,57,'أَزِفَتِ الْآزِفَةُ','آنچه باید نزدیک شود، نزدیک شده است (و قیامت فرامی‌رسد)،'];
ayat[4842]=[53,58,'لَيْسَ لَهَا مِن دُونِ اللَّهِ كَاشِفَةٌ','و هیچ کس جز خدا نمی‌تواند سختیهای آن را برطرف سازد!'];
ayat[4843]=[53,59,'أَفَمِنْ هَٰذَا الْحَدِيثِ تَعْجَبُونَ','آیا از این سخن تعجّب می‌کنید،'];
ayat[4844]=[53,60,'وَتَضْحَكُونَ وَلَا تَبْكُونَ','و می‌خندید و نمی‌گریید،'];
ayat[4845]=[53,61,'وَأَنتُمْ سَامِدُونَ','و پیوسته در غفلت و هوسرانی به سر می‌برید؟!'];
ayat[4846]=[53,62,'فَاسْجُدُوا لِلَّهِ وَاعْبُدُوا ۩','حال که چنین است همه برای خدا سجده کنید و او را بپرستید!'];
ayat[4847]=[54,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ اقْتَرَبَتِ السَّاعَةُ وَانشَقَّ الْقَمَرُ','قیامت نزدیک شد و ماه از هم شکافت!'];
ayat[4848]=[54,2,'وَإِن يَرَوْا آيَةً يُعْرِضُوا وَيَقُولُوا سِحْرٌ مُّسْتَمِرٌّ','و هرگاه نشانه و معجزه‌ای را ببینند روی گردانده، می‌گویند: «این سحری مستمر است»!'];
ayat[4849]=[54,3,'وَكَذَّبُوا وَاتَّبَعُوا أَهْوَاءَهُمْ ۚ وَكُلُّ أَمْرٍ مُّسْتَقِرٌّ','آنها (آیات خدا را) تکذیب کردند و از هوای نفسشان پیروی نمودند؛ و هر امری قرارگاهی دارد!'];
ayat[4850]=[54,4,'وَلَقَدْ جَاءَهُم مِّنَ الْأَنبَاءِ مَا فِيهِ مُزْدَجَرٌ','به اندازه کافی برای بازداشتن از بدیها اخبار (انبیا و امّتهای پیشین) به آنان رسیده است!'];
ayat[4851]=[54,5,'حِكْمَةٌ بَالِغَةٌ ۖ فَمَا تُغْنِ النُّذُرُ','این آیات، حکمت بالغه الهی است؛ امّا انذارها (برای افراد لجوج) فایده نمی‌دهد!'];
ayat[4852]=[54,6,'فَتَوَلَّ عَنْهُمْ ۘ يَوْمَ يَدْعُ الدَّاعِ إِلَىٰ شَيْءٍ نُّكُرٍ','بنابر این از آنها روی بگردان، و روزی را به یاد آور که دعوت کننده الهی مردم را به امر وحشتناکی دعوت می‌کند (دعوت به حساب اعمال)!'];
ayat[4853]=[54,7,'خُشَّعًا أَبْصَارُهُمْ يَخْرُجُونَ مِنَ الْأَجْدَاثِ كَأَنَّهُمْ جَرَادٌ مُّنتَشِرٌ','آنان در حالی که چشمهایشان از شدّت وحشت به زیر افتاده، همچون ملخهای پراکنده از قبرها خارج می‌شوند،'];
ayat[4854]=[54,8,'مُّهْطِعِينَ إِلَى الدَّاعِ ۖ يَقُولُ الْكَافِرُونَ هَٰذَا يَوْمٌ عَسِرٌ','در حالی که (بر اثر وحشت و اضطراب) بسوی این دعوت کننده گردن می‌کشند؛ کافران می‌گویند: «امروز روز سخت و دردناکی است!»'];
ayat[4855]=[54,9,'۞ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ فَكَذَّبُوا عَبْدَنَا وَقَالُوا مَجْنُونٌ وَازْدُجِرَ','پیش از آنها قوم نوح تکذیب کردند، (آری) بنده ما (نوح) را تکذیب کرده و گفتند: «او دیوانه است!» و (با انواع آزارها از ادامه رسالتش) بازداشته شد.'];
ayat[4856]=[54,10,'فَدَعَا رَبَّهُ أَنِّي مَغْلُوبٌ فَانتَصِرْ','او به درگاه پروردگار عرضه داشت: «من مغلوب (این قوم طغیانگر) شده‌ام، انتقام مرا از آنها بگیر!»'];
ayat[4857]=[54,11,'فَفَتَحْنَا أَبْوَابَ السَّمَاءِ بِمَاءٍ مُّنْهَمِرٍ','در این هنگام درهای آسمان را با آبی فراوان و پی‌درپی گشودیم؛'];
ayat[4858]=[54,12,'وَفَجَّرْنَا الْأَرْضَ عُيُونًا فَالْتَقَى الْمَاءُ عَلَىٰ أَمْرٍ قَدْ قُدِرَ','و زمین را شکافتیم و چشمه‌های زیادی بیرون فرستادیم؛ و این دو آب به اندازه مقدّر با هم درآمیختند (و دریای وحشتناکی شد)!'];
ayat[4859]=[54,13,'وَحَمَلْنَاهُ عَلَىٰ ذَاتِ أَلْوَاحٍ وَدُسُرٍ','و او را بر مرکبی از الواح و میخهایی ساخته شده سوار کردیم؛'];
ayat[4860]=[54,14,'تَجْرِي بِأَعْيُنِنَا جَزَاءً لِّمَن كَانَ كُفِرَ','مرکبی که زیر نظر ما حرکت می‌کرد! این کیفری بود برای کسانی که (به او) کافر شده بودند!'];
ayat[4861]=[54,15,'وَلَقَد تَّرَكْنَاهَا آيَةً فَهَلْ مِن مُّدَّكِرٍ','ما این ماجرا را بعنوان نشانه‌ای در میان امّتها باقی گذاردیم؛ آیا کسی هست که پند گیرد؟!'];
ayat[4862]=[54,16,'فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ','(اکنون بنگرید) عذاب و انذارهای من چگونه بود!'];
ayat[4863]=[54,17,'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ','ما قرآن را برای تذکّر آسان ساختیم؛ آیا کسی هست که متذکّر شود؟!'];
ayat[4864]=[54,18,'كَذَّبَتْ عَادٌ فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ','قوم عاد (نیز پیامبر خود را) تکذیب کردند؛ پس (ببینید) عذاب و انذارهای من چگونه بود!'];
ayat[4865]=[54,19,'إِنَّا أَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِي يَوْمِ نَحْسٍ مُّسْتَمِرٍّ','ما تندباد وحشتناک و سردی را در یک روز شوم مستمر بر آنان فرستادیم...'];
ayat[4866]=[54,20,'تَنزِعُ النَّاسَ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ مُّنقَعِرٍ','که مردم را همچون تنه‌های نخل ریشه‌کن شده از جا برمی‌کند!'];
ayat[4867]=[54,21,'فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ','پس (ببینید) عذاب و انذارهای من چگونه بود!'];
ayat[4868]=[54,22,'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ','ما قرآن را برای تذکّر آسان ساختیم؛ آیا کسی هست که متذکّر شود!'];
ayat[4869]=[54,23,'كَذَّبَتْ ثَمُودُ بِالنُّذُرِ','طایفه ثمود (نیز) انذارهای الهی را تکذیب کردند،'];
ayat[4870]=[54,24,'فَقَالُوا أَبَشَرًا مِّنَّا وَاحِدًا نَّتَّبِعُهُ إِنَّا إِذًا لَّفِي ضَلَالٍ وَسُعُرٍ','و گفتند: «آیا ما از بشری از جنس خود پیروی کنیم؟! اگر چنین کنیم در گمراهی و جنون خواهیم بود!'];
ayat[4871]=[54,25,'أَأُلْقِيَ الذِّكْرُ عَلَيْهِ مِن بَيْنِنَا بَلْ هُوَ كَذَّابٌ أَشِرٌ','آیا از میان ما تنها بر او وحی نازل شده؟! نه، او آدم بسیار دروغگوی هوسبازی است!'];
ayat[4872]=[54,26,'سَيَعْلَمُونَ غَدًا مَّنِ الْكَذَّابُ الْأَشِرُ','ولی فردا می‌فهمند چه کسی دروغگوی هوسباز است!'];
ayat[4873]=[54,27,'إِنَّا مُرْسِلُو النَّاقَةِ فِتْنَةً لَّهُمْ فَارْتَقِبْهُمْ وَاصْطَبِرْ','ما «ناقه» را برای آزمایش آنها می‌فرستیم؛ در انتظار پایان کار آنان باش و صبر کن!'];
ayat[4874]=[54,28,'وَنَبِّئْهُمْ أَنَّ الْمَاءَ قِسْمَةٌ بَيْنَهُمْ ۖ كُلُّ شِرْبٍ مُّحْتَضَرٌ','و به آنها خبر ده که آب (قریه) باید در میانشان تقسیم شود، (یک روز سهم ناقه، و یک روز برای آنها) و هر یک در نوبت خود باید حاضر شوند!'];
ayat[4875]=[54,29,'فَنَادَوْا صَاحِبَهُمْ فَتَعَاطَىٰ فَعَقَرَ','آنها یکی از یاران خود را صدا زدند، او به سراغ این کار آمد و (ناقه را) پی کرد!'];
ayat[4876]=[54,30,'فَكَيْفَ كَانَ عَذَابِي وَنُذُرِ','پس (بنگرید) عذاب و انذارهای من چگونه بود!'];
ayat[4877]=[54,31,'إِنَّا أَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَاحِدَةً فَكَانُوا كَهَشِيمِ الْمُحْتَظِرِ','ما فقط یک صیحه [= صاعقه عظیم‌] بر آنها فرستادیم و بدنبال آن همگی بصورت گیاه خشکی درآمدند که صاحب چهارپایان (در آغل) جمع‌آوری می‌کند!'];
ayat[4878]=[54,32,'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ','ما قرآن را برای یادآوری آسان ساختیم؛ آیا کسی هست که متذکّر شود؟!'];
ayat[4879]=[54,33,'كَذَّبَتْ قَوْمُ لُوطٍ بِالنُّذُرِ','قوم لوط انذارها(ی پی‌درپی پیامبرشان) را تکذیب کردند؛'];
ayat[4880]=[54,34,'إِنَّا أَرْسَلْنَا عَلَيْهِمْ حَاصِبًا إِلَّا آلَ لُوطٍ ۖ نَّجَّيْنَاهُم بِسَحَرٍ','ما بر آنها تندبادی که ریگها را به حرکت درمی‌آورد فرستادیم (و همه را هلاک کردیم)، جز خاندان لوط را که سحرگاهان نجاتشان دادیم!'];
ayat[4881]=[54,35,'نِّعْمَةً مِّنْ عِندِنَا ۚ كَذَٰلِكَ نَجْزِي مَن شَكَرَ','این نعمتی بود از ناحیه ما؛ این گونه هر کسی را که شکر کند پاداش می‌دهیم!'];
ayat[4882]=[54,36,'وَلَقَدْ أَنذَرَهُم بَطْشَتَنَا فَتَمَارَوْا بِالنُّذُرِ','او آنها را از مجازات ما بیم داد، ولی بر آنها اصرار بر مجادله و القای شکّ داشتند!'];
ayat[4883]=[54,37,'وَلَقَدْ رَاوَدُوهُ عَن ضَيْفِهِ فَطَمَسْنَا أَعْيُنَهُمْ فَذُوقُوا عَذَابِي وَنُذُرِ','آنها از لوط خواستند میهمانانش را در اختیارشان بگذارد؛ ولی ما چشمانشان را نابینا و محو کردیم (و گفتیم:) بچشید عذاب و انذارهای مرا!'];
ayat[4884]=[54,38,'وَلَقَدْ صَبَّحَهُم بُكْرَةً عَذَابٌ مُّسْتَقِرٌّ','سرانجام صبحگاهان و در اول روز عذابی پایدار و ثابت به سراغشان آمد!'];
ayat[4885]=[54,39,'فَذُوقُوا عَذَابِي وَنُذُرِ','(و گفتیم:) پس بچشید عذاب و انذارهای مرا!'];
ayat[4886]=[54,40,'وَلَقَدْ يَسَّرْنَا الْقُرْآنَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ','ما قرآن را برای یادآوری آسان ساختیم؛ آیا کسی هست که متذکّر شود؟!'];
ayat[4887]=[54,41,'وَلَقَدْ جَاءَ آلَ فِرْعَوْنَ النُّذُرُ','و (همچنین) انذارها و هشدارها (یکی پس از دیگری) به سراغ آل فرعون آمد،'];
ayat[4888]=[54,42,'كَذَّبُوا بِآيَاتِنَا كُلِّهَا فَأَخَذْنَاهُمْ أَخْذَ عَزِيزٍ مُّقْتَدِرٍ','امّا آنها همه آیات ما را تکذیب کردند، و ما آنها را گرفتیم و مجازات کردیم، گرفتن شخصی قدرتمند و توانا!'];
ayat[4889]=[54,43,'أَكُفَّارُكُمْ خَيْرٌ مِّنْ أُولَٰئِكُمْ أَمْ لَكُم بَرَاءَةٌ فِي الزُّبُرِ','آیا کفّار شما بهتر از آنانند یا برای شما امان‌نامه‌ای در کتب آسمانی نازل شده است؟!'];
ayat[4890]=[54,44,'أَمْ يَقُولُونَ نَحْنُ جَمِيعٌ مُّنتَصِرٌ','یا می‌گویند: «ما جماعتی متحّد و نیرومند و پیروزیم»؟!'];
ayat[4891]=[54,45,'سَيُهْزَمُ الْجَمْعُ وَيُوَلُّونَ الدُّبُرَ','(ولی بدانند) که بزودی جمعشان شکست می‌خورد و پا به فرارمی‌گذارند!'];
ayat[4892]=[54,46,'بَلِ السَّاعَةُ مَوْعِدُهُمْ وَالسَّاعَةُ أَدْهَىٰ وَأَمَرُّ','(علاوه بر این) رستاخیز موعد آنهاست، و مجازات قیامت هولناکتر و تلختر است!'];
ayat[4893]=[54,47,'إِنَّ الْمُجْرِمِينَ فِي ضَلَالٍ وَسُعُرٍ','مجرمان در گمراهی و شعله‌های آتشند،'];
ayat[4894]=[54,48,'يَوْمَ يُسْحَبُونَ فِي النَّارِ عَلَىٰ وُجُوهِهِمْ ذُوقُوا مَسَّ سَقَرَ','در آن روز که در آتش دوزخ به صورتشان کشیده می‌شوند (و به آنها گفته می‌شود:) بچشید آتش دوزخ را!'];
ayat[4895]=[54,49,'إِنَّا كُلَّ شَيْءٍ خَلَقْنَاهُ بِقَدَرٍ','البته ما هر چیز را به اندازه آفریدیم!'];
ayat[4896]=[54,50,'وَمَا أَمْرُنَا إِلَّا وَاحِدَةٌ كَلَمْحٍ بِالْبَصَرِ','و فرمان ما یک امر بیش نیست، همچون یک چشم بر هم زدن!'];
ayat[4897]=[54,51,'وَلَقَدْ أَهْلَكْنَا أَشْيَاعَكُمْ فَهَلْ مِن مُّدَّكِرٍ','ما کسانی را که در گذشته شبیه شما بودند هلاک کردیم؛ آیا کسی هست که پند گیرد؟!'];
ayat[4898]=[54,52,'وَكُلُّ شَيْءٍ فَعَلُوهُ فِي الزُّبُرِ','و هر کاری را انجام دادند در نامه‌های اعمالشان ثبت است،'];
ayat[4899]=[54,53,'وَكُلُّ صَغِيرٍ وَكَبِيرٍ مُّسْتَطَرٌ','و هر کار کوچک و بزرگی نوشته شده است.'];
ayat[4900]=[54,54,'إِنَّ الْمُتَّقِينَ فِي جَنَّاتٍ وَنَهَرٍ','یقیناً پرهیزگاران در باغها و نهرهای بهشتی جای دارند،'];
ayat[4901]=[54,55,'فِي مَقْعَدِ صِدْقٍ عِندَ مَلِيكٍ مُّقْتَدِرٍ','در جایگاه صدق نزد خداوند مالک مقتدر!'];
ayat[4902]=[55,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الرَّحْمَٰنُ','خداوند رحمان،'];
ayat[4903]=[55,2,'عَلَّمَ الْقُرْآنَ','قرآن را تعلیم فرمود،'];
ayat[4904]=[55,3,'خَلَقَ الْإِنسَانَ','انسان را آفرید،'];
ayat[4905]=[55,4,'عَلَّمَهُ الْبَيَانَ','و به او «بیان» را آموخت.'];
ayat[4906]=[55,5,'الشَّمْسُ وَالْقَمَرُ بِحُسْبَانٍ','خورشید و ماه با حساب منظّمی می‌گردند،'];
ayat[4907]=[55,6,'وَالنَّجْمُ وَالشَّجَرُ يَسْجُدَانِ','و گیاه و درخت برای او سجده می‌کنند!'];
ayat[4908]=[55,7,'وَالسَّمَاءَ رَفَعَهَا وَوَضَعَ الْمِيزَانَ','و آسمان را برافراشت، و میزان و قانون (در آن) گذاشت،'];
ayat[4909]=[55,8,'أَلَّا تَطْغَوْا فِي الْمِيزَانِ','تا در میزان طغیان نکنید (و از مسیر عدالت منحرف نشوید)،'];
ayat[4910]=[55,9,'وَأَقِيمُوا الْوَزْنَ بِالْقِسْطِ وَلَا تُخْسِرُوا الْمِيزَانَ','و وزن را بر اساس عدل برپا دارید و میزان را کم نگذارید!'];
ayat[4911]=[55,10,'وَالْأَرْضَ وَضَعَهَا لِلْأَنَامِ','زمین را برای خلایق آفرید،'];
ayat[4912]=[55,11,'فِيهَا فَاكِهَةٌ وَالنَّخْلُ ذَاتُ الْأَكْمَامِ','که در آن میوه‌ها و نخلهای پرشکوفه است،'];
ayat[4913]=[55,12,'وَالْحَبُّ ذُو الْعَصْفِ وَالرَّيْحَانُ','و دانه‌هایی که همراه با ساقه و برگی است که بصورت کاه درمی‌آید، و گیاهان خوشبو!'];
ayat[4914]=[55,13,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را تکذیب می‌کنید (شما ای گروه جنّ و انس)؟!'];
ayat[4915]=[55,14,'خَلَقَ الْإِنسَانَ مِن صَلْصَالٍ كَالْفَخَّارِ','انسان را از گِل خشکیده‌ای همچون سفال آفرید،'];
ayat[4916]=[55,15,'وَخَلَقَ الْجَانَّ مِن مَّارِجٍ مِّن نَّارٍ','و جنّ را از شعله‌های مختلط و متحرّک آتش خلق کرد!'];
ayat[4917]=[55,16,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4918]=[55,17,'رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِ','او پروردگار دو مشرق و پروردگار دو مغرب است!'];
ayat[4919]=[55,18,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4920]=[55,19,'مَرَجَ الْبَحْرَيْنِ يَلْتَقِيَانِ','دو دریای مختلف (شور و شیرین، گرم و سرد) را در کنار هم قرار داد، در حالی که با هم تماس دارند؛'];
ayat[4921]=[55,20,'بَيْنَهُمَا بَرْزَخٌ لَّا يَبْغِيَانِ','در میان آن دو برزخی است که یکی بر دیگری غلبه نمی‌کند (و به هم نمی‌آمیزند)!'];
ayat[4922]=[55,21,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4923]=[55,22,'يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ وَالْمَرْجَانُ','از آن دو، لؤلؤ و مرجان خارج می‌شود.'];
ayat[4924]=[55,23,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4925]=[55,24,'وَلَهُ الْجَوَارِ الْمُنشَآتُ فِي الْبَحْرِ كَالْأَعْلَامِ','و برای اوست کشتیهای ساخته شده که در دریا به حرکت درمی‌آیند و همچون کوهی هستند!'];
ayat[4926]=[55,25,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4927]=[55,26,'كُلُّ مَنْ عَلَيْهَا فَانٍ','همه کسانی که روی آن [= زمین‌] هستند فانی می‌شوند،'];
ayat[4928]=[55,27,'وَيَبْقَىٰ وَجْهُ رَبِّكَ ذُو الْجَلَالِ وَالْإِكْرَامِ','و تنها ذات ذوالجلال و گرامی پروردگارت باقی می‌ماند!'];
ayat[4929]=[55,28,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4930]=[55,29,'يَسْأَلُهُ مَن فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ كُلَّ يَوْمٍ هُوَ فِي شَأْنٍ','تمام کسانی که در آسمانها و زمین هستند از او تقاضا می‌کنند، و او هر روز در شأن و کاری است!'];
ayat[4931]=[55,30,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4932]=[55,31,'سَنَفْرُغُ لَكُمْ أَيُّهَ الثَّقَلَانِ','بزودی به حساب شما می‌پردازیم ای دو گروه انس و جنّ!'];
ayat[4933]=[55,32,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4934]=[55,33,'يَا مَعْشَرَ الْجِنِّ وَالْإِنسِ إِنِ اسْتَطَعْتُمْ أَن تَنفُذُوا مِنْ أَقْطَارِ السَّمَاوَاتِ وَالْأَرْضِ فَانفُذُوا ۚ لَا تَنفُذُونَ إِلَّا بِسُلْطَانٍ','ای گروه جنّ و انس! اگر می‌توانید از مرزهای آسمانها و زمین بگذرید، پس بگذرید، ولی هرگز نمی‌توانید، مگر با نیرویی (فوق العاده)!'];
ayat[4935]=[55,34,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4936]=[55,35,'يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِّن نَّارٍ وَنُحَاسٌ فَلَا تَنتَصِرَانِ','شعله‌هایی از آتش بی‌دود، و دودهایی متراکم بر شما فرستاده می‌شود؛ و نمی‌توانید از کسی یاری بطلبید!'];
ayat[4937]=[55,36,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4938]=[55,37,'فَإِذَا انشَقَّتِ السَّمَاءُ فَكَانَتْ وَرْدَةً كَالدِّهَانِ','در آن هنگام که آسمان شکافته شود و همچون روغن مذاب گلگون گردد (حوادث هولناکی رخ می‌دهد که تاب تحمل آن را نخواهید داشت)!'];
ayat[4939]=[55,38,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4940]=[55,39,'فَيَوْمَئِذٍ لَّا يُسْأَلُ عَن ذَنبِهِ إِنسٌ وَلَا جَانٌّ','در آن روز هیچ کس از انس و جنّ از گناهش سؤال نمی‌شود (و همه چیز روشن است)!'];
ayat[4941]=[55,40,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4942]=[55,41,'يُعْرَفُ الْمُجْرِمُونَ بِسِيمَاهُمْ فَيُؤْخَذُ بِالنَّوَاصِي وَالْأَقْدَامِ','مجرمان از چهره‌هایشان شناخته می‌شوند؛ و آنگاه آنها را از موهای پیش سر، و پاهایشان می‌گیرند (و به دوزخ می‌افکنند)!'];
ayat[4943]=[55,42,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4944]=[55,43,'هَٰذِهِ جَهَنَّمُ الَّتِي يُكَذِّبُ بِهَا الْمُجْرِمُونَ','این همان دوزخی است که مجرمان آن را انکار می‌کردند!'];
ayat[4945]=[55,44,'يَطُوفُونَ بَيْنَهَا وَبَيْنَ حَمِيمٍ آنٍ','امروز در میان آن و آب سوزان در رفت و آمدند!'];
ayat[4946]=[55,45,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4947]=[55,46,'وَلِمَنْ خَافَ مَقَامَ رَبِّهِ جَنَّتَانِ','و برای کسی که از مقام پروردگارش بترسد، دو باغ بهشتی است!'];
ayat[4948]=[55,47,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4949]=[55,48,'ذَوَاتَا أَفْنَانٍ','(آن دو باغ بهشتی) دارای انواع نعمتها و درختان پرطراوت است!'];
ayat[4950]=[55,49,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4951]=[55,50,'فِيهِمَا عَيْنَانِ تَجْرِيَانِ','در آنها دو چشمه همیشه جاری است!'];
ayat[4952]=[55,51,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4953]=[55,52,'فِيهِمَا مِن كُلِّ فَاكِهَةٍ زَوْجَانِ','در آن دو، از هر میوه‌ای دو نوع وجود دارد (هر یک از دیگری بهتر)!'];
ayat[4954]=[55,53,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4955]=[55,54,'مُتَّكِئِينَ عَلَىٰ فُرُشٍ بَطَائِنُهَا مِنْ إِسْتَبْرَقٍ ۚ وَجَنَى الْجَنَّتَيْنِ دَانٍ','این در حالی است که آنها بر فرشهایی تکیه کرده‌اند با آسترهائی از دیبا و ابریشم، و میوه‌های رسیده آن دو باغ بهشتی در دسترس است!'];
ayat[4956]=[55,55,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4957]=[55,56,'فِيهِنَّ قَاصِرَاتُ الطَّرْفِ لَمْ يَطْمِثْهُنَّ إِنسٌ قَبْلَهُمْ وَلَا جَانٌّ','در آن باغهای بهشتی زنانی هستند که جز به همسران خود عشق نمی‌ورزند؛ و هیچ انس و جنّ پیش از اینها با آنان تماس نگرفته است.'];
ayat[4958]=[55,57,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4959]=[55,58,'كَأَنَّهُنَّ الْيَاقُوتُ وَالْمَرْجَانُ','آنها همچون یاقوت و مرجانند!'];
ayat[4960]=[55,59,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4961]=[55,60,'هَلْ جَزَاءُ الْإِحْسَانِ إِلَّا الْإِحْسَانُ','آیا جزای نیکی جز نیکی است؟!'];
ayat[4962]=[55,61,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4963]=[55,62,'وَمِن دُونِهِمَا جَنَّتَانِ','و پایین تر از آنها، دو باغ بهشتی دیگر است.'];
ayat[4964]=[55,63,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4965]=[55,64,'مُدْهَامَّتَانِ','هر دو خرّم و سرسبزند!'];
ayat[4966]=[55,65,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4967]=[55,66,'فِيهِمَا عَيْنَانِ نَضَّاخَتَانِ','در آنها دو چشمه جوشنده است!'];
ayat[4968]=[55,67,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4969]=[55,68,'فِيهِمَا فَاكِهَةٌ وَنَخْلٌ وَرُمَّانٌ','در آنها میوه‌های فراوان و درخت خرما و انار است!'];
ayat[4970]=[55,69,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4971]=[55,70,'فِيهِنَّ خَيْرَاتٌ حِسَانٌ','و در آن باغهای بهشتی زنانی نیکو خلق و زیبایند!'];
ayat[4972]=[55,71,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4973]=[55,72,'حُورٌ مَّقْصُورَاتٌ فِي الْخِيَامِ','حوریانی که در خیمه‌های بهشتی مستورند!'];
ayat[4974]=[55,73,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4975]=[55,74,'لَمْ يَطْمِثْهُنَّ إِنسٌ قَبْلَهُمْ وَلَا جَانٌّ','هیچ انس و جنّ پیش از ایشان با آنها تماس نگرفته (و دوشیزه‌اند)!'];
ayat[4976]=[55,75,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4977]=[55,76,'مُتَّكِئِينَ عَلَىٰ رَفْرَفٍ خُضْرٍ وَعَبْقَرِيٍّ حِسَانٍ','این در حالی است که بهشتیان بر تختهایی تکیه زده‌اند که با بهترین و زیباترین پارچه‌های سبزرنگ پوشانده شده است.'];
ayat[4978]=[55,77,'فَبِأَيِّ آلَاءِ رَبِّكُمَا تُكَذِّبَانِ','پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟!'];
ayat[4979]=[55,78,'تَبَارَكَ اسْمُ رَبِّكَ ذِي الْجَلَالِ وَالْإِكْرَامِ','پربرکت و زوال‌ناپذیر است نام پروردگار صاحب جلال و بزرگوار تو!'];
ayat[4980]=[56,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا وَقَعَتِ الْوَاقِعَةُ','هنگامی که واقعه عظیم (قیامت) واقع شود،'];
ayat[4981]=[56,2,'لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌ','هیچ کس نمی‌تواند آن را انکار کند!'];
ayat[4982]=[56,3,'خَافِضَةٌ رَّافِعَةٌ','(این واقعه) گروهی را پایین می‌آورد و گروهی را بالا می‌برد!'];
ayat[4983]=[56,4,'إِذَا رُجَّتِ الْأَرْضُ رَجًّا','در آن هنگام که زمین بشدّت به لرزه درمی‌آید،'];
ayat[4984]=[56,5,'وَبُسَّتِ الْجِبَالُ بَسًّا','و کوه‌ها در هم کوبیده می‌شود،'];
ayat[4985]=[56,6,'فَكَانَتْ هَبَاءً مُّنبَثًّا','و بصورت غبار پراکنده درمی‌آید،'];
ayat[4986]=[56,7,'وَكُنتُمْ أَزْوَاجًا ثَلَاثَةً','و شما سه گروه خواهید بود!'];
ayat[4987]=[56,8,'فَأَصْحَابُ الْمَيْمَنَةِ مَا أَصْحَابُ الْمَيْمَنَةِ','(نخست) سعادتمندان و خجستگان (هستند)؛ چه سعادتمندان و خجستگانی!'];
ayat[4988]=[56,9,'وَأَصْحَابُ الْمَشْأَمَةِ مَا أَصْحَابُ الْمَشْأَمَةِ','گروه دیگر شقاوتمندان و شومانند، چه شقاوتمندان و شومانی!'];
ayat[4989]=[56,10,'وَالسَّابِقُونَ السَّابِقُونَ','و (سومین گروه) پیشگامان پیشگامند،'];
ayat[4990]=[56,11,'أُولَٰئِكَ الْمُقَرَّبُونَ','آنها مقرّبانند!'];
ayat[4991]=[56,12,'فِي جَنَّاتِ النَّعِيمِ','در باغهای پرنعمت بهشت (جای دارند)!'];
ayat[4992]=[56,13,'ثُلَّةٌ مِّنَ الْأَوَّلِينَ','گروه زیادی (از آنها) از امّتهای نخستینند،'];
ayat[4993]=[56,14,'وَقَلِيلٌ مِّنَ الْآخِرِينَ','و اندکی از امّت آخرین!'];
ayat[4994]=[56,15,'عَلَىٰ سُرُرٍ مَّوْضُونَةٍ','آنها [= مقرّبان‌] بر تختهایی که صف‌کشیده و به هم پیوسته است قراردارند،'];
ayat[4995]=[56,16,'مُّتَّكِئِينَ عَلَيْهَا مُتَقَابِلِينَ','در حالی که بر آن تکیه زده و رو به روی یکدیگرند!'];
ayat[4996]=[56,17,'يَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُونَ','نوجوانانی جاودان (در شکوه و طراوت) پیوسته گرداگرد آنان می‌گردند،'];
ayat[4997]=[56,18,'بِأَكْوَابٍ وَأَبَارِيقَ وَكَأْسٍ مِّن مَّعِينٍ','با قدحها و کوزه‌ها و جامهایی از نهرهای جاری بهشتی (و شراب طهور)!'];
ayat[4998]=[56,19,'لَّا يُصَدَّعُونَ عَنْهَا وَلَا يُنزِفُونَ','امّا شرابی که از آن درد سر نمی‌گیرند و نه مست می‌شوند!'];
ayat[4999]=[56,20,'وَفَاكِهَةٍ مِّمَّا يَتَخَيَّرُونَ','و میوه‌هایی از هر نوع که انتخاب کنند،'];
ayat[5000]=[56,21,'وَلَحْمِ طَيْرٍ مِّمَّا يَشْتَهُونَ','و گوشت پرنده از هر نوع که مایل باشند!'];
ayat[5001]=[56,22,'وَحُورٌ عِينٌ','و همسرانی از حور العین دارند،'];
ayat[5002]=[56,23,'كَأَمْثَالِ اللُّؤْلُؤِ الْمَكْنُونِ','همچون مروارید در صدف پنهان!'];
ayat[5003]=[56,24,'جَزَاءً بِمَا كَانُوا يَعْمَلُونَ','اینها پاداشی است در برابر اعمالی که انجام می‌دادند!'];
ayat[5004]=[56,25,'لَا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا تَأْثِيمًا','در آن (باغهای بهشتی) نه لغو و بیهوده‌ای می‌شنوند نه سخنان گناه آلود؛'];
ayat[5005]=[56,26,'إِلَّا قِيلًا سَلَامًا سَلَامًا','تنها چیزی که می‌شنوند «سلام» است «سلام»!'];
ayat[5006]=[56,27,'وَأَصْحَابُ الْيَمِينِ مَا أَصْحَابُ الْيَمِينِ','و اصحاب یمین و خجستگان، چه اصحاب یمین و خجستگانی!'];
ayat[5007]=[56,28,'فِي سِدْرٍ مَّخْضُودٍ','آنها در سایه درختان «سِدر» بی‌خار قرار دارند،'];
ayat[5008]=[56,29,'وَطَلْحٍ مَّنضُودٍ','و در سایه درخت «طلح» پربرگ [= درختی خوشرنگ و خوشبو]،'];
ayat[5009]=[56,30,'وَظِلٍّ مَّمْدُودٍ','و سایه کشیده و گسترده،'];
ayat[5010]=[56,31,'وَمَاءٍ مَّسْكُوبٍ','و در کنار آبشارها،'];
ayat[5011]=[56,32,'وَفَاكِهَةٍ كَثِيرَةٍ','و میوه‌های فراوان،'];
ayat[5012]=[56,33,'لَّا مَقْطُوعَةٍ وَلَا مَمْنُوعَةٍ','که هرگز قطع و ممنوع نمی‌شود،'];
ayat[5013]=[56,34,'وَفُرُشٍ مَّرْفُوعَةٍ','و همسرانی بلندمرتبه!'];
ayat[5014]=[56,35,'إِنَّا أَنشَأْنَاهُنَّ إِنشَاءً','ما آنها را آفرینش نوینی بخشیدیم،'];
ayat[5015]=[56,36,'فَجَعَلْنَاهُنَّ أَبْكَارًا','و همه را دوشیزه قرار دادیم،'];
ayat[5016]=[56,37,'عُرُبًا أَتْرَابًا','زنانی که تنها به همسرشان عشق می‌ورزند و خوش زبان و فصیح و هم سن و سالند!'];
ayat[5017]=[56,38,'لِّأَصْحَابِ الْيَمِينِ','اینها همه برای اصحاب یمین است،'];
ayat[5018]=[56,39,'ثُلَّةٌ مِّنَ الْأَوَّلِينَ','که گروهی از امّتهای نخستینند،'];
ayat[5019]=[56,40,'وَثُلَّةٌ مِّنَ الْآخِرِينَ','و گروهی از امّتهای آخرین!'];
ayat[5020]=[56,41,'وَأَصْحَابُ الشِّمَالِ مَا أَصْحَابُ الشِّمَالِ','و اصحاب شمال، چه اصحاب شمالی (که نامه اعمالشان به نشانه جرمشان به دست چپ آنها داده می‌شود)!'];
ayat[5021]=[56,42,'فِي سَمُومٍ وَحَمِيمٍ','آنها در میان بادهای کشنده و آب سوزان قرار دارند،'];
ayat[5022]=[56,43,'وَظِلٍّ مِّن يَحْمُومٍ','و در سایه دودهای متراکم و آتشزا!'];
ayat[5023]=[56,44,'لَّا بَارِدٍ وَلَا كَرِيمٍ','سایه‌ای که نه خنک است و نه آرامبخش!'];
ayat[5024]=[56,45,'إِنَّهُمْ كَانُوا قَبْلَ ذَٰلِكَ مُتْرَفِينَ','آنها پیش از این (در عالم دنیا) مست و مغرور نعمت بودند،'];
ayat[5025]=[56,46,'وَكَانُوا يُصِرُّونَ عَلَى الْحِنثِ الْعَظِيمِ','و بر گناهان بزرگ اصرار می‌ورزیدند،'];
ayat[5026]=[56,47,'وَكَانُوا يَقُولُونَ أَئِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَامًا أَإِنَّا لَمَبْعُوثُونَ','و می‌گفتند: «هنگامی که ما مُردیم و خاک و استخوان شدیم، آیا برانگیخته خواهیم شد؟!'];
ayat[5027]=[56,48,'أَوَآبَاؤُنَا الْأَوَّلُونَ','یا نیاکان نخستین ما (برانگیخته می‌شوند)؟!»'];
ayat[5028]=[56,49,'قُلْ إِنَّ الْأَوَّلِينَ وَالْآخِرِينَ','بگو: «اوّلین و آخرین،'];
ayat[5029]=[56,50,'لَمَجْمُوعُونَ إِلَىٰ مِيقَاتِ يَوْمٍ مَّعْلُومٍ','همگی در موعد روز معیّنی گردآوری می‌شوند،'];
ayat[5030]=[56,51,'ثُمَّ إِنَّكُمْ أَيُّهَا الضَّالُّونَ الْمُكَذِّبُونَ','سپس شما ای گمراهان تکذیب‌کننده!'];
ayat[5031]=[56,52,'لَآكِلُونَ مِن شَجَرٍ مِّن زَقُّومٍ','قطعاً از درخت زقّوم می‌خورید،'];
ayat[5032]=[56,53,'فَمَالِئُونَ مِنْهَا الْبُطُونَ','و شکمها را از آن پر می‌کنید،'];
ayat[5033]=[56,54,'فَشَارِبُونَ عَلَيْهِ مِنَ الْحَمِيمِ','و روی آن از آب سوزان می‌نوشید،'];
ayat[5034]=[56,55,'فَشَارِبُونَ شُرْبَ الْهِيمِ','و همچون شتران مبتلا به بیماری عطش، از آن می‌آشامید!'];
ayat[5035]=[56,56,'هَٰذَا نُزُلُهُمْ يَوْمَ الدِّينِ','این است وسیله پذیرایی از آنها در قیامت!'];
ayat[5036]=[56,57,'نَحْنُ خَلَقْنَاكُمْ فَلَوْلَا تُصَدِّقُونَ','» ما شما را آفریدیم؛ پس چرا (آفرینش مجدّد را) تصدیق نمی‌کنید؟!'];
ayat[5037]=[56,58,'أَفَرَأَيْتُم مَّا تُمْنُونَ','آیا از نطفه‌ای که در رحم می‌ریزید آگاهید؟!'];
ayat[5038]=[56,59,'أَأَنتُمْ تَخْلُقُونَهُ أَمْ نَحْنُ الْخَالِقُونَ','آیا شما آن را (در دوران جنینی) آفرینش (پی در پی) می‌دهید یا ما آفریدگاریم؟!'];
ayat[5039]=[56,60,'نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوقِينَ','ما در میان شما مرگ را مقدّر ساختیم؛ و هرگز کسی بر ما پیشی نمی‌گیرد!'];
ayat[5040]=[56,61,'عَلَىٰ أَن نُّبَدِّلَ أَمْثَالَكُمْ وَنُنشِئَكُمْ فِي مَا لَا تَعْلَمُونَ','تا گروهی را به جای گروه دیگری بیاوریم و شما را در جهانی که نمی‌دانید آفرینش تازه‌ای بخشیم!'];
ayat[5041]=[56,62,'وَلَقَدْ عَلِمْتُمُ النَّشْأَةَ الْأُولَىٰ فَلَوْلَا تَذَكَّرُونَ','شما عالم نخستین را دانستید؛ چگونه متذکّر نمی‌شوید (که جهانی بعد از آن است)؟!'];
ayat[5042]=[56,63,'أَفَرَأَيْتُم مَّا تَحْرُثُونَ','آیا هیچ درباره آنچه کشت می‌کنید اندیشیده‌اید؟!'];
ayat[5043]=[56,64,'أَأَنتُمْ تَزْرَعُونَهُ أَمْ نَحْنُ الزَّارِعُونَ','آیا شما آن را می‌رویانید یا ما می‌رویانیم؟!'];
ayat[5044]=[56,65,'لَوْ نَشَاءُ لَجَعَلْنَاهُ حُطَامًا فَظَلْتُمْ تَفَكَّهُونَ','هرگاه بخواهیم آن را مبدّل به کاه در هم کوبیده می‌کنیم که تعجّب کنید!'];
ayat[5045]=[56,66,'إِنَّا لَمُغْرَمُونَ','(بگونه‌ای که بگویید:) براستی ما زیان کرده‌ایم،'];
ayat[5046]=[56,67,'بَلْ نَحْنُ مَحْرُومُونَ','بلکه ما بکلّی محرومیم!'];
ayat[5047]=[56,68,'أَفَرَأَيْتُمُ الْمَاءَ الَّذِي تَشْرَبُونَ','آیا به آبی که می‌نوشید اندیشیده‌اید؟!'];
ayat[5048]=[56,69,'أَأَنتُمْ أَنزَلْتُمُوهُ مِنَ الْمُزْنِ أَمْ نَحْنُ الْمُنزِلُونَ','آیا شما آن را از ابر نازل کرده‌اید یا ما نازل می‌کنیم؟!'];
ayat[5049]=[56,70,'لَوْ نَشَاءُ جَعَلْنَاهُ أُجَاجًا فَلَوْلَا تَشْكُرُونَ','هرگاه بخواهیم، این آب گوارا را تلخ و شور قرار می‌دهیم؛ پس چرا شکر نمی‌کنید؟!'];
ayat[5050]=[56,71,'أَفَرَأَيْتُمُ النَّارَ الَّتِي تُورُونَ','آیا درباره آتشی که می‌افروزید فکر کرده‌اید؟!'];
ayat[5051]=[56,72,'أَأَنتُمْ أَنشَأْتُمْ شَجَرَتَهَا أَمْ نَحْنُ الْمُنشِئُونَ','آیا شما درخت آن را آفریده‌اید یا ما آفریده‌ایم؟!'];
ayat[5052]=[56,73,'نَحْنُ جَعَلْنَاهَا تَذْكِرَةً وَمَتَاعًا لِّلْمُقْوِينَ','ما آن را وسیله یادآوری (برای همگان) و وسیله زندگی برای مسافران قرار داده‌ایم!'];
ayat[5053]=[56,74,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ','حال که چنین است به نام پروردگار بزرگت تسبیح کن (و او را پاک و منزّه بشمار)!'];
ayat[5054]=[56,75,'۞ فَلَا أُقْسِمُ بِمَوَاقِعِ النُّجُومِ','سوگند به جایگاه ستارگان (و محل طلوع و غروب آنها)!'];
ayat[5055]=[56,76,'وَإِنَّهُ لَقَسَمٌ لَّوْ تَعْلَمُونَ عَظِيمٌ','و این سوگندی است بسیار بزرگ، اگر بدانید!'];
ayat[5056]=[56,77,'إِنَّهُ لَقُرْآنٌ كَرِيمٌ','که آن، قرآن کریمی است،'];
ayat[5057]=[56,78,'فِي كِتَابٍ مَّكْنُونٍ','که در کتاب محفوظی جای دارد،'];
ayat[5058]=[56,79,'لَّا يَمَسُّهُ إِلَّا الْمُطَهَّرُونَ','و جز پاکان نمی‌توانند به آن دست زنند [= دست یابند].'];
ayat[5059]=[56,80,'تَنزِيلٌ مِّن رَّبِّ الْعَالَمِينَ','آن از سوی پروردگار عالمیان نازل شده؛'];
ayat[5060]=[56,81,'أَفَبِهَٰذَا الْحَدِيثِ أَنتُم مُّدْهِنُونَ','آیا این سخن را [= این قرآن را با اوصافی که گفته شد] سست و کوچک می‌شمرید،'];
ayat[5061]=[56,82,'وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ','و به جای شکر روزیهایی که به شما داده شده آن را تکذیب می‌کنید؟!'];
ayat[5062]=[56,83,'فَلَوْلَا إِذَا بَلَغَتِ الْحُلْقُومَ','پس چرا هنگامی که جان به گلوگاه می‌رسد (توانایی بازگرداندن آن را ندارید)؟!'];
ayat[5063]=[56,84,'وَأَنتُمْ حِينَئِذٍ تَنظُرُونَ','و شما در این حال نظاره می‌کنید (و کاری از دستتان ساخته نیست)؛'];
ayat[5064]=[56,85,'وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنكُمْ وَلَٰكِن لَّا تُبْصِرُونَ','و ما از شما به او نزدیکتریم ولی نمی‌بینید!'];
ayat[5065]=[56,86,'فَلَوْلَا إِن كُنتُمْ غَيْرَ مَدِينِينَ','اگر هرگز در برابر اعمالتان جزا داده نمی‌شوید،'];
ayat[5066]=[56,87,'تَرْجِعُونَهَا إِن كُنتُمْ صَادِقِينَ','پس آن (روح) را بازگردانید اگر راست می‌گویید!'];
ayat[5067]=[56,88,'فَأَمَّا إِن كَانَ مِنَ الْمُقَرَّبِينَ','پس اگر او از مقرّبان باشد،'];
ayat[5068]=[56,89,'فَرَوْحٌ وَرَيْحَانٌ وَجَنَّتُ نَعِيمٍ','در رَوح و ریحان و بهشت پرنعمت است!'];
ayat[5069]=[56,90,'وَأَمَّا إِن كَانَ مِنْ أَصْحَابِ الْيَمِينِ','امّا اگر از اصحاب یمین باشد،'];
ayat[5070]=[56,91,'فَسَلَامٌ لَّكَ مِنْ أَصْحَابِ الْيَمِينِ','(به او گفته می‌شود:) سلام بر تو از سوی دوستانت که از اصحاب یمینند!'];
ayat[5071]=[56,92,'وَأَمَّا إِن كَانَ مِنَ الْمُكَذِّبِينَ الضَّالِّينَ','امّا اگر او از تکذیب‌کنندگان گمراه باشد،'];
ayat[5072]=[56,93,'فَنُزُلٌ مِّنْ حَمِيمٍ','با آب جوشان دوزخ از او پذیرایی می‌شوید!'];
ayat[5073]=[56,94,'وَتَصْلِيَةُ جَحِيمٍ','و سرنوشت او ورود در آتش جهنّم است،'];
ayat[5074]=[56,95,'إِنَّ هَٰذَا لَهُوَ حَقُّ الْيَقِينِ','این مطلب حقّ و یقین است!'];
ayat[5075]=[56,96,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ','پس به نام پروردگار بزرگت تسبیح کن (و او را منزّه بشمار)!'];
ayat[5076]=[57,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ','آنچه در آسمانها و زمین است برای خدا تسبیح می‌گویند؛ و او عزیز و حکیم است.'];
ayat[5077]=[57,2,'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يُحْيِي وَيُمِيتُ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','مالکیّت (و حاکمیّت) آسمانها و زمین از آن اوست؛ زنده می‌کند و می‌میراند؛ و او بر هر چیز توانا است!'];
ayat[5078]=[57,3,'هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْبَاطِنُ ۖ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ','اوّل و آخر و پیدا و پنهان اوست؛ و او به هر چیز داناست.'];
ayat[5079]=[57,4,'هُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۚ يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا ۖ وَهُوَ مَعَكُمْ أَيْنَ مَا كُنتُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','او کسی است که آسمانها و زمین را در شش روز [= شش دوران‌] آفرید؛ سپس بر تخت قدرت قرار گرفت (و به تدبیر جهان پرداخت)؛ آنچه را در زمین فرو می‌رود می‌داند، و آنچه را از آن خارج می‌شود و آنچه از آسمان نازل می‌گردد و آنچه به آسمان بالا می‌رود؛ و هر جا باشید او با شما است، و خداوند نسبت به آنچه انجام می‌دهید بیناست!'];
ayat[5080]=[57,5,'لَّهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ','مالکیّت آسمانها و زمین از آن اوست؛ و همه کارها به سوی او بازمی‌گردد.'];
ayat[5081]=[57,6,'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ ۚ وَهُوَ عَلِيمٌ بِذَاتِ الصُّدُورِ','شب را در روز می‌کند و روز را در شب؛ و او به آنچه در دل سینه‌ها وجود دارد داناست.'];
ayat[5082]=[57,7,'آمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنفِقُوا مِمَّا جَعَلَكُم مُّسْتَخْلَفِينَ فِيهِ ۖ فَالَّذِينَ آمَنُوا مِنكُمْ وَأَنفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ','به خدا و رسولش ایمان بیاورید و از آنچه شما را جانشین و نماینده (خود) در آن قرار داده انفاق کنید؛ (زیرا) کسانی که از شما ایمان بیاورند و انفاق کنند، اجر بزرگی دارند!'];
ayat[5083]=[57,8,'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِاللَّهِ ۙ وَالرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَاقَكُمْ إِن كُنتُم مُّؤْمِنِينَ','چرا به خدا ایمان نیاورید در حالی که رسول (او) شما را می‌خواند که به پروردگارتان ایمان بیاورید، و از شما پیمان گرفته است (پیمانی از طریق فطرت و خِرد)، اگر آماده ایمان‌آوردنید.'];
ayat[5084]=[57,9,'هُوَ الَّذِي يُنَزِّلُ عَلَىٰ عَبْدِهِ آيَاتٍ بَيِّنَاتٍ لِّيُخْرِجَكُم مِّنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَإِنَّ اللَّهَ بِكُمْ لَرَءُوفٌ رَّحِيمٌ','او کسی است که آیات روشنی بر بنده‌اش [= محمد] نازل می‌کند تا شما را از تاریکیها به سوی نور برد؛ و خداوند نسبت به شما مهربان و رحیم است.'];
ayat[5085]=[57,10,'وَمَا لَكُمْ أَلَّا تُنفِقُوا فِي سَبِيلِ اللَّهِ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ ۚ لَا يَسْتَوِي مِنكُم مَّنْ أَنفَقَ مِن قَبْلِ الْفَتْحِ وَقَاتَلَ ۚ أُولَٰئِكَ أَعْظَمُ دَرَجَةً مِّنَ الَّذِينَ أَنفَقُوا مِن بَعْدُ وَقَاتَلُوا ۚ وَكُلًّا وَعَدَ اللَّهُ الْحُسْنَىٰ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','چرا در راه خدا انفاق نکنید در حالی که میراث آسمانها و زمین همه از آن خداست (و کسی چیزی را با خود نمی‌برد)! کسانی که قبل از پیروزی انفاق کردند و جنگیدند (با کسانی که پس از پیروزی انفاق کردند) یکسان نیستند؛ آنها بلندمقامتر از کسانی هستند که بعد از فتح انفاق نمودند و جهاد کردند؛ و خداوند به هر دو وعده نیک داده؛ و خدا به آنچه انجام می‌دهید آگاه است.'];
ayat[5086]=[57,11,'مَّن ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ وَلَهُ أَجْرٌ كَرِيمٌ','کیست که به خدا وام نیکو دهد (و از اموالی که به او ارزانی داشته انفاق کند) تا خداوند آن را برای او چندین برابر کند؟ و برای او پاداش پرارزشی است!'];
ayat[5087]=[57,12,'يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ يَسْعَىٰ نُورُهُم بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِم بُشْرَاكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ','(این پاداش بزرگ) در روزی است که مردان و زنان باایمان را می‌نگری که نورشان پیش‌رو و در سمت راستشان بسرعت حرکت می‌کند (و به آنها می‌گویند:) بشارت باد بر شما امروز به باغهایی از بهشت که نهرها زیر (درختان) آن جاری است؛ جاودانه در آن خواهید ماند! و این همان رستگاری بزرگ است!'];
ayat[5088]=[57,13,'يَوْمَ يَقُولُ الْمُنَافِقُونَ وَالْمُنَافِقَاتُ لِلَّذِينَ آمَنُوا انظُرُونَا نَقْتَبِسْ مِن نُّورِكُمْ قِيلَ ارْجِعُوا وَرَاءَكُمْ فَالْتَمِسُوا نُورًا فَضُرِبَ بَيْنَهُم بِسُورٍ لَّهُ بَابٌ بَاطِنُهُ فِيهِ الرَّحْمَةُ وَظَاهِرُهُ مِن قِبَلِهِ الْعَذَابُ','روزی که مردان و زنان منافق به مؤمنان می‌گویند: «نظری به ما بیفکنید تا از نور شما پرتوی برگیریم!» به آنها گفته می‌شود: «به پشت سر خود بازگردید و کسب نور کنید!» در این هنگام دیواری میان آنها زده می‌شود که دری دارد، درونش رحمت است و برونش عذاب!'];
ayat[5089]=[57,14,'يُنَادُونَهُمْ أَلَمْ نَكُن مَّعَكُمْ ۖ قَالُوا بَلَىٰ وَلَٰكِنَّكُمْ فَتَنتُمْ أَنفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْأَمَانِيُّ حَتَّىٰ جَاءَ أَمْرُ اللَّهِ وَغَرَّكُم بِاللَّهِ الْغَرُورُ','آنها را صدا می‌زنند: «مگر ما با شما نبودیم؟!» می‌گویند: «آری، ولی شما خود را به هلاکت افکندید و انتظار (مرگ پیامبر را) کشیدید، و (در همه چیز) شکّ و تردید داشتید، و آرزوهای دور و دراز شما را فریب داد تا فرمان خدا فرا رسید، و شیطان فریبکار شما را در برابر (فرمان) خداوند فریب داد!'];
ayat[5090]=[57,15,'فَالْيَوْمَ لَا يُؤْخَذُ مِنكُمْ فِدْيَةٌ وَلَا مِنَ الَّذِينَ كَفَرُوا ۚ مَأْوَاكُمُ النَّارُ ۖ هِيَ مَوْلَاكُمْ ۖ وَبِئْسَ الْمَصِيرُ','پس امروز نه از شما فدیه‌ای پذیرفته می‌شود، و نه از کافران؛ و جایگاهتان آتش است و همان سرپرستتان می‌باشد؛ و چه بد جایگاهی است!'];
ayat[5091]=[57,16,'۞ أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَن تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَمَا نَزَلَ مِنَ الْحَقِّ وَلَا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتَابَ مِن قَبْلُ فَطَالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ ۖ وَكَثِيرٌ مِّنْهُمْ فَاسِقُونَ','آیا وقت آن نرسیده است که دلهای مؤمنان در برابر ذکر خدا و آنچه از حقّ نازل کرده است خاشع گردد؟! و مانند کسانی نباشند که در گذشته به آنها کتاب آسمانی داده شد، سپس زمانی طولانی بر آنها گذشت و قلبهایشان قساوت پیدا کرد؛ و بسیاری از آنها گنهکارند!'];
ayat[5092]=[57,17,'اعْلَمُوا أَنَّ اللَّهَ يُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ قَدْ بَيَّنَّا لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ','بدانید خداوند زمین را بعد از مرگ آن زنده می‌کند! ما آیات (خود) را برای شما بیان کردیم، شاید اندیشه کنید!'];
ayat[5093]=[57,18,'إِنَّ الْمُصَّدِّقِينَ وَالْمُصَّدِّقَاتِ وَأَقْرَضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ','مردان و زنان انفاق‌کننده، و آنها که (از این راه) به خدا «قرض الحسنه» دهند، (این قرض الحسنه) برای آنان مضاعف می‌شود و پاداش پرارزشی دارند!'];
ayat[5094]=[57,19,'وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ أُولَٰئِكَ هُمُ الصِّدِّيقُونَ ۖ وَالشُّهَدَاءُ عِندَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ ۖ وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ','کسانی که به خدا و رسولانش ایمان آوردند، آنها صدّیقین و شهدا نزد پروردگارشانند؛ برای آنان است پاداش (اعمال) شان و نور (ایمان) شان؛ و کسانی که کافر شدند و آیات ما را تکذیب کردند، آنها دوزخیانند.'];
ayat[5095]=[57,20,'اعْلَمُوا أَنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌ بَيْنَكُمْ وَتَكَاثُرٌ فِي الْأَمْوَالِ وَالْأَوْلَادِ ۖ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَبَاتُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطَامًا ۖ وَفِي الْآخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِّنَ اللَّهِ وَرِضْوَانٌ ۚ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ','بدانید زندگی دنیا تنها بازی و سرگرمی و تجمّل پرستی و فخرفروشی در میان شما و افزون طلبی در اموال و فرزندان است، همانند بارانی که محصولش کشاورزان را در شگفتی فرو می‌برد، سپس خشک می‌شود بگونه‌ای که آن را زردرنگ می‌بینی؛ سپس تبدیل به کاه می‌شود! و در آخرت، عذاب شدید است یا مغفرت و رضای الهی؛ و (به هر حال) زندگی دنیا چیزی جز متاع فریب نیست!'];
ayat[5096]=[57,21,'سَابِقُوا إِلَىٰ مَغْفِرَةٍ مِّن رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاءِ وَالْأَرْضِ أُعِدَّتْ لِلَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ ۚ ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَن يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ','به پیش تازید برای رسیدن به مغفرت پروردگارتان و بهشتی که پهنه آن مانند پهنه آسمان و زمین است و برای کسانی که به خدا و رسولانش ایمان آورده‌اند؛ آماده شده است، این فضل خداوند است که به هر کس بخواهد می‌دهد؛ و خداوند صاحب فضل عظیم است!'];
ayat[5097]=[57,22,'مَا أَصَابَ مِن مُّصِيبَةٍ فِي الْأَرْضِ وَلَا فِي أَنفُسِكُمْ إِلَّا فِي كِتَابٍ مِّن قَبْلِ أَن نَّبْرَأَهَا ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ','هیچ مصیبتی (ناخواسته) در زمین و نه در وجود شما روی نمی‌دهد مگر اینکه همه آنها قبل از آنکه زمین را بیافرینیم در لوح محفوظ ثبت است؛ و این امر برای خدا آسان است!'];
ayat[5098]=[57,23,'لِّكَيْلَا تَأْسَوْا عَلَىٰ مَا فَاتَكُمْ وَلَا تَفْرَحُوا بِمَا آتَاكُمْ ۗ وَاللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ','این بخاطر آن است که برای آنچه از دست داده‌اید تأسف نخورید، و به آنچه به شما داده است دلبسته و شادمان نباشید؛ و خداوند هیچ متکبّر فخرفروشی را دوست ندارد!'];
ayat[5099]=[57,24,'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ ۗ وَمَن يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ','همانها که بخل می‌ورزند و مردم را به بخل دعوت می‌کنند؛ و هر کس (از این فرمان) روی‌گردان شود، (به خود زیان می‌رساند نه به خدا)، چرا که خداوند بی‌نیاز و شایسته ستایش است!'];
ayat[5100]=[57,25,'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَاتِ وَأَنزَلْنَا مَعَهُمُ الْكِتَابَ وَالْمِيزَانَ لِيَقُومَ النَّاسُ بِالْقِسْطِ ۖ وَأَنزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللَّهُ مَن يَنصُرُهُ وَرُسُلَهُ بِالْغَيْبِ ۚ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ','ما رسولان خود را با دلایل روشن فرستادیم، و با آنها کتاب (آسمانی) و میزان (شناسایی حقّ از باطل و قوانین عادلانه) نازل کردیم تا مردم قیام به عدالت کنند؛ و آهن را نازل کردیم که در آن نیروی شدید و منافعی برای مردم است، تا خداوند بداند چه کسی او و رسولانش را یاری می‌کند بی‌آنکه او را ببینند؛ خداوند قوّی و شکست‌ناپذیر است!'];
ayat[5101]=[57,26,'وَلَقَدْ أَرْسَلْنَا نُوحًا وَإِبْرَاهِيمَ وَجَعَلْنَا فِي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتَابَ ۖ فَمِنْهُم مُّهْتَدٍ ۖ وَكَثِيرٌ مِّنْهُمْ فَاسِقُونَ','ما نوح و ابراهیم را فرستادیم، و در دودمان آن دو نبوّت و کتاب قرار دادیم؛ بعضی از آنها هدایت یافته‌اند و بسیاری از آنها گنهکارند.'];
ayat[5102]=[57,27,'ثُمَّ قَفَّيْنَا عَلَىٰ آثَارِهِم بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ابْنِ مَرْيَمَ وَآتَيْنَاهُ الْإِنجِيلَ وَجَعَلْنَا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ابْتَدَعُوهَا مَا كَتَبْنَاهَا عَلَيْهِمْ إِلَّا ابْتِغَاءَ رِضْوَانِ اللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۖ فَآتَيْنَا الَّذِينَ آمَنُوا مِنْهُمْ أَجْرَهُمْ ۖ وَكَثِيرٌ مِّنْهُمْ فَاسِقُونَ','سپس در پی آنان رسولان دیگر خود را فرستادیم، و بعد از آنان عیسی بن مریم را مبعوث کردیم و به او انجیل عطا کردیم، و در دل کسانی که از او پیروی کردند رأفت و رحمت قرار دادیم؛ و رهبانیّتی را که ابداع کرده بودند، ما بر آنان مقرّر نداشته بودیم؛ گرچه هدفشان جلب خشنودی خدا بود، ولی حقّ آن را رعایت نکردند؛ از این‌رو ما به کسانی از آنها که ایمان آوردند پاداششان را دادیم؛ و بسیاری از آنها فاسقند!'];
ayat[5103]=[57,28,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَآمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِن رَّحْمَتِهِ وَيَجْعَل لَّكُمْ نُورًا تَمْشُونَ بِهِ وَيَغْفِرْ لَكُمْ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','ای کسانی که ایمان آورده‌اید! تقوای الهی پیشه کنید و به رسولش ایمان بیاورید تا دو سهم از رحمتش به شما ببخشد و برای شما نوری قرار دهد که با آن (در میان مردم و در مسیر زندگی خود) راه بروید و گناهان شما را ببخشد؛ و خداوند غفور و رحیم است.'];
ayat[5104]=[57,29,'لِّئَلَّا يَعْلَمَ أَهْلُ الْكِتَابِ أَلَّا يَقْدِرُونَ عَلَىٰ شَيْءٍ مِّن فَضْلِ اللَّهِ ۙ وَأَنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَن يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ','تا اهل کتاب بدانند که قادر بر چیزی از فضل خدا نیستند، و تمام فضل (و رحمت) به دست اوست، به هر کس بخواهد آن را می‌بخشد؛ و خداوند دارای فضل عظیم است!'];
ayat[5105]=[58,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجَادِلُكَ فِي زَوْجِهَا وَتَشْتَكِي إِلَى اللَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَا ۚ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ','خداوند سخن زنی را که درباره شوهرش به تو مراجعه کرده بود و به خداوند شکایت می‌کرد شنید (و تقاضای او را اجابت کرد)؛ خداوند گفتگوی شما را با هم (و اصرار آن زن را درباره حل مشکلش) می‌شنید؛ و خداوند شنوا و بیناست.'];
ayat[5106]=[58,2,'الَّذِينَ يُظَاهِرُونَ مِنكُم مِّن نِّسَائِهِم مَّا هُنَّ أُمَّهَاتِهِمْ ۖ إِنْ أُمَّهَاتُهُمْ إِلَّا اللَّائِي وَلَدْنَهُمْ ۚ وَإِنَّهُمْ لَيَقُولُونَ مُنكَرًا مِّنَ الْقَوْلِ وَزُورًا ۚ وَإِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ','کسانی که از شما نسبت به همسرانشان «ظهار» می‌کنند (و می‌گویند: «أَنتِ عَلیَّ کَظهَرِ أُمّی = تو نسبت به من بمنزله مادرم هستی»)، آنان هرگز مادرانشان نیستند؛ مادرانشان تنها کسانی‌اند که آنها را به دنیا آورده‌اند! آنها سخنی زشت و باطل می‌گویند؛ و خداوند بخشنده و آمرزنده است!'];
ayat[5107]=[58,3,'وَالَّذِينَ يُظَاهِرُونَ مِن نِّسَائِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا فَتَحْرِيرُ رَقَبَةٍ مِّن قَبْلِ أَن يَتَمَاسَّا ۚ ذَٰلِكُمْ تُوعَظُونَ بِهِ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','کسانی که همسران خود را «ظِهار» می‌کنند، سپس از گفته خود بازمی‌گردند، باید پیش از آمیزش جنسی با هم، برده‌ای را آزاد کنند؛ این دستوری است که به آن اندرز داده می‌شوید؛ و خداوند به آنچه انجام می‌دهید آگاه است!'];
ayat[5108]=[58,4,'فَمَن لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِن قَبْلِ أَن يَتَمَاسَّا ۖ فَمَن لَّمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِينًا ۚ ذَٰلِكَ لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ ۚ وَتِلْكَ حُدُودُ اللَّهِ ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ','و کسی که توانایی (آزاد کردن برده‌ای) نداشته باشد، دو ماه پیاپی قبل از آمیزش روزه بگیرد؛ و کسی که این را هم نتواند، شصت مسکین را اطعام کند؛ این برای آن است که به خدا و رسولش ایمان بیاورید؛ اینها مرزهای الهی است؛ و کسانی که با آن مخالفت کنند، عذاب دردناکی دارند!'];
ayat[5109]=[58,5,'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ كُبِتُوا كَمَا كُبِتَ الَّذِينَ مِن قَبْلِهِمْ ۚ وَقَدْ أَنزَلْنَا آيَاتٍ بَيِّنَاتٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ مُّهِينٌ','کسانی که با خدا و رسولش دشمنی می‌کنند خوار و ذلیل شدند آن‌گونه که پیشینیان خوار و ذلیل شدند؛ ما آیات روشنی نازل کردیم، و برای کافران عذاب خوارکننده‌ای است...'];
ayat[5110]=[58,6,'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيُنَبِّئُهُم بِمَا عَمِلُوا ۚ أَحْصَاهُ اللَّهُ وَنَسُوهُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ','در آن روز که خداوند همه آنها را برمی‌انگیزد و از اعمالی که انجام دادند با خبر می‌سازد، اعمالی که خداوند حساب آن را نگه داشته و آنها فراموشش کردند؛ و خداوند بر هر چیز شاهد و ناظر است!'];
ayat[5111]=[58,7,'أَلَمْ تَرَ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ مَا يَكُونُ مِن نَّجْوَىٰ ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَا أَدْنَىٰ مِن ذَٰلِكَ وَلَا أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا ۖ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُوا يَوْمَ الْقِيَامَةِ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ','آیا نمی‌دانی که خداوند آنچه را در آسمانها و آنچه را در زمین است می‌داند؛ هیچ گاه سه نفر با هم نجوا نمی‌کنند مگر اینکه خداوند چهارمین آنهاست، و هیچ گاه پنج نفر با هم نجوا نمی‌کنند مگر اینکه خداوند ششمین آنهاست، و نه تعدادی کمتر و نه بیشتر از آن مگر اینکه او همراه آنهاست هر جا که باشند، سپس روز قیامت آنها را از اعمالشان آگاه می‌سازد، چرا که خداوند به هر چیزی داناست!'];
ayat[5112]=[58,8,'أَلَمْ تَرَ إِلَى الَّذِينَ نُهُوا عَنِ النَّجْوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُوا عَنْهُ وَيَتَنَاجَوْنَ بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَإِذَا جَاءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللَّهُ وَيَقُولُونَ فِي أَنفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللَّهُ بِمَا نَقُولُ ۚ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا ۖ فَبِئْسَ الْمَصِيرُ','آیا ندیدی کسانی را که از نجوا [= سخنان درگوشی‌] نهی شدند، سپس به کاری که از آن نهی شده بودند بازمی‌گردند و برای انجام گناه و تعدّی و نافرمانی رسول خدا به نجوا می‌پردازند و هنگامی که نزد تو می‌آیند تو را تحیّتی (و خوشامدی) می‌گویند که خدا به تو نگفته است، و در دل می‌گویند: «چرا خداوند ما را بخاطر گفته‌هایمان عذاب نمی‌کند؟!» جهنم برای آنان کافی است، وارد آن می‌شوند، و چه بد فرجامی است!'];
ayat[5113]=[58,9,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوَىٰ ۖ وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ','ای کسانی که ایمان آورده‌اید! هنگامی که نجوا می‌کنید، و به گناه و تعدّی و نافرمانی رسول (خدا) نجوا نکنید، و به کار نیک و تقوا نجوا کنید، و از خدایی که همگی نزد او جمع می‌شوید بپرهیزید!'];
ayat[5114]=[58,10,'إِنَّمَا النَّجْوَىٰ مِنَ الشَّيْطَانِ لِيَحْزُنَ الَّذِينَ آمَنُوا وَلَيْسَ بِضَارِّهِمْ شَيْئًا إِلَّا بِإِذْنِ اللَّهِ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','نجوا تنها از سوی شیطان است؛ می‌خواهد با آن مؤمنان غمگین شوند؛ ولی نمی تواند هیچ گونه ضرری به آنها برساند جز بفرمان خدا؛ پس مؤمنان تنها بر خدا توکّل کنند!'];
ayat[5115]=[58,11,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قِيلَ لَكُمْ تَفَسَّحُوا فِي الْمَجَالِسِ فَافْسَحُوا يَفْسَحِ اللَّهُ لَكُمْ ۖ وَإِذَا قِيلَ انشُزُوا فَانشُزُوا يَرْفَعِ اللَّهُ الَّذِينَ آمَنُوا مِنكُمْ وَالَّذِينَ أُوتُوا الْعِلْمَ دَرَجَاتٍ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','ای کسانی که ایمان آورده‌اید! هنگامی که به شما گفته شود: «مجلس را وسعت بخشید (و به تازه‌واردها جا دهید)»، وسعت بخشید، خداوند (بهشت را) برای شما وسعت می‌بخشد؛ و هنگامی که گفته شود: «برخیزید»، برخیزید؛ اگر چنین کنید، خداوند کسانی را که ایمان آورده‌اند و کسانی را که علم به آنان داده شده درجات عظیمی می‌بخشد؛ و خداوند به آنچه انجام می‌دهید آگاه است!'];
ayat[5116]=[58,12,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَاجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَةً ۚ ذَٰلِكَ خَيْرٌ لَّكُمْ وَأَطْهَرُ ۚ فَإِن لَّمْ تَجِدُوا فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','ای کسانی که ایمان آورده‌اید! هنگامی که می‌خواهید با رسول خدا نجوا کنید (و سخنان درگوشی بگویید)، قبل از آن صدقه‌ای (در راه خدا) بدهید؛ این برای شما بهتر و پاکیزه‌تر است و اگر توانایی نداشته باشید، خداوند غفور و رحیم است!'];
ayat[5117]=[58,13,'أَأَشْفَقْتُمْ أَن تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَاتٍ ۚ فَإِذْ لَمْ تَفْعَلُوا وَتَابَ اللَّهُ عَلَيْكُمْ فَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ','آیا ترسیدید فقیر شوید که از دادن صدقات قبل از نجوا خودداری کردید؟! اکنون که این کار را نکردید و خداوند توبه شما را پذیرفت، نماز را برپا دارید و زکات را ادا کنید و خدا و پیامبرش را اطاعت نمایید و (بدانید) خداوند از آنچه انجام می‌دهید با خبر است!'];
ayat[5118]=[58,14,'۞ أَلَمْ تَرَ إِلَى الَّذِينَ تَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِم مَّا هُم مِّنكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُونَ','آیا ندیدی کسانی را که طرح دوستی با گروهی که مورد غضب خدا بودند ریختند؟! آنها نه از شما هستند و نه از آنان! سوگند دروغ یاد می‌کنند (که از شما هستند) در حالی که خودشان می‌دانند (دروغ نمی‌گویند)!'];
ayat[5119]=[58,15,'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ','خداوند عذاب شدیدی برای آنان فراهم ساخته، چرا که اعمال بدی انجام می‌دادند!'];
ayat[5120]=[58,16,'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَن سَبِيلِ اللَّهِ فَلَهُمْ عَذَابٌ مُّهِينٌ','آنها سوگندهای خود را سپری قرار دادند و مردم را از راه خدا بازداشتند؛ از این‌رو برای آنان عذاب خوارکننده‌ای است!'];
ayat[5121]=[58,17,'لَّن تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُم مِّنَ اللَّهِ شَيْئًا ۚ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ','هرگز اموال و اولادشان آنها را از عذاب الهی حفظ نمی‌کند؛ آنها اهل آتشند و جاودانه در آن می‌مانند!'];
ayat[5122]=[58,18,'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيَحْلِفُونَ لَهُ كَمَا يَحْلِفُونَ لَكُمْ ۖ وَيَحْسَبُونَ أَنَّهُمْ عَلَىٰ شَيْءٍ ۚ أَلَا إِنَّهُمْ هُمُ الْكَاذِبُونَ','(به خاطر بیاورید) روزی را که خداوند همه آنها را برمی‌انگیزد، آنها برای خدا نیز سوگند (دروغ) یاد می‌کنند همان‌گونه که (امروز) برای شما یاد می‌کنند؛ و گمان می‌کنند کاری می‌توانند انجام دهند؛ بدانید آنها دروغگویانند!'];
ayat[5123]=[58,19,'اسْتَحْوَذَ عَلَيْهِمُ الشَّيْطَانُ فَأَنسَاهُمْ ذِكْرَ اللَّهِ ۚ أُولَٰئِكَ حِزْبُ الشَّيْطَانِ ۚ أَلَا إِنَّ حِزْبَ الشَّيْطَانِ هُمُ الْخَاسِرُونَ','شیطان بر آنان مسلّط شده و یاد خدا را از خاطر آنها برده؛ آنان حزب شیطانند! بدانید حزب شیطان زیانکارانند!'];
ayat[5124]=[58,20,'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ أُولَٰئِكَ فِي الْأَذَلِّينَ','کسانی که با خدا و رسولش دشمنی می‌کنند، آنها در زُمره ذلیل‌ترین افرادند.!'];
ayat[5125]=[58,21,'كَتَبَ اللَّهُ لَأَغْلِبَنَّ أَنَا وَرُسُلِي ۚ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ','خداوند چنین مقرر داشته که من و رسولانم پیروز می‌شویم؛ چرا که خداوند قوّی و شکست‌ناپذیر است!'];
ayat[5126]=[58,22,'لَّا تَجِدُ قَوْمًا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ يُوَادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ وَلَوْ كَانُوا آبَاءَهُمْ أَوْ أَبْنَاءَهُمْ أَوْ إِخْوَانَهُمْ أَوْ عَشِيرَتَهُمْ ۚ أُولَٰئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمَانَ وَأَيَّدَهُم بِرُوحٍ مِّنْهُ ۖ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ أُولَٰئِكَ حِزْبُ اللَّهِ ۚ أَلَا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ','هیچ قومی را که ایمان به خدا و روز رستاخیز دارند نمی‌یابی که با دشمنان خدا و رسولش دوستی کنند، هر چند پدران یا فرزندان یا برادران یا خویشاوندانشان باشند؛ آنان کسانی هستند که خدا ایمان را بر صفحه دلهایشان نوشته و با روحی از ناحیه خودش آنها را تقویت فرموده، و آنها را در باغهایی از بهشت وارد می‌کند که نهرها از زیر (درختانش) جاری است، جاودانه در آن می‌مانند؛ خدا از آنها خشنود است، و آنان نیز از خدا خشنودند؛ آنها «حزب اللّه» اند؛ بدانید «حزب اللّه» پیروزان و رستگارانند.'];
ayat[5127]=[59,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ','آنچه در آسمانها و آنچه در زمین است، برای خدا تسبیح می‌گوید؛ و او عزیز و حکیم است!'];
ayat[5128]=[59,2,'هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ مِن دِيَارِهِمْ لِأَوَّلِ الْحَشْرِ ۚ مَا ظَنَنتُمْ أَن يَخْرُجُوا ۖ وَظَنُّوا أَنَّهُم مَّانِعَتُهُمْ حُصُونُهُم مِّنَ اللَّهِ فَأَتَاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا ۖ وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ ۚ يُخْرِبُونَ بُيُوتَهُم بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يَا أُولِي الْأَبْصَارِ','او کسی است که کافران اهل کتاب را در نخستین برخورد (با مسلمانان) از خانه‌هایشان بیرون راند! گمان نمی‌کردید آنان خارج شوند، و خودشان نیز گمان می‌کردند که دِژهای محکمشان آنها را از عذاب الهی مانع می‌شود؛ امّا خداوند از آنجا که گمان نمی‌کردند به سراغشان آمد و در دلهایشان ترس و وحشت افکند، بگونه‌ای که خانه‌های خود را با دست خویش و با دست مؤمنان ویران می‌کردند؛ پس عبرت بگیرید ای صاحبان چشم!'];
ayat[5129]=[59,3,'وَلَوْلَا أَن كَتَبَ اللَّهُ عَلَيْهِمُ الْجَلَاءَ لَعَذَّبَهُمْ فِي الدُّنْيَا ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابُ النَّارِ','و اگر نه این بود که خداوند ترک وطن را بر آنان مقرّر داشته بود، آنها را در همین دنیا مجازات می‌کرد؛ و برای آنان در آخرت نیز عذاب آتش است!'];
ayat[5130]=[59,4,'ذَٰلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ ۖ وَمَن يُشَاقِّ اللَّهَ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ','این به خاطر آن است که آنها با خدا و رسولش دشمنی کردند؛ و هر کس با خدا دشمنی کند (باید بداند) که خدا مجازات شدیدی دارد!'];
ayat[5131]=[59,5,'مَا قَطَعْتُم مِّن لِّينَةٍ أَوْ تَرَكْتُمُوهَا قَائِمَةً عَلَىٰ أُصُولِهَا فَبِإِذْنِ اللَّهِ وَلِيُخْزِيَ الْفَاسِقِينَ','هر درخت باارزش نخل را قطع کردید یا آن را به حال خود واگذاشتید، همه به فرمان خدا بود؛ و برای این بود که فاسقان را خوار و رسوا کند!'];
ayat[5132]=[59,6,'وَمَا أَفَاءَ اللَّهُ عَلَىٰ رَسُولِهِ مِنْهُمْ فَمَا أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَٰكِنَّ اللَّهَ يُسَلِّطُ رُسُلَهُ عَلَىٰ مَن يَشَاءُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','و آنچه را خدا از آنان [= یهود] به رسولش بازگردانده (و بخشیده) چیزی است که شما برای به دست آوردن آن (زحمتی نکشیدید،) نه اسبی تاختید و نه شتری؛ ولی خداوند رسولان خود را بر هر کس بخواهد مسلّط می‌سازد؛ و خدا بر هر چیز توانا است!'];
ayat[5133]=[59,7,'مَّا أَفَاءَ اللَّهُ عَلَىٰ رَسُولِهِ مِنْ أَهْلِ الْقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ كَيْ لَا يَكُونَ دُولَةً بَيْنَ الْأَغْنِيَاءِ مِنكُمْ ۚ وَمَا آتَاكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهَاكُمْ عَنْهُ فَانتَهُوا ۚ وَاتَّقُوا اللَّهَ ۖ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ','آنچه را خداوند از اهل این آبادیها به رسولش بازگرداند، از آن خدا و رسول و خویشاوندان او، و یتیمان و مستمندان و در راه ماندگان است، تا (این اموال عظیم) در میان ثروتمندان شما دست به دست نگردد! آنچه را رسول خدا برای شما آورده بگیرید (و اجرا کنید)، و از آنچه نهی کرده خودداری نمایید؛ و از (مخالفت) خدا بپرهیزید که خداوند کیفرش شدید است!'];
ayat[5134]=[59,8,'لِلْفُقَرَاءِ الْمُهَاجِرِينَ الَّذِينَ أُخْرِجُوا مِن دِيَارِهِمْ وَأَمْوَالِهِمْ يَبْتَغُونَ فَضْلًا مِّنَ اللَّهِ وَرِضْوَانًا وَيَنصُرُونَ اللَّهَ وَرَسُولَهُ ۚ أُولَٰئِكَ هُمُ الصَّادِقُونَ','این اموال برای فقیران مهاجرانی است که از خانه و کاشانه و اموال خود بیرون رانده شدند در حالی که فضل الهی و رضای او را می‌طلبند و خدا و رسولش را یاری می‌کنند؛ و آنها راستگویانند!'];
ayat[5135]=[59,9,'وَالَّذِينَ تَبَوَّءُوا الدَّارَ وَالْإِيمَانَ مِن قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِي صُدُورِهِمْ حَاجَةً مِّمَّا أُوتُوا وَيُؤْثِرُونَ عَلَىٰ أَنفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ ۚ وَمَن يُوقَ شُحَّ نَفْسِهِ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','و برای کسانی است که در این سرا [= سرزمین مدینه‌] و در سرای ایمان پیش از مهاجران مسکن گزیدند و کسانی را که به سویشان هجرت کنند دوست می‌دارند، و در دل خود نیازی به آنچه به مهاجران داده شده احساس نمی‌کنند و آنها را بر خود مقدّم می‌دارند هر چند خودشان بسیار نیازمند باشند؛ کسانی که از بخل و حرص نفس خویش باز داشته شده‌اند رستگارانند!'];
ayat[5136]=[59,10,'وَالَّذِينَ جَاءُوا مِن بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ وَلَا تَجْعَلْ فِي قُلُوبِنَا غِلًّا لِّلَّذِينَ آمَنُوا رَبَّنَا إِنَّكَ رَءُوفٌ رَّحِيمٌ','(همچنین) کسانی که بعد از آنها [= بعد از مهاجران و انصار] آمدند و می‌گویند: «پروردگارا! ما و برادرانمان را که در ایمان بر ما پیشی گرفتند بیامرز، و در دلهایمان حسد و کینه‌ای نسبت به مؤمنان قرار مده! پروردگارا، تو مهربان و رحیمی!»'];
ayat[5137]=[59,11,'۞ أَلَمْ تَرَ إِلَى الَّذِينَ نَافَقُوا يَقُولُونَ لِإِخْوَانِهِمُ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَدًا أَبَدًا وَإِن قُوتِلْتُمْ لَنَنصُرَنَّكُمْ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ','آیا منافقان را ندیدی که پیوسته به برادران کفارشان از اهل کتاب می‌گفتند: «هرگاه شما را (از وطن) بیرون کنند، ما هم با شما بیرون خواهیم رفت و هرگز سخن هیچ کس را درباره شما اطاعت نخواهیم کرد؛ و اگر با شما پیکار شود، یاریتان خواهیم نمود!» خداوند شهادت می‌دهد که آنها دروغگویانند!'];
ayat[5138]=[59,12,'لَئِنْ أُخْرِجُوا لَا يَخْرُجُونَ مَعَهُمْ وَلَئِن قُوتِلُوا لَا يَنصُرُونَهُمْ وَلَئِن نَّصَرُوهُمْ لَيُوَلُّنَّ الْأَدْبَارَ ثُمَّ لَا يُنصَرُونَ','اگر آنها را بیرون کنند با آنان بیرون نمی‌روند، و اگر با آنها پیکار شود یاریشان نخواهند کرد، و اگر یاریشان کنند پشت به میدان کرده فرار می‌کنند؛ سپس کسی آنان را یاری نمی‌کند!'];
ayat[5139]=[59,13,'لَأَنتُمْ أَشَدُّ رَهْبَةً فِي صُدُورِهِم مِّنَ اللَّهِ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَفْقَهُونَ','وحشت از شما در دلهای آنها بیش از ترس از خداست؛ این به خاطر آن است که آنها گروهی نادانند!'];
ayat[5140]=[59,14,'لَا يُقَاتِلُونَكُمْ جَمِيعًا إِلَّا فِي قُرًى مُّحَصَّنَةٍ أَوْ مِن وَرَاءِ جُدُرٍ ۚ بَأْسُهُم بَيْنَهُمْ شَدِيدٌ ۚ تَحْسَبُهُمْ جَمِيعًا وَقُلُوبُهُمْ شَتَّىٰ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَعْقِلُونَ','آنها هرگز با شما بصورت گروهی نمی‌جنگند جز در دژهای محکم یا از پشت دیوارها! پیکارشان در میان خودشان شدید است، (امّا در برابر شما ضعیف!) آنها را متّحد می‌پنداری، در حالی که دلهایشان پراکنده است؛ این به خاطر آن است که آنها قومی هستند که تعقّل نمی‌کنند!'];
ayat[5141]=[59,15,'كَمَثَلِ الَّذِينَ مِن قَبْلِهِمْ قَرِيبًا ۖ ذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ','کار این گروه از یهود همانند کسانی است که کمی قبل از آنان بودند، طعم تلخ کار خود را چشیدند و برای آنها عذابی دردناک است!'];
ayat[5142]=[59,16,'كَمَثَلِ الشَّيْطَانِ إِذْ قَالَ لِلْإِنسَانِ اكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيءٌ مِّنكَ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ','کار آنها همچون شیطان است که به انسان گفت: «کافر شو (تا مشکلات تو را حل کنم)!» امّا هنگامی که کافر شد گفت: «من از تو بیزارم، من از خداوندی که پروردگار عالمیان است بیم دارم!»'];
ayat[5143]=[59,17,'فَكَانَ عَاقِبَتَهُمَا أَنَّهُمَا فِي النَّارِ خَالِدَيْنِ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ الظَّالِمِينَ','سرانجام کارشان این شد که هر دو در آتش دوزخ خواهند بود، جاودانه در آن می‌مانند؛ و این است کیفر ستمکاران!'];
ayat[5144]=[59,18,'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَلْتَنظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ','ای کسانی که ایمان آورده‌اید از (مخالفت) خدا بپرهیزید؛ و هر کس باید بنگرد تا برای فردایش چه چیز از پیش فرستاده؛ و از خدا بپرهیزید که خداوند از آنچه انجام می‌دهید آگاه است!'];
ayat[5145]=[59,19,'وَلَا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنسَاهُمْ أَنفُسَهُمْ ۚ أُولَٰئِكَ هُمُ الْفَاسِقُونَ','و همچون کسانی نباشید که خدا را فراموش کردند و خدا نیز آنها را به «خود فراموشی» گرفتار کرد، آنها فاسقانند.'];
ayat[5146]=[59,20,'لَا يَسْتَوِي أَصْحَابُ النَّارِ وَأَصْحَابُ الْجَنَّةِ ۚ أَصْحَابُ الْجَنَّةِ هُمُ الْفَائِزُونَ','هرگز دوزخیان و بهشتیان یکسان نیستند؛ اصحاب بهشت رستگار و پیروزند!'];
ayat[5147]=[59,21,'لَوْ أَنزَلْنَا هَٰذَا الْقُرْآنَ عَلَىٰ جَبَلٍ لَّرَأَيْتَهُ خَاشِعًا مُّتَصَدِّعًا مِّنْ خَشْيَةِ اللَّهِ ۚ وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ','اگر این قرآن را بر کوهی نازل می‌کردیم، می‌دیدی که در برابر آن خاشع می‌شود و از خوف خدا می‌شکافد! اینها مثالهایی است که برای مردم می‌زنیم، شاید در آن بیندیشید!'];
ayat[5148]=[59,22,'هُوَ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ ۖ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ ۖ هُوَ الرَّحْمَٰنُ الرَّحِيمُ','او خدایی است که معبودی جز او نیست، دانای آشکار و نهان است، و او رحمان و رحیم است!'];
ayat[5149]=[59,23,'هُوَ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ الْمَلِكُ الْقُدُّوسُ السَّلَامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيزُ الْجَبَّارُ الْمُتَكَبِّرُ ۚ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ','و خدایی است که معبودی جز او نیست، حاکم و مالک اصلی اوست، از هر عیب منزّه است، به کسی یتم نمی‌کند، امنیّت بخش است، مراقب همه چیز است، قدرتمندی شکست‌ناپذیر که با اراده نافذ خود هر امری را اصلاح می‌کند، و شایسته عظمت است؛ خداوند منزّه است از آنچه شریک برای او قرارمی‌دهند!'];
ayat[5150]=[59,24,'هُوَ اللَّهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ ۖ لَهُ الْأَسْمَاءُ الْحُسْنَىٰ ۚ يُسَبِّحُ لَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ','او خداوندی است خالق، آفریننده‌ای بی‌سابقه، و صورتگری (بی‌نظیر)؛ برای او نامهای نیک است؛ آنچه در آسمانها و زمین است تسبیح او می‌گویند؛ و او عزیز و حکیم است!'];
ayat[5151]=[60,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِيَاءَ تُلْقُونَ إِلَيْهِم بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِمَا جَاءَكُم مِّنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَإِيَّاكُمْ ۙ أَن تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِن كُنتُمْ خَرَجْتُمْ جِهَادًا فِي سَبِيلِي وَابْتِغَاءَ مَرْضَاتِي ۚ تُسِرُّونَ إِلَيْهِم بِالْمَوَدَّةِ وَأَنَا أَعْلَمُ بِمَا أَخْفَيْتُمْ وَمَا أَعْلَنتُمْ ۚ وَمَن يَفْعَلْهُ مِنكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ','ای کسانی که ایمان آورده‌اید! دشمن من و دشمن خودتان را دوست نگیرید! شما نسبت به آنان اظهار محبّت می‌کنید، در حالی که آنها به آنچه از حقّ برای شما آمده کافر شده‌اند و رسول اللّه و شما را به خاطر ایمان به خداوندی که پروردگار همه شماست از شهر و دیارتان بیرون می‌رانند؛ اگر شما برای جهاد در راه من و جلب خشنودیم هجرت کرده‌اید؛ (پیوند دوستی با آنان برقرار نسازید!) شما مخفیانه با آنها رابطه دوستی برقرار می‌کنید در حالی که من به آنچه پنهان یا آشکار می‌سازید از همه داناترم! و هر کس از شما چنین کاری کند، از راه راست گمراه شده است!'];
ayat[5152]=[60,2,'إِن يَثْقَفُوكُمْ يَكُونُوا لَكُمْ أَعْدَاءً وَيَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُم بِالسُّوءِ وَوَدُّوا لَوْ تَكْفُرُونَ','اگر آنها بر شما مسلّط شوند، دشمنانتان خواهند بود و دست و زبان خود را به بدی کردن نسبت به شما می‌گشایند، و دوست دارند شما به کفر بازگردید!'];
ayat[5153]=[60,3,'لَن تَنفَعَكُمْ أَرْحَامُكُمْ وَلَا أَوْلَادُكُمْ ۚ يَوْمَ الْقِيَامَةِ يَفْصِلُ بَيْنَكُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','هرگز بستگان و فرزندانتان روز قیامت سودی به حالتان نخواهند داشت؛ میان شما جدایی می‌افکند؛ و خداوند به آنچه انجام می‌دهید بیناست.'];
ayat[5154]=[60,4,'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِي إِبْرَاهِيمَ وَالَّذِينَ مَعَهُ إِذْ قَالُوا لِقَوْمِهِمْ إِنَّا بُرَآءُ مِنكُمْ وَمِمَّا تَعْبُدُونَ مِن دُونِ اللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاءُ أَبَدًا حَتَّىٰ تُؤْمِنُوا بِاللَّهِ وَحْدَهُ إِلَّا قَوْلَ إِبْرَاهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَا أَمْلِكُ لَكَ مِنَ اللَّهِ مِن شَيْءٍ ۖ رَّبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ الْمَصِيرُ','برای شما سرمشق خوبی در زندگی ابراهیم و کسانی که با او بودند وجود داشت، در آن هنگامی که به قوم (مشرک) خود گفتند: «ما از شما و آنچه غیر از خدا می‌پرستید بیزاریم؛ ما نسبت به شما کافریم؛ و میان ما و شما عداوت و دشمنی همیشگی آشکار شده است؛ تا آن زمان که به خدای یگانه ایمان بیاورید! -جز آن سخن ابراهیم که به پدرش [= عمویش آزر] گفت (و وعده داد) که برای تو آمرزش طلب می‌کنم، و در عین حال در برابر خداوند برای تو مالک چیزی نیستم (و اختیاری ندارم)!- پروردگارا! ما بر تو توکّل کردیم و به سوی تو بازگشتیم، و همه فرجامها بسوی تو است!'];
ayat[5155]=[60,5,'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلَّذِينَ كَفَرُوا وَاغْفِرْ لَنَا رَبَّنَا ۖ إِنَّكَ أَنتَ الْعَزِيزُ الْحَكِيمُ','پروردگارا! ما را مایه گمراهی کافران قرار مده، و ما را ببخش، ای پروردگار ما که تو عزیز و حکیمی!»'];
ayat[5156]=[60,6,'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِّمَن كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ ۚ وَمَن يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ','(آری) برای شما در زندگی آنها اسوه حسنه (و سرمشق نیکویی) بود، برای کسانی که امید به خدا و روز قیامت دارند؛ و هر کس سرپیچی کند به خویشتن ضرر زده است، زیرا خداوند بی‌نیاز و شایسته ستایش است!'];
ayat[5157]=[60,7,'۞ عَسَى اللَّهُ أَن يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِينَ عَادَيْتُم مِّنْهُم مَّوَدَّةً ۚ وَاللَّهُ قَدِيرٌ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','امید است خدا میان شما و کسانی از مشرکین که با شما دشمنی کردند (از راه اسلام) پیوند محبّت برقرار کند؛ خداوند تواناست و خداوند آمرزنده و مهربان است.'];
ayat[5158]=[60,8,'لَّا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ لَمْ يُقَاتِلُوكُمْ فِي الدِّينِ وَلَمْ يُخْرِجُوكُم مِّن دِيَارِكُمْ أَن تَبَرُّوهُمْ وَتُقْسِطُوا إِلَيْهِمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ','خدا شما را از نیکی کردن و رعایت عدالت نسبت به کسانی که در راه دین با شما پیکار نکردند و از خانه و دیارتان بیرون نراندند نهی نمی‌کند؛ چرا که خداوند عدالت‌پیشگان را دوست دارد.'];
ayat[5159]=[60,9,'إِنَّمَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ قَاتَلُوكُمْ فِي الدِّينِ وَأَخْرَجُوكُم مِّن دِيَارِكُمْ وَظَاهَرُوا عَلَىٰ إِخْرَاجِكُمْ أَن تَوَلَّوْهُمْ ۚ وَمَن يَتَوَلَّهُمْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ','تنها شما را از دوستی و رابطه با کسانی نهی می‌کند که در امر دین با شما پیکار کردند و شما را از خانه هایتان بیرون راندند یا به بیرون‌راندن شما کمک کردند و هر کس با آنان رابطه دوستی داشته باشد ظالم و ستمگر است!'];
ayat[5160]=[60,10,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا جَاءَكُمُ الْمُؤْمِنَاتُ مُهَاجِرَاتٍ فَامْتَحِنُوهُنَّ ۖ اللَّهُ أَعْلَمُ بِإِيمَانِهِنَّ ۖ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَاتٍ فَلَا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ ۖ لَا هُنَّ حِلٌّ لَّهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ ۖ وَآتُوهُم مَّا أَنفَقُوا ۚ وَلَا جُنَاحَ عَلَيْكُمْ أَن تَنكِحُوهُنَّ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ ۚ وَلَا تُمْسِكُوا بِعِصَمِ الْكَوَافِرِ وَاسْأَلُوا مَا أَنفَقْتُمْ وَلْيَسْأَلُوا مَا أَنفَقُوا ۚ ذَٰلِكُمْ حُكْمُ اللَّهِ ۖ يَحْكُمُ بَيْنَكُمْ ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ','ای کسانی که ایمان آورده‌اید! هنگامی که زنان باایمان بعنوان هجرت نزد شما آیند، آنها را آزمایش کنید -خداوند به ایمانشان آگاهتر است- هرگاه آنان را مؤمن یافتید، آنها را بسوی کفّار بازنگردانید؛ نه آنها برای کفّار حلالند و نه کفّار برای آنها حلال؛ و آنچه را همسران آنها (برای ازدواج با این زنان) پرداخته‌اند به آنان بپردازید؛ و گناهی بر شما نیست که با آنها ازدواج کنید هرگاه مَهرشان را به آنان بدهید و هرگز زنان کافر را در همسری خود نگه ندارید (و اگر کسی از زنان شما کافر شد و به بلاد کفر فرار کرد،) حق دارید مَهری را که پرداخته‌اید مطالبه کنید همان‌گونه که آنها حق دارند مهر زنانشان را که از آنان جدا شده‌اند) از شما مطالبه کنند؛ این حکم خداوند است که در میان شما حکم می‌کند، و خداوند دانا و حکیم است!'];
ayat[5161]=[60,11,'وَإِن فَاتَكُمْ شَيْءٌ مِّنْ أَزْوَاجِكُمْ إِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَآتُوا الَّذِينَ ذَهَبَتْ أَزْوَاجُهُم مِّثْلَ مَا أَنفَقُوا ۚ وَاتَّقُوا اللَّهَ الَّذِي أَنتُم بِهِ مُؤْمِنُونَ','و اگر بعضی از همسران شما از دستتان بروند (و به سوی کفار بازگردند) و شما در جنگی بر آنان پیروز شدید و غنایمی گرفتید، به کسانی که همسرانشان رفته‌اند، همانند مهری را که پرداخته‌اند بدهید؛ و از (مخالفت) خداوندی که همه به او ایمان دارید بپرهیزید!'];
ayat[5162]=[60,12,'يَا أَيُّهَا النَّبِيُّ إِذَا جَاءَكَ الْمُؤْمِنَاتُ يُبَايِعْنَكَ عَلَىٰ أَن لَّا يُشْرِكْنَ بِاللَّهِ شَيْئًا وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَادَهُنَّ وَلَا يَأْتِينَ بِبُهْتَانٍ يَفْتَرِينَهُ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِي مَعْرُوفٍ ۙ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللَّهَ ۖ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','ای پیامبر! هنگامی که زنان مؤمن نزد تو آیند و با تو بیعت کنند که چیزی را شریک خدا قرار ندهند، دزدی و زنا نکنند، فرزندان خود را نکشند، تهمت و افترایی پیش دست و پای خود نیاورند و در هیچ کار شایسته‌ای مخالفت فرمان تو نکنند، با آنها بیعت کن و برای آنان از درگاه خداوند آمرزش بطلب که خداوند آمرزنده و مهربان است!'];
ayat[5163]=[60,13,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا مِنَ الْآخِرَةِ كَمَا يَئِسَ الْكُفَّارُ مِنْ أَصْحَابِ الْقُبُورِ','ای کسانی که ایمان آورده‌اید! با قومی که خداوند آنان را مورد غضب قرار داده دوستی نکنید؛ آنان از آخرت مأیوسند همان‌گونه که کفار مدفون در قبرها مأیوس می‌باشند!'];
ayat[5164]=[61,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ','آنچه در آسمانها و آنچه در زمین است همه تسبیح خدا می‌گویند؛ و او شکست‌ناپذیر و حکیم است!'];
ayat[5165]=[61,2,'يَا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ','ای کسانی که ایمان آورده‌اید! چرا سخنی می‌گویید که عمل نمی‌کنید؟!'];
ayat[5166]=[61,3,'كَبُرَ مَقْتًا عِندَ اللَّهِ أَن تَقُولُوا مَا لَا تَفْعَلُونَ','نزد خدا بسیار موجب خشم است که سخنی بگویید که عمل نمی‌کنید!'];
ayat[5167]=[61,4,'إِنَّ اللَّهَ يُحِبُّ الَّذِينَ يُقَاتِلُونَ فِي سَبِيلِهِ صَفًّا كَأَنَّهُم بُنْيَانٌ مَّرْصُوصٌ','خداوند کسانی را دوست می‌دارد که در راه او پیکار می‌کنند گوئی بنایی آهنین‌اند!'];
ayat[5168]=[61,5,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ لِمَ تُؤْذُونَنِي وَقَد تَّعْلَمُونَ أَنِّي رَسُولُ اللَّهِ إِلَيْكُمْ ۖ فَلَمَّا زَاغُوا أَزَاغَ اللَّهُ قُلُوبَهُمْ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ','(به یاد آورید) هنگامی را که موسی به قومش گفت: «ای قوم من! چرا مرا آزار می‌دهید با اینکه می‌دانید من فرستاده خدا به سوی شما هستم؟!» هنگامی که آنها از حق منحرف شدند، خداوند قلوبشان را منحرف ساخت؛ و خدا فاسقان را هدایت نمی‌کند!'];
ayat[5169]=[61,6,'وَإِذْ قَالَ عِيسَى ابْنُ مَرْيَمَ يَا بَنِي إِسْرَائِيلَ إِنِّي رَسُولُ اللَّهِ إِلَيْكُم مُّصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَمُبَشِّرًا بِرَسُولٍ يَأْتِي مِن بَعْدِي اسْمُهُ أَحْمَدُ ۖ فَلَمَّا جَاءَهُم بِالْبَيِّنَاتِ قَالُوا هَٰذَا سِحْرٌ مُّبِينٌ','و (به یاد آورید) هنگامی را که عیسی بن مریم گفت: «ای بنی اسرائیل! من فرستاده خدا به سوی شما هستم در حالی که تصدیق‌کننده کتابی که قبل از من فرستاده شده [= تورات‌] می‌باشم، و بشارت‌دهنده به رسولی که بعد از من می‌آید و نام او احمد است!» هنگامی که او [= احمد] با معجزات و دلایل روشن به سراغ آنان آمد، گفتند: «این سحری است آشکار»!'];
ayat[5170]=[61,7,'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ الْكَذِبَ وَهُوَ يُدْعَىٰ إِلَى الْإِسْلَامِ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','چه کسی ظالمتر است از آن کس که بر خدا دروغ بسته در حالی که دعوت به اسلام می‌شود؟! خداوند گروه ستمکاران را هدایت نمی‌کند!'];
ayat[5171]=[61,8,'يُرِيدُونَ لِيُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَاللَّهُ مُتِمُّ نُورِهِ وَلَوْ كَرِهَ الْكَافِرُونَ','آنان می‌خواهند نور خدا را با دهان خود خاموش سازند؛ ولی خدا نور خود را کامل می‌کند هر چند کافران خوش نداشته باشند!'];
ayat[5172]=[61,9,'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ','او کسی است که رسول خود را با هدایت و دین حق فرستاد تا او را بر همه ادیان غالب سازد، هر چند مشرکان کراهت داشته باشند!'];
ayat[5173]=[61,10,'يَا أَيُّهَا الَّذِينَ آمَنُوا هَلْ أَدُلُّكُمْ عَلَىٰ تِجَارَةٍ تُنجِيكُم مِّنْ عَذَابٍ أَلِيمٍ','ای کسانی که ایمان آورده‌اید! آیا شما را به تجارتی راهنمائی کنم که شما را از عذاب دردناک رهایی می‌بخشد؟!'];
ayat[5174]=[61,11,'تُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ وَتُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِكُمْ وَأَنفُسِكُمْ ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ','به خدا و رسولش ایمان بیاورید و با اموال و جانهایتان در راه خدا جهاد کنید؛ این برای شما (از هر چیز) بهتر است اگر بدانید!'];
ayat[5175]=[61,12,'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ','(اگر چنین کنید) گناهانتان را می‌بخشد و شما را در باغهایی از بهشت داخل می‌کند که نهرها از زیر درختانش جاری است و در مسکنهای پاکیزه در بهشت جاویدان جای می‌دهد؛ و این پیروزی عظیم است!'];
ayat[5176]=[61,13,'وَأُخْرَىٰ تُحِبُّونَهَا ۖ نَصْرٌ مِّنَ اللَّهِ وَفَتْحٌ قَرِيبٌ ۗ وَبَشِّرِ الْمُؤْمِنِينَ','و (نعمت) دیگری که آن را دوست دارید به شما می‌بخشد، و آن یاری خداوند و پیروزی نزدیک است؛ و مؤمنان را بشارت ده (به این پیروزی بزرگ)!'];
ayat[5177]=[61,14,'يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا أَنصَارَ اللَّهِ كَمَا قَالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوَارِيِّينَ مَنْ أَنصَارِي إِلَى اللَّهِ ۖ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنصَارُ اللَّهِ ۖ فَآمَنَت طَّائِفَةٌ مِّن بَنِي إِسْرَائِيلَ وَكَفَرَت طَّائِفَةٌ ۖ فَأَيَّدْنَا الَّذِينَ آمَنُوا عَلَىٰ عَدُوِّهِمْ فَأَصْبَحُوا ظَاهِرِينَ','ای کسانی که ایمان آورده‌اید! یاوران خدا باشید همان‌گونه که عیسی بن مریم به حواریون گفت: «چه کسانی در راه خدا یاوران من هستند؟!» حواریون گفتند: «ما یاوران خدائیم» در این هنگام گروهی از بنی اسرائیل ایمان آوردند و گروهی کافر شدند؛ ما کسانی را که ایمان آورده بودند در برابر دشمنانشان تأیید کردیم و سرانجام بر آنان پیروز شدند!'];
ayat[5178]=[62,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَزِيزِ الْحَكِيمِ','آنچه در آسمانها و آنچه در زمین است همواره تسبیح خدا می‌گویند، خداوندی که مالک و حاکم است و از هر عیب و نقصی مبرا، و عزیز و حکیم است!'];
ayat[5179]=[62,2,'هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولًا مِّنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِن كَانُوا مِن قَبْلُ لَفِي ضَلَالٍ مُّبِينٍ','و کسی است که در میان جمعیت درس نخوانده رسولی از خودشان برانگیخت که آیاتش را بر آنها می‌خواند و آنها را تزکیه می‌کند و به آنان کتاب (قرآن) و حکمت می‌آموزد هر چند پیش از آن در گمراهی آشکاری بودند!'];
ayat[5180]=[62,3,'وَآخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ','و (همچنین) رسول است بر گروه دیگری که هنوز به آنها ملحق نشده‌اند؛ و او عزیز و حکیم است!'];
ayat[5181]=[62,4,'ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَن يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ','این فضل خداست که به هر کس بخواهد (و شایسته بداند) می‌بخشد؛ و خداوند صاحب فضل عظیم است!'];
ayat[5182]=[62,5,'مَثَلُ الَّذِينَ حُمِّلُوا التَّوْرَاةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ أَسْفَارًا ۚ بِئْسَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ','کسانی که مکلف به تورات شدند ولی حق آن را ادا نکردند، مانند درازگوشی هستند که کتابهایی حمل می‌کند، (آن را بر دوش می‌کشد اما چیزی از آن نمی‌فهمد)! گروهی که آیات خدا را انکار کردند مثال بدی دارند، و خداوند قوم ستمگر را هدایت نمی‌کند!'];
ayat[5183]=[62,6,'قُلْ يَا أَيُّهَا الَّذِينَ هَادُوا إِن زَعَمْتُمْ أَنَّكُمْ أَوْلِيَاءُ لِلَّهِ مِن دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِن كُنتُمْ صَادِقِينَ','بگو: «ای یهودیان! اگر گمان می‌کنید که (فقط) شما دوستان خدائید نه سایر مردم، پس آرزوی مرگ کنید اگر راست می‌گویید (تا به لقای محبوبتان برسید)!»'];
ayat[5184]=[62,7,'وَلَا يَتَمَنَّوْنَهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۚ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ','ولی آنان هرگز تمنای مرگ نمی‌کنند بخاطر اعمالی که از پیش فرستاده‌اند؛ و خداوند ظالمان را بخوبی می‌شناسد!'];
ayat[5185]=[62,8,'قُلْ إِنَّ الْمَوْتَ الَّذِي تَفِرُّونَ مِنْهُ فَإِنَّهُ مُلَاقِيكُمْ ۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ','بگو: «این مرگی که از آن فرار می‌کنید سرانجام با شما ملاقات خواهد کرد؛ سپس به سوی کسی که دانای پنهان و آشکار است بازگردانده می‌شوید؛ آنگاه شما را از آنچه انجام می‌دادید خبر می‌دهد!»'];
ayat[5186]=[62,9,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نُودِيَ لِلصَّلَاةِ مِن يَوْمِ الْجُمُعَةِ فَاسْعَوْا إِلَىٰ ذِكْرِ اللَّهِ وَذَرُوا الْبَيْعَ ۚ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ','ای کسانی که ایمان آورده‌اید! هنگامی که برای نماز روز جمعه اذان گفته شود، به سوی ذکر خدا بشتابید و خرید و فروش را رها کنید که این برای شما بهتر است اگر می‌دانستید!'];
ayat[5187]=[62,10,'فَإِذَا قُضِيَتِ الصَّلَاةُ فَانتَشِرُوا فِي الْأَرْضِ وَابْتَغُوا مِن فَضْلِ اللَّهِ وَاذْكُرُوا اللَّهَ كَثِيرًا لَّعَلَّكُمْ تُفْلِحُونَ','و هنگامی که نماز پایان گرفت (شما آزادید) در زمین پراکنده شوید و از فضل خدا بطلبید، و خدا را بسیار یاد کنید شاید رستگار شوید!'];
ayat[5188]=[62,11,'وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْوًا انفَضُّوا إِلَيْهَا وَتَرَكُوكَ قَائِمًا ۚ قُلْ مَا عِندَ اللَّهِ خَيْرٌ مِّنَ اللَّهْوِ وَمِنَ التِّجَارَةِ ۚ وَاللَّهُ خَيْرُ الرَّازِقِينَ','هنگامی که آنها تجارت یا سرگرمی و لهوی را ببینند پراکنده می‌شوند و به سوی آن می روند و تو را ایستاده به حال خود رها می‌کنند؛ بگو: آنچه نزد خداست بهتر از لهو و تجارت است، و خداوند بهترین روزی‌دهندگان است.'];
ayat[5189]=[63,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا جَاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ ۗ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَاللَّهُ يَشْهَدُ إِنَّ الْمُنَافِقِينَ لَكَاذِبُونَ','هنگامی که منافقان نزد تو آیند می‌گویند: «ما شهادت می‌دهیم که یقیناً تو رسول خدایی!» خداوند می‌داند که تو رسول او هستی، ولی خداوند شهادت می‌دهد که منافقان دروغگو هستند (و به گفته خود ایمان ندارند).'];
ayat[5190]=[63,2,'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَن سَبِيلِ اللَّهِ ۚ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ','آنها سوگندهایشان را سپر ساخته‌اند تا مردم را از راه خدا باز دارند، و کارهای بسیار بدی انجام می‌دهند!'];
ayat[5191]=[63,3,'ذَٰلِكَ بِأَنَّهُمْ آمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ','این بخاطر آن است که نخست ایمان آوردند سپس کافر شدند؛ از این رو بر دلهای آنان مهر نهاده شده، و حقیقت را درک نمی‌کنند!'];
ayat[5192]=[63,4,'۞ وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ ۖ وَإِن يَقُولُوا تَسْمَعْ لِقَوْلِهِمْ ۖ كَأَنَّهُمْ خُشُبٌ مُّسَنَّدَةٌ ۖ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ ۚ هُمُ الْعَدُوُّ فَاحْذَرْهُمْ ۚ قَاتَلَهُمُ اللَّهُ ۖ أَنَّىٰ يُؤْفَكُونَ','هنگامی که آنها را می‌بینی، جسم و قیافه آنان تو را در شگفتی فرو می‌برد؛ و اگر سخن بگویند، به سخنانشان گوش فرا می‌دهی؛ اما گویی چوبهای خشکی هستند که به دیوار تکیه داده شده‌اند! هر فریادی از هر جا بلند شود بر ضد خود می‌پندارند؛ آنها دشمنان واقعی تو هستند، پس از آنان بر حذر باش! خداوند آنها را بکشد، چگونه از حق منحرف می‌شوند؟!'];
ayat[5193]=[63,5,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللَّهِ لَوَّوْا رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُم مُّسْتَكْبِرُونَ','هنگامی که به آنان گفته شود: «بیایید تا رسول خدا برای شما استغفار کند!»، سرهای خود را (از روی استهزا و کبر و غرور) تکان می‌دهند؛ و آنها را می‌بینی که از سخنان تو اعراض کرده و تکبر می‌ورزند!'];
ayat[5194]=[63,6,'سَوَاءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَن يَغْفِرَ اللَّهُ لَهُمْ ۚ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ','برای آنها تفاوت نمی‌کند، خواه استغفار برایشان کنی یا نکنی، هرگز خداوند آنان را نمی‌بخشد؛ زیرا خداوند قوم فاسق را هدایت نمی‌کند!'];
ayat[5195]=[63,7,'هُمُ الَّذِينَ يَقُولُونَ لَا تُنفِقُوا عَلَىٰ مَنْ عِندَ رَسُولِ اللَّهِ حَتَّىٰ يَنفَضُّوا ۗ وَلِلَّهِ خَزَائِنُ السَّمَاوَاتِ وَالْأَرْضِ وَلَٰكِنَّ الْمُنَافِقِينَ لَا يَفْقَهُونَ','آنها کسانی هستند که می‌گویند: «به افرادی که نزد رسول خدا هستند انفاق نکنید تا پراکنده شوند!» (غافل از اینکه) خزاین آسمانها و زمین از آن خداست، ولی منافقان نمی‌فهمند!'];
ayat[5196]=[63,8,'يَقُولُونَ لَئِن رَّجَعْنَا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ مِنْهَا الْأَذَلَّ ۚ وَلِلَّهِ الْعِزَّةُ وَلِرَسُولِهِ وَلِلْمُؤْمِنِينَ وَلَٰكِنَّ الْمُنَافِقِينَ لَا يَعْلَمُونَ','آنها می‌گویند: «اگر به مدینه بازگردیم، عزیزان ذلیلان را بیرون می‌کنند!» در حالی که عزت مخصوص خدا و رسول او و مؤمنان است؛ ولی منافقان نمی‌دانند!'];
ayat[5197]=[63,9,'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُلْهِكُمْ أَمْوَالُكُمْ وَلَا أَوْلَادُكُمْ عَن ذِكْرِ اللَّهِ ۚ وَمَن يَفْعَلْ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ','ای کسانی که ایمان آورده‌اید! اموال و فرزندانتان شما را از یاد خدا غافل نکند! و کسانی که چنین کنند، زیانکارانند!'];
ayat[5198]=[63,10,'وَأَنفِقُوا مِن مَّا رَزَقْنَاكُم مِّن قَبْلِ أَن يَأْتِيَ أَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْلَا أَخَّرْتَنِي إِلَىٰ أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُن مِّنَ الصَّالِحِينَ','از آنچه به شما روزی داده‌ایم انفاق کنید، پیش از آنکه مرگ یکی از شما فرا رسد و بگوید: «پروردگارا! چرا (مرگ) مرا مدت کمی به تأخیر نینداختی تا (در راه خدا) صدقه دهم و از صالحان باشم؟!»'];
ayat[5199]=[63,11,'وَلَن يُؤَخِّرَ اللَّهُ نَفْسًا إِذَا جَاءَ أَجَلُهَا ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ','خداوند هرگز مرگ کسی را هنگامی که اجلش فرا رسد به تأخیر نمی‌اندازد، و خداوند به آنچه انجام می‌دهید آگاه است.'];
ayat[5200]=[64,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','آنچه در آسمانها و آنچه در زمین است برای خدا تسبیح می‌گویند؛ مالکیت و حکومت از آن اوست و ستایش از آن او؛ و او بر همه چیز تواناست!'];
ayat[5201]=[64,2,'هُوَ الَّذِي خَلَقَكُمْ فَمِنكُمْ كَافِرٌ وَمِنكُم مُّؤْمِنٌ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ','او کسی است که شما را آفرید (و به شما آزادی و اختیار داد)؛ گروهی از شما کافرید و گروهی مؤمن؛ و خداوند به آنچه انجام می‌دهید بیناست!'];
ayat[5202]=[64,3,'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ ۖ وَإِلَيْهِ الْمَصِيرُ','آسمانها و زمین را بحق آفرید؛ و شما را (در عالم جنین) تصویر کرد، تصویری زیبا و دلپذیر؛ و سرانجام (همه) بسوی اوست.'];
ayat[5203]=[64,4,'يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ ۚ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','آنچه را در آسمانها و زمین است می‌داند، و از آنچه پنهان یا آشکار می‌کنید با خبر است؛ و خداوند از آنچه در درون سینه‌هاست آگاه است.'];
ayat[5204]=[64,5,'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ كَفَرُوا مِن قَبْلُ فَذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ','آیا خبر کسانی که پیش از این کافر شدند به شما نرسیده است؟! (آری) آنها طعم کیفر گناهان بزرگ خود را چشیدند؛ و عذاب دردناک برای آنهاست!'];
ayat[5205]=[64,6,'ذَٰلِكَ بِأَنَّهُ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِالْبَيِّنَاتِ فَقَالُوا أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا وَتَوَلَّوا ۚ وَّاسْتَغْنَى اللَّهُ ۚ وَاللَّهُ غَنِيٌّ حَمِيدٌ','این بخاطر آن است که رسولان آنها (پیوسته) با دلایل روشن به سراغشان می‌آمدند، ولی آنها (از روی کبر و غرور) گفتند: «آیا بشرهایی (مثل ما) می‌خواهند ما را هدایت کنند؟!» از این رو کافر شدند و روی برگرداندند؛ و خداوند (از ایمان و طاعتشان) بی نیاز بود، و خدا غنی و شایسته ستایش است!'];
ayat[5206]=[64,7,'زَعَمَ الَّذِينَ كَفَرُوا أَن لَّن يُبْعَثُوا ۚ قُلْ بَلَىٰ وَرَبِّي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ ۚ وَذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ','کافران پنداشتند که هرگز برانگیخته نخواهند شد، بگو: «آری به پروردگارم سوگند که همه شما (در قیامت) برانگیخته خواهید شد، سپس آنچه را عمل می‌کردید به شما خبر داده می‌شود، و این برای خداوند آسان است!»'];
ayat[5207]=[64,8,'فَآمِنُوا بِاللَّهِ وَرَسُولِهِ وَالنُّورِ الَّذِي أَنزَلْنَا ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ','حال که چنین است، به خدا و رسول او و نوری که نازل کرده‌ایم ایمان بیاورید؛ و بدانید خدا به آنچه انجام می‌دهید آگاه است!'];
ayat[5208]=[64,9,'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ۖ ذَٰلِكَ يَوْمُ التَّغَابُنِ ۗ وَمَن يُؤْمِن بِاللَّهِ وَيَعْمَلْ صَالِحًا يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُدْخِلْهُ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ','این در زمانی خواهد بود که همه شما را در روز اجتماع [= روز رستاخیز] گردآوری می‌کند؛ آن روز روز تغابن است (روز احساس خسارت و پشیمانی)! و هر کس به خدا ایمان بیاورد و عمل صالح انجام دهد، گناهان او را می‌بخشد و او را در باغهایی از بهشت که نهرها از زیر درختانش جاری است وارد می‌کند، جاودانه در آن می مانند؛ و این پیروزی بزرگ است!'];
ayat[5209]=[64,10,'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّارِ خَالِدِينَ فِيهَا ۖ وَبِئْسَ الْمَصِيرُ','اما کسانی که کافر شدند و آیات ما را تکذیب کردند اصحاب دوزخند، جاودانه در آن می‌مانند، و (سرانجام آنها) سرانجام بدی است!'];
ayat[5210]=[64,11,'مَا أَصَابَ مِن مُّصِيبَةٍ إِلَّا بِإِذْنِ اللَّهِ ۗ وَمَن يُؤْمِن بِاللَّهِ يَهْدِ قَلْبَهُ ۚ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ','هیچ مصیبتی رخ نمی‌دهد مگر به اذن خدا! و هر کس به خدا ایمان آورد، خداوند قلبش را هدایت می‌کند؛ و خدا به هر چیز داناست!'];
ayat[5211]=[64,12,'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ ۚ فَإِن تَوَلَّيْتُمْ فَإِنَّمَا عَلَىٰ رَسُولِنَا الْبَلَاغُ الْمُبِينُ','اطاعت کنید خدا را، و اطاعت کنید پیامبر را؛ و اگر روی‌گردان شوید، رسول ما جز ابلاغ آشکار وظیفه‌ای ندارد!'];
ayat[5212]=[64,13,'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ','خداوند کسی است که هیچ معبودی جز او نیست، و مؤمنان باید فقط بر او توکل کنند!'];
ayat[5213]=[64,14,'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْوَاجِكُمْ وَأَوْلَادِكُمْ عَدُوًّا لَّكُمْ فَاحْذَرُوهُمْ ۚ وَإِن تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','ای کسانی که ایمان آورده‌اید! بعضی از همسران و فرزندانتان دشمنان شما هستند، از آنها برحذر باشید؛ و اگر عفو کنید و چشم بپوشید و ببخشید، (خدا شما را می‌بخشد)؛ چرا که خداوند بخشنده و مهربان است!'];
ayat[5214]=[64,15,'إِنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ ۚ وَاللَّهُ عِندَهُ أَجْرٌ عَظِيمٌ','اموال و فرزندانتان فقط وسیله آزمایش شما هستند؛ و خداست که پاداش عظیم نزد اوست!'];
ayat[5215]=[64,16,'فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَأَطِيعُوا وَأَنفِقُوا خَيْرًا لِّأَنفُسِكُمْ ۗ وَمَن يُوقَ شُحَّ نَفْسِهِ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ','پس تا می‌توانید تقوای الهی پیشه کنید و گوش دهید و اطاعت نمایید و انفاق کنید که برای شما بهتر است؛ و کسانی که از بخل و حرص خویشتن مصون بمانند رستگارانند!'];
ayat[5216]=[64,17,'إِن تُقْرِضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ ۚ وَاللَّهُ شَكُورٌ حَلِيمٌ','اگر به خدا قرض‌الحسنه دهید، آن را برای شما مضاعف می‌سازد و شما را می‌بخشد؛ و خداوند شکرکننده و بردبار است!'];
ayat[5217]=[64,18,'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الْحَكِيمُ','او دانای پنهان و آشکار است؛ و او عزیز و حکیم است!'];
ayat[5218]=[65,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ إِذَا طَلَّقْتُمُ النِّسَاءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا الْعِدَّةَ ۖ وَاتَّقُوا اللَّهَ رَبَّكُمْ ۖ لَا تُخْرِجُوهُنَّ مِن بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّا أَن يَأْتِينَ بِفَاحِشَةٍ مُّبَيِّنَةٍ ۚ وَتِلْكَ حُدُودُ اللَّهِ ۚ وَمَن يَتَعَدَّ حُدُودَ اللَّهِ فَقَدْ ظَلَمَ نَفْسَهُ ۚ لَا تَدْرِي لَعَلَّ اللَّهَ يُحْدِثُ بَعْدَ ذَٰلِكَ أَمْرًا','ای پیامبر! هر زمان خواستید زنان را طلاق دهید، در زمان عدّه، آنها را طلاق گویید [= زمانی که از عادت ماهانه پاک شده و با همسرشان نزدیکی نکرده باشند]، و حساب عدّه را نگه دارید؛ و از خدایی که پروردگار شماست بپرهیزید؛ نه شما آنها را از خانه‌هایشان بیرون کنید و نه آنها (در دوران عدّه) بیرون روند، مگر آنکه کار زشت آشکاری انجام دهند؛ این حدود خداست، و هر کس از حدود الهی تجاوز کند به خویشتن ستم کرده؛ تو نمی‌دانی شاید خداوند بعد از این، وضع تازه (و وسیله اصلاحی) فراهم کند!'];
ayat[5219]=[65,2,'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا ذَوَيْ عَدْلٍ مِّنكُمْ وَأَقِيمُوا الشَّهَادَةَ لِلَّهِ ۚ ذَٰلِكُمْ يُوعَظُ بِهِ مَن كَانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ وَمَن يَتَّقِ اللَّهَ يَجْعَل لَّهُ مَخْرَجًا','و چون عده آنها سرآمد، آنها را بطرز شایسته‌ای نگه دارید یا بطرز شایسته‌ای از آنان جدا شوید؛ و دو مرد عادل از خودتان را گواه گیرید؛ و شهادت را برای خدا برپا دارید؛ این چیزی است که مؤمنان به خدا و روز قیامت به آن اندرز داده می‌شوند! و هر کس تقوای الهی پیشه کند، خداوند راه نجاتی برای او فراهم می‌کند،'];
ayat[5220]=[65,3,'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ ۚ وَمَن يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ ۚ إِنَّ اللَّهَ بَالِغُ أَمْرِهِ ۚ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْءٍ قَدْرًا','و او را از جایی که گمان ندارد روزی می‌دهد؛ و هر کس بر خدا توکّل کند، کفایت امرش را می‌کند؛ خداوند فرمان خود را به انجام می‌رساند؛ و خدا برای هر چیزی اندازه‌ای قرار داده است!'];
ayat[5221]=[65,4,'وَاللَّائِي يَئِسْنَ مِنَ الْمَحِيضِ مِن نِّسَائِكُمْ إِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَاثَةُ أَشْهُرٍ وَاللَّائِي لَمْ يَحِضْنَ ۚ وَأُولَاتُ الْأَحْمَالِ أَجَلُهُنَّ أَن يَضَعْنَ حَمْلَهُنَّ ۚ وَمَن يَتَّقِ اللَّهَ يَجْعَل لَّهُ مِنْ أَمْرِهِ يُسْرًا','و از زنانتان، آنان که از عادت ماهانه مأیوسند، اگر در وضع آنها (از نظر بارداری) شک کنید، عده آنان سه ماه است، و همچنین آنها که عادت ماهانه ندیده‌اند؛ و عده زنان باردار این است که بار خود را بر زمین بگذارند؛ و هر کس تقوای الهی پیشه کند، خداوند کار را بر او آسان می‌سازد!'];
ayat[5222]=[65,5,'ذَٰلِكَ أَمْرُ اللَّهِ أَنزَلَهُ إِلَيْكُمْ ۚ وَمَن يَتَّقِ اللَّهَ يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُعْظِمْ لَهُ أَجْرًا','این فرمان خداست که بر شما نازل کرده؛ و هر کس تقوای الهی پیشه کند، خداوند گناهانش را می‌بخشد و پاداش او را بزرگ می‌دارد!'];
ayat[5223]=[65,6,'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنتُم مِّن وُجْدِكُمْ وَلَا تُضَارُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّ ۚ وَإِن كُنَّ أُولَاتِ حَمْلٍ فَأَنفِقُوا عَلَيْهِنَّ حَتَّىٰ يَضَعْنَ حَمْلَهُنَّ ۚ فَإِنْ أَرْضَعْنَ لَكُمْ فَآتُوهُنَّ أُجُورَهُنَّ ۖ وَأْتَمِرُوا بَيْنَكُم بِمَعْرُوفٍ ۖ وَإِن تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُ أُخْرَىٰ','آنها [= زنان مطلّقه‌] را هر جا خودتان سکونت دارید و در توانایی شماست سکونت دهید؛ و به آنها زیان نرسانید تا کار را بر آنان تنگ کنید (و مجبور به ترک منزل شوند)؛ و اگر باردار باشند، نفقه آنها را بپردازید تا وضع حمل کنند؛ و اگر برای شما (فرزند را) شیر می‌دهند، پاداش آنها را بپردازید؛ و (درباره فرزندان، کار را) با مشاوره شایسته انجام دهید؛ و اگر به توافق نرسیدید، آن دیگری شیردادن آن بچه را بر عهده می‌گیرد.'];
ayat[5224]=[65,7,'لِيُنفِقْ ذُو سَعَةٍ مِّن سَعَتِهِ ۖ وَمَن قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنفِقْ مِمَّا آتَاهُ اللَّهُ ۚ لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا مَا آتَاهَا ۚ سَيَجْعَلُ اللَّهُ بَعْدَ عُسْرٍ يُسْرًا','آنان که امکانات وسیعی دارند، باید از امکانات وسیع خود انفاق کنند و آنها که تنگدستند، از آنچه که خدا به آنها داده انفاق نمایند؛ خداوند هیچ کس را جز به مقدار توانایی که به او داده تکلیف نمی‌کند؛ خداوند بزودی بعد از سختیها آسانی قرار می‌دهد!'];
ayat[5225]=[65,8,'وَكَأَيِّن مِّن قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِ فَحَاسَبْنَاهَا حِسَابًا شَدِيدًا وَعَذَّبْنَاهَا عَذَابًا نُّكْرًا','چه بسیار شهرها و آبادیها که اهل آن از فرمان خدا و رسولانش سرپیچی کردند و ما بشدّت به حسابشان رسیدیم و به مجازات کم‌نظیری گرفتار ساختیم!'];
ayat[5226]=[65,9,'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَاقِبَةُ أَمْرِهَا خُسْرًا','آنها آثار سوء کار خود را چشیدند؛ و عاقبت کارشان خسران بود!'];
ayat[5227]=[65,10,'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ الَّذِينَ آمَنُوا ۚ قَدْ أَنزَلَ اللَّهُ إِلَيْكُمْ ذِكْرًا','خداوند عذاب سختی برای آنها فراهم ساخته؛ پس از (مخالفت فرمان) خدا بپرهیزید ای خردمندانی که ایمان آورده‌اید! (زیرا) خداوند چیزی که مایه تذکّر است بر شما نازل کرده؛'];
ayat[5228]=[65,11,'رَّسُولًا يَتْلُو عَلَيْكُمْ آيَاتِ اللَّهِ مُبَيِّنَاتٍ لِّيُخْرِجَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَمَن يُؤْمِن بِاللَّهِ وَيَعْمَلْ صَالِحًا يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ قَدْ أَحْسَنَ اللَّهُ لَهُ رِزْقًا','رسولی به سوی شما فرستاده که آیات روشن خدا را بر شما تلاوت می‌کند تا کسانی را که ایمان آورده و کارهای شایسته انجام داده‌اند، از تاریکیها بسوی نور خارج سازد! و هر کس به خدا ایمان آورده و اعمال صالح انجام دهد، او را در باغهایی از بهشت وارد سازد که از زیر (درختانش) نهرها جاری است، جاودانه در آن می‌مانند، و خداوند روزی نیکویی برای او قرار داده است!'];
ayat[5229]=[65,12,'اللَّهُ الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ وَمِنَ الْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ الْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوا أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ وَأَنَّ اللَّهَ قَدْ أَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا','خداوند همان کسی است که هفت آسمان را آفرید، و از زمین نیز همانند آنها را؛ فرمان او در میان آنها پیوسته فرود می‌آید تا بدانید خداوند بر هر چیز تواناست و اینکه علم او به همه چیز احاطه دارد!'];
ayat[5230]=[66,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَا أَحَلَّ اللَّهُ لَكَ ۖ تَبْتَغِي مَرْضَاتَ أَزْوَاجِكَ ۚ وَاللَّهُ غَفُورٌ رَّحِيمٌ','ای پیامبر! چرا چیزی را که خدا بر تو حلال کرده بخاطر جلب رضایت همسرانت بر خود حرام می‌کنی؟! و خداوند آمرزنده و رحیم است.'];
ayat[5231]=[66,2,'قَدْ فَرَضَ اللَّهُ لَكُمْ تَحِلَّةَ أَيْمَانِكُمْ ۚ وَاللَّهُ مَوْلَاكُمْ ۖ وَهُوَ الْعَلِيمُ الْحَكِيمُ','خداوند راه گشودن سوگندهایتان را (در این گونه موارد) روشن ساخته؛ و خداوند مولای شماست و او دانا و حکیم است.'];
ayat[5232]=[66,3,'وَإِذْ أَسَرَّ النَّبِيُّ إِلَىٰ بَعْضِ أَزْوَاجِهِ حَدِيثًا فَلَمَّا نَبَّأَتْ بِهِ وَأَظْهَرَهُ اللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَأَعْرَضَ عَن بَعْضٍ ۖ فَلَمَّا نَبَّأَهَا بِهِ قَالَتْ مَنْ أَنبَأَكَ هَٰذَا ۖ قَالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ','(به خاطر بیاورید) هنگامی را که پیامبر یکی از رازهای خود را به بعضی از همسرانش گفت، ولی هنگامی که وی آن را افشا کرد و خداوند پیامبرش را از آن آگاه ساخت، قسمتی از آن را برای او بازگو کرد و از قسمت دیگر خودداری نمود؛ هنگامی که پیامبر همسرش را از آن خبر داد، گفت: «چه کسی تو را از این راز آگاه ساخت؟» فرمود: «خداوند عالم و آگاه مرا باخبر ساخت!»'];
ayat[5233]=[66,4,'إِن تَتُوبَا إِلَى اللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا ۖ وَإِن تَظَاهَرَا عَلَيْهِ فَإِنَّ اللَّهَ هُوَ مَوْلَاهُ وَجِبْرِيلُ وَصَالِحُ الْمُؤْمِنِينَ ۖ وَالْمَلَائِكَةُ بَعْدَ ذَٰلِكَ ظَهِيرٌ','اگر شما (همسران پیامبر) از کار خود توبه کنید (به نفع شماست، زیرا) دلهایتان از حق منحرف گشته؛ و اگر بر ضدّ او دست به دست هم دهید، (کاری از پیش نخواهید برد) زیرا خداوند یاور اوست و همچنین جبرئیل و مؤمنان صالح، و فرشتگان بعد از آنان پشتیبان اویند.'];
ayat[5234]=[66,5,'عَسَىٰ رَبُّهُ إِن طَلَّقَكُنَّ أَن يُبْدِلَهُ أَزْوَاجًا خَيْرًا مِّنكُنَّ مُسْلِمَاتٍ مُّؤْمِنَاتٍ قَانِتَاتٍ تَائِبَاتٍ عَابِدَاتٍ سَائِحَاتٍ ثَيِّبَاتٍ وَأَبْكَارًا','امید است که اگر او شما را طلاق دهد، پروردگارش به جای شما همسرانی بهتر برای او قرار دهد، همسرانی مسلمان، مؤمن، متواضع، توبه کار، عابد، هجرت‌کننده، زنانی غیرباکره و باکره!'];
ayat[5235]=[66,6,'يَا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنفُسَكُمْ وَأَهْلِيكُمْ نَارًا وَقُودُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلَائِكَةٌ غِلَاظٌ شِدَادٌ لَّا يَعْصُونَ اللَّهَ مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ','ای کسانی که ایمان آورده‌اید خود و خانواده خویش را از آتشی که هیزم آن انسانها و سنگهاست نگه دارید؛ آتشی که فرشتگانی بر آن گمارده شده که خشن و سختگیرند و هرگز فرمان خدا را مخالفت نمی‌کنند و آنچه را فرمان داده شده‌اند (به طور کامل) اجرا می‌نمایند!'];
ayat[5236]=[66,7,'يَا أَيُّهَا الَّذِينَ كَفَرُوا لَا تَعْتَذِرُوا الْيَوْمَ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ','ای کسانی که کافر شده‌اید امروز عذرخواهی نکنید، چرا که تنها به اعمالتان جزا داده می‌شوید!'];
ayat[5237]=[66,8,'يَا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَّصُوحًا عَسَىٰ رَبُّكُمْ أَن يُكَفِّرَ عَنكُمْ سَيِّئَاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ يَوْمَ لَا يُخْزِي اللَّهُ النَّبِيَّ وَالَّذِينَ آمَنُوا مَعَهُ ۖ نُورُهُمْ يَسْعَىٰ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ يَقُولُونَ رَبَّنَا أَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَا ۖ إِنَّكَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','ای کسانی که ایمان آورده‌اید بسوی خدا توبه کنید، توبه‌ای خالص؛ امید است (با این کار) پروردگارتان گناهانتان را ببخشد و شما را در باغهایی از بهشت که نهرها از زیر درختانش جاری است وارد کند، در آن روزی که خداوند پیامبر و کسانی را که با او ایمان آوردند خوار نمی‌کند؛ این در حالی است که نورشان پیشاپیش آنان و از سوی راستشان در حرکت است، و می‌گویند: «پروردگارا! نور ما را کامل کن و ما را ببخش که تو بر هر چیز توانائی!»'];
ayat[5238]=[66,9,'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ ۚ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ','ای پیامبر! با کفّار و منافقین پیکار کن و بر آنان سخت بگیر! جایگاهشان جهنم است، و بد فرجامی است!'];
ayat[5239]=[66,10,'ضَرَبَ اللَّهُ مَثَلًا لِّلَّذِينَ كَفَرُوا امْرَأَتَ نُوحٍ وَامْرَأَتَ لُوطٍ ۖ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللَّهِ شَيْئًا وَقِيلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِينَ','خداوند برای کسانی که کافر شده‌اند به همسر نوح و همسر لوط مثَل زده است، آن دو تحت سرپرستی دو بنده از بندگان صالح ما بودند، ولی به آن دو خیانت کردند و ارتباط با این دو (پیامبر) سودی به حالشان (در برابر عذاب الهی) نداشت، و به آنها گفته شد: «وارد آتش شوید همراه کسانی که وارد می‌شوند!»'];
ayat[5240]=[66,11,'وَضَرَبَ اللَّهُ مَثَلًا لِّلَّذِينَ آمَنُوا امْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ابْنِ لِي عِندَكَ بَيْتًا فِي الْجَنَّةِ وَنَجِّنِي مِن فِرْعَوْنَ وَعَمَلِهِ وَنَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ','و خداوند برای مؤمنان، به همسر فرعون مثَل زده است، در آن هنگام که گفت: «پروردگارا! خانه‌ای برای من نزد خودت در بهشت بساز، و مرا از فرعون و کار او نجات ده و مرا از گروه ستمگران رهایی بخش!»'];
ayat[5241]=[66,12,'وَمَرْيَمَ ابْنَتَ عِمْرَانَ الَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتْ بِكَلِمَاتِ رَبِّهَا وَكُتُبِهِ وَكَانَتْ مِنَ الْقَانِتِينَ','و همچنین به مریم دختر عمران که دامان خود را پاک نگه داشت، و ما را از روح خود در آن دمیدیم؛ او کلمات پروردگار و کتابهایش را تصدیق کرد و از مطیعان فرمان خدا بود!'];
ayat[5242]=[67,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ','پربرکت و زوال‌ناپذیر است کسی که حکومت جهان هستی به دست اوست، و او بر هر چیز تواناست.'];
ayat[5243]=[67,2,'الَّذِي خَلَقَ الْمَوْتَ وَالْحَيَاةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا ۚ وَهُوَ الْعَزِيزُ الْغَفُورُ','آن کس که مرگ و حیات را آفرید تا شما را بیازماید که کدام یک از شما بهتر عمل می‌کنید، و او شکست‌ناپذیر و بخشنده است.'];
ayat[5244]=[67,3,'الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ طِبَاقًا ۖ مَّا تَرَىٰ فِي خَلْقِ الرَّحْمَٰنِ مِن تَفَاوُتٍ ۖ فَارْجِعِ الْبَصَرَ هَلْ تَرَىٰ مِن فُطُورٍ','همان کسی که هفت آسمان را بر فراز یکدیگر آفرید؛ در آفرینش خداوند رحمان هیچ تضادّ و عیبی نمی‌بینی! بار دیگر نگاه کن، آیا هیچ شکاف و خللی مشاهده می‌کنی؟!'];
ayat[5245]=[67,4,'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنقَلِبْ إِلَيْكَ الْبَصَرُ خَاسِئًا وَهُوَ حَسِيرٌ','بار دیگر (به عالم هستی) نگاه کن، سرانجام چشمانت (در جستجوی خلل و نقصان ناکام مانده) به سوی تو باز می‌گردد در حالی که خسته و ناتوان است!'];
ayat[5246]=[67,5,'وَلَقَدْ زَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَجَعَلْنَاهَا رُجُومًا لِّلشَّيَاطِينِ ۖ وَأَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيرِ','ما آسمان پایین (نزدیک) را با چراغهای فروزانی زینت بخشیدیم، و آنها [= شهابها] را تیرهایی برای شیاطین قرار دادیم، و برای آنان عذاب آتش فروزان فراهم ساختیم!'];
ayat[5247]=[67,6,'وَلِلَّذِينَ كَفَرُوا بِرَبِّهِمْ عَذَابُ جَهَنَّمَ ۖ وَبِئْسَ الْمَصِيرُ','و برای کسانی که به پروردگارشان کافر شدند عذاب جهنّم است، و بد فرجامی است!'];
ayat[5248]=[67,7,'إِذَا أُلْقُوا فِيهَا سَمِعُوا لَهَا شَهِيقًا وَهِيَ تَفُورُ','هنگامی که در آن افکنده شوند صدای وحشتناکی از آن می‌شنوند، و این در حالی است که پیوسته می‌جوشد!'];
ayat[5249]=[67,8,'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِ ۖ كُلَّمَا أُلْقِيَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ نَذِيرٌ','نزدیک است (دوزخ) از شدّت غضب پاره پاره شود؛ هر زمان که گروهی در آن افکنده می‌شوند، نگهبانان دوزخ از آنها می‌پرسند: «مگر بیم‌دهنده الهی به سراغ شما نیامد؟!»'];
ayat[5250]=[67,9,'قَالُوا بَلَىٰ قَدْ جَاءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللَّهُ مِن شَيْءٍ إِنْ أَنتُمْ إِلَّا فِي ضَلَالٍ كَبِيرٍ','می‌گویند: «آری، بیم‌دهنده به سراغ ما آمد، ولی ما او را تکذیب کردیم و گفتیم: خداوند هرگز چیزی نازل نکرده، و شما در گمراهی بزرگی هستید!»'];
ayat[5251]=[67,10,'وَقَالُوا لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِي أَصْحَابِ السَّعِيرِ','و می‌گویند: «اگر ما گوش شنوا داشتیم یا تعقّل می‌کردیم، در میان دوزخیان نبودیم!»'];
ayat[5252]=[67,11,'فَاعْتَرَفُوا بِذَنبِهِمْ فَسُحْقًا لِّأَصْحَابِ السَّعِيرِ','اینجاست که به گناه خود اعتراف می‌کنند؛ دور باشند دوزخیان از رحمت خدا!'];
ayat[5253]=[67,12,'إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُم بِالْغَيْبِ لَهُم مَّغْفِرَةٌ وَأَجْرٌ كَبِيرٌ','(امّا) کسانی که از پروردگارشان در نهان می‌ترسند، مسلّماً آمرزش و پاداش بزرگی دارند!'];
ayat[5254]=[67,13,'وَأَسِرُّوا قَوْلَكُمْ أَوِ اجْهَرُوا بِهِ ۖ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ','گفتار خود را پنهان کنید یا آشکار (تفاوتی نمی‌کند)، او به آنچه در سینه‌هاست آگاه است!'];
ayat[5255]=[67,14,'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اللَّطِيفُ الْخَبِيرُ','آیا آن کسی که موجودات را آفریده از حال آنها آگاه نیست؟! در حالی که او (از اسرار دقیق) باخبر و آگاه است!'];
ayat[5256]=[67,15,'هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولًا فَامْشُوا فِي مَنَاكِبِهَا وَكُلُوا مِن رِّزْقِهِ ۖ وَإِلَيْهِ النُّشُورُ','او کسی است که زمین را برای شما رام کرد، بر شانه‌های آن راه بروید و از روزیهای خداوند بخورید؛ و بازگشت و اجتماع همه به سوی اوست!'];
ayat[5257]=[67,16,'أَأَمِنتُم مَّن فِي السَّمَاءِ أَن يَخْسِفَ بِكُمُ الْأَرْضَ فَإِذَا هِيَ تَمُورُ','آیا خود را از عذاب کسی که حاکم بر آسمان است در امان می‌دانید که دستور دهد زمین بشکافد و شما را فرو برد و به لرزش خود ادامه دهد؟!'];
ayat[5258]=[67,17,'أَمْ أَمِنتُم مَّن فِي السَّمَاءِ أَن يُرْسِلَ عَلَيْكُمْ حَاصِبًا ۖ فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ','یا خود را از عذاب خداوند آسمان در امان می‌دانید که تندبادی پر از سنگریزه بر شما فرستد؟! و بزودی خواهید دانست تهدیدهای من چگونه است!'];
ayat[5259]=[67,18,'وَلَقَدْ كَذَّبَ الَّذِينَ مِن قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيرِ','کسانی که پیش از آنان بودند (آیات الهی را) تکذیب کردند، امّا (ببین) مجازات من چگونه بود!'];
ayat[5260]=[67,19,'أَوَلَمْ يَرَوْا إِلَى الطَّيْرِ فَوْقَهُمْ صَافَّاتٍ وَيَقْبِضْنَ ۚ مَا يُمْسِكُهُنَّ إِلَّا الرَّحْمَٰنُ ۚ إِنَّهُ بِكُلِّ شَيْءٍ بَصِيرٌ','آیا به پرندگانی که بالای سرشان است، و گاه بالهای خود را گسترده و گاه جمع می‌کنند، نگاه نکردند؟! جز خداوند رحمان کسی آنها را بر فراز آسمان نگه نمی‌دارد، چرا که او به هر چیز بیناست!'];
ayat[5261]=[67,20,'أَمَّنْ هَٰذَا الَّذِي هُوَ جُندٌ لَّكُمْ يَنصُرُكُم مِّن دُونِ الرَّحْمَٰنِ ۚ إِنِ الْكَافِرُونَ إِلَّا فِي غُرُورٍ','آیا این کسی که لشکر شماست می‌تواند شما را در برابر خداوند یاری دهد؟ ولی کافران تنها گرفتار فریبند!'];
ayat[5262]=[67,21,'أَمَّنْ هَٰذَا الَّذِي يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُ ۚ بَل لَّجُّوا فِي عُتُوٍّ وَنُفُورٍ','یا آن کسی که شما را روزی می‌دهد، اگر روزیش را بازدارد (چه کسی می‌تواند نیاز شما را تأمین کند)؟! ولی آنها در سرکشی و فرار از حقیقت لجاجت می‌ورزند!'];
ayat[5263]=[67,22,'أَفَمَن يَمْشِي مُكِبًّا عَلَىٰ وَجْهِهِ أَهْدَىٰ أَمَّن يَمْشِي سَوِيًّا عَلَىٰ صِرَاطٍ مُّسْتَقِيمٍ','آیا کسی که به رو افتاده حرکت می‌کند به هدایت نزدیکتر است یا کسی که راست‌قامت در صراط مستقیم گام برمی‌دارد؟!'];
ayat[5264]=[67,23,'قُلْ هُوَ الَّذِي أَنشَأَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۖ قَلِيلًا مَّا تَشْكُرُونَ','بگو: «او کسی است که شما را آفرید و برای شما گوش و چشم و قلب قرار داد؛ امّا کمتر سپاسگزاری می‌کنید!»'];
ayat[5265]=[67,24,'قُلْ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ','بگو: «او کسی است که شما را در زمین آفرید و به سوی او محشور می‌شوید!»'];
ayat[5266]=[67,25,'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِن كُنتُمْ صَادِقِينَ','آنها می‌گویند: «اگر راست می‌گویید این وعده قیامت چه زمانی است؟!»'];
ayat[5267]=[67,26,'قُلْ إِنَّمَا الْعِلْمُ عِندَ اللَّهِ وَإِنَّمَا أَنَا نَذِيرٌ مُّبِينٌ','بگو: «علم آن تنها نزد خداست؛ و من فقط بیم‌دهنده آشکاری هستم!»'];
ayat[5268]=[67,27,'فَلَمَّا رَأَوْهُ زُلْفَةً سِيئَتْ وُجُوهُ الَّذِينَ كَفَرُوا وَقِيلَ هَٰذَا الَّذِي كُنتُم بِهِ تَدَّعُونَ','هنگامی که آن (وعده الهی) را از نزدیک می‌بینند، صورت کافران زشت و سیاه می‌گردد، و به آنها گفته می‌شود: «این همان چیزی است که تقاضای آن را داشتید»!'];
ayat[5269]=[67,28,'قُلْ أَرَأَيْتُمْ إِنْ أَهْلَكَنِيَ اللَّهُ وَمَن مَّعِيَ أَوْ رَحِمَنَا فَمَن يُجِيرُ الْكَافِرِينَ مِنْ عَذَابٍ أَلِيمٍ','بگو: «به من خبر دهید اگر خداوند مرا و تمام کسانی را که با من هستند هلاک کند، یا مورد ترحّم قرار دهد، چه کسی کافران را از عذاب دردناک پناه می‌دهد؟!»'];
ayat[5270]=[67,29,'قُلْ هُوَ الرَّحْمَٰنُ آمَنَّا بِهِ وَعَلَيْهِ تَوَكَّلْنَا ۖ فَسَتَعْلَمُونَ مَنْ هُوَ فِي ضَلَالٍ مُّبِينٍ','بگو: «او خداوند رحمان است، ما به او ایمان آورده و بر او توکّل کرده‌ایم؛ و بزودی می‌دانید چه کسی در گمراهی آشکار است!»'];
ayat[5271]=[67,30,'قُلْ أَرَأَيْتُمْ إِنْ أَصْبَحَ مَاؤُكُمْ غَوْرًا فَمَن يَأْتِيكُم بِمَاءٍ مَّعِينٍ','بگو: «به من خبر دهید اگر آبهای (سرزمین) شما در زمین فرو رود، چه کسی می‌تواند آب جاری و گوارا در دسترس شما قرار دهد؟!»'];
ayat[5272]=[68,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ ن ۚ وَالْقَلَمِ وَمَا يَسْطُرُونَ','ن، سوگند به قلم و آنچه مینویسند،'];
ayat[5273]=[68,2,'مَا أَنتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ','که به نعمت پروردگارت تو مجنون نیستی،'];
ayat[5274]=[68,3,'وَإِنَّ لَكَ لَأَجْرًا غَيْرَ مَمْنُونٍ','و برای تو پاداشی عظیم و همیشگی است!'];
ayat[5275]=[68,4,'وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٍ','و تو اخلاق عظیم و برجسته‌ای داری!'];
ayat[5276]=[68,5,'فَسَتُبْصِرُ وَيُبْصِرُونَ','و بزودی تو می‌بینی و آنان نیز می‌بینند،'];
ayat[5277]=[68,6,'بِأَييِّكُمُ الْمَفْتُونُ','که کدام یک از شما مجنونند!'];
ayat[5278]=[68,7,'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِ وَهُوَ أَعْلَمُ بِالْمُهْتَدِينَ','پروردگارت بهتر از هر کس می‌داند چه کسی از راه او گمراه شده، و هدایت‌یافتگان را نیز بهتر می‌شناسد!'];
ayat[5279]=[68,8,'فَلَا تُطِعِ الْمُكَذِّبِينَ','حال که چنین است از تکذیب‌کنندگان اطاعت مکن!'];
ayat[5280]=[68,9,'وَدُّوا لَوْ تُدْهِنُ فَيُدْهِنُونَ','آنها دوست دارند نرمش نشان دهی تا آنها (هم) نرمش نشان دهند (نرمشی توأم با انحراف از مسیر حق)!'];
ayat[5281]=[68,10,'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَّهِينٍ','و از کسی که بسیار سوگند یاد می‌کند و پست است اطاعت مکن،'];
ayat[5282]=[68,11,'هَمَّازٍ مَّشَّاءٍ بِنَمِيمٍ','کسی که بسیار عیبجوست و به سخن چینی آمد و شد می‌کند،'];
ayat[5283]=[68,12,'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ أَثِيمٍ','و بسیار مانع کار خیر، و متجاوز و گناهکار است؛'];
ayat[5284]=[68,13,'عُتُلٍّ بَعْدَ ذَٰلِكَ زَنِيمٍ','علاوه بر اینها کینه توز و پرخور و خشن و بدنام است!'];
ayat[5285]=[68,14,'أَن كَانَ ذَا مَالٍ وَبَنِينَ','مبادا بخاطر اینکه صاحب مال و فرزندان فراوان است (از او پیروی کنی)!'];
ayat[5286]=[68,15,'إِذَا تُتْلَىٰ عَلَيْهِ آيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ','هنگامی که آیات ما بر او خوانده می‌شود می‌گوید: «اینها افسانه‌های خرافی پیشینیان است!»'];
ayat[5287]=[68,16,'سَنَسِمُهُ عَلَى الْخُرْطُومِ','(ولی) ما بزودی بر بینی او علامت و داغ ننگ می‌نهیم!'];
ayat[5288]=[68,17,'إِنَّا بَلَوْنَاهُمْ كَمَا بَلَوْنَا أَصْحَابَ الْجَنَّةِ إِذْ أَقْسَمُوا لَيَصْرِمُنَّهَا مُصْبِحِينَ','ما آنها را آزمودیم، همان گونه که «صاحبان باغ» را آزمایش کردیم، هنگامی که سوگند یاد کردند که میوه‌های باغ را صبحگاهان (دور از چشم مستمندان) بچینند.'];
ayat[5289]=[68,18,'وَلَا يَسْتَثْنُونَ','و هیچ از آن استثنا نکنند؛'];
ayat[5290]=[68,19,'فَطَافَ عَلَيْهَا طَائِفٌ مِّن رَّبِّكَ وَهُمْ نَائِمُونَ','امّا عذابی فراگیر (شب هنگام) بر (تمام) باغ آنها فرود آمد در حالی که همه در خواب بودند،'];
ayat[5291]=[68,20,'فَأَصْبَحَتْ كَالصَّرِيمِ','و آن باغ سرسبز همچون شب سیاه و ظلمانی شد!'];
ayat[5292]=[68,21,'فَتَنَادَوْا مُصْبِحِينَ','صبحگاهان یکدیگر را صدا زدند،'];
ayat[5293]=[68,22,'أَنِ اغْدُوا عَلَىٰ حَرْثِكُمْ إِن كُنتُمْ صَارِمِينَ','که بسوی کشتزار و باغ خود حرکت کنید اگر قصد چیدن میوه‌ها را دارید!'];
ayat[5294]=[68,23,'فَانطَلَقُوا وَهُمْ يَتَخَافَتُونَ','آنها حرکت کردند در حالی که آهسته با هم می‌گفتند:»'];
ayat[5295]=[68,24,'أَن لَّا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُم مِّسْكِينٌ','«مواظب باشید امروز حتی یک فقیر وارد بر شما نشود!»'];
ayat[5296]=[68,25,'وَغَدَوْا عَلَىٰ حَرْدٍ قَادِرِينَ','(آری) آنها صبحگاهان تصمیم داشتند که با قدرت از مستمندان جلوگیری کنند.'];
ayat[5297]=[68,26,'فَلَمَّا رَأَوْهَا قَالُوا إِنَّا لَضَالُّونَ','هنگامی که (وارد باغ شدند و) آن را دیدند گفتند: «حقّاً» ما گمراهیم!'];
ayat[5298]=[68,27,'بَلْ نَحْنُ مَحْرُومُونَ','(آری، همه چیز از دست ما رفته) بلکه ما محرومیم!»'];
ayat[5299]=[68,28,'قَالَ أَوْسَطُهُمْ أَلَمْ أَقُل لَّكُمْ لَوْلَا تُسَبِّحُونَ','یکی از آنها که از همه عاقلتر بود گفت: «آیا به شما نگفتم چرا تسبیح خدا نمی‌گویید؟!'];
ayat[5300]=[68,29,'قَالُوا سُبْحَانَ رَبِّنَا إِنَّا كُنَّا ظَالِمِينَ','گفتند: «منزّه است پروردگار ما، مسلّماً ما ظالم بودیم!»'];
ayat[5301]=[68,30,'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَلَاوَمُونَ','سپس رو به یکدیگر کرده به ملامت هم پرداختند،'];
ayat[5302]=[68,31,'قَالُوا يَا وَيْلَنَا إِنَّا كُنَّا طَاغِينَ','(و فریادشان بلند شد) گفتند: «وای بر ما که طغیانگر بودیم!'];
ayat[5303]=[68,32,'عَسَىٰ رَبُّنَا أَن يُبْدِلَنَا خَيْرًا مِّنْهَا إِنَّا إِلَىٰ رَبِّنَا رَاغِبُونَ','امیدواریم پروردگارمان (ما را ببخشد و) بهتر از آن به جای آن به ما بدهد، چرا که ما به او علاقه‌مندیم!»'];
ayat[5304]=[68,33,'كَذَٰلِكَ الْعَذَابُ ۖ وَلَعَذَابُ الْآخِرَةِ أَكْبَرُ ۚ لَوْ كَانُوا يَعْلَمُونَ','این گونه است عذاب (خداوند در دنیا)، و عذاب آخرت از آن هم بزرگتر است اگر می‌دانستند!'];
ayat[5305]=[68,34,'إِنَّ لِلْمُتَّقِينَ عِندَ رَبِّهِمْ جَنَّاتِ النَّعِيمِ','مسلّماً برای پرهیزگاران نزد پروردگارشان باغهای پر نعمت بهشت است!'];
ayat[5306]=[68,35,'أَفَنَجْعَلُ الْمُسْلِمِينَ كَالْمُجْرِمِينَ','آیا مؤمنان را همچون مجرمان قرار می‌دهیم؟!'];
ayat[5307]=[68,36,'مَا لَكُمْ كَيْفَ تَحْكُمُونَ','شما را چه می‌شود؟! چگونه داوری می‌کنید؟!'];
ayat[5308]=[68,37,'أَمْ لَكُمْ كِتَابٌ فِيهِ تَدْرُسُونَ','آیا کتابی دارید که از آن درس می‌خوانید...'];
ayat[5309]=[68,38,'إِنَّ لَكُمْ فِيهِ لَمَا تَخَيَّرُونَ','که آنچه را شما انتخاب می‌کنید از آن شماست؟!'];
ayat[5310]=[68,39,'أَمْ لَكُمْ أَيْمَانٌ عَلَيْنَا بَالِغَةٌ إِلَىٰ يَوْمِ الْقِيَامَةِ ۙ إِنَّ لَكُمْ لَمَا تَحْكُمُونَ','یا اینکه عهد و پیمان مؤکّد و مستمرّی تا روز قیامت بر ما دارید که هر چه را حکم کنید برای شما باشد؟!'];
ayat[5311]=[68,40,'سَلْهُمْ أَيُّهُم بِذَٰلِكَ زَعِيمٌ','از آنها بپرس کدام یک از آنان چنین چیزی را تضمین می‌کند؟!'];
ayat[5312]=[68,41,'أَمْ لَهُمْ شُرَكَاءُ فَلْيَأْتُوا بِشُرَكَائِهِمْ إِن كَانُوا صَادِقِينَ','یا اینکه معبودانی دارند که آنها را شریک خدا قرار داده‌اند (و برای آنان شفاعت می‌کنند)؟! اگر راست می‌گویند معبودان خود را بیاورند!'];
ayat[5313]=[68,42,'يَوْمَ يُكْشَفُ عَن سَاقٍ وَيُدْعَوْنَ إِلَى السُّجُودِ فَلَا يَسْتَطِيعُونَ','(به خاطر بیاورید) روزی را که ساق پاها (از وحشت) برهنه می‌گردد و دعوت به سجود می‌شوند، امّا نمی‌توانند (سجود کنند).'];
ayat[5314]=[68,43,'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۖ وَقَدْ كَانُوا يُدْعَوْنَ إِلَى السُّجُودِ وَهُمْ سَالِمُونَ','این در حالی است که چشمهایشان (از شدّت شرمساری) به زیر افتاده، و ذلّت و خواری وجودشان را فراگرفته؛ آنها پیش از این دعوت به سجود می‌شدند در حالی که سالم بودند (ولی امروز دیگر توانایی آن را ندارند)!'];
ayat[5315]=[68,44,'فَذَرْنِي وَمَن يُكَذِّبُ بِهَٰذَا الْحَدِيثِ ۖ سَنَسْتَدْرِجُهُم مِّنْ حَيْثُ لَا يَعْلَمُونَ','اکنون مرا با آنها که این سخن را تکذیب می‌کنند واگذار! ما آنان را از آنجا که نمی‌دانند به تدریج به سوی عذاب پیش می‌بریم.'];
ayat[5316]=[68,45,'وَأُمْلِي لَهُمْ ۚ إِنَّ كَيْدِي مَتِينٌ','و به آنها مهلت (بازگشت) می‌دهم؛ چرا که نقشه‌های من محکم و دقیق است!'];
ayat[5317]=[68,46,'أَمْ تَسْأَلُهُمْ أَجْرًا فَهُم مِّن مَّغْرَمٍ مُّثْقَلُونَ','یا اینکه تو از آنها مُزدی می‌طلبی که پرداختش برای آنها سنگین است؟!'];
ayat[5318]=[68,47,'أَمْ عِندَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُونَ','یا اسرار غیب نزد آنهاست و آن را می‌نویسند (و به یکدیگر می‌دهند)؟!'];
ayat[5319]=[68,48,'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ الْحُوتِ إِذْ نَادَىٰ وَهُوَ مَكْظُومٌ','اکنون که چنین است صبر کن و منتظر فرمان پروردگارت باش، و مانند صاحب ماهی [= یونس‌] مباش (که در تقاضای مجازات قومش عجله کرد و گرفتار مجازات ترک اولی شد) در آن زمان که با نهایت اندوه خدا را خواند.'];
ayat[5320]=[68,49,'لَّوْلَا أَن تَدَارَكَهُ نِعْمَةٌ مِّن رَّبِّهِ لَنُبِذَ بِالْعَرَاءِ وَهُوَ مَذْمُومٌ','و اگر رحمت خدا به یاریش نیامده بود، (از شکم ماهی) بیرون افکنده می‌شد در حالی که نکوهیده بود!'];
ayat[5321]=[68,50,'فَاجْتَبَاهُ رَبُّهُ فَجَعَلَهُ مِنَ الصَّالِحِينَ','ولی پروردگارش او را برگزید و از صالحان قرار داد!'];
ayat[5322]=[68,51,'وَإِن يَكَادُ الَّذِينَ كَفَرُوا لَيُزْلِقُونَكَ بِأَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُولُونَ إِنَّهُ لَمَجْنُونٌ','نزدیک است کافران هنگامی که آیات قرآن را می‌شنوند با چشم‌زخم خود تو را از بین ببرند، و می‌گویند: «او دیوانه است!»'];
ayat[5323]=[68,52,'وَمَا هُوَ إِلَّا ذِكْرٌ لِّلْعَالَمِينَ','در حالی که این (قرآن) جز مایه بیداری برای جهانیان نیست!'];
ayat[5324]=[69,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْحَاقَّةُ','(روز رستاخیز) روزی است که مسلّماً واقع می‌شود!'];
ayat[5325]=[69,2,'مَا الْحَاقَّةُ','چه روز واقع شدنی!'];
ayat[5326]=[69,3,'وَمَا أَدْرَاكَ مَا الْحَاقَّةُ','و تو چه می‌دانی آن روز واقع شدنی چیست؟!'];
ayat[5327]=[69,4,'كَذَّبَتْ ثَمُودُ وَعَادٌ بِالْقَارِعَةِ','قوم «ثمود» و «عاد» عذاب کوبنده الهی را انکار کردند (و نتیجه شومش را دیدند)!'];
ayat[5328]=[69,5,'فَأَمَّا ثَمُودُ فَأُهْلِكُوا بِالطَّاغِيَةِ','امّا قوم «ثمود» با عذابی سرکش هلاک شدند!'];
ayat[5329]=[69,6,'وَأَمَّا عَادٌ فَأُهْلِكُوا بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ','و امّا قوم «عاد» با تندبادی طغیانگر و سرد و پرصدا به هلاکت رسیدند،'];
ayat[5330]=[69,7,'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَانِيَةَ أَيَّامٍ حُسُومًا فَتَرَى الْقَوْمَ فِيهَا صَرْعَىٰ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ خَاوِيَةٍ','(خداوند) این تندباد بنیان‌کن را هفت شب و هشت روز پی در پی بر آنها مسلّط ساخت، (و اگر آنجا بودی) می‌دیدی که آن قوم همچون تنه‌های پوسیده و تو خالی درختان نخل در میان این تند باد روی زمین افتاده و هلاک شده‌اند!'];
ayat[5331]=[69,8,'فَهَلْ تَرَىٰ لَهُم مِّن بَاقِيَةٍ','آیا کسی از آنها را باقی می‌بینی؟!'];
ayat[5332]=[69,9,'وَجَاءَ فِرْعَوْنُ وَمَن قَبْلَهُ وَالْمُؤْتَفِكَاتُ بِالْخَاطِئَةِ','و فرعون و کسانی که پیش از او بودند و همچنین اهل شهرهای زیر و رو شده [=قوم لوط] مرتکب گناهان بزرگ شدند،'];
ayat[5333]=[69,10,'فَعَصَوْا رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رَّابِيَةً','و با فرستاده پروردگارشان مخالفت کردند؛ و خداوند (نیز) آنها را به عذاب شدیدی گرفتار ساخت!'];
ayat[5334]=[69,11,'إِنَّا لَمَّا طَغَى الْمَاءُ حَمَلْنَاكُمْ فِي الْجَارِيَةِ','و هنگامی که آب طغیان کرد، ما شما را سوار بر کشتی کردیم،'];
ayat[5335]=[69,12,'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَا أُذُنٌ وَاعِيَةٌ','تا آن را وسیله تذکّری برای شما قرار دهیم و گوشهای شنوا آن را دریابد و بفهمد.'];
ayat[5336]=[69,13,'فَإِذَا نُفِخَ فِي الصُّورِ نَفْخَةٌ وَاحِدَةٌ','به محض اینکه یک بار در «صور» دمیده شود،'];
ayat[5337]=[69,14,'وَحُمِلَتِ الْأَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَاحِدَةً','و زمین و کوه‌ها از جا برداشته شوند و یکباره در هم کوبیده و متلاشی گردند،'];
ayat[5338]=[69,15,'فَيَوْمَئِذٍ وَقَعَتِ الْوَاقِعَةُ','در آن روز «واقعه عظیم» روی می‌دهد،'];
ayat[5339]=[69,16,'وَانشَقَّتِ السَّمَاءُ فَهِيَ يَوْمَئِذٍ وَاهِيَةٌ','و آسمان از هم می‌شکافد و سست می‌گردد و فرومی‌ریزد!'];
ayat[5340]=[69,17,'وَالْمَلَكُ عَلَىٰ أَرْجَائِهَا ۚ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَانِيَةٌ','فرشتگان در اطراف آسمان قرارمی‌گیرند (و برای انجام مأموریتها آماده می‌شوند)؛ و آن روز عرش پروردگارت را هشت فرشته بر فراز همه آنها حمل می‌کنند!'];
ayat[5341]=[69,18,'يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفَىٰ مِنكُمْ خَافِيَةٌ','در آن روز همگی به پیشگاه خدا عرضه می‌شوید و چیزی از کارهای شما پنهان نمی‌ماند!'];
ayat[5342]=[69,19,'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ فَيَقُولُ هَاؤُمُ اقْرَءُوا كِتَابِيَهْ','پس کسی که نامه اعمالش را به دست راستش دهند (از شدّت شادی و مباهات) فریاد می‌زند که: «(ای اهل محشر!) نامه اعمال مرا بگیرید و بخوانید!'];
ayat[5343]=[69,20,'إِنِّي ظَنَنتُ أَنِّي مُلَاقٍ حِسَابِيَهْ','من یقین داشتم که (قیامتی در کار است و) به حساب اعمالم می‌رسم!»'];
ayat[5344]=[69,21,'فَهُوَ فِي عِيشَةٍ رَّاضِيَةٍ','او در یک زندگی (کاملاً) رضایتبخش قرار خواهد داشت،'];
ayat[5345]=[69,22,'فِي جَنَّةٍ عَالِيَةٍ','در بهشتی عالی،'];
ayat[5346]=[69,23,'قُطُوفُهَا دَانِيَةٌ','که میوه هایش در دسترس است!'];
ayat[5347]=[69,24,'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا أَسْلَفْتُمْ فِي الْأَيَّامِ الْخَالِيَةِ','(و به آنان گفته می‌شود:) بخورید و بیاشامید گوارا در برابر اعمالی که در ایّام گذشته انجام دادید!'];
ayat[5348]=[69,25,'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِشِمَالِهِ فَيَقُولُ يَا لَيْتَنِي لَمْ أُوتَ كِتَابِيَهْ','امّا کسی که نامه اعمالش را به دست چپش بدهند می‌گوید: «ای کاش هرگز نامه اعمالم را به من نمی‌دادند.'];
ayat[5349]=[69,26,'وَلَمْ أَدْرِ مَا حِسَابِيَهْ','و نمی‌دانستم حساب من چیست!'];
ayat[5350]=[69,27,'يَا لَيْتَهَا كَانَتِ الْقَاضِيَةَ','ای کاش مرگم فرا می‌رسید!'];
ayat[5351]=[69,28,'مَا أَغْنَىٰ عَنِّي مَالِيَهْ ۜ','مال و ثروتم هرگز مرا بی‌نیاز نکرد،'];
ayat[5352]=[69,29,'هَلَكَ عَنِّي سُلْطَانِيَهْ','قدرت من نیز از دست رفت!»'];
ayat[5353]=[69,30,'خُذُوهُ فَغُلُّوهُ','او را بگیرید و دربند و زنجیرش کنید!'];
ayat[5354]=[69,31,'ثُمَّ الْجَحِيمَ صَلُّوهُ','سپس او را در دوزخ بیفکنید!'];
ayat[5355]=[69,32,'ثُمَّ فِي سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعًا فَاسْلُكُوهُ','بعد او را به زنجیری که هفتاد ذراع است ببندید؛'];
ayat[5356]=[69,33,'إِنَّهُ كَانَ لَا يُؤْمِنُ بِاللَّهِ الْعَظِيمِ','چرا که او هرگز به خداوند بزرگ ایمان نمی‌آورد،'];
ayat[5357]=[69,34,'وَلَا يَحُضُّ عَلَىٰ طَعَامِ الْمِسْكِينِ','و هرگز مردم را بر اطعام مستمندان تشویق نمی‌نمود؛'];
ayat[5358]=[69,35,'فَلَيْسَ لَهُ الْيَوْمَ هَاهُنَا حَمِيمٌ','از این رو امروز هم در اینجا یار مهربانی ندارد،'];
ayat[5359]=[69,36,'وَلَا طَعَامٌ إِلَّا مِنْ غِسْلِينٍ','و نه طعامی، جز از چرک و خون!'];
ayat[5360]=[69,37,'لَّا يَأْكُلُهُ إِلَّا الْخَاطِئُونَ','غذایی که جز خطاکاران آن را نمی‌خورند!'];
ayat[5361]=[69,38,'فَلَا أُقْسِمُ بِمَا تُبْصِرُونَ','سوگند به آنچه می‌بینید،'];
ayat[5362]=[69,39,'وَمَا لَا تُبْصِرُونَ','و آنچه نمی‌بینید،'];
ayat[5363]=[69,40,'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ','که این قرآن گفتار رسول بزرگواری است،'];
ayat[5364]=[69,41,'وَمَا هُوَ بِقَوْلِ شَاعِرٍ ۚ قَلِيلًا مَّا تُؤْمِنُونَ','و گفته شاعری نیست، امّا کمتر ایمان می‌آورید!'];
ayat[5365]=[69,42,'وَلَا بِقَوْلِ كَاهِنٍ ۚ قَلِيلًا مَّا تَذَكَّرُونَ','و نه گفته کاهنی، هر چند کمتر متذکّر می‌شوید!'];
ayat[5366]=[69,43,'تَنزِيلٌ مِّن رَّبِّ الْعَالَمِينَ','کلامی است که از سوی پروردگار عالمیان نازل شده است!'];
ayat[5367]=[69,44,'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْأَقَاوِيلِ','اگر او سخنی دروغ بر ما می‌بست،'];
ayat[5368]=[69,45,'لَأَخَذْنَا مِنْهُ بِالْيَمِينِ','ما او را با قدرت می‌گرفتیم،'];
ayat[5369]=[69,46,'ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِينَ','سپس رگ قلبش را قطع می‌کردیم،'];
ayat[5370]=[69,47,'فَمَا مِنكُم مِّنْ أَحَدٍ عَنْهُ حَاجِزِينَ','و هیچ کس از شما نمی‌توانست از (مجازات) او مانع شود!'];
ayat[5371]=[69,48,'وَإِنَّهُ لَتَذْكِرَةٌ لِّلْمُتَّقِينَ','و آن مسلّماً تذکری برای پرهیزگاران است!'];
ayat[5372]=[69,49,'وَإِنَّا لَنَعْلَمُ أَنَّ مِنكُم مُّكَذِّبِينَ','و ما می‌دانیم که بعضی از شما (آن را) تکذیب می‌کنید!'];
ayat[5373]=[69,50,'وَإِنَّهُ لَحَسْرَةٌ عَلَى الْكَافِرِينَ','و آن مایه حسرت کافران است!'];
ayat[5374]=[69,51,'وَإِنَّهُ لَحَقُّ الْيَقِينِ','و آن یقین خالص است!'];
ayat[5375]=[69,52,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيمِ','حال که چنین است به نام پروردگار بزرگت تسبیح گوی!'];
ayat[5376]=[70,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَأَلَ سَائِلٌ بِعَذَابٍ وَاقِعٍ','تقاضاکننده‌ای تقاضای عذابی کرد که واقع شد!'];
ayat[5377]=[70,2,'لِّلْكَافِرِينَ لَيْسَ لَهُ دَافِعٌ','این عذاب مخصوص کافران است، و هیچ کس نمی‌تواند آن را دفع کند،'];
ayat[5378]=[70,3,'مِّنَ اللَّهِ ذِي الْمَعَارِجِ','از سوی خداوند ذی المعارج [= خداوندی که فرشتگانش بر آسمانها صعود و عروج می‌کنند]!'];
ayat[5379]=[70,4,'تَعْرُجُ الْمَلَائِكَةُ وَالرُّوحُ إِلَيْهِ فِي يَوْمٍ كَانَ مِقْدَارُهُ خَمْسِينَ أَلْفَ سَنَةٍ','فرشتگان و روح [= فرشته مقرّب خداوند] بسوی او عروج می‌کنند در آن روزی که مقدارش پنجاه هزار سال است!'];
ayat[5380]=[70,5,'فَاصْبِرْ صَبْرًا جَمِيلًا','پس صبر جمیل پیشه کن،'];
ayat[5381]=[70,6,'إِنَّهُمْ يَرَوْنَهُ بَعِيدًا','زیرا آنها آن روز را دور می‌بینند،'];
ayat[5382]=[70,7,'وَنَرَاهُ قَرِيبًا','و ما آن را نزدیک می‌بینیم!'];
ayat[5383]=[70,8,'يَوْمَ تَكُونُ السَّمَاءُ كَالْمُهْلِ','همان روز که آسمان همچون فلز گداخته می‌شود،'];
ayat[5384]=[70,9,'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ','و کوه‌ها مانند پشم رنگین متلاشی خواهد بود،'];
ayat[5385]=[70,10,'وَلَا يَسْأَلُ حَمِيمٌ حَمِيمًا','و هیچ دوست صمیمی سراغ دوستش را نمی‌گیرد!'];
ayat[5386]=[70,11,'يُبَصَّرُونَهُمْ ۚ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِي مِنْ عَذَابِ يَوْمِئِذٍ بِبَنِيهِ','آنها را نشانشان می‌دهند (ولی هر کس گرفتار کار خویشتن است)، چنان است که گنهکار دوست می‌دارد فرزندان خود را در برابر عذاب آن روز فدا کند،'];
ayat[5387]=[70,12,'وَصَاحِبَتِهِ وَأَخِيهِ','و همسر و برادرش را،'];
ayat[5388]=[70,13,'وَفَصِيلَتِهِ الَّتِي تُؤْوِيهِ','و قبیله‌اش را که همیشه از او حمایت می‌کرد،'];
ayat[5389]=[70,14,'وَمَن فِي الْأَرْضِ جَمِيعًا ثُمَّ يُنجِيهِ','و همه مردم روی زمین را تا مایه نجاتش گردند؛'];
ayat[5390]=[70,15,'كَلَّا ۖ إِنَّهَا لَظَىٰ','امّا هرگز چنین نیست (که با اینها بتوان نجات یافت، آری) شعله‌های سوزان آتش است،'];
ayat[5391]=[70,16,'نَزَّاعَةً لِّلشَّوَىٰ','دست و پا و پوست سر را می‌کند و می‌برد!'];
ayat[5392]=[70,17,'تَدْعُو مَنْ أَدْبَرَ وَتَوَلَّىٰ','و کسانی را که به فرمان خدا پشت کردند صدا می‌زند،'];
ayat[5393]=[70,18,'وَجَمَعَ فَأَوْعَىٰ','و (همچنین آنها که) اموال را جمع و ذخیره کردند!'];
ayat[5394]=[70,19,'۞ إِنَّ الْإِنسَانَ خُلِقَ هَلُوعًا','به یقین انسان حریص و کم‌طاقت آفریده شده است،'];
ayat[5395]=[70,20,'إِذَا مَسَّهُ الشَّرُّ جَزُوعًا','هنگامی که بدی به او رسد بیتابی می‌کند،'];
ayat[5396]=[70,21,'وَإِذَا مَسَّهُ الْخَيْرُ مَنُوعًا','و هنگامی که خوبی به او رسد مانع دیگران می‌شود (و بخل می‌ورزد)،'];
ayat[5397]=[70,22,'إِلَّا الْمُصَلِّينَ','مگر نمازگزاران،'];
ayat[5398]=[70,23,'الَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ دَائِمُونَ','آنها که نمازها را پیوسته بجا می‌آورند،'];
ayat[5399]=[70,24,'وَالَّذِينَ فِي أَمْوَالِهِمْ حَقٌّ مَّعْلُومٌ','و آنها که در اموالشان حق معلومی است...'];
ayat[5400]=[70,25,'لِّلسَّائِلِ وَالْمَحْرُومِ','برای تقاضاکننده و محروم،'];
ayat[5401]=[70,26,'وَالَّذِينَ يُصَدِّقُونَ بِيَوْمِ الدِّينِ','و آنها که به روز جزا ایمان دارند،'];
ayat[5402]=[70,27,'وَالَّذِينَ هُم مِّنْ عَذَابِ رَبِّهِم مُّشْفِقُونَ','و آنها که از عذاب پروردگارشان بیمناکند،'];
ayat[5403]=[70,28,'إِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ','چرا که هیچ کس از عذاب پروردگارش در امان نیست،'];
ayat[5404]=[70,29,'وَالَّذِينَ هُمْ لِفُرُوجِهِمْ حَافِظُونَ','و آنها که دامان خویش را (از بی‌عفّتی) حفظ می‌کنند،'];
ayat[5405]=[70,30,'إِلَّا عَلَىٰ أَزْوَاجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ','جز با همسران و کنیزان (که در حکم همسرند آمیزش ندارند)، چرا که در بهره‌گیری از اینها مورد سرزنش نخواهند بود!'];
ayat[5406]=[70,31,'فَمَنِ ابْتَغَىٰ وَرَاءَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْعَادُونَ','و هر کس جز اینها را طلب کند، متجاوز است!'];
ayat[5407]=[70,32,'وَالَّذِينَ هُمْ لِأَمَانَاتِهِمْ وَعَهْدِهِمْ رَاعُونَ','و آنها که امانتها و عهد خود را رعایت می‌کنند،'];
ayat[5408]=[70,33,'وَالَّذِينَ هُم بِشَهَادَاتِهِمْ قَائِمُونَ','و آنها که با ادای شهادتشان قیام می‌نمایند،'];
ayat[5409]=[70,34,'وَالَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَ','و آنها که بر نماز مواظبت دارند،'];
ayat[5410]=[70,35,'أُولَٰئِكَ فِي جَنَّاتٍ مُّكْرَمُونَ','آنان در باغهای بهشتی (پذیرایی و) گرامی داشته می‌شوند.'];
ayat[5411]=[70,36,'فَمَالِ الَّذِينَ كَفَرُوا قِبَلَكَ مُهْطِعِينَ','این کافران را چه می‌شود که با سرعت نزد تو می‌آیند...'];
ayat[5412]=[70,37,'عَنِ الْيَمِينِ وَعَنِ الشِّمَالِ عِزِينَ','از راست و چپ، گروه گروه (و آرزوی بهشت دارند)!'];
ayat[5413]=[70,38,'أَيَطْمَعُ كُلُّ امْرِئٍ مِّنْهُمْ أَن يُدْخَلَ جَنَّةَ نَعِيمٍ','آیا هر یک از آنها (با این اعمال زشتش) طمع دارد که او را در بهشت پر نعمت الهی وارد کنند؟!'];
ayat[5414]=[70,39,'كَلَّا ۖ إِنَّا خَلَقْنَاهُم مِّمَّا يَعْلَمُونَ','هرگز چنین نیست؛ ما آنها را از آنچه خودشان می‌دانند آفریده‌ایم!'];
ayat[5415]=[70,40,'فَلَا أُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغَارِبِ إِنَّا لَقَادِرُونَ','سوگند به پروردگار مشرقها و مغربها که ما قادریم...'];
ayat[5416]=[70,41,'عَلَىٰ أَن نُّبَدِّلَ خَيْرًا مِّنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ','که جای آنان را به کسانی بدهیم که از آنها بهترند؛ و ما هرگز مغلوب نخواهیم شد!'];
ayat[5417]=[70,42,'فَذَرْهُمْ يَخُوضُوا وَيَلْعَبُوا حَتَّىٰ يُلَاقُوا يَوْمَهُمُ الَّذِي يُوعَدُونَ','آنان را به حال خود واگذار تا در باطل خود فروروند و بازی کنند تا زمانی که روز موعود خود را ملاقات نمایند!'];
ayat[5418]=[70,43,'يَوْمَ يَخْرُجُونَ مِنَ الْأَجْدَاثِ سِرَاعًا كَأَنَّهُمْ إِلَىٰ نُصُبٍ يُوفِضُونَ','همان روز که از قبرها بسرعت خارج می‌شوند، گویی به سوی بتها می‌دوند...'];
ayat[5419]=[70,44,'خَاشِعَةً أَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۚ ذَٰلِكَ الْيَوْمُ الَّذِي كَانُوا يُوعَدُونَ','در حالی که چشمهایشان از شرم و وحشت به زیر افتاده، و پرده‌ای از ذلّت و خواری آنها را پوشانده است! این همان روزی است که به آنها وعده داده می‌شد!'];
ayat[5420]=[71,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِ أَنْ أَنذِرْ قَوْمَكَ مِن قَبْلِ أَن يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ','ما نوح را به سوی قومش فرستادیم و گفتیم: «قوم خود را انذار کن پیش از آنکه عذاب دردناک به سراغشان آید!»'];
ayat[5421]=[71,2,'قَالَ يَا قَوْمِ إِنِّي لَكُمْ نَذِيرٌ مُّبِينٌ','گفت: «ای قوم! من برای شما بیم‌دهنده آشکاری هستم،'];
ayat[5422]=[71,3,'أَنِ اعْبُدُوا اللَّهَ وَاتَّقُوهُ وَأَطِيعُونِ','که خدا را پرستش کنید و از مخالفت او بپرهیزید و مرا اطاعت نمایید!'];
ayat[5423]=[71,4,'يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَىٰ أَجَلٍ مُّسَمًّى ۚ إِنَّ أَجَلَ اللَّهِ إِذَا جَاءَ لَا يُؤَخَّرُ ۖ لَوْ كُنتُمْ تَعْلَمُونَ','اگر چنین کنید، خدا گناهانتان را می‌آمرزد و تا زمان معیّنی شما را عمر می‌دهد؛ زیرا هنگامی که اجل الهی فرا رسد، تأخیری نخواهد داشت اگر می‌دانستید!»'];
ayat[5424]=[71,5,'قَالَ رَبِّ إِنِّي دَعَوْتُ قَوْمِي لَيْلًا وَنَهَارًا','(نوح) گفت: «پروردگارا! من قوم خود را شب و روز (بسوی تو) دعوت کردم،'];
ayat[5425]=[71,6,'فَلَمْ يَزِدْهُمْ دُعَائِي إِلَّا فِرَارًا','امّا دعوت من چیزی جز فرار از حقّ بر آنان نیفزود!'];
ayat[5426]=[71,7,'وَإِنِّي كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوا أَصَابِعَهُمْ فِي آذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَأَصَرُّوا وَاسْتَكْبَرُوا اسْتِكْبَارًا','و من هر زمان آنها را دعوت کردم که (ایمان بیاورند و) تو آنها را بیامرزی، انگشتان خویش را در گوشهایشان قرار داده و لباسهایشان را بر خود پیچیدند، و در مخالفت اصرار ورزیدند و به شدّت استکبار کردند!'];
ayat[5427]=[71,8,'ثُمَّ إِنِّي دَعَوْتُهُمْ جِهَارًا','سپس من آنها را با صدای بلند (به اطاعت فرمان تو) دعوت کردم،'];
ayat[5428]=[71,9,'ثُمَّ إِنِّي أَعْلَنتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًا','سپس آشکارا و نهان (حقیقت توحید و ایمان را) برای آنان بیان داشتم!'];
ayat[5429]=[71,10,'فَقُلْتُ اسْتَغْفِرُوا رَبَّكُمْ إِنَّهُ كَانَ غَفَّارًا','به آنها گفتم: «از پروردگار خویش آمرزش بطلبید که او بسیار آمرزنده است...'];
ayat[5430]=[71,11,'يُرْسِلِ السَّمَاءَ عَلَيْكُم مِّدْرَارًا','تا بارانهای پربرکت آسمان را پی در پی بر شما فرستد،'];
ayat[5431]=[71,12,'وَيُمْدِدْكُم بِأَمْوَالٍ وَبَنِينَ وَيَجْعَل لَّكُمْ جَنَّاتٍ وَيَجْعَل لَّكُمْ أَنْهَارًا','و شما را با اموال و فرزندان فراوان کمک کند و باغهای سرسبز و نهرهای جاری در اختیارتان قرار دهد!'];
ayat[5432]=[71,13,'مَّا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًا','چرا شما برای خدا عظمت قائل نیستید؟!'];
ayat[5433]=[71,14,'وَقَدْ خَلَقَكُمْ أَطْوَارًا','در حالی که شما را در مراحل مختلف آفرید (تا از نطفه به انسان کامل رسیدید)!'];
ayat[5434]=[71,15,'أَلَمْ تَرَوْا كَيْفَ خَلَقَ اللَّهُ سَبْعَ سَمَاوَاتٍ طِبَاقًا','آیا نمی‌دانید چگونه خداوند هفت آسمان را یکی بالای دیگری آفریده است،'];
ayat[5435]=[71,16,'وَجَعَلَ الْقَمَرَ فِيهِنَّ نُورًا وَجَعَلَ الشَّمْسَ سِرَاجًا','و ماه را در میان آسمانها مایه روشنایی، و خورشید را چراغ فروزانی قرار داده است؟!'];
ayat[5436]=[71,17,'وَاللَّهُ أَنبَتَكُم مِّنَ الْأَرْضِ نَبَاتًا','و خداوند شما را همچون گیاهی از زمین رویانید،'];
ayat[5437]=[71,18,'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًا','سپس شما را به همان زمین بازمی‌گرداند، و بار دیگر شما را خارج می‌سازد!'];
ayat[5438]=[71,19,'وَاللَّهُ جَعَلَ لَكُمُ الْأَرْضَ بِسَاطًا','و خداوند زمین را برای شما فرش گسترده‌ای قرار داد...'];
ayat[5439]=[71,20,'لِّتَسْلُكُوا مِنْهَا سُبُلًا فِجَاجًا','تا از راههای وسیع و دره‌های آن بگذرید (و به هر جا می‌خواهید بروید)!»'];
ayat[5440]=[71,21,'قَالَ نُوحٌ رَّبِّ إِنَّهُمْ عَصَوْنِي وَاتَّبَعُوا مَن لَّمْ يَزِدْهُ مَالُهُ وَوَلَدُهُ إِلَّا خَسَارًا','نوح (بعد از نومیدی از هدایت آنان) گفت: «پروردگارا! آنها نافرمانی من کردند و از کسانی پیروی نمودند که اموال و فرزندانشان چیزی جز زیانکاری بر آنها نیفزوده است!'];
ayat[5441]=[71,22,'وَمَكَرُوا مَكْرًا كُبَّارًا','و (این رهبران گمراه) مکر عظیمی به کار بردند...'];
ayat[5442]=[71,23,'وَقَالُوا لَا تَذَرُنَّ آلِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَلَا سُوَاعًا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًا','و گفتند: دست از خدایان و بتهای خود برندارید (به خصوص) بتهای «وَد»، «سواع»، «یغوث»، «یعوق» و «نسر» را رها نکنید!'];
ayat[5443]=[71,24,'وَقَدْ أَضَلُّوا كَثِيرًا ۖ وَلَا تَزِدِ الظَّالِمِينَ إِلَّا ضَلَالًا','و آنها گروه بسیاری را گمراه کردند! خداوندا، ظالمان را جز ضلالت میفزا!»'];
ayat[5444]=[71,25,'مِّمَّا خَطِيئَاتِهِمْ أُغْرِقُوا فَأُدْخِلُوا نَارًا فَلَمْ يَجِدُوا لَهُم مِّن دُونِ اللَّهِ أَنصَارًا','(آری، سرانجام) همگی بخاطر گناهانشان غرق شدند و در آتش دوزخ وارد گشتند، و جز خدا یاورانی برای خود نیافتند!'];
ayat[5445]=[71,26,'وَقَالَ نُوحٌ رَّبِّ لَا تَذَرْ عَلَى الْأَرْضِ مِنَ الْكَافِرِينَ دَيَّارًا','نوح گفت: «پروردگارا! هیچ یک از کافران را بر روی زمین باقی مگذار!'];
ayat[5446]=[71,27,'إِنَّكَ إِن تَذَرْهُمْ يُضِلُّوا عِبَادَكَ وَلَا يَلِدُوا إِلَّا فَاجِرًا كَفَّارًا','چرا که اگر آنها را باقی بگذاری، بندگانت را گمراه می‌کنند و جز نسلی فاجر و کافر به وجود نمی‌آورند!'];
ayat[5447]=[71,28,'رَّبِّ اغْفِرْ لِي وَلِوَالِدَيَّ وَلِمَن دَخَلَ بَيْتِيَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ وَلَا تَزِدِ الظَّالِمِينَ إِلَّا تَبَارًا','پروردگارا! مرا، و پدر و مادرم و تمام کسانی را که با ایمان وارد خانه من شدند، و جمیع مردان و زنان باایمان را بیامرز؛ و ظالمان را جز هلاکت میفزا!»'];
ayat[5448]=[72,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أُوحِيَ إِلَيَّ أَنَّهُ اسْتَمَعَ نَفَرٌ مِّنَ الْجِنِّ فَقَالُوا إِنَّا سَمِعْنَا قُرْآنًا عَجَبًا','بگو: به من وحی شده است که جمعی از جنّ به سخنانم گوش فراداده‌اند، سپس گفته‌اند: «ما قرآن عجیبی شنیده‌ایم...'];
ayat[5449]=[72,2,'يَهْدِي إِلَى الرُّشْدِ فَآمَنَّا بِهِ ۖ وَلَن نُّشْرِكَ بِرَبِّنَا أَحَدًا','که به راه راست هدایت می‌کند، پس ما به آن ایمان آورده‌ایم و هرگز کسی را شریک پروردگارمان قرارنمی‌دهیم!'];
ayat[5450]=[72,3,'وَأَنَّهُ تَعَالَىٰ جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَلَا وَلَدًا','و اینکه بلند است مقام باعظمت پروردگار ما، و او هرگز برای خود همسر و فرزندی انتخاب نکرده است!'];
ayat[5451]=[72,4,'وَأَنَّهُ كَانَ يَقُولُ سَفِيهُنَا عَلَى اللَّهِ شَطَطًا','و اینکه سفیه ما (ابلیس) درباره خداوند سخنان ناروا می‌گفت!'];
ayat[5452]=[72,5,'وَأَنَّا ظَنَنَّا أَن لَّن تَقُولَ الْإِنسُ وَالْجِنُّ عَلَى اللَّهِ كَذِبًا','و اینکه ما گمان می‌کردیم که انس و جنّ هرگز بر خدا دروغ نمی‌بندند!'];
ayat[5453]=[72,6,'وَأَنَّهُ كَانَ رِجَالٌ مِّنَ الْإِنسِ يَعُوذُونَ بِرِجَالٍ مِّنَ الْجِنِّ فَزَادُوهُمْ رَهَقًا','و اینکه مردانی از بشر به مردانی از جنّ پناه می‌بردند، و آنها سبب افزایش گمراهی و طغیانشان می‌شدند!'];
ayat[5454]=[72,7,'وَأَنَّهُمْ ظَنُّوا كَمَا ظَنَنتُمْ أَن لَّن يَبْعَثَ اللَّهُ أَحَدًا','و اینکه آنها گمان کردند -همان‌گونه که شما گمان می‌کردید- که خداوند هرگز کسی را (به نبوّت) مبعوث نمی‌کند!'];
ayat[5455]=[72,8,'وَأَنَّا لَمَسْنَا السَّمَاءَ فَوَجَدْنَاهَا مُلِئَتْ حَرَسًا شَدِيدًا وَشُهُبًا','و اینکه ما آسمان را جستجو کردیم و همه را پر از محافظان قوّی و تیرهای شهاب یافتیم!'];
ayat[5456]=[72,9,'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِ ۖ فَمَن يَسْتَمِعِ الْآنَ يَجِدْ لَهُ شِهَابًا رَّصَدًا','و اینکه ما پیش از این به استراق سمع در آسمانها می‌نشستیم؛ امّا اکنون هر کس بخواهد استراق سمع کند، شهابی را در کمین خود می‌یابد!'];
ayat[5457]=[72,10,'وَأَنَّا لَا نَدْرِي أَشَرٌّ أُرِيدَ بِمَن فِي الْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًا','و اینکه (با این اوضاع) ما نمی‌دانیم آیا اراده شرّی درباره اهل زمین شده یا پروردگارشان خواسته است آنان را هدایت کند؟!'];
ayat[5458]=[72,11,'وَأَنَّا مِنَّا الصَّالِحُونَ وَمِنَّا دُونَ ذَٰلِكَ ۖ كُنَّا طَرَائِقَ قِدَدًا','و اینکه در میان ما، افرادی صالح و افرادی غیر صالحند؛ و ما گروه‌های متفاوتی هستیم!'];
ayat[5459]=[72,12,'وَأَنَّا ظَنَنَّا أَن لَّن نُّعْجِزَ اللَّهَ فِي الْأَرْضِ وَلَن نُّعْجِزَهُ هَرَبًا','و اینکه ما یقین داریم هرگز نمی‌توانیم بر اراده خداوند در زمین غالب شویم و نمی‌توانیم از (پنجه قدرت) او بگریزیم!'];
ayat[5460]=[72,13,'وَأَنَّا لَمَّا سَمِعْنَا الْهُدَىٰ آمَنَّا بِهِ ۖ فَمَن يُؤْمِن بِرَبِّهِ فَلَا يَخَافُ بَخْسًا وَلَا رَهَقًا','و اینکه ما هنگامی که هدایت قرآن را شنیدیم به آن ایمان آوردیم؛ و هر کس به پروردگارش ایمان بیاورد، نه از نقصان می‌ترسد و نه از ظلم!'];
ayat[5461]=[72,14,'وَأَنَّا مِنَّا الْمُسْلِمُونَ وَمِنَّا الْقَاسِطُونَ ۖ فَمَنْ أَسْلَمَ فَأُولَٰئِكَ تَحَرَّوْا رَشَدًا','و اینکه گروهی از ما مسلمان و گروهی ظالمند؛ هر کس اسلام را اختیار کند راه راست را برگزیده است،'];
ayat[5462]=[72,15,'وَأَمَّا الْقَاسِطُونَ فَكَانُوا لِجَهَنَّمَ حَطَبًا','و امّا ظالمان آتشگیره و هیزم دوزخند!'];
ayat[5463]=[72,16,'وَأَن لَّوِ اسْتَقَامُوا عَلَى الطَّرِيقَةِ لَأَسْقَيْنَاهُم مَّاءً غَدَقًا','و اینکه اگر آنها [= جنّ و انس‌] در راه (ایمان) استقامت ورزند، با آب فراوان سیرابشان می‌کنیم!'];
ayat[5464]=[72,17,'لِّنَفْتِنَهُمْ فِيهِ ۚ وَمَن يُعْرِضْ عَن ذِكْرِ رَبِّهِ يَسْلُكْهُ عَذَابًا صَعَدًا','هدف این است که ما آنها را با این نعمت فراوان بیازماییم؛ و هر کس از یاد پروردگارش روی گرداند، او را به عذاب شدید و فزاینده‌ای گرفتار می‌سازد!'];
ayat[5465]=[72,18,'وَأَنَّ الْمَسَاجِدَ لِلَّهِ فَلَا تَدْعُوا مَعَ اللَّهِ أَحَدًا','و اینکه مساجد از آن خداست، پس هیچ کس را با خدا نخوانید!'];
ayat[5466]=[72,19,'وَأَنَّهُ لَمَّا قَامَ عَبْدُ اللَّهِ يَدْعُوهُ كَادُوا يَكُونُونَ عَلَيْهِ لِبَدًا','و اینکه هنگامی که بنده خدا [= محمّد (ص)] به عبادت برمی‌خاست و او را می‌خواند، گروهی پیرامون او بشدّت ازدحام می‌کردند!»'];
ayat[5467]=[72,20,'قُلْ إِنَّمَا أَدْعُو رَبِّي وَلَا أُشْرِكُ بِهِ أَحَدًا','بگو: «من تنها پروردگارم را می‌خوانم و هیچ کس را شریک او قرار نمی‌دهم!»'];
ayat[5468]=[72,21,'قُلْ إِنِّي لَا أَمْلِكُ لَكُمْ ضَرًّا وَلَا رَشَدًا','بگو: «من مالک زیان و هدایتی برای شما نیستم!»'];
ayat[5469]=[72,22,'قُلْ إِنِّي لَن يُجِيرَنِي مِنَ اللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِن دُونِهِ مُلْتَحَدًا','بگو: «(اگر من نیز بر خلاف فرمانش رفتار کنم) هیچ کس مرا در برابر او حمایت نمی‌کند و پناهگاهی جز او نمی‌یابم؛'];
ayat[5470]=[72,23,'إِلَّا بَلَاغًا مِّنَ اللَّهِ وَرِسَالَاتِهِ ۚ وَمَن يَعْصِ اللَّهَ وَرَسُولَهُ فَإِنَّ لَهُ نَارَ جَهَنَّمَ خَالِدِينَ فِيهَا أَبَدًا','تنها وظیفه من ابلاغ از سوی خدا و رساندن رسالات اوست؛ و هر کس نافرمانی خدا و رسولش کند، آتش دوزخ از آن اوست و جاودانه در آن می‌مانند!'];
ayat[5471]=[72,24,'حَتَّىٰ إِذَا رَأَوْا مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِرًا وَأَقَلُّ عَدَدًا','(این کار شکنی کفّار همچنان ادامه می‌یابد) تا آنچه را به آنها وعده داده شده ببینند؛ آنگاه می‌دانند چه کسی یاورش ضعیفتر و جمعیّتش کمتر است!'];
ayat[5472]=[72,25,'قُلْ إِنْ أَدْرِي أَقَرِيبٌ مَّا تُوعَدُونَ أَمْ يَجْعَلُ لَهُ رَبِّي أَمَدًا','بگو: «من نمی‌دانم آنچه به شما وعده داده شده نزدیک است یا پروردگارم زمانی برای آن قرارمی‌دهد؟!'];
ayat[5473]=[72,26,'عَالِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلَىٰ غَيْبِهِ أَحَدًا','دانای غیب اوست و هیچ کس را بر اسرار غیبش آگاه نمی‌سازد،'];
ayat[5474]=[72,27,'إِلَّا مَنِ ارْتَضَىٰ مِن رَّسُولٍ فَإِنَّهُ يَسْلُكُ مِن بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِ رَصَدًا','مگر رسولانی که آنان را برگزیده و مراقبینی از پیش رو و پشت سر برای آنها قرار می‌دهد...'];
ayat[5475]=[72,28,'لِّيَعْلَمَ أَن قَدْ أَبْلَغُوا رِسَالَاتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَىٰ كُلَّ شَيْءٍ عَدَدًا','تا بداند پیامبرانش رسالتهای پروردگارشان را ابلاغ کرده‌اند؛ و او به آنچه نزد آنهاست احاطه دارد و همه چیز را احصاء کرده است!»'];
ayat[5476]=[73,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الْمُزَّمِّلُ','ای جامه به خود پیچیده!'];
ayat[5477]=[73,2,'قُمِ اللَّيْلَ إِلَّا قَلِيلًا','شب را، جز کمی، بپاخیز!'];
ayat[5478]=[73,3,'نِّصْفَهُ أَوِ انقُصْ مِنْهُ قَلِيلًا','نیمی از شب را، یا کمی از آن کم کن،'];
ayat[5479]=[73,4,'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْآنَ تَرْتِيلًا','یا بر نصف آن بیفزا، و قرآن را با دقّت و تأمّل بخوان؛'];
ayat[5480]=[73,5,'إِنَّا سَنُلْقِي عَلَيْكَ قَوْلًا ثَقِيلًا','چرا که ما بزودی سخنی سنگین به تو القا خواهیم کرد!'];
ayat[5481]=[73,6,'إِنَّ نَاشِئَةَ اللَّيْلِ هِيَ أَشَدُّ وَطْئًا وَأَقْوَمُ قِيلًا','مسلّماً نماز و عبادت شبانه پابرجاتر و با استقامت‌تر است!'];
ayat[5482]=[73,7,'إِنَّ لَكَ فِي النَّهَارِ سَبْحًا طَوِيلًا','و تو در روز تلاش مستمر و طولانی خواهی داشت!'];
ayat[5483]=[73,8,'وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا','و نام پروردگارت را یاد کن و تنها به او دل ببند!'];
ayat[5484]=[73,9,'رَّبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَا إِلَٰهَ إِلَّا هُوَ فَاتَّخِذْهُ وَكِيلًا','همان پروردگار شرق و غرب که معبودی جز او نیست، او را نگاهبان و وکیل خود انتخاب کن،'];
ayat[5485]=[73,10,'وَاصْبِرْ عَلَىٰ مَا يَقُولُونَ وَاهْجُرْهُمْ هَجْرًا جَمِيلًا','و در برابر آنچه (دشمنان) می‌گویند شکیبا باش و بطرزی شایسته از آنان دوری گزین!'];
ayat[5486]=[73,11,'وَذَرْنِي وَالْمُكَذِّبِينَ أُولِي النَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا','و مرا با تکذیب‌کنندگان صاحب نعمت واگذار، و آنها را کمی مهلت ده،'];
ayat[5487]=[73,12,'إِنَّ لَدَيْنَا أَنكَالًا وَجَحِيمًا','که نزد ما غل و زنجیرها و (آتش) دوزخ است،'];
ayat[5488]=[73,13,'وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا','و غذایی گلوگیر، و عذابی دردناک،'];
ayat[5489]=[73,14,'يَوْمَ تَرْجُفُ الْأَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيبًا مَّهِيلًا','در آن روز که زمین و کوه‌ها سخت به لرزه درمی‌آید، و کوه‌ها (چنان درهم کوبیده می‌شود که) به شکل توده‌هایی از شن نرم درمی‌آید!'];
ayat[5490]=[73,15,'إِنَّا أَرْسَلْنَا إِلَيْكُمْ رَسُولًا شَاهِدًا عَلَيْكُمْ كَمَا أَرْسَلْنَا إِلَىٰ فِرْعَوْنَ رَسُولًا','ما پیامبری به سوی شما فرستادیم که گواه بر شماست، همان گونه که به سوی فرعون رسولی فرستادیم!'];
ayat[5491]=[73,16,'فَعَصَىٰ فِرْعَوْنُ الرَّسُولَ فَأَخَذْنَاهُ أَخْذًا وَبِيلًا','(ولی) فرعون به مخالفت و نافرمانی آن رسول برخاست، و ما او را سخت مجازات کردیم!'];
ayat[5492]=[73,17,'فَكَيْفَ تَتَّقُونَ إِن كَفَرْتُمْ يَوْمًا يَجْعَلُ الْوِلْدَانَ شِيبًا','شما (نیز) اگر کافر شوید، چگونه خود را (از عذاب الهی) بر کنار می‌دارید؟! در آن روز که کودکان را پیر می‌کند،'];
ayat[5493]=[73,18,'السَّمَاءُ مُنفَطِرٌ بِهِ ۚ كَانَ وَعْدُهُ مَفْعُولًا','و آسمان از هم شکافته می‌شود، و وعده او شدنی و حتمی است.'];
ayat[5494]=[73,19,'إِنَّ هَٰذِهِ تَذْكِرَةٌ ۖ فَمَن شَاءَ اتَّخَذَ إِلَىٰ رَبِّهِ سَبِيلًا','این هشدار و تذکّری است، پس هر کس بخواهد راهی به سوی پروردگارش برمی‌گزیند!'];
ayat[5495]=[73,20,'۞ إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِن ثُلُثَيِ اللَّيْلِ وَنِصْفَهُ وَثُلُثَهُ وَطَائِفَةٌ مِّنَ الَّذِينَ مَعَكَ ۚ وَاللَّهُ يُقَدِّرُ اللَّيْلَ وَالنَّهَارَ ۚ عَلِمَ أَن لَّن تُحْصُوهُ فَتَابَ عَلَيْكُمْ ۖ فَاقْرَءُوا مَا تَيَسَّرَ مِنَ الْقُرْآنِ ۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرْضَىٰ ۙ وَآخَرُونَ يَضْرِبُونَ فِي الْأَرْضِ يَبْتَغُونَ مِن فَضْلِ اللَّهِ ۙ وَآخَرُونَ يُقَاتِلُونَ فِي سَبِيلِ اللَّهِ ۖ فَاقْرَءُوا مَا تَيَسَّرَ مِنْهُ ۚ وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَقْرِضُوا اللَّهَ قَرْضًا حَسَنًا ۚ وَمَا تُقَدِّمُوا لِأَنفُسِكُم مِّنْ خَيْرٍ تَجِدُوهُ عِندَ اللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا ۚ وَاسْتَغْفِرُوا اللَّهَ ۖ إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ','پروردگارت می‌داند که تو و گروهی از آنها که با تو هستند نزدیک دو سوم از شب یا نصف یا ثلث آن را به پا می‌خیزند؛ خداوند شب و روز را اندازه‌گیری می‌کند؛ او می‌داند که شما نمی‌توانید مقدار آن را (به دقّت) اندازه‌گیری کنید (برای عبادت کردن)، پس شما را بخشید؛ اکنون آنچه برای شما میسّر است قرآن بخوانید او می‌داند بزودی گروهی از شما بیمار می‌شوند، و گروهی دیگر برای به دست آوردن فضل الهی (و کسب روزی) به سفر می‌روند، و گروهی دیگر در راه خدا جهاد می‌کنند (و از تلاوت قرآن بازمی‌مانند)، پس به اندازه‌ای که برای شما ممکن است از آن تلاوت کنید و نماز را بر پا دارید و زکات بپردازید و به خدا «قرض الحسنه» دهید [= در راه او انفاق نمایید] و (بدانید) آنچه را از کارهای نیک برای خود از پیش می‌فرستید نزد خدا به بهترین وجه و بزرگترین پاداش خواهید یافت؛ و از خدا آمرزش بطلبید که خداوند آمرزنده و مهربان است!'];
ayat[5496]=[74,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الْمُدَّثِّرُ','ای جامه خواب به خود پیچیده (و در بستر آرمیده)!'];
ayat[5497]=[74,2,'قُمْ فَأَنذِرْ','برخیز و انذار کن (و عالمیان را بیم ده)،'];
ayat[5498]=[74,3,'وَرَبَّكَ فَكَبِّرْ','و پروردگارت را بزرگ بشمار،'];
ayat[5499]=[74,4,'وَثِيَابَكَ فَطَهِّرْ','و لباست را پاک کن،'];
ayat[5500]=[74,5,'وَالرُّجْزَ فَاهْجُرْ','و از پلیدی دوری کن،'];
ayat[5501]=[74,6,'وَلَا تَمْنُن تَسْتَكْثِرُ','و منّت مگذار و فزونی مطلب،'];
ayat[5502]=[74,7,'وَلِرَبِّكَ فَاصْبِرْ','و بخاطر پروردگارت شکیبایی کن!'];
ayat[5503]=[74,8,'فَإِذَا نُقِرَ فِي النَّاقُورِ','هنگامی که در «صور» دمیده شود،'];
ayat[5504]=[74,9,'فَذَٰلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ','آن روز، روز سختی است،'];
ayat[5505]=[74,10,'عَلَى الْكَافِرِينَ غَيْرُ يَسِيرٍ','و برای کافران آسان نیست!'];
ayat[5506]=[74,11,'ذَرْنِي وَمَنْ خَلَقْتُ وَحِيدًا','مرا با کسی که او را خود به تنهایی آفریده‌ام واگذار!'];
ayat[5507]=[74,12,'وَجَعَلْتُ لَهُ مَالًا مَّمْدُودًا','همان کسی که برای او مال گسترده‌ای قرار دادم،'];
ayat[5508]=[74,13,'وَبَنِينَ شُهُودًا','و فرزندانی که همواره نزد او (و در خدمت او) هستند،'];
ayat[5509]=[74,14,'وَمَهَّدتُّ لَهُ تَمْهِيدًا','و وسایل زندگی را از هر نظر برای وی فراهم ساختم!'];
ayat[5510]=[74,15,'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ','باز هم طمع دارد که بر او بیفزایم!'];
ayat[5511]=[74,16,'كَلَّا ۖ إِنَّهُ كَانَ لِآيَاتِنَا عَنِيدًا','هرگز چنین نخواهد شد؛ چرا که او نسبت به آیات ما دشمنی می‌ورزد!'];
ayat[5512]=[74,17,'سَأُرْهِقُهُ صَعُودًا','و بزودی او را مجبور می‌کنم که از قلّه زندگی بالا رود (سپس او را به زیر می‌افکنم)!'];
ayat[5513]=[74,18,'إِنَّهُ فَكَّرَ وَقَدَّرَ','او (برای مبارزه با قرآن) اندیشه کرد و مطلب را آماده ساخت!'];
ayat[5514]=[74,19,'فَقُتِلَ كَيْفَ قَدَّرَ','مرگ بر او باد! چگونه (برای مبارزه با حق) مطلب را آماده کرد!'];
ayat[5515]=[74,20,'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ','باز هم مرگ بر او، چگونه مطلب (و نقشه شیطانی خود را) آماده نمود!'];
ayat[5516]=[74,21,'ثُمَّ نَظَرَ','سپس نگاهی افکند،'];
ayat[5517]=[74,22,'ثُمَّ عَبَسَ وَبَسَرَ','بعد چهره درهم کشید و عجولانه دست به کار شد؛'];
ayat[5518]=[74,23,'ثُمَّ أَدْبَرَ وَاسْتَكْبَرَ','سپس پشت (به حقّ) کرد و تکبّر ورزید،'];
ayat[5519]=[74,24,'فَقَالَ إِنْ هَٰذَا إِلَّا سِحْرٌ يُؤْثَرُ','و سرانجام گفت: «این (قرآن) چیزی جز افسون و سحری همچون سحرهای پیشینیان نیست!'];
ayat[5520]=[74,25,'إِنْ هَٰذَا إِلَّا قَوْلُ الْبَشَرِ','این فقط سخن انسان است (نه گفتار خدا)!»'];
ayat[5521]=[74,26,'سَأُصْلِيهِ سَقَرَ','(امّا) بزودی او را وارد سَقَر [= دوزخ‌] می‌کنم!'];
ayat[5522]=[74,27,'وَمَا أَدْرَاكَ مَا سَقَرُ','و تو نمی‌دانی «سقر» چیست!'];
ayat[5523]=[74,28,'لَا تُبْقِي وَلَا تَذَرُ','(آتشی است که) نه چیزی را باقی می‌گذارد و نه چیزی را رها می‌سازد!'];
ayat[5524]=[74,29,'لَوَّاحَةٌ لِّلْبَشَرِ','پوست تن را بکلّی دگرگون می‌کند!'];
ayat[5525]=[74,30,'عَلَيْهَا تِسْعَةَ عَشَرَ','نوزده نفر (از فرشتگان عذاب) بر آن گمارده شده‌اند!'];
ayat[5526]=[74,31,'وَمَا جَعَلْنَا أَصْحَابَ النَّارِ إِلَّا مَلَائِكَةً ۙ وَمَا جَعَلْنَا عِدَّتَهُمْ إِلَّا فِتْنَةً لِّلَّذِينَ كَفَرُوا لِيَسْتَيْقِنَ الَّذِينَ أُوتُوا الْكِتَابَ وَيَزْدَادَ الَّذِينَ آمَنُوا إِيمَانًا ۙ وَلَا يَرْتَابَ الَّذِينَ أُوتُوا الْكِتَابَ وَالْمُؤْمِنُونَ ۙ وَلِيَقُولَ الَّذِينَ فِي قُلُوبِهِم مَّرَضٌ وَالْكَافِرُونَ مَاذَا أَرَادَ اللَّهُ بِهَٰذَا مَثَلًا ۚ كَذَٰلِكَ يُضِلُّ اللَّهُ مَن يَشَاءُ وَيَهْدِي مَن يَشَاءُ ۚ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَ ۚ وَمَا هِيَ إِلَّا ذِكْرَىٰ لِلْبَشَرِ','مأموران دوزخ را فقط فرشتگان (عذاب) قرار دادیم، و تعداد آنها را جز برای آزمایش کافران معیّن نکردیم تا اهل کتاب [= یهود و نصاری‌] یقین پیدا کنند و بر ایمان مؤمنان بیفزاید، و اهل کتاب و مؤمنان (در حقّانیّت این کتاب آسمانی) تردید به خود راه ندهند، و بیماردلان و کافران بگویند: «خدا از این توصیف چه منظوری دارد؟!» (آری) این گونه خداوند هر کس را بخواهد گمراه می‌سازد و هر کس را بخواهد هدایت می‌کند! و لشکریان پروردگارت را جز او کسی نمی‌داند، و این جز هشدار و تذکّری برای انسانها نیست!'];
ayat[5527]=[74,32,'كَلَّا وَالْقَمَرِ','اینچنین نیست که آنها تصوّر می‌کنند سوگند به ماه،'];
ayat[5528]=[74,33,'وَاللَّيْلِ إِذْ أَدْبَرَ','و به شب، هنگامی که (دامن برچیند و) پشت کند،'];
ayat[5529]=[74,34,'وَالصُّبْحِ إِذَا أَسْفَرَ','و به صبح هنگامی که چهره بگشاید،'];
ayat[5530]=[74,35,'إِنَّهَا لَإِحْدَى الْكُبَرِ','که آن (حوادث هولناک قیامت) از مسائل مهم است!'];
ayat[5531]=[74,36,'نَذِيرًا لِّلْبَشَرِ','هشدار و انذاری است برای همه انسانها،'];
ayat[5532]=[74,37,'لِمَن شَاءَ مِنكُمْ أَن يَتَقَدَّمَ أَوْ يَتَأَخَّرَ','برای کسانی از شما که می‌خواهند پیش افتند یا عقب بمانند [= بسوی هدایت و نیکی پیش روند یا نروند]!'];
ayat[5533]=[74,38,'كُلُّ نَفْسٍ بِمَا كَسَبَتْ رَهِينَةٌ','(آری) هر کس در گرو اعمال خویش است،'];
ayat[5534]=[74,39,'إِلَّا أَصْحَابَ الْيَمِينِ','مگر «اصحاب یمین» (که نامه اعمالشان را به نشانه ایمان و تقوایشان به دست راستشان می‌دهند)!'];
ayat[5535]=[74,40,'فِي جَنَّاتٍ يَتَسَاءَلُونَ','آنها در باغهای بهشتند، و سؤال می‌کنند...'];
ayat[5536]=[74,41,'عَنِ الْمُجْرِمِينَ','از مجرمان:'];
ayat[5537]=[74,42,'مَا سَلَكَكُمْ فِي سَقَرَ','چه چیز شما را به دوزخ وارد ساخت؟!»'];
ayat[5538]=[74,43,'قَالُوا لَمْ نَكُ مِنَ الْمُصَلِّينَ','می‌گویند: «ما از نمازگزاران نبودیم،'];
ayat[5539]=[74,44,'وَلَمْ نَكُ نُطْعِمُ الْمِسْكِينَ','و اطعام مستمند نمی‌کردیم،'];
ayat[5540]=[74,45,'وَكُنَّا نَخُوضُ مَعَ الْخَائِضِينَ','و پیوسته با اهل باطل همنشین و همصدا بودیم،'];
ayat[5541]=[74,46,'وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّينِ','و همواره روز جزا را انکار می‌کردیم،'];
ayat[5542]=[74,47,'حَتَّىٰ أَتَانَا الْيَقِينُ','تا زمانی که مرگ ما فرا رسید!»'];
ayat[5543]=[74,48,'فَمَا تَنفَعُهُمْ شَفَاعَةُ الشَّافِعِينَ','از این رو شفاعت شفاعت‌کنندگان به حال آنها سودی نمی‌بخشد.'];
ayat[5544]=[74,49,'فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِينَ','چرا آنها از تذکّر روی گردانند؟!'];
ayat[5545]=[74,50,'كَأَنَّهُمْ حُمُرٌ مُّسْتَنفِرَةٌ','گویی گورخرانی رمیده‌اند،'];
ayat[5546]=[74,51,'فَرَّتْ مِن قَسْوَرَةٍ','که از (مقابل) شیری فرار کرده‌اند!'];
ayat[5547]=[74,52,'بَلْ يُرِيدُ كُلُّ امْرِئٍ مِّنْهُمْ أَن يُؤْتَىٰ صُحُفًا مُّنَشَّرَةً','بلکه هر کدام از آنها انتظار دارد نامه جداگانه‌ای (از سوی خدا) برای او فرستاده شود!'];
ayat[5548]=[74,53,'كَلَّا ۖ بَل لَّا يَخَافُونَ الْآخِرَةَ','چنین نیست که آنان می‌گویند، بلکه آنها از آخرت نمی‌ترسند!'];
ayat[5549]=[74,54,'كَلَّا إِنَّهُ تَذْكِرَةٌ','چنین نیست که آنها می‌گویند، آن (قرآن) یک تذکّر و یادآوری است!'];
ayat[5550]=[74,55,'فَمَن شَاءَ ذَكَرَهُ','هر کس بخواهد از آن پند می‌گیرد؛'];
ayat[5551]=[74,56,'وَمَا يَذْكُرُونَ إِلَّا أَن يَشَاءَ اللَّهُ ۚ هُوَ أَهْلُ التَّقْوَىٰ وَأَهْلُ الْمَغْفِرَةِ','و هیچ کس پند نمی‌گیرد مگر اینکه خدا بخواهد؛ او اهل تقوا و اهل آمرزش است!'];
ayat[5552]=[75,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لَا أُقْسِمُ بِيَوْمِ الْقِيَامَةِ','سوگند به روز قیامت،'];
ayat[5553]=[75,2,'وَلَا أُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ','و سوگند به (نفس لوّامه و) وجدان بیدار و ملامتگر (که رستاخیز حقّ است)!'];
ayat[5554]=[75,3,'أَيَحْسَبُ الْإِنسَانُ أَلَّن نَّجْمَعَ عِظَامَهُ','آیا انسان می‌پندارد که هرگز استخوانهای او را جمع نخواهیم کرد؟!'];
ayat[5555]=[75,4,'بَلَىٰ قَادِرِينَ عَلَىٰ أَن نُّسَوِّيَ بَنَانَهُ','آری قادریم که (حتّی خطوط سر) انگشتان او را موزون و مرتّب کنیم!'];
ayat[5556]=[75,5,'بَلْ يُرِيدُ الْإِنسَانُ لِيَفْجُرَ أَمَامَهُ','(انسان شک در معاد ندارد) بلکه او می‌خواهد (آزاد باشد و بدون ترس از دادگاه قیامت) در تمام عمر گناه کند!'];
ayat[5557]=[75,6,'يَسْأَلُ أَيَّانَ يَوْمُ الْقِيَامَةِ','(از این رو) می‌پرسد: «قیامت کی خواهد بود»!'];
ayat[5558]=[75,7,'فَإِذَا بَرِقَ الْبَصَرُ','(بگو:) در آن هنگام که چشمها از شدّت وحشت به گردش درآید،'];
ayat[5559]=[75,8,'وَخَسَفَ الْقَمَرُ','و ماه بی‌نور گردد،'];
ayat[5560]=[75,9,'وَجُمِعَ الشَّمْسُ وَالْقَمَرُ','و خورشید و ماه یک جا جمع شوند،'];
ayat[5561]=[75,10,'يَقُولُ الْإِنسَانُ يَوْمَئِذٍ أَيْنَ الْمَفَرُّ','آن روز انسان می‌گوید: «راه فرار کجاست؟!»'];
ayat[5562]=[75,11,'كَلَّا لَا وَزَرَ','هرگز چنین نیست، راه فرار و پناهگاهی وجود ندارد!'];
ayat[5563]=[75,12,'إِلَىٰ رَبِّكَ يَوْمَئِذٍ الْمُسْتَقَرُّ','آن روز قرارگاه نهایی تنها بسوی پروردگار تو است؛'];
ayat[5564]=[75,13,'يُنَبَّأُ الْإِنسَانُ يَوْمَئِذٍ بِمَا قَدَّمَ وَأَخَّرَ','و در آن روز انسان را از تمام کارهایی که از پیش یا پس فرستاده آگاه می‌کنند!'];
ayat[5565]=[75,14,'بَلِ الْإِنسَانُ عَلَىٰ نَفْسِهِ بَصِيرَةٌ','بلکه انسان خودش از وضع خود آگاه است،'];
ayat[5566]=[75,15,'وَلَوْ أَلْقَىٰ مَعَاذِيرَهُ','هر چند (در ظاهر) برای خود عذرهایی بتراشد!'];
ayat[5567]=[75,16,'لَا تُحَرِّكْ بِهِ لِسَانَكَ لِتَعْجَلَ بِهِ','زبانت را بخاطر عجله برای خواندن آن [= قرآن‌] حرکت مده،'];
ayat[5568]=[75,17,'إِنَّ عَلَيْنَا جَمْعَهُ وَقُرْآنَهُ','چرا که جمع‌کردن و خواندن آن بر عهده ماست!'];
ayat[5569]=[75,18,'فَإِذَا قَرَأْنَاهُ فَاتَّبِعْ قُرْآنَهُ','پس هر گاه آن را خواندیم، از خواندن آن پیروی کن!'];
ayat[5570]=[75,19,'ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُ','سپس بیان و (توضیح) آن (نیز) بر عهده ماست!'];
ayat[5571]=[75,20,'كَلَّا بَلْ تُحِبُّونَ الْعَاجِلَةَ','چنین نیست که شما می‌پندارید (و دلایل معاد را کافی نمی‌دانید)؛ بلکه شما دنیای زودگذر را دوست دارید (و هوسرانی بی‌قید و شرط را)!'];
ayat[5572]=[75,21,'وَتَذَرُونَ الْآخِرَةَ','و آخرت را رها می‌کنید!'];
ayat[5573]=[75,22,'وُجُوهٌ يَوْمَئِذٍ نَّاضِرَةٌ','(آری) در آن روز صورتهایی شاداب و مسرور است،'];
ayat[5574]=[75,23,'إِلَىٰ رَبِّهَا نَاظِرَةٌ','و به پروردگارش می‌نگرد!'];
ayat[5575]=[75,24,'وَوُجُوهٌ يَوْمَئِذٍ بَاسِرَةٌ','و در آن روز صورتهایی عبوس و در هم کشیده است،'];
ayat[5576]=[75,25,'تَظُنُّ أَن يُفْعَلَ بِهَا فَاقِرَةٌ','زیرا می‌داند عذابی در پیش دارد که پشت را در هم می‌شکند!'];
ayat[5577]=[75,26,'كَلَّا إِذَا بَلَغَتِ التَّرَاقِيَ','چنین نیست (که انسان می‌پندارد! او ایمان نمی‌آورد) تا موقعی که جان به گلوگاهش رسد،'];
ayat[5578]=[75,27,'وَقِيلَ مَنْ ۜ رَاقٍ','و گفته شود: «آیا کسی هست که (این بیمار را از مرگ) نجات دهد؟!»'];
ayat[5579]=[75,28,'وَظَنَّ أَنَّهُ الْفِرَاقُ','و به جدائی از دنیا یقین پیدا کند،'];
ayat[5580]=[75,29,'وَالْتَفَّتِ السَّاقُ بِالسَّاقِ','و ساق پاها (از سختی جان‌دادن) به هم بپیچد!'];
ayat[5581]=[75,30,'إِلَىٰ رَبِّكَ يَوْمَئِذٍ الْمَسَاقُ','(آری) در آن روز مسیر همه بسوی (دادگاه) پروردگارت خواهد بود!'];
ayat[5582]=[75,31,'فَلَا صَدَّقَ وَلَا صَلَّىٰ','(در آن روز گفته می‌شود:) او هرگز ایمان نیاورد و نماز نخواند،'];
ayat[5583]=[75,32,'وَلَٰكِن كَذَّبَ وَتَوَلَّىٰ','بلکه تکذیب کرد و روی‌گردان شد،'];
ayat[5584]=[75,33,'ثُمَّ ذَهَبَ إِلَىٰ أَهْلِهِ يَتَمَطَّىٰ','سپس بسوی خانواده خود بازگشت در حالی که متکبّرانه قدم برمی‌داشت!'];
ayat[5585]=[75,34,'أَوْلَىٰ لَكَ فَأَوْلَىٰ','(با این اعمال) عذاب الهی برای تو شایسته‌تر است، شایسته‌تر!'];
ayat[5586]=[75,35,'ثُمَّ أَوْلَىٰ لَكَ فَأَوْلَىٰ','سپس عذاب الهی برای تو شایسته‌تر است، شایسته‌تر!'];
ayat[5587]=[75,36,'أَيَحْسَبُ الْإِنسَانُ أَن يُتْرَكَ سُدًى','آیا انسان گمان می‌کند بی‌هدف رها می‌شود؟!'];
ayat[5588]=[75,37,'أَلَمْ يَكُ نُطْفَةً مِّن مَّنِيٍّ يُمْنَىٰ','آیا او نطفه‌ای از منی که در رحم ریخته می‌شود نبود؟!'];
ayat[5589]=[75,38,'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوَّىٰ','سپس بصورت خون بسته در آمد، و خداوند او را آفرید و موزون ساخت،'];
ayat[5590]=[75,39,'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْأُنثَىٰ','و از او دو زوج مرد و زن آفرید!'];
ayat[5591]=[75,40,'أَلَيْسَ ذَٰلِكَ بِقَادِرٍ عَلَىٰ أَن يُحْيِيَ الْمَوْتَىٰ','آیا چنین کسی قادر نیست که مردگان را زنده کند؟!'];
ayat[5592]=[76,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ هَلْ أَتَىٰ عَلَى الْإِنسَانِ حِينٌ مِّنَ الدَّهْرِ لَمْ يَكُن شَيْئًا مَّذْكُورًا','آیا زمانی طولانی بر انسان گذشت که چیز قابل ذکری نبود؟!'];
ayat[5593]=[76,2,'إِنَّا خَلَقْنَا الْإِنسَانَ مِن نُّطْفَةٍ أَمْشَاجٍ نَّبْتَلِيهِ فَجَعَلْنَاهُ سَمِيعًا بَصِيرًا','ما انسان را از نطفه مختلطی آفریدیم، و او را می‌آزماییم؛ (بدین جهت) او را شنوا و بینا قرار دادیم!'];
ayat[5594]=[76,3,'إِنَّا هَدَيْنَاهُ السَّبِيلَ إِمَّا شَاكِرًا وَإِمَّا كَفُورًا','ما راه را به او نشان دادیم، خواه شاکر باشد (و پذیرا گردد) یا ناسپاس!'];
ayat[5595]=[76,4,'إِنَّا أَعْتَدْنَا لِلْكَافِرِينَ سَلَاسِلَ وَأَغْلَالًا وَسَعِيرًا','ما برای کافران، زنجیرها و غلها و شعله‌های سوزان آتش آماده کرده‌ایم!'];
ayat[5596]=[76,5,'إِنَّ الْأَبْرَارَ يَشْرَبُونَ مِن كَأْسٍ كَانَ مِزَاجُهَا كَافُورًا','به یقین ابرار (و نیکان) از جامی می‌نوشند که با عطر خوشی آمیخته است،'];
ayat[5597]=[76,6,'عَيْنًا يَشْرَبُ بِهَا عِبَادُ اللَّهِ يُفَجِّرُونَهَا تَفْجِيرًا','از چشمه‌ای که بندگان خاص خدا از آن می‌نوشند، و از هر جا بخواهند آن را جاری می‌سازند!'];
ayat[5598]=[76,7,'يُوفُونَ بِالنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُ مُسْتَطِيرًا','آنها به نذر خود وفا می‌کنند، و از روزی که شرّ و عذابش گسترده است می‌ترسند،'];
ayat[5599]=[76,8,'وَيُطْعِمُونَ الطَّعَامَ عَلَىٰ حُبِّهِ مِسْكِينًا وَيَتِيمًا وَأَسِيرًا','و غذای (خود) را با اینکه به آن علاقه (و نیاز) دارند، به «مسکین» و «یتیم» و «اسیر» می‌دهند!'];
ayat[5600]=[76,9,'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللَّهِ لَا نُرِيدُ مِنكُمْ جَزَاءً وَلَا شُكُورًا','(و می‌گویند:) ما شما را بخاطر خدا اطعام می‌کنیم، و هیچ پاداش و سپاسی از شما نمی‌خواهیم!'];
ayat[5601]=[76,10,'إِنَّا نَخَافُ مِن رَّبِّنَا يَوْمًا عَبُوسًا قَمْطَرِيرًا','ما از پروردگارمان خائفیم در آن روزی که عبوس و سخت است!'];
ayat[5602]=[76,11,'فَوَقَاهُمُ اللَّهُ شَرَّ ذَٰلِكَ الْيَوْمِ وَلَقَّاهُمْ نَضْرَةً وَسُرُورًا','(بخاطر این عقیده و عمل) خداوند آنان را از شرّ آن روز نگه می‌دارد و آنها را می‌پذیرد در حالی که غرق شادی و سرورند!'];
ayat[5603]=[76,12,'وَجَزَاهُم بِمَا صَبَرُوا جَنَّةً وَحَرِيرًا','و در برابر صبرشان، بهشت و لباسهای حریر بهشتی را به آنها پاداش می‌دهد!'];
ayat[5604]=[76,13,'مُّتَّكِئِينَ فِيهَا عَلَى الْأَرَائِكِ ۖ لَا يَرَوْنَ فِيهَا شَمْسًا وَلَا زَمْهَرِيرًا','این در حالی است که در بهشت بر تختهای زیبا تکیه کرده‌اند، نه آفتاب را در آنجا می‌بینند و نه سرما را!'];
ayat[5605]=[76,14,'وَدَانِيَةً عَلَيْهِمْ ظِلَالُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًا','و در حالی است که سایه‌های درختان بهشتی بر آنها فرو افتاده و چیدن میوه‌هایش بسیار آسان است!'];
ayat[5606]=[76,15,'وَيُطَافُ عَلَيْهِم بِآنِيَةٍ مِّن فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَا','و در گرداگرد آنها ظرفهایی سیمین و قدحهایی بلورین می‌گردانند (پر از بهترین غذاها و نوشیدنی‌ها)،'];
ayat[5607]=[76,16,'قَوَارِيرَ مِن فِضَّةٍ قَدَّرُوهَا تَقْدِيرًا','ظرفهای بلورینی از نقره، که آنها را به اندازه مناسب آماده کرده‌اند!'];
ayat[5608]=[76,17,'وَيُسْقَوْنَ فِيهَا كَأْسًا كَانَ مِزَاجُهَا زَنجَبِيلًا','و در آنجا از جامهایی سیراب می‌شوند که لبریز از شراب طهوری آمیخته با زنجبیل است،'];
ayat[5609]=[76,18,'عَيْنًا فِيهَا تُسَمَّىٰ سَلْسَبِيلًا','از چشمه‌ای در بهشت که نامش سلسبیل است!'];
ayat[5610]=[76,19,'۞ وَيَطُوفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَّنثُورًا','و بر گردشان (برای پذیرایی) نوجوانانی جاودانی می‌گردند که هرگاه آنها را ببینی گمان می‌کنی مروارید پراکنده‌اند!'];
ayat[5611]=[76,20,'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيمًا وَمُلْكًا كَبِيرًا','و هنگامی که آنجا را ببینی نعمتها و ملک عظیمی را می‌بینی!'];
ayat[5612]=[76,21,'عَالِيَهُمْ ثِيَابُ سُندُسٍ خُضْرٌ وَإِسْتَبْرَقٌ ۖ وَحُلُّوا أَسَاوِرَ مِن فِضَّةٍ وَسَقَاهُمْ رَبُّهُمْ شَرَابًا طَهُورًا','بر اندام آنها [= بهشتیان‌] لباسهایی است از حریر نازک سبزرنگ، و از دیبای ضخیم، و با دستبندهایی از نقره آراسته‌اند، و پروردگارشان شراب طهور به آنان می‌نوشاند!'];
ayat[5613]=[76,22,'إِنَّ هَٰذَا كَانَ لَكُمْ جَزَاءً وَكَانَ سَعْيُكُم مَّشْكُورًا','این پاداش شماست، و سعی و تلاش شما مورد قدردانی است!'];
ayat[5614]=[76,23,'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْآنَ تَنزِيلًا','مسلّماً ما قرآن را بر تو نازل کردیم!'];
ayat[5615]=[76,24,'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ آثِمًا أَوْ كَفُورًا','پس در (تبلیغ و اجرای) حکم پروردگارت شکیبا (و با استقامت) باش، و از هیچ گنهکار یا کافری از آنان اطاعت مکن!'];
ayat[5616]=[76,25,'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَأَصِيلًا','و نام پروردگارت را هر صبح و شام به یاد آور!'];
ayat[5617]=[76,26,'وَمِنَ اللَّيْلِ فَاسْجُدْ لَهُ وَسَبِّحْهُ لَيْلًا طَوِيلًا','و در شبانگاه برای او سجده کن، و مقداری طولانی از شب، او را تسبیح گوی!'];
ayat[5618]=[76,27,'إِنَّ هَٰؤُلَاءِ يُحِبُّونَ الْعَاجِلَةَ وَيَذَرُونَ وَرَاءَهُمْ يَوْمًا ثَقِيلًا','آنها زندگی زودگذر دنیا را دوست دارند، در حالی که روز سختی را پشت سر خود رها می‌کنند!'];
ayat[5619]=[76,28,'نَّحْنُ خَلَقْنَاهُمْ وَشَدَدْنَا أَسْرَهُمْ ۖ وَإِذَا شِئْنَا بَدَّلْنَا أَمْثَالَهُمْ تَبْدِيلًا','ما آنها را آفریدیم و پیوندهای وجودشان را محکم کردیم، و هر زمان بخواهیم جای آنان را به گروه دیگری می‌دهیم!'];
ayat[5620]=[76,29,'إِنَّ هَٰذِهِ تَذْكِرَةٌ ۖ فَمَن شَاءَ اتَّخَذَ إِلَىٰ رَبِّهِ سَبِيلًا','این یک تذکر و یادآوری است، و هر کس بخواهد (با استفاده از آن) راهی به سوی پروردگارش برمی‌گزیند!'];
ayat[5621]=[76,30,'وَمَا تَشَاءُونَ إِلَّا أَن يَشَاءَ اللَّهُ ۚ إِنَّ اللَّهَ كَانَ عَلِيمًا حَكِيمًا','و شما هیچ چیز را نمی‌خواهید مگر اینکه خدا بخواهد، خداوند دانا و حکیم بوده و هست!'];
ayat[5622]=[76,31,'يُدْخِلُ مَن يَشَاءُ فِي رَحْمَتِهِ ۚ وَالظَّالِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًا','و هر کس را بخواهد (و شایسته بداند) در رحمت (وسیع) خود وارد می‌کند، و برای ظالمان عذاب دردناکی آماده ساخته است!'];
ayat[5623]=[77,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْمُرْسَلَاتِ عُرْفًا','سوگند به فرشتگانی که پی در پی فرستاده می‌شوند،'];
ayat[5624]=[77,2,'فَالْعَاصِفَاتِ عَصْفًا','و آنها که همچون تند باد حرکت می‌کنند،'];
ayat[5625]=[77,3,'وَالنَّاشِرَاتِ نَشْرًا','و سوگند به آنها که (ابرها را) می‌گسترانند،'];
ayat[5626]=[77,4,'فَالْفَارِقَاتِ فَرْقًا','و آنها که جدا می‌کنند،'];
ayat[5627]=[77,5,'فَالْمُلْقِيَاتِ ذِكْرًا','و سوگند به آنها که آیات بیدارگر (الهی) را (به انبیا) القا می‌نمایند،'];
ayat[5628]=[77,6,'عُذْرًا أَوْ نُذْرًا','برای اتمام حجّت یا برای انذار،'];
ayat[5629]=[77,7,'إِنَّمَا تُوعَدُونَ لَوَاقِعٌ','که آنچه به شما (درباره قیامت) وعده داده می‌شود، یقیناً واقع‌شدنی است!'];
ayat[5630]=[77,8,'فَإِذَا النُّجُومُ طُمِسَتْ','در آن هنگام که ستارگان محو و تاریک شوند،'];
ayat[5631]=[77,9,'وَإِذَا السَّمَاءُ فُرِجَتْ','و (کرات) آسمان از هم بشکافند،'];
ayat[5632]=[77,10,'وَإِذَا الْجِبَالُ نُسِفَتْ','و در آن زمان که کوه‌ها از جا کنده شوند،'];
ayat[5633]=[77,11,'وَإِذَا الرُّسُلُ أُقِّتَتْ','و در آن هنگام که برای پیامبران (بمنظور ادای شهادت) تعیین وقت شود!'];
ayat[5634]=[77,12,'لِأَيِّ يَوْمٍ أُجِّلَتْ','(این امر) برای چه روزی به تأخیر افتاده؟'];
ayat[5635]=[77,13,'لِيَوْمِ الْفَصْلِ','برای روز جدایی (حق از باطل)!'];
ayat[5636]=[77,14,'وَمَا أَدْرَاكَ مَا يَوْمُ الْفَصْلِ','تو چه می‌دانی روز جدایی چیست!'];
ayat[5637]=[77,15,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5638]=[77,16,'أَلَمْ نُهْلِكِ الْأَوَّلِينَ','آیا ما اقوام (مجرم) نخستین را هلاک نکردیم؟!'];
ayat[5639]=[77,17,'ثُمَّ نُتْبِعُهُمُ الْآخِرِينَ','سپس دیگر (مجرمان) را به دنبال آنها می‌فرستیم!'];
ayat[5640]=[77,18,'كَذَٰلِكَ نَفْعَلُ بِالْمُجْرِمِينَ','(آری) این گونه با مجرمان رفتار می‌کنیم!'];
ayat[5641]=[77,19,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5642]=[77,20,'أَلَمْ نَخْلُقكُّم مِّن مَّاءٍ مَّهِينٍ','آیا شما را از آبی پست و ناچیز نیافریدیم،'];
ayat[5643]=[77,21,'فَجَعَلْنَاهُ فِي قَرَارٍ مَّكِينٍ','سپس آن را در قرارگاهی محفوظ و آماده قرار دادیم،'];
ayat[5644]=[77,22,'إِلَىٰ قَدَرٍ مَّعْلُومٍ','تا مدّتی معیّن؟!'];
ayat[5645]=[77,23,'فَقَدَرْنَا فَنِعْمَ الْقَادِرُونَ','ما قدرت بر این کار داشتیم، پس ما قدرتمند خوبی هستیم (و امر معاد برای ما آسان است)!'];
ayat[5646]=[77,24,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5647]=[77,25,'أَلَمْ نَجْعَلِ الْأَرْضَ كِفَاتًا','آیا زمین را مرکز اجتماع انسانها قرار ندادیم،'];
ayat[5648]=[77,26,'أَحْيَاءً وَأَمْوَاتًا','هم در حال حیاتشان و هم مرگشان؟!'];
ayat[5649]=[77,27,'وَجَعَلْنَا فِيهَا رَوَاسِيَ شَامِخَاتٍ وَأَسْقَيْنَاكُم مَّاءً فُرَاتًا','و در آن کوه‌های استوار و بلندی قرار دادیم، و آبی گوارا به شما نوشاندیم!'];
ayat[5650]=[77,28,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5651]=[77,29,'انطَلِقُوا إِلَىٰ مَا كُنتُم بِهِ تُكَذِّبُونَ','(در آن روز به آنها گفته می‌شود:) بی‌درنگ، به سوی همان چیزی که پیوسته آن را تکذیب می‌کردید بروید!'];
ayat[5652]=[77,30,'انطَلِقُوا إِلَىٰ ظِلٍّ ذِي ثَلَاثِ شُعَبٍ','بروید به سوی سایه سه شاخه (دودهای خفقان‌بار و آتش‌زا)!'];
ayat[5653]=[77,31,'لَّا ظَلِيلٍ وَلَا يُغْنِي مِنَ اللَّهَبِ','سایه‌ای که نه آرامبخش است و نه از شعله‌های آتش جلوگیری می‌کند!'];
ayat[5654]=[77,32,'إِنَّهَا تَرْمِي بِشَرَرٍ كَالْقَصْرِ','شراره‌هایی از خود پرتاب می‌کند مانند یک کاخ!'];
ayat[5655]=[77,33,'كَأَنَّهُ جِمَالَتٌ صُفْرٌ','گویی (در سرعت و کثرت) همچون شتران زردرنگی هستند (که به هر سو پراکنده می‌شوند)!'];
ayat[5656]=[77,34,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5657]=[77,35,'هَٰذَا يَوْمُ لَا يَنطِقُونَ','امروز روزی است که سخن نمی‌گویند (و قادر بر دفاع از خویشتن نیستند)،'];
ayat[5658]=[77,36,'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ','و به آنها اجازه داده نمی‌شود که عذرخواهی کنند!'];
ayat[5659]=[77,37,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5660]=[77,38,'هَٰذَا يَوْمُ الْفَصْلِ ۖ جَمَعْنَاكُمْ وَالْأَوَّلِينَ','(و به آنها گفته می‌شود:) امروز همان روز جدایی (حق از باطل) است که شما و پیشینیان را در آن جمع کرده‌ایم!'];
ayat[5661]=[77,39,'فَإِن كَانَ لَكُمْ كَيْدٌ فَكِيدُونِ','اگر چاره‌ای در برابر من (برای فرار از چنگال مجازات) دارید انجام دهید!'];
ayat[5662]=[77,40,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5663]=[77,41,'إِنَّ الْمُتَّقِينَ فِي ظِلَالٍ وَعُيُونٍ','(در آن روز) پرهیزگاران در سایه‌های (درختان بهشتی) و در میان چشمه‌ها قرار دارند،'];
ayat[5664]=[77,42,'وَفَوَاكِهَ مِمَّا يَشْتَهُونَ','و میوه‌هایی از آنچه مایل باشند!'];
ayat[5665]=[77,43,'كُلُوا وَاشْرَبُوا هَنِيئًا بِمَا كُنتُمْ تَعْمَلُونَ','بخورید و بنوشید گوارا، اینها در برابر اعمالی است که انجام می‌دادید!'];
ayat[5666]=[77,44,'إِنَّا كَذَٰلِكَ نَجْزِي الْمُحْسِنِينَ','ما این گونه نیکوکاران را پاداش می‌دهیم!'];
ayat[5667]=[77,45,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5668]=[77,46,'كُلُوا وَتَمَتَّعُوا قَلِيلًا إِنَّكُم مُّجْرِمُونَ','(و به مجرمان بگو:) بخورید و بهره گیرید در این مدت کم (از زندگی دنیا، ولی بدانید عذاب الهی در انتظار شماست) چرا که شما مجرمید!'];
ayat[5669]=[77,47,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5670]=[77,48,'وَإِذَا قِيلَ لَهُمُ ارْكَعُوا لَا يَرْكَعُونَ','و هنگامی که به آنها گفته شود رکوع کنید رکوع نمی‌کنند!'];
ayat[5671]=[77,49,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5672]=[77,50,'فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ','(و اگر آنها به این قرآن ایمان نمی‌آورند) پس به کدام سخن بعد از آن ایمان می‌آورند؟!'];
ayat[5673]=[78,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ عَمَّ يَتَسَاءَلُونَ','آنها از چه چیز از یکدیگر سؤال می‌کنند؟!'];
ayat[5674]=[78,2,'عَنِ النَّبَإِ الْعَظِيمِ','از خبر بزرگ و پراهمیّت (رستاخیز)!'];
ayat[5675]=[78,3,'الَّذِي هُمْ فِيهِ مُخْتَلِفُونَ','همان خبری که پیوسته در آن اختلاف دارند!'];
ayat[5676]=[78,4,'كَلَّا سَيَعْلَمُونَ','چنین نیست که آنها فکر می‌کنند، و بزودی می‌فهمند!'];
ayat[5677]=[78,5,'ثُمَّ كَلَّا سَيَعْلَمُونَ','باز هم چنین نیست که آنها می‌پندارند، و بزودی می‌فهمند (که قیامت حق است)!'];
ayat[5678]=[78,6,'أَلَمْ نَجْعَلِ الْأَرْضَ مِهَادًا','آیا زمین را محل آرامش (شما) قرار ندادیم؟!'];
ayat[5679]=[78,7,'وَالْجِبَالَ أَوْتَادًا','و کوه‌ها را میخهای زمین؟!'];
ayat[5680]=[78,8,'وَخَلَقْنَاكُمْ أَزْوَاجًا','و شما را بصورت زوجها آفریدیم!'];
ayat[5681]=[78,9,'وَجَعَلْنَا نَوْمَكُمْ سُبَاتًا','و خواب شما را مایه آرامشتان قرار دادیم،'];
ayat[5682]=[78,10,'وَجَعَلْنَا اللَّيْلَ لِبَاسًا','و شب را پوششی (برای شما)،'];
ayat[5683]=[78,11,'وَجَعَلْنَا النَّهَارَ مَعَاشًا','و روز را وسیله‌ای برای زندگی و معاش!'];
ayat[5684]=[78,12,'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًا','و بر فراز شما هفت (آسمان) محکم بنا کردیم!'];
ayat[5685]=[78,13,'وَجَعَلْنَا سِرَاجًا وَهَّاجًا','و چراغی روشن و حرارت‌بخش آفریدیم!'];
ayat[5686]=[78,14,'وَأَنزَلْنَا مِنَ الْمُعْصِرَاتِ مَاءً ثَجَّاجًا','و از ابرهای باران‌زا آبی فراوان نازل کردیم،'];
ayat[5687]=[78,15,'لِّنُخْرِجَ بِهِ حَبًّا وَنَبَاتًا','تا بوسیله آن دانه و گیاه بسیار برویانیم،'];
ayat[5688]=[78,16,'وَجَنَّاتٍ أَلْفَافًا','و باغهایی پردرخت!'];
ayat[5689]=[78,17,'إِنَّ يَوْمَ الْفَصْلِ كَانَ مِيقَاتًا','(آری) روز جدایی، میعاد همگان است!'];
ayat[5690]=[78,18,'يَوْمَ يُنفَخُ فِي الصُّورِ فَتَأْتُونَ أَفْوَاجًا','روزی که در «صور» دمیده می‌شود و شما فوج فوج (به محشر) می‌آیید!'];
ayat[5691]=[78,19,'وَفُتِحَتِ السَّمَاءُ فَكَانَتْ أَبْوَابًا','و آسمان گشوده می‌شود و بصورت درهای متعددی درمی‌آید!'];
ayat[5692]=[78,20,'وَسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًا','و کوه‌ها به حرکت درمی‌آید و بصورت سرابی می‌شود!'];
ayat[5693]=[78,21,'إِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًا','مسلّماً (در آن روز) جهنّم کمینگاهی است بزرگ،'];
ayat[5694]=[78,22,'لِّلطَّاغِينَ مَآبًا','و محل بازگشتی برای طغیانگران!'];
ayat[5695]=[78,23,'لَّابِثِينَ فِيهَا أَحْقَابًا','مدّتهای طولانی در آن می‌مانند!'];
ayat[5696]=[78,24,'لَّا يَذُوقُونَ فِيهَا بَرْدًا وَلَا شَرَابًا','در آنجا نه چیز خنکی می‌چشند و نه نوشیدنی گوارایی،'];
ayat[5697]=[78,25,'إِلَّا حَمِيمًا وَغَسَّاقًا','جز آبی سوزان و مایعی از چرک و خون!'];
ayat[5698]=[78,26,'جَزَاءً وِفَاقًا','این مجازاتی است موافق و مناسب (اعمالشان)!'];
ayat[5699]=[78,27,'إِنَّهُمْ كَانُوا لَا يَرْجُونَ حِسَابًا','چرا که آنها هیچ امیدی به حساب نداشتند،'];
ayat[5700]=[78,28,'وَكَذَّبُوا بِآيَاتِنَا كِذَّابًا','و آیات ما را بکلی تکذیب کردند!'];
ayat[5701]=[78,29,'وَكُلَّ شَيْءٍ أَحْصَيْنَاهُ كِتَابًا','و ما همه چیز را شمارش و ثبت کرده‌ایم!'];
ayat[5702]=[78,30,'فَذُوقُوا فَلَن نَّزِيدَكُمْ إِلَّا عَذَابًا','پس بچشید که چیزی جز عذاب بر شما نمی‌افزاییم!'];
ayat[5703]=[78,31,'إِنَّ لِلْمُتَّقِينَ مَفَازًا','مسلّماً برای پرهیزگاران نجات و پیروزی بزرگی است:'];
ayat[5704]=[78,32,'حَدَائِقَ وَأَعْنَابًا','باغهایی سرسبز، و انواع انگورها،'];
ayat[5705]=[78,33,'وَكَوَاعِبَ أَتْرَابًا','و حوریانی بسیار جوان و هم‌سن و سال،'];
ayat[5706]=[78,34,'وَكَأْسًا دِهَاقًا','و جامهایی لبریز و پیاپی (از شراب طهور)!'];
ayat[5707]=[78,35,'لَّا يَسْمَعُونَ فِيهَا لَغْوًا وَلَا كِذَّابًا','در آنجا نه سخن لغو و بیهوده‌ای می‌شنوند و نه دروغی!'];
ayat[5708]=[78,36,'جَزَاءً مِّن رَّبِّكَ عَطَاءً حِسَابًا','این کیفری است از سوی پروردگارت و عطیه‌ای است کافی!'];
ayat[5709]=[78,37,'رَّبِّ السَّمَاوَاتِ وَالْأَرْضِ وَمَا بَيْنَهُمَا الرَّحْمَٰنِ ۖ لَا يَمْلِكُونَ مِنْهُ خِطَابًا','همان پروردگار آسمانها و زمین و آنچه در میان آن دو است، پروردگار رحمان! و (در آن روز) هیچ کس حق ندارد بی اجازه او سخنی بگوید (یا شفاعتی کند)!'];
ayat[5710]=[78,38,'يَوْمَ يَقُومُ الرُّوحُ وَالْمَلَائِكَةُ صَفًّا ۖ لَّا يَتَكَلَّمُونَ إِلَّا مَنْ أَذِنَ لَهُ الرَّحْمَٰنُ وَقَالَ صَوَابًا','روزی که «روح» و «ملائکه» در یک صف می‌ایستند و هیچ یک، جز به اذن خداوند رحمان، سخن نمی‌گویند، و (آنگاه که می‌گویند) درست می‌گویند!'];
ayat[5711]=[78,39,'ذَٰلِكَ الْيَوْمُ الْحَقُّ ۖ فَمَن شَاءَ اتَّخَذَ إِلَىٰ رَبِّهِ مَآبًا','آن روز حق است؛ هر کس بخواهد راهی به سوی پروردگارش برمی‌گزیند!'];
ayat[5712]=[78,40,'إِنَّا أَنذَرْنَاكُمْ عَذَابًا قَرِيبًا يَوْمَ يَنظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُولُ الْكَافِرُ يَا لَيْتَنِي كُنتُ تُرَابًا','و ما شما را از عذاب نزدیکی بیم دادیم! این عذاب در روزی خواهد بود که انسان آنچه را از قبل با دستهای خود فرستاده می‌بیند، و کافر می‌گوید: «ای کاش خاک بودم (و گرفتار عذاب نمی‌شدم)!»'];
ayat[5713]=[79,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالنَّازِعَاتِ غَرْقًا','سوگند به فرشتگانی که (جان مجرمان را بشدّت از بدنهایشان) برمی‌کشند،'];
ayat[5714]=[79,2,'وَالنَّاشِطَاتِ نَشْطًا','و فرشتگانی که (روح مؤمنان) را با مدارا و نشاط جدا می‌سازند،'];
ayat[5715]=[79,3,'وَالسَّابِحَاتِ سَبْحًا','و سوگند به فرشتگانی که (در اجرای فرمان الهی) با سرعت حرکت می‌کنند،'];
ayat[5716]=[79,4,'فَالسَّابِقَاتِ سَبْقًا','و سپس بر یکدیگر سبقت می‌گیرند،'];
ayat[5717]=[79,5,'فَالْمُدَبِّرَاتِ أَمْرًا','و آنها که امور را تدبیر می‌کنند!'];
ayat[5718]=[79,6,'يَوْمَ تَرْجُفُ الرَّاجِفَةُ','آن روز که زلزله‌های وحشتناک همه چیز را به لرزه درمی‌آورد،'];
ayat[5719]=[79,7,'تَتْبَعُهَا الرَّادِفَةُ','و بدنبال آن، حادثه دومین [= صیحه عظیم محشر] رخ می‌دهد،'];
ayat[5720]=[79,8,'قُلُوبٌ يَوْمَئِذٍ وَاجِفَةٌ','دلهایی در آن روز سخت مضطرب است،'];
ayat[5721]=[79,9,'أَبْصَارُهَا خَاشِعَةٌ','و چشمهای آنان از شدّت ترس فروافتاده است!'];
ayat[5722]=[79,10,'يَقُولُونَ أَإِنَّا لَمَرْدُودُونَ فِي الْحَافِرَةِ','(ولی امروز) می‌گویند: «آیا ما به زندگی مجدّد بازمی‌گردیم؟!'];
ayat[5723]=[79,11,'أَإِذَا كُنَّا عِظَامًا نَّخِرَةً','آیا هنگامی که استخوانهای پوسیده‌ای شدیم (ممکن است زنده شویم)؟!»'];
ayat[5724]=[79,12,'قَالُوا تِلْكَ إِذًا كَرَّةٌ خَاسِرَةٌ','می‌گویند: «اگر قیامتی در کار باشد، بازگشتی است زیانبار!»'];
ayat[5725]=[79,13,'فَإِنَّمَا هِيَ زَجْرَةٌ وَاحِدَةٌ','ولی (بدانید) این بازگشت تنها با یک صیحه عظیم است!'];
ayat[5726]=[79,14,'فَإِذَا هُم بِالسَّاهِرَةِ','ناگهان همگی بر عرصه زمین ظاهر می‌گردند!'];
ayat[5727]=[79,15,'هَلْ أَتَاكَ حَدِيثُ مُوسَىٰ','آیا داستان موسی به تو رسیده است؟!'];
ayat[5728]=[79,16,'إِذْ نَادَاهُ رَبُّهُ بِالْوَادِ الْمُقَدَّسِ طُوًى','در آن هنگام که پروردگارش او را در سرزمین مقدّس «طوی» ندا داد (و گفت):'];
ayat[5729]=[79,17,'اذْهَبْ إِلَىٰ فِرْعَوْنَ إِنَّهُ طَغَىٰ','به سوی فرعون برو که طغیان کرده است!'];
ayat[5730]=[79,18,'فَقُلْ هَل لَّكَ إِلَىٰ أَن تَزَكَّىٰ','و به او بگو: «آیا می‌خواهی پاکیزه شوی؟!'];
ayat[5731]=[79,19,'وَأَهْدِيَكَ إِلَىٰ رَبِّكَ فَتَخْشَىٰ','و من تو را به سوی پروردگارت هدایت کنم تا از او بترسی (و گناه نکنی)؟!»'];
ayat[5732]=[79,20,'فَأَرَاهُ الْآيَةَ الْكُبْرَىٰ','سپس موسی بزرگترین معجزه را به او نشان داد!'];
ayat[5733]=[79,21,'فَكَذَّبَ وَعَصَىٰ','امّا او تکذیب و عصیان کرد!'];
ayat[5734]=[79,22,'ثُمَّ أَدْبَرَ يَسْعَىٰ','سپس پشت کرد و پیوسته (برای محو آیین حق) تلاش نمود!'];
ayat[5735]=[79,23,'فَحَشَرَ فَنَادَىٰ','و ساحران را جمع کرد و مردم را دعوت نمود،'];
ayat[5736]=[79,24,'فَقَالَ أَنَا رَبُّكُمُ الْأَعْلَىٰ','و گفت: «من پروردگار برتر شما هستم!»'];
ayat[5737]=[79,25,'فَأَخَذَهُ اللَّهُ نَكَالَ الْآخِرَةِ وَالْأُولَىٰ','از این رو خداوند او را به عذاب آخرت و دنیا گرفتار ساخت!'];
ayat[5738]=[79,26,'إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِّمَن يَخْشَىٰ','در این عبرتی است برای کسی که (از خدا) بترسد!'];
ayat[5739]=[79,27,'أَأَنتُمْ أَشَدُّ خَلْقًا أَمِ السَّمَاءُ ۚ بَنَاهَا','آیا آفرینش شما (بعد از مرگ) مشکل‌تر است یا آفرینش آسمان که خداوند آن را بنا نهاد؟!'];
ayat[5740]=[79,28,'رَفَعَ سَمْكَهَا فَسَوَّاهَا','سقف آن را برافراشت و آن را منظّم ساخت،'];
ayat[5741]=[79,29,'وَأَغْطَشَ لَيْلَهَا وَأَخْرَجَ ضُحَاهَا','و شبش را تاریک و روزش را آشکار نمود!'];
ayat[5742]=[79,30,'وَالْأَرْضَ بَعْدَ ذَٰلِكَ دَحَاهَا','و زمین را بعد از آن گسترش داد،'];
ayat[5743]=[79,31,'أَخْرَجَ مِنْهَا مَاءَهَا وَمَرْعَاهَا','و از آن آب و چراگاهش را بیرون آورد،'];
ayat[5744]=[79,32,'وَالْجِبَالَ أَرْسَاهَا','و کوه‌ها را ثابت و محکم نمود!'];
ayat[5745]=[79,33,'مَتَاعًا لَّكُمْ وَلِأَنْعَامِكُمْ','همه اینها برای بهره‌گیری شما و چهارپایانتان است!'];
ayat[5746]=[79,34,'فَإِذَا جَاءَتِ الطَّامَّةُ الْكُبْرَىٰ','هنگامی که آن حادثه بزرگ رخ دهد،'];
ayat[5747]=[79,35,'يَوْمَ يَتَذَكَّرُ الْإِنسَانُ مَا سَعَىٰ','در آن روز انسان به یاد کوششهایش می‌افتد،'];
ayat[5748]=[79,36,'وَبُرِّزَتِ الْجَحِيمُ لِمَن يَرَىٰ','و جهنّم برای هر بیننده‌ای آشکار می‌گردد،'];
ayat[5749]=[79,37,'فَأَمَّا مَن طَغَىٰ','امّا آن کسی که طغیان کرده،'];
ayat[5750]=[79,38,'وَآثَرَ الْحَيَاةَ الدُّنْيَا','و زندگی دنیا را مقدّم داشته،'];
ayat[5751]=[79,39,'فَإِنَّ الْجَحِيمَ هِيَ الْمَأْوَىٰ','مسلّماً دوزخ جایگاه اوست!'];
ayat[5752]=[79,40,'وَأَمَّا مَنْ خَافَ مَقَامَ رَبِّهِ وَنَهَى النَّفْسَ عَنِ الْهَوَىٰ','و آن کس که از مقام پروردگارش ترسان باشد و نفس را از هوی بازدارد،'];
ayat[5753]=[79,41,'فَإِنَّ الْجَنَّةَ هِيَ الْمَأْوَىٰ','قطعاً بهشت جایگاه اوست!'];
ayat[5754]=[79,42,'يَسْأَلُونَكَ عَنِ السَّاعَةِ أَيَّانَ مُرْسَاهَا','و از تو درباره قیامت می‌پرسند که در چه زمانی واقع می‌شود؟!'];
ayat[5755]=[79,43,'فِيمَ أَنتَ مِن ذِكْرَاهَا','تو را با یادآوری این سخن چه کار؟!'];
ayat[5756]=[79,44,'إِلَىٰ رَبِّكَ مُنتَهَاهَا','نهایت آن به سوی پروردگار تو است (و هیچ کس جز خدا از زمانش آگاه نیست)!'];
ayat[5757]=[79,45,'إِنَّمَا أَنتَ مُنذِرُ مَن يَخْشَاهَا','کار تو فقط بیم‌دادن کسانی است که از آن می‌ترسند!'];
ayat[5758]=[79,46,'كَأَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوا إِلَّا عَشِيَّةً أَوْ ضُحَاهَا','آنها در آن روز که قیام قیامت را می‌بینند چنین احساس می‌کنند که گویی توقّفشان (در دنیا و برزخ) جز شامگاهی یا صبح آن بیشتر نبوده است!'];
ayat[5759]=[80,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ عَبَسَ وَتَوَلَّىٰ','چهره در هم کشید و روی برتافت...'];
ayat[5760]=[80,2,'أَن جَاءَهُ الْأَعْمَىٰ','از اینکه نابینایی به سراغ او آمده بود!'];
ayat[5761]=[80,3,'وَمَا يُدْرِيكَ لَعَلَّهُ يَزَّكَّىٰ','تو چه می‌دانی شاید او پاکی و تقوا پیشه کند،'];
ayat[5762]=[80,4,'أَوْ يَذَّكَّرُ فَتَنفَعَهُ الذِّكْرَىٰ','یا متذکّر گردد و این تذکّر به حال او مفید باشد!'];
ayat[5763]=[80,5,'أَمَّا مَنِ اسْتَغْنَىٰ','امّا آن کس که توانگر است،'];
ayat[5764]=[80,6,'فَأَنتَ لَهُ تَصَدَّىٰ','تو به او روی می‌آوری،'];
ayat[5765]=[80,7,'وَمَا عَلَيْكَ أَلَّا يَزَّكَّىٰ','در حالی که اگر او خود را پاک نسازد، چیزی بر تو نیست!'];
ayat[5766]=[80,8,'وَأَمَّا مَن جَاءَكَ يَسْعَىٰ','امّا کسی که به سراغ تو می‌آید و کوشش می‌کند،'];
ayat[5767]=[80,9,'وَهُوَ يَخْشَىٰ','و از خدا ترسان است،'];
ayat[5768]=[80,10,'فَأَنتَ عَنْهُ تَلَهَّىٰ','تو از او غافل می‌شوی!'];
ayat[5769]=[80,11,'كَلَّا إِنَّهَا تَذْكِرَةٌ','هرگز چنین نیست که آنها می‌پندارند؛ این (قرآن) تذکّر و یادآوری است،'];
ayat[5770]=[80,12,'فَمَن شَاءَ ذَكَرَهُ','و هر کس بخواهد از آن پند می‌گیرد!'];
ayat[5771]=[80,13,'فِي صُحُفٍ مُّكَرَّمَةٍ','در الواح پرارزشی ثبت است،'];
ayat[5772]=[80,14,'مَّرْفُوعَةٍ مُّطَهَّرَةٍ','الواحی والاقدر و پاکیزه،'];
ayat[5773]=[80,15,'بِأَيْدِي سَفَرَةٍ','به دست سفیرانی است‌'];
ayat[5774]=[80,16,'كِرَامٍ بَرَرَةٍ','والا مقام و فرمانبردار و نیکوکار!'];
ayat[5775]=[80,17,'قُتِلَ الْإِنسَانُ مَا أَكْفَرَهُ','مرگ بر این انسان، چقدر کافر و ناسپاس است!'];
ayat[5776]=[80,18,'مِنْ أَيِّ شَيْءٍ خَلَقَهُ','(خداوند) او را از چه چیز آفریده است؟!'];
ayat[5777]=[80,19,'مِن نُّطْفَةٍ خَلَقَهُ فَقَدَّرَهُ','او را از نطفه ناچیزی آفرید، سپس اندازه‌گیری کرد و موزون ساخت،'];
ayat[5778]=[80,20,'ثُمَّ السَّبِيلَ يَسَّرَهُ','سپس راه را برای او آسان کرد،'];
ayat[5779]=[80,21,'ثُمَّ أَمَاتَهُ فَأَقْبَرَهُ','بعد او را میراند و در قبر پنهان نمود،'];
ayat[5780]=[80,22,'ثُمَّ إِذَا شَاءَ أَنشَرَهُ','سپس هرگاه بخواهد او را زنده می‌کند!'];
ayat[5781]=[80,23,'كَلَّا لَمَّا يَقْضِ مَا أَمَرَهُ','چنین نیست که او می‌پندارد؛ او هنوز آنچه را (خدا) فرمان داده، اطاعت نکرده است!'];
ayat[5782]=[80,24,'فَلْيَنظُرِ الْإِنسَانُ إِلَىٰ طَعَامِهِ','انسان باید به غذای خویش (و آفرینش آن) بنگرد!'];
ayat[5783]=[80,25,'أَنَّا صَبَبْنَا الْمَاءَ صَبًّا','ما آب فراوان از آسمان فرو ریختیم،'];
ayat[5784]=[80,26,'ثُمَّ شَقَقْنَا الْأَرْضَ شَقًّا','سپس زمین را از هم شکافتیم،'];
ayat[5785]=[80,27,'فَأَنبَتْنَا فِيهَا حَبًّا','و در آن دانه‌های فراوانی رویاندیم،'];
ayat[5786]=[80,28,'وَعِنَبًا وَقَضْبًا','و انگور و سبزی بسیار،'];
ayat[5787]=[80,29,'وَزَيْتُونًا وَنَخْلًا','و زیتون و نخل فراوان،'];
ayat[5788]=[80,30,'وَحَدَائِقَ غُلْبًا','و باغهای پردرخت،'];
ayat[5789]=[80,31,'وَفَاكِهَةً وَأَبًّا','و میوه و چراگاه،'];
ayat[5790]=[80,32,'مَّتَاعًا لَّكُمْ وَلِأَنْعَامِكُمْ','تا وسیله‌ای برای بهره‌گیری شما و چهارپایانتان باشد!'];
ayat[5791]=[80,33,'فَإِذَا جَاءَتِ الصَّاخَّةُ','هنگامی که آن صدای مهیب [= صیحه رستاخیز] بیاید، (کافران در اندوه عمیقی فرومی‌روند)!'];
ayat[5792]=[80,34,'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ أَخِيهِ','در آن روز که انسان از برادر خود می‌گریزد،'];
ayat[5793]=[80,35,'وَأُمِّهِ وَأَبِيهِ','و از مادر و پدرش،'];
ayat[5794]=[80,36,'وَصَاحِبَتِهِ وَبَنِيهِ','و زن و فرزندانش؛'];
ayat[5795]=[80,37,'لِكُلِّ امْرِئٍ مِّنْهُمْ يَوْمَئِذٍ شَأْنٌ يُغْنِيهِ','در آن روز هر کدام از آنها وضعی دارد که او را کاملاً به خود مشغول می‌سازد!'];
ayat[5796]=[80,38,'وُجُوهٌ يَوْمَئِذٍ مُّسْفِرَةٌ','چهره‌هائی در آن روز گشاده و نورانی است،'];
ayat[5797]=[80,39,'ضَاحِكَةٌ مُّسْتَبْشِرَةٌ','خندان و مسرور است؛'];
ayat[5798]=[80,40,'وَوُجُوهٌ يَوْمَئِذٍ عَلَيْهَا غَبَرَةٌ','و صورتهایی در آن روز غبارآلود است،'];
ayat[5799]=[80,41,'تَرْهَقُهَا قَتَرَةٌ','و دود تاریکی آنها را پوشانده است،'];
ayat[5800]=[80,42,'أُولَٰئِكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ','آنان همان کافران فاجرند!'];
ayat[5801]=[81,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا الشَّمْسُ كُوِّرَتْ','در آن هنگام که خورشید در هم پیچیده شود،'];
ayat[5802]=[81,2,'وَإِذَا النُّجُومُ انكَدَرَتْ','و در آن هنگام که ستارگان بی‌فروغ شوند،'];
ayat[5803]=[81,3,'وَإِذَا الْجِبَالُ سُيِّرَتْ','و در آن هنگام که کوه‌ها به حرکت درآیند،'];
ayat[5804]=[81,4,'وَإِذَا الْعِشَارُ عُطِّلَتْ','و در آن هنگام که باارزش‌ترین اموال به دست فراموشی سپرده شود،'];
ayat[5805]=[81,5,'وَإِذَا الْوُحُوشُ حُشِرَتْ','و در آن هنگام که وحوش جمع شوند،'];
ayat[5806]=[81,6,'وَإِذَا الْبِحَارُ سُجِّرَتْ','و در آن هنگام که دریاها برافروخته شوند،'];
ayat[5807]=[81,7,'وَإِذَا النُّفُوسُ زُوِّجَتْ','و در آن هنگام که هر کس با همسان خود قرین گردد،'];
ayat[5808]=[81,8,'وَإِذَا الْمَوْءُودَةُ سُئِلَتْ','و در آن هنگام که از دختران زنده به گور شده سؤال شود:'];
ayat[5809]=[81,9,'بِأَيِّ ذَنبٍ قُتِلَتْ','به کدامین گناه کشته شدند؟!'];
ayat[5810]=[81,10,'وَإِذَا الصُّحُفُ نُشِرَتْ','و در آن هنگام که نامه‌های اعمال گشوده شود،'];
ayat[5811]=[81,11,'وَإِذَا السَّمَاءُ كُشِطَتْ','و در آن هنگام که پرده از روی آسمان برگرفته شود،'];
ayat[5812]=[81,12,'وَإِذَا الْجَحِيمُ سُعِّرَتْ','و در آن هنگام که دوزخ شعله‌ور گردد،'];
ayat[5813]=[81,13,'وَإِذَا الْجَنَّةُ أُزْلِفَتْ','و در آن هنگام که بهشت نزدیک شود،'];
ayat[5814]=[81,14,'عَلِمَتْ نَفْسٌ مَّا أَحْضَرَتْ','(آری در آن هنگام) هر کس می‌داند چه چیزی را آماده کرده است!'];
ayat[5815]=[81,15,'فَلَا أُقْسِمُ بِالْخُنَّسِ','سوگند به ستارگانی که بازمی‌گردند،'];
ayat[5816]=[81,16,'الْجَوَارِ الْكُنَّسِ','حرکت می‌کنند و از دیده‌ها پنهان می‌شوند،'];
ayat[5817]=[81,17,'وَاللَّيْلِ إِذَا عَسْعَسَ','و قسم به شب، هنگامی که پشت کند و به آخر رسد،'];
ayat[5818]=[81,18,'وَالصُّبْحِ إِذَا تَنَفَّسَ','و به صبح، هنگامی که تنفّس کند،'];
ayat[5819]=[81,19,'إِنَّهُ لَقَوْلُ رَسُولٍ كَرِيمٍ','که این (قرآن) کلام فرستاده بزرگواری است [= جبرئیل امین‌]'];
ayat[5820]=[81,20,'ذِي قُوَّةٍ عِندَ ذِي الْعَرْشِ مَكِينٍ','که صاحب قدرت است و نزد (خداوند) صاحب عرش، مقام والائی دارد!'];
ayat[5821]=[81,21,'مُّطَاعٍ ثَمَّ أَمِينٍ','در آسمانها مورد اطاعت (فرشتگان) و امین است!'];
ayat[5822]=[81,22,'وَمَا صَاحِبُكُم بِمَجْنُونٍ','و مصاحب شما [= پیامبر] دیوانه نیست!'];
ayat[5823]=[81,23,'وَلَقَدْ رَآهُ بِالْأُفُقِ الْمُبِينِ','او (جبرئیل) را در اُفق روشن دیده است!'];
ayat[5824]=[81,24,'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِينٍ','و او نسبت به آنچه از طریق وحی دریافت داشته بخل ندارد!'];
ayat[5825]=[81,25,'وَمَا هُوَ بِقَوْلِ شَيْطَانٍ رَّجِيمٍ','این (قرآن) گفته شیطان رجیم نیست!'];
ayat[5826]=[81,26,'فَأَيْنَ تَذْهَبُونَ','پس به کجا می‌روید؟!'];
ayat[5827]=[81,27,'إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَالَمِينَ','این قرآن چیزی جز تذکّری برای جهانیان نیست،'];
ayat[5828]=[81,28,'لِمَن شَاءَ مِنكُمْ أَن يَسْتَقِيمَ','برای کسی از شما که بخواهد راه مستقیم در پیش گیرد!'];
ayat[5829]=[81,29,'وَمَا تَشَاءُونَ إِلَّا أَن يَشَاءَ اللَّهُ رَبُّ الْعَالَمِينَ','و شما اراده نمی‌کنید مگر اینکه خداوند -پروردگار جهانیان- اراده کند و بخواهد!'];
ayat[5830]=[82,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا السَّمَاءُ انفَطَرَتْ','آن زمان که آسمان [= کرات آسمانی‌] از هم شکافته شود،'];
ayat[5831]=[82,2,'وَإِذَا الْكَوَاكِبُ انتَثَرَتْ','و آن زمان که ستارگان پراکنده شوند و فرو ریزند،'];
ayat[5832]=[82,3,'وَإِذَا الْبِحَارُ فُجِّرَتْ','و آن زمان که دریاها به هم پیوسته شود،'];
ayat[5833]=[82,4,'وَإِذَا الْقُبُورُ بُعْثِرَتْ','و آن زمان که قبرها زیر و رو گردد (و مردگان خارج شوند)،'];
ayat[5834]=[82,5,'عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَأَخَّرَتْ','(در آن زمان) هر کس می‌داند آنچه را از پیش فرستاده و آنچه را برای بعد گذاشته است.'];
ayat[5835]=[82,6,'يَا أَيُّهَا الْإِنسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيمِ','ای انسان! چه چیز تو را در برابر پروردگار کریمت مغرور ساخته است؟!'];
ayat[5836]=[82,7,'الَّذِي خَلَقَكَ فَسَوَّاكَ فَعَدَلَكَ','همان خدایی که تو را آفرید و سامان داد و منظّم ساخت،'];
ayat[5837]=[82,8,'فِي أَيِّ صُورَةٍ مَّا شَاءَ رَكَّبَكَ','و در هر صورتی که خواست تو را ترکیب نمود.'];
ayat[5838]=[82,9,'كَلَّا بَلْ تُكَذِّبُونَ بِالدِّينِ','(آری) آن گونه که شما می‌پندارید نیست؛ بلکه شما روز جزا را منکرید!'];
ayat[5839]=[82,10,'وَإِنَّ عَلَيْكُمْ لَحَافِظِينَ','و بی‌شک نگاهبانانی بر شما گمارده شده...'];
ayat[5840]=[82,11,'كِرَامًا كَاتِبِينَ','والا مقام و نویسنده (اعمال نیک و بد شما)،'];
ayat[5841]=[82,12,'يَعْلَمُونَ مَا تَفْعَلُونَ','که می‌دانند شما چه می‌کنید!'];
ayat[5842]=[82,13,'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ','به یقین نیکان در نعمتی فراوانند.'];
ayat[5843]=[82,14,'وَإِنَّ الْفُجَّارَ لَفِي جَحِيمٍ','و بدکاران در دوزخند،'];
ayat[5844]=[82,15,'يَصْلَوْنَهَا يَوْمَ الدِّينِ','روز جزا وارد آن می‌شوند و می‌سوزند،'];
ayat[5845]=[82,16,'وَمَا هُمْ عَنْهَا بِغَائِبِينَ','و آنان هرگز از آن غایب و دور نیستند!'];
ayat[5846]=[82,17,'وَمَا أَدْرَاكَ مَا يَوْمُ الدِّينِ','تو چه می‌دانی روز جزا چیست؟!'];
ayat[5847]=[82,18,'ثُمَّ مَا أَدْرَاكَ مَا يَوْمُ الدِّينِ','باز چه می‌دانی روز جزا چیست؟!'];
ayat[5848]=[82,19,'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْئًا ۖ وَالْأَمْرُ يَوْمَئِذٍ لِّلَّهِ','روزی است که هیچ کس قادر بر انجام کاری به سود دیگری نیست، و همه امور در آن روز از آن خداست!'];
ayat[5849]=[83,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَيْلٌ لِّلْمُطَفِّفِينَ','وای بر کم‌فروشان!'];
ayat[5850]=[83,2,'الَّذِينَ إِذَا اكْتَالُوا عَلَى النَّاسِ يَسْتَوْفُونَ','آنان که وقتی برای خود پیمانه می‌کنند، حق خود را بطور کامل می‌گیرند؛'];
ayat[5851]=[83,3,'وَإِذَا كَالُوهُمْ أَو وَّزَنُوهُمْ يُخْسِرُونَ','امّا هنگامی که می‌خواهند برای دیگران پیمانه یا وزن کنند، کم می‌گذارند!'];
ayat[5852]=[83,4,'أَلَا يَظُنُّ أُولَٰئِكَ أَنَّهُم مَّبْعُوثُونَ','آیا آنها گمان نمی‌کنند که برانگیخته می‌شوند،'];
ayat[5853]=[83,5,'لِيَوْمٍ عَظِيمٍ','در روزی بزرگ؛'];
ayat[5854]=[83,6,'يَوْمَ يَقُومُ النَّاسُ لِرَبِّ الْعَالَمِينَ','روزی که مردم در پیشگاه پروردگار جهانیان می‌ایستند.'];
ayat[5855]=[83,7,'كَلَّا إِنَّ كِتَابَ الْفُجَّارِ لَفِي سِجِّينٍ','چنین نیست که آنها (درباره قیامت) می‌پندارند، به یقین نامه اعمال بدکاران در «سجّین» است!'];
ayat[5856]=[83,8,'وَمَا أَدْرَاكَ مَا سِجِّينٌ','تو چه می‌دانی «سجّین» چیست؟'];
ayat[5857]=[83,9,'كِتَابٌ مَّرْقُومٌ','نامه‌ای است رقم زده شده و سرنوشتی است حتمی!'];
ayat[5858]=[83,10,'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ','وای در آن روز بر تکذیب‌کنندگان!'];
ayat[5859]=[83,11,'الَّذِينَ يُكَذِّبُونَ بِيَوْمِ الدِّينِ','همانها که روز جزا را انکار می‌کنند.'];
ayat[5860]=[83,12,'وَمَا يُكَذِّبُ بِهِ إِلَّا كُلُّ مُعْتَدٍ أَثِيمٍ','تنها کسی آن را انکار می‌کند که متجاوز و گنهکار است!'];
ayat[5861]=[83,13,'إِذَا تُتْلَىٰ عَلَيْهِ آيَاتُنَا قَالَ أَسَاطِيرُ الْأَوَّلِينَ','(همان کسی که) وقتی آیات ما بر او خوانده می‌شود می‌گوید: «این افسانه‌های پیشینیان است!»'];
ayat[5862]=[83,14,'كَلَّا ۖ بَلْ ۜ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُوا يَكْسِبُونَ','چنین نیست که آنها می‌پندارند، بلکه اعمالشان چون زنگاری بر دلهایشان نشسته است!'];
ayat[5863]=[83,15,'كَلَّا إِنَّهُمْ عَن رَّبِّهِمْ يَوْمَئِذٍ لَّمَحْجُوبُونَ','چنین نیست که می‌پندارند، بلکه آنها در آن روز از پروردگارشان محجوبند!'];
ayat[5864]=[83,16,'ثُمَّ إِنَّهُمْ لَصَالُو الْجَحِيمِ','سپس آنها به یقین وارد دوزخ می‌شوند!'];
ayat[5865]=[83,17,'ثُمَّ يُقَالُ هَٰذَا الَّذِي كُنتُم بِهِ تُكَذِّبُونَ','بعد به آنها گفته می‌شود: «این همان چیزی است که آن را انکار می‌کردید!»'];
ayat[5866]=[83,18,'كَلَّا إِنَّ كِتَابَ الْأَبْرَارِ لَفِي عِلِّيِّينَ','چنان نیست که آنها (درباره معاد) می‌پندارند، بلکه نامه اعمال نیکان در «علیّین» است!'];
ayat[5867]=[83,19,'وَمَا أَدْرَاكَ مَا عِلِّيُّونَ','و تو چه می‌دانی «علیّین» چیست!'];
ayat[5868]=[83,20,'كِتَابٌ مَّرْقُومٌ','نامه‌ای است رقم‌خورده و سرنوشتی است قطعی،'];
ayat[5869]=[83,21,'يَشْهَدُهُ الْمُقَرَّبُونَ','که مقربان شاهد آنند!'];
ayat[5870]=[83,22,'إِنَّ الْأَبْرَارَ لَفِي نَعِيمٍ','مسلّماً نیکان در انواع نعمت‌اند:'];
ayat[5871]=[83,23,'عَلَى الْأَرَائِكِ يَنظُرُونَ','بر تختهای زیبای بهشتی تکیه کرده و (به زیباییهای بهشت) می‌نگرند!'];
ayat[5872]=[83,24,'تَعْرِفُ فِي وُجُوهِهِمْ نَضْرَةَ النَّعِيمِ','در چهره‌هایشان طراوت و نشاط نعمت را می‌بینی و می‌شناسی!'];
ayat[5873]=[83,25,'يُسْقَوْنَ مِن رَّحِيقٍ مَّخْتُومٍ','آنها از شراب (طهور) زلال دست‌نخورده و سربسته‌ای سیراب می‌شوند!'];
ayat[5874]=[83,26,'خِتَامُهُ مِسْكٌ ۚ وَفِي ذَٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُونَ','مهری که بر آن نهاده شده از مشک است؛ و در این نعمتهای بهشتی راغبان باید بر یکدیگر پیشی گیرند!'];
ayat[5875]=[83,27,'وَمِزَاجُهُ مِن تَسْنِيمٍ','این شراب (طهور) آمیخته با «تسنیم» است،'];
ayat[5876]=[83,28,'عَيْنًا يَشْرَبُ بِهَا الْمُقَرَّبُونَ','همان چشمه‌ای که مقرّبان از آن می‌نوشند.'];
ayat[5877]=[83,29,'إِنَّ الَّذِينَ أَجْرَمُوا كَانُوا مِنَ الَّذِينَ آمَنُوا يَضْحَكُونَ','بدکاران (در دنیا) پیوسته به مؤمنان می‌خندیدند،'];
ayat[5878]=[83,30,'وَإِذَا مَرُّوا بِهِمْ يَتَغَامَزُونَ','و هنگامی که از کنارشان می‌گذشتند آنان را با اشاره تمسخر می‌کردند،'];
ayat[5879]=[83,31,'وَإِذَا انقَلَبُوا إِلَىٰ أَهْلِهِمُ انقَلَبُوا فَكِهِينَ','و چون به سوی خانواده خود بازمی‌گشتند مسرور و خندان بودند،'];
ayat[5880]=[83,32,'وَإِذَا رَأَوْهُمْ قَالُوا إِنَّ هَٰؤُلَاءِ لَضَالُّونَ','و هنگامی که آنها را می‌دیدند می‌گفتند: «اینها گمراهانند!»'];
ayat[5881]=[83,33,'وَمَا أُرْسِلُوا عَلَيْهِمْ حَافِظِينَ','در حالی که هرگز مأمور مراقبت و متکفّل آنان [= مؤمنان‌] نبودند!'];
ayat[5882]=[83,34,'فَالْيَوْمَ الَّذِينَ آمَنُوا مِنَ الْكُفَّارِ يَضْحَكُونَ','ولی امروز مؤمنان به کفار می‌خندند،'];
ayat[5883]=[83,35,'عَلَى الْأَرَائِكِ يَنظُرُونَ','در حالی که بر تختهای آراسته بهشتی نشسته و (به سرنوشت شوم آنها) می‌نگرند!'];
ayat[5884]=[83,36,'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوا يَفْعَلُونَ','آیا (با این حال) کافران پاداش اعمال خود را گرفتند؟!'];
ayat[5885]=[84,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا السَّمَاءُ انشَقَّتْ','در آن هنگام که آسمان [= کرات آسمانی‌] شکافته شود،'];
ayat[5886]=[84,2,'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ','و تسلیم فرمان پروردگارش شود -و سزاوار است چنین باشد-'];
ayat[5887]=[84,3,'وَإِذَا الْأَرْضُ مُدَّتْ','و در آن هنگام که زمین گسترده شود،'];
ayat[5888]=[84,4,'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ','و آنچه در درون دارد بیرون افکنده و خالی شود،'];
ayat[5889]=[84,5,'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ','و تسلیم فرمان پروردگارش گردد -و شایسته است که چنین باشد-'];
ayat[5890]=[84,6,'يَا أَيُّهَا الْإِنسَانُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدْحًا فَمُلَاقِيهِ','ای انسان! تو با تلاش و رنج بسوی پروردگارت می‌روی و او را ملاقات خواهی کرد!'];
ayat[5891]=[84,7,'فَأَمَّا مَنْ أُوتِيَ كِتَابَهُ بِيَمِينِهِ','پس کسی که نامه اعمالش به دست راستش داده شود،'];
ayat[5892]=[84,8,'فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا','بزودی حساب آسانی برای او می‌شود،'];
ayat[5893]=[84,9,'وَيَنقَلِبُ إِلَىٰ أَهْلِهِ مَسْرُورًا','و خوشحال به اهل و خانواده‌اش بازمی‌گردد.'];
ayat[5894]=[84,10,'وَأَمَّا مَنْ أُوتِيَ كِتَابَهُ وَرَاءَ ظَهْرِهِ','و امّا کسی که نامه اعمالش به پشت سرش داده شود،'];
ayat[5895]=[84,11,'فَسَوْفَ يَدْعُو ثُبُورًا','بزودی فریاد می‌زند وای بر من که هلاک شدم!'];
ayat[5896]=[84,12,'وَيَصْلَىٰ سَعِيرًا','و در شعله‌های سوزان آتش می‌سوزد.'];
ayat[5897]=[84,13,'إِنَّهُ كَانَ فِي أَهْلِهِ مَسْرُورًا','چرا که او در میان خانواده‌اش پیوسته (از کفر و گناه خود) مسرور بود!'];
ayat[5898]=[84,14,'إِنَّهُ ظَنَّ أَن لَّن يَحُورَ','او گمان می‌کرد هرگز بازگشت نمی‌کند!'];
ayat[5899]=[84,15,'بَلَىٰ إِنَّ رَبَّهُ كَانَ بِهِ بَصِيرًا','آری، پروردگارش نسبت به او بینا بود (و اعمالش را برای حساب ثبت کرد)!'];
ayat[5900]=[84,16,'فَلَا أُقْسِمُ بِالشَّفَقِ','سوگند به شفق،'];
ayat[5901]=[84,17,'وَاللَّيْلِ وَمَا وَسَقَ','و سوگند به شب و آنچه را جمع‌آوری می‌کند،'];
ayat[5902]=[84,18,'وَالْقَمَرِ إِذَا اتَّسَقَ','و سوگند به ماه آنگاه که بَدر کامل می‌شود،'];
ayat[5903]=[84,19,'لَتَرْكَبُنَّ طَبَقًا عَن طَبَقٍ','که همه شما پیوسته از حالی به حال دیگر منتقل می‌شوید (تا به کمال برسید).'];
ayat[5904]=[84,20,'فَمَا لَهُمْ لَا يُؤْمِنُونَ','پس چرا آنان ایمان نمی‌آورند؟!'];
ayat[5905]=[84,21,'وَإِذَا قُرِئَ عَلَيْهِمُ الْقُرْآنُ لَا يَسْجُدُونَ ۩','و هنگامی که قرآن بر آنها خوانده می‌شود سجده نمی‌کنند؟!'];
ayat[5906]=[84,22,'بَلِ الَّذِينَ كَفَرُوا يُكَذِّبُونَ','بلکه کافران پیوسته آیات الهی را انکار می‌کنند!'];
ayat[5907]=[84,23,'وَاللَّهُ أَعْلَمُ بِمَا يُوعُونَ','و خداوند آنچه را در دل پنهان می‌دارند بخوبی می‌داند!'];
ayat[5908]=[84,24,'فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ','پس آنها را به عذابی دردناک بشارت ده!'];
ayat[5909]=[84,25,'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ','مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، که برای آنان پاداشی است قطع‌نشدنی!'];
ayat[5910]=[85,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالسَّمَاءِ ذَاتِ الْبُرُوجِ','سوگند به آسمان که دارای برجهای بسیار است،'];
ayat[5911]=[85,2,'وَالْيَوْمِ الْمَوْعُودِ','و سوگند به آن روز موعود،'];
ayat[5912]=[85,3,'وَشَاهِدٍ وَمَشْهُودٍ','و سوگند به «شاهد» و «مشهود»! [«شاهد»: پیامبر و گواهان اعمال، و «مشهود»: اعمال امّت است‌]'];
ayat[5913]=[85,4,'قُتِلَ أَصْحَابُ الْأُخْدُودِ','مرگ بر شکنجه‌گران صاحب گودال (آتش)،'];
ayat[5914]=[85,5,'النَّارِ ذَاتِ الْوَقُودِ','آتشی عظیم و شعله‌ور!'];
ayat[5915]=[85,6,'إِذْ هُمْ عَلَيْهَا قُعُودٌ','هنگامی که در کنار آن نشسته بودند،'];
ayat[5916]=[85,7,'وَهُمْ عَلَىٰ مَا يَفْعَلُونَ بِالْمُؤْمِنِينَ شُهُودٌ','و آنچه را با مؤمنان انجام می‌دادند (با خونسردی و قساوت) تماشا می‌کردند!'];
ayat[5917]=[85,8,'وَمَا نَقَمُوا مِنْهُمْ إِلَّا أَن يُؤْمِنُوا بِاللَّهِ الْعَزِيزِ الْحَمِيدِ','آنها هیچ ایرادی بر مؤمنان نداشتند جز اینکه به خداوند عزیز و حمید ایمان آورده بودند؛'];
ayat[5918]=[85,9,'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ','همان کسی که حکومت آسمانها و زمین از آن اوست و خداوند بر همه چیز گواه است!'];
ayat[5919]=[85,10,'إِنَّ الَّذِينَ فَتَنُوا الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ ثُمَّ لَمْ يَتُوبُوا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيقِ','کسانی که مردان و زنان باایمان را شکنجه دادند سپس توبه نکردند، برای آنها عذاب دوزخ و عذاب آتش سوزان است!'];
ayat[5920]=[85,11,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ لَهُمْ جَنَّاتٌ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ ۚ ذَٰلِكَ الْفَوْزُ الْكَبِيرُ','و برای کسانی که ایمان آوردند و اعمال شایسته انجام دادند، باغهایی از بهشت است که نهرها زیر درختانش جاری است؛ و این نجات و پیروزی بزرگ است!'];
ayat[5921]=[85,12,'إِنَّ بَطْشَ رَبِّكَ لَشَدِيدٌ','گرفتن قهرآمیز و مجازات پروردگارت به یقین بسیار شدید است!'];
ayat[5922]=[85,13,'إِنَّهُ هُوَ يُبْدِئُ وَيُعِيدُ','اوست که آفرینش را آغاز می‌کند و بازمی‌گرداند،'];
ayat[5923]=[85,14,'وَهُوَ الْغَفُورُ الْوَدُودُ','و او آمرزنده و دوستدار (مؤمنان) است،'];
ayat[5924]=[85,15,'ذُو الْعَرْشِ الْمَجِيدُ','صاحب عرش و دارای مجد و عظمت است،'];
ayat[5925]=[85,16,'فَعَّالٌ لِّمَا يُرِيدُ','و آنچه را می‌خواهد انجام می‌دهد!'];
ayat[5926]=[85,17,'هَلْ أَتَاكَ حَدِيثُ الْجُنُودِ','آیا داستان لشکرها به تو رسیده است،'];
ayat[5927]=[85,18,'فِرْعَوْنَ وَثَمُودَ','لشکریان فرعون و ثمود؟!'];
ayat[5928]=[85,19,'بَلِ الَّذِينَ كَفَرُوا فِي تَكْذِيبٍ','ولی کافران پیوسته در تکذیب حقند،'];
ayat[5929]=[85,20,'وَاللَّهُ مِن وَرَائِهِم مُّحِيطٌ','و خداوند به همه آنها احاطه دارد!'];
ayat[5930]=[85,21,'بَلْ هُوَ قُرْآنٌ مَّجِيدٌ','(این آیات، سحر و دروغ نیست،) بلکه قرآن باعظمت است...'];
ayat[5931]=[85,22,'فِي لَوْحٍ مَّحْفُوظٍ','که در لوح محفوظ جای دارد!'];
ayat[5932]=[86,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالسَّمَاءِ وَالطَّارِقِ','سوگند به آسمان و کوبنده شب!'];
ayat[5933]=[86,2,'وَمَا أَدْرَاكَ مَا الطَّارِقُ','و تو نمی‌دانی کوبنده شب چیست!'];
ayat[5934]=[86,3,'النَّجْمُ الثَّاقِبُ','همان ستاره درخشان و شکافنده تاریکیهاست!'];
ayat[5935]=[86,4,'إِن كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌ','(به این آیت بزرگ الهی سوگند) که هر کس مراقب و محافظی دارد!'];
ayat[5936]=[86,5,'فَلْيَنظُرِ الْإِنسَانُ مِمَّ خُلِقَ','انسان باید بنگرد که از چه چیز آفریده شده است!'];
ayat[5937]=[86,6,'خُلِقَ مِن مَّاءٍ دَافِقٍ','از یک آب جهنده آفریده شده است،'];
ayat[5938]=[86,7,'يَخْرُجُ مِن بَيْنِ الصُّلْبِ وَالتَّرَائِبِ','آبی که از میان پشت و سینه‌ها خارج می‌شود!'];
ayat[5939]=[86,8,'إِنَّهُ عَلَىٰ رَجْعِهِ لَقَادِرٌ','مسلّماً او [= خدائی که انسان را از چنین چیز پستی آفرید] می‌تواند او را بازگرداند!'];
ayat[5940]=[86,9,'يَوْمَ تُبْلَى السَّرَائِرُ','در آن روز که اسرار نهان (انسان) آشکار میشود،'];
ayat[5941]=[86,10,'فَمَا لَهُ مِن قُوَّةٍ وَلَا نَاصِرٍ','و برای او هیچ نیرو و یاوری نیست!'];
ayat[5942]=[86,11,'وَالسَّمَاءِ ذَاتِ الرَّجْعِ','سوگند به آسمان پرباران،'];
ayat[5943]=[86,12,'وَالْأَرْضِ ذَاتِ الصَّدْعِ','و سوگند به زمین پرشکاف (که گیاهان از آن سر برمی‌آورند)،'];
ayat[5944]=[86,13,'إِنَّهُ لَقَوْلٌ فَصْلٌ','که این (قرآن) سخنی است که حقّ را از باطل جدا می‌کند،'];
ayat[5945]=[86,14,'وَمَا هُوَ بِالْهَزْلِ','و هرگز شوخی نیست!'];
ayat[5946]=[86,15,'إِنَّهُمْ يَكِيدُونَ كَيْدًا','آنها پیوسته حیله می‌کنند،'];
ayat[5947]=[86,16,'وَأَكِيدُ كَيْدًا','و من هم در برابر آنها چاره می‌کنم!'];
ayat[5948]=[86,17,'فَمَهِّلِ الْكَافِرِينَ أَمْهِلْهُمْ رُوَيْدًا','حال که چنین است کافران را (فقط) اندکی مهلت ده (تا سزای اعمالشان را ببینند)!'];
ayat[5949]=[87,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبِّحِ اسْمَ رَبِّكَ الْأَعْلَى','منزّه شمار نام پروردگار بلندمرتبه‌ات را!'];
ayat[5950]=[87,2,'الَّذِي خَلَقَ فَسَوَّىٰ','همان خداوندی که آفرید و منظم کرد،'];
ayat[5951]=[87,3,'وَالَّذِي قَدَّرَ فَهَدَىٰ','و همان که اندازه‌گیری کرد و هدایت نمود،'];
ayat[5952]=[87,4,'وَالَّذِي أَخْرَجَ الْمَرْعَىٰ','و آن کس را که چراگاه را به وجود آورد،'];
ayat[5953]=[87,5,'فَجَعَلَهُ غُثَاءً أَحْوَىٰ','سپس آن را خشک و تیره قرار داد!'];
ayat[5954]=[87,6,'سَنُقْرِئُكَ فَلَا تَنسَىٰ','ما بزودی (قرآن را) بر تو می‌خوانیم و هرگز فراموش نخواهی کرد،'];
ayat[5955]=[87,7,'إِلَّا مَا شَاءَ اللَّهُ ۚ إِنَّهُ يَعْلَمُ الْجَهْرَ وَمَا يَخْفَىٰ','مگر آنچه را خدا بخواهد، که او آشکار و نهان را می‌داند!'];
ayat[5956]=[87,8,'وَنُيَسِّرُكَ لِلْيُسْرَىٰ','و ما تو را برای انجام هر کار خیر آماده می‌کنیم!'];
ayat[5957]=[87,9,'فَذَكِّرْ إِن نَّفَعَتِ الذِّكْرَىٰ','پس تذکّر ده اگر تذکّر مفید باشد!'];
ayat[5958]=[87,10,'سَيَذَّكَّرُ مَن يَخْشَىٰ','و بزودی کسی که از خدا می‌ترسد متذکّر می‌شود.'];
ayat[5959]=[87,11,'وَيَتَجَنَّبُهَا الْأَشْقَى','امّا بدبخت‌ترین افراد از آن دوری می‌گزیند،'];
ayat[5960]=[87,12,'الَّذِي يَصْلَى النَّارَ الْكُبْرَىٰ','همان کسی که در آتش بزرگ وارد می‌شود،'];
ayat[5961]=[87,13,'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ','سپس در آن آتش نه می‌میرد و نه زنده می‌شود!'];
ayat[5962]=[87,14,'قَدْ أَفْلَحَ مَن تَزَكَّىٰ','به یقین کسی که پاکی جست (و خود را تزکیه کرد)، رستگار شد.'];
ayat[5963]=[87,15,'وَذَكَرَ اسْمَ رَبِّهِ فَصَلَّىٰ','و (آن که) نام پروردگارش را یاد کرد سپس نماز خواند!'];
ayat[5964]=[87,16,'بَلْ تُؤْثِرُونَ الْحَيَاةَ الدُّنْيَا','ولی شما زندگی دنیا را مقدم می‌دارید،'];
ayat[5965]=[87,17,'وَالْآخِرَةُ خَيْرٌ وَأَبْقَىٰ','در حالی که آخرت بهتر و پایدارتر است!'];
ayat[5966]=[87,18,'إِنَّ هَٰذَا لَفِي الصُّحُفِ الْأُولَىٰ','این دستورها در کتب آسمانی پیشین (نیز) آمده است،'];
ayat[5967]=[87,19,'صُحُفِ إِبْرَاهِيمَ وَمُوسَىٰ','در کتب ابراهیم و موسی.'];
ayat[5968]=[88,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ هَلْ أَتَاكَ حَدِيثُ الْغَاشِيَةِ','آیا داستان غاشیه [= روز قیامت که حوادث وحشتناکش همه را می‌پوشاند] به تو رسیده است؟!'];
ayat[5969]=[88,2,'وُجُوهٌ يَوْمَئِذٍ خَاشِعَةٌ','چهره‌هایی در آن روز خاشع و ذلّت‌بارند،'];
ayat[5970]=[88,3,'عَامِلَةٌ نَّاصِبَةٌ','آنها که پیوسته عمل کرده و خسته شده‌اند (و نتیجه‌ای عایدشان نشده است)،'];
ayat[5971]=[88,4,'تَصْلَىٰ نَارًا حَامِيَةً','و در آتش سوزان وارد می‌گردند؛'];
ayat[5972]=[88,5,'تُسْقَىٰ مِنْ عَيْنٍ آنِيَةٍ','از چشمه‌ای بسیار داغ به آنان می‌نوشانند؛'];
ayat[5973]=[88,6,'لَّيْسَ لَهُمْ طَعَامٌ إِلَّا مِن ضَرِيعٍ','غذائی جز از ضَریع [= خار خشک تلخ و بدبو] ندارند؛'];
ayat[5974]=[88,7,'لَّا يُسْمِنُ وَلَا يُغْنِي مِن جُوعٍ','غذایی که نه آنها را فربه می‌کند و نه از گرسنگی می‌رهاند!'];
ayat[5975]=[88,8,'وُجُوهٌ يَوْمَئِذٍ نَّاعِمَةٌ','چهره‌هایی در آن روز شاداب و باطراوتند،'];
ayat[5976]=[88,9,'لِّسَعْيِهَا رَاضِيَةٌ','و از سعی و تلاش خود خشنودند،'];
ayat[5977]=[88,10,'فِي جَنَّةٍ عَالِيَةٍ','در بهشتی عالی جای دارند،'];
ayat[5978]=[88,11,'لَّا تَسْمَعُ فِيهَا لَاغِيَةً','که در آن هیچ سخن لغو و بیهوده‌ای نمی‌شنوند!'];
ayat[5979]=[88,12,'فِيهَا عَيْنٌ جَارِيَةٌ','در آن چشمه‌ای جاری است،'];
ayat[5980]=[88,13,'فِيهَا سُرُرٌ مَّرْفُوعَةٌ','در آن تختهای زیبای بلندی است،'];
ayat[5981]=[88,14,'وَأَكْوَابٌ مَّوْضُوعَةٌ','و قدحهایی (که در کنار این چشمه) نهاده،'];
ayat[5982]=[88,15,'وَنَمَارِقُ مَصْفُوفَةٌ','و بالشها و پشتیهای صف‌داده شده،'];
ayat[5983]=[88,16,'وَزَرَابِيُّ مَبْثُوثَةٌ','و فرشهای فاخر گسترده!'];
ayat[5984]=[88,17,'أَفَلَا يَنظُرُونَ إِلَى الْإِبِلِ كَيْفَ خُلِقَتْ','آیا آنان به شتر نمی‌نگرند که چگونه آفریده شده است؟!'];
ayat[5985]=[88,18,'وَإِلَى السَّمَاءِ كَيْفَ رُفِعَتْ','و به آسمان نگاه نمی‌کنند که چگونه برافراشته شده؟!'];
ayat[5986]=[88,19,'وَإِلَى الْجِبَالِ كَيْفَ نُصِبَتْ','و به کوه‌ها که چگونه در جای خود نصب گردیده!'];
ayat[5987]=[88,20,'وَإِلَى الْأَرْضِ كَيْفَ سُطِحَتْ','و به زمین که چگونه گسترده و هموار گشته است؟!'];
ayat[5988]=[88,21,'فَذَكِّرْ إِنَّمَا أَنتَ مُذَكِّرٌ','پس تذکّر ده که تو فقط تذکّر دهنده‌ای!'];
ayat[5989]=[88,22,'لَّسْتَ عَلَيْهِم بِمُصَيْطِرٍ','تو سلطه‌گر بر آنان نیستی که (بر ایمان) مجبورشان کنی،'];
ayat[5990]=[88,23,'إِلَّا مَن تَوَلَّىٰ وَكَفَرَ','مگر کسی که پشت کند و کافر شود،'];
ayat[5991]=[88,24,'فَيُعَذِّبُهُ اللَّهُ الْعَذَابَ الْأَكْبَرَ','که خداوند او را به عذاب بزرگ مجازات می‌کند!'];
ayat[5992]=[88,25,'إِنَّ إِلَيْنَا إِيَابَهُمْ','به یقین بازگشت (همه) آنان به سوی ماست،'];
ayat[5993]=[88,26,'ثُمَّ إِنَّ عَلَيْنَا حِسَابَهُم','و مسلّماً حسابشان (نیز) با ماست!'];
ayat[5994]=[89,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْفَجْرِ','به سپیده دم سوگند،'];
ayat[5995]=[89,2,'وَلَيَالٍ عَشْرٍ','و به شبهای دهگانه،'];
ayat[5996]=[89,3,'وَالشَّفْعِ وَالْوَتْرِ','و به زوج و فرد،'];
ayat[5997]=[89,4,'وَاللَّيْلِ إِذَا يَسْرِ','و به شب، هنگامی که (به سوی روشنایی روز) حرکت می‌کند سوگند (که پروردگارت در کمین ظالمان است)!'];
ayat[5998]=[89,5,'هَلْ فِي ذَٰلِكَ قَسَمٌ لِّذِي حِجْرٍ','آیا در آنچه گفته شد، سوگند مهمّی برای صاحبان خرد نیست؟!'];
ayat[5999]=[89,6,'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍ','آیا ندیدی پروردگارت با قوم «عاد» چه کرد؟!'];
ayat[6000]=[89,7,'إِرَمَ ذَاتِ الْعِمَادِ','و با آن شهر «اِرَم» باعظمت،'];
ayat[6001]=[89,8,'الَّتِي لَمْ يُخْلَقْ مِثْلُهَا فِي الْبِلَادِ','همان شهری که مانندش در شهرها آفریده نشده بود!'];
ayat[6002]=[89,9,'وَثَمُودَ الَّذِينَ جَابُوا الصَّخْرَ بِالْوَادِ','و قوم «ثمود» که صخره‌های عظیم را از (کنار) درّه می‌بریدند (و از آن خانه و کاخ می‌ساختند)!'];
ayat[6003]=[89,10,'وَفِرْعَوْنَ ذِي الْأَوْتَادِ','و فرعونی که قدرتمند و شکنجه‌گر بود،'];
ayat[6004]=[89,11,'الَّذِينَ طَغَوْا فِي الْبِلَادِ','همان اقوامی که در شهرها طغیان کردند،'];
ayat[6005]=[89,12,'فَأَكْثَرُوا فِيهَا الْفَسَادَ','و فساد فراوان در آنها به بار آوردند؛'];
ayat[6006]=[89,13,'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍ','به همین سبب خداوند تازیانه عذاب را بر آنان فرو ریخت!'];
ayat[6007]=[89,14,'إِنَّ رَبَّكَ لَبِالْمِرْصَادِ','به یقین پروردگار تو در کمینگاه (ستمگران) است!'];
ayat[6008]=[89,15,'فَأَمَّا الْإِنسَانُ إِذَا مَا ابْتَلَاهُ رَبُّهُ فَأَكْرَمَهُ وَنَعَّمَهُ فَيَقُولُ رَبِّي أَكْرَمَنِ','امّا انسان هنگامی که پروردگارش او را برای آزمایش، اکرام می‌کند و نعمت می‌بخشد (مغرور می‌شود و) می‌گوید: «پروردگارم مرا گرامی داشته است!»'];
ayat[6009]=[89,16,'وَأَمَّا إِذَا مَا ابْتَلَاهُ فَقَدَرَ عَلَيْهِ رِزْقَهُ فَيَقُولُ رَبِّي أَهَانَنِ','و امّا هنگامی که برای امتحان، روزیش را بر او تنگ می‌گیرد (مأیوس می‌شود و) می‌گوید: «پروردگارم مرا خوار کرده است!»'];
ayat[6010]=[89,17,'كَلَّا ۖ بَل لَّا تُكْرِمُونَ الْيَتِيمَ','چنان نیست که شما می‌پندارید؛ شما یتیمان را گرامی نمی‌دارید،'];
ayat[6011]=[89,18,'وَلَا تَحَاضُّونَ عَلَىٰ طَعَامِ الْمِسْكِينِ','و یکدیگر را بر اطعام مستمندان تشویق نمی‌کنید،'];
ayat[6012]=[89,19,'وَتَأْكُلُونَ التُّرَاثَ أَكْلًا لَّمًّا','و میراث را (از راه مشروع و نامشروع) جمع کرده می‌خورید،'];
ayat[6013]=[89,20,'وَتُحِبُّونَ الْمَالَ حُبًّا جَمًّا','و مال و ثروت را بسیار دوست دارید (و بخاطر آن گناهان زیادی مرتکب می‌شوید)!'];
ayat[6014]=[89,21,'كَلَّا إِذَا دُكَّتِ الْأَرْضُ دَكًّا دَكًّا','چنان نیست که آنها می‌پندارند! در آن هنگام که زمین سخت در هم کوبیده شود،'];
ayat[6015]=[89,22,'وَجَاءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّا','و فرمان پروردگارت فرا رسد و فرشتگان صف در صف حاضر شوند،'];
ayat[6016]=[89,23,'وَجِيءَ يَوْمَئِذٍ بِجَهَنَّمَ ۚ يَوْمَئِذٍ يَتَذَكَّرُ الْإِنسَانُ وَأَنَّىٰ لَهُ الذِّكْرَىٰ','و در آن روز جهنم را حاضر می‌کنند؛ (آری) در آن روز انسان متذکّر می‌شود؛ امّا این تذکّر چه سودی برای او دارد؟!'];
ayat[6017]=[89,24,'يَقُولُ يَا لَيْتَنِي قَدَّمْتُ لِحَيَاتِي','می‌گوید: «ای کاش برای (این) زندگیم چیزی از پیش فرستاده بودم!»'];
ayat[6018]=[89,25,'فَيَوْمَئِذٍ لَّا يُعَذِّبُ عَذَابَهُ أَحَدٌ','در آن روز هیچ کس همانند او [= خدا] عذاب نمی‌کند،'];
ayat[6019]=[89,26,'وَلَا يُوثِقُ وَثَاقَهُ أَحَدٌ','و هیچ کس همچون او کسی را به بند نمی‌کشد!'];
ayat[6020]=[89,27,'يَا أَيَّتُهَا النَّفْسُ الْمُطْمَئِنَّةُ','تو ای روح آرام‌یافته!'];
ayat[6021]=[89,28,'ارْجِعِي إِلَىٰ رَبِّكِ رَاضِيَةً مَّرْضِيَّةً','به سوی پروردگارت بازگرد در حالی که هم تو از او خشنودی و هم او از تو خشنود است،'];
ayat[6022]=[89,29,'فَادْخُلِي فِي عِبَادِي','پس در سلک بندگانم درآی،'];
ayat[6023]=[89,30,'وَادْخُلِي جَنَّتِي','و در بهشتم وارد شو!'];
ayat[6024]=[90,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لَا أُقْسِمُ بِهَٰذَا الْبَلَدِ','قسم به این شهر مقدّس [= مکّه‌]،'];
ayat[6025]=[90,2,'وَأَنتَ حِلٌّ بِهَٰذَا الْبَلَدِ','شهری که تو در آن ساکنی،'];
ayat[6026]=[90,3,'وَوَالِدٍ وَمَا وَلَدَ','و قسم به پدر و فرزندش [= ابراهیم خلیل و فرزندش اسماعیل ذبیح‌]،'];
ayat[6027]=[90,4,'لَقَدْ خَلَقْنَا الْإِنسَانَ فِي كَبَدٍ','که ما انسان را در رنج آفریدیم (و زندگی او پر از رنجهاست)!'];
ayat[6028]=[90,5,'أَيَحْسَبُ أَن لَّن يَقْدِرَ عَلَيْهِ أَحَدٌ','آیا او گمان می‌کند که هیچ کس نمی‌تواند بر او دست یابد؟!'];
ayat[6029]=[90,6,'يَقُولُ أَهْلَكْتُ مَالًا لُّبَدًا','می‌گوید: «مال زیادی را (در کارهای خیر) نابود کرده‌ام!»'];
ayat[6030]=[90,7,'أَيَحْسَبُ أَن لَّمْ يَرَهُ أَحَدٌ','آیا (انسان) گمان می‌کند هیچ کس او را ندیده (که عمل خیری انجام نداده) است؟!'];
ayat[6031]=[90,8,'أَلَمْ نَجْعَل لَّهُ عَيْنَيْنِ','آیا برای او دو چشم قرار ندادیم،'];
ayat[6032]=[90,9,'وَلِسَانًا وَشَفَتَيْنِ','و یک زبان و دو لب؟!'];
ayat[6033]=[90,10,'وَهَدَيْنَاهُ النَّجْدَيْنِ','و او را به راه خیر و شرّ هدایت کردیم!'];
ayat[6034]=[90,11,'فَلَا اقْتَحَمَ الْعَقَبَةَ','ولی او از آن گردنه مهمّ نگذشت!'];
ayat[6035]=[90,12,'وَمَا أَدْرَاكَ مَا الْعَقَبَةُ','و تو نمی‌دانی آن گردنه چیست!'];
ayat[6036]=[90,13,'فَكُّ رَقَبَةٍ','آزادکردن برده‌ای،'];
ayat[6037]=[90,14,'أَوْ إِطْعَامٌ فِي يَوْمٍ ذِي مَسْغَبَةٍ','یا غذا دادن در روز گرسنگی...'];
ayat[6038]=[90,15,'يَتِيمًا ذَا مَقْرَبَةٍ','یتیمی از خویشاوندان،'];
ayat[6039]=[90,16,'أَوْ مِسْكِينًا ذَا مَتْرَبَةٍ','یا مستمندی خاک‌نشین را،'];
ayat[6040]=[90,17,'ثُمَّ كَانَ مِنَ الَّذِينَ آمَنُوا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِ','سپس از کسانی باشد که ایمان آورده و یکدیگر را به شکیبایی و رحمت توصیه می‌کنند!'];
ayat[6041]=[90,18,'أُولَٰئِكَ أَصْحَابُ الْمَيْمَنَةِ','آنها «اصحاب الیمین» اند (که نامه اعمالشان را به دست راستشان می‌دهند)!'];
ayat[6042]=[90,19,'وَالَّذِينَ كَفَرُوا بِآيَاتِنَا هُمْ أَصْحَابُ الْمَشْأَمَةِ','و کسانی که آیات ما را انکار کرده‌اند افرادی شومند (که نامه اعمالشان به دست چپشان داده می‌شود).'];
ayat[6043]=[90,20,'عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌ','بر آنها آتشی است فروبسته (که راه فراری از آن نیست)!'];
ayat[6044]=[91,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالشَّمْسِ وَضُحَاهَا','به خورشید و گسترش نور آن سوگند،'];
ayat[6045]=[91,2,'وَالْقَمَرِ إِذَا تَلَاهَا','و به ماه هنگامی که بعد از آن درآید،'];
ayat[6046]=[91,3,'وَالنَّهَارِ إِذَا جَلَّاهَا','و به روز هنگامی که صفحه زمین را روشن سازد،'];
ayat[6047]=[91,4,'وَاللَّيْلِ إِذَا يَغْشَاهَا','و به شب آن هنگام که زمین را بپوشاند،'];
ayat[6048]=[91,5,'وَالسَّمَاءِ وَمَا بَنَاهَا','و قسم به آسمان و کسی که آسمان را بنا کرده،'];
ayat[6049]=[91,6,'وَالْأَرْضِ وَمَا طَحَاهَا','و به زمین و کسی که آن را گسترانیده،'];
ayat[6050]=[91,7,'وَنَفْسٍ وَمَا سَوَّاهَا','و قسم به جان آدمی و آن کس که آن را (آفریده و) منظّم ساخته،'];
ayat[6051]=[91,8,'فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَاهَا','سپس فجور و تقوا (شرّ و خیرش) را به او الهام کرده است،'];
ayat[6052]=[91,9,'قَدْ أَفْلَحَ مَن زَكَّاهَا','که هر کس نفس خود را پاک و تزکیه کرده، رستگار شده؛'];
ayat[6053]=[91,10,'وَقَدْ خَابَ مَن دَسَّاهَا','و آن کس که نفس خویش را با معصیت و گناه آلوده ساخته، نومید و محروم گشته است!'];
ayat[6054]=[91,11,'كَذَّبَتْ ثَمُودُ بِطَغْوَاهَا','قوم «ثمود» بر اثر طغیان، (پیامبرشان را) تکذیب کردند،'];
ayat[6055]=[91,12,'إِذِ انبَعَثَ أَشْقَاهَا','آنگاه که شقی‌ترین آنها بپاخاست،'];
ayat[6056]=[91,13,'فَقَالَ لَهُمْ رَسُولُ اللَّهِ نَاقَةَ اللَّهِ وَسُقْيَاهَا','و فرستاده الهی [= صالح‌] به آنان گفت: «ناقه خدا [= همان شتری که معجزه الهی بود] را با آبشخورش واگذارید (و مزاحم آن نشوید)!»'];
ayat[6057]=[91,14,'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمْدَمَ عَلَيْهِمْ رَبُّهُم بِذَنبِهِمْ فَسَوَّاهَا','ولی آنها او را تکذیب و ناقه را پی کردند (و به هلاکت رساندند)؛ از این رو پروردگارشان آنها (و سرزمینشان) را بخاطر گناهانشان در هم کوبید و با خاک یکسان و صاف کرد!'];
ayat[6058]=[91,15,'وَلَا يَخَافُ عُقْبَاهَا','و او هرگز از فرجام این کار [= مجازات ستمگران‌] بیم ندارد!'];
ayat[6059]=[92,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَاللَّيْلِ إِذَا يَغْشَىٰ','قسم به شب در آن هنگام که (جهان را) بپوشاند،'];
ayat[6060]=[92,2,'وَالنَّهَارِ إِذَا تَجَلَّىٰ','و قسم به روز هنگامی که تجلّی کند،'];
ayat[6061]=[92,3,'وَمَا خَلَقَ الذَّكَرَ وَالْأُنثَىٰ','و قسم به آن کس که جنس مذکّر و مؤنّث را آفرید،'];
ayat[6062]=[92,4,'إِنَّ سَعْيَكُمْ لَشَتَّىٰ','که سعی و تلاش شما مختلف است:'];
ayat[6063]=[92,5,'فَأَمَّا مَنْ أَعْطَىٰ وَاتَّقَىٰ','امّا آن کس که (در راه خدا) انفاق کند و پرهیزگاری پیش گیرد،'];
ayat[6064]=[92,6,'وَصَدَّقَ بِالْحُسْنَىٰ','و جزای نیک (الهی) را تصدیق کند،'];
ayat[6065]=[92,7,'فَسَنُيَسِّرُهُ لِلْيُسْرَىٰ','ما او را در مسیر آسانی قرار می‌دهیم!'];
ayat[6066]=[92,8,'وَأَمَّا مَن بَخِلَ وَاسْتَغْنَىٰ','امّا کسی که بخل ورزد و (از این راه) بی‌نیازی طلبد،'];
ayat[6067]=[92,9,'وَكَذَّبَ بِالْحُسْنَىٰ','و پاداش نیک (الهی) را انکار کند،'];
ayat[6068]=[92,10,'فَسَنُيَسِّرُهُ لِلْعُسْرَىٰ','بزودی او را در مسیر دشواری قرار می‌دهیم؛'];
ayat[6069]=[92,11,'وَمَا يُغْنِي عَنْهُ مَالُهُ إِذَا تَرَدَّىٰ','و در آن هنگام که (در جهنّم) سقوط می‌کند، اموالش به حال او سودی نخواهد داشت!'];
ayat[6070]=[92,12,'إِنَّ عَلَيْنَا لَلْهُدَىٰ','به یقین هدایت کردن بر ماست،'];
ayat[6071]=[92,13,'وَإِنَّ لَنَا لَلْآخِرَةَ وَالْأُولَىٰ','و آخرت و دنیا از آن ماست،'];
ayat[6072]=[92,14,'فَأَنذَرْتُكُمْ نَارًا تَلَظَّىٰ','و من شما را از آتشی که زبانه می‌کشد بیم می‌دهم،'];
ayat[6073]=[92,15,'لَا يَصْلَاهَا إِلَّا الْأَشْقَى','کسی جز بدبخت‌ترین مردم وارد آن نمی‌شود؛'];
ayat[6074]=[92,16,'الَّذِي كَذَّبَ وَتَوَلَّىٰ','همان کس که (آیات خدا را) تکذیب کرد و به آن پشت نمود!'];
ayat[6075]=[92,17,'وَسَيُجَنَّبُهَا الْأَتْقَى','و بزودی با تقواترین مردم از آن دور داشته می‌شود،'];
ayat[6076]=[92,18,'الَّذِي يُؤْتِي مَالَهُ يَتَزَكَّىٰ','همان کس که مال خود را (در راه خدا) می‌بخشد تا پاک شود.'];
ayat[6077]=[92,19,'وَمَا لِأَحَدٍ عِندَهُ مِن نِّعْمَةٍ تُجْزَىٰ','و هیچ کس را نزد او حق نعمتی نیست تا بخواهد (به این وسیله) او را جزا دهد،'];
ayat[6078]=[92,20,'إِلَّا ابْتِغَاءَ وَجْهِ رَبِّهِ الْأَعْلَىٰ','بلکه تنها هدفش جلب رضای پروردگار بزرگ اوست؛'];
ayat[6079]=[92,21,'وَلَسَوْفَ يَرْضَىٰ','و بزودی راضی و خشنود می‌شود!'];
ayat[6080]=[93,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالضُّحَىٰ','قسم به روز در آن هنگام که آفتاب برآید (و همه جا را فراگیرد)،'];
ayat[6081]=[93,2,'وَاللَّيْلِ إِذَا سَجَىٰ','و سوگند به شب در آن هنگام که آرام گیرد،'];
ayat[6082]=[93,3,'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ','که خداوند هرگز تو را وانگذاشته و مورد خشم قرار نداده است!'];
ayat[6083]=[93,4,'وَلَلْآخِرَةُ خَيْرٌ لَّكَ مِنَ الْأُولَىٰ','و مسلّماً آخرت برای تو از دنیا بهتر است!'];
ayat[6084]=[93,5,'وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَىٰ','و بزودی پروردگارت آنقدر به تو عطا خواهد کرد که خشنود شوی!'];
ayat[6085]=[93,6,'أَلَمْ يَجِدْكَ يَتِيمًا فَآوَىٰ','آیا او تو را یتیم نیافت و پناه داد؟!'];
ayat[6086]=[93,7,'وَوَجَدَكَ ضَالًّا فَهَدَىٰ','و تو را گمشده یافت و هدایت کرد،'];
ayat[6087]=[93,8,'وَوَجَدَكَ عَائِلًا فَأَغْنَىٰ','و تو را فقیر یافت و بی‌نیاز نمود،'];
ayat[6088]=[93,9,'فَأَمَّا الْيَتِيمَ فَلَا تَقْهَرْ','حال که چنین است یتیم را تحقیر مکن،'];
ayat[6089]=[93,10,'وَأَمَّا السَّائِلَ فَلَا تَنْهَرْ','و سؤال‌کننده را از خود مران،'];
ayat[6090]=[93,11,'وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ','و نعمتهای پروردگارت را بازگو کن!'];
ayat[6091]=[94,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ','آیا ما سینه تو را گشاده نساختیم،'];
ayat[6092]=[94,2,'وَوَضَعْنَا عَنكَ وِزْرَكَ','و بار سنگین تو را از تو برنداشتیم؟!'];
ayat[6093]=[94,3,'الَّذِي أَنقَضَ ظَهْرَكَ','همان باری که سخت بر پشت تو سنگینی می‌کرد!'];
ayat[6094]=[94,4,'وَرَفَعْنَا لَكَ ذِكْرَكَ','و آوازه تو را بلند ساختیم!'];
ayat[6095]=[94,5,'فَإِنَّ مَعَ الْعُسْرِ يُسْرًا','به یقین با (هر) سختی آسانی است!'];
ayat[6096]=[94,6,'إِنَّ مَعَ الْعُسْرِ يُسْرًا','(آری) مسلّماً با (هر) سختی آسانی است،'];
ayat[6097]=[94,7,'فَإِذَا فَرَغْتَ فَانصَبْ','پس هنگامی که از کار مهمّی فارغ می‌شوی به مهم دیگری پرداز،'];
ayat[6098]=[94,8,'وَإِلَىٰ رَبِّكَ فَارْغَب','و به سوی پروردگارت توجّه کن!'];
ayat[6099]=[95,1,'بِّسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالتِّينِ وَالزَّيْتُونِ','قسم به انجیر و زیتون [یا: قسم به سرزمین شام و بیت المقدّس‌]،'];
ayat[6100]=[95,2,'وَطُورِ سِينِينَ','و سوگند به «طور سینین»،'];
ayat[6101]=[95,3,'وَهَٰذَا الْبَلَدِ الْأَمِينِ','و قسم به این شهر امن [= مکّه‌]،'];
ayat[6102]=[95,4,'لَقَدْ خَلَقْنَا الْإِنسَانَ فِي أَحْسَنِ تَقْوِيمٍ','که ما انسان را در بهترین صورت و نظام آفریدیم،'];
ayat[6103]=[95,5,'ثُمَّ رَدَدْنَاهُ أَسْفَلَ سَافِلِينَ','سپس او را به پایین‌ترین مرحله بازگرداندیم،'];
ayat[6104]=[95,6,'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ','مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند که برای آنها پاداشی تمام‌نشدنی است!'];
ayat[6105]=[95,7,'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّينِ','پس چه چیز سبب می‌شود که بعد از این همه (دلایل روشن) روز جزا را انکار کنی؟!'];
ayat[6106]=[95,8,'أَلَيْسَ اللَّهُ بِأَحْكَمِ الْحَاكِمِينَ','آیا خداوند بهترین حکم‌کنندگان نیست؟!'];
ayat[6107]=[96,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ اقْرَأْ بِاسْمِ رَبِّكَ الَّذِي خَلَقَ','بخوان به نام پروردگارت که (جهان را) آفرید،'];
ayat[6108]=[96,2,'خَلَقَ الْإِنسَانَ مِنْ عَلَقٍ','همان کس که انسان را از خون بسته‌ای خلق کرد!'];
ayat[6109]=[96,3,'اقْرَأْ وَرَبُّكَ الْأَكْرَمُ','بخوان که پروردگارت (از همه) بزرگوارتر است،'];
ayat[6110]=[96,4,'الَّذِي عَلَّمَ بِالْقَلَمِ','همان کسی که بوسیله قلم تعلیم نمود،'];
ayat[6111]=[96,5,'عَلَّمَ الْإِنسَانَ مَا لَمْ يَعْلَمْ','و به انسان آنچه را نمی‌دانست یاد داد!'];
ayat[6112]=[96,6,'كَلَّا إِنَّ الْإِنسَانَ لَيَطْغَىٰ','چنین نیست (که شما می‌پندارید) به یقین انسان طغیان می‌کند،'];
ayat[6113]=[96,7,'أَن رَّآهُ اسْتَغْنَىٰ','از اینکه خود را بی‌نیاز ببیند!'];
ayat[6114]=[96,8,'إِنَّ إِلَىٰ رَبِّكَ الرُّجْعَىٰ','و به یقین بازگشت (همه) به سوی پروردگار تو است!'];
ayat[6115]=[96,9,'أَرَأَيْتَ الَّذِي يَنْهَىٰ','به من خبر ده آیا کسی که نهی می‌کند،'];
ayat[6116]=[96,10,'عَبْدًا إِذَا صَلَّىٰ','بنده‌ای را به هنگامی که نماز می‌خواند (آیا مستحق عذاب الهی نیست)؟!'];
ayat[6117]=[96,11,'أَرَأَيْتَ إِن كَانَ عَلَى الْهُدَىٰ','به من خبر ده اگر این بنده به راه هدایت باشد،'];
ayat[6118]=[96,12,'أَوْ أَمَرَ بِالتَّقْوَىٰ','یا مردم را به تقوا فرمان دهد (آیا نهی کردن او سزاوار است)؟!'];
ayat[6119]=[96,13,'أَرَأَيْتَ إِن كَذَّبَ وَتَوَلَّىٰ','به من خبر ده اگر (این طغیانگر) حق را انکار کند و به آن پشت نماید (آیا مستحق مجازات الهی نیست)؟!'];
ayat[6120]=[96,14,'أَلَمْ يَعْلَم بِأَنَّ اللَّهَ يَرَىٰ','آیا او ندانست که خداوند (همه اعمالش را) می‌بیند؟!'];
ayat[6121]=[96,15,'كَلَّا لَئِن لَّمْ يَنتَهِ لَنَسْفَعًا بِالنَّاصِيَةِ','چنان نیست که او خیال می‌کند، اگر دست از کار خود برندارد، ناصیه‌اش [= موی پیش سرش‌] را گرفته (و به سوی عذاب می‌کشانیم)،'];
ayat[6122]=[96,16,'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍ','همان ناصیه دروغگوی خطاکار را!'];
ayat[6123]=[96,17,'فَلْيَدْعُ نَادِيَهُ','سپس هر که را می‌خواهد صدا بزند (تا یاریش کند)!'];
ayat[6124]=[96,18,'سَنَدْعُ الزَّبَانِيَةَ','ما هم بزودی مأموران دوزخ را صدا می‌زنیم (تا او را به دوزخ افکنند)!'];
ayat[6125]=[96,19,'كَلَّا لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِب ۩','چنان نیست (که آن طغیانگر می‌پندارد)؛ هرگز او را اطاعت مکن، و سجده نما و (به خدا) تقرّب جوی!'];
ayat[6126]=[97,1,'بِّسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَنزَلْنَاهُ فِي لَيْلَةِ الْقَدْرِ','ما آن [= قرآن‌] را در شب قدر نازل کردیم!'];
ayat[6127]=[97,2,'وَمَا أَدْرَاكَ مَا لَيْلَةُ الْقَدْرِ','و تو چه می‌دانی شب قدر چیست؟!'];
ayat[6128]=[97,3,'لَيْلَةُ الْقَدْرِ خَيْرٌ مِّنْ أَلْفِ شَهْرٍ','شب قدر بهتر از هزار ماه است!'];
ayat[6129]=[97,4,'تَنَزَّلُ الْمَلَائِكَةُ وَالرُّوحُ فِيهَا بِإِذْنِ رَبِّهِم مِّن كُلِّ أَمْرٍ','فرشتگان و «روح» در آن شب به اذن پروردگارشان برای (تقدیر) هر کاری نازل می‌شوند.'];
ayat[6130]=[97,5,'سَلَامٌ هِيَ حَتَّىٰ مَطْلَعِ الْفَجْرِ','شبی است سرشار از سلامت (و برکت و رحمت) تا طلوع سپیده!'];
ayat[6131]=[98,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لَمْ يَكُنِ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأْتِيَهُمُ الْبَيِّنَةُ','کافران از اهل کتاب و مشرکان (می‌گفتند:) دست از آیین خود برنمی‌دارند تا دلیل روشنی برای آنها بیاید،'];
ayat[6132]=[98,2,'رَسُولٌ مِّنَ اللَّهِ يَتْلُو صُحُفًا مُّطَهَّرَةً','پیامبری از سوی خدا (بیاید) که صحیفه‌های پاکی را (بر آنها) بخواند،'];
ayat[6133]=[98,3,'فِيهَا كُتُبٌ قَيِّمَةٌ','و در آن نوشته‌های صحیح و پرارزشی باشد! (ولی هنگامی که آمد ایمان نیاوردند، مانند اهل کتاب).'];
ayat[6134]=[98,4,'وَمَا تَفَرَّقَ الَّذِينَ أُوتُوا الْكِتَابَ إِلَّا مِن بَعْدِ مَا جَاءَتْهُمُ الْبَيِّنَةُ','اهل کتاب (نیز در دین خدا) اختلاف نکردند مگر بعد از آنکه دلیل روشن برای آنان آمد!'];
ayat[6135]=[98,5,'وَمَا أُمِرُوا إِلَّا لِيَعْبُدُوا اللَّهَ مُخْلِصِينَ لَهُ الدِّينَ حُنَفَاءَ وَيُقِيمُوا الصَّلَاةَ وَيُؤْتُوا الزَّكَاةَ ۚ وَذَٰلِكَ دِينُ الْقَيِّمَةِ','و به آنها دستوری داده نشده بود جز اینکه خدا را بپرستند در حالی که دین خود را برای او خالص کنند و از شرک به توحید بازگردند، نماز را برپا دارند و زکات را بپردازند؛ و این است آیین مستقیم و پایدار!'];
ayat[6136]=[98,6,'إِنَّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ وَالْمُشْرِكِينَ فِي نَارِ جَهَنَّمَ خَالِدِينَ فِيهَا ۚ أُولَٰئِكَ هُمْ شَرُّ الْبَرِيَّةِ','کافران از اهل کتاب و مشرکان در آتش دوزخند، جاودانه در آن می‌مانند؛ آنها بدترین مخلوقاتند!'];
ayat[6137]=[98,7,'إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ أُولَٰئِكَ هُمْ خَيْرُ الْبَرِيَّةِ','(امّا) کسانی که ایمان آوردند و اعمال صالح انجام دادند، بهترین مخلوقات (خدا) یند!'];
ayat[6138]=[98,8,'جَزَاؤُهُمْ عِندَ رَبِّهِمْ جَنَّاتُ عَدْنٍ تَجْرِي مِن تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ رَّضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ ذَٰلِكَ لِمَنْ خَشِيَ رَبَّهُ','پاداش آنها نزد پروردگارشان باغهای بهشت جاویدان است که نهرها از زیر درختانش جاری است؛ همیشه در آن می‌مانند! (هم) خدا از آنها خشنود است و (هم) آنها از خدا خشنودند؛ و این (مقام والا) برای کسی است که از پروردگارش بترسد!'];
ayat[6139]=[99,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا زُلْزِلَتِ الْأَرْضُ زِلْزَالَهَا','هنگامی که زمین شدیداً به لرزه درآید،'];
ayat[6140]=[99,2,'وَأَخْرَجَتِ الْأَرْضُ أَثْقَالَهَا','و زمین بارهای سنگینش را خارج سازد!'];
ayat[6141]=[99,3,'وَقَالَ الْإِنسَانُ مَا لَهَا','و انسان می‌گوید: «زمین را چه می‌شود (که این گونه می‌لرزد)؟!»'];
ayat[6142]=[99,4,'يَوْمَئِذٍ تُحَدِّثُ أَخْبَارَهَا','در آن روز زمین تمام خبرهایش را بازگو می‌کند؛'];
ayat[6143]=[99,5,'بِأَنَّ رَبَّكَ أَوْحَىٰ لَهَا','چرا که پروردگارت به او وحی کرده است!'];
ayat[6144]=[99,6,'يَوْمَئِذٍ يَصْدُرُ النَّاسُ أَشْتَاتًا لِّيُرَوْا أَعْمَالَهُمْ','در آن روز مردم بصورت گروه‌های پراکنده (از قبرها) خارج می‌شوند تا اعمالشان به آنها نشان داده شود!'];
ayat[6145]=[99,7,'فَمَن يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَرَهُ','پس هر کس هموزن ذرّه‌ای کار خیر انجام دهد آن را می‌بیند!'];
ayat[6146]=[99,8,'وَمَن يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَرَهُ','و هر کس هموزن ذرّه‌ای کار بد کرده آن را می‌بیند!'];
ayat[6147]=[100,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْعَادِيَاتِ ضَبْحًا','سوگند به اسبان دونده (مجاهدان) در حالی که نفس‌زنان به پیش می‌رفتند،'];
ayat[6148]=[100,2,'فَالْمُورِيَاتِ قَدْحًا','و سوگند به افروزندگان جرقه آتش (در برخورد سمهایشان با سنگهای بیابان)،'];
ayat[6149]=[100,3,'فَالْمُغِيرَاتِ صُبْحًا','و سوگند به هجوم آوران سپیده دم‌'];
ayat[6150]=[100,4,'فَأَثَرْنَ بِهِ نَقْعًا','که گرد و غبار به هر سو پراکندند،'];
ayat[6151]=[100,5,'فَوَسَطْنَ بِهِ جَمْعًا','و (ناگهان) در میان دشمن ظاهر شدند،'];
ayat[6152]=[100,6,'إِنَّ الْإِنسَانَ لِرَبِّهِ لَكَنُودٌ','که انسان در برابر نعمتهای پروردگارش بسیار ناسپاس و بخیل است؛'];
ayat[6153]=[100,7,'وَإِنَّهُ عَلَىٰ ذَٰلِكَ لَشَهِيدٌ','و او خود (نیز) بر این معنی گواه است!'];
ayat[6154]=[100,8,'وَإِنَّهُ لِحُبِّ الْخَيْرِ لَشَدِيدٌ','و او علاقه شدید به مال دارد!'];
ayat[6155]=[100,9,'۞ أَفَلَا يَعْلَمُ إِذَا بُعْثِرَ مَا فِي الْقُبُورِ','آیا نمی‌داند در آن روز که تمام کسانی که در قبرها هستند برانگیخته می‌شوند،'];
ayat[6156]=[100,10,'وَحُصِّلَ مَا فِي الصُّدُورِ','و آنچه در درون سینه‌هاست آشکار می‌گردد،'];
ayat[6157]=[100,11,'إِنَّ رَبَّهُم بِهِمْ يَوْمَئِذٍ لَّخَبِيرٌ','در آن روز پروردگارشان از آنها کاملاً باخبر است!'];
ayat[6158]=[101,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ الْقَارِعَةُ','آن حادثه کوبنده،'];
ayat[6159]=[101,2,'مَا الْقَارِعَةُ','و چه حادثه کوبنده‌ای!'];
ayat[6160]=[101,3,'وَمَا أَدْرَاكَ مَا الْقَارِعَةُ','و تو چه می‌دانی که حادثه کوبنده چیست؟! (آن حادثه همان روز قیامت است!)'];
ayat[6161]=[101,4,'يَوْمَ يَكُونُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوثِ','روزی که مردم مانند پروانه‌های پراکنده خواهند بود،'];
ayat[6162]=[101,5,'وَتَكُونُ الْجِبَالُ كَالْعِهْنِ الْمَنفُوشِ','و کوه‌ها مانند پشم رنگین حلاّجی‌شده می‌گردد!'];
ayat[6163]=[101,6,'فَأَمَّا مَن ثَقُلَتْ مَوَازِينُهُ','امّا کسی که (در آن روز) ترازوهای اعمالش سنگین است،'];
ayat[6164]=[101,7,'فَهُوَ فِي عِيشَةٍ رَّاضِيَةٍ','در یک زندگی خشنودکننده خواهد بود!'];
ayat[6165]=[101,8,'وَأَمَّا مَنْ خَفَّتْ مَوَازِينُهُ','و امّا کسی که ترازوهایش سبک است،'];
ayat[6166]=[101,9,'فَأُمُّهُ هَاوِيَةٌ','پناهگاهش «هاویه» [= دوزخ‌] است!'];
ayat[6167]=[101,10,'وَمَا أَدْرَاكَ مَا هِيَهْ','و تو چه می‌دانی «هاویه» چیست؟!'];
ayat[6168]=[101,11,'نَارٌ حَامِيَةٌ','آتشی است سوزان!'];
ayat[6169]=[102,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَلْهَاكُمُ التَّكَاثُرُ','افزون طلبی (و تفاخر) شما را به خود مشغول داشته (و از خدا غافل نموده) است.'];
ayat[6170]=[102,2,'حَتَّىٰ زُرْتُمُ الْمَقَابِرَ','تا آنجا که به دیدار قبرها رفتید (و قبور مردگان خود را برشمردید و به آن افتخار کردید)!'];
ayat[6171]=[102,3,'كَلَّا سَوْفَ تَعْلَمُونَ','چنین نیست که می‌پندارید، (آری) بزودی خواهید دانست!'];
ayat[6172]=[102,4,'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ','باز چنان نیست که شما می‌پندارید؛ بزودی خواهید دانست!'];
ayat[6173]=[102,5,'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ الْيَقِينِ','چنان نیست که شما خیال می‌کنید؛ اگر شما علم الیقین (به آخرت) داشتید (افزون طلبی شما را از خدا غافل نمی‌کرد)!'];
ayat[6174]=[102,6,'لَتَرَوُنَّ الْجَحِيمَ','قطعاً شما جهنّم را خواهید دید!'];
ayat[6175]=[102,7,'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِينِ','سپس (با ورود در آن) آن را به عین الیقین خواهید دید.'];
ayat[6176]=[102,8,'ثُمَّ لَتُسْأَلُنَّ يَوْمَئِذٍ عَنِ النَّعِيمِ','سپس در آن روز (همه شما) از نعمتهایی که داشته‌اید بازپرسی خواهید شد!'];
ayat[6177]=[103,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَالْعَصْرِ','به عصر سوگند،'];
ayat[6178]=[103,2,'إِنَّ الْإِنسَانَ لَفِي خُسْرٍ','که انسانها همه در زیانند؛'];
ayat[6179]=[103,3,'إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَتَوَاصَوْا بِالْحَقِّ وَتَوَاصَوْا بِالصَّبْرِ','مگر کسانی که ایمان آورده و اعمال صالح انجام داده‌اند، و یکدیگر را به حق سفارش کرده و یکدیگر را به شکیبایی و استقامت توصیه نموده‌اند!'];
ayat[6180]=[104,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍ','وای بر هر عیبجوی مسخره‌کننده‌ای!'];
ayat[6181]=[104,2,'الَّذِي جَمَعَ مَالًا وَعَدَّدَهُ','همان کس که مال فراوانی جمع‌آوری و شماره کرده (بی‌آنکه مشروع و نامشروع آن را حساب کند)!'];
ayat[6182]=[104,3,'يَحْسَبُ أَنَّ مَالَهُ أَخْلَدَهُ','او گمان می‌کند که اموالش او را جاودانه می‌سازد!'];
ayat[6183]=[104,4,'كَلَّا ۖ لَيُنبَذَنَّ فِي الْحُطَمَةِ','چنین نیست که می‌پندارد؛ بزودی در «حُطَمه» [= آتشی خردکننده‌] پرتاب می‌شود!'];
ayat[6184]=[104,5,'وَمَا أَدْرَاكَ مَا الْحُطَمَةُ','و تو چه می‌دانی «حُطمه» چیست؟!'];
ayat[6185]=[104,6,'نَارُ اللَّهِ الْمُوقَدَةُ','آتش برافروخته الهی است،'];
ayat[6186]=[104,7,'الَّتِي تَطَّلِعُ عَلَى الْأَفْئِدَةِ','آتشی که از دلها سرمی‌زند!'];
ayat[6187]=[104,8,'إِنَّهَا عَلَيْهِم مُّؤْصَدَةٌ','این آتش بر آنها فروبسته شده،'];
ayat[6188]=[104,9,'فِي عَمَدٍ مُّمَدَّدَةٍ','در ستونهای کشیده و طولانی!'];
ayat[6189]=[105,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَابِ الْفِيلِ','آیا ندیدی پروردگارت با فیل سواران [= لشکر ابرهه که برای نابودی کعبه آمده بودند] چه کرد؟!'];
ayat[6190]=[105,2,'أَلَمْ يَجْعَلْ كَيْدَهُمْ فِي تَضْلِيلٍ','آیا نقشه آنها را در ضلالت و تباهی قرار نداد؟!'];
ayat[6191]=[105,3,'وَأَرْسَلَ عَلَيْهِمْ طَيْرًا أَبَابِيلَ','و بر سر آنها پرندگانی را گروه گروه فرستاد،'];
ayat[6192]=[105,4,'تَرْمِيهِم بِحِجَارَةٍ مِّن سِجِّيلٍ','که با سنگهای کوچکی آنان را هدف قرارمی‌دادند؛'];
ayat[6193]=[105,5,'فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُولٍ','سرانجام آنها را همچون کاه خورده‌شده (و متلاشی) قرار داد!'];
ayat[6194]=[106,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ لِإِيلَافِ قُرَيْشٍ','(کیفر لشکر فیل‌سواران) بخاطر این بود که قریش (به این سرزمین مقدس) الفت گیرند (و زمینه ظهور پیامبر فراهم شود)!'];
ayat[6195]=[106,2,'إِيلَافِهِمْ رِحْلَةَ الشِّتَاءِ وَالصَّيْفِ','الفت آنها در سفرهای زمستانه و تابستانه (و بخاطر این الفت به آن بازگردند)!'];
ayat[6196]=[106,3,'فَلْيَعْبُدُوا رَبَّ هَٰذَا الْبَيْتِ','پس (بشکرانه این نعمت بزرگ) باید پروردگار این خانه را عبادت کنند،'];
ayat[6197]=[106,4,'الَّذِي أَطْعَمَهُم مِّن جُوعٍ وَآمَنَهُم مِّنْ خَوْفٍ','همان کس که آنها را از گرسنگی نجات داد و از ترس و ناامنی ایمن ساخت.'];
ayat[6198]=[107,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ أَرَأَيْتَ الَّذِي يُكَذِّبُ بِالدِّينِ','آیا کسی که روز جزا را پیوسته انکار می‌کند دیدی؟'];
ayat[6199]=[107,2,'فَذَٰلِكَ الَّذِي يَدُعُّ الْيَتِيمَ','او همان کسی است که یتیم را با خشونت می‌راند،'];
ayat[6200]=[107,3,'وَلَا يَحُضُّ عَلَىٰ طَعَامِ الْمِسْكِينِ','و (دیگران را) به اطعام مسکین تشویق نمی‌کند!'];
ayat[6201]=[107,4,'فَوَيْلٌ لِّلْمُصَلِّينَ','پس وای بر نمازگزارانی که...'];
ayat[6202]=[107,5,'الَّذِينَ هُمْ عَن صَلَاتِهِمْ سَاهُونَ','در نماز خود سهل‌انگاری می‌کنند،'];
ayat[6203]=[107,6,'الَّذِينَ هُمْ يُرَاءُونَ','همان کسانی که ریا می‌کنند،'];
ayat[6204]=[107,7,'وَيَمْنَعُونَ الْمَاعُونَ','و دیگران را از وسایل ضروری زندگی منع می‌نمایند!'];
ayat[6205]=[108,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِنَّا أَعْطَيْنَاكَ الْكَوْثَرَ','ما به تو کوثر [= خیر و برکت فراوان‌] عطا کردیم!'];
ayat[6206]=[108,2,'فَصَلِّ لِرَبِّكَ وَانْحَرْ','پس برای پروردگارت نماز بخوان و قربانی کن!'];
ayat[6207]=[108,3,'إِنَّ شَانِئَكَ هُوَ الْأَبْتَرُ','(و بدان) دشمن تو قطعاً بریده‌نسل و بی‌عقب است!'];
ayat[6208]=[109,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ يَا أَيُّهَا الْكَافِرُونَ','بگو: ای کافران!'];
ayat[6209]=[109,2,'لَا أَعْبُدُ مَا تَعْبُدُونَ','آنچه را شما می‌پرستید من نمی‌پرستم!'];
ayat[6210]=[109,3,'وَلَا أَنتُمْ عَابِدُونَ مَا أَعْبُدُ','و نه شما آنچه را من می‌پرستم می‌پرستید،'];
ayat[6211]=[109,4,'وَلَا أَنَا عَابِدٌ مَّا عَبَدتُّمْ','و نه من هرگز آنچه را شما پرستش کرده‌اید می‌پرستم،'];
ayat[6212]=[109,5,'وَلَا أَنتُمْ عَابِدُونَ مَا أَعْبُدُ','و نه شما آنچه را که من می‌پرستم پرستش می‌کنید؛'];
ayat[6213]=[109,6,'لَكُمْ دِينُكُمْ وَلِيَ دِينِ','(حال که چنین است) آیین شما برای خودتان، و آیین من برای خودم!'];
ayat[6214]=[110,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا جَاءَ نَصْرُ اللَّهِ وَالْفَتْحُ','هنگامی که یاری خدا و پیروزی فرارسد،'];
ayat[6215]=[110,2,'وَرَأَيْتَ النَّاسَ يَدْخُلُونَ فِي دِينِ اللَّهِ أَفْوَاجًا','و ببینی مردم گروه گروه وارد دین خدا می‌شوند،'];
ayat[6216]=[110,3,'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُ ۚ إِنَّهُ كَانَ تَوَّابًا','پروردگارت را تسبیح و حمد کن و از او آمرزش بخواه که او بسیار توبه‌پذیر است!'];
ayat[6217]=[111,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَّتْ يَدَا أَبِي لَهَبٍ وَتَبَّ','بریده باد هر دو دست ابولهب (و مرگ بر او باد)!'];
ayat[6218]=[111,2,'مَا أَغْنَىٰ عَنْهُ مَالُهُ وَمَا كَسَبَ','هرگز مال و ثروتش و آنچه را به دست آورد به حالش سودی نبخشید!'];
ayat[6219]=[111,3,'سَيَصْلَىٰ نَارًا ذَاتَ لَهَبٍ','و بزودی وارد آتشی شعله‌ور و پرلهیب می‌شود؛'];
ayat[6220]=[111,4,'وَامْرَأَتُهُ حَمَّالَةَ الْحَطَبِ','و (نیز) همسرش، در حالی که هیزم‌کش (دوزخ) است،'];
ayat[6221]=[111,5,'فِي جِيدِهَا حَبْلٌ مِّن مَّسَدٍ','و در گردنش طنابی است از لیف خرما!'];
ayat[6222]=[112,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ هُوَ اللَّهُ أَحَدٌ','بگو: خداوند، یکتا و یگانه است؛'];
ayat[6223]=[112,2,'اللَّهُ الصَّمَدُ','خداوندی است که همه نیازمندان قصد او می‌کنند؛'];
ayat[6224]=[112,3,'لَمْ يَلِدْ وَلَمْ يُولَدْ','(هرگز) نزاد، و زاده نشد،'];
ayat[6225]=[112,4,'وَلَمْ يَكُن لَّهُ كُفُوًا أَحَدٌ','و برای او هیچگاه شبیه و مانندی نبوده است!'];
ayat[6226]=[113,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ','بگو: پناه می‌برم به پروردگار سپیده صبح،'];
ayat[6227]=[113,2,'مِن شَرِّ مَا خَلَقَ','از شرّ تمام آنچه آفریده است؛'];
ayat[6228]=[113,3,'وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ','و از شرّ هر موجود شرور هنگامی که شبانه وارد می‌شود؛'];
ayat[6229]=[113,4,'وَمِن شَرِّ النَّفَّاثَاتِ فِي الْعُقَدِ','و از شرّ آنها که با افسون در گره‌ها می‌دمند (و هر تصمیمی را سست می‌کنند)؛'];
ayat[6230]=[113,5,'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ','و از شرّ هر حسودی هنگامی که حسد می‌ورزد!'];
ayat[6231]=[114,1,'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ النَّاسِ','بگو: پناه می‌برم به پروردگار مردم،'];
ayat[6232]=[114,2,'مَلِكِ النَّاسِ','به مالک و حاکم مردم،'];
ayat[6233]=[114,3,'إِلَٰهِ النَّاسِ','به (خدا و) معبود مردم،'];
ayat[6234]=[114,4,'مِن شَرِّ الْوَسْوَاسِ الْخَنَّاسِ','از شرّ وسوسه‌گر پنهانکار،'];
ayat[6235]=[114,5,'الَّذِي يُوَسْوِسُ فِي صُدُورِ النَّاسِ','که در درون سینه انسانها وسوسه می‌کند،'];
ayat[6236]=[114,6,'مِنَ الْجِنَّةِ وَالنَّاسِ','خواه از جنّ باشد یا از انسان!'];


